window.SUTTA_DB['sutta_kn_cp'] = {"id": "cp", "title": "Canon of Conduct", "structure": {"cp": {"cp-akittivagga": {"cp-danaparami": ["cp1", "cp2", "cp3", "cp4", "cp5", "cp6", "cp7", "cp8", "cp9", "cp10"]}, "cp-hatthinagavagga": {"cp-silaparami": ["cp11", "cp12", "cp13", "cp14", "cp15", "cp16", "cp17", "cp18", "cp19", "cp20"]}, "cp-yudhanjayavagga": {"cp-nekkhammaparami": ["cp21", "cp22", "cp23", "cp24", "cp25"], "cp-aditthanaparami": ["cp26"], "cp-saccaparami": ["cp27", "cp28", "cp29", "cp30", "cp31", "cp32"], "cp-mettaparami": ["cp33", "cp34"], "cp-upekkhaparami": ["cp35"]}}}, "meta": {"cp": {"type": "branch", "acronym": "Cp", "translated_title": "Canon of Conduct", "original_title": "Cariyāpiṭaka", "blurb": "The “Basket of Conduct” represents an early systematization of the concept of the Bodhisattva and his path towards awakening. A selection of 34 past lives are told, each one illustrating a specific virtue developed by the Bodhisattva. It is a late text, without direct counterparts in the northern collections. Like the Buddhavaṁsa, it is part of an extensive pan-sectarian devotional literature composed in the second and first centuries BCE, presaging the birth of the Mahāyāna.", "author_uid": null}, "cp-akittivagga": {"type": "branch", "acronym": "", "translated_title": "With Akitti", "original_title": "Akittivagga", "blurb": null, "author_uid": null}, "cp-danaparami": {"type": "branch", "acronym": "", "translated_title": "The Perfection of Generosity", "original_title": "Dānapāramī", "blurb": null, "author_uid": null}, "cp1": {"type": "leaf", "acronym": "Cp 1", "translated_title": "The Conduct of Akitti", "original_title": "Akitticariya", "blurb": null, "author_uid": "sujato"}, "cp2": {"type": "leaf", "acronym": "Cp 2", "translated_title": "The Conduct of Saṅkha", "original_title": "Saṅkhacariya", "blurb": null, "author_uid": "sujato"}, "cp3": {"type": "leaf", "acronym": "Cp 3", "translated_title": "The Conduct of the King of Kuru", "original_title": "Kururājacariya", "blurb": null, "author_uid": "sujato"}, "cp4": {"type": "leaf", "acronym": "Cp 4", "translated_title": "The Conduct of Mahāsudassana", "original_title": "Mahāsudassanacariya", "blurb": null, "author_uid": "sujato"}, "cp5": {"type": "leaf", "acronym": "Cp 5", "translated_title": "The Conduct of Mahāgovinda", "original_title": "Mahāgovindacariya", "blurb": null, "author_uid": "sujato"}, "cp6": {"type": "leaf", "acronym": "Cp 6", "translated_title": "The Conduct of King Nimi", "original_title": "Nimirājacariya", "blurb": null, "author_uid": "sujato"}, "cp7": {"type": "leaf", "acronym": "Cp 7", "translated_title": "The Conduct of Prince Canda", "original_title": "Candakumāracariya", "blurb": null, "author_uid": "sujato"}, "cp8": {"type": "leaf", "acronym": "Cp 8", "translated_title": "The Conduct of King Sivi", "original_title": "Sivirājacariya", "blurb": null, "author_uid": "sujato"}, "cp9": {"type": "leaf", "acronym": "Cp 9", "translated_title": "The Conduct of Vessantara", "original_title": "Vessantaracariya", "blurb": null, "author_uid": "sujato"}, "cp10": {"type": "leaf", "acronym": "Cp 10", "translated_title": "The Conduct of the Wise Hare", "original_title": "Sasapaṇḍitacariya", "blurb": null, "author_uid": "sujato"}, "cp-hatthinagavagga": {"type": "branch", "acronym": "", "translated_title": "The Elephant", "original_title": "Hatthināgavagga", "blurb": null, "author_uid": null}, "cp-silaparami": {"type": "branch", "acronym": "", "translated_title": "The Perfection of Morality", "original_title": "Sīlapāramī", "blurb": null, "author_uid": null}, "cp11": {"type": "leaf", "acronym": "Cp 11", "translated_title": "The Conduct of a Mother’s Provider", "original_title": "Mātuposakacariya", "blurb": null, "author_uid": "sujato"}, "cp12": {"type": "leaf", "acronym": "Cp 12", "translated_title": "The Conduct of Bhūridatta", "original_title": "Bhūridattacariya", "blurb": null, "author_uid": "sujato"}, "cp13": {"type": "leaf", "acronym": "Cp 13", "translated_title": "The Conduct of the Dragon of Campeyya", "original_title": "Campeyyanāgacariya", "blurb": null, "author_uid": "sujato"}, "cp14": {"type": "leaf", "acronym": "Cp 14", "translated_title": "The Conduct of Cūḷabodhi", "original_title": "Cūḷabodhicariya", "blurb": null, "author_uid": "sujato"}, "cp15": {"type": "leaf", "acronym": "Cp 15", "translated_title": "The Conduct of the Buffalo King", "original_title": "Mahiṁsarājacariya", "blurb": null, "author_uid": "sujato"}, "cp16": {"type": "leaf", "acronym": "Cp 16", "translated_title": "The Conduct of the Deer-king Ruru", "original_title": "Rurumigarājacariya", "blurb": null, "author_uid": "sujato"}, "cp17": {"type": "leaf", "acronym": "Cp 17", "translated_title": "The Conduct of Mātaṅga", "original_title": "Mātaṅgacariya", "blurb": null, "author_uid": "sujato"}, "cp18": {"type": "leaf", "acronym": "Cp 18", "translated_title": "The Conduct of the Deity Named Dhamma", "original_title": "Dhammadevaputtacariya", "blurb": null, "author_uid": "sujato"}, "cp19": {"type": "leaf", "acronym": "Cp 19", "translated_title": "The Conduct of Alīnasattu", "original_title": "Alīnasattucariya", "blurb": null, "author_uid": "sujato"}, "cp20": {"type": "leaf", "acronym": "Cp 20", "translated_title": "The Conduct of Saṅkhapāla", "original_title": "Saṅkhapālacariya", "blurb": null, "author_uid": "sujato"}, "cp-yudhanjayavagga": {"type": "branch", "acronym": "", "translated_title": "With Yudhañjaya", "original_title": "Yudhañjayavagga", "blurb": null, "author_uid": null}, "cp-nekkhammaparami": {"type": "branch", "acronym": "", "translated_title": "The Perfection of Renunciation", "original_title": "Nekkhammapāramī", "blurb": null, "author_uid": null}, "cp21": {"type": "leaf", "acronym": "Cp 21", "translated_title": "The Conduct of Yudhañjaya", "original_title": "Yudhañjayacariya", "blurb": null, "author_uid": "sujato"}, "cp22": {"type": "leaf", "acronym": "Cp 22", "translated_title": "The Conduct of Somanassa", "original_title": "Somanassacariya", "blurb": null, "author_uid": "sujato"}, "cp23": {"type": "leaf", "acronym": "Cp 23", "translated_title": "The Conduct of Ayoghara", "original_title": "Ayogharacariya", "blurb": null, "author_uid": "sujato"}, "cp24": {"type": "leaf", "acronym": "Cp 24", "translated_title": "The Conduct Involving Lotus Stalks", "original_title": "Bhisacariya", "blurb": null, "author_uid": "sujato"}, "cp25": {"type": "leaf", "acronym": "Cp 25", "translated_title": "The Conduct of the Scholar Soṇa", "original_title": "Soṇapaṇḍitacariya", "blurb": null, "author_uid": "sujato"}, "cp-aditthanaparami": {"type": "branch", "acronym": "", "translated_title": "The Perfection of Determination", "original_title": "Adiṭṭhānapāramī", "blurb": null, "author_uid": null}, "cp26": {"type": "leaf", "acronym": "Cp 26", "translated_title": "The Conduct of Temiya", "original_title": "Temiyacariyaṃ", "blurb": null, "author_uid": "sujato"}, "cp-saccaparami": {"type": "branch", "acronym": "", "translated_title": "The Perfection of Truth", "original_title": "Saccapāramī", "blurb": null, "author_uid": null}, "cp27": {"type": "leaf", "acronym": "Cp 27", "translated_title": "The Conduct of the Monkey-king", "original_title": "Kapirājacariya", "blurb": null, "author_uid": "sujato"}, "cp28": {"type": "leaf", "acronym": "Cp 28", "translated_title": "The Conduct of the Ascetic Sacca", "original_title": "Saccatāpasacariya", "blurb": null, "author_uid": "sujato"}, "cp29": {"type": "leaf", "acronym": "Cp 29", "translated_title": "The Conduct of the Young Quail", "original_title": "Vaṭṭapotakacariya", "blurb": null, "author_uid": "sujato"}, "cp30": {"type": "leaf", "acronym": "Cp 30", "translated_title": "The Conduct of the Fish-king", "original_title": "Maccharājacariya", "blurb": null, "author_uid": "sujato"}, "cp31": {"type": "leaf", "acronym": "Cp 31", "translated_title": "The Conduct of Kaṇhadīpāyana", "original_title": "Kaṇhadīpāyanacariya", "blurb": null, "author_uid": "sujato"}, "cp32": {"type": "leaf", "acronym": "Cp 32", "translated_title": "The Conduct of Sutasoma", "original_title": "Sutasomacariya", "blurb": null, "author_uid": "sujato"}, "cp-mettaparami": {"type": "branch", "acronym": "", "translated_title": "The Perfection of Love", "original_title": "Mettāpāramī", "blurb": null, "author_uid": null}, "cp33": {"type": "leaf", "acronym": "Cp 33", "translated_title": "The Conduct of Golden Sāma", "original_title": "Suvaṇṇasāmacariya", "blurb": null, "author_uid": "sujato"}, "cp34": {"type": "leaf", "acronym": "Cp 34", "translated_title": "The Conduct of the One King", "original_title": "Ekarājacariya", "blurb": null, "author_uid": "sujato"}, "cp-upekkhaparami": {"type": "branch", "acronym": "", "translated_title": "The Perfection of Equanimity", "original_title": "Upekkhāpāramī", "blurb": null, "author_uid": null}, "cp35": {"type": "leaf", "acronym": "Cp 35", "translated_title": "The Hair-raising Conduct", "original_title": "Mahālomahaṁsacariya", "blurb": null, "author_uid": "sujato"}}, "content": {"cp1": {"cp1:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp1'><header><ul><li class='division'>{}</li>"}, "cp1:0.2": {"pli": "Akittivagga ", "eng": "The Chapter With Akitti ", "html": "<li>{}</li>"}, "cp1:0.3": {"pli": "Dānapāramī 1 ", "eng": "The Perfection of Giving (1st) ", "html": "<li>{}</li></ul>"}, "cp1:0.4": {"pli": "Akitticariya ", "eng": "Akitti’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp1:1.1": {"pli": "Namo tassa Bhagavato Arahato Sammāsambuddhassa. ", "eng": "Homage to him, the blessed one, the perfected one, the fully awakened Buddha! ", "html": "<p class='namo'>{}</p>"}, "cp1:2.1": {"pli": "“Kappe ca satasahasse, ", "eng": "“For a hundred thousand eons ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp1:2.2": {"pli": "caturo ca asaṅkhiye; ", "eng": "and four incalculable ages, ", "html": "<span class='verse-line'>{}</span>"}, "cp1:2.3": {"pli": "Etthantare yaṁ caritaṁ, ", "eng": "all my conduct in that period ", "html": "<span class='verse-line'>{}</span>"}, "cp1:2.4": {"pli": "sabbaṁ taṁ bodhipācanaṁ. ", "eng": "ripens in awakening. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp1:3.1": {"pli": "Atītakappe caritaṁ, ", "eng": "Setting aside my conduct ", "html": "<p><span class='verse-line'>{}</span>"}, "cp1:3.2": {"pli": "ṭhapayitvā bhavābhave; ", "eng": "in past eons through different lives, ", "html": "<span class='verse-line'>{}</span>"}, "cp1:3.3": {"pli": "Imamhi kappe caritaṁ, ", "eng": "I shall speak of my conduct ", "html": "<span class='verse-line'>{}</span>"}, "cp1:3.4": {"pli": "pavakkhissaṁ suṇohi me. ", "eng": "in this eon, hear me. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp1:4.1": {"pli": "Yadā ahaṁ brahāraññe, ", "eng": "When I lived having plunged ", "html": "<p><span class='verse-line'>{}</span>"}, "cp1:4.2": {"pli": "suññe vipinakānane; ", "eng": "into the formidable jungle ", "html": "<span class='verse-line'>{}</span>"}, "cp1:4.3": {"pli": "Ajjhogāhetvā viharāmi, ", "eng": "of empty thickets and glades, ", "html": "<span class='verse-line'>{}</span>"}, "cp1:4.4": {"pli": "akitti nāma tāpaso. ", "eng": "my name was Akitti the mortifier. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp1:5.1": {"pli": "Tadā maṁ tapatejena, ", "eng": "Then, burned by the fires of my mortification, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp1:5.2": {"pli": "santatto tidivābhibhū; ", "eng": "the sovereign of the Third Heaven, ", "html": "<span class='verse-line'>{}</span>", "comm": "Namely Sakka AKA Indra. "}, "cp1:5.3": {"pli": "Dhārento brāhmaṇavaṇṇaṁ, ", "eng": "came to me for alms ", "html": "<span class='verse-line'>{}</span>"}, "cp1:5.4": {"pli": "bhikkhāya maṁ upāgami. ", "eng": "in the form of a brahmin. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp1:6.1": {"pli": "Pavanā ābhataṁ paṇṇaṁ, ", "eng": "I had gathered leaves from the forest ", "html": "<p><span class='verse-line'>{}</span>"}, "cp1:6.2": {"pli": "atelañca aloṇikaṁ; ", "eng": "without oil or salt, ", "html": "<span class='verse-line'>{}</span>"}, "cp1:6.3": {"pli": "Mama dvāre ṭhitaṁ disvā, ", "eng": "and seeing him standing at my door, ", "html": "<span class='verse-line'>{}</span>"}, "cp1:6.4": {"pli": "sakaṭāhena ākiriṁ. ", "eng": "I placed them in his pot. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp1:7.1": {"pli": "Tassa datvānahaṁ paṇṇaṁ, ", "eng": "After giving him the leaves ", "html": "<p><span class='verse-line'>{}</span>"}, "cp1:7.2": {"pli": "nikkujjitvāna bhājanaṁ; ", "eng": "I turned over my plate, ", "html": "<span class='verse-line'>{}</span>"}, "cp1:7.3": {"pli": "Punesanaṁ jahitvāna, ", "eng": "and having given up on seeking more food, ", "html": "<span class='verse-line'>{}</span>"}, "cp1:7.4": {"pli": "pāvisiṁ paṇṇasālakaṁ. ", "eng": "I entered my hut of leaves. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp1:8.1": {"pli": "Dutiyampi tatiyampi, ", "eng": "A second and a third time ", "html": "<p><span class='verse-line'>{}</span>"}, "cp1:8.2": {"pli": "upagañchi mamantikaṁ; ", "eng": "he came to me. ", "html": "<span class='verse-line'>{}</span>"}, "cp1:8.3": {"pli": "Akampito anolaggo, ", "eng": "Undisturbed, ungrasping, ", "html": "<span class='verse-line'>{}</span>"}, "cp1:8.4": {"pli": "evamevamadāsahaṁ. ", "eng": "I gave in just the same way. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp1:9.1": {"pli": "Na me tappaccayā atthi, ", "eng": "That is the reason why ", "html": "<p><span class='verse-line'>{}</span>"}, "cp1:9.2": {"pli": "sarīrasmiṁ vivaṇṇiyaṁ; ", "eng": "my body is not discolored. ", "html": "<span class='verse-line'>{}</span>"}, "cp1:9.3": {"pli": "Pītisukhena ratiyā, ", "eng": "In rapture and bliss free of relishing ", "html": "<span class='verse-line'>{}</span>"}, "cp1:9.4": {"pli": "vītināmemi taṁ divaṁ. ", "eng": "I spend the day. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp1:10.1": {"pli": "Yadi māsampi dvemāsaṁ, ", "eng": "If even for a month or two ", "html": "<p><span class='verse-line'>{}</span>"}, "cp1:10.2": {"pli": "dakkhiṇeyyaṁ varaṁ labhe; ", "eng": "I were to find a worthy recipient, ", "html": "<span class='verse-line'>{}</span>"}, "cp1:10.3": {"pli": "Akampito anolīno, ", "eng": "undisturbed, unattached, ", "html": "<span class='verse-line'>{}</span>"}, "cp1:10.4": {"pli": "dadeyyaṁ dānamuttamaṁ. ", "eng": "I would give the supreme gift. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp1:11.1": {"pli": "Na tassa dānaṁ dadamāno, ", "eng": "While giving him that gift ", "html": "<p><span class='verse-line'>{}</span>"}, "cp1:11.2": {"pli": "yasaṁ lābhañca patthayiṁ; ", "eng": "I did not wish for fame or profit. ", "html": "<span class='verse-line'>{}</span>"}, "cp1:11.3": {"pli": "Sabbaññutaṁ patthayāno, ", "eng": "Wishing for omniscience ", "html": "<span class='verse-line'>{}</span>"}, "cp1:11.4": {"pli": "tāni kammāni ācarin”ti. ", "eng": "I performed those deeds.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp1:12.1": {"pli": "Akitticariyaṁ paṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp2": {"cp2:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp2'><header><ul><li class='division'>{}</li>"}, "cp2:0.2": {"pli": "Akittivagga ", "eng": "The Chapter With Akitti ", "html": "<li>{}</li>"}, "cp2:0.3": {"pli": "Dānapāramī 2 ", "eng": "The Perfection of Giving (2nd) ", "html": "<li>{}</li></ul>"}, "cp2:0.4": {"pli": "Saṅkhacariya ", "eng": "Saṅkha’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp2:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp2:1.2": {"pli": "Brāhmaṇo saṅkhasavhayo; ", "eng": "a brahmin called Saṅkha, ", "html": "<span class='verse-line'>{}</span>"}, "cp2:1.3": {"pli": "Mahāsamuddaṁ taritukāmo, ", "eng": "wanting to cross the great ocean, ", "html": "<span class='verse-line'>{}</span>"}, "cp2:1.4": {"pli": "Upagacchāmi paṭṭanaṁ. ", "eng": "I was on my way to the port. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp2:2.1": {"pli": "Tatthaddasaṁ paṭipathe, ", "eng": "There I saw across the way ", "html": "<p><span class='verse-line'>{}</span>"}, "cp2:2.2": {"pli": "Sayambhuṁ aparājitaṁ; ", "eng": "one self-awakened, undefeated, ", "html": "<span class='verse-line'>{}</span>"}, "cp2:2.3": {"pli": "Kantāraddhānaṁ paṭipannaṁ, ", "eng": "walking a desert road ", "html": "<span class='verse-line'>{}</span>"}, "cp2:2.4": {"pli": "Tattāya kaṭhinabhūmiyā. ", "eng": "whose ground was hot and rough. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp2:3.1": {"pli": "Tamahaṁ paṭipathe disvā, ", "eng": "Seeing him across the way, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp2:3.2": {"pli": "imamatthaṁ vicintayiṁ; ", "eng": "I thought this: ", "html": "<span class='verse-line'>{}</span>"}, "cp2:3.3": {"pli": "‘Idaṁ khettaṁ anuppattaṁ, ", "eng": "‘A field has appeared ", "html": "<span class='verse-line'>{}</span>"}, "cp2:3.4": {"pli": "puññakāmassa jantuno. ", "eng": "for a personage desiring merit. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp2:4.1": {"pli": "Yathā kassako puriso, ", "eng": "Now suppose a farmer ", "html": "<p><span class='verse-line'>{}</span>"}, "cp2:4.2": {"pli": "khettaṁ disvā mahāgamaṁ; ", "eng": "seeing a fertile field ", "html": "<span class='verse-line'>{}</span>"}, "cp2:4.3": {"pli": "Tattha bījaṁ na ropeti, ", "eng": "does not sow seed there, ", "html": "<span class='verse-line'>{}</span>"}, "cp2:4.4": {"pli": "na so dhaññena atthiko. ", "eng": "then they are not in need of grain. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp2:5.1": {"pli": "Evamevāhaṁ puññakāmo, ", "eng": "So too, if I, desiring merit, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp2:5.2": {"pli": "Disvā khettavaruttamaṁ; ", "eng": "seeing a supreme excellent field, ", "html": "<span class='verse-line'>{}</span>"}, "cp2:5.3": {"pli": "Yadi tattha kāraṁ na karomi, ", "eng": "do not render service there, ", "html": "<span class='verse-line'>{}</span>"}, "cp2:5.4": {"pli": "Nāhaṁ puññena atthiko. ", "eng": "then I am not in need of merit. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp2:6.1": {"pli": "Yathā amacco muddikāmo, ", "eng": "Or suppose a minister seeking the royal seal ", "html": "<p><span class='verse-line'>{}</span>"}, "cp2:6.2": {"pli": "rañño antepure jane; ", "eng": "of authority over the people of the court, ", "html": "<span class='verse-line'>{}</span>"}, "cp2:6.3": {"pli": "Na deti tesaṁ dhanadhaññaṁ, ", "eng": "does not give them money and grain, ", "html": "<span class='verse-line'>{}</span>"}, "cp2:6.4": {"pli": "muddito parihāyati. ", "eng": "their authority dwindles. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp2:7.1": {"pli": "Evamevāhaṁ puññakāmo, ", "eng": "So too, if I, desiring merit, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp2:7.2": {"pli": "Vipulaṁ disvāna dakkhiṇaṁ; ", "eng": "seeing an abundantly worthy recipient, ", "html": "<span class='verse-line'>{}</span>"}, "cp2:7.3": {"pli": "Yadi tassa dānaṁ na dadāmi, ", "eng": "do not give gifts to them, ", "html": "<span class='verse-line'>{}</span>"}, "cp2:7.4": {"pli": "Parihāyissāmi puññato’. ", "eng": "I will dwindle in merit.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp2:8.1": {"pli": "Evāhaṁ cintayitvāna, ", "eng": "Reflecting in this way, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp2:8.2": {"pli": "orohitvā upāhanā; ", "eng": "I took off my sandals ", "html": "<span class='verse-line'>{}</span>"}, "cp2:8.3": {"pli": "Tassa pādāni vanditvā, ", "eng": "and bowed to his feet, ", "html": "<span class='verse-line'>{}</span>"}, "cp2:8.4": {"pli": "adāsiṁ chattupāhanaṁ. ", "eng": "giving him an umbrella and sandals. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp2:9.1": {"pli": "Tenevāhaṁ sataguṇato, ", "eng": "Though I was a hundred times ", "html": "<p><span class='verse-line'>{}</span>"}, "cp2:9.2": {"pli": "Sukhumālo sukhedhito; ", "eng": "more delicate and dainty than him, ", "html": "<span class='verse-line'>{}</span>"}, "cp2:9.3": {"pli": "Api ca dānaṁ paripūrento, ", "eng": "fulfilling my generosity, ", "html": "<span class='verse-line'>{}</span>"}, "cp2:9.4": {"pli": "Evaṁ tassa adāsahan”ti. ", "eng": "I gave like this to him.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp2:10.1": {"pli": "Saṅkhacariyaṁ dutiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp3": {"cp3:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp3'><header><ul><li class='division'>{}</li>"}, "cp3:0.2": {"pli": "Akittivagga ", "eng": "The Chapter With Akitti ", "html": "<li>{}</li>"}, "cp3:0.3": {"pli": "Dānapāramī 3 ", "eng": "The Perfection of Giving (3rd) ", "html": "<li>{}</li></ul>"}, "cp3:0.4": {"pli": "Kururājacariya ", "eng": "Kururāja’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp3:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I reigned ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp3:1.2": {"pli": "indapatthe puruttame; ", "eng": "in Indapatta, the capital city, ", "html": "<span class='verse-line'>{}</span>", "comm": "Indrapastha, modern Delhi. "}, "cp3:1.3": {"pli": "Rājā dhanañcayo nāma, ", "eng": "as the king named Dhanañjaya ", "html": "<span class='verse-line'>{}</span>"}, "cp3:1.4": {"pli": "kusale dasahupāgato. ", "eng": "I was blessed with the ten skillful deeds. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp3:2.1": {"pli": "Kaliṅgaraṭṭhavisayā, ", "eng": "From the realm of Kaliṅga ", "html": "<p><span class='verse-line'>{}</span>"}, "cp3:2.2": {"pli": "brāhmaṇā upagañchu maṁ; ", "eng": "brahmins came to me ", "html": "<span class='verse-line'>{}</span>"}, "cp3:2.3": {"pli": "Āyācuṁ maṁ hatthināgaṁ, ", "eng": "requesting the royal elephant ", "html": "<span class='verse-line'>{}</span>"}, "cp3:2.4": {"pli": "dhaññaṁ maṅgalasammataṁ. ", "eng": "that was lucky and deemed auspicious. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp3:3.1": {"pli": "‘Avuṭṭhiko janapado, ", "eng": "‘Our nation is suffering from drought, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp3:3.2": {"pli": "dubbhikkho chātako mahā; ", "eng": "food is scarce and famine abounds, ", "html": "<span class='verse-line'>{}</span>"}, "cp3:3.3": {"pli": "Dadāhi pavaraṁ nāgaṁ, ", "eng": "please give the noble elephant ", "html": "<span class='verse-line'>{}</span>"}, "cp3:3.4": {"pli": "nīlaṁ añjanasavhayaṁ’. ", "eng": "of midnight hue named Añjana.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp3:4.1": {"pli": "‘Na me yācakamanuppatte, ", "eng": "‘When supplicants come to me ", "html": "<p><span class='verse-line'>{}</span>"}, "cp3:4.2": {"pli": "paṭikkhepo anucchavo; ", "eng": "it is unbefitting to refuse. ", "html": "<span class='verse-line'>{}</span>"}, "cp3:4.3": {"pli": "Mā me bhijji samādānaṁ, ", "eng": "Let not my vow be broken, ", "html": "<span class='verse-line'>{}</span>"}, "cp3:4.4": {"pli": "dassāmi vipulaṁ gajaṁ’. ", "eng": "I shall give the mighty elephant.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp3:5.1": {"pli": "Nāgaṁ gahetvā soṇḍāya, ", "eng": "Taking the elephant by the trunk, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp3:5.2": {"pli": "bhiṅgāre ratanāmaye; ", "eng": "and a bejeweled ceremonial vessel, ", "html": "<span class='verse-line'>{}</span>"}, "cp3:5.3": {"pli": "Jalaṁ hatthe ākiritvā, ", "eng": "I sprinkled water on the hand, ", "html": "<span class='verse-line'>{}</span>"}, "cp3:5.4": {"pli": "brāhmaṇānaṁ adaṁ gajaṁ. ", "eng": "and gave the elephant to the brahmins. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp3:6.1": {"pli": "Tassa nāge padinnamhi, ", "eng": "When I had given the elephant, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp3:6.2": {"pli": "amaccā etadabravuṁ; ", "eng": "my ministers said, ", "html": "<span class='verse-line'>{}</span>"}, "cp3:6.3": {"pli": "‘Kiṁ nu tuyhaṁ varaṁ nāgaṁ, ", "eng": "‘Why did you give the noble elephant ", "html": "<span class='verse-line'>{}</span>"}, "cp3:6.4": {"pli": "yācakānaṁ padassasi. ", "eng": "to the supplicants? ", "html": "<span class='verse-line'>{}</span></p>"}, "cp3:7.1": {"pli": "Dhaññaṁ maṅgalasampannaṁ, ", "eng": "He was lucky and endowed with the auspicious, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp3:7.2": {"pli": "saṅgāmavijayuttamaṁ; ", "eng": "supreme victor in battle. ", "html": "<span class='verse-line'>{}</span>"}, "cp3:7.3": {"pli": "Tasmiṁ nāge padinnamhi, ", "eng": "Now you have given the elephant, ", "html": "<span class='verse-line'>{}</span>"}, "cp3:7.4": {"pli": "kiṁ te rajjaṁ karissati’. ", "eng": "how shall you govern the realm?’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp3:8.1": {"pli": "‘Rajjampi me dade sabbaṁ, ", "eng": "‘I would give the entire realm ", "html": "<p><span class='verse-line'>{}</span>"}, "cp3:8.2": {"pli": "sarīraṁ dajjamattano; ", "eng": "and my physical body as well. ", "html": "<span class='verse-line'>{}</span>"}, "cp3:8.3": {"pli": "Sabbaññutaṁ piyaṁ mayhaṁ, ", "eng": "Omniscience is precious to me, ", "html": "<span class='verse-line'>{}</span>"}, "cp3:8.4": {"pli": "tasmā nāgaṁ adāsahan’”ti. ", "eng": "which is why I gave away the elephant.’” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp3:9.1": {"pli": "Kururājacariyaṁ tatiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp4": {"cp4:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp4'><header><ul><li class='division'>{}</li>"}, "cp4:0.2": {"pli": "Akittivagga ", "eng": "The Chapter With Akitti ", "html": "<li>{}</li>"}, "cp4:0.3": {"pli": "Dānapāramī 4 ", "eng": "The Perfection of Giving (4th) ", "html": "<li>{}</li></ul>"}, "cp4:0.4": {"pli": "Mahāsudassanacariya ", "eng": "Mahāsudassana’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp4:1.1": {"pli": "“Kusāvatimhi nagare, ", "eng": "“In the city of Kusāvatī, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp4:1.2": {"pli": "yadā āsiṁ mahīpati; ", "eng": "when I was Lord of the Earth, ", "html": "<span class='verse-line'>{}</span>"}, "cp4:1.3": {"pli": "Mahāsudassano nāma, ", "eng": "my name was Sudassana the Great, ", "html": "<span class='verse-line'>{}</span>"}, "cp4:1.4": {"pli": "cakkavattī mahabbalo. ", "eng": "a wheel-turning monarch of great power. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp4:2.1": {"pli": "Tatthāhaṁ divase tikkhattuṁ, ", "eng": "There, thrice a day, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp4:2.2": {"pli": "ghosāpemi tahiṁ tahiṁ; ", "eng": "I declared in one place or another: ", "html": "<span class='verse-line'>{}</span>"}, "cp4:2.3": {"pli": "‘Ko kiṁ icchati pattheti, ", "eng": "‘Who needs what? Who wants what? ", "html": "<span class='verse-line'>{}</span>"}, "cp4:2.4": {"pli": "kassa kiṁ dīyatū dhanaṁ. ", "eng": "To whom ought riches be given? ", "html": "<span class='verse-line'>{}</span></p>"}, "cp4:3.1": {"pli": "Ko chātako ko tasito, ", "eng": "Who needs an umbrella? Who is thirsty? ", "html": "<p><span class='verse-line'>{}</span>"}, "cp4:3.2": {"pli": "ko mālaṁ ko vilepanaṁ; ", "eng": "Who a garland? Who some makeup? ", "html": "<span class='verse-line'>{}</span>"}, "cp4:3.3": {"pli": "Nānārattāni vatthāni, ", "eng": "What person, being naked, ", "html": "<span class='verse-line'>{}</span>"}, "cp4:3.4": {"pli": "ko naggo paridahissati. ", "eng": "and will put on colorful clothes? ", "html": "<span class='verse-line'>{}</span></p>"}, "cp4:4.1": {"pli": "Ko pathe chattamādeti, ", "eng": "Who will take an umbrella on a highway, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp4:4.2": {"pli": "kopāhanā mudū subhā’; ", "eng": "who needs sandals, soft and pretty?’ ", "html": "<span class='verse-line'>{}</span>"}, "cp4:4.3": {"pli": "Iti sāyañca pāto ca, ", "eng": "Thus I had it proclaimed everywhere ", "html": "<span class='verse-line'>{}</span>"}, "cp4:4.4": {"pli": "ghosāpemi tahiṁ tahiṁ. ", "eng": "morning and night. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp4:5.1": {"pli": "Na taṁ dasasu ṭhānesu, ", "eng": "It was not just in ten places, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp4:5.2": {"pli": "napi ṭhānasatesu vā; ", "eng": "or even a hundred, ", "html": "<span class='verse-line'>{}</span>"}, "cp4:5.3": {"pli": "Anekasataṭhānesu, ", "eng": "but in many hundreds of places ", "html": "<span class='verse-line'>{}</span>"}, "cp4:5.4": {"pli": "paṭiyattaṁ yācake dhanaṁ. ", "eng": "riches were prepared for supplicants. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp4:6.1": {"pli": "Divā vā yadi vā rattiṁ, ", "eng": "By day or by night ", "html": "<p><span class='verse-line'>{}</span>"}, "cp4:6.2": {"pli": "yadi eti vanibbako; ", "eng": "if a supplicant comes, ", "html": "<span class='verse-line'>{}</span>"}, "cp4:6.3": {"pli": "Laddhā yadicchakaṁ bhogaṁ, ", "eng": "then having gotten all he wants, ", "html": "<span class='verse-line'>{}</span>"}, "cp4:6.4": {"pli": "pūrahatthova gacchati. ", "eng": "he leaves with full hands. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp4:7.1": {"pli": "Evarūpaṁ mahādānaṁ, ", "eng": "I gave great gifts like this ", "html": "<p><span class='verse-line'>{}</span>"}, "cp4:7.2": {"pli": "Adāsiṁ yāvajīvikaṁ; ", "eng": "for my whole life. ", "html": "<span class='verse-line'>{}</span>"}, "cp4:7.3": {"pli": "Napāhaṁ dessaṁ dhanaṁ dammi, ", "eng": "I did not give because I disliked riches, ", "html": "<span class='verse-line'>{}</span>"}, "cp4:7.4": {"pli": "Napi natthi nicayo mayi. ", "eng": "or because I had a store set aside. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp4:8.1": {"pli": "Yathāpi āturo nāma, ", "eng": "Suppose a person is sick. ", "html": "<p><span class='verse-line'>{}</span>"}, "cp4:8.2": {"pli": "rogato parimuttiyā; ", "eng": "In order to be free of sickness ", "html": "<span class='verse-line'>{}</span>"}, "cp4:8.3": {"pli": "Dhanena vejjaṁ tappetvā, ", "eng": "having compensated the doctor with money, ", "html": "<span class='verse-line'>{}</span>"}, "cp4:8.4": {"pli": "rogato parimuccati. ", "eng": "they become freed from sickness. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp4:9.1": {"pli": "Tathevāhaṁ jānamāno, ", "eng": "Thus I, knowingly, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp4:9.2": {"pli": "paripūretumasesato; ", "eng": "in order to fulfill without remainder, ", "html": "<span class='verse-line'>{}</span>"}, "cp4:9.3": {"pli": "Ūnamanaṁ pūrayituṁ, ", "eng": "to fulfill what is lacking, ", "html": "<span class='verse-line'>{}</span>"}, "cp4:9.4": {"pli": "demi dānaṁ vanibbake; ", "eng": "gave gifts to supplicants ", "html": "<span class='verse-line'>{}</span>"}, "cp4:9.5": {"pli": "Nirālayo apaccāso, ", "eng": "without clinging or reward, ", "html": "<span class='verse-line'>{}</span>"}, "cp4:9.6": {"pli": "sambodhimanupattiyā”ti. ", "eng": "for the attainment of awakening.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp4:10.1": {"pli": "Mahāsudassanacariyaṁ catutthaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp5": {"cp5:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp5'><header><ul><li class='division'>{}</li>"}, "cp5:0.2": {"pli": "Akittivagga ", "eng": "The Chapter With Akitti ", "html": "<li>{}</li>"}, "cp5:0.3": {"pli": "Dānapāramī 5 ", "eng": "The Perfection of Giving (5th) ", "html": "<li>{}</li></ul>"}, "cp5:0.4": {"pli": "Mahāgovindacariya ", "eng": "Mahāgovinda’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp5:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp5:1.2": {"pli": "sattarājapurohito; ", "eng": "high priest for seven kings, ", "html": "<span class='verse-line'>{}</span>"}, "cp5:1.3": {"pli": "Pūjito naradevehi, ", "eng": "I was venerated by the gods among men ", "html": "<span class='verse-line'>{}</span>"}, "cp5:1.4": {"pli": "mahāgovindabrāhmaṇo. ", "eng": "as the brahmin Mahāgovinda. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp5:2.1": {"pli": "Tadāhaṁ sattarajjesu, ", "eng": "Then in all seven kingdoms ", "html": "<p><span class='verse-line'>{}</span>"}, "cp5:2.2": {"pli": "Yaṁ me āsi upāyanaṁ; ", "eng": "what I receieved as tribute ", "html": "<span class='verse-line'>{}</span>"}, "cp5:2.3": {"pli": "Tena demi mahādānaṁ, ", "eng": "I used to give a great gift ", "html": "<span class='verse-line'>{}</span>"}, "cp5:2.4": {"pli": "Akkhobbhaṁ sāgarūpamaṁ. ", "eng": "unwavering as the ocean. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp5:3.1": {"pli": "Na me dessaṁ dhanaṁ dhaññaṁ, ", "eng": "It was not because I disliked riches or grain, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp5:3.2": {"pli": "Napi natthi nicayo mayi; ", "eng": "or because I had a store set aside, ", "html": "<span class='verse-line'>{}</span>"}, "cp5:3.3": {"pli": "Sabbaññutaṁ piyaṁ mayhaṁ, ", "eng": "but because omniscience is precious to me, ", "html": "<span class='verse-line'>{}</span>"}, "cp5:3.4": {"pli": "Tasmā demi varaṁ dhanan”ti. ", "eng": "that is why I gave such fine riches.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp5:4.1": {"pli": "Mahāgovindacariyaṁ pañcamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp6": {"cp6:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp6'><header><ul><li class='division'>{}</li>"}, "cp6:0.2": {"pli": "Akittivagga ", "eng": "The Chapter With Akitti ", "html": "<li>{}</li>"}, "cp6:0.3": {"pli": "Dānapāramī 6 ", "eng": "The Perfection of Giving (6th) ", "html": "<li>{}</li></ul>"}, "cp6:0.4": {"pli": "Nimirājacariya ", "eng": "King Nimi’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp6:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp6:1.2": {"pli": "mithilāyaṁ puruttame; ", "eng": "in the capital city of Mithilā ", "html": "<span class='verse-line'>{}</span>"}, "cp6:1.3": {"pli": "Nimi nāma mahārājā, ", "eng": "I was the great king named Nimi, ", "html": "<span class='verse-line'>{}</span>"}, "cp6:1.4": {"pli": "paṇḍito kusalatthiko. ", "eng": "astute and seeking the good. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp6:2.1": {"pli": "Tadāhaṁ māpayitvāna, ", "eng": "Then having had constructed ", "html": "<p><span class='verse-line'>{}</span>"}, "cp6:2.2": {"pli": "catussālaṁ catummukhaṁ; ", "eng": "four halls with four openings, ", "html": "<span class='verse-line'>{}</span>"}, "cp6:2.3": {"pli": "Tattha dānaṁ pavattesiṁ, ", "eng": "I provided gifts there ", "html": "<span class='verse-line'>{}</span>"}, "cp6:2.4": {"pli": "migapakkhinarādinaṁ. ", "eng": "for beasts, birds, men, and so forth. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp6:3.1": {"pli": "Acchādanañca sayanaṁ, ", "eng": "Clothing and beds, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp6:3.2": {"pli": "annaṁ pānañca bhojanaṁ; ", "eng": "food and drink to enjoy: ", "html": "<span class='verse-line'>{}</span>"}, "cp6:3.3": {"pli": "Abbocchinnaṁ karitvāna, ", "eng": "I provided them without let, ", "html": "<span class='verse-line'>{}</span>"}, "cp6:3.4": {"pli": "mahādānaṁ pavattayiṁ. ", "eng": "performing a great offering. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp6:4.1": {"pli": "Yathāpi sevako sāmiṁ, ", "eng": "Suppose a servant is looking ", "html": "<p><span class='verse-line'>{}</span>"}, "cp6:4.2": {"pli": "dhanahetumupāgato; ", "eng": "to get a raise from their boss, ", "html": "<span class='verse-line'>{}</span>"}, "cp6:4.3": {"pli": "Kāyena vācā manasā, ", "eng": "they’d seek to fulfill their needs ", "html": "<span class='verse-line'>{}</span>"}, "cp6:4.4": {"pli": "ārādhanīyamesati. ", "eng": "by body, speech, and mind. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp6:5.1": {"pli": "Tathevāhaṁ sabbabhave, ", "eng": "In that way in every life ", "html": "<p><span class='verse-line'>{}</span>"}, "cp6:5.2": {"pli": "pariyesissāmi bodhijaṁ; ", "eng": "I shall seek the wisdom born of awakening. ", "html": "<span class='verse-line'>{}</span>"}, "cp6:5.3": {"pli": "Dānena satte tappetvā, ", "eng": "Having pleased beings with gifts, ", "html": "<span class='verse-line'>{}</span>"}, "cp6:5.4": {"pli": "icchāmi bodhimuttaman”ti. ", "eng": "I wish for the supreme awakening.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp6:6.1": {"pli": "Nimirājacariyaṁ chaṭṭhaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp7": {"cp7:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp7'><header><ul><li class='division'>{}</li>"}, "cp7:0.2": {"pli": "Akittivagga ", "eng": "The Chapter With Akitti ", "html": "<li>{}</li>"}, "cp7:0.3": {"pli": "Dānapāramī 7 ", "eng": "The Perfection of Giving (7th) ", "html": "<li>{}</li></ul>"}, "cp7:0.4": {"pli": "Candakumāracariya ", "eng": "Prince Candana’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp7:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp7:1.2": {"pli": "ekarājassa atrajo; ", "eng": "the true-born son of the One King ", "html": "<span class='verse-line'>{}</span>"}, "cp7:1.3": {"pli": "Nagare pupphavatiyā, ", "eng": "in the city of flowers, Pupphavatī, ", "html": "<span class='verse-line'>{}</span>"}, "cp7:1.4": {"pli": "kumāro candasavhayo. ", "eng": "I was called Prince Canda, the Moon. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp7:2.1": {"pli": "Tadāhaṁ yajanā mutto, ", "eng": "Then, released from being sacrificed, ", "html": "<p><span class='verse-line'>{}</span>", "comm": "An example of Frazer’s sacrifice of the king and release therefrom. "}, "cp7:2.2": {"pli": "nikkhanto yaññavāṭato; ", "eng": "I fled the sacrificial enclosure. ", "html": "<span class='verse-line'>{}</span>"}, "cp7:2.3": {"pli": "Saṁvegaṁ janayitvāna, ", "eng": "Giving rise to a sense of urgency, ", "html": "<span class='verse-line'>{}</span>"}, "cp7:2.4": {"pli": "mahādānaṁ pavattayiṁ. ", "eng": "I performed a great offering. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp7:3.1": {"pli": "Nāhaṁ pivāmi khādāmi, ", "eng": "I did not drink, I did not eat, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp7:3.2": {"pli": "napi bhuñjāmi bhojanaṁ; ", "eng": "I did not enjoy any food ", "html": "<span class='verse-line'>{}</span>"}, "cp7:3.3": {"pli": "Dakkhiṇeyye adatvāna, ", "eng": "for five or six days ", "html": "<span class='verse-line'>{}</span>"}, "cp7:3.4": {"pli": "api chappañcarattiyo. ", "eng": "with having first offering to a worthy recipient. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp7:4.1": {"pli": "Yathāpi vāṇijo nāma, ", "eng": "Suppose there was a merchant ", "html": "<p><span class='verse-line'>{}</span>"}, "cp7:4.2": {"pli": "Katvāna bhaṇḍasañcayaṁ; ", "eng": "who had made a store of goods. ", "html": "<span class='verse-line'>{}</span>"}, "cp7:4.3": {"pli": "Yattha lābho mahā hoti, ", "eng": "They would take those goods ", "html": "<span class='verse-line'>{}</span>"}, "cp7:4.4": {"pli": "Tattha taṁ harati bhaṇḍakaṁ. ", "eng": "to where they could make a large profit. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp7:5.1": {"pli": "Tatheva sakabhuttāpi, ", "eng": "So too, even giving what one has used oneself ", "html": "<p><span class='verse-line'>{}</span>"}, "cp7:5.2": {"pli": "pare dinnaṁ mahapphalaṁ; ", "eng": "to others is very fruitful. ", "html": "<span class='verse-line'>{}</span>"}, "cp7:5.3": {"pli": "Tasmā parassa dātabbaṁ, ", "eng": "That is why what is to be given to others ", "html": "<span class='verse-line'>{}</span>"}, "cp7:5.4": {"pli": "satabhāgo bhavissati. ", "eng": "multiplies a hundredfold. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp7:6.1": {"pli": "Etamatthavasaṁ ñatvā, ", "eng": "Knowing the reason for this, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp7:6.2": {"pli": "demi dānaṁ bhavābhave; ", "eng": "I gave gifts in life after life. ", "html": "<span class='verse-line'>{}</span>"}, "cp7:6.3": {"pli": "Na paṭikkamāmi dānato, ", "eng": "I never relented in giving ", "html": "<span class='verse-line'>{}</span>"}, "cp7:6.4": {"pli": "sambodhimanupattiyā”ti. ", "eng": "for the attainment of awakening.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp7:7.1": {"pli": "Candakumāracariyaṁ sattamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp8": {"cp8:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp8'><header><ul><li class='division'>{}</li>"}, "cp8:0.2": {"pli": "Akittivagga ", "eng": "The Chapter With Akitti ", "html": "<li>{}</li>"}, "cp8:0.3": {"pli": "Dānapāramī 8 ", "eng": "The Perfection of Giving (8th) ", "html": "<li>{}</li></ul>"}, "cp8:0.4": {"pli": "Sivirājacariya ", "eng": "King Sivi’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp8:1.1": {"pli": "“Ariṭṭhasavhaye nagare, ", "eng": "“In the city called Ariṭṭha ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp8:1.2": {"pli": "sivināmāsi khattiyo; ", "eng": "I was the aristocrat named Sivi. ", "html": "<span class='verse-line'>{}</span>"}, "cp8:1.3": {"pli": "Nisajja pāsādavare, ", "eng": "Seated in my finest longhouse, ", "html": "<span class='verse-line'>{}</span>"}, "cp8:1.4": {"pli": "evaṁ cintesahaṁ tadā. ", "eng": "I then had the following thought: ", "html": "<span class='verse-line'>{}</span></p>"}, "cp8:2.1": {"pli": "‘Yaṁ kiñci mānusaṁ dānaṁ, ", "eng": "‘Whatever human gifts there are, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp8:2.2": {"pli": "adinnaṁ me na vijjati; ", "eng": "none may be found that I have not given. ", "html": "<span class='verse-line'>{}</span>"}, "cp8:2.3": {"pli": "Yopi yāceyya maṁ cakkhuṁ, ", "eng": "Even if someone were to ask for my eye, ", "html": "<span class='verse-line'>{}</span>"}, "cp8:2.4": {"pli": "dadeyyaṁ avikampito’. ", "eng": "I would give it without wavering.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp8:3.1": {"pli": "Mama saṅkappamaññāya, ", "eng": "Knowing my thoughts, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp8:3.2": {"pli": "sakko devānamissaro; ", "eng": "Sakka the lord of gods, ", "html": "<span class='verse-line'>{}</span>"}, "cp8:3.3": {"pli": "Nisinno devaparisāya, ", "eng": "sitting in the assembly of gods ", "html": "<span class='verse-line'>{}</span>"}, "cp8:3.4": {"pli": "idaṁ vacanamabravi. ", "eng": "said the following. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp8:4.1": {"pli": "‘Nisajja pāsādavare, ", "eng": "‘Seated in his finest longhouse, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp8:4.2": {"pli": "sivirājā mahiddhiko; ", "eng": "the mighty King Sivi ", "html": "<span class='verse-line'>{}</span>"}, "cp8:4.3": {"pli": "Cintento vividhaṁ dānaṁ, ", "eng": "is thinking of manifold gifts ", "html": "<span class='verse-line'>{}</span>"}, "cp8:4.4": {"pli": "adeyyaṁ so na passati. ", "eng": "and cannot see any he would not give. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp8:5.1": {"pli": "Tathaṁ nu vitathaṁ netaṁ, ", "eng": "Is this is true or not? ", "html": "<p><span class='verse-line'>{}</span>"}, "cp8:5.2": {"pli": "handa vīmaṁsayāmi taṁ; ", "eng": "Come, let us test him. ", "html": "<span class='verse-line'>{}</span>"}, "cp8:5.3": {"pli": "Muhuttaṁ āgameyyātha, ", "eng": "Wait a while ", "html": "<span class='verse-line'>{}</span>"}, "cp8:5.4": {"pli": "yāva jānāmi taṁ manaṁ’. ", "eng": "until I know his mind.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp8:6.1": {"pli": "Pavedhamāno palitasiro, ", "eng": "Appearing as a frail old man, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp8:6.2": {"pli": "Valigatto jarāturo; ", "eng": "with grey hair and wrinkled skin, ", "html": "<span class='verse-line'>{}</span>"}, "cp8:6.3": {"pli": "Andhavaṇṇova hutvāna, ", "eng": "feeble and blind, ", "html": "<span class='verse-line'>{}</span>"}, "cp8:6.4": {"pli": "Rājānaṁ upasaṅkami. ", "eng": "he approached the king. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp8:7.1": {"pli": "So tadā paggahetvāna, ", "eng": "Then, stretching out ", "html": "<p><span class='verse-line'>{}</span>"}, "cp8:7.2": {"pli": "vāmaṁ dakkhiṇabāhu ca; ", "eng": "his left and right arms, ", "html": "<span class='verse-line'>{}</span>"}, "cp8:7.3": {"pli": "Sirasmiṁ añjaliṁ katvā, ", "eng": "he raised his joined palms to his head ", "html": "<span class='verse-line'>{}</span>"}, "cp8:7.4": {"pli": "idaṁ vacanamabravi. ", "eng": "and said the following. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp8:8.1": {"pli": "‘Yācāmi taṁ mahārāja, ", "eng": "‘Great king, I have a request for you. ", "html": "<p><span class='verse-line'>{}</span>"}, "cp8:8.2": {"pli": "dhammika raṭṭhavaḍḍhana; ", "eng": "Under your just rule the kingdom has flourished, ", "html": "<span class='verse-line'>{}</span>"}, "cp8:8.3": {"pli": "Tava dānaratā kitti, ", "eng": "and the renown of your love of giving ", "html": "<span class='verse-line'>{}</span>"}, "cp8:8.4": {"pli": "uggatā devamānuse. ", "eng": "has risen among gods and humans. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp8:9.1": {"pli": "Ubhopi nettā nayanā, ", "eng": "Both my eyes, by which I find my way, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp8:9.2": {"pli": "andhā upahatā mama; ", "eng": "are ruined and become blind. ", "html": "<span class='verse-line'>{}</span>"}, "cp8:9.3": {"pli": "Ekaṁ me nayanaṁ dehi, ", "eng": "Give me just one eye, ", "html": "<span class='verse-line'>{}</span>"}, "cp8:9.4": {"pli": "tvampi ekena yāpaya’. ", "eng": "while you too get by with one.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp8:10.1": {"pli": "Tassāhaṁ vacanaṁ sutvā, ", "eng": "After hearing his words ", "html": "<p><span class='verse-line'>{}</span>"}, "cp8:10.2": {"pli": "haṭṭho saṁviggamānaso; ", "eng": "I was thrilled, my mind elated, ", "html": "<span class='verse-line'>{}</span>"}, "cp8:10.3": {"pli": "Katañjalī vedajāto, ", "eng": "so with joined palms, inspired, ", "html": "<span class='verse-line'>{}</span>"}, "cp8:10.4": {"pli": "idaṁ vacanamabraviṁ. ", "eng": "I said the following. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp8:11.1": {"pli": "‘Idānāhaṁ cintayitvāna, ", "eng": "‘Just now I was reflecting on this ", "html": "<p><span class='verse-line'>{}</span>"}, "cp8:11.2": {"pli": "pāsādato idhāgato; ", "eng": "when I came here from the longhouse. ", "html": "<span class='verse-line'>{}</span>"}, "cp8:11.3": {"pli": "Tvaṁ mama cittamaññāya, ", "eng": "Understanding my mind, ", "html": "<span class='verse-line'>{}</span>"}, "cp8:11.4": {"pli": "nettaṁ yācitumāgato. ", "eng": "you have come to ask for an eye. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp8:12.1": {"pli": "Aho me mānasaṁ siddhaṁ, ", "eng": "Oh! My wish has come true! ", "html": "<p><span class='verse-line'>{}</span>"}, "cp8:12.2": {"pli": "saṅkappo paripūrito; ", "eng": "My intention is fulfilled! ", "html": "<span class='verse-line'>{}</span>"}, "cp8:12.3": {"pli": "Adinnapubbaṁ dānavaraṁ, ", "eng": "Today I shall give to a supplicant ", "html": "<span class='verse-line'>{}</span>"}, "cp8:12.4": {"pli": "ajja dassāmi yācake. ", "eng": "a fine gift never before given.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp8:13.1": {"pli": "Ehi sivaka uṭṭhehi, ", "eng": "‘Come Sivaka, stand up, ", "html": "<p><span class='verse-line'>{}</span>", "comm": "Commentary says these two verses were spoken by the king’s minister. "}, "cp8:13.2": {"pli": "mā dandhayi mā pavedhayi; ", "eng": "do not hesitate or waver. ", "html": "<span class='verse-line'>{}</span>"}, "cp8:13.3": {"pli": "Ubhopi nayanaṁ dehi, ", "eng": "Pluck out even both eyes ", "html": "<span class='verse-line'>{}</span>"}, "cp8:13.4": {"pli": "uppāṭetvā vaṇibbake’. ", "eng": "and give them to the supplicant.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp8:14.1": {"pli": "Tato so codito mayhaṁ, ", "eng": "And then, urged on by me, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp8:14.2": {"pli": "sivako vacanaṁ karo; ", "eng": "Sivaka did as I said. ", "html": "<span class='verse-line'>{}</span>"}, "cp8:14.3": {"pli": "Uddharitvāna pādāsi, ", "eng": "Extracting both eyes, ", "html": "<span class='verse-line'>{}</span>"}, "cp8:14.4": {"pli": "tālamiñjaṁva yācake. ", "eng": "he presented them to the trembling supplicant. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp8:15.1": {"pli": "Dadamānassa dentassa, ", "eng": "‘While planning to give, while giving, ", "html": "<p><span class='verse-line'>{}</span>", "comm": "Both <i lang='pi' translate='no'>dadamāna</i> and <i lang='pi' translate='no'>denta</i> are present participles, but the commentary explains <i lang='pi' translate='no'>dadamāna</i> as infinitive (<i lang='pi' translate='no'>dātuṁ</i>). "}, "cp8:15.2": {"pli": "dinnadānassa me sato; ", "eng": "and after I had given, ", "html": "<span class='verse-line'>{}</span>"}, "cp8:15.3": {"pli": "Cittassa aññathā natthi, ", "eng": "my mind did not falter, ", "html": "<span class='verse-line'>{}</span>"}, "cp8:15.4": {"pli": "bodhiyāyeva kāraṇā. ", "eng": "because it was solely for awakening. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp8:16.1": {"pli": "Na me dessā ubho cakkhū, ", "eng": "It was not because I disliked my eyes, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp8:16.2": {"pli": "attā na me na dessiyo; ", "eng": "or that I disliked myself, ", "html": "<span class='verse-line'>{}</span>"}, "cp8:16.3": {"pli": "Sabbaññutaṁ piyaṁ mayhaṁ, ", "eng": "but because omniscience is precious to me, ", "html": "<span class='verse-line'>{}</span>"}, "cp8:16.4": {"pli": "tasmā cakkhuṁ adāsahan”ti. ", "eng": "that is why I gave away my eyes.’” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp8:17.1": {"pli": "Sivirājacariyaṁ aṭṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp9": {"cp9:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp9'><header><ul><li class='division'>{}</li>"}, "cp9:0.2": {"pli": "Akittivagga ", "eng": "The Chapter With Akitti ", "html": "<li>{}</li>"}, "cp9:0.3": {"pli": "Dānapāramī 9 ", "eng": "The Perfection of Giving (9th) ", "html": "<li>{}</li></ul>"}, "cp9:0.4": {"pli": "Vessantaracariya ", "eng": "Vessantara’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp9:1.1": {"pli": "“Yā me ahosi janikā, ", "eng": "“She who was my mother, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp9:1.2": {"pli": "phussatī nāma khattiyā; ", "eng": "the aristocrat lady named Phussatī, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:1.3": {"pli": "Sā atītāsu jātīsu, ", "eng": "was Sakka’s beloved queen ", "html": "<span class='verse-line'>{}</span>"}, "cp9:1.4": {"pli": "sakkassa mahesī piyā. ", "eng": "in past lives. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:2.1": {"pli": "Tassā āyukkhayaṁ ñatvā, ", "eng": "Knowing that she was at the end of life, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:2.2": {"pli": "devindo etadabravi; ", "eng": "the lord of gods said this: ", "html": "<span class='verse-line'>{}</span>"}, "cp9:2.3": {"pli": "‘Dadāmi te dasa vare, ", "eng": "‘I grant you ten favors, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:2.4": {"pli": "varabhadde yadicchasi’. ", "eng": "my dear, whatever favor you wish.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:3.1": {"pli": "Evaṁ vuttā ca sā devī, ", "eng": "When he said this the queen ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:3.2": {"pli": "sakkaṁ punidamabravi; ", "eng": "replied to Sakka: ", "html": "<span class='verse-line'>{}</span>"}, "cp9:3.3": {"pli": "‘Kiṁ nu me aparādhatthi, ", "eng": "‘In what way am I deficient? ", "html": "<span class='verse-line'>{}</span>"}, "cp9:3.4": {"pli": "kiṁ nu dessā ahaṁ tava; ", "eng": "How am I displeasing to you? ", "html": "<span class='verse-line'>{}</span>"}, "cp9:3.5": {"pli": "Rammā cāvesi maṁ ṭhānā, ", "eng": "You send me from this lovely place, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:3.6": {"pli": "vātova dharaṇīruhaṁ’. ", "eng": "like the wind a feeble tree.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:4.1": {"pli": "Evaṁ vutto ca so sakko, ", "eng": "When she said this Sakka ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:4.2": {"pli": "puna tassidamabravi; ", "eng": "replied to her: ", "html": "<span class='verse-line'>{}</span>"}, "cp9:4.3": {"pli": "‘Na ceva te kataṁ pāpaṁ, ", "eng": "‘You have done nothing wrong, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:4.4": {"pli": "na ca me tvaṁsi appiyā. ", "eng": "nor are you not beloved to me. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:5.1": {"pli": "Ettakaṁyeva te āyu, ", "eng": "But your life lasts only so long, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:5.2": {"pli": "cavanakālo bhavissati; ", "eng": "your time of death will come. ", "html": "<span class='verse-line'>{}</span>"}, "cp9:5.3": {"pli": "Paṭiggaṇha mayā dinne, ", "eng": "Accept the ten supreme favors ", "html": "<span class='verse-line'>{}</span>"}, "cp9:5.4": {"pli": "vare dasa varuttame’. ", "eng": "I offer to you.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:6.1": {"pli": "Sakkena sā dinnavarā, ", "eng": "She accepted the offerings of Sakka, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:6.2": {"pli": "tuṭṭhahaṭṭhā pamoditā; ", "eng": "full of joy and happiness. ", "html": "<span class='verse-line'>{}</span>"}, "cp9:6.3": {"pli": "Mamaṁ abbhantaraṁ katvā, ", "eng": "Having conceived me, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:6.4": {"pli": "phussatī dasa vare varī. ", "eng": "Phussatī was blessed with the ten favors. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:7.1": {"pli": "Tato cutā sā phussatī, ", "eng": "Then Phussatī, having passed from there, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:7.2": {"pli": "khattiye upapajjatha; ", "eng": "was reborn as an aristocrat ", "html": "<span class='verse-line'>{}</span>"}, "cp9:7.3": {"pli": "Jetuttaramhi nagare, ", "eng": "in the city of Jetuttara, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:7.4": {"pli": "sañjayena samāgami. ", "eng": "where she married Sañjaya. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:8.1": {"pli": "Yadāhaṁ phussatiyā kucchiṁ, ", "eng": "Once I was conceived in the womb ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:8.2": {"pli": "okkanto piyamātuyā; ", "eng": "of my dear mother Phussatī, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:8.3": {"pli": "Mama tejena me mātā, ", "eng": "because of my power, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:8.4": {"pli": "sadā dānaratā ahu. ", "eng": "my mother always delighted in giving. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:9.1": {"pli": "Adhane āture jiṇṇe, ", "eng": "She gave to the destitute, the ailing and aging, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:9.2": {"pli": "yācake addhike jane; ", "eng": "to beggars, travelers, and folk, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:9.3": {"pli": "Samaṇe brāhmaṇe khīṇe, ", "eng": "to ascetics and brahmins, and the dispossessed, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:9.4": {"pli": "deti dānaṁ akiñcane. ", "eng": "and to those who had nothing. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:10.1": {"pli": "Dasa māse dhārayitvāna, ", "eng": "Having carried me for ten months, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:10.2": {"pli": "karonte puraṁ padakkhiṇaṁ; ", "eng": "she was proceeding around the city ", "html": "<span class='verse-line'>{}</span>"}, "cp9:10.3": {"pli": "Vessānaṁ vīthiyā majjhe, ", "eng": "when Phussatī gave birth to me ", "html": "<span class='verse-line'>{}</span>"}, "cp9:10.4": {"pli": "janesi phussatī mamaṁ. ", "eng": "in the middle of the peasant’s street. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:11.1": {"pli": "Na mayhaṁ mattikaṁ nāmaṁ, ", "eng": "My name was not from my mother’s side, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:11.2": {"pli": "napi pettikasambhavaṁ; ", "eng": "nor from my father’s. ", "html": "<span class='verse-line'>{}</span>"}, "cp9:11.3": {"pli": "Jātettha vessavīthiyā, ", "eng": "I was born there in the peasant’s street, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:11.4": {"pli": "tasmā vessantaro ahu. ", "eng": "and that’s why I’m called Vessantara. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:12.1": {"pli": "Yadāhaṁ dārako homi, ", "eng": "When I was a boy ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:12.2": {"pli": "jātiyā aṭṭhavassiko; ", "eng": "of only eight years old, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:12.3": {"pli": "Tadā nisajja pāsāde, ", "eng": "sitting in the stilt longhouse, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:12.4": {"pli": "dānaṁ dātuṁ vicintayiṁ. ", "eng": "I thought of giving gifts. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:13.1": {"pli": "‘Hadayaṁ dadeyyaṁ cakkhuṁ, ", "eng": "‘I would give my heart and my eyes, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:13.2": {"pli": "maṁsampi rudhirampi ca; ", "eng": "my flesh and blood too.’ ", "html": "<span class='verse-line'>{}</span>"}, "cp9:13.3": {"pli": "Dadeyyaṁ kāyaṁ sāvetvā, ", "eng": "I announced that I would give my body, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:13.4": {"pli": "yadi koci yācaye mamaṁ’. ", "eng": "if anyone asked me. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:14.1": {"pli": "Sabhāvaṁ cintayantassa, ", "eng": "Reflecting on my state of mind, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:14.2": {"pli": "akampitamasaṇṭhitaṁ; ", "eng": "so undisturbed and steadfast, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:14.3": {"pli": "Akampi tattha pathavī, ", "eng": "the earth quaked there, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:14.4": {"pli": "sineruvanavaṭaṁsakā. ", "eng": "and Mount Sineru, wreathed in forests. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:15.1": {"pli": "Anvaddhamāse pannarase, ", "eng": "Every fortnight, the fifteenth, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:15.2": {"pli": "puṇṇamāse uposathe; ", "eng": "and the sabbath when the moon was full, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:15.3": {"pli": "Paccayaṁ nāgamāruyha, ", "eng": "I mounted the tusker Paccaya ", "html": "<span class='verse-line'>{}</span>"}, "cp9:15.4": {"pli": "dānaṁ dātuṁ upāgamiṁ. ", "eng": "and went to give gifts. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:16.1": {"pli": "Kaliṅgaraṭṭhavisayā, ", "eng": "From the country of Kaliṅga, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:16.2": {"pli": "brāhmaṇā upagañchu maṁ; ", "eng": "the brahmins came to me, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:16.3": {"pli": "Ayācuṁ maṁ hatthināgaṁ, ", "eng": "requesting the royal elephant, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:16.4": {"pli": "dhaññaṁ maṅgalasammataṁ. ", "eng": "that was lucky and deemed as auspicious. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:17.1": {"pli": "‘Avuṭṭhiko janapado, ", "eng": "‘Our nation is suffering from drought, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:17.2": {"pli": "dubbhikkho chātako mahā; ", "eng": "food is scarce and famine abounds, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:17.3": {"pli": "Dadāhi pavaraṁ nāgaṁ, ", "eng": "please give the noble elephant ", "html": "<span class='verse-line'>{}</span>"}, "cp9:17.4": {"pli": "sabbasetaṁ gajuttamaṁ’. ", "eng": "the all-white supreme elephant.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:18.1": {"pli": "Dadāmi na vikampāmi, ", "eng": "‘I give without hesitation ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:18.2": {"pli": "yaṁ maṁ yācanti brāhmaṇā; ", "eng": "what you ask of me, brahmins. ", "html": "<span class='verse-line'>{}</span>"}, "cp9:18.3": {"pli": "Santaṁ nappatigūhāmi, ", "eng": "I do not hide what I have, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:18.4": {"pli": "dāne me ramate mano. ", "eng": "for my heart is happy to give. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:19.1": {"pli": "Na me yācakamanuppatte, ", "eng": "When supplicants come to me ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:19.2": {"pli": "paṭikkhepo anucchavo; ", "eng": "it is unbefitting to refuse. ", "html": "<span class='verse-line'>{}</span>"}, "cp9:19.3": {"pli": "Mā me bhijji samādānaṁ, ", "eng": "Let not my vow be broken, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:19.4": {"pli": "dassāmi vipulaṁ gajaṁ. ", "eng": "I shall give the mighty elephant.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:20.1": {"pli": "Nāgaṁ gahetvā soṇḍāya, ", "eng": "Taking the elephant by the trunk, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:20.2": {"pli": "bhiṅgāre ratanāmaye; ", "eng": "and a bejeweled ceremonial vessel, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:20.3": {"pli": "Jalaṁ hatthe ākiritvā, ", "eng": "I sprinkled water on the hand, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:20.4": {"pli": "brāhmaṇānaṁ adaṁ gajaṁ. ", "eng": "and gave the elephant to the brahmins. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:21.1": {"pli": "Punāparaṁ dadantassa, ", "eng": "Then again when I was giving ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:21.2": {"pli": "sabbasetaṁ gajuttamaṁ; ", "eng": "the all-white supreme elephant, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:21.3": {"pli": "Tadāpi pathavī kampi, ", "eng": "the earth quaked again, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:21.4": {"pli": "sineruvanavaṭaṁsakā. ", "eng": "and Mount Sineru, wreathed in forests. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:22.1": {"pli": "Tassa nāgassa dānena, ", "eng": "At the gift of the tusker, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:22.2": {"pli": "sivayo kuddhā samāgatā; ", "eng": "angry townsfolk gathered ", "html": "<span class='verse-line'>{}</span>"}, "cp9:22.3": {"pli": "Pabbājesuṁ sakā raṭṭhā, ", "eng": "and banished me from my kingdom, saying: ", "html": "<span class='verse-line'>{}</span>"}, "cp9:22.4": {"pli": "‘vaṅkaṁ gacchatu pabbataṁ’. ", "eng": "‘Go to Crooked Mountain.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:23.1": {"pli": "Tesaṁ nicchubhamānānaṁ, ", "eng": "While they were ejecting me, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:23.2": {"pli": "akampitamasaṇṭhitaṁ; ", "eng": "I was undisturbed and steadfast, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:23.3": {"pli": "Mahādānaṁ pavattetuṁ, ", "eng": "I asked one favor: ", "html": "<span class='verse-line'>{}</span>"}, "cp9:23.4": {"pli": "ekaṁ varamayācisaṁ. ", "eng": "to perform a great offering. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:24.1": {"pli": "Yācitā sivayo sabbe, ", "eng": "When I asked, all the townsfolk ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:24.2": {"pli": "ekaṁ varamadaṁsu me; ", "eng": "granted me that one favor. ", "html": "<span class='verse-line'>{}</span>"}, "cp9:24.3": {"pli": "Sāvayitvā kaṇṇabheriṁ, ", "eng": "Sounding the drums, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:24.4": {"pli": "mahādānaṁ dadāmahaṁ. ", "eng": "I gave a great offering. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:25.1": {"pli": "Athettha vattatī saddo, ", "eng": "And as the sound went forth, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:25.2": {"pli": "tumulo bheravo mahā; ", "eng": "there was a great tumult and upset. ", "html": "<span class='verse-line'>{}</span>"}, "cp9:25.3": {"pli": "Dānenimaṁ nīharanti, ", "eng": "They drove me out because of giving, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:25.4": {"pli": "puna dānaṁ dadātayaṁ. ", "eng": "but I just gave again. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:26.1": {"pli": "Hatthiṁ asse rathe datvā, ", "eng": "Elephants, horses, chariots, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:26.2": {"pli": "dāsiṁ dāsaṁ gavaṁ dhanaṁ; ", "eng": "male and female slaves, cattle, and riches: ", "html": "<span class='verse-line'>{}</span>"}, "cp9:26.3": {"pli": "Mahādānaṁ daditvāna, ", "eng": "after giving this great gift ", "html": "<span class='verse-line'>{}</span>"}, "cp9:26.4": {"pli": "nagarā nikkhamiṁ tadā. ", "eng": "I then left the city. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:27.1": {"pli": "Nikkhamitvāna nagarā, ", "eng": "After leaving the city, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:27.2": {"pli": "nivattitvā vilokite; ", "eng": "I turned back to look, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:27.3": {"pli": "Tadāpi pathavī kampi, ", "eng": "at which the earth quaked, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:27.4": {"pli": "sineruvanavaṭaṁsakā. ", "eng": "and Mount Sineru, wreathed in forests. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:28.1": {"pli": "Catuvāhiṁ rathaṁ datvā, ", "eng": "Having given away my four-horse chariot, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:28.2": {"pli": "ṭhatvā cātummahāpathe; ", "eng": "I was standing at the crossroads ", "html": "<span class='verse-line'>{}</span>"}, "cp9:28.3": {"pli": "Ekākiyo adutiyo, ", "eng": "alone with no partner, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:28.4": {"pli": "maddideviṁ idamabraviṁ. ", "eng": "and I said to Queen Maddī: ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:29.1": {"pli": "‘Tvaṁ maddi kaṇhaṁ gaṇhāhi, ", "eng": "‘Maddī, you take Kaṇhā, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:29.2": {"pli": "lahukā esā kaniṭṭhikā; ", "eng": "for she, as the younger sister, is the lighter. ", "html": "<span class='verse-line'>{}</span>"}, "cp9:29.3": {"pli": "Ahaṁ jāliṁ gahessāmi, ", "eng": "I shall take Jāli, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:29.4": {"pli": "garuko bhātiko hi so’. ", "eng": "for he, as the brother, is heavier.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:30.1": {"pli": "Padumaṁ puṇḍarīkaṁva, ", "eng": "Like a pink or white lotus, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:30.2": {"pli": "maddī kaṇhājinaggahī; ", "eng": "Maddī picked up Kaṇhājinā. ", "html": "<span class='verse-line'>{}</span>"}, "cp9:30.3": {"pli": "Ahaṁ suvaṇṇabimbaṁva, ", "eng": "Like a golden statue, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:30.4": {"pli": "jāliṁ khattiyamaggahiṁ. ", "eng": "I picked up Jāli, the aristocrat. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:31.1": {"pli": "Abhijātā sukhumālā, ", "eng": "High-born and delicate, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:31.2": {"pli": "khattiyā caturo janā; ", "eng": "four aristocrat folk ", "html": "<span class='verse-line'>{}</span>"}, "cp9:31.3": {"pli": "Visamaṁ samaṁ akkamantā, ", "eng": "traveled ground even and uneven, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:31.4": {"pli": "vaṅkaṁ gacchāma pabbataṁ. ", "eng": "going to Crooked Mountain. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:32.1": {"pli": "Ye keci manujā enti, ", "eng": "Whenever men would come along, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:32.2": {"pli": "anumagge paṭippathe; ", "eng": "coming the other way along the road, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:32.3": {"pli": "Maggante paṭipucchāma, ", "eng": "we asked them in the middle of the street, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:32.4": {"pli": "‘kuhiṁ vaṅkantapabbato’. ", "eng": "‘Where is Crooked Mountain?’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:33.1": {"pli": "Te tattha amhe passitvā, ", "eng": "Seeing us there, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:33.2": {"pli": "karuṇaṁ giramudīrayuṁ; ", "eng": "they spoke to us kindly, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:33.3": {"pli": "Dukkhante paṭivedenti, ", "eng": "‘It will be hard for you,’ they explained, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:33.4": {"pli": "dūre vaṅkantapabbato. ", "eng": "‘for Crooked Mountain is far.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:34.1": {"pli": "Yadi passanti pavane, ", "eng": "If the children saw fruit ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:34.2": {"pli": "dārakā phaline dume; ", "eng": "on a tree in the forest, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:34.3": {"pli": "Tesaṁ phalānaṁ hetumhi, ", "eng": "then for the sake of that fruit, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:34.4": {"pli": "uparodanti dārakā. ", "eng": "the children cried. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:35.1": {"pli": "Rodante dārake disvā, ", "eng": "Seeing the children cry, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:35.2": {"pli": "ubbiddhā vipulā dumā; ", "eng": "the tall, massive trees ", "html": "<span class='verse-line'>{}</span>"}, "cp9:35.3": {"pli": "Sayamevoṇamitvāna, ", "eng": "bent down of their own accord, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:35.4": {"pli": "upagacchanti dārake. ", "eng": "within reach of the children. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:36.1": {"pli": "Idaṁ acchariyaṁ disvā, ", "eng": "Seeing this marvel, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:36.2": {"pli": "abbhutaṁ lomahaṁsanaṁ; ", "eng": "so astonishing and hair-raising, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:36.3": {"pli": "Sāhukāraṁ pavattesi, ", "eng": "Maddī, beautiful in every limb, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:36.4": {"pli": "maddī sabbaṅgasobhanā. ", "eng": "exclaimed in celebration: ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:37.1": {"pli": "Accheraṁ vata lokasmiṁ, ", "eng": "‘Such a marvel in the world, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:37.2": {"pli": "abbhutaṁ lomahaṁsanaṁ; ", "eng": "so astonishing and hair-raising, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:37.3": {"pli": "Vessantarassa tejena, ", "eng": "due to the power of Vessantara, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:37.4": {"pli": "sayamevoṇatā dumā. ", "eng": "the trees bent down of their own accord.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:38.1": {"pli": "Saṅkhipiṁsu pathaṁ yakkhā, ", "eng": "Native spirits, out of compassion, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:38.2": {"pli": "anukampāya dārake; ", "eng": "shortened the path, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:38.3": {"pli": "Nikkhantadivaseneva, ", "eng": "so that on the very day they set out, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:38.4": {"pli": "cetaraṭṭhamupāgamuṁ. ", "eng": "they arrived at the kingdom of Ceta. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:39.1": {"pli": "Saṭṭhirājasahassāni, ", "eng": "Sixty thousand kings ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:39.2": {"pli": "tadā vasanti mātule; ", "eng": "were living at Mātula then. ", "html": "<span class='verse-line'>{}</span>", "comm": "<i lang='pi' translate='no'>Mātule</i>; at <a href='https://suttacentral.net/dn26/en/sujato#1.2'>DN 26:1.2</a> this is feminine, <i lang='pi' translate='no'>mātulāyaṁ</i>. "}, "cp9:39.3": {"pli": "Sabbe pañjalikā hutvā, ", "eng": "All raised their joined palms, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:39.4": {"pli": "rodamānā upāgamuṁ. ", "eng": "and approached, weeping. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:40.1": {"pli": "Tattha vattetvā sallāpaṁ, ", "eng": "There they held conversation ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:40.2": {"pli": "cetehi cetaputtehi; ", "eng": "with the folks of Ceta and their children. ", "html": "<span class='verse-line'>{}</span>"}, "cp9:40.3": {"pli": "Te tato nikkhamitvāna, ", "eng": "Then, leaving that place, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:40.4": {"pli": "vaṅkaṁ agamu pabbataṁ. ", "eng": "they arrived at Crooked Mountain. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:41.1": {"pli": "Āmantayitvā devindo, ", "eng": "The lord of gods addressed ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:41.2": {"pli": "vissakammaṁ mahiddhikaṁ; ", "eng": "Vissakamma of great psychic power: ", "html": "<span class='verse-line'>{}</span>"}, "cp9:41.3": {"pli": "Assamaṁ sukataṁ rammaṁ, ", "eng": "‘Build a hermitage, well-made and delightful, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:41.4": {"pli": "paṇṇasālaṁ sumāpaya. ", "eng": "with a hall of leaves.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:42.1": {"pli": "Sakkassa vacanaṁ sutvā, ", "eng": "After hearing Sakka’s words, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:42.2": {"pli": "vissakammo mahiddhiko; ", "eng": "Vissakamma of great psychic power ", "html": "<span class='verse-line'>{}</span>"}, "cp9:42.3": {"pli": "Assamaṁ sukataṁ rammaṁ, ", "eng": "built a hermitage, well-made and delightful, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:42.4": {"pli": "paṇṇasālaṁ sumāpayi. ", "eng": "with a hall of leaves. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:43.1": {"pli": "Ajjhogāhetvā pavanaṁ, ", "eng": "Having plunged into the forest, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:43.2": {"pli": "appasaddaṁ nirākulaṁ; ", "eng": "quiet and undisturbed, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:43.3": {"pli": "Caturo janā mayaṁ tattha, ", "eng": "we four individuals ", "html": "<span class='verse-line'>{}</span>"}, "cp9:43.4": {"pli": "vasāma pabbatantare. ", "eng": "lived there in the mountains. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:44.1": {"pli": "Ahañca maddidevī ca, ", "eng": "Myself and Queen Maddī, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:44.2": {"pli": "jālī kaṇhājinā cubho; ", "eng": "with both Jāli and Kaṇhājinā, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:44.3": {"pli": "Aññamaññaṁ sokanudā, ", "eng": "dispelling each other’s sorrow ", "html": "<span class='verse-line'>{}</span>"}, "cp9:44.4": {"pli": "vasāma assame tadā. ", "eng": "lived then in that hermitage. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:45.1": {"pli": "Dārake anurakkhanto, ", "eng": "While caring for the children, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:45.2": {"pli": "asuñño homi assame; ", "eng": "I was not idle in the hermitage. ", "html": "<span class='verse-line'>{}</span>"}, "cp9:45.3": {"pli": "Maddī phalaṁ āharitvā, ", "eng": "Maddī gathered fruit, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:45.4": {"pli": "poseti sā tayo jane. ", "eng": "and provided for three individuals. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:46.1": {"pli": "Pavane vasamānassa, ", "eng": "While I was living in the forest, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:46.2": {"pli": "addhiko maṁ upāgami; ", "eng": "a traveler came to me ", "html": "<span class='verse-line'>{}</span>"}, "cp9:46.3": {"pli": "Āyāci puttake mayhaṁ, ", "eng": "and asked for my children, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:46.4": {"pli": "jāliṁ kaṇhājinaṁ cubho. ", "eng": "both Jāli and Kaṇhājinā. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:47.1": {"pli": "Yācakaṁ upagataṁ disvā, ", "eng": "When I saw the supplicant approaching, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:47.2": {"pli": "hāso me upapajjatha; ", "eng": "I felt so full of joy. ", "html": "<span class='verse-line'>{}</span>"}, "cp9:47.3": {"pli": "Ubho putte gahetvāna, ", "eng": "Taking both my children, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:47.4": {"pli": "adāsiṁ brāhmaṇe tadā. ", "eng": "I gave them to the brahmin then. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:48.1": {"pli": "Sake putte cajantassa, ", "eng": "When I gave away my own children, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:48.2": {"pli": "jūjake brāhmaṇe yadā; ", "eng": "to the brahmin Jūjaka, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:48.3": {"pli": "Tadāpi pathavī kampi, ", "eng": "the earth quaked again, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:48.4": {"pli": "sineruvanavaṭaṁsakā. ", "eng": "and Mount Sineru, wreathed in forests. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:49.1": {"pli": "Punadeva sakko oruyha, ", "eng": "And then Sakka descended ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:49.2": {"pli": "hutvā brāhmaṇasannibho; ", "eng": "in the guise of a brahmin. ", "html": "<span class='verse-line'>{}</span>"}, "cp9:49.3": {"pli": "Āyāci maṁ maddideviṁ, ", "eng": "He asked me for Queen Maddī, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:49.4": {"pli": "sīlavantiṁ patibbataṁ. ", "eng": "virtuous, and devoted. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:50.1": {"pli": "Maddiṁ hatthe gahetvāna, ", "eng": "Taking Maddī by the hand, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:50.2": {"pli": "udakañjali pūriya; ", "eng": "filling clasped hands with water, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:50.3": {"pli": "Pasannamanasaṅkappo, ", "eng": "with a confident heart, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:50.4": {"pli": "tassa maddiṁ adāsahaṁ. ", "eng": "I gave Maddī to him. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:51.1": {"pli": "Maddiyā dīyamānāya, ", "eng": "As Maddī was being given, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:51.2": {"pli": "gagane devā pamoditā; ", "eng": "gods applauded in the welkin, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:51.3": {"pli": "Tadāpi pathavī kampi, ", "eng": "and the earth quaked again, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:51.4": {"pli": "sineruvanavaṭaṁsakā. ", "eng": "and Mount Sineru, wreathed in forests. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:52.1": {"pli": "Jāliṁ kaṇhājinaṁ dhītaṁ, ", "eng": "Jāli and Kaṇhājinā my daughter, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:52.2": {"pli": "maddideviṁ patibbataṁ; ", "eng": "and Queen Maddī my devoted wife: ", "html": "<span class='verse-line'>{}</span>"}, "cp9:52.3": {"pli": "Cajamāno na cintesiṁ, ", "eng": "I gave them up without a second thought, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:52.4": {"pli": "bodhiyāyeva kāraṇā. ", "eng": "because it was solely for awakening. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:53.1": {"pli": "Na me dessā ubho puttā, ", "eng": "I had no dislike of my children, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:53.2": {"pli": "maddidevī na dessiyā; ", "eng": "nor for Queen Maddī. ", "html": "<span class='verse-line'>{}</span>"}, "cp9:53.3": {"pli": "Sabbaññutaṁ piyaṁ mayhaṁ, ", "eng": "But because omniscience is precious to me, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:53.4": {"pli": "tasmā piye adāsahaṁ. ", "eng": "I gave my loved ones away. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:54.1": {"pli": "Punāparaṁ brahāraññe, ", "eng": "Later on in the mighty forest, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:54.2": {"pli": "mātāpitusamāgame; ", "eng": "we were reunited by my mother and father. ", "html": "<span class='verse-line'>{}</span>"}, "cp9:54.3": {"pli": "Karuṇaṁ paridevante, ", "eng": "Crying pitifully, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:54.4": {"pli": "sallapante sukhaṁ dukhaṁ. ", "eng": "they spoke of my happiness and sadness. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:55.1": {"pli": "Hirottappena garunā, ", "eng": "With deep conscience and prudence, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:55.2": {"pli": "ubhinnaṁ upasaṅkami; ", "eng": "I approached them, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:55.3": {"pli": "Tadāpi pathavī kampi, ", "eng": "at which the earth quaked, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:55.4": {"pli": "sineruvanavaṭaṁsakā. ", "eng": "and Mount Sineru, wreathed in forests. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:56.1": {"pli": "Punāparaṁ brahāraññā, ", "eng": "Later on in the mighty forest, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:56.2": {"pli": "nikkhamitvā sañātibhi; ", "eng": "having left the forest with my family, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:56.3": {"pli": "Pavisāmi puraṁ rammaṁ, ", "eng": "I entered the delightful city ", "html": "<span class='verse-line'>{}</span>"}, "cp9:56.4": {"pli": "jetuttaraṁ puruttamaṁ. ", "eng": "Jetuttara, the capital city. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:57.1": {"pli": "Ratanāni satta vassiṁsu, ", "eng": "The seven gems rained down, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:57.2": {"pli": "mahāmegho pavassatha; ", "eng": "raining forth from a mighty cloud, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:57.3": {"pli": "Tadāpi pathavī kampi, ", "eng": "and the earth quaked again, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:57.4": {"pli": "sineruvanavaṭaṁsakā. ", "eng": "and Mount Sineru, wreathed in forests. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp9:58.1": {"pli": "Acetanāyaṁ pathavī, ", "eng": "Even this insentient earth, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp9:58.2": {"pli": "aviññāya sukhaṁ dukhaṁ; ", "eng": "with no knowledge of happiness or sadness, ", "html": "<span class='verse-line'>{}</span>"}, "cp9:58.3": {"pli": "Sāpi dānabalā mayhaṁ, ", "eng": "quaked seven times ", "html": "<span class='verse-line'>{}</span>"}, "cp9:58.4": {"pli": "sattakkhattuṁ pakampathā”ti. ", "eng": "at the power of my giving.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp9:59.1": {"pli": "Vessantaracariyaṁ navamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp10": {"cp10:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp10'><header><ul><li class='division'>{}</li>"}, "cp10:0.2": {"pli": "Akittivagga ", "eng": "The Chapter With Akitti ", "html": "<li>{}</li>"}, "cp10:0.3": {"pli": "Dānapāramī 10 ", "eng": "The Perfection of Giving (10th) ", "html": "<li>{}</li></ul>"}, "cp10:0.4": {"pli": "Sasapaṇḍitacariya ", "eng": "The Wise Hare’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp10:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp10:1.2": {"pli": "Sasako pavanacārako; ", "eng": "a hare who roamed the forest, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:1.3": {"pli": "Tiṇapaṇṇasākaphalabhakkho, ", "eng": "eating grass, leaves, vegetables, and fruit, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:1.4": {"pli": "Paraheṭhanavivajjito. ", "eng": "refraining from harming others, ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:2.1": {"pli": "Makkaṭo ca siṅgālo ca, ", "eng": "a monkey, a jackal, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:2.2": {"pli": "suttapoto cahaṁ tadā; ", "eng": "a young otter and I ", "html": "<span class='verse-line'>{}</span>"}, "cp10:2.3": {"pli": "Vasāma ekasāmantā, ", "eng": "lived in the same neighborhood ", "html": "<span class='verse-line'>{}</span>"}, "cp10:2.4": {"pli": "sāyaṁ pāto ca dissare. ", "eng": "and were seen together morning and evening. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:3.1": {"pli": "Ahaṁ te anusāsāmi, ", "eng": "I advised them ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:3.2": {"pli": "kiriye kalyāṇapāpake; ", "eng": "regarding good and bad deeds: ", "html": "<span class='verse-line'>{}</span>"}, "cp10:3.3": {"pli": "‘Pāpāni parivajjetha, ", "eng": "‘Shun bad deeds, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:3.4": {"pli": "kalyāṇe abhinivissatha’. ", "eng": "and cling to the good.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:4.1": {"pli": "Uposathamhi divase, ", "eng": "Seeing the full moon ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:4.2": {"pli": "candaṁ disvāna pūritaṁ; ", "eng": "on the sabbath day, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:4.3": {"pli": "Etesaṁ tattha ācikkhiṁ, ", "eng": "I pointed it out to them, saying, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:4.4": {"pli": "‘divaso ajjuposatho. ", "eng": "‘Today is the sabbath. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:5.1": {"pli": "Dānāni paṭiyādetha, ", "eng": "Prepare gifts ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:5.2": {"pli": "dakkhiṇeyyassa dātave; ", "eng": "for one worthy of offerings. ", "html": "<span class='verse-line'>{}</span>"}, "cp10:5.3": {"pli": "Datvā dānaṁ dakkhiṇeyye, ", "eng": "Having given to one worthy of offerings, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:5.4": {"pli": "upavassathuposathaṁ’. ", "eng": "observe the sabbath day.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:6.1": {"pli": "Te me sādhūti vatvāna, ", "eng": "Saying ‘good!’ to me, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:6.2": {"pli": "yathāsatti yathābalaṁ; ", "eng": "they prepared gifts ", "html": "<span class='verse-line'>{}</span>"}, "cp10:6.3": {"pli": "Dānāni paṭiyādetvā, ", "eng": "according to their capacity and means, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:6.4": {"pli": "dakkhiṇeyyaṁ gavesisuṁ. ", "eng": "and sought out one worthy of offerings. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:7.1": {"pli": "Ahaṁ nisajja cintesiṁ, ", "eng": "As I was sitting I thought about ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:7.2": {"pli": "dānaṁ dakkhiṇanucchavaṁ; ", "eng": "what sort of gift would suit one worthy of offerings: ", "html": "<span class='verse-line'>{}</span>"}, "cp10:7.3": {"pli": "‘Yadihaṁ labhe dakkhiṇeyyaṁ, ", "eng": "‘If I were to find a worthy recipient, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:7.4": {"pli": "kiṁ me dānaṁ bhavissati. ", "eng": "what gift would i give? ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:8.1": {"pli": "Na me atthi tilā muggā, ", "eng": "I have no sesame or mung beans, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:8.2": {"pli": "māsā vā taṇḍulā ghataṁ; ", "eng": "no beans or rice or ghee. ", "html": "<span class='verse-line'>{}</span>"}, "cp10:8.3": {"pli": "Ahaṁ tiṇena yāpemi, ", "eng": "I feed only on grass, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:8.4": {"pli": "na sakkā tiṇa dātave. ", "eng": "but that’s not something I can give. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:9.1": {"pli": "Yadi koci eti dakkhiṇeyyo, ", "eng": "If someone worthy of gifts comes, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:9.2": {"pli": "Bhikkhāya mama santike; ", "eng": "seeking alms near me, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:9.3": {"pli": "Dajjāhaṁ sakamattānaṁ, ", "eng": "I shall give my own self: ", "html": "<span class='verse-line'>{}</span>"}, "cp10:9.4": {"pli": "Na so tuccho gamissati’. ", "eng": "they shall not leave empty-handed.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:10.1": {"pli": "Mama saṅkappamaññāya, ", "eng": "Knowing my thoughts, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:10.2": {"pli": "sakko brāhmaṇavaṇṇinā; ", "eng": "Sakka in the guise of a brahmin ", "html": "<span class='verse-line'>{}</span>"}, "cp10:10.3": {"pli": "Āsayaṁ me upāgacchi, ", "eng": "approached my lair ", "html": "<span class='verse-line'>{}</span>"}, "cp10:10.4": {"pli": "dānavīmaṁsanāya me. ", "eng": "in order to test my generosity. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:11.1": {"pli": "Tamahaṁ disvāna santuṭṭho, ", "eng": "Seeing him I was elated, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:11.2": {"pli": "idaṁ vacanamabraviṁ; ", "eng": "and said the following. ", "html": "<span class='verse-line'>{}</span>"}, "cp10:11.3": {"pli": "‘Sādhu khosi anuppatto, ", "eng": "‘It is so good that you have come ", "html": "<span class='verse-line'>{}</span>"}, "cp10:11.4": {"pli": "ghāsahetu mamantike. ", "eng": "to me looking for food. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:12.1": {"pli": "Adinnapubbaṁ dānavaraṁ, ", "eng": "Today I shall give to you ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:12.2": {"pli": "ajja dassāmi te ahaṁ; ", "eng": "a fine gift never before given. ", "html": "<span class='verse-line'>{}</span>"}, "cp10:12.3": {"pli": "Tuvaṁ sīlaguṇūpeto, ", "eng": "You are furnished with virtue, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:12.4": {"pli": "ayuttaṁ te paraheṭhanaṁ. ", "eng": "unattached to harming others. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:13.1": {"pli": "Ehi aggiṁ padīpehi, ", "eng": "Come, light a fire, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:13.2": {"pli": "nānākaṭṭhe samānaya; ", "eng": "having gathered different sticks. ", "html": "<span class='verse-line'>{}</span>"}, "cp10:13.3": {"pli": "Ahaṁ pacissamattānaṁ, ", "eng": "I shall cook myself, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:13.4": {"pli": "pakkaṁ tvaṁ bhakkhayissasi’. ", "eng": "you shall eat me roasted.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:14.1": {"pli": "‘Sādhū’ti so haṭṭhamano, ", "eng": "‘Good!’ he said happily, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:14.2": {"pli": "nānākaṭṭhe samānayi; ", "eng": "and gathered different sticks. ", "html": "<span class='verse-line'>{}</span>"}, "cp10:14.3": {"pli": "Mahantaṁ akāsi citakaṁ, ", "eng": "Building a great pyre, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:14.4": {"pli": "katvā aṅgāragabbhakaṁ. ", "eng": "he placed an ember in the recess. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:15.1": {"pli": "Aggiṁ tattha padīpesi, ", "eng": "He lit such a fire there ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:15.2": {"pli": "yathā so khippaṁ mahā bhave; ", "eng": "that would quickly grow big. ", "html": "<span class='verse-line'>{}</span>"}, "cp10:15.3": {"pli": "Phoṭetvā rajagate gatte, ", "eng": "Shaking my dusty limbs, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:15.4": {"pli": "ekamantaṁ upāvisiṁ. ", "eng": "I withdrew to one side. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:16.1": {"pli": "Yadā mahākaṭṭhapuñjo, ", "eng": "When the great pile of sticks ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:16.2": {"pli": "āditto dhamadhamāyati; ", "eng": "was burning and blazing, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:16.3": {"pli": "Taduppatitvā papatiṁ, ", "eng": "I leapt up and fell on it, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:16.4": {"pli": "majjhe jālasikhantare. ", "eng": "in the midst of the fiery flames. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:17.1": {"pli": "Yathā sītodakaṁ nāma, ", "eng": "Like diving ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:17.2": {"pli": "Paviṭṭhaṁ yassa kassaci; ", "eng": "into cool water, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:17.3": {"pli": "Sameti darathapariḷāhaṁ, ", "eng": "allaying distress and fever, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:17.4": {"pli": "Assādaṁ deti pīti ca. ", "eng": "bringing satisfaction and joy. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:18.1": {"pli": "Tatheva jalitaṁ aggiṁ, ", "eng": "That’s how it was ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:18.2": {"pli": "paviṭṭhassa mamaṁ tadā; ", "eng": "when I entered the burning flame. ", "html": "<span class='verse-line'>{}</span>"}, "cp10:18.3": {"pli": "Sabbaṁ sameti darathaṁ, ", "eng": "It allayed distress and fever, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:18.4": {"pli": "yathā sītodakaṁ viya. ", "eng": "just like cool water. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:19.1": {"pli": "Chaviṁ cammaṁ maṁsaṁ nhāruṁ, ", "eng": "Outer skin and inner, flesh and sinews, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:19.2": {"pli": "Aṭṭhiṁ hadayabandhanaṁ; ", "eng": "bones, and heart with cords: ", "html": "<span class='verse-line'>{}</span>"}, "cp10:19.3": {"pli": "Kevalaṁ sakalaṁ kāyaṁ, ", "eng": "my whole entire body ", "html": "<span class='verse-line'>{}</span>"}, "cp10:19.4": {"pli": "Brāhmaṇassa adāsahan”ti. ", "eng": "I gave to the brahmin.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp10:20.1": {"pli": "Sasapaṇḍitacariyaṁ dasamaṁ. ", "html": "<p class='endsutta'>{}</p>"}, "cp10:21.1": {"pli": "Akittivaggo paṭhamo. ", "html": "<p class='endsection'>{}</p>"}, "cp10:22.1": {"pli": "Tassuddānaṁ ", "html": "<p class='uddana-intro'>{}</p>"}, "cp10:23.1": {"pli": "Akittibrāhmaṇo saṅkho, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "cp10:23.2": {"pli": "kururājā dhanañcayo; ", "html": "<span class='verse-line'>{}</span>"}, "cp10:23.3": {"pli": "Mahāsudassano rājā, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:23.4": {"pli": "mahāgovindabrāhmaṇo. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:24.1": {"pli": "Nimi candakumāro ca, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:24.2": {"pli": "sivi vessantaro saso; ", "html": "<span class='verse-line'>{}</span>"}, "cp10:24.3": {"pli": "Ahameva tadā āsiṁ, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:24.4": {"pli": "yo te dānavare adā. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:25.1": {"pli": "Ete dānaparikkhārā, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:25.2": {"pli": "ete dānassa pāramī; ", "html": "<span class='verse-line'>{}</span>"}, "cp10:25.3": {"pli": "Jīvitaṁ yācake datvā, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:25.4": {"pli": "imaṁ pārami pūrayiṁ. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp10:26.1": {"pli": "Bhikkhāya upagataṁ disvā, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp10:26.2": {"pli": "sakattānaṁ pariccajiṁ; ", "html": "<span class='verse-line'>{}</span>"}, "cp10:26.3": {"pli": "Dānena me samo natthi, ", "html": "<span class='verse-line'>{}</span>"}, "cp10:26.4": {"pli": "esā me dānapāramīti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp10:27.1": {"pli": "Dānapāraminiddeso niṭṭhito. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp11": {"cp11:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp11'><header><ul><li class='division'>{}</li>"}, "cp11:0.2": {"pli": "Hatthināgavagga ", "eng": "The Chapter on an Elephant ", "html": "<li>{}</li>"}, "cp11:0.3": {"pli": "Sīlapāramī 1 ", "eng": "The Perfection of Ethics (1st) ", "html": "<li>{}</li></ul>"}, "cp11:0.4": {"pli": "Mātuposakacariya ", "eng": "The Conduct of One Who Provided for His Mother ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp11:1.1": {"pli": "“Yadā ahosiṁ pavane, ", "eng": "“Once I lived in the forest ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp11:1.2": {"pli": "kuñjaro mātuposako; ", "eng": "as a elephant who provided for his mother. ", "html": "<span class='verse-line'>{}</span>"}, "cp11:1.3": {"pli": "Na tadā atthi mahiyā, ", "eng": "At that time no-one on earth ", "html": "<span class='verse-line'>{}</span>"}, "cp11:1.4": {"pli": "guṇena mama sādiso. ", "eng": "could equal my virtues. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp11:2.1": {"pli": "Pavane disvā vanacaro, ", "eng": "Seeing me in the forest, a forester ", "html": "<p><span class='verse-line'>{}</span>"}, "cp11:2.2": {"pli": "rañño maṁ paṭivedayi; ", "eng": "informed the king about me. ", "html": "<span class='verse-line'>{}</span>"}, "cp11:2.3": {"pli": "‘Tavānucchavo mahārāja, ", "eng": "‘Great King, an elephant that befits you ", "html": "<span class='verse-line'>{}</span>"}, "cp11:2.4": {"pli": "gajo vasati kānane. ", "eng": "is dwelling in the forest grove. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp11:3.1": {"pli": "Na tassa parikkhāyattho, ", "eng": "No need to dig a moat around him, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp11:3.2": {"pli": "napi āḷakakāsuyā; ", "eng": "nor tether him to a post in a pit. ", "html": "<span class='verse-line'>{}</span>"}, "cp11:3.3": {"pli": "Saha gahite soṇḍāya, ", "eng": "Taken by the trunk, ", "html": "<span class='verse-line'>{}</span>"}, "cp11:3.4": {"pli": "sayameva idhehi’ti. ", "eng": "he will come here by himself.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp11:4.1": {"pli": "Tassa taṁ vacanaṁ sutvā, ", "eng": "When he heard these words, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp11:4.2": {"pli": "rājāpi tuṭṭhamānaso; ", "eng": "the king, elated, ", "html": "<span class='verse-line'>{}</span>"}, "cp11:4.3": {"pli": "Pesesi hatthidamakaṁ, ", "eng": "sent an elephant-tamer, ", "html": "<span class='verse-line'>{}</span>"}, "cp11:4.4": {"pli": "chekācariyaṁ susikkhitaṁ. ", "eng": "a well-trained and expert tutor. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp11:5.1": {"pli": "Gantvā so hatthidamako, ", "eng": "When the elephant-tamer reached there, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp11:5.2": {"pli": "addasa padumassare; ", "eng": "he saw me in a lotus pond, ", "html": "<span class='verse-line'>{}</span>"}, "cp11:5.3": {"pli": "Bhisamuḷālaṁ uddharantaṁ, ", "eng": "pulling out lotus roots ", "html": "<span class='verse-line'>{}</span>"}, "cp11:5.4": {"pli": "yāpanatthāya mātuyā. ", "eng": "to feed my mother. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp11:6.1": {"pli": "Viññāya me sīlaguṇaṁ, ", "eng": "Discerning my moral virtue, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp11:6.2": {"pli": "lakkhaṇaṁ upadhārayi; ", "eng": "he checked me for marks. ", "html": "<span class='verse-line'>{}</span>"}, "cp11:6.3": {"pli": "‘Ehi puttā’ti vatvāna, ", "eng": "Saying, ‘Come, child’, ", "html": "<span class='verse-line'>{}</span>"}, "cp11:6.4": {"pli": "mama soṇḍāya aggahi. ", "eng": "he took me by the trunk. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp11:7.1": {"pli": "Yaṁ me tadā pākatikaṁ, ", "eng": "What was at that time ", "html": "<p><span class='verse-line'>{}</span>"}, "cp11:7.2": {"pli": "sarīrānugataṁ balaṁ; ", "eng": "the natural strength of my body, ", "html": "<span class='verse-line'>{}</span>"}, "cp11:7.3": {"pli": "Ajja nāgasahassānaṁ, ", "eng": "would equal a thousand ", "html": "<span class='verse-line'>{}</span>"}, "cp11:7.4": {"pli": "balena samasādisaṁ. ", "eng": "of the elephants of today. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp11:8.1": {"pli": "Yadihaṁ tesaṁ pakuppeyyaṁ, ", "eng": "If I had been upset ", "html": "<p><span class='verse-line'>{}</span>"}, "cp11:8.2": {"pli": "upetānaṁ gahaṇāya maṁ; ", "eng": "with those who came to capture me, ", "html": "<span class='verse-line'>{}</span>"}, "cp11:8.3": {"pli": "Paṭibalo bhave tesaṁ, ", "eng": "I would have had the power ", "html": "<span class='verse-line'>{}</span>"}, "cp11:8.4": {"pli": "yāva rajjampi mānusaṁ. ", "eng": "to defeat even a kingdom of men. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp11:9.1": {"pli": "Api cāhaṁ sīlarakkhāya, ", "eng": "Nevertheless, for the sake of guarding my ethics, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp11:9.2": {"pli": "Sīlapāramipūriyā; ", "eng": "and fulfilling my perfection of ethics, ", "html": "<span class='verse-line'>{}</span>"}, "cp11:9.3": {"pli": "Na karomi citte aññathattaṁ, ", "eng": "I did not let my mind change, ", "html": "<span class='verse-line'>{}</span>"}, "cp11:9.4": {"pli": "Pakkhipantaṁ mamāḷake. ", "eng": "even as they caged me. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp11:10.1": {"pli": "Yadi te maṁ tattha koṭṭeyyuṁ, ", "eng": "And even were they to beat me ", "html": "<p><span class='verse-line'>{}</span>"}, "cp11:10.2": {"pli": "pharasūhi tomarehi ca; ", "eng": "with hatchets and lances, ", "html": "<span class='verse-line'>{}</span>"}, "cp11:10.3": {"pli": "Neva tesaṁ pakuppeyyaṁ, ", "eng": "I would never become upset, ", "html": "<span class='verse-line'>{}</span>"}, "cp11:10.4": {"pli": "sīlakhaṇḍabhayā mamā”ti. ", "eng": "for fear of violating my ethics.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp11:11.1": {"pli": "Mātuposakacariyaṁ paṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp12": {"cp12:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp12'><header><ul><li class='division'>{}</li>"}, "cp12:0.2": {"pli": "Hatthināgavagga ", "eng": "The Chapter on an Elephant ", "html": "<li>{}</li>"}, "cp12:0.3": {"pli": "Sīlapāramī 2 ", "eng": "The Perfection of Ethics (2nd) ", "html": "<li>{}</li></ul>"}, "cp12:0.4": {"pli": "Bhūridattacariya ", "eng": "The Dragon Bhūridatta’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp12:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp12:1.2": {"pli": "bhūridatto mahiddhiko; ", "eng": "Bhūridatta, a dragon of great psychic power, ", "html": "<span class='verse-line'>{}</span>", "comm": "The text does not specify what kind of creature he was, thus relying on the fuller details of <a href='https://suttacentral.net/ja543/en/sujato'>Ja 543</a>. "}, "cp12:1.3": {"pli": "Virūpakkhena mahāraññā, ", "eng": "together with Virūpakkha the Great King ", "html": "<span class='verse-line'>{}</span>"}, "cp12:1.4": {"pli": "devalokamagañchahaṁ. ", "eng": "I went to a heavenly realm. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp12:2.1": {"pli": "Tattha passitvāhaṁ deve, ", "eng": "There I saw gods ", "html": "<p><span class='verse-line'>{}</span>"}, "cp12:2.2": {"pli": "ekantaṁ sukhasamappite; ", "eng": "wholly given over to pleasure. ", "html": "<span class='verse-line'>{}</span>"}, "cp12:2.3": {"pli": "Taṁ saggagamanatthāya, ", "eng": "For the sake of going to that heaven, ", "html": "<span class='verse-line'>{}</span>"}, "cp12:2.4": {"pli": "sīlabbataṁ samādiyiṁ. ", "eng": "I undertook the observance of ethics. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp12:3.1": {"pli": "Sarīrakiccaṁ katvāna, ", "eng": "After tending to me physical needs, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp12:3.2": {"pli": "bhutvā yāpanamattakaṁ; ", "eng": "and eating enough to get by, ", "html": "<span class='verse-line'>{}</span>"}, "cp12:3.3": {"pli": "Caturo aṅge adhiṭṭhāya, ", "eng": "I resolved on the four factors, ", "html": "<span class='verse-line'>{}</span>"}, "cp12:3.4": {"pli": "semi vammikamuddhani. ", "eng": "and lay down atop a termite mound, thinking: ", "html": "<span class='verse-line'>{}</span></p>"}, "cp12:4.1": {"pli": "‘Chaviyā cammena maṁsena, ", "eng": "‘My outer skin and inner, flesh, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp12:4.2": {"pli": "Nahāruaṭṭhikehi vā; ", "eng": "sinews and bones: ", "html": "<span class='verse-line'>{}</span>"}, "cp12:4.3": {"pli": "Yassa etena karaṇīyaṁ, ", "eng": "whoever has use for these, ", "html": "<span class='verse-line'>{}</span>"}, "cp12:4.4": {"pli": "Dinnaṁyeva harātu so’. ", "eng": "they are already given, please take them.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp12:5.1": {"pli": "Saṁsito akataññunā, ", "eng": "While lying there the ingrate ", "html": "<p><span class='verse-line'>{}</span>"}, "cp12:5.2": {"pli": "ālampāyano mamaggahi; ", "eng": "Ālampāyana grabbed me. ", "html": "<span class='verse-line'>{}</span>"}, "cp12:5.3": {"pli": "Peḷāya pakkhipitvāna, ", "eng": "He threw me in a basket ", "html": "<span class='verse-line'>{}</span>"}, "cp12:5.4": {"pli": "kīḷeti maṁ tahiṁ tahiṁ. ", "eng": "and made me perform in places all over. ", "html": "<span class='verse-line'>{}</span></p>", "comm": "The text conflates a divine <i lang='pi' translate='no'>nāga</i> with a cobra in a way we don’t really find in the suttas. "}, "cp12:6.1": {"pli": "Peḷāya pakkhipantepi, ", "eng": "Though thrown in a basket ", "html": "<p><span class='verse-line'>{}</span>"}, "cp12:6.2": {"pli": "sammaddantepi pāṇinā; ", "eng": "and crushed by his hands, ", "html": "<span class='verse-line'>{}</span>"}, "cp12:6.3": {"pli": "Ālampāyane na kuppāmi, ", "eng": "I did not get upset with Ālampāyana, ", "html": "<span class='verse-line'>{}</span>"}, "cp12:6.4": {"pli": "sīlakhaṇḍabhayā mama. ", "eng": "for fear of breaking my ethics. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp12:7.1": {"pli": "Sakajīvitapariccāgo, ", "eng": "To give up my own life ", "html": "<p><span class='verse-line'>{}</span>"}, "cp12:7.2": {"pli": "tiṇato lahuko mama; ", "eng": "was as light as a blade of grass. ", "html": "<span class='verse-line'>{}</span>"}, "cp12:7.3": {"pli": "Sīlavītikkamo mayhaṁ, ", "eng": "To transgress my ethical principles ", "html": "<span class='verse-line'>{}</span>"}, "cp12:7.4": {"pli": "pathavīuppatanaṁ viya. ", "eng": "was like overturning the earth. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp12:8.1": {"pli": "Nirantaraṁ jātisataṁ, ", "eng": "In a hundred successive lives ", "html": "<p><span class='verse-line'>{}</span>"}, "cp12:8.2": {"pli": "cajeyyaṁ mama jīvitaṁ; ", "eng": "I would give up my own life. ", "html": "<span class='verse-line'>{}</span>"}, "cp12:8.3": {"pli": "Neva sīlaṁ pabhindeyyaṁ, ", "eng": "I would not even violate my ethics ", "html": "<span class='verse-line'>{}</span>"}, "cp12:8.4": {"pli": "catuddīpāna hetupi. ", "eng": "for the sake of the four continents. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp12:9.1": {"pli": "Api cāhaṁ sīlarakkhāya, ", "eng": "For the sake of protecting ethics, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp12:9.2": {"pli": "Sīlapāramipūriyā; ", "eng": "and fulfilling my perfection of ethics, ", "html": "<span class='verse-line'>{}</span>"}, "cp12:9.3": {"pli": "Na karomi citte aññathattaṁ, ", "eng": "I did not let my mind change, ", "html": "<span class='verse-line'>{}</span>"}, "cp12:9.4": {"pli": "Pakkhipantampi peḷake”ti. ", "eng": "even when thrown in a basket.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp12:10.1": {"pli": "Bhūridattacariyaṁ dutiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp13": {"cp13:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp13'><header><ul><li class='division'>{}</li>"}, "cp13:0.2": {"pli": "Hatthināgavagga ", "eng": "The Chapter on an Elephant ", "html": "<li>{}</li>"}, "cp13:0.3": {"pli": "Sīlapāramī 3 ", "eng": "The Perfection of Ethics (3rd) ", "html": "<li>{}</li></ul>"}, "cp13:0.4": {"pli": "Campeyyanāgacariya ", "eng": "The Dragon Campeyyaka’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp13:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp13:1.2": {"pli": "campeyyako mahiddhiko; ", "eng": "Campeyyaka, a dragon of great psychic power, ", "html": "<span class='verse-line'>{}</span>", "comm": "The titular deity of the people of Campā. "}, "cp13:1.3": {"pli": "Tadāpi dhammiko āsiṁ, ", "eng": "I was just, ", "html": "<span class='verse-line'>{}</span>"}, "cp13:1.4": {"pli": "sīlabbatasamappito. ", "eng": "furnished with precepts and vows. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp13:2.1": {"pli": "Tadāpi maṁ dhammacāriṁ, ", "eng": "Then the snake-charmer who caught me ", "html": "<p><span class='verse-line'>{}</span>"}, "cp13:2.2": {"pli": "upavutthaṁ uposathaṁ; ", "eng": "lived righteously, ", "html": "<span class='verse-line'>{}</span>"}, "cp13:2.3": {"pli": "Ahituṇḍiko gahetvāna, ", "eng": "observing the Sabbath, ", "html": "<span class='verse-line'>{}</span>"}, "cp13:2.4": {"pli": "rājadvāramhi kīḷati. ", "eng": "and he made me perform at the royal gate. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp13:3.1": {"pli": "Yaṁ yaṁ so vaṇṇaṁ cintayi, ", "eng": "Whatever color he thought of—", "html": "<p><span class='verse-line'>{}</span>"}, "cp13:3.2": {"pli": "nīlaṁva pītalohitaṁ; ", "eng": "blue, yellow, or red—", "html": "<span class='verse-line'>{}</span>"}, "cp13:3.3": {"pli": "Tassa cittānuvattanto, ", "eng": "I conformed to his wish, ", "html": "<span class='verse-line'>{}</span>"}, "cp13:3.4": {"pli": "homi cintitasannibho. ", "eng": "taking the appearance of his thought. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp13:4.1": {"pli": "Thalaṁ kareyyamudakaṁ, ", "eng": "I could turn dry land to water, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp13:4.2": {"pli": "udakampi thalaṁ kare; ", "eng": "and water to dry land. ", "html": "<span class='verse-line'>{}</span>"}, "cp13:4.3": {"pli": "Yadihaṁ tassa pakuppeyyaṁ, ", "eng": "If I were upset with him ", "html": "<span class='verse-line'>{}</span>"}, "cp13:4.4": {"pli": "khaṇena chārikaṁ kare. ", "eng": "I could reduce him to ash in an instant. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp13:5.1": {"pli": "Yadi cittavasī hessaṁ, ", "eng": "If I was under the sway of my thoughts, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp13:5.2": {"pli": "parihāyissāmi sīlato; ", "eng": "I would have fallen from my ethics. ", "html": "<span class='verse-line'>{}</span>"}, "cp13:5.3": {"pli": "Sīlena parihīnassa, ", "eng": "But one who has fallen from their ethics ", "html": "<span class='verse-line'>{}</span>"}, "cp13:5.4": {"pli": "uttamattho na sijjhati. ", "eng": "does not succeed at the highest goal. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp13:6.1": {"pli": "Kāmaṁ bhijjatuyaṁ kāyo, ", "eng": "Gladly, let this body be broken! ", "html": "<p><span class='verse-line'>{}</span>"}, "cp13:6.2": {"pli": "idheva vikirīyatu; ", "eng": "Let it be scattered in this very place! ", "html": "<span class='verse-line'>{}</span>"}, "cp13:6.3": {"pli": "Neva sīlaṁ pabhindeyyaṁ, ", "eng": "Not even for that would I break my ethics, ", "html": "<span class='verse-line'>{}</span>"}, "cp13:6.4": {"pli": "vikirante bhusaṁ viyā”ti. ", "eng": "if it were being scattered like hay.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp13:7.1": {"pli": "Campeyyanāgacariyaṁ tatiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp14": {"cp14:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp14'><header><ul><li class='division'>{}</li>"}, "cp14:0.2": {"pli": "Hatthināgavagga ", "eng": "The Chapter on an Elephant ", "html": "<li>{}</li>"}, "cp14:0.3": {"pli": "Sīlapāramī 4 ", "eng": "The Perfection of Ethics (4th) ", "html": "<li>{}</li></ul>"}, "cp14:0.4": {"pli": "Cūḷabodhicariya ", "eng": "Cūḷabodhi’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp14:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp14:1.2": {"pli": "cūḷabodhi susīlavā; ", "eng": "the very virtuous Cūḷabodhi, ", "html": "<span class='verse-line'>{}</span>"}, "cp14:1.3": {"pli": "Bhavaṁ disvāna bhayato, ", "eng": "seeing rebirth as fearful, ", "html": "<span class='verse-line'>{}</span>"}, "cp14:1.4": {"pli": "nekkhammaṁ abhinikkhamiṁ. ", "eng": "I renounced worldly life. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp14:2.1": {"pli": "Yā me dutiyikā āsi, ", "eng": "She who had been my partner, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp14:2.2": {"pli": "brāhmaṇī kanakasannibhā; ", "eng": "a brahmin lady with golden skin, ", "html": "<span class='verse-line'>{}</span>"}, "cp14:2.3": {"pli": "Sāpi vaṭṭe anapekkhā, ", "eng": "unconcerned with the cycle of rebirths, ", "html": "<span class='verse-line'>{}</span>"}, "cp14:2.4": {"pli": "nekkhammaṁ abhinikkhami. ", "eng": "also renounced worldly life. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp14:3.1": {"pli": "Nirālayā chinnabandhū, ", "eng": "Without clinging, cutting off family, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp14:3.2": {"pli": "anapekkhā kule gaṇe; ", "eng": "unconcerned with family or group, ", "html": "<span class='verse-line'>{}</span>"}, "cp14:3.3": {"pli": "Carantā gāmanigamaṁ, ", "eng": "we walked from village to town ", "html": "<span class='verse-line'>{}</span>"}, "cp14:3.4": {"pli": "bārāṇasimupāgamuṁ. ", "eng": "until we reached Varanasi. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp14:4.1": {"pli": "Tattha vasāma nipakā, ", "eng": "There we lived alert, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp14:4.2": {"pli": "asaṁsaṭṭhā kule gaṇe; ", "eng": "mixing not with a family or group. ", "html": "<span class='verse-line'>{}</span>"}, "cp14:4.3": {"pli": "Nirākule appasadde, ", "eng": "Quiet and undisturbed, ", "html": "<span class='verse-line'>{}</span>"}, "cp14:4.4": {"pli": "rājuyyāne vasāmubho. ", "eng": "we both stayed in the royal park. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp14:5.1": {"pli": "Uyyānadassanaṁ gantvā, ", "eng": "Visiting the park, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp14:5.2": {"pli": "rājā addasa brāhmaṇiṁ; ", "eng": "the king saw the brahmin lady. ", "html": "<span class='verse-line'>{}</span>"}, "cp14:5.3": {"pli": "Upagamma mamaṁ pucchi, ", "eng": "He approached me and asked, ", "html": "<span class='verse-line'>{}</span>"}, "cp14:5.4": {"pli": "‘tuyhesā kā kassa bhariyā’. ", "eng": "‘Is she your wife? Or whose is she?’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp14:6.1": {"pli": "Evaṁ vutte, ahaṁ tassa, ", "eng": "When he said this, to him ", "html": "<p><span class='verse-line'>{}</span>"}, "cp14:6.2": {"pli": "idaṁ vacanamabraviṁ; ", "eng": "I said the following. ", "html": "<span class='verse-line'>{}</span>"}, "cp14:6.3": {"pli": "‘Na mayhaṁ bhariyā esā, ", "eng": "‘She is not my wife. ", "html": "<span class='verse-line'>{}</span>"}, "cp14:6.4": {"pli": "sahadhammā ekasāsanī’. ", "eng": "We share the same teaching and belief.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp14:7.1": {"pli": "Tissā sārattagadhito, ", "eng": "Infatuated with her, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp14:7.2": {"pli": "gāhāpetvāna ceṭake; ", "eng": "he had his men seize her. ", "html": "<span class='verse-line'>{}</span>"}, "cp14:7.3": {"pli": "Nippīḷayanto balasā, ", "eng": "Compelling her by force ", "html": "<span class='verse-line'>{}</span>"}, "cp14:7.4": {"pli": "antepuraṁ pavesayi. ", "eng": "he brought her to the royal compound. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp14:8.1": {"pli": "Odapattakiyā mayhaṁ, ", "eng": "She who became mine by touching a water-jar, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp14:8.2": {"pli": "sahajā ekasāsanī; ", "eng": "born together, one in beliefs, ", "html": "<span class='verse-line'>{}</span>"}, "cp14:8.3": {"pli": "Ākaḍḍhitvā nayantiyā, ", "eng": "when he dragged her away, ", "html": "<span class='verse-line'>{}</span>"}, "cp14:8.4": {"pli": "kopo me upapajjatha. ", "eng": "I felt so angry. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp14:9.1": {"pli": "Saha kope samuppanne, ", "eng": "Together with the arising of anger, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp14:9.2": {"pli": "sīlabbatamanussariṁ; ", "eng": "I remembered my precepts and vows, ", "html": "<span class='verse-line'>{}</span>"}, "cp14:9.3": {"pli": "Tattheva kopaṁ niggaṇhiṁ, ", "eng": "and right there controlled my anger, ", "html": "<span class='verse-line'>{}</span>"}, "cp14:9.4": {"pli": "nādāsiṁ vaḍḍhitūpari. ", "eng": "I did not allow it to grow. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp14:10.1": {"pli": "Yadi naṁ brāhmaṇiṁ koci, ", "eng": "If anyone were to attack ", "html": "<p><span class='verse-line'>{}</span>"}, "cp14:10.2": {"pli": "koṭṭeyya tiṇhasattiyā; ", "eng": "that brahmin lady with a sharp knife, ", "html": "<span class='verse-line'>{}</span>"}, "cp14:10.3": {"pli": "Neva sīlaṁ pabhindeyyaṁ, ", "eng": "not even for that would I violate my ethics, ", "html": "<span class='verse-line'>{}</span>"}, "cp14:10.4": {"pli": "bodhiyāyeva kāraṇā. ", "eng": "because it was solely for awakening. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp14:11.1": {"pli": "Na mesā brāhmaṇī dessā, ", "eng": "I did not dislike the brahmin lady, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp14:11.2": {"pli": "napi me balaṁ na vijjati; ", "eng": "nor did I lack the strength. ", "html": "<span class='verse-line'>{}</span>"}, "cp14:11.3": {"pli": "Sabbaññutaṁ piyaṁ mayhaṁ, ", "eng": "But because omniscience is precious to me, ", "html": "<span class='verse-line'>{}</span>"}, "cp14:11.4": {"pli": "tasmā sīlānurakkhisan”ti. ", "eng": "that’s why I guarded my ethics.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp14:12.1": {"pli": "Cūḷabodhicariyaṁ catutthaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp15": {"cp15:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp15'><header><ul><li class='division'>{}</li>"}, "cp15:0.2": {"pli": "Hatthināgavagga ", "eng": "The Chapter on an Elephant ", "html": "<li>{}</li>"}, "cp15:0.3": {"pli": "Sīlapāramī 5 ", "eng": "The Perfection of Ethics (5th) ", "html": "<li>{}</li></ul>"}, "cp15:0.4": {"pli": "Mahiṁsarājacariya ", "eng": "The Buffalo King’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp15:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp15:1.2": {"pli": "mahiṁso pavanacārako; ", "eng": "a buffalo who roamed the forest, ", "html": "<span class='verse-line'>{}</span>"}, "cp15:1.3": {"pli": "Pavaḍḍhakāyo balavā, ", "eng": "large in body and strong, ", "html": "<span class='verse-line'>{}</span>"}, "cp15:1.4": {"pli": "mahanto bhīmadassano. ", "eng": "big and scary to see. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp15:2.1": {"pli": "Pabbhāre giridugge ca, ", "eng": "In a cave, a craggy hillside, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp15:2.2": {"pli": "rukkhamūle dakāsaye; ", "eng": "at a tree root or a waterhole, ", "html": "<span class='verse-line'>{}</span>"}, "cp15:2.3": {"pli": "Hotettha ṭhānaṁ mahiṁsānaṁ, ", "eng": "there’d be somewhere for buffalos, ", "html": "<span class='verse-line'>{}</span>"}, "cp15:2.4": {"pli": "koci koci tahiṁ tahiṁ. ", "eng": "in one place or another. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp15:3.1": {"pli": "Vicaranto brahāraññe, ", "eng": "Wandering the mighty forest, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp15:3.2": {"pli": "ṭhānaṁ addasa bhaddakaṁ; ", "eng": "I saw a fine spot. ", "html": "<span class='verse-line'>{}</span>"}, "cp15:3.3": {"pli": "Taṁ ṭhānaṁ upagantvāna, ", "eng": "Having gone there, ", "html": "<span class='verse-line'>{}</span>"}, "cp15:3.4": {"pli": "tiṭṭhāmi ca sayāmi ca. ", "eng": "I stood and lay to rest. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp15:4.1": {"pli": "Athettha kapimāgantvā, ", "eng": "Then a monkey came along, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp15:4.2": {"pli": "pāpo anariyo lahu; ", "eng": "wicked, ignoble, and nimble. ", "html": "<span class='verse-line'>{}</span>"}, "cp15:4.3": {"pli": "Khandhe nalāṭe bhamuke, ", "eng": "On my shoulder, forehead, and eyebrows, ", "html": "<span class='verse-line'>{}</span>"}, "cp15:4.4": {"pli": "mutteti ohadeti taṁ. ", "eng": "he peed and pooed. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp15:5.1": {"pli": "Sakimpi divasaṁ dutiyaṁ, ", "eng": "Not just that day, but a second, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp15:5.2": {"pli": "tatiyaṁ catutthampi ca; ", "eng": "a third, and a fourth; ", "html": "<span class='verse-line'>{}</span>"}, "cp15:5.3": {"pli": "Dūseti maṁ sabbakālaṁ, ", "eng": "he soiled me all the time, ", "html": "<span class='verse-line'>{}</span>"}, "cp15:5.4": {"pli": "tena homi upadduto. ", "eng": "he was harrying me. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp15:6.1": {"pli": "Mamaṁ upaddutaṁ disvā, ", "eng": "Seeing me being harried, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp15:6.2": {"pli": "yakkho maṁ idamabravi; ", "eng": "a spirit said to me: ", "html": "<span class='verse-line'>{}</span>"}, "cp15:6.3": {"pli": "‘Nāsehetaṁ chavaṁ pāpaṁ, ", "eng": "‘Kill that vile, wicked thing ", "html": "<span class='verse-line'>{}</span>"}, "cp15:6.4": {"pli": "siṅgehi ca khurehi ca’. ", "eng": "with your horns and hooves.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp15:7.1": {"pli": "Evaṁ vutte, tadā yakkhe, ", "eng": "When he spoke, I said ", "html": "<p><span class='verse-line'>{}</span>"}, "cp15:7.2": {"pli": "ahaṁ taṁ idamabraviṁ; ", "eng": "this to that spirit: ", "html": "<span class='verse-line'>{}</span>"}, "cp15:7.3": {"pli": "‘Kiṁ tvaṁ makkhesi kuṇapena, ", "eng": "‘Why would you smear me ", "html": "<span class='verse-line'>{}</span>"}, "cp15:7.4": {"pli": "pāpena anariyena maṁ. ", "eng": "with a wicked, ignoble carcass? ", "html": "<span class='verse-line'>{}</span></p>"}, "cp15:8.1": {"pli": "Yadihaṁ tassa pakuppeyyaṁ, ", "eng": "If I were upset with him ", "html": "<p><span class='verse-line'>{}</span>"}, "cp15:8.2": {"pli": "tato hīnataro bhave; ", "eng": "I would degrade myself, ", "html": "<span class='verse-line'>{}</span>"}, "cp15:8.3": {"pli": "Sīlañca me pabhijjeyya, ", "eng": "violating my ethics, ", "html": "<span class='verse-line'>{}</span>"}, "cp15:8.4": {"pli": "viññū ca garaheyyu maṁ. ", "eng": "and being criticized by sensible people. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp15:9.1": {"pli": "Hīḷitā jīvitā vāpi, ", "eng": "Better to die from purity ", "html": "<p><span class='verse-line'>{}</span>"}, "cp15:9.2": {"pli": "parisuddhena mataṁ varaṁ; ", "eng": "than live in shame. ", "html": "<span class='verse-line'>{}</span>"}, "cp15:9.3": {"pli": "Kyāhaṁ jīvitahetūpi, ", "eng": "How could I, for the sake of life, ", "html": "<span class='verse-line'>{}</span>"}, "cp15:9.4": {"pli": "kāhāmi paraheṭhanaṁ’. ", "eng": "harm another?’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp15:10.1": {"pli": "Mamevāyaṁ maññamāno, ", "eng": "Thinking of me, this one ", "html": "<p><span class='verse-line'>{}</span>"}, "cp15:10.2": {"pli": "aññepevaṁ karissati; ", "eng": "will do the same to others, ", "html": "<span class='verse-line'>{}</span>"}, "cp15:10.3": {"pli": "Teva tassa vadhissanti, ", "eng": "and for that they will kill him. ", "html": "<span class='verse-line'>{}</span>"}, "cp15:10.4": {"pli": "sā me mutti bhavissati. ", "eng": "Then I shall be free. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp15:11.1": {"pli": "Hīnamajjhimaukkaṭṭhe, ", "eng": "Forgiving disrespect ", "html": "<p><span class='verse-line'>{}</span>"}, "cp15:11.2": {"pli": "sahanto avamānitaṁ; ", "eng": "from the low, middle and high, ", "html": "<span class='verse-line'>{}</span>"}, "cp15:11.3": {"pli": "Evaṁ labhati sappañño, ", "eng": "the wise one gains ", "html": "<span class='verse-line'>{}</span>"}, "cp15:11.4": {"pli": "manasā yathāpatthitan”ti. ", "eng": "their heart’s desire.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp15:12.1": {"pli": "Mahiṁsarājacariyaṁ pañcamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp16": {"cp16:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp16'><header><ul><li class='division'>{}</li>"}, "cp16:0.2": {"pli": "Hatthināgavagga ", "eng": "The Chapter on an Elephant ", "html": "<li>{}</li>"}, "cp16:0.3": {"pli": "Sīlapāramī 6 ", "eng": "The Perfection of Ethics (6th) ", "html": "<li>{}</li></ul>"}, "cp16:0.4": {"pli": "Rurumigarājacariya ", "eng": "Ruru the Deer King’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp16:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp16:1.2": {"pli": "sutattakanakasannibho; ", "eng": "the king of deer named Ruru, ", "html": "<span class='verse-line'>{}</span>"}, "cp16:1.3": {"pli": "Migarājā ruru nāma, ", "eng": "in appearance I was like burnished gold, ", "html": "<span class='verse-line'>{}</span>"}, "cp16:1.4": {"pli": "paramasīlasamāhito. ", "eng": "focused on the highest ethics. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp16:2.1": {"pli": "Ramme padese ramaṇīye, ", "eng": "There was a pleasant, delightful region, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp16:2.2": {"pli": "vivitte amanussake; ", "eng": "free of humans, ", "html": "<span class='verse-line'>{}</span>"}, "cp16:2.3": {"pli": "Tattha vāsaṁ upagañchiṁ, ", "eng": "so I went there and stayed ", "html": "<span class='verse-line'>{}</span>"}, "cp16:2.4": {"pli": "gaṅgākūle manorame. ", "eng": "on a lovely bank of the Ganges. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp16:3.1": {"pli": "Atha upari gaṅgāya, ", "eng": "Then a man upstream, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp16:3.2": {"pli": "dhanikehi paripīḷito; ", "eng": "harassed by creditors, ", "html": "<span class='verse-line'>{}</span>"}, "cp16:3.3": {"pli": "Puriso gaṅgāya papati, ", "eng": "fell into the Ganges, thinking, ", "html": "<span class='verse-line'>{}</span>"}, "cp16:3.4": {"pli": "‘jīvāmi vā marāmi vā’. ", "eng": "‘I live or I die!’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp16:4.1": {"pli": "Rattindivaṁ so gaṅgāya, ", "eng": "Day and night in the Ganges ", "html": "<p><span class='verse-line'>{}</span>"}, "cp16:4.2": {"pli": "vuyhamāno mahodake; ", "eng": "he was swept along the stream, ", "html": "<span class='verse-line'>{}</span>"}, "cp16:4.3": {"pli": "Ravanto karuṇaṁ ravaṁ, ", "eng": "crying out pitifully, ", "html": "<span class='verse-line'>{}</span>"}, "cp16:4.4": {"pli": "majjhe gaṅgāya gacchati. ", "eng": "along the middle of the Ganges. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp16:5.1": {"pli": "Tassāhaṁ saddaṁ sutvāna, ", "eng": "When I heard the sound ", "html": "<p><span class='verse-line'>{}</span>"}, "cp16:5.2": {"pli": "karuṇaṁ paridevato; ", "eng": "of his pitiful crying, ", "html": "<span class='verse-line'>{}</span>"}, "cp16:5.3": {"pli": "Gaṅgāya tīre ṭhatvāna, ", "eng": "I stood on the Ganges bank ", "html": "<span class='verse-line'>{}</span>"}, "cp16:5.4": {"pli": "apucchiṁ ‘kosi tvaṁ naro’. ", "eng": "and asked, ‘What man are you?’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp16:6.1": {"pli": "So me puṭṭho ca byākāsi, ", "eng": "When asked he explained ", "html": "<p><span class='verse-line'>{}</span>"}, "cp16:6.2": {"pli": "attano karaṇaṁ tadā; ", "eng": "to me his own deed: ", "html": "<span class='verse-line'>{}</span>"}, "cp16:6.3": {"pli": "‘Dhanikehi bhīto tasito, ", "eng": "‘I was so scared of my creditors, ", "html": "<span class='verse-line'>{}</span>"}, "cp16:6.4": {"pli": "pakkhandohaṁ mahānadiṁ’. ", "eng": "I jumped in the great river!’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp16:7.1": {"pli": "Tassa katvāna kāruññaṁ, ", "eng": "Taking pity on him, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp16:7.2": {"pli": "cajitvā mama jīvitaṁ; ", "eng": "surrendering my own life, ", "html": "<span class='verse-line'>{}</span>"}, "cp16:7.3": {"pli": "Pavisitvā nīhariṁ tassa, ", "eng": "I entered and dragged him out ", "html": "<span class='verse-line'>{}</span>"}, "cp16:7.4": {"pli": "andhakāramhi rattiyā. ", "eng": "in the darkness of the night. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp16:8.1": {"pli": "Assatthakālamaññāya, ", "eng": "When I knew he had recovered, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp16:8.2": {"pli": "tassāhaṁ idamabraviṁ; ", "eng": "I said to him, ", "html": "<span class='verse-line'>{}</span>"}, "cp16:8.3": {"pli": "‘Ekaṁ taṁ varaṁ yācāmi, ", "eng": "‘I ask of you one favor: ", "html": "<span class='verse-line'>{}</span>"}, "cp16:8.4": {"pli": "mā maṁ kassaci pāvada’. ", "eng": "tell no-one about me.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp16:9.1": {"pli": "Nagaraṁ gantvāna ācikkhi, ", "eng": "But once he had returned to the city, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp16:9.2": {"pli": "pucchito dhanahetuko; ", "eng": "for the sake of money he told when asked. ", "html": "<span class='verse-line'>{}</span>"}, "cp16:9.3": {"pli": "Rājānaṁ so gahetvāna, ", "eng": "Taking the king, ", "html": "<span class='verse-line'>{}</span>"}, "cp16:9.4": {"pli": "upagañchi mamantikaṁ. ", "eng": "he drew close to me. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp16:10.1": {"pli": "Yāvatā karaṇaṁ sabbaṁ, ", "eng": "All that I had done ", "html": "<p><span class='verse-line'>{}</span>"}, "cp16:10.2": {"pli": "rañño ārocitaṁ mayā; ", "eng": "was related to the king. ", "html": "<span class='verse-line'>{}</span>"}, "cp16:10.3": {"pli": "Rājā sutvāna vacanaṁ, ", "eng": "When the king heard this, ", "html": "<span class='verse-line'>{}</span>"}, "cp16:10.4": {"pli": "usuṁ tassa pakappayi; ", "eng": "he fitted his arrow, thinking, ", "html": "<span class='verse-line'>{}</span>"}, "cp16:10.5": {"pli": "‘Idheva ghātayissāmi, ", "eng": "‘Right here I shall kill ", "html": "<span class='verse-line'>{}</span>"}, "cp16:10.6": {"pli": "mittadubbhiṁ anāriyaṁ’. ", "eng": "this ignoble betrayer of a friend.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp16:11.1": {"pli": "Tamahaṁ anurakkhanto, ", "eng": "I protected him, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp16:11.2": {"pli": "nimminiṁ mama attanā; ", "eng": "substituting myself: ", "html": "<span class='verse-line'>{}</span>"}, "cp16:11.3": {"pli": "‘Tiṭṭhateso mahārāja, ", "eng": "‘Let him be, great king. ", "html": "<span class='verse-line'>{}</span>"}, "cp16:11.4": {"pli": "kāmakāro bhavāmi te’. ", "eng": "I shall carry out your pleasure.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp16:12.1": {"pli": "Anurakkhiṁ mama sīlaṁ, ", "eng": "I guarded my ethics, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp16:12.2": {"pli": "nārakkhiṁ mama jīvitaṁ; ", "eng": "not my life. ", "html": "<span class='verse-line'>{}</span>"}, "cp16:12.3": {"pli": "Sīlavā hi tadā āsiṁ, ", "eng": "For then I was ethical, ", "html": "<span class='verse-line'>{}</span>"}, "cp16:12.4": {"pli": "bodhiyāyeva kāraṇā”ti. ", "eng": "because it was solely for awakening.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp16:13.1": {"pli": "Rurumigarājacariyaṁ chaṭṭhaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp17": {"cp17:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp17'><header><ul><li class='division'>{}</li>"}, "cp17:0.2": {"pli": "Hatthināgavagga ", "eng": "The Chapter on an Elephant ", "html": "<li>{}</li>"}, "cp17:0.3": {"pli": "Sīlapāramī 7 ", "eng": "The Perfection of Ethics (7th) ", "html": "<li>{}</li></ul>"}, "cp17:0.4": {"pli": "Mātaṅgacariya ", "eng": "Mātaṅga’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp17:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp17:1.2": {"pli": "jaṭilo uggatāpano; ", "eng": "a matted-hair ascetic of severe penance, ", "html": "<span class='verse-line'>{}</span>"}, "cp17:1.3": {"pli": "Mātaṅgo nāma nāmena, ", "eng": "Mātaṅga was my name, ", "html": "<span class='verse-line'>{}</span>"}, "cp17:1.4": {"pli": "sīlavā susamāhito. ", "eng": "I was virtuous and serene. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp17:2.1": {"pli": "Ahañca brāhmaṇo eko, ", "eng": "A brahmin and I both lived ", "html": "<p><span class='verse-line'>{}</span>"}, "cp17:2.2": {"pli": "gaṅgākūle vasāmubho; ", "eng": "on a bank of the Ganges. ", "html": "<span class='verse-line'>{}</span>"}, "cp17:2.3": {"pli": "Ahaṁ vasāmi upari, ", "eng": "I stayed upstream, ", "html": "<span class='verse-line'>{}</span>"}, "cp17:2.4": {"pli": "heṭṭhā vasati brāhmaṇo. ", "eng": "the brahmin downstream. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp17:3.1": {"pli": "Vicaranto anukūlamhi, ", "eng": "Wandering along the riverbank, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp17:3.2": {"pli": "uddhaṁ me assamaddasa; ", "eng": "he saw my hermitage upstream. ", "html": "<span class='verse-line'>{}</span>"}, "cp17:3.3": {"pli": "Tattha maṁ paribhāsetvā, ", "eng": "Then he abused and cursed me, ", "html": "<span class='verse-line'>{}</span>"}, "cp17:3.4": {"pli": "abhisapi muddhaphālanaṁ. ", "eng": "that my head would split in seven. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp17:4.1": {"pli": "Yadihaṁ tassa pakuppeyyaṁ, ", "eng": "If I were upset with him ", "html": "<p><span class='verse-line'>{}</span>"}, "cp17:4.2": {"pli": "yadi sīlaṁ na gopaye; ", "eng": "and if I were not taking care of my ethics, ", "html": "<span class='verse-line'>{}</span>"}, "cp17:4.3": {"pli": "Oloketvānahaṁ tassa, ", "eng": "then just with a glance ", "html": "<span class='verse-line'>{}</span>"}, "cp17:4.4": {"pli": "kareyyaṁ chārikaṁ viya. ", "eng": "I could have reduced him to ashes. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp17:5.1": {"pli": "Yaṁ so tadā maṁ abhisapi, ", "eng": "But when he cursed me then, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp17:5.2": {"pli": "kupito duṭṭhamānaso; ", "eng": "so angry and bitter-hearted, ", "html": "<span class='verse-line'>{}</span>"}, "cp17:5.3": {"pli": "Tasseva matthake nipati, ", "eng": "it fell right back on his own head: ", "html": "<span class='verse-line'>{}</span>"}, "cp17:5.4": {"pli": "yogena taṁ pamocayiṁ. ", "eng": "I freed him from that yoke. ", "html": "<span class='verse-line'>{}</span></p>", "comm": "This again requires knowledge of the Jātaka (<a href='https://suttacentral.net/ja497/en/sujato'>Ja 497</a>). The commentary says he placed a lump of clay on the wicked brahmin’s head, which bore the brunt of the curse. "}, "cp17:6.1": {"pli": "Anurakkhiṁ mama sīlaṁ, ", "eng": "I guarded my ethics, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp17:6.2": {"pli": "nārakkhiṁ mama jīvitaṁ; ", "eng": "not my life. ", "html": "<span class='verse-line'>{}</span>"}, "cp17:6.3": {"pli": "Sīlavā hi tadā āsiṁ, ", "eng": "For then I was ethical, ", "html": "<span class='verse-line'>{}</span>"}, "cp17:6.4": {"pli": "bodhiyāyeva kāraṇā”ti. ", "eng": "because it was solely for awakening.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp17:7.1": {"pli": "Mātaṅgacariyaṁ sattamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp18": {"cp18:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp18'><header><ul><li class='division'>{}</li>"}, "cp18:0.2": {"pli": "Hatthināgavagga ", "eng": "The Chapter on an Elephant ", "html": "<li>{}</li>"}, "cp18:0.3": {"pli": "Sīlapāramī 8 ", "eng": "The Perfection of Ethics (8th) ", "html": "<li>{}</li></ul>"}, "cp18:0.4": {"pli": "Dhammadevaputtacariya ", "eng": "The Deity Dhamma’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp18:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp18:1.2": {"pli": "mahāpakkho mahiddhiko; ", "eng": "of great retinue and psychic power, ", "html": "<span class='verse-line'>{}</span>"}, "cp18:1.3": {"pli": "Dhammo nāma mahāyakkho, ", "eng": "a great spirit named Dhamma, ", "html": "<span class='verse-line'>{}</span>"}, "cp18:1.4": {"pli": "sabbalokānukampako. ", "eng": "I had sympathy for the whole world. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp18:2.1": {"pli": "Dasakusalakammapathe, ", "eng": "Establishing the populace ", "html": "<p><span class='verse-line'>{}</span>"}, "cp18:2.2": {"pli": "samādapento mahājanaṁ; ", "eng": "in the ten ways of skillful deeds, ", "html": "<span class='verse-line'>{}</span>"}, "cp18:2.3": {"pli": "Carāmi gāmanigamaṁ, ", "eng": "I wandered from village to town, ", "html": "<span class='verse-line'>{}</span>"}, "cp18:2.4": {"pli": "samitto saparijjano. ", "eng": "with friends and associates. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp18:3.1": {"pli": "Pāpo kadariyo yakkho, ", "eng": "A wicked, miserly spirit ", "html": "<p><span class='verse-line'>{}</span>"}, "cp18:3.2": {"pli": "dīpento dasa pāpake; ", "eng": "promoting the ten wicked ways ", "html": "<span class='verse-line'>{}</span>"}, "cp18:3.3": {"pli": "Sopettha mahiyā carati, ", "eng": "was also wandering the earth ", "html": "<span class='verse-line'>{}</span>"}, "cp18:3.4": {"pli": "samitto saparijjano. ", "eng": "with friends and associates. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp18:4.1": {"pli": "Dhammavādī adhammo ca, ", "eng": "The righteous and unrighteous, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp18:4.2": {"pli": "ubho paccanikā mayaṁ; ", "eng": "we were each other’s foe. ", "html": "<span class='verse-line'>{}</span>"}, "cp18:4.3": {"pli": "Dhure dhuraṁ ghaṭṭayantā, ", "eng": "Slamming chariot shafts, ", "html": "<span class='verse-line'>{}</span>"}, "cp18:4.4": {"pli": "samimhā paṭipathe ubho. ", "eng": "we collided together. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp18:5.1": {"pli": "Kalaho vattatī bhesmā, ", "eng": "A dreadful quarrel ensued ", "html": "<p><span class='verse-line'>{}</span>"}, "cp18:5.2": {"pli": "kalyāṇapāpakassa ca; ", "eng": "between the good and the ill, ", "html": "<span class='verse-line'>{}</span>"}, "cp18:5.3": {"pli": "Maggā okkamanatthāya, ", "eng": "a great war loomed ", "html": "<span class='verse-line'>{}</span>"}, "cp18:5.4": {"pli": "mahāyuddho upaṭṭhito. ", "eng": "aiming to push the other off the road. ", "html": "<span class='verse-line'>{}</span></p>", "comm": "According to the Jātaka, the conflict took place in the sky (<a href='https://suttacentral.net/ja457/en/sujato'>Ja 457</a>). "}, "cp18:6.1": {"pli": "Yadihaṁ tassa kuppeyyaṁ, ", "eng": "If I had been upset with him, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp18:6.2": {"pli": "yadi bhinde tapoguṇaṁ; ", "eng": "if I had broken my asceticism, ", "html": "<span class='verse-line'>{}</span>"}, "cp18:6.3": {"pli": "Sahaparijanaṁ tassa, ", "eng": "I could have reduced him to dust, ", "html": "<span class='verse-line'>{}</span>"}, "cp18:6.4": {"pli": "rajabhūtaṁ kareyyahaṁ. ", "eng": "together with his companions. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp18:7.1": {"pli": "Api cāhaṁ sīlarakkhāya, ", "eng": "Nevertheless, for the sake of guarding my ethics, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp18:7.2": {"pli": "nibbāpetvāna mānasaṁ; ", "eng": "I quelled my heart. ", "html": "<span class='verse-line'>{}</span>"}, "cp18:7.3": {"pli": "Saha janenokkamitvā, ", "eng": "I left the road with my folk, ", "html": "<span class='verse-line'>{}</span>"}, "cp18:7.4": {"pli": "pathaṁ pāpassa dāsahaṁ. ", "eng": "giving it to the wicked one. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp18:8.1": {"pli": "Saha pathato okkante, ", "eng": "As soon as I stepped down off the path, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp18:8.2": {"pli": "katvā cittassa nibbutiṁ; ", "eng": "and I had quelled my mind, ", "html": "<span class='verse-line'>{}</span>"}, "cp18:8.3": {"pli": "Vivaraṁ adāsi pathavī, ", "eng": "the earth opened up ", "html": "<span class='verse-line'>{}</span>"}, "cp18:8.4": {"pli": "pāpayakkhassa tāvade”ti. ", "eng": "for that wicked spirit.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp18:9.1": {"pli": "Dhammadevaputtacariyaṁ aṭṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp19": {"cp19:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp19'><header><ul><li class='division'>{}</li>"}, "cp19:0.2": {"pli": "Hatthināgavagga ", "eng": "The Chapter on an Elephant ", "html": "<li>{}</li>"}, "cp19:0.3": {"pli": "Sīlapāramī 9 ", "eng": "The Perfection of Ethics (9th) ", "html": "<li>{}</li></ul>"}, "cp19:0.4": {"pli": "Alīnasattucariya ", "eng": "Alīnasattu’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp19:1.1": {"pli": "“Pañcālaraṭṭhe nagaravare, ", "eng": "“In the fine city of kingdom of Pañcāla, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp19:1.2": {"pli": "Kapilāyaṁ puruttame; ", "eng": "in the capital city of Kapilā, ", "html": "<span class='verse-line'>{}</span>"}, "cp19:1.3": {"pli": "Rājā jayaddiso nāma, ", "eng": "the king named Jayaddisa ", "html": "<span class='verse-line'>{}</span>"}, "cp19:1.4": {"pli": "Sīlaguṇamupāgato. ", "eng": "was furnished with virtuous qualities. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp19:2.1": {"pli": "Tassa rañño ahaṁ putto, ", "eng": "I was that king’s son, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp19:2.2": {"pli": "Sutadhammo susīlavā; ", "eng": "well educated and virtuous; ", "html": "<span class='verse-line'>{}</span>"}, "cp19:2.3": {"pli": "Alīnasatto guṇavā, ", "eng": "Alīnasattu of fine qualities, ", "html": "<span class='verse-line'>{}</span>"}, "cp19:2.4": {"pli": "Anurakkhaparijano sadā. ", "eng": "I always cared for the attendants. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp19:3.1": {"pli": "Pitā me migavaṁ gantvā, ", "eng": "My father had gone deer hunting ", "html": "<p><span class='verse-line'>{}</span>"}, "cp19:3.2": {"pli": "porisādaṁ upāgami; ", "eng": "when he came across a cannibal, ", "html": "<span class='verse-line'>{}</span>"}, "cp19:3.3": {"pli": "So me pitumaggahesi, ", "eng": "who seized my father, saying, ", "html": "<span class='verse-line'>{}</span>"}, "cp19:3.4": {"pli": "‘bhakkhosi mama mā cali’. ", "eng": "‘You are my prey, don’t move!’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp19:4.1": {"pli": "Tassa taṁ vacanaṁ sutvā, ", "eng": "When he heard these words, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp19:4.2": {"pli": "bhīto tasitavedhito; ", "eng": "he was scared and trembled with fear, ", "html": "<span class='verse-line'>{}</span>"}, "cp19:4.3": {"pli": "Ūrukkhambho ahu tassa, ", "eng": "his thighs became stiff ", "html": "<span class='verse-line'>{}</span>"}, "cp19:4.4": {"pli": "disvāna porisādakaṁ. ", "eng": "when he saw the cannibal. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp19:5.1": {"pli": "Migavaṁ gahetvā muñcassu, ", "eng": "‘Let me go, take the deer meat!’ he said, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp19:5.2": {"pli": "katvā āgamanaṁ puna; ", "eng": "‘I promise to return.’ ", "html": "<span class='verse-line'>{}</span>"}, "cp19:5.3": {"pli": "Brāhmaṇassa dhanaṁ datvā, ", "eng": "Having paid the brahmin, ", "html": "<span class='verse-line'>{}</span>", "comm": "This references a detail in the Jātaka (<a href='https://suttacentral.net/ja513/en/sujato'>Ja 513</a>). Before the hunt started, a brahmin they met on the road recited four verses, and the king promised that he would be paid. "}, "cp19:5.4": {"pli": "pitā āmantayī mamaṁ. ", "eng": "my father said to me: ", "html": "<span class='verse-line'>{}</span></p>"}, "cp19:6.1": {"pli": "‘Rajjaṁ putta paṭipajja, ", "eng": "‘Son, rule the realm. ", "html": "<p><span class='verse-line'>{}</span>"}, "cp19:6.2": {"pli": "mā pamajji puraṁ idaṁ; ", "eng": "Do not neglect this city. ", "html": "<span class='verse-line'>{}</span>"}, "cp19:6.3": {"pli": "Kataṁ me porisādena, ", "eng": "I promised the cannibal ", "html": "<span class='verse-line'>{}</span>"}, "cp19:6.4": {"pli": "mama āgamanaṁ puna’. ", "eng": "that I will return.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp19:7.1": {"pli": "Mātāpitū ca vanditvā, ", "eng": "I bowed to my mother and father, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp19:7.2": {"pli": "nimminitvāna attanā; ", "eng": "and substituted myself. ", "html": "<span class='verse-line'>{}</span>"}, "cp19:7.3": {"pli": "Nikkhipitvā dhanuṁ khaggaṁ, ", "eng": "Tossing my bow and sword, ", "html": "<span class='verse-line'>{}</span>"}, "cp19:7.4": {"pli": "porisādaṁ upāgamiṁ. ", "eng": "I approached the cannibal. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp19:8.1": {"pli": "Sasatthahatthūpagataṁ, ", "eng": "Perhaps if I have weapons in hand ", "html": "<p><span class='verse-line'>{}</span>"}, "cp19:8.2": {"pli": "kadāci so tasissati; ", "eng": "that might make him afraid. ", "html": "<span class='verse-line'>{}</span>"}, "cp19:8.3": {"pli": "Tena bhijjissati sīlaṁ, ", "eng": "And that would violate my ethics, ", "html": "<span class='verse-line'>{}</span>"}, "cp19:8.4": {"pli": "parittāsaṁ kate mayi. ", "eng": "were I to frighten him. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp19:9.1": {"pli": "Sīlakhaṇḍabhayā mayhaṁ, ", "eng": "For fear of violating my ethics, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp19:9.2": {"pli": "tassa dessaṁ na byāhariṁ; ", "eng": "I said nothing hateful to him. ", "html": "<span class='verse-line'>{}</span>"}, "cp19:9.3": {"pli": "Mettacitto hitavādī, ", "eng": "Lovingly and beneficially, ", "html": "<span class='verse-line'>{}</span>"}, "cp19:9.4": {"pli": "idaṁ vacanamabraviṁ. ", "eng": "I said the following. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp19:10.1": {"pli": "‘Ujjālehi mahāaggiṁ, ", "eng": "‘Kindle a great fire. ", "html": "<p><span class='verse-line'>{}</span>"}, "cp19:10.2": {"pli": "papatissāmi rukkhato; ", "eng": "I will drop into it from a tree. ", "html": "<span class='verse-line'>{}</span>"}, "cp19:10.3": {"pli": "Tvaṁ pakkakālamaññāya, ", "eng": "When you know I am cooked, ", "html": "<span class='verse-line'>{}</span>"}, "cp19:10.4": {"pli": "bhakkhaya maṁ pitāmaha’. ", "eng": "eat me, Grandfather.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp19:11.1": {"pli": "Iti sīlavataṁ hetu, ", "eng": "And so for the sake of my ethical vows ", "html": "<p><span class='verse-line'>{}</span>"}, "cp19:11.2": {"pli": "nārakkhiṁ mama jīvitaṁ; ", "eng": "I did not protect my life. ", "html": "<span class='verse-line'>{}</span>"}, "cp19:11.3": {"pli": "Pabbājesiṁ cahaṁ tassa, ", "eng": "And I drove out forever ", "html": "<span class='verse-line'>{}</span>"}, "cp19:11.4": {"pli": "sadā pāṇātipātikan”ti. ", "eng": "his attacks on living creatures.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp19:12.1": {"pli": "Alīnasattucariyaṁ navamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp20": {"cp20:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp20'><header><ul><li class='division'>{}</li>"}, "cp20:0.2": {"pli": "Hatthināgavagga ", "eng": "The Chapter on an Elephant ", "html": "<li>{}</li>"}, "cp20:0.3": {"pli": "Sīlapāramī 10 ", "eng": "The Perfection of Ethics (10th) ", "html": "<li>{}</li></ul>"}, "cp20:0.4": {"pli": "Saṅkhapālacariya ", "eng": "Saṅkhapāla’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp20:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp20:1.2": {"pli": "saṅkhapālo mahiddhiko; ", "eng": "Saṅkhapāla, a dragon of great psychic power, ", "html": "<span class='verse-line'>{}</span>"}, "cp20:1.3": {"pli": "Dāṭhāvudho ghoraviso, ", "eng": "deadly-fanged, lethally venomous, ", "html": "<span class='verse-line'>{}</span>"}, "cp20:1.4": {"pli": "dvijivho uragādhibhū. ", "eng": "two-tongued, lord of serpents. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp20:2.1": {"pli": "Catuppathe mahāmagge, ", "eng": "At a crossroad on a highway ", "html": "<p><span class='verse-line'>{}</span>"}, "cp20:2.2": {"pli": "nānājanasamākule; ", "eng": "crowded with many people, ", "html": "<span class='verse-line'>{}</span>"}, "cp20:2.3": {"pli": "Caturo aṅge adhiṭṭhāya, ", "eng": "I resolved on the four factors, ", "html": "<span class='verse-line'>{}</span>"}, "cp20:2.4": {"pli": "tattha vāsamakappayiṁ. ", "eng": "and made my home there. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp20:3.1": {"pli": "Chaviyā cammena maṁsena, ", "eng": "‘My outer skin and inner, flesh, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp20:3.2": {"pli": "Nahāruaṭṭhikehi vā; ", "eng": "sinews and bones: ", "html": "<span class='verse-line'>{}</span>"}, "cp20:3.3": {"pli": "Yassa etena karaṇīyaṁ, ", "eng": "whoever has use for these, ", "html": "<span class='verse-line'>{}</span>"}, "cp20:3.4": {"pli": "Dinnaṁyeva harātu so. ", "eng": "they are already given, please take them.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp20:4.1": {"pli": "Addasaṁsu bhojaputtā, ", "eng": "Seeing me, the Bhojans, ", "html": "<p><span class='verse-line'>{}</span>", "comm": "People from the country of Bhoja in the Vindhya range. In Sanskrit texts they are renowned for cruelty, being devotees of Durga. "}, "cp20:4.2": {"pli": "kharā luddā akāruṇā; ", "eng": "those violent and pitiless hunters, ", "html": "<span class='verse-line'>{}</span>"}, "cp20:4.3": {"pli": "Upagañchuṁ mamaṁ tattha, ", "eng": "came to me there ", "html": "<span class='verse-line'>{}</span>"}, "cp20:4.4": {"pli": "daṇḍamuggarapāṇino. ", "eng": "with sticks and hammers in their hands. ", "html": "<span class='verse-line'>{}</span></p>", "comm": "At <a href='https://suttacentral.net/pli-tv-bu-vb-np4/en/sujato#2.1.16'>Bu NP 4:2.1.16</a>, a <i lang='pi' translate='no'>muggara</i> is used to beat died cloth. "}, "cp20:5.1": {"pli": "Nāsāya vinivijjhitvā, ", "eng": "Having pierced my nose, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp20:5.2": {"pli": "naṅguṭṭhe piṭṭhikaṇṭake; ", "eng": "tail, and spine, ", "html": "<span class='verse-line'>{}</span>"}, "cp20:5.3": {"pli": "Kāje āropayitvāna, ", "eng": "and fastened me to a pole, ", "html": "<span class='verse-line'>{}</span>"}, "cp20:5.4": {"pli": "bhojaputtā hariṁsu maṁ. ", "eng": "the Bhojans carried me away. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp20:6.1": {"pli": "Sasāgarantaṁ pathaviṁ, ", "eng": "The earth from sea to sea, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp20:6.2": {"pli": "sakānanaṁ sapabbataṁ; ", "eng": "with its forests and mountains—", "html": "<span class='verse-line'>{}</span>"}, "cp20:6.3": {"pli": "Icchamāno cahaṁ tattha, ", "eng": "had I wished, right there ", "html": "<span class='verse-line'>{}</span>"}, "cp20:6.4": {"pli": "nāsāvātena jhāpaye. ", "eng": "I could have burnt it up with a blast from my nose. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp20:7.1": {"pli": "Sūlehi vinivijjhante, ", "eng": "Though pierced with stakes, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp20:7.2": {"pli": "Koṭṭayantepi sattibhi; ", "eng": "and stabbed with knives, ", "html": "<span class='verse-line'>{}</span>"}, "cp20:7.3": {"pli": "Bhojaputte na kuppāmi, ", "eng": "I did not get upset with the Bhojans: ", "html": "<span class='verse-line'>{}</span>"}, "cp20:7.4": {"pli": "Esā me sīlapāramī”ti. ", "eng": "this is my perfection of ethics.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp20:8.1": {"pli": "Saṅkhapālacariyaṁ dasamaṁ. ", "html": "<p class='endsutta'>{}</p>"}, "cp20:9.1": {"pli": "Hatthināgavaggo dutiyo. ", "html": "<p class='endsection'>{}</p>"}, "cp20:10.1": {"pli": "Tassuddānaṁ ", "html": "<p class='uddana-intro'>{}</p>"}, "cp20:11.1": {"pli": "Hatthināgo bhūridatto, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "cp20:11.2": {"pli": "campeyyo bodhi mahiṁso; ", "html": "<span class='verse-line'>{}</span>"}, "cp20:11.3": {"pli": "Ruru mātaṅgo dhammo ca, ", "html": "<span class='verse-line'>{}</span>"}, "cp20:11.4": {"pli": "atrajo ca jayaddiso. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp20:12.1": {"pli": "Ete nava sīlabalā, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp20:12.2": {"pli": "parikkhārā padesikā; ", "html": "<span class='verse-line'>{}</span>"}, "cp20:12.3": {"pli": "Jīvitaṁ parirakkhitvā, ", "html": "<span class='verse-line'>{}</span>"}, "cp20:12.4": {"pli": "sīlāni anurakkhisaṁ. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp20:13.1": {"pli": "Saṅkhapālassa me sato, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp20:13.2": {"pli": "sabbakālampi jīvitaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "cp20:13.3": {"pli": "Yassa kassaci niyyattaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "cp20:13.4": {"pli": "tasmā sā sīlapāramīti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp20:14.1": {"pli": "Sīlapāraminiddeso niṭṭhito. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp21": {"cp21:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp21'><header><ul><li class='division'>{}</li>"}, "cp21:0.2": {"pli": "Yudhañjayavagga ", "eng": "The Chapter With Yudhañjaya ", "html": "<li>{}</li>"}, "cp21:0.3": {"pli": "Nekkhammapāramī 1 ", "eng": "The Perfection of Renunciation (1st) ", "html": "<li>{}</li></ul>"}, "cp21:0.4": {"pli": "Yudhañjayacariya ", "eng": "Yudhañjaya’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp21:1.1": {"pli": "“Yadāhaṁ amitayaso, ", "eng": "“When I was Prince Yudhañjaya ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp21:1.2": {"pli": "Rājaputto yudhañjayo; ", "eng": "of infinite fame, ", "html": "<span class='verse-line'>{}</span>"}, "cp21:1.3": {"pli": "Ussāvabinduṁ sūriyātape, ", "eng": "I was stirred with urgency on seeing ", "html": "<span class='verse-line'>{}</span>"}, "cp21:1.4": {"pli": "Patitaṁ disvāna saṁvijiṁ. ", "eng": "a fallen dewdrop in the heat of the sun. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp21:2.1": {"pli": "Taññevādhipatiṁ katvā, ", "eng": "Taking that to be of prime importance, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp21:2.2": {"pli": "saṁvegamanubrūhayiṁ; ", "eng": "I fostered that urgency. ", "html": "<span class='verse-line'>{}</span>"}, "cp21:2.3": {"pli": "Mātāpitū ca vanditvā, ", "eng": "I bowed to my mother and father, ", "html": "<span class='verse-line'>{}</span>"}, "cp21:2.4": {"pli": "pabbajjamanuyācahaṁ. ", "eng": "and begged permission to go forth. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp21:3.1": {"pli": "Yācanti maṁ pañjalikā, ", "eng": "With joined palms, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp21:3.2": {"pli": "sanegamā saraṭṭhakā; ", "eng": "along with the citizens of town and country, they asked, ", "html": "<span class='verse-line'>{}</span>"}, "cp21:3.3": {"pli": "‘Ajjeva putta paṭipajja, ", "eng": "‘This very day, rule this great land, ", "html": "<span class='verse-line'>{}</span>"}, "cp21:3.4": {"pli": "iddhaṁ phītaṁ mahāmahiṁ’. ", "eng": "so successful and prosperous.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp21:4.1": {"pli": "Sarājake sahorodhe, ", "eng": "The King and the Queens, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp21:4.2": {"pli": "sanegame saraṭṭhake; ", "eng": "with the citizens of town and country, ", "html": "<span class='verse-line'>{}</span>"}, "cp21:4.3": {"pli": "Karuṇaṁ paridevante, ", "eng": "cried pitifully, ", "html": "<span class='verse-line'>{}</span>"}, "cp21:4.4": {"pli": "anapekkhova pariccajiṁ. ", "eng": "as I forsook unconcerned. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp21:5.1": {"pli": "Kevalaṁ pathaviṁ rajjaṁ, ", "eng": "Kingship over the entire earth, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp21:5.2": {"pli": "ñātiparijanaṁ yasaṁ; ", "eng": "family, retinue, and fame—", "html": "<span class='verse-line'>{}</span>"}, "cp21:5.3": {"pli": "Cajamāno na cintesiṁ, ", "eng": "I gave them up without a second thought, ", "html": "<span class='verse-line'>{}</span>"}, "cp21:5.4": {"pli": "bodhiyāyeva kāraṇā. ", "eng": "because it was solely for awakening. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp21:6.1": {"pli": "Mātāpitā na me dessā, ", "eng": "I had no dislike of my parents, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp21:6.2": {"pli": "napi me dessaṁ mahāyasaṁ; ", "eng": "nor did I dislike the great fame. ", "html": "<span class='verse-line'>{}</span>"}, "cp21:6.3": {"pli": "Sabbaññutaṁ piyaṁ mayhaṁ, ", "eng": "But because omniscience is precious to me, ", "html": "<span class='verse-line'>{}</span>"}, "cp21:6.4": {"pli": "tasmā rajjaṁ pariccajin”ti. ", "eng": "that is why I forsook kingship.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp21:7.1": {"pli": "Yudhañjayacariyaṁ paṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp22": {"cp22:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp22'><header><ul><li class='division'>{}</li>"}, "cp22:0.2": {"pli": "Yudhañjayavagga ", "eng": "The Chapter With Yudhañjaya ", "html": "<li>{}</li>"}, "cp22:0.3": {"pli": "Nekkhammapāramī 2 ", "eng": "The Perfection of Renunciation (2nd) ", "html": "<li>{}</li></ul>"}, "cp22:0.4": {"pli": "Somanassacariya ", "eng": "Somanassa’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp22:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp22:1.2": {"pli": "indapatthe puruttame; ", "eng": "in Indapatta, the capital city, ", "html": "<span class='verse-line'>{}</span>", "comm": "Modern Delhi. "}, "cp22:1.3": {"pli": "Kāmito dayito putto, ", "eng": "I was a loved and cherished child ", "html": "<span class='verse-line'>{}</span>"}, "cp22:1.4": {"pli": "somanassoti vissuto. ", "eng": "renowned as Somanassa. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp22:2.1": {"pli": "Sīlavā guṇasampanno, ", "eng": "I was ethical, full of good qualities, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp22:2.2": {"pli": "kalyāṇapaṭibhānavā; ", "eng": "of fine and eloquent speech, ", "html": "<span class='verse-line'>{}</span>"}, "cp22:2.3": {"pli": "Vuḍḍhāpacāyī hirīmā, ", "eng": "honoring elders, conscientious, ", "html": "<span class='verse-line'>{}</span>"}, "cp22:2.4": {"pli": "saṅgahesu ca kovido. ", "eng": "and expert in the methods of inclusiveness. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp22:3.1": {"pli": "Tassa rañño patikaro, ", "eng": "Now there was this charlatan renunciant ", "html": "<p><span class='verse-line'>{}</span>", "comm": "According to <a href='https://suttacentral.net/ja505/en/sujato'>Ja 505</a> commentary, he overheard proper ascetics predicting the birth of a prince. He curried favor with the king by lying that he predicted this. "}, "cp22:3.2": {"pli": "ahosi kuhakatāpaso; ", "eng": "who was favored by the king. ", "html": "<span class='verse-line'>{}</span>"}, "cp22:3.3": {"pli": "Ārāmaṁ mālāvacchañca, ", "eng": "He made a living by cultivating ", "html": "<span class='verse-line'>{}</span>"}, "cp22:3.4": {"pli": "ropayitvāna jīvati. ", "eng": "an orchard and flowering shrubs. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp22:4.1": {"pli": "Tamahaṁ disvāna kuhakaṁ, ", "eng": "Seeing he was a charlatan, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp22:4.2": {"pli": "thusarāsiṁva ataṇḍulaṁ; ", "eng": "like a pile of chaff without grain, ", "html": "<span class='verse-line'>{}</span>"}, "cp22:4.3": {"pli": "Dumaṁva anto susiraṁ, ", "eng": "like a hollow tree, ", "html": "<span class='verse-line'>{}</span>"}, "cp22:4.4": {"pli": "kadaliṁva asārakaṁ. ", "eng": "like a plantain tree with no core, I thought: ", "html": "<span class='verse-line'>{}</span></p>"}, "cp22:5.1": {"pli": "Natthimassa sataṁ dhammo, ", "eng": "‘There is no good in him, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp22:5.2": {"pli": "sāmaññāpagato ayaṁ; ", "eng": "he has strayed from asceticism. ", "html": "<span class='verse-line'>{}</span>"}, "cp22:5.3": {"pli": "Hirīsukkadhammajahito, ", "eng": "He has left the bright qualities of conscience ", "html": "<span class='verse-line'>{}</span>"}, "cp22:5.4": {"pli": "jīvitavuttikāraṇā. ", "eng": "for the sake of making a living.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp22:6.1": {"pli": "Kupito ahu paccanto, ", "eng": "Then the borders were in turmoil ", "html": "<p><span class='verse-line'>{}</span>"}, "cp22:6.2": {"pli": "aṭavīhi parantihi; ", "eng": "by wild frontier savages. ", "html": "<span class='verse-line'>{}</span>"}, "cp22:6.3": {"pli": "Taṁ nisedhetuṁ gacchanto, ", "eng": "Before going to put an end to it, ", "html": "<span class='verse-line'>{}</span>"}, "cp22:6.4": {"pli": "anusāsi pitā mamaṁ. ", "eng": "my father instructed me: ", "html": "<span class='verse-line'>{}</span></p>"}, "cp22:7.1": {"pli": "‘Mā pamajji tuvaṁ tāta, ", "eng": "‘My dear, do not neglect ", "html": "<p><span class='verse-line'>{}</span>"}, "cp22:7.2": {"pli": "jaṭilaṁ uggatāpanaṁ; ", "eng": "the matted-hair ascetic of severe penance. ", "html": "<span class='verse-line'>{}</span>"}, "cp22:7.3": {"pli": "Yadicchakaṁ pavattehi, ", "eng": "He is the granter of our wishes, ", "html": "<span class='verse-line'>{}</span>"}, "cp22:7.4": {"pli": "sabbakāmadado hi so’. ", "eng": "give him all he desires.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp22:8.1": {"pli": "Tamahaṁ gantvānupaṭṭhānaṁ, ", "eng": "I went then to serve him, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp22:8.2": {"pli": "Idaṁ vacanamabraviṁ; ", "eng": "and said the following. ", "html": "<span class='verse-line'>{}</span>"}, "cp22:8.3": {"pli": "‘Kacci te gahapati kusalaṁ, ", "eng": "‘I hope you are well, householder. ", "html": "<span class='verse-line'>{}</span>"}, "cp22:8.4": {"pli": "Kiṁ vā te āharīyatu’. ", "eng": "Is there anything I can bring for you?’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp22:9.1": {"pli": "Tena so kupito āsi, ", "eng": "At that he was upset with me, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp22:9.2": {"pli": "kuhako mānanissito; ", "eng": "that prideful charlatan. ", "html": "<span class='verse-line'>{}</span>"}, "cp22:9.3": {"pli": "‘Ghātāpemi tuvaṁ ajja, ", "eng": "‘I’ll have you killed right now! ", "html": "<span class='verse-line'>{}</span>"}, "cp22:9.4": {"pli": "raṭṭhā pabbājayāmi vā’. ", "eng": "Or banished from the realm!’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp22:10.1": {"pli": "Nisedhayitvā paccantaṁ, ", "eng": "After putting an end to the border conflict, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp22:10.2": {"pli": "rājā kuhakamabravi; ", "eng": "the king said to the charlatan, ", "html": "<span class='verse-line'>{}</span>"}, "cp22:10.3": {"pli": "‘Kacci te bhante khamanīyaṁ, ", "eng": "‘I hope you’re keeping well, sir, ", "html": "<span class='verse-line'>{}</span>"}, "cp22:10.4": {"pli": "sammāno te pavattito’. ", "eng": "and honors were served you?’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp22:11.1": {"pli": "Tassa ācikkhatī pāpo, ", "eng": "That wicked one told him ", "html": "<p><span class='verse-line'>{}</span>"}, "cp22:11.2": {"pli": "kumāro yathā nāsiyo; ", "eng": "how the prince deserved execution. ", "html": "<span class='verse-line'>{}</span>"}, "cp22:11.3": {"pli": "Tassa taṁ vacanaṁ sutvā, ", "eng": "When he heard these words, ", "html": "<span class='verse-line'>{}</span>"}, "cp22:11.4": {"pli": "āṇāpesi mahīpati. ", "eng": "the Lord of the Earth commanded: ", "html": "<span class='verse-line'>{}</span></p>"}, "cp22:12.1": {"pli": "‘Sīsaṁ tattheva chinditvā, ", "eng": "‘Cut off his head right there! ", "html": "<p><span class='verse-line'>{}</span>"}, "cp22:12.2": {"pli": "katvāna catukhaṇḍikaṁ; ", "eng": "Saw his corpse into four pieces! ", "html": "<span class='verse-line'>{}</span>"}, "cp22:12.3": {"pli": "Rathiyā rathiyaṁ dassetha, ", "eng": "And then show them from street to street: ", "html": "<span class='verse-line'>{}</span>"}, "cp22:12.4": {"pli": "sā gati jaṭilahīḷitā’. ", "eng": "this is the fate of one who shames the ascetic.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp22:13.1": {"pli": "Tattha kāraṇikā gantvā, ", "eng": "Rightaway left the punishers—", "html": "<p><span class='verse-line'>{}</span>"}, "cp22:13.2": {"pli": "caṇḍā luddā akāruṇā; ", "eng": "fierce, violent and pitiless. ", "html": "<span class='verse-line'>{}</span>"}, "cp22:13.3": {"pli": "Mātu aṅke nisinnassa, ", "eng": "They dragged me from my mother’s lap ", "html": "<span class='verse-line'>{}</span>"}, "cp22:13.4": {"pli": "ākaḍḍhitvā nayanti maṁ. ", "eng": "and led me away. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp22:14.1": {"pli": "Tesāhaṁ evamavacaṁ, ", "eng": "I said to them ", "html": "<p><span class='verse-line'>{}</span>"}, "cp22:14.2": {"pli": "bandhataṁ gāḷhabandhanaṁ; ", "eng": "as they bound me with tight bonds: ", "html": "<span class='verse-line'>{}</span>"}, "cp22:14.3": {"pli": "‘Rañño dassetha maṁ khippaṁ, ", "eng": "‘Present me to the king right now, ", "html": "<span class='verse-line'>{}</span>"}, "cp22:14.4": {"pli": "rājakiriyāni atthi me’. ", "eng": "I have an issue for the king!’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp22:15.1": {"pli": "Te maṁ rañño dassayiṁsu, ", "eng": "They presented me to the king, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp22:15.2": {"pli": "pāpassa pāpasevino; ", "eng": "follower of the wicked one and his wicked ways. ", "html": "<span class='verse-line'>{}</span>"}, "cp22:15.3": {"pli": "Disvāna taṁ saññāpesiṁ, ", "eng": "On seeing me, I persuaded him ", "html": "<span class='verse-line'>{}</span>"}, "cp22:15.4": {"pli": "mamañca vasamānayiṁ. ", "eng": "and brought him under my influence. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp22:16.1": {"pli": "So maṁ tattha khamāpesi, ", "eng": "He asked for my forgiveness then, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp22:16.2": {"pli": "mahārajjamadāsi me; ", "eng": "and offered me the kingdom. ", "html": "<span class='verse-line'>{}</span>"}, "cp22:16.3": {"pli": "Sohaṁ tamaṁ dālayitvā, ", "eng": "But having burst the darkness asunder, ", "html": "<span class='verse-line'>{}</span>"}, "cp22:16.4": {"pli": "pabbajiṁ anagāriyaṁ. ", "eng": "I went forth to homelessness. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp22:17.1": {"pli": "Na me dessaṁ mahārajjaṁ, ", "eng": "I did not dislike great kingship, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp22:17.2": {"pli": "kāmabhogo na dessiyo; ", "eng": "nor did I dislike sensual enjoyment. ", "html": "<span class='verse-line'>{}</span>"}, "cp22:17.3": {"pli": "Sabbaññutaṁ piyaṁ mayhaṁ, ", "eng": "But because omniscience is precious to me, ", "html": "<span class='verse-line'>{}</span>"}, "cp22:17.4": {"pli": "tasmā rajjaṁ pariccajin”ti. ", "eng": "that is why I forsook kingship.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp22:18.1": {"pli": "Somanassacariyaṁ dutiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp23": {"cp23:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp23'><header><ul><li class='division'>{}</li>"}, "cp23:0.2": {"pli": "Yudhañjayavagga ", "eng": "The Chapter With Yudhañjaya ", "html": "<li>{}</li>"}, "cp23:0.3": {"pli": "Nekkhammapāramī 3 ", "eng": "The Perfection of Renunciation (3rd) ", "html": "<li>{}</li></ul>"}, "cp23:0.4": {"pli": "Ayogharacariya ", "eng": "Ayoghara’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp23:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp23:1.2": {"pli": "kāsirājassa atrajo; ", "eng": "the true-born son of the king of Kāsi, ", "html": "<span class='verse-line'>{}</span>"}, "cp23:1.3": {"pli": "Ayogharamhi saṁvaḍḍho, ", "eng": "I grew up in an iron house, ", "html": "<span class='verse-line'>{}</span>", "comm": "For protection against <i lang='pi' translate='no'>yakkhas</i> who, according to <a href='https://suttacentral.net/ja510/en/sujato'>Ja 510</a>, are afraid of iron. As holdovers of the “old gods” they were threatened by the new technology of iron-smelting. "}, "cp23:1.4": {"pli": "nāmenāsi ayogharo. ", "eng": "so I was named Ayoghara. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp23:2.1": {"pli": "‘Dukkhena jīvito laddho, ", "eng": "‘Your life has been a painful one, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp23:2.2": {"pli": "saṁpīḷe patiposito; ", "eng": "as you’ve been raised in confinement. ", "html": "<span class='verse-line'>{}</span>"}, "cp23:2.3": {"pli": "Ajjeva putta paṭipajja, ", "eng": "Today, my son, rule ", "html": "<span class='verse-line'>{}</span>"}, "cp23:2.4": {"pli": "kevalaṁ vasudhaṁ imaṁ’. ", "eng": "the entirety of this land.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp23:3.1": {"pli": "Saraṭṭhakaṁ sanigamaṁ, ", "eng": "Having bowed to the citizens of country and town, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp23:3.2": {"pli": "sajanaṁ vanditva khattiyaṁ; ", "eng": "the king and his people, ", "html": "<span class='verse-line'>{}</span>"}, "cp23:3.3": {"pli": "Añjaliṁ paggahetvāna, ", "eng": "I raised my joined palms ", "html": "<span class='verse-line'>{}</span>"}, "cp23:3.4": {"pli": "idaṁ vacanamabraviṁ. ", "eng": "and said the following. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp23:4.1": {"pli": "‘Ye keci mahiyā sattā, ", "eng": "‘All the creatures of this earth—", "html": "<p><span class='verse-line'>{}</span>"}, "cp23:4.2": {"pli": "hīnamukkaṭṭhamajjhimā; ", "eng": "low, middle, or high—", "html": "<span class='verse-line'>{}</span>"}, "cp23:4.3": {"pli": "Nirārakkhā sake gehe, ", "eng": "are unprotected in their own home, ", "html": "<span class='verse-line'>{}</span>"}, "cp23:4.4": {"pli": "vaḍḍhanti sakañātibhi. ", "eng": "in which they grew up with their families. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp23:5.1": {"pli": "Idaṁ loke uttariyaṁ, ", "eng": "It was unique in the world ", "html": "<p><span class='verse-line'>{}</span>", "comm": "Commentary has <i lang='pi' translate='no'>asadisaṁ</i>, <i lang='pi' translate='no'>āveṇikaṁ</i> for <i lang='pi' translate='no'>uttariya</i>. The expected sense “superior” doesn’t fit, unless it is sarcastic. "}, "cp23:5.2": {"pli": "saṁpīḷe mama posanaṁ; ", "eng": "to be raised in confinement. ", "html": "<span class='verse-line'>{}</span>"}, "cp23:5.3": {"pli": "Ayogharamhi saṁvaḍḍho, ", "eng": "I grew up in an iron house, ", "html": "<span class='verse-line'>{}</span>"}, "cp23:5.4": {"pli": "appabhe candasūriye. ", "eng": "scarcely lit by sun or moon. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp23:6.1": {"pli": "Pūtikuṇapasampuṇṇā, ", "eng": "Filled with rotting carcass, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp23:6.2": {"pli": "muccitvā mātukucchito; ", "eng": "I escaped my mother’s womb. ", "html": "<span class='verse-line'>{}</span>"}, "cp23:6.3": {"pli": "Tato ghoratare dukkhe, ", "eng": "Then to a viler suffering ", "html": "<span class='verse-line'>{}</span>"}, "cp23:6.4": {"pli": "puna pakkhittayoghare. ", "eng": "I was tossed in an iron house. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp23:7.1": {"pli": "Yadihaṁ tādisaṁ patvā, ", "eng": "Having fallen into such ", "html": "<p><span class='verse-line'>{}</span>"}, "cp23:7.2": {"pli": "dukkhaṁ paramadāruṇaṁ; ", "eng": "appallingly dreadful suffering, ", "html": "<span class='verse-line'>{}</span>"}, "cp23:7.3": {"pli": "Rajjesu yadi rajjāmi, ", "eng": "if I desired kingships, ", "html": "<span class='verse-line'>{}</span>"}, "cp23:7.4": {"pli": "pāpānaṁ uttamo siyaṁ. ", "eng": "I’d be the worst of the worst. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp23:8.1": {"pli": "Ukkaṇṭhitomhi kāyena, ", "eng": "I’m fed up with the body, ", "html": "<p><span class='verse-line'>{}</span>", "comm": "These lines were adapted from <a href='https://suttacentral.net/thag16.1/en/sujato#14.1'>Thag 16.1:14.1</a>. "}, "cp23:8.2": {"pli": "rajjenamhi anatthiko; ", "eng": "I have no need for kingship. ", "html": "<span class='verse-line'>{}</span>"}, "cp23:8.3": {"pli": "Nibbutiṁ pariyesissaṁ, ", "eng": "I shall seek quenching ", "html": "<span class='verse-line'>{}</span>"}, "cp23:8.4": {"pli": "yattha maṁ maccu na maddiye’. ", "eng": "where Death will not crush me.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp23:9.1": {"pli": "Evāhaṁ cintayitvāna, ", "eng": "Reflecting in this way, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp23:9.2": {"pli": "viravante mahājane; ", "eng": "as the great crowd wailed, ", "html": "<span class='verse-line'>{}</span>"}, "cp23:9.3": {"pli": "Nāgova bandhanaṁ chetvā, ", "eng": "like a bull elephant bursting his ropes, ", "html": "<span class='verse-line'>{}</span>"}, "cp23:9.4": {"pli": "pāvisiṁ kānanaṁ vanaṁ. ", "eng": "I entered the forest grove. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp23:10.1": {"pli": "Mātāpitā na me dessā, ", "eng": "I had no dislike of my parents, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp23:10.2": {"pli": "napi me dessaṁ mahāyasaṁ; ", "eng": "nor did I dislike the great fame. ", "html": "<span class='verse-line'>{}</span>"}, "cp23:10.3": {"pli": "Sabbaññutaṁ piyaṁ mayhaṁ, ", "eng": "But because omniscience is precious to me, ", "html": "<span class='verse-line'>{}</span>"}, "cp23:10.4": {"pli": "tasmā rajjaṁ pariccajin”ti. ", "eng": "that is why I forsook kingship.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp23:11.1": {"pli": "Ayogharacariyaṁ tatiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp24": {"cp24:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp24'><header><ul><li class='division'>{}</li>"}, "cp24:0.2": {"pli": "Yudhañjayavagga ", "eng": "The Chapter With Yudhañjaya ", "html": "<li>{}</li>"}, "cp24:0.3": {"pli": "Nekkhammapāramī 4 ", "eng": "The Perfection of Renunciation (4th) ", "html": "<li>{}</li></ul>"}, "cp24:0.4": {"pli": "Bhisacariya ", "eng": "The Conduct of the Lotus-eaters ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp24:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>", "comm": "According to <a href='https://suttacentral.net/ja488/en/sujato'>Ja 488</a> his name was Kañcana, “golden”. "}, "cp24:1.2": {"pli": "kāsīnaṁ puravaruttame; ", "eng": "in the capital citadel of Kāsi, ", "html": "<span class='verse-line'>{}</span>"}, "cp24:1.3": {"pli": "Bhaginī ca bhātaro satta, ", "eng": "a sister and seven brothers ", "html": "<span class='verse-line'>{}</span>"}, "cp24:1.4": {"pli": "nibbattā sotthiye kule. ", "eng": "were born in a learned family. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp24:2.1": {"pli": "Etesaṁ pubbajo āsiṁ, ", "eng": "I was the first-born of them all, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp24:2.2": {"pli": "hirīsukkamupāgato; ", "eng": "furnishing with pure conscience. ", "html": "<span class='verse-line'>{}</span>"}, "cp24:2.3": {"pli": "Bhavaṁ disvāna bhayato, ", "eng": "Seeing rebirth as fearful, ", "html": "<span class='verse-line'>{}</span>"}, "cp24:2.4": {"pli": "nekkhammābhirato ahaṁ. ", "eng": "I longed for renunciation. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp24:3.1": {"pli": "Mātāpitūhi pahitā, ", "eng": "Sent for by my mother and father, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp24:3.2": {"pli": "sahāyā ekamānasā; ", "eng": "my friends with one mind ", "html": "<span class='verse-line'>{}</span>"}, "cp24:3.3": {"pli": "Kāmehi maṁ nimantenti, ", "eng": "invited me to enjoy sensual pleasures, saying: ", "html": "<span class='verse-line'>{}</span>"}, "cp24:3.4": {"pli": "‘kulavaṁsaṁ dharehi’ti. ", "eng": "‘Maintain the family lineage.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp24:4.1": {"pli": "Yaṁ tesaṁ vacanaṁ vuttaṁ, ", "eng": "The words they said ", "html": "<p><span class='verse-line'>{}</span>"}, "cp24:4.2": {"pli": "gihīdhamme sukhāvahaṁ; ", "eng": "about the ways a householder can find happiness, ", "html": "<span class='verse-line'>{}</span>"}, "cp24:4.3": {"pli": "Taṁ me ahosi kaṭhinaṁ, ", "eng": "were hard for me to hear, ", "html": "<span class='verse-line'>{}</span>"}, "cp24:4.4": {"pli": "tattaphālasamaṁ viya. ", "eng": "like a heated ploughshare. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp24:5.1": {"pli": "Te maṁ tadā ukkhipantaṁ, ", "eng": "When I turned down their proposal, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp24:5.2": {"pli": "pucchiṁsu patthitaṁ mama; ", "eng": "they asked me what I wished for: ", "html": "<span class='verse-line'>{}</span>"}, "cp24:5.3": {"pli": "‘Kiṁ tvaṁ patthayase samma, ", "eng": "‘My dear, what is it that you wish, ", "html": "<span class='verse-line'>{}</span>"}, "cp24:5.4": {"pli": "yadi kāme na bhuñjasi’. ", "eng": "since you do not long for sensual pleasures?’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp24:6.1": {"pli": "Tesāhaṁ evamavacaṁ, ", "eng": "I spoke to my beneficiaries, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp24:6.2": {"pli": "atthakāmo hitesinaṁ; ", "eng": "wanting what is best. ", "html": "<span class='verse-line'>{}</span>"}, "cp24:6.3": {"pli": "‘Nāhaṁ patthemi gihībhāvaṁ, ", "eng": "‘I wish not for the householder’s state, ", "html": "<span class='verse-line'>{}</span>"}, "cp24:6.4": {"pli": "nekkhammābhirato ahaṁ’. ", "eng": "I long for renunciation.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp24:7.1": {"pli": "Te mayhaṁ vacanaṁ sutvā, ", "eng": "Hearing my words, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp24:7.2": {"pli": "pitu mātu ca sāvayuṁ; ", "eng": "they told my mother and father . ", "html": "<span class='verse-line'>{}</span>"}, "cp24:7.3": {"pli": "Mātāpitā evamāhu, ", "eng": "My parents said this: ", "html": "<span class='verse-line'>{}</span>"}, "cp24:7.4": {"pli": "‘sabbeva pabbajāma bho’. ", "eng": "‘Sirs, let all of us go forth!’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp24:8.1": {"pli": "Ubho mātāpitā mayhaṁ, ", "eng": "So both my mother and father, ", "html": "<p><span class='verse-line'>{}</span>", "comm": "In <a href='https://suttacentral.net/ja488/en/sujato'>Ja 488</a> he renounced only after the parents died. "}, "cp24:8.2": {"pli": "bhaginī ca satta bhātaro; ", "eng": "sister and seven brothers, ", "html": "<span class='verse-line'>{}</span>"}, "cp24:8.3": {"pli": "Amitadhanaṁ chaḍḍayitvā, ", "eng": "discarding countless riches, ", "html": "<span class='verse-line'>{}</span>"}, "cp24:8.4": {"pli": "pāvisimhā mahāvanan”ti. ", "eng": "entered the great forest.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>", "comm": "This is only the preliminary to the Jātaka. They eventually are reduced to eating only lotus stalks, hence the title. "}, "cp24:9.1": {"pli": "Bhisacariyaṁ catutthaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp25": {"cp25:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp25'><header><ul><li class='division'>{}</li>"}, "cp25:0.2": {"pli": "Yudhañjayavagga ", "eng": "The Chapter With Yudhañjaya ", "html": "<li>{}</li>"}, "cp25:0.3": {"pli": "Nekkhammapāramī 5 ", "eng": "The Perfection of Renunciation (5th) ", "html": "<li>{}</li></ul>"}, "cp25:0.4": {"pli": "Soṇapaṇḍitacariya ", "eng": "Soṇa the Astute’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp25:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp25:1.2": {"pli": "nagare brahmavaḍḍhane; ", "eng": "in the city of Brahmavaḍḍhana, ", "html": "<span class='verse-line'>{}</span>", "comm": "“The Flourishing of Brahmā” was, according to the commentary, an alternate name for Benares. "}, "cp25:1.3": {"pli": "Tattha kulavare seṭṭhe, ", "eng": "I was reborn there ", "html": "<span class='verse-line'>{}</span>"}, "cp25:1.4": {"pli": "mahāsāle ajāyahaṁ. ", "eng": "in a fine, wealthy, and eminent family. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp25:2.1": {"pli": "Tadāpi lokaṁ disvāna, ", "eng": "Even so, I saw that the world ", "html": "<p><span class='verse-line'>{}</span>"}, "cp25:2.2": {"pli": "Andhībhūtaṁ tamotthaṭaṁ; ", "eng": "had become blind, smothered in darkness. ", "html": "<span class='verse-line'>{}</span>"}, "cp25:2.3": {"pli": "Cittaṁ bhavato patikuṭati, ", "eng": "My mind recoiled from rebirth, ", "html": "<span class='verse-line'>{}</span>"}, "cp25:2.4": {"pli": "Tuttavegahataṁ viya. ", "eng": "as if harshly pricked by a goad. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp25:3.1": {"pli": "Disvāna vividhaṁ pāpaṁ, ", "eng": "Seeing the many forms of wickedness, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp25:3.2": {"pli": "evaṁ cintesahaṁ tadā; ", "eng": "I though to myself: ", "html": "<span class='verse-line'>{}</span>"}, "cp25:3.3": {"pli": "‘Kadāhaṁ gehā nikkhamma, ", "eng": "‘When shall I leave the house ", "html": "<span class='verse-line'>{}</span>"}, "cp25:3.4": {"pli": "pavisissāmi kānanaṁ’. ", "eng": "and enter the forest?’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp25:4.1": {"pli": "Tadāpi maṁ nimantesuṁ, ", "eng": "Then too my relatives invited me ", "html": "<p><span class='verse-line'>{}</span>"}, "cp25:4.2": {"pli": "kāmabhogehi ñātayo; ", "eng": "to enjoy the pleasures of the senses. ", "html": "<span class='verse-line'>{}</span>"}, "cp25:4.3": {"pli": "Tesampi chandamācikkhiṁ, ", "eng": "I told them of my desire, saying, ", "html": "<span class='verse-line'>{}</span>"}, "cp25:4.4": {"pli": "‘mā nimantetha tehi maṁ’. ", "eng": "‘Do not invite me to such things!’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp25:5.1": {"pli": "Yo me kaniṭṭhako bhātā, ", "eng": "My younger brother, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp25:5.2": {"pli": "nando nāmāsi paṇḍito; ", "eng": "who was named Nanda the astute, ", "html": "<span class='verse-line'>{}</span>"}, "cp25:5.3": {"pli": "Sopi maṁ anusikkhanto, ", "eng": "followed my ethical practices, ", "html": "<span class='verse-line'>{}</span>"}, "cp25:5.4": {"pli": "pabbajjaṁ samarocayi. ", "eng": "and chose to go forth. ", "html": "<span class='verse-line'>{}</span></p>", "comm": "Horner’s “found equal pleasure” for <i lang='pi' translate='no'>samarocayi</i> is wrong. It occurs at <a href='https://suttacentral.net/thig13.4/en/sujato#11.2'>Thig 13.4:11.2</a> and <a href='https://suttacentral.net/snp3.1/en/sujato#1.4'>Snp 3.1:1.4</a> where it simply describes the decision to go forth. "}, "cp25:6.1": {"pli": "Ahaṁ soṇo ca nando ca, ", "eng": "I, Soṇa, and Nanda, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp25:6.2": {"pli": "ubho mātāpitā mama; ", "eng": "and both my mother and father, ", "html": "<span class='verse-line'>{}</span>"}, "cp25:6.3": {"pli": "Tadāpi bhoge chaḍḍetvā, ", "eng": "then discarded our riches ", "html": "<span class='verse-line'>{}</span>"}, "cp25:6.4": {"pli": "pāvisimhā mahāvanan”ti. ", "eng": "entered the great forest.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp25:7.1": {"pli": "Soṇapaṇḍitacariyaṁ pañcamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp26": {"cp26:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp26'><header><ul><li class='division'>{}</li>"}, "cp26:0.2": {"pli": "Yudhañjayavagga ", "eng": "The Chapter With Yudhañjaya ", "html": "<li>{}</li>"}, "cp26:0.3": {"pli": "Adiṭṭhānapāramī 1 ", "eng": "The Perfection of Resolve ", "html": "<li>{}</li></ul>"}, "cp26:0.4": {"pli": "Temiyacariya ", "eng": "Temiya’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp26:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp26:1.2": {"pli": "kāsirājassa atrajo; ", "eng": "the true-born son of the king of Kāsi, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:1.3": {"pli": "Mūgapakkhoti nāmena, ", "eng": "Mūgapakkha was my name, ", "html": "<span class='verse-line'>{}</span>", "comm": "“Dumb and Crippled”. "}, "cp26:1.4": {"pli": "temiyoti vadanti maṁ. ", "eng": "they called me Temiya. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp26:2.1": {"pli": "Soḷasitthisahassānaṁ, ", "eng": "None of the sixteen thousand court women ", "html": "<p><span class='verse-line'>{}</span>"}, "cp26:2.2": {"pli": "na vijjati pumo tadā; ", "eng": "had given birth to a male. ", "html": "<span class='verse-line'>{}</span>"}, "cp26:2.3": {"pli": "Ahorattānaṁ accayena, ", "eng": "After many days and nights ", "html": "<span class='verse-line'>{}</span>"}, "cp26:2.4": {"pli": "nibbatto ahamekako. ", "eng": "I was born, the only one. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp26:3.1": {"pli": "Kicchā laddhaṁ piyaṁ puttaṁ, ", "eng": "To my father I was a dear son born with difficulty, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp26:3.2": {"pli": "abhijātaṁ jutindharaṁ; ", "eng": "a light-bringer of impeccable lineage. ", "html": "<span class='verse-line'>{}</span>"}, "cp26:3.3": {"pli": "Setacchattaṁ dhārayitvāna, ", "eng": "Holding a white umbrella over my bed, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:3.4": {"pli": "sayane poseti maṁ pitā. ", "eng": "my father provided for me. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp26:4.1": {"pli": "Niddāyamāno sayanavare, ", "eng": "Sleeping on the fine bed, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp26:4.2": {"pli": "Pabujjhitvānahaṁ tadā; ", "eng": "then having awoken, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:4.3": {"pli": "Addasaṁ paṇḍaraṁ chattaṁ, ", "eng": "I saw the pale umbrella, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:4.4": {"pli": "Yenāhaṁ nirayaṁ gato. ", "eng": "by which I had gone to hell. ", "html": "<span class='verse-line'>{}</span></p>", "comm": "According to <a href='https://suttacentral.net/ja538/en/sujato'>Ja 538</a>, seeing his father meting out cruel punishments, he realized that the umbrella of royal authority meant that one day he would have to do the same and suffer the consequences. "}, "cp26:5.1": {"pli": "Saha diṭṭhassa me chattaṁ, ", "eng": "At the sight of the umbrella ", "html": "<p><span class='verse-line'>{}</span>"}, "cp26:5.2": {"pli": "tāso uppajji bheravo; ", "eng": "a terrible dread arose in me. ", "html": "<span class='verse-line'>{}</span>"}, "cp26:5.3": {"pli": "Vinicchayaṁ samāpanno, ", "eng": "I reached the decision, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:5.4": {"pli": "‘kathāhaṁ imaṁ muñcissaṁ’. ", "eng": "‘How can I be free of this?’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp26:6.1": {"pli": "Pubbasālohitā mayhaṁ, ", "eng": "A former blood-relation of mine, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp26:6.2": {"pli": "devatā atthakāminī; ", "eng": "a goddess wishing to help, ", "html": "<span class='verse-line'>{}</span>", "comm": "This phrase is identical with <a href='https://suttacentral.net/snp5.1/en/sujato#11.2'>Snp 5.1:11.2</a>. "}, "cp26:6.3": {"pli": "Sā maṁ disvāna dukkhitaṁ, ", "eng": "saw my suffering ", "html": "<span class='verse-line'>{}</span>"}, "cp26:6.4": {"pli": "tīsu ṭhānesu yojayi. ", "eng": "and enjoined me to three states. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp26:7.1": {"pli": "‘Mā paṇḍiccayaṁ vibhāvaya, ", "eng": "‘Don’t show intelligence, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp26:7.2": {"pli": "Bālamato bhava sabbapāṇinaṁ; ", "eng": "be as a fool to all creatures, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:7.3": {"pli": "Sabbo taṁ jano ocināyatu, ", "eng": "let all folk scorn you, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:7.4": {"pli": "Evaṁ tava attho bhavissati’. ", "eng": "thus you will achieve your goal.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp26:8.1": {"pli": "Evaṁ vuttāyahaṁ tassā, ", "eng": "When she had spoken, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp26:8.2": {"pli": "idaṁ vacanamabraviṁ; ", "eng": "I said the following. ", "html": "<span class='verse-line'>{}</span>"}, "cp26:8.3": {"pli": "‘Karomi te taṁ vacanaṁ, ", "eng": "‘I shall do your bidding, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:8.4": {"pli": "yaṁ tvaṁ bhaṇasi devate; ", "eng": "just as you say, goddess. ", "html": "<span class='verse-line'>{}</span>"}, "cp26:8.5": {"pli": "Atthakāmāsi me amma, ", "eng": "You want what’s best for me, mom, ", "html": "<span class='verse-line'>{}</span>", "comm": "She was his mother in a past life. "}, "cp26:8.6": {"pli": "hitakāmāsi devate’. ", "eng": "you desire my welfare, goddess.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp26:9.1": {"pli": "Tassāhaṁ vacanaṁ sutvā, ", "eng": "After hearing her words, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp26:9.2": {"pli": "sāgareva thalaṁ labhiṁ; ", "eng": "I was like one lost at sea reaching the shore. ", "html": "<span class='verse-line'>{}</span>"}, "cp26:9.3": {"pli": "Haṭṭho saṁviggamānaso, ", "eng": "I was thrilled, my mind elated, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:9.4": {"pli": "tayo aṅge adhiṭṭhahiṁ. ", "eng": "and I resolved on three factors. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp26:10.1": {"pli": "Mūgo ahosiṁ badhiro, ", "eng": "I became deaf, and dumb, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp26:10.2": {"pli": "pakkho gativivajjito; ", "eng": "and crippled, unable to walk. ", "html": "<span class='verse-line'>{}</span>"}, "cp26:10.3": {"pli": "Ete aṅge adhiṭṭhāya, ", "eng": "Resolving on these factors, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:10.4": {"pli": "vassāni soḷasaṁ vasiṁ. ", "eng": "I lived for sixteen years. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp26:11.1": {"pli": "Tato me hatthapāde ca, ", "eng": "Then they rubbed my hands and feet, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp26:11.2": {"pli": "jivhaṁ sotañca maddiya; ", "eng": "my tongue and ears as well, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:11.3": {"pli": "Anūnataṁ me passitvā, ", "eng": "and seeing no defect in me, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:11.4": {"pli": "‘kāḷakaṇṇī’ti nindisuṁ. ", "eng": "denounced me as ‘black-ear’. ", "html": "<span class='verse-line'>{}</span></p>", "comm": "<i lang='pi' translate='no'>Kāḷakaṇṇī</i> is an odd term meaning “unlucky one”. It is also a name of a dark goddess opposed to Siri, goddess of luck. Could this imply that he had a dark goddess whispering in his ear? "}, "cp26:12.1": {"pli": "Tato jānapadā sabbe, ", "eng": "Due to that, the entire country, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp26:12.2": {"pli": "senāpatipurohitā; ", "eng": "including the generals and priests, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:12.3": {"pli": "Sabbe ekamanā hutvā, ", "eng": "with one mind ", "html": "<span class='verse-line'>{}</span>"}, "cp26:12.4": {"pli": "chaḍḍanaṁ anumodisuṁ. ", "eng": "cheered as I was discarded. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp26:13.1": {"pli": "Sohaṁ tesaṁ matiṁ sutvā, ", "eng": "When I heard of their decision ", "html": "<p><span class='verse-line'>{}</span>"}, "cp26:13.2": {"pli": "haṭṭho saṁviggamānaso; ", "eng": "I was thrilled, my mind elated, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:13.3": {"pli": "‘Yassatthāya tapociṇṇo, ", "eng": "‘The goal for which I lived fervently ", "html": "<span class='verse-line'>{}</span>"}, "cp26:13.4": {"pli": "so me attho samijjhatha’. ", "eng": "I have achieved.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp26:14.1": {"pli": "Nhāpetvā anulimpitvā, ", "eng": "Bathed and oiled, ", "html": "<p><span class='verse-line'>{}</span>", "comm": "On the queen’s intercession, he was granted seven days ceremonial kingship. "}, "cp26:14.2": {"pli": "veṭhetvā rājaveṭhanaṁ; ", "eng": "with my turban wrapped, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:14.3": {"pli": "Chattena abhisiñcitvā, ", "eng": "anointed, they held the umbrella ", "html": "<span class='verse-line'>{}</span>"}, "cp26:14.4": {"pli": "kāresuṁ puraṁ padakkhiṇaṁ. ", "eng": "as they paraded me around the city. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp26:15.1": {"pli": "Sattāhaṁ dhārayitvāna, ", "eng": "After the seventh day holding the umbrella, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp26:15.2": {"pli": "uggate ravimaṇḍale; ", "eng": "when the sun’s orb had risen, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:15.3": {"pli": "Rathena maṁ nīharitvā, ", "eng": "I was taken out by chariot, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:15.4": {"pli": "sārathī vanamupāgami. ", "eng": "until the charioteer came to a wood. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp26:16.1": {"pli": "Ekokāse rathaṁ katvā, ", "eng": "Leaving the chariot in the open, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp26:16.2": {"pli": "sajjassaṁ hatthamuccito; ", "eng": "the bridled horse released from his hand, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:16.3": {"pli": "Sārathī khaṇatī kāsuṁ, ", "eng": "the charioteer dug a pit ", "html": "<span class='verse-line'>{}</span>"}, "cp26:16.4": {"pli": "nikhātuṁ pathaviyā mamaṁ. ", "eng": "to bury me in the ground. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp26:17.1": {"pli": "Adhiṭṭhitamadhiṭṭhānaṁ, ", "eng": "I was afraid for the many different ", "html": "<p><span class='verse-line'>{}</span>"}, "cp26:17.2": {"pli": "tajjento vividhakāraṇā; ", "eng": "resolves that I had undertaken, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:17.3": {"pli": "Na bhindiṁ tamadhiṭṭhānaṁ, ", "eng": "so I did not break that resolve, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:17.4": {"pli": "bodhiyāyeva kāraṇā. ", "eng": "because it was solely for awakening. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp26:18.1": {"pli": "Mātāpitā na me dessā, ", "eng": "I had no dislike of my parents, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp26:18.2": {"pli": "attā me na ca dessiyo; ", "eng": "nor did I dislike myself. ", "html": "<span class='verse-line'>{}</span>"}, "cp26:18.3": {"pli": "Sabbaññutaṁ piyaṁ mayhaṁ, ", "eng": "But because omniscience is precious to me, ", "html": "<span class='verse-line'>{}</span>"}, "cp26:18.4": {"pli": "tasmā vatamadhiṭṭhahiṁ. ", "eng": "that is why I resolved on my vow. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp26:19.1": {"pli": "Ete aṅge adhiṭṭhāya, ", "eng": "Having resolved the three factors, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp26:19.2": {"pli": "Vassāni soḷasaṁ vasiṁ; ", "eng": "I lived for sixteen years. ", "html": "<span class='verse-line'>{}</span>"}, "cp26:19.3": {"pli": "Adhiṭṭhānena me samo natthi, ", "eng": "There is no-one to equal my resolve: ", "html": "<span class='verse-line'>{}</span>"}, "cp26:19.4": {"pli": "Esā me adhiṭṭhānapāramī”ti. ", "eng": "this is my perfection of resolve.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp26:20.1": {"pli": "Temiyacariyaṁ chaṭṭhaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp27": {"cp27:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp27'><header><ul><li class='division'>{}</li>"}, "cp27:0.2": {"pli": "Yudhañjayavagga ", "eng": "The Chapter With Yudhañjaya ", "html": "<li>{}</li>"}, "cp27:0.3": {"pli": "Saccapāramī 1 ", "eng": "The Perfection of Truth (1st) ", "html": "<li>{}</li></ul>"}, "cp27:0.4": {"pli": "Kapirājacariya ", "eng": "The Monkey King’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp27:1.1": {"pli": "“Yadā ahaṁ kapi āsiṁ, ", "eng": "“When I was a monkey ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp27:1.2": {"pli": "nadīkūle darīsaye; ", "eng": "in a cleft on the riverbank, ", "html": "<span class='verse-line'>{}</span>"}, "cp27:1.3": {"pli": "Pīḷito susumārena, ", "eng": "harassed by a crocodile ", "html": "<span class='verse-line'>{}</span>"}, "cp27:1.4": {"pli": "gamanaṁ na labhāmahaṁ. ", "eng": "I could not get across. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp27:2.1": {"pli": "Yamhokāse ahaṁ ṭhatvā, ", "eng": "Where I used to land ", "html": "<p><span class='verse-line'>{}</span>"}, "cp27:2.2": {"pli": "orā pāraṁ patāmahaṁ; ", "eng": "when jumping from the near shore to the far, ", "html": "<span class='verse-line'>{}</span>"}, "cp27:2.3": {"pli": "Tatthacchi sattu vadhako, ", "eng": "there crouched my deadly foe, ", "html": "<span class='verse-line'>{}</span>"}, "cp27:2.4": {"pli": "kumbhīlo luddadassano. ", "eng": "the baleful gharial. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp27:3.1": {"pli": "So maṁ asaṁsi ‘ehī’ti, ", "eng": "Reassuring me, he said ‘Come!’ ", "html": "<p><span class='verse-line'>{}</span>"}, "cp27:3.2": {"pli": "‘ahampemī’ti taṁ vatiṁ; ", "eng": "‘I am coming,’ I said to him. ", "html": "<span class='verse-line'>{}</span>"}, "cp27:3.3": {"pli": "Tassa matthakamakkamma, ", "eng": "Stepping on his head ", "html": "<span class='verse-line'>{}</span>"}, "cp27:3.4": {"pli": "parakūle patiṭṭhahiṁ. ", "eng": "I made it to the far bank. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp27:4.1": {"pli": "Na tassa alikaṁ bhaṇitaṁ, ", "eng": "I spoke no lie to him, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp27:4.2": {"pli": "yathā vācaṁ akāsahaṁ; ", "eng": "but acted according to my word. ", "html": "<span class='verse-line'>{}</span>"}, "cp27:4.3": {"pli": "Saccena me samo natthi, ", "eng": "There is no-one to equal my truthfulness: ", "html": "<span class='verse-line'>{}</span>"}, "cp27:4.4": {"pli": "esā me saccapāramī”ti. ", "eng": "this is my perfection of truth.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp27:4.5": {"pli": "Kapirājacariyaṁ sattamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp28": {"cp28:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp28'><header><ul><li class='division'>{}</li>"}, "cp28:0.2": {"pli": "Yudhañjayavagga ", "eng": "The Chapter With Yudhañjaya ", "html": "<li>{}</li>"}, "cp28:0.3": {"pli": "Saccapāramī 2 ", "eng": "The Perfection of Truth (2nd) ", "html": "<li>{}</li></ul>"}, "cp28:0.4": {"pli": "Saccatāpasacariya ", "eng": "The Ascetic Truthful’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp28:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp28:1.2": {"pli": "Tāpaso saccasavhayo; ", "eng": "an ascetic known as Truthful, ", "html": "<span class='verse-line'>{}</span>"}, "cp28:1.3": {"pli": "Saccena lokaṁ pālesiṁ, ", "eng": "I protected the world with my truth, ", "html": "<span class='verse-line'>{}</span>"}, "cp28:1.4": {"pli": "Samaggaṁ janamakāsahan”ti. ", "eng": "uniting the people.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>", "comm": "This alludes to <a href='https://suttacentral.net/ja73/en/sujato'>Ja 73</a> Saccaṁkirajātaka. "}, "cp28:2.1": {"pli": "Saccatāpasacariyaṁ aṭṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp29": {"cp29:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp29'><header><ul><li class='division'>{}</li>"}, "cp29:0.2": {"pli": "Yudhañjayavagga ", "eng": "The Chapter With Yudhañjaya ", "html": "<li>{}</li>"}, "cp29:0.3": {"pli": "Saccapāramī 3 ", "eng": "The Perfection of Truth (3rd) ", "html": "<li>{}</li></ul>"}, "cp29:0.4": {"pli": "Vaṭṭapotakacariya ", "eng": "The Baby Quail’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp29:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp29:1.2": {"pli": "magadhe vaṭṭapotako; ", "eng": "a baby quail in Magadha, ", "html": "<span class='verse-line'>{}</span>"}, "cp29:1.3": {"pli": "Ajātapakkho taruṇo, ", "eng": "I was young, with wings unsprouted, ", "html": "<span class='verse-line'>{}</span>"}, "cp29:1.4": {"pli": "maṁsapesi kulāvake. ", "eng": "just a scrap of meat in the nest. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp29:2.1": {"pli": "Mukhatuṇḍakenāharitvā, ", "eng": "With food she brought in her beak ", "html": "<p><span class='verse-line'>{}</span>"}, "cp29:2.2": {"pli": "mātā posayatī mamaṁ; ", "eng": "my mother nurtured me. ", "html": "<span class='verse-line'>{}</span>"}, "cp29:2.3": {"pli": "Tassā phassena jīvāmi, ", "eng": "I survived through her touch, ", "html": "<span class='verse-line'>{}</span>"}, "cp29:2.4": {"pli": "natthi me kāyikaṁ balaṁ. ", "eng": "for I had no physical strength. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp29:3.1": {"pli": "Saṁvacchare gimhasamaye, ", "eng": "One year, in the hot season, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp29:3.2": {"pli": "davaḍāho padippati; ", "eng": "the forest caught fire. ", "html": "<span class='verse-line'>{}</span>"}, "cp29:3.3": {"pli": "Upagacchati amhākaṁ, ", "eng": "It came towards us, ", "html": "<span class='verse-line'>{}</span>"}, "cp29:3.4": {"pli": "pāvako kaṇhavattanī. ", "eng": "a conflagration with a blackened trail. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp29:4.1": {"pli": "Dhamadhamāiti evaṁ, ", "eng": "The fire roared and rumbled ", "html": "<p><span class='verse-line'>{}</span>"}, "cp29:4.2": {"pli": "saddāyanto mahāsikhī; ", "eng": "with great crests of flame, ", "html": "<span class='verse-line'>{}</span>"}, "cp29:4.3": {"pli": "Anupubbena jhāpento, ", "eng": "gradually burning ", "html": "<span class='verse-line'>{}</span>"}, "cp29:4.4": {"pli": "aggi mamamupāgami. ", "eng": "closer to me. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp29:5.1": {"pli": "Aggivegabhayātītā, ", "eng": "Alarmed and terrified at the fierce fire, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp29:5.2": {"pli": "tasitā mātāpitā mama; ", "eng": "my mother and father ", "html": "<span class='verse-line'>{}</span>"}, "cp29:5.3": {"pli": "Kulāvake maṁ chaḍḍetvā, ", "eng": "abandoned me in the nest, ", "html": "<span class='verse-line'>{}</span>"}, "cp29:5.4": {"pli": "attānaṁ parimocayuṁ. ", "eng": "saving themselves. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp29:6.1": {"pli": "Pāde pakkhe pajahāmi, ", "eng": "I beat my feet and wings, ", "html": "<p><span class='verse-line'>{}</span>", "comm": "Text’s <i lang='pi' translate='no'>pajahāmi</i> does not seem to fit. Commentary notes variant readings <i lang='pi' translate='no'>paṭīhāmi</i> and <i lang='pi' translate='no'>patīhāmi</i> from <i lang='pi' translate='no'>īhati</i> “to endeavor, to strive”. "}, "cp29:6.2": {"pli": "natthi me kāyikaṁ balaṁ; ", "eng": "but I had no physical strength. ", "html": "<span class='verse-line'>{}</span>"}, "cp29:6.3": {"pli": "Sohaṁ agatiko tattha, ", "eng": "When unable to leave, ", "html": "<span class='verse-line'>{}</span>"}, "cp29:6.4": {"pli": "evaṁ cintesahaṁ tadā. ", "eng": "I thought this there: ", "html": "<span class='verse-line'>{}</span></p>"}, "cp29:7.1": {"pli": "‘Yesāhaṁ upadhāveyyaṁ, ", "eng": "‘Those to whom I ought to run, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp29:7.2": {"pli": "bhīto tasitavedhito; ", "eng": "scared and trembling with fear, ", "html": "<span class='verse-line'>{}</span>"}, "cp29:7.3": {"pli": "Te maṁ ohāya pakkantā, ", "eng": "have escaped, leaving me behind. ", "html": "<span class='verse-line'>{}</span>"}, "cp29:7.4": {"pli": "kathaṁ me ajja kātave. ", "eng": "How should I act today? ", "html": "<span class='verse-line'>{}</span></p>"}, "cp29:8.1": {"pli": "Atthi loke sīlaguṇo, ", "eng": "There is the quality of virtue in the world, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp29:8.2": {"pli": "saccaṁ soceyyanuddayā; ", "eng": "truth, purity, and mercy. ", "html": "<span class='verse-line'>{}</span>"}, "cp29:8.3": {"pli": "Tena saccena kāhāmi, ", "eng": "By this truth I will make ", "html": "<span class='verse-line'>{}</span>"}, "cp29:8.4": {"pli": "saccakiriyamuttamaṁ. ", "eng": "a supreme declaration of truth.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp29:9.1": {"pli": "Āvejjetvā dhammabalaṁ, ", "eng": "Inspired by the power of Dhamma, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp29:9.2": {"pli": "saritvā pubbake jine; ", "eng": "recollecting the former Victors, ", "html": "<span class='verse-line'>{}</span>"}, "cp29:9.3": {"pli": "Saccabalamavassāya, ", "eng": "relying on the power of truth, ", "html": "<span class='verse-line'>{}</span>"}, "cp29:9.4": {"pli": "saccakiriyamakāsahaṁ. ", "eng": "I made a declaration of truth. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp29:10.1": {"pli": "Santi pakkhā apatanā, ", "eng": "‘I have wings that do not fly! ", "html": "<p><span class='verse-line'>{}</span>", "comm": "<i lang='pi' translate='no'>Me</i> is elided in these lines, per commentary. "}, "cp29:10.2": {"pli": "santi pādā avañcanā; ", "eng": "I have feet that do not walk! ", "html": "<span class='verse-line'>{}</span>"}, "cp29:10.3": {"pli": "Mātāpitā ca nikkhantā, ", "eng": "Mother and father have fled! ", "html": "<span class='verse-line'>{}</span>"}, "cp29:10.4": {"pli": "jātaveda paṭikkama’. ", "eng": "Jātaveda the fire: go back!’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp29:11.1": {"pli": "Sahasacce kate mayhaṁ, ", "eng": "As I declared this truth ", "html": "<p><span class='verse-line'>{}</span>"}, "cp29:11.2": {"pli": "Mahāpajjalito sikhī; ", "eng": "the great crested flames ", "html": "<span class='verse-line'>{}</span>"}, "cp29:11.3": {"pli": "Vajjesi soḷasakarīsāni, ", "eng": "withdrew sixteen leagues, ", "html": "<span class='verse-line'>{}</span>"}, "cp29:11.4": {"pli": "Udakaṁ patvā yathā sikhī; ", "eng": "as if they had come to water. ", "html": "<span class='verse-line'>{}</span>"}, "cp29:11.5": {"pli": "Saccena me samo natthi, ", "eng": "There is no-one to equal my truthfulness: ", "html": "<span class='verse-line'>{}</span>"}, "cp29:11.6": {"pli": "Esā me saccapāramī”ti. ", "eng": "this is my perfection of truth.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp29:12.1": {"pli": "Vaṭṭapotakacariyaṁ navamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp30": {"cp30:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp30'><header><ul><li class='division'>{}</li>"}, "cp30:0.2": {"pli": "Yudhañjayavagga ", "eng": "The Chapter With Yudhañjaya ", "html": "<li>{}</li>"}, "cp30:0.3": {"pli": "Saccapāramī 4 ", "eng": "The Perfection of Truth (4th) ", "html": "<li>{}</li></ul>"}, "cp30:0.4": {"pli": "Maccharājacariya ", "eng": "The Fish King’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp30:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp30:1.2": {"pli": "maccharājā mahāsare; ", "eng": "a fish king in a great lake, ", "html": "<span class='verse-line'>{}</span>"}, "cp30:1.3": {"pli": "Uṇhe sūriyasantāpe, ", "eng": "in the heat of the sun ", "html": "<span class='verse-line'>{}</span>"}, "cp30:1.4": {"pli": "sare udaka khīyatha. ", "eng": "the water in the lake evaporated. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp30:2.1": {"pli": "Tato kākā ca gijjhā ca, ", "eng": "Then crows and vultures, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp30:2.2": {"pli": "kaṅkā kulalasenakā; ", "eng": "herons, hawks, and falcons, ", "html": "<span class='verse-line'>{}</span>"}, "cp30:2.3": {"pli": "Bhakkhayanti divārattiṁ, ", "eng": "perched near the fish ", "html": "<span class='verse-line'>{}</span>"}, "cp30:2.4": {"pli": "macche upanisīdiya. ", "eng": "and devoured them day and night. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp30:3.1": {"pli": "Evaṁ cintesahaṁ tattha, ", "eng": "There I had the following thought, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp30:3.2": {"pli": "saha ñātīhi pīḷito; ", "eng": "being harassed with my relatives: ", "html": "<span class='verse-line'>{}</span>"}, "cp30:3.3": {"pli": "‘Kena nu kho upāyena, ", "eng": "‘By what means ", "html": "<span class='verse-line'>{}</span>"}, "cp30:3.4": {"pli": "ñātī dukkhā pamocaye’. ", "eng": "can I free my relatives from this suffering?’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp30:4.1": {"pli": "Vicintayitvā dhammatthaṁ, ", "eng": "Considering the value of the Dhamma, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp30:4.2": {"pli": "saccaṁ addasa passayaṁ; ", "eng": "I saw the truth as a support. ", "html": "<span class='verse-line'>{}</span>"}, "cp30:4.3": {"pli": "Sacce ṭhatvā pamocesiṁ, ", "eng": "Standing in the truth, ", "html": "<span class='verse-line'>{}</span>"}, "cp30:4.4": {"pli": "ñātīnaṁ taṁ atikkhayaṁ. ", "eng": "I freed my relatives from that holocaust. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp30:5.1": {"pli": "Anussaritvā sataṁ dhammaṁ, ", "eng": "Recollecting the true Dhamma, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp30:5.2": {"pli": "paramatthaṁ vicintayaṁ; ", "eng": "considered the ultimate goal, ", "html": "<span class='verse-line'>{}</span>"}, "cp30:5.3": {"pli": "Akāsiṁ saccakiriyaṁ, ", "eng": "I made a declaration of truth ", "html": "<span class='verse-line'>{}</span>"}, "cp30:5.4": {"pli": "yaṁ loke dhuvasassataṁ. ", "eng": "that would last eternally in the world. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp30:6.1": {"pli": "‘Yato sarāmi attānaṁ, ", "eng": "‘So long as I can recall myself, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp30:6.2": {"pli": "yato pattosmi viññutaṁ; ", "eng": "since I became aware, ", "html": "<span class='verse-line'>{}</span>"}, "cp30:6.3": {"pli": "Nābhijānāmi sañcicca, ", "eng": "I do not recall deliberately ", "html": "<span class='verse-line'>{}</span>"}, "cp30:6.4": {"pli": "ekapāṇampi hiṁsitaṁ. ", "eng": "harming even a single living creature. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp30:7.1": {"pli": "Etena saccavajjena, ", "eng": "By this declaration of truth, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp30:7.2": {"pli": "pajjunno abhivassatu; ", "eng": "may Pajjuna rain down! ", "html": "<span class='verse-line'>{}</span>"}, "cp30:7.3": {"pli": "Abhitthanaya pajjunna, ", "eng": "Thunder forth, Pajjuna! ", "html": "<span class='verse-line'>{}</span>", "comm": "This verse also in <a href='https://suttacentral.net/ja75/en/sujato'>Ja 75</a>. "}, "cp30:7.4": {"pli": "nidhiṁ kākassa nāsaya; ", "eng": "Disperse the trove of the crow! ", "html": "<span class='verse-line'>{}</span>"}, "cp30:7.5": {"pli": "Kākaṁ sokāya randhehi, ", "eng": "Subject the crow to sorrow, ", "html": "<span class='verse-line'>{}</span>", "comm": "It seems a little immoderate. "}, "cp30:7.6": {"pli": "macche sokā pamocaya’. ", "eng": "and free the fish from sorrow!’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp30:8.1": {"pli": "Saha kate saccavare, ", "eng": "As this excellent truth was declared, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp30:8.2": {"pli": "pajjunno abhigajjiya; ", "eng": "Pajjuna thundered forth. ", "html": "<span class='verse-line'>{}</span>"}, "cp30:8.3": {"pli": "Thalaṁ ninnañca pūrento, ", "eng": "In an instant the rain poured down, ", "html": "<span class='verse-line'>{}</span>"}, "cp30:8.4": {"pli": "khaṇena abhivassatha. ", "eng": "soaking the uplands and valleys. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp30:9.1": {"pli": "Evarūpaṁ saccavaraṁ, ", "eng": "Having made such supreme effort ", "html": "<p><span class='verse-line'>{}</span>"}, "cp30:9.2": {"pli": "katvā vīriyamuttamaṁ; ", "eng": "for this excellent truth, ", "html": "<span class='verse-line'>{}</span>"}, "cp30:9.3": {"pli": "Vassāpesiṁ mahāmeghaṁ, ", "eng": "I made the great cloud pour down, ", "html": "<span class='verse-line'>{}</span>"}, "cp30:9.4": {"pli": "saccatejabalassito; ", "eng": "relying on the force of the power of truth. ", "html": "<span class='verse-line'>{}</span>"}, "cp30:9.5": {"pli": "Saccena me samo natthi, ", "eng": "There is no-one to equal my truthfulness: ", "html": "<span class='verse-line'>{}</span>"}, "cp30:9.6": {"pli": "esā me saccapāramī”ti. ", "eng": "this is my perfection of truth.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp30:10.1": {"pli": "Maccharājacariyaṁ dasamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp31": {"cp31:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp31'><header><ul><li class='division'>{}</li>"}, "cp31:0.2": {"pli": "Yudhañjayavagga ", "eng": "The Chapter With Yudhañjaya ", "html": "<li>{}</li>"}, "cp31:0.3": {"pli": "Saccapāramī 5 ", "eng": "The Perfection of Truth (5th) ", "html": "<li>{}</li></ul>"}, "cp31:0.4": {"pli": "Kaṇhadīpāyanacariya ", "eng": "Dark Light’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp31:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp31:1.2": {"pli": "kaṇhadīpāyano isi; ", "eng": "the seer Dark Light, ", "html": "<span class='verse-line'>{}</span>", "comm": "A “dark hermit” comparable to Asita Kaṇhasiri (or Kāladevala) of <a href='https://suttacentral.net/snp3.11/en/sujato'>Snp 3.11</a>, Asita Devala of <a href='https://suttacentral.net/mn93/en/sujato'>MN 93</a>, and Kaṇha of <a href='https://suttacentral.net/dn3/en/sujato'>DN 3</a>. "}, "cp31:1.3": {"pli": "Paropaññāsavassāni, ", "eng": "for more than fifty years ", "html": "<span class='verse-line'>{}</span>"}, "cp31:1.4": {"pli": "anabhiratocariṁ ahaṁ. ", "eng": "I lived dissatisfied. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp31:2.1": {"pli": "Na koci etaṁ jānāti, ", "eng": "No-one knew of this ", "html": "<p><span class='verse-line'>{}</span>"}, "cp31:2.2": {"pli": "Anabhiratimanaṁ mama; ", "eng": "dissatisfaction of mine, ", "html": "<span class='verse-line'>{}</span>"}, "cp31:2.3": {"pli": "Ahañhi kassaci nācikkhiṁ, ", "eng": "for I mentioned it to no-one, ", "html": "<span class='verse-line'>{}</span>"}, "cp31:2.4": {"pli": "Arati me carati mānase. ", "eng": "it only went on in my mind. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp31:3.1": {"pli": "Sabrahmacārī maṇḍabyo, ", "eng": "Maṇḍabya, a spiritual companion ", "html": "<p><span class='verse-line'>{}</span>"}, "cp31:3.2": {"pli": "sahāyo me mahāisi; ", "eng": "and friend of mine was a great seer. ", "html": "<span class='verse-line'>{}</span>"}, "cp31:3.3": {"pli": "Pubbakammasamāyutto, ", "eng": "Bound to a deed in a past life, ", "html": "<span class='verse-line'>{}</span>"}, "cp31:3.4": {"pli": "sūlamāropanaṁ labhi. ", "eng": "he got impaled on a stake. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp31:4.1": {"pli": "Tamahaṁ upaṭṭhahitvāna, ", "eng": "I nursed him ", "html": "<p><span class='verse-line'>{}</span>"}, "cp31:4.2": {"pli": "ārogyamanupāpayiṁ; ", "eng": "and brought him back to health. ", "html": "<span class='verse-line'>{}</span>"}, "cp31:4.3": {"pli": "Āpucchitvāna āgañchiṁ, ", "eng": "Asking leave, I returned ", "html": "<span class='verse-line'>{}</span>"}, "cp31:4.4": {"pli": "yaṁ mayhaṁ sakamassamaṁ. ", "eng": "to my own hermitage. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp31:5.1": {"pli": "Sahāyo brāhmaṇo mayhaṁ, ", "eng": "A brahmin friend of mine, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp31:5.2": {"pli": "bhariyaṁ ādāya puttakaṁ; ", "eng": "bringing wife and child, ", "html": "<span class='verse-line'>{}</span>"}, "cp31:5.3": {"pli": "Tayo janā samāgantvā, ", "eng": "came to me, all three of them, ", "html": "<span class='verse-line'>{}</span>"}, "cp31:5.4": {"pli": "āgañchuṁ pāhunāgataṁ. ", "eng": "as my guests. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp31:6.1": {"pli": "Sammodamāno tehi saha, ", "eng": "As I exchanged greetings with them, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp31:6.2": {"pli": "nisinno sakamassame; ", "eng": "seated in my own hermitage, ", "html": "<span class='verse-line'>{}</span>"}, "cp31:6.3": {"pli": "Dārako vaṭṭamanukkhipaṁ, ", "eng": "the boy threw a ball ", "html": "<span class='verse-line'>{}</span>"}, "cp31:6.4": {"pli": "āsīvisamakopayi. ", "eng": "and angered a viper. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp31:7.1": {"pli": "Tato so vaṭṭagataṁ maggaṁ, ", "eng": "Then the boy, looking which way ", "html": "<p><span class='verse-line'>{}</span>"}, "cp31:7.2": {"pli": "anvesanto kumārako; ", "eng": "the ball had gone, ", "html": "<span class='verse-line'>{}</span>"}, "cp31:7.3": {"pli": "Āsīvisassa hatthena, ", "eng": "touched the viper’s head ", "html": "<span class='verse-line'>{}</span>"}, "cp31:7.4": {"pli": "uttamaṅgaṁ parāmasi. ", "eng": "with his hand. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp31:8.1": {"pli": "Tassa āmasane kuddho, ", "eng": "Angered at his touch, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp31:8.2": {"pli": "sappo visabalassito; ", "eng": "the snake, relying on its potent poison, ", "html": "<span class='verse-line'>{}</span>"}, "cp31:8.3": {"pli": "Kupito paramakopena, ", "eng": "completely enraged, ", "html": "<span class='verse-line'>{}</span>"}, "cp31:8.4": {"pli": "aḍaṁsi dārakaṁ khaṇe. ", "eng": "bit the boy right away. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp31:9.1": {"pli": "Sahadaṭṭho āsīvisena, ", "eng": "When bitten by the viper, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp31:9.2": {"pli": "dārako papati bhūmiyaṁ; ", "eng": "the boy fainted on the ground. ", "html": "<span class='verse-line'>{}</span>"}, "cp31:9.3": {"pli": "Tenāhaṁ dukkhito āsiṁ, ", "eng": "It made me distraught, ", "html": "<span class='verse-line'>{}</span>"}, "cp31:9.4": {"pli": "mama vāhasi taṁ dukkhaṁ. ", "eng": "the pain became as if mine. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp31:10.1": {"pli": "Tyāhaṁ assāsayitvāna, ", "eng": "Comforting the parents, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp31:10.2": {"pli": "dukkhite sokasallite; ", "eng": "in their suffering and sorrow, ", "html": "<span class='verse-line'>{}</span>"}, "cp31:10.3": {"pli": "Paṭhamaṁ akāsiṁ kiriyaṁ, ", "eng": "I made the first act, ", "html": "<span class='verse-line'>{}</span>"}, "cp31:10.4": {"pli": "aggaṁ saccaṁ varuttamaṁ. ", "eng": "the original, supreme declaration of truth: ", "html": "<span class='verse-line'>{}</span></p>"}, "cp31:11.1": {"pli": "‘Sattāhamevāhaṁ pasannacitto, ", "eng": "‘For just seven days with a mind of faith ", "html": "<p><span class='verse-line'>{}</span>"}, "cp31:11.2": {"pli": "Puññatthiko acariṁ brahmacariyaṁ; ", "eng": "I led the spiritual life seeking merit. ", "html": "<span class='verse-line'>{}</span>"}, "cp31:11.3": {"pli": "Athāparaṁ yaṁ caritaṁ mamedaṁ, ", "eng": "My life since then, ", "html": "<span class='verse-line'>{}</span>"}, "cp31:11.4": {"pli": "Vassāni paññāsasamādhikāni. ", "eng": "for fifty years or more, ", "html": "<span class='verse-line'>{}</span></p>"}, "cp31:12.1": {"pli": "Akāmako vāhi ahaṁ carāmi, ", "eng": "I have lived unwillingly. ", "html": "<p><span class='verse-line'>{}</span>"}, "cp31:12.2": {"pli": "Etena saccena suvatthi hotu; ", "eng": "By this truth, may he be well! ", "html": "<span class='verse-line'>{}</span>"}, "cp31:12.3": {"pli": "Hataṁ visaṁ jīvatu yaññadatto’. ", "eng": "May the poison die! May Yaññadatta live!’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp31:13.1": {"pli": "Saha sacce kate mayhaṁ, ", "eng": "As I declared this truth, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp31:13.2": {"pli": "visavegena vedhito; ", "eng": "the boy trembling with poison, ", "html": "<span class='verse-line'>{}</span>"}, "cp31:13.3": {"pli": "Abujjhitvāna vuṭṭhāsi, ", "eng": "awoke and got up, ", "html": "<span class='verse-line'>{}</span>"}, "cp31:13.4": {"pli": "arogo cāsi māṇavo; ", "eng": "the brahmin youth was well. ", "html": "<span class='verse-line'>{}</span>"}, "cp31:13.5": {"pli": "Saccena me samo natthi, ", "eng": "There is no-one to equal my truthfulness: ", "html": "<span class='verse-line'>{}</span>"}, "cp31:13.6": {"pli": "esā me saccapāramī”ti. ", "eng": "this is my perfection of truth.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp31:14.1": {"pli": "Kaṇhadīpāyanacariyaṁ ekādasamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp32": {"cp32:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp32'><header><ul><li class='division'>{}</li>"}, "cp32:0.2": {"pli": "Yudhañjayavagga ", "eng": "The Chapter With Yudhañjaya ", "html": "<li>{}</li>"}, "cp32:0.3": {"pli": "Saccapāramī 6 ", "eng": "The Perfection of Truth (6th) ", "html": "<li>{}</li></ul>"}, "cp32:0.4": {"pli": "Sutasomacariya ", "eng": "Sutasoma’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp32:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp32:1.2": {"pli": "sutasomo mahīpati; ", "eng": "Sutasoma, Lord of the Earth, ", "html": "<span class='verse-line'>{}</span>"}, "cp32:1.3": {"pli": "Gahito porisādena, ", "eng": "captured by a cannibal ", "html": "<span class='verse-line'>{}</span>"}, "cp32:1.4": {"pli": "brāhmaṇe saṅgaraṁ sariṁ. ", "eng": "I remembered my promise to a brahmin. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp32:2.1": {"pli": "Khattiyānaṁ ekasataṁ, ", "eng": "A hundred aristocrat warriors ", "html": "<p><span class='verse-line'>{}</span>"}, "cp32:2.2": {"pli": "āvuṇitvā karattale; ", "eng": "he strung up by their hands . ", "html": "<span class='verse-line'>{}</span>"}, "cp32:2.3": {"pli": "Etesaṁ pamilāpetvā, ", "eng": "Having drained their blood, ", "html": "<span class='verse-line'>{}</span>"}, "cp32:2.4": {"pli": "yaññatthe upanayī mamaṁ. ", "eng": "he brought me for sacrifice. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp32:3.1": {"pli": "Apucchi maṁ porisādo, ", "eng": "The cannibal asked me, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp32:3.2": {"pli": "‘kiṁ tvaṁ icchasi nissajaṁ; ", "eng": "‘What do you wish for—release? ", "html": "<span class='verse-line'>{}</span>"}, "cp32:3.3": {"pli": "Yathāmati te kāhāmi, ", "eng": "I shall act as you think, ", "html": "<span class='verse-line'>{}</span>"}, "cp32:3.4": {"pli": "yadi me tvaṁ punehisi’. ", "eng": "so long as you will return.’ ", "html": "<span class='verse-line'>{}</span></p>"}, "cp32:4.1": {"pli": "Tassa paṭissuṇitvāna, ", "eng": "I promised him that I ", "html": "<p><span class='verse-line'>{}</span>"}, "cp32:4.2": {"pli": "paṇhe āgamanaṁ mama; ", "eng": "would return at dawn. ", "html": "<span class='verse-line'>{}</span>"}, "cp32:4.3": {"pli": "Upagantvā puraṁ rammaṁ, ", "eng": "I went to the delightful city ", "html": "<span class='verse-line'>{}</span>"}, "cp32:4.4": {"pli": "rajjaṁ niyyādayiṁ tadā. ", "eng": "and then renounced kingship. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp32:5.1": {"pli": "Anussaritvā sataṁ dhammaṁ, ", "eng": "Recollecting the true Dhamma, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp32:5.2": {"pli": "pubbakaṁ jinasevitaṁ; ", "eng": "fostered by former Victors, ", "html": "<span class='verse-line'>{}</span>"}, "cp32:5.3": {"pli": "Brāhmaṇassa dhanaṁ datvā, ", "eng": "I gave money to the brahmin, ", "html": "<span class='verse-line'>{}</span>"}, "cp32:5.4": {"pli": "porisādaṁ upāgamiṁ. ", "eng": "and returned to the cannibal. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp32:6.1": {"pli": "Natthi me saṁsayo tattha, ", "eng": "I had no doubt ", "html": "<p><span class='verse-line'>{}</span>"}, "cp32:6.2": {"pli": "Ghātayissati vā na vā; ", "eng": "that he was going to kill me. ", "html": "<span class='verse-line'>{}</span>"}, "cp32:6.3": {"pli": "Saccavācānurakkhanto, ", "eng": "Protecting my truthful speech, ", "html": "<span class='verse-line'>{}</span>"}, "cp32:6.4": {"pli": "Jīvitaṁ cajitumupāgamiṁ; ", "eng": "I went to give up my life. ", "html": "<span class='verse-line'>{}</span>"}, "cp32:6.5": {"pli": "Saccena me samo natthi, ", "eng": "There is no-one to equal my truthfulness: ", "html": "<span class='verse-line'>{}</span>"}, "cp32:6.6": {"pli": "Esā me saccapāramī”ti. ", "eng": "this is my perfection of truth.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp32:7.1": {"pli": "Sutasomacariyaṁ dvādasamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp33": {"cp33:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp33'><header><ul><li class='division'>{}</li>"}, "cp33:0.2": {"pli": "Yudhañjayavagga ", "eng": "The Chapter With Yudhañjaya ", "html": "<li>{}</li>"}, "cp33:0.3": {"pli": "Mettāpāramī 1 ", "eng": "The Perfection of Love (1st) ", "html": "<li>{}</li></ul>"}, "cp33:0.4": {"pli": "Suvaṇṇasāmacariya ", "eng": "Goldblack’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp33:1.1": {"pli": "“Sāmo yadā vane āsiṁ, ", "eng": "“When in a wood I was Sāma the black, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>", "comm": "Yet another dark hermit. "}, "cp33:1.2": {"pli": "sakkena abhinimmito; ", "eng": "created by Sakka. ", "html": "<span class='verse-line'>{}</span>"}, "cp33:1.3": {"pli": "Pavane sīhabyagghe ca, ", "eng": "I led the lions and tigers ", "html": "<span class='verse-line'>{}</span>"}, "cp33:1.4": {"pli": "mettāya mupanāmayiṁ. ", "eng": "in the forest by love. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp33:2.1": {"pli": "Sīhabyagghehi dīpīhi, ", "eng": "Surrounded by lions and tigers, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp33:2.2": {"pli": "acchehi mahisehi ca; ", "eng": "leopards, bears, buffaloes, ", "html": "<span class='verse-line'>{}</span>"}, "cp33:2.3": {"pli": "Pasadamigavarāhehi, ", "eng": "spotted deer, and wild boar, ", "html": "<span class='verse-line'>{}</span>"}, "cp33:2.4": {"pli": "parivāretvā vane vasiṁ. ", "eng": "I stayed in the forest. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp33:3.1": {"pli": "Na maṁ koci uttasati, ", "eng": "None were scared of me, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp33:3.2": {"pli": "napi bhāyāmi kassaci; ", "eng": "nor I of them. ", "html": "<span class='verse-line'>{}</span>"}, "cp33:3.3": {"pli": "Mettābalenupatthaddho, ", "eng": "Sustained by the power of love ", "html": "<span class='verse-line'>{}</span>"}, "cp33:3.4": {"pli": "ramāmi pavane tadā”ti. ", "eng": "I delighted in the forest then.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp33:4.1": {"pli": "Suvaṇṇasāmacariyaṁ terasamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp34": {"cp34:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp34'><header><ul><li class='division'>{}</li>"}, "cp34:0.2": {"pli": "Yudhañjayavagga ", "eng": "The Chapter With Yudhañjaya ", "html": "<li>{}</li>"}, "cp34:0.3": {"pli": "Mettāpāramī 2 ", "eng": "The Perfection of Love (2nd) ", "html": "<li>{}</li></ul>"}, "cp34:0.4": {"pli": "Ekarājacariya ", "eng": "The One King’s Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp34:1.1": {"pli": "“Punāparaṁ yadā homi, ", "eng": "“Then again when I was ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp34:1.2": {"pli": "ekarājāti vissuto; ", "eng": "the renowned One King, ", "html": "<span class='verse-line'>{}</span>"}, "cp34:1.3": {"pli": "Paramaṁ sīlaṁ adhiṭṭhāya, ", "eng": "resolved on highest ethics, ", "html": "<span class='verse-line'>{}</span>"}, "cp34:1.4": {"pli": "pasāsāmi mahāmahiṁ. ", "eng": "I ruled the great earth. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp34:2.1": {"pli": "Dasa kusalakammapathe, ", "eng": "Without exception I followed ", "html": "<p><span class='verse-line'>{}</span>"}, "cp34:2.2": {"pli": "vattāmi anavasesato; ", "eng": "the ten ways of skillful deeds. ", "html": "<span class='verse-line'>{}</span>"}, "cp34:2.3": {"pli": "Catūhi saṅgahavatthūhi, ", "eng": "With the four ways of being inclusive, ", "html": "<span class='verse-line'>{}</span>"}, "cp34:2.4": {"pli": "saṅgaṇhāmi mahājanaṁ. ", "eng": "I brought together the population. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp34:3.1": {"pli": "Evaṁ me appamattassa, ", "eng": "As I was being diligent, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp34:3.2": {"pli": "idha loke parattha ca; ", "eng": "for the sake of this world and the next, ", "html": "<span class='verse-line'>{}</span>"}, "cp34:3.3": {"pli": "Dabbaseno upagantvā, ", "eng": "Dabbasena attacked ", "html": "<span class='verse-line'>{}</span>"}, "cp34:3.4": {"pli": "acchindanto puraṁ mama. ", "eng": "and sacked my city. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp34:4.1": {"pli": "Rājūpajīve nigame, ", "eng": "The royal dependents in the city, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp34:4.2": {"pli": "sabalaṭṭhe saraṭṭhake; ", "eng": "with the military and the citizens—", "html": "<span class='verse-line'>{}</span>"}, "cp34:4.3": {"pli": "Sabbaṁ hatthagataṁ katvā, ", "eng": "he captured them all, ", "html": "<span class='verse-line'>{}</span>"}, "cp34:4.4": {"pli": "kāsuyā nikhaṇī mamaṁ. ", "eng": "and buried me in a pit. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp34:5.1": {"pli": "Amaccamaṇḍalaṁ rajjaṁ, ", "eng": "The court ministers, the flourishing kingdom, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp34:5.2": {"pli": "phītaṁ antepuraṁ mama; ", "eng": "and my royal court—", "html": "<span class='verse-line'>{}</span>"}, "cp34:5.3": {"pli": "Acchinditvāna gahitaṁ, ", "eng": "were captured and held, ", "html": "<span class='verse-line'>{}</span>"}, "cp34:5.4": {"pli": "piyaṁ puttaṁva passahaṁ; ", "eng": "I even saw my beloved son. ", "html": "<span class='verse-line'>{}</span>"}, "cp34:5.5": {"pli": "Mettāya me samo natthi, ", "eng": "There is no-one to equal my love: ", "html": "<span class='verse-line'>{}</span>"}, "cp34:5.6": {"pli": "esā me mettāpāramī”ti. ", "eng": "this is my perfection of love.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp34:6.1": {"pli": "Ekarājacariyaṁ cuddasamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "cp35": {"cp35:0.1": {"pli": "Cariyāpiṭaka ", "eng": "The Conduct Leading to Buddhahood ", "html": "<article id='cp35'><header><ul><li class='division'>{}</li>"}, "cp35:0.2": {"pli": "Yudhañjayavagga ", "eng": "The Chapter With Yudhañjaya ", "html": "<li>{}</li>"}, "cp35:0.3": {"pli": "Upekkhāpāramī 1 ", "eng": "The Perfection of Equanimity ", "html": "<li>{}</li></ul>"}, "cp35:0.4": {"pli": "Mahālomahaṁsacariya ", "eng": "The Great Hair-raising Conduct ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "cp35:1.1": {"pli": "“Susāne seyyaṁ kappemi, ", "eng": "“I made my bed in a charnel ground, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp35:1.2": {"pli": "Chavaṭṭhikaṁ upanidhāyahaṁ; ", "eng": "resting on a skeleton. ", "html": "<span class='verse-line'>{}</span>"}, "cp35:1.3": {"pli": "Gāmaṇḍalā upāgantvā, ", "eng": "Village louts came up ", "html": "<span class='verse-line'>{}</span>"}, "cp35:1.4": {"pli": "Rūpaṁ dassentinappakaṁ. ", "eng": "and mocked me in all sorts of ways. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp35:2.1": {"pli": "Apare gandhamālañca, ", "eng": "Others brought me ", "html": "<p><span class='verse-line'>{}</span>"}, "cp35:2.2": {"pli": "bhojanaṁ vividhaṁ bahuṁ; ", "eng": "incense and flowers, ", "html": "<span class='verse-line'>{}</span>"}, "cp35:2.3": {"pli": "Upāyanānūpanenti, ", "eng": "and many kinds of cooked food, ", "html": "<span class='verse-line'>{}</span>"}, "cp35:2.4": {"pli": "haṭṭhā saṁviggamānasā. ", "eng": "all thrilled, their minds elated. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp35:3.1": {"pli": "Ye me dukkhaṁ upaharanti, ", "eng": "Those who brought me pain, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp35:3.2": {"pli": "ye ca denti sukhaṁ mama; ", "eng": "and those who gave me happiness, ", "html": "<span class='verse-line'>{}</span>"}, "cp35:3.3": {"pli": "Sabbesaṁ samako homi, ", "eng": "were all the same to me. ", "html": "<span class='verse-line'>{}</span>"}, "cp35:3.4": {"pli": "dayā kopo na vijjati. ", "eng": "I felt no favor or anger. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp35:4.1": {"pli": "Sukhadukkhe tulābhūto, ", "eng": "I became balanced towards pleasure and pain, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp35:4.2": {"pli": "Yasesu ayasesu ca; ", "eng": "fame and disgrace. ", "html": "<span class='verse-line'>{}</span>"}, "cp35:4.3": {"pli": "Sabbattha samako homi, ", "eng": "I was equal in every case: ", "html": "<span class='verse-line'>{}</span>"}, "cp35:4.4": {"pli": "Esā me upekkhāpāramī”ti. ", "eng": "this is my perfection of equanimity.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp35:5.1": {"pli": "Mahālomahaṁsacariyaṁ pannarasamaṁ. ", "html": "<p class='endsutta'>{}</p>"}, "cp35:6.1": {"pli": "Yudhañjayavaggo tatiyo. ", "html": "<p class='endsection'>{}</p>"}, "cp35:7.1": {"pli": "Tassuddānaṁ ", "html": "<p class='uddana-intro'>{}</p>"}, "cp35:8.1": {"pli": "Yudhañjayo somanasso, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "cp35:8.2": {"pli": "ayogharabhisena ca; ", "html": "<span class='verse-line'>{}</span>"}, "cp35:8.3": {"pli": "Soṇanando mūgapakkho, ", "html": "<span class='verse-line'>{}</span>"}, "cp35:8.4": {"pli": "kapirājā saccasavhayo. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp35:9.1": {"pli": "Vaṭṭako maccharājā ca, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp35:9.2": {"pli": "kaṇhadīpāyano isi; ", "html": "<span class='verse-line'>{}</span>"}, "cp35:9.3": {"pli": "Sutasomo puna āsiṁ, ", "html": "<span class='verse-line'>{}</span>"}, "cp35:9.4": {"pli": "sāmo ca ekarājahu; ", "html": "<span class='verse-line'>{}</span>"}, "cp35:9.5": {"pli": "Upekkhāpāramī āsi, ", "html": "<span class='verse-line'>{}</span>"}, "cp35:9.6": {"pli": "iti vutthaṁ mahesinā. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp35:10.1": {"pli": "Evaṁ bahubbidhaṁ dukkhaṁ, ", "eng": "Having thus experience many kinds of suffering ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "cp35:10.2": {"pli": "sampattī ca bahubbidhā; ", "eng": "and many kinds of success, ", "html": "<span class='verse-line'>{}</span>"}, "cp35:10.3": {"pli": "Bhavābhave anubhavitvā, ", "eng": "in various past lives, ", "html": "<span class='verse-line'>{}</span>"}, "cp35:10.4": {"pli": "patto sambodhimuttamaṁ. ", "eng": "I realized supreme awakening. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp35:11.1": {"pli": "Datvā dātabbakaṁ dānaṁ, ", "eng": "I gave what could be given, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp35:11.2": {"pli": "sīlaṁ pūretvā asesato; ", "eng": "and fulfilled ethics without reserve. ", "html": "<span class='verse-line'>{}</span>"}, "cp35:11.3": {"pli": "Nekkhamme pāramiṁ gantvā, ", "eng": "having won the perfection of renunciation, ", "html": "<span class='verse-line'>{}</span>"}, "cp35:11.4": {"pli": "patto sambodhimuttamaṁ. ", "eng": "I realized supreme awakening. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp35:12.1": {"pli": "Paṇḍite paripucchitvā, ", "eng": "I inquired of the astute, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp35:12.2": {"pli": "vīriyaṁ katvāna muttamaṁ; ", "eng": "and put forth highest effort, ", "html": "<span class='verse-line'>{}</span>"}, "cp35:12.3": {"pli": "Khantiyā pāramiṁ gantvā, ", "eng": "having won the perfection of acceptance, ", "html": "<span class='verse-line'>{}</span>"}, "cp35:12.4": {"pli": "patto sambodhimuttamaṁ. ", "eng": "I realized supreme awakening. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp35:13.1": {"pli": "Katvā daḷhamadhiṭṭhānaṁ, ", "eng": "Having made a firm resolve, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp35:13.2": {"pli": "saccavācānurakkhiya; ", "eng": "and guarded truthful speech, ", "html": "<span class='verse-line'>{}</span>"}, "cp35:13.3": {"pli": "Mettāya pāramiṁ gantvā, ", "eng": "having won the perfection of love, ", "html": "<span class='verse-line'>{}</span>"}, "cp35:13.4": {"pli": "patto sambodhimuttamaṁ. ", "eng": "I realized supreme awakening. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp35:14.1": {"pli": "Lābhālābhe yasāyase, ", "eng": "In gain and loss, fame and disgrace, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp35:14.2": {"pli": "sammānanāvamānane; ", "eng": "respect and contempt, ", "html": "<span class='verse-line'>{}</span>"}, "cp35:14.3": {"pli": "Sabbattha samako hutvā, ", "eng": "having become equal in every case, ", "html": "<span class='verse-line'>{}</span>"}, "cp35:14.4": {"pli": "patto sambodhimuttamaṁ. ", "eng": "I realized supreme awakening. ", "html": "<span class='verse-line'>{}</span></p>"}, "cp35:15.1": {"pli": "Kosajjaṁ bhayato disvā, ", "eng": "Seeing laziness as fearful, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp35:15.2": {"pli": "vīriyārambhañca khemato; ", "eng": "and energy as a sanctuary, ", "html": "<span class='verse-line'>{}</span>"}, "cp35:15.3": {"pli": "Āraddhavīriyā hotha, ", "eng": "rouse up energy: ", "html": "<span class='verse-line'>{}</span>"}, "cp35:15.4": {"pli": "esā buddhānusāsanī. ", "eng": "this is the Buddhas instruction! ", "html": "<span class='verse-line'>{}</span></p>"}, "cp35:16.1": {"pli": "Vivādaṁ bhayato disvā, ", "eng": "Seeing dispute as fearful, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp35:16.2": {"pli": "avivādañca khemato; ", "eng": "and harmony as a sanctuary, ", "html": "<span class='verse-line'>{}</span>"}, "cp35:16.3": {"pli": "Samaggā sakhilā hotha, ", "eng": "be harmonious and courteous: ", "html": "<span class='verse-line'>{}</span>"}, "cp35:16.4": {"pli": "esā buddhānusāsanī. ", "eng": "this is the Buddhas instruction! ", "html": "<span class='verse-line'>{}</span></p>"}, "cp35:17.1": {"pli": "Pamādaṁ bhayato disvā, ", "eng": "Seeing negligence as fearful, ", "html": "<p><span class='verse-line'>{}</span>"}, "cp35:17.2": {"pli": "appamādañca khemato; ", "eng": "and diligence as a sanctuary, ", "html": "<span class='verse-line'>{}</span>"}, "cp35:17.3": {"pli": "Bhāvethaṭṭhaṅgikaṁ maggaṁ, ", "eng": "develop the eightfold path: ", "html": "<span class='verse-line'>{}</span>"}, "cp35:17.4": {"pli": "esā buddhānusāsanī. ", "eng": "this is the Buddhas instruction! ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "cp35:18.1": {"pli": "Itthaṁ sudaṁ bhagavā attano pubbacariyaṁ sambhāvayamāno buddhāpadāniyaṁ nāma dhammapariyāyaṁ abhāsitthāti. ", "eng": "That is how the Buddha, illustrating his own conduct in past lives, spoke the exposition of the teaching name the “Legends of the Buddha”. ", "html": "<p>{}</p>"}, "cp35:19.1": {"pli": "Cariyāpiṭakaṁ niṭṭhitaṁ. ", "html": "<p class='endbook'>{}</p></article>"}}}};