window.SUTTA_DB['sutta_kn_dhp'] = {"id": "dhp", "title": "Sayings of the Dhamma", "structure": {"dhp": ["dhp1-20", "dhp21-32", "dhp33-43", "dhp44-59", "dhp60-75", "dhp76-89", "dhp90-99", "dhp100-115", "dhp116-128", "dhp129-145", "dhp146-156", "dhp157-166", "dhp167-178", "dhp179-196", "dhp197-208", "dhp209-220", "dhp221-234", "dhp235-255", "dhp256-272", "dhp273-289", "dhp290-305", "dhp306-319", "dhp320-333", "dhp334-359", "dhp360-382", "dhp383-423"]}, "meta": {"dhp": {"type": "branch", "acronym": "Dhp", "translated_title": "Sayings of the Dhamma", "original_title": "Dhammapada", "blurb": "The Pali version of this famous text, consisting of 423 verses organized into memorable themes. It is the most widely read of the early texts, and has been translated many times into many languages. Versions of this text are found in Chinese, Tibetan, and several Indic languages, attesting to its timeless, universal appeal.", "author_uid": null}, "dhp1-20": {"type": "leaf", "acronym": "Dhp 1–20", "translated_title": "1. Pairs", "original_title": "Yamakavagga", "blurb": null, "author_uid": "sujato"}, "dhp21-32": {"type": "leaf", "acronym": "Dhp 21–32", "translated_title": "2. Diligence", "original_title": "Appamādavagga", "blurb": null, "author_uid": "sujato"}, "dhp33-43": {"type": "leaf", "acronym": "Dhp 33–43", "translated_title": "3. The Mind", "original_title": "Cittavagga", "blurb": null, "author_uid": "sujato"}, "dhp44-59": {"type": "leaf", "acronym": "Dhp 44–59", "translated_title": "4. Flowers", "original_title": "Pupphavagga", "blurb": null, "author_uid": "sujato"}, "dhp60-75": {"type": "leaf", "acronym": "Dhp 60–75", "translated_title": "5. Fools", "original_title": "Bālavagga", "blurb": null, "author_uid": "sujato"}, "dhp76-89": {"type": "leaf", "acronym": "Dhp 76–89", "translated_title": "6. The Astute", "original_title": "Paṇḍitavagga", "blurb": null, "author_uid": "sujato"}, "dhp90-99": {"type": "leaf", "acronym": "Dhp 90–99", "translated_title": "7. The Perfected Ones", "original_title": "Arahantavagga", "blurb": null, "author_uid": "sujato"}, "dhp100-115": {"type": "leaf", "acronym": "Dhp 100–115", "translated_title": "8. The Thousands", "original_title": "Sahassavagga", "blurb": null, "author_uid": "sujato"}, "dhp116-128": {"type": "leaf", "acronym": "Dhp 116–128", "translated_title": "9. Wickedness", "original_title": "Pāpavagga", "blurb": null, "author_uid": "sujato"}, "dhp129-145": {"type": "leaf", "acronym": "Dhp 129–145", "translated_title": "10. The Rod", "original_title": "Daṇḍavagga", "blurb": null, "author_uid": "sujato"}, "dhp146-156": {"type": "leaf", "acronym": "Dhp 146–156", "translated_title": "11. Old Age", "original_title": "Jarāvagga", "blurb": null, "author_uid": "sujato"}, "dhp157-166": {"type": "leaf", "acronym": "Dhp 157–166", "translated_title": "12. The Self", "original_title": "Attavagga", "blurb": null, "author_uid": "sujato"}, "dhp167-178": {"type": "leaf", "acronym": "Dhp 167–178", "translated_title": "13. The World", "original_title": "Lokavagga", "blurb": null, "author_uid": "sujato"}, "dhp179-196": {"type": "leaf", "acronym": "Dhp 179–196", "translated_title": "14. The Buddhas", "original_title": "Buddhavagga", "blurb": null, "author_uid": "sujato"}, "dhp197-208": {"type": "leaf", "acronym": "Dhp 197–208", "translated_title": "15. Happiness", "original_title": "Sukhavagga", "blurb": null, "author_uid": "sujato"}, "dhp209-220": {"type": "leaf", "acronym": "Dhp 209–220", "translated_title": "16. The Beloved", "original_title": "Piyavagga", "blurb": null, "author_uid": "sujato"}, "dhp221-234": {"type": "leaf", "acronym": "Dhp 221–234", "translated_title": "17. Anger", "original_title": "Kodhavagga", "blurb": null, "author_uid": "sujato"}, "dhp235-255": {"type": "leaf", "acronym": "Dhp 235–255", "translated_title": "18. Stains", "original_title": "Malavagga", "blurb": null, "author_uid": "sujato"}, "dhp256-272": {"type": "leaf", "acronym": "Dhp 256–272", "translated_title": "19. The Just", "original_title": "Dhammaṭṭhavagga", "blurb": null, "author_uid": "sujato"}, "dhp273-289": {"type": "leaf", "acronym": "Dhp 273–289", "translated_title": "20. The Path", "original_title": "Maggavagga", "blurb": null, "author_uid": "sujato"}, "dhp290-305": {"type": "leaf", "acronym": "Dhp 290–305", "translated_title": "21. Miscellaneous", "original_title": "Pakiṇṇakavagga", "blurb": null, "author_uid": "sujato"}, "dhp306-319": {"type": "leaf", "acronym": "Dhp 306–319", "translated_title": "22. Hell", "original_title": "Nirayavagga", "blurb": null, "author_uid": "sujato"}, "dhp320-333": {"type": "leaf", "acronym": "Dhp 320–333", "translated_title": "23. Elephants", "original_title": "Nāgavagga", "blurb": null, "author_uid": "sujato"}, "dhp334-359": {"type": "leaf", "acronym": "Dhp 334–359", "translated_title": "24. Craving", "original_title": "Taṇhāvagga", "blurb": null, "author_uid": "sujato"}, "dhp360-382": {"type": "leaf", "acronym": "Dhp 360–382", "translated_title": "25. Mendicants", "original_title": "Bhikkhuvagga", "blurb": null, "author_uid": "sujato"}, "dhp383-423": {"type": "leaf", "acronym": "Dhp 383–423", "translated_title": "26. Brahmins", "original_title": "Brāhmaṇavagga", "blurb": null, "author_uid": "sujato"}}, "content": {"dhp1-20": {"dhp1:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp1-20'><header><ul><li class='division'>{}</li>"}, "dhp1:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 1–20 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp1:0.3": {"pli": "Yamakavagga ", "eng": "1. Pairs ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp1:0.4": {"pli": "Cakkhupālattheravatthu ", "html": "<article id='dhp1'><h2 class='sutta-title'>{}</h2>"}, "dhp1:1": {"pli": "Manopubbaṅgamā dhammā, ", "eng": "Intention shapes experiences; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>", "comm": "<i lang='pi' translate='no'>Mano</i> here has its usual role as the active aspect of mind, effectively meaning <i lang='pi' translate='no'>kamma</i>. Translating as “mind” obscures the ethical reading in favor of an idealistic one, which given the remainder of the verse is surely not justified. "}, "dhp1:2": {"pli": "manoseṭṭhā manomayā; ", "eng": "intention is first, they’re made by intention. ", "html": "<span class='verse-line'>{}</span>"}, "dhp1:3": {"pli": "Manasā ce paduṭṭhena, ", "eng": "If with corrupt intent ", "html": "<span class='verse-line'>{}</span>"}, "dhp1:4": {"pli": "bhāsati vā karoti vā; ", "eng": "you speak or act, ", "html": "<span class='verse-line'>{}</span>"}, "dhp1:5": {"pli": "Tato naṁ dukkhamanveti, ", "eng": "suffering follows you, ", "html": "<span class='verse-line'>{}</span>"}, "dhp1:6": {"pli": "cakkaṁva vahato padaṁ. ", "eng": "like a wheel, the ox’s foot. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp2:0": {"pli": "Maṭṭhakuṇḍalīvatthu ", "html": "<article id='dhp2'><h2 class='sutta-title'>{}</h2>"}, "dhp2:1": {"pli": "Manopubbaṅgamā dhammā, ", "eng": "Intention shapes experiences; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp2:2": {"pli": "manoseṭṭhā manomayā; ", "eng": "intention is first, they’re made by intention. ", "html": "<span class='verse-line'>{}</span>"}, "dhp2:3": {"pli": "Manasā ce pasannena, ", "eng": "If with pure intent ", "html": "<span class='verse-line'>{}</span>"}, "dhp2:4": {"pli": "bhāsati vā karoti vā; ", "eng": "you speak or act, ", "html": "<span class='verse-line'>{}</span>"}, "dhp2:5": {"pli": "Tato naṁ sukhamanveti, ", "eng": "happiness follows you ", "html": "<span class='verse-line'>{}</span>"}, "dhp2:6": {"pli": "chāyāva anapāyinī. ", "eng": "like a shadow that never leaves. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp3:0": {"pli": "Tissattheravatthu ", "html": "<article id='dhp3'><h2 class='sutta-title'>{}</h2>"}, "dhp3:1": {"pli": "Akkocchi maṁ avadhi maṁ, ", "eng": "“They abused me, they hit me! ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp3:2": {"pli": "ajini maṁ ahāsi me; ", "eng": "They beat me, they robbed me!” ", "html": "<span class='verse-line'>{}</span>"}, "dhp3:3": {"pli": "Ye ca taṁ upanayhanti, ", "eng": "For those who bear such a grudge, ", "html": "<span class='verse-line'>{}</span>"}, "dhp3:4": {"pli": "veraṁ tesaṁ na sammati. ", "eng": "hatred is never laid to rest. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp4:1": {"pli": "Akkocchi maṁ avadhi maṁ, ", "eng": "“They abused me, they hit me! ", "html": "<article id='dhp4'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp4:2": {"pli": "ajini maṁ ahāsi me; ", "eng": "They beat me, they robbed me!” ", "html": "<span class='verse-line'>{}</span>"}, "dhp4:3": {"pli": "Ye ca taṁ nupanayhanti, ", "eng": "For those who bear no such grudge, ", "html": "<span class='verse-line'>{}</span>"}, "dhp4:4": {"pli": "veraṁ tesūpasammati. ", "eng": "hatred is laid to rest. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp5:0": {"pli": "Kāḷayakkhinīvatthu ", "html": "<article id='dhp5'><h2 class='sutta-title'>{}</h2>"}, "dhp5:1": {"pli": "Na hi verena verāni, ", "eng": "For never is hatred ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp5:2": {"pli": "sammantīdha kudācanaṁ; ", "eng": "laid to rest by hate, ", "html": "<span class='verse-line'>{}</span>"}, "dhp5:3": {"pli": "Averena ca sammanti, ", "eng": "it’s laid to rest by love: ", "html": "<span class='verse-line'>{}</span>"}, "dhp5:4": {"pli": "esa dhammo sanantano. ", "eng": "this is an ancient teaching. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp6:0": {"pli": "Kosambakavatthu ", "html": "<article id='dhp6'><h2 class='sutta-title'>{}</h2>"}, "dhp6:1": {"pli": "Pare ca na vijānanti, ", "eng": "When others do not understand, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp6:2": {"pli": "mayamettha yamāmase; ", "eng": "let us, who do understand this, ", "html": "<span class='verse-line'>{}</span>"}, "dhp6:3": {"pli": "Ye ca tattha vijānanti, ", "eng": "restrain ourselves in this regard; ", "html": "<span class='verse-line'>{}</span>"}, "dhp6:4": {"pli": "tato sammanti medhagā. ", "eng": "for that is how conflicts are laid to rest. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp7:0": {"pli": "Mahākāḷattheravatthu ", "html": "<article id='dhp7'><h2 class='sutta-title'>{}</h2>"}, "dhp7:1": {"pli": "Subhānupassiṁ viharantaṁ, ", "eng": "Those who contemplate the beautiful, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp7:2": {"pli": "indriyesu asaṁvutaṁ; ", "eng": "their faculties unrestrained, ", "html": "<span class='verse-line'>{}</span>"}, "dhp7:3": {"pli": "Bhojanamhi cāmattaññuṁ, ", "eng": "immoderate in eating, ", "html": "<span class='verse-line'>{}</span>"}, "dhp7:4": {"pli": "kusītaṁ hīnavīriyaṁ; ", "eng": "lazy, lacking energy: ", "html": "<span class='verse-line'>{}</span>"}, "dhp7:5": {"pli": "Taṁ ve pasahati māro, ", "eng": "Māra strikes them down ", "html": "<span class='verse-line'>{}</span>"}, "dhp7:6": {"pli": "vāto rukkhaṁva dubbalaṁ. ", "eng": "like the wind, a feeble tree. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp8:1": {"pli": "Asubhānupassiṁ viharantaṁ, ", "eng": "Those who contemplate the ugly, ", "html": "<article id='dhp8'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp8:2": {"pli": "Indriyesu susaṁvutaṁ; ", "eng": "their faculties well-restrained, ", "html": "<span class='verse-line'>{}</span>"}, "dhp8:3": {"pli": "Bhojanamhi ca mattaññuṁ, ", "eng": "eating in moderation, ", "html": "<span class='verse-line'>{}</span>"}, "dhp8:4": {"pli": "Saddhaṁ āraddhavīriyaṁ; ", "eng": "faithful and energetic: ", "html": "<span class='verse-line'>{}</span>"}, "dhp8:5": {"pli": "Taṁ ve nappasahati māro, ", "eng": "Māra cannot strike them down, ", "html": "<span class='verse-line'>{}</span>"}, "dhp8:6": {"pli": "Vāto selaṁva pabbataṁ. ", "eng": "like the wind, a rocky mountain. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp9:0": {"pli": "Devadattavatthu ", "html": "<article id='dhp9'><h2 class='sutta-title'>{}</h2>"}, "dhp9:1": {"pli": "Anikkasāvo kāsāvaṁ, ", "eng": "One who, not free of stains themselves, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp9:2": {"pli": "yo vatthaṁ paridahissati; ", "eng": "would wear the robe stained in ocher, ", "html": "<span class='verse-line'>{}</span>"}, "dhp9:3": {"pli": "Apeto damasaccena, ", "eng": "bereft of self-control and of truth: ", "html": "<span class='verse-line'>{}</span>"}, "dhp9:4": {"pli": "na so kāsāvamarahati. ", "eng": "they are not worthy of the ocher robe. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp10:1": {"pli": "Yo ca vantakasāvassa, ", "eng": "One who’s purged all their stains, ", "html": "<article id='dhp10'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp10:2": {"pli": "sīlesu susamāhito; ", "eng": "steady in ethics, ", "html": "<span class='verse-line'>{}</span>"}, "dhp10:3": {"pli": "Upeto damasaccena, ", "eng": "possessed of self-control and of truth, ", "html": "<span class='verse-line'>{}</span>"}, "dhp10:4": {"pli": "sa ve kāsāvamarahati. ", "eng": "they are truly worthy of the ocher robe. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp11:0": {"pli": "Sāriputtattheravatthu ", "html": "<article id='dhp11'><h2 class='sutta-title'>{}</h2>"}, "dhp11:1": {"pli": "Asāre sāramatino, ", "eng": "Thinking the inessential is essential, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp11:2": {"pli": "sāre cāsāradassino; ", "eng": "seeing the essential as inessential; ", "html": "<span class='verse-line'>{}</span>"}, "dhp11:3": {"pli": "Te sāraṁ nādhigacchanti, ", "eng": "they don’t realize the essential, ", "html": "<span class='verse-line'>{}</span>"}, "dhp11:4": {"pli": "micchāsaṅkappagocarā. ", "eng": "for wrong thoughts are their habitat. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp12:1": {"pli": "Sārañca sārato ñatvā, ", "eng": "Having known the essential as essential, ", "html": "<article id='dhp12'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp12:2": {"pli": "asārañca asārato; ", "eng": "and the inessential as inessential; ", "html": "<span class='verse-line'>{}</span>"}, "dhp12:3": {"pli": "Te sāraṁ adhigacchanti, ", "eng": "they realize the essential, ", "html": "<span class='verse-line'>{}</span>"}, "dhp12:4": {"pli": "sammāsaṅkappagocarā. ", "eng": "for right thoughts are their habitat. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp13:0": {"pli": "Nandattheravatthu ", "html": "<article id='dhp13'><h2 class='sutta-title'>{}</h2>"}, "dhp13:1": {"pli": "Yathā agāraṁ ducchannaṁ, ", "eng": "Just as rain seeps into ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp13:2": {"pli": "vuṭṭhī samativijjhati; ", "eng": "a poorly roofed house, ", "html": "<span class='verse-line'>{}</span>"}, "dhp13:3": {"pli": "Evaṁ abhāvitaṁ cittaṁ, ", "eng": "lust seeps into ", "html": "<span class='verse-line'>{}</span>"}, "dhp13:4": {"pli": "rāgo samativijjhati. ", "eng": "an undeveloped mind. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp14:1": {"pli": "Yathā agāraṁ suchannaṁ, ", "eng": "Just as rain doesn’t seep into ", "html": "<article id='dhp14'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp14:2": {"pli": "vuṭṭhī na samativijjhati; ", "eng": "a well roofed house, ", "html": "<span class='verse-line'>{}</span>"}, "dhp14:3": {"pli": "Evaṁ subhāvitaṁ cittaṁ, ", "eng": "lust doesn’t seep into ", "html": "<span class='verse-line'>{}</span>"}, "dhp14:4": {"pli": "rāgo na samativijjhati. ", "eng": "a well developed mind. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp15:0": {"pli": "Cundasūkarikavatthu ", "html": "<article id='dhp15'><h2 class='sutta-title'>{}</h2>"}, "dhp15:1": {"pli": "Idha socati pecca socati, ", "eng": "Here they grieve, hereafter they grieve, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp15:2": {"pli": "Pāpakārī ubhayattha socati; ", "eng": "an evildoer grieves in both places. ", "html": "<span class='verse-line'>{}</span>"}, "dhp15:3": {"pli": "So socati so vihaññati, ", "eng": "They grieve and fret, ", "html": "<span class='verse-line'>{}</span>"}, "dhp15:4": {"pli": "Disvā kammakiliṭṭhamattano. ", "eng": "seeing their own corrupt deeds. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp16:0": {"pli": "Dhammikaupāsakavatthu ", "html": "<article id='dhp16'><h2 class='sutta-title'>{}</h2>"}, "dhp16:1": {"pli": "Idha modati pecca modati, ", "eng": "Here they rejoice, hereafter they rejoice, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp16:2": {"pli": "Katapuñño ubhayattha modati; ", "eng": "one who does good rejoices in both places. ", "html": "<span class='verse-line'>{}</span>"}, "dhp16:3": {"pli": "So modati so pamodati, ", "eng": "They rejoice and celebrate, ", "html": "<span class='verse-line'>{}</span>"}, "dhp16:4": {"pli": "Disvā kammavisuddhimattano. ", "eng": "seeing their own pure deeds. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp17:0": {"pli": "Devadattavatthu ", "html": "<article id='dhp17'><h2 class='sutta-title'>{}</h2>"}, "dhp17:1": {"pli": "Idha tappati pecca tappati, ", "eng": "Here they’re tormented, <j>hereafter they’re tormented, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp17:2": {"pli": "Pāpakārī ubhayattha tappati; ", "eng": "an evildoer is tormented in both places. ", "html": "<span class='verse-line'>{}</span>"}, "dhp17:3": {"pli": "“Pāpaṁ me katan”ti tappati, ", "eng": "They’re tormented <j>thinking of bad things they’ve done; ", "html": "<span class='verse-line'>{}</span>"}, "dhp17:4": {"pli": "Bhiyyo tappati duggatiṁ gato. ", "eng": "when gone to a bad place, <j>they’re tormented all the more. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp18:0": {"pli": "Sumanadevīvatthu ", "html": "<article id='dhp18'><h2 class='sutta-title'>{}</h2>"}, "dhp18:1": {"pli": "Idha nandati pecca nandati, ", "eng": "Here they delight, hereafter they delight, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp18:2": {"pli": "Katapuñño ubhayattha nandati; ", "eng": "one who does good delights in both places. ", "html": "<span class='verse-line'>{}</span>"}, "dhp18:3": {"pli": "“Puññaṁ me katan”ti nandati, ", "eng": "They delight thinking of good things they’ve done; ", "html": "<span class='verse-line'>{}</span>"}, "dhp18:4": {"pli": "Bhiyyo nandati suggatiṁ gato. ", "eng": "when gone to a good place, they delight all the more. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp19:0": {"pli": "Dvesahāyakabhikkhuvatthu ", "html": "<article id='dhp19'><h2 class='sutta-title'>{}</h2>"}, "dhp19:1": {"pli": "Bahumpi ce saṁhita bhāsamāno, ", "eng": "Much though they may recite scripture, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp19:2": {"pli": "Na takkaro hoti naro pamatto; ", "eng": "if a negligent person does not apply them, ", "html": "<span class='verse-line'>{}</span>"}, "dhp19:3": {"pli": "Gopova gāvo gaṇayaṁ paresaṁ, ", "eng": "then, like a cowherd who counts the cattle of others, ", "html": "<span class='verse-line'>{}</span>"}, "dhp19:4": {"pli": "Na bhāgavā sāmaññassa hoti. ", "eng": "they miss out on the blessings of the ascetic life. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp20:1": {"pli": "Appampi ce saṁhita bhāsamāno, ", "eng": "Little though they may recite scripture, ", "html": "<article id='dhp20'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp20:2": {"pli": "Dhammassa hoti anudhammacārī; ", "eng": "if they live in line with the teaching, ", "html": "<span class='verse-line'>{}</span>"}, "dhp20:3": {"pli": "Rāgañca dosañca pahāya mohaṁ, ", "eng": "having given up greed, hate, and delusion, ", "html": "<span class='verse-line'>{}</span>"}, "dhp20:4": {"pli": "Sammappajāno suvimuttacitto; ", "eng": "with deep understanding and heart well freed, ", "html": "<span class='verse-line'>{}</span>"}, "dhp20:5": {"pli": "Anupādiyāno idha vā huraṁ vā, ", "eng": "not grasping to this life or the next, ", "html": "<span class='verse-line'>{}</span>"}, "dhp20:6": {"pli": "Sa bhāgavā sāmaññassa hoti. ", "eng": "they share in the blessings of the ascetic life. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp20:7": {"pli": "Yamakavaggo paṭhamo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp21-32": {"dhp21:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp21-32'><header><ul><li class='division'>{}</li>"}, "dhp21:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 21–32 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp21:0.3": {"pli": "Appamādavagga ", "eng": "2. Diligence ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp21:0.4": {"pli": "Sāmāvatīvatthu ", "html": "<article id='dhp21'><h2 class='sutta-title'>{}</h2>"}, "dhp21:1": {"pli": "Appamādo amatapadaṁ, ", "eng": "Heedfulness is the state free of death; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp21:2": {"pli": "pamādo maccuno padaṁ; ", "eng": "heedlessness is the state of death. ", "html": "<span class='verse-line'>{}</span>"}, "dhp21:3": {"pli": "Appamattā na mīyanti, ", "eng": "The heedful do not die, ", "html": "<span class='verse-line'>{}</span>"}, "dhp21:4": {"pli": "ye pamattā yathā matā. ", "eng": "while the heedless are like the dead. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp22:1": {"pli": "Evaṁ visesato ñatvā, ", "eng": "Understanding this distinction ", "html": "<article id='dhp22'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp22:2": {"pli": "appamādamhi paṇḍitā; ", "eng": "when it comes to heedfulness, ", "html": "<span class='verse-line'>{}</span>"}, "dhp22:3": {"pli": "Appamāde pamodanti, ", "eng": "the astute rejoice in heedfulness, ", "html": "<span class='verse-line'>{}</span>"}, "dhp22:4": {"pli": "ariyānaṁ gocare ratā. ", "eng": "happy in the noble ones’ domain. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp23:1": {"pli": "Te jhāyino sātatikā, ", "eng": "They who regularly meditate, ", "html": "<article id='dhp23'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp23:2": {"pli": "niccaṁ daḷhaparakkamā; ", "eng": "always staunchly vigorous; ", "html": "<span class='verse-line'>{}</span>"}, "dhp23:3": {"pli": "Phusanti dhīrā nibbānaṁ, ", "eng": "the attentive realize extinguishment, ", "html": "<span class='verse-line'>{}</span>"}, "dhp23:4": {"pli": "yogakkhemaṁ anuttaraṁ. ", "eng": "the supreme sanctuary from the yoke. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp24:0": {"pli": "Kumbaghosakaseṭṭhivatthu ", "html": "<article id='dhp24'><h2 class='sutta-title'>{}</h2>"}, "dhp24:1": {"pli": "Uṭṭhānavato satīmato, ", "eng": "For the hard-working and mindful, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp24:2": {"pli": "Sucikammassa nisammakārino; ", "eng": "pure of deed and attentive, ", "html": "<span class='verse-line'>{}</span>"}, "dhp24:3": {"pli": "Saññatassa dhammajīvino, ", "eng": "restrained, living righteously, and diligent, ", "html": "<span class='verse-line'>{}</span>"}, "dhp24:4": {"pli": "Appamattassa yasobhivaḍḍhati. ", "eng": "their reputation only grows. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp25:0": {"pli": "Cūḷapanthakavatthu ", "html": "<article id='dhp25'><h2 class='sutta-title'>{}</h2>"}, "dhp25:1": {"pli": "Uṭṭhānenappamādena, ", "eng": "By hard work and diligence, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp25:2": {"pli": "saṁyamena damena ca; ", "eng": "by restraint and by self-control, ", "html": "<span class='verse-line'>{}</span>"}, "dhp25:3": {"pli": "Dīpaṁ kayirātha medhāvī, ", "eng": "a smart person would build an island ", "html": "<span class='verse-line'>{}</span>"}, "dhp25:4": {"pli": "yaṁ ogho nābhikīrati. ", "eng": "that the floods cannot overflow. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp26:0": {"pli": "Bālanakkhattasaṅghuṭṭhavatthu ", "html": "<article id='dhp26'><h2 class='sutta-title'>{}</h2>"}, "dhp26:1": {"pli": "Pamādamanuyuñjanti, ", "eng": "Fools and simpletons ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp26:2": {"pli": "bālā dummedhino janā; ", "eng": "devote themselves to negligence. ", "html": "<span class='verse-line'>{}</span>"}, "dhp26:3": {"pli": "Appamādañca medhāvī, ", "eng": "But the wise protect diligence ", "html": "<span class='verse-line'>{}</span>"}, "dhp26:4": {"pli": "dhanaṁ seṭṭhaṁva rakkhati. ", "eng": "as their best treasure. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp27:1": {"pli": "Mā pamādamanuyuñjetha, ", "eng": "Don’t devote yourself to negligence, ", "html": "<article id='dhp27'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp27:2": {"pli": "mā kāmaratisanthavaṁ; ", "eng": "or delight in erotic intimacy. ", "html": "<span class='verse-line'>{}</span>"}, "dhp27:3": {"pli": "Appamatto hi jhāyanto, ", "eng": "For if you’re diligent and meditate, ", "html": "<span class='verse-line'>{}</span>"}, "dhp27:4": {"pli": "pappoti vipulaṁ sukhaṁ. ", "eng": "you’ll attain abundant happiness. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp28:0": {"pli": "Mahākassapattheravatthu ", "html": "<article id='dhp28'><h2 class='sutta-title'>{}</h2>"}, "dhp28:1": {"pli": "Pamādaṁ appamādena, ", "eng": "When the astute dispel negligence ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp28:2": {"pli": "yadā nudati paṇḍito; ", "eng": "by means of diligence, ", "html": "<span class='verse-line'>{}</span>"}, "dhp28:3": {"pli": "Paññāpāsādamāruyha, ", "eng": "ascending the palace of wisdom, ", "html": "<span class='verse-line'>{}</span>"}, "dhp28:4": {"pli": "asoko sokiniṁ pajaṁ; ", "eng": "sorrowless, they behold this generation of sorrow, ", "html": "<span class='verse-line'>{}</span>"}, "dhp28:5": {"pli": "Pabbataṭṭhova bhūmaṭṭhe, ", "eng": "as an attentive one on a mountain top ", "html": "<span class='verse-line'>{}</span>"}, "dhp28:6": {"pli": "dhīro bāle avekkhati. ", "eng": "beholds the fools below. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp29:0": {"pli": "Pamattāpamattadvesahāyakabhikkhuvatthu ", "html": "<article id='dhp29'><h2 class='sutta-title'>{}</h2>"}, "dhp29:1": {"pli": "Appamatto pamattesu, ", "eng": "Heedful among the heedless, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp29:2": {"pli": "suttesu bahujāgaro; ", "eng": "wide awake while others sleep—", "html": "<span class='verse-line'>{}</span>"}, "dhp29:3": {"pli": "Abalassaṁva sīghasso, ", "eng": "a true sage leaves them behind, ", "html": "<span class='verse-line'>{}</span>"}, "dhp29:4": {"pli": "hitvā yāti sumedhaso. ", "eng": "like a swift horse passing a feeble. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp30:0": {"pli": "Maghavatthu ", "html": "<article id='dhp30'><h2 class='sutta-title'>{}</h2>"}, "dhp30:1": {"pli": "Appamādena maghavā, ", "eng": "Maghavā became chief of the gods ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp30:2": {"pli": "devānaṁ seṭṭhataṁ gato; ", "eng": "by means of diligence. ", "html": "<span class='verse-line'>{}</span>"}, "dhp30:3": {"pli": "Appamādaṁ pasaṁsanti, ", "eng": "People praise diligence, ", "html": "<span class='verse-line'>{}</span>"}, "dhp30:4": {"pli": "pamādo garahito sadā. ", "eng": "while negligence is always deplored. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp31:0": {"pli": "Aññatarabhikkhuvatthu ", "html": "<article id='dhp31'><h2 class='sutta-title'>{}</h2>"}, "dhp31:1": {"pli": "Appamādarato bhikkhu, ", "eng": "A mendicant who loves to be diligent, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp31:2": {"pli": "pamāde bhayadassi vā; ", "eng": "seeing fear in negligence—", "html": "<span class='verse-line'>{}</span>"}, "dhp31:3": {"pli": "Saṁyojanaṁ aṇuṁ thūlaṁ, ", "eng": "advances like fire, ", "html": "<span class='verse-line'>{}</span>"}, "dhp31:4": {"pli": "ḍahaṁ aggīva gacchati. ", "eng": "burning up fetters big and small. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp32:0": {"pli": "Nigamavāsitissattheravatthu ", "html": "<article id='dhp32'><h2 class='sutta-title'>{}</h2>"}, "dhp32:1": {"pli": "Appamādarato bhikkhu, ", "eng": "A mendicant who loves to be diligent, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp32:2": {"pli": "pamāde bhayadassi vā; ", "eng": "seeing fear in negligence—", "html": "<span class='verse-line'>{}</span>"}, "dhp32:3": {"pli": "Abhabbo parihānāya, ", "eng": "such a one can’t decline, ", "html": "<span class='verse-line'>{}</span>"}, "dhp32:4": {"pli": "nibbānasseva santike. ", "eng": "and has drawn near to extinguishment. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp32:5": {"pli": "Appamādavaggo dutiyo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp33-43": {"dhp33:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp33-43'><header><ul><li class='division'>{}</li>"}, "dhp33:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 33–43 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp33:0.3": {"pli": "Cittavagga ", "eng": "3. The Mind ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp33:0.4": {"pli": "Meghiyattheravatthu ", "html": "<article id='dhp33'><h2 class='sutta-title'>{}</h2>"}, "dhp33:1": {"pli": "Phandanaṁ capalaṁ cittaṁ, ", "eng": "The mind quivers and shakes, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp33:2": {"pli": "dūrakkhaṁ dunnivārayaṁ; ", "eng": "hard to guard, hard to curb. ", "html": "<span class='verse-line'>{}</span>"}, "dhp33:3": {"pli": "Ujuṁ karoti medhāvī, ", "eng": "The discerning straighten it out, ", "html": "<span class='verse-line'>{}</span>"}, "dhp33:4": {"pli": "usukārova tejanaṁ. ", "eng": "like a fletcher straightens an arrow. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp34:1": {"pli": "Vārijova thale khitto, ", "eng": "Like a fish pulled from the sea ", "html": "<article id='dhp34'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp34:2": {"pli": "okamokataubbhato; ", "eng": "and cast upon the shore, ", "html": "<span class='verse-line'>{}</span>"}, "dhp34:3": {"pli": "Pariphandatidaṁ cittaṁ, ", "eng": "this mind flounders about, ", "html": "<span class='verse-line'>{}</span>"}, "dhp34:4": {"pli": "māradheyyaṁ pahātave. ", "eng": "trying to throw off Māra’s dominion. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp35:0": {"pli": "Aññatarabhikkhuvatthu ", "html": "<article id='dhp35'><h2 class='sutta-title'>{}</h2>"}, "dhp35:1": {"pli": "Dunniggahassa lahuno, ", "eng": "Hard to hold back, flighty, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp35:2": {"pli": "yatthakāmanipātino; ", "eng": "alighting where it will; ", "html": "<span class='verse-line'>{}</span>"}, "dhp35:3": {"pli": "Cittassa damatho sādhu, ", "eng": "it’s good to tame the mind; ", "html": "<span class='verse-line'>{}</span>"}, "dhp35:4": {"pli": "cittaṁ dantaṁ sukhāvahaṁ. ", "eng": "a tamed mind leads to bliss. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp36:0": {"pli": "Aññataraukkaṇṭhitabhikkhuvatthu ", "html": "<article id='dhp36'><h2 class='sutta-title'>{}</h2>"}, "dhp36:1": {"pli": "Sududdasaṁ sunipuṇaṁ, ", "eng": "So hard to see, so subtle, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp36:2": {"pli": "yatthakāmanipātinaṁ; ", "eng": "alighting where it will; ", "html": "<span class='verse-line'>{}</span>"}, "dhp36:3": {"pli": "Cittaṁ rakkhetha medhāvī, ", "eng": "the discerning protect the mind, ", "html": "<span class='verse-line'>{}</span>"}, "dhp36:4": {"pli": "cittaṁ guttaṁ sukhāvahaṁ. ", "eng": "a guarded mind leads to bliss. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp37:0": {"pli": "Saṅgharakkhitabhāgineyyattheravatthu ", "html": "<article id='dhp37'><h2 class='sutta-title'>{}</h2>"}, "dhp37:1": {"pli": "Dūraṅgamaṁ ekacaraṁ, ", "eng": "The mind travels far, wandering alone; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp37:2": {"pli": "asarīraṁ guhāsayaṁ; ", "eng": "incorporeal, it lies hidden in the heart. ", "html": "<span class='verse-line'>{}</span>"}, "dhp37:3": {"pli": "Ye cittaṁ saṁyamissanti, ", "eng": "Those who will restrain the mind ", "html": "<span class='verse-line'>{}</span>"}, "dhp37:4": {"pli": "mokkhanti mārabandhanā. ", "eng": "are freed from Māra’s bonds. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp38:0": {"pli": "Cittahatthattheravatthu ", "html": "<article id='dhp38'><h2 class='sutta-title'>{}</h2>"}, "dhp38:1": {"pli": "Anavaṭṭhitacittassa, ", "eng": "Those of unsteady mind, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp38:2": {"pli": "saddhammaṁ avijānato; ", "eng": "who don’t understand the true teaching, ", "html": "<span class='verse-line'>{}</span>"}, "dhp38:3": {"pli": "Pariplavapasādassa, ", "eng": "and whose confidence wavers, ", "html": "<span class='verse-line'>{}</span>"}, "dhp38:4": {"pli": "paññā na paripūrati. ", "eng": "do not perfect their wisdom. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp39:1": {"pli": "Anavassutacittassa, ", "eng": "One whose mind is not festering, ", "html": "<article id='dhp39'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp39:2": {"pli": "ananvāhatacetaso; ", "eng": "whose heart is undamaged, ", "html": "<span class='verse-line'>{}</span>"}, "dhp39:3": {"pli": "Puññapāpapahīnassa, ", "eng": "who’s given up right and wrong, ", "html": "<span class='verse-line'>{}</span>"}, "dhp39:4": {"pli": "natthi jāgarato bhayaṁ. ", "eng": "alert, has nothing to fear. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp40:0": {"pli": "Pañcasatabhikkhuvatthu ", "html": "<article id='dhp40'><h2 class='sutta-title'>{}</h2>"}, "dhp40:1": {"pli": "Kumbhūpamaṁ kāyamimaṁ viditvā, ", "eng": "Knowing this body breaks like a pot, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp40:2": {"pli": "Nagarūpamaṁ cittamidaṁ ṭhapetvā; ", "eng": "and fortifying the mind like a citadel, ", "html": "<span class='verse-line'>{}</span>"}, "dhp40:3": {"pli": "Yodhetha māraṁ paññāvudhena, ", "eng": "attack Māra with the sword of wisdom, ", "html": "<span class='verse-line'>{}</span>"}, "dhp40:4": {"pli": "Jitañca rakkhe anivesano siyā. ", "eng": "guard your conquest, and never settle. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp41:0": {"pli": "Pūtigattatissattheravatthu ", "html": "<article id='dhp41'><h2 class='sutta-title'>{}</h2>"}, "dhp41:1": {"pli": "Aciraṁ vatayaṁ kāyo, ", "eng": "All too soon this body ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp41:2": {"pli": "pathaviṁ adhisessati; ", "eng": "will lie upon the earth, ", "html": "<span class='verse-line'>{}</span>"}, "dhp41:3": {"pli": "Chuddho apetaviññāṇo, ", "eng": "bereft of consciousness, ", "html": "<span class='verse-line'>{}</span>"}, "dhp41:4": {"pli": "niratthaṁva kaliṅgaraṁ. ", "eng": "tossed aside like a worthless log. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>", "comm": "As the sacrificed beast “lies there useless as a block of wood” (<i lang='pi' translate='no'>dārveva tarhi bhūto ’narthyaḥ śete</i>, Śatapatha Brāhmaṇa 3.8.3.15). "}, "dhp42:0": {"pli": "Nandagopālakavatthu ", "html": "<article id='dhp42'><h2 class='sutta-title'>{}</h2>"}, "dhp42:1": {"pli": "Diso disaṁ yaṁ taṁ kayirā, ", "eng": "A wrongly directed mind ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp42:2": {"pli": "verī vā pana verinaṁ; ", "eng": "would do you more harm ", "html": "<span class='verse-line'>{}</span>"}, "dhp42:3": {"pli": "Micchāpaṇihitaṁ cittaṁ, ", "eng": "than a hater to the hated, ", "html": "<span class='verse-line'>{}</span>"}, "dhp42:4": {"pli": "pāpiyo naṁ tato kare. ", "eng": "or an enemy to their foe. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp43:0": {"pli": "Soreyyavatthu ", "html": "<article id='dhp43'><h2 class='sutta-title'>{}</h2>"}, "dhp43:1": {"pli": "Na taṁ mātā pitā kayirā, ", "eng": "A rightly directed mind ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp43:2": {"pli": "aññe vāpi ca ñātakā; ", "eng": "would do you more good ", "html": "<span class='verse-line'>{}</span>"}, "dhp43:3": {"pli": "Sammāpaṇihitaṁ cittaṁ, ", "eng": "than your mother or father ", "html": "<span class='verse-line'>{}</span>"}, "dhp43:4": {"pli": "seyyaso naṁ tato kare. ", "eng": "or any other relative. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp43:5": {"pli": "Cittavaggo tatiyo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp44-59": {"dhp44:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp44-59'><header><ul><li class='division'>{}</li>"}, "dhp44:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 44–59 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp44:0.3": {"pli": "Pupphavagga ", "eng": "4. Flowers ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp44:0.4": {"pli": "Pathavikathāpasutapañcasatabhikkhuvatthu ", "html": "<article id='dhp44'><h2 class='sutta-title'>{}</h2>"}, "dhp44:1": {"pli": "Ko imaṁ pathaviṁ vicessati, ", "eng": "Who bestirs this earth, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>", "comm": "The verbs <i lang='pi' translate='no'>vicessati</i> and <i lang='pi' translate='no'>pacessati</i> in this verse appear to be unique. I take them to be from the Sanskrit <i lang='pi' translate='no'>ceṣṭ</i> (“move, explore”) rather than <i lang='pi' translate='no'>ci</i> (“gather”). They can then be read as present tense, which makes better sense of the verse. Sanskrit and Gandhari parallels have a variety of forms, all of which maintain the distinction between the <i lang='pi' translate='no'>vi</i> and <i lang='pi' translate='no'>pa</i> prefixes. | I take <i lang='pi' translate='no'>vicessati</i> in the sense “stir, agitate”, with reference to such passages as <a href='https://suttacentral.net/an7.69/en/sujato#15.1'>AN 7.69:15.1</a>, where the noble one inspires the gods. The commentary, rather, suggests the sense “explore, investigate”. A variant reading <i lang='pi' translate='no'>vijessati</i> yields the sense “shall conquer”. "}, "dhp44:2": {"pli": "Yamalokañca imaṁ sadevakaṁ; ", "eng": "and the Yama realm with its gods? ", "html": "<span class='verse-line'>{}</span>"}, "dhp44:3": {"pli": "Ko dhammapadaṁ sudesitaṁ, ", "eng": "Who sets out the well-taught word of truth, ", "html": "<span class='verse-line'>{}</span>", "comm": "The verb <i lang='pi' translate='no'>pacessati</i> is exceedingly rare, but the late tantric text Sarvatathāgatādhiṣṭhāna Vyūhasūtra has <i lang='pi' translate='no'>praceṣṭavyaḥ</i>, evidently in the sense of “setting out” scents, flowers, etc. on a shrine. Compare the sense of “having prepared” (the offering for the fire, <i lang='pi' translate='no'>ceṣṭitvā</i>) at Bṛhadāraṇyaka Upaniṣad 6.4.19. "}, "dhp44:4": {"pli": "Kusalo pupphamiva pacessati. ", "eng": "as an expert a flower? ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp45:1": {"pli": "Sekho pathaviṁ vicessati, ", "eng": "A trainee bestirs this earth, ", "html": "<article id='dhp45'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp45:2": {"pli": "Yamalokañca imaṁ sadevakaṁ; ", "eng": "and the Yama realm with its gods. ", "html": "<span class='verse-line'>{}</span>"}, "dhp45:3": {"pli": "Sekho dhammapadaṁ sudesitaṁ, ", "eng": "A trainee sets out the well-taught word of truth, ", "html": "<span class='verse-line'>{}</span>"}, "dhp45:4": {"pli": "Kusalo pupphamiva pacessati. ", "eng": "as an expert a flower. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp46:0": {"pli": "Marīcikammaṭṭhānikabhikkhuvatthu ", "html": "<article id='dhp46'><h2 class='sutta-title'>{}</h2>"}, "dhp46:1": {"pli": "Pheṇūpamaṁ kāyamimaṁ viditvā, ", "eng": "Knowing this body’s like foam, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp46:2": {"pli": "Marīcidhammaṁ abhisambudhāno; ", "eng": "realizing it’s all just a mirage, ", "html": "<span class='verse-line'>{}</span>"}, "dhp46:3": {"pli": "Chetvāna mārassa papupphakāni, ", "eng": "and cutting off Māra’s blossoming, ", "html": "<span class='verse-line'>{}</span>"}, "dhp46:4": {"pli": "Adassanaṁ maccurājassa gacche. ", "eng": "vanish from the King of Death. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp47:0": {"pli": "Viṭaṭūbhavatthu ", "html": "<article id='dhp47'><h2 class='sutta-title'>{}</h2>"}, "dhp47:1": {"pli": "Pupphāni heva pacinantaṁ, ", "eng": "As a mighty flood sweeps off a sleeping village, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp47:2": {"pli": "byāsattamanasaṁ naraṁ; ", "eng": "death steals away a man ", "html": "<span class='verse-line'>{}</span>"}, "dhp47:3": {"pli": "Suttaṁ gāmaṁ mahoghova, ", "eng": "even as he gathers flowers, ", "html": "<span class='verse-line'>{}</span>"}, "dhp47:4": {"pli": "maccu ādāya gacchati. ", "eng": "his mind caught up in them. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp48:0": {"pli": "Patipūjikakumārivatthu ", "html": "<article id='dhp48'><h2 class='sutta-title'>{}</h2>"}, "dhp48:1": {"pli": "Pupphāni heva pacinantaṁ, ", "eng": "The terminator gains control of the man ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp48:2": {"pli": "byāsattamanasaṁ naraṁ; ", "eng": "who has not had his fill of pleasures, ", "html": "<span class='verse-line'>{}</span>"}, "dhp48:3": {"pli": "Atittaññeva kāmesu, ", "eng": "even as he gathers flowers, ", "html": "<span class='verse-line'>{}</span>"}, "dhp48:4": {"pli": "antako kurute vasaṁ. ", "eng": "his mind caught up in them. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp49:0": {"pli": "Macchariyakosiyaseṭṭhivatthu ", "html": "<article id='dhp49'><h2 class='sutta-title'>{}</h2>"}, "dhp49:1": {"pli": "Yathāpi bhamaro pupphaṁ, ", "eng": "A bee takes the nectar ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp49:2": {"pli": "vaṇṇagandhamaheṭhayaṁ; ", "eng": "and moves on, doing no damage ", "html": "<span class='verse-line'>{}</span>"}, "dhp49:3": {"pli": "Paleti rasamādāya, ", "eng": "to the flower’s beauty and fragrance; ", "html": "<span class='verse-line'>{}</span>"}, "dhp49:4": {"pli": "evaṁ gāme munī care. ", "eng": "and that’s how a sage should walk in the village. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp50:0": {"pli": "Pāveyyaājīvakavatthu ", "html": "<article id='dhp50'><h2 class='sutta-title'>{}</h2>"}, "dhp50:1": {"pli": "Na paresaṁ vilomāni, ", "eng": "Don’t find fault with others, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp50:2": {"pli": "na paresaṁ katākataṁ; ", "eng": "with what they’ve done or left undone. ", "html": "<span class='verse-line'>{}</span>"}, "dhp50:3": {"pli": "Attanova avekkheyya, ", "eng": "You should only watch yourself, ", "html": "<span class='verse-line'>{}</span>"}, "dhp50:4": {"pli": "katāni akatāni ca. ", "eng": "what you’ve done or left undone. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp51:0": {"pli": "Chattapāṇiupāsakavatthu ", "html": "<article id='dhp51'><h2 class='sutta-title'>{}</h2>"}, "dhp51:1": {"pli": "Yathāpi ruciraṁ pupphaṁ, ", "eng": "Just like a glorious flower ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp51:2": {"pli": "vaṇṇavantaṁ agandhakaṁ; ", "eng": "that’s colorful but lacks fragrance; ", "html": "<span class='verse-line'>{}</span>"}, "dhp51:3": {"pli": "Evaṁ subhāsitā vācā, ", "eng": "eloquent speech is fruitless ", "html": "<span class='verse-line'>{}</span>"}, "dhp51:4": {"pli": "aphalā hoti akubbato. ", "eng": "for one who does not act on it. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp52:1": {"pli": "Yathāpi ruciraṁ pupphaṁ, ", "eng": "Just like a glorious flower ", "html": "<article id='dhp52'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp52:2": {"pli": "Vaṇṇavantaṁ sagandhakaṁ; ", "eng": "that’s both colorful and fragrant, ", "html": "<span class='verse-line'>{}</span>"}, "dhp52:3": {"pli": "Evaṁ subhāsitā vācā, ", "eng": "eloquent speech is fruitful ", "html": "<span class='verse-line'>{}</span>"}, "dhp52:4": {"pli": "Saphalā hoti kubbato. ", "eng": "for one who acts on it. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp53:0": {"pli": "Visākhāvatthu ", "html": "<article id='dhp53'><h2 class='sutta-title'>{}</h2>"}, "dhp53:1": {"pli": "Yathāpi puppharāsimhā, ", "eng": "Just as one would create many garlands ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp53:2": {"pli": "kayirā mālāguṇe bahū; ", "eng": "from a heap of flowers, ", "html": "<span class='verse-line'>{}</span>"}, "dhp53:3": {"pli": "Evaṁ jātena maccena, ", "eng": "when a person has come to be born, ", "html": "<span class='verse-line'>{}</span>"}, "dhp53:4": {"pli": "kattabbaṁ kusalaṁ bahuṁ. ", "eng": "they should do many skillful things. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp54:0": {"pli": "Ānandattherapañhavatthu ", "html": "<article id='dhp54'><h2 class='sutta-title'>{}</h2>"}, "dhp54:1": {"pli": "Na pupphagandho paṭivātameti, ", "eng": "The fragrance of flowers doesn’t spread upwind, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp54:2": {"pli": "Na candanaṁ tagaramallikā vā; ", "eng": "nor sandalwood, pinwheel, or jasmine; ", "html": "<span class='verse-line'>{}</span>"}, "dhp54:3": {"pli": "Satañca gandho paṭivātameti, ", "eng": "but the fragrance of the good spreads upwind; ", "html": "<span class='verse-line'>{}</span>"}, "dhp54:4": {"pli": "Sabbā disā sappuriso pavāyati. ", "eng": "a true person’s virtue spreads in every direction. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp55:1": {"pli": "Candanaṁ tagaraṁ vāpi, ", "eng": "Among all the fragrances—", "html": "<article id='dhp55'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp55:2": {"pli": "uppalaṁ atha vassikī; ", "eng": "sandalwood or pinwheel ", "html": "<span class='verse-line'>{}</span>"}, "dhp55:3": {"pli": "Etesaṁ gandhajātānaṁ, ", "eng": "or lotus or jasmine—", "html": "<span class='verse-line'>{}</span>"}, "dhp55:4": {"pli": "sīlagandho anuttaro. ", "eng": "the fragrance of virtue is supreme. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp56:0": {"pli": "Mahākassapattherapiṇḍapātadinnavatthu ", "html": "<article id='dhp56'><h2 class='sutta-title'>{}</h2>"}, "dhp56:1": {"pli": "Appamatto ayaṁ gandho, ", "eng": "Faint is the fragrance ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp56:2": {"pli": "yāyaṁ tagaracandanī; ", "eng": "of sandal or pinwheel; ", "html": "<span class='verse-line'>{}</span>"}, "dhp56:3": {"pli": "Yo ca sīlavataṁ gandho, ", "eng": "but the fragrance of the virtuous ", "html": "<span class='verse-line'>{}</span>"}, "dhp56:4": {"pli": "vāti devesu uttamo. ", "eng": "floats to the highest gods. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp57:0": {"pli": "Godhikattheraparinibbānavatthu ", "html": "<article id='dhp57'><h2 class='sutta-title'>{}</h2>"}, "dhp57:1": {"pli": "Tesaṁ sampannasīlānaṁ, ", "eng": "For those accomplished in ethics, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp57:2": {"pli": "appamādavihārinaṁ; ", "eng": "meditating diligently, ", "html": "<span class='verse-line'>{}</span>"}, "dhp57:3": {"pli": "Sammadaññāvimuttānaṁ, ", "eng": "freed through the highest knowledge, ", "html": "<span class='verse-line'>{}</span>"}, "dhp57:4": {"pli": "māro maggaṁ na vindati. ", "eng": "Māra cannot find their path. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp58:0": {"pli": "Garahadinnavatthu ", "html": "<article id='dhp58'><h2 class='sutta-title'>{}</h2>"}, "dhp58:1": {"pli": "Yathā saṅkāradhānasmiṁ, ", "eng": "From a heap of trash ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp58:2": {"pli": "ujjhitasmiṁ mahāpathe; ", "eng": "discarded on the highway, ", "html": "<span class='verse-line'>{}</span>"}, "dhp58:3": {"pli": "Padumaṁ tattha jāyetha, ", "eng": "a lotus might blossom, ", "html": "<span class='verse-line'>{}</span>"}, "dhp58:4": {"pli": "sucigandhaṁ manoramaṁ. ", "eng": "fragrant and delightful. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp59:1": {"pli": "Evaṁ saṅkārabhūtesu, ", "eng": "So too, among those thought of as trash, ", "html": "<article id='dhp59'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp59:2": {"pli": "andhabhūte puthujjane; ", "eng": "a disciple of the perfect Buddha ", "html": "<span class='verse-line'>{}</span>"}, "dhp59:3": {"pli": "Atirocati paññāya, ", "eng": "outshines with their wisdom ", "html": "<span class='verse-line'>{}</span>"}, "dhp59:4": {"pli": "sammāsambuddhasāvako. ", "eng": "the blind ordinary folk. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp59:5": {"pli": "Pupphavaggo catuttho. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp60-75": {"dhp60:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp60-75'><header><ul><li class='division'>{}</li>"}, "dhp60:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 60–75 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp60:0.3": {"pli": "Bālavagga ", "eng": "5. The Fool ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp60:0.4": {"pli": "Aññatarapurisavatthu ", "html": "<article id='dhp60'><h2 class='sutta-title'>{}</h2>"}, "dhp60:1": {"pli": "Dīghā jāgarato ratti, ", "eng": "Long is the night for the wakeful; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp60:2": {"pli": "dīghaṁ santassa yojanaṁ; ", "eng": "long is the league for the weary; ", "html": "<span class='verse-line'>{}</span>"}, "dhp60:3": {"pli": "Dīgho bālāna saṁsāro, ", "eng": "long transmigrate the fools ", "html": "<span class='verse-line'>{}</span>"}, "dhp60:4": {"pli": "saddhammaṁ avijānataṁ. ", "eng": "who don’t understand the true teaching. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp61:0": {"pli": "Mahākassapasaddhivihārikavatthu ", "html": "<article id='dhp61'><h2 class='sutta-title'>{}</h2>"}, "dhp61:1": {"pli": "Carañce nādhigaccheyya, ", "eng": "If while wandering you find no partner ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp61:2": {"pli": "Seyyaṁ sadisamattano; ", "eng": "equal or better than yourself, ", "html": "<span class='verse-line'>{}</span>"}, "dhp61:3": {"pli": "Ekacariyaṁ daḷhaṁ kayirā, ", "eng": "then firmly resolve to wander alone—", "html": "<span class='verse-line'>{}</span>"}, "dhp61:4": {"pli": "Natthi bāle sahāyatā. ", "eng": "there’s no fellowship with fools. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp62:0": {"pli": "Ānandaseṭṭhivatthu ", "html": "<article id='dhp62'><h2 class='sutta-title'>{}</h2>"}, "dhp62:1": {"pli": "Puttā matthi dhanaṁ matthi, ", "eng": "“Sons are mine, wealth is mine”—", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp62:2": {"pli": "iti bālo vihaññati; ", "eng": "thus the fool frets. ", "html": "<span class='verse-line'>{}</span>"}, "dhp62:3": {"pli": "Attā hi attano natthi, ", "eng": "For even your self is not your own, ", "html": "<span class='verse-line'>{}</span>"}, "dhp62:4": {"pli": "kuto puttā kuto dhanaṁ. ", "eng": "let alone your sons or wealth. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp63:0": {"pli": "Gaṇṭhibhedakacoravatthu ", "html": "<article id='dhp63'><h2 class='sutta-title'>{}</h2>"}, "dhp63:1": {"pli": "Yo bālo maññati bālyaṁ, ", "eng": "The fool who thinks they’re a fool ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp63:2": {"pli": "paṇḍito vāpi tena so; ", "eng": "is wise at least to that extent. ", "html": "<span class='verse-line'>{}</span>"}, "dhp63:3": {"pli": "Bālo ca paṇḍitamānī, ", "eng": "But the true fool is said to be one ", "html": "<span class='verse-line'>{}</span>"}, "dhp63:4": {"pli": "sa ve “bālo”ti vuccati. ", "eng": "who imagines that they are wise. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp64:0": {"pli": "Udāyittheravatthu ", "html": "<article id='dhp64'><h2 class='sutta-title'>{}</h2>"}, "dhp64:1": {"pli": "Yāvajīvampi ce bālo, ", "eng": "Though a fool attends to the wise ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp64:2": {"pli": "paṇḍitaṁ payirupāsati; ", "eng": "even for the rest of their life, ", "html": "<span class='verse-line'>{}</span>"}, "dhp64:3": {"pli": "Na so dhammaṁ vijānāti, ", "eng": "they still don’t understand the teaching, ", "html": "<span class='verse-line'>{}</span>"}, "dhp64:4": {"pli": "dabbī sūparasaṁ yathā. ", "eng": "like a spoon the taste of the soup. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp65:0": {"pli": "Tiṁsapāveyyakabhikkhuvatthu ", "html": "<article id='dhp65'><h2 class='sutta-title'>{}</h2>"}, "dhp65:1": {"pli": "Muhuttamapi ce viññū, ", "eng": "If a clever person attends to the wise ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp65:2": {"pli": "paṇḍitaṁ payirupāsati; ", "eng": "even just for an hour or so, ", "html": "<span class='verse-line'>{}</span>"}, "dhp65:3": {"pli": "Khippaṁ dhammaṁ vijānāti, ", "eng": "they swiftly understand the teaching, ", "html": "<span class='verse-line'>{}</span>"}, "dhp65:4": {"pli": "jivhā sūparasaṁ yathā. ", "eng": "like a tongue the taste of the soup. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp66:0": {"pli": "Suppabuddhakuṭṭhivatthu ", "html": "<article id='dhp66'><h2 class='sutta-title'>{}</h2>"}, "dhp66:1": {"pli": "Caranti bālā dummedhā, ", "eng": "Fools and simpletons behave ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp66:2": {"pli": "amitteneva attanā; ", "eng": "like their own worst enemies, ", "html": "<span class='verse-line'>{}</span>"}, "dhp66:3": {"pli": "Karontā pāpakaṁ kammaṁ, ", "eng": "doing wicked deeds ", "html": "<span class='verse-line'>{}</span>"}, "dhp66:4": {"pli": "yaṁ hoti kaṭukapphalaṁ. ", "eng": "that ripen as bitter fruit. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp67:0": {"pli": "Kassakavatthu ", "html": "<article id='dhp67'><h2 class='sutta-title'>{}</h2>"}, "dhp67:1": {"pli": "Na taṁ kammaṁ kataṁ sādhu, ", "eng": "It’s not good to do a deed ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp67:2": {"pli": "yaṁ katvā anutappati; ", "eng": "that plagues you later on, ", "html": "<span class='verse-line'>{}</span>"}, "dhp67:3": {"pli": "Yassa assumukho rodaṁ, ", "eng": "for which you weep and wail, ", "html": "<span class='verse-line'>{}</span>"}, "dhp67:4": {"pli": "vipākaṁ paṭisevati. ", "eng": "as its effect stays with you. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp68:0": {"pli": "Sumanamālākāravatthu ", "html": "<article id='dhp68'><h2 class='sutta-title'>{}</h2>"}, "dhp68:1": {"pli": "Tañca kammaṁ kataṁ sādhu, ", "eng": "It is good to do a deed ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp68:2": {"pli": "yaṁ katvā nānutappati; ", "eng": "that doesn’t plague you later on, ", "html": "<span class='verse-line'>{}</span>"}, "dhp68:3": {"pli": "Yassa patīto sumano, ", "eng": "that gladdens and cheers, ", "html": "<span class='verse-line'>{}</span>"}, "dhp68:4": {"pli": "vipākaṁ paṭisevati. ", "eng": "as its effect stays with you. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp69:0": {"pli": "Uppalavaṇṇattherīvatthu ", "html": "<article id='dhp69'><h2 class='sutta-title'>{}</h2>"}, "dhp69:1": {"pli": "Madhuṁvā maññati bālo, ", "eng": "The fool imagines that evil is sweet, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp69:2": {"pli": "yāva pāpaṁ na paccati; ", "eng": "so long as it has not yet ripened. ", "html": "<span class='verse-line'>{}</span>"}, "dhp69:3": {"pli": "Yadā ca paccati pāpaṁ, ", "eng": "But as soon as that evil ripens, ", "html": "<span class='verse-line'>{}</span>"}, "dhp69:4": {"pli": "atha dukkhaṁ nigacchati. ", "eng": "they fall into suffering. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp70:0": {"pli": "Jambukattheravatthu ", "html": "<article id='dhp70'><h2 class='sutta-title'>{}</h2>"}, "dhp70:1": {"pli": "Māse māse kusaggena, ", "eng": "Month after month a fool may eat ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp70:2": {"pli": "bālo bhuñjeyya bhojanaṁ; ", "eng": "food from a grass-blade’s tip; ", "html": "<span class='verse-line'>{}</span>"}, "dhp70:3": {"pli": "Na so saṅkhātadhammānaṁ, ", "eng": "but they’ll never be worth a sixteenth part ", "html": "<span class='verse-line'>{}</span>"}, "dhp70:4": {"pli": "kalaṁ agghati soḷasiṁ. ", "eng": "of one who has appraised the teaching. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp71:0": {"pli": "Ahipetavatthu ", "html": "<article id='dhp71'><h2 class='sutta-title'>{}</h2>"}, "dhp71:1": {"pli": "Na hi pāpaṁ kataṁ kammaṁ, ", "eng": "For a wicked deed that has been done ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp71:2": {"pli": "Sajjukhīraṁva muccati; ", "eng": "does not curdle quickly like milk. ", "html": "<span class='verse-line'>{}</span>"}, "dhp71:3": {"pli": "Ḍahantaṁ bālamanveti, ", "eng": "Smoldering, it follows the fool, ", "html": "<span class='verse-line'>{}</span>"}, "dhp71:4": {"pli": "Bhasmacchannova pāvako. ", "eng": "like a fire smothered over with ash. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp72:0": {"pli": "Saṭṭhikūṭapetavatthu ", "html": "<article id='dhp72'><h2 class='sutta-title'>{}</h2>"}, "dhp72:1": {"pli": "Yāvadeva anatthāya, ", "eng": "Whatever fame a fool may get, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp72:2": {"pli": "ñattaṁ bālassa jāyati; ", "eng": "it only gives rise to harm. ", "html": "<span class='verse-line'>{}</span>"}, "dhp72:3": {"pli": "Hanti bālassa sukkaṁsaṁ, ", "eng": "Whatever good features they have it ruins, ", "html": "<span class='verse-line'>{}</span>"}, "dhp72:4": {"pli": "muddhamassa vipātayaṁ. ", "eng": "and blows their head into bits. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp73:0": {"pli": "Cittagahapativatthu ", "html": "<article id='dhp73'><h2 class='sutta-title'>{}</h2>"}, "dhp73:1": {"pli": "Asantaṁ bhāvanamiccheyya, ", "eng": "They’d seek the esteem that they lack, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp73:2": {"pli": "Purekkhārañca bhikkhusu; ", "eng": "and status among the mendicants; ", "html": "<span class='verse-line'>{}</span>"}, "dhp73:3": {"pli": "Āvāsesu ca issariyaṁ, ", "eng": "authority over monasteries, ", "html": "<span class='verse-line'>{}</span>"}, "dhp73:4": {"pli": "Pūjaṁ parakulesu ca. ", "eng": "and honor among other families. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp74:1": {"pli": "Mameva kata maññantu, ", "eng": "“Let both layfolk and renunciants think ", "html": "<article id='dhp74'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp74:2": {"pli": "gihī pabbajitā ubho; ", "eng": "the work was done by me alone. ", "html": "<span class='verse-line'>{}</span>"}, "dhp74:3": {"pli": "Mamevātivasā assu, ", "eng": "In anything at all that’s to be done, ", "html": "<span class='verse-line'>{}</span>"}, "dhp74:4": {"pli": "kiccākiccesu kismici; ", "eng": "let them fall under my sway alone.” ", "html": "<span class='verse-line'>{}</span>"}, "dhp74:5": {"pli": "Iti bālassa saṅkappo, ", "eng": "So thinks the fool, ", "html": "<span class='verse-line'>{}</span>"}, "dhp74:6": {"pli": "icchā māno ca vaḍḍhati. ", "eng": "their greed and pride only growing. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp75:0": {"pli": "Vanavāsitissasāmaṇeravatthu ", "html": "<article id='dhp75'><h2 class='sutta-title'>{}</h2>"}, "dhp75:1": {"pli": "Aññā hi lābhūpanisā, ", "eng": "For the means to profit and the path to quenching ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp75:2": {"pli": "aññā nibbānagāminī; ", "eng": "are two quite different things. ", "html": "<span class='verse-line'>{}</span>"}, "dhp75:3": {"pli": "Evametaṁ abhiññāya, ", "eng": "A mendicant disciple of the Buddha, ", "html": "<span class='verse-line'>{}</span>"}, "dhp75:4": {"pli": "bhikkhu buddhassa sāvako; ", "eng": "understanding what this really means, ", "html": "<span class='verse-line'>{}</span>"}, "dhp75:5": {"pli": "Sakkāraṁ nābhinandeyya, ", "eng": "would never delight in honors, ", "html": "<span class='verse-line'>{}</span>"}, "dhp75:6": {"pli": "vivekamanubrūhaye. ", "eng": "but rather would foster seclusion. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp75:7": {"pli": "Bālavaggo pañcamo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp76-89": {"dhp76:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp76-89'><header><ul><li class='division'>{}</li>"}, "dhp76:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 76–89 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp76:0.3": {"pli": "Paṇḍitavagga ", "eng": "6. The Astute ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp76:0.4": {"pli": "Rādhattheravatthu ", "html": "<article id='dhp76'><h2 class='sutta-title'>{}</h2>"}, "dhp76:1": {"pli": "Nidhīnaṁva pavattāraṁ, ", "eng": "Regard one who sees your faults ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp76:2": {"pli": "yaṁ passe vajjadassinaṁ; ", "eng": "as a guide to a hidden treasure. ", "html": "<span class='verse-line'>{}</span>"}, "dhp76:3": {"pli": "Niggayhavādiṁ medhāviṁ, ", "eng": "Stay close to one so wise and astute ", "html": "<span class='verse-line'>{}</span>"}, "dhp76:4": {"pli": "tādisaṁ paṇḍitaṁ bhaje; ", "eng": "who corrects you when you need it. ", "html": "<span class='verse-line'>{}</span>"}, "dhp76:5": {"pli": "Tādisaṁ bhajamānassa, ", "eng": "Sticking close to such an impartial person, ", "html": "<span class='verse-line'>{}</span>"}, "dhp76:6": {"pli": "seyyo hoti na pāpiyo. ", "eng": "things get better, not worse. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp77:0": {"pli": "Assajipunabbasukavatthu ", "html": "<article id='dhp77'><h2 class='sutta-title'>{}</h2>"}, "dhp77:1": {"pli": "Ovadeyyānusāseyya, ", "eng": "Advise and instruct; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp77:2": {"pli": "asabbhā ca nivāraye; ", "eng": "curb wickedness: ", "html": "<span class='verse-line'>{}</span>"}, "dhp77:3": {"pli": "Satañhi so piyo hoti, ", "eng": "for you shall be loved by the good, ", "html": "<span class='verse-line'>{}</span>"}, "dhp77:4": {"pli": "asataṁ hoti appiyo. ", "eng": "and disliked by the bad. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp78:0": {"pli": "Channattheravatthu ", "html": "<article id='dhp78'><h2 class='sutta-title'>{}</h2>"}, "dhp78:1": {"pli": "Na bhaje pāpake mitte, ", "eng": "Don’t mix with bad friends, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp78:2": {"pli": "na bhaje purisādhame; ", "eng": "nor with the worst of men. ", "html": "<span class='verse-line'>{}</span>"}, "dhp78:3": {"pli": "Bhajetha mitte kalyāṇe, ", "eng": "Mix with spiritual friends, ", "html": "<span class='verse-line'>{}</span>"}, "dhp78:4": {"pli": "bhajetha purisuttame. ", "eng": "and with the best of men. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp79:0": {"pli": "Mahākappinattheravatthu ", "html": "<article id='dhp79'><h2 class='sutta-title'>{}</h2>"}, "dhp79:1": {"pli": "Dhammapīti sukhaṁ seti, ", "eng": "Through joy in the teaching you sleep at ease, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp79:2": {"pli": "vippasannena cetasā; ", "eng": "with clear and confident heart. ", "html": "<span class='verse-line'>{}</span>"}, "dhp79:3": {"pli": "Ariyappavedite dhamme, ", "eng": "An astute person always delights in the teaching ", "html": "<span class='verse-line'>{}</span>"}, "dhp79:4": {"pli": "sadā ramati paṇḍito. ", "eng": "proclaimed by the Noble One. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp80:0": {"pli": "Paṇḍitasāmaṇeravatthu ", "html": "<article id='dhp80'><h2 class='sutta-title'>{}</h2>"}, "dhp80:1": {"pli": "Udakañhi nayanti nettikā, ", "eng": "Irrigators guide water, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp80:2": {"pli": "Usukārā namayanti tejanaṁ; ", "eng": "fletchers straighten arrows, ", "html": "<span class='verse-line'>{}</span>"}, "dhp80:3": {"pli": "Dāruṁ namayanti tacchakā, ", "eng": "carpenters carve timber, ", "html": "<span class='verse-line'>{}</span>"}, "dhp80:4": {"pli": "Attānaṁ damayanti paṇḍitā. ", "eng": "the astute tame themselves. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp81:0": {"pli": "Lakuṇḍakabhaddiyattheravatthu ", "html": "<article id='dhp81'><h2 class='sutta-title'>{}</h2>"}, "dhp81:1": {"pli": "Selo yathā ekaghano, ", "eng": "As the wind cannot stir ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp81:2": {"pli": "vātena na samīrati; ", "eng": "a solid mass of rock, ", "html": "<span class='verse-line'>{}</span>"}, "dhp81:3": {"pli": "Evaṁ nindāpasaṁsāsu, ", "eng": "so too blame and praise ", "html": "<span class='verse-line'>{}</span>"}, "dhp81:4": {"pli": "na samiñjanti paṇḍitā. ", "eng": "do not affect the wise. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp82:0": {"pli": "Kāṇamātāvatthu ", "html": "<article id='dhp82'><h2 class='sutta-title'>{}</h2>"}, "dhp82:1": {"pli": "Yathāpi rahado gambhīro, ", "eng": "Like a deep lake, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp82:2": {"pli": "vippasanno anāvilo; ", "eng": "clear and unclouded, ", "html": "<span class='verse-line'>{}</span>"}, "dhp82:3": {"pli": "Evaṁ dhammāni sutvāna, ", "eng": "so clear are the astute ", "html": "<span class='verse-line'>{}</span>"}, "dhp82:4": {"pli": "vippasīdanti paṇḍitā. ", "eng": "when they hear the teachings. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp83:0": {"pli": "Pañcasatabhikkhuvatthu ", "html": "<article id='dhp83'><h2 class='sutta-title'>{}</h2>"}, "dhp83:1": {"pli": "Sabbattha ve sappurisā cajanti, ", "eng": "True persons give up everything, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp83:2": {"pli": "Na kāmakāmā lapayanti santo; ", "eng": "they don’t cajole for the things they desire. ", "html": "<span class='verse-line'>{}</span>"}, "dhp83:3": {"pli": "Sukhena phuṭṭhā atha vā dukhena, ", "eng": "Though touched by sadness or happiness, ", "html": "<span class='verse-line'>{}</span>"}, "dhp83:4": {"pli": "Na uccāvacaṁ paṇḍitā dassayanti. ", "eng": "the astute appear neither depressed nor elated. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp84:0": {"pli": "Dhammikattheravatthu ", "html": "<article id='dhp84'><h2 class='sutta-title'>{}</h2>"}, "dhp84:1": {"pli": "Na attahetu na parassa hetu, ", "eng": "Never wish for success by unjust means, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp84:2": {"pli": "Na puttamicche na dhanaṁ na raṭṭhaṁ; ", "eng": "for your own sake or that of another, ", "html": "<span class='verse-line'>{}</span>"}, "dhp84:3": {"pli": "Na iccheyya adhammena samiddhimattano, ", "eng": "desiring children, wealth, or nation; ", "html": "<span class='verse-line'>{}</span>"}, "dhp84:4": {"pli": "Sa sīlavā paññavā dhammiko siyā. ", "eng": "rather, be virtuous, wise, and just. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp85:0": {"pli": "Dhammassavanattheravatthu ", "html": "<article id='dhp85'><h2 class='sutta-title'>{}</h2>"}, "dhp85:1": {"pli": "Appakā te manussesu, ", "eng": "Few are those among humans ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp85:2": {"pli": "ye janā pāragāmino; ", "eng": "who cross to the far shore. ", "html": "<span class='verse-line'>{}</span>"}, "dhp85:3": {"pli": "Athāyaṁ itarā pajā, ", "eng": "The rest just run around ", "html": "<span class='verse-line'>{}</span>"}, "dhp85:4": {"pli": "tīramevānudhāvati. ", "eng": "on the near shore. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp86:1": {"pli": "Ye ca kho sammadakkhāte, ", "eng": "When the teaching is well explained, ", "html": "<article id='dhp86'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp86:2": {"pli": "dhamme dhammānuvattino; ", "eng": "those who practice accordingly ", "html": "<span class='verse-line'>{}</span>"}, "dhp86:3": {"pli": "Te janā pāramessanti, ", "eng": "will cross over ", "html": "<span class='verse-line'>{}</span>"}, "dhp86:4": {"pli": "maccudheyyaṁ suduttaraṁ. ", "eng": "Death’s dominion so hard to pass. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp87:0": {"pli": "Pañcasataāgantukabhikkhuvatthu ", "html": "<article id='dhp87'><h2 class='sutta-title'>{}</h2>"}, "dhp87:1": {"pli": "Kaṇhaṁ dhammaṁ vippahāya, ", "eng": "Rid of dark qualities, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp87:2": {"pli": "Sukkaṁ bhāvetha paṇḍito; ", "eng": "an astute person should develop the bright. ", "html": "<span class='verse-line'>{}</span>"}, "dhp87:3": {"pli": "Okā anokamāgamma, ", "eng": "Leaving home behind ", "html": "<span class='verse-line'>{}</span>"}, "dhp87:4": {"pli": "Viveke yattha dūramaṁ. ", "eng": "for the seclusion so hard to enjoy, ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp88:1": {"pli": "Tatrābhiratimiccheyya, ", "eng": "try to find satisfaction there, ", "html": "<article id='dhp88'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp88:2": {"pli": "hitvā kāme akiñcano; ", "eng": "having left behind sensual pleasures. ", "html": "<span class='verse-line'>{}</span>"}, "dhp88:3": {"pli": "Pariyodapeyya attānaṁ, ", "eng": "Owning nothing, an astute person ", "html": "<span class='verse-line'>{}</span>"}, "dhp88:4": {"pli": "cittaklesehi paṇḍito. ", "eng": "would cleanse themselves of mental corruptions. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp89:1": {"pli": "Yesaṁ sambodhiyaṅgesu, ", "eng": "Those whose minds are rightly developed ", "html": "<article id='dhp89'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp89:2": {"pli": "sammā cittaṁ subhāvitaṁ; ", "eng": "in the awakening factors; ", "html": "<span class='verse-line'>{}</span>"}, "dhp89:3": {"pli": "Ādānapaṭinissagge, ", "eng": "who, letting go of attachments, ", "html": "<span class='verse-line'>{}</span>"}, "dhp89:4": {"pli": "anupādāya ye ratā; ", "eng": "delight in not grasping: ", "html": "<span class='verse-line'>{}</span>"}, "dhp89:5": {"pli": "Khīṇāsavā jutimanto, ", "eng": "with defilements ended, brilliant, ", "html": "<span class='verse-line'>{}</span>"}, "dhp89:6": {"pli": "te loke parinibbutā. ", "eng": "they are quenched in this world. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp89:7": {"pli": "Paṇḍitavaggo chaṭṭho. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp90-99": {"dhp90:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp90-99'><header><ul><li class='division'>{}</li>"}, "dhp90:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 90–99 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp90:0.3": {"pli": "Arahantavagga ", "eng": "7. The Perfected Ones ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp90:0.4": {"pli": "Jīvakapañhavatthu ", "html": "<article id='dhp90'><h2 class='sutta-title'>{}</h2>"}, "dhp90:1": {"pli": "Gataddhino visokassa, ", "eng": "At journey’s end, rid of sorrow; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp90:2": {"pli": "vippamuttassa sabbadhi; ", "eng": "everywhere free, ", "html": "<span class='verse-line'>{}</span>"}, "dhp90:3": {"pli": "Sabbaganthappahīnassa, ", "eng": "all ties given up, ", "html": "<span class='verse-line'>{}</span>"}, "dhp90:4": {"pli": "pariḷāho na vijjati. ", "eng": "no fever is found in them. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp91:0": {"pli": "Mahākassapattheravatthu ", "html": "<article id='dhp91'><h2 class='sutta-title'>{}</h2>"}, "dhp91:1": {"pli": "Uyyuñjanti satīmanto, ", "eng": "The mindful apply themselves; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp91:2": {"pli": "na nikete ramanti te; ", "eng": "they delight in no abode. ", "html": "<span class='verse-line'>{}</span>"}, "dhp91:3": {"pli": "Haṁsāva pallalaṁ hitvā, ", "eng": "Like a swan gone from the marsh, ", "html": "<span class='verse-line'>{}</span>"}, "dhp91:4": {"pli": "okamokaṁ jahanti te. ", "eng": "they leave home after home behind. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp92:0": {"pli": "Belaṭṭhasīsattheravatthu ", "html": "<article id='dhp92'><h2 class='sutta-title'>{}</h2>"}, "dhp92:1": {"pli": "Yesaṁ sannicayo natthi, ", "eng": "Those with nothing stored up, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp92:2": {"pli": "ye pariññātabhojanā; ", "eng": "who have understood their food, ", "html": "<span class='verse-line'>{}</span>"}, "dhp92:3": {"pli": "Suññato animitto ca, ", "eng": "whose domain is the liberation ", "html": "<span class='verse-line'>{}</span>"}, "dhp92:4": {"pli": "vimokkho yesaṁ gocaro; ", "eng": "of the signless and the empty: ", "html": "<span class='verse-line'>{}</span>"}, "dhp92:5": {"pli": "Ākāseva sakuntānaṁ, ", "eng": "their path is hard to trace, ", "html": "<span class='verse-line'>{}</span>"}, "dhp92:6": {"pli": "gati tesaṁ durannayā. ", "eng": "like birds in the sky. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp93:0": {"pli": "Anuruddhattheravatthu ", "html": "<article id='dhp93'><h2 class='sutta-title'>{}</h2>"}, "dhp93:1": {"pli": "Yassāsavā parikkhīṇā, ", "eng": "One whose defilements have ended; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp93:2": {"pli": "āhāre ca anissito; ", "eng": "who’s not attached to food; ", "html": "<span class='verse-line'>{}</span>"}, "dhp93:3": {"pli": "Suññato animitto ca, ", "eng": "whose domain is the liberation ", "html": "<span class='verse-line'>{}</span>"}, "dhp93:4": {"pli": "vimokkho yassa gocaro; ", "eng": "of the signless and the empty: ", "html": "<span class='verse-line'>{}</span>"}, "dhp93:5": {"pli": "Ākāseva sakuntānaṁ, ", "eng": "their track is hard to trace, ", "html": "<span class='verse-line'>{}</span>"}, "dhp93:6": {"pli": "padaṁ tassa durannayaṁ. ", "eng": "like birds in the sky. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp94:0": {"pli": "Mahākaccāyanattheravatthu ", "html": "<article id='dhp94'><h2 class='sutta-title'>{}</h2>"}, "dhp94:1": {"pli": "Yassindriyāni samathaṅgatāni, ", "eng": "Whose faculties have become serene, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp94:2": {"pli": "Assā yathā sārathinā sudantā; ", "eng": "like horses tamed by a charioteer, ", "html": "<span class='verse-line'>{}</span>"}, "dhp94:3": {"pli": "Pahīnamānassa anāsavassa, ", "eng": "who has abandoned conceit and defilements; ", "html": "<span class='verse-line'>{}</span>"}, "dhp94:4": {"pli": "Devāpi tassa pihayanti tādino. ", "eng": "the unaffected one is envied by even the gods. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp95:0": {"pli": "Sāriputtattheravatthu ", "html": "<article id='dhp95'><h2 class='sutta-title'>{}</h2>"}, "dhp95:1": {"pli": "Pathavisamo no virujjhati, ", "eng": "Undisturbed like the earth, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp95:2": {"pli": "Indakhilupamo tādi subbato; ", "eng": "true to their vows, steady as Indra’s pillar, ", "html": "<span class='verse-line'>{}</span>"}, "dhp95:3": {"pli": "Rahadova apetakaddamo, ", "eng": "like a lake clear of mud; ", "html": "<span class='verse-line'>{}</span>"}, "dhp95:4": {"pli": "Saṁsārā na bhavanti tādino. ", "eng": "such a one does not transmigrate. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp96:0": {"pli": "Kosambivāsitissattherasāmaṇeravatthu ", "html": "<article id='dhp96'><h2 class='sutta-title'>{}</h2>"}, "dhp96:1": {"pli": "Santaṁ tassa manaṁ hoti, ", "eng": "Their mind is peaceful, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp96:2": {"pli": "santā vācā ca kamma ca; ", "eng": "peaceful are their speech and deeds. ", "html": "<span class='verse-line'>{}</span>"}, "dhp96:3": {"pli": "Sammadaññāvimuttassa, ", "eng": "Such a one is at peace, ", "html": "<span class='verse-line'>{}</span>"}, "dhp96:4": {"pli": "upasantassa tādino. ", "eng": "rightly freed through enlightenment. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp97:0": {"pli": "Sāriputtattheravatthu ", "html": "<article id='dhp97'><h2 class='sutta-title'>{}</h2>"}, "dhp97:1": {"pli": "Assaddho akataññū ca, ", "eng": "Lacking faith, a house-breaker, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp97:2": {"pli": "sandhicchedo ca yo naro; ", "eng": "one who acknowledges nothing, ", "html": "<span class='verse-line'>{}</span>"}, "dhp97:3": {"pli": "Hatāvakāso vantāso, ", "eng": "purged of hope, they’ve wasted their chance: ", "html": "<span class='verse-line'>{}</span>"}, "dhp97:4": {"pli": "sa ve uttamaporiso. ", "eng": "that is indeed the supreme person! ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp98:0": {"pli": "Khadiravaniyarevatattheravatthu ", "html": "<article id='dhp98'><h2 class='sutta-title'>{}</h2>"}, "dhp98:1": {"pli": "Gāme vā yadi vāraññe, ", "eng": "Whether in village or wilderness, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp98:2": {"pli": "Ninne vā yadi vā thale; ", "eng": "in a valley or the uplands, ", "html": "<span class='verse-line'>{}</span>"}, "dhp98:3": {"pli": "Yattha arahanto viharanti, ", "eng": "wherever the perfected ones live ", "html": "<span class='verse-line'>{}</span>"}, "dhp98:4": {"pli": "Taṁ bhūmirāmaṇeyyakaṁ. ", "eng": "is a delightful place. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp99:0": {"pli": "Aññataraitthivatthu ", "html": "<article id='dhp99'><h2 class='sutta-title'>{}</h2>"}, "dhp99:1": {"pli": "Ramaṇīyāni araññāni, ", "eng": "Delightful are the wildernesses ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp99:2": {"pli": "yattha na ramatī jano; ", "eng": "where no people delight. ", "html": "<span class='verse-line'>{}</span>"}, "dhp99:3": {"pli": "Vītarāgā ramissanti, ", "eng": "Those free of greed will delight there, ", "html": "<span class='verse-line'>{}</span>"}, "dhp99:4": {"pli": "na te kāmagavesino. ", "eng": "not those who seek sensual pleasures. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp99:5": {"pli": "Arahantavaggo sattamo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp100-115": {"dhp100:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp100-115'><header><ul><li class='division'>{}</li>"}, "dhp100:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 100–115 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp100:0.3": {"pli": "Sahassavagga ", "eng": "8. The Thousands ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp100:0.4": {"pli": "Tambadāṭhikacoraghātakavatthu ", "html": "<article id='dhp100'><h2 class='sutta-title'>{}</h2>"}, "dhp100:1": {"pli": "Sahassamapi ce vācā, ", "eng": "Better than a thousand ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp100:2": {"pli": "anatthapadasaṁhitā; ", "eng": "meaningless sayings ", "html": "<span class='verse-line'>{}</span>"}, "dhp100:3": {"pli": "Ekaṁ atthapadaṁ seyyo, ", "eng": "is a single meaningful saying, ", "html": "<span class='verse-line'>{}</span>"}, "dhp100:4": {"pli": "yaṁ sutvā upasammati. ", "eng": "hearing which brings you peace. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp101:0": {"pli": "Bāhiyadārucīriyattheravatthu ", "html": "<article id='dhp101'><h2 class='sutta-title'>{}</h2>"}, "dhp101:1": {"pli": "Sahassamapi ce gāthā, ", "eng": "Better than a thousand ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp101:2": {"pli": "anatthapadasaṁhitā; ", "eng": "meaningless verses ", "html": "<span class='verse-line'>{}</span>"}, "dhp101:3": {"pli": "Ekaṁ gāthāpadaṁ seyyo, ", "eng": "is a single meaningful verse, ", "html": "<span class='verse-line'>{}</span>"}, "dhp101:4": {"pli": "yaṁ sutvā upasammati. ", "eng": "hearing which brings you peace. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp102:0": {"pli": "Kuṇḍalakesitherīvatthu ", "html": "<article id='dhp102'><h2 class='sutta-title'>{}</h2>"}, "dhp102:1": {"pli": "Yo ca gāthā sataṁ bhāse, ", "eng": "Better than reciting ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp102:2": {"pli": "anatthapadasaṁhitā; ", "eng": "a hundred meaningless verses ", "html": "<span class='verse-line'>{}</span>"}, "dhp102:3": {"pli": "Ekaṁ dhammapadaṁ seyyo, ", "eng": "is a single saying of Dhamma, ", "html": "<span class='verse-line'>{}</span>"}, "dhp102:4": {"pli": "yaṁ sutvā upasammati. ", "eng": "hearing which brings you peace. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp103:1": {"pli": "Yo sahassaṁ sahassena, ", "eng": "The supreme conqueror is ", "html": "<article id='dhp103'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp103:2": {"pli": "saṅgāme mānuse jine; ", "eng": "not he who conquers a million men in battle, ", "html": "<span class='verse-line'>{}</span>"}, "dhp103:3": {"pli": "Ekañca jeyyamattānaṁ, ", "eng": "but he who conquers a single man: ", "html": "<span class='verse-line'>{}</span>"}, "dhp103:4": {"pli": "sa ve saṅgāmajuttamo. ", "eng": "himself. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp104:0": {"pli": "Anatthapucchakabrāhmaṇavatthu ", "html": "<article id='dhp104'><h2 class='sutta-title'>{}</h2>"}, "dhp104:1": {"pli": "Attā have jitaṁ seyyo, ", "eng": "It is surely better to conquer oneself ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp104:2": {"pli": "yā cāyaṁ itarā pajā; ", "eng": "than all those other folk. ", "html": "<span class='verse-line'>{}</span>"}, "dhp104:3": {"pli": "Attadantassa posassa, ", "eng": "When a person has tamed themselves, ", "html": "<span class='verse-line'>{}</span>"}, "dhp104:4": {"pli": "niccaṁ saññatacārino. ", "eng": "always living restrained, ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp105:1": {"pli": "Neva devo na gandhabbo, ", "eng": "no god nor centaur, ", "html": "<article id='dhp105'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp105:2": {"pli": "na māro saha brahmunā; ", "eng": "nor Māra nor divinity, ", "html": "<span class='verse-line'>{}</span>"}, "dhp105:3": {"pli": "Jitaṁ apajitaṁ kayirā, ", "eng": "can undo the victory ", "html": "<span class='verse-line'>{}</span>"}, "dhp105:4": {"pli": "tathārūpassa jantuno. ", "eng": "of such a personage. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp106:0": {"pli": "Sāriputtattherassa mātulabrāhmaṇavatthu ", "html": "<article id='dhp106'><h2 class='sutta-title'>{}</h2>"}, "dhp106:1": {"pli": "Māse māse sahassena, ", "eng": "Rather than a thousandfold sacrifice, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp106:2": {"pli": "yo yajetha sataṁ samaṁ; ", "eng": "every month for a full century, ", "html": "<span class='verse-line'>{}</span>"}, "dhp106:3": {"pli": "Ekañca bhāvitattānaṁ, ", "eng": "it’s better to honor for a single hour ", "html": "<span class='verse-line'>{}</span>"}, "dhp106:4": {"pli": "muhuttamapi pūjaye; ", "eng": "one who has developed themselves. ", "html": "<span class='verse-line'>{}</span>"}, "dhp106:5": {"pli": "Sāyeva pūjanā seyyo, ", "eng": "That offering is better ", "html": "<span class='verse-line'>{}</span>"}, "dhp106:6": {"pli": "yañce vassasataṁ hutaṁ. ", "eng": "than the hundred year sacrifice. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp107:0": {"pli": "Sāriputtattherassa bhāgineyyavatthu ", "html": "<article id='dhp107'><h2 class='sutta-title'>{}</h2>"}, "dhp107:1": {"pli": "Yo ca vassasataṁ jantu, ", "eng": "Rather than serve the sacred flame ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp107:2": {"pli": "aggiṁ paricare vane; ", "eng": "in the forest for a hundred years, ", "html": "<span class='verse-line'>{}</span>"}, "dhp107:3": {"pli": "Ekañca bhāvitattānaṁ, ", "eng": "it’s better to honor for a single hour ", "html": "<span class='verse-line'>{}</span>"}, "dhp107:4": {"pli": "muhuttamapi pūjaye; ", "eng": "a personage who has developed themselves. ", "html": "<span class='verse-line'>{}</span>"}, "dhp107:5": {"pli": "Sāyeva pūjanā seyyo, ", "eng": "That offering is better ", "html": "<span class='verse-line'>{}</span>"}, "dhp107:6": {"pli": "yañce vassasataṁ hutaṁ. ", "eng": "than the hundred year sacrifice. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp108:0": {"pli": "Sāriputtatherassa sahāyakabrāhmaṇavatthu ", "html": "<article id='dhp108'><h2 class='sutta-title'>{}</h2>"}, "dhp108:1": {"pli": "Yaṁ kiñci yiṭṭhaṁ va hutaṁ va loke, ", "eng": "Whatever sacrifice or offering in the world ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp108:2": {"pli": "Saṁvaccharaṁ yajetha puññapekkho; ", "eng": "a seeker of merit may make for a year, ", "html": "<span class='verse-line'>{}</span>"}, "dhp108:3": {"pli": "Sabbampi taṁ na catubhāgameti, ", "eng": "none of it is worth a quarter ", "html": "<span class='verse-line'>{}</span>"}, "dhp108:4": {"pli": "Abhivādanā ujjugatesu seyyo. ", "eng": "of bowing to the sincere. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp109:0": {"pli": "Āyuvaḍḍhanakumāravatthu ", "html": "<article id='dhp109'><h2 class='sutta-title'>{}</h2>"}, "dhp109:1": {"pli": "Abhivādanasīlissa, ", "eng": "For one in the habit of bowing, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp109:2": {"pli": "niccaṁ vuḍḍhāpacāyino; ", "eng": "always honoring the elders, ", "html": "<span class='verse-line'>{}</span>"}, "dhp109:3": {"pli": "Cattāro dhammā vaḍḍhanti, ", "eng": "four blessings grow: ", "html": "<span class='verse-line'>{}</span>"}, "dhp109:4": {"pli": "āyu vaṇṇo sukhaṁ balaṁ. ", "eng": "lifespan, beauty, happiness, and strength. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp110:0": {"pli": "Saṅkiccasāmaṇeravatthu ", "html": "<article id='dhp110'><h2 class='sutta-title'>{}</h2>"}, "dhp110:1": {"pli": "Yo ca vassasataṁ jīve, ", "eng": "Better to live a single day ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp110:2": {"pli": "dussīlo asamāhito; ", "eng": "ethical and absorbed in meditation ", "html": "<span class='verse-line'>{}</span>"}, "dhp110:3": {"pli": "Ekāhaṁ jīvitaṁ seyyo, ", "eng": "than to live a hundred years ", "html": "<span class='verse-line'>{}</span>"}, "dhp110:4": {"pli": "sīlavantassa jhāyino. ", "eng": "unethical and lacking immersion. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp111:0": {"pli": "Khāṇukoṇḍaññattheravatthu ", "html": "<article id='dhp111'><h2 class='sutta-title'>{}</h2>"}, "dhp111:1": {"pli": "Yo ca vassasataṁ jīve, ", "eng": "Better to live a single day ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp111:2": {"pli": "duppañño asamāhito; ", "eng": "wise and absorbed in meditation ", "html": "<span class='verse-line'>{}</span>"}, "dhp111:3": {"pli": "Ekāhaṁ jīvitaṁ seyyo, ", "eng": "than to live a hundred years ", "html": "<span class='verse-line'>{}</span>"}, "dhp111:4": {"pli": "paññavantassa jhāyino. ", "eng": "witless and lacking immersion. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp112:0": {"pli": "Sabbadāsattheravatthu ", "html": "<article id='dhp112'><h2 class='sutta-title'>{}</h2>"}, "dhp112:1": {"pli": "Yo ca vassasataṁ jīve, ", "eng": "Better to live a single day ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp112:2": {"pli": "kusīto hīnavīriyo; ", "eng": "energetic and strong, ", "html": "<span class='verse-line'>{}</span>"}, "dhp112:3": {"pli": "Ekāhaṁ jīvitaṁ seyyo, ", "eng": "than to live a hundred years ", "html": "<span class='verse-line'>{}</span>"}, "dhp112:4": {"pli": "vīriyamārabhato daḷhaṁ. ", "eng": "lazy and lacking energy. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp113:0": {"pli": "Paṭācārātherīvatthu ", "html": "<article id='dhp113'><h2 class='sutta-title'>{}</h2>"}, "dhp113:1": {"pli": "Yo ca vassasataṁ jīve, ", "eng": "Better to live a single day ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp113:2": {"pli": "apassaṁ udayabbayaṁ; ", "eng": "seeing rise and fall ", "html": "<span class='verse-line'>{}</span>"}, "dhp113:3": {"pli": "Ekāhaṁ jīvitaṁ seyyo, ", "eng": "than to live a hundred years ", "html": "<span class='verse-line'>{}</span>"}, "dhp113:4": {"pli": "passato udayabbayaṁ. ", "eng": "blind to rise and fall. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp114:0": {"pli": "Kisāgotamīvatthu ", "html": "<article id='dhp114'><h2 class='sutta-title'>{}</h2>"}, "dhp114:1": {"pli": "Yo ca vassasataṁ jīve, ", "eng": "Better to live a single day ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp114:2": {"pli": "apassaṁ amataṁ padaṁ; ", "eng": "seeing the state free of death ", "html": "<span class='verse-line'>{}</span>"}, "dhp114:3": {"pli": "Ekāhaṁ jīvitaṁ seyyo, ", "eng": "than to live a hundred years ", "html": "<span class='verse-line'>{}</span>"}, "dhp114:4": {"pli": "passato amataṁ padaṁ. ", "eng": "blind to the state free of death. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp115:0": {"pli": "Bahuputtikattherīvatthu ", "html": "<article id='dhp115'><h2 class='sutta-title'>{}</h2>"}, "dhp115:1": {"pli": "Yo ca vassasataṁ jīve, ", "eng": "Better to live a single day ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp115:2": {"pli": "apassaṁ dhammamuttamaṁ; ", "eng": "seeing the supreme teaching ", "html": "<span class='verse-line'>{}</span>"}, "dhp115:3": {"pli": "Ekāhaṁ jīvitaṁ seyyo, ", "eng": "than to live a hundred years ", "html": "<span class='verse-line'>{}</span>"}, "dhp115:4": {"pli": "passato dhammamuttamaṁ. ", "eng": "blind to the supreme teaching. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp115:5": {"pli": "Sahassavaggo aṭṭhamo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp116-128": {"dhp116:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp116-128'><header><ul><li class='division'>{}</li>"}, "dhp116:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 116–128 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp116:0.3": {"pli": "Pāpavagga ", "eng": "9. Wickedness ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp116:0.4": {"pli": "Cūḷekasāṭakabrāhmaṇavatthu ", "html": "<article id='dhp116'><h2 class='sutta-title'>{}</h2>"}, "dhp116:1": {"pli": "Abhittharetha kalyāṇe, ", "eng": "Rush to do good, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp116:2": {"pli": "pāpā cittaṁ nivāraye; ", "eng": "shield your mind from evil; ", "html": "<span class='verse-line'>{}</span>"}, "dhp116:3": {"pli": "Dandhañhi karoto puññaṁ, ", "eng": "for when you’re slow to do good, ", "html": "<span class='verse-line'>{}</span>"}, "dhp116:4": {"pli": "pāpasmiṁ ramatī mano. ", "eng": "your thoughts delight in wickedness. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp117:0": {"pli": "Seyyasakattheravatthu ", "html": "<article id='dhp117'><h2 class='sutta-title'>{}</h2>"}, "dhp117:1": {"pli": "Pāpañce puriso kayirā, ", "eng": "If you do something bad, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp117:2": {"pli": "Na naṁ kayirā punappunaṁ; ", "eng": "don’t do it again and again, ", "html": "<span class='verse-line'>{}</span>"}, "dhp117:3": {"pli": "Na tamhi chandaṁ kayirātha, ", "eng": "don’t set your heart on it, ", "html": "<span class='verse-line'>{}</span>"}, "dhp117:4": {"pli": "Dukkho pāpassa uccayo. ", "eng": "for piling up evil is suffering. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp118:0": {"pli": "Lājadevadhītāvatthu ", "html": "<article id='dhp118'><h2 class='sutta-title'>{}</h2>"}, "dhp118:1": {"pli": "Puññañce puriso kayirā, ", "eng": "If you do something good, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp118:2": {"pli": "kayirā naṁ punappunaṁ; ", "eng": "do it again and again, ", "html": "<span class='verse-line'>{}</span>"}, "dhp118:3": {"pli": "Tamhi chandaṁ kayirātha, ", "eng": "set your heart on it, ", "html": "<span class='verse-line'>{}</span>"}, "dhp118:4": {"pli": "sukho puññassa uccayo. ", "eng": "for piling up goodness is joyful. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp119:0": {"pli": "Anāthapiṇḍikaseṭṭhivatthu ", "html": "<article id='dhp119'><h2 class='sutta-title'>{}</h2>"}, "dhp119:1": {"pli": "Pāpopi passati bhadraṁ, ", "eng": "Even the wicked see good things, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp119:2": {"pli": "Yāva pāpaṁ na paccati; ", "eng": "so long as their wickedness has not ripened. ", "html": "<span class='verse-line'>{}</span>"}, "dhp119:3": {"pli": "Yadā ca paccati pāpaṁ, ", "eng": "But as soon as that wickedness ripens, ", "html": "<span class='verse-line'>{}</span>"}, "dhp119:4": {"pli": "Atha pāpo pāpāni passati. ", "eng": "then the wicked see wicked things. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp120:1": {"pli": "Bhadropi passati pāpaṁ, ", "eng": "Even the good see wicked things, ", "html": "<article id='dhp120'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp120:2": {"pli": "Yāva bhadraṁ na paccati; ", "eng": "so long as their goodness has not ripened. ", "html": "<span class='verse-line'>{}</span>"}, "dhp120:3": {"pli": "Yadā ca paccati bhadraṁ, ", "eng": "But as soon as that goodness ripens, ", "html": "<span class='verse-line'>{}</span>"}, "dhp120:4": {"pli": "Atha bhadro bhadrāni passati. ", "eng": "then the good see good things. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp121:0": {"pli": "Asaññataparikkhāravatthu ", "html": "<article id='dhp121'><h2 class='sutta-title'>{}</h2>"}, "dhp121:1": {"pli": "Māvamaññetha pāpassa, ", "eng": "Think not lightly of evil, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp121:2": {"pli": "na mantaṁ āgamissati; ", "eng": "that it won’t come back to you. ", "html": "<span class='verse-line'>{}</span>"}, "dhp121:3": {"pli": "Udabindunipātena, ", "eng": "The pot is filled with water ", "html": "<span class='verse-line'>{}</span>"}, "dhp121:4": {"pli": "udakumbhopi pūrati; ", "eng": "falling drop by drop; ", "html": "<span class='verse-line'>{}</span>"}, "dhp121:5": {"pli": "Bālo pūrati pāpassa, ", "eng": "the fool is filled with wickedness ", "html": "<span class='verse-line'>{}</span>"}, "dhp121:6": {"pli": "thokaṁ thokampi ācinaṁ. ", "eng": "piled up bit by bit. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp122:0": {"pli": "Biḷālapādakaseṭṭhivatthu ", "html": "<article id='dhp122'><h2 class='sutta-title'>{}</h2>"}, "dhp122:1": {"pli": "Māvamaññetha puññassa, ", "eng": "Think not lightly of goodness, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp122:2": {"pli": "na mandaṁ āgamissati; ", "eng": "that it won’t come back to you. ", "html": "<span class='verse-line'>{}</span>"}, "dhp122:3": {"pli": "Udabindunipātena, ", "eng": "The pot is filled with water ", "html": "<span class='verse-line'>{}</span>"}, "dhp122:4": {"pli": "udakumbhopi pūrati; ", "eng": "falling drop by drop; ", "html": "<span class='verse-line'>{}</span>"}, "dhp122:5": {"pli": "Dhīro pūrati puññassa, ", "eng": "the attentive one is filled with goodness ", "html": "<span class='verse-line'>{}</span>"}, "dhp122:6": {"pli": "thokaṁ thokampi ācinaṁ. ", "eng": "piled up bit by bit. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp123:0": {"pli": "Mahādhanavāṇijavatthu ", "html": "<article id='dhp123'><h2 class='sutta-title'>{}</h2>"}, "dhp123:1": {"pli": "Vāṇijova bhayaṁ maggaṁ, ", "eng": "Avoid wickedness, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp123:2": {"pli": "appasattho mahaddhano; ", "eng": "as a merchant with rich cargo and small escort ", "html": "<span class='verse-line'>{}</span>"}, "dhp123:3": {"pli": "Visaṁ jīvitukāmova, ", "eng": "would avoid a dangerous road, ", "html": "<span class='verse-line'>{}</span>"}, "dhp123:4": {"pli": "pāpāni parivajjaye. ", "eng": "or one who loves life would avoid drinking poison. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp124:0": {"pli": "Kukkuṭamittanesādavatthu ", "html": "<article id='dhp124'><h2 class='sutta-title'>{}</h2>"}, "dhp124:1": {"pli": "Pāṇimhi ce vaṇo nāssa, ", "eng": "You can carry poison in your hand ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp124:2": {"pli": "hareyya pāṇinā visaṁ; ", "eng": "if it has no wound, ", "html": "<span class='verse-line'>{}</span>"}, "dhp124:3": {"pli": "Nābbaṇaṁ visamanveti, ", "eng": "for poison does not infect without a wound; ", "html": "<span class='verse-line'>{}</span>"}, "dhp124:4": {"pli": "natthi pāpaṁ akubbato. ", "eng": "nothing bad happens unless you do bad. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp125:0": {"pli": "Kokasunakhaluddakavatthu ", "html": "<article id='dhp125'><h2 class='sutta-title'>{}</h2>"}, "dhp125:1": {"pli": "Yo appaduṭṭhassa narassa dussati, ", "eng": "Whoever wrongs a man who has done no wrong, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp125:2": {"pli": "Suddhassa posassa anaṅgaṇassa; ", "eng": "a pure man who has not a blemish, ", "html": "<span class='verse-line'>{}</span>"}, "dhp125:3": {"pli": "Tameva bālaṁ pacceti pāpaṁ, ", "eng": "the evil backfires on the fool, ", "html": "<span class='verse-line'>{}</span>"}, "dhp125:4": {"pli": "Sukhumo rajo paṭivātaṁva khitto. ", "eng": "like fine dust thrown upwind. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp126:0": {"pli": "Maṇikārakulūpakatissattheravatthu ", "html": "<article id='dhp126'><h2 class='sutta-title'>{}</h2>"}, "dhp126:1": {"pli": "Gabbhameke uppajjanti, ", "eng": "Some are born in a womb; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp126:2": {"pli": "nirayaṁ pāpakammino; ", "eng": "evil-doers go to hell; ", "html": "<span class='verse-line'>{}</span>"}, "dhp126:3": {"pli": "Saggaṁ sugatino yanti, ", "eng": "the virtuous go to heaven; ", "html": "<span class='verse-line'>{}</span>"}, "dhp126:4": {"pli": "parinibbanti anāsavā. ", "eng": "the undefiled are fully extinguished. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp127:0": {"pli": "Tayojanavatthu ", "html": "<article id='dhp127'><h2 class='sutta-title'>{}</h2>"}, "dhp127:1": {"pli": "Na antalikkhe na samuddamajjhe, ", "eng": "Not in midair, nor mid-ocean, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp127:2": {"pli": "Na pabbatānaṁ vivaraṁ pavissa; ", "eng": "nor hiding in a mountain cleft; ", "html": "<span class='verse-line'>{}</span>"}, "dhp127:3": {"pli": "Na vijjatī so jagatippadeso, ", "eng": "you’ll find no place on the planet ", "html": "<span class='verse-line'>{}</span>"}, "dhp127:4": {"pli": "Yatthaṭṭhito mucceyya pāpakammā. ", "eng": "to escape your wicked deeds. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp128:0": {"pli": "Suppabuddhasakyavatthu ", "html": "<article id='dhp128'><h2 class='sutta-title'>{}</h2>"}, "dhp128:1": {"pli": "Na antalikkhe na samuddamajjhe, ", "eng": "Not in midair, nor mid-ocean, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp128:2": {"pli": "Na pabbatānaṁ vivaraṁ pavissa; ", "eng": "nor hiding in a mountain cleft; ", "html": "<span class='verse-line'>{}</span>"}, "dhp128:3": {"pli": "Na vijjatī so jagatippadeso, ", "eng": "you’ll find no place on the planet ", "html": "<span class='verse-line'>{}</span>"}, "dhp128:4": {"pli": "Yatthaṭṭhitaṁ nappasaheyya maccu. ", "eng": "where you won’t be vanquished by death. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp128:5": {"pli": "Pāpavaggo navamo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp129-145": {"dhp129:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp129-145'><header><ul><li class='division'>{}</li>"}, "dhp129:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 129–145 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp129:0.3": {"pli": "Daṇḍavagga ", "eng": "10. The Rod ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp129:0.4": {"pli": "Chabbaggiyabhikkhuvatthu ", "html": "<article id='dhp129'><h2 class='sutta-title'>{}</h2>"}, "dhp129:1": {"pli": "Sabbe tasanti daṇḍassa, ", "eng": "All tremble at the rod, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp129:2": {"pli": "sabbe bhāyanti maccuno; ", "eng": "all fear death. ", "html": "<span class='verse-line'>{}</span>"}, "dhp129:3": {"pli": "Attānaṁ upamaṁ katvā, ", "eng": "Treating others like oneself, ", "html": "<span class='verse-line'>{}</span>"}, "dhp129:4": {"pli": "na haneyya na ghātaye. ", "eng": "neither kill nor incite to kill. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp130:0": {"pli": "Chabbaggiyavatthu ", "html": "<article id='dhp130'><h2 class='sutta-title'>{}</h2>"}, "dhp130:1": {"pli": "Sabbe tasanti daṇḍassa, ", "eng": "All tremble at the rod, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp130:2": {"pli": "sabbesaṁ jīvitaṁ piyaṁ; ", "eng": "all love life. ", "html": "<span class='verse-line'>{}</span>"}, "dhp130:3": {"pli": "Attānaṁ upamaṁ katvā, ", "eng": "Treating others like oneself, ", "html": "<span class='verse-line'>{}</span>"}, "dhp130:4": {"pli": "na haneyya na ghātaye. ", "eng": "neither kill nor incite to kill. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp131:0": {"pli": "Sambahulakumārakavatthu ", "html": "<article id='dhp131'><h2 class='sutta-title'>{}</h2>"}, "dhp131:1": {"pli": "Sukhakāmāni bhūtāni, ", "eng": "Creatures love happiness, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp131:2": {"pli": "yo daṇḍena vihiṁsati; ", "eng": "so if you harm them with a stick ", "html": "<span class='verse-line'>{}</span>", "comm": "This verse parallels <a href='https://suttacentral.net/ud2.3/en/sujato'>Ud 2.3</a> where it is clear an actual stick is meant. "}, "dhp131:3": {"pli": "Attano sukhamesāno, ", "eng": "in search of your own happiness, ", "html": "<span class='verse-line'>{}</span>"}, "dhp131:4": {"pli": "pecca so na labhate sukhaṁ. ", "eng": "after death you won’t find happiness. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp132:1": {"pli": "Sukhakāmāni bhūtāni, ", "eng": "Creatures love happiness, ", "html": "<article id='dhp132'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp132:2": {"pli": "yo daṇḍena na hiṁsati; ", "eng": "so if you don’t harm them with a stick ", "html": "<span class='verse-line'>{}</span>"}, "dhp132:3": {"pli": "Attano sukhamesāno, ", "eng": "in search of your own happiness, ", "html": "<span class='verse-line'>{}</span>"}, "dhp132:4": {"pli": "pecca so labhate sukhaṁ. ", "eng": "after death you will find happiness. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp133:0": {"pli": "Koṇḍadhānattheravatthu ", "html": "<article id='dhp133'><h2 class='sutta-title'>{}</h2>"}, "dhp133:1": {"pli": "Māvoca pharusaṁ kañci, ", "eng": "Don’t speak harshly, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp133:2": {"pli": "vuttā paṭivadeyyu taṁ; ", "eng": "they may speak harshly back. ", "html": "<span class='verse-line'>{}</span>"}, "dhp133:3": {"pli": "Dukkhā hi sārambhakathā, ", "eng": "For aggressive speech is painful, ", "html": "<span class='verse-line'>{}</span>"}, "dhp133:4": {"pli": "paṭidaṇḍā phuseyyu taṁ. ", "eng": "and the rod may spring back on you. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp134:1": {"pli": "Sace neresi attānaṁ, ", "eng": "If you still yourself ", "html": "<article id='dhp134'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp134:2": {"pli": "kaṁso upahato yathā; ", "eng": "like a broken gong, ", "html": "<span class='verse-line'>{}</span>"}, "dhp134:3": {"pli": "Esa pattosi nibbānaṁ, ", "eng": "you reach extinguishment ", "html": "<span class='verse-line'>{}</span>"}, "dhp134:4": {"pli": "sārambho te na vijjati. ", "eng": "and know no conflict. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp135:0": {"pli": "Uposathikaitthīnaṁvatthu ", "html": "<article id='dhp135'><h2 class='sutta-title'>{}</h2>"}, "dhp135:1": {"pli": "Yathā daṇḍena gopālo, ", "eng": "As a cowherd drives the cows ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp135:2": {"pli": "gāvo pājeti gocaraṁ; ", "eng": "to pasture with the rod, ", "html": "<span class='verse-line'>{}</span>"}, "dhp135:3": {"pli": "Evaṁ jarā ca maccu ca, ", "eng": "so too old age and death ", "html": "<span class='verse-line'>{}</span>"}, "dhp135:4": {"pli": "āyuṁ pājenti pāṇinaṁ. ", "eng": "drive life from living beings. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp136:0": {"pli": "Ajagarapetavatthu ", "html": "<article id='dhp136'><h2 class='sutta-title'>{}</h2>"}, "dhp136:1": {"pli": "Atha pāpāni kammāni, ", "eng": "The fool does not understand ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp136:2": {"pli": "karaṁ bālo na bujjhati; ", "eng": "the evil that they do. ", "html": "<span class='verse-line'>{}</span>"}, "dhp136:3": {"pli": "Sehi kammehi dummedho, ", "eng": "But because of those deeds, that simpleton ", "html": "<span class='verse-line'>{}</span>"}, "dhp136:4": {"pli": "aggidaḍḍhova tappati. ", "eng": "is tormented as if burnt by fire. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp137:0": {"pli": "Mahāmoggallānattheravatthu ", "html": "<article id='dhp137'><h2 class='sutta-title'>{}</h2>"}, "dhp137:1": {"pli": "Yo daṇḍena adaṇḍesu, ", "eng": "One who violently attacks ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp137:2": {"pli": "appaduṭṭhesu dussati; ", "eng": "the peaceful and the innocent ", "html": "<span class='verse-line'>{}</span>"}, "dhp137:3": {"pli": "Dasannamaññataraṁ ṭhānaṁ, ", "eng": "swiftly falls ", "html": "<span class='verse-line'>{}</span>"}, "dhp137:4": {"pli": "khippameva nigacchati. ", "eng": "to one of ten bad states: ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp138:1": {"pli": "Vedanaṁ pharusaṁ jāniṁ, ", "eng": "harsh pain; loss; ", "html": "<article id='dhp138'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp138:2": {"pli": "sarīrassa va bhedanaṁ; ", "eng": "the breakup of the body; ", "html": "<span class='verse-line'>{}</span>"}, "dhp138:3": {"pli": "Garukaṁ vāpi ābādhaṁ, ", "eng": "serious illness; ", "html": "<span class='verse-line'>{}</span>"}, "dhp138:4": {"pli": "cittakkhepaṁ va pāpuṇe. ", "eng": "mental distress; ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp139:1": {"pli": "Rājato vā upasaggaṁ, ", "eng": "hazards from rulers; ", "html": "<article id='dhp139'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp139:2": {"pli": "Abbhakkhānaṁ va dāruṇaṁ; ", "eng": "vicious slander; ", "html": "<span class='verse-line'>{}</span>"}, "dhp139:3": {"pli": "Parikkhayaṁ va ñātīnaṁ, ", "eng": "loss of kin; ", "html": "<span class='verse-line'>{}</span>"}, "dhp139:4": {"pli": "Bhogānaṁ va pabhaṅguraṁ. ", "eng": "destruction of wealth; ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp140:1": {"pli": "Atha vāssa agārāni, ", "eng": "or else their home ", "html": "<article id='dhp140'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp140:2": {"pli": "aggi ḍahati pāvako; ", "eng": "is consumed by fire. ", "html": "<span class='verse-line'>{}</span>"}, "dhp140:3": {"pli": "Kāyassa bhedā duppañño, ", "eng": "When their body breaks up, that witless person ", "html": "<span class='verse-line'>{}</span>"}, "dhp140:4": {"pli": "nirayaṁ sopapajjati. ", "eng": "is reborn in hell. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp141:0": {"pli": "Bahubhaṇḍikabhikkhuvatthu ", "html": "<article id='dhp141'><h2 class='sutta-title'>{}</h2>"}, "dhp141:1": {"pli": "Na naggacariyā na jaṭā na paṅkā, ", "eng": "Not nudity, nor matted hair, nor mud, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp141:2": {"pli": "Nānāsakā thaṇḍilasāyikā vā; ", "eng": "nor fasting, nor lying on bare ground, ", "html": "<span class='verse-line'>{}</span>"}, "dhp141:3": {"pli": "Rajojallaṁ ukkuṭikappadhānaṁ, ", "eng": "nor wearing dust and dirt, or squatting on the heels, ", "html": "<span class='verse-line'>{}</span>"}, "dhp141:4": {"pli": "Sodhenti maccaṁ avitiṇṇakaṅkhaṁ. ", "eng": "will cleanse a mortal not free of doubt. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp142:0": {"pli": "Santatimahāmattavatthu ", "html": "<article id='dhp142'><h2 class='sutta-title'>{}</h2>"}, "dhp142:1": {"pli": "Alaṅkato cepi samaṁ careyya, ", "eng": "Dressed up they may be, but if they live well—", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp142:2": {"pli": "Santo danto niyato brahmacārī; ", "eng": "peaceful, tamed, committed to the spiritual path, ", "html": "<span class='verse-line'>{}</span>"}, "dhp142:3": {"pli": "Sabbesu bhūtesu nidhāya daṇḍaṁ, ", "eng": "having laid aside violence towards all creatures—", "html": "<span class='verse-line'>{}</span>"}, "dhp142:4": {"pli": "So brāhmaṇo so samaṇo sa bhikkhu. ", "eng": "they are a brahmin, an ascetic, a mendicant. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp143:0": {"pli": "Pilotikatissattheravatthu ", "html": "<article id='dhp143'><h2 class='sutta-title'>{}</h2>"}, "dhp143:1": {"pli": "Hirīnisedho puriso, ", "eng": "Can a person constrained by conscience ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp143:2": {"pli": "koci lokasmi vijjati; ", "eng": "be found in the world? ", "html": "<span class='verse-line'>{}</span>"}, "dhp143:3": {"pli": "Yo niddaṁ apabodheti, ", "eng": "Who shies away from blame, ", "html": "<span class='verse-line'>{}</span>"}, "dhp143:4": {"pli": "asso bhadro kasāmiva. ", "eng": "like a fine horse from the whip? ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp144:1": {"pli": "Asso yathā bhadro kasāniviṭṭho, ", "eng": "Like a fine horse under the whip, ", "html": "<article id='dhp144'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp144:2": {"pli": "Ātāpino saṁvegino bhavātha; ", "eng": "be keen and full of urgency. ", "html": "<span class='verse-line'>{}</span>"}, "dhp144:3": {"pli": "Saddhāya sīlena ca vīriyena ca, ", "eng": "With faith, ethics, and energy, ", "html": "<span class='verse-line'>{}</span>"}, "dhp144:4": {"pli": "Samādhinā dhammavinicchayena ca; ", "eng": "immersion, and investigation of principles, ", "html": "<span class='verse-line'>{}</span>"}, "dhp144:5": {"pli": "Sampannavijjācaraṇā patissatā, ", "eng": "accomplished in knowledge and conduct, mindful, ", "html": "<span class='verse-line'>{}</span>"}, "dhp144:6": {"pli": "Jahissatha dukkhamidaṁ anappakaṁ. ", "eng": "give up this vast suffering. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp145:0": {"pli": "Sukhasāmaṇeravatthu ", "html": "<article id='dhp145'><h2 class='sutta-title'>{}</h2>"}, "dhp145:1": {"pli": "Udakañhi nayanti nettikā, ", "eng": "While irrigators guide water, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp145:2": {"pli": "Usukārā namayanti tejanaṁ; ", "eng": "fletchers shape arrows, ", "html": "<span class='verse-line'>{}</span>"}, "dhp145:3": {"pli": "Dāruṁ namayanti tacchakā, ", "eng": "and carpenters carve timber—", "html": "<span class='verse-line'>{}</span>"}, "dhp145:4": {"pli": "Attānaṁ damayanti subbatā. ", "eng": "those true to their vows tame themselves. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp145:5": {"pli": "Daṇḍavaggo dasamo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp146-156": {"dhp146:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp146-156'><header><ul><li class='division'>{}</li>"}, "dhp146:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 146–156 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp146:0.3": {"pli": "Jarāvagga ", "eng": "11. Old Age ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp146:0.4": {"pli": "Visākhāyasahāyikānaṁvatthu ", "html": "<article id='dhp146'><h2 class='sutta-title'>{}</h2>"}, "dhp146:1": {"pli": "Ko nu hāso kimānando, ", "eng": "What is joy, what is laughter, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp146:2": {"pli": "niccaṁ pajjalite sati; ", "eng": "when the flames are ever burning? ", "html": "<span class='verse-line'>{}</span>"}, "dhp146:3": {"pli": "Andhakārena onaddhā, ", "eng": "Shrouded by darkness, ", "html": "<span class='verse-line'>{}</span>"}, "dhp146:4": {"pli": "padīpaṁ na gavesatha. ", "eng": "would you not seek a light? ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp147:0": {"pli": "Sirimāvatthu ", "html": "<article id='dhp147'><h2 class='sutta-title'>{}</h2>"}, "dhp147:1": {"pli": "Passa cittakataṁ bimbaṁ, ", "eng": "See this fancy puppet, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp147:2": {"pli": "arukāyaṁ samussitaṁ; ", "eng": "a body built of sores, ", "html": "<span class='verse-line'>{}</span>"}, "dhp147:3": {"pli": "Āturaṁ bahusaṅkappaṁ, ", "eng": "diseased, obsessed over, ", "html": "<span class='verse-line'>{}</span>"}, "dhp147:4": {"pli": "yassa natthi dhuvaṁ ṭhiti. ", "eng": "in which nothing lasts at all. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp148:0": {"pli": "Uttarātherīvatthu ", "html": "<article id='dhp148'><h2 class='sutta-title'>{}</h2>"}, "dhp148:1": {"pli": "Parijiṇṇamidaṁ rūpaṁ, ", "eng": "This body is decrepit and frail, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp148:2": {"pli": "roganīḷaṁ pabhaṅguraṁ; ", "eng": "a nest of disease. ", "html": "<span class='verse-line'>{}</span>"}, "dhp148:3": {"pli": "Bhijjati pūtisandeho, ", "eng": "This foul carcass falls apart, ", "html": "<span class='verse-line'>{}</span>"}, "dhp148:4": {"pli": "maraṇantañhi jīvitaṁ. ", "eng": "for life ends in death. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp149:0": {"pli": "Sambahulaadhimānikabhikkhuvatthu ", "html": "<article id='dhp149'><h2 class='sutta-title'>{}</h2>"}, "dhp149:1": {"pli": "Yānimāni apatthāni, ", "eng": "These dove-grey bones ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp149:2": {"pli": "alābūneva sārade; ", "eng": "are tossed away like ", "html": "<span class='verse-line'>{}</span>"}, "dhp149:3": {"pli": "Kāpotakāni aṭṭhīni, ", "eng": "dried gourds in the autumn—", "html": "<span class='verse-line'>{}</span>"}, "dhp149:4": {"pli": "tāni disvāna kā rati. ", "eng": "what joy is there in such a sight? ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp150:0": {"pli": "Janapadakalyāṇīrūpanandātherīvatthu ", "html": "<article id='dhp150'><h2 class='sutta-title'>{}</h2>"}, "dhp150:1": {"pli": "Aṭṭhīnaṁ nagaraṁ kataṁ, ", "eng": "In this city built of bones, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp150:2": {"pli": "maṁsalohitalepanaṁ; ", "eng": "plastered with flesh and blood, ", "html": "<span class='verse-line'>{}</span>"}, "dhp150:3": {"pli": "Yattha jarā ca maccu ca, ", "eng": "old age and death are stashed away, ", "html": "<span class='verse-line'>{}</span>"}, "dhp150:4": {"pli": "māno makkho ca ohito. ", "eng": "along with conceit and contempt. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp151:0": {"pli": "Mallikādevīvatthu ", "html": "<article id='dhp151'><h2 class='sutta-title'>{}</h2>"}, "dhp151:1": {"pli": "Jīranti ve rājarathā sucittā, ", "eng": "Fancy chariots of kings wear out, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp151:2": {"pli": "Atho sarīrampi jaraṁ upeti; ", "eng": "and even this body gets old. ", "html": "<span class='verse-line'>{}</span>"}, "dhp151:3": {"pli": "Satañca dhammo na jaraṁ upeti, ", "eng": "But the truth of the good never gets old—", "html": "<span class='verse-line'>{}</span>"}, "dhp151:4": {"pli": "Santo have sabbhi pavedayanti. ", "eng": "so the good proclaim to the good. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp152:0": {"pli": "Lāḷudāyītheravatthu ", "html": "<article id='dhp152'><h2 class='sutta-title'>{}</h2>"}, "dhp152:1": {"pli": "Appassutāyaṁ puriso, ", "eng": "A person of little learning ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp152:2": {"pli": "balībaddhova jīrati; ", "eng": "ages like an ox—", "html": "<span class='verse-line'>{}</span>"}, "dhp152:3": {"pli": "Maṁsāni tassa vaḍḍhanti, ", "eng": "their flesh grows, ", "html": "<span class='verse-line'>{}</span>"}, "dhp152:4": {"pli": "paññā tassa na vaḍḍhati. ", "eng": "but not their wisdom. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp153:0": {"pli": "Udānavatthu ", "html": "<article id='dhp153'><h2 class='sutta-title'>{}</h2>"}, "dhp153:1": {"pli": "Anekajātisaṁsāraṁ, ", "eng": "Transmigrating through countless rebirths, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp153:2": {"pli": "sandhāvissaṁ anibbisaṁ; ", "eng": "I’ve journeyed without reward, ", "html": "<span class='verse-line'>{}</span>"}, "dhp153:3": {"pli": "Gahakāraṁ gavesanto, ", "eng": "searching for the house-builder; ", "html": "<span class='verse-line'>{}</span>"}, "dhp153:4": {"pli": "dukkhā jāti punappunaṁ. ", "eng": "painful is birth again and again. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp154:1": {"pli": "Gahakāraka diṭṭhosi, ", "eng": "I’ve seen you, house-builder! ", "html": "<article id='dhp154'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp154:2": {"pli": "puna gehaṁ na kāhasi; ", "eng": "You won’t build a house again! ", "html": "<span class='verse-line'>{}</span>"}, "dhp154:3": {"pli": "Sabbā te phāsukā bhaggā, ", "eng": "Your rafters are all broken, ", "html": "<span class='verse-line'>{}</span>"}, "dhp154:4": {"pli": "gahakūṭaṁ visaṅkhataṁ; ", "eng": "your roof-peak is demolished. ", "html": "<span class='verse-line'>{}</span>"}, "dhp154:5": {"pli": "Visaṅkhāragataṁ cittaṁ, ", "eng": "My mind, set on demolition, ", "html": "<span class='verse-line'>{}</span>"}, "dhp154:6": {"pli": "taṇhānaṁ khayamajjhagā. ", "eng": "has reached the end of craving. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp155:0": {"pli": "Mahādhanaseṭṭhiputtavatthu ", "html": "<article id='dhp155'><h2 class='sutta-title'>{}</h2>"}, "dhp155:1": {"pli": "Acaritvā brahmacariyaṁ, ", "eng": "When young they spurned the spiritual path ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp155:2": {"pli": "aladdhā yobbane dhanaṁ; ", "eng": "and failed to earn any wealth. ", "html": "<span class='verse-line'>{}</span>"}, "dhp155:3": {"pli": "Jiṇṇakoñcāva jhāyanti, ", "eng": "Now they brood like old cranes ", "html": "<span class='verse-line'>{}</span>"}, "dhp155:4": {"pli": "khīṇamaccheva pallale. ", "eng": "in a pond bereft of fish. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp156:1": {"pli": "Acaritvā brahmacariyaṁ, ", "eng": "When young they spurned the spiritual path ", "html": "<article id='dhp156'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp156:2": {"pli": "aladdhā yobbane dhanaṁ; ", "eng": "and failed to earn any wealth. ", "html": "<span class='verse-line'>{}</span>"}, "dhp156:3": {"pli": "Senti cāpātikhīṇāva, ", "eng": "Now they lie like spent arrows, ", "html": "<span class='verse-line'>{}</span>"}, "dhp156:4": {"pli": "purāṇāni anutthunaṁ. ", "eng": "bemoaning over things past. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp156:5": {"pli": "Jarāvaggo ekādasamo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp157-166": {"dhp157:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp157-166'><header><ul><li class='division'>{}</li>"}, "dhp157:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 157–166 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp157:0.3": {"pli": "Attavagga ", "eng": "12. The Self ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp157:0.4": {"pli": "Bodhirājakumāravatthu ", "html": "<article id='dhp157'><h2 class='sutta-title'>{}</h2>"}, "dhp157:1": {"pli": "Attānañce piyaṁ jaññā, ", "eng": "If you knew your self as beloved, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp157:2": {"pli": "rakkheyya naṁ surakkhitaṁ; ", "eng": "you’d look after it so well. ", "html": "<span class='verse-line'>{}</span>"}, "dhp157:3": {"pli": "Tiṇṇaṁ aññataraṁ yāmaṁ, ", "eng": "In one of the night’s three watches, ", "html": "<span class='verse-line'>{}</span>"}, "dhp157:4": {"pli": "paṭijaggeyya paṇḍito. ", "eng": "an astute person would remain alert. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp158:0": {"pli": "Upanandasakyaputtattheravatthu ", "html": "<article id='dhp158'><h2 class='sutta-title'>{}</h2>"}, "dhp158:1": {"pli": "Attānameva paṭhamaṁ, ", "eng": "The astute would avoid being corrupted ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp158:2": {"pli": "patirūpe nivesaye; ", "eng": "by first grounding themselves ", "html": "<span class='verse-line'>{}</span>"}, "dhp158:3": {"pli": "Athaññamanusāseyya, ", "eng": "in what is suitable, ", "html": "<span class='verse-line'>{}</span>"}, "dhp158:4": {"pli": "na kilisseyya paṇḍito. ", "eng": "and then instructing others. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp159:0": {"pli": "Padhānikatissattheravatthu ", "html": "<article id='dhp159'><h2 class='sutta-title'>{}</h2>"}, "dhp159:1": {"pli": "Attānañce tathā kayirā, ", "eng": "If one were to treat oneself ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>", "comm": "Accept Pali reading of <i lang='pi' translate='no'>attānaṁ</i> as accusative, but note the Prakrit variant <i lang='pi' translate='no'>attanā</i> in instrumental. "}, "dhp159:2": {"pli": "yathāññamanusāsati; ", "eng": "as one instructs another, ", "html": "<span class='verse-line'>{}</span>"}, "dhp159:3": {"pli": "Sudanto vata dametha, ", "eng": "the well-tamed indeed would tame: ", "html": "<span class='verse-line'>{}</span>", "comm": "Read <i lang='pi' translate='no'>dametha</i> as third singular optative reflexive. "}, "dhp159:4": {"pli": "attā hi kira duddamo. ", "eng": "for the self, it seems, is hard to tame. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp160:0": {"pli": "Kumārakassapamātuttherivatthu ", "html": "<article id='dhp160'><h2 class='sutta-title'>{}</h2>"}, "dhp160:1": {"pli": "Attā hi attano nātho, ", "eng": "One is indeed the lord of oneself, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp160:2": {"pli": "ko hi nātho paro siyā; ", "eng": "for who else would be one’s lord? ", "html": "<span class='verse-line'>{}</span>"}, "dhp160:3": {"pli": "Attanā hi sudantena, ", "eng": "By means of a well-tamed self, ", "html": "<span class='verse-line'>{}</span>"}, "dhp160:4": {"pli": "nāthaṁ labhati dullabhaṁ. ", "eng": "one gains a lord that’s rare indeed. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp161:0": {"pli": "Mahākālaupāsakavatthu ", "html": "<article id='dhp161'><h2 class='sutta-title'>{}</h2>"}, "dhp161:1": {"pli": "Attanā hi kataṁ pāpaṁ, ", "eng": "For the evil that is done by oneself, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp161:2": {"pli": "Attajaṁ attasambhavaṁ; ", "eng": "born and produced in oneself, ", "html": "<span class='verse-line'>{}</span>"}, "dhp161:3": {"pli": "Abhimatthati dummedhaṁ, ", "eng": "grinds down a simpleton, ", "html": "<span class='verse-line'>{}</span>"}, "dhp161:4": {"pli": "Vajiraṁvasmamayaṁ maṇiṁ. ", "eng": "as diamond grinds a lesser gem. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp162:0": {"pli": "Devadattavatthu ", "html": "<article id='dhp162'><h2 class='sutta-title'>{}</h2>"}, "dhp162:1": {"pli": "Yassa accantadussilyaṁ, ", "eng": "One choked by immorality, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp162:2": {"pli": "māluvā sālamivotthataṁ; ", "eng": "as a sal tree by a creeper, ", "html": "<span class='verse-line'>{}</span>"}, "dhp162:3": {"pli": "Karoti so tathattānaṁ, ", "eng": "does to themselves ", "html": "<span class='verse-line'>{}</span>"}, "dhp162:4": {"pli": "yathā naṁ icchatī diso. ", "eng": "what a foe only wishes. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp163:0": {"pli": "Saṅghabhedaparisakkanavatthu ", "html": "<article id='dhp163'><h2 class='sutta-title'>{}</h2>"}, "dhp163:1": {"pli": "Sukarāni asādhūni, ", "eng": "It’s easy to do bad things ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp163:2": {"pli": "attano ahitāni ca; ", "eng": "harmful to oneself, ", "html": "<span class='verse-line'>{}</span>"}, "dhp163:3": {"pli": "Yaṁ ve hitañca sādhuñca, ", "eng": "but good things that are helpful ", "html": "<span class='verse-line'>{}</span>"}, "dhp163:4": {"pli": "taṁ ve paramadukkaraṁ. ", "eng": "are the hardest things to do. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp164:0": {"pli": "Kālattheravatthu ", "html": "<article id='dhp164'><h2 class='sutta-title'>{}</h2>"}, "dhp164:1": {"pli": "Yo sāsanaṁ arahataṁ, ", "eng": "On account of wicked views—", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp164:2": {"pli": "ariyānaṁ dhammajīvinaṁ; ", "eng": "scorning the guidance ", "html": "<span class='verse-line'>{}</span>"}, "dhp164:3": {"pli": "Paṭikkosati dummedho, ", "eng": "of the perfected ones, ", "html": "<span class='verse-line'>{}</span>"}, "dhp164:4": {"pli": "diṭṭhiṁ nissāya pāpikaṁ; ", "eng": "the noble ones living righteously—", "html": "<span class='verse-line'>{}</span>"}, "dhp164:5": {"pli": "Phalāni kaṭṭhakasseva, ", "eng": "the idiot begets their own self’s demise, ", "html": "<span class='verse-line'>{}</span>"}, "dhp164:6": {"pli": "attaghātāya phallati. ", "eng": "like the bamboo bearing fruit. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp165:0": {"pli": "Cūḷakālaupāsakavatthu ", "html": "<article id='dhp165'><h2 class='sutta-title'>{}</h2>"}, "dhp165:1": {"pli": "Attanā hi kataṁ pāpaṁ, ", "eng": "For it is by oneself that evil’s done, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp165:2": {"pli": "attanā saṅkilissati; ", "eng": "one is corrupted by oneself. ", "html": "<span class='verse-line'>{}</span>"}, "dhp165:3": {"pli": "Attanā akataṁ pāpaṁ, ", "eng": "It’s by oneself that evil’s not done, ", "html": "<span class='verse-line'>{}</span>"}, "dhp165:4": {"pli": "attanāva visujjhati; ", "eng": "one is purified by oneself. ", "html": "<span class='verse-line'>{}</span>"}, "dhp165:5": {"pli": "Suddhī asuddhi paccattaṁ, ", "eng": "Purity and impurity are personal matters, ", "html": "<span class='verse-line'>{}</span>"}, "dhp165:6": {"pli": "nāñño aññaṁ visodhaye. ", "eng": "no one can purify another. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp166:0": {"pli": "Attadatthattheravatthu ", "html": "<article id='dhp166'><h2 class='sutta-title'>{}</h2>"}, "dhp166:1": {"pli": "Attadatthaṁ paratthena, ", "eng": "Never neglect what is good for yourself ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp166:2": {"pli": "bahunāpi na hāpaye; ", "eng": "for the sake of another, however great. ", "html": "<span class='verse-line'>{}</span>"}, "dhp166:3": {"pli": "Attadatthamabhiññāya, ", "eng": "Knowing well what is good for yourself, ", "html": "<span class='verse-line'>{}</span>"}, "dhp166:4": {"pli": "sadatthapasuto siyā. ", "eng": "be intent upon your heart’s goal. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp166:5": {"pli": "Attavaggo dvādasamo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp167-178": {"dhp167:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp167-178'><header><ul><li class='division'>{}</li>"}, "dhp167:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 167–178 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp167:0.3": {"pli": "Lokavagga ", "eng": "13. The World ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp167:0.4": {"pli": "Daharabhikkhuvatthu ", "html": "<article id='dhp167'><h2 class='sutta-title'>{}</h2>"}, "dhp167:1": {"pli": "Hīnaṁ dhammaṁ na seveyya, ", "eng": "Don’t resort to lowly things, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp167:2": {"pli": "pamādena na saṁvase; ", "eng": "don’t abide in negligence, ", "html": "<span class='verse-line'>{}</span>"}, "dhp167:3": {"pli": "Micchādiṭṭhiṁ na seveyya, ", "eng": "don’t resort to wrong views, ", "html": "<span class='verse-line'>{}</span>"}, "dhp167:4": {"pli": "na siyā lokavaḍḍhano. ", "eng": "don’t perpetuate the world. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp168:0": {"pli": "Suddhodanavatthu ", "html": "<article id='dhp168'><h2 class='sutta-title'>{}</h2>"}, "dhp168:1": {"pli": "Uttiṭṭhe nappamajjeyya, ", "eng": "Get up, don’t be heedless, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp168:2": {"pli": "dhammaṁ sucaritaṁ care; ", "eng": "live by principle, with good conduct. ", "html": "<span class='verse-line'>{}</span>"}, "dhp168:3": {"pli": "Dhammacārī sukhaṁ seti, ", "eng": "For one of good conduct sleeps at ease, ", "html": "<span class='verse-line'>{}</span>"}, "dhp168:4": {"pli": "asmiṁ loke paramhi ca. ", "eng": "in this world and the next. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp169:1": {"pli": "Dhammaṁ care sucaritaṁ, ", "eng": "Live by principle, with good conduct, ", "html": "<article id='dhp169'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp169:2": {"pli": "na naṁ duccaritaṁ care; ", "eng": "don’t conduct yourself badly. ", "html": "<span class='verse-line'>{}</span>"}, "dhp169:3": {"pli": "Dhammacārī sukhaṁ seti, ", "eng": "For one of good conduct sleeps at ease, ", "html": "<span class='verse-line'>{}</span>"}, "dhp169:4": {"pli": "asmiṁ loke paramhi ca. ", "eng": "in this world and the next. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp170:0": {"pli": "Pañcasatavipassakabhikkhuvatthu ", "html": "<article id='dhp170'><h2 class='sutta-title'>{}</h2>"}, "dhp170:1": {"pli": "Yathā pubbuḷakaṁ passe, ", "eng": "Look upon the world ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp170:2": {"pli": "yathā passe marīcikaṁ; ", "eng": "as a bubble ", "html": "<span class='verse-line'>{}</span>"}, "dhp170:3": {"pli": "Evaṁ lokaṁ avekkhantaṁ, ", "eng": "or a mirage, ", "html": "<span class='verse-line'>{}</span>"}, "dhp170:4": {"pli": "maccurājā na passati. ", "eng": "then the King of Death won’t see you. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp171:0": {"pli": "Abhayarājakumāravatthu ", "html": "<article id='dhp171'><h2 class='sutta-title'>{}</h2>"}, "dhp171:1": {"pli": "Etha passathimaṁ lokaṁ, ", "eng": "Come, see this world decked out ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp171:2": {"pli": "cittaṁ rājarathūpamaṁ; ", "eng": "like a fancy royal chariot. ", "html": "<span class='verse-line'>{}</span>"}, "dhp171:3": {"pli": "Yattha bālā visīdanti, ", "eng": "Here fools founder, ", "html": "<span class='verse-line'>{}</span>"}, "dhp171:4": {"pli": "natthi saṅgo vijānataṁ. ", "eng": "but the discerning are not chained. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp172:0": {"pli": "Sammajjanattheravatthu ", "html": "<article id='dhp172'><h2 class='sutta-title'>{}</h2>"}, "dhp172:1": {"pli": "Yo ca pubbe pamajjitvā, ", "eng": "He who once was heedless, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp172:2": {"pli": "pacchā so nappamajjati; ", "eng": "but turned to heedfulness, ", "html": "<span class='verse-line'>{}</span>"}, "dhp172:3": {"pli": "Somaṁ lokaṁ pabhāseti, ", "eng": "lights up the world ", "html": "<span class='verse-line'>{}</span>"}, "dhp172:4": {"pli": "abbhā muttova candimā. ", "eng": "like the moon freed from clouds. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp173:0": {"pli": "Aṅgulimālattheravatthu ", "html": "<article id='dhp173'><h2 class='sutta-title'>{}</h2>"}, "dhp173:1": {"pli": "Yassa pāpaṁ kataṁ kammaṁ, ", "eng": "Someone whose bad deed ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp173:2": {"pli": "kusalena pidhīyati; ", "eng": "is supplanted by the good, ", "html": "<span class='verse-line'>{}</span>"}, "dhp173:3": {"pli": "Somaṁ lokaṁ pabhāseti, ", "eng": "lights up the world, ", "html": "<span class='verse-line'>{}</span>"}, "dhp173:4": {"pli": "abbhā muttova candimā. ", "eng": "like the moon freed from clouds. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp174:0": {"pli": "Pesakāradhītāvatthu ", "html": "<article id='dhp174'><h2 class='sutta-title'>{}</h2>"}, "dhp174:1": {"pli": "Andhabhūto ayaṁ loko, ", "eng": "Blind is the world, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp174:2": {"pli": "tanukettha vipassati; ", "eng": "few are those who clearly see. ", "html": "<span class='verse-line'>{}</span>"}, "dhp174:3": {"pli": "Sakuṇo jālamuttova, ", "eng": "Only a handful go to heaven, ", "html": "<span class='verse-line'>{}</span>"}, "dhp174:4": {"pli": "appo saggāya gacchati. ", "eng": "like a bird freed from a net. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp175:0": {"pli": "Tiṁsabhikkhuvatthu ", "html": "<article id='dhp175'><h2 class='sutta-title'>{}</h2>"}, "dhp175:1": {"pli": "Haṁsādiccapathe yanti, ", "eng": "Swans fly by the sun’s path, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp175:2": {"pli": "ākāse yanti iddhiyā; ", "eng": "psychic sages fly through space. ", "html": "<span class='verse-line'>{}</span>"}, "dhp175:3": {"pli": "Nīyanti dhīrā lokamhā, ", "eng": "The attentive leave the world, ", "html": "<span class='verse-line'>{}</span>"}, "dhp175:4": {"pli": "jetvā māraṁ savāhiniṁ. ", "eng": "having vanquished Māra with his legions. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp176:0": {"pli": "Ciñcamāṇavikāvatthu ", "html": "<article id='dhp176'><h2 class='sutta-title'>{}</h2>"}, "dhp176:1": {"pli": "Ekaṁ dhammaṁ atītassa, ", "eng": "When a personage, spurning the hereafter, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp176:2": {"pli": "musāvādissa jantuno; ", "eng": "transgresses in just one thing—", "html": "<span class='verse-line'>{}</span>"}, "dhp176:3": {"pli": "Vitiṇṇaparalokassa, ", "eng": "lying—", "html": "<span class='verse-line'>{}</span>"}, "dhp176:4": {"pli": "natthi pāpaṁ akāriyaṁ. ", "eng": "there is no evil they would not do. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp177:0": {"pli": "Asadisadānavatthu ", "html": "<article id='dhp177'><h2 class='sutta-title'>{}</h2>"}, "dhp177:1": {"pli": "Na ve kadariyā devalokaṁ vajanti, ", "eng": "The miserly don’t ascend to heaven, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp177:2": {"pli": "Bālā have nappasaṁsanti dānaṁ; ", "eng": "it takes a fool to not praise giving. ", "html": "<span class='verse-line'>{}</span>"}, "dhp177:3": {"pli": "Dhīro ca dānaṁ anumodamāno, ", "eng": "The attentive celebrate giving, ", "html": "<span class='verse-line'>{}</span>"}, "dhp177:4": {"pli": "Teneva so hoti sukhī parattha. ", "eng": "and so find happiness in the hereafter. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp178:0": {"pli": "Anāthapiṇḍikaputtakālavatthu ", "html": "<article id='dhp178'><h2 class='sutta-title'>{}</h2>"}, "dhp178:1": {"pli": "Pathabyā ekarajjena, ", "eng": "The fruit of stream-entry is better ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp178:2": {"pli": "saggassa gamanena vā; ", "eng": "than being the one king of the earth, ", "html": "<span class='verse-line'>{}</span>"}, "dhp178:3": {"pli": "Sabbalokādhipaccena, ", "eng": "than going to heaven, ", "html": "<span class='verse-line'>{}</span>"}, "dhp178:4": {"pli": "sotāpattiphalaṁ varaṁ. ", "eng": "than lordship over all the world. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp178:5": {"pli": "Lokavaggo terasamo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp179-196": {"dhp179:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp179-196'><header><ul><li class='division'>{}</li>"}, "dhp179:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 179–196 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp179:0.3": {"pli": "Buddhavagga ", "eng": "14. The Buddhas ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp179:0.4": {"pli": "Māradhītaravatthu ", "html": "<article id='dhp179'><h2 class='sutta-title'>{}</h2>"}, "dhp179:1": {"pli": "Yassa jitaṁ nāvajīyati, ", "eng": "He whose victory may not be undone, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp179:2": {"pli": "Jitaṁ yassa noyāti koci loke; ", "eng": "a victory unrivaled in all the world; ", "html": "<span class='verse-line'>{}</span>"}, "dhp179:3": {"pli": "Taṁ buddhamanantagocaraṁ, ", "eng": "by what track would you trace that Buddha, ", "html": "<span class='verse-line'>{}</span>"}, "dhp179:4": {"pli": "Apadaṁ kena padena nessatha. ", "eng": "who leaves no track in his infinite range? ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp180:1": {"pli": "Yassa jālinī visattikā, ", "eng": "Of craving, the weaver, the clinger, he has none: ", "html": "<article id='dhp180'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp180:2": {"pli": "Taṇhā natthi kuhiñci netave; ", "eng": "so where can he be traced? ", "html": "<span class='verse-line'>{}</span>"}, "dhp180:3": {"pli": "Taṁ buddhamanantagocaraṁ, ", "eng": "By what track would you trace that Buddha, ", "html": "<span class='verse-line'>{}</span>"}, "dhp180:4": {"pli": "Apadaṁ kena padena nessatha. ", "eng": "who leaves no track in his infinite range? ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp181:0": {"pli": "Devorohaṇavatthu ", "html": "<article id='dhp181'><h2 class='sutta-title'>{}</h2>"}, "dhp181:1": {"pli": "Ye jhānapasutā dhīrā, ", "eng": "The attentive intent on absorption, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp181:2": {"pli": "nekkhammūpasame ratā; ", "eng": "who love the peace of renunciation, ", "html": "<span class='verse-line'>{}</span>"}, "dhp181:3": {"pli": "Devāpi tesaṁ pihayanti, ", "eng": "the Buddhas, ever mindful, ", "html": "<span class='verse-line'>{}</span>"}, "dhp181:4": {"pli": "sambuddhānaṁ satīmataṁ. ", "eng": "are envied by even the gods. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp182:0": {"pli": "Erakapattanāgarājavatthu ", "html": "<article id='dhp182'><h2 class='sutta-title'>{}</h2>"}, "dhp182:1": {"pli": "Kiccho manussapaṭilābho, ", "eng": "It’s hard to gain a human birth; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp182:2": {"pli": "kicchaṁ maccāna jīvitaṁ; ", "eng": "the life of mortals is hard; ", "html": "<span class='verse-line'>{}</span>"}, "dhp182:3": {"pli": "Kicchaṁ saddhammassavanaṁ, ", "eng": "it’s hard to hear the true teaching; ", "html": "<span class='verse-line'>{}</span>"}, "dhp182:4": {"pli": "kiccho buddhānamuppādo. ", "eng": "the arising of Buddhas is hard. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp183:0": {"pli": "Ānandattherapañhavatthu ", "html": "<article id='dhp183'><h2 class='sutta-title'>{}</h2>"}, "dhp183:1": {"pli": "Sabbapāpassa akaraṇaṁ, ", "eng": "Not to do any evil; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp183:2": {"pli": "kusalassa upasampadā; ", "eng": "to embrace the good; ", "html": "<span class='verse-line'>{}</span>"}, "dhp183:3": {"pli": "Sacittapariyodapanaṁ, ", "eng": "to purify one’s mind: ", "html": "<span class='verse-line'>{}</span>"}, "dhp183:4": {"pli": "etaṁ buddhāna sāsanaṁ. ", "eng": "this is the instruction of the Buddhas. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp184:1": {"pli": "Khantī paramaṁ tapo titikkhā, ", "eng": "Patient acceptance is the ultimate fervor. ", "html": "<article id='dhp184'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp184:2": {"pli": "Nibbānaṁ paramaṁ vadanti buddhā; ", "eng": "Extinguishment is the ultimate, say the Buddhas. ", "html": "<span class='verse-line'>{}</span>"}, "dhp184:3": {"pli": "Na hi pabbajito parūpaghātī, ", "eng": "No true renunciate injures another, ", "html": "<span class='verse-line'>{}</span>"}, "dhp184:4": {"pli": "Na samaṇo hoti paraṁ viheṭhayanto. ", "eng": "nor does an ascetic hurt another. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp185:1": {"pli": "Anūpavādo anūpaghāto, ", "eng": "Not speaking ill nor doing harm; ", "html": "<article id='dhp185'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp185:2": {"pli": "Pātimokkhe ca saṁvaro; ", "eng": "restraint in the monastic code; ", "html": "<span class='verse-line'>{}</span>"}, "dhp185:3": {"pli": "Mattaññutā ca bhattasmiṁ, ", "eng": "moderation in eating; ", "html": "<span class='verse-line'>{}</span>"}, "dhp185:4": {"pli": "Pantañca sayanāsanaṁ; ", "eng": "staying in remote lodgings; ", "html": "<span class='verse-line'>{}</span>"}, "dhp185:5": {"pli": "Adhicitte ca āyogo, ", "eng": "commitment to the higher mind—", "html": "<span class='verse-line'>{}</span>"}, "dhp185:6": {"pli": "Etaṁ buddhāna sāsanaṁ. ", "eng": "this is the instruction of the Buddhas. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp186:0": {"pli": "Anabhiratabhikkhuvatthu ", "html": "<article id='dhp186'><h2 class='sutta-title'>{}</h2>"}, "dhp186:1": {"pli": "Na kahāpaṇavassena, ", "eng": "Even if it were raining money, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp186:2": {"pli": "titti kāmesu vijjati; ", "eng": "you’d not be sated in sensual pleasures. ", "html": "<span class='verse-line'>{}</span>"}, "dhp186:3": {"pli": "Appassādā dukhā kāmā, ", "eng": "An astute person understands that sensual pleasures ", "html": "<span class='verse-line'>{}</span>"}, "dhp186:4": {"pli": "iti viññāya paṇḍito. ", "eng": "offer little gratification and much suffering. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp187:1": {"pli": "Api dibbesu kāmesu, ", "eng": "Thus they find no delight ", "html": "<article id='dhp187'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp187:2": {"pli": "ratiṁ so nādhigacchati; ", "eng": "even in celestial pleasures. ", "html": "<span class='verse-line'>{}</span>"}, "dhp187:3": {"pli": "Taṇhakkhayarato hoti, ", "eng": "A disciple of the fully awakened Buddha ", "html": "<span class='verse-line'>{}</span>"}, "dhp187:4": {"pli": "sammāsambuddhasāvako. ", "eng": "delights in the ending of craving. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp188:0": {"pli": "Aggidattabrāhmaṇavatthu ", "html": "<article id='dhp188'><h2 class='sutta-title'>{}</h2>"}, "dhp188:1": {"pli": "Bahuṁ ve saraṇaṁ yanti, ", "eng": "So many go for refuge ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp188:2": {"pli": "pabbatāni vanāni ca; ", "eng": "to mountains and forest groves, ", "html": "<span class='verse-line'>{}</span>"}, "dhp188:3": {"pli": "Ārāmarukkhacetyāni, ", "eng": "to shrines in tended parks; ", "html": "<span class='verse-line'>{}</span>"}, "dhp188:4": {"pli": "manussā bhayatajjitā. ", "eng": "those people are driven by fear. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp189:1": {"pli": "Netaṁ kho saraṇaṁ khemaṁ, ", "eng": "But such refuge is no sanctuary, ", "html": "<article id='dhp189'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp189:2": {"pli": "netaṁ saraṇamuttamaṁ; ", "eng": "it is no supreme refuge. ", "html": "<span class='verse-line'>{}</span>"}, "dhp189:3": {"pli": "Netaṁ saraṇamāgamma, ", "eng": "By going to that refuge, ", "html": "<span class='verse-line'>{}</span>"}, "dhp189:4": {"pli": "sabbadukkhā pamuccati. ", "eng": "you’re not released from all suffering. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp190:1": {"pli": "Yo ca buddhañca dhammañca, ", "eng": "One gone for refuge to the Buddha, ", "html": "<article id='dhp190'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp190:2": {"pli": "saṅghañca saraṇaṁ gato; ", "eng": "to his teaching and to the Saṅgha, ", "html": "<span class='verse-line'>{}</span>"}, "dhp190:3": {"pli": "Cattāri ariyasaccāni, ", "eng": "sees the four noble truths ", "html": "<span class='verse-line'>{}</span>"}, "dhp190:4": {"pli": "sammappaññāya passati. ", "eng": "with right understanding: ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp191:1": {"pli": "Dukkhaṁ dukkhasamuppādaṁ, ", "eng": "suffering, suffering’s origin, ", "html": "<article id='dhp191'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp191:2": {"pli": "dukkhassa ca atikkamaṁ; ", "eng": "suffering’s transcendence, ", "html": "<span class='verse-line'>{}</span>"}, "dhp191:3": {"pli": "Ariyaṁ caṭṭhaṅgikaṁ maggaṁ, ", "eng": "and the noble eightfold path ", "html": "<span class='verse-line'>{}</span>"}, "dhp191:4": {"pli": "dukkhūpasamagāminaṁ. ", "eng": "that leads to the stilling of suffering. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp192:1": {"pli": "Etaṁ kho saraṇaṁ khemaṁ, ", "eng": "Such refuge is a sanctuary, ", "html": "<article id='dhp192'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp192:2": {"pli": "etaṁ saraṇamuttamaṁ; ", "eng": "it is the supreme refuge. ", "html": "<span class='verse-line'>{}</span>"}, "dhp192:3": {"pli": "Etaṁ saraṇamāgamma, ", "eng": "By going to that refuge, ", "html": "<span class='verse-line'>{}</span>"}, "dhp192:4": {"pli": "sabbadukkhā pamuccati. ", "eng": "you’re released from all suffering. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp193:0": {"pli": "Ānandattherapañhavatthu ", "html": "<article id='dhp193'><h2 class='sutta-title'>{}</h2>"}, "dhp193:1": {"pli": "Dullabho purisājañño, ", "eng": "It’s hard to find a thoroughbred man: ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp193:2": {"pli": "na so sabbattha jāyati; ", "eng": "they’re not born just anywhere. ", "html": "<span class='verse-line'>{}</span>"}, "dhp193:3": {"pli": "Yattha so jāyati dhīro, ", "eng": "A family where that attentive one is born ", "html": "<span class='verse-line'>{}</span>"}, "dhp193:4": {"pli": "taṁ kulaṁ sukhamedhati. ", "eng": "prospers in happiness. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp194:0": {"pli": "Sambahulabhikkhuvatthu ", "html": "<article id='dhp194'><h2 class='sutta-title'>{}</h2>"}, "dhp194:1": {"pli": "Sukho buddhānamuppādo, ", "eng": "Happy, the arising of Buddhas! ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp194:2": {"pli": "sukhā saddhammadesanā; ", "eng": "Happy, the teaching of Dhamma! ", "html": "<span class='verse-line'>{}</span>"}, "dhp194:3": {"pli": "Sukhā saṅghassa sāmaggī, ", "eng": "Happy is the harmony of the Saṅgha, ", "html": "<span class='verse-line'>{}</span>"}, "dhp194:4": {"pli": "samaggānaṁ tapo sukho. ", "eng": "and the striving of the harmonious is happy. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp195:0": {"pli": "Kassapadasabalassasuvaṇṇacetiyavatthu ", "html": "<article id='dhp195'><h2 class='sutta-title'>{}</h2>"}, "dhp195:1": {"pli": "Pūjārahe pūjayato, ", "eng": "When a person venerates the worthy—", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp195:2": {"pli": "buddhe yadi va sāvake; ", "eng": "the Buddha or his disciple, ", "html": "<span class='verse-line'>{}</span>"}, "dhp195:3": {"pli": "Papañcasamatikkante, ", "eng": "who have transcended proliferation, ", "html": "<span class='verse-line'>{}</span>"}, "dhp195:4": {"pli": "tiṇṇasokapariddave. ", "eng": "and have left behind grief and lamentation, ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp196:1": {"pli": "Te tādise pūjayato, ", "eng": "quenched, fearing nothing from any quarter—", "html": "<article id='dhp196'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp196:2": {"pli": "nibbute akutobhaye; ", "eng": "the merit of one venerating such as these, ", "html": "<span class='verse-line'>{}</span>"}, "dhp196:3": {"pli": "Na sakkā puññaṁ saṅkhātuṁ, ", "eng": "cannot be calculated by anyone, ", "html": "<span class='verse-line'>{}</span>"}, "dhp196:4": {"pli": "imettamapi kenaci. ", "eng": "saying it is just this much. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp196:5": {"pli": "Buddhavaggo cuddasamo. ", "html": "<p class='endvagga'>{}</p>"}, "dhp196:6": {"pli": "Paṭhamabhāṇavāraṁ. ", "html": "<p class='endsection'>{}</p></article></section>"}}, "dhp197-208": {"dhp197:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp197-208'><header><ul><li class='division'>{}</li>"}, "dhp197:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 197–208 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp197:0.3": {"pli": "Sukhavagga ", "eng": "15. Happiness ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp197:0.4": {"pli": "Ñātikalahavūpasamanavatthu ", "html": "<article id='dhp197'><h2 class='sutta-title'>{}</h2>"}, "dhp197:1": {"pli": "Susukhaṁ vata jīvāma, ", "eng": "Let us live so very happily, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp197:2": {"pli": "verinesu averino; ", "eng": "loving among the hostile. ", "html": "<span class='verse-line'>{}</span>"}, "dhp197:3": {"pli": "Verinesu manussesu, ", "eng": "Among hostile humans, ", "html": "<span class='verse-line'>{}</span>"}, "dhp197:4": {"pli": "viharāma averino. ", "eng": "let us live with love. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp198:1": {"pli": "Susukhaṁ vata jīvāma, ", "eng": "Let us live so very happily, ", "html": "<article id='dhp198'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp198:2": {"pli": "āturesu anāturā; ", "eng": "healthy among the ailing. ", "html": "<span class='verse-line'>{}</span>"}, "dhp198:3": {"pli": "Āturesu manussesu, ", "eng": "Among ailing humans ", "html": "<span class='verse-line'>{}</span>"}, "dhp198:4": {"pli": "viharāma anāturā. ", "eng": "let us live healthily. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp199:1": {"pli": "Susukhaṁ vata jīvāma, ", "eng": "Let us live so very happily, ", "html": "<article id='dhp199'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp199:2": {"pli": "ussukesu anussukā; ", "eng": "content among the greedy. ", "html": "<span class='verse-line'>{}</span>"}, "dhp199:3": {"pli": "Ussukesu manussesu, ", "eng": "Among greedy humans, ", "html": "<span class='verse-line'>{}</span>"}, "dhp199:4": {"pli": "viharāma anussukā. ", "eng": "let us live content. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp200:0": {"pli": "Māravatthu ", "html": "<article id='dhp200'><h2 class='sutta-title'>{}</h2>"}, "dhp200:1": {"pli": "Susukhaṁ vata jīvāma, ", "eng": "Let us live so very happily, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp200:2": {"pli": "yesaṁ no natthi kiñcanaṁ; ", "eng": "we who have nothing. ", "html": "<span class='verse-line'>{}</span>"}, "dhp200:3": {"pli": "Pītibhakkhā bhavissāma, ", "eng": "We shall feed on rapture, ", "html": "<span class='verse-line'>{}</span>"}, "dhp200:4": {"pli": "devā ābhassarā yathā. ", "eng": "like the gods of streaming radiance. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp201:0": {"pli": "Kosalaraññoparājayavatthu ", "html": "<article id='dhp201'><h2 class='sutta-title'>{}</h2>"}, "dhp201:1": {"pli": "Jayaṁ veraṁ pasavati, ", "eng": "Victory breeds enmity; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp201:2": {"pli": "dukkhaṁ seti parājito; ", "eng": "the defeated sleep badly. ", "html": "<span class='verse-line'>{}</span>"}, "dhp201:3": {"pli": "Upasanto sukhaṁ seti, ", "eng": "The peaceful sleep at ease, ", "html": "<span class='verse-line'>{}</span>"}, "dhp201:4": {"pli": "hitvā jayaparājayaṁ. ", "eng": "having left victory and defeat behind. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp202:0": {"pli": "Aññatarakuladārikāvatthu ", "html": "<article id='dhp202'><h2 class='sutta-title'>{}</h2>"}, "dhp202:1": {"pli": "Natthi rāgasamo aggi, ", "eng": "There is no fire like greed, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp202:2": {"pli": "Natthi dosasamo kali; ", "eng": "no crime like hate, ", "html": "<span class='verse-line'>{}</span>"}, "dhp202:3": {"pli": "Natthi khandhasamā dukkhā, ", "eng": "no suffering like the aggregates, ", "html": "<span class='verse-line'>{}</span>"}, "dhp202:4": {"pli": "Natthi santiparaṁ sukhaṁ. ", "eng": "no bliss beyond peace. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp203:0": {"pli": "Ekaupāsakavatthu ", "html": "<article id='dhp203'><h2 class='sutta-title'>{}</h2>"}, "dhp203:1": {"pli": "Jighacchāparamā rogā, ", "eng": "Hunger is the worst illness, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp203:2": {"pli": "saṅkhāraparamā dukhā; ", "eng": "conditions are the worst suffering. ", "html": "<span class='verse-line'>{}</span>"}, "dhp203:3": {"pli": "Etaṁ ñatvā yathābhūtaṁ, ", "eng": "For one who truly knows this, ", "html": "<span class='verse-line'>{}</span>"}, "dhp203:4": {"pli": "nibbānaṁ paramaṁ sukhaṁ. ", "eng": "extinguishment is the ultimate happiness. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp204:0": {"pli": "Pasenadikosalavatthu ", "html": "<article id='dhp204'><h2 class='sutta-title'>{}</h2>"}, "dhp204:1": {"pli": "Ārogyaparamā lābhā, ", "eng": "Health is the ultimate blessing; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp204:2": {"pli": "Santuṭṭhiparamaṁ dhanaṁ; ", "eng": "contentment, the ultimate wealth; ", "html": "<span class='verse-line'>{}</span>"}, "dhp204:3": {"pli": "Vissāsaparamā ñāti, ", "eng": "trust is the ultimate family; ", "html": "<span class='verse-line'>{}</span>"}, "dhp204:4": {"pli": "Nibbānaṁ paramaṁ sukhaṁ. ", "eng": "extinguishment, the ultimate happiness. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp205:0": {"pli": "Tissattheravatthu ", "html": "<article id='dhp205'><h2 class='sutta-title'>{}</h2>"}, "dhp205:1": {"pli": "Pavivekarasaṁ pitvā, ", "eng": "Having drunk the nectar of seclusion ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp205:2": {"pli": "rasaṁ upasamassa ca; ", "eng": "and the nectar of peace—", "html": "<span class='verse-line'>{}</span>"}, "dhp205:3": {"pli": "Niddaro hoti nippāpo, ", "eng": "free of stress, free of evil, ", "html": "<span class='verse-line'>{}</span>"}, "dhp205:4": {"pli": "dhammapītirasaṁ pivaṁ. ", "eng": "drink the joyous nectar of truth. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp206:0": {"pli": "Sakkavatthu ", "html": "<article id='dhp206'><h2 class='sutta-title'>{}</h2>"}, "dhp206:1": {"pli": "Sāhu dassanamariyānaṁ, ", "eng": "It’s good to see the noble ones, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp206:2": {"pli": "sannivāso sadā sukho; ", "eng": "staying with them is always good. ", "html": "<span class='verse-line'>{}</span>"}, "dhp206:3": {"pli": "Adassanena bālānaṁ, ", "eng": "Were you not to see fools, ", "html": "<span class='verse-line'>{}</span>"}, "dhp206:4": {"pli": "niccameva sukhī siyā. ", "eng": "you’d always be happy. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp207:1": {"pli": "Bālasaṅgatacārī hi, ", "eng": "For one who consorts with fools ", "html": "<article id='dhp207'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp207:2": {"pli": "dīghamaddhāna socati; ", "eng": "grieves long. ", "html": "<span class='verse-line'>{}</span>"}, "dhp207:3": {"pli": "Dukkho bālehi saṁvāso, ", "eng": "Painful is living with fools, ", "html": "<span class='verse-line'>{}</span>"}, "dhp207:4": {"pli": "amitteneva sabbadā; ", "eng": "like being stuck with your enemy. ", "html": "<span class='verse-line'>{}</span>"}, "dhp207:5": {"pli": "Dhīro ca sukhasaṁvāso, ", "eng": "Happy is living with an attentive one, ", "html": "<span class='verse-line'>{}</span>"}, "dhp207:6": {"pli": "ñātīnaṁva samāgamo. ", "eng": "like meeting with your kin. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp208:1": {"pli": "Tasmā hi—", "eng": "Therefore: ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp208:2": {"pli": "Dhīrañca paññañca bahussutañca, ", "eng": "An attentive one, wise and learned, ", "html": "<span class='verse-line'>{}</span>"}, "dhp208:3": {"pli": "Dhorayhasīlaṁ vatavantamariyaṁ; ", "eng": "a behemoth of virtue, true to their vows, noble: ", "html": "<span class='verse-line'>{}</span>"}, "dhp208:4": {"pli": "Taṁ tādisaṁ sappurisaṁ sumedhaṁ, ", "eng": "follow a true and intelligent person such as this, ", "html": "<span class='verse-line'>{}</span>"}, "dhp208:5": {"pli": "Bhajetha nakkhattapathaṁva candimā. ", "eng": "as the moon tracks the path of the stars. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp208:6": {"pli": "Sukhavaggo pannarasamo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp209-220": {"dhp209:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp209-220'><header><ul><li class='division'>{}</li>"}, "dhp209:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 209–220 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp209:0.3": {"pli": "Piyavagga ", "eng": "16. The Beloved ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp209:0.4": {"pli": "Tayojanapabbajitavatthu ", "html": "<article id='dhp209'><h2 class='sutta-title'>{}</h2>"}, "dhp209:1": {"pli": "Ayoge yuñjamattānaṁ, ", "eng": "Applying yourself where you ought not, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp209:2": {"pli": "yogasmiñca ayojayaṁ; ", "eng": "neglecting what you should be doing, ", "html": "<span class='verse-line'>{}</span>"}, "dhp209:3": {"pli": "Atthaṁ hitvā piyaggāhī, ", "eng": "forgetting your goal, you cling to what you hold dear, ", "html": "<span class='verse-line'>{}</span>"}, "dhp209:4": {"pli": "pihetattānuyoginaṁ. ", "eng": "jealous of those devoted to their heart’s goal. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp210:1": {"pli": "Mā piyehi samāgañchi, ", "eng": "Don’t ever get too close ", "html": "<article id='dhp210'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp210:2": {"pli": "appiyehi kudācanaṁ; ", "eng": "to those you like or dislike. ", "html": "<span class='verse-line'>{}</span>"}, "dhp210:3": {"pli": "Piyānaṁ adassanaṁ dukkhaṁ, ", "eng": "For not seeing the liked is suffering, ", "html": "<span class='verse-line'>{}</span>"}, "dhp210:4": {"pli": "appiyānañca dassanaṁ. ", "eng": "and so is seeing the disliked. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp211:1": {"pli": "Tasmā piyaṁ na kayirātha, ", "eng": "Therefore don’t hold anything dear, ", "html": "<article id='dhp211'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp211:2": {"pli": "piyāpāyo hi pāpako; ", "eng": "for it’s bad to lose those you love. ", "html": "<span class='verse-line'>{}</span>"}, "dhp211:3": {"pli": "Ganthā tesaṁ na vijjanti, ", "eng": "No ties are found in they who ", "html": "<span class='verse-line'>{}</span>"}, "dhp211:4": {"pli": "yesaṁ natthi piyāppiyaṁ. ", "eng": "hold nothing loved or loathed. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp212:0": {"pli": "Aññatarakuṭumbikavatthu ", "html": "<article id='dhp212'><h2 class='sutta-title'>{}</h2>"}, "dhp212:1": {"pli": "Piyato jāyatī soko, ", "eng": "Sorrow springs from what we hold dear, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp212:2": {"pli": "piyato jāyatī bhayaṁ; ", "eng": "fear springs from what we hold dear; ", "html": "<span class='verse-line'>{}</span>"}, "dhp212:3": {"pli": "Piyato vippamuttassa, ", "eng": "one free from holding anything dear ", "html": "<span class='verse-line'>{}</span>"}, "dhp212:4": {"pli": "natthi soko kuto bhayaṁ. ", "eng": "has no sorrow, let alone fear. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp213:0": {"pli": "Visākhāvatthu ", "html": "<article id='dhp213'><h2 class='sutta-title'>{}</h2>"}, "dhp213:1": {"pli": "Pemato jāyatī soko, ", "eng": "Sorrow springs from attachment, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp213:2": {"pli": "pemato jāyatī bhayaṁ; ", "eng": "fear springs from attachment; ", "html": "<span class='verse-line'>{}</span>"}, "dhp213:3": {"pli": "Pemato vippamuttassa, ", "eng": "one free from attachment ", "html": "<span class='verse-line'>{}</span>"}, "dhp213:4": {"pli": "natthi soko kuto bhayaṁ. ", "eng": "has no sorrow, let alone fear. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp214:0": {"pli": "Licchavīvatthu ", "html": "<article id='dhp214'><h2 class='sutta-title'>{}</h2>"}, "dhp214:1": {"pli": "Ratiyā jāyatī soko, ", "eng": "Sorrow springs from relishing, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp214:2": {"pli": "ratiyā jāyatī bhayaṁ; ", "eng": "fear springs from relishing; ", "html": "<span class='verse-line'>{}</span>"}, "dhp214:3": {"pli": "Ratiyā vippamuttassa, ", "eng": "one free from relishing ", "html": "<span class='verse-line'>{}</span>"}, "dhp214:4": {"pli": "natthi soko kuto bhayaṁ. ", "eng": "has no sorrow, let alone fear. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp215:0": {"pli": "Anitthigandhakumāravatthu ", "html": "<article id='dhp215'><h2 class='sutta-title'>{}</h2>"}, "dhp215:1": {"pli": "Kāmato jāyatī soko, ", "eng": "Sorrow springs from desire, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp215:2": {"pli": "kāmato jāyatī bhayaṁ; ", "eng": "fear springs from desire; ", "html": "<span class='verse-line'>{}</span>"}, "dhp215:3": {"pli": "Kāmato vippamuttassa, ", "eng": "one free from desire ", "html": "<span class='verse-line'>{}</span>"}, "dhp215:4": {"pli": "natthi soko kuto bhayaṁ. ", "eng": "has no sorrow, let alone fear. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp216:0": {"pli": "Aññatarabrāhmaṇavatthu ", "html": "<article id='dhp216'><h2 class='sutta-title'>{}</h2>"}, "dhp216:1": {"pli": "Taṇhāya jāyatī soko, ", "eng": "Sorrow springs from craving, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp216:2": {"pli": "taṇhāya jāyatī bhayaṁ; ", "eng": "fear springs from craving; ", "html": "<span class='verse-line'>{}</span>"}, "dhp216:3": {"pli": "Taṇhāya vippamuttassa, ", "eng": "one free from craving ", "html": "<span class='verse-line'>{}</span>"}, "dhp216:4": {"pli": "natthi soko kuto bhayaṁ. ", "eng": "has no sorrow, let alone fear. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp217:0": {"pli": "Pañcasatadārakavatthu ", "html": "<article id='dhp217'><h2 class='sutta-title'>{}</h2>"}, "dhp217:1": {"pli": "Sīladassanasampannaṁ, ", "eng": "One accomplished in virtue and vision, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp217:2": {"pli": "dhammaṭṭhaṁ saccavedinaṁ; ", "eng": "firmly principled, and truthful, ", "html": "<span class='verse-line'>{}</span>"}, "dhp217:3": {"pli": "Attano kamma kubbānaṁ, ", "eng": "doing oneself what ought be done: ", "html": "<span class='verse-line'>{}</span>"}, "dhp217:4": {"pli": "taṁ jano kurute piyaṁ. ", "eng": "that’s who the people love. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp218:0": {"pli": "Ekaanāgāmittheravatthu ", "html": "<article id='dhp218'><h2 class='sutta-title'>{}</h2>"}, "dhp218:1": {"pli": "Chandajāto anakkhāte, ", "eng": "One eager to realize the ineffable ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp218:2": {"pli": "Manasā ca phuṭo siyā; ", "eng": "would be filled with awareness. ", "html": "<span class='verse-line'>{}</span>"}, "dhp218:3": {"pli": "Kāmesu ca appaṭibaddhacitto, ", "eng": "Their mind not bound to pleasures of sense, ", "html": "<span class='verse-line'>{}</span>"}, "dhp218:4": {"pli": "Uddhaṁsototi vuccati. ", "eng": "they’re said to be heading upstream. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp219:0": {"pli": "Nandiyavatthu ", "html": "<article id='dhp219'><h2 class='sutta-title'>{}</h2>"}, "dhp219:1": {"pli": "Cirappavāsiṁ purisaṁ, ", "eng": "When a man returns safely ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp219:2": {"pli": "dūrato sotthimāgataṁ; ", "eng": "after a long time spent abroad, ", "html": "<span class='verse-line'>{}</span>"}, "dhp219:3": {"pli": "Ñātimittā suhajjā ca, ", "eng": "family, friends, and loved ones ", "html": "<span class='verse-line'>{}</span>"}, "dhp219:4": {"pli": "abhinandanti āgataṁ. ", "eng": "celebrate his return. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp220:1": {"pli": "Tatheva katapuññampi, ", "eng": "Just so, when one who has done good ", "html": "<article id='dhp220'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp220:2": {"pli": "asmā lokā paraṁ gataṁ; ", "eng": "goes from this world to the next, ", "html": "<span class='verse-line'>{}</span>"}, "dhp220:3": {"pli": "Puññāni paṭigaṇhanti, ", "eng": "their good deeds receive them there, ", "html": "<span class='verse-line'>{}</span>"}, "dhp220:4": {"pli": "piyaṁ ñātīva āgataṁ. ", "eng": "as family welcomes home one they love. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp220:5": {"pli": "Piyavaggo soḷasamo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp221-234": {"dhp221:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp221-234'><header><ul><li class='division'>{}</li>"}, "dhp221:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 221–234 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp221:0.3": {"pli": "Kodhavagga ", "eng": "17. Anger ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp221:0.4": {"pli": "Rohinīkhattiyakaññāvatthu ", "html": "<article id='dhp221'><h2 class='sutta-title'>{}</h2>"}, "dhp221:1": {"pli": "Kodhaṁ jahe vippajaheyya mānaṁ, ", "eng": "Give up anger, get rid of conceit, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp221:2": {"pli": "Saṁyojanaṁ sabbamatikkameyya; ", "eng": "and escape every fetter. ", "html": "<span class='verse-line'>{}</span>"}, "dhp221:3": {"pli": "Taṁ nāmarūpasmimasajjamānaṁ, ", "eng": "Sufferings don’t befall one who has nothing, ", "html": "<span class='verse-line'>{}</span>"}, "dhp221:4": {"pli": "Akiñcanaṁ nānupatanti dukkhā. ", "eng": "not clinging to name and form. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp222:0": {"pli": "Aññatarabhikkhuvatthu ", "html": "<article id='dhp222'><h2 class='sutta-title'>{}</h2>"}, "dhp222:1": {"pli": "Yo ve uppatitaṁ kodhaṁ, ", "eng": "When anger surges like a lurching chariot, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp222:2": {"pli": "rathaṁ bhantaṁva vāraye; ", "eng": "keep it in check. ", "html": "<span class='verse-line'>{}</span>"}, "dhp222:3": {"pli": "Tamahaṁ sārathiṁ brūmi, ", "eng": "That’s what I call a charioteer; ", "html": "<span class='verse-line'>{}</span>"}, "dhp222:4": {"pli": "rasmiggāho itaro jano. ", "eng": "others just hold the reins. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp223:0": {"pli": "Uttarāupāsikavatthu ", "html": "<article id='dhp223'><h2 class='sutta-title'>{}</h2>"}, "dhp223:1": {"pli": "Akkodhena jine kodhaṁ, ", "eng": "Defeat anger with kindness, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp223:2": {"pli": "asādhuṁ sādhunā jine; ", "eng": "villainy with virtue, ", "html": "<span class='verse-line'>{}</span>"}, "dhp223:3": {"pli": "Jine kadariyaṁ dānena, ", "eng": "stinginess with giving, ", "html": "<span class='verse-line'>{}</span>"}, "dhp223:4": {"pli": "saccenālikavādinaṁ. ", "eng": "and lies with truth. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp224:0": {"pli": "Mahāmoggallānapañhavatthu ", "html": "<article id='dhp224'><h2 class='sutta-title'>{}</h2>"}, "dhp224:1": {"pli": "Saccaṁ bhaṇe na kujjheyya, ", "eng": "Speak the truth, do not be angry, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp224:2": {"pli": "dajjā appampi yācito; ", "eng": "and give when asked, if only a little. ", "html": "<span class='verse-line'>{}</span>"}, "dhp224:3": {"pli": "Etehi tīhi ṭhānehi, ", "eng": "By these three means, ", "html": "<span class='verse-line'>{}</span>"}, "dhp224:4": {"pli": "gacche devāna santike. ", "eng": "you may enter the presence of the gods. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp225:0": {"pli": "Buddhapitubrāhmaṇavatthu ", "html": "<article id='dhp225'><h2 class='sutta-title'>{}</h2>"}, "dhp225:1": {"pli": "Ahiṁsakā ye munayo, ", "eng": "Those harmless sages, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp225:2": {"pli": "niccaṁ kāyena saṁvutā; ", "eng": "always restrained in body, ", "html": "<span class='verse-line'>{}</span>"}, "dhp225:3": {"pli": "Te yanti accutaṁ ṭhānaṁ, ", "eng": "go to the state that does not pass, ", "html": "<span class='verse-line'>{}</span>"}, "dhp225:4": {"pli": "yattha gantvā na socare. ", "eng": "where there is no sorrow. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp226:0": {"pli": "Puṇṇadāsīvatthu ", "html": "<article id='dhp226'><h2 class='sutta-title'>{}</h2>"}, "dhp226:1": {"pli": "Sadā jāgaramānānaṁ, ", "eng": "Always wakeful, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp226:2": {"pli": "ahorattānusikkhinaṁ; ", "eng": "practicing night and day, ", "html": "<span class='verse-line'>{}</span>"}, "dhp226:3": {"pli": "Nibbānaṁ adhimuttānaṁ, ", "eng": "focused only on extinguishment, ", "html": "<span class='verse-line'>{}</span>"}, "dhp226:4": {"pli": "atthaṁ gacchanti āsavā. ", "eng": "their defilements come to an end. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp227:0": {"pli": "Atulaupāsakavatthu ", "html": "<article id='dhp227'><h2 class='sutta-title'>{}</h2>"}, "dhp227:1": {"pli": "Porāṇametaṁ atula, ", "eng": "It’s always been this way, Atula, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp227:2": {"pli": "netaṁ ajjatanāmiva; ", "eng": "it’s not just today. ", "html": "<span class='verse-line'>{}</span>"}, "dhp227:3": {"pli": "Nindanti tuṇhimāsīnaṁ, ", "eng": "They blame you when you’re silent, ", "html": "<span class='verse-line'>{}</span>"}, "dhp227:4": {"pli": "nindanti bahubhāṇinaṁ; ", "eng": "they blame you when you speak a lot, ", "html": "<span class='verse-line'>{}</span>"}, "dhp227:5": {"pli": "Mitabhāṇimpi nindanti, ", "eng": "and even when you speak just right: ", "html": "<span class='verse-line'>{}</span>"}, "dhp227:6": {"pli": "natthi loke anindito. ", "eng": "no-one in the world escapes blame. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp228:1": {"pli": "Na cāhu na ca bhavissati, ", "eng": "There never was, nor will be, ", "html": "<article id='dhp228'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp228:2": {"pli": "na cetarahi vijjati; ", "eng": "nor is there today, ", "html": "<span class='verse-line'>{}</span>"}, "dhp228:3": {"pli": "Ekantaṁ nindito poso, ", "eng": "someone who is wholly praised ", "html": "<span class='verse-line'>{}</span>"}, "dhp228:4": {"pli": "ekantaṁ vā pasaṁsito. ", "eng": "or wholly blamed. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp229:1": {"pli": "Yañce viññū pasaṁsanti, ", "eng": "If, after watching them day in day out, ", "html": "<article id='dhp229'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp229:2": {"pli": "anuvicca suve suve; ", "eng": "discerning people praise ", "html": "<span class='verse-line'>{}</span>"}, "dhp229:3": {"pli": "Acchiddavuttiṁ medhāviṁ, ", "eng": "that sage of impeccable conduct, ", "html": "<span class='verse-line'>{}</span>"}, "dhp229:4": {"pli": "paññāsīlasamāhitaṁ. ", "eng": "endowed with ethics and wisdom; ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp230:1": {"pli": "Nikkhaṁ jambonadasseva, ", "eng": "like a pendant of Black Plum River gold, ", "html": "<article id='dhp230'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp230:2": {"pli": "ko taṁ ninditumarahati; ", "eng": "who is worthy to criticize them? ", "html": "<span class='verse-line'>{}</span>"}, "dhp230:3": {"pli": "Devāpi naṁ pasaṁsanti, ", "eng": "Even the gods praise them, ", "html": "<span class='verse-line'>{}</span>"}, "dhp230:4": {"pli": "brahmunāpi pasaṁsito. ", "eng": "and by the Divinity, too, they’re praised. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp231:0": {"pli": "Chabbaggiyavatthu ", "html": "<article id='dhp231'><h2 class='sutta-title'>{}</h2>"}, "dhp231:1": {"pli": "Kāyappakopaṁ rakkheyya, ", "eng": "Guard against ill-tempered deeds, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp231:2": {"pli": "kāyena saṁvuto siyā; ", "eng": "be restrained in body. ", "html": "<span class='verse-line'>{}</span>"}, "dhp231:3": {"pli": "Kāyaduccaritaṁ hitvā, ", "eng": "Giving up bad bodily conduct, ", "html": "<span class='verse-line'>{}</span>"}, "dhp231:4": {"pli": "kāyena sucaritaṁ care. ", "eng": "conduct yourself well in body. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp232:1": {"pli": "Vacīpakopaṁ rakkheyya, ", "eng": "Guard against ill-tempered words, ", "html": "<article id='dhp232'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp232:2": {"pli": "vācāya saṁvuto siyā; ", "eng": "be restrained in speech. ", "html": "<span class='verse-line'>{}</span>"}, "dhp232:3": {"pli": "Vacīduccaritaṁ hitvā, ", "eng": "Giving up bad verbal conduct, ", "html": "<span class='verse-line'>{}</span>"}, "dhp232:4": {"pli": "vācāya sucaritaṁ care. ", "eng": "conduct yourself well in speech. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp233:1": {"pli": "Manopakopaṁ rakkheyya, ", "eng": "Guard against ill-tempered thoughts, ", "html": "<article id='dhp233'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp233:2": {"pli": "manasā saṁvuto siyā; ", "eng": "be restrained in mind. ", "html": "<span class='verse-line'>{}</span>"}, "dhp233:3": {"pli": "Manoduccaritaṁ hitvā, ", "eng": "Giving up bad mental conduct, ", "html": "<span class='verse-line'>{}</span>"}, "dhp233:4": {"pli": "manasā sucaritaṁ care. ", "eng": "conduct yourself well in mind. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp234:1": {"pli": "Kāyena saṁvutā dhīrā, ", "eng": "An attentive one is restrained in body ", "html": "<article id='dhp234'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp234:2": {"pli": "atho vācāya saṁvutā; ", "eng": "restrained also in speech, ", "html": "<span class='verse-line'>{}</span>"}, "dhp234:3": {"pli": "Manasā saṁvutā dhīrā, ", "eng": "in thought, too, they are restrained: ", "html": "<span class='verse-line'>{}</span>"}, "dhp234:4": {"pli": "te ve suparisaṁvutā. ", "eng": "they are restrained in every way. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp234:5": {"pli": "Kodhavaggo sattarasamo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp235-255": {"dhp235:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp235-255'><header><ul><li class='division'>{}</li>"}, "dhp235:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 235–255 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp235:0.3": {"pli": "Malavagga ", "eng": "18. Stains ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp235:0.4": {"pli": "Goghātakaputtavatthu ", "html": "<article id='dhp235'><h2 class='sutta-title'>{}</h2>"}, "dhp235:1": {"pli": "Paṇḍupalāsova dānisi, ", "eng": "Today you’re like a withered leaf, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp235:2": {"pli": "Yamapurisāpi ca te upaṭṭhitā; ", "eng": "Yama’s men await you. ", "html": "<span class='verse-line'>{}</span>"}, "dhp235:3": {"pli": "Uyyogamukhe ca tiṭṭhasi, ", "eng": "You stand at the departure gates, ", "html": "<span class='verse-line'>{}</span>"}, "dhp235:4": {"pli": "Pātheyyampi ca te na vijjati. ", "eng": "yet you have no supplies for the road. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp236:1": {"pli": "So karohi dīpamattano, ", "eng": "Make an island of yourself! ", "html": "<article id='dhp236'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp236:2": {"pli": "Khippaṁ vāyama paṇḍito bhava; ", "eng": "Swiftly strive, learn to be wise! ", "html": "<span class='verse-line'>{}</span>"}, "dhp236:3": {"pli": "Niddhantamalo anaṅgaṇo, ", "eng": "Purged of stains, flawless, ", "html": "<span class='verse-line'>{}</span>"}, "dhp236:4": {"pli": "Dibbaṁ ariyabhūmiṁ upehisi. ", "eng": "you’ll go to the heavenly realm of the noble ones. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp237:1": {"pli": "Upanītavayo ca dānisi, ", "eng": "You’ve journeyed the stages of life, ", "html": "<article id='dhp237'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp237:2": {"pli": "Sampayātosi yamassa santikaṁ; ", "eng": "and now you set out to meet Yama. ", "html": "<span class='verse-line'>{}</span>"}, "dhp237:3": {"pli": "Vāso te natthi antarā, ", "eng": "Along the way there’s nowhere to stay, ", "html": "<span class='verse-line'>{}</span>"}, "dhp237:4": {"pli": "Pātheyyampi ca te na vijjati. ", "eng": "yet you have no supplies for the road. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp238:1": {"pli": "So karohi dīpamattano, ", "eng": "Make an island of yourself! ", "html": "<article id='dhp238'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp238:2": {"pli": "Khippaṁ vāyama paṇḍito bhava; ", "eng": "Swiftly strive, learn to be wise! ", "html": "<span class='verse-line'>{}</span>"}, "dhp238:3": {"pli": "Niddhantamalo anaṅgaṇo, ", "eng": "Purged of stains, flawless, ", "html": "<span class='verse-line'>{}</span>"}, "dhp238:4": {"pli": "Na punaṁ jātijaraṁ upehisi. ", "eng": "you’ll not come again to rebirth and old age. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp239:0": {"pli": "Aññatarabrāhmaṇavatthu ", "html": "<article id='dhp239'><h2 class='sutta-title'>{}</h2>"}, "dhp239:1": {"pli": "Anupubbena medhāvī, ", "eng": "A smart person would purge ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp239:2": {"pli": "thokaṁ thokaṁ khaṇe khaṇe; ", "eng": "their own stains gradually, ", "html": "<span class='verse-line'>{}</span>"}, "dhp239:3": {"pli": "Kammāro rajatasseva, ", "eng": "bit by bit, moment by moment, ", "html": "<span class='verse-line'>{}</span>"}, "dhp239:4": {"pli": "niddhame malamattano. ", "eng": "like a smith smelting silver. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp240:0": {"pli": "Tissattheravatthu ", "html": "<article id='dhp240'><h2 class='sutta-title'>{}</h2>"}, "dhp240:1": {"pli": "Ayasāva malaṁ samuṭṭhitaṁ, ", "eng": "It is the rust born on the iron ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp240:2": {"pli": "Tatuṭṭhāya tameva khādati; ", "eng": "that eats away the place it arose. ", "html": "<span class='verse-line'>{}</span>"}, "dhp240:3": {"pli": "Evaṁ atidhonacārinaṁ, ", "eng": "And so it is their own deeds ", "html": "<span class='verse-line'>{}</span>"}, "dhp240:4": {"pli": "Sāni kammāni nayanti duggatiṁ. ", "eng": "that lead the overly-ascetic to a bad place. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp241:0": {"pli": "Lāḷudāyīvatthu ", "html": "<article id='dhp241'><h2 class='sutta-title'>{}</h2>"}, "dhp241:1": {"pli": "Asajjhāyamalā mantā, ", "eng": "Not rehearsing is the stain of hymns. ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp241:2": {"pli": "anuṭṭhānamalā gharā; ", "eng": "The stain of houses is neglect. ", "html": "<span class='verse-line'>{}</span>"}, "dhp241:3": {"pli": "Malaṁ vaṇṇassa kosajjaṁ, ", "eng": "Laziness is the stain of beauty. ", "html": "<span class='verse-line'>{}</span>"}, "dhp241:4": {"pli": "pamādo rakkhato malaṁ. ", "eng": "A guard’s stain is negligence. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp242:0": {"pli": "Aññatarakulaputtavatthu ", "html": "<article id='dhp242'><h2 class='sutta-title'>{}</h2>"}, "dhp242:1": {"pli": "Malitthiyā duccaritaṁ, ", "eng": "Misconduct is a woman’s stain. ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp242:2": {"pli": "maccheraṁ dadato malaṁ; ", "eng": "A giver’s stain is stinginess. ", "html": "<span class='verse-line'>{}</span>"}, "dhp242:3": {"pli": "Malā ve pāpakā dhammā, ", "eng": "Bad qualities are a stain ", "html": "<span class='verse-line'>{}</span>"}, "dhp242:4": {"pli": "asmiṁ loke paramhi ca. ", "eng": "in this world and the next. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp243:1": {"pli": "Tato malā malataraṁ, ", "eng": "But a worse stain than these ", "html": "<article id='dhp243'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp243:2": {"pli": "avijjā paramaṁ malaṁ; ", "eng": "is ignorance, the worst stain of all. ", "html": "<span class='verse-line'>{}</span>"}, "dhp243:3": {"pli": "Etaṁ malaṁ pahantvāna, ", "eng": "Having given up that stain, ", "html": "<span class='verse-line'>{}</span>"}, "dhp243:4": {"pli": "nimmalā hotha bhikkhavo. ", "eng": "be without stains, mendicants! ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp244:0": {"pli": "Cūḷasāribhikkhuvatthu ", "html": "<article id='dhp244'><h2 class='sutta-title'>{}</h2>"}, "dhp244:1": {"pli": "Sujīvaṁ ahirikena, ", "eng": "Life is easy for the shameless. ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp244:2": {"pli": "kākasūrena dhaṁsinā; ", "eng": "With all the rude courage of a crow, ", "html": "<span class='verse-line'>{}</span>"}, "dhp244:3": {"pli": "Pakkhandinā pagabbhena, ", "eng": "they live pushy, ", "html": "<span class='verse-line'>{}</span>"}, "dhp244:4": {"pli": "saṅkiliṭṭhena jīvitaṁ. ", "eng": "rude, and corrupt. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp245:1": {"pli": "Hirīmatā ca dujjīvaṁ, ", "eng": "Life is hard for the conscientious, ", "html": "<article id='dhp245'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp245:2": {"pli": "niccaṁ sucigavesinā; ", "eng": "always seeking purity, ", "html": "<span class='verse-line'>{}</span>"}, "dhp245:3": {"pli": "Alīnenāppagabbhena, ", "eng": "neither clinging nor rude, ", "html": "<span class='verse-line'>{}</span>"}, "dhp245:4": {"pli": "suddhājīvena passatā. ", "eng": "pure of livelihood and discerning. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp246:0": {"pli": "Pañcaupāsakavatthu ", "html": "<article id='dhp246'><h2 class='sutta-title'>{}</h2>"}, "dhp246:1": {"pli": "Yo pāṇamatipāteti, ", "eng": "Take anyone in this world ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp246:2": {"pli": "musāvādañca bhāsati; ", "eng": "who kills living creatures, ", "html": "<span class='verse-line'>{}</span>"}, "dhp246:3": {"pli": "Loke adinnamādiyati, ", "eng": "speaks falsely, steals, ", "html": "<span class='verse-line'>{}</span>"}, "dhp246:4": {"pli": "paradārañca gacchati. ", "eng": "commits adultery, ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp247:1": {"pli": "Surāmerayapānañca, ", "eng": "and indulges in drinking ", "html": "<article id='dhp247'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp247:2": {"pli": "yo naro anuyuñjati; ", "eng": "beer and wine. ", "html": "<span class='verse-line'>{}</span>"}, "dhp247:3": {"pli": "Idheva meso lokasmiṁ, ", "eng": "Right here they dig up ", "html": "<span class='verse-line'>{}</span>"}, "dhp247:4": {"pli": "mūlaṁ khaṇati attano. ", "eng": "the root of their own self. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp248:1": {"pli": "Evaṁ bho purisa jānāhi, ", "eng": "Know this, good fellow: ", "html": "<article id='dhp248'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp248:2": {"pli": "pāpadhammā asaññatā; ", "eng": "they are unrestrained and wicked. ", "html": "<span class='verse-line'>{}</span>"}, "dhp248:3": {"pli": "Mā taṁ lobho adhammo ca, ", "eng": "Don’t let that greed and unrighteousness ", "html": "<span class='verse-line'>{}</span>"}, "dhp248:4": {"pli": "ciraṁ dukkhāya randhayuṁ. ", "eng": "inflict pain on you for long. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp249:0": {"pli": "Tissadaharavatthu ", "html": "<article id='dhp249'><h2 class='sutta-title'>{}</h2>"}, "dhp249:1": {"pli": "Dadāti ve yathāsaddhaṁ, ", "eng": "The people give according to their faith, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp249:2": {"pli": "yathāpasādanaṁ jano; ", "eng": "according to their confidence. ", "html": "<span class='verse-line'>{}</span>"}, "dhp249:3": {"pli": "Tattha yo ca maṅku bhavati, ", "eng": "If you get upset over that, ", "html": "<span class='verse-line'>{}</span>"}, "dhp249:4": {"pli": "paresaṁ pānabhojane; ", "eng": "over other’s food and drink, ", "html": "<span class='verse-line'>{}</span>"}, "dhp249:5": {"pli": "Na so divā vā rattiṁ vā, ", "eng": "you’ll not, by day or by night, ", "html": "<span class='verse-line'>{}</span>"}, "dhp249:6": {"pli": "samādhimadhigacchati. ", "eng": "become immersed in samādhi. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp250:1": {"pli": "Yassa cetaṁ samucchinnaṁ, ", "eng": "Those who have cut that out, ", "html": "<article id='dhp250'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp250:2": {"pli": "mūlaghaccaṁ samūhataṁ; ", "eng": "dug it up at the root, eradicated it, ", "html": "<span class='verse-line'>{}</span>"}, "dhp250:3": {"pli": "Sa ve divā vā rattiṁ vā, ", "eng": "they will, by day or by night, ", "html": "<span class='verse-line'>{}</span>"}, "dhp250:4": {"pli": "samādhimadhigacchati. ", "eng": "become immersed in samādhi. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp251:0": {"pli": "Pañcaupāsakavatthu ", "html": "<article id='dhp251'><h2 class='sutta-title'>{}</h2>"}, "dhp251:1": {"pli": "Natthi rāgasamo aggi, ", "eng": "There is no fire like greed, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp251:2": {"pli": "natthi dosasamo gaho; ", "eng": "no crime like hate, ", "html": "<span class='verse-line'>{}</span>"}, "dhp251:3": {"pli": "Natthi mohasamaṁ jālaṁ, ", "eng": "no net like delusion, ", "html": "<span class='verse-line'>{}</span>"}, "dhp251:4": {"pli": "natthi taṇhāsamā nadī. ", "eng": "no river like craving. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp252:0": {"pli": "Meṇḍakaseṭṭhivatthu ", "html": "<article id='dhp252'><h2 class='sutta-title'>{}</h2>"}, "dhp252:1": {"pli": "Sudassaṁ vajjamaññesaṁ, ", "eng": "It’s easy to see the faults of others, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp252:2": {"pli": "attano pana duddasaṁ; ", "eng": "hard to see one’s own. ", "html": "<span class='verse-line'>{}</span>"}, "dhp252:3": {"pli": "Paresaṁ hi so vajjāni, ", "eng": "For the faults of others ", "html": "<span class='verse-line'>{}</span>"}, "dhp252:4": {"pli": "opunāti yathā bhusaṁ; ", "eng": "are tossed high like hay, ", "html": "<span class='verse-line'>{}</span>"}, "dhp252:5": {"pli": "Attano pana chādeti, ", "eng": "while one’s own are hidden, ", "html": "<span class='verse-line'>{}</span>"}, "dhp252:6": {"pli": "kaliṁva kitavā saṭho. ", "eng": "as a cheat hides a bad hand. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp253:0": {"pli": "Ujjhānasaññittheravatthu ", "html": "<article id='dhp253'><h2 class='sutta-title'>{}</h2>"}, "dhp253:1": {"pli": "Paravajjānupassissa, ", "eng": "When you look for the flaws of others, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp253:2": {"pli": "niccaṁ ujjhānasaññino; ", "eng": "always finding fault, ", "html": "<span class='verse-line'>{}</span>"}, "dhp253:3": {"pli": "Āsavā tassa vaḍḍhanti, ", "eng": "your defilements only grow, ", "html": "<span class='verse-line'>{}</span>"}, "dhp253:4": {"pli": "ārā so āsavakkhayā. ", "eng": "you’re far from ending defilements. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp254:0": {"pli": "Subhaddaparibbājakavatthu ", "html": "<article id='dhp254'><h2 class='sutta-title'>{}</h2>"}, "dhp254:1": {"pli": "Ākāseva padaṁ natthi, ", "eng": "In the atmosphere there is no track, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp254:2": {"pli": "samaṇo natthi bāhire; ", "eng": "there’s no true ascetic outside here. ", "html": "<span class='verse-line'>{}</span>"}, "dhp254:3": {"pli": "Papañcābhiratā pajā, ", "eng": "People enjoy proliferation, ", "html": "<span class='verse-line'>{}</span>"}, "dhp254:4": {"pli": "nippapañcā tathāgatā. ", "eng": "the Realized Ones are free of proliferation. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp255:1": {"pli": "Ākāseva padaṁ natthi, ", "eng": "In the atmosphere there is no track, ", "html": "<article id='dhp255'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp255:2": {"pli": "samaṇo natthi bāhire; ", "eng": "there’s no true ascetic outside here. ", "html": "<span class='verse-line'>{}</span>"}, "dhp255:3": {"pli": "Saṅkhārā sassatā natthi, ", "eng": "No conditions last forever, ", "html": "<span class='verse-line'>{}</span>"}, "dhp255:4": {"pli": "natthi buddhānamiñjitaṁ. ", "eng": "the Awakened Ones are not shaken. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp255:5": {"pli": "Malavaggo aṭṭhārasamo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp256-272": {"dhp256:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp256-272'><header><ul><li class='division'>{}</li>"}, "dhp256:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 256–272 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp256:0.3": {"pli": "Dhammaṭṭhavagga ", "eng": "19. The Just ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp256:0.4": {"pli": "Vinicchayamahāmattavatthu ", "html": "<article id='dhp256'><h2 class='sutta-title'>{}</h2>"}, "dhp256:1": {"pli": "Na tena hoti dhammaṭṭho, ", "eng": "You don’t become just ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp256:2": {"pli": "yenatthaṁ sāhasā naye; ", "eng": "by passing hasty judgment. ", "html": "<span class='verse-line'>{}</span>"}, "dhp256:3": {"pli": "Yo ca atthaṁ anatthañca, ", "eng": "An astute person evaluates both ", "html": "<span class='verse-line'>{}</span>"}, "dhp256:4": {"pli": "ubho niccheyya paṇḍito. ", "eng": "what is pertinent and what is irrelevant. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp257:1": {"pli": "Asāhasena dhammena, ", "eng": "A wise one judges others without haste, ", "html": "<article id='dhp257'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp257:2": {"pli": "samena nayatī pare; ", "eng": "justly and impartially; ", "html": "<span class='verse-line'>{}</span>"}, "dhp257:3": {"pli": "Dhammassa gutto medhāvī, ", "eng": "that guardian of the law ", "html": "<span class='verse-line'>{}</span>"}, "dhp257:4": {"pli": "“dhammaṭṭho”ti pavuccati. ", "eng": "is said to be just. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp258:0": {"pli": "Chabbaggiyavatthu ", "html": "<article id='dhp258'><h2 class='sutta-title'>{}</h2>"}, "dhp258:1": {"pli": "Na tena paṇḍito hoti, ", "eng": "You’re not an astute scholar ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp258:2": {"pli": "yāvatā bahu bhāsati; ", "eng": "just because you speak a lot. ", "html": "<span class='verse-line'>{}</span>"}, "dhp258:3": {"pli": "Khemī averī abhayo, ", "eng": "One who is secure, free of enmity and fear, ", "html": "<span class='verse-line'>{}</span>"}, "dhp258:4": {"pli": "“paṇḍito”ti pavuccati. ", "eng": "is said to be astute. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp259:0": {"pli": "Ekudānakhīṇāsavavatthu ", "html": "<article id='dhp259'><h2 class='sutta-title'>{}</h2>"}, "dhp259:1": {"pli": "Na tāvatā dhammadharo, ", "eng": "You’re not one who has memorized the teaching ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp259:2": {"pli": "yāvatā bahu bhāsati; ", "eng": "just because you recite a lot. ", "html": "<span class='verse-line'>{}</span>"}, "dhp259:3": {"pli": "Yo ca appampi sutvāna, ", "eng": "Someone who directly sees the teaching ", "html": "<span class='verse-line'>{}</span>"}, "dhp259:4": {"pli": "dhammaṁ kāyena passati; ", "eng": "after hearing only a little ", "html": "<span class='verse-line'>{}</span>"}, "dhp259:5": {"pli": "Sa ve dhammadharo hoti, ", "eng": "is truly one who has memorized the teaching, ", "html": "<span class='verse-line'>{}</span>"}, "dhp259:6": {"pli": "yo dhammaṁ nappamajjati. ", "eng": "for they can never forget it. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp260:0": {"pli": "Lakuṇḍakabhaddiyattheravatthu ", "html": "<article id='dhp260'><h2 class='sutta-title'>{}</h2>"}, "dhp260:1": {"pli": "Na tena thero so hoti, ", "eng": "You don’t become a senior ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp260:2": {"pli": "yenassa palitaṁ siro; ", "eng": "by getting some grey hairs; ", "html": "<span class='verse-line'>{}</span>"}, "dhp260:3": {"pli": "Paripakko vayo tassa, ", "eng": "for one ripe only in age, ", "html": "<span class='verse-line'>{}</span>"}, "dhp260:4": {"pli": "“moghajiṇṇo”ti vuccati. ", "eng": "is said to have aged in vain. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp261:1": {"pli": "Yamhi saccañca dhammo ca, ", "eng": "One who is truthful and principled, ", "html": "<article id='dhp261'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp261:2": {"pli": "ahiṁsā saṁyamo damo; ", "eng": "harmless, restrained, and self-controlled, ", "html": "<span class='verse-line'>{}</span>"}, "dhp261:3": {"pli": "Sa ve vantamalo dhīro, ", "eng": "attentive, purged of stains, ", "html": "<span class='verse-line'>{}</span>"}, "dhp261:4": {"pli": "“thero” iti pavuccati. ", "eng": "is said to be a senior. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp262:0": {"pli": "Sambahulabhikkhuvatthu ", "html": "<article id='dhp262'><h2 class='sutta-title'>{}</h2>"}, "dhp262:1": {"pli": "Na vākkaraṇamattena, ", "eng": "Not by mere enunciation, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp262:2": {"pli": "vaṇṇapokkharatāya vā; ", "eng": "or a beautiful complexion ", "html": "<span class='verse-line'>{}</span>"}, "dhp262:3": {"pli": "Sādhurūpo naro hoti, ", "eng": "does a person become holy, ", "html": "<span class='verse-line'>{}</span>"}, "dhp262:4": {"pli": "issukī maccharī saṭho. ", "eng": "if they’re jealous, stingy, and devious. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp263:1": {"pli": "Yassa cetaṁ samucchinnaṁ, ", "eng": "But if they’ve cut that out, ", "html": "<article id='dhp263'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp263:2": {"pli": "mūlaghaccaṁ samūhataṁ; ", "eng": "dug it up at the root, eradicated it, ", "html": "<span class='verse-line'>{}</span>"}, "dhp263:3": {"pli": "Sa vantadoso medhāvī, ", "eng": "that wise one, purged of vice, ", "html": "<span class='verse-line'>{}</span>"}, "dhp263:4": {"pli": "“sādhurūpo”ti vuccati. ", "eng": "is said to be holy. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp264:0": {"pli": "Hatthakavatthu ", "html": "<article id='dhp264'><h2 class='sutta-title'>{}</h2>"}, "dhp264:1": {"pli": "Na muṇḍakena samaṇo, ", "eng": "A liar and breaker of vows is no ascetic ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp264:2": {"pli": "abbato alikaṁ bhaṇaṁ; ", "eng": "just because they shave their head. ", "html": "<span class='verse-line'>{}</span>"}, "dhp264:3": {"pli": "Icchālobhasamāpanno, ", "eng": "How on earth can one be an ascetic ", "html": "<span class='verse-line'>{}</span>"}, "dhp264:4": {"pli": "samaṇo kiṁ bhavissati. ", "eng": "who’s full of desire and greed? ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp265:1": {"pli": "Yo ca sameti pāpāni, ", "eng": "One who stops all wicked deeds, ", "html": "<article id='dhp265'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp265:2": {"pli": "aṇuṁthūlāni sabbaso; ", "eng": "great and small, ", "html": "<span class='verse-line'>{}</span>"}, "dhp265:3": {"pli": "Samitattā hi pāpānaṁ, ", "eng": "because of stopping wicked deeds ", "html": "<span class='verse-line'>{}</span>"}, "dhp265:4": {"pli": "“samaṇo”ti pavuccati. ", "eng": "is said to be an ascetic. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp266:0": {"pli": "Aññatarabrāhmaṇavatthu ", "html": "<article id='dhp266'><h2 class='sutta-title'>{}</h2>"}, "dhp266:1": {"pli": "Na tena bhikkhu so hoti, ", "eng": "You don’t become a mendicant ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp266:2": {"pli": "yāvatā bhikkhate pare; ", "eng": "just by begging from others. ", "html": "<span class='verse-line'>{}</span>"}, "dhp266:3": {"pli": "Vissaṁ dhammaṁ samādāya, ", "eng": "One who has undertaken domestic duties ", "html": "<span class='verse-line'>{}</span>"}, "dhp266:4": {"pli": "bhikkhu hoti na tāvatā. ", "eng": "has not yet become a mendicant. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp267:1": {"pli": "Yodha puññañca pāpañca, ", "eng": "But one living a spiritual life, ", "html": "<article id='dhp267'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp267:2": {"pli": "bāhetvā brahmacariyavā; ", "eng": "who has banished both merit and evil, ", "html": "<span class='verse-line'>{}</span>"}, "dhp267:3": {"pli": "Saṅkhāya loke carati, ", "eng": "who wanders having appraised the world, ", "html": "<span class='verse-line'>{}</span>"}, "dhp267:4": {"pli": "sa ve “bhikkhū”ti vuccati. ", "eng": "is said to be a mendicant. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp268:0": {"pli": "Titthiyavatthu ", "html": "<article id='dhp268'><h2 class='sutta-title'>{}</h2>"}, "dhp268:1": {"pli": "Na monena munī hoti, ", "eng": "You don’t become a sage by being sagelike, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>", "comm": "<i lang='pi' translate='no'>Mona</i> (“sagacity”, Sanskrit <i lang='pi' translate='no'>mauna</i>) is the abstract noun from <i lang='pi' translate='no'>muni</i> (‘sage”). Over time it acquired the sense “silence”, as a thoughtful man is silent, and it is explained as such here by the commentary as well as the Chinese translation (T 210:27.13 at T iv 586c19). However I have not found any early instances to support this. In Pali, silence is <i lang='pi' translate='no'>tuṇhī</i>; a “vow of silence”—forbidden to monastics—is <i lang='pi' translate='no'>mūgabbata</i> (<a href='https://suttacentral.net/pli-tv-kd4/en/sujato#1.12.6'>Kd 4:1.12.6</a>). Nothing in the context here suggests a vow of silence. In the Rig Veda (7.56.8, 10.136) the <i lang='pi' translate='no'>muni</i> appears as an ecstatic, raving as if drunk, apparently from drugs (<i lang='pi' translate='no'>soma</i>, <i lang='pi' translate='no'>viṣa</i>), suggesting a sense of “mind-altered” or “mind-expanded”. Chāndogya Upaniṣad 8.5.2 refers to <i lang='pi' translate='no'>mauna</i> as the life of a student, characterized by <i lang='pi' translate='no'>manute</i> (“thinking”, “reflection”), so we can well translate “thoughtfulness”. Bṛhadāraṇyaka Upaniṣad 3.5.1 enjoins the student to become like a child, then reject both scholarship and childishness, thus becoming a “sage” (<i lang='pi' translate='no'>muni</i>). Then, rejecting sagacity and lack of sagacity they become a brahmin. This could easily have the Vedic sense of “mind-expanded (through drugs or meditation)”. A similar sense probably applies here. The verse enjoins “weighed” or rational thought as opposed to confused, perhaps “ecstatic” or “mind-altered” thought. "}, "dhp268:2": {"pli": "mūḷharūpo aviddasu; ", "eng": "while still confused and ignorant. ", "html": "<span class='verse-line'>{}</span>"}, "dhp268:3": {"pli": "Yo ca tulaṁva paggayha, ", "eng": "The astute one who holds the scales, ", "html": "<span class='verse-line'>{}</span>"}, "dhp268:4": {"pli": "varamādāya paṇḍito. ", "eng": "taking only the best, ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp269:1": {"pli": "Pāpāni parivajjeti, ", "eng": "and shunning the bad—", "html": "<article id='dhp269'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp269:2": {"pli": "sa munī tena so muni; ", "eng": "that is a sage, <j>and that is how one becomes a sage. ", "html": "<span class='verse-line'>{}</span>"}, "dhp269:3": {"pli": "Yo munāti ubho loke, ", "eng": "One who sagely weighs both in the world, ", "html": "<span class='verse-line'>{}</span>", "comm": "These two lines recapitulate the previous lines. <i lang='pi' translate='no'>Munāti</i> (“sagely weighs”, literally “thinks, examines”) is punned with <i lang='pi' translate='no'>mināti</i> (“measure, weigh”) following the imagery of the scales. “Both” calls back to both good and evil. "}, "dhp269:4": {"pli": "“muni” tena pavuccati. ", "eng": "is thereby said to be a sage. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp270:0": {"pli": "Bālisikavatthu ", "html": "<article id='dhp270'><h2 class='sutta-title'>{}</h2>"}, "dhp270:1": {"pli": "Na tena ariyo hoti, ", "eng": "You don’t become a noble one ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp270:2": {"pli": "yena pāṇāni hiṁsati; ", "eng": "by harming living beings. ", "html": "<span class='verse-line'>{}</span>"}, "dhp270:3": {"pli": "Ahiṁsā sabbapāṇānaṁ, ", "eng": "One harmless towards all living beings ", "html": "<span class='verse-line'>{}</span>"}, "dhp270:4": {"pli": "“ariyo”ti pavuccati. ", "eng": "is said to be a noble one. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp271:0": {"pli": "Sambahulasīlādisampannabhikkhuvatthu ", "html": "<article id='dhp271'><h2 class='sutta-title'>{}</h2>"}, "dhp271:1": {"pli": "Na sīlabbatamattena, ", "eng": "Not by precepts and observances, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp271:2": {"pli": "bāhusaccena vā pana; ", "eng": "nor by much learning, ", "html": "<span class='verse-line'>{}</span>"}, "dhp271:3": {"pli": "Atha vā samādhilābhena, ", "eng": "nor by meditative immersion, ", "html": "<span class='verse-line'>{}</span>"}, "dhp271:4": {"pli": "vivittasayanena vā. ", "eng": "nor by living in seclusion, ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp272:1": {"pli": "Phusāmi nekkhammasukhaṁ, ", "eng": "do I experience the bliss of renunciation ", "html": "<article id='dhp272'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp272:2": {"pli": "aputhujjanasevitaṁ; ", "eng": "not frequented by ordinary people. ", "html": "<span class='verse-line'>{}</span>"}, "dhp272:3": {"pli": "Bhikkhu vissāsamāpādi, ", "eng": "A mendicant cannot rest confident ", "html": "<span class='verse-line'>{}</span>"}, "dhp272:4": {"pli": "appatto āsavakkhayaṁ. ", "eng": "without attaining the end of defilements. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp272:5": {"pli": "Dhammaṭṭhavaggo ekūnavīsatimo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp273-289": {"dhp273:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp273-289'><header><ul><li class='division'>{}</li>"}, "dhp273:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 273–289 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp273:0.3": {"pli": "Maggavagga ", "eng": "20. The Path ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp273:0.4": {"pli": "Pañcasatabhikkhuvatthu ", "html": "<article id='dhp273'><h2 class='sutta-title'>{}</h2>"}, "dhp273:1": {"pli": "Maggānaṭṭhaṅgiko seṭṭho, ", "eng": "Of paths, the eightfold is the best; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp273:2": {"pli": "saccānaṁ caturo padā; ", "eng": "of truths, the four statements; ", "html": "<span class='verse-line'>{}</span>"}, "dhp273:3": {"pli": "Virāgo seṭṭho dhammānaṁ, ", "eng": "dispassion is the best of things, ", "html": "<span class='verse-line'>{}</span>"}, "dhp273:4": {"pli": "dvipadānañca cakkhumā. ", "eng": "and the Clear-eyed One is the best of humans. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp274:1": {"pli": "Eseva maggo natthañño, ", "eng": "<em>This</em> is the path, there is no other ", "html": "<article id='dhp274'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp274:2": {"pli": "Dassanassa visuddhiyā; ", "eng": "for the purification of vision. ", "html": "<span class='verse-line'>{}</span>"}, "dhp274:3": {"pli": "Etañhi tumhe paṭipajjatha, ", "eng": "You all must practice this, ", "html": "<span class='verse-line'>{}</span>"}, "dhp274:4": {"pli": "Mārassetaṁ pamohanaṁ. ", "eng": "it is the way to baffle Māra. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp275:1": {"pli": "Etañhi tumhe paṭipannā, ", "eng": "When you all are practicing this, ", "html": "<article id='dhp275'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp275:2": {"pli": "dukkhassantaṁ karissatha; ", "eng": "you will make an end of suffering. ", "html": "<span class='verse-line'>{}</span>"}, "dhp275:3": {"pli": "Akkhāto vo mayā maggo, ", "eng": "I have explained the path to you ", "html": "<span class='verse-line'>{}</span>"}, "dhp275:4": {"pli": "aññāya sallakantanaṁ. ", "eng": "for extracting the thorn with wisdom. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp276:1": {"pli": "Tumhehi kiccamātappaṁ, ", "eng": "You yourselves must do the work, ", "html": "<article id='dhp276'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp276:2": {"pli": "akkhātāro tathāgatā; ", "eng": "the Realized Ones just show the way. ", "html": "<span class='verse-line'>{}</span>"}, "dhp276:3": {"pli": "Paṭipannā pamokkhanti, ", "eng": "Meditators practicing absorption ", "html": "<span class='verse-line'>{}</span>"}, "dhp276:4": {"pli": "jhāyino mārabandhanā. ", "eng": "are released from Māra’s bonds. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp277:0": {"pli": "Aniccalakkhaṇavatthu ", "html": "<article id='dhp277'><h2 class='sutta-title'>{}</h2>"}, "dhp277:1": {"pli": "“Sabbe saṅkhārā aniccā”ti, ", "eng": "All conditions are impermanent—", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp277:2": {"pli": "yadā paññāya passati; ", "eng": "when this is seen with wisdom, ", "html": "<span class='verse-line'>{}</span>"}, "dhp277:3": {"pli": "Atha nibbindati dukkhe, ", "eng": "one grows disillusioned with suffering: ", "html": "<span class='verse-line'>{}</span>"}, "dhp277:4": {"pli": "esa maggo visuddhiyā. ", "eng": "this is the path to purity. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp278:0": {"pli": "Dukkhalakkhaṇavatthu ", "html": "<article id='dhp278'><h2 class='sutta-title'>{}</h2>"}, "dhp278:1": {"pli": "“Sabbe saṅkhārā dukkhā”ti, ", "eng": "All conditions are suffering—", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp278:2": {"pli": "yadā paññāya passati; ", "eng": "when this is seen with wisdom, ", "html": "<span class='verse-line'>{}</span>"}, "dhp278:3": {"pli": "Atha nibbindati dukkhe, ", "eng": "one grows disillusioned with suffering: ", "html": "<span class='verse-line'>{}</span>"}, "dhp278:4": {"pli": "esa maggo visuddhiyā. ", "eng": "this is the path to purity. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp279:0": {"pli": "Anattalakkhaṇavatthu ", "html": "<article id='dhp279'><h2 class='sutta-title'>{}</h2>"}, "dhp279:1": {"pli": "“Sabbe dhammā anattā”ti, ", "eng": "All things are not-self—", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp279:2": {"pli": "yadā paññāya passati; ", "eng": "when this is seen with wisdom, ", "html": "<span class='verse-line'>{}</span>"}, "dhp279:3": {"pli": "Atha nibbindati dukkhe, ", "eng": "one grows disillusioned with suffering: ", "html": "<span class='verse-line'>{}</span>"}, "dhp279:4": {"pli": "esa maggo visuddhiyā. ", "eng": "this is the path to purity. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp280:0": {"pli": "Padhānakammikatissattheravatthu ", "html": "<article id='dhp280'><h2 class='sutta-title'>{}</h2>"}, "dhp280:1": {"pli": "Uṭṭhānakālamhi anuṭṭhahāno, ", "eng": "They don’t get going when it’s time to start; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp280:2": {"pli": "Yuvā balī ālasiyaṁ upeto; ", "eng": "they’re young and strong, but given to sloth. ", "html": "<span class='verse-line'>{}</span>"}, "dhp280:3": {"pli": "Saṁsannasaṅkappamano kusīto, ", "eng": "Their mind depressed in sunken thought, ", "html": "<span class='verse-line'>{}</span>"}, "dhp280:4": {"pli": "Paññāya maggaṁ alaso na vindati. ", "eng": "lazy and slothful, they can’t discern the path. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp281:0": {"pli": "Sūkarapetavatthu ", "html": "<article id='dhp281'><h2 class='sutta-title'>{}</h2>"}, "dhp281:1": {"pli": "Vācānurakkhī manasā susaṁvuto, ", "eng": "Guarded in speech, restrained in mind, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp281:2": {"pli": "Kāyena ca nākusalaṁ kayirā; ", "eng": "doing no unskillful bodily deed. ", "html": "<span class='verse-line'>{}</span>"}, "dhp281:3": {"pli": "Ete tayo kammapathe visodhaye, ", "eng": "Purify these three ways of performing deeds, ", "html": "<span class='verse-line'>{}</span>"}, "dhp281:4": {"pli": "Ārādhaye maggamisippaveditaṁ. ", "eng": "and win the path known to seers. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp282:0": {"pli": "Poṭṭhilattheravatthu ", "html": "<article id='dhp282'><h2 class='sutta-title'>{}</h2>"}, "dhp282:1": {"pli": "Yogā ve jāyatī bhūri, ", "eng": "From meditation springs wisdom, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp282:2": {"pli": "ayogā bhūrisaṅkhayo; ", "eng": "without meditation, wisdom ends. ", "html": "<span class='verse-line'>{}</span>"}, "dhp282:3": {"pli": "Etaṁ dvedhāpathaṁ ñatvā, ", "eng": "Knowing these two paths—", "html": "<span class='verse-line'>{}</span>"}, "dhp282:4": {"pli": "bhavāya vibhavāya ca; ", "eng": "of progress and decline—", "html": "<span class='verse-line'>{}</span>", "comm": "Here <i lang='pi' translate='no'>bhava</i> and <i lang='pi' translate='no'>vibhava</i> do not have the normal sense of “continued life” and “annihilation”. "}, "dhp282:5": {"pli": "Tathāttānaṁ niveseyya, ", "eng": "you should conduct yourself ", "html": "<span class='verse-line'>{}</span>"}, "dhp282:6": {"pli": "yathā bhūri pavaḍḍhati. ", "eng": "so that wisdom grows. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp283:0": {"pli": "Pañcamahallakabhikkhuvatthu ", "html": "<article id='dhp283'><h2 class='sutta-title'>{}</h2>"}, "dhp283:1": {"pli": "Vanaṁ chindatha mā rukkhaṁ, ", "eng": "Cut down the jungle, not just a tree; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp283:2": {"pli": "vanato jāyate bhayaṁ; ", "eng": "from the jungle springs fear. ", "html": "<span class='verse-line'>{}</span>"}, "dhp283:3": {"pli": "Chetvā vanañca vanathañca, ", "eng": "Having cut down jungle and snarl, ", "html": "<span class='verse-line'>{}</span>"}, "dhp283:4": {"pli": "nibbanā hotha bhikkhavo. ", "eng": "be free of jungles, mendicants! ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp284:1": {"pli": "Yāva hi vanatho na chijjati, ", "eng": "So long as the vine, no matter how small, ", "html": "<article id='dhp284'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp284:2": {"pli": "Aṇumattopi narassa nārisu; ", "eng": "that ties a man to women is not cut, ", "html": "<span class='verse-line'>{}</span>"}, "dhp284:3": {"pli": "Paṭibaddhamanova tāva so, ", "eng": "his mind remains trapped, ", "html": "<span class='verse-line'>{}</span>"}, "dhp284:4": {"pli": "Vaccho khīrapakova mātari. ", "eng": "like a calf suckling its mother. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp285:0": {"pli": "Suvaṇṇakārattheravatthu ", "html": "<article id='dhp285'><h2 class='sutta-title'>{}</h2>"}, "dhp285:1": {"pli": "Ucchinda sinehamattano, ", "eng": "Cut out affection for oneself, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp285:2": {"pli": "Kumudaṁ sāradikaṁva pāṇinā; ", "eng": "like plucking an autumn lotus. ", "html": "<span class='verse-line'>{}</span>"}, "dhp285:3": {"pli": "Santimaggameva brūhaya, ", "eng": "Foster only the path to peace, ", "html": "<span class='verse-line'>{}</span>"}, "dhp285:4": {"pli": "Nibbānaṁ sugatena desitaṁ. ", "eng": "the extinguishment the Holy One taught. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp286:0": {"pli": "Mahādhanavāṇijavatthu ", "html": "<article id='dhp286'><h2 class='sutta-title'>{}</h2>"}, "dhp286:1": {"pli": "Idha vassaṁ vasissāmi, ", "eng": "“Here I will stay for the rains; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp286:2": {"pli": "idha hemantagimhisu; ", "eng": "here for winter, here the summer”; ", "html": "<span class='verse-line'>{}</span>"}, "dhp286:3": {"pli": "Iti bālo vicinteti, ", "eng": "thus the fool thinks, ", "html": "<span class='verse-line'>{}</span>"}, "dhp286:4": {"pli": "antarāyaṁ na bujjhati. ", "eng": "not realizing the danger. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp287:0": {"pli": "Kisāgotamīvatthu ", "html": "<article id='dhp287'><h2 class='sutta-title'>{}</h2>"}, "dhp287:1": {"pli": "Taṁ puttapasusammattaṁ, ", "eng": "As a mighty flood sweeps away a sleeping village, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp287:2": {"pli": "byāsattamanasaṁ naraṁ; ", "eng": "death steals away a man ", "html": "<span class='verse-line'>{}</span>"}, "dhp287:3": {"pli": "Suttaṁ gāmaṁ mahoghova, ", "eng": "who dotes on children and cattle, ", "html": "<span class='verse-line'>{}</span>"}, "dhp287:4": {"pli": "maccu ādāya gacchati. ", "eng": "his mind caught up in them. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp288:0": {"pli": "Paṭācārāvatthu ", "html": "<article id='dhp288'><h2 class='sutta-title'>{}</h2>"}, "dhp288:1": {"pli": "Na santi puttā tāṇāya, ", "eng": "Children provide you no shelter, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp288:2": {"pli": "na pitā nāpi bandhavā; ", "eng": "nor does father, nor relatives. ", "html": "<span class='verse-line'>{}</span>"}, "dhp288:3": {"pli": "Antakenādhipannassa, ", "eng": "When you’re seized by the terminator, ", "html": "<span class='verse-line'>{}</span>"}, "dhp288:4": {"pli": "natthi ñātīsu tāṇatā. ", "eng": "there’s no shelter in family. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp289:1": {"pli": "Etamatthavasaṁ ñatvā, ", "eng": "Knowing the reason for this, ", "html": "<article id='dhp289'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp289:2": {"pli": "paṇḍito sīlasaṁvuto; ", "eng": "astute, and ethically restrained, ", "html": "<span class='verse-line'>{}</span>"}, "dhp289:3": {"pli": "Nibbānagamanaṁ maggaṁ, ", "eng": "one would quickly clear the path ", "html": "<span class='verse-line'>{}</span>"}, "dhp289:4": {"pli": "khippameva visodhaye. ", "eng": "that leads to extinguishment. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp289:5": {"pli": "Maggavaggo vīsatimo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp290-305": {"dhp290:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp290-305'><header><ul><li class='division'>{}</li>"}, "dhp290:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 290–305 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp290:0.3": {"pli": "Pakiṇṇakavagga ", "eng": "21. Miscellaneous ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp290:0.4": {"pli": "Attanopubbakammavatthu ", "html": "<article id='dhp290'><h2 class='sutta-title'>{}</h2>"}, "dhp290:1": {"pli": "Mattāsukhapariccāgā, ", "eng": "If by giving up material happiness ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp290:2": {"pli": "passe ce vipulaṁ sukhaṁ; ", "eng": "one sees abundant happiness, ", "html": "<span class='verse-line'>{}</span>"}, "dhp290:3": {"pli": "Caje mattāsukhaṁ dhīro, ", "eng": "the attentive would give up material happiness, ", "html": "<span class='verse-line'>{}</span>"}, "dhp290:4": {"pli": "sampassaṁ vipulaṁ sukhaṁ. ", "eng": "seeing the abundant happiness. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp291:0": {"pli": "Kukkuṭaṇḍakhādikāvatthu ", "html": "<article id='dhp291'><h2 class='sutta-title'>{}</h2>"}, "dhp291:1": {"pli": "Paradukkhūpadhānena, ", "eng": "Seeking their own happiness ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp291:2": {"pli": "attano sukhamicchati; ", "eng": "by imposing suffering on others, ", "html": "<span class='verse-line'>{}</span>"}, "dhp291:3": {"pli": "Verasaṁsaggasaṁsaṭṭho, ", "eng": "living intimate with enmity, ", "html": "<span class='verse-line'>{}</span>"}, "dhp291:4": {"pli": "verā so na parimuccati. ", "eng": "they’re not freed from enmity. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp292:0": {"pli": "Bhaddiyānaṁbhikkhūnaṁvatthu ", "html": "<article id='dhp292'><h2 class='sutta-title'>{}</h2>"}, "dhp292:1": {"pli": "Yañhi kiccaṁ apaviddhaṁ, ", "eng": "They dump what should be done, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp292:2": {"pli": "akiccaṁ pana karīyati; ", "eng": "and do what should not be done. ", "html": "<span class='verse-line'>{}</span>"}, "dhp292:3": {"pli": "Unnaḷānaṁ pamattānaṁ, ", "eng": "For the insolent and the negligent, ", "html": "<span class='verse-line'>{}</span>"}, "dhp292:4": {"pli": "tesaṁ vaḍḍhanti āsavā. ", "eng": "their defilements only grow. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp293:1": {"pli": "Yesañca susamāraddhā, ", "eng": "Those that have properly undertaken ", "html": "<article id='dhp293'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp293:2": {"pli": "niccaṁ kāyagatā sati; ", "eng": "constant mindfulness of the body, ", "html": "<span class='verse-line'>{}</span>"}, "dhp293:3": {"pli": "Akiccaṁ te na sevanti, ", "eng": "don’t cultivate what should not be done, ", "html": "<span class='verse-line'>{}</span>"}, "dhp293:4": {"pli": "kicce sātaccakārino; ", "eng": "but always do what should be done. ", "html": "<span class='verse-line'>{}</span>"}, "dhp293:5": {"pli": "Satānaṁ sampajānānaṁ, ", "eng": "Mindful and aware, ", "html": "<span class='verse-line'>{}</span>"}, "dhp293:6": {"pli": "atthaṁ gacchanti āsavā. ", "eng": "their defilements come to an end. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp294:0": {"pli": "Lakuṇḍakabhaddiyavatthu ", "html": "<article id='dhp294'><h2 class='sutta-title'>{}</h2>"}, "dhp294:1": {"pli": "Mātaraṁ pitaraṁ hantvā, ", "eng": "Having slain mother and father, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>", "comm": "The following explanations are according to the commentary. “Mother” is craving, “father” is conceit. "}, "dhp294:2": {"pli": "rājāno dve ca khattiye; ", "eng": "and two aristocratic kings, ", "html": "<span class='verse-line'>{}</span>", "comm": "The “two aristocratic kings” are eternalism and annihilationism. "}, "dhp294:3": {"pli": "Raṭṭhaṁ sānucaraṁ hantvā, ", "eng": "and having wiped out <j>the kingdom with its tax collector, ", "html": "<span class='verse-line'>{}</span>", "comm": "The “kingdom” is the senses and their objects, while the “tax collector” is relishing and lust. "}, "dhp294:4": {"pli": "anīgho yāti brāhmaṇo. ", "eng": "the brahmin walks on untroubled. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp295:1": {"pli": "Mātaraṁ pitaraṁ hantvā, ", "eng": "Having slain mother and father, ", "html": "<article id='dhp295'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp295:2": {"pli": "rājāno dve ca sotthiye; ", "eng": "and two prosperous kings, ", "html": "<span class='verse-line'>{}</span>"}, "dhp295:3": {"pli": "Veyagghapañcamaṁ hantvā, ", "eng": "and a tiger as the fifth, ", "html": "<span class='verse-line'>{}</span>", "comm": "The five hindrances with doubt as fifth. "}, "dhp295:4": {"pli": "anīgho yāti brāhmaṇo. ", "eng": "the brahmin walks on untroubled. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp296:0": {"pli": "Dārusākaṭikaputtavatthu ", "html": "<article id='dhp296'><h2 class='sutta-title'>{}</h2>"}, "dhp296:1": {"pli": "Suppabuddhaṁ pabujjhanti, ", "eng": "The disciples of Gotama ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp296:2": {"pli": "sadā gotamasāvakā; ", "eng": "always wake up refreshed, ", "html": "<span class='verse-line'>{}</span>"}, "dhp296:3": {"pli": "Yesaṁ divā ca ratto ca, ", "eng": "who day and night ", "html": "<span class='verse-line'>{}</span>"}, "dhp296:4": {"pli": "niccaṁ buddhagatā sati. ", "eng": "constantly recollect the Buddha. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp297:1": {"pli": "Suppabuddhaṁ pabujjhanti, ", "eng": "The disciples of Gotama ", "html": "<article id='dhp297'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp297:2": {"pli": "sadā gotamasāvakā; ", "eng": "always wake up refreshed, ", "html": "<span class='verse-line'>{}</span>"}, "dhp297:3": {"pli": "Yesaṁ divā ca ratto ca, ", "eng": "who day and night ", "html": "<span class='verse-line'>{}</span>"}, "dhp297:4": {"pli": "niccaṁ dhammagatā sati. ", "eng": "constantly recollect the teaching. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp298:1": {"pli": "Suppabuddhaṁ pabujjhanti, ", "eng": "The disciples of Gotama ", "html": "<article id='dhp298'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp298:2": {"pli": "sadā gotamasāvakā; ", "eng": "always wake up refreshed, ", "html": "<span class='verse-line'>{}</span>"}, "dhp298:3": {"pli": "Yesaṁ divā ca ratto ca, ", "eng": "who day and night ", "html": "<span class='verse-line'>{}</span>"}, "dhp298:4": {"pli": "niccaṁ saṅghagatā sati. ", "eng": "constantly recollect the Saṅgha. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp299:1": {"pli": "Suppabuddhaṁ pabujjhanti, ", "eng": "The disciples of Gotama ", "html": "<article id='dhp299'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp299:2": {"pli": "sadā gotamasāvakā; ", "eng": "always wake up refreshed, ", "html": "<span class='verse-line'>{}</span>"}, "dhp299:3": {"pli": "Yesaṁ divā ca ratto ca, ", "eng": "who day and night ", "html": "<span class='verse-line'>{}</span>"}, "dhp299:4": {"pli": "niccaṁ kāyagatā sati. ", "eng": "are constantly mindful of the body. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp300:1": {"pli": "Suppabuddhaṁ pabujjhanti, ", "eng": "The disciples of Gotama ", "html": "<article id='dhp300'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp300:2": {"pli": "sadā gotamasāvakā; ", "eng": "always wake up refreshed, ", "html": "<span class='verse-line'>{}</span>"}, "dhp300:3": {"pli": "Yesaṁ divā ca ratto ca, ", "eng": "whose minds day and night ", "html": "<span class='verse-line'>{}</span>"}, "dhp300:4": {"pli": "ahiṁsāya rato mano. ", "eng": "delight in harmlessness. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp301:1": {"pli": "Suppabuddhaṁ pabujjhanti, ", "eng": "The disciples of Gotama ", "html": "<article id='dhp301'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp301:2": {"pli": "sadā gotamasāvakā; ", "eng": "always wake up refreshed, ", "html": "<span class='verse-line'>{}</span>"}, "dhp301:3": {"pli": "Yesaṁ divā ca ratto ca, ", "eng": "whose minds day and night ", "html": "<span class='verse-line'>{}</span>"}, "dhp301:4": {"pli": "bhāvanāya rato mano. ", "eng": "delight in meditation. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp302:0": {"pli": "Vajjiputtakabhikkhuvatthu ", "html": "<article id='dhp302'><h2 class='sutta-title'>{}</h2>"}, "dhp302:1": {"pli": "Duppabbajjaṁ durabhiramaṁ, ", "eng": "Going forth is hard, it’s hard to be happy; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp302:2": {"pli": "Durāvāsā gharā dukhā; ", "eng": "life at home is hard too, and painful, ", "html": "<span class='verse-line'>{}</span>"}, "dhp302:3": {"pli": "Dukkhosamānasaṁvāso, ", "eng": "it’s painful to stay when you’ve nothing in common. ", "html": "<span class='verse-line'>{}</span>"}, "dhp302:4": {"pli": "Dukkhānupatitaddhagū; ", "eng": "A traveler is a prey to pain, ", "html": "<span class='verse-line'>{}</span>"}, "dhp302:5": {"pli": "Tasmā na caddhagū siyā, ", "eng": "so don’t be a traveler, ", "html": "<span class='verse-line'>{}</span>"}, "dhp302:6": {"pli": "Na ca dukkhānupatito siyā. ", "eng": "don’t be prey to pain. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp303:0": {"pli": "Cittagahapativatthu ", "html": "<article id='dhp303'><h2 class='sutta-title'>{}</h2>"}, "dhp303:1": {"pli": "Saddho sīlena sampanno, ", "eng": "One who is faithful, accomplished in ethics, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp303:2": {"pli": "yasobhogasamappito; ", "eng": "blessed with fame and wealth, ", "html": "<span class='verse-line'>{}</span>"}, "dhp303:3": {"pli": "Yaṁ yaṁ padesaṁ bhajati, ", "eng": "is honored in whatever place ", "html": "<span class='verse-line'>{}</span>"}, "dhp303:4": {"pli": "tattha tattheva pūjito. ", "eng": "they frequent. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp304:0": {"pli": "Cūḷasubhaddāvatthu ", "html": "<article id='dhp304'><h2 class='sutta-title'>{}</h2>"}, "dhp304:1": {"pli": "Dūre santo pakāsenti, ", "eng": "The good shine from afar, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp304:2": {"pli": "himavantova pabbato; ", "eng": "like the Himalayan peaks, ", "html": "<span class='verse-line'>{}</span>"}, "dhp304:3": {"pli": "Asantettha na dissanti, ", "eng": "but the wicked are not seen, ", "html": "<span class='verse-line'>{}</span>"}, "dhp304:4": {"pli": "rattiṁ khittā yathā sarā. ", "eng": "like arrows scattered in the night. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp305:0": {"pli": "Ekavihārittheravatthu ", "html": "<article id='dhp305'><h2 class='sutta-title'>{}</h2>"}, "dhp305:1": {"pli": "Ekāsanaṁ ekaseyyaṁ, ", "eng": "Sitting alone, sleeping alone, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp305:2": {"pli": "eko caramatandito; ", "eng": "tirelessly wandering alone; ", "html": "<span class='verse-line'>{}</span>"}, "dhp305:3": {"pli": "Eko damayamattānaṁ, ", "eng": "one who tames themselves alone ", "html": "<span class='verse-line'>{}</span>"}, "dhp305:4": {"pli": "vanante ramito siyā. ", "eng": "would delight within a forest. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp305:5": {"pli": "Pakiṇṇakavaggo ekavīsatimo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp306-319": {"dhp306:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp306-319'><header><ul><li class='division'>{}</li>"}, "dhp306:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 306–319 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp306:0.3": {"pli": "Nirayavagga ", "eng": "22. Hell ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp306:0.4": {"pli": "Sundarīparibbājikāvatthu ", "html": "<article id='dhp306'><h2 class='sutta-title'>{}</h2>"}, "dhp306:1": {"pli": "Abhūtavādī nirayaṁ upeti, ", "eng": "A liar goes to hell, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp306:2": {"pli": "Yo vāpi katvā na karomi cāha; ", "eng": "as does one who denies what they did. ", "html": "<span class='verse-line'>{}</span>"}, "dhp306:3": {"pli": "Ubhopi te pecca samā bhavanti, ", "eng": "Both are equal in the hereafter, ", "html": "<span class='verse-line'>{}</span>"}, "dhp306:4": {"pli": "Nihīnakammā manujā parattha. ", "eng": "those men of base deeds. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp307:0": {"pli": "Duccaritaphalapīḷitavatthu ", "html": "<article id='dhp307'><h2 class='sutta-title'>{}</h2>"}, "dhp307:1": {"pli": "Kāsāvakaṇṭhā bahavo, ", "eng": "Many who wear a scrap of ocher cloth ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp307:2": {"pli": "pāpadhammā asaññatā; ", "eng": "are unrestrained and wicked. ", "html": "<span class='verse-line'>{}</span>"}, "dhp307:3": {"pli": "Pāpā pāpehi kammehi, ", "eng": "Being wicked, they are reborn in hell ", "html": "<span class='verse-line'>{}</span>"}, "dhp307:4": {"pli": "nirayaṁ te upapajjare. ", "eng": "due to their bad deeds. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp308:0": {"pli": "Vaggumudātīriyabhikkhuvatthu ", "html": "<article id='dhp308'><h2 class='sutta-title'>{}</h2>"}, "dhp308:1": {"pli": "Seyyo ayoguḷo bhutto, ", "eng": "It’d be better for the immoral and unrestrained ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp308:2": {"pli": "tatto aggisikhūpamo; ", "eng": "to eat an iron ball, ", "html": "<span class='verse-line'>{}</span>"}, "dhp308:3": {"pli": "Yañce bhuñjeyya dussīlo, ", "eng": "scorching, like a burning flame, ", "html": "<span class='verse-line'>{}</span>"}, "dhp308:4": {"pli": "raṭṭhapiṇḍamasaññato. ", "eng": "than to eat the nation’s alms. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp309:0": {"pli": "Khemakaseṭṭhiputtavatthu ", "html": "<article id='dhp309'><h2 class='sutta-title'>{}</h2>"}, "dhp309:1": {"pli": "Cattāri ṭhānāni naro pamatto, ", "eng": "Four things befall a heedless man ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp309:2": {"pli": "Āpajjati paradārūpasevī; ", "eng": "who sleeps with another’s wife: ", "html": "<span class='verse-line'>{}</span>"}, "dhp309:3": {"pli": "Apuññalābhaṁ na nikāmaseyyaṁ, ", "eng": "wickedness, poor sleep, ", "html": "<span class='verse-line'>{}</span>"}, "dhp309:4": {"pli": "Nindaṁ tatīyaṁ nirayaṁ catutthaṁ. ", "eng": "ill-repute, and rebirth in hell. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp310:1": {"pli": "Apuññalābho ca gatī ca pāpikā, ", "eng": "He accrues wickedness and is reborn in a bad place, ", "html": "<article id='dhp310'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp310:2": {"pli": "Bhītassa bhītāya ratī ca thokikā; ", "eng": "all so a frightened couple <j>may snatch a moment’s pleasure, ", "html": "<span class='verse-line'>{}</span>"}, "dhp310:3": {"pli": "Rājā ca daṇḍaṁ garukaṁ paṇeti, ", "eng": "for which rulers impose a heavy punishment. ", "html": "<span class='verse-line'>{}</span>"}, "dhp310:4": {"pli": "Tasmā naro paradāraṁ na seve. ", "eng": "That’s why a man should not <j>sleep with another’s wife. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp311:0": {"pli": "Dubbacabhikkhuvatthu ", "html": "<article id='dhp311'><h2 class='sutta-title'>{}</h2>"}, "dhp311:1": {"pli": "Kuso yathā duggahito, ", "eng": "When kusa grass is wrongly grasped ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp311:2": {"pli": "hatthamevānukantati; ", "eng": "it only cuts the hand. ", "html": "<span class='verse-line'>{}</span>"}, "dhp311:3": {"pli": "Sāmaññaṁ dupparāmaṭṭhaṁ, ", "eng": "So too, the ascetic life, when wrongly taken, ", "html": "<span class='verse-line'>{}</span>"}, "dhp311:4": {"pli": "nirayāyupakaḍḍhati. ", "eng": "drags you to hell. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp312:1": {"pli": "Yaṁ kiñci sithilaṁ kammaṁ, ", "eng": "Any lax act, ", "html": "<article id='dhp312'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp312:2": {"pli": "saṅkiliṭṭhañca yaṁ vataṁ; ", "eng": "any corrupt observance, ", "html": "<span class='verse-line'>{}</span>"}, "dhp312:3": {"pli": "Saṅkassaraṁ brahmacariyaṁ, ", "eng": "or suspicious spiritual life, ", "html": "<span class='verse-line'>{}</span>"}, "dhp312:4": {"pli": "na taṁ hoti mahapphalaṁ. ", "eng": "is not very fruitful. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp313:1": {"pli": "Kayirā ce kayirāthenaṁ, ", "eng": "If one is to do what should be done, ", "html": "<article id='dhp313'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp313:2": {"pli": "daḷhamenaṁ parakkame; ", "eng": "one should staunchly strive. ", "html": "<span class='verse-line'>{}</span>"}, "dhp313:3": {"pli": "Sithilo hi paribbājo, ", "eng": "For the life gone forth when laxly led ", "html": "<span class='verse-line'>{}</span>"}, "dhp313:4": {"pli": "bhiyyo ākirate rajaṁ. ", "eng": "just stirs up dust all the more. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp314:0": {"pli": "Issāpakataitthivatthu ", "html": "<article id='dhp314'><h2 class='sutta-title'>{}</h2>"}, "dhp314:1": {"pli": "Akataṁ dukkaṭaṁ seyyo, ", "eng": "A bad deed is better left undone, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp314:2": {"pli": "pacchā tappati dukkaṭaṁ; ", "eng": "for it will plague you later on. ", "html": "<span class='verse-line'>{}</span>"}, "dhp314:3": {"pli": "Katañca sukataṁ seyyo, ", "eng": "A good deed is better done, ", "html": "<span class='verse-line'>{}</span>"}, "dhp314:4": {"pli": "yaṁ katvā nānutappati. ", "eng": "one that does not plague you. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp315:0": {"pli": "Sambahulabhikkhuvatthu ", "html": "<article id='dhp315'><h2 class='sutta-title'>{}</h2>"}, "dhp315:1": {"pli": "Nagaraṁ yathā paccantaṁ, ", "eng": "As a frontier city ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp315:2": {"pli": "guttaṁ santarabāhiraṁ; ", "eng": "is guarded inside and out, ", "html": "<span class='verse-line'>{}</span>"}, "dhp315:3": {"pli": "Evaṁ gopetha attānaṁ, ", "eng": "so you should ward yourselves—", "html": "<span class='verse-line'>{}</span>"}, "dhp315:4": {"pli": "khaṇo vo mā upaccagā; ", "eng": "don’t let the moment pass you by. ", "html": "<span class='verse-line'>{}</span>"}, "dhp315:5": {"pli": "Khaṇātītā hi socanti, ", "eng": "For if you miss your moment ", "html": "<span class='verse-line'>{}</span>"}, "dhp315:6": {"pli": "nirayamhi samappitā. ", "eng": "you’ll grieve when sent to hell. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp316:0": {"pli": "Nigaṇṭhavatthu ", "html": "<article id='dhp316'><h2 class='sutta-title'>{}</h2>"}, "dhp316:1": {"pli": "Alajjitāye lajjanti, ", "eng": "Unashamed of what is shameful, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp316:2": {"pli": "lajjitāye na lajjare; ", "eng": "ashamed of what is not shameful; ", "html": "<span class='verse-line'>{}</span>"}, "dhp316:3": {"pli": "Micchādiṭṭhisamādānā, ", "eng": "beings who uphold wrong view ", "html": "<span class='verse-line'>{}</span>"}, "dhp316:4": {"pli": "sattā gacchanti duggatiṁ. ", "eng": "go to a bad place. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp317:1": {"pli": "Abhaye bhayadassino, ", "eng": "Seeing danger where there is none, ", "html": "<article id='dhp317'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp317:2": {"pli": "bhaye cābhayadassino; ", "eng": "and blind to the actual danger, ", "html": "<span class='verse-line'>{}</span>"}, "dhp317:3": {"pli": "Micchādiṭṭhisamādānā, ", "eng": "beings who uphold wrong view ", "html": "<span class='verse-line'>{}</span>"}, "dhp317:4": {"pli": "sattā gacchanti duggatiṁ. ", "eng": "go to a bad place. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp318:0": {"pli": "Titthiyasāvakavatthu ", "html": "<article id='dhp318'><h2 class='sutta-title'>{}</h2>"}, "dhp318:1": {"pli": "Avajje vajjamatino, ", "eng": "Seeing fault where there is none, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp318:2": {"pli": "vajje cāvajjadassino; ", "eng": "and blind to the actual fault, ", "html": "<span class='verse-line'>{}</span>"}, "dhp318:3": {"pli": "Micchādiṭṭhisamādānā, ", "eng": "beings who uphold wrong view ", "html": "<span class='verse-line'>{}</span>"}, "dhp318:4": {"pli": "sattā gacchanti duggatiṁ. ", "eng": "go to a bad place. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp319:1": {"pli": "Vajjañca vajjato ñatvā, ", "eng": "Knowing a fault as a fault ", "html": "<article id='dhp319'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp319:2": {"pli": "avajjañca avajjato; ", "eng": "and the faultless as faultless, ", "html": "<span class='verse-line'>{}</span>"}, "dhp319:3": {"pli": "Sammādiṭṭhisamādānā, ", "eng": "beings who uphold right view ", "html": "<span class='verse-line'>{}</span>"}, "dhp319:4": {"pli": "sattā gacchanti suggatiṁ. ", "eng": "go to a good place. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp319:5": {"pli": "Nirayavaggo dvāvīsatimo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp320-333": {"dhp320:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp320-333'><header><ul><li class='division'>{}</li>"}, "dhp320:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 320–333 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp320:0.3": {"pli": "Nāgavagga ", "eng": "23. Elephants ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp320:0.4": {"pli": "Attadantavatthu ", "html": "<article id='dhp320'><h2 class='sutta-title'>{}</h2>"}, "dhp320:1": {"pli": "Ahaṁ nāgova saṅgāme, ", "eng": "Like an elephant struck ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp320:2": {"pli": "cāpato patitaṁ saraṁ; ", "eng": "with arrows in battle, ", "html": "<span class='verse-line'>{}</span>"}, "dhp320:3": {"pli": "Ativākyaṁ titikkhissaṁ, ", "eng": "I shall withstand abuse, ", "html": "<span class='verse-line'>{}</span>"}, "dhp320:4": {"pli": "dussīlo hi bahujjano. ", "eng": "for so many folk are badly behaved. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp321:1": {"pli": "Dantaṁ nayanti samitiṁ, ", "eng": "The well-tamed beast is the one led to the crowd; ", "html": "<article id='dhp321'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp321:2": {"pli": "dantaṁ rājābhirūhati; ", "eng": "the tamed elephant’s the one the king mounts; ", "html": "<span class='verse-line'>{}</span>"}, "dhp321:3": {"pli": "Danto seṭṭho manussesu, ", "eng": "the tamed person who withstands abuse ", "html": "<span class='verse-line'>{}</span>"}, "dhp321:4": {"pli": "yotivākyaṁ titikkhati. ", "eng": "is the best of human beings. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp322:1": {"pli": "Varamassatarā dantā, ", "eng": "Those who have tamed themselves are better ", "html": "<article id='dhp322'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp322:2": {"pli": "ājānīyā ca sindhavā; ", "eng": "than fine tamed mules, ", "html": "<span class='verse-line'>{}</span>"}, "dhp322:3": {"pli": "Kuñjarā ca mahānāgā, ", "eng": "thoroughbreds from Sindh, ", "html": "<span class='verse-line'>{}</span>"}, "dhp322:4": {"pli": "attadanto tato varaṁ. ", "eng": "or giant tuskers. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp323:0": {"pli": "Hatthācariyapubbakabhikkhuvatthu ", "html": "<article id='dhp323'><h2 class='sutta-title'>{}</h2>"}, "dhp323:1": {"pli": "Na hi etehi yānehi, ", "eng": "For not on those mounts ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp323:2": {"pli": "gaccheyya agataṁ disaṁ; ", "eng": "would you go to the untrodden place, ", "html": "<span class='verse-line'>{}</span>"}, "dhp323:3": {"pli": "Yathāttanā sudantena, ", "eng": "whereas, with the help of one <j>whose self is well tamed, ", "html": "<span class='verse-line'>{}</span>"}, "dhp323:4": {"pli": "danto dantena gacchati. ", "eng": "you go there, tamed by the tamed. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp324:0": {"pli": "Parijiṇṇabrāhmaṇaputtavatthu ", "html": "<article id='dhp324'><h2 class='sutta-title'>{}</h2>"}, "dhp324:1": {"pli": "Dhanapālo nāma kuñjaro, ", "eng": "The tusker named Dhanapāla ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp324:2": {"pli": "Kaṭukabhedano dunnivārayo; ", "eng": "is musky in rut, hard to control. ", "html": "<span class='verse-line'>{}</span>"}, "dhp324:3": {"pli": "Baddho kabaḷaṁ na bhuñjati, ", "eng": "Bound, he eats not a morsel, ", "html": "<span class='verse-line'>{}</span>"}, "dhp324:4": {"pli": "Sumarati nāgavanassa kuñjaro. ", "eng": "for he misses the elephant forest. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp325:0": {"pli": "Pasenadikosalavatthu ", "html": "<article id='dhp325'><h2 class='sutta-title'>{}</h2>"}, "dhp325:1": {"pli": "Middhī yadā hoti mahagghaso ca, ", "eng": "One who gets drowsy from overeating, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp325:2": {"pli": "Niddāyitā samparivattasāyī; ", "eng": "fond of sleep, rolling round the bed ", "html": "<span class='verse-line'>{}</span>"}, "dhp325:3": {"pli": "Mahāvarāhova nivāpapuṭṭho, ", "eng": "like a great hog stuffed with grain: ", "html": "<span class='verse-line'>{}</span>"}, "dhp325:4": {"pli": "Punappunaṁ gabbhamupeti mando. ", "eng": "that dullard returns to the womb again and again. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp326:0": {"pli": "Sānusāmaṇeravatthu ", "html": "<article id='dhp326'><h2 class='sutta-title'>{}</h2>"}, "dhp326:1": {"pli": "Idaṁ pure cittamacāri cārikaṁ, ", "eng": "In the past my mind wandered ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp326:2": {"pli": "Yenicchakaṁ yatthakāmaṁ yathāsukhaṁ; ", "eng": "how it wished, where it liked, as it pleased. ", "html": "<span class='verse-line'>{}</span>"}, "dhp326:3": {"pli": "Tadajjahaṁ niggahessāmi yoniso, ", "eng": "Now I’ll carefully guide it, ", "html": "<span class='verse-line'>{}</span>"}, "dhp326:4": {"pli": "Hatthippabhinnaṁ viya aṅkusaggaho. ", "eng": "as a trainer with a hook guides a rutting elephant. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp327:0": {"pli": "Pāveyyakahatthivatthu ", "html": "<article id='dhp327'><h2 class='sutta-title'>{}</h2>"}, "dhp327:1": {"pli": "Appamādaratā hotha, ", "eng": "Delight in diligence! ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp327:2": {"pli": "sacittamanurakkhatha; ", "eng": "Take good care of your mind! ", "html": "<span class='verse-line'>{}</span>"}, "dhp327:3": {"pli": "Duggā uddharathattānaṁ, ", "eng": "Pull yourself out of this pit, ", "html": "<span class='verse-line'>{}</span>"}, "dhp327:4": {"pli": "paṅke sannova kuñjaro. ", "eng": "like an elephant sunk in a bog. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp328:0": {"pli": "Sambahulabhikkhuvatthu ", "html": "<article id='dhp328'><h2 class='sutta-title'>{}</h2>"}, "dhp328:1": {"pli": "Sace labhetha nipakaṁ sahāyaṁ, ", "eng": "If you find an alert companion, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp328:2": {"pli": "Saddhiṁ caraṁ sādhuvihāridhīraṁ; ", "eng": "an attentive friend to live happily together, ", "html": "<span class='verse-line'>{}</span>"}, "dhp328:3": {"pli": "Abhibhuyya sabbāni parissayāni, ", "eng": "then, overcoming all adversities, ", "html": "<span class='verse-line'>{}</span>"}, "dhp328:4": {"pli": "Careyya tenattamano satīmā. ", "eng": "wander with them, joyful and mindful. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp329:1": {"pli": "No ce labhetha nipakaṁ sahāyaṁ, ", "eng": "If you find no alert companion, ", "html": "<article id='dhp329'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp329:2": {"pli": "Saddhiṁ caraṁ sādhuvihāridhīraṁ; ", "eng": "no attentive friend to live happily together, ", "html": "<span class='verse-line'>{}</span>"}, "dhp329:3": {"pli": "Rājāva raṭṭhaṁ vijitaṁ pahāya, ", "eng": "then, like a king who flees his conquered realm, ", "html": "<span class='verse-line'>{}</span>"}, "dhp329:4": {"pli": "Eko care mātaṅgaraññeva nāgo. ", "eng": "wander alone like a tusker in the wilds. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp330:1": {"pli": "Ekassa caritaṁ seyyo, ", "eng": "It’s better to wander alone, ", "html": "<article id='dhp330'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp330:2": {"pli": "Natthi bāle sahāyatā; ", "eng": "there’s no fellowship with fools. ", "html": "<span class='verse-line'>{}</span>"}, "dhp330:3": {"pli": "Eko care na ca pāpāni kayirā, ", "eng": "Wander alone and do no wrong, ", "html": "<span class='verse-line'>{}</span>"}, "dhp330:4": {"pli": "Appossukko mātaṅgaraññeva nāgo. ", "eng": "at ease like a tusker in the wilds. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp331:0": {"pli": "Māravatthu ", "html": "<article id='dhp331'><h2 class='sutta-title'>{}</h2>"}, "dhp331:1": {"pli": "Atthamhi jātamhi sukhā sahāyā, ", "eng": "A friend in need is a blessing; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp331:2": {"pli": "Tuṭṭhī sukhā yā itarītarena; ", "eng": "it’s a blessing to be content with whatever; ", "html": "<span class='verse-line'>{}</span>"}, "dhp331:3": {"pli": "Puññaṁ sukhaṁ jīvitasaṅkhayamhi, ", "eng": "good deeds are a blessing at the end of life, ", "html": "<span class='verse-line'>{}</span>"}, "dhp331:4": {"pli": "Sabbassa dukkhassa sukhaṁ pahānaṁ. ", "eng": "and giving up all suffering is a blessing. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp332:1": {"pli": "Sukhā matteyyatā loke, ", "eng": "In this world it’s a blessing to serve ", "html": "<article id='dhp332'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp332:2": {"pli": "atho petteyyatā sukhā; ", "eng": "one’s mother and one’s father. ", "html": "<span class='verse-line'>{}</span>"}, "dhp332:3": {"pli": "Sukhā sāmaññatā loke, ", "eng": "And it’s a blessing also to serve ", "html": "<span class='verse-line'>{}</span>"}, "dhp332:4": {"pli": "atho brahmaññatā sukhā. ", "eng": "ascetics and brahmins. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp333:1": {"pli": "Sukhaṁ yāva jarā sīlaṁ, ", "eng": "It’s a blessing to keep precepts until you grow old; ", "html": "<article id='dhp333'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp333:2": {"pli": "sukhā saddhā patiṭṭhitā; ", "eng": "a blessing to be grounded in faith; ", "html": "<span class='verse-line'>{}</span>"}, "dhp333:3": {"pli": "Sukho paññāya paṭilābho, ", "eng": "the getting of wisdom’s a blessing; ", "html": "<span class='verse-line'>{}</span>"}, "dhp333:4": {"pli": "pāpānaṁ akaraṇaṁ sukhaṁ. ", "eng": "and it’s a blessing to avoid doing wrong. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp333:5": {"pli": "Nāgavaggo tevīsatimo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp334-359": {"dhp334:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp334-359'><header><ul><li class='division'>{}</li>"}, "dhp334:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 334–359 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp334:0.3": {"pli": "Taṇhāvagga ", "eng": "24. Craving ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp334:0.4": {"pli": "Kapilamacchavatthu ", "html": "<article id='dhp334'><h2 class='sutta-title'>{}</h2>"}, "dhp334:1": {"pli": "Manujassa pamattacārino, ", "eng": "When a man lives heedlessly, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp334:2": {"pli": "Taṇhā vaḍḍhati māluvā viya; ", "eng": "craving grows in them like a camel’s foot creeper. ", "html": "<span class='verse-line'>{}</span>"}, "dhp334:3": {"pli": "So plavatī hurā huraṁ, ", "eng": "They jump from one thing to the next, like a langur ", "html": "<span class='verse-line'>{}</span>"}, "dhp334:4": {"pli": "Phalamicchaṁva vanasmi vānaro. ", "eng": "greedy for fruit in a forest grove. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>", "comm": "The <i lang='pi' translate='no'>vānara</i> is a type of human-like monkey often identified with the langur. See <a href='https://suttacentral.net/ja57/en/sujato'>Ja 57</a>. "}, "dhp335:1": {"pli": "Yaṁ esā sahate jammī, ", "eng": "Whoever is beaten by this wretched craving, ", "html": "<article id='dhp335'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp335:2": {"pli": "taṇhā loke visattikā; ", "eng": "this attachment to the world, ", "html": "<span class='verse-line'>{}</span>"}, "dhp335:3": {"pli": "Sokā tassa pavaḍḍhanti, ", "eng": "their sorrow grows, ", "html": "<span class='verse-line'>{}</span>"}, "dhp335:4": {"pli": "abhivaṭṭhaṁva bīraṇaṁ. ", "eng": "like grass in the rain. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp336:1": {"pli": "Yo cetaṁ sahate jammiṁ, ", "eng": "But whoever prevails over this wretched craving, ", "html": "<article id='dhp336'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp336:2": {"pli": "taṇhaṁ loke duraccayaṁ; ", "eng": "so hard to get over in the world, ", "html": "<span class='verse-line'>{}</span>"}, "dhp336:3": {"pli": "Sokā tamhā papatanti, ", "eng": "their sorrows fall from them, ", "html": "<span class='verse-line'>{}</span>"}, "dhp336:4": {"pli": "udabinduva pokkharā. ", "eng": "like a drop from a lotus-leaf. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp337:1": {"pli": "Taṁ vo vadāmi bhaddaṁ vo, ", "eng": "I say this to you, good people, ", "html": "<article id='dhp337'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp337:2": {"pli": "yāvantettha samāgatā; ", "eng": "all those who have gathered here: ", "html": "<span class='verse-line'>{}</span>"}, "dhp337:3": {"pli": "Taṇhāya mūlaṁ khaṇatha, ", "eng": "dig up the root of craving, ", "html": "<span class='verse-line'>{}</span>"}, "dhp337:4": {"pli": "usīratthova bīraṇaṁ; ", "eng": "as you’d dig up grass in search of roots. ", "html": "<span class='verse-line'>{}</span>"}, "dhp337:5": {"pli": "Mā vo naḷaṁva sotova, ", "eng": "Don’t let Māra break you again and again, ", "html": "<span class='verse-line'>{}</span>"}, "dhp337:6": {"pli": "māro bhañji punappunaṁ. ", "eng": "like a stream breaking a reed. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp338:0": {"pli": "Sūkarapotikāvatthu ", "html": "<article id='dhp338'><h2 class='sutta-title'>{}</h2>"}, "dhp338:1": {"pli": "Yathāpi mūle anupaddave daḷhe, ", "eng": "A tree grows back even when cut down, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp338:2": {"pli": "Chinnopi rukkho punareva rūhati; ", "eng": "so long as its roots are strong and undamaged; ", "html": "<span class='verse-line'>{}</span>"}, "dhp338:3": {"pli": "Evampi taṇhānusaye anūhate, ", "eng": "suffering springs up again and again, ", "html": "<span class='verse-line'>{}</span>"}, "dhp338:4": {"pli": "Nibbattatī dukkhamidaṁ punappunaṁ. ", "eng": "so long as the tendency to craving is not pulled out. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp339:1": {"pli": "Yassa chattiṁsati sotā, ", "eng": "A person of low views ", "html": "<article id='dhp339'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp339:2": {"pli": "manāpasavanā bhusā; ", "eng": "in whom the thirty-six streams ", "html": "<span class='verse-line'>{}</span>"}, "dhp339:3": {"pli": "Mahāvahanti duddiṭṭhiṁ, ", "eng": "that flow to pleasure are mighty, ", "html": "<span class='verse-line'>{}</span>"}, "dhp339:4": {"pli": "saṅkappā rāganissitā. ", "eng": "is swept away by lustful thoughts. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp340:1": {"pli": "Savanti sabbadhi sotā, ", "eng": "The streams flow everywhere; ", "html": "<article id='dhp340'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp340:2": {"pli": "latā uppajja tiṭṭhati; ", "eng": "a weed springs up and remains. ", "html": "<span class='verse-line'>{}</span>"}, "dhp340:3": {"pli": "Tañca disvā lataṁ jātaṁ, ", "eng": "Seeing this weed that has been born, ", "html": "<span class='verse-line'>{}</span>"}, "dhp340:4": {"pli": "mūlaṁ paññāya chindatha. ", "eng": "cut the root with wisdom. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp341:1": {"pli": "Saritāni sinehitāni ca, ", "eng": "A personage’s joys ", "html": "<article id='dhp341'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp341:2": {"pli": "Somanassāni bhavanti jantuno; ", "eng": "flow from senses and cravings. ", "html": "<span class='verse-line'>{}</span>"}, "dhp341:3": {"pli": "Te sātasitā sukhesino, ", "eng": "Seekers of happiness, bent on pleasure, ", "html": "<span class='verse-line'>{}</span>"}, "dhp341:4": {"pli": "Te ve jātijarūpagā narā. ", "eng": "continue to be reborn and grow old. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp342:1": {"pli": "Tasiṇāya purakkhatā pajā, ", "eng": "People governed by thirst, ", "html": "<article id='dhp342'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp342:2": {"pli": "Parisappanti sasova bandhito; ", "eng": "crawl about like a trapped rabbit. ", "html": "<span class='verse-line'>{}</span>"}, "dhp342:3": {"pli": "Saṁyojanasaṅgasattakā, ", "eng": "Bound and fettered, for a long time ", "html": "<span class='verse-line'>{}</span>"}, "dhp342:4": {"pli": "Dukkhamupenti punappunaṁ cirāya. ", "eng": "they return to pain time and again. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp343:1": {"pli": "Tasiṇāya purakkhatā pajā, ", "eng": "People governed by thirst, ", "html": "<article id='dhp343'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp343:2": {"pli": "Parisappanti sasova bandhito; ", "eng": "crawl about like a trapped rabbit. ", "html": "<span class='verse-line'>{}</span>"}, "dhp343:3": {"pli": "Tasmā tasiṇaṁ vinodaye, ", "eng": "That’s why one who longs for dispassion ", "html": "<span class='verse-line'>{}</span>"}, "dhp343:4": {"pli": "Ākaṅkhanta virāgamattano. ", "eng": "should dispel thirst. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp344:0": {"pli": "Vibbhantabhikkhuvatthu ", "html": "<article id='dhp344'><h2 class='sutta-title'>{}</h2>"}, "dhp344:1": {"pli": "Yo nibbanatho vanādhimutto, ", "eng": "Unsnarled, they set out for the jungle, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp344:2": {"pli": "Vanamutto vanameva dhāvati; ", "eng": "then they run right back to the jungle they left behind. ", "html": "<span class='verse-line'>{}</span>"}, "dhp344:3": {"pli": "Taṁ puggalametha passatha, ", "eng": "Just look at this individual! ", "html": "<span class='verse-line'>{}</span>"}, "dhp344:4": {"pli": "Mutto bandhanameva dhāvati. ", "eng": "Freed, they run to bondage. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp345:0": {"pli": "Bandhanāgāravatthu ", "html": "<article id='dhp345'><h2 class='sutta-title'>{}</h2>"}, "dhp345:1": {"pli": "Na taṁ daḷhaṁ bandhanamāhu dhīrā, ", "eng": "The attentive say that shackle is not strong ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp345:2": {"pli": "Yadāyasaṁ dārujapabbajañca; ", "eng": "that’s made of iron, wood, or knots. ", "html": "<span class='verse-line'>{}</span>"}, "dhp345:3": {"pli": "Sārattarattā maṇikuṇḍalesu, ", "eng": "But obsession with jeweled earrings, ", "html": "<span class='verse-line'>{}</span>"}, "dhp345:4": {"pli": "Puttesu dāresu ca yā apekkhā. ", "eng": "concern for your partners and children: ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp346:1": {"pli": "Etaṁ daḷhaṁ bandhanamāhu dhīrā, ", "eng": "this, say the attentive, is a strong shackle ", "html": "<article id='dhp346'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp346:2": {"pli": "Ohārinaṁ sithilaṁ duppamuñcaṁ; ", "eng": "dragging the indulgent down, hard to escape. ", "html": "<span class='verse-line'>{}</span>"}, "dhp346:3": {"pli": "Etampi chetvāna paribbajanti, ", "eng": "Having cut this one too they go forth, ", "html": "<span class='verse-line'>{}</span>"}, "dhp346:4": {"pli": "Anapekkhino kāmasukhaṁ pahāya. ", "eng": "unconcerned, having given up sensual pleasures. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp347:0": {"pli": "Khemātherīvatthu ", "html": "<article id='dhp347'><h2 class='sutta-title'>{}</h2>"}, "dhp347:1": {"pli": "Ye rāgarattānupatanti sotaṁ, ", "eng": "Besotted by lust they fall into the stream, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp347:2": {"pli": "Sayaṅkataṁ makkaṭakova jālaṁ; ", "eng": "like a spider caught in the web she wove. ", "html": "<span class='verse-line'>{}</span>"}, "dhp347:3": {"pli": "Etampi chetvāna vajanti dhīrā, ", "eng": "The attentive proceed, having cut this one too, ", "html": "<span class='verse-line'>{}</span>"}, "dhp347:4": {"pli": "Anapekkhino sabbadukkhaṁ pahāya. ", "eng": "unconcerned, having given up all suffering. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp348:0": {"pli": "Uggasenavatthu ", "html": "<article id='dhp348'><h2 class='sutta-title'>{}</h2>"}, "dhp348:1": {"pli": "Muñca pure muñca pacchato, ", "eng": "Let go of the past, let go of the future, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp348:2": {"pli": "Majjhe muñca bhavassa pāragū; ", "eng": "let go of the present, having gone beyond rebirth. ", "html": "<span class='verse-line'>{}</span>"}, "dhp348:3": {"pli": "Sabbattha vimuttamānaso, ", "eng": "With your heart freed in every respect, ", "html": "<span class='verse-line'>{}</span>"}, "dhp348:4": {"pli": "Na punaṁ jātijaraṁ upehisi. ", "eng": "you’ll not come again to rebirth and old age. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp349:0": {"pli": "Cūḷadhanuggahapaṇḍitavatthu ", "html": "<article id='dhp349'><h2 class='sutta-title'>{}</h2>"}, "dhp349:1": {"pli": "Vitakkamathitassa jantuno, ", "eng": "For a personage churned by thoughts, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp349:2": {"pli": "Tibbarāgassa subhānupassino; ", "eng": "very lustful, focusing on beauty, ", "html": "<span class='verse-line'>{}</span>"}, "dhp349:3": {"pli": "Bhiyyo taṇhā pavaḍḍhati, ", "eng": "their craving grows and grows, ", "html": "<span class='verse-line'>{}</span>"}, "dhp349:4": {"pli": "Esa kho daḷhaṁ karoti bandhanaṁ. ", "eng": "tying them with a stout bond. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp350:1": {"pli": "Vitakkūpasame ca yo rato, ", "eng": "But one who loves to calm their thoughts, ", "html": "<article id='dhp350'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp350:2": {"pli": "Asubhaṁ bhāvayate sadā sato; ", "eng": "developing perception of ugliness, ever mindful, ", "html": "<span class='verse-line'>{}</span>"}, "dhp350:3": {"pli": "Esa kho byanti kāhiti, ", "eng": "will surely eliminate that craving, ", "html": "<span class='verse-line'>{}</span>"}, "dhp350:4": {"pli": "Esa checchati mārabandhanaṁ. ", "eng": "cutting off the bonds of Māra. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp351:0": {"pli": "Māravatthu ", "html": "<article id='dhp351'><h2 class='sutta-title'>{}</h2>"}, "dhp351:1": {"pli": "Niṭṭhaṅgato asantāsī, ", "eng": "One who is confident, unafraid, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp351:2": {"pli": "vītataṇho anaṅgaṇo; ", "eng": "rid of craving, free of blemish, ", "html": "<span class='verse-line'>{}</span>"}, "dhp351:3": {"pli": "Acchindi bhavasallāni, ", "eng": "having struck down the arrows flying to future lives, ", "html": "<span class='verse-line'>{}</span>"}, "dhp351:4": {"pli": "antimoyaṁ samussayo. ", "eng": "this bag of bones is their last. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp352:1": {"pli": "Vītataṇho anādāno, ", "eng": "Rid of craving, free of grasping, ", "html": "<article id='dhp352'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp352:2": {"pli": "Niruttipadakovido; ", "eng": "expert in the definition of terms, ", "html": "<span class='verse-line'>{}</span>"}, "dhp352:3": {"pli": "Akkharānaṁ sannipātaṁ, ", "eng": "knowing the correct ", "html": "<span class='verse-line'>{}</span>"}, "dhp352:4": {"pli": "Jaññā pubbāparāni ca; ", "eng": "structure and sequence of syllables, ", "html": "<span class='verse-line'>{}</span>"}, "dhp352:5": {"pli": "Sa ve “antimasārīro, ", "eng": "they are said to be one who bears their final body, ", "html": "<span class='verse-line'>{}</span>"}, "dhp352:6": {"pli": "Mahāpañño mahāpuriso”ti vuccati. ", "eng": "one of great wisdom, a great person. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp353:0": {"pli": "Upakājīvakavatthu ", "html": "<article id='dhp353'><h2 class='sutta-title'>{}</h2>"}, "dhp353:1": {"pli": "Sabbābhibhū sabbavidūhamasmi, ", "eng": "I am the champion, the knower of all, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp353:2": {"pli": "Sabbesu dhammesu anūpalitto; ", "eng": "unsullied in the midst of all things. ", "html": "<span class='verse-line'>{}</span>"}, "dhp353:3": {"pli": "Sabbañjaho taṇhakkhaye vimutto, ", "eng": "I’ve given up all, freed in the ending of craving. ", "html": "<span class='verse-line'>{}</span>"}, "dhp353:4": {"pli": "Sayaṁ abhiññāya kamuddiseyyaṁ. ", "eng": "Since I know for myself, whose follower should I be? ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp354:0": {"pli": "Sakkapañhavatthu ", "html": "<article id='dhp354'><h2 class='sutta-title'>{}</h2>"}, "dhp354:1": {"pli": "Sabbadānaṁ dhammadānaṁ jināti, ", "eng": "The gift of the teaching surmounts all other gifts; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp354:2": {"pli": "Sabbarasaṁ dhammaraso jināti; ", "eng": "the taste of the teaching surmounts all other tastes; ", "html": "<span class='verse-line'>{}</span>"}, "dhp354:3": {"pli": "Sabbaratiṁ dhammarati jināti, ", "eng": "the joy of the teaching surmounts all other joys; ", "html": "<span class='verse-line'>{}</span>"}, "dhp354:4": {"pli": "Taṇhakkhayo sabbadukkhaṁ jināti. ", "eng": "the ending of craving surmounts all suffering. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp355:0": {"pli": "Aputtakaseṭṭhivatthu ", "html": "<article id='dhp355'><h2 class='sutta-title'>{}</h2>"}, "dhp355:1": {"pli": "Hananti bhogā dummedhaṁ, ", "eng": "Riches ruin a simpleton, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp355:2": {"pli": "no ca pāragavesino; ", "eng": "but not a seeker of the far shore. ", "html": "<span class='verse-line'>{}</span>"}, "dhp355:3": {"pli": "Bhogataṇhāya dummedho, ", "eng": "From craving for wealth, a simpleton ", "html": "<span class='verse-line'>{}</span>"}, "dhp355:4": {"pli": "hanti aññeva attanaṁ. ", "eng": "ruins themselves and others. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp356:0": {"pli": "Aṅkuravatthu ", "html": "<article id='dhp356'><h2 class='sutta-title'>{}</h2>"}, "dhp356:1": {"pli": "Tiṇadosāni khettāni, ", "eng": "Weeds are the bane of crops, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp356:2": {"pli": "rāgadosā ayaṁ pajā; ", "eng": "but greed is these folk’s bane. ", "html": "<span class='verse-line'>{}</span>"}, "dhp356:3": {"pli": "Tasmā hi vītarāgesu, ", "eng": "That’s why a gift to one rid of greed ", "html": "<span class='verse-line'>{}</span>"}, "dhp356:4": {"pli": "dinnaṁ hoti mahapphalaṁ. ", "eng": "is so very fruitful. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp357:1": {"pli": "Tiṇadosāni khettāni, ", "eng": "Weeds are the bane of crops, ", "html": "<article id='dhp357'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp357:2": {"pli": "dosadosā ayaṁ pajā; ", "eng": "but hate is these folk’s bane. ", "html": "<span class='verse-line'>{}</span>"}, "dhp357:3": {"pli": "Tasmā hi vītadosesu, ", "eng": "That’s why a gift to one rid of hate ", "html": "<span class='verse-line'>{}</span>"}, "dhp357:4": {"pli": "dinnaṁ hoti mahapphalaṁ. ", "eng": "is so very fruitful. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp358:1": {"pli": "Tiṇadosāni khettāni, ", "eng": "Weeds are the bane of crops, ", "html": "<article id='dhp358'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp358:2": {"pli": "mohadosā ayaṁ pajā; ", "eng": "but delusion is these folk’s bane. ", "html": "<span class='verse-line'>{}</span>"}, "dhp358:3": {"pli": "Tasmā hi vītamohesu, ", "eng": "That’s why a gift to one rid of delusion ", "html": "<span class='verse-line'>{}</span>"}, "dhp358:4": {"pli": "dinnaṁ hoti mahapphalaṁ. ", "eng": "is so very fruitful. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp359:1": {"pli": "Tiṇadosāni khettāni, ", "eng": "Weeds are the bane of crops, ", "html": "<article id='dhp359'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp359:2": {"pli": "icchādosā ayaṁ pajā; ", "eng": "but desire is these folk’s bane. ", "html": "<span class='verse-line'>{}</span>"}, "dhp359:3": {"pli": "Tasmā hi vigaticchesu, ", "eng": "That’s why a gift to one rid of desire ", "html": "<span class='verse-line'>{}</span>"}, "dhp359:4": {"pli": "dinnaṁ hoti mahapphalaṁ. ", "eng": "is so very fruitful. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp359:5": {"pli": "Taṇhāvaggo catuvīsatimo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp360-382": {"dhp360:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp360-382'><header><ul><li class='division'>{}</li>"}, "dhp360:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 360–382 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp360:0.3": {"pli": "Bhikkhuvagga ", "eng": "25. Mendicants ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp360:0.4": {"pli": "Pañcabhikkhuvatthu ", "html": "<article id='dhp360'><h2 class='sutta-title'>{}</h2>"}, "dhp360:1": {"pli": "Cakkhunā saṁvaro sādhu, ", "eng": "Restraint of the eye is good; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp360:2": {"pli": "sādhu sotena saṁvaro; ", "eng": "good is restraint of the ear; ", "html": "<span class='verse-line'>{}</span>"}, "dhp360:3": {"pli": "Ghānena saṁvaro sādhu, ", "eng": "restraint of the nose is good; ", "html": "<span class='verse-line'>{}</span>"}, "dhp360:4": {"pli": "sādhu jivhāya saṁvaro. ", "eng": "good is restraint of the tongue. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp361:1": {"pli": "Kāyena saṁvaro sādhu, ", "eng": "Restraint of the body is good; ", "html": "<article id='dhp361'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp361:2": {"pli": "sādhu vācāya saṁvaro; ", "eng": "good is restraint of speech; ", "html": "<span class='verse-line'>{}</span>"}, "dhp361:3": {"pli": "Manasā saṁvaro sādhu, ", "eng": "restraint of mind is good; ", "html": "<span class='verse-line'>{}</span>"}, "dhp361:4": {"pli": "sādhu sabbattha saṁvaro; ", "eng": "everywhere, restraint is good. ", "html": "<span class='verse-line'>{}</span>"}, "dhp361:5": {"pli": "Sabbattha saṁvuto bhikkhu, ", "eng": "The mendicant restrained everywhere ", "html": "<span class='verse-line'>{}</span>"}, "dhp361:6": {"pli": "sabbadukkhā pamuccati. ", "eng": "is released from all suffering. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp362:0": {"pli": "Haṁsaghātakabhikkhuvatthu ", "html": "<article id='dhp362'><h2 class='sutta-title'>{}</h2>"}, "dhp362:1": {"pli": "Hatthasaṁyato pādasaṁyato, ", "eng": "One restrained in hand and foot, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp362:2": {"pli": "Vācāsaṁyato saṁyatuttamo; ", "eng": "and in speech, the supreme restraint; ", "html": "<span class='verse-line'>{}</span>"}, "dhp362:3": {"pli": "Ajjhattarato samāhito, ", "eng": "happy inside, serene, ", "html": "<span class='verse-line'>{}</span>"}, "dhp362:4": {"pli": "Eko santusito tamāhu bhikkhuṁ. ", "eng": "solitary, content, I call a mendicant. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp363:0": {"pli": "Kokālikavatthu ", "html": "<article id='dhp363'><h2 class='sutta-title'>{}</h2>"}, "dhp363:1": {"pli": "Yo mukhasaṁyato bhikkhu, ", "eng": "When a mendicant of restrained mouth, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp363:2": {"pli": "mantabhāṇī anuddhato; ", "eng": "thoughtful in counsel, not restless, ", "html": "<span class='verse-line'>{}</span>"}, "dhp363:3": {"pli": "Atthaṁ dhammañca dīpeti, ", "eng": "explains the text and its meaning, ", "html": "<span class='verse-line'>{}</span>"}, "dhp363:4": {"pli": "madhuraṁ tassa bhāsitaṁ. ", "eng": "their words are sweet. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp364:0": {"pli": "Dhammārāmattheravatthu ", "html": "<article id='dhp364'><h2 class='sutta-title'>{}</h2>"}, "dhp364:1": {"pli": "Dhammārāmo dhammarato, ", "eng": "Delighting in the teaching, enjoying the teaching, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp364:2": {"pli": "dhammaṁ anuvicintayaṁ; ", "eng": "contemplating the teaching, ", "html": "<span class='verse-line'>{}</span>"}, "dhp364:3": {"pli": "Dhammaṁ anussaraṁ bhikkhu, ", "eng": "a mendicant who recollects the teaching ", "html": "<span class='verse-line'>{}</span>"}, "dhp364:4": {"pli": "saddhammā na parihāyati. ", "eng": "doesn’t fall away from the true teaching. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp365:0": {"pli": "Vipakkhasevakabhikkhuvatthu ", "html": "<article id='dhp365'><h2 class='sutta-title'>{}</h2>"}, "dhp365:1": {"pli": "Salābhaṁ nātimaññeyya, ", "eng": "A well-off mendicant ought not look down ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp365:2": {"pli": "nāññesaṁ pihayaṁ care; ", "eng": "on others, nor should they be envious. ", "html": "<span class='verse-line'>{}</span>"}, "dhp365:3": {"pli": "Aññesaṁ pihayaṁ bhikkhu, ", "eng": "A mendicant who envies others ", "html": "<span class='verse-line'>{}</span>"}, "dhp365:4": {"pli": "samādhiṁ nādhigacchati. ", "eng": "does not achieve immersion. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp366:1": {"pli": "Appalābhopi ce bhikkhu, ", "eng": "If a mendicant is poor in offerings, ", "html": "<article id='dhp366'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp366:2": {"pli": "salābhaṁ nātimaññati; ", "eng": "the well-to-do ought not look down on them. ", "html": "<span class='verse-line'>{}</span>"}, "dhp366:3": {"pli": "Taṁ ve devā pasaṁsanti, ", "eng": "For the gods indeed praise them, ", "html": "<span class='verse-line'>{}</span>"}, "dhp366:4": {"pli": "suddhājīviṁ atanditaṁ. ", "eng": "who are tireless and pure of livelihood. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp367:0": {"pli": "Pañcaggadāyakabrāhmaṇavatthu ", "html": "<article id='dhp367'><h2 class='sutta-title'>{}</h2>"}, "dhp367:1": {"pli": "Sabbaso nāmarūpasmiṁ, ", "eng": "One who has no sense of ownership ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp367:2": {"pli": "yassa natthi mamāyitaṁ; ", "eng": "in the whole realm of name and form, ", "html": "<span class='verse-line'>{}</span>"}, "dhp367:3": {"pli": "Asatā ca na socati, ", "eng": "who does not grieve for that which is not, ", "html": "<span class='verse-line'>{}</span>"}, "dhp367:4": {"pli": "sa ve “bhikkhū”ti vuccati. ", "eng": "is said to be a mendicant. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp368:0": {"pli": "Sambahulabhikkhuvatthu ", "html": "<article id='dhp368'><h2 class='sutta-title'>{}</h2>"}, "dhp368:1": {"pli": "Mettāvihārī yo bhikkhu, ", "eng": "A mendicant who meditates on love, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp368:2": {"pli": "pasanno buddhasāsane; ", "eng": "devoted to the Buddha’s teaching, ", "html": "<span class='verse-line'>{}</span>"}, "dhp368:3": {"pli": "Adhigacche padaṁ santaṁ, ", "eng": "would realize the peaceful state, ", "html": "<span class='verse-line'>{}</span>"}, "dhp368:4": {"pli": "saṅkhārūpasamaṁ sukhaṁ. ", "eng": "the blissful stilling of conditions. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp369:1": {"pli": "Siñca bhikkhu imaṁ nāvaṁ, ", "eng": "Bail out this boat, mendicant! ", "html": "<article id='dhp369'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp369:2": {"pli": "sittā te lahumessati; ", "eng": "When bailed out it will float lightly. ", "html": "<span class='verse-line'>{}</span>"}, "dhp369:3": {"pli": "Chetvā rāgañca dosañca, ", "eng": "Having cut off desire and hate, ", "html": "<span class='verse-line'>{}</span>"}, "dhp369:4": {"pli": "tato nibbānamehisi. ", "eng": "you shall reach extinguishment. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp370:1": {"pli": "Pañca chinde pañca jahe, ", "eng": "Five to cut, five to drop, ", "html": "<article id='dhp370'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp370:2": {"pli": "pañca cuttari bhāvaye; ", "eng": "and five more to develop. ", "html": "<span class='verse-line'>{}</span>"}, "dhp370:3": {"pli": "Pañca saṅgātigo bhikkhu, ", "eng": "When a mendicant slips five chains ", "html": "<span class='verse-line'>{}</span>"}, "dhp370:4": {"pli": "“oghatiṇṇo”ti vuccati. ", "eng": "they’re said to have crossed the flood. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp371:1": {"pli": "Jhāya bhikkhu mā pamādo, ", "eng": "Practice absorption, don’t be negligent! ", "html": "<article id='dhp371'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp371:2": {"pli": "Mā te kāmaguṇe ramessu cittaṁ; ", "eng": "Don’t let the mind delight in the senses! ", "html": "<span class='verse-line'>{}</span>"}, "dhp371:3": {"pli": "Mā lohaguḷaṁ gilī pamatto, ", "eng": "Don’t heedlessly swallow a hot iron ball! ", "html": "<span class='verse-line'>{}</span>"}, "dhp371:4": {"pli": "Mā kandi “dukkhamidan”ti dayhamāno. ", "eng": "And when it burns, don’t cry, “Oh, the pain!” ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp372:1": {"pli": "Natthi jhānaṁ apaññassa, ", "eng": "No absorption for one without wisdom, ", "html": "<article id='dhp372'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp372:2": {"pli": "paññā natthi ajhāyato; ", "eng": "no wisdom for one without absorption. ", "html": "<span class='verse-line'>{}</span>"}, "dhp372:3": {"pli": "Yamhi jhānañca paññā ca, ", "eng": "But one with absorption and wisdom—", "html": "<span class='verse-line'>{}</span>"}, "dhp372:4": {"pli": "sa ve nibbānasantike. ", "eng": "they have truly drawn near to extinguishment. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp373:1": {"pli": "Suññāgāraṁ paviṭṭhassa, ", "eng": "A mendicant who enters an empty hut ", "html": "<article id='dhp373'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp373:2": {"pli": "santacittassa bhikkhuno; ", "eng": "with mind at peace ", "html": "<span class='verse-line'>{}</span>"}, "dhp373:3": {"pli": "Amānusī rati hoti, ", "eng": "finds a superhuman delight ", "html": "<span class='verse-line'>{}</span>"}, "dhp373:4": {"pli": "sammā dhammaṁ vipassato. ", "eng": "as they rightly discern the Dhamma. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp374:1": {"pli": "Yato yato sammasati, ", "eng": "Whenever they are mindful ", "html": "<article id='dhp374'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp374:2": {"pli": "khandhānaṁ udayabbayaṁ; ", "eng": "of the rise and fall of the aggregates, ", "html": "<span class='verse-line'>{}</span>"}, "dhp374:3": {"pli": "Labhatī pītipāmojjaṁ, ", "eng": "they feel rapture and joy: ", "html": "<span class='verse-line'>{}</span>"}, "dhp374:4": {"pli": "amataṁ taṁ vijānataṁ. ", "eng": "that is freedom from death for one who knows. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp375:1": {"pli": "Tatrāyamādi bhavati, ", "eng": "This is the very start of the path ", "html": "<article id='dhp375'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp375:2": {"pli": "idha paññassa bhikkhuno; ", "eng": "for a wise mendicant: ", "html": "<span class='verse-line'>{}</span>"}, "dhp375:3": {"pli": "Indriyagutti santuṭṭhi, ", "eng": "guarding the senses, contentment, ", "html": "<span class='verse-line'>{}</span>"}, "dhp375:4": {"pli": "pātimokkhe ca saṁvaro. ", "eng": "and restraint in the monastic code. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp376:1": {"pli": "Mitte bhajassu kalyāṇe, ", "eng": "Mix with spiritual friends, ", "html": "<article id='dhp376'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp376:2": {"pli": "suddhājīve atandite; ", "eng": "who are tireless and pure of livelihood. ", "html": "<span class='verse-line'>{}</span>"}, "dhp376:3": {"pli": "Paṭisanthāravutyassa, ", "eng": "Share what you have with others, ", "html": "<span class='verse-line'>{}</span>"}, "dhp376:4": {"pli": "ācārakusalo siyā; ", "eng": "being skillful in your conduct. ", "html": "<span class='verse-line'>{}</span>"}, "dhp376:5": {"pli": "Tato pāmojjabahulo, ", "eng": "And when you’re full of joy, ", "html": "<span class='verse-line'>{}</span>"}, "dhp376:6": {"pli": "dukkhassantaṁ karissati. ", "eng": "you’ll make an end to suffering. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp377:0": {"pli": "Pañcasatabhikkhuvatthu ", "html": "<article id='dhp377'><h2 class='sutta-title'>{}</h2>"}, "dhp377:1": {"pli": "Vassikā viya pupphāni, ", "eng": "As a jasmine sheds ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp377:2": {"pli": "maddavāni pamuñcati; ", "eng": "its withered flowers, ", "html": "<span class='verse-line'>{}</span>"}, "dhp377:3": {"pli": "Evaṁ rāgañca dosañca, ", "eng": "O mendicants, ", "html": "<span class='verse-line'>{}</span>"}, "dhp377:4": {"pli": "vippamuñcetha bhikkhavo. ", "eng": "shed greed and hate. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp378:0": {"pli": "Santakāyattheravatthu ", "html": "<article id='dhp378'><h2 class='sutta-title'>{}</h2>"}, "dhp378:1": {"pli": "Santakāyo santavāco, ", "eng": "Calm in body, calm in speech, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp378:2": {"pli": "santavā susamāhito; ", "eng": "peaceful and serene; ", "html": "<span class='verse-line'>{}</span>"}, "dhp378:3": {"pli": "Vantalokāmiso bhikkhu, ", "eng": "a mendicant who’s spat out the world’s bait ", "html": "<span class='verse-line'>{}</span>"}, "dhp378:4": {"pli": "“upasanto”ti vuccati. ", "eng": "is said to be one at peace. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp379:0": {"pli": "Naṅgalakulattheravatthu ", "html": "<article id='dhp379'><h2 class='sutta-title'>{}</h2>"}, "dhp379:1": {"pli": "Attanā codayattānaṁ, ", "eng": "Urge yourself on, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp379:2": {"pli": "paṭimaṁsetha attanā; ", "eng": "reflect on yourself. ", "html": "<span class='verse-line'>{}</span>"}, "dhp379:3": {"pli": "So attagutto satimā, ", "eng": "A mendicant self-controlled and mindful ", "html": "<span class='verse-line'>{}</span>"}, "dhp379:4": {"pli": "sukhaṁ bhikkhu vihāhisi. ", "eng": "will always dwell in happiness. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp380:1": {"pli": "Attā hi attano nātho, ", "eng": "Self is indeed the lord of self, ", "html": "<article id='dhp380'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp380:2": {"pli": "ko hi nātho paro siyā; ", "eng": "for who else would be one’s lord? ", "html": "<span class='verse-line'>{}</span>"}, "dhp380:3": {"pli": "Attā hi attano gati, ", "eng": "Self is indeed the home of self, ", "html": "<span class='verse-line'>{}</span>", "comm": "<i lang='pi' translate='no'>Gati</i> is glossed by the commentary as <i lang='pi' translate='no'>saraṇa</i> (“refuge”), which agrees with the Sanskrit Udānavarga’s <i lang='pi' translate='no'>śaraṇa</i>. "}, "dhp380:4": {"pli": "tasmā saṁyamamattānaṁ; ", "eng": "so restrain yourself, ", "html": "<span class='verse-line'>{}</span>"}, "dhp380:5": {"pli": "Assaṁ bhadraṁva vāṇijo. ", "eng": "as a merchant his thoroughbred steed. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp381:0": {"pli": "Vakkalittheravatthu ", "html": "<article id='dhp381'><h2 class='sutta-title'>{}</h2>"}, "dhp381:1": {"pli": "Pāmojjabahulo bhikkhu, ", "eng": "A monk full of joy ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp381:2": {"pli": "pasanno buddhasāsane; ", "eng": "trusting in the Buddha’s teaching, ", "html": "<span class='verse-line'>{}</span>"}, "dhp381:3": {"pli": "Adhigacche padaṁ santaṁ, ", "eng": "would realize the peaceful state, ", "html": "<span class='verse-line'>{}</span>"}, "dhp381:4": {"pli": "saṅkhārūpasamaṁ sukhaṁ. ", "eng": "the blissful stilling of conditions. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp382:0": {"pli": "Sumanasāmaṇeravatthu ", "html": "<article id='dhp382'><h2 class='sutta-title'>{}</h2>"}, "dhp382:1": {"pli": "Yo have daharo bhikkhu, ", "eng": "A young mendicant ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp382:2": {"pli": "yuñjati buddhasāsane; ", "eng": "devoted to the Buddha’s teaching, ", "html": "<span class='verse-line'>{}</span>"}, "dhp382:3": {"pli": "Somaṁ lokaṁ pabhāseti, ", "eng": "lights up the world, ", "html": "<span class='verse-line'>{}</span>"}, "dhp382:4": {"pli": "abbhā muttova candimā. ", "eng": "like the moon freed from clouds. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp382:5": {"pli": "Bhikkhuvaggo pañcavīsatimo. ", "html": "<p class='endvagga'>{}</p></article></section>"}}, "dhp383-423": {"dhp383:0.1": {"pli": "Khuddakanikāya ", "eng": "Minor Collection ", "html": "<section class='range'  id='dhp383-423'><header><ul><li class='division'>{}</li>"}, "dhp383:0.2": {"pli": "Dhammapada ", "eng": "Sayings of the Dhamma 383–423 ", "html": "<li class='subdivision'>{}</li></ul>"}, "dhp383:0.3": {"pli": "Brāhmaṇavagga ", "eng": "26. Brahmins ", "html": "<h1 class='range-title'>{}</h1></header>"}, "dhp383:0.4": {"pli": "Pasādabahulabrāhmaṇavatthu ", "html": "<article id='dhp383'><h2 class='sutta-title'>{}</h2>"}, "dhp383:1": {"pli": "Chinda sotaṁ parakkamma, ", "eng": "Cut the stream, striving! ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp383:2": {"pli": "kāme panuda brāhmaṇa; ", "eng": "Cast aside sensual pleasures, brahmin. ", "html": "<span class='verse-line'>{}</span>"}, "dhp383:3": {"pli": "Saṅkhārānaṁ khayaṁ ñatvā, ", "eng": "Knowing the ending of conditions, ", "html": "<span class='verse-line'>{}</span>"}, "dhp383:4": {"pli": "akataññūsi brāhmaṇa. ", "eng": "know the uncreated, brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp384:0": {"pli": "Sambahulabhikkhuvatthu ", "html": "<article id='dhp384'><h2 class='sutta-title'>{}</h2>"}, "dhp384:1": {"pli": "Yadā dvayesu dhammesu, ", "eng": "When a brahmin has gone beyond ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp384:2": {"pli": "pāragū hoti brāhmaṇo; ", "eng": "dualistic phenomena, ", "html": "<span class='verse-line'>{}</span>", "comm": "The commentary says this refers to the mastery of two things, serenity and discernment (cf. <a href='https://suttacentral.net/dn34/en/sujato#1.3.5'>DN 34:1.3.5</a>). However, the term is not “two” but “duality” (<i lang='pi' translate='no'>dvaya</i>), suggesting a link to <a href='https://suttacentral.net/sn35.92/en/sujato#2.2'>SN 35.92:2.2</a>, the duality of sense experience. "}, "dhp384:3": {"pli": "Athassa sabbe saṁyogā, ", "eng": "then they consciously ", "html": "<span class='verse-line'>{}</span>"}, "dhp384:4": {"pli": "atthaṁ gacchanti jānato. ", "eng": "make an end of all fetters. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp385:0": {"pli": "Māravatthu ", "html": "<article id='dhp385'><h2 class='sutta-title'>{}</h2>"}, "dhp385:1": {"pli": "Yassa pāraṁ apāraṁ vā, ", "eng": "One for whom there is no crossing over ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp385:2": {"pli": "pārāpāraṁ na vijjati; ", "eng": "or crossing back, or crossing over and back; ", "html": "<span class='verse-line'>{}</span>"}, "dhp385:3": {"pli": "Vītaddaraṁ visaṁyuttaṁ, ", "eng": "stress-free, detached, ", "html": "<span class='verse-line'>{}</span>"}, "dhp385:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp386:0": {"pli": "Aññatarabrāhmaṇavatthu ", "html": "<article id='dhp386'><h2 class='sutta-title'>{}</h2>"}, "dhp386:1": {"pli": "Jhāyiṁ virajamāsīnaṁ, ", "eng": "Absorbed, rid of hopes, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp386:2": {"pli": "katakiccamanāsavaṁ; ", "eng": "their task completed, without defilements, ", "html": "<span class='verse-line'>{}</span>"}, "dhp386:3": {"pli": "Uttamatthamanuppattaṁ, ", "eng": "arrived at the highest goal: ", "html": "<span class='verse-line'>{}</span>"}, "dhp386:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp387:0": {"pli": "Ānandattheravatthu ", "html": "<article id='dhp387'><h2 class='sutta-title'>{}</h2>"}, "dhp387:1": {"pli": "Divā tapati ādicco, ", "eng": "The sun blazes by day, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp387:2": {"pli": "rattimābhāti candimā; ", "eng": "the moon radiates at night, ", "html": "<span class='verse-line'>{}</span>"}, "dhp387:3": {"pli": "Sannaddho khattiyo tapati, ", "eng": "the aristocrat shines in armor, ", "html": "<span class='verse-line'>{}</span>"}, "dhp387:4": {"pli": "jhāyī tapati brāhmaṇo; ", "eng": "and the brahmin shines in absorption. ", "html": "<span class='verse-line'>{}</span>"}, "dhp387:5": {"pli": "Atha sabbamahorattiṁ, ", "eng": "But all day and all night, ", "html": "<span class='verse-line'>{}</span>"}, "dhp387:6": {"pli": "buddho tapati tejasā. ", "eng": "the Buddha blazes with glory. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp388:0": {"pli": "Aññatarabrāhmaṇapabbajitavatthu ", "html": "<article id='dhp388'><h2 class='sutta-title'>{}</h2>"}, "dhp388:1": {"pli": "Bāhitapāpoti brāhmaṇo, ", "eng": "A brahmin’s so-called <j>since they’ve banished evil, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp388:2": {"pli": "Samacariyā samaṇoti vuccati; ", "eng": "an ascetic’s so-called <j>since they live a serene life. ", "html": "<span class='verse-line'>{}</span>"}, "dhp388:3": {"pli": "Pabbājayamattano malaṁ, ", "eng": "One who has renounced all stains ", "html": "<span class='verse-line'>{}</span>"}, "dhp388:4": {"pli": "Tasmā “pabbajito”ti vuccati. ", "eng": "is said to be a “renunciant”. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp389:0": {"pli": "Sāriputtattheravatthu ", "html": "<article id='dhp389'><h2 class='sutta-title'>{}</h2>"}, "dhp389:1": {"pli": "Na brāhmaṇassa pahareyya, ", "eng": "One should never strike a brahmin, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp389:2": {"pli": "nāssa muñcetha brāhmaṇo; ", "eng": "nor should a brahmin retaliate. ", "html": "<span class='verse-line'>{}</span>"}, "dhp389:3": {"pli": "Dhī brāhmaṇassa hantāraṁ, ", "eng": "Woe to the one who hurts a brahmin, ", "html": "<span class='verse-line'>{}</span>"}, "dhp389:4": {"pli": "tato dhī yassa muñcati. ", "eng": "and woe for the one who retaliates. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp390:1": {"pli": "Na brāhmaṇassetadakiñci seyyo, ", "eng": "Nothing is better for a brahmin ", "html": "<article id='dhp390'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp390:2": {"pli": "Yadā nisedho manaso piyehi; ", "eng": "than to hold their mind back from attachment. ", "html": "<span class='verse-line'>{}</span>"}, "dhp390:3": {"pli": "Yato yato hiṁsamano nivattati, ", "eng": "From wherever a cruel wish recoils, ", "html": "<span class='verse-line'>{}</span>"}, "dhp390:4": {"pli": "Tato tato sammatimeva dukkhaṁ. ", "eng": "right there suffering subsides. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp391:0": {"pli": "Mahāpajāpatigotamīvatthu ", "html": "<article id='dhp391'><h2 class='sutta-title'>{}</h2>"}, "dhp391:1": {"pli": "Yassa kāyena vācāya, ", "eng": "Who does nothing wrong ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp391:2": {"pli": "manasā natthi dukkaṭaṁ; ", "eng": "by body, speech or mind, ", "html": "<span class='verse-line'>{}</span>"}, "dhp391:3": {"pli": "Saṁvutaṁ tīhi ṭhānehi, ", "eng": "restrained in these three respects, ", "html": "<span class='verse-line'>{}</span>"}, "dhp391:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp392:0": {"pli": "Sāriputtattheravatthu ", "html": "<article id='dhp392'><h2 class='sutta-title'>{}</h2>"}, "dhp392:1": {"pli": "Yamhā dhammaṁ vijāneyya, ", "eng": "You should graciously honor ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp392:2": {"pli": "sammāsambuddhadesitaṁ; ", "eng": "the one from whom you learn the Dhamma ", "html": "<span class='verse-line'>{}</span>"}, "dhp392:3": {"pli": "Sakkaccaṁ taṁ namasseyya, ", "eng": "taught by the awakened Buddha, ", "html": "<span class='verse-line'>{}</span>"}, "dhp392:4": {"pli": "aggihuttaṁva brāhmaṇo. ", "eng": "as a brahmin honors the sacred flame. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp393:0": {"pli": "Jaṭilabrāhmaṇavatthu ", "html": "<article id='dhp393'><h2 class='sutta-title'>{}</h2>"}, "dhp393:1": {"pli": "Na jaṭāhi na gottena, ", "eng": "Not by matted hair or family, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp393:2": {"pli": "na jaccā hoti brāhmaṇo; ", "eng": "or birth is one a brahmin. ", "html": "<span class='verse-line'>{}</span>"}, "dhp393:3": {"pli": "Yamhi saccañca dhammo ca, ", "eng": "Those who are  truthful and principled: ", "html": "<span class='verse-line'>{}</span>"}, "dhp393:4": {"pli": "so sucī so ca brāhmaṇo. ", "eng": "they are pure, they are brahmins. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp394:0": {"pli": "Kuhakabrāhmaṇavatthu ", "html": "<article id='dhp394'><h2 class='sutta-title'>{}</h2>"}, "dhp394:1": {"pli": "Kiṁ te jaṭāhi dummedha, ", "eng": "Why the matted hair, you simpleton, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp394:2": {"pli": "kiṁ te ajinasāṭiyā; ", "eng": "and why the skin of deer? ", "html": "<span class='verse-line'>{}</span>"}, "dhp394:3": {"pli": "Abbhantaraṁ te gahanaṁ, ", "eng": "The tangle is inside you, ", "html": "<span class='verse-line'>{}</span>"}, "dhp394:4": {"pli": "bāhiraṁ parimajjasi. ", "eng": "yet you polish up your outsides. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp395:0": {"pli": "Kisāgotamīvatthu ", "html": "<article id='dhp395'><h2 class='sutta-title'>{}</h2>"}, "dhp395:1": {"pli": "Paṁsukūladharaṁ jantuṁ, ", "eng": "A personage who wears robes of rags, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp395:2": {"pli": "kisaṁ dhamanisanthataṁ; ", "eng": "lean, their limbs showing veins, ", "html": "<span class='verse-line'>{}</span>"}, "dhp395:3": {"pli": "Ekaṁ vanasmiṁ jhāyantaṁ, ", "eng": "meditating alone in the forest, ", "html": "<span class='verse-line'>{}</span>"}, "dhp395:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp396:0": {"pli": "Ekabrāhmaṇavatthu ", "html": "<article id='dhp396'><h2 class='sutta-title'>{}</h2>"}, "dhp396:1": {"pli": "Na cāhaṁ brāhmaṇaṁ brūmi, ", "eng": "I don’t call someone a brahmin ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp396:2": {"pli": "yonijaṁ mattisambhavaṁ; ", "eng": "after the mother’s womb they’re born from. ", "html": "<span class='verse-line'>{}</span>"}, "dhp396:3": {"pli": "Bhovādi nāma so hoti, ", "eng": "If they still have attachments, ", "html": "<span class='verse-line'>{}</span>"}, "dhp396:4": {"pli": "sace hoti sakiñcano; ", "eng": "they’re just someone who says “worthy”. ", "html": "<span class='verse-line'>{}</span>"}, "dhp396:5": {"pli": "Akiñcanaṁ anādānaṁ, ", "eng": "Having nothing, taking nothing: ", "html": "<span class='verse-line'>{}</span>"}, "dhp396:6": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp397:0": {"pli": "Uggasenaseṭṭhiputtavatthu ", "html": "<article id='dhp397'><h2 class='sutta-title'>{}</h2>"}, "dhp397:1": {"pli": "Sabbasaṁyojanaṁ chetvā, ", "eng": "Having cut off all fetters ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp397:2": {"pli": "yo ve na paritassati; ", "eng": "they have no anxiety. ", "html": "<span class='verse-line'>{}</span>"}, "dhp397:3": {"pli": "Saṅgātigaṁ visaṁyuttaṁ, ", "eng": "They’ve slipped their chains and are detached: ", "html": "<span class='verse-line'>{}</span>"}, "dhp397:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp398:0": {"pli": "Dvebrāhmaṇavatthu ", "html": "<article id='dhp398'><h2 class='sutta-title'>{}</h2>"}, "dhp398:1": {"pli": "Chetvā naddhiṁ varattañca, ", "eng": "They’ve cut the strap and harness, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp398:2": {"pli": "sandānaṁ sahanukkamaṁ; ", "eng": "the halter and bridle too, ", "html": "<span class='verse-line'>{}</span>"}, "dhp398:3": {"pli": "Ukkhittapalighaṁ buddhaṁ, ", "eng": "with cross-bar lifted, they’re awakened: ", "html": "<span class='verse-line'>{}</span>"}, "dhp398:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp399:0": {"pli": "Akkosakabhāradvājavatthu ", "html": "<article id='dhp399'><h2 class='sutta-title'>{}</h2>"}, "dhp399:1": {"pli": "Akkosaṁ vadhabandhañca, ", "eng": "Abuse, killing, caging: ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp399:2": {"pli": "aduṭṭho yo titikkhati; ", "eng": "they withstand these without anger. ", "html": "<span class='verse-line'>{}</span>"}, "dhp399:3": {"pli": "Khantībalaṁ balānīkaṁ, ", "eng": "Patience is their powerful army: ", "html": "<span class='verse-line'>{}</span>"}, "dhp399:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp400:0": {"pli": "Sāriputtattheravatthu ", "html": "<article id='dhp400'><h2 class='sutta-title'>{}</h2>"}, "dhp400:1": {"pli": "Akkodhanaṁ vatavantaṁ, ", "eng": "Not irritable or pretentious, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp400:2": {"pli": "sīlavantaṁ anussadaṁ; ", "eng": "dutiful in precepts and observances, ", "html": "<span class='verse-line'>{}</span>"}, "dhp400:3": {"pli": "Dantaṁ antimasārīraṁ, ", "eng": "tamed, bearing their final body: ", "html": "<span class='verse-line'>{}</span>"}, "dhp400:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp401:0": {"pli": "Uppalavaṇṇāttherīvatthu ", "html": "<article id='dhp401'><h2 class='sutta-title'>{}</h2>"}, "dhp401:1": {"pli": "Vāri pokkharapatteva, ", "eng": "Like water from a lotus leaf, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp401:2": {"pli": "āraggeriva sāsapo; ", "eng": "like a mustard seed off a pin-point, ", "html": "<span class='verse-line'>{}</span>"}, "dhp401:3": {"pli": "Yo na limpati kāmesu, ", "eng": "sensual pleasures slip off them: ", "html": "<span class='verse-line'>{}</span>"}, "dhp401:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp402:0": {"pli": "Aññatarabrāhmaṇavatthu ", "html": "<article id='dhp402'><h2 class='sutta-title'>{}</h2>"}, "dhp402:1": {"pli": "Yo dukkhassa pajānāti, ", "eng": "They understand for themselves ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp402:2": {"pli": "idheva khayamattano; ", "eng": "the end of suffering in this life; ", "html": "<span class='verse-line'>{}</span>"}, "dhp402:3": {"pli": "Pannabhāraṁ visaṁyuttaṁ, ", "eng": "with burden put down, detached: ", "html": "<span class='verse-line'>{}</span>"}, "dhp402:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp403:0": {"pli": "Khemābhikkhunīvatthu ", "html": "<article id='dhp403'><h2 class='sutta-title'>{}</h2>"}, "dhp403:1": {"pli": "Gambhīrapaññaṁ medhāviṁ, ", "eng": "Deep in wisdom, intelligent, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp403:2": {"pli": "maggāmaggassa kovidaṁ; ", "eng": "expert in the path and what is not the path; ", "html": "<span class='verse-line'>{}</span>"}, "dhp403:3": {"pli": "Uttamatthamanuppattaṁ, ", "eng": "arrived at the highest goal: ", "html": "<span class='verse-line'>{}</span>"}, "dhp403:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp404:0": {"pli": "Pabbhāravāsitissattheravatthu ", "html": "<article id='dhp404'><h2 class='sutta-title'>{}</h2>"}, "dhp404:1": {"pli": "Asaṁsaṭṭhaṁ gahaṭṭhehi, ", "eng": "Mixing with neither ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp404:2": {"pli": "anāgārehi cūbhayaṁ; ", "eng": "householders nor the homeless; ", "html": "<span class='verse-line'>{}</span>"}, "dhp404:3": {"pli": "Anokasārimappicchaṁ, ", "eng": "a migrant with no bastion, few in wishes: ", "html": "<span class='verse-line'>{}</span>"}, "dhp404:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp405:0": {"pli": "Aññatarabhikkhuvatthu ", "html": "<article id='dhp405'><h2 class='sutta-title'>{}</h2>"}, "dhp405:1": {"pli": "Nidhāya daṇḍaṁ bhūtesu, ", "eng": "They’ve laid aside violence ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp405:2": {"pli": "tasesu thāvaresu ca; ", "eng": "against creatures firm and frail; ", "html": "<span class='verse-line'>{}</span>"}, "dhp405:3": {"pli": "Yo na hanti na ghāteti, ", "eng": "not killing or making others kill: ", "html": "<span class='verse-line'>{}</span>"}, "dhp405:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp406:0": {"pli": "Sāmaṇerānaṁvatthu ", "html": "<article id='dhp406'><h2 class='sutta-title'>{}</h2>"}, "dhp406:1": {"pli": "Aviruddhaṁ viruddhesu, ", "eng": "Not fighting among those who fight, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp406:2": {"pli": "attadaṇḍesu nibbutaṁ; ", "eng": "quenched among those who have taken up arms, ", "html": "<span class='verse-line'>{}</span>"}, "dhp406:3": {"pli": "Sādānesu anādānaṁ, ", "eng": "not grasping among those who grasp: ", "html": "<span class='verse-line'>{}</span>"}, "dhp406:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp407:0": {"pli": "Mahāpanthakattheravatthu ", "html": "<article id='dhp407'><h2 class='sutta-title'>{}</h2>"}, "dhp407:1": {"pli": "Yassa rāgo ca doso ca, ", "eng": "They’ve discarded greed and hate, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp407:2": {"pli": "māno makkho ca pātito; ", "eng": "along with conceit and contempt, ", "html": "<span class='verse-line'>{}</span>"}, "dhp407:3": {"pli": "Sāsaporiva āraggā, ", "eng": "like a mustard seed off the point of a pin: ", "html": "<span class='verse-line'>{}</span>"}, "dhp407:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp408:0": {"pli": "Pilindavacchattheravatthu ", "html": "<article id='dhp408'><h2 class='sutta-title'>{}</h2>"}, "dhp408:1": {"pli": "Akakkasaṁ viññāpaniṁ, ", "eng": "The words they utter ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp408:2": {"pli": "giraṁ saccamudīraye; ", "eng": "are polished, informative, and true, ", "html": "<span class='verse-line'>{}</span>"}, "dhp408:3": {"pli": "Yāya nābhisaje kañci, ", "eng": "and don’t offend anyone: ", "html": "<span class='verse-line'>{}</span>"}, "dhp408:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp409:0": {"pli": "Aññatarattheravatthu ", "html": "<article id='dhp409'><h2 class='sutta-title'>{}</h2>"}, "dhp409:1": {"pli": "Yodha dīghaṁ va rassaṁ vā, ", "eng": "They don’t steal anything in the world, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp409:2": {"pli": "aṇuṁ thūlaṁ subhāsubhaṁ; ", "eng": "long or short, ", "html": "<span class='verse-line'>{}</span>"}, "dhp409:3": {"pli": "Loke adinnaṁ nādiyati, ", "eng": "fine or coarse, beautiful or ugly: ", "html": "<span class='verse-line'>{}</span>"}, "dhp409:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp410:0": {"pli": "Sāriputtattheravatthu ", "html": "<article id='dhp410'><h2 class='sutta-title'>{}</h2>"}, "dhp410:1": {"pli": "Āsā yassa na vijjanti, ", "eng": "They have no hope ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp410:2": {"pli": "asmiṁ loke paramhi ca; ", "eng": "in this world or the next; ", "html": "<span class='verse-line'>{}</span>"}, "dhp410:3": {"pli": "Nirāsāsaṁ visaṁyuttaṁ, ", "eng": "with no need for hope, detached: ", "html": "<span class='verse-line'>{}</span>"}, "dhp410:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp411:0": {"pli": "Mahāmoggallānattheravatthu ", "html": "<article id='dhp411'><h2 class='sutta-title'>{}</h2>"}, "dhp411:1": {"pli": "Yassālayā na vijjanti, ", "eng": "They have no clinging, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp411:2": {"pli": "aññāya akathaṅkathī; ", "eng": "knowledge has freed them of indecision, ", "html": "<span class='verse-line'>{}</span>"}, "dhp411:3": {"pli": "Amatogadhamanuppattaṁ, ", "eng": "they’ve arrived at the objective, freedom from death: ", "html": "<span class='verse-line'>{}</span>"}, "dhp411:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp412:0": {"pli": "Revatattheravatthu ", "html": "<article id='dhp412'><h2 class='sutta-title'>{}</h2>"}, "dhp412:1": {"pli": "Yodha puññañca pāpañca, ", "eng": "They’ve escaped clinging ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp412:2": {"pli": "ubho saṅgamupaccagā; ", "eng": "to both good and bad deeds; ", "html": "<span class='verse-line'>{}</span>"}, "dhp412:3": {"pli": "Asokaṁ virajaṁ suddhaṁ, ", "eng": "sorrowless, stainless, pure: ", "html": "<span class='verse-line'>{}</span>"}, "dhp412:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp413:0": {"pli": "Candābhattheravatthu ", "html": "<article id='dhp413'><h2 class='sutta-title'>{}</h2>"}, "dhp413:1": {"pli": "Candaṁva vimalaṁ suddhaṁ, ", "eng": "Pure as the spotless moon, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp413:2": {"pli": "vippasannamanāvilaṁ; ", "eng": "clear and undisturbed, ", "html": "<span class='verse-line'>{}</span>"}, "dhp413:3": {"pli": "Nandībhavaparikkhīṇaṁ, ", "eng": "they’ve ended relish for rebirth: ", "html": "<span class='verse-line'>{}</span>"}, "dhp413:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp414:0": {"pli": "Sīvalittheravatthu ", "html": "<article id='dhp414'><h2 class='sutta-title'>{}</h2>"}, "dhp414:1": {"pli": "Yomaṁ palipathaṁ duggaṁ, ", "eng": "They’ve got past this grueling swamp ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp414:2": {"pli": "saṁsāraṁ mohamaccagā; ", "eng": "of delusion, transmigration. ", "html": "<span class='verse-line'>{}</span>"}, "dhp414:3": {"pli": "Tiṇṇo pāraṅgato jhāyī, ", "eng": "Meditating in stillness, free of indecision, ", "html": "<span class='verse-line'>{}</span>"}, "dhp414:4": {"pli": "anejo akathaṅkathī; ", "eng": "they have crossed over to the far shore. ", "html": "<span class='verse-line'>{}</span>"}, "dhp414:5": {"pli": "Anupādāya nibbuto, ", "eng": "They’re quenched by not grasping: ", "html": "<span class='verse-line'>{}</span>"}, "dhp414:6": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp415:0": {"pli": "Sundarasamuddattheravatthu ", "html": "<article id='dhp415'><h2 class='sutta-title'>{}</h2>"}, "dhp415:1": {"pli": "Yodha kāme pahantvāna, ", "eng": "They’ve given up sensual stimulations, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp415:2": {"pli": "anāgāro paribbaje; ", "eng": "and have gone forth from lay life; ", "html": "<span class='verse-line'>{}</span>"}, "dhp415:3": {"pli": "Kāmabhavaparikkhīṇaṁ, ", "eng": "they’ve ended rebirth in the sensual realm: ", "html": "<span class='verse-line'>{}</span>"}, "dhp415:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp416:0": {"pli": "Jaṭilattheravatthu ", "html": "<article id='dhp416'><h2 class='sutta-title'>{}</h2>"}, "dhp416:1": {"pli": "Yodha taṇhaṁ pahantvāna, ", "eng": "They’ve given up craving, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>", "comm": "This verse and the next are identical. The commentary says they were spoken on two separate occasions. "}, "dhp416:2": {"pli": "anāgāro paribbaje; ", "eng": "and have gone forth from lay life; ", "html": "<span class='verse-line'>{}</span>"}, "dhp416:3": {"pli": "Taṇhābhavaparikkhīṇaṁ, ", "eng": "they’ve ended craving to be reborn: ", "html": "<span class='verse-line'>{}</span>"}, "dhp416:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp416:5.0": {"pli": "Jotikattheravatthu ", "html": "<h2 class='sutta-title'>{}</h2>"}, "dhp416:5.1": {"pli": "Yodha taṇhaṁ pahantvāna, ", "eng": "They’ve given up craving, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp416:6": {"pli": "anāgāro paribbaje; ", "eng": "and have gone forth from lay life; ", "html": "<span class='verse-line'>{}</span>"}, "dhp416:7": {"pli": "Taṇhābhavaparikkhīṇaṁ, ", "eng": "they’ve ended craving to be reborn: ", "html": "<span class='verse-line'>{}</span>"}, "dhp416:8": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp417:0": {"pli": "Naṭaputtakattheravatthu ", "html": "<article id='dhp417'><h2 class='sutta-title'>{}</h2>"}, "dhp417:1": {"pli": "Hitvā mānusakaṁ yogaṁ, ", "eng": "They’ve thrown off the human yoke, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp417:2": {"pli": "dibbaṁ yogaṁ upaccagā; ", "eng": "and slipped out of the heavenly yoke; ", "html": "<span class='verse-line'>{}</span>"}, "dhp417:3": {"pli": "Sabbayogavisaṁyuttaṁ, ", "eng": "unyoked from all yokes: ", "html": "<span class='verse-line'>{}</span>"}, "dhp417:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp418:0": {"pli": "Naṭaputtakattheravatthu ", "html": "<article id='dhp418'><h2 class='sutta-title'>{}</h2>"}, "dhp418:1": {"pli": "Hitvā ratiñca aratiñca, ", "eng": "Giving up desire and discontent, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp418:2": {"pli": "sītibhūtaṁ nirūpadhiṁ; ", "eng": "they’re cooled and free of attachments; ", "html": "<span class='verse-line'>{}</span>"}, "dhp418:3": {"pli": "Sabbalokābhibhuṁ vīraṁ, ", "eng": "a hero, master of the whole world: ", "html": "<span class='verse-line'>{}</span>"}, "dhp418:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp419:0": {"pli": "Vaṅgīsattheravatthu ", "html": "<article id='dhp419'><h2 class='sutta-title'>{}</h2>"}, "dhp419:1": {"pli": "Cutiṁ yo vedi sattānaṁ, ", "eng": "They know the passing away ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp419:2": {"pli": "upapattiñca sabbaso; ", "eng": "and rebirth of all beings; ", "html": "<span class='verse-line'>{}</span>"}, "dhp419:3": {"pli": "Asattaṁ sugataṁ buddhaṁ, ", "eng": "unattached, holy, awakened: ", "html": "<span class='verse-line'>{}</span>"}, "dhp419:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp420:1": {"pli": "Yassa gatiṁ na jānanti, ", "eng": "Gods, centaurs, and humans ", "html": "<article id='dhp420'><blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp420:2": {"pli": "devā gandhabbamānusā; ", "eng": "don’t know their destiny; ", "html": "<span class='verse-line'>{}</span>"}, "dhp420:3": {"pli": "Khīṇāsavaṁ arahantaṁ, ", "eng": "the perfected ones with defilements ended: ", "html": "<span class='verse-line'>{}</span>"}, "dhp420:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp421:0": {"pli": "Dhammadinnāttherīvatthu ", "html": "<article id='dhp421'><h2 class='sutta-title'>{}</h2>"}, "dhp421:1": {"pli": "Yassa pure ca pacchā ca, ", "eng": "They have nothing before or after, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp421:2": {"pli": "majjhe ca natthi kiñcanaṁ; ", "eng": "or even in between. ", "html": "<span class='verse-line'>{}</span>"}, "dhp421:3": {"pli": "Akiñcanaṁ anādānaṁ, ", "eng": "Having nothing, taking nothing: ", "html": "<span class='verse-line'>{}</span>"}, "dhp421:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp422:0": {"pli": "Aṅgulimālattheravatthu ", "html": "<article id='dhp422'><h2 class='sutta-title'>{}</h2>"}, "dhp422:1": {"pli": "Usabhaṁ pavaraṁ vīraṁ, ", "eng": "Captain of the herd, excellent hero, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp422:2": {"pli": "mahesiṁ vijitāvinaṁ; ", "eng": "great seer and victor; ", "html": "<span class='verse-line'>{}</span>"}, "dhp422:3": {"pli": "Anejaṁ nhātakaṁ buddhaṁ, ", "eng": "unstirred, washed, awakened: ", "html": "<span class='verse-line'>{}</span>"}, "dhp422:4": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}, "dhp423:0": {"pli": "Devahitabrāhmaṇavatthu ", "html": "<article id='dhp423'><h2 class='sutta-title'>{}</h2>"}, "dhp423:1": {"pli": "Pubbenivāsaṁ yo vedi, ", "eng": "One who knows their past lives, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp423:2": {"pli": "saggāpāyañca passati; ", "eng": "sees heaven and places of loss, ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:3": {"pli": "Atho jātikkhayaṁ patto, ", "eng": "and has attained the end of rebirth; ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:4": {"pli": "abhiññāvosito muni; ", "eng": "a sage of perfect insight ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:5": {"pli": "Sabbavositavosānaṁ, ", "eng": "at the summit of spiritual perfection: ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:6": {"pli": "tamahaṁ brūmi brāhmaṇaṁ. ", "eng": "that’s who I declare a brahmin. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp423:7": {"pli": "Brāhmaṇavaggo chabbīsatimo. ", "html": "<p class='endvagga'>{}</p>"}, "dhp423:8": {"pli": "Ettāvatā sabbapaṭhame yamakavagge cuddasa vatthūni, appamādavagge nava, cittavagge nava, pupphavagge dvādasa, bālavagge pannarasa, paṇḍitavagge ekādasa, arahantavagge dasa, sahassavagge cuddasa, pāpavagge dvādasa, daṇḍavagge ekādasa, jarāvagge nava, attavagge dasa, lokavagge ekādasa, buddhavagge nava, sukhavagge aṭṭha, piyavagge nava, kodhavagge aṭṭha, malavagge dvādasa, dhammaṭṭhavagge dasa, maggavagge dvādasa, pakiṇṇakavagge nava, nirayavagge nava, nāgavagge aṭṭha, taṇhāvagge dvādasa, bhikkhuvagge dvādasa, brāhmaṇavagge cattālīsāti pañcādhikāni tīṇi vatthusatāni. ", "html": "<div class='uddana'><p>{}</p>"}, "dhp423:9": {"pli": "Satevīsacatussatā, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "dhp423:10": {"pli": "catusaccavibhāvinā; ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:11": {"pli": "Satattayañca vatthūnaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:12": {"pli": "pañcādhikaṁ samuṭṭhitāti. ", "html": "<span class='verse-line'>{}</span></p>"}, "dhp423:13": {"pli": "Dhammapade vaggānamuddānaṁ ", "html": "<p class='uddana-intro'>{}</p></blockquote>"}, "dhp423:14": {"pli": "Yamakappamādo cittaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp423:15": {"pli": "pupphaṁ bālena paṇḍito; ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:16": {"pli": "Arahanto sahassañca, ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:17": {"pli": "pāpaṁ daṇḍena te dasa. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp423:18": {"pli": "Jarā attā ca loko ca, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp423:19": {"pli": "buddho sukhaṁ piyena ca; ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:20": {"pli": "Kodho malañca dhammaṭṭho, ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:21": {"pli": "maggavaggena vīsati. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp423:22": {"pli": "Pakiṇṇaṁ nirayo nāgo, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp423:23": {"pli": "taṇhā bhikkhu ca brāhmaṇo; ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:24": {"pli": "Ete chabbīsati vaggā, ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:25": {"pli": "desitādiccabandhunā. ", "html": "<span class='verse-line'>{}</span></p>"}, "dhp423:26": {"pli": "Gāthānamuddānaṁ ", "html": "<p class='uddana-intro'>{}</p></blockquote>"}, "dhp423:27": {"pli": "Yamake vīsati gāthā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp423:28": {"pli": "appamādamhi dvādasa; ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:29": {"pli": "Ekādasa cittavagge, ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:30": {"pli": "pupphavaggamhi soḷasa. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp423:31": {"pli": "Bāle ca soḷasa gāthā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp423:32": {"pli": "paṇḍitamhi catuddasa; ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:33": {"pli": "Arahante dasa gāthā, ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:34": {"pli": "sahasse honti soḷasa. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp423:35": {"pli": "Terasa pāpavaggamhi, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp423:36": {"pli": "daṇḍamhi dasa satta ca; ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:37": {"pli": "Ekādasa jarā vagge, ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:38": {"pli": "attavaggamhi tā dasa. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp423:39": {"pli": "Dvādasa lokavaggamhi, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp423:40": {"pli": "buddhavaggamhi ṭhārasa; ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:41": {"pli": "Sukhe ca piyavagge ca, ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:42": {"pli": "gāthāyo honti dvādasa. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp423:43": {"pli": "Cuddasa kodhavaggamhi, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp423:44": {"pli": "malavaggekavīsati; ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:45": {"pli": "Sattarasa ca dhammaṭṭhe, ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:46": {"pli": "maggavagge sattarasa. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp423:47": {"pli": "Pakiṇṇe soḷasa gāthā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp423:48": {"pli": "niraye nāge ca cuddasa; ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:49": {"pli": "Chabbīsa taṇhāvaggamhi, ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:50": {"pli": "tevīsa bhikkhuvaggikā. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "dhp423:51": {"pli": "Ekatālīsagāthāyo, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "dhp423:52": {"pli": "brāhmaṇe vaggamuttame; ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:53": {"pli": "Gāthāsatāni cattāri, ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:54": {"pli": "tevīsa ca punāpare; ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:55": {"pli": "Dhammapade nipātamhi, ", "html": "<span class='verse-line'>{}</span>"}, "dhp423:56": {"pli": "desitādiccabandhunāti. ", "html": "<span class='verse-line'>{}</span></p></blockquote></div>"}, "dhp423:57": {"pli": "Dhammapadapāḷi samattā. ", "eng": "The Sayings of the Dhamma are completed. ", "html": "<p class='endbook'>{}</p></article></section>"}}}};