window.SUTTA_DB['sutta_kn_kp'] = {"id": "kp", "title": "Basic Passages", "structure": {"kp": ["kp1", "kp2", "kp3", "kp4", "kp5", "kp6", "kp7", "kp8", "kp9"]}, "meta": {"kp": {"type": "branch", "acronym": "Kp", "translated_title": "Basic Passages", "original_title": "Khuddakapāṭha", "blurb": "Nine short texts, which appear to have been collected as a kind of basic study curriculum for novices. The collection itself was probably made in Sri Lanka, but most of the texts are found elsewhere in the <i lang='pi' translate='no'>nikāyas</i>.", "author_uid": null, "scroll_target": null}, "kp1": {"type": "leaf", "acronym": "Kp 1", "translated_title": "The Three Refuges", "original_title": "Saraṇattaya", "blurb": "The three refuges formula", "author_uid": "sujato", "scroll_target": null}, "kp2": {"type": "leaf", "acronym": "Kp 2", "translated_title": "The Ten Precepts", "original_title": "Dasasikkhāpada", "blurb": "The ten precepts of a novice mendicant", "author_uid": "sujato", "scroll_target": null}, "kp3": {"type": "leaf", "acronym": "Kp 3", "translated_title": "The Thirty-two Parts of the Body", "original_title": "Dvattiṁsākāra", "blurb": "The thirty-two parts of the body for reflection", "author_uid": "sujato", "scroll_target": null}, "kp4": {"type": "leaf", "acronym": "Kp 4", "translated_title": "The Boy’s Questions", "original_title": "Kumārapañhā", "blurb": "A list of ten doctrinal factors", "author_uid": "sujato", "scroll_target": null}, "kp5": {"type": "leaf", "acronym": "Kp 5", "translated_title": "True Blessings", "original_title": "Maṅgalasutta", "blurb": "A list of the types of skillful behavior that give blessings and protection.", "author_uid": "sujato", "scroll_target": null}, "kp6": {"type": "leaf", "acronym": "Kp 6", "translated_title": "Gems", "original_title": "Ratanasutta", "blurb": "The many treasures to be found in the Triple Gem.", "author_uid": "sujato", "scroll_target": null}, "kp7": {"type": "leaf", "acronym": "Kp 7", "translated_title": "Without the Walls", "original_title": "Tirokuṭṭasutta", "blurb": "The Supreme Buddha explains how to share merit with past relatives.", "author_uid": "sujato", "scroll_target": null}, "kp8": {"type": "leaf", "acronym": "Kp 8", "translated_title": "Storing Your Savings", "original_title": "Nidhikaṇḍasutta", "blurb": "Merit is the only sure investment", "author_uid": "sujato", "scroll_target": null}, "kp9": {"type": "leaf", "acronym": "Kp 9", "translated_title": "The Teaching on Love", "original_title": "Mettāsutta", "blurb": "The practice of developing universal goodwill: the practices that form a foundation for the practice, the attitude of universal goodwill itself, and the steps that lead from goodwill to awakening.", "author_uid": "sujato", "scroll_target": null}}, "content": {"kp1": {"kp1:0.1": {"pli": "Khuddakapāṭha 1 ", "eng": "Basic Passages 1 ", "html": "<article id='kp1'><header><ul><li class='division'>{}</li></ul>"}, "kp1:0.2": {"pli": "Saraṇattaya ", "eng": "The Three Refuges ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "kp1:1.1": {"pli": "Namo tassa Bhagavato Arahato Sammāsambuddhassa. ", "eng": "Homage to him, the blessed one, the perfected one, the fully awakened Buddha! ", "html": "<p class='namo'>{}</p>"}, "kp1:2.1": {"pli": "Buddhaṁ saraṇaṁ gacchāmi, ", "eng": "I take refuge in the Buddha, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "kp1:2.2": {"pli": "Dhammaṁ saraṇaṁ gacchāmi, ", "eng": "I take refuge in the Teaching, ", "html": "<span class='verse-line'>{}</span>"}, "kp1:2.3": {"pli": "Saṅghaṁ saraṇaṁ gacchāmi. ", "eng": "I take refuge in the Sangha. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp1:3.1": {"pli": "Dutiyampi buddhaṁ saraṇaṁ gacchāmi, ", "eng": "For the second time I take refuge in the Buddha, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp1:3.2": {"pli": "Dutiyampi dhammaṁ saraṇaṁ gacchāmi, ", "eng": "for the second time I take refuge in the Teaching, ", "html": "<span class='verse-line'>{}</span>"}, "kp1:3.3": {"pli": "Dutiyampi saṅghaṁ saraṇaṁ gacchāmi. ", "eng": "for the second time I take refuge in the Sangha. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp1:4.1": {"pli": "Tatiyampi buddhaṁ saraṇaṁ gacchāmi, ", "eng": "For the third time I take refuge in the Buddha, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp1:4.2": {"pli": "Tatiyampi dhammaṁ saraṇaṁ gacchāmi, ", "eng": "for the third time I take refuge in the Teaching, ", "html": "<span class='verse-line'>{}</span>"}, "kp1:4.3": {"pli": "Tatiyampi saṅghaṁ saraṇaṁ gacchāmi. ", "eng": "for the third time I take refuge in the Sangha. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "kp1:5.1": {"pli": "Saraṇattayaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "kp2": {"kp2:0.1": {"pli": "Khuddakapāṭha 2 ", "eng": "Basic Passages 2 ", "html": "<article id='kp2'><header><ul><li class='division'>{}</li></ul>"}, "kp2:0.2": {"pli": "Dasasikkhāpada ", "eng": "The Ten Precepts ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "kp2:1.1": {"pli": "Pāṇātipātā veramaṇisikkhāpadaṁ samādiyāmi. ", "eng": "I undertake the precept to refrain from killing living creatures. ", "html": "<p>{}</p>"}, "kp2:2.1": {"pli": "Adinnādānā veramaṇisikkhāpadaṁ samādiyāmi. ", "eng": "I undertake the precept to refrain from stealing. ", "html": "<p>{}</p>"}, "kp2:3.1": {"pli": "Abrahmacariyā veramaṇisikkhāpadaṁ samādiyāmi. ", "eng": "I undertake the precept to refrain from sexual activity. ", "html": "<p>{}</p>"}, "kp2:4.1": {"pli": "Musāvādā veramaṇisikkhāpadaṁ samādiyāmi. ", "eng": "I undertake the precept to refrain from lying. ", "html": "<p>{}</p>"}, "kp2:5.1": {"pli": "Surāmerayamajjapamādaṭṭhānā veramaṇisikkhāpadaṁ samādiyāmi. ", "eng": "I undertake the precept to refrain from consuming beer, wine, and liquor intoxicants. ", "html": "<p>{}</p>"}, "kp2:6.1": {"pli": "Vikālabhojanā veramaṇisikkhāpadaṁ samādiyāmi. ", "eng": "I undertake the precept to refrain from food at the wrong time. ", "html": "<p>{}</p>"}, "kp2:7.1": {"pli": "Naccagītavāditavisūkadassanā veramaṇisikkhāpadaṁ samādiyāmi. ", "eng": "I undertake the precept to refrain from seeing shows of dancing, singing, and music . ", "html": "<p>{}</p>"}, "kp2:8.1": {"pli": "Mālāgandhavilepanadhāraṇamaṇḍanavibhūsanaṭṭhānā veramaṇisikkhāpadaṁ samādiyāmi. ", "eng": "I undertake the precept to refrain from attiring and adorning myself with garlands, fragrance, and makeup. ", "html": "<p>{}</p>"}, "kp2:9.1": {"pli": "Uccāsayanamahāsayanā veramaṇisikkhāpadaṁ samādiyāmi. ", "eng": "I undertake the precept to refrain from high and luxurious beds. ", "html": "<p>{}</p>"}, "kp2:10.1": {"pli": "Jātarūparajatapaṭiggahaṇā veramaṇisikkhāpadaṁ samādiyāmi. ", "eng": "I undertake the precept to refrain from receiving gold and currency. ", "html": "<p>{}</p>"}, "kp2:11.1": {"pli": "Dasasikkhāpadaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "kp3": {"kp3:0.1": {"pli": "Khuddakapāṭha 3 ", "eng": "Basic Passages 3 ", "html": "<article id='kp3'><header><ul><li class='division'>{}</li></ul>"}, "kp3:0.2": {"pli": "Dvattiṁsākāra ", "eng": "The Thirty-Two Parts of the Body ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "kp3:1.1": {"pli": "Atthi imasmiṁ kāye—Kesā lomā nakhā dantā taco, maṁsaṁ nhāru aṭṭhi aṭṭhimiñjaṁ vakkaṁ, hadayaṁ yakanaṁ kilomakaṁ pihakaṁ papphāsaṁ, antaṁ antaguṇaṁ udariyaṁ karīsaṁ matthaluṅgaṁ, pittaṁ semhaṁ pubbo lohitaṁ sedo medo, assu vasā kheḷo siṅghāṇikā lasikā muttanti. ", "eng": "“In this body there is head hair, body hair, nails, teeth, skin, flesh, sinews, bones, bone marrow, kidneys, heart, liver, diaphragm, spleen, lungs, intestines, mesentery, undigested food, feces, brain, bile, phlegm, pus, blood, sweat, fat, tears, grease, saliva, snot, synovial fluid, urine.” ", "html": "<p>{}</p>"}, "kp3:2.1": {"pli": "Dvattiṁsākāraṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "kp4": {"kp4:0.1": {"pli": "Khuddakapāṭha 4 ", "eng": "Basic Passages 4 ", "html": "<article id='kp4'><header><ul><li class='division'>{}</li></ul>"}, "kp4:0.2": {"pli": "Kumārapañhā ", "eng": "The Boy’s Questions ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "kp4:1.1": {"pli": "Ekaṁ nāma kiṁ? ", "eng": "What is the one? ", "html": "<p>{}"}, "kp4:1.2": {"pli": "Sabbe sattā āhāraṭṭhitikā. ", "eng": "All sentient beings are sustained by food. ", "html": "{}</p>"}, "kp4:2.1": {"pli": "Dve nāma kiṁ? ", "eng": "What is the two? ", "html": "<p>{}"}, "kp4:2.2": {"pli": "Nāmañca rūpañca. ", "eng": "Name and form. ", "html": "{}</p>"}, "kp4:3.1": {"pli": "Tīṇi nāma kiṁ? ", "eng": "What is the three? ", "html": "<p>{}"}, "kp4:3.2": {"pli": "Tisso vedanā. ", "eng": "Three feelings. ", "html": "{}</p>"}, "kp4:4.1": {"pli": "Cattāri nāma kiṁ? ", "eng": "What is the four? ", "html": "<p>{}"}, "kp4:4.2": {"pli": "Cattāri ariyasaccāni. ", "eng": "Four noble truths. ", "html": "{}</p>"}, "kp4:5.1": {"pli": "Pañca nāma kiṁ? ", "eng": "What is the five? ", "html": "<p>{}"}, "kp4:5.2": {"pli": "Pañcupādānakkhandhā. ", "eng": "Five grasping aggregates. ", "html": "{}</p>"}, "kp4:6.1": {"pli": "Cha nāma kiṁ? ", "eng": "What is the six? ", "html": "<p>{}"}, "kp4:6.2": {"pli": "Cha ajjhattikāni āyatanāni. ", "eng": "Six interior sense fields. ", "html": "{}</p>"}, "kp4:7.1": {"pli": "Satta nāma kiṁ? ", "eng": "What is the seven? ", "html": "<p>{}"}, "kp4:7.2": {"pli": "Satta bojjhaṅgā. ", "eng": "Seven awakening factors. ", "html": "{}</p>"}, "kp4:8.1": {"pli": "Aṭṭha nāma kiṁ? ", "eng": "What is the eight? ", "html": "<p>{}"}, "kp4:8.2": {"pli": "Ariyo aṭṭhaṅgiko maggo. ", "eng": "The noble eightfold path. ", "html": "{}</p>"}, "kp4:9.1": {"pli": "Nava nāma kiṁ? ", "eng": "What is the nine? ", "html": "<p>{}"}, "kp4:9.2": {"pli": "Nava sattāvāsā. ", "eng": "Nine abodes of sentient beings. ", "html": "{}</p>"}, "kp4:10.1": {"pli": "Dasa nāma kiṁ? ", "eng": "What is the ten? ", "html": "<p>{}"}, "kp4:10.2": {"pli": "Dasahaṅgehi samannāgato “arahā”ti vuccatīti. ", "eng": "One endowed with ten factors is called “perfected”. ", "html": "{}</p>"}, "kp4:11.1": {"pli": "Kumārapañhā. ", "html": "<p class='endsutta'>{}</p></article>"}}, "kp5": {"kp5:0.1": {"pli": "Khuddakapāṭha 5 ", "eng": "Basic Passages 5 ", "html": "<article id='kp5'><header><ul><li class='division'>{}</li></ul>"}, "kp5:0.2": {"pli": "Maṅgalasutta ", "eng": "Blessings ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "kp5:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p>{}"}, "kp5:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "kp5:1.3": {"pli": "Atha kho aññatarā devatā abhikkantāya rattiyā abhikkantavaṇṇā kevalakappaṁ jetavanaṁ obhāsetvā yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ aṭṭhāsi. ", "eng": "Then, late at night, a glorious deity, lighting up the entire Jeta’s Grove, went up to the Buddha, bowed, and stood to one side. ", "html": "{}"}, "kp5:1.4": {"pli": "Ekamantaṁ ṭhitā kho sā devatā bhagavantaṁ gāthāya ajjhabhāsi—", "eng": "That deity addressed the Buddha in verse: ", "html": "{}</p>"}, "kp5:2.1": {"pli": "“Bahū devā manussā ca, ", "eng": "“Many gods and humans ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "kp5:2.2": {"pli": "maṅgalāni acintayuṁ; ", "eng": "have thought about blessings ", "html": "<span class='verse-line'>{}</span>"}, "kp5:2.3": {"pli": "Ākaṅkhamānā sotthānaṁ, ", "eng": "desiring well-being: ", "html": "<span class='verse-line'>{}</span>"}, "kp5:2.4": {"pli": "brūhi maṅgalamuttamaṁ”. ", "eng": "declare the highest blessing.” ", "html": "<span class='verse-line'>{}</span></p>"}, "kp5:3.1": {"pli": "“Asevanā ca bālānaṁ, ", "eng": "“Not to fraternize with fools, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp5:3.2": {"pli": "paṇḍitānañca sevanā; ", "eng": "but to fraternize with the wise, ", "html": "<span class='verse-line'>{}</span>"}, "kp5:3.3": {"pli": "Pūjā ca pūjaneyyānaṁ, ", "eng": "and honoring those worthy of honor: ", "html": "<span class='verse-line'>{}</span>"}, "kp5:3.4": {"pli": "etaṁ maṅgalamuttamaṁ. ", "eng": "this is the highest blessing. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp5:4.1": {"pli": "Patirūpadesavāso ca, ", "eng": "Living in a suitable region, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp5:4.2": {"pli": "pubbe ca katapuññatā; ", "eng": "having made merit in the past, ", "html": "<span class='verse-line'>{}</span>"}, "kp5:4.3": {"pli": "Attasammāpaṇidhi ca, ", "eng": "being rightly resolved in oneself, ", "html": "<span class='verse-line'>{}</span>"}, "kp5:4.4": {"pli": "etaṁ maṅgalamuttamaṁ. ", "eng": "this is the highest blessing. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp5:5.1": {"pli": "Bāhusaccañca sippañca, ", "eng": "Education and a craft, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp5:5.2": {"pli": "vinayo ca susikkhito; ", "eng": "discipline and training, ", "html": "<span class='verse-line'>{}</span>"}, "kp5:5.3": {"pli": "Subhāsitā ca yā vācā, ", "eng": "and well-spoken speech: ", "html": "<span class='verse-line'>{}</span>"}, "kp5:5.4": {"pli": "etaṁ maṅgalamuttamaṁ. ", "eng": "this is the highest blessing. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp5:6.1": {"pli": "Mātāpituupaṭṭhānaṁ, ", "eng": "Caring for mother and father, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp5:6.2": {"pli": "puttadārassa saṅgaho; ", "eng": "kindness to children and partners, ", "html": "<span class='verse-line'>{}</span>"}, "kp5:6.3": {"pli": "Anākulā ca kammantā, ", "eng": "and unstressful work: ", "html": "<span class='verse-line'>{}</span>"}, "kp5:6.4": {"pli": "etaṁ maṅgalamuttamaṁ. ", "eng": "this is the highest blessing. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp5:7.1": {"pli": "Dānañca dhammacariyā ca, ", "eng": "Giving and righteous conduct, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp5:7.2": {"pli": "ñātakānañca saṅgaho; ", "eng": "kindness to relatives, ", "html": "<span class='verse-line'>{}</span>"}, "kp5:7.3": {"pli": "Anavajjāni kammāni, ", "eng": "blameless deeds: ", "html": "<span class='verse-line'>{}</span>"}, "kp5:7.4": {"pli": "etaṁ maṅgalamuttamaṁ. ", "eng": "this is the highest blessing. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp5:8.1": {"pli": "Āratī viratī pāpā, ", "eng": "Desisting and abstaining from evil, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp5:8.2": {"pli": "majjapānā ca saṁyamo; ", "eng": "avoiding drinking liquor, ", "html": "<span class='verse-line'>{}</span>"}, "kp5:8.3": {"pli": "Appamādo ca dhammesu, ", "eng": "diligence in good qualities: ", "html": "<span class='verse-line'>{}</span>"}, "kp5:8.4": {"pli": "etaṁ maṅgalamuttamaṁ. ", "eng": "this is the highest blessing. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp5:9.1": {"pli": "Gāravo ca nivāto ca, ", "eng": "Respect and humility, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp5:9.2": {"pli": "santuṭṭhi ca kataññutā; ", "eng": "contentment and gratitude, ", "html": "<span class='verse-line'>{}</span>"}, "kp5:9.3": {"pli": "Kālena dhammassavanaṁ, ", "eng": "and timely listening to the teaching: ", "html": "<span class='verse-line'>{}</span>"}, "kp5:9.4": {"pli": "etaṁ maṅgalamuttamaṁ. ", "eng": "this is the highest blessing. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp5:10.1": {"pli": "Khantī ca sovacassatā, ", "eng": "Patience, being easy to admonish, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp5:10.2": {"pli": "samaṇānañca dassanaṁ; ", "eng": "the sight of ascetics, ", "html": "<span class='verse-line'>{}</span>"}, "kp5:10.3": {"pli": "Kālena dhammasākacchā, ", "eng": "and timely discussion of the teaching: ", "html": "<span class='verse-line'>{}</span>"}, "kp5:10.4": {"pli": "etaṁ maṅgalamuttamaṁ. ", "eng": "this is the highest blessing. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp5:11.1": {"pli": "Tapo ca brahmacariyañca, ", "eng": "Fervor and chastity ", "html": "<p><span class='verse-line'>{}</span>"}, "kp5:11.2": {"pli": "ariyasaccāna dassanaṁ; ", "eng": "seeing the noble truths, ", "html": "<span class='verse-line'>{}</span>"}, "kp5:11.3": {"pli": "Nibbānasacchikiriyā ca, ", "eng": "and realization of extinguishment: ", "html": "<span class='verse-line'>{}</span>"}, "kp5:11.4": {"pli": "etaṁ maṅgalamuttamaṁ. ", "eng": "this is the highest blessing. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp5:12.1": {"pli": "Phuṭṭhassa lokadhammehi, ", "eng": "Though touched by worldly conditions, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp5:12.2": {"pli": "cittaṁ yassa na kampati; ", "eng": "their mind does not tremble; ", "html": "<span class='verse-line'>{}</span>"}, "kp5:12.3": {"pli": "Asokaṁ virajaṁ khemaṁ, ", "eng": "sorrowless, stainless, secure: ", "html": "<span class='verse-line'>{}</span>"}, "kp5:12.4": {"pli": "etaṁ maṅgalamuttamaṁ. ", "eng": "this is the highest blessing. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp5:13.1": {"pli": "Etādisāni katvāna, ", "eng": "Having completed these things, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp5:13.2": {"pli": "Sabbattha maparājitā; ", "eng": "undefeated everywhere; ", "html": "<span class='verse-line'>{}</span>"}, "kp5:13.3": {"pli": "Sabbattha sotthiṁ gacchanti, ", "eng": "everywhere they go in safety: ", "html": "<span class='verse-line'>{}</span>"}, "kp5:13.4": {"pli": "Taṁ tesaṁ maṅgalamuttaman”ti. ", "eng": "this is their highest blessing.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "kp5:14.1": {"pli": "Maṅgalasuttaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "kp6": {"kp6:0.1": {"pli": "Khuddakapāṭha 6 ", "eng": "Basic Passages 6 ", "html": "<article id='kp6'><header><ul><li class='division'>{}</li></ul>"}, "kp6:0.2": {"pli": "Ratanasutta ", "eng": "Gems ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "kp6:1.1": {"pli": "Yānīdha bhūtāni samāgatāni, ", "eng": "Whatever beings have gathered here, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "kp6:1.2": {"pli": "Bhummāni vā yāni va antalikkhe; ", "eng": "on the ground or in the sky, ", "html": "<span class='verse-line'>{}</span>"}, "kp6:1.3": {"pli": "Sabbeva bhūtā sumanā bhavantu, ", "eng": "may beings all be of happy heart, ", "html": "<span class='verse-line'>{}</span>"}, "kp6:1.4": {"pli": "Athopi sakkacca suṇantu bhāsitaṁ. ", "eng": "and listen carefully to what is said. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp6:2.1": {"pli": "Tasmā hi bhūtā nisāmetha sabbe, ", "eng": "So pay heed, all you beings, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp6:2.2": {"pli": "Mettaṁ karotha mānusiyā pajāya; ", "eng": "have love for humankind, ", "html": "<span class='verse-line'>{}</span>"}, "kp6:2.3": {"pli": "Divā ca ratto ca haranti ye baliṁ, ", "eng": "who day and night bring offerings; ", "html": "<span class='verse-line'>{}</span>"}, "kp6:2.4": {"pli": "Tasmā hi ne rakkhatha appamattā. ", "eng": "please protect them diligently. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp6:3.1": {"pli": "Yaṁ kiñci vittaṁ idha vā huraṁ vā, ", "eng": "There’s no wealth this life or the next, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp6:3.2": {"pli": "Saggesu vā yaṁ ratanaṁ paṇītaṁ; ", "eng": "no sublime gem in the heavens, ", "html": "<span class='verse-line'>{}</span>"}, "kp6:3.3": {"pli": "Na no samaṁ atthi tathāgatena, ", "eng": "that equals the Realized One. ", "html": "<span class='verse-line'>{}</span>"}, "kp6:3.4": {"pli": "Idampi buddhe ratanaṁ paṇītaṁ; ", "eng": "This sublime gem is in the Buddha: ", "html": "<span class='verse-line'>{}</span>"}, "kp6:3.5": {"pli": "Etena saccena suvatthi hotu. ", "eng": "by this truth, may you be well! ", "html": "<span class='verse-line'>{}</span></p>"}, "kp6:4.1": {"pli": "Khayaṁ virāgaṁ amataṁ paṇītaṁ, ", "eng": "Ending, dispassion, the undying, the sublime, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp6:4.2": {"pli": "Yadajjhagā sakyamunī samāhito; ", "eng": "attained by the Sakyan Sage immersed in samādhi; ", "html": "<span class='verse-line'>{}</span>"}, "kp6:4.3": {"pli": "Na tena dhammena samatthi kiñci, ", "eng": "there is nothing equal to that Dhamma. ", "html": "<span class='verse-line'>{}</span>"}, "kp6:4.4": {"pli": "Idampi dhamme ratanaṁ paṇītaṁ; ", "eng": "This sublime gem is in the Dhamma: ", "html": "<span class='verse-line'>{}</span>"}, "kp6:4.5": {"pli": "Etena saccena suvatthi hotu. ", "eng": "by this truth, may you be well! ", "html": "<span class='verse-line'>{}</span></p>"}, "kp6:5.1": {"pli": "Yaṁ buddhaseṭṭho parivaṇṇayī suciṁ, ", "eng": "The purity praised by the highest Buddha, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp6:5.2": {"pli": "Samādhimānantarikaññamāhu; ", "eng": "is said to be the “immersion with immediate fruit”; ", "html": "<span class='verse-line'>{}</span>"}, "kp6:5.3": {"pli": "Samādhinā tena samo na vijjati, ", "eng": "no equal to that immersion is found. ", "html": "<span class='verse-line'>{}</span>"}, "kp6:5.4": {"pli": "Idampi dhamme ratanaṁ paṇītaṁ; ", "eng": "This sublime gem is in the Dhamma: ", "html": "<span class='verse-line'>{}</span>"}, "kp6:5.5": {"pli": "Etena saccena suvatthi hotu. ", "eng": "by this truth, may you be well! ", "html": "<span class='verse-line'>{}</span></p>"}, "kp6:6.1": {"pli": "Ye puggalā aṭṭha sataṁ pasatthā, ", "eng": "The eight individual persons praised by the good ", "html": "<p><span class='verse-line'>{}</span>"}, "kp6:6.2": {"pli": "Cattāri etāni yugāni honti; ", "eng": "are the four pairs of the Holy One’s disciples; ", "html": "<span class='verse-line'>{}</span>"}, "kp6:6.3": {"pli": "Te dakkhiṇeyyā sugatassa sāvakā, ", "eng": "they are worthy of religious donations, ", "html": "<span class='verse-line'>{}</span>"}, "kp6:6.4": {"pli": "Etesu dinnāni mahapphalāni; ", "eng": "what’s given to them is very fruitful. ", "html": "<span class='verse-line'>{}</span>"}, "kp6:6.5": {"pli": "Idampi saṅghe ratanaṁ paṇītaṁ, ", "eng": "This sublime gem is in the Saṅgha: ", "html": "<span class='verse-line'>{}</span>"}, "kp6:6.6": {"pli": "Etena saccena suvatthi hotu. ", "eng": "by this truth, may you be well! ", "html": "<span class='verse-line'>{}</span></p>"}, "kp6:7.1": {"pli": "Ye suppayuttā manasā daḷhena, ", "eng": "Dedicated to Gotama’s dispensation, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp6:7.2": {"pli": "Nikkāmino gotamasāsanamhi; ", "eng": "strong-minded, free of sense desire, ", "html": "<span class='verse-line'>{}</span>"}, "kp6:7.3": {"pli": "Te pattipattā amataṁ vigayha, ", "eng": "they’ve attained the goal, plunged into freedom from death, ", "html": "<span class='verse-line'>{}</span>"}, "kp6:7.4": {"pli": "Laddhā mudhā nibbutiṁ bhuñjamānā; ", "eng": "and enjoy the quenching they’ve freely gained. ", "html": "<span class='verse-line'>{}</span>"}, "kp6:7.5": {"pli": "Idampi saṅghe ratanaṁ paṇītaṁ, ", "eng": "This sublime gem is in the Saṅgha: ", "html": "<span class='verse-line'>{}</span>"}, "kp6:7.6": {"pli": "Etena saccena suvatthi hotu. ", "eng": "by this truth, may you be well! ", "html": "<span class='verse-line'>{}</span></p>"}, "kp6:8.1": {"pli": "Yathindakhīlo pathavissito siyā, ", "eng": "As Indra’s pillar rooted in earth ", "html": "<p><span class='verse-line'>{}</span>"}, "kp6:8.2": {"pli": "Catubbhi vātehi asampakampiyo; ", "eng": "is not shaken by the four winds, ", "html": "<span class='verse-line'>{}</span>"}, "kp6:8.3": {"pli": "Tathūpamaṁ sappurisaṁ vadāmi, ", "eng": "I say a true person is like this, ", "html": "<span class='verse-line'>{}</span>"}, "kp6:8.4": {"pli": "Yo ariyasaccāni avecca passati; ", "eng": "who sees the noble truths in experience. ", "html": "<span class='verse-line'>{}</span>"}, "kp6:8.5": {"pli": "Idampi saṅghe ratanaṁ paṇītaṁ, ", "eng": "This sublime gem is in the Saṅgha: ", "html": "<span class='verse-line'>{}</span>"}, "kp6:8.6": {"pli": "Etena saccena suvatthi hotu. ", "eng": "by this truth, may you be well! ", "html": "<span class='verse-line'>{}</span></p>"}, "kp6:9.1": {"pli": "Ye ariyasaccāni vibhāvayanti, ", "eng": "Those who fathom the noble truths ", "html": "<p><span class='verse-line'>{}</span>"}, "kp6:9.2": {"pli": "Gambhīrapaññena sudesitāni; ", "eng": "taught by the one of deep wisdom, ", "html": "<span class='verse-line'>{}</span>"}, "kp6:9.3": {"pli": "Kiñcāpi te honti bhusaṁ pamattā, ", "eng": "do not take an eighth life, ", "html": "<span class='verse-line'>{}</span>"}, "kp6:9.4": {"pli": "Na te bhavaṁ aṭṭhamamādiyanti; ", "eng": "even if they are hugely negligent. ", "html": "<span class='verse-line'>{}</span>"}, "kp6:9.5": {"pli": "Idampi saṅghe ratanaṁ paṇītaṁ, ", "eng": "This sublime gem is in the Saṅgha: ", "html": "<span class='verse-line'>{}</span>"}, "kp6:9.6": {"pli": "Etena saccena suvatthi hotu. ", "eng": "by this truth, may you be well! ", "html": "<span class='verse-line'>{}</span></p>"}, "kp6:10.1": {"pli": "Sahāvassa dassanasampadāya, ", "eng": "When they attain to vision ", "html": "<p><span class='verse-line'>{}</span>"}, "kp6:10.2": {"pli": "Tayassu dhammā jahitā bhavanti; ", "eng": "they give up three things: ", "html": "<span class='verse-line'>{}</span>"}, "kp6:10.3": {"pli": "Sakkāyadiṭṭhī vicikicchitañca, ", "eng": "substantialist view, doubt, and any ", "html": "<span class='verse-line'>{}</span>"}, "kp6:10.4": {"pli": "Sīlabbataṁ vāpi yadatthi kiñci. ", "eng": "attachment to precepts and observances. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp6:11.1": {"pli": "Catūhapāyehi ca vippamutto, ", "eng": "They’re freed from the four places of loss, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp6:11.2": {"pli": "Chaccābhiṭhānāni abhabba kātuṁ; ", "eng": "and cannot do six things. ", "html": "<span class='verse-line'>{}</span>"}, "kp6:11.3": {"pli": "Idampi saṅghe ratanaṁ paṇītaṁ, ", "eng": "This sublime gem is in the Saṅgha: ", "html": "<span class='verse-line'>{}</span>"}, "kp6:11.4": {"pli": "Etena saccena suvatthi hotu. ", "eng": "by this truth, may you be well! ", "html": "<span class='verse-line'>{}</span></p>"}, "kp6:12.1": {"pli": "Kiñcāpi so kamma karoti pāpakaṁ, ", "eng": "Even if they do a bad deed ", "html": "<p><span class='verse-line'>{}</span>"}, "kp6:12.2": {"pli": "Kāyena vācā uda cetasā vā; ", "eng": "by body, speech, or mind, ", "html": "<span class='verse-line'>{}</span>"}, "kp6:12.3": {"pli": "Abhabba so tassa paṭicchadāya, ", "eng": "they are unable to conceal it; ", "html": "<span class='verse-line'>{}</span>"}, "kp6:12.4": {"pli": "Abhabbatā diṭṭhapadassa vuttā; ", "eng": "they say this inability applies to one who has seen the truth. ", "html": "<span class='verse-line'>{}</span>"}, "kp6:12.5": {"pli": "Idampi saṅghe ratanaṁ paṇītaṁ, ", "eng": "This sublime gem is in the Saṅgha: ", "html": "<span class='verse-line'>{}</span>"}, "kp6:12.6": {"pli": "Etena saccena suvatthi hotu. ", "eng": "by this truth, may you be well! ", "html": "<span class='verse-line'>{}</span></p>"}, "kp6:13.1": {"pli": "Vanappagumbe yatha phussitagge, ", "eng": "Like a tall forest tree crowned with flowers ", "html": "<p><span class='verse-line'>{}</span>"}, "kp6:13.2": {"pli": "Gimhānamāse paṭhamasmiṁ gimhe; ", "eng": "in the first month of summer; ", "html": "<span class='verse-line'>{}</span>"}, "kp6:13.3": {"pli": "Tathūpamaṁ dhammavaraṁ adesayi, ", "eng": "that’s how he taught the superb Dhamma, ", "html": "<span class='verse-line'>{}</span>"}, "kp6:13.4": {"pli": "Nibbānagāmiṁ paramaṁ hitāya; ", "eng": "leading to extinguishment, the ultimate benefit. ", "html": "<span class='verse-line'>{}</span>"}, "kp6:13.5": {"pli": "Idampi buddhe ratanaṁ paṇītaṁ, ", "eng": "This sublime gem is in the Buddha: ", "html": "<span class='verse-line'>{}</span>"}, "kp6:13.6": {"pli": "Etena saccena suvatthi hotu. ", "eng": "by this truth, may you be well! ", "html": "<span class='verse-line'>{}</span></p>"}, "kp6:14.1": {"pli": "Varo varaññū varado varāharo, ", "eng": "The superb, knower of the superb, giver of the superb, bringer of the superb; ", "html": "<p><span class='verse-line'>{}</span>"}, "kp6:14.2": {"pli": "Anuttaro dhammavaraṁ adesayi; ", "eng": "taught the superb Dhamma supreme. ", "html": "<span class='verse-line'>{}</span>"}, "kp6:14.3": {"pli": "Idampi buddhe ratanaṁ paṇītaṁ, ", "eng": "This sublime gem is in the Buddha: ", "html": "<span class='verse-line'>{}</span>"}, "kp6:14.4": {"pli": "Etena saccena suvatthi hotu. ", "eng": "by this truth, may you be well! ", "html": "<span class='verse-line'>{}</span></p>"}, "kp6:15.1": {"pli": "Khīṇaṁ purāṇaṁ nava natthi sambhavaṁ, ", "eng": "The old is ended, nothing new is produced. ", "html": "<p><span class='verse-line'>{}</span>"}, "kp6:15.2": {"pli": "Virattacittāyatike bhavasmiṁ; ", "eng": "their minds have no desire for future rebirth. ", "html": "<span class='verse-line'>{}</span>"}, "kp6:15.3": {"pli": "Te khīṇabījā avirūḷhichandā, ", "eng": "Withered are the seeds, there’s no desire for growth, ", "html": "<span class='verse-line'>{}</span>"}, "kp6:15.4": {"pli": "Nibbanti dhīrā yathāyaṁ padīpo; ", "eng": "those attentive ones are extinguished just like this lamp. ", "html": "<span class='verse-line'>{}</span>"}, "kp6:15.5": {"pli": "Idampi saṅghe ratanaṁ paṇītaṁ, ", "eng": "This sublime gem is in the Saṅgha: ", "html": "<span class='verse-line'>{}</span>"}, "kp6:15.6": {"pli": "Etena saccena suvatthi hotu. ", "eng": "by this truth, may you be well! ", "html": "<span class='verse-line'>{}</span></p>"}, "kp6:16.1": {"pli": "Yānīdha bhūtāni samāgatāni, ", "eng": "Whatever beings have gathered here, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp6:16.2": {"pli": "Bhummāni vā yāni va antalikkhe; ", "eng": "on the ground or in the sky: ", "html": "<span class='verse-line'>{}</span>"}, "kp6:16.3": {"pli": "Tathāgataṁ devamanussapūjitaṁ, ", "eng": "the Realized One is honored by gods and humans! ", "html": "<span class='verse-line'>{}</span>"}, "kp6:16.4": {"pli": "Buddhaṁ namassāma suvatthi hotu. ", "eng": "We bow to the Buddha! May you be safe! ", "html": "<span class='verse-line'>{}</span></p>"}, "kp6:17.1": {"pli": "Yānīdha bhūtāni samāgatāni, ", "eng": "Whatever beings have gathered here, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp6:17.2": {"pli": "Bhummāni vā yāni va antalikkhe; ", "eng": "on the ground or in the sky: ", "html": "<span class='verse-line'>{}</span>"}, "kp6:17.3": {"pli": "Tathāgataṁ devamanussapūjitaṁ, ", "eng": "the Realized One is honored by gods and humans! ", "html": "<span class='verse-line'>{}</span>"}, "kp6:17.4": {"pli": "Dhammaṁ namassāma suvatthi hotu. ", "eng": "We bow to the Dhamma! May you be safe! ", "html": "<span class='verse-line'>{}</span></p>"}, "kp6:18.1": {"pli": "Yānīdha bhūtāni samāgatāni, ", "eng": "Whatever beings have gathered here, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp6:18.2": {"pli": "Bhummāni vā yāni va antalikkhe; ", "eng": "on the ground or in the sky: ", "html": "<span class='verse-line'>{}</span>"}, "kp6:18.3": {"pli": "Tathāgataṁ devamanussapūjitaṁ, ", "eng": "the Realized One is honored by gods and humans! ", "html": "<span class='verse-line'>{}</span>"}, "kp6:18.4": {"pli": "Saṅghaṁ namassāma suvatthi hotūti. ", "eng": "We bow to the Saṅgha! May you be safe! ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "kp6:19.1": {"pli": "Ratanasuttaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "kp7": {"kp7:0.1": {"pli": "Khuddakapāṭha 7 ", "eng": "Basic Passages 7 ", "html": "<article id='kp7'><header><ul><li class='division'>{}</li></ul>"}, "kp7:0.2": {"pli": "Tirokuṭṭasutta ", "eng": "Outside the Walls ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "kp7:1.1": {"pli": "Tirokuṭṭesu tiṭṭhanti, ", "eng": "Outside the walls they stand and wait, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "kp7:1.2": {"pli": "sandhisiṅghāṭakesu ca; ", "eng": "at the junctions and the crossroads. ", "html": "<span class='verse-line'>{}</span>"}, "kp7:1.3": {"pli": "Dvārabāhāsu tiṭṭhanti, ", "eng": "Returning to their former homes ", "html": "<span class='verse-line'>{}</span>"}, "kp7:1.4": {"pli": "āgantvāna sakaṁ gharaṁ. ", "eng": "they wait beside the door posts. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp7:2.1": {"pli": "Pahūte annapānamhi, ", "eng": "But when lavish food and drink ", "html": "<p><span class='verse-line'>{}</span>"}, "kp7:2.2": {"pli": "khajjabhojje upaṭṭhite; ", "eng": "of many kinds is set out, ", "html": "<span class='verse-line'>{}</span>"}, "kp7:2.3": {"pli": "Na tesaṁ koci sarati, ", "eng": "no-one remembers them at all, ", "html": "<span class='verse-line'>{}</span>"}, "kp7:2.4": {"pli": "sattānaṁ kammapaccayā. ", "eng": "because of those beings’s deeds. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp7:3.1": {"pli": "Evaṁ dadanti ñātīnaṁ, ", "eng": "That’s why those who have compassion ", "html": "<p><span class='verse-line'>{}</span>"}, "kp7:3.2": {"pli": "ye honti anukampakā; ", "eng": "give to their relatives ", "html": "<span class='verse-line'>{}</span>"}, "kp7:3.3": {"pli": "Suciṁ paṇītaṁ kālena, ", "eng": "food and drink at the right time, ", "html": "<span class='verse-line'>{}</span>"}, "kp7:3.4": {"pli": "kappiyaṁ pānabhojanaṁ. ", "eng": "that’s clean, delicious, and suitable. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp7:4.1": {"pli": "Idaṁ vo ñātīnaṁ hotu, ", "eng": "“May this be for our relatives! ", "html": "<p><span class='verse-line'>{}</span>"}, "kp7:4.2": {"pli": "sukhitā hontu ñātayo; ", "eng": "May our relatives be happy!” ", "html": "<span class='verse-line'>{}</span>"}, "kp7:4.3": {"pli": "Te ca tattha samāgantvā, ", "eng": "Those ghosts who have gathered there, ", "html": "<span class='verse-line'>{}</span>"}, "kp7:4.4": {"pli": "ñātipetā samāgatā. ", "eng": "the departed relatives who have come ", "html": "<span class='verse-line'>{}</span></p>"}, "kp7:5.1": {"pli": "Pahūte annapānamhi, ", "eng": "for the lavish food and drink ", "html": "<p><span class='verse-line'>{}</span>"}, "kp7:5.2": {"pli": "sakkaccaṁ anumodare; ", "eng": "gratefully express appreciation: ", "html": "<span class='verse-line'>{}</span>"}, "kp7:5.3": {"pli": "Ciraṁ jīvantu no ñātī, ", "eng": "“May our relatives live long! ", "html": "<span class='verse-line'>{}</span>"}, "kp7:5.4": {"pli": "yesaṁ hetu labhāmase. ", "eng": "For those to whom we owe this gain, ", "html": "<span class='verse-line'>{}</span></p>"}, "kp7:6.1": {"pli": "Amhākañca katā pūjā, ", "eng": "who have given honor to us, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp7:6.2": {"pli": "dāyakā ca anipphalā; ", "eng": "it will not be fruitless for the donor.” ", "html": "<span class='verse-line'>{}</span>"}, "kp7:6.3": {"pli": "Na hi tattha kasī atthi, ", "eng": "There is no farming there, ", "html": "<span class='verse-line'>{}</span>"}, "kp7:6.4": {"pli": "gorakkhettha na vijjati. ", "eng": "no cow pasture can be found; ", "html": "<span class='verse-line'>{}</span></p>"}, "kp7:7.1": {"pli": "Vaṇijjā tādisī natthi, ", "eng": "likewise there’s no trading, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp7:7.2": {"pli": "hiraññena kayākayaṁ; ", "eng": "and no commerce in gold coin. ", "html": "<span class='verse-line'>{}</span>"}, "kp7:7.3": {"pli": "Ito dinnena yāpenti, ", "eng": "The departed, the dead in that place ", "html": "<span class='verse-line'>{}</span>"}, "kp7:7.4": {"pli": "petā kālagatā tahiṁ. ", "eng": "live on what is given here. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp7:8.1": {"pli": "Unname udakaṁ vuṭṭhaṁ, ", "eng": "Just as water that rains on high ", "html": "<p><span class='verse-line'>{}</span>"}, "kp7:8.2": {"pli": "yathā ninnaṁ pavattati; ", "eng": "flows down to the plains, ", "html": "<span class='verse-line'>{}</span>"}, "kp7:8.3": {"pli": "Evamevaṁ ito dinnaṁ, ", "eng": "so too what is given here ", "html": "<span class='verse-line'>{}</span>"}, "kp7:8.4": {"pli": "petānaṁ upakappati. ", "eng": "aids the departed ghosts. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp7:9.1": {"pli": "Yathā vārivahā pūrā, ", "eng": "Just as the rivers full ", "html": "<p><span class='verse-line'>{}</span>"}, "kp7:9.2": {"pli": "paripūrenti sāgaraṁ; ", "eng": "swell the ocean seas ", "html": "<span class='verse-line'>{}</span>"}, "kp7:9.3": {"pli": "Evamevaṁ ito dinnaṁ, ", "eng": "so too what is given here ", "html": "<span class='verse-line'>{}</span>"}, "kp7:9.4": {"pli": "petānaṁ upakappati. ", "eng": "aids the departed ghosts. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp7:10.1": {"pli": "Adāsi me akāsi me, ", "eng": "Thinking: “They gave to me, they did for me, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp7:10.2": {"pli": "ñāti mittā sakhā ca me; ", "eng": "they were my family, friend, comrade”, ", "html": "<span class='verse-line'>{}</span>"}, "kp7:10.3": {"pli": "Petānaṁ dakkhiṇaṁ dajjā, ", "eng": "give offerings to departed kin, ", "html": "<span class='verse-line'>{}</span>"}, "kp7:10.4": {"pli": "pubbe katamanussaraṁ. ", "eng": "remembering past deeds. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp7:11.1": {"pli": "Na hi ruṇṇaṁ vā soko vā, ", "eng": "For neither tears nor grief ", "html": "<p><span class='verse-line'>{}</span>"}, "kp7:11.2": {"pli": "yā caññā paridevanā; ", "eng": "or other lamentations ", "html": "<span class='verse-line'>{}</span>"}, "kp7:11.3": {"pli": "Na taṁ petānamatthāya, ", "eng": "are of any use to the departed, ", "html": "<span class='verse-line'>{}</span>"}, "kp7:11.4": {"pli": "evaṁ tiṭṭhanti ñātayo. ", "eng": "so long as their relatives stay like this. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp7:12.1": {"pli": "Ayañca kho dakkhiṇā dinnā, ", "eng": "This offering that has been given, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp7:12.2": {"pli": "saṅghamhi suppatiṭṭhitā; ", "eng": "well placed in the Saṅgha, ", "html": "<span class='verse-line'>{}</span>"}, "kp7:12.3": {"pli": "Dīgharattaṁ hitāyassa, ", "eng": "is for their lasting welfare, ", "html": "<span class='verse-line'>{}</span>"}, "kp7:12.4": {"pli": "ṭhānaso upakappati. ", "eng": "and aids them right away. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp7:13.1": {"pli": "So ñātidhammo ca ayaṁ nidassito, ", "eng": "The relative’s duty has now been shown: ", "html": "<p><span class='verse-line'>{}</span>"}, "kp7:13.2": {"pli": "Petāna pūjā ca katā uḷārā; ", "eng": "how high honor to departed is performed, ", "html": "<span class='verse-line'>{}</span>"}, "kp7:13.3": {"pli": "Balañca bhikkhūnamanuppadinnaṁ, ", "eng": "how the mendicants can be kept strong, ", "html": "<span class='verse-line'>{}</span>"}, "kp7:13.4": {"pli": "Tumhehi puññaṁ pasutaṁ anappakanti. ", "eng": "and how no little merit is produced by you. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "kp7:14.1": {"pli": "Tirokuṭṭasuttaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "kp8": {"kp8:0.1": {"pli": "Khuddakapāṭha 8 ", "eng": "Basic Passages 8 ", "html": "<article id='kp8'><header><ul><li class='division'>{}</li></ul>"}, "kp8:0.2": {"pli": "Nidhikaṇḍasutta ", "eng": "A Treasure Trove ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "kp8:1.1": {"pli": "Nidhiṁ nidheti puriso, ", "eng": "A person stores away their savings ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "kp8:1.2": {"pli": "gambhīre odakantike; ", "eng": "in a deep trove by the water’s edge: ", "html": "<span class='verse-line'>{}</span>"}, "kp8:1.3": {"pli": "Atthe kicce samuppanne, ", "eng": "“When need arises ", "html": "<span class='verse-line'>{}</span>"}, "kp8:1.4": {"pli": "atthāya me bhavissati. ", "eng": "it will be there to help ", "html": "<span class='verse-line'>{}</span></p>"}, "kp8:2.1": {"pli": "Rājato vā duruttassa, ", "eng": "free me from rulers if I am slandered, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp8:2.2": {"pli": "corato pīḷitassa vā; ", "eng": "or from bandits if harassed, ", "html": "<span class='verse-line'>{}</span>"}, "kp8:2.3": {"pli": "Iṇassa vā pamokkhāya, ", "eng": "or to release me from debt, ", "html": "<span class='verse-line'>{}</span>"}, "kp8:2.4": {"pli": "dubbhikkhe āpadāsu vā; ", "eng": "or in case of famine or losses.” ", "html": "<span class='verse-line'>{}</span>"}, "kp8:2.5": {"pli": "Etadatthāya lokasmiṁ, ", "eng": "What the world calls savings ", "html": "<span class='verse-line'>{}</span>"}, "kp8:2.6": {"pli": "nidhi nāma nidhīyati. ", "eng": "get stored away for such reasons. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp8:3.1": {"pli": "Tāvassunihito santo, ", "eng": "But no matter how well stored away they are ", "html": "<p><span class='verse-line'>{}</span>"}, "kp8:3.2": {"pli": "gambhīre odakantike; ", "eng": "in a deep pit by the water’s edge, ", "html": "<span class='verse-line'>{}</span>"}, "kp8:3.3": {"pli": "Na sabbo sabbadā eva, ", "eng": "all their savings will fail ", "html": "<span class='verse-line'>{}</span>"}, "kp8:3.4": {"pli": "tassa taṁ upakappati. ", "eng": "to aid them all the time. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp8:4.1": {"pli": "Nidhi vā ṭhānā cavati, ", "eng": "For perhaps those savings are removed from there, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp8:4.2": {"pli": "saññā vāssa vimuyhati; ", "eng": "or they forget what marks the site, ", "html": "<span class='verse-line'>{}</span>"}, "kp8:4.3": {"pli": "Nāgā vā apanāmenti, ", "eng": "or dragons make off with them, ", "html": "<span class='verse-line'>{}</span>"}, "kp8:4.4": {"pli": "yakkhā vāpi haranti naṁ. ", "eng": "or spirits carry them away, ", "html": "<span class='verse-line'>{}</span></p>"}, "kp8:5.1": {"pli": "Appiyā vāpi dāyādā, ", "eng": "or unloved heirs ", "html": "<p><span class='verse-line'>{}</span>"}, "kp8:5.2": {"pli": "uddharanti apassato; ", "eng": "secretly unearth them. ", "html": "<span class='verse-line'>{}</span>"}, "kp8:5.3": {"pli": "Yadā puññakkhayo hoti, ", "eng": "When their merit is used up, ", "html": "<span class='verse-line'>{}</span>"}, "kp8:5.4": {"pli": "sabbametaṁ vinassati. ", "eng": "all of that will vanish. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp8:6.1": {"pli": "Yassa dānena sīlena, ", "eng": "But by giving and morality, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp8:6.2": {"pli": "saṁyamena damena ca; ", "eng": "restraint and self-control, ", "html": "<span class='verse-line'>{}</span>"}, "kp8:6.3": {"pli": "Nidhī sunihito hoti, ", "eng": "a woman or man ", "html": "<span class='verse-line'>{}</span>"}, "kp8:6.4": {"pli": "itthiyā purisassa vā. ", "eng": "keeps their savings safe. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp8:7.1": {"pli": "Cetiyamhi ca saṅghe vā, ", "eng": "At a shrine or with the Saṅgha, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp8:7.2": {"pli": "puggale atithīsu vā; ", "html": "<span class='verse-line'>{}</span>"}, "kp8:7.3": {"pli": "Mātari pitari cāpi, ", "eng": "with mother or father, ", "html": "<span class='verse-line'>{}</span>"}, "kp8:7.4": {"pli": "atho jeṭṭhamhi bhātari. ", "eng": "or else an elder sibling, ", "html": "<span class='verse-line'>{}</span></p>"}, "kp8:8.1": {"pli": "Eso nidhi sunihito, ", "eng": "those savings are kept safe, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp8:8.2": {"pli": "ajeyyo anugāmiko; ", "eng": "they stay with you, undecaying. ", "html": "<span class='verse-line'>{}</span>"}, "kp8:8.3": {"pli": "Pahāya gamanīyesu, ", "eng": "We must go on leaving all behind, ", "html": "<span class='verse-line'>{}</span>"}, "kp8:8.4": {"pli": "etaṁ ādāya gacchati. ", "eng": "only this you take when you go. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp8:9.1": {"pli": "Asādhāraṇamaññesaṁ, ", "eng": "You don’t have to share it with others, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp8:9.2": {"pli": "acorāharaṇo nidhi; ", "eng": "no thief makes off with your savings. ", "html": "<span class='verse-line'>{}</span>"}, "kp8:9.3": {"pli": "Kayirātha dhīro puññāni, ", "eng": "An attentive one would make merit, ", "html": "<span class='verse-line'>{}</span>"}, "kp8:9.4": {"pli": "yo nidhi anugāmiko. ", "eng": "the savings that stay with you. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp8:10.1": {"pli": "Esa devamanussānaṁ, ", "eng": "Such savings grant every desire ", "html": "<p><span class='verse-line'>{}</span>"}, "kp8:10.2": {"pli": "sabbakāmadado nidhi; ", "eng": "of gods and humans too. ", "html": "<span class='verse-line'>{}</span>"}, "kp8:10.3": {"pli": "Yaṁ yadevābhipatthenti, ", "eng": "Whatever it is that they wish for ", "html": "<span class='verse-line'>{}</span>"}, "kp8:10.4": {"pli": "sabbametena labbhati. ", "eng": "through this they have it all. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp8:11.1": {"pli": "Suvaṇṇatā susaratā, ", "eng": "Good looks, a sweet voice, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp8:11.2": {"pli": "susaṇṭhānā surūpatā; ", "eng": "a good shape, and good appearance, ", "html": "<span class='verse-line'>{}</span>"}, "kp8:11.3": {"pli": "Ādhipaccaparivāro, ", "eng": "leadership and followers: ", "html": "<span class='verse-line'>{}</span>"}, "kp8:11.4": {"pli": "sabbametena labbhati. ", "eng": "through this they have it all. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp8:12.1": {"pli": "Padesarajjaṁ issariyaṁ, ", "eng": "Sovereignty of a local kingdom, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp8:12.2": {"pli": "cakkavattisukhaṁ piyaṁ; ", "eng": "the happiness of a Wheel-Turning Monarch, ", "html": "<span class='verse-line'>{}</span>"}, "kp8:12.3": {"pli": "Devarajjampi dibbesu, ", "eng": "even kingship in the heavens: ", "html": "<span class='verse-line'>{}</span>"}, "kp8:12.4": {"pli": "sabbametena labbhati. ", "eng": "through this they have it all. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp8:13.1": {"pli": "Mānussikā ca sampatti, ", "eng": "Human success, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp8:13.2": {"pli": "devaloke ca yā rati; ", "eng": "heavenly delight, ", "html": "<span class='verse-line'>{}</span>"}, "kp8:13.3": {"pli": "Yā ca nibbānasampatti, ", "eng": "attaining extinguishment: ", "html": "<span class='verse-line'>{}</span>"}, "kp8:13.4": {"pli": "sabbametena labbhati. ", "eng": "through this they have it all. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp8:14.1": {"pli": "Mittasampadamāgamma, ", "eng": "Relying on having good friends, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp8:14.2": {"pli": "yonisova payuñjato; ", "eng": "rational application of effort, ", "html": "<span class='verse-line'>{}</span>"}, "kp8:14.3": {"pli": "Vijjāvimuttivasībhāvo, ", "eng": "mastery of knowledge and freedom: ", "html": "<span class='verse-line'>{}</span>"}, "kp8:14.4": {"pli": "sabbametena labbhati. ", "eng": "through this they have it all. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp8:15.1": {"pli": "Paṭisambhidā vimokkhā ca, ", "eng": "Analytical knowledge, the liberations, ", "html": "<p><span class='verse-line'>{}</span>", "comm": "The inclusion of <i lang='pi' translate='no'>paṭisambhidā</i> here with the late terms in the following lines reinforces the sense that this is a late term. Here it could refer either to the “analytical knowledge” of the elements (<a href='index.html?q=an1.595'>AN 1.595</a>), or the “analytical knowledge” of the text(<a href='index.html?q=an5.95'>AN 5.95</a>), in which case it is fourfold and should be translated as plural. "}, "kp8:15.2": {"pli": "yā ca sāvakapāramī; ", "eng": "the perfections of the disciple, ", "html": "<span class='verse-line'>{}</span>"}, "kp8:15.3": {"pli": "Paccekabodhi buddhabhūmi, ", "eng": "the plane of an independent Buddha: ", "html": "<span class='verse-line'>{}</span>"}, "kp8:15.4": {"pli": "sabbametena labbhati. ", "eng": "through this they have it all. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp8:16.1": {"pli": "Evaṁ mahatthikā esā, ", "eng": "This accomplishment in merit ", "html": "<p><span class='verse-line'>{}</span>"}, "kp8:16.2": {"pli": "yadidaṁ puññasampadā; ", "eng": "is so very beneficial. ", "html": "<span class='verse-line'>{}</span>"}, "kp8:16.3": {"pli": "Tasmā dhīrā pasaṁsanti, ", "eng": "That’s why the attentive and the astute ", "html": "<span class='verse-line'>{}</span>"}, "kp8:16.4": {"pli": "paṇḍitā katapuññatanti. ", "eng": "praise the making of merit. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "kp8:17.1": {"pli": "Nidhikaṇḍasuttaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "kp9": {"kp9:0.1": {"pli": "Khuddakapāṭha 9 ", "eng": "Basic Passages 9 ", "html": "<article id='kp9'><header><ul><li class='division'>{}</li></ul>"}, "kp9:0.2": {"pli": "Mettasutta ", "eng": "The Discourse on Love ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "kp9:1.1": {"pli": "Karaṇīyamatthakusalena, ", "eng": "Those who are skilled in the meaning of scripture ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "kp9:1.2": {"pli": "Yanta santaṁ padaṁ abhisamecca; ", "eng": "should practice like this so as to realize the state of peace. ", "html": "<span class='verse-line'>{}</span>"}, "kp9:1.3": {"pli": "Sakko ujū ca suhujū ca, ", "eng": "Let them be capable and upright, very upright, ", "html": "<span class='verse-line'>{}</span>"}, "kp9:1.4": {"pli": "Sūvaco cassa mudu anatimānī. ", "eng": "easy to speak to, gentle and humble; ", "html": "<span class='verse-line'>{}</span></p>"}, "kp9:2.1": {"pli": "Santussako ca subharo ca, ", "eng": "content and unburdensome, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp9:2.2": {"pli": "Appakicco ca sallahukavutti; ", "eng": "unbusied, living lightly, ", "html": "<span class='verse-line'>{}</span>"}, "kp9:2.3": {"pli": "Santindriyo ca nipako ca, ", "eng": "alert, with senses calmed, ", "html": "<span class='verse-line'>{}</span>"}, "kp9:2.4": {"pli": "Appagabbho kulesvananugiddho. ", "eng": "courteous, not fawning on families. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp9:3.1": {"pli": "Na ca khuddamācare kiñci, ", "eng": "Let them not do the slightest thing ", "html": "<p><span class='verse-line'>{}</span>"}, "kp9:3.2": {"pli": "Yena viññū pare upavadeyyuṁ; ", "eng": "that others who are wise would blame. ", "html": "<span class='verse-line'>{}</span>"}, "kp9:3.3": {"pli": "Sukhino va khemino hontu, ", "eng": "May they be happy and safe! ", "html": "<span class='verse-line'>{}</span>"}, "kp9:3.4": {"pli": "Sabbasattā bhavantu sukhitattā. ", "eng": "May all beings be happy! ", "html": "<span class='verse-line'>{}</span></p>"}, "kp9:4.1": {"pli": "Ye keci pāṇabhūtatthi, ", "eng": "Whatever living creatures there are ", "html": "<p><span class='verse-line'>{}</span>"}, "kp9:4.2": {"pli": "Tasā vā thāvarā vanavasesā; ", "eng": "with not a one left out—", "html": "<span class='verse-line'>{}</span>"}, "kp9:4.3": {"pli": "Dīghā vā ye va mahantā, ", "eng": "frail or firm, long or large, ", "html": "<span class='verse-line'>{}</span>"}, "kp9:4.4": {"pli": "Majjhimā rassakā aṇukathūlā. ", "eng": "medium, small, tiny or round, ", "html": "<span class='verse-line'>{}</span></p>"}, "kp9:5.1": {"pli": "Diṭṭhā vā ye va adiṭṭhā, ", "eng": "seen or unseen, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp9:5.2": {"pli": "Ye va dūre vasanti avidūre; ", "eng": "living far or near, ", "html": "<span class='verse-line'>{}</span>"}, "kp9:5.3": {"pli": "Bhūtā va sambhavesī va, ", "eng": "those who have been born <j>and those about to be born—", "html": "<span class='verse-line'>{}</span>"}, "kp9:5.4": {"pli": "Sabbasattā bhavantu sukhitattā. ", "eng": "may all beings be happy! ", "html": "<span class='verse-line'>{}</span></p>"}, "kp9:6.1": {"pli": "Na paro paraṁ nikubbetha, ", "eng": "Let none deceive another, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp9:6.2": {"pli": "Nātimaññetha katthaci na kañci; ", "eng": "nor look down on anyone anywhere. ", "html": "<span class='verse-line'>{}</span>"}, "kp9:6.3": {"pli": "Byārosanā paṭighasañña, ", "eng": "Though provoked or aggrieved, ", "html": "<span class='verse-line'>{}</span>"}, "kp9:6.4": {"pli": "Nāññamaññassa dukkhamiccheyya. ", "eng": "let them not wish pain on each other. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp9:7.1": {"pli": "Mātā yathā niyaṁ puttam ", "eng": "Even as a mother would protect with her life ", "html": "<p><span class='verse-line'>{}</span>"}, "kp9:7.2": {"pli": "Āyusā ekaputtamanurakkhe; ", "eng": "her child, her only child, ", "html": "<span class='verse-line'>{}</span>"}, "kp9:7.3": {"pli": "Evampi sabbabhūtesu, ", "eng": "so too for all creatures ", "html": "<span class='verse-line'>{}</span>"}, "kp9:7.4": {"pli": "Mānasaṁ bhāvaye aparimāṇaṁ. ", "eng": "unfold a boundless heart. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp9:8.1": {"pli": "Mettañca sabbalokasmi, ", "eng": "With love for the whole world, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp9:8.2": {"pli": "Mānasaṁ bhāvaye aparimāṇaṁ; ", "eng": "unfold a boundless heart: ", "html": "<span class='verse-line'>{}</span>"}, "kp9:8.3": {"pli": "Uddhaṁ adho ca tiriyañca, ", "eng": "above, below, all round, ", "html": "<span class='verse-line'>{}</span>"}, "kp9:8.4": {"pli": "Asambādhaṁ averamasapattaṁ. ", "eng": "unconstricted, without enmity or foe. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp9:9.1": {"pli": "Tiṭṭhaṁ caraṁ nisinno va, ", "eng": "When standing, walking, sitting, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp9:9.2": {"pli": "Sayāno yāvatāssa vitamiddho; ", "eng": "or lying down while yet unweary, ", "html": "<span class='verse-line'>{}</span>"}, "kp9:9.3": {"pli": "Etaṁ satiṁ adhiṭṭheyya, ", "eng": "keep this ever in mind; ", "html": "<span class='verse-line'>{}</span>"}, "kp9:9.4": {"pli": "Brahmametaṁ vihāramidhamāhu. ", "eng": "for this, they say, is a divine meditation in this life. ", "html": "<span class='verse-line'>{}</span></p>"}, "kp9:10.1": {"pli": "Diṭṭhiñca anupaggamma, ", "eng": "Avoiding harmful views, ", "html": "<p><span class='verse-line'>{}</span>"}, "kp9:10.2": {"pli": "Sīlavā dassanena sampanno; ", "eng": "virtuous, accomplished in insight, ", "html": "<span class='verse-line'>{}</span>"}, "kp9:10.3": {"pli": "Kāmesu vinaya gedhaṁ, ", "eng": "with desire for sensual pleasures dispelled, ", "html": "<span class='verse-line'>{}</span>"}, "kp9:10.4": {"pli": "Na hi jātuggabbhaseyya punaretīti. ", "eng": "they never return to a womb again. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "kp9:11.1": {"pli": "Mettasuttaṁ. ", "html": "<p class='endsutta'>{}</p>"}, "kp9:12.1": {"pli": "Khuddakapāṭhapāḷi niṭṭhitā. ", "html": "<p class='endbook'>{}</p></article>"}}}};