window.SUTTA_DB['sutta_kn_pe'] = {"id": "pe", "title": "Interpretation of the Canon", "structure": {"pe": ["pe1", "pe2", "pe3", "pe4", "pe5", "pe6", "pe7", "pe8", "pe9"]}, "meta": {"pe": {"type": "branch", "acronym": "Pe", "translated_title": "Interpretation of the Canon", "original_title": "Peṭakopadesa", "blurb": "The Peṭakopadesa or “Illustration of the Baskets” is a systematic treatise intended to provide a guide for reading and interpreting the texts of the <i lang='pi' translate='no'>nikāyas</i>. It is similar to the Netti, and the exact relationship between these two texts is not clear. The text provides an array of methods for reading texts, illustrating them with quotations, and taking the four noble truths as the overall framework. The Pali text of the Peṭakopadesa is quite corrupt, which is unusual in the Pali canon. A colophon in the text attributes it to the Buddha's disciple Kaccāna. It is, however, a late text, lacking counterparts in the northern collections. It is not universally accepted as canonical; SuttaCentral’s Pali texts, however, stem from the Mahāsaṅgīti edition, which as a Burmese text does include it.", "author_uid": null, "scroll_target": null}, "pe1": {"type": "leaf", "acronym": "Pe 1", "translated_title": "I. The Display of the Noble Truths", "original_title": "Ariyasaccappakāsanapaṭhamabhūmi", "blurb": null, "author_uid": null, "scroll_target": null}, "pe2": {"type": "leaf", "acronym": "Pe 2", "translated_title": "II. The Pattern of the Dispensation", "original_title": "Sāsanapaṭṭhānadutiyabhūmi", "blurb": null, "author_uid": null, "scroll_target": null}, "pe3": {"type": "leaf", "acronym": "Pe 3", "translated_title": "III. Terms of Expression in the Thread", "original_title": "Suttādhiṭṭhānatatiyabhūmi", "blurb": null, "author_uid": null, "scroll_target": null}, "pe4": {"type": "leaf", "acronym": "Pe 4", "translated_title": "IV. Investigation of Threads", "original_title": "Suttavicayacatutthabhūmi", "blurb": null, "author_uid": null, "scroll_target": null}, "pe5": {"type": "leaf", "acronym": "Pe 5", "translated_title": "V. 16 Modes of Conveying in Separate Treatment", "original_title": "Hāravibhaṅgapañcamabhūmi", "blurb": null, "author_uid": null, "scroll_target": null}, "pe6": {"type": "leaf", "acronym": "Pe 6", "translated_title": "VI. Compendium of the Thread's Meaning", "original_title": "Suttatthasamuccayabhūmi", "blurb": null, "author_uid": null, "scroll_target": null}, "pe7": {"type": "leaf", "acronym": "Pe 7", "translated_title": "VI. Compendium of the Thread's Meaning (continued)", "original_title": "Pakiṇṇakaniddesa", "blurb": null, "author_uid": null, "scroll_target": null}, "pe8": {"type": "leaf", "acronym": "Pe 8", "translated_title": "VI. Compendium of the Thread's Meaning (continued)", "original_title": "Hārasampātabhūmi", "blurb": null, "author_uid": null, "scroll_target": null}, "pe9": {"type": "leaf", "acronym": "Pe 9", "translated_title": "VIII. The Moulding of the Guide-Lines", "original_title": "Suttavebhaṅgiya", "blurb": null, "author_uid": null, "scroll_target": null}}, "content": {"pe1": {"pe1:0.1": {"pli": "Peṭakopadesa ", "html": "<article id='pe1'><header><ul><li class='division'>{}</li></ul>"}, "pe1:0.2": {"pli": "1. Ariyasaccappakāsanapaṭhamabhūmi ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "pe1:1.1": {"pli": "Namo tassa Bhagavato Arahato Sammāsambuddhassa. ", "html": "<p class='namo'>{}</p>"}, "pe1:2.1": {"pli": "Namo sammāsambuddhānaṁ paramatthadassīnaṁ sīlādiguṇapāramippattānaṁ. ", "html": "<p class='namo'>{}</p>"}, "pe1:3.1": {"pli": "Duve hetū duve paccayā sāvakassa sammādiṭṭhiyā uppādāya—", "html": "<p>{}"}, "pe1:3.2": {"pli": "parato ca ghoso saccānusandhi, ajjhattañca yoniso manasikāro. ", "html": "{}"}, "pe1:3.3": {"pli": "Tattha katamo parato ghoso? ", "html": "{}"}, "pe1:3.4": {"pli": "Yā parato desanā ovādo anusāsanī saccakathā saccānulomo. ", "html": "{}"}, "pe1:3.5": {"pli": "Cattāri saccāni—", "html": "{}"}, "pe1:3.6": {"pli": "dukkhaṁ samudayo nirodho maggo. ", "html": "{}"}, "pe1:3.7": {"pli": "Imesaṁ catunnaṁ saccānaṁ yā desanā sandassanā vivaraṇā vibhajanā uttānīkiriyā pakāsanā—", "html": "{}"}, "pe1:3.8": {"pli": "ayaṁ vuccati saccānulomo ghosoti. ", "html": "{}</p>"}, "pe1:4.1": {"pli": "Tattha katamo ajjhattaṁ yoniso manasikāro? ", "html": "<p>{}</p>"}, "pe1:5.1": {"pli": "Ajjhattaṁ yoniso manasikāro nāma yo yathādesite dhamme bahiddhā ārammaṇaṁ anabhinīharitvā yoniso manasikāro—", "html": "<p>{}"}, "pe1:5.2": {"pli": "ayaṁ vuccati yoniso manasikāro. ", "html": "{}</p>"}, "pe1:6.1": {"pli": "Taṁākāro yoniso dvāro vidhi upāyo. ", "html": "<p>{}"}, "pe1:6.2": {"pli": "Yathā puriso sukkhe kaṭṭhe vigatasnehe sukkhāya uttarāraṇiyā thale abhimanthamānaṁ bhabbo jotissa adhigamāya. ", "html": "{}"}, "pe1:6.3": {"pli": "Taṁ kissa hetu. ", "html": "{}"}, "pe1:6.4": {"pli": "Yoniso aggissa adhigamāya. ", "html": "{}"}, "pe1:6.5": {"pli": "Evamevassa yamidaṁ dukkhasamudayanirodhamaggānaṁ aviparītadhammadesanaṁ manasikaroti—", "html": "{}"}, "pe1:6.6": {"pli": "ayaṁ vuccati yoniso manasikāro. ", "html": "{}</p>"}, "pe1:7.1": {"pli": "Yathā tisso upamā pubbe assutā ca assutapubbā ca paṭibhanti. ", "html": "<p>{}"}, "pe1:7.2": {"pli": "Yo hi koci kāmesu avītarāgoti …pe… ", "html": "{}"}, "pe1:7.3": {"pli": "duve upamā ayoniso kātabbā pacchimesu vuttaṁ. ", "html": "{}"}, "pe1:7.4": {"pli": "Tattha yo ca parato ghoso yo ca ajjhattaṁ yoniso manasikāro—", "html": "{}"}, "pe1:7.5": {"pli": "ime dve paccayā. ", "html": "{}"}, "pe1:7.6": {"pli": "Parato ghosena yā uppajjati paññā—", "html": "{}"}, "pe1:7.7": {"pli": "ayaṁ vuccati sutamayī paññā. ", "html": "{}"}, "pe1:7.8": {"pli": "Yā ajjhattaṁ yoniso manasikārena uppajjati paññā—", "html": "{}"}, "pe1:7.9": {"pli": "ayaṁ vuccati cintāmayī paññāti. ", "html": "{}"}, "pe1:7.10": {"pli": "Imā dve paññā veditabbā. ", "html": "{}"}, "pe1:7.11": {"pli": "Purimakā ca dve paccayā. ", "html": "{}"}, "pe1:7.12": {"pli": "Ime dve hetū dve paccayā sāvakassa sammādiṭṭhiyā uppādāya. ", "html": "{}</p>"}, "pe1:8.1": {"pli": "Tattha parato ghosassa saccānusandhissa desitassa atthaṁ avijānanto atthappaṭisaṁvedī bhavissatīti netaṁ ṭhānaṁ vijjati. ", "html": "<p>{}"}, "pe1:8.2": {"pli": "Na ca atthappaṭisaṁvedī yoniso manasikarissatīti netaṁ ṭhānaṁ vijjati. ", "html": "{}"}, "pe1:8.3": {"pli": "Parato ghosassa saccānusandhissa desitassa atthaṁ vijānanto atthappaṭisaṁvedī bhavissatīti ṭhānametaṁ vijjati. ", "html": "{}"}, "pe1:8.4": {"pli": "Atthappaṭisaṁvedī ca yoniso manasikarissatīti ṭhānametaṁ vijjati. ", "html": "{}"}, "pe1:8.5": {"pli": "Esa hetu etaṁ ārammaṇaṁ eso upāyo sāvakassa niyyānassa, natthañño. ", "html": "{}"}, "pe1:8.6": {"pli": "Soyaṁ na ca suttassa atthavijānanāya saha yutto nāpi ghosānuyogena parato ghosassa atthaṁ avijānantena sakkā uttari manussadhammaṁ alamariyañāṇadassanaṁ adhigantuṁ, tasmā nibbāyitukāmena sutamayena atthā pariyesitabbā. ", "html": "{}"}, "pe1:8.7": {"pli": "Tattha pariyesanāya ayaṁ anupubbī bhavati soḷasa hārā, pañca nayā, aṭṭhārasa mūlapadāni. ", "html": "{}</p>"}, "pe1:9.1": {"pli": "Tatthāyaṁ uddānagāthā ", "html": "<p class='uddana-intro'>{}</p>"}, "pe1:10.1": {"pli": "Soḷasahārā nettī, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "pe1:10.2": {"pli": "Pañcanayā sāsanassa pariyeṭṭhi; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:10.3": {"pli": "Aṭṭhārasamūlapadā, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:10.4": {"pli": "Kaccāyanagottaniddiṭṭhā. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:11.1": {"pli": "Tattha katame <b>soḷasahārā</b>? ", "html": "<p>{}</p>"}, "pe1:12.1": {"pli": "Desanā vicayo yutti padaṭṭhānaṁ lakkhaṇaṁ catubyūho āvaṭṭo vibhatti parivattano vevacano paññatti otaraṇo sodhano adhiṭṭhāno parikkhāro samāropano—", "html": "<p>{}"}, "pe1:12.2": {"pli": "ime soḷasa hārā. ", "html": "{}</p>"}, "pe1:13.1": {"pli": "Tattha uddānagāthā ", "html": "<p class='uddana-intro'>{}</p>"}, "pe1:14.1": {"pli": "Desanā vicayo yutti, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "pe1:14.2": {"pli": "padaṭṭhāno ca lakkhaṇo; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:14.3": {"pli": "Catubyūho ca āvaṭṭo, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:14.4": {"pli": "vibhatti parivattano. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe1:15.1": {"pli": "Vevacano ca paññatti, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe1:15.2": {"pli": "otaraṇo ca sodhano; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:15.3": {"pli": "Adhiṭṭhāno parikkhāro, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:15.4": {"pli": "samāropano soḷaso. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:16.1": {"pli": "Tattha katame <b>pañca nayā</b>? ", "html": "<p>{}</p>"}, "pe1:17.1": {"pli": "Nandiyāvaṭṭo tipukkhalo sīhavikkīḷito disālocano aṅkusoti. ", "html": "<p>{}</p>"}, "pe1:18.1": {"pli": "Tattha uddānagāthā ", "html": "<p class='uddana-intro'>{}</p>"}, "pe1:19.1": {"pli": "Paṭhamo nandiyāvaṭṭo, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "pe1:19.2": {"pli": "dutiyo ca tipukkhalo; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:19.3": {"pli": "Sīhavikkīḷito nāma, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:19.4": {"pli": "tatiyo hoti so nayo. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe1:20.1": {"pli": "Disālocanamāhaṁsu, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe1:20.2": {"pli": "catuttho nayalañjako; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:20.3": {"pli": "Pañcamo aṅkuso nāma, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:20.4": {"pli": "sabbe pañca nayā gatā. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:21.1": {"pli": "Tattha katamāni <b>aṭṭhārasa mūlapadāni</b>? ", "html": "<p>{}</p>"}, "pe1:22.1": {"pli": "Avijjā taṇhā lobho doso moho subhasaññā sukhasaññā niccasaññā attasaññā samatho vipassanā alobho adoso amoho asubhasaññā dukkhasaññā aniccasaññā anattasaññā, imāni aṭṭhārasa mūlapadāni. ", "html": "<p>{}"}, "pe1:22.2": {"pli": "Tattha nava padāni akusalāni yattha sabbaṁ akusalaṁ samosarati. ", "html": "{}"}, "pe1:22.3": {"pli": "Nava padāni kusalāni yattha sabbaṁ kusalaṁ samosarati. ", "html": "{}</p>"}, "pe1:23.1": {"pli": "Katamāni nava padāni akusalāni yattha sabbaṁ akusalaṁ samosarati? ", "html": "<p>{}</p>"}, "pe1:24.1": {"pli": "Avijjā yāva attasaññā, imāni nava padāni akusalāni, yattha sabbaṁ akusalaṁ samosarati. ", "html": "<p>{}</p>"}, "pe1:25.1": {"pli": "Katamāni nava padāni kusalāni yattha sabbaṁ kusalaṁ samosarati? ", "html": "<p>{}</p>"}, "pe1:26.1": {"pli": "Samatho yāva anattasaññā, imāni nava padāni kusalāni yattha sabbaṁ kusalaṁ samosarati. ", "html": "<p>{}"}, "pe1:26.2": {"pli": "Imāni aṭṭhārasa mūlapadāni. ", "html": "{}</p>"}, "pe1:27.1": {"pli": "Tattha imā uddānagāthā ", "html": "<p class='uddana-intro'>{}</p>"}, "pe1:28.1": {"pli": "Taṇhā ca avijjā lobho, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "pe1:28.2": {"pli": "Doso tatheva moho ca; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:28.3": {"pli": "Cattāro ca vipallāsā, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:28.4": {"pli": "Kilesabhūmi nava padāni. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe1:29.1": {"pli": "Ye ca satipaṭṭhānā samatho, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe1:29.2": {"pli": "Vipassanā kusalamūlaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:29.3": {"pli": "Etaṁ sabbaṁ kusalaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:29.4": {"pli": "Indriyabhūmi navapadāni. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe1:30.1": {"pli": "Sabbaṁ kusalaṁ navahi padehi yujjati, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe1:30.2": {"pli": "Navahi ceva akusalaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:30.3": {"pli": "Ekake nava mūlapadāni, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:30.4": {"pli": "Ubhayato aṭṭhārasa mūlapadāni. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:31.1": {"pli": "Imesaṁ aṭṭhārasannaṁ mūlapadānaṁ yāni nava padāni akusalāni, ayaṁ dukkhasamudayo; ", "html": "<p>{}"}, "pe1:31.2": {"pli": "yāni nava padāni kusalāni, ayaṁ dukkhanirodhagāminī paṭipadā. ", "html": "{}"}, "pe1:31.3": {"pli": "Iti samudayassa dukkhaṁ phalaṁ, dukkhanirodhagāminiyā paṭipadāya nirodhaṁ phalaṁ. ", "html": "{}"}, "pe1:31.4": {"pli": "Imāni cattāri ariyasaccāni bhagavatā bārāṇasiyaṁ desitāni. ", "html": "{}</p>"}, "pe1:32.1": {"pli": "Tattha dukkhassa ariyasaccassa aparimāṇāni akkharāni padāni byañjanāni ākārāni niruttiyo niddesā desitā etassevatthassa saṅkāsanāya pakāsanāya vivaraṇāya vibhajanāya uttānīkammatāya paññāpanāyāti. ", "html": "<p>{}"}, "pe1:32.2": {"pli": "Yā evaṁ sabbesaṁ saccānaṁ iti ekamekaṁ saccaṁ aparimāṇehi akkharapadabyañjanaākāraniruttiniddesehi pariyesitabbaṁ, tañca byañjanaṁ atthaputhuttena pana attheva byañjanaputhuttena. ", "html": "{}</p>"}, "pe1:33.1": {"pli": "Yo hi koci samaṇo vā brāhmaṇo vā evaṁ vadeyya “ahaṁ idaṁ dukkhaṁ paccakkhāya aññaṁ dukkhaṁ paññapessāmī”ti tassa taṁ vācāvatthukamevassa pucchito ca na sampāyissati. ", "html": "<p>{}"}, "pe1:33.2": {"pli": "Evaṁ saccāni. ", "html": "{}"}, "pe1:33.3": {"pli": "Yañca rattiṁ bhagavā abhisambuddho, yañca rattiṁ anupādāya parinibbuto, etthantare yaṁ kiñci bhagavatā bhāsitaṁ suttaṁ geyyaṁ veyyākaraṇaṁ gāthā udānaṁ itivuttakaṁ jātakaṁ abbhutadhammaṁ vedallaṁ, sabbaṁ taṁ dhammacakkaṁ pavattitaṁ. ", "html": "{}"}, "pe1:33.4": {"pli": "Na kiñci buddhānaṁ bhagavantānaṁ dhammadesanāya dhammacakkato bahiddhā, tassa sabbaṁ suttaṁ ariyadhammesu pariyesitabbaṁ. ", "html": "{}"}, "pe1:33.5": {"pli": "Tattha pariggaṇhanāya ālokasabhāni cattāri ariyasaccāni thāvarāni imāni. ", "html": "{}</p>"}, "pe1:34.1": {"pli": "Tattha katamaṁ <b>dukkhaṁ</b>? ", "html": "<p>{}"}, "pe1:34.2": {"pli": "Jāti jarā byādhi maraṇaṁ saṅkhittena pañcupādānakkhandhā dukkhā. ", "html": "{}"}, "pe1:34.3": {"pli": "Tatthāyaṁ lakkhaṇaniddeso, pātubhāvalakkhaṇā jāti, paripākalakkhaṇā jarā, dukkhadukkhatālakkhaṇo byādhi, cutilakkhaṇaṁ maraṇaṁ, piyavippayogavipariṇāmaparitāpanalakkhaṇo soko, lālappanalakkhaṇo paridevo, kāyasampīḷanalakkhaṇaṁ dukkhaṁ, cittasampīḷanalakkhaṇaṁ domanassaṁ, kilesaparidahanalakkhaṇo upāyāso, amanāpasamodhānalakkhaṇo appiyasampayogo, manāpavinābhāvalakkhaṇo piyavippayogo, adhippāyavivattanalakkhaṇo alābho, apariññālakkhaṇā pañcupādānakkhandhā, paripākacutilakkhaṇaṁ jarāmaraṇaṁ, pātubhāvacutilakkhaṇaṁ cutopapatti, paṭisandhinibbattanalakkhaṇo samudayo, samudayaparijahanalakkhaṇo nirodho, anusayasamucchedalakkhaṇo maggo. ", "html": "{}"}, "pe1:34.4": {"pli": "Byādhilakkhaṇaṁ dukkhaṁ, sañjānanalakkhaṇo samudayo, niyyānikalakkhaṇo maggo, santilakkhaṇo nirodho. ", "html": "{}"}, "pe1:34.5": {"pli": "Appaṭisandhibhāvanirodhalakkhaṇā anupādisesā nibbānadhātu, dukkhañca samudayo ca, dukkhañca nirodho ca, dukkhañca maggo ca, samudayo ca dukkhañca, samudayo ca nirodho ca, samudayo ca maggo ca, nirodho ca samudayo ca, nirodho ca dukkhañca, nirodho ca maggo ca, maggo ca nirodho ca, maggo ca samudayo ca, maggo ca dukkhañca. ", "html": "{}</p>"}, "pe1:35.1": {"pli": "Tatthimāni suttāni. ", "html": "<p>{}</p>"}, "pe1:36.1": {"pli": "“Yamekarattiṁ paṭhamaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:36.2": {"pli": "gabbhe vasati māṇavo; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:36.3": {"pli": "Abbhuṭṭhitova so yāti, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:36.4": {"pli": "sa gacchaṁ na nivattatī”ti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:37.1": {"pli": "Aṭṭhimā, ānanda, dānūpapattiyo ekuttarike suttaṁ—", "html": "<p>{}"}, "pe1:37.2": {"pli": "ayaṁ jāti. ", "html": "{}</p>"}, "pe1:38.1": {"pli": "Tattha katamā jarā? ", "html": "<p>{}</p>"}, "pe1:39.1": {"pli": "“Acaritvā brahmacariyaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:39.2": {"pli": "aladdhā yobbane dhanaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:39.3": {"pli": "Jiṇṇakoñcāva jhāyanti, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:39.4": {"pli": "khīṇamaccheva pallale”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:40.1": {"pli": "Pañca pubbanimittāni devesu—", "html": "<p>{}"}, "pe1:40.2": {"pli": "ayaṁ jarā. ", "html": "{}</p>"}, "pe1:41.1": {"pli": "Tattha katamo byādhi? ", "html": "<p>{}</p>"}, "pe1:42.1": {"pli": "Sāmaṁ tena kuto rāja, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:42.2": {"pli": "Tuvampi jarāyanti vedesi; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:42.3": {"pli": "Khattiya kammassa phalo, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:42.4": {"pli": "Loko na hi kammaṁ panayati. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:43.1": {"pli": "Tayo gilānā—", "html": "<p>{}"}, "pe1:43.2": {"pli": "ayaṁ byādhi. ", "html": "{}</p>"}, "pe1:44.1": {"pli": "Tattha katamaṁ maraṇaṁ? ", "html": "<p>{}</p>"}, "pe1:45.1": {"pli": "Yathāpi kumbhakārassa, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:45.2": {"pli": "kataṁ mattikabhājanaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:45.3": {"pli": "Khuddakañca mahantañca, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:45.4": {"pli": "yaṁ pakkaṁ yañca āmakaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:45.5": {"pli": "Sabbaṁ bhedanapariyantaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:45.6": {"pli": "evaṁ maccāna jīvitaṁ. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe1:46.1": {"pli": "Mamāyite passatha phandamāne, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe1:46.2": {"pli": "Maccheva appodake khīṇasote; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:46.3": {"pli": "Etampi disvā amamo careyya, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:46.4": {"pli": "Bhavesu āsattimakubbamāno. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:47.1": {"pli": "Udakappanasuttaṁ—", "html": "<p>{}"}, "pe1:47.2": {"pli": "idaṁ maraṇaṁ. ", "html": "{}</p>"}, "pe1:48.1": {"pli": "Tattha katamo soko? ", "html": "<p>{}</p>"}, "pe1:49.1": {"pli": "Idha socati pecca socati, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:49.2": {"pli": "Pāpakārī ubhayattha socati; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:49.3": {"pli": "So socati so vihaññati, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:49.4": {"pli": "Disvā kammakiliṭṭhamattano. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:50.1": {"pli": "Tīṇi duccaritāni—", "html": "<p>{}"}, "pe1:50.2": {"pli": "ayaṁ soko. ", "html": "{}</p>"}, "pe1:51.1": {"pli": "Tattha katamo paridevo? ", "html": "<p>{}</p>"}, "pe1:52.1": {"pli": "Kāmesu giddhā pasutā pamūḷhā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:52.2": {"pli": "Avadāniyā te visame niviṭṭhā; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:52.3": {"pli": "Dukkhūpanītā paridevayanti, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:52.4": {"pli": "Kiṁsu bhavissāma ito cutāse. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:53.1": {"pli": "Tisso vipattiyo—", "html": "<p>{}"}, "pe1:53.2": {"pli": "ayaṁ paridevo. ", "html": "{}</p>"}, "pe1:54.1": {"pli": "Tattha katamaṁ dukkhaṁ? ", "html": "<p>{}</p>"}, "pe1:55.1": {"pli": "Sataṁ āsi ayosaṅkū, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:55.2": {"pli": "sabbe paccattavedanā; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:55.3": {"pli": "Jalitā jātavedāva, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:55.4": {"pli": "accisaṅghasamākulā. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:56.1": {"pli": "Mahā vata so pariḷāho saṁyuttake suttaṁ saccasaṁyuttesu—", "html": "<p>{}"}, "pe1:56.2": {"pli": "idaṁ dukkhaṁ. ", "html": "{}</p>"}, "pe1:57.1": {"pli": "Tattha katamaṁ domanassaṁ? ", "html": "<p>{}</p>"}, "pe1:58.1": {"pli": "Saṅkappehi pareto so, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:58.2": {"pli": "kapaṇo viya jhāyati; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:58.3": {"pli": "Sutvā paresaṁ nigghosaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:58.4": {"pli": "maṅku hoti tathāvidho. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:59.1": {"pli": "Dveme tapanīyā dhammā—", "html": "<p>{}"}, "pe1:59.2": {"pli": "idaṁ domanassaṁ. ", "html": "{}</p>"}, "pe1:60.1": {"pli": "Tattha katamo upāyāso? ", "html": "<p>{}</p>"}, "pe1:61.1": {"pli": "Kammārānaṁ yathā ukkā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:61.2": {"pli": "anto ḍayhati no bahi; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:61.3": {"pli": "Evaṁ ḍayhati me hadayaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:61.4": {"pli": "sutvā nibbattamambujaṁ. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:62.1": {"pli": "Tayo aggī—", "html": "<p>{}"}, "pe1:62.2": {"pli": "ayaṁ upāyāso. ", "html": "{}</p>"}, "pe1:63.1": {"pli": "Tattha katamo appiyasampayogo? ", "html": "<p>{}</p>"}, "pe1:64.1": {"pli": "Ayasāva malaṁ samuṭṭhitaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:64.2": {"pli": "Tatuṭṭhāya tameva khādati; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:64.3": {"pli": "Evaṁ atidhonacārinaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:64.4": {"pli": "Sāni kammāni nayanti duggatiṁ. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:65.1": {"pli": "Dveme tathāgataṁ abbhācikkhanti, ekuttarike suttaṁ dukesu—", "html": "<p>{}"}, "pe1:65.2": {"pli": "ayaṁ appiyasampayogo. ", "html": "{}</p>"}, "pe1:66.1": {"pli": "Tattha katamo piyavippayogo? ", "html": "<p>{}</p>"}, "pe1:67.1": {"pli": "Supinena yathāpi saṅgataṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:67.2": {"pli": "Paṭibuddho puriso na passati; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:67.3": {"pli": "Evampi piyāyitaṁ janaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:67.4": {"pli": "Petaṁ kālaṅkataṁ na passati. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:68.1": {"pli": "Te devā cavanadhammaṁ viditvā tīhi vācāhi anusāsanti. ", "html": "<p>{}"}, "pe1:68.2": {"pli": "Ayaṁ piyavippayogo. ", "html": "{}</p>"}, "pe1:69.1": {"pli": "Yampicchaṁ na labhati, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:69.2": {"pli": "tisso māradhītaro; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:69.3": {"pli": "Tassa ce kāmayānassa, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:69.4": {"pli": "chandajātassa jantuno; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:69.5": {"pli": "Te kāmā parihāyanti, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:69.6": {"pli": "sallaviddhova ruppati. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:70.1": {"pli": "Saṅkhittena pañcupādānakkhandhā dukkhā. ", "html": "<p>{}</p>"}, "pe1:71.1": {"pli": "Cakkhu sotañca ghānañca, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:71.2": {"pli": "jivhā kāyo tato manaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:71.3": {"pli": "Ete lokāmisā ghorā, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:71.4": {"pli": "yattha sattā puthujjanā. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:72.1": {"pli": "Pañcime bhikkhave khandhā—", "html": "<p>{}"}, "pe1:72.2": {"pli": "idaṁ dukkhaṁ. ", "html": "{}</p>"}, "pe1:73.1": {"pli": "Tattha katamā jarā ca maraṇañca? ", "html": "<p>{}</p>"}, "pe1:74.1": {"pli": "Appaṁ vata jīvitaṁ idaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:74.2": {"pli": "Oraṁ vassasatāpi mīyate; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:74.3": {"pli": "Atha vāpi akicchaṁ jīvitaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:74.4": {"pli": "Atha kho so jarasāpi mīyate. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:75.1": {"pli": "Saṁyuttake pasenadisaṁyuttake suttaṁ ayyikā me kālaṅkatā—", "html": "<p>{}"}, "pe1:75.2": {"pli": "ayaṁ jarā ca maraṇañca. ", "html": "{}</p>"}, "pe1:76.1": {"pli": "Tattha katamā cuti ca upapatti ca? ", "html": "<p>{}</p>"}, "pe1:77.1": {"pli": "“Sabbe sattā marissanti, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:77.2": {"pli": "Maraṇantaṁ hi jīvitaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:77.3": {"pli": "Yathākammaṁ gamissanti, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:77.4": {"pli": "Attakammaphalūpagā”ti.—", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:78.1": {"pli": "Ayaṁ cuti ca upapatti ca. ", "html": "<p>{}</p>"}, "pe1:79.1": {"pli": "Imehi suttehi ekasadisehi ca aññehi navavidhaṁ suttaṁ taṁ anupaviṭṭhehi lakkhaṇato dukkhaṁ ñatvā sādhāraṇañca asādhāraṇañca dukkhaṁ ariyasaccaṁ niddisitabbaṁ. ", "html": "<p>{}"}, "pe1:79.2": {"pli": "Gāthāhi gāthā anuminitabbā, byākaraṇehi vā byākaraṇaṁ—", "html": "{}"}, "pe1:79.3": {"pli": "idaṁ dukkhaṁ. ", "html": "{}</p>"}, "pe1:80.1": {"pli": "Tattha katamo <b>dukkhasamudayo</b>? ", "html": "<p>{}</p>"}, "pe1:81.1": {"pli": "Kāmesu sattā kāmasaṅgasattā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:81.2": {"pli": "Saṁyojane vajjamapassamānā; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:81.3": {"pli": "Na hi jātu saṁyojanasaṅgasattā, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:81.4": {"pli": "Oghaṁ tareyyuṁ vipulaṁ mahantaṁ. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:82.1": {"pli": "Cattāro āsavā suttaṁ—", "html": "<p>{}"}, "pe1:82.2": {"pli": "ayaṁ dukkhasamudayo. ", "html": "{}</p>"}, "pe1:83.1": {"pli": "Tattha katamo <b>dukkhanirodho</b>? ", "html": "<p>{}</p>"}, "pe1:84.1": {"pli": "Yamhi na māyā vasatī na māno, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:84.2": {"pli": "Yo vītalobho amamo nirāso; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:84.3": {"pli": "Panuṇṇakodho abhinibbutatto, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:84.4": {"pli": "So brāhmaṇo so samaṇo sa bhikkhu. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:85.1": {"pli": "Dvemā vimuttiyo, rāgavirāgā ca cetovimutti; ", "html": "<p>{}"}, "pe1:85.2": {"pli": "avijjāvirāgā ca paññāvimutti—", "html": "{}"}, "pe1:85.3": {"pli": "ayaṁ nirodho. ", "html": "{}</p>"}, "pe1:86.1": {"pli": "Tattha katamo <b>maggo</b>? ", "html": "<p>{}</p>"}, "pe1:87.1": {"pli": "Eseva maggo natthañño, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:87.2": {"pli": "dassanassa visuddhiyā; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:87.3": {"pli": "Ariyo aṭṭhaṅgiko maggo, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:87.4": {"pli": "mārassetaṁ pamohanaṁ. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:88.1": {"pli": "Sattime, bhikkhave, bojjhaṅgā—", "html": "<p>{}"}, "pe1:88.2": {"pli": "ayaṁ maggo. ", "html": "{}</p>"}, "pe1:89.1": {"pli": "Tattha katamāni <b>cattāri ariyasaccāni</b>? ", "html": "<p>{}</p>"}, "pe1:90.1": {"pli": "“Ye dhammā hetuppabhavā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:90.2": {"pli": "Tesaṁ hetuṁ tathāgato āha; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:90.3": {"pli": "Tesañca yo nirodho, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:90.4": {"pli": "Evaṁvādī mahāsamaṇo”ti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:91.1": {"pli": "Hetuppabhavā dhammā dukkhaṁ, hetusamudayo, yaṁ bhagavato vacanaṁ. ", "html": "<p>{}"}, "pe1:91.2": {"pli": "Ayaṁ dhammo yo nirodho, ye hi keci saṁyojaniyesu dhammesu assadānupassino viharanti. ", "html": "{}"}, "pe1:91.3": {"pli": "Kilesā taṇhā pavaḍḍhati, taṇhāpaccayā upādānaṁ …pe… ", "html": "{}"}, "pe1:91.4": {"pli": "evametassa kevalassa dukkhakkhandhassa samudayo hoti. ", "html": "{}"}, "pe1:91.5": {"pli": "Tattha yaṁ saṁyojanaṁ—", "html": "{}"}, "pe1:91.6": {"pli": "ayaṁ samudayo. ", "html": "{}"}, "pe1:91.7": {"pli": "Ye saṁyojaniyā dhammā ye ca sokaparidevadukkhadomanassupāyāsā sambhavanti—", "html": "{}"}, "pe1:91.8": {"pli": "idaṁ dukkhaṁ. ", "html": "{}"}, "pe1:91.9": {"pli": "Yā saṁyojaniyesu dhammesu ādīnavānupassanā—", "html": "{}"}, "pe1:91.10": {"pli": "ayaṁ maggo. ", "html": "{}"}, "pe1:91.11": {"pli": "Parimuccati jātiyā jarāya byādhīhi maraṇehi sokehi paridevehi yāva upāyāsehi—", "html": "{}"}, "pe1:91.12": {"pli": "idaṁ nibbānaṁ. ", "html": "{}"}, "pe1:91.13": {"pli": "Imāni cattāri saccāni. ", "html": "{}</p>"}, "pe1:92.1": {"pli": "Tattha katamā <b>anupādisesā nibbānadhātu</b>? ", "html": "<p>{}</p>"}, "pe1:93.1": {"pli": "Atthaṅgatassa na pamāṇamatthi, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:93.2": {"pli": "Taṁ hi vā natthi yena naṁ paññapeyya; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:93.3": {"pli": "Sabbasaṅgānaṁ samūhatattā vidū, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:93.4": {"pli": "Sitā vādasatassu sabbe. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:94.1": {"pli": "Saṁyuttake godhikasaṁyuttaṁ. ", "html": "<p class='endsutta'>{}</p>"}, "pe1:95.1": {"pli": "Imāni <b>asādhāraṇāni suttāni</b>. ", "html": "<p>{}"}, "pe1:95.2": {"pli": "Yahiṁ yahiṁ saccāni niddiṭṭhāni, tahiṁ tahiṁ saccalakkhaṇato otāretvā aparimāṇehi byañjanehi so attho pariyesitabbo. ", "html": "{}"}, "pe1:95.3": {"pli": "Tattha atthānuparivatti byañjanena puna byañjanānuparivatti atthena tassa ekamekassa aparimāṇāni byañjanāni imehi suttehi yathānikkhittehi cattāri ariyasaccāni niddisitabbāni. ", "html": "{}"}, "pe1:95.4": {"pli": "Pañcanikāye anupaviṭṭhāhi gāthāhi gāthā anuminitabbā, byākaraṇena byākaraṇaṁ. ", "html": "{}"}, "pe1:95.5": {"pli": "Imāni asādhāraṇāni suttāni. ", "html": "{}</p>"}, "pe1:96.1": {"pli": "Tesaṁ imā uddānagāthā ", "html": "<p class='uddana-intro'>{}</p>"}, "pe1:97.1": {"pli": "Yamekarattiṁ paṭhamaṁ, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "pe1:97.2": {"pli": "aṭṭha dānūpapattiyo; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:97.3": {"pli": "Pañca pubbanimittāni, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:97.4": {"pli": "khīṇamacchaṁva pallalaṁ. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe1:98.1": {"pli": "Sāmaṁ tena kuto rāja, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe1:98.2": {"pli": "tayo devā gilānakā; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:98.3": {"pli": "Yathāpi kumbhakārassa, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:98.4": {"pli": "yathā nadidakappanaṁ. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe1:99.1": {"pli": "Idha socati pecca socati, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe1:99.2": {"pli": "Tīṇi duccaritāni ca; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:99.3": {"pli": "Kāmesu giddhā pasutā, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:99.4": {"pli": "Yāva tisso vipattiyo. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe1:100.1": {"pli": "Sataṁ āsi ayosaṅkū, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe1:100.2": {"pli": "pariḷāho mahattaro; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:100.3": {"pli": "Saṅkappehi pareto so, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:100.4": {"pli": "tattha tapaniyehi ca. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe1:101.1": {"pli": "Kammārānaṁ yathā ukkā, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe1:101.2": {"pli": "tayo aggī pakāsitā; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:101.3": {"pli": "Ayato malamuppannaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:101.4": {"pli": "abbhakkhānaṁ tathāgate. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe1:102.1": {"pli": "Tividhaṁ devānusāsanti, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe1:102.2": {"pli": "supinena saṅgamo yathā; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:102.3": {"pli": "Tisso ceva māradhītā, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:102.4": {"pli": "sallaviddhova ruppati. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe1:103.1": {"pli": "Cakkhu sotañca ghānañca, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe1:103.2": {"pli": "pañcakkhandhā pakāsitā; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:103.3": {"pli": "Appaṁ vata jīvitaṁ idaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:103.4": {"pli": "ayyikā me mahallikā. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe1:104.1": {"pli": "Sabbe sattā marissanti, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe1:104.2": {"pli": "upapatti cuticayaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:104.3": {"pli": "Kāmesu sattā pasutā, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:104.4": {"pli": "āsavehi catūhi ca. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe1:105.1": {"pli": "Yamhi na māyā vasati, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe1:105.2": {"pli": "dvemā cetovimuttiyo; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:105.3": {"pli": "Eseva maggo natthañño, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:105.4": {"pli": "bojjhaṅgā ca sudesitā. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe1:106.1": {"pli": "Atthaṅgatassa na pamāṇamatthi, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe1:106.2": {"pli": "Godhiko parinibbuto; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:106.3": {"pli": "Ye dhammā hetuppabhavā, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:106.4": {"pli": "Saṁyojanānupassino. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:107.1": {"pli": "Imā dasa tesaṁ uddānagāthā. ", "html": "<p class='endsutta'>{}</p>"}, "pe1:108.1": {"pli": "Tatthimāni <b>sādhāraṇāni suttāni</b> yesu suttesu sādhāraṇāni saccāni desitāni anulomampi paṭilomampi vomissakampi. ", "html": "<p>{}"}, "pe1:108.2": {"pli": "Tattha ayaṁ ādi. ", "html": "{}</p>"}, "pe1:109.1": {"pli": "Avijjāya nivuto loko, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:109.2": {"pli": "(ajitāti bhagavā) ", "html": "<span class='speaker'>{}</span>"}, "pe1:109.3": {"pli": "Vivicchā pamādā nappakāsati; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:109.4": {"pli": "Jappābhilepanaṁ brūmi, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:109.5": {"pli": "Dukkhamassa mahabbhayaṁ. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:110.1": {"pli": "Tattha yā avijjā ca vivicchā ca, ayaṁ samudayo. ", "html": "<p>{}"}, "pe1:110.2": {"pli": "Yaṁ mahabbhayaṁ, idaṁ dukkhaṁ. ", "html": "{}"}, "pe1:110.3": {"pli": "Imāni dve saccāni—", "html": "{}"}, "pe1:110.4": {"pli": "dukkhañca samudayo ca. ", "html": "{}"}, "pe1:110.5": {"pli": "“Saṁyojanaṁ saṁyojaniyā ca dhammā”ti saṁyuttake cittasaṁyuttakesu byākaraṇaṁ. ", "html": "{}"}, "pe1:110.6": {"pli": "Tattha yaṁ saṁyojanaṁ, ayaṁ samudayo. ", "html": "{}"}, "pe1:110.7": {"pli": "Ye saṁyojaniyā dhammā, idaṁ dukkhaṁ. ", "html": "{}"}, "pe1:110.8": {"pli": "Imāni dve saccāni—", "html": "{}"}, "pe1:110.9": {"pli": "dukkhañca samudayo ca. ", "html": "{}</p>"}, "pe1:111.1": {"pli": "Tattha katamaṁ dukkhañca nirodho ca? ", "html": "<p>{}</p>"}, "pe1:112.1": {"pli": "Ucchinnabhavataṇhassa, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:112.2": {"pli": "netticchinnassa bhikkhuno; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:112.3": {"pli": "Vikkhīṇo jātisaṁsāro, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:112.4": {"pli": "natthi dāni punabbhavo. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:113.1": {"pli": "Yaṁ cittaṁ, idaṁ dukkhaṁ. ", "html": "<p>{}"}, "pe1:113.2": {"pli": "Yo bhavataṇhāya upacchedo, ayaṁ dukkhanirodho. ", "html": "{}"}, "pe1:113.3": {"pli": "Vikkhīṇo jātisaṁsāro, natthi dāni punabbhavoti niddeso. ", "html": "{}"}, "pe1:113.4": {"pli": "Imāni dve saccāni—", "html": "{}"}, "pe1:113.5": {"pli": "dukkhañca nirodho ca. ", "html": "{}"}, "pe1:113.6": {"pli": "Dvemā, bhikkhave, vimuttiyo; ", "html": "{}"}, "pe1:113.7": {"pli": "rāgavirāgā ca cetovimutti, avijjāvirāgā ca paññāvimutti. ", "html": "{}"}, "pe1:113.8": {"pli": "Yaṁ cittaṁ, idaṁ dukkhaṁ. ", "html": "{}"}, "pe1:113.9": {"pli": "Yā vimutti, ayaṁ nirodho. ", "html": "{}"}, "pe1:113.10": {"pli": "Imāni dve saccāni—", "html": "{}"}, "pe1:113.11": {"pli": "dukkhañca nirodho ca. ", "html": "{}</p>"}, "pe1:114.1": {"pli": "Tattha katamaṁ dukkhañca maggo ca? ", "html": "<p>{}</p>"}, "pe1:115.1": {"pli": "Kumbhūpamaṁ kāyamimaṁ viditvā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:115.2": {"pli": "Nagarūpamaṁ cittamidaṁ ṭhapetvā; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:115.3": {"pli": "Yodhetha māraṁ paññāvudhena, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:115.4": {"pli": "Jitañca rakkhe anivesano siyā. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:116.1": {"pli": "Tattha yañca kumbhūpamo kāyo yañca nagarūpamaṁ cittaṁ, idaṁ dukkhaṁ. ", "html": "<p>{}"}, "pe1:116.2": {"pli": "Yaṁ paññāvudhena māraṁ yodhethāti ayaṁ maggo. ", "html": "{}"}, "pe1:116.3": {"pli": "Imāni dve saccāni. ", "html": "{}"}, "pe1:116.4": {"pli": "Yaṁ, bhikkhave, na tumhākaṁ, taṁ pajahitabbaṁ. ", "html": "{}"}, "pe1:116.5": {"pli": "Yā saṁyojanā, ayaṁ maggo. ", "html": "{}"}, "pe1:116.6": {"pli": "Ye te dhammā anattaniyā pahātabbā, rūpaṁ yāva viññāṇaṁ, idaṁ dukkhañca maggo ca. ", "html": "{}</p>"}, "pe1:117.1": {"pli": "Tattha katamaṁ dukkhañca samudayo ca nirodho ca? ", "html": "<p>{}</p>"}, "pe1:118.1": {"pli": "Ye keci sokā paridevitā vā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:118.2": {"pli": "Dukkhā ca lokasmimanekarūpā; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:118.3": {"pli": "Piyaṁ paṭiccappabhavanti ete, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:118.4": {"pli": "Piye asante na bhavanti ete. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:119.1": {"pli": "Ye sokaparidevā, yaṁ ca anekarūpaṁ dukkhaṁ, yaṁ pemato bhavati, idaṁ dukkhaṁ. ", "html": "<p>{}"}, "pe1:119.2": {"pli": "Yaṁ pemaṁ, ayaṁ samudayo. ", "html": "{}"}, "pe1:119.3": {"pli": "Yo tattha chandarāgavinayo piyassa akiriyā, ayaṁ nirodho. ", "html": "{}"}, "pe1:119.4": {"pli": "Imāni tīṇi saccāni. ", "html": "{}"}, "pe1:119.5": {"pli": "Timbaruko paribbājako pacceti “sayaṅkataṁ paraṅkatan”ti. ", "html": "{}"}, "pe1:119.6": {"pli": "Yathesā vīmaṁsā, idaṁ dukkhaṁ. ", "html": "{}"}, "pe1:119.7": {"pli": "Yā ete dve ante anupagamma majjhimā paṭipadā avijjāpaccayā saṅkhārā yāva jātipaccayā jarāmaraṇaṁ, idampi dukkhañca samudayo ca. ", "html": "{}"}, "pe1:119.8": {"pli": "Viññāṇaṁ nāmarūpaṁ saḷāyatanaṁ phasso vedanā bhavo jāti jarāmaraṇaṁ, idaṁ dukkhaṁ. ", "html": "{}"}, "pe1:119.9": {"pli": "Avijjā saṅkhārā taṇhā upādānaṁ, ayaṁ samudayo. ", "html": "{}"}, "pe1:119.10": {"pli": "Iti idaṁ sayaṅkataṁ vīmaṁseyyāti yañca paṭiccasamuppāde dukkhaṁ, idaṁ eso samudayo niddiṭṭho. ", "html": "{}"}, "pe1:119.11": {"pli": "Avijjānirodhā saṅkhāranirodho ca yāva ca jarāmaraṇanirodhoti ayaṁ nirodho. ", "html": "{}"}, "pe1:119.12": {"pli": "Imāni tīṇi saccāni dukkhañca samudayo ca nirodho ca. ", "html": "{}</p>"}, "pe1:120.1": {"pli": "Tattha katamaṁ dukkhañca samudayo ca maggo ca? ", "html": "<p>{}</p>"}, "pe1:121.1": {"pli": "“Yo dukkhamaddakkhi yatonidānaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:121.2": {"pli": "Kāmesu so jantu kathaṁ nameyya; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:121.3": {"pli": "Kāmā hi loke saṅgāti ñatvā, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:121.4": {"pli": "Tesaṁ satīmā vinayāya sikkhe”ti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:122.1": {"pli": "Yo dukkhamaddakkhi, idaṁ dukkhaṁ. ", "html": "<p>{}"}, "pe1:122.2": {"pli": "Yato bhavati, ayaṁ samudayo. ", "html": "{}"}, "pe1:122.3": {"pli": "Sandiṭṭhaṁ yato bhavati yāva tassa vinayāya sikkhā, ayaṁ maggo. ", "html": "{}"}, "pe1:122.4": {"pli": "Imāni tīṇi saccāni. ", "html": "{}</p>"}, "pe1:123.1": {"pli": "Ekādasaṅguttaresu gopālakopamasuttaṁ. ", "html": "<p class='endsutta'>{}</p>"}, "pe1:124.1": {"pli": "Tattha yāva rūpasaññuttā yañca saḷāyatanaṁ yathā vaṇaṁ paṭicchādeti yañca titthaṁ yathā ca labhati dhammūpasañhitaṁ uḷāraṁ pītipāmojjaṁ catubbidhaṁ ca attabhāvato ca vatthu, idaṁ dukkhaṁ. ", "html": "<p>{}"}, "pe1:124.2": {"pli": "Yāva āsāṭikaṁ hāretā hoti, ayaṁ samudayo. ", "html": "{}"}, "pe1:124.3": {"pli": "Rūpasaññuttā āsāṭakaharaṇaṁ vaṇapaṭicchādanaṁ vīthiññutā gocarakusalañca, ayaṁ maggo. ", "html": "{}"}, "pe1:124.4": {"pli": "Avasesā dhammā atthi hetū atthi paccayā atthi nissayā sāvasesadohitā anekapūjā ca kalyāṇamittatappaccayā dhammā vīthiññutā ca hetu, imāni tīṇi saccāni. ", "html": "{}</p>"}, "pe1:125.1": {"pli": "Tattha katamaṁ dukkhañca maggo ca nirodho ca? ", "html": "<p>{}</p>"}, "pe1:126.1": {"pli": "Sati kāyagatā upaṭṭhitā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:126.2": {"pli": "Chasu phassāyatanesu saṁvuto; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:126.3": {"pli": "Satataṁ bhikkhu samāhito, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:126.4": {"pli": "Jaññā nibbānamattano. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:127.1": {"pli": "Tattha yā ca kāyagatā sati yañca saḷāyatanaṁ yattha sabbañcetaṁ dukkhaṁ. ", "html": "<p>{}"}, "pe1:127.2": {"pli": "Yā ca kāyagatā sati yo ca sīlasaṁvaro yo ca samādhi yattha yā sati, ayaṁ paññākkhandho. ", "html": "{}"}, "pe1:127.3": {"pli": "Sabbampi sīlakkhandho samādhikkhandho, ayaṁ maggo. ", "html": "{}"}, "pe1:127.4": {"pli": "Evaṁvihārinā ñātabbaṁ nibbānaṁ. ", "html": "{}"}, "pe1:127.5": {"pli": "Ayaṁ nirodho, imāni tīṇi saccāni. ", "html": "{}"}, "pe1:127.6": {"pli": "Sīle patiṭṭhāya dve dhammā bhāvetabbā samatho ca vipassanā ca. ", "html": "{}"}, "pe1:127.7": {"pli": "Tattha yaṁ cittasahajātā dhammā, idaṁ dukkhaṁ. ", "html": "{}"}, "pe1:127.8": {"pli": "Yo ca samatho yā ca vipassanā, ayaṁ maggo. ", "html": "{}"}, "pe1:127.9": {"pli": "Rāgavirāgā ca cetovimutti, avijjāvirāgā ca paññāvimutti, ayaṁ nirodho. ", "html": "{}"}, "pe1:127.10": {"pli": "Imāni tīṇi saccāni. ", "html": "{}</p>"}, "pe1:128.1": {"pli": "Tattha katamo samudayo ca nirodho ca? ", "html": "<p>{}</p>"}, "pe1:129.1": {"pli": "Āsā ca pīhā abhinandanā ca, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:129.2": {"pli": "Anekadhātūsu sarā patiṭṭhitā; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:129.3": {"pli": "Aññāṇamūlappabhavā pajappitā, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:129.4": {"pli": "Sabbā mayā byantikatā samūlikā. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:130.1": {"pli": "Aññāṇamūlappabhavāti purimakehi samudayo. ", "html": "<p>{}"}, "pe1:130.2": {"pli": "Sabbā mayā byantikatā samūlikāti nirodho. ", "html": "{}"}, "pe1:130.3": {"pli": "Imāni dve saccāni. ", "html": "{}"}, "pe1:130.4": {"pli": "Catunnaṁ dhammānaṁ ananubodhā appaṭivedhā vitthārena kātabbaṁ. ", "html": "{}"}, "pe1:130.5": {"pli": "Ariyassa sīlassa samādhino paññāya vimuttiyā. ", "html": "{}"}, "pe1:130.6": {"pli": "Tattha yo imesaṁ catunnaṁ dhammānaṁ ananubodhā appaṭivedhā, ayaṁ samudayo. ", "html": "{}"}, "pe1:130.7": {"pli": "Paṭivedho bhavanettiyā, ayaṁ nirodho. ", "html": "{}"}, "pe1:130.8": {"pli": "Ayaṁ samudayo ca nirodho ca. ", "html": "{}</p>"}, "pe1:131.1": {"pli": "Tattha katamo samudayo ca maggo ca? ", "html": "<p>{}</p>"}, "pe1:132.1": {"pli": "Yāni sotāni lokasmiṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:132.2": {"pli": "(ajitāti bhagavā) ", "html": "<span class='speaker'>{}</span>"}, "pe1:132.3": {"pli": "Sati tesaṁ nivāraṇaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:132.4": {"pli": "Sotānaṁ saṁvaraṁ brūmi, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:132.5": {"pli": "Paññāyete pidhīyare. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:133.1": {"pli": "Yāni sotānīti ayaṁ samudayo. ", "html": "<p>{}"}, "pe1:133.2": {"pli": "Yā ca paññā yā ca sati nivāraṇaṁ pidhānañca, ayaṁ maggo. ", "html": "{}"}, "pe1:133.3": {"pli": "Imāni dve saccāni. ", "html": "{}"}, "pe1:133.4": {"pli": "Sañcetaniyaṁ suttaṁ daḷhanemiyānākāro chahi māsehi niddiṭṭho. ", "html": "{}"}, "pe1:133.5": {"pli": "Tattha yaṁ kāyaṁ kāyakammaṁ savaṅkaṁ sadosaṁ sakasāvaṁ yā savaṅkatā sadosatā sakasāvatā, ayaṁ samudayo. ", "html": "{}"}, "pe1:133.6": {"pli": "Evaṁ vacīkammaṁ manokammaṁ avaṅkaṁ adosaṁ akasāvaṁ, yā avaṅkatā adosatā akasāvatā, ayaṁ maggo. ", "html": "{}"}, "pe1:133.7": {"pli": "Evaṁ vacīkammaṁ manokammaṁ. ", "html": "{}"}, "pe1:133.8": {"pli": "Imāni dve saccāni samudayo ca maggo ca. ", "html": "{}</p>"}, "pe1:134.1": {"pli": "Tattha katamo samudayo ca nirodho ca maggo ca? ", "html": "<p>{}</p>"}, "pe1:135.1": {"pli": "“Nissitassa calitaṁ, anissitassa calitaṁ natthi, calite asati passaddhi, passaddhiyā sati nati na hoti, natiyā asati āgatigati na hoti, āgatigatiyā asati cutūpapāto na hoti, cutūpapāte asati nevidha na huraṁ na ubhayamantarena. ", "html": "<p>{}"}, "pe1:135.2": {"pli": "Esevanto dukkhassā”ti. ", "html": "{}</p>"}, "pe1:136.1": {"pli": "Tattha dve nissayā, ayaṁ samudayo. ", "html": "<p>{}"}, "pe1:136.2": {"pli": "Yo ca anissayo, yā ca anati, ayaṁ maggo. ", "html": "{}"}, "pe1:136.3": {"pli": "Yā āgatigati na hoti cutūpapāto ca yo esevanto dukkhassāti, ayaṁ nirodho. ", "html": "{}"}, "pe1:136.4": {"pli": "Imāni tīṇi saccāni. ", "html": "{}"}, "pe1:136.5": {"pli": "Anupaṭṭhitakāyagatā sati …pe… ", "html": "{}"}, "pe1:136.6": {"pli": "yaṁ vimuttiñāṇadassanaṁ, ayaṁ samudayo. ", "html": "{}"}, "pe1:136.7": {"pli": "Ekārasaupanissayā vimuttiyo yāva upanissayaupasampadā upaṭṭhitakāyagatāsatissa viharati. ", "html": "{}"}, "pe1:136.8": {"pli": "Sīlasaṁvaro sosāniyo hoti, yañca vimuttiñāṇadassanaṁ, ayaṁ maggo. ", "html": "{}"}, "pe1:136.9": {"pli": "Yā ca vimutti, ayaṁ nirodho. ", "html": "{}"}, "pe1:136.10": {"pli": "Imāni tīṇi saccāni. ", "html": "{}"}, "pe1:136.11": {"pli": "Samudayo ca nirodho ca maggo ca. ", "html": "{}</p>"}, "pe1:137.1": {"pli": "Tattha katamo nirodho ca maggo ca? ", "html": "<p>{}</p>"}, "pe1:138.1": {"pli": "Sayaṁ katena saccena, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe1:138.2": {"pli": "Tena attanā abhinibbānagato vitiṇṇakaṅkho; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:138.3": {"pli": "Vibhavañca ñatvā lokasmiṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:138.4": {"pli": "Tāva khīṇapunabbhavo sa bhikkhu. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:139.1": {"pli": "Yaṁ saccena, ayaṁ maggo. ", "html": "<p>{}"}, "pe1:139.2": {"pli": "Yaṁ khīṇapunabbhavo, ayaṁ nirodho. ", "html": "{}"}, "pe1:139.3": {"pli": "Imāni dve saccāni. ", "html": "{}"}, "pe1:139.4": {"pli": "Pañca vimuttāyatanāni satthā vā dhammaṁ desesi aññataro vā viññū sabrahmacārīti vitthārena kātabbā. ", "html": "{}"}, "pe1:139.5": {"pli": "Tassa atthappaṭisaṁvedissa pāmojjaṁ jāyati, pamuditassa pīti jāyati, yāva nibbindanto virajjati, ayaṁ maggo. ", "html": "{}"}, "pe1:139.6": {"pli": "Yā vimutti, ayaṁ nirodho. ", "html": "{}"}, "pe1:139.7": {"pli": "Evaṁ pañca vimuttāyatanāni vitthārena. ", "html": "{}"}, "pe1:139.8": {"pli": "Imāni dve saccāni nirodho ca maggo ca. ", "html": "{}</p>"}, "pe1:140.1": {"pli": "Imāni sādhāraṇāni suttāni. ", "html": "<p>{}"}, "pe1:140.2": {"pli": "Imehi sādhāraṇehi suttehi yathānikkhittehi paṭivedhato ca lakkhaṇato ca otāretvā aññāni suttāni niddisitabbāni aparihāyantena. ", "html": "{}"}, "pe1:140.3": {"pli": "Gāthāhi gāthā anuminitabbā, byākaraṇehi byākaraṇaṁ. ", "html": "{}"}, "pe1:140.4": {"pli": "Ime ca sādhāraṇā dasa parivaḍḍhakā eko ca catukko niddeso sādhāraṇo. ", "html": "{}"}, "pe1:140.5": {"pli": "Ayañca pakiṇṇakaniddeso. ", "html": "{}"}, "pe1:140.6": {"pli": "Ekaṁ pañca cha ca savekadeso sabbaṁ. ", "html": "{}"}, "pe1:140.7": {"pli": "Ime dve parivajjanā purimakā ca dasa. ", "html": "{}"}, "pe1:140.8": {"pli": "Ime dvādasa parivaḍḍhakā saccāni. ", "html": "{}"}, "pe1:140.9": {"pli": "Ettāvatā sabbaṁ suttaṁ natthi, taṁ byākaraṇaṁ vā gāthā viya. ", "html": "{}"}, "pe1:140.10": {"pli": "Imehi dvādasahi parivaḍḍhakehi na otarituṁ appamattena pariyesitvā niddisitabbā. ", "html": "{}</p>"}, "pe1:141.1": {"pli": "Tatthāyaṁ saṅkhepo. ", "html": "<p>{}"}, "pe1:141.2": {"pli": "Sabbaṁ dukkhaṁ sattahi padehi samosaraṇaṁ gacchati. ", "html": "{}"}, "pe1:141.3": {"pli": "Katarehi sattahi? ", "html": "{}"}, "pe1:141.4": {"pli": "Appiyasampayogo ca piyavippayogo ca, imehi dvīhi padehi sabbaṁ dukkhaṁ niddisitabbaṁ. ", "html": "{}"}, "pe1:141.5": {"pli": "Tassa dve nissayā—", "html": "{}"}, "pe1:141.6": {"pli": "kāyo ca cittañca. ", "html": "{}"}, "pe1:141.7": {"pli": "Tena vuccati “kāyikaṁ dukkhaṁ cetasikañce”ti, natthi taṁ dukkhaṁ na kāyikaṁ vā na cetasikaṁ, sabbaṁ dukkhaṁ dvīhi dukkhehi niddisitabbaṁ kāyikena ca cetasikena ca. ", "html": "{}"}, "pe1:141.8": {"pli": "Tīhi dukkhatāhi saṅgahitaṁ dukkhadukkhatāya saṅkhāradukkhatāya vipariṇāmadukkhatāya. ", "html": "{}"}, "pe1:141.9": {"pli": "Iti taṁ sabbaṁ dukkhaṁ tīhi dukkhatāhi saṅgahitaṁ. ", "html": "{}"}, "pe1:141.10": {"pli": "Iti idañca dukkhaṁ tividhaṁ. ", "html": "{}"}, "pe1:141.11": {"pli": "Duvidhaṁ dukkhaṁ kāyikañca cetasikañca. ", "html": "{}"}, "pe1:141.12": {"pli": "Duvidhaṁ appiyasampayogo ca piyavippayogo ca. ", "html": "{}"}, "pe1:141.13": {"pli": "Idaṁ sattavidhaṁ dukkhaṁ. ", "html": "{}</p>"}, "pe1:142.1": {"pli": "Tattha tividho samudayo acatuttho apañcamo. ", "html": "<p>{}"}, "pe1:142.2": {"pli": "Katamo tividho? ", "html": "{}"}, "pe1:142.3": {"pli": "Taṇhā ca diṭṭhi ca kammaṁ. ", "html": "{}"}, "pe1:142.4": {"pli": "Tattha taṇhā ca bhavasamudayo kammaṁ. ", "html": "{}"}, "pe1:142.5": {"pli": "Tathā nibbattassa hīnapaṇītatā, ayaṁ samudayo. ", "html": "{}"}, "pe1:142.6": {"pli": "Iti yāpi bhavagatīsu hīnatā ca paṇītatā ca, yāpi tīhi dukkhatāhi saṅgahitā, yopi dvīhi mūlehi samudānīto avijjāya nivutassa bhavataṇhāsaṁyuttassa saviññāṇako kāyo, sopi tīhi dukkhatāhi saṅgahito. ", "html": "{}</p>"}, "pe1:143.1": {"pli": "Tathā vipallāsato diṭṭhi āgantabbā. ", "html": "<p>{}"}, "pe1:143.2": {"pli": "Sā sattavidhā niddisitabbā. ", "html": "{}"}, "pe1:143.3": {"pli": "Eko vipallāso tīṇi niddisīyati, cattāri vipallāsavatthūni. ", "html": "{}"}, "pe1:143.4": {"pli": "Tattha katamo eko vipallāso? ", "html": "{}"}, "pe1:143.5": {"pli": "Yo viparītaggāho paṭikkhepena, otaraṇaṁ yathā “anicce niccam”iti viparītaṁ gaṇhāti. ", "html": "{}"}, "pe1:143.6": {"pli": "Evaṁ cattāro vipallāsā. ", "html": "{}"}, "pe1:143.7": {"pli": "Ayameko vipallāsīyati saññā cittaṁ diṭṭhi. ", "html": "{}"}, "pe1:143.8": {"pli": "Katamāni cattāri vipallāsavatthūni? ", "html": "{}"}, "pe1:143.9": {"pli": "Kāyo vedanā cittaṁ dhammā. ", "html": "{}"}, "pe1:143.10": {"pli": "Evaṁ vipallāsagatassa akusalañca pavaḍḍheti. ", "html": "{}"}, "pe1:143.11": {"pli": "Tattha saññāvipallāso dosaṁ akusalamūlaṁ pavaḍḍheti. ", "html": "{}"}, "pe1:143.12": {"pli": "Cittavipallāso lobhaṁ akusalamūlaṁ pavaḍḍheti. ", "html": "{}"}, "pe1:143.13": {"pli": "Diṭṭhivipallāso mohaṁ akusalamūlaṁ pavaḍḍheti. ", "html": "{}"}, "pe1:143.14": {"pli": "Tattha dosassa akusalamūlassa tīṇi micchattāni phalaṁ—", "html": "{}"}, "pe1:143.15": {"pli": "micchāvācā micchākammanto micchāājīvo; ", "html": "{}"}, "pe1:143.16": {"pli": "lobhassa akusalamūlassa tīṇi micchattāni phalaṁ—", "html": "{}"}, "pe1:143.17": {"pli": "micchāsaṅkappo micchāvāyāmo micchāsamādhi; ", "html": "{}"}, "pe1:143.18": {"pli": "mohassa akusalamūlassa dve micchattāni phalaṁ—", "html": "{}"}, "pe1:143.19": {"pli": "micchādiṭṭhi ca micchāsati ca. ", "html": "{}"}, "pe1:143.20": {"pli": "Evaṁ akusalaṁ sahetu sappaccayaṁ vipallāsā ca paccayo, akusalamūlāni sahetū eteyeva paṭipakkhena anūnā anadhikā dvīhi paccayehi niddisitabbā. ", "html": "{}"}, "pe1:143.21": {"pli": "Nirodhe ca magge ca vipallāsamupādāya parato paṭipakkhena catasso. ", "html": "{}</p>"}, "pe1:144.1": {"pli": "Tatthimā uddānagāthā ", "html": "<p class='uddana-intro'>{}</p>"}, "pe1:145.1": {"pli": "Avijjāya nivuto loko, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "pe1:145.2": {"pli": "cittaṁ saṁyojanampi; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:145.3": {"pli": "Sā pacchinnabhavataṇhā, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:145.4": {"pli": "dvemā ceva vimuttiyo. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe1:146.1": {"pli": "Kumbhūpamaṁ kāyamimaṁ, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe1:146.2": {"pli": "Yaṁ na tumhākaṁ taṁ pajaha; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:146.3": {"pli": "Ye keci sokaparidevā, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:146.4": {"pli": "Timbaruko ca sayaṅkataṁ. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe1:147.1": {"pli": "Dukkhaṁ diṭṭhi ca uppannaṁ, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe1:147.2": {"pli": "yañca gopālakopamaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:147.3": {"pli": "Sati kāyagatā māhu, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:147.4": {"pli": "samatho ca vipassanā. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe1:148.1": {"pli": "Āsā pihā ca abhinandanā ca, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe1:148.2": {"pli": "Catunnamananubodhanā; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:148.3": {"pli": "Yāni sotāni lokasmiṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:148.4": {"pli": "Daḷhaṁ nemiyānākāro. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe1:149.1": {"pli": "Yaṁ nissitassa calitaṁ, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe1:149.2": {"pli": "Anupaṭṭhitakāyagatāsati; ", "html": "<span class='verse-line'>{}</span>"}, "pe1:149.3": {"pli": "Sayaṁ katena saccena, ", "html": "<span class='verse-line'>{}</span>"}, "pe1:149.4": {"pli": "Vimuttāyatanehi ca. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe1:150.1": {"pli": "Peṭakopadese mahākaccāyanena bhāsite paṭhamabhūmi ariyasaccappakāsanā nāma taṁ jīvatā bhagavatā mādisena samuddanena tathāgatenāti. ", "html": "<p class='endsutta'>{}</p></article>"}}, "pe2": {"pe2:0.1": {"pli": "Peṭakopadesa ", "html": "<article id='pe2'><header><ul><li class='division'>{}</li></ul>"}, "pe2:0.2": {"pli": "2. Sāsanapaṭṭhānadutiyabhūmi ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "pe2:1.1": {"pli": "Tattha katamaṁ sāsanappaṭṭhānaṁ? ", "html": "<p>{}"}, "pe2:1.2": {"pli": "Saṅkilesabhāgiyaṁ suttaṁ, vāsanābhāgiyaṁ suttaṁ, nibbedhabhāgiyaṁ suttaṁ, asekkhabhāgiyaṁ suttaṁ, saṅkilesabhāgiyañca vāsanābhāgiyañca, saṅkilesabhāgiyañca nibbedhabhāgiyañca, saṅkilesabhāgiyañca nibbedhabhāgiyañca asekkhabhāgiyañca, vāsanābhāgiyañca nibbedhabhāgiyañca. ", "html": "{}"}, "pe2:1.3": {"pli": "Āṇatti, phalaṁ, upāyo, āṇatti ca phalañca, phalañca upāyo ca, āṇatti ca phalañca upāyo ca. ", "html": "{}"}, "pe2:1.4": {"pli": "Assādo, ādīnavo, nissaraṇaṁ, assādo ca ādīnavo ca, assādo ca nissaraṇañca, ādīnavo ca nissaraṇañca, assādo ca ādīnavo ca nissaraṇañca. ", "html": "{}"}, "pe2:1.5": {"pli": "Lokikaṁ, lokuttaraṁ, lokikañca lokuttarañca. ", "html": "{}"}, "pe2:1.6": {"pli": "Kammaṁ, vipāko, kammañca vipāko ca. ", "html": "{}"}, "pe2:1.7": {"pli": "Niddiṭṭhaṁ, aniddiṭṭhaṁ, niddiṭṭhañca aniddiṭṭhañca. ", "html": "{}"}, "pe2:1.8": {"pli": "Ñāṇaṁ, ñeyyaṁ, ñāṇañca ñeyyañca. ", "html": "{}"}, "pe2:1.9": {"pli": "Dassanaṁ, bhāvanā, dassanañca bhāvanā ca. ", "html": "{}"}, "pe2:1.10": {"pli": "Vipākakammaṁ, na vipākakammaṁ, nevavipākanavipākakammaṁ. ", "html": "{}"}, "pe2:1.11": {"pli": "Sakavacanaṁ, paravacanaṁ, sakavacanañca paravacanañca. ", "html": "{}"}, "pe2:1.12": {"pli": "Sattādhiṭṭhānaṁ, dhammādhiṭṭhānaṁ, sattādhiṭṭhānañca dhammādhiṭṭhānañca. ", "html": "{}"}, "pe2:1.13": {"pli": "Thavo, sakavacanādhiṭṭhānaṁ, paravacanādhiṭṭhānaṁ, sakavacanādhiṭṭhānañca paravacanādhiṭṭhānañca. ", "html": "{}"}, "pe2:1.14": {"pli": "Kiriyaṁ, phalaṁ, kiriyañca phalañca. ", "html": "{}"}, "pe2:1.15": {"pli": "Anuññātaṁ, paṭikkhittaṁ, anuññātañca paṭikkhittañca. ", "html": "{}"}, "pe2:1.16": {"pli": "Imāni cha paṭikkhittāni. ", "html": "{}</p>"}, "pe2:2.1": {"pli": "Tattha katamaṁ saṅkilesabhāgiyaṁ suttaṁ? ", "html": "<p>{}</p>"}, "pe2:3.1": {"pli": "Kāmandhā jālasañchannā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:3.2": {"pli": "Taṇhāchadanachāditā; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:3.3": {"pli": "Pamattabandhunā baddhā, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:3.4": {"pli": "Macchāva kumināmukhe; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:3.5": {"pli": "Jarāmaraṇamanventi, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:3.6": {"pli": "Vaccho khīrapakova mātaraṁ. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:4.1": {"pli": "Pañcime, bhikkhave, nīvaraṇā. ", "html": "<p class='endsutta'>{}</p>"}, "pe2:5.1": {"pli": "Tattha katamaṁ vāsanābhāgiyaṁ suttaṁ? ", "html": "<p>{}</p>"}, "pe2:6.1": {"pli": "Manopubbaṅgamā dhammā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:6.2": {"pli": "manoseṭṭhā manomayā; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:6.3": {"pli": "Manasā ce pasannena, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:6.4": {"pli": "bhāsati vā karoti vā; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:6.5": {"pli": "Tato naṁ sukhamanveti, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:6.6": {"pli": "chāyāva anapāyinī. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:7.1": {"pli": "Saṁyuttake suttaṁ. ", "html": "<p class='endsutta'>{}</p>"}, "pe2:8.1": {"pli": "Mahānāmassa sakkassa idaṁ bhagavā sakyānaṁ kapilavatthumhi nagare nayavitthārena saddhāsīlaparibhāvitaṁ suttaṁ bhāvaññena paribhāvitaṁ taṁ nāma pacchime kāle. ", "html": "<p>{}</p>"}, "pe2:9.1": {"pli": "Tattha katamaṁ nibbedhabhāgiyaṁ suttaṁ? ", "html": "<p>{}</p>"}, "pe2:10.1": {"pli": "Uddhaṁ adho sabbadhi vippamutto, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:10.2": {"pli": "Ayaṁ ahasmīti anānupassī; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:10.3": {"pli": "Evaṁ vimutto udatāri oghaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:10.4": {"pli": "Atiṇṇapubbaṁ apunabbhavāya. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:11.1": {"pli": "Sīlāni nu kho bhavanti kimatthiyāni ānando pucchati satthāraṁ. ", "html": "<p>{}</p>"}, "pe2:12.1": {"pli": "Tattha katamaṁ asekkhabhāgiyaṁ suttaṁ? ", "html": "<p>{}</p>"}, "pe2:13.1": {"pli": "“Yassa selūpamaṁ cittaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:13.2": {"pli": "ṭhitaṁ nānupakampati; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:13.3": {"pli": "Virattaṁ rajanīyesu, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:13.4": {"pli": "kopaneyye na kuppati; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:13.5": {"pli": "Yassevaṁ bhāvitaṁ cittaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:13.6": {"pli": "kuto taṁ dukkhamessatī”ti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:14.1": {"pli": "Sāriputto nāma bhagavā theraññataro so maṁ āsajja appaṭinissajja cārikaṁ pakkamati, sāriputtassa byākaraṇaṁ kātabbaṁ. ", "html": "<p>{}"}, "pe2:14.2": {"pli": "Yassa nūna bhagavā kāyagatā sati abhāvitā assa abahulīkatā vitthārena kātabbaṁ. ", "html": "{}</p>"}, "pe2:15.1": {"pli": "Tattha katamaṁ saṅkilesabhāgiyañca vāsanābhāgiyañca? ", "html": "<p>{}</p>"}, "pe2:16.1": {"pli": "Channamativassati, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:16.2": {"pli": "vivaṭaṁ nātivassati; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:16.3": {"pli": "Tasmā channaṁ vivaretha, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:16.4": {"pli": "evaṁ taṁ nātivassati. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:17.1": {"pli": "Channamativassatīti saṅkileso. ", "html": "<p>{}"}, "pe2:17.2": {"pli": "Vivaṭaṁ nātivassatīti vāsanā. ", "html": "{}"}, "pe2:17.3": {"pli": "Tamo tamaparāyanoti vitthārena. ", "html": "{}"}, "pe2:17.4": {"pli": "Tattha yo ca tamo yo ca tamaparāyano, ayaṁ saṅkileso. ", "html": "{}"}, "pe2:17.5": {"pli": "Yo ca joti yo ca jotiparāyano, ayaṁ vāsanā. ", "html": "{}</p>"}, "pe2:18.1": {"pli": "Tattha katamaṁ saṅkilesabhāgiyañca nibbedhabhāgiyañca suttaṁ? ", "html": "<p>{}</p>"}, "pe2:19.1": {"pli": "Na taṁ daḷhaṁ bandhanamāhu dhīrā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:19.2": {"pli": "Yadāyasaṁ dārujapabbajañca; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:19.3": {"pli": "Sārattarattā maṇikuṇḍalesu, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:19.4": {"pli": "Puttesu dāresu ca yā apekkhā. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:20.1": {"pli": "Na taṁ daḷhaṁ bandhanamāhu dhīrā, yadā puttesu dāresu ca yā apekkhā, ayaṁ saṅkileso. ", "html": "<p>{}"}, "pe2:20.2": {"pli": "Etampi chetvā paribbajanti dhīrā anapekkhino sabbakāme pahāyāti, ayaṁ nibbedho. ", "html": "{}"}, "pe2:20.3": {"pli": "Yaṁ cetayitaṁ pakappitaṁ yā ca nāmarūpassa avakkanti hoti. ", "html": "{}"}, "pe2:20.4": {"pli": "Imehi catūhi padehi saṅkileso. ", "html": "{}"}, "pe2:20.5": {"pli": "Pacchimakehi catūhi nibbedho. ", "html": "{}</p>"}, "pe2:21.1": {"pli": "Tattha katamaṁ saṅkilesabhāgiyañca nibbedhabhāgiyañca asekkhabhāgiyañca suttaṁ? ", "html": "<p>{}</p>"}, "pe2:22.1": {"pli": "Ayaṁ loko santāpajāto, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:22.2": {"pli": "Phassapareto rogaṁ vadati attato; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:22.3": {"pli": "Yena yena hi maññanti, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:22.4": {"pli": "Tato taṁ hoti aññathā. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:23.1": {"pli": "Aññathābhāvī bhavasatto loko, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:23.2": {"pli": "Bhavapareto bhavamevābhinandati; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:23.3": {"pli": "Yadabhinandati taṁ bhayaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:23.4": {"pli": "Yassa bhāyati taṁ dukkhaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:23.5": {"pli": "Bhavavippahānāya kho panidaṁ brahmacariyaṁ vussati. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:24.1": {"pli": "Ye hi keci samaṇā vā brāhmaṇā vā bhavena bhavassa vippamokkhamāhaṁsu, sabbete “avippamuttā bhavasmā”ti vadāmi. ", "html": "<p>{}"}, "pe2:24.2": {"pli": "Ye vā pana keci samaṇā vā brāhmaṇā vā vibhavena bhavassa nissaraṇamāhaṁsu, sabbete “anissaṭā bhavasmā”ti vadāmi. ", "html": "{}"}, "pe2:24.3": {"pli": "Upadhiṁ hi paṭicca dukkhamidaṁ sambhoti, sabbupādānakkhayā natthi dukkhassa sambhavo, lokamimaṁ passa, puthū avijjāya paretā bhūtā bhūtaratā bhavā aparimuttā. ", "html": "{}"}, "pe2:24.4": {"pli": "Ye hi keci bhavā sabbadhi sabbatthatāya sabbete bhavā aniccā dukkhā vipariṇāmadhammāti. ", "html": "{}</p>"}, "pe2:25.1": {"pli": "“Evametaṁ yathābhūtaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:25.2": {"pli": "Sammappaññāya passato; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:25.3": {"pli": "Bhavataṇhā pahīyati, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:25.4": {"pli": "Vibhavaṁ nābhinandati; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:25.5": {"pli": "Sabbaso taṇhānaṁ khayā, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:25.6": {"pli": "Asesavirāganirodho nibbānaṁ. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:26.1": {"pli": "Tassa nibbutassa bhikkhuno, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:26.2": {"pli": "Anupādā punabbhavo na hoti; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:26.3": {"pli": "Abhibhūto māro vijitasaṅgāmo, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:26.4": {"pli": "Upeccagā sabbabhavāni tādī”ti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:27.1": {"pli": "Ayaṁ loko santāpajāto yāva dukkhanti yaṁ taṇhā saṅkileso. ", "html": "<p>{}</p>"}, "pe2:28.1": {"pli": "Yaṁ punaggahaṇaṁ ye hi keci samaṇā vā brāhmaṇā vā bhavena bhavassa vimokkhamāhaṁsu, sabbete “avimuttā bhavasmā”ti vadāmi. ", "html": "<p>{}"}, "pe2:28.2": {"pli": "Ye vā pana keci samaṇā vā brāhmaṇā vā vibhavena bhavassa nissaraṇamāhaṁsu “anissaṭā bhavasmā”ti vadāmi. ", "html": "{}"}, "pe2:28.3": {"pli": "Ayaṁ diṭṭhisaṅkileso, taṁ diṭṭhisaṅkileso ca taṇhāsaṅkileso ca, ubhayametaṁ saṅkileso. ", "html": "{}"}, "pe2:28.4": {"pli": "Yaṁ punaggahaṇaṁ bhavavippahānāya brahmacariyaṁ vussati, yāva sabbaso upādānakkhayā sambhavā, idaṁ nibbedhabhāgiyaṁ. ", "html": "{}"}, "pe2:28.5": {"pli": "Tassa nibbutassa bhikkhuno yāva upaccagā sabbabhavāni tādīti idaṁ asekkhabhāgiyaṁ. ", "html": "{}"}, "pe2:28.6": {"pli": "Cattāro puggalā anusotagāmī saṅkileso ṭhitatto ca paṭisotagāmī ca nibbedho. ", "html": "{}"}, "pe2:28.7": {"pli": "Thale tiṭṭhatīti asekkhabhūmi. ", "html": "{}</p>"}, "pe2:29.1": {"pli": "Tattha katamaṁ vāsanābhāgiyañca nibbedhabhāgiyañca suttaṁ? ", "html": "<p>{}</p>"}, "pe2:30.1": {"pli": "“Dadato puññaṁ pavaḍḍhati, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:30.2": {"pli": "Saṁyamato veraṁ na cīyati; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:30.3": {"pli": "Kusalo ca jahāti pāpakaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:30.4": {"pli": "Rāgadosamohakkhayā sanibbuto”ti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:31.1": {"pli": "“Dadato puññaṁ pavaḍḍhati, saṁyamato veraṁ na cīyatī”ti vāsanā. ", "html": "<p>{}"}, "pe2:31.2": {"pli": "“Kusalo ca jahāti pāpakaṁ, rāgadosamohakkhayā sanibbuto”ti nibbedho. ", "html": "{}</p>"}, "pe2:32.1": {"pli": "Sotānugatesu dhammesu vacasā paricitesu manasānupekkhitesu diṭṭhiyā suppaṭividdhesu pañcānisaṁsā pāṭikaṅkhā. ", "html": "<p>{}"}, "pe2:32.2": {"pli": "Idhekaccassa bahussutā dhammā honti dhātā apamuṭṭhā vacasā paricitā manasānupekkhitā diṭṭhiyā suppaṭividdhā, so yuñjanto ghaṭento vāyāmanto diṭṭheva dhamme visesaṁ pappoti. ", "html": "{}"}, "pe2:32.3": {"pli": "No ce diṭṭheva dhamme visesaṁ pappoti, gilāno pappoti. ", "html": "{}"}, "pe2:32.4": {"pli": "No ce gilāno pappoti, maraṇakālasamaye pappoti. ", "html": "{}"}, "pe2:32.5": {"pli": "No ce maraṇakālasamaye pappoti, devabhūto pāpuṇāti. ", "html": "{}"}, "pe2:32.6": {"pli": "No ce devabhūto pāpuṇāti, tena dhammarāgena tāya dhammanandiyā paccekabodhiṁ pāpuṇāti. ", "html": "{}</p>"}, "pe2:33.1": {"pli": "Tatthāyaṁ diṭṭheva dhamme pāpuṇāti, ayaṁ nibbedho. ", "html": "<p>{}"}, "pe2:33.2": {"pli": "Yaṁ samparāye paccekabodhiṁ pāpuṇāti, ayaṁ vāsanā. ", "html": "{}"}, "pe2:33.3": {"pli": "Imāni soḷasa suttāni sabbasāsanaṁ atiggaṇhanto tiṭṭhanti. ", "html": "{}"}, "pe2:33.4": {"pli": "Imehi soḷasahi suttehi navavidho suttanto vibhatto bhavati. ", "html": "{}"}, "pe2:33.5": {"pli": "So ca paññavato no duppaññassa, yuttassa no ayuttassa, akammassa vihārissa pakatiyā loke saṅkileso carati. ", "html": "{}"}, "pe2:33.6": {"pli": "So saṅkileso tividho—", "html": "{}"}, "pe2:33.7": {"pli": "taṇhāsaṅkileso diṭṭhisaṅkileso duccaritasaṅkileso. ", "html": "{}"}, "pe2:33.8": {"pli": "Tato saṅkilesato uṭṭhahanto saṅkileso dhammesu patiṭṭhahati, lokiyesu patiṭṭhahatīti. ", "html": "{}"}, "pe2:33.9": {"pli": "Tatthākusalo diṭṭhato sace taṁ sīlañca diṭṭhiñca parāmasati, tassa so taṇhāsaṅkileso hoti. ", "html": "{}"}, "pe2:33.10": {"pli": "Sace panassa evaṁ hoti “imināhaṁ sīlena vā vatena vā brahmacariyena vā devo vā bhavissaṁ devaññataro vā”ti yassa hoti micchādiṭṭhi, etassa micchādiṭṭhisaṅkileso bhavati. ", "html": "{}"}, "pe2:33.11": {"pli": "Sace pana sīle patiṭṭhito aparāmaṭṭhassa hi sīlavataṁ hoti, tassa taṁ sīlavato yoniso gahitaṁ avippaṭisāraṁ janeti yāva vimuttiñāṇadassanaṁ, tañca tassa diṭṭheva dhamme kālaṅkatassa vā tamhiyeva vā pana aparāpariyāyena vā, aññesu khandhesu evaṁ sutaṁ “sucaritaṁ vāsanāya saṁvattatī”ti vāsanābhāgiyaṁ suttaṁ vuccati. ", "html": "{}"}, "pe2:33.12": {"pli": "Tattha sīlesu ṭhitassa vinīvaraṇaṁ cittaṁ, taṁ tato sakkāyadiṭṭhippahānāya bhagavā dhammaṁ deseti. ", "html": "{}"}, "pe2:33.13": {"pli": "So accantaniṭṭhaṁ nibbānaṁ pāpuṇāti; ", "html": "{}"}, "pe2:33.14": {"pli": "yadi vā sāsanantare, accantaṁ nibbānaṁ pāpuṇāti, yadi vā ekāsane cha abhiññe. ", "html": "{}"}, "pe2:33.15": {"pli": "Tattha dve puggalā ariyadhamme pāpuṇanti saddhānusārī ca dhammānusārī ca. ", "html": "{}"}, "pe2:33.16": {"pli": "Tattha dhammānusārī ugghaṭitaññū, saddhānusārī neyyo. ", "html": "{}"}, "pe2:33.17": {"pli": "Tattha ugghaṭitaññū duvidho—", "html": "{}"}, "pe2:33.18": {"pli": "koci tikkhindriyo koci mudindriyo. ", "html": "{}"}, "pe2:33.19": {"pli": "Tattha neyyopi duvidho—", "html": "{}"}, "pe2:33.20": {"pli": "koci tikkhindriyo koci mudindriyo. ", "html": "{}"}, "pe2:33.21": {"pli": "Tattha yo ca ugghaṭitaññū mudindriyo, yo ca neyyo tikkhindriyo, ime puggalā asamindriyā honti. ", "html": "{}"}, "pe2:33.22": {"pli": "Tattha ime puggalā samindriyā parihāyanti ca ugghaṭitaññuto, vipañcitaññū neyyato, ime majjhimā bhūmigatā vipañcitaññū hoti. ", "html": "{}"}, "pe2:33.23": {"pli": "Ime tayo puggalā. ", "html": "{}</p>"}, "pe2:34.1": {"pli": "Tattha catutthā pana pañcamā ugghaṭitaññū vipañcitaññū neyyo ca, tattha ugghaṭitaññū puggalo indriyāni paṭilabhitvā dassanabhūmiyaṁ ṭhito sotāpattiphalañca pāpuṇāti, ekabījī hoti paṭhamo sotāpanno. ", "html": "<p>{}"}, "pe2:34.2": {"pli": "Tattha vipañcitaññū puggalo indriyāni paṭilabhitvā dassanabhūmiyaṁ ṭhito sotāpattiphalañca pāpuṇāti, kolaṅkolo ca hoti dutiyo sotāpanno. ", "html": "{}"}, "pe2:34.3": {"pli": "Tattha neyyo puggalo indriyāni paṭilabhitvā dassanabhūmiyaṁ ṭhito sotāpattiphalañca pāpuṇāti, sattakkhattuparamo ca hoti, ayaṁ tatiyo sotāpanno. ", "html": "{}"}, "pe2:34.4": {"pli": "Ime tayo puggalā indriyavemattatāya sotāpattiphale ṭhitā. ", "html": "{}</p>"}, "pe2:35.1": {"pli": "Ugghaṭitaññū ekabījī hoti, vipañcitaññū kolaṅkolo hoti, neyyo sattakkhattuparamo hoti. ", "html": "<p>{}"}, "pe2:35.2": {"pli": "Idaṁ nibbedhabhāgiyaṁ suttaṁ. ", "html": "{}"}, "pe2:35.3": {"pli": "Sace pana taduttari vāyamati, accantaniṭṭhaṁ nibbānaṁ pāpuṇāti. ", "html": "{}"}, "pe2:35.4": {"pli": "Tattha ugghaṭitaññū puggalo yo tikkhindriyo, te dve puggalā honti—", "html": "{}"}, "pe2:35.5": {"pli": "anāgāmiphalaṁ pāpuṇitvā antarāparinibbāyī ca upahaccaparinibbāyī ca. ", "html": "{}"}, "pe2:35.6": {"pli": "Tattha vipañcitaññū puggalo yo tikkhindriyo, te dve puggalā honti—", "html": "{}"}, "pe2:35.7": {"pli": "anāgāmiphalaṁ pāpuṇanti asaṅkhāraparinibbāyī ca sasaṅkhāraparinibbāyī ca. ", "html": "{}"}, "pe2:35.8": {"pli": "Tattha neyyo anāgāmiphalaṁ pāpuṇanto uddhaṁsoto akaniṭṭhagāmī hoti, ugghaṭitaññū ca vipañcitaññū ca, indriyanānattena ugghaṭitaññū puggalo tikkhindriyo antarāparinibbāyī hoti, ugghaṭitaññū mudindriyo uddhaṁsoto akaniṭṭhagāmī hoti. ", "html": "{}"}, "pe2:35.9": {"pli": "Ugghaṭitaññū ca vipañcitaññū ca indriyanānattena ugghaṭitaññū puggalo tikkhindriyo sasaṅkhāraparinibbāyī hoti, tikkhindriyo antarāparinibbāyī hoti, ugghaṭitaññū mudindriyo upahaccaparinibbāyī hoti. ", "html": "{}"}, "pe2:35.10": {"pli": "Vipañcitaññū tikkhindriyo asaṅkhāraparinibbāyī hoti, vipañcitaññū mudindriyo sasaṅkhāraparinibbāyī hoti, neyyo upahaccaparinibbāyī hoti, vipañcitaññū tikkhindriyo asaṅkhāraparinibbāyī hoti. ", "html": "{}"}, "pe2:35.11": {"pli": "Vipañcitaññū mudindriyo sasaṅkhāraparinibbāyī hoti, neyyo uddhaṁsoto akaniṭṭhagāmī hoti. ", "html": "{}"}, "pe2:35.12": {"pli": "Iti pañca anāgāmino, chaṭṭho sakadāgāmī, tayo ca sotāpannāti ime nava sekkhā. ", "html": "{}</p>"}, "pe2:36.1": {"pli": "Tattha ugghaṭitaññū puggalo tikkhindriyo arahattaṁ pāpuṇanto dve puggalā honti ubhatobhāgavimutto paññāvimutto ca. ", "html": "<p>{}"}, "pe2:36.2": {"pli": "Tattha ugghaṭitaññū puggalo mudindriyo arahattaṁ pāpuṇanto dve puggalā honti, ṭhitakappī ca paṭivedhanabhāvo ca. ", "html": "{}"}, "pe2:36.3": {"pli": "Tattha vipañcitaññū puggalo ca tikkhindriyo so arahattaṁ pāpuṇanto dve puggalā honti cetanābhabbo ca rakkhaṇābhabbo ca. ", "html": "{}"}, "pe2:36.4": {"pli": "Tattha vipañcitaññū mudindriyo arahattaṁ pāpuṇanto dve puggalā honti, sace ceteti na parinibbāyī, no ce ceteti parinibbāyīti. ", "html": "{}"}, "pe2:36.5": {"pli": "Sace anurakkhati na parinibbāyī, no ce anurakkhati parinibbāyīti. ", "html": "{}"}, "pe2:36.6": {"pli": "Tattha neyyo puggalo bhāvanānuyogamanuyutto parihānadhammo hoti kammaniyato vā samasīsi vā, ime nava arahanto idaṁ catubbidhaṁ suttaṁ saṅkilesabhāgiyaṁ asekkhabhāgiyaṁ. ", "html": "{}"}, "pe2:36.7": {"pli": "Imesu puggalesu tathāgatassa dasavidhaṁ balaṁ pavattati. ", "html": "{}</p>"}, "pe2:37.1": {"pli": "Katamaṁ dasavidhaṁ? ", "html": "<p>{}"}, "pe2:37.2": {"pli": "Idha buddhānaṁ bhagavantānaṁ appavattite dhammacakke mahesakkhā devaputtā yācanāya abhiyātā honti “desetu sugato dhamman”ti. ", "html": "{}"}, "pe2:37.3": {"pli": "So anuttarena buddhacakkhunā volokento addasāsi sattānaṁ tayo rāsīnaṁ sammattaniyato micchattaniyato aniyato. ", "html": "{}"}, "pe2:37.4": {"pli": "Tattha sammattaniyato rāsi micchāsatiṁ āpajjeyyāti netaṁ ṭhānaṁ vijjati, asatthuko parinibbāyeyyāti netaṁ ṭhānaṁ vijjati, samāpattiṁ āpajjeyyāti ṭhānametaṁ vijjati. ", "html": "{}"}, "pe2:37.5": {"pli": "Tattha micchattaniyato rāsi ariyasamāpattiṁ paṭipajjissatīti netaṁ ṭhānaṁ vijjati, anariyamicchāpaṭipattiṁ paṭipajjissatīti ṭhānametaṁ vijjati. ", "html": "{}"}, "pe2:37.6": {"pli": "Tattha aniyato rāsi sammāpaṭipajjamānaṁ sammattaniyatarāsiṁ gamissatīti ṭhānametaṁ vijjati, micchāpaṭipajjamāno sammattaniyatarāsiṁ gamissatīti netaṁ ṭhānaṁ vijjati. ", "html": "{}"}, "pe2:37.7": {"pli": "Sammāpaṭipajjamānaṁ sammattaniyatarāsiṁ gamissatīti ṭhānametaṁ vijjati, micchāpaṭipajjamānaṁ micchattaniyatarāsiṁ gamissatīti ṭhānametaṁ vijjati. ", "html": "{}"}, "pe2:37.8": {"pli": "Ime tayo anuttarena buddhacakkhunā volokentassa sammāsambuddhassa me sato ime dhammā anabhisambuddhāti ettāvatā maṁ koci sahadhammena paṭicodissatīti netaṁ ṭhānaṁ vijjati, vītarāgassa te paṭijānato akhīṇāsavatāya sahadhammena koci paṭicodissatīti netaṁ ṭhānaṁ vijjati. ", "html": "{}"}, "pe2:37.9": {"pli": "Yato pana imassa aniyatassa rāsissa dhammadesanā, sā na dissati takkarassa sammādukkhakkhayāyāti netaṁ ṭhānaṁ vijjati, tathā ovadito yaṁ pana me aniyatarāsi sāvako pubbenāparaṁ visesaṁ na sacchikarissatīti netaṁ ṭhānaṁ vijjati. ", "html": "{}</p>"}, "pe2:38.1": {"pli": "Yaṁ kho muni nānappakārassa nānāniruttiyo devanāgayakkhānaṁ dameti dhamme vavatthānena vatvā kāraṇato aññaṁ pāraṁ gamissatīti netaṁ ṭhānaṁ vijjati. ", "html": "<p>{}"}, "pe2:38.2": {"pli": "Dhammapaṭisambhidā. ", "html": "{}"}, "pe2:38.3": {"pli": "Yato panimā niruttito satta satta niruttiyo nābhisambhuneyyāti netaṁ ṭhānaṁ vijjati. ", "html": "{}"}, "pe2:38.4": {"pli": "Niruttipaṭisambhidā. ", "html": "{}"}, "pe2:38.5": {"pli": "Nirutti kho pana abhisamaggaratānaṁ sāvakānaṁ tamatthamaviññāpayeti netaṁ ṭhānaṁ vijjati. ", "html": "{}"}, "pe2:38.6": {"pli": "Atthapaṭisambhidā. ", "html": "{}"}, "pe2:38.7": {"pli": "Mahesakkhā devaputtā upasaṅkamitvā pañhe pucchiṁsu. ", "html": "{}"}, "pe2:38.8": {"pli": "Kāyikena vā mānasikena vā paripīḷitassa hatthakuṇīti vā pāde vā khañje dandhassa so attho na paribhājiyatīti netaṁ ṭhānaṁ vijjati. ", "html": "{}"}, "pe2:38.9": {"pli": "Paṭibhānapaṭisambhidā. ", "html": "{}"}, "pe2:38.10": {"pli": "Yamhi taṁ tesaṁ hoti tamhi asantaṁ bhavatīti netaṁ ṭhānaṁ vijjati. ", "html": "{}"}, "pe2:38.11": {"pli": "Yaṁ hi nāsaṁ tesaṁ na bhavati, tamhi nāsaṁ tesaṁ bhavissatīti netaṁ ṭhānaṁ vijjati. ", "html": "{}"}, "pe2:38.12": {"pli": "Evaṁ samudayassa nirodhāya dasa akusalakammapathā. ", "html": "{}"}, "pe2:38.13": {"pli": "Māro vā indo vā brahmā vā tathāgato vā cakkavattī vā so vata nāma mātugāmo bhavissatīti netaṁ ṭhānaṁ vijjati, puriso assa rājā cakkavattī sakko devānamindo bhavissatīti ṭhānametaṁ vijjati. ", "html": "{}"}, "pe2:38.14": {"pli": "Itissa evarūpaṁ balaṁ evarūpaṁ ñāṇaṁ, idaṁ vuccati ṭhānāṭṭhānañāṇaṁ paṭhamaṁ tathāgatabalaṁ taṁ niddisitabbaṁ. ", "html": "{}"}, "pe2:38.15": {"pli": "Tīhi rāsīhi catūhi vesārajjehi catūhi paṭisambhidāhi paṭiccasamuppādassa pavattiyaṁ nivattiyaṁ bhāgiyañca. ", "html": "{}"}, "pe2:38.16": {"pli": "Kusalaṁ kusalavipākesu ca upapajjati yañca itthipurisānaṁ. ", "html": "{}"}, "pe2:38.17": {"pli": "Idaṁ paṭhamaṁ balaṁ tathāgato evaṁ jānāti. ", "html": "{}</p>"}, "pe2:39.1": {"pli": "Yesaṁ pana sammattaniyato rāsi, nāyaṁ sabbatthagāminī paṭipadā, nibbānagāminīyevāyaṁ paṭipadā. ", "html": "<p>{}"}, "pe2:39.2": {"pli": "Tattha siyā micchattaniyato rāsi, esāpi na sabbatthagāminī paṭipadā. ", "html": "{}"}, "pe2:39.3": {"pli": "Sakkāyasamudayagāminīyevāyaṁ paṭipadā hotu, ayaṁ tattha tattha paṭipattiyā ṭhito gacchati nibbānaṁ, gacchati apāyaṁ, gacchati devamanussassa. ", "html": "{}"}, "pe2:39.4": {"pli": "Yaṁ yaṁ vā paṭipadaṁ paṭipajjeyya sabbattha gaccheyya, ayaṁ sabbatthagāminī paṭipadā. ", "html": "{}"}, "pe2:39.5": {"pli": "Yaṁ ettha ñāṇaṁ yathābhūtaṁ, idaṁ vuccati sabbatthagāminī paṭipadāñāṇaṁ dutiyaṁ tathāgatabalaṁ. ", "html": "{}</p>"}, "pe2:40.1": {"pli": "Sā kho panāyaṁ sabbatthagāminī paṭipadā nānādhimuttā keci kāmesu keci dukkarakāriyaṁ keci attakilamathānuyogamanuyuttā keci saṁsārena suddhiṁ paccenti keci anajjābhāvanāti. ", "html": "<p>{}"}, "pe2:40.2": {"pli": "Tena tena caritena vinibandhānaṁ sattānaṁ yaṁ ñāṇaṁ yathābhūtaṁ nānāgataṁ lokassa anekādhimuttagataṁ yathābhūtaṁ pajānāti. ", "html": "{}"}, "pe2:40.3": {"pli": "Idaṁ tatiyaṁ tathāgatabalaṁ. ", "html": "{}</p>"}, "pe2:41.1": {"pli": "Tattha sattānaṁ adhimuttā bhavanti āsevanti bhāventi bahulīkaronti. ", "html": "<p>{}"}, "pe2:41.2": {"pli": "Tesaṁ kammupasayānaṁ tadādhimuttānaṁ. ", "html": "{}"}, "pe2:41.3": {"pli": "Sā ceva dhātu saṁvahati. ", "html": "{}"}, "pe2:41.4": {"pli": "Katarā panesā dhātu nekkhammadhātu baladhātu kāci sampatti kāci micchattañca dhātu adhimuttā bhavanti. ", "html": "{}"}, "pe2:41.5": {"pli": "Aññatarā uttari na samanupassanti. ", "html": "{}"}, "pe2:41.6": {"pli": "Te tadevaṭṭhānaṁ mayā jarāmaraṇassa abhinivissa voharanti “idameva saccaṁ moghamaññan”ti. ", "html": "{}"}, "pe2:41.7": {"pli": "Yathā bhagavā sakkassa devānamindassa bhāsitaṁ. ", "html": "{}"}, "pe2:41.8": {"pli": "Yaṁ tattha yathābhūtaṁ ñāṇaṁ. ", "html": "{}"}, "pe2:41.9": {"pli": "Idaṁ vuccati catutthaṁ tathāgatabalaṁ. ", "html": "{}</p>"}, "pe2:42.1": {"pli": "Tattha yaṁyeva dhātu seṭṭhanti taṁ taṁ kāyena ca vācāya ca ārambhanti cetasiko. ", "html": "<p>{}"}, "pe2:42.2": {"pli": "Ārambho cetanā kammaṁ kāyikā vācasikā ārambho cetasikattā kammantaraṁ tathāgato evaṁ pajānāti “iminā sattena evaṁ dhātukena evarūpaṁ kammaṁ kataṁ, taṁ atītamaddhānaṁ iminā hetunā tassa evarūpo vipāko vipaccati etarahi vipaccissati vā anāgatamaddhānan”ti. ", "html": "{}"}, "pe2:42.3": {"pli": "Evaṁ paccuppannamaddhānaṁ pajānāti “ayaṁ puggalo evaṁdhātuko idaṁ kammaṁ karoti. ", "html": "{}"}, "pe2:42.4": {"pli": "Taṇhāya ca diṭṭhiyā ca iminā hetunā na tassa vipāko diṭṭheyeva dhamme nibbattissati, upapajje vā”ti aparamhi vā pariyāye evaṁ pajānāti “ayaṁ puggalo evarūpaṁ kammaṁ karissati anāgatamaddhānaṁ, iminā hetunā tassa evarūpo vipāko nibbattissati, iminā hetunā yāni cattāri kammaṭṭhānāni idaṁ kammaṭṭhānaṁ paccuppannasukhaṁ āyatiṁ ca sukhavipākaṁ” …pe… ", "html": "{}"}, "pe2:42.5": {"pli": "iti ayaṁ atītānāgatapaccuppannānaṁ kammasamādānānaṁ hetuso ṭhānaso vipākavemattataṁ pajānāti uccāvacā hīnapaṇītatā, idaṁ vuccati kammavipākañāṇaṁ pañcamaṁ tathāgatabalaṁ. ", "html": "{}</p>"}, "pe2:43.1": {"pli": "Tathā sattā yaṁ vā kammasamādānaṁ samādiyantā tattha evaṁ pajānāti imassa puggalassa kammādhimuttassa rāgacaritassa nekkhammadhātūnaṁ pāripūriṁ gacchanti, tassa rāgānugate suññamānassa paṭhamaṁ jhānaṁ saṅkilissati, sace puna uttari vāyāmato jhānavodānagate mānase visesabhāgiyaṁ paṭipadaṁ anuyuñjiyati. ", "html": "<p>{}"}, "pe2:43.2": {"pli": "Tassa hi jhānabhāgiyaṁyeva paṭhamajjhāne ṭhitassa dutiyaṁ jhānaṁ vodānaṁ gacchati, tatiyañca jhānaṁ samāpajjitukāmassa somanassindriyaṁ cittaṁ pariyādāya tiṭṭhati, tassa sā pīti avisesabhāgiyaṁ tatiyaṁ jhānaṁ ādissa tiṭṭhati. ", "html": "{}"}, "pe2:43.3": {"pli": "Sace tassa nissaraṇaṁ yathābhūtaṁ pajānāti. ", "html": "{}"}, "pe2:43.4": {"pli": "Tathāgatassa catutthajjhānaṁ vodānaṁ gacchatiyeva, catutthassa jhānassa hānabhāgiyā dhammā, te ca dhammā yattha pajāyanti yehi catutthajjhānaṁ vodānaṁ dissati. ", "html": "{}"}, "pe2:43.5": {"pli": "Evaṁ ajjhāsayasamāpattiyā yā catasso samāpattiyo tīṇi vimokkhamukhāni aṭṭha vimokkhajhānānīti cattāri jhānāni vimokkhāti. ", "html": "{}"}, "pe2:43.6": {"pli": "Aṭṭha ca vimokkhā tīṇi ca vimokkhamukhāni. ", "html": "{}"}, "pe2:43.7": {"pli": "Samādhīti cattāro samādhī—", "html": "{}"}, "pe2:43.8": {"pli": "chandasamādhi vīriyasamādhi cittasamādhi vīmaṁsāsamādhīti. ", "html": "{}"}, "pe2:43.9": {"pli": "Samāpattiyo catasso ajjhāsayasamāpattiyo iti imesaṁ jhānānaṁ vimokkhasamāpattīti evarūpo saṅkileso rāgacaritassa puggalassa. ", "html": "{}"}, "pe2:43.10": {"pli": "Evaṁ dosacaritassa … mohacaritassa … rāgacaritassa puggalassa evarūpaṁ vodānaṁ iti yaṁ ettha ñāṇaṁ yathābhūtaṁ asādhāraṇaṁ sabbasattehi. ", "html": "{}"}, "pe2:43.11": {"pli": "Idaṁ vuccati chaṭṭhaṁ tathāgatabalaṁ. ", "html": "{}</p>"}, "pe2:44.1": {"pli": "Tattha tathāgato evaṁ pajānāti lokikā dhammā lokuttarā dhammā bhāvanābhāgiyaṁ indriyaṁ nāmaṁ labhanti. ", "html": "<p>{}"}, "pe2:44.2": {"pli": "Ādhipateyyabhūmiṁ upādāya balaṁ nāmaṁ labhanti thāmagataṁ mano manindriyaṁ taṁ upādāya. ", "html": "{}"}, "pe2:44.3": {"pli": "Vīriyaṁ nāmaṁ labhanti ārambhadhātuṁ upādāya. ", "html": "{}"}, "pe2:44.4": {"pli": "Itissa deva evarūpaṁ ñāṇaṁ imehi ca dhammehi ime puggalā samannāgatātipi dhammadesanaṁ akāsi. ", "html": "{}"}, "pe2:44.5": {"pli": "Ākārato ca vokārato ca āsayajjhāsayassa adhimuttisamannāgatānaṁ. ", "html": "{}"}, "pe2:44.6": {"pli": "Idaṁ vuccati parasattānaṁ parapuggalānaṁ indriyabalavīriyavemattataṁ ñāṇaṁ sattamaṁ tathāgatabalaṁ. ", "html": "{}</p>"}, "pe2:45.1": {"pli": "Tattha ca tathāgato lokādīsu ca bhūmīsu saṁyojanānañca sekkhānaṁ dvīhi balehi gatiṁ pajānāti, pubbenivāsānussatiyā atīte saṁsāre etarahi ca paccuppanne dibbacakkhunā cutūpapātaṁ iti imāni dve balāni dibbacakkhuto abhinīhitāni. ", "html": "<p>{}"}, "pe2:45.2": {"pli": "So atītamaddhānaṁ dibbassa cakkhuno gocaro so etarahi sati gocaro iti attano ca paresaṁ ca pubbenivāsañāṇaṁ anekavidhaṁ nānappakārakaṁ paccuppannamaddhānaṁ dibbena cakkhunā imāni dve tathāgatabalāni, aṭṭhamaṁ pubbenivāso, navamaṁ dibbacakkhu. ", "html": "{}</p>"}, "pe2:46.1": {"pli": "Puna caparaṁ tathāgato ariyapuggalānaṁ jhānaṁ vodānaṁ nibbedhabhāgiyaṁ pajānāti ayaṁ puggalo iminā maggena imāya paṭipadāya āsavānaṁ khayā anāsavaṁ cetovimuttiṁ paññāvimuttiṁ diṭṭheva dhamme sacchikatvā upasampajja viharatīti iti attano ca āsavānaṁ khayaṁ ñāṇaṁ diṭṭhekaṭṭhānaṁ catubhūmimupādāya yāva navannaṁ arahantānaṁ āsavakkhayo odhiso sekkhānaṁ anodhiso arahantānaṁ. ", "html": "<p>{}"}, "pe2:46.2": {"pli": "Tattha cetovimutti dvīhi āsavehi anāsavā kāmāsavena ca bhavāsavena ca, paññāvimutti dvīhi āsavehi anāsavā diṭṭhāsavena ca avijjāsavena ca, imāsaṁ dvinnaṁ vimuttīnaṁ yathābhūtaṁ ñāṇaṁ, idaṁ vuccati āsavakkhaye ñāṇaṁ. ", "html": "{}"}, "pe2:46.3": {"pli": "Dasamaṁ tathāgatabalaṁ. ", "html": "{}</p>"}, "pe2:47.1": {"pli": "Imesu dasasu balesu ṭhito tathāgato pañcavidhaṁ sāsanaṁ deseti saṅkilesabhāgiyaṁ vāsanābhāgiyaṁ dassanabhāgiyaṁ bhāvanābhāgiyaṁ asekkhabhāgiyaṁ. ", "html": "<p>{}"}, "pe2:47.2": {"pli": "Tattha yo taṇhāsaṅkileso, imassa alobho nissaraṇaṁ. ", "html": "{}"}, "pe2:47.3": {"pli": "Yo diṭṭhisaṅkileso, imassa amoho nissaraṇaṁ. ", "html": "{}"}, "pe2:47.4": {"pli": "Yo duccaritasaṅkileso, imassa tīṇi kusalāni nissaraṇaṁ. ", "html": "{}"}, "pe2:47.5": {"pli": "Kiṁ nidānaṁ? ", "html": "{}"}, "pe2:47.6": {"pli": "Tīṇi imāni manoduccaritāni—", "html": "{}"}, "pe2:47.7": {"pli": "abhijjhā byāpādo micchādiṭṭhi. ", "html": "{}"}, "pe2:47.8": {"pli": "Tattha abhijjhā manoduccaritaṁ kāyakammaṁ upaṭṭhapeti, adinnādānaṁ sabbañca tadupanibbaddhaṁ vācākammaṁ upaṭṭhapeti, musāvādañca sabbavitathaṁ sabbaṁ vācamabhāvaṁ sabbamakkhaṁ palāsaṁ abhijjhā akusalamūlanti, sucarite sucaritaṁ musāvādā adinnādānā abhijjhāya cetanā, tattha byāpādo manoduccaritaṁ kāyakammaṁ upaṭṭhapeti, pāṇātipātaṁ sabbañca metaṁ ākaḍḍhanaṁ parikaḍḍhanaṁ nibbaddhaṁ rocanaṁ vācākammaṁ upaṭṭhapeti, pisuṇavācaṁ pharusavācaṁ micchādiṭṭhi manoduccaritañca abhijjhaṁ byāpādaṁ micchādiṭṭhiṁ payojeti, tassa yo koci micchādiṭṭhi cāgo rāgajo vā dosajo vā sabbaso micchādiṭṭhi sambhūto iminā kāraṇena micchādiṭṭhiṁ upaṭṭhapeti, kāmesumicchācāraṁ vacīkammaṁ upaṭṭhapeti samphappalāpaṁ. ", "html": "{}"}, "pe2:47.9": {"pli": "Imāni tīṇi duccaritāni akusalamūlāni. ", "html": "{}</p>"}, "pe2:48.1": {"pli": "Yā abhijjhā, so lobho. ", "html": "<p>{}"}, "pe2:48.2": {"pli": "Yo byāpādo, so doso. ", "html": "{}"}, "pe2:48.3": {"pli": "Yā micchādiṭṭhi, so moho. ", "html": "{}"}, "pe2:48.4": {"pli": "Tāni aṭṭha micchattāni upaṭṭhapenti. ", "html": "{}"}, "pe2:48.5": {"pli": "Tesu gahitesu tīsu akusalamūlesu dasavidhaṁ akusalamūlaṁ pāripūriṁ gacchati, tassa tividhassa duccaritasaṅkilesassa vāsanābhāgiyañca suttaṁ nissaraṇaṁ. ", "html": "{}"}, "pe2:48.6": {"pli": "Tattha yo bahusito niddeso yathā lobho doso mohopi, tattha asituṁ ettha lobho ussado tena kāraṇena tesu vā dhammesu lobho paññapiyati. ", "html": "{}"}, "pe2:48.7": {"pli": "Tatthāyaṁ moho akusalaṁ moho ayaṁ avijjā, sā catubbidhā rūpe abhiniviṭṭhā, rūpaṁ attato samanupassati, avijjāgato rūpavantaṁ attānaṁ, attani vā rūpaṁ, rūpasmiṁ vā attānaṁ. ", "html": "{}"}, "pe2:48.8": {"pli": "Tattha katamaṁ padaṁ sakkāyadiṭṭhiyā ucchedaṁ vadati “taṁ jīvaṁ taṁ sarīran”ti natthikadiṭṭhi adhiccasamuppannadiṭṭhi ca añño ca karoti, añño paṭisaṁvediyati. ", "html": "{}"}, "pe2:48.9": {"pli": "Pacchimasaṭṭhikappānaṁ tīṇi padāni sakkāyadiṭṭhiyā sassataṁ bhajanti “aññaṁ jīvaṁ aññaṁ sarīran”ti akiriyañca taṁ dukkhamicchato ahetukā ca patanti anajjhābhāvo ca kammānaṁ sabbañca mānayi. ", "html": "{}"}, "pe2:48.10": {"pli": "Tattha “idameva saccaṁ moghamaññan”ti saṁsārena suddhi ājīvakā chaḷāsīti paññapenti. ", "html": "{}"}, "pe2:48.11": {"pli": "Yathārūpe sakkāyadiṭṭhiyā catuvatthukā, evaṁ pañcasu khandhesu vīsativatthukā sakkāyadiṭṭhiyā sassataṁ bhajati. ", "html": "{}"}, "pe2:48.12": {"pli": "Aññājīvakā ca sassatavādike ca sīlabbataṁ bhajanti parāmasanti iminā bhavissāmi devo vā devaññataro vā, ayaṁ sīlabbataparāmāso. ", "html": "{}"}, "pe2:48.13": {"pli": "Tattha sakkāyadiṭṭhiyā so rūpaṁ attato samanupassati, “taṁ jīvaṁ taṁ sarīram”iti taṁ kaṅkhati vicikicchati nādhimuccati nābhippasīdati pubbante aparante pubbantāparante …pe… ", "html": "{}"}, "pe2:48.14": {"pli": "iti vāsanābhāgiyesu ṭhitassa ayaṁ upakkileso. ", "html": "{}</p>"}, "pe2:49.1": {"pli": "Tattha saddhindriyena sabbaṁ vicikicchitaṁ pajahati, paññindriyena udayabbayaṁ passati, samādhindriyena cittaṁ ekodi karoti vīriyindriyena ārabhati. ", "html": "<p>{}"}, "pe2:49.2": {"pli": "So imehi pañcahi indriyehi saddhānusārī aveccappasāde nirato anantariyaṁ samādhiṁ uppādeti. ", "html": "{}"}, "pe2:49.3": {"pli": "Indriyehi suddhehi dhammānusārī appaccayatāya anantariyaṁ samādhiṁ uppādeti. ", "html": "{}"}, "pe2:49.4": {"pli": "So “idaṁ dukkhan”ti yathābhūtaṁ pajānāti. ", "html": "{}"}, "pe2:49.5": {"pli": "Saccāni idaṁ dassanabhāgiyaṁ suttaṁ. ", "html": "{}"}, "pe2:49.6": {"pli": "Tassa pañcannaṁ orambhāgiyānaṁ saṁyojanānaṁ tīṇi saṁyojanāni dassanapahātabbāni sabbena sabbaṁ pahīnāni dve puggalakatāni. ", "html": "{}"}, "pe2:49.7": {"pli": "Tattha tīṇi akusalamūlāni bhāvanāpahātabbāni uparikkhittāni cha bhave nibbattenti. ", "html": "{}"}, "pe2:49.8": {"pli": "Tattha tesu abhijjhāya ca byāpādesu tanukatesu cha bhavā parikkhayā mariyādaṁ gacchanti, dve bhavā avasiṭṭhā. ", "html": "{}"}, "pe2:49.9": {"pli": "Tassa abhijjhā ca byāpādo ca sabbena sabbaṁ parikkhīṇā honti. ", "html": "{}"}, "pe2:49.10": {"pli": "Eko bhavo avasiṭṭho hoti. ", "html": "{}"}, "pe2:49.11": {"pli": "So ca mānavasena nibbatteti. ", "html": "{}"}, "pe2:49.12": {"pli": "Kiñcāpi ettha aññepi cattāro kilesā rūparāgo bhavarāgo avijjā uddhaccaṁ ketusmimānabhūtā nappaṭibalā asmimānaṁ vinivattetuṁ, sabbepi te asmimānassa pahānaṁ ārabhate. ", "html": "{}"}, "pe2:49.13": {"pli": "Khīṇesu na ca tesu idamuttaridassanabhūmiyaṁ pañcasu sekkhapuggalesu tīsu ca paṭippannakesu dvīsu ca phalaṭṭhesu bhāvanābhāgiyaṁ suttaṁ. ", "html": "{}"}, "pe2:49.14": {"pli": "Taduttari asekkhabhāgiyasuttaṁ, katthaci bhūmi nipīḷiyati. ", "html": "{}"}, "pe2:49.15": {"pli": "Idañca pañcamaṁ suttaṁ. ", "html": "{}"}, "pe2:49.16": {"pli": "Tiṇṇaṁ puggalānaṁ desitaṁ puthujjanassa sekkhassa asekkhassa saṅkilesabhāgiyaṁ vāsanābhāgiyaṁ. ", "html": "{}"}, "pe2:49.17": {"pli": "Puthujjanassa dassanabhāgiyaṁ. ", "html": "{}"}, "pe2:49.18": {"pli": "Bhāvanābhāgiyaṁ pañcannaṁ sekkhānaṁ. ", "html": "{}"}, "pe2:49.19": {"pli": "Yaṁ paṭhamaniddiṭṭhaṁ asekkhabhāgiyaṁ sabbesaṁ arahantānaṁ. ", "html": "{}"}, "pe2:49.20": {"pli": "Sā pana pañcavidhā sattavīsaākāre pariyesitabbaṁ. ", "html": "{}"}, "pe2:49.21": {"pli": "Etesu tassa gatīnaṁ tato uttari. ", "html": "{}"}, "pe2:49.22": {"pli": "Tañca kho saṅkhepena paññāsāya ākārehi sampatati, ye paññāsa ākārā sāsane niddiṭṭhā, te saṅkhipiyantā dasahi ākārehi patanti. ", "html": "{}"}, "pe2:49.23": {"pli": "Ye ariyasaccaṁ nikkhepena ṭhite saṅkhipiyattā aṭṭhasu ākāresu patanti. ", "html": "{}"}, "pe2:49.24": {"pli": "Catūsu ca sādhāraṇesu suttesu yā hārasampātassa bhūmi, te saṅkhipiyantā pañcasu suttesu patanti. ", "html": "{}"}, "pe2:49.25": {"pli": "Saṅkilesabhāgiye vāsanābhāgiye bhāvanābhāgiye nibbedhabhāgiye asekkhabhāgiye ca. ", "html": "{}"}, "pe2:49.26": {"pli": "Te saṅkhipiyantā catūsu suttesu patanti. ", "html": "{}"}, "pe2:49.27": {"pli": "Saṅkilesabhāgiye vāsanābhāgiye nibbedhabhāgiye asekkhabhāgiye ca. ", "html": "{}"}, "pe2:49.28": {"pli": "Te saṅkhipiyamānā tīsu suttesu patanti, puthujjanabhāgiye sekkhabhāgiye asekkhabhāgiye ca. ", "html": "{}"}, "pe2:49.29": {"pli": "Te saṅkhipiyantā dvīsu suttesu patanti nibbedhabhāgiye ca pubbayogabhāgiye ca. ", "html": "{}"}, "pe2:49.30": {"pli": "Yathā vuttaṁ bhagavatā dve atthavase sampassamānā tathāgatā arahanto sammāsambuddhā dhammaṁ desenti suttaṁ geyyaṁ …pe… ", "html": "{}"}, "pe2:49.31": {"pli": "satthā pubbayogasamannāgate appakasirena maññamānā vasiyanti pubbayogā ca bhavissanti santānaṁ maññamānādharāya. ", "html": "{}"}, "pe2:49.32": {"pli": "Tattha paññāvemattataṁ attano samanupassamānena aṭṭhavidhe suttasaṅkhepe, yattha yattha sakkoti, tattha tattha yojetabbaṁ. ", "html": "{}"}, "pe2:49.33": {"pli": "Tattha tattha yojetvā suttassa attho niddisitabbo. ", "html": "{}"}, "pe2:49.34": {"pli": "Na hi sati vedanā mano dhāretvā sakkā yena kenaci suttassa attho yathābhūtaṁ niddisituṁ. ", "html": "{}</p>"}, "pe2:50.1": {"pli": "Tattha purimakānaṁ suttānaṁ imā uddānagāthā ", "html": "<p class='uddana-intro'>{}</p>"}, "pe2:51.1": {"pli": "Kāmandhā jālasañchannā, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "pe2:51.2": {"pli": "pañca nīvaraṇāni ca; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:51.3": {"pli": "Manopubbaṅgamā dhammā, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:51.4": {"pli": "mahānāmo ca sākiyo. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:52.1": {"pli": "Uddhaṁ adho vippamutto, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:52.2": {"pli": "yañca sīlakimatthiyā; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:52.3": {"pli": "Yassa selūpamaṁ cittaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:52.4": {"pli": "upatissa pucchādikā. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:53.1": {"pli": "Yassa kāyagatāsati, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:53.2": {"pli": "channaṁ tamoparāyaṇo; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:53.3": {"pli": "Na taṁ daḷhaṁ cetasikaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:53.4": {"pli": "ayaṁ lokotiādikaṁ. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:54.1": {"pli": "Cattāro ceva puggalā, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:54.2": {"pli": "dadato puññaṁ pavaḍḍhitaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:54.3": {"pli": "Sotānugatadhammesu, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:54.4": {"pli": "imā tesaṁ uddānagāthā. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:55.1": {"pli": "Tattha katamā āṇatti? ", "html": "<p>{}</p>"}, "pe2:56.1": {"pli": "“Sace bhāyatha dukkhassa, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:56.2": {"pli": "sace vo dukkhamappiyaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:56.3": {"pli": "Mākattha pāpakaṁ kammaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:56.4": {"pli": "āvi vā yadi vā raho”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:57.1": {"pli": "“Atīte, rādha, rūpe anapekkho hohī”ti vitthārena kātabbā. ", "html": "<p>{}"}, "pe2:57.2": {"pli": "“Sīlavantena, ānanda, puggalena sadā karaṇīyā kintime avippaṭisāro assā”ti. ", "html": "{}"}, "pe2:57.3": {"pli": "Ayaṁ vuccati āṇatti. ", "html": "{}</p>"}, "pe2:58.1": {"pli": "Tattha katamaṁ phalaṁ? ", "html": "<p>{}</p>"}, "pe2:59.1": {"pli": "“Dhammo have rakkhati dhammacāriṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:59.2": {"pli": "Chattaṁ mahantaṁ yatha vassakāle; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:59.3": {"pli": "Esānisaṁso dhamme suciṇṇe, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:59.4": {"pli": "Na duggatiṁ gacchati dhammacārī”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:60.1": {"pli": "Idaṁ phalaṁ. ", "html": "<p class='endsutta'>{}</p>"}, "pe2:61.1": {"pli": "Tattha katamo upāyo? ", "html": "<p>{}</p>"}, "pe2:62.1": {"pli": "“Sabbe dhammā anattāti, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:62.2": {"pli": "yadā paññāya passati; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:62.3": {"pli": "Atha nibbindati dukkhe, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:62.4": {"pli": "esa maggo visuddhiyā”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:63.1": {"pli": "“Sattahaṅgehi samannāgato kho, bhikkhu, api himavantaṁ pabbatarājānaṁ cāleyya, ko pana vādo chavaṁ avijjaṁ sattakesu” veyyākaraṇaṁ kātabbaṁ. ", "html": "<p>{}"}, "pe2:63.2": {"pli": "Ayaṁ upāyo. ", "html": "{}</p>"}, "pe2:64.1": {"pli": "Tattha katamā āṇatti ca phalañca? ", "html": "<p>{}</p>"}, "pe2:65.1": {"pli": "Sace bhāyatha dukkhassa, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:65.2": {"pli": "sace vo dukkhamappiyaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:65.3": {"pli": "Mākattha pāpakaṁ kammaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:65.4": {"pli": "āvi vā yadi vā raho. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:66.1": {"pli": "“Sace hi pāpakaṁ kammaṁ, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:66.2": {"pli": "karotha vā karissatha; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:66.3": {"pli": "Na vo dukkhā pamokkhātthi, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:66.4": {"pli": "upaccāpi palāyataṁ”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:67.1": {"pli": "Purimikāya gāthāya āṇatti pacchimikāya phalaṁ. ", "html": "<p>{}"}, "pe2:67.2": {"pli": "Sīle patiṭṭhāya dve dhammā bhāvetabbā yā ca cittabhāvanā yā ca paññābhāvanā yā ca āṇatti rāgavirāgā ca phalaṁ. ", "html": "{}</p>"}, "pe2:68.1": {"pli": "Tattha katamaṁ phalañca upāyo ca? ", "html": "<p>{}</p>"}, "pe2:69.1": {"pli": "“Sīle patiṭṭhāya naro sapañño, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:69.2": {"pli": "Cittaṁ paññañca bhāvayaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:69.3": {"pli": "Ātāpī nipako bhikkhu, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:69.4": {"pli": "So imaṁ vijaṭaye jaṭaṁ”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:70.1": {"pli": "Purimikāya aḍḍhagāthāya upāyo, pacchimikāya aḍḍhagāthāya phalaṁ. ", "html": "<p>{}"}, "pe2:70.2": {"pli": "Nandiyo sakko isivutthapuririkāmaekarakkhe suttaṁ mūlato upādāya yāva chasu dhammesu. ", "html": "{}"}, "pe2:70.3": {"pli": "Uttari pañcasu dhammesu yācayogo karaṇīyo, ayaṁ upāyo. ", "html": "{}"}, "pe2:70.4": {"pli": "Asahagatassa kāmāsavāpi cittaṁ muccatīti. ", "html": "{}"}, "pe2:70.5": {"pli": "Sabbāsu chasu tīsu. ", "html": "{}"}, "pe2:70.6": {"pli": "Ayaṁ upāyo ca phalañca. ", "html": "{}</p>"}, "pe2:71.1": {"pli": "Tattha katamā āṇatti ca phalañca upāyo ca? ", "html": "<p>{}</p>"}, "pe2:72.1": {"pli": "“Suññato lokaṁ avekkhassu, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:72.2": {"pli": "mogharāja sadā sato; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:72.3": {"pli": "Attānudiṭṭhiṁ uhacca, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:72.4": {"pli": "evaṁ maccutaro siyā”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:73.1": {"pli": "“Suññato lokaṁ avekkhassu, mogharājā”ti āṇatti. ", "html": "<p>{}"}, "pe2:73.2": {"pli": "“Sadā sato”ti upāyo. ", "html": "{}"}, "pe2:73.3": {"pli": "“Attānudiṭṭhiṁ uhacca, evaṁ maccutaro siyā”ti phalaṁ. ", "html": "{}"}, "pe2:73.4": {"pli": "“Samādhiṁ, bhikkhave, bhāvetha, samāhito, bhikkhave, bhikkhu rūpaṁ aniccanti pajānāti. ", "html": "{}"}, "pe2:73.5": {"pli": "Evaṁ passaṁ ariyasāvako parimuccati jātiyāpi …pe… ", "html": "{}"}, "pe2:73.6": {"pli": "upāyāsehipi idha tīṇipi”. ", "html": "{}</p>"}, "pe2:74.1": {"pli": "Tattha katamo assādo? ", "html": "<p>{}</p>"}, "pe2:75.1": {"pli": "Kāmaṁ kāmayamānassa, tassa cetaṁ samijjhati. ", "html": "<p>{}"}, "pe2:75.2": {"pli": "Ayaṁ assādo. ", "html": "{}</p>"}, "pe2:76.1": {"pli": "“Dhammacariyā samacariyā kusalacariyā hetūhi, brāhmaṇa, evam’idhekacce sattā kāyassa bhedā sugatiṁ saggaṁ lokaṁ upapajjanti”. ", "html": "<p>{}"}, "pe2:76.2": {"pli": "Ayaṁ assādo. ", "html": "{}</p>"}, "pe2:77.1": {"pli": "Tattha katamo ādīnavo? ", "html": "<p>{}</p>"}, "pe2:78.1": {"pli": "Kāmesu ve haññate sabbā mucceva—", "html": "<p>{}"}, "pe2:78.2": {"pli": "ayaṁ ādīnavo. ", "html": "{}"}, "pe2:78.3": {"pli": "Pasenadisaṁyuttake sutte pabbatopamā—", "html": "{}"}, "pe2:78.4": {"pli": "ayaṁ ādīnavo. ", "html": "{}</p>"}, "pe2:79.1": {"pli": "Tattha katamaṁ nissaraṇaṁ? ", "html": "<p>{}</p>"}, "pe2:80.1": {"pli": "“Yo kāme parivajjeti, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:80.2": {"pli": "sappasseva padā siro; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:80.3": {"pli": "Somaṁ visattikaṁ loke, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:80.4": {"pli": "sato samativattati”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:81.1": {"pli": "Saṁyuttake suttaṁ pāricchattako paṇḍupalāso sannipalāso—", "html": "<p>{}"}, "pe2:81.2": {"pli": "idaṁ nissaraṇaṁ. ", "html": "{}</p>"}, "pe2:82.1": {"pli": "Tattha katamo assādo ca ādīnavo ca? ", "html": "<p>{}</p>"}, "pe2:83.1": {"pli": "“Yāni karoti puriso, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:83.2": {"pli": "tāni attani passati; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:83.3": {"pli": "Kalyāṇakārī kalyāṇaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:83.4": {"pli": "pāpakārī ca pāpakaṁ”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:84.1": {"pli": "Tattha yaṁ pāpakārī paccanubhoti ayaṁ assādo. ", "html": "<p>{}"}, "pe2:84.2": {"pli": "Lābhālābhaaṭṭhakesu byākaraṇaṁ, tattha alābho ayaso nindā dukkhaṁ, ayaṁ ādīnavo. ", "html": "{}"}, "pe2:84.3": {"pli": "Lābho yaso sukhaṁ pasaṁsā, ayaṁ assādo. ", "html": "{}</p>"}, "pe2:85.1": {"pli": "Tattha katamaṁ assādo ca nissaraṇañca? ", "html": "<p>{}</p>"}, "pe2:86.1": {"pli": "“Sukho vipāko puññānaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:86.2": {"pli": "adhippāyo ca ijjhati; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:86.3": {"pli": "Khippañca paramaṁ santiṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:86.4": {"pli": "nibbānamadhigacchatī”ti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:87.1": {"pli": "Yo ca vipāko puññānaṁ yā ca adhippāyassa ijjhanā, ayaṁ assādo. ", "html": "<p>{}"}, "pe2:87.2": {"pli": "Yaṁ khippañca paramaṁ santiṁ nibbānamadhigacchati, idaṁ nissaraṇaṁ. ", "html": "{}</p>"}, "pe2:88.1": {"pli": "Bāttiṁsāya ceva mahāpurisalakkhaṇehi samannāgatassa mahāpurisassa dveyeva gatiyo honti, sace agāraṁ ajjhāvasati, rājā hoti cakkavattī yāva abhivijinitvā ajjhāvasati ayaṁ assādo. ", "html": "<p>{}"}, "pe2:88.2": {"pli": "Sace agārasmā anagāriyaṁ pabbajati sabbena oghena nissaraṇaṁ ayaṁ assādo ca nissaraṇañca. ", "html": "{}</p>"}, "pe2:89.1": {"pli": "Tattha katamo ādīnavo ca nissaraṇañca? ", "html": "<p>{}</p>"}, "pe2:90.1": {"pli": "“Ādānassa bhayaṁ ñatvā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:90.2": {"pli": "Jātimaraṇasambhavaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:90.3": {"pli": "Anādātuṁ nibbattati, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:90.4": {"pli": "Jātimaraṇasaṅkhayā”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:91.1": {"pli": "Purimikāya aḍḍhagāthāya jātimaraṇasambhavo ādīnavo. ", "html": "<p>{}"}, "pe2:91.2": {"pli": "Anādātuṁ nibbattati jātimaraṇasaṅkhayāti nissaraṇaṁ. ", "html": "{}</p>"}, "pe2:92.1": {"pli": "Kicchaṁ vatāyaṁ loko āpanno yamidaṁ jāyate ca mīyate ca. ", "html": "<p>{}"}, "pe2:92.2": {"pli": "Yāva kudassunāmassa dukkhassa anto bhavissati parato vāti ettha yā uparikkhā, ayaṁ ādīnavo. ", "html": "{}"}, "pe2:92.3": {"pli": "Yo gedhaṁ ñatvā abhinikkhamati yāva purāṇakāya rājadhāniyā, idaṁ nissaraṇaṁ. ", "html": "{}"}, "pe2:92.4": {"pli": "Ayaṁ ādīnavo ca nissaraṇañca. ", "html": "{}</p>"}, "pe2:93.1": {"pli": "Tattha katamo assādo ca ādīnavo ca nissaraṇañca? ", "html": "<p>{}</p>"}, "pe2:94.1": {"pli": "“Kāmā hi citrā vividhā manoramā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:94.2": {"pli": "Virūparūpehi mathenti cittaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:94.3": {"pli": "Tasmā ahaṁ pabbajitomhi rāja, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:94.4": {"pli": "Apaṇṇakaṁ sāmaññameva seyyo”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:95.1": {"pli": "Yaṁ “kāmā hi citrā vividhā manoramā”ti ayaṁ assādo. ", "html": "<p>{}"}, "pe2:95.2": {"pli": "Yaṁ “virūparūpehi mathenti cittan”ti ayaṁ ādīnavo. ", "html": "{}"}, "pe2:95.3": {"pli": "Yaṁ ahaṁ agārasmā pabbajitomhi rāja apaṇṇakaṁ sāmaññameva seyyoti idaṁ nissaraṇaṁ. ", "html": "{}</p>"}, "pe2:96.1": {"pli": "Balavaṁ bālopamasuttaṁ yaṁ āsāya vā vedanīyaṁ kammaṁ gāhati, tathā cepi yaṁ yaṁ pāpakammaṁ anubhoti, tattha dukkhavedanīyena kammena abhāvitakāyena ca yāva parittacetaso ca ādīnavaṁ dasseti sukhavedanīyena kammena assādeti. ", "html": "<p>{}"}, "pe2:96.2": {"pli": "Yaṁ purāsadiso hoti. ", "html": "{}"}, "pe2:96.3": {"pli": "Bhāvitacitto bhāvitakāyo bhāvitapañño mahānāmo aparittacetaso, idaṁ nissaraṇaṁ. ", "html": "{}</p>"}, "pe2:97.1": {"pli": "Tattha katamaṁ lokikaṁ suttaṁ? ", "html": "<p>{}</p>"}, "pe2:98.1": {"pli": "“Na hi pāpaṁ kataṁ kammaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:98.2": {"pli": "Sajjukhīraṁva muccati; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:98.3": {"pli": "Ḍahantaṁ bālamanveti, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:98.4": {"pli": "Bhasmacchannova pāvako”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:99.1": {"pli": "Cattāri agatigamanāni, idaṁ lokikaṁ suttaṁ. ", "html": "<p>{}</p>"}, "pe2:100.1": {"pli": "Tattha katamaṁ lokuttaraṁ suttaṁ? ", "html": "<p>{}</p>"}, "pe2:101.1": {"pli": "“Yassindriyāni samathaṅgatāni, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:101.2": {"pli": "Assā yathā sārathinā sudantā; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:101.3": {"pli": "Pahīnamānassa anāsavassa, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:101.4": {"pli": "Devāpi tassa pihayanti tādino”ti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:102.1": {"pli": "“Ariyaṁ vo, bhikkhave, sammāsamādhiṁ desessāmī”ti idaṁ lokuttaraṁ suttaṁ. ", "html": "<p>{}</p>"}, "pe2:103.1": {"pli": "Tattha katamaṁ lokikaṁ lokuttarañca suttaṁ? ", "html": "<p>{}</p>"}, "pe2:104.1": {"pli": "“Sattiyā viya omaṭṭho, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:104.2": {"pli": "dayhamānova matthake; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:104.3": {"pli": "Kāmarāgappahānāya, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:104.4": {"pli": "sato bhikkhu paribbaje”. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:105.1": {"pli": "“Sattiyā viya omaṭṭho, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:105.2": {"pli": "Dayhamānova matthake”ti lokikaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:105.3": {"pli": "“Kāmarāgappahānāya, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:105.4": {"pli": "Sato bhikkhu paribbaje”ti lokuttaraṁ. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:106.1": {"pli": "Kabaḷīkāre āhāre atthi chandoti lokikaṁ. ", "html": "<p>{}"}, "pe2:106.2": {"pli": "Natthi chandoti lokuttaraṁ suttaṁ. ", "html": "{}</p>"}, "pe2:107.1": {"pli": "Tattha katamaṁ kammaṁ? ", "html": "<p>{}</p>"}, "pe2:108.1": {"pli": "“Yo pāṇamatipāteti, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:108.2": {"pli": "musāvādañca bhāsati; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:108.3": {"pli": "Loke adinnaṁ ādiyati, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:108.4": {"pli": "paradārañca gacchati. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:109.1": {"pli": "Surāmerayapānañca, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:109.2": {"pli": "yo naro anuyuñjati; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:109.3": {"pli": "Appahāya pañca verāni, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:109.4": {"pli": "dussīlo iti vuccati”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:110.1": {"pli": "Tīṇimāni, bhikkhave, duccaritāni. ", "html": "<p>{}"}, "pe2:110.2": {"pli": "Idaṁ kammaṁ. ", "html": "{}</p>"}, "pe2:111.1": {"pli": "Tattha katamo vipāko? ", "html": "<p>{}</p>"}, "pe2:112.1": {"pli": "Saṭṭhivassasahassāni, yathārūpī vipaccagā. ", "html": "<p>{}</p>"}, "pe2:113.1": {"pli": "“Diṭṭhā mayā, bhikkhave, cha phassāyatanikā nāma nirayā. ", "html": "<p>{}"}, "pe2:113.2": {"pli": "Diṭṭhā mayā, bhikkhave, cha phassāyatanikā nāma saggā”. ", "html": "{}"}, "pe2:113.3": {"pli": "Ayaṁ vipāko. ", "html": "{}</p>"}, "pe2:114.1": {"pli": "Tattha katamaṁ kammañca vipāko ca? ", "html": "<p>{}</p>"}, "pe2:115.1": {"pli": "“Ayasāva malaṁ samuṭṭhitaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:115.2": {"pli": "Tatuṭṭhāya tameva khādati; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:115.3": {"pli": "Evaṁ atidhonacārinaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:115.4": {"pli": "Sāni kammāni nayanti duggatiṁ”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:116.1": {"pli": "Ayasāva malaṁ samuṭṭhitaṁ, yāva sāni kammānīti idaṁ kammaṁ. ", "html": "<p>{}"}, "pe2:116.2": {"pli": "Nayanti duggatinti vipāko. ", "html": "{}</p>"}, "pe2:117.1": {"pli": "Catūsu sammāpaṭipajjamāno mātari pitari tathāgate tathāgatasāvake yā sammāpaṭipatti, idaṁ kammaṁ. ", "html": "<p>{}"}, "pe2:117.2": {"pli": "Yaṁ devesu upapajjati, ayaṁ vipāko. ", "html": "{}"}, "pe2:117.3": {"pli": "Idaṁ kammañca vipāko ca. ", "html": "{}</p>"}, "pe2:118.1": {"pli": "Tattha katamaṁ niddiṭṭhaṁ suttaṁ? ", "html": "<p>{}</p>"}, "pe2:119.1": {"pli": "“Nelaṅgo setapacchādo, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:119.2": {"pli": "ekāro vattatī ratho; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:119.3": {"pli": "Anīghaṁ passa āyantaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:119.4": {"pli": "chinnasotaṁ abandhanaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:119.5": {"pli": "Yaṁ vā cittaṁ samaṇesu, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:119.6": {"pli": "cittāgahapati dissati”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:120.1": {"pli": "Evaṁ imāya gāthāya niddiṭṭho attho. ", "html": "<p>{}</p>"}, "pe2:121.1": {"pli": "Gopālakopame ekādasa padāni. ", "html": "<p>{}"}, "pe2:121.2": {"pli": "Evaṁ kho, bhikkhave, bhikkhu rūpaññū hoti. ", "html": "{}"}, "pe2:121.3": {"pli": "Yā ca atirekapūjāya pūjetā hotīti. ", "html": "{}"}, "pe2:121.4": {"pli": "Imāni ekādasa padāni yathābhāsitāni niddiṭṭho attho. ", "html": "{}</p>"}, "pe2:122.1": {"pli": "Tattha katamo aniddiṭṭho attho? ", "html": "<p>{}</p>"}, "pe2:123.1": {"pli": "“Sukho viveko tuṭṭhassa, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:123.2": {"pli": "sutadhammassa passato; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:123.3": {"pli": "Abyāpajjaṁ sukhaṁ loke, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:123.4": {"pli": "pāṇabhūtesu saṁyamoti. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:124.1": {"pli": "Sukhā virāgatā loke, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:124.2": {"pli": "kāmānaṁ samatikkamo; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:124.3": {"pli": "Asmimānassa yo vinayo, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:124.4": {"pli": "etaṁ ve paramaṁ sukhanti”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:125.1": {"pli": "Idaṁ aniddiṭṭhaṁ. ", "html": "<p>{}"}, "pe2:125.2": {"pli": "Aṭṭha mahāpurisavitakkā. ", "html": "{}"}, "pe2:125.3": {"pli": "Idaṁ aniddiṭṭhaṁ. ", "html": "{}</p>"}, "pe2:126.1": {"pli": "Tattha katamaṁ niddiṭṭhañca aniddiṭṭhañca? ", "html": "<p>{}</p>"}, "pe2:127.1": {"pli": "“Pasannanetto sumukho, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:127.2": {"pli": "brahā uju patāpavā; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:127.3": {"pli": "Majjhe samaṇasaṅghassa, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:127.4": {"pli": "ādiccova virocasi”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:128.1": {"pli": "Pasannanetto yāva ādiccova virocasīti niddiṭṭho. ", "html": "<p>{}"}, "pe2:128.2": {"pli": "Pasannanetto yo bhagavā kathañca pana pasannanettatā, kathaṁ sumukhatā, kathaṁ brahakāyatā, kathaṁ ujukatā, kathaṁ patāpavatā, kathaṁ virocatāti aniddiṭṭho. ", "html": "{}"}, "pe2:128.3": {"pli": "Pheṇapiṇḍopamaṁ veyyākaraṇaṁ yathā pheṇapiṇḍo evaṁ rūpaṁ yathā pubbuḷo evaṁ vedanā māyā viññāṇaṁ pañcakkhandhā pañcahi upamāhi niddiṭṭhā. ", "html": "{}"}, "pe2:128.4": {"pli": "Kena kāraṇena pheṇapiṇḍopamaṁ rūpaṁ sabbañca cakkhuviññeyyaṁ yaṁ vā catūhi āyatanehi? ", "html": "{}"}, "pe2:128.5": {"pli": "Kathaṁ vedanā pubbuḷūpamā? ", "html": "{}"}, "pe2:128.6": {"pli": "Katarā ca sā vedanā sukhā dukkhā adukkhamasukhā? ", "html": "{}"}, "pe2:128.7": {"pli": "Evamesā aniddiṭṭhā. ", "html": "{}"}, "pe2:128.8": {"pli": "Evaṁ niddiṭṭhañca aniddiṭṭhañca. ", "html": "{}</p>"}, "pe2:129.1": {"pli": "Tattha katamaṁ ñāṇaṁ? ", "html": "<p>{}</p>"}, "pe2:130.1": {"pli": "“Paññā hi seṭṭhā lokasmiṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:130.2": {"pli": "yāyaṁ nibbedhagāminī; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:130.3": {"pli": "Yāya sammā pajānāti, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:130.4": {"pli": "jātimaraṇasaṅkhayaṁ”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:131.1": {"pli": "Tīṇimāni indriyāni anaññātaññassāmītindriyaṁ aññindriyaṁ aññātāvindriyaṁ, idaṁ ñāṇaṁ. ", "html": "<p>{}</p>"}, "pe2:132.1": {"pli": "Tattha katamaṁ neyyaṁ? ", "html": "<p>{}</p>"}, "pe2:133.1": {"pli": "“Kāmesu sattā kāmasaṅgasattā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:133.2": {"pli": "Saṁyojane vajjamapassamānā; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:133.3": {"pli": "Na hi jātu saṁyojanasaṅgasattā, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:133.4": {"pli": "Oghaṁ tareyyuṁ vipulaṁ mahantaṁ”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:134.1": {"pli": "Catūhi aṅgehi samannāgatā kāyassa bhedā devesu uppajjanti. ", "html": "<p>{}"}, "pe2:134.2": {"pli": "Udāne kāpiyaṁ suttaṁ apaṇṇakapasādanīyaṁ—", "html": "{}"}, "pe2:134.3": {"pli": "idaṁ neyyaṁ. ", "html": "{}</p>"}, "pe2:135.1": {"pli": "Tattha katamaṁ ñāṇañca neyyañca? ", "html": "<p>{}</p>"}, "pe2:136.1": {"pli": "“Sabbe dhammā anattāti, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:136.2": {"pli": "yadā paññāya passati; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:136.3": {"pli": "Atha nibbindati dukkhe, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:136.4": {"pli": "esa maggo visuddhiyā”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:137.1": {"pli": "Yadā passatīti ñāṇaṁ. ", "html": "<p>{}"}, "pe2:137.2": {"pli": "Yo sabbadhamme anattākārena upaṭṭhapeti idaṁ neyyaṁ. ", "html": "{}</p>"}, "pe2:138.1": {"pli": "Cattāri ariyasaccāni, tattha tīṇi neyyāni maggasaccaṁ sīlakkhandho ca paññākkhandho ca, idaṁ ñāṇañca neyyañca. ", "html": "<p>{}</p>"}, "pe2:139.1": {"pli": "Tattha katamaṁ dassanaṁ? ", "html": "<p>{}</p>"}, "pe2:140.1": {"pli": "“Eseva maggo natthañño, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:140.2": {"pli": "Dassanassa visuddhiyā; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:140.3": {"pli": "Etañhi tumhe paṭipajjatha, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:140.4": {"pli": "Mārassetaṁ pamohanaṁ”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:141.1": {"pli": "Catūhi aṅgehi samannāgato ariyasāvako attanāva attānaṁ byākareyya “khīṇanirayomhi yāva sotāpannohamasmi avinipātadhammo niyato sambodhiparāyaṇo”ti. ", "html": "<p>{}"}, "pe2:141.2": {"pli": "Idaṁ dassanaṁ. ", "html": "{}</p>"}, "pe2:142.1": {"pli": "Tattha katamā bhāvanā? ", "html": "<p>{}</p>"}, "pe2:143.1": {"pli": "“Yassindriyāni subhāvitāni, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:143.2": {"pli": "Ajjhattaṁ bahiddhā ca sabbaloke; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:143.3": {"pli": "So puggalo mati ca rūpasaññī, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:143.4": {"pli": "Sumohagatā na jānāti”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:144.1": {"pli": "Cattāri dhammapadāni—", "html": "<p>{}"}, "pe2:144.2": {"pli": "anabhijjhā abyāpādo sammāsati sammāsamādhi. ", "html": "{}"}, "pe2:144.3": {"pli": "Ayaṁ bhāvanā. ", "html": "{}</p>"}, "pe2:145.1": {"pli": "Tattha katamaṁ dassanañca bhāvanā ca? ", "html": "<p>{}</p>"}, "pe2:146.1": {"pli": "“Vacasā manasātha kammunā ca, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:146.2": {"pli": "Aviruddho sammā viditvā dhammaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:146.3": {"pli": "Nibbānapadābhipatthayāno, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:146.4": {"pli": "Sammā so loke paribbajeyya”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:147.1": {"pli": "Sotāpattiphalaṁ sacchikātukāmena katame dhammā manasikātabbā, bhagavā āha pañcupādānakkhandhā. ", "html": "<p>{}"}, "pe2:147.2": {"pli": "Idaṁ dassanañca bhāvanā ca. ", "html": "{}</p>"}, "pe2:148.1": {"pli": "Tattha katame vipākadhammadhammā? ", "html": "<p>{}</p>"}, "pe2:149.1": {"pli": "Yāni karoti purisoti vitthāro. ", "html": "<p>{}"}, "pe2:149.2": {"pli": "Tīṇimāni, bhikkhave, sucaritāni. ", "html": "{}"}, "pe2:149.3": {"pli": "Ime vipākadhammadhammā. ", "html": "{}</p>"}, "pe2:150.1": {"pli": "Tattha katame navipākadhammadhammā? ", "html": "<p>{}</p>"}, "pe2:151.1": {"pli": "“Rūpaṁ vedayitaṁ saññā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:151.2": {"pli": "Viññāṇaṁ yā ceva cetanā; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:151.3": {"pli": "Nesohamasmi na meso attā, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:151.4": {"pli": "Iti diṭṭho virajjati”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:152.1": {"pli": "Pañcime, bhikkhave, khandhā—", "html": "<p>{}"}, "pe2:152.2": {"pli": "ime navipākadhammadhammā. ", "html": "{}</p>"}, "pe2:153.1": {"pli": "Tattha katamo nevavipāko navipākadhammadhammo? ", "html": "<p>{}</p>"}, "pe2:154.1": {"pli": "“Ye evaṁ paṭipajjanti, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:154.2": {"pli": "nayaṁ buddhena desitaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:154.3": {"pli": "Te dukkhassantaṁ karissanti, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:154.4": {"pli": "satthusāsanakārakā”ti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:155.1": {"pli": "Iti yā ca sammāpaṭipatti yo ca nirodho, ubhayametaṁ nevavipāko navipākadhammo. ", "html": "<p>{}"}, "pe2:155.2": {"pli": "Brahmacariyaṁ vo, bhikkhave, desessāmi, brahmacariyaphalāni ca brahmacariyañca ariyo aṭṭhaṅgiko maggo brahmacariyaphalāni sotāpattiphalaṁ yāva arahattaṁ. ", "html": "{}</p>"}, "pe2:156.1": {"pli": "Tattha katamaṁ sakavacanaṁ? ", "html": "<p>{}</p>"}, "pe2:157.1": {"pli": "“Sabbapāpassa akaraṇaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:157.2": {"pli": "kusalassa upasampadā; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:157.3": {"pli": "Sacittapariyodāpanaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:157.4": {"pli": "etaṁ buddhāna sāsanaṁ”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:158.1": {"pli": "Tīṇimāni, bhikkhave, vimokkhamukhāni. ", "html": "<p>{}"}, "pe2:158.2": {"pli": "Idaṁ sakavacanaṁ. ", "html": "{}</p>"}, "pe2:159.1": {"pli": "Tattha katamaṁ paravacanaṁ? ", "html": "<p>{}</p>"}, "pe2:160.1": {"pli": "“Natthi puttasamaṁ pemaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:160.2": {"pli": "Natthi goṇasamitaṁ dhanaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:160.3": {"pli": "Natthi sūriyasamā ābhā, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:160.4": {"pli": "Samuddaparamā sarā”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:161.1": {"pli": "Hetunā mārisā kosiyā subhāsitena saṅgāmavijayo sopi nāma, bhikkhave, sakko devānamindo sakaṁ phalaṁ paribhuñjamānoti vitthārena kātabbaṁ. ", "html": "<p>{}"}, "pe2:161.2": {"pli": "Idaṁ paravacanaṁ. ", "html": "{}</p>"}, "pe2:162.1": {"pli": "Tattha katamaṁ sakavacanañca paravacanañca? ", "html": "<p>{}</p>"}, "pe2:163.1": {"pli": "“Yaṁ pattaṁ yañca pattabbaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:163.2": {"pli": "Ubhayametaṁ rajānukiṇṇaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:163.3": {"pli": "Ye evaṁvādino natthi, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:163.4": {"pli": "Tesaṁ kāmesu doso”ti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:164.1": {"pli": "Idaṁ paravacanaṁ. ", "html": "<p>{}"}, "pe2:164.2": {"pli": "Ye ca kho te ubho ante anupagamma vaṭṭaṁ tesaṁ natthi paññāpanāya. ", "html": "{}"}, "pe2:164.3": {"pli": "Idaṁ sakavacanaṁ. ", "html": "{}</p>"}, "pe2:165.1": {"pli": "“Nandati puttehi puttimā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:165.2": {"pli": "Gomā gohi tatheva nandati; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:165.3": {"pli": "Upadhī hi narassa nandanā, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:165.4": {"pli": "Na hi so nandati yo nirūpadhī”ti—", "html": "{}"}, "pe2:165.5": {"pli": "paravacanaṁ. ", "html": "{}</p>"}, "pe2:166.1": {"pli": "“Socati puttehi puttimā, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:166.2": {"pli": "Gomā gohi tatheva socati; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:166.3": {"pli": "Upadhī hi narassa socanā, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:166.4": {"pli": "Na hi so socati yo nirūpadhī”ti—", "html": "{}"}, "pe2:166.5": {"pli": "sakavacanaṁ. ", "html": "{}</p></blockquote>"}, "pe2:167.1": {"pli": "Idaṁ sakavacanaṁ paravacanañca. ", "html": "<p class='endsutta'>{}</p>"}, "pe2:168.1": {"pli": "Tattha katamaṁ sattādhiṭṭhānaṁ? ", "html": "<p>{}</p>"}, "pe2:169.1": {"pli": "“Ye keci bhūtā bhavissanti ye vāpi, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:169.2": {"pli": "Sabbe gamissanti pahāya dehaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:169.3": {"pli": "Taṁ sabbajāniṁ kusalo viditvā, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:169.4": {"pli": "Dhamme ṭhito brahmacariyaṁ careyya”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:170.1": {"pli": "Tayome, bhikkhave, satthāro, tathāgato arahaṁ sekkho paṭipado. ", "html": "<p>{}"}, "pe2:170.2": {"pli": "Idaṁ sattādhiṭṭhānaṁ. ", "html": "{}</p>"}, "pe2:171.1": {"pli": "Tattha katamaṁ dhammādhiṭṭhānaṁ? ", "html": "<p>{}</p>"}, "pe2:172.1": {"pli": "“Yañca kāmasukhaṁ loke, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:172.2": {"pli": "yañcidaṁ diviyaṁ sukhaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:172.3": {"pli": "Taṇhakkhayasukhassete, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:172.4": {"pli": "kalaṁ nāgghanti soḷasiṁ”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:173.1": {"pli": "Sattime, bhikkhave, bojjhaṅgā, idaṁ dhammādhiṭṭhānaṁ. ", "html": "<p>{}</p>"}, "pe2:174.1": {"pli": "Tattha katamaṁ sattādhiṭṭhānañca dhammādhiṭṭhānañca? ", "html": "<p>{}"}, "pe2:174.2": {"pli": "Duddasamantaṁ saccaṁ duddaso paṭivedho bālehi, jānato passato natthi nandīti vadāmi. ", "html": "{}"}, "pe2:174.3": {"pli": "Duddasamantaṁ saccaṁ duddaso paṭivedho bālehīti dhammādhiṭṭhānaṁ. ", "html": "{}"}, "pe2:174.4": {"pli": "Jānato passato natthi nandīti sattādhiṭṭhānaṁ. ", "html": "{}"}, "pe2:174.5": {"pli": "Dārukkhandhopamaṁ gaṅgāya tīriyā orimañca tīraṁ pārimañca tīraṁ thale vā na ca ussīdanaṁ, majjhe ca na saṁsīdanaṁ manussaggāho ca amanussaggāho ca antopūtibhāvo ca, idaṁ dhammādhiṭṭhānaṁ. ", "html": "{}"}, "pe2:174.6": {"pli": "Evaṁ pana bhikkhu nibbānaninno bhavissati nibbānaparāyaṇoti sattādhiṭṭhānaṁ. ", "html": "{}"}, "pe2:174.7": {"pli": "Idaṁ sattādhiṭṭhānañca dhammādhiṭṭhānañca. ", "html": "{}</p>"}, "pe2:175.1": {"pli": "Tattha katamo thavo? ", "html": "<p>{}</p>"}, "pe2:176.1": {"pli": "“Maggānaṭṭhaṅgiko seṭṭho, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:176.2": {"pli": "saccānaṁ caturo padā; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:176.3": {"pli": "Virāgo seṭṭho dhammānaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:176.4": {"pli": "dvipadānañca cakkhumā”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:177.1": {"pli": "Tīṇimāni, bhikkhave, aggāni—", "html": "<p>{}"}, "pe2:177.2": {"pli": "buddho sattānaṁ, virāgo dhammānaṁ, saṅgho gaṇānaṁ. ", "html": "{}"}, "pe2:177.3": {"pli": "Ayaṁ thavo. ", "html": "{}</p>"}, "pe2:178.1": {"pli": "Tattha katamaṁ anuññātaṁ? ", "html": "<p>{}</p>"}, "pe2:179.1": {"pli": "“Kāyena saṁvaro sādhu, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:179.2": {"pli": "sādhu vācāya saṁvaro; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:179.3": {"pli": "Manasā saṁvaro sādhu, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:179.4": {"pli": "sādhu sabbattha saṁvuto; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:179.5": {"pli": "Sabbattha saṁvuto bhikkhu, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:179.6": {"pli": "sabbadukkhā pamuccati”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:180.1": {"pli": "Idaṁ bhagavatā anuññātaṁ. ", "html": "<p class='endsutta'>{}</p>"}, "pe2:181.1": {"pli": "Tīṇimāni, bhikkhave, karaṇīyāni—", "html": "<p>{}"}, "pe2:181.2": {"pli": "kāyasucaritaṁ vacīsucaritaṁ manosucaritaṁ. ", "html": "{}"}, "pe2:181.3": {"pli": "Idaṁ anuññātaṁ. ", "html": "{}</p>"}, "pe2:182.1": {"pli": "Tattha katamaṁ paṭikkhittaṁ? ", "html": "<p>{}</p>"}, "pe2:183.1": {"pli": "Natthi puttasamaṁ pemaṁ. ", "html": "<p>{}"}, "pe2:183.2": {"pli": "Vitthāro idaṁ paṭikkhittaṁ. ", "html": "{}</p>"}, "pe2:184.1": {"pli": "Tīṇimāni, bhikkhave, akaraṇīyāni sayaṁ abhiññāya desitāni. ", "html": "<p>{}"}, "pe2:184.2": {"pli": "Katamāni tīṇi? ", "html": "{}"}, "pe2:184.3": {"pli": "Kāyaduccaritaṁ vacīduccaritaṁ manoduccaritaṁ. ", "html": "{}"}, "pe2:184.4": {"pli": "Idaṁ paṭikkhittaṁ. ", "html": "{}</p>"}, "pe2:185.1": {"pli": "Tattha katamaṁ anuññātañca paṭikkhittañca? ", "html": "<p>{}</p>"}, "pe2:186.1": {"pli": "“Kāyena kusalaṁ kare, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe2:186.2": {"pli": "assa kāyena saṁvuto; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:186.3": {"pli": "Kāyaduccaritaṁ hitvā, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:186.4": {"pli": "kāyasucaritaṁ care”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:187.1": {"pli": "Dvīhi paṭhamapadehi catutthena ca padena anujānāti. ", "html": "<p>{}"}, "pe2:187.2": {"pli": "Kāyaduccaritaṁ hitvāti tatiyena padena paṭikkhittanti. ", "html": "{}"}, "pe2:187.3": {"pli": "Mahāvibhaṅgo aciratapānādo. ", "html": "{}</p>"}, "pe2:188.1": {"pli": "Tatthimā uddānagāthā ", "html": "<p class='uddana-intro'>{}</p>"}, "pe2:189.1": {"pli": "Sace bhāyasi dukkhassa, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "pe2:189.2": {"pli": "mābhinandi anāgataṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:189.3": {"pli": "Vassakāle yathā chattaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:189.4": {"pli": "kusalāni kamatthake. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:190.1": {"pli": "Sabbe dhammā anattāti, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:190.2": {"pli": "samāgataṁ vicālaye; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:190.3": {"pli": "Na vo dukkhā pamokkhātthi, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:190.4": {"pli": "samatho ca vipassanā. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:191.1": {"pli": "Kāmacchandaṁ upādāya, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:191.2": {"pli": "yo so vitakkehi khajjati; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:191.3": {"pli": "Subhāvitatte bojjhaṅge, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:191.4": {"pli": "so imaṁ vijaṭaye jaṭaṁ. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:192.1": {"pli": "Suññato lokaṁ avekkhassu, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:192.2": {"pli": "samādhibhāvi bhāvase; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:192.3": {"pli": "Kāmaṁ kāmayamānassa, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:192.4": {"pli": "dhammacariyāya sugatiṁ. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:193.1": {"pli": "Haññate sabbā mucceva, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:193.2": {"pli": "nippoṭhento catuddisā; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:193.3": {"pli": "Yo kāme parivajjeti, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:193.4": {"pli": "pārichattopameva ca. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:194.1": {"pli": "Yāni karoti puriso, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:194.2": {"pli": "lokadhammā pakāsitā; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:194.3": {"pli": "Sukho vipāko puññānaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:194.4": {"pli": "tatiyaṁ aññaṁ na vijjati. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:195.1": {"pli": "Ādānassa bhayaṁ ñatvā, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:195.2": {"pli": "jāyate jīyatepi ca; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:195.3": {"pli": "Kāmā hi citrā vividhā, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:195.4": {"pli": "atha loṇasallopamaṁ. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:196.1": {"pli": "Na hi pāpaṁ kataṁ kammaṁ, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:196.2": {"pli": "Agatīhi ca gacchati; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:196.3": {"pli": "Yassindriyāni samathaṅgatāni, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:196.4": {"pli": "Tatheva pañcañāṇiko. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:197.1": {"pli": "Sattiyā viya omaṭṭho, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:197.2": {"pli": "viññāṇañca patiṭṭhitā; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:197.3": {"pli": "Yo pāṇamatipāteti, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:197.4": {"pli": "tīṇi duccaritāni ca. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:198.1": {"pli": "Saṭṭhivassasahassāni, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:198.2": {"pli": "Khaṇaṁ laddhāna dullabhaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:198.3": {"pli": "Ayasāva malaṁ samuṭṭhitaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:198.4": {"pli": "Catūsu paṭipattisu. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:199.1": {"pli": "Nelaṅgo setapacchādo, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:199.2": {"pli": "atha gopālakopamaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:199.3": {"pli": "Sukho viveko tuṭṭhassa, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:199.4": {"pli": "vitakkā ca sudesitā. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:200.1": {"pli": "Pheṇapiṇḍopamaṁ rūpaṁ, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:200.2": {"pli": "brahā uju patāpavā; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:200.3": {"pli": "Paññā hi seṭṭhā lokasmiṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:200.4": {"pli": "anaññā tīṇi indriyāni. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:201.1": {"pli": "Kāmesu sattā kāmasaṅgasattā, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:201.2": {"pli": "Atha vaṇṇo rahassavā; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:201.3": {"pli": "Sabbe dhammā anattāti, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:201.4": {"pli": "Ariyasaccañca desitaṁ. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:202.1": {"pli": "Eseva maggo natthañño, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:202.2": {"pli": "Sotāpannoti byākare; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:202.3": {"pli": "Yassindriyāni subhāvitāni, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:202.4": {"pli": "Atha dhammapadehi ca. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:203.1": {"pli": "Vacasā manasā ceva, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:203.2": {"pli": "pañcakkhandhā aniccato; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:203.3": {"pli": "Yāni karoti puriso, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:203.4": {"pli": "tīṇi sucaritāni ca. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:204.1": {"pli": "Rūpaṁ vedayitaṁ saññā, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:204.2": {"pli": "pañcakkhandhā pakāsitā; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:204.3": {"pli": "Yo evaṁ paṭipajjati, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:204.4": {"pli": "brahmā ceva phalāni ca. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:205.1": {"pli": "Sabbapāpassa akaraṇaṁ, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:205.2": {"pli": "vimokkhā taṁ hi desitā; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:205.3": {"pli": "Natthi puttasamaṁ pemaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:205.4": {"pli": "devānaṁ asurāna ca. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:206.1": {"pli": "Yaṁ pattaṁ yañca pattabbaṁ, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:206.2": {"pli": "nandati socati niccaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:206.3": {"pli": "Ye keci bhūtā bhavissanti, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:206.4": {"pli": "satthāro ca pakāsitā. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:207.1": {"pli": "Yañca kāmasukhaṁ loke, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:207.2": {"pli": "bojjhaṅgā ca sudesitā; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:207.3": {"pli": "Maggānaṭṭhaṅgiko seṭṭho, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:207.4": {"pli": "tayo ca aggapattiyo. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:208.1": {"pli": "Kāyena saṁvaro sādhu, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:208.2": {"pli": "karaṇīyañca desitaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:208.3": {"pli": "Natthi attasamaṁ pemaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:208.4": {"pli": "ariyā tīṇi ca desitā. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe2:209.1": {"pli": "Kāyena kusalaṁ abhirato, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe2:209.2": {"pli": "Vinayañca kāmasukhaṁ loke; ", "html": "<span class='verse-line'>{}</span>"}, "pe2:209.3": {"pli": "Bojjhaṅgā ca sudesitā, ", "html": "<span class='verse-line'>{}</span>"}, "pe2:209.4": {"pli": "Duddasaṁ anataṁ ceva parāparaṁ ca. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe2:210.1": {"pli": "Peṭakopadese sāsanappaṭṭhānaṁ nāma dutiyabhūmi samattā. ", "html": "<p class='endsutta'>{}</p></article>"}}, "pe3": {"pe3:0.1": {"pli": "Peṭakopadesa ", "html": "<article id='pe3'><header><ul><li class='division'>{}</li></ul>"}, "pe3:0.2": {"pli": "3. Suttādhiṭṭhānatatiyabhūmi ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "pe3:1.1": {"pli": "Tattha katamaṁ suttādhiṭṭhānaṁ? ", "html": "<p>{}</p>"}, "pe3:2.1": {"pli": "Lobhādhiṭṭhānaṁ dosādhiṭṭhānaṁ mohādhiṭṭhānaṁ alobhādhiṭṭhānaṁ adosādhiṭṭhānaṁ amohādhiṭṭhānaṁ kāyakammādhiṭṭhānaṁ vācākammādhiṭṭhānaṁ manokammādhiṭṭhānaṁ saddhindriyādhiṭṭhānaṁ vīriyindriyādhiṭṭhānaṁ satindriyādhiṭṭhānaṁ samādhindriyādhiṭṭhānaṁ paññindriyādhiṭṭhānaṁ. ", "html": "<p>{}</p>"}, "pe3:3.1": {"pli": "Tattha katamaṁ lobhādhiṭṭhānaṁ? ", "html": "<p>{}</p>"}, "pe3:4.1": {"pli": "“Vitakkamathitassa jantuno, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe3:4.2": {"pli": "Tibbarāgassa subhānupassino; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:4.3": {"pli": "Bhiyyo taṇhā pavaḍḍhati, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:4.4": {"pli": "Esa kho gāḷhaṁ karoti bandhanaṁ”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe3:5.1": {"pli": "Vitakkamathitassāti kāmarāgo. ", "html": "<p>{}"}, "pe3:5.2": {"pli": "Subhānupassinoti kāmarāgavatthu. ", "html": "{}"}, "pe3:5.3": {"pli": "Bhiyyo taṇhā pavaḍḍhatīti kāmataṇhā. ", "html": "{}"}, "pe3:5.4": {"pli": "Esa gāḷhaṁ karoti bandhananti rāgaṁ, iti yo yo dhammo mūlanikkhitto, so yevettha dhammo uggāvahitabbo. ", "html": "{}"}, "pe3:5.5": {"pli": "Na bhagavā ekaṁ dhammaṁ ārabbha aññaṁ dhammaṁ deseti. ", "html": "{}"}, "pe3:5.6": {"pli": "Yassa vitakketi kāmavitakko tameva vitakkaṁ kāmavitakkena niddisīyati. ", "html": "{}"}, "pe3:5.7": {"pli": "Tibbarāgassāti tasseva vitakkassa vatthuṁ niddisati. ", "html": "{}"}, "pe3:5.8": {"pli": "Subhānupassino bhiyyo taṇhā pavaḍḍhatīti tameva rāgaṁ kāmataṇhāti niddisati. ", "html": "{}"}, "pe3:5.9": {"pli": "Esa gāḷhaṁ karoti bandhananti tameva taṇhāsaṁyojanaṁ niddisati. ", "html": "{}"}, "pe3:5.10": {"pli": "Evaṁ gāthāsu anuminitabbaṁ. ", "html": "{}"}, "pe3:5.11": {"pli": "Evaṁ saveyyākaraṇesu. ", "html": "{}</p>"}, "pe3:6.1": {"pli": "Tattha bhagavā ekaṁ dhammaṁ tividhaṁ niddisati, nissandato hetuto phalato. ", "html": "<p>{}</p>"}, "pe3:7.1": {"pli": "“Dadaṁ piyo hoti bhajanti naṁ bahū, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe3:7.2": {"pli": "Kittiñca pappoti yaso ca vaḍḍhati; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:7.3": {"pli": "Amaṅkubhūto parisaṁ vigāhati, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:7.4": {"pli": "Visārado hoti naro amaccharī”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe3:8.1": {"pli": "Dadanti yaṁ yaṁ dānaṁ, idaṁ dānamayikaṁ puññakriyaṁ. ", "html": "<p>{}"}, "pe3:8.2": {"pli": "Tattha hetu. ", "html": "{}"}, "pe3:8.3": {"pli": "Yañcetaṁ. ", "html": "{}"}, "pe3:8.4": {"pli": "Bhajanti naṁ bahū, kittinti yo ca kalyāṇo kittisaddo loke abbhuggacchati, yaṁ bahukassa janassa piyo bhavati manāpo ca. ", "html": "{}"}, "pe3:8.5": {"pli": "Yañca avippaṭisārī kālaṁ karoti ayaṁ nissando. ", "html": "{}"}, "pe3:8.6": {"pli": "Yaṁ kāyassa bhedā devesu upapajjatīti idaṁ phalaṁ. ", "html": "{}"}, "pe3:8.7": {"pli": "Idaṁ lobhādhiṭṭhānaṁ. ", "html": "{}</p>"}, "pe3:9.1": {"pli": "Tattha katamaṁ dosādhiṭṭhānaṁ? ", "html": "<p>{}</p>"}, "pe3:10.1": {"pli": "“Yo pāṇamatipāteti, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe3:10.2": {"pli": "musāvādañca bhāsati; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:10.3": {"pli": "Loke adinnaṁ ādiyati, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:10.4": {"pli": "paradārañca gacchati; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:10.5": {"pli": "Surāmerayapānañca, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:10.6": {"pli": "yo naro anuyuñjati. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe3:11.1": {"pli": "Appahāya pañca verāni, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe3:11.2": {"pli": "dussīlo iti vuccati; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:11.3": {"pli": "Kāyassa bhedā duppañño, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:11.4": {"pli": "nirayaṁ sopapajjati”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe3:12.1": {"pli": "Yo pāṇamatipātetīti duṭṭho pāṇamatipāteti. ", "html": "<p>{}"}, "pe3:12.2": {"pli": "Musāvādañca bhāsatīti dosopaghātāya musāvādañca bhāsati. ", "html": "{}"}, "pe3:12.3": {"pli": "Surāmerayapānañca, yo naro anuyuñjatīti doso nidānaṁ. ", "html": "{}"}, "pe3:12.4": {"pli": "Yo ca surāmerayapānaṁ anuyuñjati yathāparadāravihārī amittā janayanti. ", "html": "{}</p>"}, "pe3:13.1": {"pli": "Pañca verāni appahāyāti pañcannaṁ bhikkhāpadānaṁ samatikkamanaṁ sabbesaṁ dosajānaṁ sā paṇṇatti, teneva dosajanitena kammena dussīlo iti vuccati sopi dhammo hetunā niddisitabbo, nissandena phalena ca. ", "html": "<p>{}</p>"}, "pe3:14.1": {"pli": "Tīṇi bālassa bālalakkhaṇāni—", "html": "<p>{}"}, "pe3:14.2": {"pli": "dubbhāsitabhāsī ca hoti, duccintitacintī ca dukkaṭakammakārī ca. ", "html": "{}"}, "pe3:14.3": {"pli": "Tattha yaṁ kāyena ca vācāya ca parakkamati, idamassa dukkaṭakammakārī. ", "html": "{}"}, "pe3:14.4": {"pli": "Tāyaṁ yathā ca musāvādaṁ bhāsati yathā pubbaniddiṭṭhaṁ, idamassa dubbhāsitā. ", "html": "{}"}, "pe3:14.5": {"pli": "Yañca saṅkappeti manoduccaritaṁ byāpādaṁ, idamassa duccintitacintitā. ", "html": "{}"}, "pe3:14.6": {"pli": "Yaṁ so imehi tīhi bālalakkhaṇehi samannāgato tīṇi tajjāni dukkhāni domanassāni anubhavati, so ca hoti sabhaggato vā parisaggato vā tajjaṁ kathaṁ kathanti. ", "html": "{}"}, "pe3:14.7": {"pli": "Yadā bhavati so ca pāṇātipātādidasaakusalakammapatho, so tatonidānaṁ dukkhaṁ domanassaṁ paṭisaṁvedetīti. ", "html": "{}"}, "pe3:14.8": {"pli": "Puna caparaṁ yadā passati coraṁ rājāparādhikaṁ raññā gahitaṁ jīvitā voropetaṁ, tassevaṁ bhavati sace mamampi rājā jāneyya mamampi rājā gāhāpetvā jīvitā voropeyyāti, so tatonidānaṁ dukkhaṁ domanassaṁ paṭisaṁvedeti. ", "html": "{}"}, "pe3:14.9": {"pli": "Puna caparaṁ bālo yadā bhavati āsanā samārūḷho yāva yā me gati bhavissati ito pecca paraṁ maraṇāti so tatonidānaṁ dukkhaṁ domanassaṁ paṭisaṁvedeti iti bālalakkhaṇaṁ hetu. ", "html": "{}"}, "pe3:14.10": {"pli": "Tīṇi tajjāni dukkhāni nissando. ", "html": "{}"}, "pe3:14.11": {"pli": "Kāyassa bhedā nirayesu upapajjati, idaṁ phalaṁ. ", "html": "{}"}, "pe3:14.12": {"pli": "Idaṁ dosādhiṭṭhānaṁ. ", "html": "{}</p>"}, "pe3:15.1": {"pli": "Tattha katamaṁ mohādhiṭṭhānaṁ? ", "html": "<p>{}</p>"}, "pe3:16.1": {"pli": "“Satañceva sahassānaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe3:16.2": {"pli": "kappānaṁ saṁsarissati; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:16.3": {"pli": "Atha vāpi tato bhiyyo, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:16.4": {"pli": "gabbhā gabbhaṁ gamissatha. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe3:17.1": {"pli": "Anupādāya buddhavacanaṁ, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe3:17.2": {"pli": "Saṅkhāre attato upādāya; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:17.3": {"pli": "Dukkhassantaṁ karissanti, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:17.4": {"pli": "Ṭhānametaṁ na vijjati”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe3:18.1": {"pli": "Yo yaṁ anamataggasaṁsāraṁ samāpanno jāyate ca mīyate ca, ayaṁ avijjāhetukā. ", "html": "<p>{}"}, "pe3:18.2": {"pli": "Yānipi ca saṅkhārānaṁ payojanāni, tānipi avijjāpaccayāni, yaṁ adassanaṁ buddhavacanassa, ayaṁ avijjāsutteyeva niddiṭṭhaṁ. ", "html": "{}"}, "pe3:18.3": {"pli": "Yo ca saṅkhāre attato harati pañcakkhandhe pañca diṭṭhiyo upagacchati. ", "html": "{}"}, "pe3:18.4": {"pli": "“Etaṁ mama, esohamasmi, eso me attā”ti idaṁ suttaṁ avijjāya nikkhittaṁ, avijjāya nikkhipitaṁ. ", "html": "{}"}, "pe3:18.5": {"pli": "Evaṁ satthā sutte nayena dhammena niddisati. ", "html": "{}"}, "pe3:18.6": {"pli": "Asādhāraṇena taṁyeva tattha niddisitabbaṁ. ", "html": "{}"}, "pe3:18.7": {"pli": "Na aññaṁ. ", "html": "{}</p>"}, "pe3:19.1": {"pli": "Ye hi keci, bhikkhave, samaṇā vā brāhmaṇā vā “idaṁ dukkhan”ti nappajānanti cattāri saccāni vitthārena, yaṁ tattha appajānanā, idaṁ dukkhaṁ, ayaṁ hetu. ", "html": "<p>{}"}, "pe3:19.2": {"pli": "Appajānanto vividhe saṅkhāre abhisaṅkharoti, ayaṁ nissando. ", "html": "{}"}, "pe3:19.3": {"pli": "Yañca diṭṭhigatāni parāmasati “idameva saccaṁ moghamaññan”ti ayaṁ nissando. ", "html": "{}"}, "pe3:19.4": {"pli": "Yaṁ punabbhavaṁ nibbatteti, idaṁ phalaṁ. ", "html": "{}"}, "pe3:19.5": {"pli": "Ayampi dhammo saniddiṭṭho hetuto ca phalato ca nissandato ca. ", "html": "{}</p>"}, "pe3:20.1": {"pli": "Ettha pana keci dhammā sādhāraṇā bhavanti. ", "html": "<p>{}"}, "pe3:20.2": {"pli": "Hetu khalu āditoyeva sutte nikkhipissanti. ", "html": "{}"}, "pe3:20.3": {"pli": "Yathā kiṁ bhave cattārimāni, bhikkhave, agatigamanāni. ", "html": "{}"}, "pe3:20.4": {"pli": "Tattha yañca chandāgatiṁ gacchati yañca bhayāgatiṁ gacchati, ayaṁ lobho akusalamūlaṁ. ", "html": "{}"}, "pe3:20.5": {"pli": "Yaṁ dosā, ayaṁ dosoyeva. ", "html": "{}"}, "pe3:20.6": {"pli": "Yaṁ mohā, ayaṁ mohoyeva. ", "html": "{}"}, "pe3:20.7": {"pli": "Evaṁ imāni tīṇi akusalamūlāni āditoyeva upaparikkhitabbāni. ", "html": "{}"}, "pe3:20.8": {"pli": "Yattha ekaṁ niddisitabbaṁ, tattha ekaṁ niddisīyati. ", "html": "{}"}, "pe3:20.9": {"pli": "Tathā dve yathā tīṇi, na hi ādīhi anikkhitte hetu vā nissando vā phalaṁ vā niddisitabbaṁ. ", "html": "{}</p>"}, "pe3:21.1": {"pli": "Ayañcettha gāthā—", "html": "<p>{}</p>"}, "pe3:22.1": {"pli": "“Chandā dosā bhayā mohā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe3:22.2": {"pli": "yo dhammaṁ ativattati; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:22.3": {"pli": "Nihīyati tassa yaso, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:22.4": {"pli": "kāḷapakkheva candimā”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe3:23.1": {"pli": "Kattha chandā ca ayaṁ lobho yathā niddiṭṭhaṁ pubbe. ", "html": "<p>{}"}, "pe3:23.2": {"pli": "Idaṁ mohādhiṭṭhānaṁ. ", "html": "{}</p>"}, "pe3:24.1": {"pli": "Tattha katamaṁ alobhādhiṭṭhānaṁ? ", "html": "<p>{}</p>"}, "pe3:25.1": {"pli": "“Asubhānupassiṁ viharantaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe3:25.2": {"pli": "Indriyesu susaṁvutaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:25.3": {"pli": "Bhojanamhi ca mattaññuṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:25.4": {"pli": "Saddhaṁ āraddhavīriyaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:25.5": {"pli": "Taṁ ve nappasahati māro, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:25.6": {"pli": "Vāto selaṁva pabbatan”ti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe3:26.1": {"pli": "Tattha yā asubhāya upaparikkhā, ayaṁ kāmesu ādīnavadassanena pariccāgo. ", "html": "<p>{}"}, "pe3:26.2": {"pli": "Indriyesu susaṁvuto tasseva alobhassa pāripūriyaṁ mama āyatanasocitaṁ anupādāya. ", "html": "{}"}, "pe3:26.3": {"pli": "Bhojanamhi ca mattaññunti rasataṇhāpahānaṁ. ", "html": "{}"}, "pe3:26.4": {"pli": "Iti ayaṁ alobho asubhānupassitāya vatthuto dhārayati, so alobho hetu. ", "html": "{}"}, "pe3:26.5": {"pli": "Indriyesu guttadvāratāya gocarato dhārayati, bhojanemattaññutāya parato dhārayati, ayaṁ nissando. ", "html": "{}"}, "pe3:26.6": {"pli": "Taṁ ve nappasahati māro, vāto selaṁ va pabbatanti, idaṁ phalaṁ. ", "html": "{}"}, "pe3:26.7": {"pli": "Iti yoyeva dhammo ādimhi nikkhitto, soyeva majjhe ceva avasāne ca. ", "html": "{}</p>"}, "pe3:27.1": {"pli": "Nāhaṁ, bhikkhave, aññaṁ ekadhammampi samanupassāmi asamuppannassa kāmacchandassa anuppādāya uppannassa vā pahānāya, yathayidaṁ asubhanimittaṁ. ", "html": "<p>{}"}, "pe3:27.2": {"pli": "Tattha asubhanimittaṁ manasikarontassa anuppanno ceva kāmacchando na uppajjati, uppanno ca kāmacchando pahīyati. ", "html": "{}"}, "pe3:27.3": {"pli": "Idaṁ alobhassa vatthu. ", "html": "{}"}, "pe3:27.4": {"pli": "Yaṁ puna anuppanno kāmarāgo pariyādiyati rūparāgaṁ arūparāgaṁ, iti phalaṁ. ", "html": "{}"}, "pe3:27.5": {"pli": "Iti ayampi ca dhammo niddiṭṭho hetuto ca nissandato ca phalato ca. ", "html": "{}"}, "pe3:27.6": {"pli": "Idaṁ alobhādhiṭṭhānaṁ. ", "html": "{}</p>"}, "pe3:28.1": {"pli": "Tattha katamaṁ adosādhiṭṭhānaṁ? ", "html": "<p>{}</p>"}, "pe3:29.1": {"pli": "“Ekampi ce pāṇamaduṭṭhacitto, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe3:29.2": {"pli": "Mettāyati kusalo tena hoti; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:29.3": {"pli": "Sabbe ca pāṇe manasānukampaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:29.4": {"pli": "Pahūtamariyo pakaroti puññaṁ”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe3:30.1": {"pli": "Ekampi ce pāṇamaduṭṭhacitto mettāyatīti ayaṁ adoso. ", "html": "<p>{}"}, "pe3:30.2": {"pli": "Nigghātena assādo, kusalo tena hotīti tena kusalena dhammena saṁyutto dhammapaññattiṁ gacchati. ", "html": "{}"}, "pe3:30.3": {"pli": "Kusaloti yathā paññāya pañño paṇḍiccena paṇḍito. ", "html": "{}"}, "pe3:30.4": {"pli": "Pahūtamariyo pakaroti puññanti tassāyeva vipāko ayaṁ lokiyassa, na hi lokuttarassa. ", "html": "{}"}, "pe3:30.5": {"pli": "Tattha yā mettāyanā, ayaṁ hetu. ", "html": "{}"}, "pe3:30.6": {"pli": "Yaṁ kusalo bhavati ayaṁ nissando. ", "html": "{}"}, "pe3:30.7": {"pli": "Yāva abyāpajjo bhūmiyaṁ bahupuññaṁ pasavati, idaṁ phalaṁ. ", "html": "{}"}, "pe3:30.8": {"pli": "Iti adoso niddiṭṭho hetuto ca nissandato ca phalato ca. ", "html": "{}</p>"}, "pe3:31.1": {"pli": "Ekādasānisaṁsā mettāya cetovimuttiyā. ", "html": "<p>{}"}, "pe3:31.2": {"pli": "Tattha yā mettācetovimutti, ayaṁ ariyadhammesu rāgavirāgā cetovimutti, lokikāya bhūmikā hetu, yaṁ sukhaṁ āyatiṁ manāpo hoti manussānaṁ, ime ekādasa dhammā nissando. ", "html": "{}"}, "pe3:31.3": {"pli": "Yañca akatāvī brahmakāye upapajjati. ", "html": "{}"}, "pe3:31.4": {"pli": "Idaṁ phalaṁ. ", "html": "{}"}, "pe3:31.5": {"pli": "Idaṁ adosādhiṭṭhānaṁ. ", "html": "{}</p>"}, "pe3:32.1": {"pli": "Tattha katamaṁ amohādhiṭṭhānaṁ? ", "html": "<p>{}</p>"}, "pe3:33.1": {"pli": "“Paññā hi seṭṭhā lokasmiṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe3:33.2": {"pli": "yāyaṁ nibbedhagāminī; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:33.3": {"pli": "Yāya sammā pajānāti, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:33.4": {"pli": "jātimaraṇasaṅkhayaṁ”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe3:34.1": {"pli": "Paññā hi seṭṭhāti vatthuṁ. ", "html": "<p>{}"}, "pe3:34.2": {"pli": "Nibbedhagāminīti nibbānagāminiyaṁ yathābhūtaṁ paṭivijjhati. ", "html": "{}"}, "pe3:34.3": {"pli": "Sammā pajānāti, jātimaraṇasaṅkhayanti amoho. ", "html": "{}"}, "pe3:34.4": {"pli": "Paññāti hetu. ", "html": "{}"}, "pe3:34.5": {"pli": "Yaṁ pajānāti ayaṁ nissando. ", "html": "{}"}, "pe3:34.6": {"pli": "Yo jātimaraṇasaṅkhayo, idaṁ phalaṁ. ", "html": "{}"}, "pe3:34.7": {"pli": "Iti amoho niddiṭṭho hetunā ca nissandena ca phalena ca. ", "html": "{}</p>"}, "pe3:35.1": {"pli": "Tīṇimāni, bhikkhave, indriyāni anaññātaññassāmītindriyaṁ aññindriyaṁ aññātāvindriyaṁ. ", "html": "<p>{}"}, "pe3:35.2": {"pli": "Tattha katamaṁ anaññātaññassāmītindriyaṁ? ", "html": "{}"}, "pe3:35.3": {"pli": "Idha, bhikkhave, bhikkhu anabhisametassa dukkhassa ariyasaccassa abhisamayāya chandaṁ janeti vāyamati, vīriyaṁ ārabhati, cittaṁ paggaṇhāti padahati. ", "html": "{}"}, "pe3:35.4": {"pli": "Evaṁ catunnaṁ ariyasaccānaṁ kātabbaṁ. ", "html": "{}"}, "pe3:35.5": {"pli": "Tattha katamaṁ aññindriyaṁ? ", "html": "{}"}, "pe3:35.6": {"pli": "Idha, bhikkhave, bhikkhu “idaṁ dukkhaṁ ariyasaccan”ti yathābhūtaṁ pajānāti, yā ca maggo, idaṁ aññindriyaṁ. ", "html": "{}"}, "pe3:35.7": {"pli": "Āsavakkhayā anāsavo hoti, idaṁ vuccati aññātāvindriyaṁ. ", "html": "{}"}, "pe3:35.8": {"pli": "Tathāyaṁ paññā, ayaṁ hetu. ", "html": "{}"}, "pe3:35.9": {"pli": "Yaṁ chandaṁ janeti vāyamati, yā pajānāti, ayaṁ nissando. ", "html": "{}"}, "pe3:35.10": {"pli": "Yena sabbaso āsavānaṁ khayā hetu, yaṁ khaye ñāṇamuppajjati, anuppāde ñāṇañca, ayaṁ nissando. ", "html": "{}"}, "pe3:35.11": {"pli": "Yaṁ arahattaṁ, idaṁ phalaṁ. ", "html": "{}"}, "pe3:35.12": {"pli": "Tattha khīṇā me jāti, vusitaṁ brahmacariyaṁ, kataṁ karaṇīyanti, idaṁ khaye ñāṇaṁ. ", "html": "{}"}, "pe3:35.13": {"pli": "Nāparaṁ itthattāyāti pajānāmīti idaṁ anuppāde ñāṇaṁ. ", "html": "{}"}, "pe3:35.14": {"pli": "Iti imāni indriyāni amoho niddiṭṭho hetunā ca nissandena ca phalena ca. ", "html": "{}"}, "pe3:35.15": {"pli": "Imāni asādhāraṇāni niddiṭṭhāni. ", "html": "{}</p>"}, "pe3:36.1": {"pli": "Tattha katamāni kusalamūlāni sādhāraṇāni? ", "html": "<p>{}"}, "pe3:36.2": {"pli": "Kusalañca vo, bhikkhave, desessāmi kusalamūlañceva. ", "html": "{}"}, "pe3:36.3": {"pli": "Tattha katamaṁ kusalamūlaṁ? ", "html": "{}"}, "pe3:36.4": {"pli": "Alobho adoso amoho. ", "html": "{}"}, "pe3:36.5": {"pli": "Tattha katamaṁ kusalaṁ? ", "html": "{}"}, "pe3:36.6": {"pli": "Aṭṭha sammattāni sammādiṭṭhi yāva sammāsamādhi. ", "html": "{}"}, "pe3:36.7": {"pli": "Tattha yāni kusalamūlāni, ayaṁ hetu. ", "html": "{}"}, "pe3:36.8": {"pli": "Yañca alobho tīṇi kammāni samuṭṭhāpeti saṅkappaṁ vāyāmaṁ samādhiñca, ayaṁ alobhassa nissando. ", "html": "{}"}, "pe3:36.9": {"pli": "Tattha yo adoso, ayaṁ hetu. ", "html": "{}"}, "pe3:36.10": {"pli": "Yaṁ tayo dhamme paṭṭhapeti sammāvācaṁ sammākammantaṁ sammāājīvañca, ayaṁ nissando. ", "html": "{}"}, "pe3:36.11": {"pli": "Tattha yo amoho hetu, yaṁ dve dhamme upaṭṭhapeti aviparītadassanampi ca anabhilāpanaṁ, ayaṁ nissando. ", "html": "{}"}, "pe3:36.12": {"pli": "Imassa brahmacariyassa yaṁ phalaṁ, tā dve vimuttiyo rāgavirāgā cetovimutti avijjā virāgā ca paññāvimutti, idaṁ phalaṁ. ", "html": "{}"}, "pe3:36.13": {"pli": "Iti imāni tīṇi kusalamūlāni niddiṭṭhāni hetuto ca nissandato ca phalato ca. ", "html": "{}"}, "pe3:36.14": {"pli": "Evaṁ sādhāraṇāni kusalāni paṭivijjhitabbāni. ", "html": "{}</p>"}, "pe3:37.1": {"pli": "Yattha duve yattha tīṇi. ", "html": "<p>{}"}, "pe3:37.2": {"pli": "Ayañcettha gāthā. ", "html": "{}</p>"}, "pe3:38.1": {"pli": "“Tulamatulañca sambhavaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe3:38.2": {"pli": "Bhavasaṅkhāramavassaji muni; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:38.3": {"pli": "Ajjhattarato samāhito, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:38.4": {"pli": "Abhindi kavacamivattasambhavan”ti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe3:39.1": {"pli": "Tulamatulañca sambhavanti tulasaṅkhataṁ atulasaṅkhataṁ. ", "html": "<p>{}"}, "pe3:39.2": {"pli": "Tattha ye saṅkhatā tulaṁ, te dve dhammā assādo ca ādīnavo ca tulitā bhavanti. ", "html": "{}"}, "pe3:39.3": {"pli": "Ettako kāmesu assādo. ", "html": "{}"}, "pe3:39.4": {"pli": "Ettako ādīnavo imassa, idaṁ nissaraṇanti iti nibbānaṁ pajānāti. ", "html": "{}"}, "pe3:39.5": {"pli": "Dvīhi kāraṇehi atulaṁ na ca sakkā tulayituṁ. ", "html": "{}"}, "pe3:39.6": {"pli": "Ettakaṁ etaṁ netaṁ paramatthīti tena atulaṁ. ", "html": "{}"}, "pe3:39.7": {"pli": "Atha pāpuṇā ratanaṁ karitvā acchariyabhāvena atulaṁ. ", "html": "{}"}, "pe3:39.8": {"pli": "Tattha kusalassa ca abhisambhavā jānanā passanā, ayaṁ amoho. ", "html": "{}"}, "pe3:39.9": {"pli": "Yaṁ tattha ñātā osiraṇā bhavasaṅkhārānaṁ, ayaṁ alobho. ", "html": "{}"}, "pe3:39.10": {"pli": "Yaṁ ajjhattarato samāhitoti vikkhepapaṭisaṁharaṇā, ayaṁ adoso. ", "html": "{}"}, "pe3:39.11": {"pli": "Iti imāni tīṇi kusalamūlāni. ", "html": "{}"}, "pe3:39.12": {"pli": "Tulamatulasambhavanti ayaṁ amoho. ", "html": "{}"}, "pe3:39.13": {"pli": "Yo bhavasaṅkhārānaṁ samosaraṇaṁ lobho sammāsamādhīnaṁ assādo, ayaṁ hetu. ", "html": "{}"}, "pe3:39.14": {"pli": "Yaṁ ajjhattarato avijjaṇḍakosaṁ sambhedo, ayaṁ nissando. ", "html": "{}"}, "pe3:39.15": {"pli": "Sā pavatti imāni tīṇi niddiṭṭhāni kusalamūlāni hetuto ca nissandato ca phalato ca. ", "html": "{}</p>"}, "pe3:40.1": {"pli": "Ettāvatā esā pavatti ca nivatti ca akusalamūlehi pavattati, kusalamūlehi nivattatīti imehi ca tīhi sabbaṁ akusalamūlaṁ samosaraṇaṁ gacchati. ", "html": "<p>{}"}, "pe3:40.2": {"pli": "So dhamme vā vacanato niddiṭṭho taṇhāti vā kodhoti vā asampajaññanti vā anusayoti vā makkhoti vā paḷāsoti vā assatīti vā issāti vā macchariyanti vā aññāṇanti vā, tehi ye ca vatthūhi niddisitabbaṁ. ", "html": "{}"}, "pe3:40.3": {"pli": "Yassimāni dve vacanāni dhammapadāni niddiṭṭhāni na so atthi kilesā, yo imesu navasu padesu samodhānaṁ samosaraṇaṁ gacchati. ", "html": "{}"}, "pe3:40.4": {"pli": "Ayaṁ kileso, na ca lobho, na ca doso, na ca moho. ", "html": "{}</p>"}, "pe3:41.1": {"pli": "Yathā akusalamūlāni, evaṁ kusalāni paṭikkhepena niddisitabbāni. ", "html": "<p>{}"}, "pe3:41.2": {"pli": "Idaṁ amohādhiṭṭhānaṁ. ", "html": "{}</p>"}, "pe3:42.1": {"pli": "Tattha katamaṁ kāyakammādhiṭṭhānaṁ? ", "html": "<p>{}</p>"}, "pe3:43.1": {"pli": "“Kāyena kusalaṁ kare, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe3:43.2": {"pli": "assa kāyena saṁvuto; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:43.3": {"pli": "Kāyaduccaritaṁ hitvā, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:43.4": {"pli": "kāyena sucaritaṁ care”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe3:44.1": {"pli": "Tīṇimāni, bhikkhave, sucaritāni. ", "html": "<p>{}"}, "pe3:44.2": {"pli": "Pāṇātipātā veramaṇī, adinnādānā veramaṇī, kāmesumicchācārā veramaṇī, idaṁ kāyakammādhiṭṭhānaṁ. ", "html": "{}</p>"}, "pe3:45.1": {"pli": "Tattha katamaṁ vācākammādhiṭṭhānaṁ? ", "html": "<p>{}</p>"}, "pe3:46.1": {"pli": "“Subhāsitaṁ uttamamāhu santo, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe3:46.2": {"pli": "Dhammaṁ bhaṇe nādhammaṁ taṁ dutiyaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:46.3": {"pli": "Piyaṁ bhaṇe nāppiyaṁ taṁ tatiyaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:46.4": {"pli": "Saccaṁ bhaṇe nālikaṁ taṁ catutthaṁ”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe3:47.1": {"pli": "Cattārimāni ca vacīsucaritāni idaṁ vācākammādhiṭṭhānaṁ. ", "html": "<p>{}</p>"}, "pe3:48.1": {"pli": "Tattha katamaṁ manokammādhiṭṭhānaṁ? ", "html": "<p>{}</p>"}, "pe3:49.1": {"pli": "“Manena kusalaṁ kammaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe3:49.2": {"pli": "manasā saṁvuto bhave; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:49.3": {"pli": "Manoduccaritaṁ hitvā, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:49.4": {"pli": "manasā sucaritaṁ care”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe3:50.1": {"pli": "Tīṇimāni manosucaritāni, anabhijjhā, abyāpādo, sammādiṭṭhi, idaṁ manokammādhiṭṭhānaṁ. ", "html": "<p>{}"}, "pe3:50.2": {"pli": "Imāni asādhāraṇāni suttāni. ", "html": "{}</p>"}, "pe3:51.1": {"pli": "Tattha katamāni sādhāraṇāni suttāni? ", "html": "<p>{}</p>"}, "pe3:52.1": {"pli": "“Vācānurakkhī manasā susaṁvuto, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe3:52.2": {"pli": "Kāyena ca nākusalaṁ kayirā; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:52.3": {"pli": "Ete tayo kammapathe visodhaye, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:52.4": {"pli": "Ārādhaye maggamisippaveditaṁ”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe3:53.1": {"pli": "Tisso imā, bhikkhave, pārisuddhiyo—", "html": "<p>{}"}, "pe3:53.2": {"pli": "kāyakammapārisuddhi, vācākammapārisuddhi, manokammapārisuddhi. ", "html": "{}</p>"}, "pe3:54.1": {"pli": "Tattha katamā kāyakammapārisuddhi? ", "html": "<p>{}"}, "pe3:54.2": {"pli": "Pāṇātipātā veramaṇī, adinnādānā veramaṇī, kāmesumicchācārā veramaṇī. ", "html": "{}"}, "pe3:54.3": {"pli": "Tattha katamā vacīkammapārisuddhi? ", "html": "{}"}, "pe3:54.4": {"pli": "Musāvādā veramaṇī …pe… ", "html": "{}"}, "pe3:54.5": {"pli": "samphappalāpā veramaṇī. ", "html": "{}"}, "pe3:54.6": {"pli": "Tattha katamā manokammapārisuddhi? ", "html": "{}"}, "pe3:54.7": {"pli": "Anabhijjhā abyāpādo sammādiṭṭhi. ", "html": "{}"}, "pe3:54.8": {"pli": "Idaṁ sādhāraṇasuttaṁ. ", "html": "{}</p>"}, "pe3:55.1": {"pli": "Iti sādhāraṇāni ca suttāni asādhāraṇāni ca suttāni paṭivijjhitabbāni. ", "html": "<p>{}"}, "pe3:55.2": {"pli": "Paṭivijjhitvā vācāya kāyena ca suttassa attho niddisitabbo. ", "html": "{}</p>"}, "pe3:56.1": {"pli": "Tattha katamaṁ saddhindriyādhiṭṭhānaṁ? ", "html": "<p>{}</p>"}, "pe3:57.1": {"pli": "“Yassa saddhā tathāgate, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe3:57.2": {"pli": "acalā suppatiṭṭhitā; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:57.3": {"pli": "Sīlañca yassa kalyāṇaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:57.4": {"pli": "ariyakantaṁ pasaṁsitaṁ. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe3:58.1": {"pli": "Saṅghe pasādo yassatthi, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe3:58.2": {"pli": "ujubhūtañca dassanaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:58.3": {"pli": "Adaliddoti taṁ āhu, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:58.4": {"pli": "amoghaṁ tassa jīvitaṁ. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe3:59.1": {"pli": "Saddhā ve nandikā ārādhiko, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe3:59.2": {"pli": "No tassa saddhoti; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:59.3": {"pli": "Sabbaṁ siyāti bhagavantaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:59.4": {"pli": "Tathārūpo dhammasampasādo”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe3:60.1": {"pli": "Idaṁ saddhindriyādhiṭṭhānaṁ. ", "html": "<p class='endsutta'>{}</p>"}, "pe3:61.1": {"pli": "Tattha katamaṁ vīriyādhiṭṭhānaṁ? ", "html": "<p>{}</p>"}, "pe3:62.1": {"pli": "“Ārambhatha nikkamatha, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe3:62.2": {"pli": "yuñjatha buddhasāsane; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:62.3": {"pli": "Dhunātha maccuno senaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:62.4": {"pli": "naḷāgāraṁva kuñjaro”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe3:63.1": {"pli": "Cattārome, bhikkhave, sammappadhānā, idaṁ vīriyādhiṭṭhānaṁ. ", "html": "<p>{}</p>"}, "pe3:64.1": {"pli": "Tattha katamaṁ satindriyādhiṭṭhānaṁ? ", "html": "<p>{}</p>"}, "pe3:65.1": {"pli": "“Satīmato sadā bhaddaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe3:65.2": {"pli": "bhaddamatthu satīmato; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:65.3": {"pli": "Satīmato sadā seyyo, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:65.4": {"pli": "satīmā sukhamedhati”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe3:66.1": {"pli": "Cattāro satipaṭṭhānā vitthārena kātabbā, idaṁ satindriyādhiṭṭhānaṁ. ", "html": "<p>{}</p>"}, "pe3:67.1": {"pli": "Tattha katamaṁ samādhindriyādhiṭṭhānaṁ? ", "html": "<p>{}</p>"}, "pe3:68.1": {"pli": "“Ākaṅkhato te naradammasārathi, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe3:68.2": {"pli": "Devā manussā manasā vicintitaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:68.3": {"pli": "Sabbe na jaññā kasiṇāpi pāṇino, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:68.4": {"pli": "Santaṁ samādhiṁ araṇaṁ nisevato”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe3:69.1": {"pli": "Tayome, bhikkhave, samādhī—", "html": "<p>{}"}, "pe3:69.2": {"pli": "savitakko savicāro, avitakko vicāramatto, avitakko avicāro. ", "html": "{}"}, "pe3:69.3": {"pli": "Idaṁ samādhindriyādhiṭṭhānaṁ. ", "html": "{}</p>"}, "pe3:70.1": {"pli": "Tattha katamaṁ paññindriyādhiṭṭhānaṁ? ", "html": "<p>{}</p>"}, "pe3:71.1": {"pli": "Paññā hi seṭṭhā lokasminti vitthārena. ", "html": "<p>{}</p>"}, "pe3:72.1": {"pli": "Tisso imā, bhikkhave, paññā—", "html": "<p>{}"}, "pe3:72.2": {"pli": "sutamayī, cintāmayī, bhāvanāmayī, idaṁ paññindriyādhiṭṭhānaṁ suttaṁ, imāni indriyādhiṭṭhānāni asādhāraṇāni suttāni. ", "html": "{}</p>"}, "pe3:73.1": {"pli": "Tattha katamāni sādhāraṇāni indriyādhiṭṭhānāni suttāni? ", "html": "<p>{}</p>"}, "pe3:74.1": {"pli": "“Avītarāgo kāmesu, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe3:74.2": {"pli": "yassa pañcindriyā mudū; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:74.3": {"pli": "Saddhā sati ca vīriyaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:74.4": {"pli": "samatho ca vipassanā; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:74.5": {"pli": "Tādisaṁ bhikkhumāsajja, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:74.6": {"pli": "pubbeva upahaññati”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe3:75.1": {"pli": "Pañcimāni indriyāni. ", "html": "<p>{}"}, "pe3:75.2": {"pli": "Saddhindriyādiindriyaṁ daṭṭhabbaṁ. ", "html": "{}"}, "pe3:75.3": {"pli": "Tīsu aveccappasāde vitthārena suttaṁ kātabbaṁ. ", "html": "{}"}, "pe3:75.4": {"pli": "Imāni sādhāraṇāni indriyādhiṭṭhānāni suttāni. ", "html": "{}"}, "pe3:75.5": {"pli": "Yaṁ yassa sambandhaṁ kusalassa vā akusalassa vā tena tena adhiṭṭhānena taṁ suttaṁ niddisitabbaṁ, natthañño dhammo niddisitabbo. ", "html": "{}"}, "pe3:75.6": {"pli": "Tattha sādhāraṇaṁ kusalaṁ nāpi kusalaṁ akusalaṁ yathā sādhāraṇāni ca kusalamūlāni sādhāraṇāni ca akusalamūlāni uppannaṁ kāmavitakkaṁ pajahati …pe… ", "html": "{}"}, "pe3:75.7": {"pli": "cattāro sammappadhānā kusalaṁ akusalañca. ", "html": "{}</p>"}, "pe3:76.1": {"pli": "Tatthimā uddānagāthā ", "html": "<p class='uddana-intro'>{}</p>"}, "pe3:77.1": {"pli": "Vitakko hi mamatthiko, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "pe3:77.2": {"pli": "dadaṁ piyo naro iti; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:77.3": {"pli": "Yo pāṇamatipāteti, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:77.4": {"pli": "tīṇi tassa bālalakkhaṇaṁ. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe3:78.1": {"pli": "Satañceva sahassānaṁ, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe3:78.2": {"pli": "ye ca samaṇabrāhmaṇā; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:78.3": {"pli": "Chandā dosā bhayā mohā, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:78.4": {"pli": "catūhi agatīhi ca. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe3:79.1": {"pli": "Asubhānupassiṁ viharantaṁ, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe3:79.2": {"pli": "Nimittesu asubhā ca; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:79.3": {"pli": "Ekampi ce piyaṁ pāṇaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:79.4": {"pli": "Mittā sace subhāsitā. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe3:80.1": {"pli": "Paññā hi seṭṭhā lokasmiṁ, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe3:80.2": {"pli": "Anuññā tīṇi indriyāni; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:80.3": {"pli": "Kusalākusalamūlāni ca, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:80.4": {"pli": "Tulamatulañca sambhavaṁ. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe3:81.1": {"pli": "Kāyena kusalaṁ kare, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe3:81.2": {"pli": "Tīṇi sucaritāni ca; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:81.3": {"pli": "Subhāsitaṁ uttamamāhu, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:81.4": {"pli": "Santo vacīsucaritāni ca. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe3:82.1": {"pli": "Kāyena ca kusalaṁ kayirā, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe3:82.2": {"pli": "Manoduccaritāni ca; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:82.3": {"pli": "Kāyānurakkhī ca sadā, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:82.4": {"pli": "Tisso ca pārisuddhiyo. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe3:83.1": {"pli": "Yassa saddhā tathāgate, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe3:83.2": {"pli": "samuppāde ca desito; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:83.3": {"pli": "Ārambhatha nikkamatha, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:83.4": {"pli": "yā ca sammappadhānatā. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe3:84.1": {"pli": "Satīmato sadā bhaddaṁ, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe3:84.2": {"pli": "satipaṭṭhānabhāvanā; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:84.3": {"pli": "Ākaṅkhato ca anaññāṇaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:84.4": {"pli": "ye ca tīṇi samādhayo. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe3:85.1": {"pli": "Paññā hi seṭṭhā lokasmiṁ, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe3:85.2": {"pli": "tisso paññā pakāsitā; ", "html": "<span class='verse-line'>{}</span>"}, "pe3:85.3": {"pli": "Avītarāgo kāmesu, ", "html": "<span class='verse-line'>{}</span>"}, "pe3:85.4": {"pli": "tatheva pañcindriyā. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe3:86.1": {"pli": "Iti therassa mahākaccāyanassa jambuvanavāsino peṭakopadese tatiyabhūmi suttādhiṭṭhānaṁ nāma. ", "html": "<p class='endsutta'>{}</p></article>"}}, "pe4": {"pe4:0.1": {"pli": "Peṭakopadesa ", "html": "<article id='pe4'><header><ul><li class='division'>{}</li></ul>"}, "pe4:0.2": {"pli": "4. Suttavicayacatutthabhūmi ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "pe4:1.1": {"pli": "Tattha katamo suttavicayo? ", "html": "<p>{}</p>"}, "pe4:2.1": {"pli": "Tattha kusalehi dhammehi akusalehi dhammehi pubbāparaso sādhukaṁ upaparikkhiyati. ", "html": "<p>{}"}, "pe4:2.2": {"pli": "Kiṁ nu kho idaṁ suttaṁ ārabhi …pe… tehi suttehi saha adhisannaṭṭhehi yujjati udāhu na yujjatīti? ", "html": "{}</p>"}, "pe4:3.1": {"pli": "Yathā bhagavā kilese ādimhi tattha deseti. ", "html": "<p>{}"}, "pe4:3.2": {"pli": "Kiṁ desitaṁ? ", "html": "{}"}, "pe4:3.3": {"pli": "Tesaṁ kilesānaṁ pahānaṁ udāhu no desitanti upaparikkhitabbaṁ. ", "html": "{}"}, "pe4:3.4": {"pli": "Yadi na desitaṁ bhagavati tesaṁ kilesānaṁ pahānaṁ kusalā dhammā pariyesitabbā; ", "html": "{}"}, "pe4:3.5": {"pli": "yattha te akusalā pahānaṁ gacchanti. ", "html": "{}"}, "pe4:3.6": {"pli": "Sace samannehamāno na labhati. ", "html": "{}"}, "pe4:3.7": {"pli": "Tattha akusalā dhammā apakaḍḍhitabbā vīmaṁsitabbā, saṅkilesabhāgiyasuttaṁ, yadi kilesā apakaḍḍhiyantā. ", "html": "{}"}, "pe4:3.8": {"pli": "Ye vā na denti tattha upaparikkhitabbā ariyamaggadhammā tāsu bhūmīsu kilesā pahānaṁ gacchanti, udāhu na gacchantīti. ", "html": "{}"}, "pe4:3.9": {"pli": "Yattakā pana kilesā desitā. ", "html": "{}"}, "pe4:3.10": {"pli": "Na tattakā ariyadhammā desitā. ", "html": "{}"}, "pe4:3.11": {"pli": "Yattha kilesā pahānaṁ gacchanti, tattha ye kilesā ariyadhammānaṁ paṭipakkhena na yujjanti, te apakaḍḍhitabbā, sace apakaḍḍhiyantā yojanaṁ deti. ", "html": "{}"}, "pe4:3.12": {"pli": "Tattha evaṁ vīmaṁsitabbaṁ. ", "html": "{}"}, "pe4:3.13": {"pli": "Dve tīṇi vā taduttari vā kilesā ekena ariyamaggena pahānaṁ gacchantīti. ", "html": "{}"}, "pe4:3.14": {"pli": "Sace evaṁ vīmaṁsiyantā yojanaṁ deti, tattha upaparikkhitabbaṁ. ", "html": "{}"}, "pe4:3.15": {"pli": "Paramparāya vā piṭakasampadānena vā suttassa attho ca nattho ca. ", "html": "{}"}, "pe4:3.16": {"pli": "Yaṁ vā na sakkā suttaṁ niddisituṁ neva suttaṁ vicikicchitabbaṁ. ", "html": "{}"}, "pe4:3.17": {"pli": "Evaṁ yathā ādimhi kusalā dhammā honti. ", "html": "{}"}, "pe4:3.18": {"pli": "Ye kilesā te pahīneyyāti. ", "html": "{}"}, "pe4:3.19": {"pli": "Te upaparikkhitabbā. ", "html": "{}"}, "pe4:3.20": {"pli": "Puro vā kusalo paṭipakkhena vā puro desanā, anūnā anadhikā uggahetabbā. ", "html": "{}"}, "pe4:3.21": {"pli": "Yathā paṭhamo uttilo yesamidāni kilesānaṁ ye ariyadhammā desitā ime kilesā imehi ariyadhammehi pahīyanti, udāhu nappahīyantīti vicinitabbā. ", "html": "{}"}, "pe4:3.22": {"pli": "Yadi upaparikkhiyamānā yujjanti, gahetabbā. ", "html": "{}"}, "pe4:3.23": {"pli": "Atha na yujjanti, ye kilesā apaṭipakkhā honti, te kilesā aparipakkhitabbā. ", "html": "{}"}, "pe4:3.24": {"pli": "Ye ca ariyadhammā paṭipakkhā honti, te ariyadhammā apakaḍḍhitabbā. ", "html": "{}"}, "pe4:3.25": {"pli": "Na hi ariyadhammā anāgāmikilesappahānaṁ gacchanti, nāpi ariyadhammā sabbakilesānaṁ pahānāya saṁvattanti. ", "html": "{}"}, "pe4:3.26": {"pli": "Yathā kusalā mettā akusalo rāgo na tu kusalā mettāti kāretvā akusalassa rāgassa pahānāya sambhavati byāpādo mettāya pahānaṁ gacchati. ", "html": "{}"}, "pe4:3.27": {"pli": "Tasmā ubho kilesā upaparikkhitabbā. ", "html": "{}"}, "pe4:3.28": {"pli": "Yo yo ca dhammo upadisiyati kusalo vā akusalo vā so apakaḍḍhitabbo. ", "html": "{}"}, "pe4:3.29": {"pli": "Sace te yujjanti apakaḍḍhiyamāno natthi upaparikkhitabbaṁ. ", "html": "{}"}, "pe4:3.30": {"pli": "Dve vā kilesā ekena ariyadhammena pahīneyyāti dvīhi vā ariyadhammehi eko vā kileso pahīyatīti. ", "html": "{}</p>"}, "pe4:4.1": {"pli": "Atha vā evampi upaparikkhiyamānaṁ yujjati, tattha vīmaṁsitabbaṁ vā yathā yujjati tattha vīmaṁsitabbaṁ vā, yathā nanu sakkā suttaṁ niddisituṁ, na hi sutte vicikicchitabbaṁ. ", "html": "<p>{}"}, "pe4:4.2": {"pli": "Kileso maṁ ariyadhammesu desitesu ubhayato upaparikkhitabbaṁ. ", "html": "{}"}, "pe4:4.3": {"pli": "Kira ye vā ime kilesā desitā ye ca ariyadhammā desitā gāthāya vā byākaraṇena vā, kiṁ nu kho ime kilesā imehi ariyadhammehi pahīyanti, udāhu nappahīyanti? ", "html": "{}"}, "pe4:4.4": {"pli": "Ime vā ariyadhammā imesaṁ kilesānaṁ pahānāya saṁvattantīti. ", "html": "{}"}, "pe4:4.5": {"pli": "Kiñcāpi kusalehi dhammehi akusalā dhammā pahānaṁ gacchanti. ", "html": "{}"}, "pe4:4.6": {"pli": "Na tu sabbehi ariyadhammehi sabbākusalā pahānaṁ gacchanti. ", "html": "{}"}, "pe4:4.7": {"pli": "Yathā mettā kusalo akusalo ca rāgo na tu kusalā mettā akusalo rāgoti kāretvā mettāya rāgo pahānaṁ, byāpādo mettāya pahānaṁ gacchanti. ", "html": "{}"}, "pe4:4.8": {"pli": "Evaṁ kilesoti kāretvā suttena pahānaṁ gacchati. ", "html": "{}"}, "pe4:4.9": {"pli": "Na sutto dhammoti kāretvā sabbaṁ kilesassa pahānāya saṁvattati. ", "html": "{}"}, "pe4:4.10": {"pli": "Yaṁ tu suttassa ariyadhammo saṅkilesapaṭipakkho, so tena pahānaṁ gacchatīti. ", "html": "{}</p>"}, "pe4:5.1": {"pli": "Tattha kusale desite sutte byākaraṇe vā saṅkilesā na yujjanti ariyadhammā vā, te mahāpadese niddisitabbāvayavena apakaḍḍhitabbā. ", "html": "<p>{}"}, "pe4:5.2": {"pli": "Tattha kilesehi ca desitehi ariyadhammesu ca yadipi tena ariyadhammena te kilesā pahānaṁ gacchanti. ", "html": "{}"}, "pe4:5.3": {"pli": "Tatthapi uttari upaparikkhitabbaṁ. ", "html": "{}"}, "pe4:5.4": {"pli": "Kena kāraṇena ete kilesā pajahitabbā, kena kāraṇena ariyadhammā desitāti? ", "html": "{}"}, "pe4:5.5": {"pli": "Yena yena vā ākārena ariyadhammā desitā, tena tena pakārena ayaṁ kileso ṭhito. ", "html": "{}"}, "pe4:5.6": {"pli": "Atthi hi eko kileso, tena vā ariyadhammā na aññathā aññathā pahātabbo, yathā diṭṭhi rāgo avijjā ca dassanena pahātabbā. ", "html": "{}"}, "pe4:5.7": {"pli": "Sā ce evañca avijjā bhāvanāya bhūmi vā dhammā bhāvanāya pahātabbā. ", "html": "{}"}, "pe4:5.8": {"pli": "Sāyeva uddhambhāgiyaṁ asaṅkhatadassanāya vimuttiyā animittena cetosamādhinā amanasikārena pahīyati. ", "html": "{}"}, "pe4:5.9": {"pli": "Evaṁ sātthaṁ sabyañjanaṁ upaparikkhitabbaṁ. ", "html": "{}"}, "pe4:5.10": {"pli": "Ye dassanena pahātabbā kilesā dassanākārena ariyadhammo desito, bhāvanāya pahātabbā bhāvanākārena ariyadhammo desito, patisevanā pahātabbā patisevanākārena ariyadhammo desito, evaṁ vinodanapahātabbā yāva satta āsavā kātabbā, yāvaññathā. ", "html": "{}"}, "pe4:5.11": {"pli": "Aññathā hesa dhammo pahātabbo aññenākārena ariyadhammo desito, so ariyadhammo aññathā pariyesitabbo. ", "html": "{}"}, "pe4:5.12": {"pli": "Yadi ayaṁ dhammo pariyesato yo ca deseti yena yenākārena, so ariyadhammo pariyesitabbo, tenākārena kileso pahīyati. ", "html": "{}"}, "pe4:5.13": {"pli": "So tattha upaparikkhitabbo. ", "html": "{}"}, "pe4:5.14": {"pli": "Atha na yujjati yadi hi tena suttena vihitaṁ suttaṁ vīmaṁsitabbaṁ. ", "html": "{}"}, "pe4:5.15": {"pli": "Yathā yujjati, tathā gahetabbaṁ. ", "html": "{}"}, "pe4:5.16": {"pli": "Yathā na yujjati, tathā na gahetabbaṁ, addhā etaṁ bhagavatā na bhāsitaṁ, āyasmatā vā duggahitaṁ, yathā mahāpadese niddisitabbaṁ, bhagavatā yathābhūtaṁ desitaṁ, yo ca dhammo desito kusalo ca akusalo ca tassa dhammassa paccayo pariyesitabbo. ", "html": "{}"}, "pe4:5.17": {"pli": "Na hi paccayā vinā dhammo appaccayo uppajjati. ", "html": "{}"}, "pe4:5.18": {"pli": "Tattha ko ākāro pariyesanāya? ", "html": "{}</p>"}, "pe4:6.1": {"pli": "Tattha tathārūpaṁ sahetu sappaccayaṁ soyaṁ dhammo vuttoti idaṁ vīmaṁsitabbaṁ. ", "html": "<p>{}"}, "pe4:6.2": {"pli": "So ca paccayo tividho—", "html": "{}"}, "pe4:6.3": {"pli": "mudu majjho adhimatto. ", "html": "{}"}, "pe4:6.4": {"pli": "Tattha mudumhi paccaye mududhammo gahetabbo, evaṁ satyesa paccayo duvidho paramparāpaccayo ca samanantarapaccayo ca. ", "html": "{}"}, "pe4:6.5": {"pli": "So paccayo mudutena byādhimattaṁ pariyesitabbaṁ. ", "html": "{}"}, "pe4:6.6": {"pli": "Kiṅkāraṇaṁ? ", "html": "{}"}, "pe4:6.7": {"pli": "Aññataropi paccayo aññehi paccayehi pariyattiṁ vā pāripūriṁ vā gacchati. ", "html": "{}"}, "pe4:6.8": {"pli": "Tattha yo dhammo desito, tassa dhammassa etena vā kāraṇena vā hetu pariyesitabbo. ", "html": "{}"}, "pe4:6.9": {"pli": "Yathā paccayo hetunā paccayena ca, so tassa dhammassa nissando pariyesitabbo. ", "html": "{}"}, "pe4:6.10": {"pli": "Yathā niddiṭṭho adhiṭṭhāne padhānaṁ pariyesati, so paccayo pariyesitabbo. ", "html": "{}"}, "pe4:6.11": {"pli": "Na hi mudussa dhammassa adhimatto nissando adhimattassa vā nissandassa mududhammo, atha mudussa mudu majjhāya majjho adhimattassa adhimatto yujjati, taṁ gahetabbaṁ, atha na yujjati na gahetabbaṁ. ", "html": "{}"}, "pe4:6.12": {"pli": "Yañca bhagavā ārabhati dhammaṁ desetuṁ, taṁyeva dhammaṁ majjhantapariyosānaṁ deseti, yathā suttādhiṭṭhāne dhammā ādimhi niddisati, taṁyeva bahu tassa suttassa pariyosānaṁ. ", "html": "{}"}, "pe4:6.13": {"pli": "Tassa hi dhammassa vasena taṁ suttaṁ hoti gāthā vā byākaraṇaṁ khuddakaṁ mahantaṁ vā, yathā pana duvidhā anurūpanti vā thapanā ca desanāthapanā. ", "html": "{}"}, "pe4:6.14": {"pli": "Rūpantipi dhammassa pariyesitabbā. ", "html": "{}"}, "pe4:6.15": {"pli": "Yathā ca bhagavatā pañcannaṁ indriyānaṁ saṁvaraṇaṁ desitaṁ taṇhāya niggahaṇatthaṁ icchāva hoti. ", "html": "{}"}, "pe4:6.16": {"pli": "Deseti yathā gopālakopame sutte aññehipi suttehi bhagavā bhāsati icchāva hoti majjhimanikāye vitakko ayaṁ bhagavato desanānurūpanti iti so dhammo aññesupi veyyākaraṇesu pariyesitabbo. ", "html": "{}"}, "pe4:6.17": {"pli": "Na hi ekaṁ hi sutte daṭṭhabbo. ", "html": "{}"}, "pe4:6.18": {"pli": "Yujjanaṁ taṁ gahetabbaṁ. ", "html": "{}</p>"}, "pe4:7.1": {"pli": "Tattha katamaṁ anuññātaṁ? ", "html": "<p>{}"}, "pe4:7.2": {"pli": "Yaṁ kiñci suttaṁ bhagavatā na bhāsitaṁ tañca suttesuyeva dissati, evametaṁ dhāretabbaṁ. ", "html": "{}"}, "pe4:7.3": {"pli": "Yathā asukena bhāsitanti, taṁ suttaṁ vīmaṁsitabbaṁ. ", "html": "{}"}, "pe4:7.4": {"pli": "Kiṁ nu kho imaṁ suttaṁ anuññātaṁ khamaṁ bhagavato udāhu nānuññātaṁ khamaṁ, kiñci rūpañca suttaṁ bhagavato anuññātaṁ khamaṁ kiñci rūpañca nānuññātaṁ khamaṁ? ", "html": "{}"}, "pe4:7.5": {"pli": "Yaṁ sabbaso anotāretvā dasabalo gocaraṁ deseti, taṁ sabbaṁ suttaṁ bhagavato nānuññātaṁ khamaṁ. ", "html": "{}"}, "pe4:7.6": {"pli": "Atthipi so sāvako dasabalānaṁ gocaraṁ jānāti odhiso anodhiso, taṁ pana balaṁ sabbaso na jānāti aññathā nāma savanena, yathā āyasmatā sāriputtena yena brāhmaṇo ovadito, tassa āyasmato natthi indriyabalavemattañāṇaṁ, tena puggalaparo parañca taṁ ajānanto sati uttarikaraṇīye uppādito, so bhagavatā apasādito. ", "html": "{}"}, "pe4:7.7": {"pli": "Yathāva āyasmā mahākassapo bhāgineyyaṁ ovadati anantariyasamannāgato iddhipāṭihīrena aṅguliyo adīpetvā yaṁ sabbesaṁ dhammānaṁ kammasamādānānaṁ hetuso ṭhānaso yathābhūtaṁ ñāṇaṁ, tassa āyasmato saṁvijjate, tena naṁ ovadati, taṁ bhagavā karoti. ", "html": "{}</p>"}, "pe4:8.1": {"pli": "“Sacepi dasa pajjote, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe4:8.2": {"pli": "dhārayissasi kassapa; ", "html": "<span class='verse-line'>{}</span>"}, "pe4:8.3": {"pli": "Neva dakkhati rūpāni, ", "html": "<span class='verse-line'>{}</span>"}, "pe4:8.4": {"pli": "cakkhu tassa na vijjatī”ti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe4:9.1": {"pli": "Api ca kho yathā dūto rājavacanena sattamanusāsati, evaṁ sesānugo aññātakaṁ ghosaṁ paresaṁ deseti. ", "html": "<p>{}"}, "pe4:9.2": {"pli": "Anuññātakhamasuttaṁ gahetabbaṁ. ", "html": "{}"}, "pe4:9.3": {"pli": "Ananuññātakhamaṁ na gahetabbaṁ. ", "html": "{}</p>"}, "pe4:10.1": {"pli": "Tattha katamo suttasaṅkaro? ", "html": "<p>{}"}, "pe4:10.2": {"pli": "Pañcavidhaṁ suttaṁ, saṅkilesabhāgiyaṁ vāsanābhāgiyaṁ dassanabhāgiyaṁ bhāvanābhāgiyaṁ asekkhabhāgiyaṁ. ", "html": "{}"}, "pe4:10.3": {"pli": "Aññaṁ ārādheyya aññaṁ deseti aññassa ca suttassa atthaṁ aññamhi sutte niddisati. ", "html": "{}"}, "pe4:10.4": {"pli": "Suttassa vā hi anekākāraṁ atthaṁ niddisati. ", "html": "{}"}, "pe4:10.5": {"pli": "Ariyadhammasādhane atthaṁ vivarati. ", "html": "{}"}, "pe4:10.6": {"pli": "Vāsanābhāgiyassa atthaṁ dassanabhāgiyesu niddisati. ", "html": "{}"}, "pe4:10.7": {"pli": "Orambhāgiyānaṁ saṁyojanānaṁ atthaṁ uddhambhāgiyesu niddisati. ", "html": "{}"}, "pe4:10.8": {"pli": "Mudumajjhānaṁ indriyānaṁ adhimattesu suttesu niddisati. ", "html": "{}"}, "pe4:10.9": {"pli": "Iti ayaṁ suttaṁ sambhedaṁ hetunā ca nissandena ca phalena ca niddesena ca mudumajjhādhimattatāyapi ca atthena ca byañjanena ca yo sambhedo, ayaṁ vuccati suttasaṅkaro. ", "html": "{}"}, "pe4:10.10": {"pli": "Yo asambhedo, ayaṁ vuccati suttavicayo. ", "html": "{}</p>"}, "pe4:11.1": {"pli": "Tatthāyaṁ uddānagāthā ", "html": "<p class='uddana-intro'>{}</p>"}, "pe4:12.1": {"pli": "Purimānaṁ akkhaṇḍaṁ, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "pe4:12.2": {"pli": "yathābhūtassa paccayo; ", "html": "<span class='verse-line'>{}</span>"}, "pe4:12.3": {"pli": "Nissando vāsanāsaddhi, ", "html": "<span class='verse-line'>{}</span>"}, "pe4:12.4": {"pli": "anuññā suttasaṅkaro. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe4:13.1": {"pli": "Therassa mahākaccāyanassa suttavicayo nāma catutthabhūmi. ", "html": "<p class='endsutta'>{}</p></article>"}}, "pe5": {"pe5:0.1": {"pli": "Peṭakopadesa ", "html": "<article id='pe5'><header><ul><li class='division'>{}</li></ul>"}, "pe5:0.2": {"pli": "5. Hāravibhaṅgapañcamabhūmi ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "pe5:1.1": {"pli": "Tattha katamo <b>hāravibhaṅgo</b>? ", "html": "<p data-counter='1'>{}"}, "pe5:1.2": {"pli": "Yattha soḷasa hārā akkharaso bhedaṁ gacchanti. ", "html": "{}"}, "pe5:1.3": {"pli": "Tattha ādimhi <b>desanāhāro</b>. ", "html": "{}"}, "pe5:1.4": {"pli": "Tattha ayaṁ gāthā kusalā vā akusalā vā saccāni vā saccekadeso vā. ", "html": "{}"}, "pe5:1.5": {"pli": "Kiṁ desitanti? ", "html": "{}"}, "pe5:1.6": {"pli": "Sutte vīmaṁsā desanāhāro. ", "html": "{}"}, "pe5:1.7": {"pli": "Yathā ariyasaccāni nikkhepo cattāri saccāni sādhāraṇāni asādhāraṇāni ca. ", "html": "{}"}, "pe5:1.8": {"pli": "Yāni ca aṭṭhārasa padāni dukkhato satta padāni saṅkhepena kāyikena cetasikena dukkhena, appiyasampayogena piyavippayogena ca tīhi ca saṅkhatāhi. ", "html": "{}"}, "pe5:1.9": {"pli": "Tattha tīṇi saṅkhatalakkhaṇāni tisso dukkhatā uppādo saṅkhatalakkhaṇaṁ, saṅkhāradukkhatāya dukkhatā ca saṅkhatalakkhaṇaṁ, vipariṇāmadukkhatāya dukkhatāti aññathattaṁ ca saṅkhatalakkhaṇaṁ, dukkhadukkhatāya ca dukkhatā, imesaṁ tiṇṇaṁ saṅkhatalakkhaṇānaṁ tīsu vedanābhūmīsu adukkhamasukhā vedanā uppādo saṅkhatalakkhaṇaṁ, saṅkhāradukkhatāya ca dukkhatā tayo saṅkhatalakkhaṇaṁ, sukhā vedanāya ca vipariṇāmadukkhatāya ca dukkhatāti aññathattaṁ saṅkhatalakkhaṇaṁ, dukkhāvedanā dukkhadukkhatā ca dukkhatā imamhi imesu navapadesu paṭhamakesu sattasu padesu soḷasasu padesu dukkhā pariyesitabbā, ekādasa dukkhatāya ca lakkhaṇaṁ niddese niddiṭṭhaṁ. ", "html": "{}"}, "pe5:1.10": {"pli": "Pātubhāvalakkhaṇā jātiyā ca pātubhāvacutilakkhaṇo cutoti vitthārena pannarasapadāni kattabbāni, evaṁ sādhāraṇāni asādhāraṇāni ca sattasu dasasu padesu paññāsa tividhe ca sāsanappaṭṭhāne aṭṭhārasavidhesu ca suttādhiṭṭhānesu dasavidhesu ca suttavidheyyesu soḷasavidhesu ca hāresu ekavīsatividhāya ca pavicayavīmaṁsāyāti idaṁ desitaṁ. ", "html": "{}"}, "pe5:1.11": {"pli": "Yathābhūtañca desitanti, ayaṁ vuccati desanāhāro. ", "html": "{}</p>"}, "pe5:2.1": {"pli": "Tattha katamo <b>vicayo hāro</b>? ", "html": "<p>{}</p>"}, "pe5:3.1": {"pli": "“Padaṁ pañhā ca pucchā ca, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe5:3.2": {"pli": "kiṁ pubbaṁ kiñca pacchimaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe5:3.3": {"pli": "Anugīti sā ca vicayo, ", "html": "<span class='verse-line'>{}</span>"}, "pe5:3.4": {"pli": "hāro vicayoti niddiṭṭho”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe5:4.1": {"pli": "Padanti paṭhamaṁ padaṁ. ", "html": "<p>{}"}, "pe5:4.2": {"pli": "Tassa ko attho? ", "html": "{}"}, "pe5:4.3": {"pli": "Yaṁ bhagavā puṭṭho āyasmatā ajitena taṁ gahetabbaṁ, katipadāni puṭṭhāni yathā kiṁ kenassu nivuto lokoti gāthā, imāni katipadāni cattāri iti visajjanāya pucchā. ", "html": "{}"}, "pe5:4.4": {"pli": "Yattakehi padehi bhagavatā visajjitāni padāni iti pucchāya ca yā padānaṁ saṅkāsanā, idaṁ vuccati padanti. ", "html": "{}</p>"}, "pe5:5.1": {"pli": "Pañhāti imāni cattāri padāni. ", "html": "<p>{}"}, "pe5:5.2": {"pli": "Kati pañhā? ", "html": "{}"}, "pe5:5.3": {"pli": "Eko vā dve vā taduttari vā imāni cattāri padāni eko pañho, atthānuparivatti byañjanaṁ hoti, sambahulānipi padāni ekamevatthaṁ pucchati. ", "html": "{}"}, "pe5:5.4": {"pli": "Imāni cattāri padāni anuparivattīni taṁ byañjanena eko pañhova hoti. ", "html": "{}"}, "pe5:5.5": {"pli": "Kenassu nivuto lokoti lokaṁ sandhāya pucchati, kenassu nappakāsati kissābhilepanaṁ brūsīti taṁyeva pucchati. ", "html": "{}"}, "pe5:5.6": {"pli": "Kiṁsu tassa mahabbhayanti taṁyeva pucchati. ", "html": "{}"}, "pe5:5.7": {"pli": "Evaṁ atthānuparivatti byañjanaṁ eko pañho hoti, so pañho catubbidho ekaṁsabyākaraṇīyo vibhajjabyākaraṇīyo paṭipucchābyākaraṇīyo ṭhapaniyoti. ", "html": "{}"}, "pe5:5.8": {"pli": "Tattha cakkhu aniccanti ekaṁsabyākaraṇīyo, yaṁ aniccaṁ taṁ dukkhanti vibhajjabyākaraṇīyo, siyā aniccaṁ na cakkhu, yānipi āyatanāni ca na cakkhu, tānipi aniccanti na cakkhuyeva, ayaṁ vibhajjabyākaraṇīyo, yaṁ cakkhu taṁ cakkhundriyaṁ neti paṭipucchābyākaraṇīyo, taṁ cakkhu tathāgatoti ṭhapaniyo. ", "html": "{}"}, "pe5:5.9": {"pli": "Aññatra cakkhunāti ṭhapaniyo pañho. ", "html": "{}"}, "pe5:5.10": {"pli": "Idaṁ pañhaṁ bhagavā kiṁ pucchito, lokassa saṅkileso pucchito. ", "html": "{}"}, "pe5:5.11": {"pli": "Kiṅkāraṇaṁ? ", "html": "{}"}, "pe5:5.12": {"pli": "Tividho hi saṅkileso taṇhāsaṅkileso ca diṭṭhisaṅkileso ca duccaritasaṅkileso ca. ", "html": "{}"}, "pe5:5.13": {"pli": "Tattha avijjāya nivutoti avijjaṁ dasseti, jappāti taṇhaṁ dasseti, mahabbhayanti akusalassa kammassa vipākaṁ dasseti, sotaṁ nāma sukhavedanīyassa kammassa dukkhavedanīyo vipāko bhavissatīti netaṁ ṭhānaṁ vijjatīti bhagavā visajjeti, catūhi yo padehi avijjāya nivuto lokoti …pe… ", "html": "{}"}, "pe5:5.14": {"pli": "evaṁ vuccati. ", "html": "{}</p>"}, "pe5:6.1": {"pli": "Taduttari paṭipucchati, savanti sabbadhi sotāti gāthā, cattāri padāni pucchati taṁ bhagavā dvīhi padehi visajjeti. ", "html": "<p>{}</p>"}, "pe5:7.1": {"pli": "“Yāni sotāni lokasmiṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe5:7.2": {"pli": "sati tesaṁ nivāraṇaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe5:7.3": {"pli": "Sotānaṁ saṁvaraṁ brūmi, ", "html": "<span class='verse-line'>{}</span>"}, "pe5:7.4": {"pli": "paññāyete pidhīyare”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe5:8.1": {"pli": "Imāni cattāri padāni dvīhi padehi visajjeti. ", "html": "<p>{}"}, "pe5:8.2": {"pli": "Idaṁ padanti pucchito, tassa saṅkiliṭṭhassa lokassa vodānaṁ pucchito, sotāni cha taṇhākāyā bahulādhivacanena niddiṭṭhā bhavanti sabbehi āyatanehi. ", "html": "{}"}, "pe5:8.3": {"pli": "Tāni sotāni kena nivāriyantīti pariyuṭṭhānapahānaṁ pucchati, kena sotā pidhīyareti anusayasamugghātaṁ pucchati. ", "html": "{}"}, "pe5:8.4": {"pli": "Tattha bhagavā chasu dvāresu satiyā deseti, yo hi sampajāno viharati satidovārikena ca tassa indriyāni guttāni sambhavanti. ", "html": "{}"}, "pe5:8.5": {"pli": "Tattha guttesu indriyesu yā yā vipassanā, sā sā tesaṁ tesaṁ sotānaṁ tassā ca avijjāya yo loko nivuto accantapahānāya saṁvattati. ", "html": "{}"}, "pe5:8.6": {"pli": "Evaṁ sotāni pihitānipi bhavanti tato uttari pucchati. ", "html": "{}</p>"}, "pe5:9.1": {"pli": "Paññā ca sati ca nāmarūpassa kho tassa bhagavantaṁ puṭṭhumāgamma katthetaṁ upasammati imāni cattāri padāni bhagavā ekena padena visajjeti. ", "html": "<p>{}</p>"}, "pe5:10.1": {"pli": "“Yametaṁ pañhaṁ apucchi, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe5:10.2": {"pli": "Ajita taṁ vadāmi te; ", "html": "<span class='verse-line'>{}</span>"}, "pe5:10.3": {"pli": "…pe… ", "html": "<span class='verse-line'>{}</span>"}, "pe5:10.4": {"pli": "Viññāṇassa nirodhena, ", "html": "<span class='verse-line'>{}</span>"}, "pe5:10.5": {"pli": "Etthetaṁ upasammati”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe5:11.1": {"pli": "Iminā pañhena kiṁ pucchati? ", "html": "<p>{}"}, "pe5:11.2": {"pli": "Anupādisesanibbānadhātuṁ pucchati, taṁ bhagavā anupādisesāya nibbānadhātuyā visajjeti. ", "html": "{}"}, "pe5:11.3": {"pli": "Tattha paṭhamena pañhena saṅkilesaṁ pucchati. ", "html": "{}"}, "pe5:11.4": {"pli": "Dutiyena pañhena vodānaṁ pucchati. ", "html": "{}"}, "pe5:11.5": {"pli": "Tatiyena pañhena sopādisesanibbānadhātuṁ pucchati. ", "html": "{}"}, "pe5:11.6": {"pli": "Catutthena pañhena anupādisesanibbānadhātuṁ paṭipucchati tato uttari paṭipucchati. ", "html": "{}</p>"}, "pe5:12.1": {"pli": "“Ye ca saṅkhātadhammāse, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe5:12.2": {"pli": "ye ca sekhā puthū idha; ", "html": "<span class='verse-line'>{}</span>"}, "pe5:12.3": {"pli": "Tesaṁ me nipako iriyaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe5:12.4": {"pli": "puṭṭho pabrūhi mārisa”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe5:13.1": {"pli": "Imāni cattāri padāni pucchati. ", "html": "<p>{}"}, "pe5:13.2": {"pli": "Kati ca pana te pañhe saṅkhātadhammā ca arahantā sekkhā ca? ", "html": "{}"}, "pe5:13.3": {"pli": "Kiṁ pubbaṁ kiñca pacchimanti ayamattho. ", "html": "{}"}, "pe5:13.4": {"pli": "Tattha kataraṁ paṭhamaṁ pucchati, kataraṁ pacchā? ", "html": "{}"}, "pe5:13.5": {"pli": "Arahantaṁ paṭhamaṁ pucchati. ", "html": "{}"}, "pe5:13.6": {"pli": "Sekkhadhamme tattha kena padena saṅkhātadhammāti arahanto gahitā, puthūti sekkhā gahitā. ", "html": "{}"}, "pe5:13.7": {"pli": "Tesaṁ me nipakoti sādhāraṇaṁ padaṁ bhagavantaṁ pucchati. ", "html": "{}"}, "pe5:13.8": {"pli": "Tassa sādhāraṇāni ca asādhāraṇāni ca pañhesu pucchitabbāni. ", "html": "{}"}, "pe5:13.9": {"pli": "Taṁ bhagavā visajjeti. ", "html": "{}"}, "pe5:13.10": {"pli": "Na tathā puṭṭhaṁ, paṭhamaṁ puṭṭhaṁ, taṁ pucchā visajjeti. ", "html": "{}"}, "pe5:13.11": {"pli": "Yaṁ pacchā pucchitaṁ paṭhamaṁ visajjeti. ", "html": "{}"}, "pe5:13.12": {"pli": "Kiñca idaṁ pucchitaṁ visuddhānaṁ visujjhantānañca kā iriyāti idaṁ pucchi, taṁ kāmesu nābhigijjheyya. ", "html": "{}"}, "pe5:13.13": {"pli": "Manasānāvilo siyāti pariyuṭṭhānāni vitakkena ca bhagavā nivāreti, dve pana vitakkaanāvilatāya pariyuṭṭhānaṁ, yathā nīvaraṇesu niddiṭṭhaṁ. ", "html": "{}"}, "pe5:13.14": {"pli": "Kusalā sabbadhammesūti arahantaṁ visajjeti. ", "html": "{}</p>"}, "pe5:14.1": {"pli": "Kenassu tarati oghanti gāthā, imāni cattāri padāni. ", "html": "<p>{}"}, "pe5:14.2": {"pli": "Cattāroyeva pañhā. ", "html": "{}"}, "pe5:14.3": {"pli": "Kiṅkāraṇaṁ, na hi ettha atthānuparivatti byañjanaṁ yathā paṭhamaṁ ajitapañhesu, tassa na ekaṁsena bahūni visajjanāni, bahukā pañhā, ekova na cāpi, sabbe pucchati, pubbe visajjito, yathā catuttho ajitapañhe, yaṁ ettha yathābhūtaṁ pariyesanāpadabandhena visajjanāyo evaṁ yathābhūtaṁ pariyesati. ", "html": "{}"}, "pe5:14.4": {"pli": "Yo puna ettha yaṁ evaṁ pucchati tattha ayamākāro pucchanāyaṁ antojaṭā bahijaṭāti gāthā pucchitavisajjanāya maggitabbā. ", "html": "{}"}, "pe5:14.5": {"pli": "Kathaṁ visajjitāti bhagavāti visajjeti? ", "html": "{}"}, "pe5:14.6": {"pli": "Sīle patiṭṭhāya naro sapaññoti gāthā. ", "html": "{}"}, "pe5:14.7": {"pli": "Tattha cittabhāvanāya samathā, paññābhāvanāya vipassanā. ", "html": "{}"}, "pe5:14.8": {"pli": "Tattha evaṁ anumīyati, ye dhammā samathena ca vipassanāya ca pahīyanti, te ime antojaṭā bahijaṭā. ", "html": "{}"}, "pe5:14.9": {"pli": "Tattha visajjanaṁ samathena rāgo pahīyati, vipassanāya avijjā. ", "html": "{}"}, "pe5:14.10": {"pli": "Ajjhattavatthuko rāgo antojaṭā, bāhiravatthuko rāgo bahijaṭā. ", "html": "{}"}, "pe5:14.11": {"pli": "Ajjhattavatthukā sakkāyadiṭṭhi, ayaṁ antojaṭā. ", "html": "{}"}, "pe5:14.12": {"pli": "Ekasaṭṭhi diṭṭhigatāni ca bāhiravatthukāni bahijaṭā, yā hi ajjhattavatthukā yā diṭṭhibhāgiyena bhavissati, ayaṁ jaṭā. ", "html": "{}"}, "pe5:14.13": {"pli": "Tathā saṅkhittena yā kāci ajjhattavatthukā taṇhā ca diṭṭhi ca, ayaṁ antojaṭā. ", "html": "{}"}, "pe5:14.14": {"pli": "Yā kāci bāhiravatthukā taṇhā ca diṭṭhi ca, ayaṁ bahijaṭā. ", "html": "{}</p>"}, "pe5:15.1": {"pli": "Yathā devatā bhagavantaṁ pucchati “catucakkaṁ navadvāran”ti gāthā. ", "html": "<p>{}"}, "pe5:15.2": {"pli": "Tattha bhagavā visajjeti “chetvā naddhiṁ varattaṁ cā”ti gāthā, idaṁ bhagavā dukkhanirodhagāminiṁ paṭipadaṁ visajjeti. ", "html": "{}"}, "pe5:15.3": {"pli": "Imāya visajjanāya bhagavā anumīyati kilese ettha purimāya gāthāya niddisitabbena. ", "html": "{}"}, "pe5:15.4": {"pli": "Taṁ hi catucakkanti cattāro vā hatthapādā. ", "html": "{}"}, "pe5:15.5": {"pli": "Navadvāranti nava vaṇamukhāni. ", "html": "{}"}, "pe5:15.6": {"pli": "Yathā catucakkanti cattāro upādānā, upādānappaccayā bhavo, upādānanirodhā bhavanirodho. ", "html": "{}"}, "pe5:15.7": {"pli": "Navadvāranti nava mānavidhā, mānajātikāya hi dukkhaṁ seyyenamhi paraso tīṇi tikāni puṇṇaṁ. ", "html": "{}"}, "pe5:15.8": {"pli": "Tikena saṁyuttaṁ hi pañcakāmaguṇiko rāgo. ", "html": "{}"}, "pe5:15.9": {"pli": "Tattha naddhīti taṇhā visajjīyati. ", "html": "{}"}, "pe5:15.10": {"pli": "Varattanti mānaṁ visajjeti, icchā lobho ca pāpakoti pañcakāmaguṇiko rāgo. ", "html": "{}"}, "pe5:15.11": {"pli": "Tattha visamalobho pāpakoti niddisiyati samūlataṇhanti. ", "html": "{}"}, "pe5:15.12": {"pli": "Aññāṇamūlakā taṇhāti aññāṇamūlakā taṇhā, taṇhāya ca diṭṭhiyā ca pahānaṁ. ", "html": "{}"}, "pe5:15.13": {"pli": "Ye ca puna aññepi keci catucakkayogena teneva kāraṇena ca yujjanti, saṁsāragāmino dhammā sabbe niddisitabbā. ", "html": "{}"}, "pe5:15.14": {"pli": "Tatthāyaṁ gāthā visajjanā pucchāya ca visajjanāya sameti. ", "html": "{}"}, "pe5:15.15": {"pli": "Yaṁ yadi sandena atha saha byākaraṇena anugītiyaṁ ca so vicayoti bhagavā yattakāni padāni nikkhipati, tattakehi anugāyati. ", "html": "{}</p>"}, "pe5:16.1": {"pli": "Aṭṭhahi, bhikkhave, aṅgehi samannāgato bhikkhu dūteyyaṁ gantumarahati. ", "html": "<p>{}"}, "pe5:16.2": {"pli": "Imāni aṭṭha padāni nikkhittāni. ", "html": "{}"}, "pe5:16.3": {"pli": "Chahi padehi bhagavā anugāyati. ", "html": "{}</p>"}, "pe5:17.1": {"pli": "“Yo ve na byathati patvā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe5:17.2": {"pli": "parisaṁ uggavādiniṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe5:17.3": {"pli": "Na ca hāpeti vacanaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe5:17.4": {"pli": "na ca chādeti sāsanaṁ. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe5:18.1": {"pli": "Asandiddhiṁ ca bhaṇati, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe5:18.2": {"pli": "Pucchito na ca kuppati; ", "html": "<span class='verse-line'>{}</span>"}, "pe5:18.3": {"pli": "Sa ve tādisako bhikkhu, ", "html": "<span class='verse-line'>{}</span>"}, "pe5:18.4": {"pli": "Dūteyyaṁ gantumarahatī”ti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe5:19.1": {"pli": "Tattha pana bhagavā yattakāni padāni nikkhipati, tattakehi anugāyati. ", "html": "<p data-counter='2'>{}"}, "pe5:19.2": {"pli": "Sattahi, bhikkhave, aṅgehi samannāgato kalyāṇamitto piyo garubhāvanīyoti vitthārena, idaṁ bhagavā sattahi padehi anugāyati. ", "html": "{}"}, "pe5:19.3": {"pli": "Iti bahussutavā anugāyati, appatarakathaṁ padaṁ vā nikkhepo, bahussutavā nava padāni nikkhepo, appatarikā anugītiyā bahutarikā anugāyati. ", "html": "{}"}, "pe5:19.4": {"pli": "Ayaṁ vuccati te anugīti ca vicayo, ayaṁ vicayo nāma hāro. ", "html": "{}</p>"}, "pe5:20.1": {"pli": "Tattha katamo <b>yuttihāro</b>? ", "html": "<p>{}</p>"}, "pe5:21.1": {"pli": "“Sabbesaṁ hārānaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe5:21.2": {"pli": "Yā bhūmī yo ca gocaro tesaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe5:21.3": {"pli": "Yuttāyutti parikkhā, ", "html": "<span class='verse-line'>{}</span>"}, "pe5:21.4": {"pli": "Hāro yuttīti niddiṭṭho”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe5:22.1": {"pli": "Hārānaṁ soḷasannaṁ yathā desanā yathā vicayo yo ca niddisiyati, ayaṁ niddeso. ", "html": "<p>{}"}, "pe5:22.2": {"pli": "Ayaṁ pucchā suttesu na yujjatīti yā tattha vīmaṁsā, ayaṁ yutti. ", "html": "{}</p>"}, "pe5:23.1": {"pli": "Yathā hi sahetū sappaccayā sattā saṅkilissanti, atthi hetu atthi paccayo sattānaṁ saṅkilesāya, sahetū sappaccayā sattā visujjhanti, atthi hetu atthi paccayo sattānaṁ visuddhiyā. ", "html": "<p>{}"}, "pe5:23.2": {"pli": "Sīlavatā, ānanda, puggalena na veyyākaraṇiyā kinti me vippaṭisāro uppādeyya …pe… abyākaraṇaṁ kattabbaṁ, ayaṁ visuddhiyā maggo. ", "html": "{}"}, "pe5:23.3": {"pli": "Tassa hetu ko paccayo, sīlakkhandhassa cattāri cattāri hetu ca paccayo ca. ", "html": "{}"}, "pe5:23.4": {"pli": "Sappurisasaṁsevo yo ca patirūpadesavāso ca, ayaṁ upādāpaccayatā sappaccayo. ", "html": "{}"}, "pe5:23.5": {"pli": "Yaṁ porāṇakammaṁ assa vipāko paccayo, tāya paccayāya attasammāpaṇidhi, ayaṁ hetu. ", "html": "{}"}, "pe5:23.6": {"pli": "Iti sīlakkhandho sahetu sappaccayoti idaṁ lokikaṁ sīlaṁ. ", "html": "{}</p>"}, "pe5:24.1": {"pli": "Yaṁ pana lokuttaraṁ sīlaṁ, tassa tīṇi indriyāni paccayo—", "html": "<p data-counter='3'>{}"}, "pe5:24.2": {"pli": "saddhindriyaṁ vīriyindriyaṁ samādhindriyaṁ—", "html": "{}"}, "pe5:24.3": {"pli": "ayaṁ paccayo. ", "html": "{}"}, "pe5:24.4": {"pli": "Satindriyañca paññindriyañca hetu. ", "html": "{}"}, "pe5:24.5": {"pli": "Paññāya nibbedhagāminiyā, yaṁ sīlaṁ jāyati. ", "html": "{}"}, "pe5:24.6": {"pli": "Sotāpannassa ca sīlaṁ tenāyaṁ hetu ayaṁ paccayo. ", "html": "{}"}, "pe5:24.7": {"pli": "Yaṁ puna samādhino passaddhi ca pīti ca pāmojjaṁ paccayo. ", "html": "{}"}, "pe5:24.8": {"pli": "Yaṁ sukhaṁ hetu tena samādhikkhandho sahetu sappaccayo. ", "html": "{}"}, "pe5:24.9": {"pli": "Yaṁ samāhito yathābhūtaṁ pajānāti, ayaṁ paññā. ", "html": "{}"}, "pe5:24.10": {"pli": "Tassa paratoghoso ajjhattaṁ ca yoniso manasikāro hetu ca paccayo ca, iti ime tayo khandhā sahetū sappaccayā evaṁ satta paññā. ", "html": "{}"}, "pe5:24.11": {"pli": "Sattabyākaraṇīsu ca suttesu na yujjati. ", "html": "{}"}, "pe5:24.12": {"pli": "Ayaṁ yuttihāro. ", "html": "{}"}, "pe5:24.13": {"pli": "So catūsu mahāpadesesu daṭṭhabbo. ", "html": "{}</p>"}, "pe5:25.1": {"pli": "Tattha katamaṁ <b>padaṭṭhānaṁ</b>? ", "html": "<p>{}</p>"}, "pe5:26.1": {"pli": "“Dhammaṁ deseti jino, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe5:26.2": {"pli": "Tassa ca dhammassa yaṁ padaṭṭhānaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe5:26.3": {"pli": "Iti yāva sabbadhammā, ", "html": "<span class='verse-line'>{}</span>"}, "pe5:26.4": {"pli": "Eso hāro padaṭṭhāno”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe5:27.1": {"pli": "Tattha pañca kāmaguṇā kāmarāgassa padaṭṭhānaṁ. ", "html": "<p>{}"}, "pe5:27.2": {"pli": "Yesaṁ kesañci kāmarāgo uppajjati uppanno vā uppajjissati vā, etesu yepi pañcasu rūpesu āyatanesu nāññatra etehi kāmarāgassa padaṭṭhānanti. ", "html": "{}"}, "pe5:27.3": {"pli": "Vuccate, tena pañca kāmaguṇā kāmarāgassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.4": {"pli": "Pañcindriyāni rūparāgassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.5": {"pli": "Manindriyaṁ bhavarāgassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.6": {"pli": "Pañcakkhandhā sakkāyadiṭṭhiyā padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.7": {"pli": "Ekasaṭṭhi diṭṭhigatāni diṭṭhirāgassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.8": {"pli": "Kāmadhātu kāmarāgassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.9": {"pli": "Arūpadhātu arūparāgassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.10": {"pli": "Sukhasaññā kāmarāgassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.11": {"pli": "Byāpādasaññā byāpādassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.12": {"pli": "Asampajaññatā sammohassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.13": {"pli": "Nava āghātavatthūni byāpādassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.14": {"pli": "Navavidhaṁ mānaṁ mānassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.15": {"pli": "Sukhā vedanā rāgānusayassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.16": {"pli": "Dukkhā vedanā paṭighānusayassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.17": {"pli": "Adukkhamasukhā vedanā avijjānusayassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.18": {"pli": "Attavādupādānañca musāvādo ca lobhassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.19": {"pli": "Pāṇātipāto ca pisuṇavācā ca pharusavācā ca byāpādassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.20": {"pli": "Micchattañca samphappalāpo ca mohassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.21": {"pli": "Bhavaṁ bhogañca vokāro ahaṅkārassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.22": {"pli": "Bāhirānaṁ pariggaho mamaṅkārassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.23": {"pli": "Kāyassa saṅgaṁ diṭṭhiyā padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.24": {"pli": "Kāyikadoso dosassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.25": {"pli": "Kāyikakāsāvo lobhassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.26": {"pli": "Yo yo vā pana dhammo yena yena ārammaṇena uppajjati saccādhiṭṭhānena vā dhammādhiṭṭhānena vā anusayanena vā, so dhammo tassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:27.27": {"pli": "Tena sārammaṇena so dhammo uppajjati. ", "html": "{}</p>"}, "pe5:28.1": {"pli": "Yathā manusso purimassa padassa padaṭṭhānaṁ alabhanto dutiyaṁ padaṁ uddharati, so pacchānupadaṁ saṁharati. ", "html": "<p data-counter='4'>{}"}, "pe5:28.2": {"pli": "Yadi pana yo na dutiyapadassa padaṭṭhānaṁ labhati, aparaṁ padaṁ uddharati. ", "html": "{}"}, "pe5:28.3": {"pli": "Tassa yo ceso paccayo bhavati. ", "html": "{}"}, "pe5:28.4": {"pli": "Evaṁ dhammo kusalo vā akusalo vā abyākato vā padaṭṭhānaṁ alabhanto na pavattati. ", "html": "{}"}, "pe5:28.5": {"pli": "Yathā payuttassa dhammassa yonilābho, ayaṁ vuccati padaṭṭhāno hāro. ", "html": "{}</p>"}, "pe5:29.1": {"pli": "Tattha katamo <b>lakkhaṇo hāro</b>? ", "html": "<p>{}</p>"}, "pe5:30.1": {"pli": "“Vuttamhi ekadhamme, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe5:30.2": {"pli": "Ye dhammā ekalakkhaṇā tena; ", "html": "<span class='verse-line'>{}</span>"}, "pe5:30.3": {"pli": "Sabbe bhavanti vuttā, ", "html": "<span class='verse-line'>{}</span>"}, "pe5:30.4": {"pli": "So hāro lakkhaṇo nāma”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe5:31.1": {"pli": "Yesañca susamāraddhā, niccaṁ kāyagatāsatīti gāthāya vuttāya kāyagatāsatiyā vuttā vedanāgatā cittagatā dhammagatā ca sati catunnaṁ satipaṭṭhānānaṁ ekena satipaṭṭhānena. ", "html": "<p>{}"}, "pe5:31.2": {"pli": "Na hi cittaṁ ekasmiṁ viññāṇaṭṭhitiyā pavattati, nānāsu gatīsu pavattati, kāyagatāsatiyā vuttāya vuttā vedanāgatā cittadhammagatā ca. ", "html": "{}"}, "pe5:31.3": {"pli": "Na hi kāyagatāsatiyā bhāvitāya satipaṭṭhānā cattāro bhāvanāpāripūriṁ na gacchanti. ", "html": "{}"}, "pe5:31.4": {"pli": "Evaṁ tassadisesu dhammesu vuttesu sabbadhammā vuttā ca bhavanti. ", "html": "{}</p>"}, "pe5:32.1": {"pli": "Sacittapariyodāpanaṁ, etaṁ buddhāna sāsananti gāthā cetasikā dhammā vuttā, citte rūpaṁ vuttaṁ. ", "html": "<p data-counter='5'>{}"}, "pe5:32.2": {"pli": "Idaṁ nāmarūpaṁ dukkhaṁ ariyasaccaṁ. ", "html": "{}"}, "pe5:32.3": {"pli": "Tato sacittapariyodāpanā yaṁ yaṁ odapeti, taṁ dukkhaṁ. ", "html": "{}"}, "pe5:32.4": {"pli": "Yena odapeti, so maggo. ", "html": "{}"}, "pe5:32.5": {"pli": "Yato odapanā, so nirodho. ", "html": "{}"}, "pe5:32.6": {"pli": "Cakkhuṁ ca paṭicca rūpe ca uppajjati cakkhuviññāṇaṁ, tattha sahajātā vedanā saññā cetanā phasso manasikāro etete dhammā ekalakkhaṇā uppādalakkhaṇena. ", "html": "{}"}, "pe5:32.7": {"pli": "Yo ca rūpe nibbindati, vedanāya so nibbindati, saññāsaṅkhāraviññāṇesupi so nibbindati. ", "html": "{}"}, "pe5:32.8": {"pli": "Iti ye ekalakkhaṇā dhammā, tesaṁ ekamhi dhamme niddiṭṭhe sabbe dhammā niddiṭṭhā honti, ayaṁ vuccati lakkhaṇo hāro. ", "html": "{}</p>"}, "pe5:33.1": {"pli": "Tattha katamo <b>catubyūho hāro</b>? ", "html": "<p>{}</p>"}, "pe5:34.1": {"pli": "“Nirutti adhippāyo ca, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe5:34.2": {"pli": "byañjanā desanāya ca; ", "html": "<span class='verse-line'>{}</span>"}, "pe5:34.3": {"pli": "Suttattho pubbāparasandhi, ", "html": "<span class='verse-line'>{}</span>"}, "pe5:34.4": {"pli": "eso hāro catubyūho”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe5:35.1": {"pli": "Tattha katamā nirutti, sā kathaṁ pariyesitabbā? ", "html": "<p>{}"}, "pe5:35.2": {"pli": "Yathā vuttaṁ bhagavatā ekādasahi aṅgehi samannāgato bhikkhu khippaṁ dhammesu mahattaṁ pāpuṇāti, atthakusalo ca hoti, dhammakusalo ca hoti, niruttikusalo ca hoti, itthādhivacanakusalo ca hoti, purisādhivacanakusalo ca, vipurisādhivacanakusalo ca, atītādhivacanakusalo ca, anāgatādhivacanakusalo ca, paccuppannādhivacanakusalo ca. ", "html": "{}"}, "pe5:35.3": {"pli": "Ekādhippāyena kusalo nānādhippāyena kusalo. ", "html": "{}"}, "pe5:35.4": {"pli": "Kimhi desitaṁ, atītānāgatapaccuppannaṁ. ", "html": "{}"}, "pe5:35.5": {"pli": "Itthādhivacanena purisādhivacanena vipurisādhivacanena sabbaṁ yathāsuttaṁ niddiṭṭhaṁ. ", "html": "{}"}, "pe5:35.6": {"pli": "Taṁ byañjanato niruttikosallato yo yaṁ suttassa suniruttidunniruttitaṁ avekkhati, idaṁ evaṁ niropayitabbaṁ. ", "html": "{}"}, "pe5:35.7": {"pli": "Idampi na niropayitabbaṁ. ", "html": "{}"}, "pe5:35.8": {"pli": "Idaṁ vuccate niruttikosallaṁ. ", "html": "{}</p>"}, "pe5:36.1": {"pli": "Tattha katamaṁ adhippāyakosallaṁ? ", "html": "<p>{}"}, "pe5:36.2": {"pli": "Yathādesitassa suttassa sabbassa vāraṁ gacchati imena bhagavatā desitabbanti. ", "html": "{}"}, "pe5:36.3": {"pli": "Yathā kiṁ appamādo amataṁ padaṁ, pamādo maccuno padanti gāthā. ", "html": "{}"}, "pe5:36.4": {"pli": "Ettha bhagavato ko adhippāyo? ", "html": "{}"}, "pe5:36.5": {"pli": "Ye asītimeva ākaṅkhanti te appamattā viharissanti, ayaṁ adhippāyo. ", "html": "{}</p>"}, "pe5:37.1": {"pli": "“Yogassa kālaṁ na nivattati yā ca, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe5:37.2": {"pli": "So na tattha pāpintave bhavanti; ", "html": "<span class='verse-line'>{}</span>"}, "pe5:37.3": {"pli": "Vedanāmaggaisinā paveditaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe5:37.4": {"pli": "Dhutarajāsavā dukkhā pamokkhātā”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe5:38.1": {"pli": "Ettha bhagavato ko adhippāyo? ", "html": "<p>{}"}, "pe5:38.2": {"pli": "Ye dukkhe nāssādakā, te vīriyamārabhissanti dukkhakkhayāyāti. ", "html": "{}"}, "pe5:38.3": {"pli": "Ayaṁ tattha bhagavato adhippāyo. ", "html": "{}"}, "pe5:38.4": {"pli": "Iti gāthāya vā byākaraṇena vā desite iminā suttena sādhakā, yo evaṁ dhammānudhammaṁ paṭipajjatīti so adhippāyo, ayaṁ vuccati desanādhippāyo. ", "html": "{}</p>"}, "pe5:39.1": {"pli": "Tattha katamo pubbāparasandhi? ", "html": "<p data-counter='6'>{}"}, "pe5:39.2": {"pli": "Yaṁ gāthāyaṁ vā suttesu vā padāni asīti tāni bhavanti evaṁ vā evameti tassā gāthāya suttassa vā yāni purimāni padāni yāni ca pacchimakāni, tāni samosāretabbāni. ", "html": "{}"}, "pe5:39.3": {"pli": "Evaṁ so pubbāparena sandhi ñāyati. ", "html": "{}"}, "pe5:39.4": {"pli": "Yā ekā samāraddhā gāthā dve tīṇi vā tassa mekadese bhāsitānaṁ abhāsitāhi gāthāhi aniddiṭṭho attho bhavati tadupadhāritabbaṁ. ", "html": "{}"}, "pe5:39.5": {"pli": "Yaṁva sabbā itissa pariyesamānassa pariyesanā kaṅkhā, tassa vā puggalassa paññattīnaṁ apare pariyesitabbaṁ. ", "html": "{}"}, "pe5:39.6": {"pli": "Idaṁ vuccate pubbāparena sandhi. ", "html": "{}"}, "pe5:39.7": {"pli": "Kosallanti vatthuto nidānakosallaṁ. ", "html": "{}"}, "pe5:39.8": {"pli": "Byañjanato niruttikosallaṁ. ", "html": "{}"}, "pe5:39.9": {"pli": "Desanādhippāyakosallaṁ. ", "html": "{}"}, "pe5:39.10": {"pli": "Pubbāparena sandhikosallaṁ. ", "html": "{}"}, "pe5:39.11": {"pli": "Tattha tassa gāthā pariyesitā nidānaṁ vā. ", "html": "{}"}, "pe5:39.12": {"pli": "Upalabbhituṁ na attho niddisitabbo vatthuto nidānakosallaṁ atthakosallaṁ imehi catūhi padehi attho pariyesiyanto yathābhūtaṁ pariyiṭṭho hoti. ", "html": "{}"}, "pe5:39.13": {"pli": "Atha ca sabbo vatthuto vā nidānena vā yo adhippāyo byañjano nirutti sandhi ca anuttaro eso pubbāparena evaṁ suttatthena desitabbaṁ. ", "html": "{}"}, "pe5:39.14": {"pli": "Ayaṁ catubyūho hāro. ", "html": "{}</p>"}, "pe5:40.1": {"pli": "Tattha katamo <b>āvaṭṭo hāro</b>? ", "html": "<p>{}</p>"}, "pe5:41.1": {"pli": "“Ekamhi padaṭṭhāne, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe5:41.2": {"pli": "Pariyesati sesakaṁ padaṭṭhānaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe5:41.3": {"pli": "Āvaṭṭati paṭipakkhe, ", "html": "<span class='verse-line'>{}</span>"}, "pe5:41.4": {"pli": "Āvaṭṭo nāma so hāro”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe5:42.1": {"pli": "Yathā kiṁ unnaḷānaṁ pamattānanti gāthāyo. ", "html": "<p>{}"}, "pe5:42.2": {"pli": "Yaṁ pamādo, idaṁ kissa padaṭṭhānaṁ? ", "html": "{}"}, "pe5:42.3": {"pli": "Kusalānaṁ dhammānaṁ osaggassa. ", "html": "{}"}, "pe5:42.4": {"pli": "Kusaladhammosaggo pana kissa padaṭṭhānaṁ? ", "html": "{}"}, "pe5:42.5": {"pli": "Akusaladhammapaṭisevanāya. ", "html": "{}"}, "pe5:42.6": {"pli": "Kissa padaṭṭhānaṁ, kusaladhammapaṭisevanāya? ", "html": "{}"}, "pe5:42.7": {"pli": "Kissa padaṭṭhānaṁ, kilesavatthupaṭisevanāya? ", "html": "{}"}, "pe5:42.8": {"pli": "Iti pamādena mohapakkhiyā diṭṭhi avijjā chandarāgapakkhiyā. ", "html": "{}"}, "pe5:42.9": {"pli": "Tattha taṇhā ca diṭṭhi cattāro āsavā taṇhā kāmāsavo ca bhavāsavo ca diṭṭhāsavo ca avijjāsavo ca. ", "html": "{}"}, "pe5:42.10": {"pli": "Tattha citte atthīti diṭṭhi cetasikesu niccanti pañcasu kāmaguṇesu ajjhāvahanena kāmāsavo, upapattīsu āsatti bhavāsavo. ", "html": "{}"}, "pe5:42.11": {"pli": "Tattha rūpakāyo kāmāsavassa bhavāsavassa ca padaṭṭhānaṁ. ", "html": "{}"}, "pe5:42.12": {"pli": "Nāmakāyo diṭṭhāsavassa avijjāsavassa ca padaṭṭhānaṁ. ", "html": "{}</p>"}, "pe5:43.1": {"pli": "Tattha alliyanāya ajjhattavāhanaṁ kāmāsavassa lakkhaṇaṁ. ", "html": "<p data-counter='7'>{}"}, "pe5:43.2": {"pli": "Patthanaganthanaabhisaṅkhārakāyasaṅkhāraṇaṁ bhavāsavassa lakkhaṇaṁ, abhiniveso ca parāmāso ca diṭṭhāsavassa lakkhaṇaṁ. ", "html": "{}"}, "pe5:43.3": {"pli": "Appaṭivedho dhammesu asampajaññā ca avijjāsavassa lakkhaṇaṁ. ", "html": "{}"}, "pe5:43.4": {"pli": "Ime cattāro āsavā cattāri upādānāni. ", "html": "{}"}, "pe5:43.5": {"pli": "Kāmāsavo kāmupādānaṁ, bhavāsavo bhavupādānaṁ, diṭṭhāsavo diṭṭhupādānaṁ, avijjāsavo attavādupādānaṁ, imehi catūhi upādānehi pañcakkhandhā. ", "html": "{}"}, "pe5:43.6": {"pli": "Tattha avijjāsavo citte pahātabbo, so citte cittānupassissa pahīyati. ", "html": "{}"}, "pe5:43.7": {"pli": "Diṭṭhāsavo dhammesu pahātabbo, so dhammesu dhammānupassissa pahīyati. ", "html": "{}"}, "pe5:43.8": {"pli": "Bhavāsavo āsattiyā pahātabbo, so vedanāsu vedanānupassissa pahīyati. ", "html": "{}"}, "pe5:43.9": {"pli": "Kāmāsavo pañcasu kāmaguṇesu pahātabbo, so kāye kāyānupassissa pahīyati. ", "html": "{}"}, "pe5:43.10": {"pli": "Tattha kāyānupassanā dukkhamariyasaccaṁ bhajati. ", "html": "{}"}, "pe5:43.11": {"pli": "Vedanānupassanā pañcannaṁ indriyānaṁ paccayo sukhindriyassa dukkhindriyassa somanassindriyassa domanassindriyassa upekkhindriyassa, sattakilesopacāro tena samudayaṁ bhajati. ", "html": "{}"}, "pe5:43.12": {"pli": "Citte cittānupassanā nirodhaṁ bhajati. ", "html": "{}"}, "pe5:43.13": {"pli": "Dhammesu dhammānupassanā maggaṁ bhajati. ", "html": "{}"}, "pe5:43.14": {"pli": "Tenassa catūsu ca dassanena tasseva sabbe pahīyanti, yena niddiṭṭhā paṭhamaṁ unnaḷānaṁ pamattānaṁ tesaṁ vaḍḍhanti āsavā. ", "html": "{}"}, "pe5:43.15": {"pli": "Jānato hi passato āsavānaṁ khayo dukkhaṁ samudayo nirodho maggo hi akusalā dhammā. ", "html": "{}"}, "pe5:43.16": {"pli": "Evaṁ pariyesitabbā. ", "html": "{}"}, "pe5:43.17": {"pli": "Yāva tassa akusalassa gati tato paṭipakkhena akusale dhamme pariyesati tesaṁ kilesānaṁ hārena āvaṭṭati. ", "html": "{}"}, "pe5:43.18": {"pli": "Ayaṁ vuccate āvaṭṭo hāro. ", "html": "{}"}, "pe5:43.19": {"pli": "Evaṁ sukkāpi dhammā pariyesitabbā. ", "html": "{}"}, "pe5:43.20": {"pli": "Akusaladhamme āgamissa. ", "html": "{}</p>"}, "pe5:44.1": {"pli": "Tattha āvaṭṭassa hārassa ayaṁ bhūmi sati upaṭṭhānā ca vipallāsā ca cattāri ñāṇāni sakkāyasamuppādāyagāminī ca paṭipadā sakkāyanirodhagāminī paṭipadā. ", "html": "<p>{}</p>"}, "pe5:45.1": {"pli": "Tattha katamo <b>vibhatti hāro</b>? ", "html": "<p data-counter='8'>{}"}, "pe5:45.2": {"pli": "Yaṁ kiñci vibhajjabyākaraṇīyaṁ vuccati vibhatti hāro. ", "html": "{}"}, "pe5:45.3": {"pli": "Yathā kiṁ āgantvā ca puna puggalo hoti, no vāgataṁ na paribhāsati paripucchatāya pañhāya atiyanaṁ ekassa kiñci—", "html": "{}"}, "pe5:45.4": {"pli": "ayaṁ vuccate vibhatti hāro. ", "html": "{}</p>"}, "pe5:46.1": {"pli": "Tattha katamo <b>parivattano hāro</b>? ", "html": "<p data-counter='9'>{}"}, "pe5:46.2": {"pli": "Yaṁ kiñci paṭipakkhaniddeso, ayaṁ vuccati parivattano hāro. ", "html": "{}"}, "pe5:46.3": {"pli": "Yathā vuttaṁ bhagavatā sammādiṭṭhikassa purisapuggalassa micchādiṭṭhi nijjiṇṇā hotīti vitthārena sabbāni maggaṅgāni. ", "html": "{}"}, "pe5:46.4": {"pli": "Ayaṁ vuccate parivattano hāro. ", "html": "{}</p>"}, "pe5:47.1": {"pli": "Tattha katamo <b>vevacano hāro</b>? ", "html": "<p>{}</p>"}, "pe5:48.1": {"pli": "“Vevacanehi anekehi, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe5:48.2": {"pli": "Ekaṁ dhammaṁ pakāsitaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe5:48.3": {"pli": "Sutte yo jānāti suttavidū, ", "html": "<span class='verse-line'>{}</span>"}, "pe5:48.4": {"pli": "Vevacano nāma so hāro”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe5:49.1": {"pli": "Yathā āyasmā sāriputto ekamhi vatthumhi vevacanena nānāvuttena bhagavatā pasaṁsito “mahāpañño sāriputto hāsapañño javanapañño”ti idaṁ paññāya vevacanaṁ. ", "html": "<p data-counter='10'>{}"}, "pe5:49.2": {"pli": "Yathā ca maggavibhaṅge niyyānattho ekamekaṁ maggaṅgaṁ vevacanehi niddiṭṭhaṁ. ", "html": "{}"}, "pe5:49.3": {"pli": "Evaṁ avijjāya vevacanā. ", "html": "{}"}, "pe5:49.4": {"pli": "Ekaṁ akusalamūlaṁ tadeva santaṁ tesu tesu janapadesu tena tena pajānanti. ", "html": "{}"}, "pe5:49.5": {"pli": "Na hi anena tadevapi ālapiyanti aññaṁ bhajati. ", "html": "{}"}, "pe5:49.6": {"pli": "Sabbakāmajahassa bhikkhunoti kāmā ālapitā. ", "html": "{}"}, "pe5:49.7": {"pli": "Yassa nitthiṇṇo saṅkoti teyeva kāme saṅkāti ālapati. ", "html": "{}"}, "pe5:49.8": {"pli": "Suṇamānassa puretaraṁ rajjanti teyeva kāme rajjanti ālapati. ", "html": "{}"}, "pe5:49.9": {"pli": "Evaṁ suttamhi yo dhammo desiyati tassa pariyeṭṭhi “katamassa dhammassa idaṁ nāmaṁ katamassa idaṁ vevacanan”ti. ", "html": "{}"}, "pe5:49.10": {"pli": "Sabbaññū hi yesaṁ yesaṁ yā nirutti hoti, yathā gāmi tena tena desetīti tassa vevacanaṁ pariyesitabbaṁ. ", "html": "{}"}, "pe5:49.11": {"pli": "Ayaṁ vevacano hāro. ", "html": "{}</p>"}, "pe5:50.1": {"pli": "Tattha katamo <b>paññatti hāro</b>? ", "html": "<p>{}"}, "pe5:50.2": {"pli": "Cattāri ariyasaccānīti suttaṁ niddisati, nikkhepapaññatti. ", "html": "{}"}, "pe5:50.3": {"pli": "Yā samudayapaññatti. ", "html": "{}"}, "pe5:50.4": {"pli": "Kabaḷīkāre āhāre atthi chando atthi rāgo yāva patiṭṭhitaṁ. ", "html": "{}"}, "pe5:50.5": {"pli": "Tattha viññāṇaṁ pabhavapaññattiṁ paññapeti. ", "html": "{}"}, "pe5:50.6": {"pli": "Kabaḷīkāre āhāre natthi chando …pe… samugghāti paññatti. ", "html": "{}</p>"}, "pe5:51.1": {"pli": "Tassa kāmāsavāpi cittaṁ vimuccati, bhavāsavāpi cittaṁ vimuccati, avijjāsavāpi cittaṁ vimuccatīti pahānapaññattiṁ paññapeti. ", "html": "<p data-counter='11'>{}"}, "pe5:51.2": {"pli": "Taṇhā yassa purakkhatā paññā parivattati gāthā manāpapaññattiṁ paññapeti. ", "html": "{}"}, "pe5:51.3": {"pli": "Evaṁ pana manāpapaññattīti ekadhammaṁ bhagavā paññapeti. ", "html": "{}"}, "pe5:51.4": {"pli": "Na hi taṇhā dukkhasamudayoti kāretvā sabbattha taṇhāsamudayo niddisitabbo. ", "html": "{}"}, "pe5:51.5": {"pli": "Yathā uppannaṁ kāmavitakkaṁ nādhivāseti vinodeti pajahatīti paṭikkhepapaññatti. ", "html": "{}"}, "pe5:51.6": {"pli": "Evaṁ sabbesaṁ dhammānaṁ kusalānañca akusalānañca yañcassa dhammakkhettaṁ bhavati, so ceva dhammo tattha pavattati. ", "html": "{}"}, "pe5:51.7": {"pli": "Tadavasiṭṭhā dhammā tassānuvattakā honti. ", "html": "{}"}, "pe5:51.8": {"pli": "Sā duvidhā paññatti—", "html": "{}"}, "pe5:51.9": {"pli": "parādhīnapaññatti ca sādhīnapaññatti ca. ", "html": "{}"}, "pe5:51.10": {"pli": "Katamā sādhīnapaññatti? ", "html": "{}"}, "pe5:51.11": {"pli": "Samādhiṁ, bhikkhave, bhāvetha, samāhito, bhikkhave, bhikkhu yathābhūtaṁ pajānāti. ", "html": "{}"}, "pe5:51.12": {"pli": "“Rūpaṁ aniccan”ti yathābhūtaṁ pajānāti, ayaṁ sādhīnapaññatti parādhīnapaññatti ca, sā paññatti paññāya ca sīlassa ca, yathā cattāri jhānāni bhāvetha. ", "html": "{}"}, "pe5:51.13": {"pli": "Tassa atthi samādhindriyaṁ mudūni cattāri indriyāni tāni catuparādhīnāni, tīṇi aveccappasādeti parādhīnaṁ samādhindriyaṁ cattāri indriyāni parādhīnāti catūsu ariyasaccesu aparādhīnaṁ paññindriyaṁ satipaṭṭhānesu sammappadhānesu vīriyindriyaṁ. ", "html": "{}"}, "pe5:51.14": {"pli": "Iti sake padaṭṭhāne sake khettasādhīno so dhammo, so ca tattha paññāpetabbo. ", "html": "{}"}, "pe5:51.15": {"pli": "Tassa paṭipakkhā nighāto niddisitabbo. ", "html": "{}"}, "pe5:51.16": {"pli": "Etthāyaṁ anekākārapaññatti kena kāraṇena ayaṁ dhammo paññattoti. ", "html": "{}"}, "pe5:51.17": {"pli": "Ayaṁ vuccate paññatti. ", "html": "{}</p>"}, "pe5:52.1": {"pli": "Tattha katamo <b>otaraṇo hāro</b>? ", "html": "<p>{}"}, "pe5:52.2": {"pli": "Chasu dhammesu otāretabbaṁ. ", "html": "{}"}, "pe5:52.3": {"pli": "Katamesu chasu? ", "html": "{}"}, "pe5:52.4": {"pli": "Khandhesu dhātūsu āyatanesu indriyesu saccesu paṭiccasamuppādesu. ", "html": "{}"}, "pe5:52.5": {"pli": "Natthi taṁ suttaṁ vā gāthā vā byākaraṇaṁ vā. ", "html": "{}"}, "pe5:52.6": {"pli": "Imesu channaṁ dhammānaṁ aññatarasmiṁ na sandissati. ", "html": "{}"}, "pe5:52.7": {"pli": "Ettāvatā esa sabbā desanā yā tā khandhā vā dhātuyo vā āyatanāni vā saccāni vā paṭiccasamuppādo vā, tattha pañcannaṁ khandhānaṁ vedanākkhandho rāgadosamohānaṁ padaṭṭhānaṁ. ", "html": "{}"}, "pe5:52.8": {"pli": "Tattha tisso vedanāyo tassa sukhāya vedanāya somanasso savicāro, dukkhāya vedanāya domanasso savicāro, adukkhamasukhāya vedanāya upekkho savicāro. ", "html": "{}"}, "pe5:52.9": {"pli": "Yaṁ puna tattha vedayitaṁ idaṁ dukkhasaccaṁ, khandhesu saṅkhārakkhandho tattha kāyo pamattaṁ saupavattati, tañca saṅkhāragato dvidhā ca bhavaṅgotaraṇaṁ kammaṁ tīṇi ca saṅkhārāni puññābhisaṅkhārā vā apuññā vā āneñjā vā hetu sabbasarāgassa no vītarāgassa, dosassa abhisaṅkhārāni ca avītarāgo ceteti ca pakappeti ca, vītarāgo pana ceteti ca no abhisaṅkharoti, yaṁ uṇhaṁ vajiraṁ kaṭṭhe vā rukkhe vā aññattha vā patantaṁ bhindati ca ḍahati ca, evaṁ sarāgacetanā ceteti ca abhisaṅkharoti ca. ", "html": "{}"}, "pe5:52.10": {"pli": "Yathā sataṁ vajiraṁ na bhindati na ca ḍahati, evaṁ vītarāgacetanā ceteti na ca abhisaṅkharoti. ", "html": "{}"}, "pe5:52.11": {"pli": "Tattha pañcannaṁ khandhānaṁ eko khandho anindriyasarīraṁ saññākkhandho. ", "html": "{}</p>"}, "pe5:53.1": {"pli": "Tattha dhātūnaṁ aṭṭhārasa dhātuyo. ", "html": "<p>{}"}, "pe5:53.2": {"pli": "Tattha yā rūpī dasa dhātuyo, tāsu desiyamānāsu rūpakkhandho niddisitabbo, dukkhaṁ ariyasaccaṁ. ", "html": "{}"}, "pe5:53.3": {"pli": "Yepi ca cha viññāṇakāyā manodhātusattamā, tattha viññāṇakkhandho ca niddisitabbo, dukkhaṁ ariyasaccaṁ. ", "html": "{}"}, "pe5:53.4": {"pli": "Dhammadhātu pana dhammasamosaraṇā, so dhammo hetunā ca nissandena ca phalena ca kiccena ca vevacanena ca yena yena upalabbhati, tena tena niddisitabbo. ", "html": "{}"}, "pe5:53.5": {"pli": "Yadi vā kusalā yadi vā akusalā yadi vā abyākatā yadi vā asaṅkhatā. ", "html": "{}"}, "pe5:53.6": {"pli": "Dvādasannaṁ āyatanānaṁ dasa āyatanāni rūpāni taṁ dukkhaṁ ariyasaccaṁ niddisitabbaṁ. ", "html": "{}"}, "pe5:53.7": {"pli": "Rūpakkhandho ca manāyatanañca viññāṇakkhandhena niddisitabbaṁ, dukkhaṁ ariyasaccaṁ. ", "html": "{}"}, "pe5:53.8": {"pli": "Dhammāyatanaṁ nānādhammasamosaraṇaṁ. ", "html": "{}"}, "pe5:53.9": {"pli": "Tattha ye dhammā indriyānaṁ indriyesu niddisitabbā, ye anindriyānaṁ anindriyesu niddisitabbā. ", "html": "{}"}, "pe5:53.10": {"pli": "Pariyāyato ca otāretabbā. ", "html": "{}"}, "pe5:53.11": {"pli": "Yathā sā dhammadhātu tathā dhammāyatanaṁ pariyesitabbaṁ. ", "html": "{}"}, "pe5:53.12": {"pli": "Yāyeva hi dhammadhātu tadeva dhammāyatanaṁ anūnaṁ anadhikaṁ. ", "html": "{}</p>"}, "pe5:54.1": {"pli": "Tattha paṭiccasamuppādo atthi tividho, atthi catubbidho, atthi duvidho. ", "html": "<p>{}"}, "pe5:54.2": {"pli": "Tattha tividho paṭiccasamuppādo hetuphalanissando. ", "html": "{}"}, "pe5:54.3": {"pli": "Avijjā saṅkhārā taṇhā upādānaṁ ca ayaṁ hetu, viññāṇaṁ nāmarūpaṁ saḷāyatanaṁ phasso vedanā ca ayaṁ paccayo, yo bhavo ayaṁ vipāko, yā jāti maraṇaṁ ayaṁ nissando. ", "html": "{}</p>"}, "pe5:55.1": {"pli": "Kathaṁ catubbidho hetu paccayo vipāko nissando ca? ", "html": "<p>{}"}, "pe5:55.2": {"pli": "Avijjā ca taṇhāsaṅkhārā ca upādānaṁ ca—", "html": "{}"}, "pe5:55.3": {"pli": "ayaṁ hetu. ", "html": "{}"}, "pe5:55.4": {"pli": "Viññāṇaṁ nāmarūpassa paccayo. ", "html": "{}"}, "pe5:55.5": {"pli": "Nāmarūpaṁ upapajjati, tathā upapannassa saḷāyatanaṁ phasso vedanā ca—", "html": "{}"}, "pe5:55.6": {"pli": "ayaṁ paccayo. ", "html": "{}"}, "pe5:55.7": {"pli": "Yo bhavo ayaṁ vipāko. ", "html": "{}"}, "pe5:55.8": {"pli": "Yā jāti yā ca jarāmaraṇaṁ—", "html": "{}"}, "pe5:55.9": {"pli": "ayaṁ nissando. ", "html": "{}</p>"}, "pe5:56.1": {"pli": "Kathaṁ duvidho paṭiccasamuppādo? ", "html": "<p>{}"}, "pe5:56.2": {"pli": "Avijjā saṅkhārā taṇhā upādānaṁ—", "html": "{}"}, "pe5:56.3": {"pli": "ayaṁ samudayo. ", "html": "{}"}, "pe5:56.4": {"pli": "Viññāṇaṁ nāmarūpaṁ saḷāyatanaṁ phasso vedanā bhavo jāti maraṇañca—", "html": "{}"}, "pe5:56.5": {"pli": "idaṁ dukkhaṁ. ", "html": "{}"}, "pe5:56.6": {"pli": "Yaṁ pana avijjānirodhā saṅkhāranirodho imāni tappaṭipakkhena dve saccāni. ", "html": "{}"}, "pe5:56.7": {"pli": "Tasmā paṭiccasamuppādo yena ākārena niddiṭṭho, tena tena niddisitabbo. ", "html": "{}</p>"}, "pe5:57.1": {"pli": "Tathā bāvīsati indriyāni. ", "html": "<p data-counter='12'>{}"}, "pe5:57.2": {"pli": "Dvādasa indriyāni cakkhundriyāni cakkhundriyaṁ yena domanassindriyaṁ, idaṁ dukkhaṁ. ", "html": "{}"}, "pe5:57.3": {"pli": "Purisindriyaṁ ca diṭṭhiyā ca taṇhāpadaṭṭhānaṁ. ", "html": "{}"}, "pe5:57.4": {"pli": "Yato puriso purisakānaṁ taṁ evaṁ kātabbatā. ", "html": "{}"}, "pe5:57.5": {"pli": "Atha ajjhattaṁ sārajjati. ", "html": "{}"}, "pe5:57.6": {"pli": "Ayaṁ ahaṅkāro taṁ yasā sāratto bahiddhā pariyesati, ayaṁ mamaṅkāro evaṁ itthī, tattha sukhindriyaṁ ca somanassindriyaṁ ca purisindriyassānuvattakā honti. ", "html": "{}"}, "pe5:57.7": {"pli": "Tassa adhippāyaparipuṇṇā lobhadhammā kusalamūle pavaḍḍhenti. ", "html": "{}"}, "pe5:57.8": {"pli": "Tassa ce ayamadhippāyo na pāripūriṁ gacchati. ", "html": "{}"}, "pe5:57.9": {"pli": "Tassa dukkhindriyaṁ ca domanassindriyaṁ ca vattati. ", "html": "{}"}, "pe5:57.10": {"pli": "Doso ca akusalamūlaṁ pavaḍḍhati. ", "html": "{}"}, "pe5:57.11": {"pli": "Sace pana upekkhā bhāveti upekkhindriyassa anuvattakāmā bhavati. ", "html": "{}"}, "pe5:57.12": {"pli": "Amoho ca kusalamūlaṁ pavaḍḍhati. ", "html": "{}"}, "pe5:57.13": {"pli": "Iti satta indriyāni kilesavatthumupādāya ananvemāni avamāni sabbassa vedanā itthindriyaṁ purisindriyaṁ. ", "html": "{}"}, "pe5:57.14": {"pli": "Tattha aṭṭha indriyāni saddhindriyaṁ yāva aññātāvino indriyaṁ, ayaṁ dukkhanirodhagāminī paṭipadā. ", "html": "{}"}, "pe5:57.15": {"pli": "Dasannaṁ paññindriyānaṁ kāmarāgassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:57.16": {"pli": "Manindriyaṁ bhavarāgassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:57.17": {"pli": "Paññindriyāni rūparāgassa padaṭṭhānaṁ. ", "html": "{}"}, "pe5:57.18": {"pli": "Itthindriyaṁ ca purisindriyaṁ ca satta paññattiyā padaṭṭhānaṁ. ", "html": "{}"}, "pe5:57.19": {"pli": "Tattha yena yena indriyena yuttaṁ vā gāthāya otāretuṁ sakkoti, tena tena niddisitabbo. ", "html": "{}"}, "pe5:57.20": {"pli": "Evaṁ khandhesu dhātūsu āyatanesu saccesu paṭiccasamuppādesu ayaṁ otaraṇo hāro. ", "html": "{}</p>"}, "pe5:58.1": {"pli": "Tattha katamo <b>sodhano hāro</b>? ", "html": "<p>{}"}, "pe5:58.2": {"pli": "Yo gāthā ekena ārambho bhāsissanti. ", "html": "{}"}, "pe5:58.3": {"pli": "Tattha ekissā bhāsitāya avasiṭṭhāsu bhāsitāsu so attho na niddisitabbo. ", "html": "{}"}, "pe5:58.4": {"pli": "Kiṅkāraṇaṁ? ", "html": "{}"}, "pe5:58.5": {"pli": "Na hi tāva so attho bhāsito, so abhāsito na sakkā niddisituṁ. ", "html": "{}"}, "pe5:58.6": {"pli": "Yathā kiṁ appamādo amataṁ padanti gāthā ayamekā gāthā niddisitabbā. ", "html": "{}"}, "pe5:58.7": {"pli": "Kiṅkāraṇaṁ, atthikkhātāva imassa ārambhassa anabhāsitaṁ? ", "html": "{}</p>"}, "pe5:59.1": {"pli": "“Evaṁ visesato ñatvā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe5:59.2": {"pli": "Appamādamhi paṇḍitā; ", "html": "<span class='verse-line'>{}</span>"}, "pe5:59.3": {"pli": "Appamāde pamodanti, ", "html": "<span class='verse-line'>{}</span>"}, "pe5:59.4": {"pli": "Ariyānaṁ gocare ratāti”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe5:60.1": {"pli": "Idaṁ abhāsitaṁ. ", "html": "<p data-counter='13'>{}"}, "pe5:60.2": {"pli": "Imissāpi gāthāya bhāsitāya attho niddisitabbo. ", "html": "{}"}, "pe5:60.3": {"pli": "Kiṅkāraṇaṁ, atthi tattha avasiṭṭhaṁ? ", "html": "{}"}, "pe5:60.4": {"pli": "Te jhāyino sātatikā, niccaṁ daḷhaparakkamāti gāthā, evaṁ imā gāthāyo upadhāritā yadā bhavanti, tadā attho niddisitabbo. ", "html": "{}"}, "pe5:60.5": {"pli": "Evaṁ assutapubbesu suttesu byākaraṇesu vā ekuddeso bhāsito. ", "html": "{}"}, "pe5:60.6": {"pli": "Yā vīmaṁsā tulanā idaṁ atthi kiccaṁ, idaṁ suttaṁ bhāsitaṁ tassa vevacanaṁ niddiṭṭhaṁ vā na vāti. ", "html": "{}"}, "pe5:60.7": {"pli": "Tattha yā vīmaṁsā, ayaṁ vuccate sodhano hāro. ", "html": "{}</p>"}, "pe5:61.1": {"pli": "Tattha katamo <b>adhiṭṭhāno hāro</b>? ", "html": "<p>{}"}, "pe5:61.2": {"pli": "Ekattatā ca vemattatā ca. ", "html": "{}"}, "pe5:61.3": {"pli": "Tattha kitapaññatti ca kiccapaññatti ca. ", "html": "{}"}, "pe5:61.4": {"pli": "Sā ekattatā ca vemattatā ca yathā paññatti ekavevacanena vemattatā pajānātīti paññā, sā ca ādhipateyyaṭṭhena paññatti. ", "html": "{}"}, "pe5:61.5": {"pli": "Yaṁ anomattiyaṭṭhena paññattanti. ", "html": "{}"}, "pe5:61.6": {"pli": "Taṁ anomattiyaṭṭhena paññābalaṁ. ", "html": "{}"}, "pe5:61.7": {"pli": "Tanubhūtā gocarattavasā sevasati tīsu ratanesu anussati buddhānussati dhammānussati saṅghānussati aviparītānussaraṇatāya. ", "html": "{}"}, "pe5:61.8": {"pli": "Sammādiṭṭhi dhammānaṁ pavicayena dhammavicayasambojjhaṅgo abhinīhārato abhiññāti. ", "html": "{}"}, "pe5:61.9": {"pli": "Saṅkhepena maggā kā vatthu avikopanatāya ekattā, yathā uṇhena saṁsaṭṭhaṁ uṇhodakaṁ, sītena saṁsaṭṭhaṁ sītodakaṁ khārodakaṁ guḷhodakanti, idaṁ ekattatā vemattatā ca. ", "html": "{}</p>"}, "pe5:62.1": {"pli": "Atthi puna dhammo nānādhammasaṅghato ekato yathārūpaṁ cattāro vāretabbā, tañca rūpanti ekattatā. ", "html": "<p>{}"}, "pe5:62.2": {"pli": "Pathavīdhātu āpo tejo vāyodhātūti vemattatā. ", "html": "{}"}, "pe5:62.3": {"pli": "Evaṁ sabbā catasso dhātuyo rūpanti ekattatā, pathavīdhātu āpo tejo vāyodhātūti vemattatā. ", "html": "{}"}, "pe5:62.4": {"pli": "Pathavīdhātūti lakkhaṇato ekattatā, saṅkiṇṇavatthuto vemattatā. ", "html": "{}"}, "pe5:62.5": {"pli": "Yaṁ kiñci kakkhaḷalakkhaṇaṁ, sabbaṁ taṁ pathavīdhātūti ekattatā. ", "html": "{}"}, "pe5:62.6": {"pli": "Kesā lomā nakhā dantā chavi cammanti vemattatā. ", "html": "{}"}, "pe5:62.7": {"pli": "Evaṁ sabbaṁ catasso dhātuyo rūpanti ekattaṁ. ", "html": "{}"}, "pe5:62.8": {"pli": "Saddā gandhā rasā phoṭṭhabbāti vemattatā. ", "html": "{}</p>"}, "pe5:63.1": {"pli": "Atthi puna dhammo vemattatā añño nāmaṁ labhati. ", "html": "<p data-counter='14'>{}"}, "pe5:63.2": {"pli": "Yathā kāyānupassanāya navasaññā vinīlakasaññā uddhumātakasaññā, ayaṁ asubhasaññā, yā ekattatā ārammaṇato vemattato, sā evaṁ saññāvedanāsu ādīnavaṁ samanupassato tathādhiṭṭhānaṁ samādhindriyaṁ ca sāyeva dhammesu tattha saññābhāvanā vīriyindriyaṁ ca dhammesu dhammānupassanā citte attasaññaṁ pajahato paññindriyaṁ ca citte cittānupassanā. ", "html": "{}"}, "pe5:63.3": {"pli": "Iti yo koci ñāṇapacāro sabbaso paññāya gocaro paññā, ayaṁ vemattatā, yathā kāmarāgo bhavarāgo diṭṭhirāgoti vemattatā taṇhāya. ", "html": "{}"}, "pe5:63.4": {"pli": "Iti yaṁ ekattatāya ca vemattatāya ca ñāṇaṁ vīmaṁsanā tulanā. ", "html": "{}"}, "pe5:63.5": {"pli": "Ayaṁ adhiṭṭhāno hāro. ", "html": "{}</p>"}, "pe5:64.1": {"pli": "Tattha katamo <b>parikkhāro hāro</b>? ", "html": "<p>{}"}, "pe5:64.2": {"pli": "Sahetu sappaccayaṁ vodānañca saṅkileso ca, yaṁ tadubhayaṁ pariyeṭṭhi, saparikkhāro hāro. ", "html": "{}"}, "pe5:64.3": {"pli": "Iti dhammānaṁ sahetukānaṁ hetu pariyesitabbo, sappaccayānaṁ paccayo pariyesitabbo. ", "html": "{}</p>"}, "pe5:65.1": {"pli": "Tattha kiṁ nānākaraṇaṁ, hetussa ca paccayassa ca? ", "html": "<p>{}"}, "pe5:65.2": {"pli": "Sabhāvo hetu, parabhāvo paccayo. ", "html": "{}"}, "pe5:65.3": {"pli": "Parabhāvassa paccayo hetupi, sabhāvassa hetuyā parabhāvassa kassaci paccayo avutto hetu, vutto paccayo. ", "html": "{}"}, "pe5:65.4": {"pli": "Ajjhattiko hetu, bāhiro paccayo. ", "html": "{}"}, "pe5:65.5": {"pli": "Sabhāvo hetu, parabhāvo paccayo. ", "html": "{}"}, "pe5:65.6": {"pli": "Nibbattako hetu, paṭiggāhako paccayo. ", "html": "{}"}, "pe5:65.7": {"pli": "Nevāsiko hetu, āgantuko paccayo. ", "html": "{}"}, "pe5:65.8": {"pli": "Asādhāraṇo hetu, sādhāraṇo paccayo. ", "html": "{}"}, "pe5:65.9": {"pli": "Ekoyeva hetu, aparāparo paccayo. ", "html": "{}</p>"}, "pe5:66.1": {"pli": "Hetussa upakaraṇaṁ samudānetabbo. ", "html": "<p>{}"}, "pe5:66.2": {"pli": "Samudānaṁ hetu, tattha duvidho hetu. ", "html": "{}"}, "pe5:66.3": {"pli": "Duvidho paccayo—", "html": "{}"}, "pe5:66.4": {"pli": "samanantarapaccayo ca paramparapaccayo ca. ", "html": "{}"}, "pe5:66.5": {"pli": "Hetupi duvidho—", "html": "{}"}, "pe5:66.6": {"pli": "samanantarahetu ca paramparahetu ca. ", "html": "{}"}, "pe5:66.7": {"pli": "Tattha katamo paramparapaccayo? ", "html": "{}"}, "pe5:66.8": {"pli": "Avijjā nāmarūpassa paramparapaccayo, viññāṇaṁ samanantarapaccayatāya paccayo. ", "html": "{}"}, "pe5:66.9": {"pli": "Yadi ādimhi avijjānirodho bhavati nāmarūpassa nirodhopi. ", "html": "{}"}, "pe5:66.10": {"pli": "Tattha samanantaraṁ kiṅkāraṇaṁ paramparapaccayo samanantarapaccayo samuddānito, ayaṁ paccayato. ", "html": "{}"}, "pe5:66.11": {"pli": "Tattha katamo paramparahetu? ", "html": "{}"}, "pe5:66.12": {"pli": "Vijānantassa paramparahetutāya hetu, aññākāro samanantarahetutāya hetu. ", "html": "{}"}, "pe5:66.13": {"pli": "Yassa hi yaṁ samanantaraṁ nibbattati, so tassa hetupi jātinirodhā bahi ākāranirodho, ākāranirodhā daṇḍanirodho, daṇḍanirodhā khaṇḍanirodho. ", "html": "{}"}, "pe5:66.14": {"pli": "Evaṁ hetupi dvidhā so tāhi passitabbo. ", "html": "{}</p>"}, "pe5:67.1": {"pli": "Paṭiccasamuppādo yathā avijjāpaccayo tassa puna kiṁpaccayo, ayoniso manasikāro. ", "html": "<p>{}"}, "pe5:67.2": {"pli": "So kassa paccayo saṅkhārānaṁ, iti paccayo ca samuppannaṁ ca tassa ko hetu avijjāyeva. ", "html": "{}"}, "pe5:67.3": {"pli": "Tathā hi purimā koṭi na paññāyati. ", "html": "{}"}, "pe5:67.4": {"pli": "Tattha avijjānusayo avijjāpariyuṭṭhānassa hetu purimā hetu pacchā paccayo, sāpi avijjāsaṅkhārānaṁ paccayo catūhi kāraṇehi sahajātapaccayatāya samanantarapaccayatāya abhisandanapaccayatāya patiṭṭhānapaccayatāya. ", "html": "{}</p>"}, "pe5:68.1": {"pli": "Kathaṁ sahajātapaccayatāya avijjāsaṅkhārānaṁ paccayo? ", "html": "<p>{}"}, "pe5:68.2": {"pli": "Yaṁ cittaṁ rāgapariyuṭṭhaṁ, tattha avijjāpariyuṭṭhānena sabbaṁ paññāya gocaraṁ hanti. ", "html": "{}"}, "pe5:68.3": {"pli": "Tattha saṅkhārā tipaccayaṭṭhikā addhābhūmikāramahattassa ayaṁ avijjāsahasamuppannaṁ vuddhiṁ virūḷhiṁ vepullatamāpajjantī catūhi kāraṇehi paññā pahīyati. ", "html": "{}"}, "pe5:68.4": {"pli": "Katamehi catūhi? ", "html": "{}"}, "pe5:68.5": {"pli": "Anusayo pariyuṭṭhānaṁ saṁyojanaṁ upādānaṁ. ", "html": "{}"}, "pe5:68.6": {"pli": "Tattha anusayo pariyuṭṭhānaṁ jāti pariyuṭṭhitā saṁyujjati saṁyuttā upādiyati upādānapaccayā bhavo. ", "html": "{}"}, "pe5:68.7": {"pli": "Evaṁ te saṅkhārā tividhā uppannā bhūmigatā nāsaññattha ayaṁ maggena vinītattāyāti te thāmagatā apativinītātipi te saṅkhārāti vuccati, evaṁ sahetusamuppannaṭṭhena atthi meva paccayā saṅkhārānaṁ paccayo niddiṭṭhaṁ apanetvā kusalaṁ akusalaṁ kusalo ca akusalo ca pakkhipitabbo, vipākadhammā apanetvā vacanīyaṁ avacanīyaṁ vacanīyañca avacanīyañca pakkhipitabbaṁ, bhavaapevirittā, sabbasuttaṁ parikkhipitabbaṁ. ", "html": "{}</p>"}, "pe5:69.1": {"pli": "Dasa tathāgatabalāni cattāri vesārajjāni puññāni anaññākataṁ avijjā samanantarapaccayatāya saṅkhārānaṁ paccayo yena cittena saha samuppannā avijjā tassa cittassa samanantaracittaṁ samuppannanti, tassa yaṁ samanantaracittaṁ samuppannanti, tassa pacchimassa cittassa purimacittaṁ hetupaccayatāya paccayo, tena avijjā hetu tena cittena upādānaṁ anokāsakatā ñāṇaṁ na uppajjanti. ", "html": "<p>{}"}, "pe5:69.2": {"pli": "Yā tassa appamādā dhātu abhijjhābhisanditā tahiṁ vipallāsā uppajjanti “asubhe subhan”ti “dukkhe sukhan”ti, tattha saṅkhārā uppajjanti rattā duṭṭhā mūlassa cetanā rāgapariyuṭṭhānena byāpādapariyuṭṭhānena avijjāpariyuṭṭhānena diṭṭhivipallāso vatthuniddese niddisitabbo, yaṁ viparītacitto vijānāti ayaṁ cittavipallāso, yā viparītasaññā upaggaṇhāti ayaṁ saññāvipallāso. ", "html": "{}"}, "pe5:69.3": {"pli": "Yaṁ viparītadiṭṭhi abhinivisati ayaṁ diṭṭhivipallāso. ", "html": "{}"}, "pe5:69.4": {"pli": "Aṭṭha micchattāni vaḍḍhanti, tīṇi akusalāni ayoniso manasikāre uppannaṁ viññāṇañca vijjañca karonti. ", "html": "{}"}, "pe5:69.5": {"pli": "Iti pubbāparante akusalānātaritaro saṅkhārā vuddhiṁ vepullataṁ gacchanti. ", "html": "{}"}, "pe5:69.6": {"pli": "Te ca mahatā ca appaṭividitā ponobhavikā saṅkhārā bhavanti. ", "html": "{}"}, "pe5:69.7": {"pli": "Iti evaṁ avijjā sahajātapaccayatāya saṅkhārānaṁ paccayo samanantarapaccayatāya ca. ", "html": "{}</p>"}, "pe5:70.1": {"pli": "Kathaṁ abhisandanākārena avijjā saṅkhārānaṁ paccayo? ", "html": "<p>{}"}, "pe5:70.2": {"pli": "Sā avijjā te saṅkhāre abhisanneti parippharati. ", "html": "{}"}, "pe5:70.3": {"pli": "Seyyathāpi nāma uppalaṁ vā padumaṁ vā taṁ udake vaḍḍhaṁ assa, sītena vārinā abhisannaṁ parisandanaṁ vuddhiṁ virūḷhiṁ vepullataṁ āpajjati. ", "html": "{}"}, "pe5:70.4": {"pli": "Evaṁ abhisandanaṭṭhena avijjā saṅkhārānaṁ paccayo. ", "html": "{}</p>"}, "pe5:71.1": {"pli": "Kathaṁ patiṭṭhahanaṭṭhena avijjā saṅkhārānaṁ paccayo? ", "html": "<p>{}"}, "pe5:71.2": {"pli": "Te saṅkhārā avijjāyaṁ nissāya vuddhiṁ virūḷhiṁ vepullataṁ āpajjanti. ", "html": "{}"}, "pe5:71.3": {"pli": "Seyyathāpi nāma uppalaṁ vā padumaṁ vā pathaviṁ nissāya pathaviṁ patiṭṭhāya vuddhiṁ virūḷhiṁ vepullataṁ āpajjati. ", "html": "{}"}, "pe5:71.4": {"pli": "Ete saṅkhārā avijjāyaṁ patiṭṭhitā avijjāyaṁ nissāya vuddhiṁ virūḷhiṁ vepullataṁ gacchanti. ", "html": "{}"}, "pe5:71.5": {"pli": "Evaṁ patiṭṭhahanaṭṭhena avijjā saṅkhārānaṁ paccayo. ", "html": "{}</p>"}, "pe5:72.1": {"pli": "Puna rāgasahagatassa kammassa vipākena paṭisandhimhi bhavo nibbattati, taṁ kammassa sabbaṁ abhiniviṭṭhaṁ aññāṇavasena ponobhavikā saṅkhārāti vuccanti, evampi avijjāpaccayā saṅkhārā atthi. ", "html": "<p>{}"}, "pe5:72.2": {"pli": "Puna pañcasu ye ca sekkhā puggalā, ye ca asaññisamāpattiṁ samāpannā, ye ca bhavagatā, ye ca antogatāyeva saṁsedajā, ye ca vā pana añño hi koci anāgāmibhūtā na cetenti na ca patthenti, tesaṁ kiṁ paccayā saṅkhārā. ", "html": "{}"}, "pe5:72.3": {"pli": "Puna rāgā atthi tesaṁ saṅkhārāni upādānāni cittamanussarantiyeva avipakkavipākasamūhatā asamucchinnapaccayā tesaṁ puna ca gato bhavati. ", "html": "{}"}, "pe5:72.4": {"pli": "Evampi hi avijjāpaccayā saṅkhārā. ", "html": "{}"}, "pe5:72.5": {"pli": "Puna sā te na upādānā napi saṅkhārā atthi, puna tesaṁ satta anusayā asamūhatā asamucchinnā tadārammaṇaṁ bhavati. ", "html": "{}"}, "pe5:72.6": {"pli": "Viññāṇassa patiṭṭhāya viññāṇapaccayā nāmarūpaṁ. ", "html": "{}"}, "pe5:72.7": {"pli": "Evampi avijjāpaccayā saṅkhārā. ", "html": "{}"}, "pe5:72.8": {"pli": "Puna sā yaṁ kiñci kammaṁ ācayagāmi sabbaṁ taṁ avijjāvasena abhisaṅkhariyati taṇhāvasena ca allīyati aññāṇavasena ca tattha ādīnavampi na jānāti. ", "html": "{}"}, "pe5:72.9": {"pli": "Tadeva viññāṇabījaṁ bhavati, sāyeva taṇhāsineho bhavati. ", "html": "{}"}, "pe5:72.10": {"pli": "Sāyeva avijjā sammohoti. ", "html": "{}"}, "pe5:72.11": {"pli": "Evampi avijjāpaccayā saṅkhārā vattabbā. ", "html": "{}"}, "pe5:72.12": {"pli": "Iti imehi ākārehi avijjā saṅkhārānaṁ paccayo. ", "html": "{}</p>"}, "pe5:73.1": {"pli": "Tattha avijjāya hetu ayoniso manasikāro paccayo hoti. ", "html": "<p data-counter='15'>{}"}, "pe5:73.2": {"pli": "Tattha abhicchedo ayaṁ tattha tatiyaṁ balaṁ nivatti, ayaṁ paṭisandhi. ", "html": "{}"}, "pe5:73.3": {"pli": "Tattha punabbhavo yo avecchedo asamugghātanaṭṭhena ayaṁ anusayo. ", "html": "{}"}, "pe5:73.4": {"pli": "Yathā paṭākaṁ vā sāṭakaṁ vā dve janā pīḷesu ca ekā vā balaṁ vā assa nivāṭassesu, na pana pīḷesu soseyya. ", "html": "{}"}, "pe5:73.5": {"pli": "Tattha yaṁ sinehā āpodhātu anupullanā sosetabbā. ", "html": "{}"}, "pe5:73.6": {"pli": "Uṇhadhātumāgamma sace puna taṁ ākāse nikkhipeyya taṁ ussāvena yebhuyyataraṁ sinehamāpajjeyya, na hi anāgamma tejodhātuṁ parisesaṁ gaccheyya. ", "html": "{}"}, "pe5:73.7": {"pli": "Evameva bhavaggaparamāpi samāpatti na anurūpassa samugghātāya saṁvattati. ", "html": "{}"}, "pe5:73.8": {"pli": "Te hi ālayanti sammasanti, na ca taṇhāya taṇhāpahānaṁ gacchanti. ", "html": "{}"}, "pe5:73.9": {"pli": "Tattha so asamugghāto. ", "html": "{}"}, "pe5:73.10": {"pli": "Avijjāya anusayo ca cittassa sampalibodho, idaṁ pariyuṭṭhānaṁ. ", "html": "{}"}, "pe5:73.11": {"pli": "Yathābhūtaṁ viññāṇassa appaṭivedho ayaṁ avijjāāsavo avijjāviññāṇabījaṁ bhavati. ", "html": "{}"}, "pe5:73.12": {"pli": "Yaṁ bījaṁ so hetu na samucchijjati, asamucchijjanto paṭisandehati. ", "html": "{}"}, "pe5:73.13": {"pli": "Paṭisandahanto na samugghātaṁ gacchati. ", "html": "{}"}, "pe5:73.14": {"pli": "Asamugghātaṁ cittaṁ pariyonahati, pariyonaddhacitto yathābhūtaṁ nappajānāti, iti saññāṇassa sāsavattho, avijjattho, hetuattho, avacchedattho, anivattiattho, phalattho paṭisandhiattho, punabbhavattho, asamugghātattho, anusayattho, pariyuṭṭhānattho, apaṭivedhanattho. ", "html": "{}"}, "pe5:73.15": {"pli": "Ettāvatā avijjāya khettaṁ niddiṭṭhaṁ bhavati. ", "html": "{}"}, "pe5:73.16": {"pli": "Ayaṁ vuccate parikkhāro nāma hāro. ", "html": "{}</p>"}, "pe5:74.1": {"pli": "Tattha katamo <b>samāropano hāro</b>? ", "html": "<p>{}"}, "pe5:74.2": {"pli": "Ugghaṭitamhi tamhi santañceva ca naṁ vitthāraṁ pana vattabbaṁ. ", "html": "{}"}, "pe5:74.3": {"pli": "Vitthāravidhaṁ cittaññā ayaṁ samāropano hāro. ", "html": "{}"}, "pe5:74.4": {"pli": "Tattha nāmaniddeso upaghaṭakā vatthuniddeso vevacanaṁ vatthubhūto vitthāro. ", "html": "{}"}, "pe5:74.5": {"pli": "Yathā kiṁ, yā bhikkhūnaṁ vattato pahātabbo, ayaṁ upaghaṭanā. ", "html": "{}</p>"}, "pe5:75.1": {"pli": "Tattha katamo samāropano? ", "html": "<p>{}"}, "pe5:75.2": {"pli": "Kiñci na vattabbaṁ, rūparāgaṁ vā nāmavantapahātabbaṁ. ", "html": "{}"}, "pe5:75.3": {"pli": "Yāva viññāṇanti vitthārena kātabbāni. ", "html": "{}"}, "pe5:75.4": {"pli": "Avijjā tā opammena paññāpetabbā, ayaṁ samāropano. ", "html": "{}"}, "pe5:75.5": {"pli": "Nissitacittassa ca mattiko ca nissayo taṇhā ca diṭṭhi ca. ", "html": "{}"}, "pe5:75.6": {"pli": "Tattha diṭṭhi avijjā taṇhā saṅkhārā. ", "html": "{}"}, "pe5:75.7": {"pli": "Tattha diṭṭhipaccayā taṇhā ime avijjāpaccayā saṅkhārā. ", "html": "{}"}, "pe5:75.8": {"pli": "Tattha nissitaṁ viññāṇaṁ idaṁ saṅkhārapaccayā viññāṇaṁ yāva jarāmaraṇaṁ, idaṁ saṅkhittena bhāsite avasiṭṭhaṁ paropayati. ", "html": "{}</p>"}, "pe5:76.1": {"pli": "Anissitassa calitaṁ natthīti tassa evaṁ diṭṭhiyā taṇhāya ca pahānaṁ tattha diṭṭhiavijjānirodhāya bhūtaṁ viññāṇaṁ sarāgaṭṭhāniyesu dhammesu taṁ taṁ dhammaṁ upecca aññaṁ dhammaṁ dhāvati makkaṭopamatāya, atha khvassa parittesu dhammesu sarāgaṭṭhāniyesu chandarāgo natthi kuto tato calanā, adhimattesu sattesu cittaṁ nivessayati taṁ apatiṭṭhitaṁ viññāṇaṁ anāhāraṁ nirujjhati viññāṇanirodhā nāmarūpanirodho yāva jarāmaraṇanirodho. ", "html": "<p data-counter='16'>{}"}, "pe5:76.2": {"pli": "Ayaṁ samāropano. ", "html": "{}</p>"}, "pe5:77.1": {"pli": "Tattha rāgavasena viññāṇassa calitaṁ sapariggaho, tasmiṁ calite asati yo parikilesopacāro tividho aggi paṭippassaddho bhavati. ", "html": "<p>{}"}, "pe5:77.2": {"pli": "Tenāha calite asante passaddhi hoti. ", "html": "{}"}, "pe5:77.3": {"pli": "Tattha yaṁ samāropanā passaddhakāyo sukhaṁ vedeti, sukhino cittaṁ samādhiyati. ", "html": "{}"}, "pe5:77.4": {"pli": "Yāva vimuttitamiti ñāṇadassanaṁ bhavati. ", "html": "{}"}, "pe5:77.5": {"pli": "So āsavānaṁ khayā ca vimutti no upapajjati. ", "html": "{}"}, "pe5:77.6": {"pli": "Tassa upapattissa āgatigatiyā asantiyā nevidha na huraṁ na ubhayamantarena. ", "html": "{}"}, "pe5:77.7": {"pli": "Esevanto dukkhassāti anupādisesā nibbānadhātu. ", "html": "{}"}, "pe5:77.8": {"pli": "Idamassa suttassa majjhe samāropitaṁ paṭiccasamuppāde ca vimuttiyaṁ ca yogo na ca etaṁ tassa saṅkhittena bhāsitassa vitthārena atthaṁ vibhajjanti. ", "html": "{}"}, "pe5:77.9": {"pli": "Ayaṁ vuccate samāropano hāro. ", "html": "{}"}, "pe5:77.10": {"pli": "Na ca saṅkilesabhāgiyena suttena saṅkilesabhāgiyo ye ca dhammā samāropayitabbā nāññe. ", "html": "{}"}, "pe5:77.11": {"pli": "Evaṁ vāsanābhāgiye nibbedhabhāgiye, ayaṁ samāropano hāro. ", "html": "{}"}, "pe5:77.12": {"pli": "Ime soḷasa hārā. ", "html": "{}</p>"}, "pe5:78.1": {"pli": "Suvīrassa mahākaccāyanassa jambuvanavāsino peṭakopadese hāravibhaṅgo nāma pañcamā bhūmi. ", "html": "<p class='endsutta'>{}</p></article>"}}, "pe6": {"pe6:0.1": {"pli": "Peṭakopadesa ", "html": "<article id='pe6'><header><ul><li class='division'>{}</li></ul>"}, "pe6:0.2": {"pli": "6. Suttatthasamuccayabhūmi ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "pe6:1.1": {"pli": "Buddhānaṁ bhagavantānaṁ sāsanaṁ tividhena saṅgahaṁ gacchati, khandhesu dhātūsu āyatanesu ca. ", "html": "<p data-counter='1'>{}"}, "pe6:1.2": {"pli": "Tattha <b>pañcakkhandhā</b> rūpakkhandho yāva viññāṇakkhandho. ", "html": "{}"}, "pe6:1.3": {"pli": "Dasa rūpaāyatanāni cakkhu rūpā ca yāva kāyo phoṭṭhabbā ca, ayaṁ rūpakkhandho. ", "html": "{}"}, "pe6:1.4": {"pli": "Tattha cha vedanākāyā vedanākkhandho cakkhusamphassajā vedanā yāva manosamphassajā vedanā, ayaṁ vedanākkhandho. ", "html": "{}"}, "pe6:1.5": {"pli": "Tattha cha saññākāyā saññākkhandho, rūpasaññā yāva dhammasaññā ime cha saññākāyā, ayaṁ saññākkhandho. ", "html": "{}"}, "pe6:1.6": {"pli": "Tattha cha cetanākāyā saṅkhārakkhandho, rūpasañcetanā yāva dhammasañcetanā ime cha cetanākāyā, ayaṁ saṅkhārakkhandho. ", "html": "{}"}, "pe6:1.7": {"pli": "Tattha cha viññāṇakāyā viññāṇakkhandho, cakkhuviññāṇaṁ yāva manoviññāṇaṁ ime cha viññāṇakāyā, ayaṁ viññāṇakkhandho. ", "html": "{}"}, "pe6:1.8": {"pli": "Ime pañcakkhandhā. ", "html": "{}</p>"}, "pe6:2.1": {"pli": "Tesaṁ kā pariññā? ", "html": "<p>{}"}, "pe6:2.2": {"pli": "Aniccaṁ dukkhaṁ saññā anattāti esā etesaṁ pariññā. ", "html": "{}"}, "pe6:2.3": {"pli": "Tattha katamo khandhattho? ", "html": "{}"}, "pe6:2.4": {"pli": "Samūhattho khandhattho, puñjattho khandhattho, rāsattho khandhattho. ", "html": "{}"}, "pe6:2.5": {"pli": "Taṁ yathā dabbakkhandho vanakkhandho dārukkhandho aggikkhandho udakakkhandho vāyukkhandho iti evaṁ khandhesu sabbasaṅgahova evaṁ khandhattho. ", "html": "{}</p>"}, "pe6:3.1": {"pli": "Tattha <b>aṭṭhārasa dhātuyo</b> cakkhudhātu rūpadhātu cakkhuviññāṇadhātu …pe… ", "html": "<p data-counter='2'>{}"}, "pe6:3.2": {"pli": "manodhātu dhammadhātu manoviññāṇadhātu. ", "html": "{}"}, "pe6:3.3": {"pli": "Etāyo aṭṭhārasa dhātuyo. ", "html": "{}"}, "pe6:3.4": {"pli": "Tāsaṁ pariññā aniccaṁ dukkhaṁ saññā anattāti esā etāsaṁ pariññā. ", "html": "{}"}, "pe6:3.5": {"pli": "Tattha ko dhātuattho? ", "html": "{}"}, "pe6:3.6": {"pli": "Vuccate avayavattho dhātuattho. ", "html": "{}"}, "pe6:3.7": {"pli": "Avayavoti cakkhu no pasādo cakkhudhātu. ", "html": "{}"}, "pe6:3.8": {"pli": "Evaṁ pañcasu dhātūsu puna rāgavavacchedattho dhātuattho. ", "html": "{}"}, "pe6:3.9": {"pli": "Vavacchinnā hi cakkhudhātu. ", "html": "{}"}, "pe6:3.10": {"pli": "Evaṁ pañcasu punarāha ekantipakatyatthena dhātuatthoti vuccate. ", "html": "{}"}, "pe6:3.11": {"pli": "Taṁ yathā, pakatiyā ayaṁ puriso pittiko semhiko vātiko sannipātikoti evaṁ pakaticakkhudhātu dasannaṁ piyā ca sabbesu indriyesu …pe… ", "html": "{}"}, "pe6:3.12": {"pli": "visabhāgattho dhātuattho. ", "html": "{}</p>"}, "pe6:4.1": {"pli": "Tattha <b>dvādasāyatanāni</b> katamāni? ", "html": "<p data-counter='3'>{}"}, "pe6:4.2": {"pli": "Cha ajjhattikāni cha bāhirāni. ", "html": "{}"}, "pe6:4.3": {"pli": "Cakkhāyatanaṁ yāva manāyatananti ajjhattikaṁ, rūpāyatanaṁ yāva dhammāyatananti bāhiraṁ. ", "html": "{}"}, "pe6:4.4": {"pli": "Etāni dvādasa āyatanāni. ", "html": "{}"}, "pe6:4.5": {"pli": "Etesaṁ kā pariññā? ", "html": "{}"}, "pe6:4.6": {"pli": "Aniccaṁ dukkhaṁ saññā anattāti, esā etesaṁ pariññā. ", "html": "{}"}, "pe6:4.7": {"pli": "Api ca dvidhā pariññā ñātapariññā ca pahānapariññā ca. ", "html": "{}"}, "pe6:4.8": {"pli": "Tattha ñātapariññā nāma aniccaṁ dukkhaṁ saññā anattāti, esā ñātapariññā. ", "html": "{}"}, "pe6:4.9": {"pli": "Pahānapariññā pana chandarāgappahānā, esā pahānapariññā. ", "html": "{}"}, "pe6:4.10": {"pli": "Tattha katamo āyatanattho? ", "html": "{}"}, "pe6:4.11": {"pli": "Vuccate ākārattho āyatanattho. ", "html": "{}"}, "pe6:4.12": {"pli": "Yathā suvaṇṇākaro dubbaṇṇākaro, yathā dvīhi tehi ākārehi te te gāvā uttiṭṭhanti. ", "html": "{}"}, "pe6:4.13": {"pli": "Evaṁ ete hi cittacetasikā gāvā uttiṭṭhanti kammakilesā dukkhadhammā ca. ", "html": "{}"}, "pe6:4.14": {"pli": "Punarāha āyadānattho āyatanattho. ", "html": "{}"}, "pe6:4.15": {"pli": "Yathā rañño āyadānehi āyo bhavati, evaṁ āyadānattho āyatanattho. ", "html": "{}</p>"}, "pe6:5.1": {"pli": "<b>Cattāri ariyasaccāni</b> dukkhaṁ samudayo nirodho maggo ca. ", "html": "<p data-counter='4'>{}"}, "pe6:5.2": {"pli": "Dukkhaṁ yathā samāsena dhammācariyaṁ mānasañca, samudayo samāsena avijjā ca taṇhā ca, nirodho samāsena vijjā ca vimutti ca, maggo samāsena samatho ca vipassanā ca. ", "html": "{}</p>"}, "pe6:6.1": {"pli": "Tattha <b>sattatiṁsa bodhipakkhikā dhammā</b> katame? ", "html": "<p>{}"}, "pe6:6.2": {"pli": "Cattāro satipaṭṭhānā yāva ariyo aṭṭhaṅgiko maggo, evamete sattatiṁsa bodhipakkhikā dhammā. ", "html": "{}"}, "pe6:6.3": {"pli": "Ye dhammā atītānāgatapaccuppannānaṁ buddhānaṁ bhagavantānaṁ paccekabuddhānaṁ sāvakānaṁ ca nibbānāya saṁvattantīti, so maggo cattāro satipaṭṭhānā. ", "html": "{}"}, "pe6:6.4": {"pli": "Katame cattāro? ", "html": "{}"}, "pe6:6.5": {"pli": "Idha bhikkhu kāye kāyānupassī viharati, ", "html": "{}"}, "pe6:6.6": {"pli": "sammappadhānaṁ … ", "html": "{}"}, "pe6:6.7": {"pli": "iddhipādaṁ … ", "html": "{}"}, "pe6:6.8": {"pli": "indriyāni … ", "html": "{}"}, "pe6:6.9": {"pli": "balāni … ", "html": "{}"}, "pe6:6.10": {"pli": "tattha ko indriyattho? ", "html": "{}"}, "pe6:6.11": {"pli": "Indattho indriyattho, ādhipateyyattho indriyattho, pasādattho indriyattho, asādhāraṇaṁ kassa kiriyattho indriyattho; ", "html": "{}"}, "pe6:6.12": {"pli": "anavapariyattho balattho, thāmattho balattho, upādāyattho balattho, upatthambhanattho balattho. ", "html": "{}</p>"}, "pe6:7.1": {"pli": "Tattha katame satta bojjhaṅgā? ", "html": "<p>{}"}, "pe6:7.2": {"pli": "Satisambojjhaṅgo yāva upekkhāsambojjhaṅgo. ", "html": "{}"}, "pe6:7.3": {"pli": "Tattha katamo aṭṭhaṅgiko maggo? ", "html": "{}"}, "pe6:7.4": {"pli": "Sammādiṭṭhi yāva sammāsamādhi. ", "html": "{}"}, "pe6:7.5": {"pli": "Tattha aṭṭhaṅgiko maggoti khandho sīlakkhandho ca samādhikkhandho ca paññākkhandho ca. ", "html": "{}"}, "pe6:7.6": {"pli": "Tattha yā ca sammāvācā yo ca sammākammanto yo ca sammāājīvo, ayaṁ sīlakkhandho. ", "html": "{}"}, "pe6:7.7": {"pli": "Yā ca sammāsati yo ca sammāvāyāmo yo ca sammāsamādhi, ayaṁ samādhikkhandho. ", "html": "{}"}, "pe6:7.8": {"pli": "Yo ca sammāsaṅkappo yā ca sammādiṭṭhi, ayaṁ paññākkhandho. ", "html": "{}"}, "pe6:7.9": {"pli": "Evaṁ tāyo tisso sikkhā. ", "html": "{}"}, "pe6:7.10": {"pli": "Evaṁ tīhākārehi dasa padāni …pe…. ", "html": "{}</p>"}, "pe6:8.1": {"pli": "Tattha yogāvacaro sīlakkhandhe ṭhito dosaṁ akusalaṁ na upādiyati, dosānusayaṁ samūhanati, dosasallaṁ uddharati, dukkhavedanaṁ parijānāti, kāmadhātuṁ samatikkamati. ", "html": "<p data-counter='5'>{}"}, "pe6:8.2": {"pli": "Samādhikkhandhe ṭhito lobhaṁ akusalaṁ na upādiyati, rāgānusayaṁ samūhanati, lobhasallaṁ uddharati, sukhavedanaṁ parijānāti, rūpadhātuṁ samatikkamati. ", "html": "{}"}, "pe6:8.3": {"pli": "Paññākkhandhe ṭhito mohaṁ akusalaṁ na upādiyati, avijjānusayaṁ samūhanati, mohasallaṁ diṭṭhisallañca uddharati, adukkhamasukhavedanaṁ parijānāti, arūpadhātuṁ samatikkamati. ", "html": "{}"}, "pe6:8.4": {"pli": "Iti tīhi khandhehi tīṇi akusalamūlāni na upādiyati, cattāri sallāni uddharati, tisso vedanā parijānāti, tedhātukaṁ samatikkamati. ", "html": "{}</p>"}, "pe6:9.1": {"pli": "Tattha katamā <b>avijjā</b>? ", "html": "<p>{}"}, "pe6:9.2": {"pli": "Yaṁ catūsu ariyasaccesu aññāṇanti vitthārena yathā so pāṇasajjesu kathaṅkathā kātabbaṁ. ", "html": "{}"}, "pe6:9.3": {"pli": "Tattha katamaṁ viññāṇaṁ? ", "html": "{}"}, "pe6:9.4": {"pli": "Cha viññāṇakāyā vedanā saññā cetanā phasso manasikāro, idaṁ nāmaṁ. ", "html": "{}"}, "pe6:9.5": {"pli": "Tattha katamaṁ rūpaṁ? ", "html": "{}"}, "pe6:9.6": {"pli": "Cātumahābhūtikaṁ catunnaṁ mahābhūtānaṁ upādāyarūpassa paññattiṁ. ", "html": "{}"}, "pe6:9.7": {"pli": "Iti purimakañca nāmaṁ idañca rūpaṁ tadubhayaṁ nāmarūpanti vuccati. ", "html": "{}"}, "pe6:9.8": {"pli": "Tattha chaḷāyatananti cha ajjhattikāni āyatanāni, cakkhu ajjhattikaṁ āyatanaṁ yāva mano ajjhattikaṁ āyatanaṁ. ", "html": "{}"}, "pe6:9.9": {"pli": "Phassoti cha phassakāyā cakkhusamphasso yāva manosamphassoti phasso. ", "html": "{}"}, "pe6:9.10": {"pli": "Cha vedanākāyā vedanā. ", "html": "{}"}, "pe6:9.11": {"pli": "Taṇhāti cha taṇhākāyā taṇhā. ", "html": "{}"}, "pe6:9.12": {"pli": "Upādānanti cattāri upādānāni kāmupādānaṁ diṭṭhupādānaṁ sīlabbatupādānaṁ attavādupādānanti upādānaṁ. ", "html": "{}"}, "pe6:9.13": {"pli": "Bhavoti tayo bhavā kāmabhavo rūpabhavo arūpabhavo. ", "html": "{}"}, "pe6:9.14": {"pli": "Tattha katamā jāti? ", "html": "{}"}, "pe6:9.15": {"pli": "Yā paṭhamaṁ khandhānaṁ paṭhamaṁ dhātūnaṁ paṭhamaṁ āyatanānaṁ uppatti jāti sañjāti okkanti abhinibbatti khandhānaṁ pātubhāvo, ayaṁ jāti. ", "html": "{}"}, "pe6:9.16": {"pli": "Tattha katamā jarā? ", "html": "{}"}, "pe6:9.17": {"pli": "Jarā nāma yaṁ taṁ khaṇḍiccaṁ pāliccaṁ valittacatā pavivittaṁ catunnaṁ mahābhūtānaṁ vivaṇṇataṁ bhaggo taṁ jarā hīyanā pahīyanā āyuno hāni saṁhāni indriyānaṁ paribhedo upanāho paripāko, ayaṁ jarā. ", "html": "{}"}, "pe6:9.18": {"pli": "Tattha katamaṁ maraṇaṁ? ", "html": "{}"}, "pe6:9.19": {"pli": "Maraṇaṁ nāma yaṁ tasmiṁ tasmiṁ sattanikāye tesaṁ tesaṁ sattānaṁ cuti cavanatā maraṇaṁ kālaṅkiriyā uddhumātakānaṁ bhedo kāyassa jīvitindriyassa upacchedo, idaṁ maraṇaṁ. ", "html": "{}"}, "pe6:9.20": {"pli": "Iti purimikā ca jarā idañca maraṇaṁ tadubhayaṁ jarāmaraṇaṁ. ", "html": "{}</p>"}, "pe6:10.1": {"pli": "Tattha andhakāratimisā yathābhūtaṁ appajānanalakkhaṇā avijjā saṅkhārānaṁ padaṭṭhānaṁ. ", "html": "<p>{}"}, "pe6:10.2": {"pli": "Abhisaṅkharaṇalakkhaṇā saṅkhārā, upacayapunabbhavābhiropanapaccupaṭṭhānā. ", "html": "{}"}, "pe6:10.3": {"pli": "Te viññāṇassa padaṭṭhānaṁ. ", "html": "{}"}, "pe6:10.4": {"pli": "Vatthu saviññattilakkhaṇaṁ viññāṇaṁ, taṁ nāmarūpassa padaṭṭhānaṁ. ", "html": "{}"}, "pe6:10.5": {"pli": "Anekasannissayalakkhaṇaṁ nāmarūpaṁ, taṁ saḷāyatanassa padaṭṭhānaṁ. ", "html": "{}"}, "pe6:10.6": {"pli": "Indriyavavatthāpanalakkhaṇaṁ saḷāyatanaṁ, taṁ phassassa padaṭṭhānaṁ. ", "html": "{}"}, "pe6:10.7": {"pli": "Sannipātalakkhaṇo phasso, so vedanāya padaṭṭhānaṁ. ", "html": "{}"}, "pe6:10.8": {"pli": "Anubhavanalakkhaṇā vedanā, sā taṇhāya padaṭṭhānaṁ. ", "html": "{}"}, "pe6:10.9": {"pli": "Ajjhosānalakkhaṇā taṇhā, sā upādānassa padaṭṭhānaṁ. ", "html": "{}"}, "pe6:10.10": {"pli": "Ādānaparihananalakkhaṇaṁ upādānaṁ, taṁ bhavassa padaṭṭhānaṁ. ", "html": "{}"}, "pe6:10.11": {"pli": "Nānāgativikkhepalakkhaṇo bhavo, so jātiyā padaṭṭhānaṁ. ", "html": "{}"}, "pe6:10.12": {"pli": "Khandhānaṁ pātubhāvalakkhaṇā jāti, sā jarāya padaṭṭhānaṁ. ", "html": "{}"}, "pe6:10.13": {"pli": "Upanayaparipākalakkhaṇā jarā, sā maraṇassa padaṭṭhānaṁ. ", "html": "{}"}, "pe6:10.14": {"pli": "Āyukkhayajīvitauparodhalakkhaṇaṁ maraṇaṁ, taṁ dukkhassa padaṭṭhānaṁ. ", "html": "{}"}, "pe6:10.15": {"pli": "Kāyasampīḷanalakkhaṇaṁ dukkhaṁ, taṁ domanassassa padaṭṭhānaṁ. ", "html": "{}"}, "pe6:10.16": {"pli": "Cittasampīḷanalakkhaṇaṁ domanassaṁ, taṁ sokassa padaṭṭhānaṁ. ", "html": "{}"}, "pe6:10.17": {"pli": "Socanalakkhaṇo soko, so paridevassa padaṭṭhānaṁ. ", "html": "{}"}, "pe6:10.18": {"pli": "Vacīnicchāraṇalakkhaṇo paridevo, so upāyāsassa padaṭṭhānaṁ. ", "html": "{}"}, "pe6:10.19": {"pli": "Ye āyāsā te upāyāsā. ", "html": "{}</p>"}, "pe6:11.1": {"pli": "Nava padāni yattha sabbo akusalapakkho saṅgahaṁ samosaraṇaṁ gacchati. ", "html": "<p>{}"}, "pe6:11.2": {"pli": "Katamāni nava padāni? ", "html": "{}"}, "pe6:11.3": {"pli": "Dve mūlakilesā, tīṇi akusalamūlāni, cattāro vipallāsā. ", "html": "{}"}, "pe6:11.4": {"pli": "Tattha dve mūlakilesā avijjā ca bhavataṇhā ca, tīṇi akusalamūlāni lobho doso moho ca. ", "html": "{}"}, "pe6:11.5": {"pli": "Cattāro vipallāsā—", "html": "{}"}, "pe6:11.6": {"pli": "“anicce niccan”ti saññāvipallāso cittavipallāso diṭṭhivipallāso, “dukkhe sukhan”ti saññāvipallāso cittavipallāso diṭṭhivipallāso, “anattani attā”ti saññāvipallāso cittavipallāso diṭṭhivipallāso, “asubhe subhan”ti saññāvipallāso cittavipallāso diṭṭhivipallāso. ", "html": "{}</p>"}, "pe6:12.1": {"pli": "Tattha avijjā nāma catūsu ariyasaccesu yathābhūtaṁ aññāṇaṁ, ayaṁ avijjā. ", "html": "<p>{}"}, "pe6:12.2": {"pli": "Bhavataṇhā nāma yo bhavesu rāgo sārāgo icchā mucchā patthanā nandī ajjhosānaṁ apariccāgo, ayaṁ bhavataṇhā. ", "html": "{}</p>"}, "pe6:13.1": {"pli": "Tattha katamo lobho akusalamūlaṁ? ", "html": "<p>{}"}, "pe6:13.2": {"pli": "Lobho nāma so tesu tesu paravatthūsu paradabbesu paraṭṭhānesu parasāpateyyesu parapariggahitesu lobho lubbhanā icchā mucchā patthanā nandī ajjhosānaṁ apariccāgo, ayaṁ lobho akusalamūlaṁ. ", "html": "{}"}, "pe6:13.3": {"pli": "Kassetaṁ mūlaṁ? ", "html": "{}"}, "pe6:13.4": {"pli": "Lobho lobhajassa akusalassa kāyakammassa vacīkammassa manokammassa ca, tathā yathā taṁsampayuttānaṁ cittacetasikānaṁ dhammānaṁ mūlaṁ. ", "html": "{}</p>"}, "pe6:14.1": {"pli": "Tattha katamo doso akusalamūlaṁ? ", "html": "<p>{}"}, "pe6:14.2": {"pli": "So sattesu āghāto akkhanti appaccayo byāpādo padoso anatthakāmatā cetaso paṭighāto, ayaṁ doso akusalamūlaṁ. ", "html": "{}</p>"}, "pe6:15.1": {"pli": "Kassetaṁ mūlaṁ? ", "html": "<p>{}"}, "pe6:15.2": {"pli": "Dosajassa kāyakammassa vacīkammassa manokammassa sampayuttānañca cittacetasikānaṁ dhammānaṁ mūlaṁ. ", "html": "{}</p>"}, "pe6:16.1": {"pli": "Tattha katamo moho akusalamūlaṁ? ", "html": "<p>{}"}, "pe6:16.2": {"pli": "Yaṁ catūsu ariyasaccesu anabhisamayo asampajjaggāho appaṭivedho moho muyhanā sammoho sammuyhanā avijjā tamo andhakāro āvaraṇaṁ nīvaraṇaṁ chadanaṁ acchadanaṁ apasacchāgamanaṁ kusalānaṁ dhammānaṁ, ayaṁ moho akusalamūlaṁ. ", "html": "{}</p>"}, "pe6:17.1": {"pli": "Kassetaṁ mūlaṁ? ", "html": "<p data-counter='6'>{}"}, "pe6:17.2": {"pli": "Mohajassa akusalassa kāyakammassa vacīkammassa manokammassa ca taṁsampayuttakānañca cittacetasikānaṁ dhammānaṁ mūlaṁ. ", "html": "{}</p>"}, "pe6:18.1": {"pli": "Tattha <b>vipallāsā</b> jānitabbā, vipallāsānaṁ vatthu jānitabbaṁ. ", "html": "<p>{}"}, "pe6:18.2": {"pli": "Yaṁ vipallāsaṁ siyā, taṁ jānitabbaṁ. ", "html": "{}"}, "pe6:18.3": {"pli": "Tattha eko vipallāso tīṇi vipallāsāni cattāri vipallāsavatthūni. ", "html": "{}"}, "pe6:18.4": {"pli": "Katamo eko vipallāso ca, yena paṭipakkhena vipallāsitaṁ gaṇhāti? ", "html": "{}"}, "pe6:18.5": {"pli": "“Anicce niccan”ti, “dukkhe sukhan”ti, “anattani attā”ti, “asubhe subhan”ti, ayaṁ eko vipallāso. ", "html": "{}</p>"}, "pe6:19.1": {"pli": "Katamāni cattāri vipallāsavatthūni? ", "html": "<p>{}"}, "pe6:19.2": {"pli": "Kāyo vedanā cittaṁ dhammā ca. ", "html": "{}"}, "pe6:19.3": {"pli": "Imāni cattāri vipallāsavatthūni. ", "html": "{}</p>"}, "pe6:20.1": {"pli": "Katamāni tīṇi vipallāsāni? ", "html": "<p>{}"}, "pe6:20.2": {"pli": "Saññā cittaṁ diṭṭhi ca. ", "html": "{}"}, "pe6:20.3": {"pli": "Imāni tīṇi vipallāsāni. ", "html": "{}</p>"}, "pe6:21.1": {"pli": "Tattha manāpike vatthumhi indriyavatthe vaṇṇāyatane vā yo nimittassa uggāho, ayaṁ saññāvipallāso. ", "html": "<p>{}"}, "pe6:21.2": {"pli": "Tattha viparītacittassa vatthumhi sati viññatti, ayaṁ cittavipallāso. ", "html": "{}"}, "pe6:21.3": {"pli": "Tattha viparītacittassa tamhi rūpe “asubhe subhan”ti yā khanti ruci upekkhanā nicchayo diṭṭhi nidassanaṁ santīraṇā, ayaṁ diṭṭhivipallāso. ", "html": "{}"}, "pe6:21.4": {"pli": "Tattha vatthubhedena kāyesu dvādasa vipallāsā bhavanti. ", "html": "{}"}, "pe6:21.5": {"pli": "Tayo kāye tayo vedanāya tayo citte tayo dhamme, cattāro saññāvipallāsā cattāro cittavipallāsā cattāro diṭṭhivipallāsā, āyatanūpacayato cakkhuviññāṇasaññāsamaṅgissa rūpesu dvādasa vipallāsā yāva mano saññāsamaṅgissa, dhammesu dvādasa vipallāsā cha dvādasakā cattāri vipallāsā bhavanti. ", "html": "{}"}, "pe6:21.6": {"pli": "Ārammaṇanānattato hi aparimitasaṅkheyyānaṁ sattānaṁ aparimitamasaṅkheyyā vipallāsā bhavanti hīnukkaṭṭhamajjhimatāya. ", "html": "{}</p>"}, "pe6:22.1": {"pli": "Tattha pañcakkhandhā cattāri attabhāvavatthūni bhavanti. ", "html": "<p>{}"}, "pe6:22.2": {"pli": "Yo rūpakkhandho, so kāyo attabhāvavatthu. ", "html": "{}"}, "pe6:22.3": {"pli": "Yo vedanākkhandho, so vedanā attabhāvavatthu. ", "html": "{}"}, "pe6:22.4": {"pli": "Yo saññākkhandho ca saṅkhārakkhandho ca, te dhammā attabhāvavatthu. ", "html": "{}"}, "pe6:22.5": {"pli": "Yo viññāṇakkhandho, so cittaṁ attabhāvavatthu. ", "html": "{}"}, "pe6:22.6": {"pli": "Iti pañcakkhandhā cattāri attabhāvavatthūni. ", "html": "{}"}, "pe6:22.7": {"pli": "Tattha kāye “asubhe subhan”ti. ", "html": "{}"}, "pe6:22.8": {"pli": "Vipallāso bhavati. ", "html": "{}"}, "pe6:22.9": {"pli": "Evaṁ vedanāsu … ", "html": "{}"}, "pe6:22.10": {"pli": "citte … ", "html": "{}"}, "pe6:22.11": {"pli": "dhammesu ca attavipallāso bhavati. ", "html": "{}"}, "pe6:22.12": {"pli": "Tattha catunnaṁ vipallāsānaṁ samugghātanatthaṁ bhagavā cattāro satipaṭṭhāne deseti paññapeti kāye kāyānupassī viharato “asubhe subhan”ti. ", "html": "{}"}, "pe6:22.13": {"pli": "Vipallāsaṁ samugghāteti, evaṁ vedanāsu, citte, dhammesu ca kātabbaṁ. ", "html": "{}</p>"}, "pe6:23.1": {"pli": "Tattha andhakāratimisā appaṭivedhalakkhaṇā avijjā, tassā vipallāsapadaṭṭhānaṁ. ", "html": "<p>{}"}, "pe6:23.2": {"pli": "Ajjhosānalakkhaṇā taṇhā, tassā piyarūpasātarūpaṁ padaṭṭhānaṁ. ", "html": "{}"}, "pe6:23.3": {"pli": "Attāsayavañcanālakkhaṇo lobho, tassa adinnādānaṁ padaṭṭhānaṁ. ", "html": "{}"}, "pe6:23.4": {"pli": "Idha vivādalakkhaṇo doso, tassa pāṇātipāto padaṭṭhānaṁ. ", "html": "{}"}, "pe6:23.5": {"pli": "Vatthuvippaṭipattilakkhaṇo moho, tassa micchāpaṭipatti padaṭṭhānaṁ. ", "html": "{}"}, "pe6:23.6": {"pli": "Saṅkhatānaṁ dhammānaṁ avināsaggahaṇalakkhaṇā niccasaññā, tassā sabbasaṅkhārā padaṭṭhānaṁ. ", "html": "{}"}, "pe6:23.7": {"pli": "Sāsavaphassopagamanalakkhaṇā sukhasaññā, tassā mamaṅkāro padaṭṭhānaṁ. ", "html": "{}"}, "pe6:23.8": {"pli": "Dhammesu upagamanalakkhaṇā attasaññā, tassā ahaṅkāro padaṭṭhānaṁ. ", "html": "{}"}, "pe6:23.9": {"pli": "Vaṇṇasaṅgahaṇalakkhaṇā subhasaññā, tassā indriyaasaṁvaro padaṭṭhānaṁ. ", "html": "{}"}, "pe6:23.10": {"pli": "Etehi navahi padehi uddiṭṭhehi sabbo akusalapakkho niddiṭṭho bhavati, so ca kho bahussutena sakkā jānituṁ no appassutena, paññavatā no duppaññena, yuttena no ayuttena. ", "html": "{}</p>"}, "pe6:24.1": {"pli": "Nava padāni kusalāni yattha sabbo kusalapakkho saṅgaho samosaraṇaṁ gacchanti. ", "html": "<p data-counter='7'>{}"}, "pe6:24.2": {"pli": "Katamāni nava padāni? ", "html": "{}"}, "pe6:24.3": {"pli": "Samatho vipassanā alobho adoso amoho aniccasaññā dukkhasaññā anattasaññā asubhasaññā ca. ", "html": "{}</p>"}, "pe6:25.1": {"pli": "Tattha katamo <b>samatho</b>? ", "html": "<p data-counter='8'>{}"}, "pe6:25.2": {"pli": "Yā cittassa ṭhiti saṇṭhiti avaṭṭhiti ṭhānaṁ paṭṭhānaṁ upaṭṭhānaṁ samādhi samādhānaṁ avikkhepo avippaṭisāro vūpasamo mānaso ekaggaṁ cittassa, ayaṁ samatho. ", "html": "{}</p>"}, "pe6:26.1": {"pli": "Tattha katamā <b>vipassanā</b>? ", "html": "<p data-counter='9'>{}"}, "pe6:26.2": {"pli": "Khandhesu vā dhātūsu vā āyatanesu vā nāmarūpesu vā paṭiccasamuppādesu vā paṭiccasamuppannesu vā dhammesu dukkhesu vā samudayesu vā nirodhe vā magge vā kusalākusalesu vā dhammesu sāvajjaanavajjesu vā kaṇhasukkesu vā sevitabbaasevitabbesu vā so yathābhūtaṁ vicayo pavicayo vīmaṁsā paravīmaṁsā gāhanā aggāhanā pariggāhanā cittena paricitanā tulanā upaparikkhā ñāṇaṁ vijjā vā cakkhu buddhi medhā paññā obhāso āloko ābhā pabhā khaggo nārāco dhammavicayasambojjhaṅgo sammādiṭṭhi maggaṅgaṁ, ayaṁ vipassanā. ", "html": "{}"}, "pe6:26.3": {"pli": "Tenesā vipassanā iti vuccati vividhā vā esā vipassanāti, tasmā esā vipassanāti vuccati. ", "html": "{}"}, "pe6:26.4": {"pli": "Dvidhā cesā hi vipassanā dhammavipassanāti vuccati, dvidhā imāya passati subhañca asubhañca kaṇhañca sukkañca sevitabbañca asevitabbañca kammañca vipākañca bandhañca vimokkhañca ācayañca apacayañca pavattiñca nivattiñca saṅkilesañca vodānañca, evaṁ vipassanāti vuccati. ", "html": "{}"}, "pe6:26.5": {"pli": "Atha vā viiti upasaggo passanāti attho tasmā vipassanāti vuccate, ayaṁ vipassanā. ", "html": "{}</p>"}, "pe6:27.1": {"pli": "Tattha dve rogā sattānaṁ avijjā ca bhavataṇhā ca, etesaṁ dvinnaṁ rogānaṁ nighātāya bhagavatā dve bhesajjāni vuttāni samatho ca vipassanā ca. ", "html": "<p>{}"}, "pe6:27.2": {"pli": "Imāni dve bhesajjāni paṭisevento dve aroge sacchikaroti rāgavirāgaṁ cetovimuttiṁ avijjāvirāgañca paññāvimuttiṁ. ", "html": "{}"}, "pe6:27.3": {"pli": "Tattha taṇhārogassa samatho bhesajjaṁ, rāgavirāgā cetovimutti arogaṁ. ", "html": "{}"}, "pe6:27.4": {"pli": "Avijjārogassa vipassanābhesajjaṁ avijjāvirāgā paññāvimutti arogaṁ. ", "html": "{}"}, "pe6:27.5": {"pli": "Evañhi bhagavā cāha, “dve dhammā pariññeyyā nāmañca rūpañca, dve dhammā pahātabbā avijjā ca bhavataṇhā ca, dve dhammā bhāvetabbā samatho ca vipassanā ca, dve dhammā sacchikātabbā vijjā ca vimutti cā”ti. ", "html": "{}"}, "pe6:27.6": {"pli": "Tattha samathaṁ bhāvento rūpaṁ parijānāti, rūpaṁ parijānanto taṇhaṁ pajahati, taṇhaṁ pajahanto rāgavirāgā cetovimuttiṁ sacchikaroti, vipassanaṁ bhāvento nāmaṁ parijānāti, nāmaṁ parijānanto avijjaṁ pajahati, avijjaṁ pajahanto avijjāvirāgā paññāvimuttiṁ sacchikaroti. ", "html": "{}"}, "pe6:27.7": {"pli": "Yadā bhikkhuno dve dhammā pariññātā bhavanti nāmañca rūpañca, tathāssa dve dhammā pahīnā bhavanti avijjā ca bhavataṇhā ca. ", "html": "{}"}, "pe6:27.8": {"pli": "Dve dhammā bhāvitā bhavanti samatho ca vipassanā ca, dve dhammā sacchikātabbā bhavanti vijjā ca vimutti ca. ", "html": "{}"}, "pe6:27.9": {"pli": "Ettāvatā bhikkhu katakicco bhavati. ", "html": "{}"}, "pe6:27.10": {"pli": "Esā sopādisesā nibbānadhātu. ", "html": "{}"}, "pe6:27.11": {"pli": "Tassa āyupariyādānā jīvitindriyassa uparodhā idañca dukkhaṁ nirujjhati, aññañca dukkhaṁ na uppajjati. ", "html": "{}"}, "pe6:27.12": {"pli": "Tattha yo imesaṁ khandhānaṁ dhātuāyatanānaṁ nirodho vūpasamo aññesañca khandhadhātuāyatanānaṁ appaṭisandhi apātubhāvo, ayaṁ anupādisesā nibbānadhātu. ", "html": "{}</p>"}, "pe6:28.1": {"pli": "Tattha katamaṁ <b>alobho kusalamūlaṁ</b>? ", "html": "<p data-counter='10'>{}"}, "pe6:28.2": {"pli": "Yaṁdhātuko alobho alubbhanā alubbhitattaṁ anicchā apatthanā akantā anajjhosānaṁ. ", "html": "{}"}, "pe6:28.3": {"pli": "Ayaṁ alobho kusalamūlaṁ. ", "html": "{}"}, "pe6:28.4": {"pli": "Kassetaṁ mūlaṁ? ", "html": "{}"}, "pe6:28.5": {"pli": "Alobhajassa kusalassa kāyakammassa vacīkammassa manokammassa taṁsampayuttānañca cittacetasikānaṁ dhammānaṁ mūlaṁ. ", "html": "{}"}, "pe6:28.6": {"pli": "Atha vā ariyo aṭṭhaṅgiko maggo kusalanti vuccati, so tiṇṇaṁ maggaṅgānaṁ mūlaṁ. ", "html": "{}"}, "pe6:28.7": {"pli": "Katamesaṁ tiṇṇaṁ, sammāsaṅkappassa sammāvāyāmassa sammāsamādhissa ca imesaṁ mūlanti, tasmā kusalamūlanti vuccati. ", "html": "{}</p>"}, "pe6:29.1": {"pli": "Tattha katamaṁ <b>adoso kusalamūlaṁ</b>? ", "html": "<p data-counter='11'>{}"}, "pe6:29.2": {"pli": "Yā sattesu vā saṅkhāresu vā anaghāto appaṭighāto abyāpatti abyāpādo adoso mettā mettāyanā atthakāmatā hitakāmatā cetaso pasādo, ayaṁ adoso kusalamūlaṁ. ", "html": "{}"}, "pe6:29.3": {"pli": "Kassetaṁ mūlaṁ? ", "html": "{}"}, "pe6:29.4": {"pli": "Adosajassa kusalassa kāyakammassa vacīkammassa manokammassa taṁsampayuttānañca cittacetasikānaṁ dhammānaṁ mūlaṁ. ", "html": "{}"}, "pe6:29.5": {"pli": "Atha vā tiṇṇaṁ maggaṅgānaṁ mūlaṁ. ", "html": "{}"}, "pe6:29.6": {"pli": "Katamesaṁ tiṇṇaṁ? ", "html": "{}"}, "pe6:29.7": {"pli": "Sammāvācāya sammākammantassa sammāājīvassa ca imesaṁ tiṇṇaṁ maggaṅgānaṁ mūlaṁ, tasmā kusalamūlanti vuccati. ", "html": "{}</p>"}, "pe6:30.1": {"pli": "Tattha katamaṁ <b>amoho kusalamūlaṁ</b>? ", "html": "<p data-counter='12'>{}"}, "pe6:30.2": {"pli": "Yaṁ catūsu ariyasaccesu yathābhūtaṁ ñāṇadassanaṁ abhisamayo sammā ca paccāgamo paṭivedho amoho asammuyhanā asammoho vijjāpakāso āloko anāvaraṇaṁ sekkhānaṁ kusalānaṁ dhammānaṁ, ayaṁ amoho kusalamūlaṁ. ", "html": "{}"}, "pe6:30.3": {"pli": "Kassetaṁ mūlaṁ? ", "html": "{}"}, "pe6:30.4": {"pli": "Amohajassa kusalassa kāyakammassa vacīkammassa manokammassa taṁsampayuttānañca cittacetasikānaṁ dhammānaṁ mūlaṁ. ", "html": "{}"}, "pe6:30.5": {"pli": "Atha vā dvinnaṁ maggaṅgānaṁ etaṁ mūlaṁ. ", "html": "{}"}, "pe6:30.6": {"pli": "Katamesaṁ dvinnaṁ? ", "html": "{}"}, "pe6:30.7": {"pli": "Sammādiṭṭhiyā ca sammāsatiyā ca imesaṁ dvinnaṁ maggaṅgānaṁ mūlaṁ, tasmā kusalamūlanti vuccati. ", "html": "{}</p>"}, "pe6:31.1": {"pli": "Evaṁ imesaṁ tīhi kusalamūlehi aṭṭhaṅgiko maggo yojetabbo. ", "html": "<p>{}</p>"}, "pe6:32.1": {"pli": "Tattha katamā <b>aniccasaññā</b>? ", "html": "<p>{}"}, "pe6:32.2": {"pli": "“Sabbe saṅkhārā uppādavayadhammino”ti ca yā saññā sañjānanā vavatthapanā uggāho, ayaṁ aniccasaññā. ", "html": "{}"}, "pe6:32.3": {"pli": "Tassā ko nissando? ", "html": "{}"}, "pe6:32.4": {"pli": "Aniccasaññāya bhāvitāya bahulīkatāya aṭṭhasu lokadhammesu cittaṁ nānusandhati na sandhati na saṇṭhahati, upekkhā vā paṭikkūlatā vā saṇṭhahati, ayamassā nissando. ", "html": "{}</p>"}, "pe6:33.1": {"pli": "Tattha katamā <b>dukkhasaññā</b>? ", "html": "<p>{}"}, "pe6:33.2": {"pli": "“Sabbe saṅkhārā dukkhā”ti yā saññā sañjānanā vavatthapanā uggāho, ayaṁ dukkhasaññā. ", "html": "{}"}, "pe6:33.3": {"pli": "Tassā ko nissando? ", "html": "{}"}, "pe6:33.4": {"pli": "Dukkhasaññāya bhāvitāya bahulīkatāya ālasse sampamāde vimhaye ca cittaṁ nānusandhati na sandhati na saṇṭhahati, upekkhā vā paṭikkūlatā vā saṇṭhahati, ayamassā nissando. ", "html": "{}</p>"}, "pe6:34.1": {"pli": "Tattha katamā <b>anattasaññā</b>? ", "html": "<p>{}"}, "pe6:34.2": {"pli": "“Sabbesu dhammesu anattā”ti yā saññā sañjānanā vavatthapanā uggāho, ayaṁ anattasaññā. ", "html": "{}"}, "pe6:34.3": {"pli": "Tassā ko nissando, anattasaññāya bhāvitāya bahulīkatāya ahaṅkāro cittaṁ nānusandhati na sandhati, mamaṅkāro na saṇṭhahati, upekkhā vā paṭikkūlatā vā saṇṭhahati, ayamassā nissando. ", "html": "{}</p>"}, "pe6:35.1": {"pli": "Tattha katamā <b>asubhasaññā</b>? ", "html": "<p>{}"}, "pe6:35.2": {"pli": "“Satta saṅkhārā asubhā”ti yā saññā sañjānanā vavatthapanā uggāho, ayaṁ asubhasaññā. ", "html": "{}"}, "pe6:35.3": {"pli": "Tassā ko nissando? ", "html": "{}"}, "pe6:35.4": {"pli": "Asubhasaññāya bhāvitāya bahulīkatāya subhanimitte cittaṁ nānusandhati na sandhati na saṇṭhahati, upekkhā vā paṭikkūlatā vā saṇṭhahati, ayamassā nissando. ", "html": "{}</p>"}, "pe6:36.1": {"pli": "Tattha pañcannaṁ khandhānaṁ pariññā bhagavatā desitā, yo tattha asubhasaññā rūpakkhandhassa pariññattaṁ, dukkhasaññā vedanākkhandhassa pariññattaṁ, anattasaññā saññākkhandhassa saṅkhārakkhandhassa pariññattaṁ, aniccasaññā viññāṇakkhandhassa pariññattaṁ. ", "html": "<p>{}"}, "pe6:36.2": {"pli": "Tattha samathena taṇhaṁ samugghāteti, vipassanāya avijjaṁ samugghāteti, adosena dosaṁ samugghāteti, amohena mohaṁ samugghāteti, aniccasaññāya niccasaññaṁ samugghāteti, dukkhasaññāya sukhasaññaṁ samugghāteti, anattasaññāya attasaññaṁ samugghāteti, asubhasaññāya subhasaññaṁ samugghāteti. ", "html": "{}</p>"}, "pe6:37.1": {"pli": "Cittavikkhepapaṭisaṁharaṇalakkhaṇo samatho, tassa jhānāni padaṭṭhānaṁ. ", "html": "<p>{}"}, "pe6:37.2": {"pli": "Sabbadhammaṁ yathābhūtaṁ paṭivedhalakkhaṇā vipassanā, tassā sabbaneyyaṁ padaṭṭhānaṁ. ", "html": "{}"}, "pe6:37.3": {"pli": "Icchāpaṭisaṁharaṇalakkhaṇo alobho, tassa adinnādānā veramaṇī padaṭṭhānaṁ. ", "html": "{}"}, "pe6:37.4": {"pli": "Abyāpādalakkhaṇo adoso, tassa pāṇātipātā veramaṇī padaṭṭhānaṁ. ", "html": "{}"}, "pe6:37.5": {"pli": "Vatthuappaṭihatalakkhaṇo amoho, tassa sammāpaṭipatti padaṭṭhānaṁ. ", "html": "{}"}, "pe6:37.6": {"pli": "Saṅkhatānaṁ dhammānaṁ vināsaggahaṇalakkhaṇā aniccasaññā, tassā udayabbayo padaṭṭhānaṁ. ", "html": "{}"}, "pe6:37.7": {"pli": "Sāsavaphassasañjānanalakkhaṇā dukkhasaññā, tassā vedanā padaṭṭhānaṁ. ", "html": "{}"}, "pe6:37.8": {"pli": "Sabbadhammaanupagamanalakkhaṇā anattasaññā, tassā dhammasaññā padaṭṭhānaṁ. ", "html": "{}"}, "pe6:37.9": {"pli": "Vinīlakavipubbakauddhumātakasamuggahaṇalakkhaṇā asubhasaññā, tassā nibbidā padaṭṭhānaṁ. ", "html": "{}"}, "pe6:37.10": {"pli": "Imesu navasu padesu upadiṭṭhesu sabbo kusalapakkho upadiṭṭho bhavati, so ca bahussutena sakkā jānituṁ no appassutena, paññavatā no duppaññena, yuttena no ayuttenāti. ", "html": "{}</p>"}, "pe6:38.1": {"pli": "Tattha niccasaññādhimuttassa aparāparaṁ cittaṁ paṇāmento satimapaccavekkhato aniccasaññā na upaṭṭhāti, pañcasu kāmaguṇesu sukhassādādhimuttassa iriyāpathassa agatimapaccavekkhato dukkhasaññā na upaṭṭhāti, khandhadhātuāyatanesu attādhimuttassa nānādhātuanekadhātuvinibbhogamapaccavekkhato anattasaññā na upaṭṭhāti, vaṇṇasaṇṭhānābhiratassa kāye subhādhimuttassa ca vippaṭicchannā asubhasaññā na upaṭṭhāti. ", "html": "<p>{}</p>"}, "pe6:39.1": {"pli": "Avippaṭisāralakkhaṇā saddhā, saddahanā paccupaṭṭhānaṁ. ", "html": "<p>{}"}, "pe6:39.2": {"pli": "Tassa cattāri sotāpattiyaṅgāni padaṭṭhānaṁ. ", "html": "{}"}, "pe6:39.3": {"pli": "Evañhi vuttaṁ bhagavatā saddhindriyaṁ bhikkhave, kuhiṁ daṭṭhabbaṁ, catūsu sotāpattiyaṅgesu kusalesu dhammesu. ", "html": "{}</p>"}, "pe6:40.1": {"pli": "Sūrāapaṭikkhepanalakkhaṇaṁ vīriyindriyaṁ, vīriyindriyārambho paccupaṭṭhānaṁ. ", "html": "<p>{}"}, "pe6:40.2": {"pli": "Tassa atītā cattāro sammappadhānā padaṭṭhānaṁ. ", "html": "{}"}, "pe6:40.3": {"pli": "Yathā vuttaṁ bhagavatā vīriyindriyaṁ, bhikkhave, kuhiṁ daṭṭhabbaṁ, catūsu sammappadhānesu. ", "html": "{}</p>"}, "pe6:41.1": {"pli": "Sati saraṇalakkhaṇā, asammohapaccupaṭṭhānā. ", "html": "<p>{}"}, "pe6:41.2": {"pli": "Tassa atītā cattāro satipaṭṭhānā padaṭṭhānaṁ. ", "html": "{}"}, "pe6:41.3": {"pli": "Yathā vuttaṁ bhagavatā satindriyaṁ bhikkhave, kuhiṁ daṭṭhabbaṁ, catūsu satipaṭṭhānesu. ", "html": "{}</p>"}, "pe6:42.1": {"pli": "Ekaggalakkhaṇo samādhi, avikkhepapaccupaṭṭhāno, tassa cattāri ñāṇāni padaṭṭhānaṁ. ", "html": "<p>{}"}, "pe6:42.2": {"pli": "Yathā vuttaṁ bhagavatā samādhindriyaṁ, bhikkhave, kuhiṁ daṭṭhabbaṁ, catūsu jhānesu. ", "html": "{}</p>"}, "pe6:43.1": {"pli": "Pajānanalakkhaṇā paññā, bhūtatthasantīraṇā paccupaṭṭhānā, tassa cattāri ariyasaccāni padaṭṭhānaṁ. ", "html": "<p data-counter='13'>{}"}, "pe6:43.2": {"pli": "Yathā vuttaṁ bhagavatā paññindriyaṁ, bhikkhave, kuhiṁ daṭṭhabbaṁ, catūsu ariyasaccesu. ", "html": "{}</p>"}, "pe6:44.1": {"pli": "<b>Cattāri cakkāni</b> patirūpadesavāso cakkaṁ, sappurisūpanissayo cakkaṁ, attasammāpaṇidhānaṁ cakkaṁ, pubbe katapuññatā cakkaṁ. ", "html": "<p>{}"}, "pe6:44.2": {"pli": "Tattha ariyasannissayalakkhaṇo patirūpadesavāso, so sappurisūpanissayassa padaṭṭhānaṁ. ", "html": "{}"}, "pe6:44.3": {"pli": "Ariyasannissayalakkhaṇo sappurisūpanissayo, so attasammāpaṇidhānassa padaṭṭhānaṁ. ", "html": "{}"}, "pe6:44.4": {"pli": "Sammāpaṭipattilakkhaṇaṁ attasammāpaṇidhānaṁ, taṁ puññānaṁ padaṭṭhānaṁ. ", "html": "{}"}, "pe6:44.5": {"pli": "Kusaladhammopacayalakkhaṇaṁ puññaṁ, taṁ sabbasampattīnaṁ padaṭṭhānaṁ. ", "html": "{}</p>"}, "pe6:45.1": {"pli": "Ekādasasīlamūlakā dhammā sīlavato avippaṭisāro bhavati …pe… ", "html": "<p data-counter='14'>{}"}, "pe6:45.2": {"pli": "so vimuttiñāṇadassanaṁ “nāparaṁ itthattāyā”ti pajānanā. ", "html": "{}"}, "pe6:45.3": {"pli": "Tattha veramaṇilakkhaṇaṁ sīlaṁ, taṁ avippaṭisārassa padaṭṭhānaṁ. ", "html": "{}"}, "pe6:45.4": {"pli": "Na attānuvādalakkhaṇo avippaṭisāro, so pāmojjassa padaṭṭhānaṁ. ", "html": "{}"}, "pe6:45.5": {"pli": "Abhippamodanalakkhaṇaṁ pāmojjaṁ, taṁ pītiyā padaṭṭhānaṁ. ", "html": "{}"}, "pe6:45.6": {"pli": "Attamanalakkhaṇā pīti, sā passaddhiyā padaṭṭhānaṁ. ", "html": "{}"}, "pe6:45.7": {"pli": "Kammaniyalakkhaṇā passaddhi, sā sukhassa padaṭṭhānaṁ. ", "html": "{}"}, "pe6:45.8": {"pli": "Abyāpādalakkhaṇaṁ sukhaṁ, taṁ samādhino padaṭṭhānaṁ. ", "html": "{}"}, "pe6:45.9": {"pli": "Avikkhepanalakkhaṇo samādhi, so yathābhūtañāṇadassanassa padaṭṭhānaṁ. ", "html": "{}"}, "pe6:45.10": {"pli": "Aviparītasantīraṇalakkhaṇā paññā, sā nibbidāya padaṭṭhānaṁ anālayanalakkhaṇā nibbidā, sā virāgassa padaṭṭhānaṁ. ", "html": "{}"}, "pe6:45.11": {"pli": "Asaṅkilesalakkhaṇo virāgo, so vimuttiyā padaṭṭhānaṁ. ", "html": "{}"}, "pe6:45.12": {"pli": "Akusaladhammavivekalakkhaṇā vimutti, sā vimuttino vodānassa padaṭṭhānaṁ. ", "html": "{}</p>"}, "pe6:46.1": {"pli": "<b>Catasso ariyabhūmiyo cattāri sāmaññaphalāni</b>. ", "html": "<p>{}"}, "pe6:46.2": {"pli": "Tattha yo yathābhūtaṁ pajānāti, esā dassanabhūmi. ", "html": "{}"}, "pe6:46.3": {"pli": "Sotāpattiphalañca so yathābhūtaṁ pajānitvā nibbindati, idaṁ tanukāmarāgassa padaṭṭhānaṁ byāpādānaṁ. ", "html": "{}"}, "pe6:46.4": {"pli": "Sakadāgāmiphalañca saṇhaṁ virajjati, ayaṁ rāgavirāgā cetovimutti. ", "html": "{}"}, "pe6:46.5": {"pli": "Anāgāmiphalañca yaṁ avijjāvirāgā vimuccati, ayaṁ katābhūmi. ", "html": "{}"}, "pe6:46.6": {"pli": "Arahattañca sāmaññaphalānīti ko vacanattho, ariyo aṭṭhaṅgiko maggo sāmaññaṁ, tassetāni phalāni sāmaññaphalānīti vuccati. ", "html": "{}"}, "pe6:46.7": {"pli": "Kissa brahmaññaphalānīti vuccante? ", "html": "{}"}, "pe6:46.8": {"pli": "Brahmaññaariyo aṭṭhaṅgiko maggo, tassa tāni phalānīti brahmaññaphalānīti vuccante. ", "html": "{}</p>"}, "pe6:47.1": {"pli": "Tattha sotāpanno kathaṁ hoti? ", "html": "<p>{}"}, "pe6:47.2": {"pli": "Saha saccābhisamayā ariyasāvakassa tīṇi saṁyojanāni pahīyanti sakkāyadiṭṭhi vicikicchā sīlabbataparāmāso ca, imesaṁ tiṇṇaṁ saṁyojanānaṁ pahānā parikkhayā ariyasāvako hoti sotāpanno avinipātadhammo yāva dukkhassantaṁ karoti. ", "html": "{}</p>"}, "pe6:48.1": {"pli": "Tattha katamā sakkāyadiṭṭhi? ", "html": "<p>{}"}, "pe6:48.2": {"pli": "Assutavā bālo puthujjano yāva ariyadhamme akovido, so rūpaṁ attato samanupassati yāva viññāṇasmiṁ attānaṁ, so imesu pañcasu khandhesu attaggāho vā attaniyaggāho vā esohamasmi ekasmiṁ vasavattiko pakkhitto anuggaho anusayanto aṅgamaṅganti parati. ", "html": "{}"}, "pe6:48.3": {"pli": "Yā tathābhūtassa khanti ruci pekkhanā ākāraparivitakko diṭṭhinijjhāyanā abhippasannā, ayaṁ vuccate sakkāyadiṭṭhīti. ", "html": "{}</p>"}, "pe6:49.1": {"pli": "Tattha pañca diṭṭhiyo ucchedaṁ bhajanti. ", "html": "<p>{}"}, "pe6:49.2": {"pli": "Katamāyo pañca? ", "html": "{}"}, "pe6:49.3": {"pli": "Rūpaṁ attato samanupassati, yāva viññāṇaṁ attato samanupassati, imāyo pañca ucchedaṁ bhajanti, avasesāyo pannarasa sassataṁ bhajanti. ", "html": "{}"}, "pe6:49.4": {"pli": "Iti sakkāyadiṭṭhipahānā dvāsaṭṭhidiṭṭhigatāni pahīyanti. ", "html": "{}"}, "pe6:49.5": {"pli": "Pahānā ucchedaṁ sassatañca na bhajati. ", "html": "{}"}, "pe6:49.6": {"pli": "Iti ucchedasassatappahānā ariyasāvakassa na kiñci diṭṭhigataṁ bhavati, aññā vā lokuttarāya sammādiṭṭhiyā. ", "html": "{}"}, "pe6:49.7": {"pli": "Kathaṁ pana sakkāyadiṭṭhi na bhavati? ", "html": "{}"}, "pe6:49.8": {"pli": "Idha ariyasāvako sutavā hoti, sabbo sukkapakkho kātabbo, yāva ariyadhammesu kovido rūpaṁ anattato samanupassati, yāva viññāṇaṁ …pe… ", "html": "{}"}, "pe6:49.9": {"pli": "evamassa samanupassantassa sakkāyadiṭṭhi na bhavati. ", "html": "{}</p>"}, "pe6:50.1": {"pli": "Kathaṁ vicikicchā na bhavati? ", "html": "<p>{}"}, "pe6:50.2": {"pli": "Idha ariyasāvako buddhe na kaṅkhati, na vicikicchati abhippasīdati, itipi so bhagavāti sabbaṁ. ", "html": "{}"}, "pe6:50.3": {"pli": "Dhamme na kaṅkhati na vicikicchati sabbaṁ. ", "html": "{}"}, "pe6:50.4": {"pli": "Yāva taṇhakkhayo virāgo nirodho nibbānanti, iminā dutiyena ākaṅkhiyena dhammena samannāgato hoti. ", "html": "{}"}, "pe6:50.5": {"pli": "Saṅghe na kaṅkhati …pe… ", "html": "{}"}, "pe6:50.6": {"pli": "yāva pūjā devānañca manussānañcāti, iminā tatiyena ākaṅkhiyena dhammena samannāgato hoti. ", "html": "{}</p>"}, "pe6:51.1": {"pli": "Sabbe saṅkhārā dukkhāti na kaṅkhati na vicikicchati adhimuccati abhippasīdati. ", "html": "<p>{}"}, "pe6:51.2": {"pli": "Taṇhā dukkhasamudayoti na kaṅkhati na vicikicchati. ", "html": "{}"}, "pe6:51.3": {"pli": "Taṇhānirodhā dukkhanirodhoti na kaṅkhati na vicikicchati. ", "html": "{}"}, "pe6:51.4": {"pli": "Ariyo aṭṭhaṅgiko maggo dukkhanirodhagāminī paṭipadāti na kaṅkhati na vicikicchati adhimuccati abhippasīdati. ", "html": "{}"}, "pe6:51.5": {"pli": "Yāva buddhe vā dhamme vā saṅghe vā dukkhe vā samudaye vā nirodhe vā magge vā kaṅkhāyanā vimati vicikicchā dvedhāpathā āsappanā parisappanā anavaṭṭhānaṁ adhiṭṭhāgamanaṁ anekaṁso anekaṁsikatā, te tassa pahīnā bhavanti paṇunnā ucchinnamūlā tālāvatthukatā anabhāvaṅkatā āyatiṁ anuppādadhammā. ", "html": "{}</p>"}, "pe6:52.1": {"pli": "Tattha sīlabbataparāmāso dvidhā—", "html": "<p>{}"}, "pe6:52.2": {"pli": "sīlassa vā suddhassa vā. ", "html": "{}"}, "pe6:52.3": {"pli": "Tattha sīlassa sīlabbataparāmāso imināhaṁ sīlena vā vatena vā tapena vā brahmacariyena vā devo vā bhavissāmi devaññataro vā tattha kapotapādāhi accharāhi saddhiṁ kīḷissāmi ramissāmi paricarissāmīti. ", "html": "{}"}, "pe6:52.4": {"pli": "Yathābhūtadassananti rucivimutti rāgo rāgaparivattakā diṭṭhirūpanā passanā asantussitassa sīlabbataparāmāso. ", "html": "{}"}, "pe6:52.5": {"pli": "Tattha katamo suddhassa sīlabbataparāmāso? ", "html": "{}"}, "pe6:52.6": {"pli": "Idhekacco sīlaṁ parāmasati, sīlena sujjhati, sīlena nīyati, sīlena muccati, sukhaṁ vītikkamati, dukkhaṁ vītikkamati, sukhadukkhaṁ vītikkamati anupāpuṇāti uparimena. ", "html": "{}"}, "pe6:52.7": {"pli": "Tadubhayaṁ sīlavataṁ parāmasati tadubhayena sīlavatena sujjhanti muccanti nīyanti, sukhaṁ vītikkamanti, dukkhaṁ vītikkamanti, sukhadukkhaṁ vītikkamanti, anupāpuṇantīti avisucikaraṁ dhammaṁ avimuttikaraṁ dhammaṁ visucito vimuttito paccāgacchantassa yā tathābhūtassa khanti ruci mutti pekkhanā ākāraparivitakko diṭṭhinijjhāyanā passanā, ayaṁ suddhassa sīlabbataparāmāso. ", "html": "{}"}, "pe6:52.8": {"pli": "Ete ubho parāmāsā ariyasāvakassa pahīnā bhavanti yāva āyatiṁ anuppādadhammā, so sīlavā bhavati ariyakantehi sīlehi samannāgato akkhaṇḍehi yāva upasamasaṁvattanikehi. ", "html": "{}"}, "pe6:52.9": {"pli": "Imesaṁ tiṇṇaṁ saṁyojanānaṁ pahānā sutavā ariyasāvako bhavati sotāpanno avinipātadhammo, sabbaṁ. ", "html": "{}</p>"}, "pe6:53.1": {"pli": "Sahasaccābhisamayā, iti ko vacanattho? ", "html": "<p>{}"}, "pe6:53.2": {"pli": "Cattāro abhisamayā, pariññābhisamayo pahānābhisamayo sacchikiriyābhisamayo bhāvanābhisamayo. ", "html": "{}</p>"}, "pe6:54.1": {"pli": "Tattha ariyasāvako dukkhaṁ pariññābhisamayena abhisameti, samudayaṁ pahānābhisamayena abhisameti, nirodhaṁ sacchikiriyābhisamayena abhisameti, maggaṁ bhāvanābhisamayena abhisameti. ", "html": "<p>{}"}, "pe6:54.2": {"pli": "Kiṅkāraṇaṁ? ", "html": "{}"}, "pe6:54.3": {"pli": "Dukkhassa pariññābhisamayo, samudayassa pahānābhisamayo, nirodhassa sacchikiriyābhisamayo, maggassa bhāvanābhisamayo. ", "html": "{}"}, "pe6:54.4": {"pli": "Samathavipassanāya kathaṁ abhisameti? ", "html": "{}"}, "pe6:54.5": {"pli": "Ārammaṇe cittaṁ upanibandhetvā pañcakkhandhe dukkhato passati. ", "html": "{}"}, "pe6:54.6": {"pli": "Tattha yo upanibandho, ayaṁ samatho. ", "html": "{}"}, "pe6:54.7": {"pli": "Yā pariyogāhanā, ayaṁ vipassanā. ", "html": "{}"}, "pe6:54.8": {"pli": "Pañcakkhandhe dukkhāti passato yo pañcakkhandhesu ālayo nikanti upagamanaṁ ajjhosānā icchā mucchā paṇidhi patthanā pahīyati. ", "html": "{}"}, "pe6:54.9": {"pli": "Tattha pañcakkhandhā dukkhaṁ. ", "html": "{}"}, "pe6:54.10": {"pli": "Yo tattha ālayo nikanti upagamanaṁ ajjhosānaṁ icchā mucchā paṇidhi patthanā, ayaṁ samudayo. ", "html": "{}"}, "pe6:54.11": {"pli": "Yaṁ tassa pahānaṁ, so nirodho samatho vipassanā ca maggo, evaṁ tesaṁ catunnaṁ ariyasaccānaṁ ekakāle ekakkhaṇe ekacitte apubbaṁ acarimaṁ abhisamayo bhavati. ", "html": "{}"}, "pe6:54.12": {"pli": "Tenāha bhagavā “sahasaccābhisamayā ariyasāvakassa tīṇi saṁyojanāni pahīyantī”ti. ", "html": "{}</p>"}, "pe6:55.1": {"pli": "Tattha samathavipassanā yuganaddhā vattamānā ekakāle ekakkhaṇe ekacitte cattāri kiccāni karoti, dukkhaṁ pariññābhisamayena abhisameti, yāva maggaṁ bhāvanābhisamayena abhisameti. ", "html": "<p>{}"}, "pe6:55.2": {"pli": "Kiṁ kāraṇā? ", "html": "{}"}, "pe6:55.3": {"pli": "Dukkhaṁ pariññābhisamayo, yāva maggaṁ bhāvanābhisamayo. ", "html": "{}"}, "pe6:55.4": {"pli": "Evaṁ diṭṭhanto yathā nāvā jalaṁ gacchantī cattāri kiccāni karoti, pārimaṁ tīraṁ pāpeti, orimaṁ tīraṁ jahati, bhāraṁ vahati, sotaṁ chindati; ", "html": "{}"}, "pe6:55.5": {"pli": "evameva samathavipassanā yuganaddhā vattamānā ekakāle ekakkhaṇe ekacitte cattāri kiccāni karoti, dukkhaṁ pariññābhisamayena abhisameti, yāva maggaṁ bhāvanābhisamayena abhisameti. ", "html": "{}"}, "pe6:55.6": {"pli": "Yathā vā sūriyo udayanto ekakāle apubbaṁ acarimaṁ cattāri kiccāni karoti, andhakāraṁ vidhamati, ālokaṁ pātukaroti, rūpaṁ nidassīyati, sītaṁ pariyādiyati; ", "html": "{}"}, "pe6:55.7": {"pli": "evameva samathavipassanā yuganaddhā vattamānā ekakāle …pe… ", "html": "{}"}, "pe6:55.8": {"pli": "yathā padīpo jalanto ekakāle apubbaṁ acarimaṁ cattāri kiccāni karoti, andhakāraṁ vidhamati, ālokaṁ pātukaroti, rūpaṁ nidassīyati, upādānaṁ pariyādiyati; ", "html": "{}"}, "pe6:55.9": {"pli": "evameva samathavipassanā yuganaddhā vattamānā ekakāle …pe…. ", "html": "{}</p>"}, "pe6:56.1": {"pli": "Yadā ariyasāvako sotāpanno bhavati avinipātadhammo niyato yāva dukkhassantaṁ karoti, ayaṁ dassanabhūmi. ", "html": "<p>{}"}, "pe6:56.2": {"pli": "Sotāpattiphalañca sotāpattiphale ṭhito uttari samathavipassanaṁ bhāvento yuganaddhā vattamānā kāmarāgabyāpādānaṁ yebhuyyena pahānā ariyasāvako hoti. ", "html": "{}"}, "pe6:56.3": {"pli": "Sakadāgāmi pariniṭṭhitattā sakideva imaṁ lokaṁ āgantvā dukkhassantaṁ karoti, ayaṁ tanubhūmi. ", "html": "{}</p>"}, "pe6:57.1": {"pli": "Sakadāgāmiphalañca yo sakadāgāmiphale ṭhito vipassanaṁ bhāvento kāmarāgabyāpāde sānusaye anavasesaṁ pajahati, kāmarāgabyāpādesu anavasesaṁ pahīnesu pañcorambhāgiyāni saṁyojanāni pahīnāni bhavanti sakkāyadiṭṭhi sīlabbataparāmāso vicikicchā kāmacchando byāpādo ca, imesaṁ pañcannaṁ orambhāgiyānaṁ saṁyojanānaṁ pahānā ariyasāvako hoti anāgāmī tattha parinibbāyī anāvattidhammo tasmā lokā, ayaṁ vītarāgabhūmi. ", "html": "<p>{}</p>"}, "pe6:58.1": {"pli": "Anāgāmiphalañca anāgāmiphale ṭhito uttari samathavipassanaṁ bhāvento pañca uddhambhāgiyāni saṁyojanāni pajahati rūparāgaarūparāgamānauddhaccaavijjañca. ", "html": "<p>{}"}, "pe6:58.2": {"pli": "Imesaṁ pañcannaṁ uddhambhāgiyānaṁ saṁyojanānaṁ pahānā ariyasāvako arahā bhavati, khīṇāsavo vusitavā sammadaññāvimutto parikkhīṇabhavasaṁyojano anuppattasadattho, ayaṁ katābhūmi. ", "html": "{}</p>"}, "pe6:59.1": {"pli": "Arahantova ayaṁ sopādisesā nibbānadhātu. ", "html": "<p data-counter='15'>{}"}, "pe6:59.2": {"pli": "Tassa āyukkhayā jīvitindriyāparodhā idañca dukkhaṁ nirujjhati, aññañca dukkhaṁ na uppajjati. ", "html": "{}"}, "pe6:59.3": {"pli": "Yo imassa dukkhassa nirodho vūpasamo, aññassa ca apātubhāvo, ayaṁ anupādisesā nibbānadhātu. ", "html": "{}"}, "pe6:59.4": {"pli": "Imā dve nibbānadhātuyo. ", "html": "{}"}, "pe6:59.5": {"pli": "Iti saccāni vuttāni. ", "html": "{}"}, "pe6:59.6": {"pli": "Saccābhisamayo vutto, kilesavavatthānaṁ vuttaṁ, pahānaṁ vuttaṁ, bhūmiyo vuttā, phalāni vuttāni, nibbānadhātuyo vuttā. ", "html": "{}"}, "pe6:59.7": {"pli": "Evamimesu vuttesu sabbabodhi vuttā bhavati. ", "html": "{}"}, "pe6:59.8": {"pli": "Ettha yogo karaṇīyo. ", "html": "{}</p>"}, "pe6:60.1": {"pli": "Tattha katamāyo <b>nava anupubbasamāpattiyo</b>? ", "html": "<p>{}"}, "pe6:60.2": {"pli": "Cattāri jhānāni catasso ca arūpasamāpattiyo nirodhasamāpatti ca. ", "html": "{}"}, "pe6:60.3": {"pli": "Tattha cattāri jhānāni katamāni? ", "html": "{}"}, "pe6:60.4": {"pli": "Idha, bhikkhave, bhikkhu vivicceva kāmehīti vitthārena kātabbāni. ", "html": "{}"}, "pe6:60.5": {"pli": "Tattha katamā cattāro arūpasamāpattiyo? ", "html": "{}"}, "pe6:60.6": {"pli": "Virāgino vata vattabbo, yāva nirodhasamāpatti vitthārena kātabbā. ", "html": "{}"}, "pe6:60.7": {"pli": "Imāyo nava anupubbasamāpattiyo. ", "html": "{}</p>"}, "pe6:61.1": {"pli": "Tattha katamaṁ paṭhamaṁ jhānaṁ? ", "html": "<p>{}"}, "pe6:61.2": {"pli": "Pañcaṅgavippayuttaṁ pañcaṅgasamannāgataṁ. ", "html": "{}"}, "pe6:61.3": {"pli": "Katamehi pañcahi aṅgehi vippayuttaṁ? ", "html": "{}"}, "pe6:61.4": {"pli": "Pañcahi nīvaraṇehi. ", "html": "{}"}, "pe6:61.5": {"pli": "Tattha katamāni pañca nīvaraṇāni? ", "html": "{}"}, "pe6:61.6": {"pli": "Kāmacchandoti vitthāretabbo. ", "html": "{}"}, "pe6:61.7": {"pli": "Tattha katamo kāmacchando? ", "html": "{}"}, "pe6:61.8": {"pli": "Yo pañcasu kāmaguṇesu chandarāgo pemaṁ nikanti ajjhosānaṁ icchā mucchā patthanā apariccāgo anusayo pariyuṭṭhānaṁ, ayaṁ kāmacchandanīvaraṇaṁ. ", "html": "{}"}, "pe6:61.9": {"pli": "Tattha katamaṁ byāpādanīvaraṇaṁ? ", "html": "{}"}, "pe6:61.10": {"pli": "Yo sattesu saṅkhāresu ca āghāto …pe… ", "html": "{}"}, "pe6:61.11": {"pli": "yathā dose tathā nioṭṭhānā, ayaṁ byāpādo nīvaraṇaṁ. ", "html": "{}"}, "pe6:61.12": {"pli": "Tattha katamaṁ middhaṁ? ", "html": "{}"}, "pe6:61.13": {"pli": "Yā cittassa jaḷatā cittassa garuttaṁ cittassa akammaniyatā cittassa nikkhepo niddāyanā pacalikatā pacalāyanā pacalāyanaṁ, idaṁ middhaṁ. ", "html": "{}"}, "pe6:61.14": {"pli": "Tattha katamaṁ thinaṁ? ", "html": "{}"}, "pe6:61.15": {"pli": "Yā kāyassa thinatā jaḷatā kāyassa garuttā kāyassa appassaddhi, idaṁ thinaṁ. ", "html": "{}"}, "pe6:61.16": {"pli": "Iti idañca thinaṁ purimakañca middhaṁ tadubhayaṁ thinamiddhanīvaraṇanti vuccati. ", "html": "{}"}, "pe6:61.17": {"pli": "Tattha katamaṁ uddhaccaṁ? ", "html": "{}"}, "pe6:61.18": {"pli": "Yo avūpasamo cittassa, idaṁ uddhaccaṁ. ", "html": "{}"}, "pe6:61.19": {"pli": "Tattha katamaṁ kukkuccaṁ? ", "html": "{}"}, "pe6:61.20": {"pli": "Yo cetaso vilekho alañcanā vilañcanā hadayalekho vippaṭisāro, idaṁ kukkuccaṁ. ", "html": "{}"}, "pe6:61.21": {"pli": "Iti idañca kukkuccaṁ purimakañca uddhaccaṁ tadubhayaṁ uddhaccakukkuccanīvaraṇanti vuccati. ", "html": "{}"}, "pe6:61.22": {"pli": "Tattha katamaṁ vicikicchānīvaraṇaṁ? ", "html": "{}"}, "pe6:61.23": {"pli": "Yo buddhe vā dhamme vā saṅghe vā …pe… ", "html": "{}"}, "pe6:61.24": {"pli": "ayaṁ vicikicchā. ", "html": "{}"}, "pe6:61.25": {"pli": "Api ca kho pana pañca vicikicchāyo samanantarāyikā desantarāyikā samāpattantarāyikā maggantarāyikā saggantarāyikā, imāyo pañca vicikicchāyo. ", "html": "{}"}, "pe6:61.26": {"pli": "Idha pana samāpattantarāyikā vicikicchā adhippetā. ", "html": "{}"}, "pe6:61.27": {"pli": "Ime pañca nīvaraṇā. ", "html": "{}</p>"}, "pe6:62.1": {"pli": "Tattha nīvaraṇānīti ko vacanattho, kuto nivārayantīti? ", "html": "<p>{}"}, "pe6:62.2": {"pli": "Sabbato kusalapakkhikā nivārayanti. ", "html": "{}"}, "pe6:62.3": {"pli": "Kathaṁ nivārayanti? ", "html": "{}"}, "pe6:62.4": {"pli": "Kāmacchando asubhato nivārayati, byāpādo mettāya nivārayati, thinaṁ passaddhito nivārayati, middhaṁ vīriyārambhato nivārayati, uddhaccaṁ samathato nivārayati, kukkuccaṁ avippaṭisārato nivārayati, vicikicchā paññāto paṭiccasamuppādato nivārayati. ", "html": "{}</p>"}, "pe6:63.1": {"pli": "Aparo pariyāyo. ", "html": "<p>{}"}, "pe6:63.2": {"pli": "Kāmacchando alobhato kusalamūlato nivārayati, byāpādo adosato nivārayati, thinamiddhaṁ samādhito nivārayati, uddhaccakukkuccaṁ satipaṭṭhānehi nivārayati, vicikicchā amohato kusalamūlato nivārayati. ", "html": "{}</p>"}, "pe6:64.1": {"pli": "Aparo pariyāyo. ", "html": "<p>{}"}, "pe6:64.2": {"pli": "Tayo vihārā dibbavihāro brahmavihāro ariyavihāro. ", "html": "{}"}, "pe6:64.3": {"pli": "Dibbavihāro cattāri jhānāni, brahmavihāro cattāri appamāṇāni, ariyavihāro sattatiṁsa bodhipakkhiyā dhammā. ", "html": "{}"}, "pe6:64.4": {"pli": "Tattha kāmacchando uddhaccaṁ kukkuccañca dibbavihāraṁ nivārayati, byāpādo brahmavihāraṁ nivārayati, thinamiddhaṁ vicikicchā ca ariyavihāraṁ nivārayati. ", "html": "{}</p>"}, "pe6:65.1": {"pli": "Aparo pariyāyo. ", "html": "<p>{}"}, "pe6:65.2": {"pli": "Kāmacchando byāpādo uddhaccakukkuccañca samathaṁ nivārayanti, thinamiddhaṁ vicikicchā ca vipassanaṁ nivārayanti, ato nīvaraṇanti vuccante. ", "html": "{}"}, "pe6:65.3": {"pli": "Imehi pañcahi aṅgehi vippayuttaṁ paṭhamaṁ jhānaṁ. ", "html": "{}</p>"}, "pe6:66.1": {"pli": "Katamehi pañcahi aṅgehi sampayuttaṁ <b>paṭhamaṁ jhānaṁ</b>? ", "html": "<p data-counter='1'>{}"}, "pe6:66.2": {"pli": "Vitakkavicārehi pītiyā sukhena ca cittekaggatāya ca. ", "html": "{}"}, "pe6:66.3": {"pli": "Imesaṁ pañcannaṁ aṅgānaṁ uppādapaṭilābhasamannāgamo sacchikiriyaṁ paṭhamaṁ jhānaṁ paṭiladdhanti vuccati. ", "html": "{}"}, "pe6:66.4": {"pli": "Imāni pañca aṅgāni uppādetvā viharatīti, tena vuccate paṭhamaṁ jhānaṁ upasampajja viharatīti dibbena vihārena. ", "html": "{}</p>"}, "pe6:67.1": {"pli": "Tattha <b>dutiyaṁ jhānaṁ</b> caturaṅgasamannāgataṁ pītisukhena cittekaggatāya ajjhattaṁ sampasādanena imāni cattāri aṅgāni uppādetvā sampādetvā viharati, tena vuccati dutiyaṁ jhānaṁ upasampajja viharatīti. ", "html": "<p data-counter='2'>{}</p>"}, "pe6:68.1": {"pli": "Tattha pañcaṅgasamannāgataṁ <b>tatiyaṁ jhānaṁ</b> satiyā sampajaññena sukhena cittekaggatāya upekkhāya imāni pañcaṅgāni uppādetvā sampādetvā viharati, tena vuccati tatiyaṁ jhānaṁ upasampajja viharatīti. ", "html": "<p data-counter='3'>{}</p>"}, "pe6:69.1": {"pli": "Tattha <b>catutthaṁ jhānaṁ</b> caturaṅgasamannāgataṁ upekkhāya satipārisuddhiyā adukkhamasukhāya vedanāya cittekaggatā ca, imehi catūhaṅgehi samannāgataṁ catutthaṁ jhānaṁ. ", "html": "<p data-counter='4'>{}"}, "pe6:69.2": {"pli": "Iti imesaṁ catunnaṁ aṅgānaṁ uppādo paṭilābho samannāgamo sacchikiriyā catutthaṁ jhānaṁ paṭiladdhanti vuccati. ", "html": "{}"}, "pe6:69.3": {"pli": "Imāni cattāri jhānāni uppādetvā sampādetvā upasampajja viharati, tena vuccati dibbena vihārena viharatīti. ", "html": "{}</p>"}, "pe6:70.1": {"pli": "Tattha katamo aniccaṭṭho? ", "html": "<p>{}"}, "pe6:70.2": {"pli": "Pīḷanaṭṭho aniccaṭṭho pabhaṅgaṭṭho sampāpanaṭṭho vivekaṭṭho aniccaṭṭho, ayaṁ aniccaṭṭho. ", "html": "{}</p>"}, "pe6:71.1": {"pli": "Tattha katamo dukkhaṭṭho? ", "html": "<p>{}"}, "pe6:71.2": {"pli": "Pīḷanaṭṭho dukkhaṭṭho sampīḷanaṭṭho saṁvegaṭṭho byādhinaṭṭho, ayaṁ dukkhaṭṭho. ", "html": "{}</p>"}, "pe6:72.1": {"pli": "Tattha katamo suññaṭṭho? ", "html": "<p>{}"}, "pe6:72.2": {"pli": "Anupalitto suññaṭṭho, asambhajanaṭṭho gatapaṭṭho vivaṭṭaṭṭho, ayaṁ suññaṭṭho. ", "html": "{}</p>"}, "pe6:73.1": {"pli": "Tattha katamo anattaṭṭho? ", "html": "<p>{}"}, "pe6:73.2": {"pli": "Anissariyaṭṭho anattaṭṭho, avasavattanaṭṭho, akāmakāriṭṭho parividaṭṭho, ayaṁ anattaṭṭhoti. ", "html": "{}</p>"}, "pe6:74.1": {"pli": "Suttatthasamuccayo nāma saṁvattisantikā peṭakabhūmi samattā. ", "html": "<p class='endsutta'>{}</p></article>"}}, "pe7": {"pe7:0.1": {"pli": "Peṭakopadesa ", "html": "<article id='pe7'><header><ul><li class='division'>{}</li></ul>"}, "pe7:0.2": {"pli": "Pakiṇṇakaniddesa ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "pe7:1.1": {"pli": "Jhānaṁ virāgo. ", "html": "<p>{}"}, "pe7:1.2": {"pli": "Cattāri jhānāni vitthārena kātabbāni. ", "html": "{}"}, "pe7:1.3": {"pli": "Tāni duvidhāni; ", "html": "{}"}, "pe7:1.4": {"pli": "bojjhaṅgavippayuttāni ca bojjhaṅgasampayuttāni ca. ", "html": "{}"}, "pe7:1.5": {"pli": "Tattha bojjhaṅgavippayuttāni bāhirakāni, bojjhaṅgasampayuttāni ariyapuggalāni. ", "html": "{}"}, "pe7:1.6": {"pli": "Tattha yena cha puggalamūlāni tesaṁ nikkhipetvā rāgacarito, dosacarito, mohacarito, rāgadosacarito, rāgamohacarito, dosamohacarito, samabhāgacarito, iti imesaṁ puggalānaṁ jhānaṁ samāpajjitānaṁ pañca nīvaraṇāni paṭipakkho tesaṁ paṭighātāya yathā asamattho tīṇi akusalamūlāni niggaṇhāti. ", "html": "{}"}, "pe7:1.7": {"pli": "Lobhena akusalamūlena abhijjhā ca uddhaccañca uppilavataṁ alobhena kusalamūlena niggaṇhāti, kukkuccañca vicikicchā ca mohapakkho, taṁ amohena niggaṇhāti. ", "html": "{}"}, "pe7:1.8": {"pli": "Doso ca thinamiddhañca dosapakkho, taṁ adosena niggaṇhāti. ", "html": "{}</p>"}, "pe7:2.1": {"pli": "Tattha alobhassa pāripūriyā nekkhammavitakkaṁ vitakketi. ", "html": "<p>{}"}, "pe7:2.2": {"pli": "Tattha adosassa pāripūriyā abyāpādavitakkaṁ vitakketi. ", "html": "{}"}, "pe7:2.3": {"pli": "Tattha amohassa pāripūriyā avihiṁsāvitakkaṁ vitakketi. ", "html": "{}"}, "pe7:2.4": {"pli": "Tattha alobhassa pāripūriyā vivitto hoti kāmehi. ", "html": "{}"}, "pe7:2.5": {"pli": "Tattha adosassa pāripūriyā amohassa pāripūriyā ca vivitto hoti pāpakehi akusalehi dhammehi, savitakkaṁ savicāraṁ vivekajaṁ pītisukhaṁ paṭhamaṁ jhānaṁ upasampajja viharati. ", "html": "{}</p>"}, "pe7:3.1": {"pli": "Vitakkāti tayo vitakkā—", "html": "<p>{}"}, "pe7:3.2": {"pli": "nekkhammavitakko abyāpādavitakko avihiṁsāvitakko. ", "html": "{}"}, "pe7:3.3": {"pli": "Tattha paṭhamābhinipāto vitakko, paṭiladdhassa vicaraṇaṁ vicāro. ", "html": "{}"}, "pe7:3.4": {"pli": "Yathā puriso dūrato purisaṁ passati āgacchantaṁ, na ca tāva jānāti eso itthīti vā purisoti vā yadā tu paṭilabhati itthīti vā purisoti vā evaṁ vaṇṇoti vā evaṁ saṇṭhānoti vā ime vitakkayanto uttari upaparikkhanti kiṁ nu kho ayaṁ sīlavā udāhu dussīlo aḍḍho vā duggatoti vā. ", "html": "{}"}, "pe7:3.5": {"pli": "Evaṁ vicāro vitakke appeti, vicāro cariyati ca anuvattati ca. ", "html": "{}"}, "pe7:3.6": {"pli": "Yathā pakkhī pubbaṁ āyūhati pacchā nāyūhati yathā āyūhanā evaṁ vitakko, yathā pakkhānaṁ pasāraṇaṁ evaṁ vicāro anupālati vitakketi vicarati vicāreti. ", "html": "{}"}, "pe7:3.7": {"pli": "Vitakkayati vitakketi, anuvicarati vicāreti. ", "html": "{}"}, "pe7:3.8": {"pli": "Kāmasaññāya paṭipakkho vitakko, byāpādasaññāya vihiṁsasaññāya ca paṭipakkho vicāro. ", "html": "{}"}, "pe7:3.9": {"pli": "Vitakkānaṁ kammaṁ akusalassa amanasikāro, vicārānaṁ kammaṁ jeṭṭhānaṁ saṁvāraṇā. ", "html": "{}"}, "pe7:3.10": {"pli": "Yathā paliko tuṇhiko sajjhāyaṁ karoti evaṁ vitakko, yathā taṁyeva anupassati evaṁ vicāro. ", "html": "{}"}, "pe7:3.11": {"pli": "Yathā apariññā evaṁ vitakko. ", "html": "{}"}, "pe7:3.12": {"pli": "Yathā pariññā evaṁ vicāro. ", "html": "{}"}, "pe7:3.13": {"pli": "Niruttipaṭisambhidāyañca paṭibhānapaṭisambhidāyañca vitakko, dhammapaṭisambhidāyañca atthapaṭisambhidāyañca vicāro. ", "html": "{}"}, "pe7:3.14": {"pli": "Kallitā kosallattaṁ cittassa vitakko, abhinīhārakosallaṁ cittassa vicāro. ", "html": "{}"}, "pe7:3.15": {"pli": "Idaṁ kusalaṁ idaṁ akusalaṁ idaṁ bhāvetabbaṁ idaṁ pahātabbaṁ idaṁ sacchikātabbanti vitakko, yathā pahānañca bhāvanā ca sacchikiriyā ca evaṁ vicāro. ", "html": "{}"}, "pe7:3.16": {"pli": "Imesu vitakkavicāresu ṭhitassa duvidhaṁ dukkhaṁ na uppajjati kāyikañca cetasikañca; ", "html": "{}"}, "pe7:3.17": {"pli": "duvidhaṁ sukhaṁ uppajjati kāyikañca cetasikañca. ", "html": "{}"}, "pe7:3.18": {"pli": "Iti vitakkajanitaṁ cetasikaṁ sukhaṁ pīti kāyikaṁ sukhaṁ kāyikoyeva. ", "html": "{}"}, "pe7:3.19": {"pli": "Yā tattha cittassa ekaggatā, ayaṁ samādhi. ", "html": "{}"}, "pe7:3.20": {"pli": "Iti paṭhamaṁ jhānaṁ pañcaṅgavippahīnaṁ pañcaṅgasamannāgataṁ. ", "html": "{}</p>"}, "pe7:4.1": {"pli": "Tesaṁyeva vitakkavicārānaṁ abhikkhaṇaṁ āsevanāya tassa tappoṇamānasaṁ hoti. ", "html": "<p>{}"}, "pe7:4.2": {"pli": "Tassa vitakkavicārā oḷārikā khāyanti. ", "html": "{}"}, "pe7:4.3": {"pli": "Yañca pītisukhañca nekkhammañca oḷārikaṁ bhavati. ", "html": "{}"}, "pe7:4.4": {"pli": "Api ca samādhijā pīti rati ca jāyati. ", "html": "{}"}, "pe7:4.5": {"pli": "Tassa vicārārammaṇaṁ. ", "html": "{}"}, "pe7:4.6": {"pli": "Tesaṁ vūpasamā ajjhattaṁ ceto sampasīdati. ", "html": "{}"}, "pe7:4.7": {"pli": "Ye vitakkavicārā dve dhammānussaritabbā. ", "html": "{}"}, "pe7:4.8": {"pli": "Paccuppannā daraṇitabbaṁ. ", "html": "{}"}, "pe7:4.9": {"pli": "Tesaṁ vūpasamā ekodibhāvaṁ cittekaggataṁ hoti. ", "html": "{}"}, "pe7:4.10": {"pli": "Tassa ekodibhāvena pīti pāripūriṁ gacchati. ", "html": "{}"}, "pe7:4.11": {"pli": "Yā pīti, taṁ somanassindriyaṁ, yaṁ sukhaṁ, taṁ sukhindriyaṁ. ", "html": "{}"}, "pe7:4.12": {"pli": "Yā cittekaggatā, ayaṁ samādhi. ", "html": "{}"}, "pe7:4.13": {"pli": "Taṁ dutiyaṁ jhānaṁ caturaṅgasamannāgataṁ. ", "html": "{}"}, "pe7:4.14": {"pli": "So pītiyā virāgā yāti ojahi jallasahagataṁ. ", "html": "{}</p>"}, "pe7:5.1": {"pli": "Tattha somanassacittamupādānanti ca so taṁ vicinanto upekkhameva manasikaroti. ", "html": "<p>{}"}, "pe7:5.2": {"pli": "So pītiyā virāgā upekkhako viharati. ", "html": "{}"}, "pe7:5.3": {"pli": "Yathā ca pītiyā sukhamānitaṁ, taṁ kāyena paṭisaṁvedeti sampajāno viharati. ", "html": "{}"}, "pe7:5.4": {"pli": "Yena satisampajaññena upekkhāpāripūriṁ gacchati. ", "html": "{}"}, "pe7:5.5": {"pli": "Idaṁ tatiyaṁ jhānaṁ caturaṅgasamannāgataṁ. ", "html": "{}</p>"}, "pe7:6.1": {"pli": "Tathā kāyikassa sukhassa pahānāya paṭhame jhāne somanassindriyaṁ nirujjhati. ", "html": "<p>{}"}, "pe7:6.2": {"pli": "Dutiye jhāne dukkhindriyaṁ nirujjhati. ", "html": "{}"}, "pe7:6.3": {"pli": "So sukhassa ca pahānā dukkhassa ca pahānā pubbeva somanassadomanassānaṁ atthaṅgamā adukkhamasukhaṁ upekkhāsatipārisuddhiṁ catutthaṁ jhānaṁ upasampajja viharati. ", "html": "{}"}, "pe7:6.4": {"pli": "Tattha catūhi indriyehi upekkhā pasādā hoti, dukkhindriyena domanassindriyena sukhindriyena somanassindriyena ca. ", "html": "{}"}, "pe7:6.5": {"pli": "Tesaṁ nirodhā upekkhāsampajaññaṁ hoti, tattha sukhindriyena somanassindriyena ca asati hoti, tesaṁ nirodhā satimā hoti, dukkhindriyena domanassindriyena ca asampajaññaṁ, tesaṁ nirodhā sampajaññaṁ hoti, iti upekkhāya ca saññā, sato sampajāno cittekaggatā ca idaṁ vuccate ca catutthaṁ jhānaṁ. ", "html": "{}</p>"}, "pe7:7.1": {"pli": "Tattha yo rāgacarito puggalo tassa sukhindriyañca somanassindriyañca; ", "html": "<p>{}"}, "pe7:7.2": {"pli": "yo dosacarito puggalo tassa dukkhindriyañca domanassindriyañca; ", "html": "{}"}, "pe7:7.3": {"pli": "yo mohacarito puggalo tassa asati ca asampajaññañca. ", "html": "{}</p>"}, "pe7:8.1": {"pli": "Tattha rāgacaritassa puggalassa tatiye jhāne catutthe ca anunayo nirujjhati, dosacaritassa paṭhame jhāne dutiye ca paṭighaṁ nirujjhati, mohacaritassa puggalassa paṭhame jhāne dutiye ca asampajaññaṁ nirujjhati. ", "html": "<p>{}"}, "pe7:8.2": {"pli": "Tatiye jhāne catutthe ca asati nirujjhati, evameva tesaṁ tiṇṇaṁ puggalānaṁ cattāri jhānāni vodānaṁ gamissanti. ", "html": "{}</p>"}, "pe7:9.1": {"pli": "Tattha rāgadosacaritassa puggalassa asampajaññañca anunayo ca paṭighañca, tena hānabhāgiyaṁ jhānaṁ hoti. ", "html": "<p>{}"}, "pe7:9.2": {"pli": "Tattha rāgamohacaritassa puggalassa anunayattaṁ ca ādīnavaṁ dassitā, taṁ tassa hānabhāgiyaṁ jhānaṁ hoti. ", "html": "{}"}, "pe7:9.3": {"pli": "Tattha dosamohacaritassa puggalassa paṭigho ca asati ca asampajaññañca ādīnavaṁ dassitā tena tassa hānabhāgiyaṁ jhānaṁ hoti. ", "html": "{}</p>"}, "pe7:10.1": {"pli": "Tattha rāgadosamohasamabhāgacaritassa puggalassa visesabhāgiyaṁ jhānaṁ hoti, imāni cattāri jhānāni sattasu puggalesu niddisitabbāni. ", "html": "<p>{}"}, "pe7:10.2": {"pli": "Catūsu ca samādhīsu chandasamādhinā paṭhamaṁ jhānaṁ, vīriyasamādhinā dutiyaṁ jhānaṁ, cittasamādhinā tatiyaṁ jhānaṁ, vīmaṁsāsamādhinā catutthaṁ jhānaṁ. ", "html": "{}"}, "pe7:10.3": {"pli": "Appaṇihitena paṭhamaṁ jhānaṁ, suññatāya dutiyaṁ jhānaṁ, animittena tatiyaṁ jhānaṁ, ānāpānassatiyā catutthaṁ jhānaṁ. ", "html": "{}"}, "pe7:10.4": {"pli": "Kāmavitakkabyāpādānañca taṁ taṁ vūpasamena paṭhamaṁ jhānaṁ hoti, vitakkavicārānaṁ vūpasamena dutiyaṁ jhānaṁ, sukhindriyasomanassindriyānaṁ vūpasamena tatiyaṁ jhānaṁ, kāyasaṅkhārānaṁ vūpasamena catutthaṁ jhānañca. ", "html": "{}"}, "pe7:10.5": {"pli": "Cāgādhiṭṭhānena paṭhamaṁ jhānaṁ, saccādhiṭṭhānena dutiyaṁ jhānaṁ, paññādhiṭṭhānena tatiyaṁ jhānaṁ, upasamādhiṭṭhānena catutthaṁ jhānaṁ. ", "html": "{}"}, "pe7:10.6": {"pli": "Imāni cattāri jhānāni saṅkhepaniddesena niddiṭṭhāni, tattha samādhindriyaṁ pāripūriṁ gacchati. ", "html": "{}"}, "pe7:10.7": {"pli": "Anuvattanakāni cattāri, tattha yo paṭhamaṁ jhānaṁ nissāya āsavakkhayaṁ pāpuṇāti, so sukhāya paṭipadāya dandhābhiññāya domanassindriyapaṭipakkhena. ", "html": "{}"}, "pe7:10.8": {"pli": "Yo dutiyaṁ jhānaṁ nissāya āsavānaṁ khayaṁ pāpuṇāti, so sukhāya paṭipadāya khippābhiññāya dukkhindriyapaṭipakkhena. ", "html": "{}"}, "pe7:10.9": {"pli": "Yo tatiyaṁ jhānaṁ nissāya āsavānaṁ khayaṁ pāpuṇāti, so sukhāya paṭipadāya dandhābhiññāya somanassindriyapaṭipakkhena. ", "html": "{}"}, "pe7:10.10": {"pli": "Yo catutthaṁ jhānaṁ nissāya āsavānaṁ khayaṁ pāpuṇāti, so sukhāya paṭipadāya khippābhiññāya sukhindriyapaṭipakkhena gato. ", "html": "{}</p>"}, "pe7:11.1": {"pli": "Pakiṇṇakaniddeso. ", "html": "<p class='endsutta'>{}</p></article>"}}, "pe8": {"pe8:0.1": {"pli": "Peṭakopadesa ", "html": "<article id='pe8'><header><ul><li class='division'>{}</li></ul>"}, "pe8:0.2": {"pli": "Hārasampātabhūmi ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "pe8:1.1": {"pli": "Yāni cattāri jhānāni, tesaṁ jhānānaṁ imāni aṅgāni, tesaṁ aṅgānaṁ samūho assa aṅgā, ayaṁ jhānabhūmi ko visesoti assa viseso. ", "html": "<p>{}"}, "pe8:1.2": {"pli": "Ime sambhārā tehi ayaṁ samudāgamo, tassa samudāgamassa ayaṁ upanisā, tāya upanisāya ayaṁ bhāvanā. ", "html": "{}"}, "pe8:1.3": {"pli": "Tassā bhāvanāya ayaṁ ādīnavo. ", "html": "{}"}, "pe8:1.4": {"pli": "Tena ayaṁ parihāni. ", "html": "{}"}, "pe8:1.5": {"pli": "Kassa parihānīti tadupagajjhāyino. ", "html": "{}"}, "pe8:1.6": {"pli": "Taṁ yathā bhaṇitaṁ paccavekkhanto ayaṁ viseso. ", "html": "{}"}, "pe8:1.7": {"pli": "Tena visesena ayaṁ assādo, so kassa assādo ajhāniyā jhāyino, tassā ajhāniyā jhāyino, idaṁ kallitā kosalle ṭhitajjhānaṁ anomaddiyataṁ gacchati jhānabalaṁ, jhānabale ṭhitassa ayaṁ pāramippattassa imāni jhānaṅgāni anāvilasaṅkappo paṭhame jhāne jhānaṅgāni bhāvī. ", "html": "{}"}, "pe8:1.8": {"pli": "So pīti tadanusārittāva paṭhame jhāne jhānaṅgaṁ tassaṅguno ca dhammā tadabhisannitāya ca. ", "html": "{}"}, "pe8:1.9": {"pli": "Pīti dutiye jhāne jhānaṅgadhammatā kho pana tathā pavattassa sahagataṁ jhānaṅgadhammaṁ sasukhatāya ajjhattaṁ sampasādo dutiye jhāne jhānaṅgaṁ manosampasādanatāya tadabhisannitāya ca. ", "html": "{}"}, "pe8:1.10": {"pli": "Pīti dutiye jhāne jhānaṅgaṁ ajjhattaṁ sampasādanaṁ samādhitā pīti dutiye jhāne jhānaṅgaṁ, cetaso ekodibhāvo dutiye jhāne jhānaṅgaṁ, upekkhā phassatā tatiye jhāne jhānaṅgaṁ, sukhaṁ tassa aṅganti ca. ", "html": "{}"}, "pe8:1.11": {"pli": "Cetaso ekodibhāvo catutthe jhāne jhānaṅgaṁ, upekkhā adukkhamasukhā catutthe jhāne jhānaṅgaṁ, abhinisābhūmi upekkhāsatipārisuddhi catutthe jhāne jhānaṅgaṁ. ", "html": "{}"}, "pe8:1.12": {"pli": "Satipārisuddhi ca anekajjhānabhūmīsu jhānaṅgasamāyuttā pīti cetaso ekodibhāvo catutthe jhāne jhānaṅgaṁ. ", "html": "{}</p>"}, "pe8:2.1": {"pli": "Tattha katamā jhānabhūmi? ", "html": "<p>{}"}, "pe8:2.2": {"pli": "Savitakke savicāre vivekā anugatā paṭhame jhāne jhānabhūmi. ", "html": "{}"}, "pe8:2.3": {"pli": "Avitakke avicāre ajjhattaṁ sampasādanaṁ janitaṁ pītimanugatā dutiye jhāne jhānabhūmi. ", "html": "{}"}, "pe8:2.4": {"pli": "Sukhasātasamohitā sappītikā tatiye jhāne jhānabhūmi. ", "html": "{}"}, "pe8:2.5": {"pli": "Tassa sukhadukkhasahagatā abhinīhārasahagatā catutthe jhāne jhānabhūmi. ", "html": "{}"}, "pe8:2.6": {"pli": "Appamāṇasahagatā sattārammaṇā paṭhame jhāne jhānabhūmi. ", "html": "{}"}, "pe8:2.7": {"pli": "Abhibhūmiāyatanasahagatā rūpasaññīsu dutiye jhāne jhānabhūmi. ", "html": "{}"}, "pe8:2.8": {"pli": "Vimokkhasahagatānaṁ vimokkhesu tatiye jhāne jhānabhūmi. ", "html": "{}"}, "pe8:2.9": {"pli": "Anupassanāsahagatā kāyasaṅkhārā sammā catutthassa jhānassa bhūmi. ", "html": "{}</p>"}, "pe8:3.1": {"pli": "Tattha katame jhānavisesā? ", "html": "<p>{}"}, "pe8:3.2": {"pli": "Vivicceva kāmehi vivicca pāpakehi akusalehi dhammehi cittacetasikasahagatā kāmadhātusamatikkamanatāpi, ayaṁ jhānaviseso. ", "html": "{}"}, "pe8:3.3": {"pli": "Avitakkā ceva avicārā ca sappītikāya satisahagatāya pītisahagatā saññāmanasikārā samudācaranti. ", "html": "{}"}, "pe8:3.4": {"pli": "Ayaṁ jhānaviseso. ", "html": "{}"}, "pe8:3.5": {"pli": "Avitakkāya bhūmiyā avicāreyeva sati anugatā upekkhāsahagatā manasikārā samudācaranti. ", "html": "{}"}, "pe8:3.6": {"pli": "Tadanudhammatāya ca sati saṇḍahati. ", "html": "{}"}, "pe8:3.7": {"pli": "Tañca bhūmiṁ upasampajja viharati, ayaṁ jhānaviseso. ", "html": "{}"}, "pe8:3.8": {"pli": "Satipārisuddhisahagatā saññāmanasikārā samudācaranti, tañca bhūmiṁ upasampajja viharati, ayaṁ jhānaviseso. ", "html": "{}"}, "pe8:3.9": {"pli": "Viññāṇañcāyatanasahagatāya bhūmiyaṁ ākiñcaññāyatanasahagatā saññāmanasikārā samudācaranti, tañca bhūmiṁ upasampajja viharati, ayaṁ jhānaviseso. ", "html": "{}</p>"}, "pe8:4.1": {"pli": "Jhānasambhārā nekkhammavitakko sambhāro kāmavitakkavinodanādhippāyatā. ", "html": "<p>{}"}, "pe8:4.2": {"pli": "Abyāpādavitakko sambhāro byāpādavitakkapaṭivinodanādhippāyatā. ", "html": "{}"}, "pe8:4.3": {"pli": "Avihiṁsāvitakko sambhāro vihiṁsāvitakkapaṭivinodanādhippāyatā. ", "html": "{}"}, "pe8:4.4": {"pli": "Indriyesu guttadvāratā appicchatā sambhāro parisuddhājīvo catunnaṁ samāpattīnaṁ sambhāro akammassa vihāritā. ", "html": "{}"}, "pe8:4.5": {"pli": "Maggasambhāro samāpattipajjanatā. ", "html": "{}"}, "pe8:4.6": {"pli": "Phalasambhāro jhānanibbattitāya jhānasamudāgamo. ", "html": "{}"}, "pe8:4.7": {"pli": "Kusalahetu yaṁ jhānaṁ samudayaṁ gacchanti ko ca na kutoci nekkhammappattā samudāgacchanti. ", "html": "{}"}, "pe8:4.8": {"pli": "Ālambanirodhasamādhi santo samudāgacchanti. ", "html": "{}"}, "pe8:4.9": {"pli": "Avītikkantā samudāgacchanti. ", "html": "{}"}, "pe8:4.10": {"pli": "Sukhindriyaṁ somanassindriyaṁ pahānāya te ca abyāpajjatāya samudāgacchanti. ", "html": "{}"}, "pe8:4.11": {"pli": "Taṁ pana sandhāya samudāgacchanti. ", "html": "{}"}, "pe8:4.12": {"pli": "Aparidāhanāya samudāgacchanti. ", "html": "{}"}, "pe8:4.13": {"pli": "Ayaṁ ñāṇasamudāgamo. ", "html": "{}</p>"}, "pe8:5.1": {"pli": "Tattha katamā upanisā? ", "html": "<p>{}"}, "pe8:5.2": {"pli": "Kalyāṇamittatā jhānassa upanisā. ", "html": "{}"}, "pe8:5.3": {"pli": "Kalyāṇasampavaṅkatā jhānassa upanisā. ", "html": "{}"}, "pe8:5.4": {"pli": "Indriyesu guttadvāratā jhānassa upanisā. ", "html": "{}"}, "pe8:5.5": {"pli": "Asantuṭṭhitā kusalesu dhammesu jhānassa upanisā. ", "html": "{}"}, "pe8:5.6": {"pli": "Saddhammassavanaṁ jhānassa upanisā. ", "html": "{}"}, "pe8:5.7": {"pli": "Saṁvejaniye ṭhāne saṁviggassa yoniso padhānaṁ. ", "html": "{}"}, "pe8:5.8": {"pli": "Ayaṁ jhānopanisā. ", "html": "{}</p>"}, "pe8:6.1": {"pli": "Tattha katamā bhāvanā? ", "html": "<p>{}"}, "pe8:6.2": {"pli": "Mettāsevanā abyāpādavitakkabhāvanā. ", "html": "{}"}, "pe8:6.3": {"pli": "Karuṇāsevanā avihiṁsāvitakkabhāvanā. ", "html": "{}"}, "pe8:6.4": {"pli": "Muditābhāvanā pītisukhasampajaññā kāritā. ", "html": "{}"}, "pe8:6.5": {"pli": "Upekkhābhāvanā passavatā upekkhābhāvanā apassavatā upekkhā ca ajjhupekkhā ca, asubhasaññābhāvanā dukkhāpaṭipadā dandhābhiññā bhavasandhābhiññā bhavasandhānaṁ, sā chabbidhā bhāvanā bhāvitā bahulīkatā anuṭṭhitā vatthukatā yānīkatā paricitā susamāraddhā. ", "html": "{}"}, "pe8:6.6": {"pli": "Ayaṁ bhāvanā. ", "html": "{}</p>"}, "pe8:7.1": {"pli": "Evaṁ bhāvayantassa ayaṁ ādīnavo. ", "html": "<p>{}"}, "pe8:7.2": {"pli": "Paṭhame jhāne saṅkhārasamannāgato eso dhammo assuto sāsavo. ", "html": "{}"}, "pe8:7.3": {"pli": "Sace esa dhammo ayaṁ sīlo āsannapaṭipakkho ca esa dhammo kāmo paticāro pativicāro samāpattīnaṁ ca sabboḷāriko esa dhammo vitakkavicāro ca. ", "html": "{}"}, "pe8:7.4": {"pli": "Tattha cittaṁ khobhenti, kāyo cettha kilamati, kāyamhi cettha kilante cittaṁ vihaññati. ", "html": "{}"}, "pe8:7.5": {"pli": "Anabhinīhārakkhamova abhiññānaṁ ime ādīnavā paṭhame jhāne. ", "html": "{}</p>"}, "pe8:8.1": {"pli": "Dutiye jhāne ime ādīnavā pītipharaṇasahagato ca eso dhammo, na samudācārasseti cittaṁ. ", "html": "<p>{}"}, "pe8:8.2": {"pli": "Asodhayaṁ upagamo cesa dhammo upagamiparissayo domanassapaccatthiko cesa dhammo. ", "html": "{}"}, "pe8:8.3": {"pli": "Tattha tattha yuttīnaṁ pīti parajjato cesa dhammo dukkaraṁ hoti, avattasantāsabhūmiparivajjayanto catūsu dukkhatāsu esa dhammo anuviddhāpanasaddhāya dukkhatāya ca na palibodhadukkhatāya ca abhiññādukkhatāya ca rogadukkhatāya ca, ime ādīnavā dutiye jhāne. ", "html": "{}</p>"}, "pe8:9.1": {"pli": "Tattha katame ādīnavā tatiye jhāne? ", "html": "<p>{}"}, "pe8:9.2": {"pli": "Upekkhāsukhasahagatāya tattha sātāvīnaṁ pañcannaṁ upekkhāsukhaṁ parivattito esa dhammo tena niccasaññitānañca yaṁ hoti. ", "html": "{}"}, "pe8:9.3": {"pli": "Dukkhopaniyaṁ sukhaṁ cittassa saṅkhobhataṁ upādāya sukhadukkhāya gato savati. ", "html": "{}"}, "pe8:9.4": {"pli": "Sukhadukkhānukatañca upādāya anabhihārakkhamaṁ cittaṁ hoti. ", "html": "{}"}, "pe8:9.5": {"pli": "Abhiññāya sacchikiriyāsu sabbepi cete dhammā tīsu jhānasamāpattīsu catūhi ca dukkhatāhi anuviddhānaṁ sā bhayā dukkhatāya palibodhadukkhatāya ca abhiññāya dukkhatāya ca ime ādīnavā tatiye jhāne. ", "html": "{}</p>"}, "pe8:10.1": {"pli": "Tattha katame ādīnavā catutthe jhāne? ", "html": "<p>{}"}, "pe8:10.2": {"pli": "Ākiñcaññāsamāpattikā te dhammānusamāpattikā etissā ca bhūmiyaṁ sātānaṁ bālaputhujjanānaṁ anekavidhāni diṭṭhigatāni uppajjanti. ", "html": "{}"}, "pe8:10.3": {"pli": "Oḷārikā sukhumehi ca rūpasaññāhi anuvidhāni etāni jhānāni sadā anudayamettājhānakalānudanukalāya sādhāraṇā, dukkarā ca sabbe cattāro mahāsambhārā samudāgatāni ca etāni jhānāni aññamaññaṁ nissāya samudāgacchanti. ", "html": "{}"}, "pe8:10.4": {"pli": "Ettha samudāgatā ca ete dhammā na samattā honti. ", "html": "{}"}, "pe8:10.5": {"pli": "Asamuggahitanimittā ca ete dhammā parihāyanti. ", "html": "{}"}, "pe8:10.6": {"pli": "Nirujjhanti ca ete dhammā na upādiyanti nirujjhaṅgāni ca, etesaṁ dhammānaṁ jhānāni nimittāni na jhānanimittasaññā vokirati. ", "html": "{}"}, "pe8:10.7": {"pli": "Appaṭiladdhapubbā ca jhāyīvasena ca bhavati. ", "html": "{}"}, "pe8:10.8": {"pli": "Imehi ādīnavehi ayaṁ jhānaparihāni. ", "html": "{}</p>"}, "pe8:11.1": {"pli": "Nirodhasamāpattiyā apaṭisaṅkhāya avasesasaññino ākiñcaññāyatanasahagatā saññāmanasikārā samudācaranti, so nirodhasamāpattito parihāyati. ", "html": "<p>{}"}, "pe8:11.2": {"pli": "Āneñjasaññino asaññāyatanaṁ samāpannassa ākiñcaññāyatanasahagatā manasikārā samudācaranti, tañca bhūmiṁ na pajānāti, so tato parihāyati. ", "html": "{}"}, "pe8:11.3": {"pli": "Ākiñcaññāyatanaṁ samāpannassa viññāṇañcāyatanasaññā manasikārā samudācaranti, tañca bhūmiṁ na pajānāti, so tato parihāyati. ", "html": "{}"}, "pe8:11.4": {"pli": "Viññāṇañcāyatanaṁ samāpannassa rūpasaññāsahagatā. ", "html": "{}"}, "pe8:11.5": {"pli": "Vitthārena …pe… ", "html": "{}"}, "pe8:11.6": {"pli": "yāva paṭhame jhāne kāmasaññāsahagatā kātabbā. ", "html": "{}"}, "pe8:11.7": {"pli": "Sakassa parihāyati, kalaṅkajjhāne kalaṅkaṁ jhāyati, parisamantato jhāyati, bhindanto jhāyati, na sajjhāyati, āyūhanto jhāyati, kiñci ca niparicito jhāyati. ", "html": "{}"}, "pe8:11.8": {"pli": "Atividhāvanto jhāyati, atimaññanto jhāyati, kāyasaṅkhāre appaṭisambhāre jhāyati, pariyuṭṭhānassa nissaraṇaṁ ajānanto jhāyati, nīvaraṇābhibhūto jhāyati, assāpattimanasikaronto jhānassa assādo kāmarāgapariyuṭṭhānaṁ pahānaṁ jhānassa assādo kāmarāgahetūnaṁ dhammānaṁ udayanti, nirujjhaṅgāni etesaṁ dhammānaṁ jhānāni uparimā sukhupekkhā kāmakammakilesānaṁ pahānaṁ assādo, evaṁ kho puna jhānassa assādo mahāsaṁvāsamappīḷite lokasannivāse asambodhokāsā vigamessamidaṁ jhānappahānā. ", "html": "{}"}, "pe8:11.9": {"pli": "Ayaṁ palirodhamappalirodhalokasannivāse esanidhamidaṁ jhānaṁ anamataggasaṁsārasamāpannānaṁ sattānaṁ saṁsārappahānanā ānisaṁso, yamidaṁ jhānassa assādo kāyassa ajhāniyajhāyino bhavati. ", "html": "{}"}, "pe8:11.10": {"pli": "Ajhāniyajhāniyajhāyīhi aparāmasanto ajhāniyajhāyitaṁ jhāyati, yāni kalaṅkajjhāyino padāni, tāni anudhitāni paṭipakkhe. ", "html": "{}</p>"}, "pe8:12.1": {"pli": "Tattha katamaṁ jhānakosallaṁ? ", "html": "<p>{}"}, "pe8:12.2": {"pli": "Samāpattikosallaṁ jhānakosallaṁ, jhānavisesakosallaṁ jhānakosallaṁ, jhānantarikakosallaṁ jhānakosallaṁ, samāpattivuṭṭhānakosallaṁ jhānakosallaṁ, jhāne sabhāvakosallaṁ jhānakosallaṁ, jhāne ādīnavakosallaṁ jhānakosallaṁ, jhāne nissaraṇakosallaṁ jhānakosallaṁ, jhānaphalena upādāya kosallaṁ, jhānaphalena paṭisaṅkhānaphale aparihānadhammatā nibbattijhāne ca kīḷitāpi visesabhāgiyaṁ jhānaṁ paṭilabbhati. ", "html": "{}"}, "pe8:12.3": {"pli": "Idaṁ panassāti bhavahāritā ca ārammaṇānimittaggāho anabhinīhārabalaṁ, cittekaggatā nimittāsu gatisahitā samathabalena asaṁsīdanañca jhāne maggaphalaṁ samathaṁ pavatte samādhino upekkhāpalipubbāparanimittāsayo paggāhino satibalaṁ taṁ pavattitānañca vipassanānaṁ samaññābale. ", "html": "{}</p>"}, "pe8:12.4": {"pli": "Tattha katamā jhānapāramitā? ", "html": "<p>{}"}, "pe8:13.1": {"pli": "Supāramitā mettā kāmesu sattā kāmasaṅgasattāti yamhi sutte desanāya vohārena dve saccāni niddiṭṭhāni, dukkhañca samudayo ca, vicayena hārena ye saṁyojanīyesu dhammesu vajjaṁ na passanti, te oghaṁ tarissantīti netaṁ ṭhānaṁ vijjati. ", "html": "{}"}, "pe8:13.2": {"pli": "Na tarissantīti atthi esā yutti ca vicayo ca idaṁ nu kissa padaṭṭhānaṁ, kāmesu sattāti pañca kāmaguṇā, taṁ kāmataṇhāya padaṭṭhānaṁ. ", "html": "{}"}, "pe8:13.3": {"pli": "Saṁyojane vajjamapassamānāti avijjāya padaṭṭhānaṁ, na hi jātu saṁyojanasaṅgasattā oghaṁ tareyyuṁ vipulaṁ mahantanti upādānassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:13.4": {"pli": "Kāmesu sattāti kāmā dvidhā—", "html": "{}"}, "pe8:13.5": {"pli": "vatthukāmā ca kilesakāmā ca, tattha kilesakāmā kāmataṇhā kāmataṇhāya yuttā bhavanti rūpataṇhā bhavataṇhā lakkhaṇena hārena, saṁyojane vajjamapassamānāti saṁyojanassa. ", "html": "{}"}, "pe8:13.6": {"pli": "Yo tattha chandarāgo tassa kiṁ padaṭṭhānaṁ? ", "html": "{}"}, "pe8:13.7": {"pli": "Sukhā vedanā dve ca indriyāni—", "html": "{}"}, "pe8:13.8": {"pli": "sukhindriyañca somanassindriyañca. ", "html": "{}"}, "pe8:13.9": {"pli": "Iti sukhāya vedanāya gahitāya tayopi vedanā gahitā honti. ", "html": "{}"}, "pe8:13.10": {"pli": "Vedanākkhandhe gahite sabbe pañcakkhandhā gahitā honti. ", "html": "{}"}, "pe8:13.11": {"pli": "Rūpasaddagandharasaphoṭṭhabbā gahitā, vatthukāmesu gahitesu sabbāni cha bāhirāni āyatanāni gahitāni honti. ", "html": "{}"}, "pe8:13.12": {"pli": "Ajjhattikabāhiresu āyatanesu yo sato, ayaṁ vuccate lakkhaṇo hāro, tattha yo oḷārikamhi kilese ajjhāvasito sabbakilesesu yo na tato sukhumataresu na vītarāgo bhavati. ", "html": "{}"}, "pe8:13.13": {"pli": "Tattha bāhirasaṁyojanaṁ mamanti ajjhattasaṁyojanaṁ ahanti. ", "html": "{}"}, "pe8:13.14": {"pli": "Tattha bhagavato ko adhippāyo? ", "html": "{}"}, "pe8:13.15": {"pli": "Ye oghaṁ taritukāmā te saṁyojanīyesu dhammesu ādīnavānupassino viharissantīti ayamettha bhagavato adhippāyo. ", "html": "{}"}, "pe8:13.16": {"pli": "Kāmesu sattāti yesu ca sattā yena ca sattā yesañca sattā ayaṁ catubbidho ākāro sabbesaṁ hārabhāgiyo. ", "html": "{}</p>"}, "pe8:14.1": {"pli": "Tattha katamāni tīṇi vipallāsāni padaṭṭhānāni ca? ", "html": "<p>{}"}, "pe8:14.2": {"pli": "Cittavipallāsassa diṭṭhivipallāsassa saññāvipallāsassa tayo vipallāsā tīṇi akusalamūlāni padaṭṭhānaṁ. ", "html": "{}"}, "pe8:14.3": {"pli": "Tīṇi akusalamūlāni hīnappaṇītakāriyakammassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:14.4": {"pli": "Catunnañca upādānānaṁ doso akusalamūlaṁ dissati. ", "html": "{}"}, "pe8:14.5": {"pli": "Hīnappaṇītakāriyakammassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:14.6": {"pli": "Yathā mātuyā vā pituno vā aññatarassa vā puna uḷārassa bhikkhuno abhayaṁ deti. ", "html": "{}"}, "pe8:14.7": {"pli": "Tattha añño micchā paṭipajjeyya kāyena vā vācāya vā. ", "html": "{}"}, "pe8:14.8": {"pli": "Tattha so byāpādamupādāya tesaṁ uḷārānaṁ rakkhāvaraṇaguttiyā anupālayanto yo uḷārānaṁ abhayaṁ deti. ", "html": "{}"}, "pe8:14.9": {"pli": "Tesaṁ abhaye dinne yo tattha micchā paṭipajjeyya. ", "html": "{}"}, "pe8:14.10": {"pli": "Tattha so byāpādaṁ upādāyanto dosajaṁ kammaṁ karoti. ", "html": "{}"}, "pe8:14.11": {"pli": "Yo tattha asādhu indriyā nīvaraṇaṁ yaṁ tesaṁ abhayaṁ dakkhiṇato saññaṁ idaṁ paṇītaṁ kāraṇaṁ mayā puna tattha micchāpaṭipatti ayaṁ byāpādo hīnagamivakammaṁ lobho moho ca imāni nīvaraṇāni vacanāni tāni cattāri upādānāni tehi catūhi upādānehi yo so upādāno itthī vā puriso vā tesaṁ pañcakkhandhānaṁ teyeva upādāno samudayo idaṁ dukkhañca samudayo ca soyeva desanāhāro. ", "html": "{}</p>"}, "pe8:15.1": {"pli": "Tattha kāmesu ye na pajjanti, te ādīnavānupassanāya pajjanti. ", "html": "<p>{}"}, "pe8:15.2": {"pli": "Itissā kāmadhātuyā nikkhamitukāmatā, ayaṁ vuccati nekkhammacchando. ", "html": "{}"}, "pe8:15.3": {"pli": "Yo tattha anabhisaṅkhārānaṁ kiñci visodheti tassa dhāvarā vā, ayaṁ abyāpādacchando. ", "html": "{}"}, "pe8:15.4": {"pli": "Kiñci vihiṁsati, ayaṁ vihiṁsāchando. ", "html": "{}"}, "pe8:15.5": {"pli": "Iti nekkhammābhinīhatā tayo chandā—", "html": "{}"}, "pe8:15.6": {"pli": "nekkhammacchando abyāpādacchando avihiṁsāchando. ", "html": "{}"}, "pe8:15.7": {"pli": "Tattha nekkhammacchando alobho; ", "html": "{}"}, "pe8:15.8": {"pli": "abyāpādacchando adoso; ", "html": "{}"}, "pe8:15.9": {"pli": "avihiṁsāchando amoho. ", "html": "{}"}, "pe8:15.10": {"pli": "Imāni tīṇi kusalamūlāni aṭṭhasu sampattesu parahitāni, tesaṁyeva catunnaṁ upādānānaṁ nirodhāya saṁvattanti. ", "html": "{}"}, "pe8:15.11": {"pli": "Sace vā puna kammaṁ kareyya kaṇhaṁ vā sukkaṁ vā tassa vipākahānāya saṁvattanti. ", "html": "{}"}, "pe8:15.12": {"pli": "Idaṁ kammaṁ akaṇhaṁ asukkaṁ kammakkhayāya saṁvattati. ", "html": "{}"}, "pe8:15.13": {"pli": "Tattha yo tiṇṇaṁ akusalamūlānaṁ nirodho, ayaṁ nirodho. ", "html": "{}"}, "pe8:15.14": {"pli": "Soyeva maggo tattha paṭipadāni imāni dve saccāni imāni cattāri saccāni āvaṭṭo hāro. ", "html": "{}</p>"}, "pe8:16.1": {"pli": "Kāmesu sattāti ye sekkhā, te ekenevākārena sattā. ", "html": "<p>{}"}, "pe8:16.2": {"pli": "Ye puthujjanā, te dvīhākārehi sattā, tassāyaṁ pañho vibhajjabyākaraṇīyo vattabbo. ", "html": "{}"}, "pe8:16.3": {"pli": "Kiñcāpi sotāpanno paṭisevanāya, no ca kho abhinivese satto yo hi apacayāya padahati, na upacayāya. ", "html": "{}"}, "pe8:16.4": {"pli": "Sekkho hi kilesavasena kāme paṭisevati. ", "html": "{}"}, "pe8:16.5": {"pli": "Puthujjano pana kilesasamuṭṭhānāya kāme paṭisevati. ", "html": "{}"}, "pe8:16.6": {"pli": "Tattha kāmesu sattānaṁ catuoghaṁ tarissatīti vibhajjabyākaraṇīyo, ayaṁ vibhatti. ", "html": "{}</p>"}, "pe8:17.1": {"pli": "Parivattanoti kāme ye neva sajjanti na ca saṁyojanehi saṁyuttā, te oghaṁ tarissanti vipulaṁ mahantanti. ", "html": "<p>{}"}, "pe8:17.2": {"pli": "Ayaṁ suttassa paṭipakkho. ", "html": "{}</p>"}, "pe8:18.1": {"pli": "Vevacananti yo kāmesu satto yo ca tattha kāmānaṁ guṇo, tattha viso satto. ", "html": "<p>{}"}, "pe8:18.2": {"pli": "Yepi kāmānaṁ āhārā dhammā, tattha viso satto. ", "html": "{}"}, "pe8:18.3": {"pli": "Tatthimaṁ kāmānaṁ vevacanaṁ pāko rajo sallaṁ gaṇḍo īti upaddavoti. ", "html": "{}"}, "pe8:18.4": {"pli": "Yāni vā pana aññāni vevacanāni tattha viso sattoti vevacanaṁ. ", "html": "{}"}, "pe8:18.5": {"pli": "Satto bandho mucchito gadhito ajjhosito kāme ajjhāpannā parimutto tabbahulavihārīti. ", "html": "{}"}, "pe8:18.6": {"pli": "Yāni vā pana aññāni vevacanāni, ayaṁ vevacano nāma. ", "html": "{}"}, "pe8:18.7": {"pli": "Kāmappacārapaññattiyā kilesagocarapaññattiyā paññattā cittanti vevacanaṁ. ", "html": "{}"}, "pe8:18.8": {"pli": "Satto tabbahulavihārīti yāni vā pana aññāni. ", "html": "{}"}, "pe8:18.9": {"pli": "Ime kāmappacārapaññattiyā kilesagocarapaññattiyā paññattā, bījapaññattiyā paññattā, saṅkhārā saṁyojanapaññattiyā paññattā, upādānaṁ hetupaññattiyā paññattaṁ, puggalo puthupaññattiyā paññatto. ", "html": "{}</p>"}, "pe8:19.1": {"pli": "Otaraṇoti imāya paṭiccasamuppādo dukkhañca samudayo ca. ", "html": "<p>{}"}, "pe8:19.2": {"pli": "Ye kilesā ye saṅkhārā saṁyojanāni ca pañcasu khandhesu saṅkhārakkhandho dhammāyatanesu akusalā dhammāyatanāni indriyesu sukhindriyañca, somanassindriyañca, ayaṁ indriyotaraṇo. ", "html": "{}</p>"}, "pe8:20.1": {"pli": "Sodhanoti ettako. ", "html": "<p>{}"}, "pe8:20.2": {"pli": "Eseva ārambho niddisitabbo suttattho. ", "html": "{}</p>"}, "pe8:21.1": {"pli": "Adhiṭṭhānoti ime dhammā atthi ekattatāya paññattā atthi vemattatāya. ", "html": "<p>{}"}, "pe8:21.2": {"pli": "Ye saññā bāhiro kāme, te vemattatāya paññattā. ", "html": "{}"}, "pe8:21.3": {"pli": "Pañcasu kāmaguṇesu sattāti pariyuṭṭhānavipallāsā vemattatāya paññattā oghaṁ tareyyuṁ. ", "html": "{}"}, "pe8:21.4": {"pli": "Vipulaṁ mahantanti avijjā ekattatāya paññattā. ", "html": "{}</p>"}, "pe8:22.1": {"pli": "Parikkhāroti tassa ko hetu ko paccayo? ", "html": "<p>{}"}, "pe8:22.2": {"pli": "Ārammaṇapaccayatāya paccayo. ", "html": "{}"}, "pe8:22.3": {"pli": "Ayoniso ca manasikāro sannissayassa paccayatāya paccayo. ", "html": "{}"}, "pe8:22.4": {"pli": "Avijjā samanantarapaccayatāya paccayo. ", "html": "{}"}, "pe8:22.5": {"pli": "Rāgānusayo hetupaccayatāya paccayo. ", "html": "{}"}, "pe8:22.6": {"pli": "Ayaṁ hetu, ayaṁ paccayo. ", "html": "{}</p>"}, "pe8:23.1": {"pli": "Samāropano paccayoti ye kāmesu sattā sugatā surūpāti ayaṁ kāmadhātuyā chando rāgo te apuññamayā saṅkhārā. ", "html": "<p>{}"}, "pe8:23.2": {"pli": "Te kiṁ paccayā? ", "html": "{}"}, "pe8:23.3": {"pli": "Avijjā paccayā. ", "html": "{}"}, "pe8:23.4": {"pli": "Te kissa paccayā? ", "html": "{}"}, "pe8:23.5": {"pli": "Viññāṇassa paccayā. ", "html": "{}"}, "pe8:23.6": {"pli": "Iti avijjāpaccayā saṅkhārā. ", "html": "{}"}, "pe8:23.7": {"pli": "Saṅkhārapaccayā viññāṇaṁ yāva jarāmaraṇaṁ evametassa kevalassa mahato dukkhakkhandhassa samudayo hoti ekaṁ suttaṁ gataṁ. ", "html": "{}"}, "pe8:23.8": {"pli": "Pañcanīvaraṇikaṁ suttaṁ kātabbaṁ. ", "html": "{}</p>"}, "pe8:24.1": {"pli": "Tattha katamo desanāhāro nāma? ", "html": "<p data-counter='1'>{}"}, "pe8:24.2": {"pli": "Yā ca abhijjhā yo ca byāpādo yañca uddhaccaṁ, ayaṁ taṇhā. ", "html": "{}"}, "pe8:24.3": {"pli": "Yañca thinamiddhaṁ, yañca kukkuccaṁ yā ca vicikicchā, ayaṁ diṭṭhi. ", "html": "{}"}, "pe8:24.4": {"pli": "Yā pana kāyassa akammaniyatā kiñcāpi taṁ middhaṁ no tu sabhāvakilesatāya kileso, iti yā ca cittasallīyanā yā ca kāyākammaniyatā, ayaṁ pakkhopakileso na tu sabhāvakileso. ", "html": "{}"}, "pe8:24.5": {"pli": "Tattha attasaññānupacittaṁ kilamatho kukkuccānupacittaṁ thinaṁ yā cittassa līyanā, iti ime pañca nīvaraṇā cattāri nīvaraṇāni sabhāvakilesā thinamiddhaṁ nīvaraṇapakkhopakileso. ", "html": "{}"}, "pe8:24.6": {"pli": "Yathā cattāro āsavā sabhāvaāsavatāya āsavā no tu cittasāsavatāya āsavā. ", "html": "{}"}, "pe8:24.7": {"pli": "Sabhāvatāya āsavā. ", "html": "{}"}, "pe8:24.8": {"pli": "Pakkhe āsavatāya āsavā. ", "html": "{}"}, "pe8:24.9": {"pli": "Atha panāha suttantaṁ yena te sampayuttā vā vippayuttā vā āsavā, teyeva ete vattabbā sāsavā vā anāsavā vā. ", "html": "{}</p>"}, "pe8:25.1": {"pli": "Tattha katamo vicayo. ", "html": "<p data-counter='2'>{}"}, "pe8:25.2": {"pli": "Abhijjhā kāmataṇhā rūpataṇhā bhavataṇhā. ", "html": "{}"}, "pe8:25.3": {"pli": "Yaṁ vā pana kiñci ajjhosānagataṁ sāsavā abhijjhitassa mettānupassiya yo anatthaṁ carati. ", "html": "{}"}, "pe8:25.4": {"pli": "Tattha yo byāpādaṁ uppādeti, acari carissatīti. ", "html": "{}"}, "pe8:25.5": {"pli": "Evaṁ nava āghātavatthūni kattabbāni, tassevaṁ byāpādānupassissa kileso yo paridāho kāyakilamatho akammaniyatā middhaṁ. ", "html": "{}"}, "pe8:25.6": {"pli": "Cittānupassissa paṭighātena khiyanā, idaṁ thinamiddhaṁ. ", "html": "{}"}, "pe8:25.7": {"pli": "Tattha adhikaraṇaavūpasamo, idaṁ uddhaccaṁ. ", "html": "{}"}, "pe8:25.8": {"pli": "Yaṁ kiṁ kasathamīti idaṁ kukkuccaṁ. ", "html": "{}"}, "pe8:25.9": {"pli": "Yaṁ yathā idaṁ santīraṇaṁ, ayaṁ vicikicchā. ", "html": "{}"}, "pe8:25.10": {"pli": "Tattha avijjā ca taṇhā ca atthi, idaṁ pariyuṭṭhānaṁ. ", "html": "{}"}, "pe8:25.11": {"pli": "Āvaraṇaṁ nīvaraṇaṁ chadanaṁ upakkileso ca atthi, idaṁ kāmacchando kāmarāgapariyuṭṭhānassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:25.12": {"pli": "Byāpādo byāpādapariyuṭṭhānassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:25.13": {"pli": "Thinamiddhaṁ thinamiddhapariyuṭṭhānassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:25.14": {"pli": "Uddhaccakukkuccaṁ avijjāpariyuṭṭhānassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:25.15": {"pli": "Vicikicchā vicikicchāpariyuṭṭhānassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:25.16": {"pli": "Kāmarāgapariyuṭṭhānaṁ anusayasaṁyojanassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:25.17": {"pli": "Byāpādapariyuṭṭhānaṁ paṭighasaṁyojanassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:25.18": {"pli": "Thinamiddhapariyuṭṭhānaṁ mānasaṁyojanassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:25.19": {"pli": "Avijjāpariyuṭṭhānañca vicikicchāpariyuṭṭhānañca diṭṭhisaṁyojanassa padaṭṭhānaṁ. ", "html": "{}</p>"}, "pe8:26.1": {"pli": "Tattha katamo lakkhaṇo hāro? ", "html": "<p data-counter='3'>{}"}, "pe8:26.2": {"pli": "Kāmarāgapariyuṭṭhāne vutte sabbāni pariyuṭṭhānāni vuttāni hontīti. ", "html": "{}"}, "pe8:26.3": {"pli": "Saṁyojanesu vuttesu sabbasaṁyojanāni vuttāni honti. ", "html": "{}"}, "pe8:26.4": {"pli": "Ayaṁ lakkhaṇo hāro. ", "html": "{}</p>"}, "pe8:27.1": {"pli": "Tattha katamo catubyūho hāro? ", "html": "<p data-counter='4'>{}"}, "pe8:27.2": {"pli": "Ye ime pañca nīvaraṇā jhānapaṭipakkho so dukkhasamudayo. ", "html": "{}"}, "pe8:27.3": {"pli": "Yaṁ phalaṁ, idaṁ dukkhaṁ. ", "html": "{}"}, "pe8:27.4": {"pli": "Tattha kāmacchandassa nekkhammavitakko paṭipakkho; ", "html": "{}"}, "pe8:27.5": {"pli": "byāpādassa abyāpādavitakko paṭipakkho; ", "html": "{}"}, "pe8:27.6": {"pli": "tiṇṇaṁ nīvaraṇānaṁ avihiṁsāvitakko paṭipakkho. ", "html": "{}"}, "pe8:27.7": {"pli": "Iti ime tayo vitakkā. ", "html": "{}"}, "pe8:27.8": {"pli": "Nekkhammavitakko samādhikkhandhaṁ bhajati. ", "html": "{}"}, "pe8:27.9": {"pli": "Abyāpādavitakko sīlakkhandhaṁ bhajati. ", "html": "{}"}, "pe8:27.10": {"pli": "Avihiṁsāvitakko paññākkhandhaṁ bhajati. ", "html": "{}"}, "pe8:27.11": {"pli": "Ime tayo khandhā. ", "html": "{}"}, "pe8:27.12": {"pli": "Ariyo aṭṭhaṅgiko maggo nīvaraṇappahānāya saṁvattati. ", "html": "{}"}, "pe8:27.13": {"pli": "Yaṁ nīvaraṇappahānaṁ, ayaṁ nirodho. ", "html": "{}"}, "pe8:27.14": {"pli": "Imāni cattāri saccāni. ", "html": "{}"}, "pe8:27.15": {"pli": "Ayaṁ catubyūho hāro. ", "html": "{}</p>"}, "pe8:28.1": {"pli": "Tattha katamo āvaṭṭo hāro? ", "html": "<p data-counter='5'>{}"}, "pe8:28.2": {"pli": "Pañca nīvaraṇāni dasa bhavanti. ", "html": "{}"}, "pe8:28.3": {"pli": "Yadapi ajjhattaṁ sārajjati, tadapi nīvaraṇaṁ. ", "html": "{}"}, "pe8:28.4": {"pli": "Yadapi bahiddhā sārajjati, tadapi nīvaraṇaṁ, evaṁ yāva vicikicchā ime dasa nīvaraṇā. ", "html": "{}"}, "pe8:28.5": {"pli": "Ajjhattabahiddhā kilesā imāni dve saṁyojanāni ajjhattasaṁyojanañca bahiddhāsaṁyojanañca. ", "html": "{}"}, "pe8:28.6": {"pli": "Tattha ahanti ajjhattaṁ, mamanti bahiddhā. ", "html": "{}"}, "pe8:28.7": {"pli": "Sakkāyadiṭṭhi ajjhattaṁ, ekasaṭṭhi diṭṭhigatāni bahiddhā. ", "html": "{}"}, "pe8:28.8": {"pli": "Yo ajjhattaṁ chandarāgo rūpesu avītarāgo bhavati avītacchando. ", "html": "{}"}, "pe8:28.9": {"pli": "Evaṁ yāva viññāṇe, ayaṁ ajjhattā taṇhā. ", "html": "{}"}, "pe8:28.10": {"pli": "Yaṁ chasu bāhiresu āyatanesu tīsu ca bhavesu ajjhosānaṁ, ayaṁ bahiddhā taṇhā. ", "html": "{}"}, "pe8:28.11": {"pli": "Imāni dve saccāni saṁyojanāni saṁyojanīyā ca dhammā. ", "html": "{}"}, "pe8:28.12": {"pli": "Tattha saṁyojanesu dhammesu yā nibbidānupassanā ca, ayaṁ maggo. ", "html": "{}"}, "pe8:28.13": {"pli": "Yaṁ saṁyojanappahānaṁ, ayaṁ nirodho. ", "html": "{}"}, "pe8:28.14": {"pli": "Ayaṁ āvaṭṭo hāro. ", "html": "{}</p>"}, "pe8:29.1": {"pli": "Tattha katamo vibhattihāro? ", "html": "<p data-counter='6'>{}"}, "pe8:29.2": {"pli": "Saṁyojananti na etaṁ ekaṁsena. ", "html": "{}"}, "pe8:29.3": {"pli": "Mānasaṁyojanaṁ diṭṭhibhāgiyanti na taṁ ekaṁsena adiṭṭhamānaṁ nissāyamānaṁ na pajahati. ", "html": "{}"}, "pe8:29.4": {"pli": "Yo pañca uddhambhāgiyo māno kiñcāpi so diṭṭhipakkhe siyā. ", "html": "{}"}, "pe8:29.5": {"pli": "Na tu orambhāgiyaṁ saṁyojanaṁ tassa pahānāya saṁvattatīti. ", "html": "{}"}, "pe8:29.6": {"pli": "Yo ca ahaṅkāro na paviddhoyaṁ panassa evaṁ hoti. ", "html": "{}"}, "pe8:29.7": {"pli": "Kadāsu nāmāhaṁ taṁ santaṁ āyatanaṁ sacchikatvā upasampajja viharissāmi, yaṁ ariyā santaṁ āyatanaṁ upasampajja viharissantīti, ayaṁ abhijjhā na ca taṁ nīvaraṇaṁ. ", "html": "{}"}, "pe8:29.8": {"pli": "Atthi pana arahato kāyakilesamiddhañca okkamati na ca taṁ nīvaraṇaṁ tassa thinamiddhaṁ nīvaraṇanti. ", "html": "{}"}, "pe8:29.9": {"pli": "Na ekaṁsena. ", "html": "{}"}, "pe8:29.10": {"pli": "Ayaṁ vibhattihāro. ", "html": "{}</p>"}, "pe8:30.1": {"pli": "Parivattanoti pañca nīvaraṇā pañcaṅgikena jhānena pahānaṁ gacchanti. ", "html": "<p data-counter='7'>{}"}, "pe8:30.2": {"pli": "Ayaṁ tesaṁ paṭipakkho nīvaraṇo asukassa pahīnāti na aññānuminitabbaṁ, paramatthamajjhattaṁ, ayaṁ parivattanā. ", "html": "{}</p>"}, "pe8:31.1": {"pli": "Tattha katamo vevacano? ", "html": "<p data-counter='8'>{}"}, "pe8:31.2": {"pli": "Kāmacchando chandarāgo pemaṁ nikantīti vevacanaṁ. ", "html": "{}"}, "pe8:31.3": {"pli": "Nīvaraṇaṁ chadanaṁ upakkileso pariyuṭṭhānanti vevacanaṁ. ", "html": "{}</p>"}, "pe8:32.1": {"pli": "Paññattīti avijjāpaccayā kiccapaññattiyā paññatti, byāpādo vikkhepapaññattiyā paññatti, thinamiddhaṁ asamugghātapaññattiyā paññatti. ", "html": "<p data-counter='9'>{}"}, "pe8:32.2": {"pli": "Evaṁ sabbepi ete pañca nīvaraṇā imamhi sutte vikkhepapaññattiyā paññatti. ", "html": "{}</p>"}, "pe8:33.1": {"pli": "Tattha katamo otaraṇo? ", "html": "<p data-counter='10'>{}"}, "pe8:33.2": {"pli": "Ime pañca nīvaraṇā avijjā ca taṇhā ca tattha avijjāmūlā nīvaraṇā. ", "html": "{}"}, "pe8:33.3": {"pli": "Yā taṇhā ime saṅkhārā, te avijjāpaccayā ime dve dhammā pañcasu khandhesu saṅkhārakkhandhapariyāpannā, āyatanesu dhammāyatanaṁ, dhātūsu dhammadhātu, indriyesu imesaṁ dhammānaṁ padaṭṭhānaṁ sukhindriyassa ca somanassindriyassa ca itthindriyassa ca purisindriyassa ca. ", "html": "{}</p>"}, "pe8:34.1": {"pli": "Tattha katamo sodhano hāro? ", "html": "<p data-counter='11'>{}"}, "pe8:34.2": {"pli": "Idaṁ suttaṁ yathā ārabbha nikkhittaṁ so attho bhāsito imehi pañcahi padehi. ", "html": "{}</p>"}, "pe8:35.1": {"pli": "Tattha kāmacchando ca byāpādo ca vicikicchā ca na ekattatāya paññattā, kāmāti na ekattatāya paññattā, atha khalu vemattatāya paññattā. ", "html": "<p data-counter='12'>{}"}, "pe8:35.2": {"pli": "Ayaṁ adhiṭṭhāno hāro. ", "html": "{}</p>"}, "pe8:36.1": {"pli": "Tattha katamo parikkhāro? ", "html": "<p data-counter='13'>{}"}, "pe8:36.2": {"pli": "Kāmacchandassa ayoniso manasikāro subhārammaṇapaccayo; ", "html": "{}"}, "pe8:36.3": {"pli": "subhanimittañca hetu. ", "html": "{}"}, "pe8:36.4": {"pli": "Byāpādassa ayoniso manasikāro āghātavatthūni ca paccayo; ", "html": "{}"}, "pe8:36.5": {"pli": "paṭighānusayo hetu. ", "html": "{}"}, "pe8:36.6": {"pli": "Thinamiddhassa paṭisaṁhāro paccayo; ", "html": "{}"}, "pe8:36.7": {"pli": "pavattiyā kilamathā calanā tañca hetu. ", "html": "{}"}, "pe8:36.8": {"pli": "Uddhaccakukkuccassa rajanīyaṁ ārammaṇiyaṁ assādiyākindriyaṁ tāva aparipuṇṇañca ñāṇaṁ paccayo; ", "html": "{}"}, "pe8:36.9": {"pli": "kāmasaññā ca diṭṭhianusayo ca hetu. ", "html": "{}"}, "pe8:36.10": {"pli": "Vicikicchāya nava mānavidhā ārammaṇaṁ mānānusayo, sova paccayo; ", "html": "{}"}, "pe8:36.11": {"pli": "vicikicchānusayo hetu. ", "html": "{}"}, "pe8:36.12": {"pli": "Ete pañca dhammā sahetu sappaccayā uppajjanti. ", "html": "{}</p>"}, "pe8:37.1": {"pli": "Tattha katamo samāropano hāro? ", "html": "<p data-counter='14'>{}"}, "pe8:37.2": {"pli": "Ime pañca nīvaraṇā cattāropi ete āsavā gaṇḍāpi ete sallāpi ete upādānāni ete. ", "html": "{}"}, "pe8:37.3": {"pli": "Tesu eva bāhiresu dhammesu saṅkilesabhāgiyaṁ suttanti paññattiṁ gacchati. ", "html": "{}"}, "pe8:37.4": {"pli": "Ayaṁ samāropano hāro. ", "html": "{}</p>"}, "pe8:37.5": {"pli": "Niddiṭṭhaṁ saṅkilesikabhāgiyaṁ suttaṁ. ", "html": "<p class='endsutta'>{}</p>"}, "pe8:38.1": {"pli": "Manopubbaṅgamā dhammāti gāthā. ", "html": "<p>{}</p>"}, "pe8:39.1": {"pli": "Tattha katamo desanā hāro? ", "html": "<p>{}"}, "pe8:39.2": {"pli": "Imamhi sutte ko attho khandhavavatthānena viññāṇakkhandhaṁ deseti, dhātuvavatthānena manoviññāṇadhātuṁ, āyatanavavatthānena manāyatanaṁ, indriyavavatthānena manindriyaṁ. ", "html": "{}"}, "pe8:39.3": {"pli": "Tassa kiṁ pubbaṅgamā dhammā? ", "html": "{}"}, "pe8:39.4": {"pli": "Saṅkhittena cha dhammā pubbaṅgamā dhammā kusalamūlāni ca akusalamūlāni ca animittaṁ imamhi sutte kusalamūlaṁ desitaṁ. ", "html": "{}"}, "pe8:39.5": {"pli": "Tattha katamā manopubbaṅgamā dhammā? ", "html": "{}"}, "pe8:39.6": {"pli": "Mano tesaṁ pubbaṅgamaṁ, yathāpi balassa rājā pubbaṅgamo, evameva dhammānaṁ manopubbaṅgamā. ", "html": "{}"}, "pe8:39.7": {"pli": "Tattha tividhānaṁ pubbaṅgamānaṁ nekkhammacchandena abyāpādacchandena avihiṁsāchandena. ", "html": "{}"}, "pe8:39.8": {"pli": "Alobhassa nekkhammacchandena pubbaṅgamā. ", "html": "{}"}, "pe8:39.9": {"pli": "Adosassa abyāpādacchandena pubbaṅgamā. ", "html": "{}"}, "pe8:39.10": {"pli": "Amohassa avihiṁsāchandena pubbaṅgamā. ", "html": "{}"}, "pe8:39.11": {"pli": "Tattha <b>manoseṭṭhā</b>ti manasā ime dhammā ussaṭā manena vā nimmitā. ", "html": "{}"}, "pe8:39.12": {"pli": "Manova imesaṁ dhammānaṁ seṭṭhoti manova imesaṁ dhammānaṁ seṭṭhajeṭṭhoti manova imesaṁ dhammānaṁ ādhipaccaṁ karotīti manoseṭṭhā. ", "html": "{}"}, "pe8:39.13": {"pli": "<b>Manojavā</b>ti yattha mano gacchati. ", "html": "{}"}, "pe8:39.14": {"pli": "Tattha ime dhammā gacchantīti manojavā. ", "html": "{}"}, "pe8:39.15": {"pli": "Yathā vāto sīghaṁ gacchati añño vā koci sīghaṁ gāmako vuccate vātajavoti pakkhigāmikoti, evameva ime dhammā manena sampajāyamānā gacchanti, tattha ime dhammā gacchantīti manojavāti. ", "html": "{}"}, "pe8:39.16": {"pli": "Te tividhā chandasamudānitā anāvilatā ca saṅkappo. ", "html": "{}"}, "pe8:39.17": {"pli": "Sattavidhā ca kāyikaṁ sucaritaṁ vācasikaṁ sucaritaṁ, te dasa kusalakammapathā. ", "html": "{}"}, "pe8:39.18": {"pli": "Tattha <b>manasā ce pasannenā</b>ti manokammaṁ. ", "html": "{}"}, "pe8:39.19": {"pli": "<b>Bhāsati vā</b>ti vacīkammaṁ. ", "html": "{}"}, "pe8:39.20": {"pli": "<b>Karoti vā</b>ti kāyakammaṁ. ", "html": "{}"}, "pe8:39.21": {"pli": "Imehi imasmiṁ sutte dasa kusalakammapathā paramāpi santā sīlavatā paramā. ", "html": "{}"}, "pe8:39.22": {"pli": "So bhavati vivattiyaṁ na lokaniyyānāya vāsanābhāgiyaṁ suttaṁ bhavati. ", "html": "{}"}, "pe8:39.23": {"pli": "Ayaṁ desanā. ", "html": "{}</p>"}, "pe8:40.1": {"pli": "Tattha katamo vicayo hāro? ", "html": "<p>{}"}, "pe8:40.2": {"pli": "Manopubbaṅgamā dhammāti kusalamūlāni ca aṭṭhaṅgasammattāni. ", "html": "{}"}, "pe8:40.3": {"pli": "Idaṁ suttaṁ. ", "html": "{}</p>"}, "pe8:41.1": {"pli": "Yuttīti dasannaṁ kusalakammapathānaṁ yo vipāko, so sukhavedanīyo abyāpādassaṅgamāno. ", "html": "<p>{}"}, "pe8:41.2": {"pli": "<b>Chāyāva anapāyinī</b>ti anugacchati atthi esā yutti. ", "html": "{}</p>"}, "pe8:42.1": {"pli": "Padaṭṭhānanti aṭṭhārasannaṁ manopavicārānaṁ padaṭṭhānaṁ. ", "html": "<p>{}"}, "pe8:42.2": {"pli": "Manopubbaṅgamā dhammāti sabbakusalapakkhassa ime dhammā padaṭṭhānaṁ. ", "html": "{}"}, "pe8:42.3": {"pli": "<b>Manasā ce pasannenā</b>ti yo cetaso pasādo, idaṁ saddhindriyassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:42.4": {"pli": "<b>Bhāsati vā</b>ti sammāvācāya. ", "html": "{}"}, "pe8:42.5": {"pli": "<b>Karoti vā</b>ti sammākammantassa ca sammāvāyāmassa ca padaṭṭhānaṁ. ", "html": "{}</p>"}, "pe8:43.1": {"pli": "Lakkhaṇoti iti pubbaṅgamā dhammāti vedanāpubbaṅgamāpi ete, saññāpubbaṅgamāpi ete, saṅkhārapubbaṅgamāpi ete. ", "html": "<p>{}"}, "pe8:43.2": {"pli": "Ye keci dhammā sahajātā sabbe pubbaṅgamā etesaṁ dhammānaṁ. ", "html": "{}"}, "pe8:43.3": {"pli": "<b>Tato naṁ sukhamanvetī</b>ti somanassamapi naṁ anveti yaṁ susukhacchāyā tadāpi naṁ sukhaṁ tadapi anveti. ", "html": "{}</p>"}, "pe8:44.1": {"pli": "Tattha katamo catubyūho hāro? ", "html": "<p>{}"}, "pe8:44.2": {"pli": "Manopubbaṅgamāti na idaṁ ekādivacanaṁ. ", "html": "{}"}, "pe8:44.3": {"pli": "Kiṁ kāraṇā? ", "html": "{}"}, "pe8:44.4": {"pli": "Sabbeyeva ime chaviññāṇakāyā, imamhi bhagavato ko adhippāyo? ", "html": "{}"}, "pe8:44.5": {"pli": "Ye sukhena atthikā, te manaṁ pasādentīti ayaṁ imamhi sutte bhagavato adhippāyo. ", "html": "{}"}, "pe8:44.6": {"pli": "Attho pubbeyeva niddiṭṭho. ", "html": "{}</p>"}, "pe8:45.1": {"pli": "Yāni hi kusalamūlāni, tāni aṭṭhānisaṁsamattā hetu, ayaṁ aṭṭhaṅgiko maggo. ", "html": "<p>{}"}, "pe8:45.2": {"pli": "Dasa ṭhānāni desanāhetūni desanāpaccayā niddesanā ca. ", "html": "{}"}, "pe8:45.3": {"pli": "Tattha yaṁ maññe dukkhena saha nāmarūpaṁ viññāṇasaccanti aṅgena kusalamūlaṁ pahīyati, ayaṁ appahīnabhūmiyaṁ samudayo. ", "html": "{}"}, "pe8:45.4": {"pli": "Yaṁ tesaṁ pahānā, ayaṁ nirodho. ", "html": "{}"}, "pe8:45.5": {"pli": "Imāni cattāri saccāni. ", "html": "{}"}, "pe8:45.6": {"pli": "Ayaṁ āvaṭṭo hāro. ", "html": "{}</p>"}, "pe8:46.1": {"pli": "Vibhattīti—", "html": "<p>{}</p>"}, "pe8:47.1": {"pli": "“Manopubbaṅgamā dhammā, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe8:47.2": {"pli": "Manoseṭṭhā manojavā; ", "html": "<span class='verse-line'>{}</span>"}, "pe8:47.3": {"pli": "Manasā ce pasannena, ", "html": "<span class='verse-line'>{}</span>"}, "pe8:47.4": {"pli": "Bhāsati vā karoti vā; ", "html": "<span class='verse-line'>{}</span>"}, "pe8:47.5": {"pli": "Tato naṁ sukhamanveti, ", "html": "<span class='verse-line'>{}</span>"}, "pe8:47.6": {"pli": "Chāyāva anapāyinī”ti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe8:48.1": {"pli": "Taṁ na ekaṁsena samaṇassa vā brāhmaṇassa vā pana hoti. ", "html": "<p>{}"}, "pe8:48.2": {"pli": "Tassa vā micchādiṭṭhikassa sakasatthe cittaṁ pasādeti, tena ca pasannena cittena bhāsati byākaroti na taṁ sukhamanveti na chāyāva anugāminī, dukkhameva taṁ anveti. ", "html": "{}"}, "pe8:48.3": {"pli": "Yathā vahantaṁ cakkaṁ padamanveti, idaṁ taṁ vibhajjabyākaraṇīyaṁ. ", "html": "{}"}, "pe8:48.4": {"pli": "Manasā ce pasannena kāyakammaṁ vacīkammaṁ sukhavedanīyanti samaggate sukhavedanīyaṁ micchaggate dukkhavedanīyaṁ, ayaṁ vibhatti. ", "html": "{}</p>"}, "pe8:49.1": {"pli": "Tattha katamo parivattano hāro? ", "html": "<p>{}"}, "pe8:49.2": {"pli": "Manopubbaṅgamā dhammāti yaṁ manasā paduṭṭhena bhāsati vā karoti vā dukkhamassānugāminī, etāniyeva dve suttāni bhāsitāni esa eva ca paṭipakkho. ", "html": "{}"}, "pe8:49.3": {"pli": "Vevacananti yadidaṁ mano cittaṁ viññāṇaṁ manindriyaṁ manoviññāṇadhātu. ", "html": "{}</p>"}, "pe8:50.1": {"pli": "Paññattīti manopubbaṅgamā dhammāti ayaṁ mano kiñci paññattiyā paññattaṁ. ", "html": "<p>{}"}, "pe8:50.2": {"pli": "Dhammāti kusalakammapathapaññattiyā paññattaṁ. ", "html": "{}"}, "pe8:50.3": {"pli": "Manoseṭṭhāti visiṭṭhapaññattiyā paññattaṁ. ", "html": "{}"}, "pe8:50.4": {"pli": "Manojavāti sahapaññattiyā paññattaṁ. ", "html": "{}"}, "pe8:50.5": {"pli": "Cittanti nekkhammapaññattiyā paññattaṁ. ", "html": "{}"}, "pe8:50.6": {"pli": "Manasā ce pasannenāti saddhindriyapaññattiyā paññattaṁ. ", "html": "{}"}, "pe8:50.7": {"pli": "Manasā ce pasannenāti anāvilasaṅkappadutiyajjhānapaññattiyā paññattaṁ. ", "html": "{}"}, "pe8:50.8": {"pli": "Manasā ce pasannenāti assaddhānaṁ paṭipakkhapaññattiyā paññattaṁ. ", "html": "{}"}, "pe8:50.9": {"pli": "Bhāsati vāti sammāvācāpaññattiyā paññattaṁ. ", "html": "{}"}, "pe8:50.10": {"pli": "Karoti vāti sammākammantapaññattiyā paññattaṁ. ", "html": "{}"}, "pe8:50.11": {"pli": "Tato naṁ sukhamanvetīti jhānasamādhānaṁ. ", "html": "{}"}, "pe8:50.12": {"pli": "Indriyesu manindriyaṁ. ", "html": "{}"}, "pe8:50.13": {"pli": "Paṭiccasamuppāde viññāṇaṁ. ", "html": "{}"}, "pe8:50.14": {"pli": "Manopubbaṅgamā dhammāti mettā ca mudutā ca jhānesu dutiyaṁ jhānaṁ tatiyañca. ", "html": "{}"}, "pe8:50.15": {"pli": "Khandhesu saṅkhārakkhandhapariyāpanno. ", "html": "{}"}, "pe8:50.16": {"pli": "Dhātūsu dhammadhātu, āyatanesu dhammāyatanaṁ. ", "html": "{}"}, "pe8:50.17": {"pli": "Yaṁ kusalaṁ indriyesu sukhindriyañca somanassindriyañca padaṭṭhānaṁ. ", "html": "{}"}, "pe8:50.18": {"pli": "Imesaṁ dhammānaṁ paṭiccasamuppannānaṁ phassapaccayā sukhavedanīyo phasso sukhavedanā manopavicāresu somanassavicāro chattiṁsesu paṭhamapadesu cha somanassanekkhammassitā. ", "html": "{}"}, "pe8:50.19": {"pli": "Iti ayaṁ otaraṇo hāro. ", "html": "{}</p>"}, "pe8:51.1": {"pli": "Tattha katamo sodhano hāro? ", "html": "<p>{}"}, "pe8:51.2": {"pli": "Yaṁ atthaṁ ārabbha idaṁ suttaṁ bhāsitaṁ. ", "html": "{}"}, "pe8:51.3": {"pli": "So attho niyutto etamatthaṁ ārabbha suttaṁ. ", "html": "{}"}, "pe8:51.4": {"pli": "Ayaṁ sodhano hāro. ", "html": "{}</p>"}, "pe8:52.1": {"pli": "Tattha katamo adhiṭṭhāno hāro? ", "html": "<p>{}"}, "pe8:52.2": {"pli": "Manopubbaṅgamā dhammāti vevacanapaññatti, na ekattapaññatti. ", "html": "{}"}, "pe8:52.3": {"pli": "Dhammāti ekato na vevacanapaññatti. ", "html": "{}"}, "pe8:52.4": {"pli": "Manasā ce pasannenāti so pasādo dvidho ajjhattañca abyāpādāvikkhambhanabahiddhā ca okappanato. ", "html": "{}"}, "pe8:52.5": {"pli": "So ajjhattapasādo dvidho. ", "html": "{}"}, "pe8:52.6": {"pli": "Samugghātapasādo ca vikkhambhanapasādo ca byāpādapariyuṭṭhānaṁ. ", "html": "{}"}, "pe8:52.7": {"pli": "Vighāto na mūlapasādo jātamūlampi vā. ", "html": "{}"}, "pe8:52.8": {"pli": "Pasādo sabyāpādaṁ vighātena. ", "html": "{}"}, "pe8:52.9": {"pli": "Tato naṁ sukhamanvetīti sukhaṁ kāyikañca cetasikañca appiyavippayogopi piyasampayogopi nekkhammasukhampi puthujjanasukhampi pītisambojjhaṅgampi cetasikaṁ sukhaṁ. ", "html": "{}"}, "pe8:52.10": {"pli": "Yampi passaddhakāyo sukhaṁ vedeti, tampi kāyikaṁ sukhaṁ bojjhaṅgā ca cetasikaṁ sukhaṁ. ", "html": "{}"}, "pe8:52.11": {"pli": "Yampi passaddhakāyo sukhaṁ vedesi, tampi tañca sukhapadaṭṭhānaṁ paññattiyā yathāvuttaṁ taṁ aparāmaṭṭhaṁ kusalānaṁ dhammānaṁ. ", "html": "{}"}, "pe8:52.12": {"pli": "Anvetīti appanā sandissati na cāyaṁ vā pattabhūto anveti. ", "html": "{}"}, "pe8:52.13": {"pli": "Tadidaṁ suttaṁ dvīhi ākārehi adhiṭṭhātabbaṁ. ", "html": "{}"}, "pe8:52.14": {"pli": "Hetunā ca yo pasannamānaso vipākena ca yo dukkhavedanīyo. ", "html": "{}</p>"}, "pe8:53.1": {"pli": "Parikkhāroti bhagavā pañcasatena bhikkhusaṅghena nagaraṁ pavisati rājagahaṁ. ", "html": "<p>{}"}, "pe8:53.2": {"pli": "Tattha manusso puggalo bhagavantaṁ parivisati, tassa pasādo uppanno kusalamūlapubbayogāvacaropi. ", "html": "{}"}, "pe8:53.3": {"pli": "So aññesañca akkhāti, idaṁ vācaṁ bhāsati lābhā tesaṁ, yesaṁ nivesanaṁ bhagavā pavisati, amhākampi yadi bhaveyya mayampi bhagavato sampasādaṁ lacchamhāti. ", "html": "{}"}, "pe8:53.4": {"pli": "Yena bhagavā tenañjaliṁ paṇāmetvā “namo bhagavato namo bhagavato”ti abyāpādamāno ekamante aṭṭhāsi. ", "html": "{}"}, "pe8:53.5": {"pli": "Tadanantare bhagavā imaṁ suttaṁ abhāsittha “manopubbaṅgamā dhammā”ti. ", "html": "{}"}, "pe8:53.6": {"pli": "Sabbaṁ suttaṁ tathā yaṁ paresaṁ bhāsati idaṁ vācākammaṁ. ", "html": "{}"}, "pe8:53.7": {"pli": "Yaṁ añjaliṁ paṇāmeti, idaṁ kāyakammaṁ. ", "html": "{}"}, "pe8:53.8": {"pli": "Yo manopasādo, idaṁ manokammaṁ. ", "html": "{}"}, "pe8:53.9": {"pli": "Tattha yaṁ paresaṁ pakāseti bhāsati vaṇṇaṁ. ", "html": "{}"}, "pe8:53.10": {"pli": "Yesaṁ bhagavā nivesanaṁ gacchatīti. ", "html": "{}"}, "pe8:53.11": {"pli": "Sabbaṁ tassa alobho kusalamūlaṁ. ", "html": "{}"}, "pe8:53.12": {"pli": "Yaṁ bhagavati mettacitto, tassa adoso kusalamūlaṁ. ", "html": "{}"}, "pe8:53.13": {"pli": "Yaṁ añjaliṁ paṇāmeti mānañca niggaṇhāti, tatthassa amoho kusalamūlaṁ pātubhavati. ", "html": "{}"}, "pe8:53.14": {"pli": "Yaṁ uḷārapaññaṁ paṭilabhati, idamassa diṭṭhivipallāsappahānaṁ. ", "html": "{}"}, "pe8:53.15": {"pli": "Yaṁ tathāyeva saṁvaro hoti, idamassa saññāvipallāsappahānaṁ. ", "html": "{}"}, "pe8:53.16": {"pli": "Yaṁ manassa pasādanaṁ, idamassa cittavipallāsappahānanti akusalavipallāsānaṁ vikkhambhanaṁ pahānaṁ paccayo. ", "html": "{}"}, "pe8:53.17": {"pli": "Tīṇi kusalamūlāni yo anāvilacittasaṅkappo, so tassa manasikāroti vuccati. ", "html": "{}"}, "pe8:53.18": {"pli": "Yaṁ kilesehi vikkhambhanaṁ iti vipallāsā ca ārammaṇā sappaccayatāya paccayo kusalamūlāni ca sandissayatāya paccayo, so ca manasikāro hetunā iminā paccayena cittaṁ uppannaṁ. ", "html": "{}"}, "pe8:53.19": {"pli": "Tattha yaṁ sasatthārammaṇaṁ cittaṁ pavattaṁ ayaṁ buddhānussati. ", "html": "{}"}, "pe8:53.20": {"pli": "Yampi bhagavato guṇe manasi karoti, ayamassa dhammānussati. ", "html": "{}"}, "pe8:53.21": {"pli": "Tattha satisampajaññaṁ hetu, ayañca paccayo. ", "html": "{}"}, "pe8:53.22": {"pli": "Vācā paññā hetu vitakkavicārā paccayo. ", "html": "{}"}, "pe8:53.23": {"pli": "Kāyasaṅkhārā kammassa abhisaṅkhāro nāma hetu vā appaccayo sukhavedanīyassa kammassa upacayo hetukā kammassa paccayo. ", "html": "{}</p>"}, "pe8:54.1": {"pli": "Tattha katamo samāropano hāro? ", "html": "<p>{}"}, "pe8:54.2": {"pli": "Manasāyeva pasannena satoyevettha pasanno api ca cittavodānā sattā vimuccantīti tena sattā cittapubbaṅgamā cittena pasannena cetanāpi tattha cittabhūtā bhavantīti paṭighā ayaṁ cetanānaṁ pasādena kāyo cassa pasādo, so ca ārabhati pasādena pasanno saññānanti cassa aviparītā, so pañcavidho vikkhambhanā, kāyapassambhanāyeva pasādo cittasito cittaṁ pana pubbaṁyeva pasannaṁ. ", "html": "{}"}, "pe8:54.3": {"pli": "Ayaṁ samāropanā. ", "html": "{}"}, "pe8:54.4": {"pli": "Evaṁ pañcannampi pasādo. ", "html": "{}"}, "pe8:54.5": {"pli": "Tato naṁ sukhamanvetīti katamaṁ bhagavā niddisati? ", "html": "{}"}, "pe8:54.6": {"pli": "Na hi attasaccaṁ tassa kammassa vipāko anveti. ", "html": "{}"}, "pe8:54.7": {"pli": "Tassa upāyo anugacchati yadā sitapaccayā uppajjate somanassaṁ avippaṭisāropi anveti. ", "html": "{}"}, "pe8:54.8": {"pli": "Ayaṁ samāropano hāro. ", "html": "{}</p>"}, "pe8:55.1": {"pli": "Mahānāmasakkassa suttaṁ. ", "html": "<p>{}"}, "pe8:55.2": {"pli": "Tasmiñce samaye assato asampajāno kālaṁ kareyya kāme bhavati. ", "html": "{}"}, "pe8:55.3": {"pli": "Assato abhisamāhāro yo mā bhāyi, mahānāma, yaṁ taṁ cittaṁ dīgharattaṁ saddhāparibhāvitaṁ sīlaparibhāvitaṁ sutacāgaparibhāvitanti vitthārena kātabbaṁ. ", "html": "{}"}, "pe8:55.4": {"pli": "Cāgena ca paññāya ca kiṁ dasseti? ", "html": "{}"}, "pe8:55.5": {"pli": "Yā saddhā, sā cetaso pasādo. ", "html": "{}"}, "pe8:55.6": {"pli": "Yā anāvilasaṅkappitā, sā saddhā. ", "html": "{}"}, "pe8:55.7": {"pli": "Kiṁ kāraṇā? ", "html": "{}"}, "pe8:55.8": {"pli": "Anāvilalakkhaṇā. ", "html": "{}"}, "pe8:55.9": {"pli": "Anāvilalakkhaṇā hi saddhā. ", "html": "{}"}, "pe8:55.10": {"pli": "Apare āhu guṇaparisuddhiniṭṭhāgamanalakkhaṇā, yañca apare vā vacanapaṭiggahalakkhaṇā saddhā. ", "html": "{}"}, "pe8:55.11": {"pli": "Aparo pariyāyo attānaṁ yadi evaṁ okappeti “nāhaṁ kiñci jānāmīti esā ahaṁ tattha anuññattā anaññatā”ti. ", "html": "{}"}, "pe8:55.12": {"pli": "Ayaṁ saddhāti. ", "html": "{}"}, "pe8:55.13": {"pli": "Aparo pariyāyo ekasaṭṭhiyā diṭṭhigatānaṁ ādīnavānupassanā aniccaṁ dukkhamanattāti. ", "html": "{}"}, "pe8:55.14": {"pli": "Tena ca padiṭṭhaṁ bhavati yathā gambhīre udapāne udakaṁ cakkhunā passati na ca kāyena abhisambhunāti. ", "html": "{}"}, "pe8:55.15": {"pli": "Evamassa ariyā nijjhānakkhantiyā diṭṭhi bhavati, na ca sacchikatā. ", "html": "{}"}, "pe8:55.16": {"pli": "Ayaṁ vuccati saddhā. ", "html": "{}"}, "pe8:55.17": {"pli": "Sā ca lokikā. ", "html": "{}"}, "pe8:55.18": {"pli": "Aparo pariyāyo khamati puthujjanabhūtassa vīsati cāti ko sakkāyādhīnā na niveso. ", "html": "{}"}, "pe8:55.19": {"pli": "Na etaṁ ekanti nayasaññā yathābhūtaṁ diṭṭhiyā tu khalu mudūhi pañcahi indriyehi dassanamaggena pahīnā bhavanti. ", "html": "{}"}, "pe8:55.20": {"pli": "Diṭṭhekaṭṭhā ca kilesā, ayaṁ saddhā. ", "html": "{}</p>"}, "pe8:56.1": {"pli": "Sotāpattaṅgamadukkhāyaṁ bhūmiyaṁ paripuṇṇā vuccati. ", "html": "<p>{}"}, "pe8:56.2": {"pli": "Tasmiṁyeva bhūmiyaṁ sekkhasīlaṁ ariyā dhāranti vuccati. ", "html": "{}"}, "pe8:56.3": {"pli": "Tasmiṁyeva bhūmiyaṁ mudupaññā paññindriyanti vuccati. ", "html": "{}"}, "pe8:56.4": {"pli": "Tasmiṁyeva bhūmiyaṁ khandhehi anatthikatā, ayaṁ cāgo. ", "html": "{}"}, "pe8:56.5": {"pli": "Tasmā saddhā cāgādhiṭṭhānena niddisitabbā. ", "html": "{}"}, "pe8:56.6": {"pli": "Yatikena bhiyyo manena sā hissa viparītā diṭṭhikā assaddhā, sā nayanaupadhīsu pamattā samādinnā. ", "html": "{}"}, "pe8:56.7": {"pli": "Tattha saddhindriyaṁ yo kāmaṁ parivissanti iti santapāpapaṭinissaggā na cāgādhiṭṭhānaṁ paññindriyena paññādhiṭṭhānaṁ, sīlena upasamādhiṭṭhānaṁ. ", "html": "{}"}, "pe8:56.8": {"pli": "Ime cattāro dhammā sīlaṁ paribhāvayanti saddhā sīlaṁ cāgo ca paññāti. ", "html": "{}"}, "pe8:56.9": {"pli": "Tattha saddhāya oghaṁ tarati. ", "html": "{}"}, "pe8:56.10": {"pli": "Yaṁ sīlaṁ, ayaṁ appamādo. ", "html": "{}"}, "pe8:56.11": {"pli": "Yo cāgo, idaṁ paññāya kammaṁ. ", "html": "{}"}, "pe8:56.12": {"pli": "Yā paññā, idaṁ paññindriyaṁ, tattha yaṁ saddhindriyaṁ. ", "html": "{}"}, "pe8:56.13": {"pli": "Taṁ tīsu aveccappasādesu. ", "html": "{}"}, "pe8:56.14": {"pli": "Yaṁ sīlaṁ, taṁ saddhindriyesu. ", "html": "{}"}, "pe8:56.15": {"pli": "Yo cāgo, so catūsu jhānesu. ", "html": "{}"}, "pe8:56.16": {"pli": "Yā paññā, sā saccesu, sati sabbatthagāminī. ", "html": "{}"}, "pe8:56.17": {"pli": "Tassa sekkhassa bhaddikā bhati, bhaddiko abhisamparāyo. ", "html": "{}"}, "pe8:56.18": {"pli": "Tassa sammuṭṭhassatikassa sīlaṁ karontassa na kāyasammuṭṭhassatitāya tāni vā indriyāni taṁ vā kusalamūlaṁ kammavipākaṁ bhavati. ", "html": "{}"}, "pe8:56.19": {"pli": "Tassa tikassa atthaniddeso. ", "html": "{}"}, "pe8:56.20": {"pli": "Tattha saddhā sīlaṁ cāgo paññā cattāro dhammā. ", "html": "{}"}, "pe8:56.21": {"pli": "Yā saddhā yā ca paññā, idaṁ manosucaritaṁ. ", "html": "{}"}, "pe8:56.22": {"pli": "Yaṁ sīlaṁ, idaṁ kāyikaṁ vācasikaṁ sucaritaṁ. ", "html": "{}"}, "pe8:56.23": {"pli": "Yo cāgo, idaṁ cetasikaṁ alobho sucaritaṁ. ", "html": "{}"}, "pe8:56.24": {"pli": "Iti citte gahite pañcakkhandhā gahitā bhavanti. ", "html": "{}"}, "pe8:56.25": {"pli": "Imehi dhammehi sucaritaṁ idaṁ dukkhañca ariyasaccaṁ padaṭṭhānaṁ maggassa. ", "html": "{}</p>"}, "pe8:57.1": {"pli": "Tattha katamo vicayo hāro? ", "html": "<p>{}"}, "pe8:57.2": {"pli": "Yā ca saddhā yañca sīlaṁ. ", "html": "{}"}, "pe8:57.3": {"pli": "Taṁ kissa karoti? ", "html": "{}"}, "pe8:57.4": {"pli": "Yā saddhā tāya bhagavantaṁ anussarati mattenapi hatthinā samāgatā, assa bho kukkurā sabbaṁ sīlena nappaṭipajjati kāyena vā vācāya vā ṭhānaṁ visārado bhavatīti avippaṭisārī paññā yassa paññattaṁ upaṭṭhapeti. ", "html": "{}"}, "pe8:57.5": {"pli": "Tassa akhaṇḍassa sīlaṁ yaṁ na pacchi tassaṁ mohassa akusalacittaṁ uppajjati micchādiṭṭhisahagataṁ vā, ayaṁ vicayo hāro. ", "html": "{}"}, "pe8:57.6": {"pli": "Dhammavādino bhaddikārāti bhavissati atthi esā yutti. ", "html": "{}</p>"}, "pe8:58.1": {"pli": "Tattha katamo padaṭṭhāno hāro? ", "html": "<p>{}"}, "pe8:58.2": {"pli": "Yamidaṁ cittaṁ dīgharattaṁ paribhāvitaṁ saddhāya sīlena cāgena paññāya samādhinā paṭhamajjhānassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:58.3": {"pli": "Yā saddhā assa anāvilasaṅkappo, taṁ dutiyajjhānassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:58.4": {"pli": "Tīṇi ca aveccappasādā yaṁ sīlaṁ, taṁ ariyakantaṁ, taṁ sīlakkhandhassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:58.5": {"pli": "Yā paññā, sā paññākkhandhassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:58.6": {"pli": "Ime ca dhammā idañca cittaṁ ekodibhūtasamādhissa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:58.7": {"pli": "Saddhā saddhindriyassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:58.8": {"pli": "Cāgo samādhindriyassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:58.9": {"pli": "Paññā paññindriyassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:58.10": {"pli": "Saddhā ca paññā ca vipassanāya padaṭṭhānaṁ. ", "html": "{}"}, "pe8:58.11": {"pli": "Sīlañca cāgo ca samathassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:58.12": {"pli": "Saddhā ca paññā ca avijjā virāgāya paññāvimuttiyā padaṭṭhānaṁ. ", "html": "{}"}, "pe8:58.13": {"pli": "Sīlañca cāgo ca rāgavirāgāya cetovimuttiyā padaṭṭhānaṁ. ", "html": "{}</p>"}, "pe8:59.1": {"pli": "Tattha katamo lakkhaṇo hāro? ", "html": "<p>{}"}, "pe8:59.2": {"pli": "Viññāṇe vutte saddhāsatibhāvite sabbe pañcakkhandhā vuttā bhavanti. ", "html": "{}"}, "pe8:59.3": {"pli": "Saddhāya bhaṇitāya sabbāni satta dhanāni bhaṇitāni honti saddhādhanaṁ …pe… ", "html": "{}"}, "pe8:59.4": {"pli": "sīlakkhandhe vutte samādhikkhandho ca paññākkhandho ca vuttā bhavanti. ", "html": "{}"}, "pe8:59.5": {"pli": "Yaṁ taṁ cittaṁ dīgharattaṁ paribhāvitaṁ pacchimake kāle na tadanuparivatti bhavissatīti netaṁ ṭhānaṁ vijjati. ", "html": "{}"}, "pe8:59.6": {"pli": "Tattha saññāpi tadanuparivattinī bhavati. ", "html": "{}"}, "pe8:59.7": {"pli": "Yepi tajjātikā dhammā, tepi tadanuparivattino bhavanti. ", "html": "{}"}, "pe8:59.8": {"pli": "Rūpasaññā rūpasañcetanānupassanamanasikāro evaṁ channaṁ āyatanānaṁ viññāṇakāye, ayaṁ lakkhaṇo hāro. ", "html": "{}</p>"}, "pe8:60.1": {"pli": "Tattha katamo catubyūho hāro? ", "html": "<p>{}"}, "pe8:60.2": {"pli": "Idha sutte bhagavato ko adhippāyo? ", "html": "{}"}, "pe8:60.3": {"pli": "Ye bhaddikaṁ bhatiṁ ākaṅkheyya bhaddikañca abhisamparāyaṁ, te saddhaṁ sīlaṁ cāgaṁ paññañca manasi karissanti, ayaṁ adhippāyo. ", "html": "{}"}, "pe8:60.4": {"pli": "Ye caññepi sattā tathāgatassa sammukhaṁ na paṭiyujjhante, imaṁ dhammaṁ sotā avippaṭisārato kālaṁ karissantīti, ayaṁ adhippāyo. ", "html": "{}</p>"}, "pe8:61.1": {"pli": "Tattha katamo āvaṭṭo hāro? ", "html": "<p>{}"}, "pe8:61.2": {"pli": "Idampi cattāro dhammā saddhā ca paññā ca assaddhiyañca avijjañca hananti. ", "html": "{}"}, "pe8:61.3": {"pli": "Sīlañca cāgo ca taṇhā ca dosañca hananti. ", "html": "{}"}, "pe8:61.4": {"pli": "Tassa dve mūlāni pahīyanti. ", "html": "{}"}, "pe8:61.5": {"pli": "Dukkhaṁ nivatteti appahīnabhūmiyañca dvimūlāni pañcakkhandhā. ", "html": "{}"}, "pe8:61.6": {"pli": "Dve ariyasaccāni samatho ca vipassanā ca. ", "html": "{}"}, "pe8:61.7": {"pli": "Dvinnaṁ mūlānaṁ pahānaṁ. ", "html": "{}"}, "pe8:61.8": {"pli": "Imāni dve saccāni nirodho ca maggo ca. ", "html": "{}"}, "pe8:61.9": {"pli": "Ayaṁ āvaṭṭo hāro. ", "html": "{}</p>"}, "pe8:62.1": {"pli": "Tattha katamo vibhatti? ", "html": "<p>{}"}, "pe8:62.2": {"pli": "Yaṁ taṁ cittaṁ saddhāparibhāvitaṁ …pe… ", "html": "{}"}, "pe8:62.3": {"pli": "sace puthujjanassa tassapi bhaddikā bhati bhavissatīti na ekaṁsena tassa kammaṁ diṭṭheyeva dhamme vipākanti paccessati, aparamhi vā pariyāye bhavissati. ", "html": "{}"}, "pe8:62.4": {"pli": "Yaṁ vā atītaṁ vipākāya paccupaṭṭhitaṁ, tappaccayāni cetāni, ye yathā mahākammavibhaṅge “tenāyaṁ vibhajjabyākaraṇiyo niddeso dhammacārino yā bhaddikā bhatī”ti. ", "html": "{}</p>"}, "pe8:63.1": {"pli": "Tattha katamā parivattanā? ", "html": "<p>{}"}, "pe8:63.2": {"pli": "Assaddhiyaṁ dussīlyaṁ yaṁ maccheraṁ duppaññaṁ ca yañca paṭipakkhena pahīnā bhavanti, ayaṁ parivattanā. ", "html": "{}</p>"}, "pe8:64.1": {"pli": "Tattha katamaṁ vevacanaṁ? ", "html": "<p>{}"}, "pe8:64.2": {"pli": "Yaṁ taṁ cittaṁ dīgharattaṁ paribhāvitaṁ cittaṁ manoviññāṇaṁ …pe… ", "html": "{}"}, "pe8:64.3": {"pli": "yaṁ saddhābalaṁ saddhindriyaṁ, yaṁ sīlaṁ taṁ sucaritaṁ, saṁyamo niyamo damo khandhatā imāni tassa vevacanāni. ", "html": "{}"}, "pe8:64.4": {"pli": "Yo cāgo so paṭinissaggo alobho vosaggo cāgoyiṭṭhānaṁ. ", "html": "{}"}, "pe8:64.5": {"pli": "Yā paññā sā paññattā paññappabhā paññindriyaṁ paññābalaṁ. ", "html": "{}</p>"}, "pe8:65.1": {"pli": "Tattha katamā paññatti? ", "html": "<p>{}"}, "pe8:65.2": {"pli": "Yaṁ taṁ cittaṁ bījaṁ paññattiyā paññattaṁ. ", "html": "{}"}, "pe8:65.3": {"pli": "Paribhāvanā vāsanā paññattiyā paññatti. ", "html": "{}"}, "pe8:65.4": {"pli": "Saddhā pasādapaññattiyā paññattā. ", "html": "{}"}, "pe8:65.5": {"pli": "Sīlaṁ sucaritapaññattiyā paññattaṁ. ", "html": "{}"}, "pe8:65.6": {"pli": "Cāgo puññakiriyapaññattiyā paññatto. ", "html": "{}"}, "pe8:65.7": {"pli": "Paññā vīmaṁsapaññattiyā paññattā. ", "html": "{}"}, "pe8:65.8": {"pli": "Ime tayo dhammā saddhā sīlaṁ cāgo paññavato pārisuddhiṁ gacchanti. ", "html": "{}</p>"}, "pe8:66.1": {"pli": "Tattha katamo otaraṇo? ", "html": "<p>{}"}, "pe8:66.2": {"pli": "Yaṁ cittaṁ, taṁ khandhesu viññāṇakkhandho, dhātūsu manoviññāṇadhātu, āyatanesu manāyatanaṁ. ", "html": "{}"}, "pe8:66.3": {"pli": "Ye cattāro dhammā, te khandhesu saṅkhārakkhandhe pariyāpannā …pe… ", "html": "{}"}, "pe8:66.4": {"pli": "dhātūsu āyatanesu. ", "html": "{}</p>"}, "pe8:67.1": {"pli": "Tattha katamo sodhano hāro? ", "html": "<p>{}"}, "pe8:67.2": {"pli": "Idaṁ bhagavato bhāsitaṁ mahānāmena sakkena pucchitena sabbaṁ taṁ niyuttaṁ. ", "html": "{}</p>"}, "pe8:68.1": {"pli": "Tattha katamo adhiṭṭhāno? ", "html": "<p>{}"}, "pe8:68.2": {"pli": "Idaṁ cittaṁ vemattatāya paññattaṁ akusalehi cittehi aparibhāvitehi paribhāvitanti yāni puna paribhāvitāni aññesampi tattha upādāya paññattaṁ sabbepime cattāro dhammā ekattatāya paññattā. ", "html": "{}"}, "pe8:68.3": {"pli": "Bhaddikā bhatīti kāmabhogino teva rūpadhātu arūpadhātu manussāti sabbā bhaddikā bhati tadeva kathāya paññattaṁ, ayaṁ paññatti. ", "html": "{}</p>"}, "pe8:69.1": {"pli": "Tattha katamo parikkhāro? ", "html": "<p>{}"}, "pe8:69.2": {"pli": "Cittassa indriyāni paccayo ādhipateyyapaccayatāya manasikāro. ", "html": "{}"}, "pe8:69.3": {"pli": "Hetupaccayatāya paccayo. ", "html": "{}"}, "pe8:69.4": {"pli": "Saddhāya lokikā paññā hetupaccayatāya paccayo. ", "html": "{}"}, "pe8:69.5": {"pli": "Yoniso ca manasikāro paccayo. ", "html": "{}"}, "pe8:69.6": {"pli": "Sīlassa patirūpadesavāso paccayo. ", "html": "{}"}, "pe8:69.7": {"pli": "Attasammāpaṇidhānañca hetu. ", "html": "{}"}, "pe8:69.8": {"pli": "Cāgassa alobho hetu. ", "html": "{}"}, "pe8:69.9": {"pli": "Avippaṭisāro ca hetupaccayo. ", "html": "{}"}, "pe8:69.10": {"pli": "Paññā parato ca ghoso ajjhattañca yoniso manasikāro hetupaccayo ca. ", "html": "{}</p>"}, "pe8:70.1": {"pli": "Tattha katamo samāropano? ", "html": "<p>{}"}, "pe8:70.2": {"pli": "Yaṁ taṁ cittaṁ dīgharattaṁ paribhāvitanti cetasikāpi. ", "html": "{}"}, "pe8:70.3": {"pli": "Ettha sabbe dhammā paribhāvitā bhaddikā te bhati bhavissati, bhaddikā upapattiko abhisamparāyo. ", "html": "{}"}, "pe8:70.4": {"pli": "Iti ye keci manussakā upabhogaparibhogā sabbe bhaddikā bhatiyeva, ayaṁ samāropano. ", "html": "{}</p>"}, "pe8:71.1": {"pli": "Uddhaṁ adho sabbadhi vītarāgoti gāthā. ", "html": "<p>{}"}, "pe8:71.2": {"pli": "Tattha kiṁ uddhaṁ nāma? ", "html": "{}"}, "pe8:71.3": {"pli": "Yaṁ ito uddhaṁ bhavissati anāgāmī, idaṁ uddhaṁ. ", "html": "{}"}, "pe8:71.4": {"pli": "Adho nāma yamatikkantamatītaṁ, idamavoca apadānatanti uddhaṁ. ", "html": "{}"}, "pe8:71.5": {"pli": "Tattha atītena sassatadiṭṭhi pubbantākappikānaṁ aparantadiṭṭhi kesañci, ucchedadiṭṭhiṁ yaṁ vuttakappikānaṁ imā ceva diṭṭhiyo ucchedadiṭṭhi ca sassatadiṭṭhi ca. ", "html": "{}"}, "pe8:71.6": {"pli": "Tatthāyaṁ sassatadiṭṭhi imāni pannarasa padāni sakkāyadiṭṭhi sassataṁ bhajanti. ", "html": "{}"}, "pe8:71.7": {"pli": "Rūpavantaṁ me attā, attani me rūpaṁ, rūpaṁ me attāti yaduccate paññaṁ paridahanti. ", "html": "{}"}, "pe8:71.8": {"pli": "Yā ucchedadiṭṭhi sā pañcasatāni ucchedaṁ bhajanti. ", "html": "{}"}, "pe8:71.9": {"pli": "Te “taṁ jīvaṁ taṁ sarīran”ti passanti, rūpaṁ me attāti tathārūpā catubbidhā sakkāyadiṭṭhi ucchedena ca sassatena ca. ", "html": "{}"}, "pe8:71.10": {"pli": "Evaṁ pañcasu khandhesu vīsativatthukāya diṭṭhiyā pannarasa padāni pubbantaṁ bhajanti. ", "html": "{}"}, "pe8:71.11": {"pli": "Sassatadiṭṭhiyā pañca padāni aparantaṁ bhajanti ucchedadiṭṭhiyā. ", "html": "{}"}, "pe8:71.12": {"pli": "Tattha “ayamahamasmī”ti passantā rūpaṁ attato samanupassati, so ucchedavādī rūpavantañca attānaṁ, attani ca rūpaṁ, rūpasmiṁ vā attāti so passati cāti iti ucchedadiṭṭhi ca, attato paṭissarati sassatadiṭṭhi pubbantato ca paṭissarati. ", "html": "{}"}, "pe8:71.13": {"pli": "“Ayamahamasmī”ti na samanupassati. ", "html": "{}"}, "pe8:71.14": {"pli": "Tassa diṭṭhāsavā pahānaṁ gacchanti. ", "html": "{}"}, "pe8:71.15": {"pli": "Yo tīsu addhāsu pubbante ca aparante ca tena tena niddiṭṭhānena uddhaṁ adho sabbadhi vītarāgo “ahamasmī”ti na anupassatīti iminā dvārena iminā payogena iminā upāyena idaṁ dassanabhūmi ca sotāpattiphalañca so ariyo payogo anabhāvaṅgatena saṁsārena apunabbhavāti yo koci ariyo payogo punabbhavāya mudūni vā pañcindriyāni majjhāni adhimattāni vā sabbaṁ apunabbhavappahānāya saṁvattanti. ", "html": "{}"}, "pe8:71.16": {"pli": "Ahanti diṭṭhogho kāmogho bhavogho avijjogho ca odhiso. ", "html": "{}"}, "pe8:71.17": {"pli": "Tattha desanāhārena cattāri saccāni pañcahi indriyehi sotāpattiphalena ca dve saccāni maggo ca nirodho ca. ", "html": "{}"}, "pe8:71.18": {"pli": "Sakkāyasamudayena dve saccāni dukkhañca samudayo ca, ayaṁ desanā hāro. ", "html": "{}</p>"}, "pe8:72.1": {"pli": "Tattha katamo vicayo? ", "html": "<p>{}"}, "pe8:72.2": {"pli": "“Ayamahamasmī”ti asamanupassanto tīṇi dassanappahātabbāni saṁyojanāni pajahati. ", "html": "{}"}, "pe8:72.3": {"pli": "Ayaṁ vicayo. ", "html": "{}</p>"}, "pe8:73.1": {"pli": "Tattha katamā yutti? ", "html": "<p>{}"}, "pe8:73.2": {"pli": "Tividhā puggalā koci ugghaṭitaññū koci vipañcitaññū koci neyyo. ", "html": "{}"}, "pe8:73.3": {"pli": "Ugghaṭitaññū tikkhindriyo ca tato vipañcitaññū mudindriyo tato mudindriyehi neyyo. ", "html": "{}"}, "pe8:73.4": {"pli": "Tattha ugghaṭitaññū tikkhindriyatāya dassanabhūmimāgamma sotāpattiphalaṁ pāpuṇāti, ekabījako bhavati. ", "html": "{}"}, "pe8:73.5": {"pli": "Ayaṁ paṭhamo sotāpanno. ", "html": "{}"}, "pe8:73.6": {"pli": "Vipañcitaññū mudūhi indriyehi dassanabhūmimāgamma sotāpattiphalaṁ pāpuṇāti, kolaṅkolo ca hoti. ", "html": "{}"}, "pe8:73.7": {"pli": "Ayaṁ dutiyo sotāpanno. ", "html": "{}"}, "pe8:73.8": {"pli": "Tattha neyyo dassanabhūmimāgamma sotāpattiphalaṁ pāpuṇāti, sattakkhattuparamo ca bhavati. ", "html": "{}"}, "pe8:73.9": {"pli": "Ayaṁ tatiyo sotāpanno. ", "html": "{}</p>"}, "pe8:74.1": {"pli": "Atthi esā yutti mudumajjhādhimattehi indriyehi mudumajjhādhimattaṁ bhūmiṁ sacchikareyya sakkāyadiṭṭhippahānena vā diṭṭhigatāni pajahati. ", "html": "<p>{}"}, "pe8:74.2": {"pli": "Ayaṁ yutti. ", "html": "{}</p>"}, "pe8:75.1": {"pli": "Tattha katamo padaṭṭhāno? ", "html": "<p>{}"}, "pe8:75.2": {"pli": "Tattha sakkāyadiṭṭhi sabbamicchādiṭṭhiyā padaṭṭhānaṁ. ", "html": "{}"}, "pe8:75.3": {"pli": "Sakkāyo nāmarūpassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:75.4": {"pli": "Nāmarūpaṁ sakkāyadiṭṭhiyā padaṭṭhānaṁ. ", "html": "{}"}, "pe8:75.5": {"pli": "Pañca indriyāni rūpīni rūparāgassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:75.6": {"pli": "Saḷāyatanaṁ ahaṅkārassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:75.7": {"pli": "Tattha katamo lakkhaṇo? ", "html": "{}"}, "pe8:75.8": {"pli": "Dvīsu diṭṭhīsu pahīnāsu tattha ekā diṭṭhi diṭṭhigatāni pahānaṁ gacchanti. ", "html": "{}"}, "pe8:75.9": {"pli": "Uddhaṁ ca adho ca vītarāgo sabbarajanīyesu vītarāgo hoti. ", "html": "{}"}, "pe8:75.10": {"pli": "Tajjā parabhūmiyaṁ, idaṁ paccayanti yathābhūtaṁ passati. ", "html": "{}"}, "pe8:75.11": {"pli": "So sabbapaṭiccasamuppādaṁ āmasati. ", "html": "{}"}, "pe8:75.12": {"pli": "Ayaṁ lakkhaṇo hāro. ", "html": "{}</p>"}, "pe8:76.1": {"pli": "Tattha katamo catubyūho hāro? ", "html": "<p>{}"}, "pe8:76.2": {"pli": "Imamhi sutte bhagavato ko adhippāyo? ", "html": "{}"}, "pe8:76.3": {"pli": "Ye sattā ye nābhiramissanti, te diṭṭhippahānāya vāyamissanti. ", "html": "{}"}, "pe8:76.4": {"pli": "Ayamettha bhagavato adhippāyo. ", "html": "{}"}, "pe8:76.5": {"pli": "Ayaṁ catubyūho hāro. ", "html": "{}</p>"}, "pe8:77.1": {"pli": "Tattha katamo āvaṭṭo hāro? ", "html": "<p>{}"}, "pe8:77.2": {"pli": "Yānimāni mudūni pañcindriyāni tāni orambhāgiyāni pañcindriyāni. ", "html": "{}"}, "pe8:77.3": {"pli": "Sabbena sabbaṁ samūhananti abhijjhābyāpādo ca bhāvanākārena sekkhāya vimuttiyā balaṁ saddhā, uddhambhāgiyāni diṭṭhivasena balaṁ saddhā, vīriyindriyaṁ ārabhitattā satindriyaṁ paggahitattā accantaṁ niṭṭhaṁ gacchanti. ", "html": "{}"}, "pe8:77.4": {"pli": "Tattha yāni indriyāni, ayaṁ maggo saṅkilesappahānaṁ. ", "html": "{}"}, "pe8:77.5": {"pli": "Ayaṁ nirodho āyatiṁ anuppādadhammo, idaṁ dukkhaṁ. ", "html": "{}"}, "pe8:77.6": {"pli": "Ayaṁ āvaṭṭo hāro. ", "html": "{}</p>"}, "pe8:78.1": {"pli": "Tattha katamo vibhatti hāro? ", "html": "<p>{}"}, "pe8:78.2": {"pli": "“Ayamahamasmī”ti yo samanupassati, so ca kho adhimattena lokikā yaṁ bhūmiyaṁ na tu ariyena payogena so sakkāyadiṭṭhi pajahati. ", "html": "{}"}, "pe8:78.3": {"pli": "Yaṁ vuccati tajjāya bhūmiyā adhimattāya. ", "html": "{}"}, "pe8:78.4": {"pli": "Tattha tajjāya bhūmiyaṁ pañcahi ākārehi adhimattataṁ paṭilabhati sīlena vatena bāhusaccena samādhinā nekkhammasukhena. ", "html": "{}"}, "pe8:78.5": {"pli": "Tattha appatte pattasaññī adhimānaṁ gaṇhāti. ", "html": "{}"}, "pe8:78.6": {"pli": "Etasmiṁyeva vatthuppattiyaṁ bhagavā idaṁ suttaṁ bhāsati. ", "html": "{}"}, "pe8:78.7": {"pli": "Sīlavā vatamattenāti. ", "html": "{}"}, "pe8:78.8": {"pli": "Tattha yo appatte pattasaññī tassa yo samādhi, so sāmiso kāpurisasevito pana so kāpurisā vuccanti puthujjanā. ", "html": "{}"}, "pe8:78.9": {"pli": "Āmisaṁ yañca ariyamaggamāgamma lokikā anariyaṁ tena samādhi hoti anariyo kāpurisasevito. ", "html": "{}"}, "pe8:78.10": {"pli": "Yo pana ariyākārena yathābhūtaṁ na jānāti na passati, so adhigamanaṁ pajahati yo ariyena samādhinā akāpurisasevitena nirāmisena nīyati, tattha akāpurisā vuccanti ariyapuggalā. ", "html": "{}"}, "pe8:78.11": {"pli": "Yo tehi sevito samādhi, so akāpurisasevito. ", "html": "{}"}, "pe8:78.12": {"pli": "Tasmā ekaṁ vibhajjabyākaraṇīyaṁ “ayamahamasmī”ti asamanupassanto tathā pāteti. ", "html": "{}</p>"}, "pe8:79.1": {"pli": "Tattha katamā parivattanā? ", "html": "<p>{}"}, "pe8:79.2": {"pli": "Imāya dassanabhūmiyā kilesā pahātabbā, tehi pahīyanti aniddiṭṭhāpi bhagavatā niddisitabbā yo. ", "html": "{}</p>"}, "pe8:80.1": {"pli": "Tattha katamaṁ vevacanaṁ? ", "html": "<p>{}"}, "pe8:80.2": {"pli": "Yā sakkāyadiṭṭhiyā attadiṭṭhiyā. ", "html": "{}"}, "pe8:80.3": {"pli": "Ayaṁ bhūmi. ", "html": "{}"}, "pe8:80.4": {"pli": "Ye kilesā pahātabbā. ", "html": "{}"}, "pe8:80.5": {"pli": "Te appahīyanti aniddiṭṭhāpi bhagavatā sassatadiṭṭhi ca ucchedadiṭṭhi ca, sā pariyantadiṭṭhi ca. ", "html": "{}"}, "pe8:80.6": {"pli": "Yā apariyantadiṭṭhi ca, sā sassatadiṭṭhi ca. ", "html": "{}"}, "pe8:80.7": {"pli": "Yā ucchedadiṭṭhi, sā natthikā diṭṭhi. ", "html": "{}"}, "pe8:80.8": {"pli": "Yā sassatadiṭṭhi, sā akiriyadiṭṭhi. ", "html": "{}"}, "pe8:80.9": {"pli": "Idaṁ vevacanaṁ. ", "html": "{}</p>"}, "pe8:81.1": {"pli": "Tattha katamā paññatti? ", "html": "<p>{}"}, "pe8:81.2": {"pli": "Taṇhā saṁyojanapaññattiyā paññattā. ", "html": "{}"}, "pe8:81.3": {"pli": "Maggo paṭilābhapaññattiyā paññatto. ", "html": "{}"}, "pe8:81.4": {"pli": "Indriyā paṭilābhapaññattiyā paññattāti. ", "html": "{}"}, "pe8:81.5": {"pli": "Tattha katamo otaraṇo? ", "html": "{}"}, "pe8:81.6": {"pli": "Sakkāyo dukkhaṁ dassanappahātabbo. ", "html": "{}"}, "pe8:81.7": {"pli": "Samudayo maggo. ", "html": "{}"}, "pe8:81.8": {"pli": "Indriyāni tāni ca niddiṭṭhāni khandhadhātuāyatanesu. ", "html": "{}</p>"}, "pe8:82.1": {"pli": "Tattha katamo sodhano hāro? ", "html": "<p>{}"}, "pe8:82.2": {"pli": "Yañhi ārabbha bhagavatā idaṁ suttaṁ bhāsitaṁ, so ārabbha niddiṭṭho. ", "html": "{}"}, "pe8:82.3": {"pli": "Tattha katamo parikkhāro? ", "html": "{}"}, "pe8:82.4": {"pli": "Nāmarūpassa hetupaccayopi viññāṇaṁ hetu bījaṁ. ", "html": "{}"}, "pe8:82.5": {"pli": "Tena avijjā ca saṅkhārā ca paccayo. ", "html": "{}"}, "pe8:82.6": {"pli": "Nivattinayo na aparo pariyāyo sabbabhavo, ye ca sabbabhavassa hetu parabhaṇḍapaccayo iti sammādiṭṭhi parato ca ghoso yoniso ca manasikāro paccayo. ", "html": "{}"}, "pe8:82.7": {"pli": "Yā paññā uppādeti, esā hetu sammādiṭṭhiyā sammāsaṅkappo bhavati, yā sammāsamādhi, ayaṁ parikkhāro. ", "html": "{}</p>"}, "pe8:83.1": {"pli": "Tattha katamo samāropano? ", "html": "<p>{}"}, "pe8:83.2": {"pli": "“Ayamahamasmī”ti asamanupassī dukkhato rogato …pe… ", "html": "{}"}, "pe8:83.3": {"pli": "pannarasa padāni. ", "html": "{}"}, "pe8:83.4": {"pli": "Sīlāni bhagavā kimatthiyāni kimānisaṁsāni. ", "html": "{}"}, "pe8:83.5": {"pli": "Sīlāni, ānanda, avippaṭisāratthāni yāva vimutti. ", "html": "{}"}, "pe8:83.6": {"pli": "Tattha duvidho attho—", "html": "{}"}, "pe8:83.7": {"pli": "purisattho ca vacanattho ca. ", "html": "{}</p>"}, "pe8:84.1": {"pli": "Tattha katamo purisattho? ", "html": "<p>{}"}, "pe8:84.2": {"pli": "Yāyaṁ na pacchānutāpitā ayaṁ avippaṭisāro, ayaṁ purisattho. ", "html": "{}"}, "pe8:84.3": {"pli": "Yathā koci brūhayati imatthamāsevati so bhaṇeyya, kiñci mamettha adhīnaṁ tassatthāya idaṁ kiriyaṁ ārabhāmīti. ", "html": "{}"}, "pe8:84.4": {"pli": "Ayaṁ purisattho. ", "html": "{}</p>"}, "pe8:85.1": {"pli": "Tattha katamo vacanattho? ", "html": "<p>{}"}, "pe8:85.2": {"pli": "Sīlāni kāyikaṁ vā vācasikaṁ vā sucaritaṁ avippaṭisāroti. ", "html": "{}"}, "pe8:85.3": {"pli": "Tattha sīlassa vatassa ca bhāsoyeva. ", "html": "{}"}, "pe8:85.4": {"pli": "Anaññā sugatakammatā sucaritaṁ ayaṁ avippaṭisāro. ", "html": "{}"}, "pe8:85.5": {"pli": "Evaṁ yāva vimuttīti ekamekasmiṁ pade dve atthā—", "html": "{}"}, "pe8:85.6": {"pli": "purisattho ca vacanattho ca, yathā imamhi sutte evaṁ sabbesu suttesu dve dve atthā. ", "html": "{}"}, "pe8:85.7": {"pli": "Ayaṁ hi paramattho uttamattho ca. ", "html": "{}"}, "pe8:85.8": {"pli": "Yaṁ nibbānasacchikaṁ nissāya yaṁ sakaṁ sacchikātabbaṁ bhavati, so vuccati katassa katthoti. ", "html": "{}"}, "pe8:85.9": {"pli": "Ayaṁ puna vevacanaṁ sampajānāti. ", "html": "{}"}, "pe8:85.10": {"pli": "Iminā niyuttatthamabhilabbhanti vacanattho. ", "html": "{}"}, "pe8:85.11": {"pli": "Tattha yaṁ atthaṁ sāvako abhikaṅkhati. ", "html": "{}"}, "pe8:85.12": {"pli": "Tassa yo paṭilābho, ayaṁ purisattho. ", "html": "{}"}, "pe8:85.13": {"pli": "Yaṁ yaṁ bhagavā dhammaṁ deseti, tassa tassa dhammassa yā atthaviññatti. ", "html": "{}"}, "pe8:85.14": {"pli": "Ayaṁ attho, tattha sīlānaṁ avippaṭisāro atthopi ānisaṁsopi. ", "html": "{}"}, "pe8:85.15": {"pli": "Eso ca ānisaṁso yaṁ duggatiṁ na gacchati. ", "html": "{}"}, "pe8:85.16": {"pli": "Yathā taṁ bhagavatā esānisaṁso dhamme suciṇṇe na duggatiṁ gacchati dhammacārī, ayaṁ attho. ", "html": "{}</p>"}, "pe8:86.1": {"pli": "Yaṁ puriso bhāvanābhūmiyaṁ sīlāni ārabbha sīlena saṁyutto hoti evaṁ yāva vimutti tathā sīlakkhandho. ", "html": "<p>{}"}, "pe8:86.2": {"pli": "Tattha yo ca avippaṭisāro anusayavasena niddiṭṭho, tañca sīlaṁ ayaṁ sīlakkhandho. ", "html": "{}"}, "pe8:86.3": {"pli": "Pāmojjapītipassaddhīti ca samādhindriyena, ayaṁ samādhikkhandho. ", "html": "{}"}, "pe8:86.4": {"pli": "Yaṁ samāhito yathābhūtaṁ pajānāti, ayaṁ paññākkhandho. ", "html": "{}"}, "pe8:86.5": {"pli": "Ime tayo khandhā sīlaṁ samādhi paññā ca tathā sīlaṁ paripūreti yaṁ vīriyindriyaṁ tena kāraṇena so sīlaṁ paripūreti, anuppannassa ca akusalassa anuppādāya vāyamati, uppannassa ca pahānāya anuppannassa ca kusalassa uppādāya, uppannassa ca kusalassa bhiyyobhāvāya iti vīriyindriyaṁ niddiṭṭhaṁ. ", "html": "{}"}, "pe8:86.6": {"pli": "Tattha yo samādhikkhandho, idaṁ samādhindriyaṁ. ", "html": "{}"}, "pe8:86.7": {"pli": "Paññākkhandho paññindriyaṁ, taṁ catūsu sammappadhānesu daṭṭhabbaṁ. ", "html": "{}"}, "pe8:86.8": {"pli": "Tathā yo anuppannassa ca akusalassa anuppādāya vāyamati, idaṁ paṭhamaṁ sammappadhānaṁ. ", "html": "{}"}, "pe8:86.9": {"pli": "Yaṁ uppannassa, idaṁ dutiyaṁ. ", "html": "{}"}, "pe8:86.10": {"pli": "Cattāri sammappadhānāni catūsu jhānesu passitabbāni. ", "html": "{}"}, "pe8:86.11": {"pli": "Tathā sīlakkhandhena nekkhammadhātu ca adhikā, tayo ca vitakkā nekkhammavitakko abyāpādavitakko avihiṁsāvitakko ca. ", "html": "{}"}, "pe8:86.12": {"pli": "Sādhāraṇabhūtā. ", "html": "{}"}, "pe8:86.13": {"pli": "Yā piyāyamānassa pāmojjena idaṁ kāyikaṁ sukhaṁ ānitaṁ aniyamītipemena, idaṁ dukkhaṁ. ", "html": "{}"}, "pe8:86.14": {"pli": "Yo tattha avikkhepo, ayaṁ samādhi. ", "html": "{}"}, "pe8:86.15": {"pli": "Idaṁ pañcaṅgikaṁ paṭhamaṁ jhānaṁ. ", "html": "{}"}, "pe8:86.16": {"pli": "Yā cetasikā passaddhi savitakkaṁ savicāraṁ virodhanaṁ, yo kileso ca paridāho, so paṭhame jhāne niruddho. ", "html": "{}"}, "pe8:86.17": {"pli": "Tathā yā ca kilesapassaddhi yā ca vitakkavicārānaṁ passaddhi, ubhayepi ete dhamme passaddhāyaṁ. ", "html": "{}"}, "pe8:86.18": {"pli": "Tattha kāyassa cittassa ca sukhaṁ sukhāyanā, idaṁ pītisukhino passaddhi. ", "html": "{}"}, "pe8:86.19": {"pli": "Yopi ekodibhāvo cittassa, tena ekodibhāvena yaṁ cittassa ajjhattaṁ sampasādanaṁ, idaṁ catutthaṁ jhānaṅgaṁ. ", "html": "{}"}, "pe8:86.20": {"pli": "Iti ajjhattañca sampasādo cetaso ca ekodibhāvo pīti ca sukhañca, idaṁ dutiyaṁ jhānaṁ caturaṅgikaṁ. ", "html": "{}"}, "pe8:86.21": {"pli": "Yo passaddhakāyo sukhaṁ vedeti, tena adhimattena sukhena pharitvā sukhaṁ cetasikaṁ yaṁ, so pītivītarāgo evaṁ tassa pītivītarāgatāya upekkhaṁ paṭilabhati. ", "html": "{}"}, "pe8:86.22": {"pli": "So pītiyā ca virāgā upekkhaṁ paṭilabhati. ", "html": "{}"}, "pe8:86.23": {"pli": "Sukhañca paṭisaṁvedeti. ", "html": "{}"}, "pe8:86.24": {"pli": "Sati ca sammā paññāya paṭilabhati. ", "html": "{}"}, "pe8:86.25": {"pli": "Sace sati ekaggatā idaṁ pañcaṅgikaṁ tatiyaṁ jhānaṁ. ", "html": "{}"}, "pe8:86.26": {"pli": "Yaṁ sukhino cittaṁ samādhiyati, ayaṁ ekaggatāya parāvidhānabhāgiyā, paṭhame jhāne atthi cittekaggatā no cakkhussa vedanā sabbaṁ pāripūriṁ gacchati. ", "html": "{}"}, "pe8:86.27": {"pli": "Yathā catutthe jhāne, tathā yā upekkhā passambhayaṁ satisampajaññaṁ cittekaggatā ca, idaṁ catutthaṁ jhānaṁ. ", "html": "{}</p>"}, "pe8:87.1": {"pli": "Yathā samādhi dassayitabbaṁ, tathā paññindriyaṁ taṁ catūsu ariyasaccesu passitabbaṁ. ", "html": "<p>{}"}, "pe8:87.2": {"pli": "Yaṁ samāhito yathābhūtaṁ pajānāti, sā pajānanā catubbidhā asubhato dukkhato anattato ca, yadārammaṇaṁ taṁ dukkhaṁ ariyasaccaṁ, yaṁ pajānanto nibbindati vimuccati tathā yaṁ kāmāsavassa pahānaṁ bhavāsavassa diṭṭhāsavassa avijjāsavassa, ayaṁ nirodho appahīnabhūmiyaṁ āsavasamudayo. ", "html": "{}"}, "pe8:87.3": {"pli": "Imāni cattāri ariyasaccāni yathā paññindriyaṁ passitabbaṁ. ", "html": "{}"}, "pe8:87.4": {"pli": "Yathāyaṁ samāhito yathābhūtaṁ pajānāti, ayaṁ dassanabhūmi. ", "html": "{}"}, "pe8:87.5": {"pli": "Sotāpattiphalañca yathābhūtaṁ pajānanto nibbindatīti, idaṁ tanukañca. ", "html": "{}"}, "pe8:87.6": {"pli": "Kāmarāgabyāpādaṁ sakadāgāmiphalañca yaṁ nibbindati virajjati, ayaṁ paṭhamajjhānabhāvanābhūmi ca rāgavirāgā cetovimutti anāgāmiphalañca. ", "html": "{}"}, "pe8:87.7": {"pli": "Yaṁ vimutti vimuccati, ayaṁ avijjāvirāgā paññāvimutti arahattañca. ", "html": "{}"}, "pe8:87.8": {"pli": "Ime avippaṭisārā ca vīriyindriyañca cattāro sammappadhānā avippaṭisārā tañca upari yāva samādhi, evaṁ te cattāri jhānāni samādhindriyañca yaṁ samāhito yathābhūtaṁ pajānāti. ", "html": "{}"}, "pe8:87.9": {"pli": "Ime cattāro satipaṭṭhānā sīlapāripūrimupādāya cāgasaṁhitena ca nibbedhikānañca nimittānaṁ anāvilamanā, idaṁ satindriyaṁ cattāro satipaṭṭhānā. ", "html": "{}"}, "pe8:87.10": {"pli": "Yaṁ puna imāya dhammadesanāya tīsu ṭhānesu diṭṭhogamanakindriyaṁ kilesapahānena ca sekkhasīlaṁ, idaṁ saddhindriyaṁ. ", "html": "{}"}, "pe8:87.11": {"pli": "Cattāri ca sotāpattiyaṅgāni phalāni. ", "html": "{}"}, "pe8:87.12": {"pli": "Samādhindriyāni sopaniyāhārīni sabbasuttesu niddisitabbāni. ", "html": "{}"}, "pe8:87.13": {"pli": "Yaṁ jhānaṁ paṭilabhanaṁ vīriyagahitaṁyeva ñāṇaṁ paṭissarato, ayaṁ sutamayī paññā. ", "html": "{}"}, "pe8:87.14": {"pli": "Yo samādhi pubbāparanimittābhāso anomagatitāya yathākāmo, ayaṁ cintāmayī paññā, yaṁ tathāsamāhito yathābhūtaṁ pajānāti, ayaṁ bhāvanāmayī paññā. ", "html": "{}"}, "pe8:87.15": {"pli": "Ayaṁ suttaniddeso. ", "html": "{}</p>"}, "pe8:88.1": {"pli": "Imaṁ suttaṁ nibbedhabhāgiyaṁ bujjhakāradhikaṁ bujjhitabbaṁ. ", "html": "<p>{}"}, "pe8:88.2": {"pli": "Yehi aṅgehi samannāgataṁ taṁ bujjhissanti tassa aṅgāni bujjhissanti, tena bojjhaṅgā. ", "html": "{}"}, "pe8:88.3": {"pli": "Tathā ādito yāva sīlaṁ vataṁ cetanā karaṇīyā, kissa sīlāni pāripūreti. ", "html": "{}"}, "pe8:88.4": {"pli": "Anuppannassa ca akusalassa anuppādāya uppannassa ca akusalassa pahānāya anuppannassa kusalassa uppādāya uppannassa ca kusalassa bhiyyobhāvāya, idaṁ vīriyaṁ tassa tassa bujjhitassa aṅganti. ", "html": "{}"}, "pe8:88.5": {"pli": "Ayaṁ vīriyasambojjhaṅgo. ", "html": "{}"}, "pe8:88.6": {"pli": "Iminā vīriyena dve dhammā ādito avippaṭisāro pāmojjañca yā puna pīti avippaṭisārapaccayā pāmojjapaccayā, ayaṁ pītisambojjhaṅgo. ", "html": "{}"}, "pe8:88.7": {"pli": "Yaṁ pītimanassa kāyo passambhati. ", "html": "{}"}, "pe8:88.8": {"pli": "Ayaṁ passaddhisambojjhaṅgo. ", "html": "{}"}, "pe8:88.9": {"pli": "Tena kāyikasukhamānitaṁ yaṁ sukhino cittaṁ samādhiyati, ayaṁ samādhisambojjhaṅgo. ", "html": "{}"}, "pe8:88.10": {"pli": "Yaṁ samāhito yathābhūtaṁ pajānāti, ayaṁ dhammavicayasambojjhaṅgo. ", "html": "{}"}, "pe8:88.11": {"pli": "Yā sīlamupādāya pañcannaṁ bojjhaṅgānaṁ upādāyānulomatā nimittāyanā pītibhāgiyānañca visesabhāgiyānañca apilāpanatā sahagatā hoti anavamaggo, ayaṁ satisambojjhaṅgo. ", "html": "{}"}, "pe8:88.12": {"pli": "Yaṁ yathābhūtaṁ pajānāti, accāraddhavīriyaṁ karoti. ", "html": "{}"}, "pe8:88.13": {"pli": "Uddhaccabhūmīti katā abhipatthitaṁ peseti. ", "html": "{}"}, "pe8:88.14": {"pli": "Kosajjabhūmīti garahito rahitehi aṅgehi bujjhati yaṁ cakkhusamathapathaṁ, sā upekkhāti. ", "html": "{}"}, "pe8:88.15": {"pli": "Tena sā upekkhā tassa bojjhaṅgassa aṅganti karitvā upekkhāsambojjhaṅgoti vuccate. ", "html": "{}"}, "pe8:88.16": {"pli": "Eso suttaniddeso. ", "html": "{}</p>"}, "pe8:89.1": {"pli": "Tattha katamā desanā? ", "html": "<p>{}"}, "pe8:89.2": {"pli": "Asmiṁ sutte cattāri ariyasaccāni desitāni. ", "html": "{}"}, "pe8:89.3": {"pli": "Tattha katamo vicayo? ", "html": "{}"}, "pe8:89.4": {"pli": "Sīlavato avippaṭisāro yāva vimutti imissāya pucchāya minikimatthassamīti dve padāni pucchā dve padāni visajjanāni dvīhi padehi dve abhiññaṁ dvīhi ceva padehi visajjanā kiṁ pucchati nibbādhikaṁ kāyabhūmiṁ kammassa tathā hi patiṭṭhā ca asekkhe dhamme uppādeti. ", "html": "{}"}, "pe8:89.5": {"pli": "Tattha katamā yutti? ", "html": "{}"}, "pe8:89.6": {"pli": "Sīlavato avippaṭisāro bhavati kiṁ nicchandassa ca virāgo atthi esā yutti. ", "html": "{}"}, "pe8:89.7": {"pli": "Tattha katamaṁ padaṭṭhānaṁ? ", "html": "{}"}, "pe8:89.8": {"pli": "Vīriyaṁ vīriyindriyassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:89.9": {"pli": "Samādhi samādhindriyassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:89.10": {"pli": "Paññā paññindriyassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:89.11": {"pli": "Vīriyaṁ adosassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:89.12": {"pli": "Samādhi alobhassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:89.13": {"pli": "Paññā amohassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:89.14": {"pli": "Vīriyindriyaṁ tiṇṇaṁ maggaṅgānaṁ padaṭṭhānaṁ, sammāvācāya sammākammantassa sammāājīvassa. ", "html": "{}"}, "pe8:89.15": {"pli": "Samādhindriyaṁ tiṇṇaṁ maggaṅgānaṁ padaṭṭhānaṁ, sammāsaṅkappassa sammāvācāya sammāsamādhino. ", "html": "{}"}, "pe8:89.16": {"pli": "Paññindriyaṁ dvinnaṁ maggaṅgānaṁ padaṭṭhānaṁ, sammāsatiyā sammādiṭṭhiyā ca. ", "html": "{}</p>"}, "pe8:90.1": {"pli": "Tattha katamo lakkhaṇo? ", "html": "<p>{}"}, "pe8:90.2": {"pli": "Sīlakkhandhe vutte sabbe tayo khandhā vuttā bhavanti, sīlameva hi selopamatā yathā selo sabbapaccatthikehi akaraṇīyo evaṁ taṁ cittaṁ sabbakilesehi na kampatīti, ayaṁ amoho. ", "html": "{}"}, "pe8:90.3": {"pli": "Virattaṁ rajanīyesūti ayaṁ alobho. ", "html": "{}"}, "pe8:90.4": {"pli": "Kopaneyye na kuppatīti ayaṁ adoso. ", "html": "{}"}, "pe8:90.5": {"pli": "Tattha paññā amoho kusalamūlaṁ, alobho alobhoyeva, adoso adosoyeva. ", "html": "{}"}, "pe8:90.6": {"pli": "Imehi tīhi kusalamūlehi sekkhabhūmiyaṁ ṭhito asekkhamaggaṁ uppādeti. ", "html": "{}"}, "pe8:90.7": {"pli": "Sekkhabhūmi sampattikammadhamme uppādeti, sā ca sammāvimutti, yañca vimuttirasañāṇadassanaṁ ime dasa asekkhānaṁ arahattaṁ dhammā. ", "html": "{}"}, "pe8:90.8": {"pli": "Tattha aṭṭhaṅgikena maggena catubbidhā bhāvanāpi labbhati. ", "html": "{}"}, "pe8:90.9": {"pli": "Sīlabhāvanā kāyabhāvanā cittabhāvanā paññābhāvanā ca. ", "html": "{}"}, "pe8:90.10": {"pli": "Tattha sammākammantena sammāājīvena ca kāyo bhāvito. ", "html": "{}"}, "pe8:90.11": {"pli": "Sammāvācāya sammāvāyāmena ca sīlaṁ bhāvitaṁ. ", "html": "{}"}, "pe8:90.12": {"pli": "Sammāsaṅkappena sammāsamādhinā ca cittaṁ bhāvitaṁ. ", "html": "{}"}, "pe8:90.13": {"pli": "Sammādiṭṭhiyā sammāsatiyā ca paññā bhāvitā. ", "html": "{}"}, "pe8:90.14": {"pli": "Imāya catubbidhāya bhāvanāya dve dhammā bhāvanāpāripūriṁ gacchanti cittaṁ paññañca. ", "html": "{}"}, "pe8:90.15": {"pli": "Cittaṁ bhāvanāya samatho, paññā bhāvanāya vipassanā. ", "html": "{}"}, "pe8:90.16": {"pli": "Tattha paññā avijjāpahānena cittaṁ upakkilesehi amissīkatanti. ", "html": "{}"}, "pe8:90.17": {"pli": "Paññā bhāvanāya cittabhāvanaṁyeva paripūreti. ", "html": "{}"}, "pe8:90.18": {"pli": "Evaṁ yassa subhāvitaṁ cittaṁ kuto taṁ dukkhamessatīti. ", "html": "{}"}, "pe8:90.19": {"pli": "Api ca kho pana tassa āyasmato abyāpādadhātu adhimuttā, na so petaṁ samāpanno tassa saṅkhāpahāraṁ deti, saṅkhāvitakkite sarīre dukkhaṁ na vediyati, ayaṁ suttattho. ", "html": "{}</p>"}, "pe8:91.1": {"pli": "Tattha katamā desanā? ", "html": "<p>{}"}, "pe8:91.2": {"pli": "Imamhi sutte dasa asekkhā arahattadhammā desitā appamāṇā ca sammā vibhāvanā. ", "html": "{}"}, "pe8:91.3": {"pli": "Tattha katamo vicayo? ", "html": "{}"}, "pe8:91.4": {"pli": "Selopamatā ye ye dhammā vedanīyasukhadukkhopagatā, te sabbe nirūpaṁ vānupassantānaṁ vūpagatā kāyato vedayitaparikkhāro appavattito dukkhaṁ na vediyati. ", "html": "{}"}, "pe8:91.5": {"pli": "Tattha katamā yutti, yassevaṁ bhāvitaṁ cittaṁ kuto taṁ dukkhamessatīti. ", "html": "{}"}, "pe8:91.6": {"pli": "Tīsu bhāvanāsu dukkhaṁ nakkhamati cittaṁ cittabhāvanāya ca. ", "html": "{}"}, "pe8:91.7": {"pli": "Nirodhabhāvanāya ca ānantarikā samādhibhāvanāya ca. ", "html": "{}"}, "pe8:91.8": {"pli": "Iti yassevaṁ bhāvitaṁ cittanti samādhi phalassa padaṭṭhānaṁ. ", "html": "{}</p>"}, "pe8:92.1": {"pli": "Tattha katamo lakkhaṇo? ", "html": "<p>{}"}, "pe8:92.2": {"pli": "Yassevaṁ bhāvitaṁ cittanti cittāni bhāvitāni yathā paṭhamaṁ niddiṭṭhāni paññā sīlaṁ kāyo cittaṁ, sīlampi subhāvitaṁ kāyikacetasikañca ṭhitattā nānupakampatīti vedanāpi tathā saññāpi saṅkhārāpi. ", "html": "{}"}, "pe8:92.3": {"pli": "Kuto taṁ dukkhamessatīti sukhampi nānugacchati, adukkhamasukhampi nāgatanti. ", "html": "{}</p>"}, "pe8:93.1": {"pli": "Tattha katamo catubyūho hāro? ", "html": "<p>{}"}, "pe8:93.2": {"pli": "Idha bhagavato ko adhippāyo? ", "html": "{}"}, "pe8:93.3": {"pli": "Ye dukkhena adhikā bhavissanti, te evarūpāhi samāpattīhi virahissanti. ", "html": "{}"}, "pe8:93.4": {"pli": "Ayamettha bhagavato adhippāyo. ", "html": "{}"}, "pe8:93.5": {"pli": "Ye ca appasannā, te hi bhavissanti, pasannānañca pītipāmojjaṁ bhavissati, ayaṁ tattha bhagavato adhippāyo. ", "html": "{}"}, "pe8:93.6": {"pli": "Āvaṭṭoti natthi āvaṭṭanassa bhūmi. ", "html": "{}</p>"}, "pe8:94.1": {"pli": "Vibhattīti yassevaṁ bhāvitaṁ cittaṁ kuto taṁ dukkhamessatīti duvidho niddeso—", "html": "<p>{}"}, "pe8:94.2": {"pli": "dukkhahetuniddeso ca paṭipakkhaniddeso ca. ", "html": "{}"}, "pe8:94.3": {"pli": "Ko so dukkhahetu? ", "html": "{}"}, "pe8:94.4": {"pli": "Yato dukkhaṁ āgacchati paṭipakkhe vutte sesadhammānaṁ sīlaṁ hetu ca paccayo ca, te sabbe dhammā vuttā honti. ", "html": "{}"}, "pe8:94.5": {"pli": "Ekabodhipakkhiye dhamme vutte sabbe bodhagamanīyā dhammā vuttā bhavanti. ", "html": "{}</p>"}, "pe8:95.1": {"pli": "Tattha katamo catubyūho hāro? ", "html": "<p>{}"}, "pe8:95.2": {"pli": "Imamhi sutte bhagavato ko adhippāyo? ", "html": "{}"}, "pe8:95.3": {"pli": "Ye avippaṭisārena chandikā, te sīlapāripūrī bhavanti pāmojjachandikā avippaṭisārīpāripūrī, ayamettha bhagavato adhippāyo …pe… ", "html": "{}"}, "pe8:95.4": {"pli": "ayaṁ catubyūho hāro. ", "html": "{}</p>"}, "pe8:96.1": {"pli": "Tattha katamo āvaṭṭo? ", "html": "<p>{}"}, "pe8:96.2": {"pli": "Idaṁ suttaṁ nibbedhabhāgiyaṁ. ", "html": "{}"}, "pe8:96.3": {"pli": "Yo nibbedho, ayaṁ nirodho. ", "html": "{}"}, "pe8:96.4": {"pli": "Yena nibbijjhati, so maggo. ", "html": "{}"}, "pe8:96.5": {"pli": "Yaṁ nibbijjhati, taṁ dukkhaṁ. ", "html": "{}"}, "pe8:96.6": {"pli": "Yaṁ nibbedhagāminā maggena pahīyati, samudayoyaṁ vutto. ", "html": "{}</p>"}, "pe8:97.1": {"pli": "Tattha katamā vibhatti? ", "html": "<p>{}"}, "pe8:97.2": {"pli": "Sīlavato avippaṭisāroti vibhajjabyākaraṇīyaṁ, parāmasantassa natthi avippaṭisāro yāva dosakataṁ kāyena vā vācāya vā akusalaṁ ārabhati. ", "html": "{}"}, "pe8:97.3": {"pli": "Kiñcipissa evaṁ hoti “sukatametaṁ sucaritametaṁ no cassa tena avippaṭisārena pāmojjaṁ jāyati yāva vimutti, tassa sīlavato avippaṭisāro”ti vibhajjabyākaraṇīyaṁ, ayaṁ vibhattihāro. ", "html": "{}</p>"}, "pe8:98.1": {"pli": "Tattha katamā parivattanā? ", "html": "<p>{}"}, "pe8:98.2": {"pli": "Imehi sattahi upanisāsampattīhi ekādasa upanisā vibhattiyaṁ pajahānaṁ pajahanti, ayaṁ parivattanā. ", "html": "{}</p>"}, "pe8:99.1": {"pli": "Tattha katamā vevacanā? ", "html": "<p>{}"}, "pe8:99.2": {"pli": "Imesaṁ ariyadhammānaṁ balabojjhaṅgavimokkhasamādhisamāpattīnaṁ imāni vevacanāni. ", "html": "{}</p>"}, "pe8:100.1": {"pli": "Tattha katamā paññatti? ", "html": "<p>{}"}, "pe8:100.2": {"pli": "Sīlavato avippaṭisāroti sīlakkhandhe nekkhammapaññattiyā paññattaṁ, nisajjapaññatti ca evaṁ dasa aṅgāni dvīhi dvīhi aṅgehi paññattāni. ", "html": "{}</p>"}, "pe8:101.1": {"pli": "Tattha katamo otaraṇo? ", "html": "<p>{}"}, "pe8:101.2": {"pli": "Idaṁ nibbedhabhāgiyasuttaṁ pañcasu otiṇṇaṁ yathā yaṁ paṭhamaṁ niddiṭṭhaṁ evamindriyādikhandhadhātuāyatanesu niddisitabbāni. ", "html": "{}</p>"}, "pe8:102.1": {"pli": "Tattha katamo sodhano hāro? ", "html": "<p>{}"}, "pe8:102.2": {"pli": "Sīlavato avippaṭisāroti na tāva suddho ārambho avippaṭisārino pāmojjanti na tāva suddho ārambho yāni ekādasa padāni desitāni yadā tadā suddho ārambho, ayaṁ sodhano. ", "html": "{}</p>"}, "pe8:103.1": {"pli": "Tattha katamo adhiṭṭhāno? ", "html": "<p>{}"}, "pe8:103.2": {"pli": "Sīlavemattatāya paññattaṁ evaṁ dasa padāni sabbāni sīlakkhandhassa ānisaṁso, te ca patirūpadesavāso ca paccayo attasammāpaṇidhānañca hetu, samādhikkhandhassa sukhaṁ hetu passaddhi paccayo, yena jhānasahajāti ca ṭhānanti jhānaṅgā aparo pariyāyo kāmesu ādīnavānupassanā samādhino paccayo nekkhamme ānisaṁsadassāvitā hetu. ", "html": "{}</p>"}, "pe8:104.1": {"pli": "Tattha katamā samāropanā? ", "html": "<p>{}"}, "pe8:104.2": {"pli": "Yaṁ vīriyindriyaṁ, so sīlakkhandho. ", "html": "{}"}, "pe8:104.3": {"pli": "Yaṁ sīlaṁ, te cattāro dhammā padhānā. ", "html": "{}"}, "pe8:104.4": {"pli": "Yaṁ dhammānudhammapaṭipatti, so pātimokkhasaṁvaro. ", "html": "{}</p>"}, "pe8:105.1": {"pli": "Yassa selopamaṁ cittanti gāthā, selopamanti upamā yathā selo vātena na kampati na uṇhena na sītena saṅkampati. ", "html": "<p>{}"}, "pe8:105.2": {"pli": "Yathā anekā acetanā, te uṇhena milāyanti, sītena avasussanti, vātena bhajanti. ", "html": "{}"}, "pe8:105.3": {"pli": "Na evaṁ selo (…) virattaṁ rajanīyesu dosanīye na dussatīti kāraṇaṁ dosanīye domanassantaṁ, na duṭṭhena vā kampati uṇhena vā, so milāyati sītena vā avasussati, evaṁ cittaṁ rāgena nānussati sītena kampatīti. ", "html": "{}"}, "pe8:105.4": {"pli": "Kiṅkāraṇaṁ? ", "html": "{}"}, "pe8:105.5": {"pli": "Virattaṁ rajanīyesu dosanīye na dussati. ", "html": "{}"}, "pe8:105.6": {"pli": "Kiṅkāraṇaṁ? ", "html": "{}"}, "pe8:105.7": {"pli": "Dosanīye panassanti na dussati, aduṭṭhaṁ taṁ na kosissanti, tena kuppanīye na kuppati, yassevaṁ bhāvitaṁ cittaṁ kuto taṁ dukkhaniddeso ca kuto evarūpassa dukkhaṁ āgamissatīti niddiṭṭhaṁ. ", "html": "{}</p>"}, "pe8:106.1": {"pli": "Parivattanāti kuto taṁ dukkhamessatīti yaṁ cetasikaṁ sukhaṁ anupādisesā ayaṁ natthi sopādisesā ayaṁ atthi. ", "html": "<p>{}"}, "pe8:106.2": {"pli": "Puna evamāhaṁsu taṁ khaṇaṁ taṁ muhuttaṁ ubhayameva avedayitaṁ sopādisesaṁ yañca anupādisesaṁ yañca taṁ khaṇaṁ taṁ muhuttaṁ anupādisesaṁ yañca sopādisesaṁ ca avedayitaṁ. ", "html": "{}"}, "pe8:106.3": {"pli": "Sukhamāpannassa anāvattikanti ayamettha viseso parivattanā. ", "html": "{}</p>"}, "pe8:107.1": {"pli": "Tattha katamo vevacano? ", "html": "<p>{}"}, "pe8:107.2": {"pli": "Yassevaṁ bhāvitaṁ cittaṁ vā bhāvitaṁ subhāvitaṁ anuṭṭhitaṁ vatthukataṁ susamāraddhaṁ. ", "html": "{}"}, "pe8:107.3": {"pli": "Cittanti mano viññāṇaṁ manindriyaṁ manoviññāṇadhātu. ", "html": "{}</p>"}, "pe8:108.1": {"pli": "Tattha katamā paññatti? ", "html": "<p>{}"}, "pe8:108.2": {"pli": "Cittaṁ mano saṅkhārā vūpasamapaññattiyā paññattaṁ. ", "html": "{}"}, "pe8:108.3": {"pli": "Samādhi asekkhapaññattiyā paññatto. ", "html": "{}"}, "pe8:108.4": {"pli": "Dukkhaṁ ucchinnapaññattiyā paññattaṁ. ", "html": "{}</p>"}, "pe8:109.1": {"pli": "Tattha katamo otaraṇo? ", "html": "<p>{}"}, "pe8:109.2": {"pli": "Citte niddiṭṭhe pañcakkhandhā niddiṭṭhā honti, ayaṁ khandhesu otaraṇo, manoviññāṇadhātuyā niddiṭṭhāya aṭṭhārasa dhātuyo niddiṭṭhā honti, ayaṁ dhātūsu otaraṇo. ", "html": "{}"}, "pe8:109.3": {"pli": "Manāyatane niddiṭṭhe sabbāni āyatanāni niddiṭṭhāni honti. ", "html": "{}"}, "pe8:109.4": {"pli": "Tattha manāyatanaṁ nāmarūpassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:109.5": {"pli": "Nāmarūpapaccayā saḷāyatanaṁ. ", "html": "{}"}, "pe8:109.6": {"pli": "Tathā paṭiccasamuppāde. ", "html": "{}"}, "pe8:109.7": {"pli": "Ayaṁ otaraṇo. ", "html": "{}"}, "pe8:109.8": {"pli": "Tattha katamo sodhano suddhoyeva ārambho. ", "html": "{}</p>"}, "pe8:110.1": {"pli": "Tattha katamo adhiṭṭhāno? ", "html": "<p>{}"}, "pe8:110.2": {"pli": "Chaḷindriyaṁ bhāvanā ekattāyaṁ paññatti chaṭṭhitena kāyo ekattāya paññatto. ", "html": "{}</p>"}, "pe8:111.1": {"pli": "Tattha katamo parikkhāro? ", "html": "<p>{}"}, "pe8:111.2": {"pli": "Cittassa pubbahetu samuppādāya manasikāro ca tappoṇatā ca yaṁ asamāhitabhūmiyaṁ ca visesadhammānaṁ abhāvitattā cittasatataṁ gacchati, sace samādhino sukhaṁ hetu avippaṭisāro paccayo, ayaṁ hetu ayaṁ paccayo parikkhāro. ", "html": "{}</p>"}, "pe8:112.1": {"pli": "Tattha katamā samāropanā? ", "html": "<p>{}"}, "pe8:112.2": {"pli": "Yassevaṁ bhāvitanti tassa dhammā samāropayitabbā. ", "html": "{}"}, "pe8:112.3": {"pli": "Kāyo sīlaṁ paññā bhāvitacittanti anabhirataṁ anapaṇataṁ anekaṁ anutaṁ anāpajjāsattaṁ ayaṁ samaññāyatanā na tassa sekkhassa sammāsamādhi sabbe asekkhā dasa arahantadhammā niddiṭṭhā honti. ", "html": "{}"}, "pe8:112.4": {"pli": "Asekkhabhāgiyāni suttāni. ", "html": "{}</p>"}, "pe8:113.1": {"pli": "Yassa nūna, bhante, kāyagatāsati abhāvitā, ayaṁ so aññataraṁ sabrahmacāriṁ āsajja samāpajja appaṭinissajja janapadacārikaṁ pakkameyya, so āyasmā imasmiṁ vippaṭijānāti dve pajāni paṭijānāti cittabhāvanāyañca diṭṭhiyā pahānaṁ, kāyabhāvanāyañca diṭṭhippahānaṁ, kāyabhāvanāyañca taṇhāpahānaṁ, yaṁ paṭhamaṁ upamaṁ karoti. ", "html": "<p>{}"}, "pe8:113.2": {"pli": "Asucināpi sucināpi pathavī neva aṭṭiyati na jigucchati na pītipāmojjaṁ paṭilabhati, evameva hi pathavīsamena so cetasā anvayena appakena averena abyāpajjena viharāmīti. ", "html": "{}"}, "pe8:113.3": {"pli": "Iti so āyasmā kiṁ paṭijānāti, kāyabhāvanāya sukhindriyapahānaṁ paṭijānāti, cittabhāvanāya somanassindriyapahānaṁ paṭijānāti. ", "html": "{}"}, "pe8:113.4": {"pli": "Kāyikā vedanā rāgānusayamanugatānaṁ sukhindriyaṁ paṭikkhipati. ", "html": "{}"}, "pe8:113.5": {"pli": "Na hi vedanākkhandhaṁ yā cetasikā sukhavedanā tattha ayaṁ paṭilābhapaccayā uppajjati sukhaṁ somanassaṁ. ", "html": "{}"}, "pe8:113.6": {"pli": "Sotaṁ paṭikkhipati, na hi manosamphassajaṁ vedanaṁ. ", "html": "{}"}, "pe8:113.7": {"pli": "Tattha catūsu mahābhūtesu rūpakkhandhassa anusayapaṭighapahānaṁ bhaṇati. ", "html": "{}"}, "pe8:113.8": {"pli": "Kāme rūpañca tañca asekkhabhūmiyaṁ. ", "html": "{}"}, "pe8:113.9": {"pli": "Kāye kāyānupassanā diṭṭhadhammasukhavihārañca. ", "html": "{}"}, "pe8:113.10": {"pli": "Balena ca ussāhena ca sabbaṁ manasi katattānaṁ pahānaṁ medaṁ katālikāya ca purisena ca maṇḍanakajātikena ca, etehi imassa mātāpitusambhūtaṁ paccavekkhaṇaṁ, so kāyena ca kāyānupassanāya ca cittena ca cittānupassanāya ca dve dhamme dhāreti. ", "html": "{}"}, "pe8:113.11": {"pli": "Kāyakilesavatthuṁ cittena ca cittasannissaye cittena subhāvitena sattannaṁ ca samāpattīnaṁ viharituṁ paṭijānāti. ", "html": "{}</p>"}, "pe8:114.1": {"pli": "Gahapatiputtopamatāya ca yathā gahapatiputtassa nānāraṅgānaṁ vatthakaraṇḍako puṇṇo bhaveyya, so yaṁ yadeva vatthayugaṁ pubbaṇhasamaye ākaṅkhati, pubbaṇhasamaye nibbāpeti, evaṁ majjhanhikasamaye, sāyanhasamaye, evameva so āyasmā cittassa subhāvitattā yathārūpena vihārena ākaṅkhati pubbaṇhasamayaṁ viharituṁ, tathārūpena pubbaṇhasamayaṁ viharati, majjhanhikasamaye, sāyanhasamaye. ", "html": "<p>{}"}, "pe8:114.2": {"pli": "Tena vesa āyasmatā upamāya me āsitāya pathavī vā anuttarā indriyabhāvanā bhāvitacittena. ", "html": "{}"}, "pe8:114.3": {"pli": "Tena so āyasmā idaṁ aṭṭhavidhaṁ bhāvanaṁ paṭijānāti catūsu mahābhūtesu, kāyabhāvanaṁ upakacaṇḍālaṁ purisametakaṁ bhavatalākāsu cittabhāvanaṁ, imāhi bhāvanāhi tāya bhāvanāya ca samathā pāripūrimantehi. ", "html": "{}"}, "pe8:114.4": {"pli": "Imehi catūhi paññāpāripūrimantehi. ", "html": "{}</p>"}, "pe8:115.1": {"pli": "Kathaṁ upakacaṇḍālaṁ paṭikūlesu dhammesu appaṭikūlasaññī viharati? ", "html": "<p>{}"}, "pe8:115.2": {"pli": "Kāyo pakatiyā appaṭikūlaṁ kāye uddhumātakasaññā saṅkhittena nava saññā ime paṭikūlā dhammā ceso āyasmā paṭikūlato ajigucchito kāyagatāsatiyā bhāvanānuyogamanuyutto viharati, na hi tassa jigucchappahāya cittaṁ paṭikūlati. ", "html": "{}</p>"}, "pe8:116.1": {"pli": "Kathaṁ appaṭikūlesu dhammesu paṭikūlasaññī viharatīti? ", "html": "<p>{}"}, "pe8:116.2": {"pli": "Kāyo sabbalokassa appaṭikūlo taṁ so āyasmā asubhasaññāya viharati. ", "html": "{}"}, "pe8:116.3": {"pli": "Evaṁ appaṭikūlesu dhammesu paṭikūlasaññī viharati. ", "html": "{}</p>"}, "pe8:117.1": {"pli": "Kathaṁ paṭikūlesu ca appaṭikūlesu ca appaṭikūlasaññī viharatīti api sabboyaṁ lokassa yamidaṁ muṇḍo pattapāṇī kulesu piṇḍāya vicarati, tena ca so āyasmā suvaṇṇadubbaṇṇena appaṭikūlasaññī cittena ca kāyena nibbidāsahagatena appaṭikūlasaññī, evaṁ paṭikūlesu appaṭikūlesu ca dhammesu appaṭikūlasaññī viharati. ", "html": "<p>{}</p>"}, "pe8:118.1": {"pli": "Kathaṁ paṭikūlesu ca dhammesu appaṭikūlasaññī viharati? ", "html": "<p>{}"}, "pe8:118.2": {"pli": "Paṭikūlesu ca dhammesu subhasaññino itthirūpe paṭikūlesu ca jigucchino vinīlakavipubbake tattha so āyasmā paṭikūlasaññī viharati. ", "html": "{}</p>"}, "pe8:119.1": {"pli": "Kathaṁ paṭikūlesu dhammesu tadubhayaṁ abhinivajjayitvā upekkhako viharati sato ca sampajāno ca? ", "html": "<p>{}"}, "pe8:119.2": {"pli": "Appaṭikūlesu ca dhammesu subhasaññino itthirūpe paṭikūlesu ca jigucchino vinīlakavipubbake tadubhayaṁ abhinivajjayitvā “netaṁ mama, nesohamasmi, neso me attā”ti viharati. ", "html": "{}"}, "pe8:119.3": {"pli": "Evaṁ tadubhayaṁ abhinivajjayitvā upekkhako viharati sato sampajāno. ", "html": "{}</p>"}, "pe8:120.1": {"pli": "Aparo pariyāyo. ", "html": "<p>{}"}, "pe8:120.2": {"pli": "Tedhātuko lokasannivāso sabbabālaputhujjanānaṁ appaṭikūlasaññā. ", "html": "{}"}, "pe8:120.3": {"pli": "Tattha ca āyasmā sāriputto appaṭikūlasaññī viharati. ", "html": "{}"}, "pe8:120.4": {"pli": "Evaṁ appaṭikūlesu dhammesu paṭikūlasaññī viharati. ", "html": "{}</p>"}, "pe8:121.1": {"pli": "Kathaṁ paṭikūlesu dhammesu appaṭikūlasaññī viharati? ", "html": "<p>{}"}, "pe8:121.2": {"pli": "Paṭikūlasaññino sabbasekkhā idha kā tedhātuke sabbaloke. ", "html": "{}"}, "pe8:121.3": {"pli": "Tattha katamo bhūmippatto samādhiphale sacchikato appaṭikūlasaññī viharati? ", "html": "{}"}, "pe8:121.4": {"pli": "Kiṅkāraṇaṁ? ", "html": "{}"}, "pe8:121.5": {"pli": "Na hi taṁ atthi yassa lokassa pahānāya paṭikūlasaññī uppādeyya. ", "html": "{}</p>"}, "pe8:122.1": {"pli": "Kathaṁ paṭikūlesu ca appaṭikūlesu ca dhammesu paṭikūlasaññī viharati? ", "html": "<p>{}"}, "pe8:122.2": {"pli": "Tedhātuke lokasannivāse yāva kāmalokabhūmatā hi rāgānaṁ vītarāgānaṁ paṭikūlasamatā rūpārūpadhātuṁ appaṭikūlasamatā. ", "html": "{}"}, "pe8:122.3": {"pli": "Tattha ca āyasmā sāriputto paṭikūlasaññī viharati. ", "html": "{}"}, "pe8:122.4": {"pli": "Evaṁ paṭikūlesu ca appaṭikūlesu ca dhammesu paṭikūlasaññī viharati. ", "html": "{}</p>"}, "pe8:123.1": {"pli": "Kathaṁ paṭikūlesu ca appaṭikūlesu ca dhammesu appaṭikūlasaññī viharati? ", "html": "<p>{}"}, "pe8:123.2": {"pli": "Yaṁ kiñci parato duruttānaṁ durāgatānaṁ vacanapathānaṁ taṁ vacanaṁ appaṭikūlaṁ yāvatā vācaso appatirūpā tathā janassa appaṭikūlasaññā. ", "html": "{}"}, "pe8:123.3": {"pli": "Tattha āyasmā sāriputto abhiññāya sacchikato appaṭikūlasaññī viharati, evaṁ paṭikūlesu ca appaṭikūlesu ca dhammesu appaṭikūlasaññī viharati. ", "html": "{}</p>"}, "pe8:124.1": {"pli": "Kathaṁ paṭikūlesu ca appaṭikūlesu ca dhammesu tadubhayaṁ abhinivajjayitvā upekkhako ca viharati sato ca sampajāno? ", "html": "<p>{}"}, "pe8:124.2": {"pli": "Yañca nesaṁ samanupassati ye dhammā duccaritā, te dhammā appaṭikūlā. ", "html": "{}"}, "pe8:124.3": {"pli": "Tattha āyasmā sāriputto iti paṭisañcikkhati ye dhammā duccaritā, te dhammā aniṭṭhavipākā. ", "html": "{}"}, "pe8:124.4": {"pli": "Ye dhammā sucaritā, te ācayagāmino. ", "html": "{}"}, "pe8:124.5": {"pli": "So ca sucaritaṁ ācayagāminiṁ karitvā duccaritaṁ aniṭṭhavipākaṁ karitvā tadubhayaṁ abhinivajjayitvā upekkhako viharati. ", "html": "{}</p>"}, "pe8:125.1": {"pli": "Atha paṭikūlesu ca dhammesu appaṭikūlesu ca paṭikūlasaññī viharati. ", "html": "<p>{}"}, "pe8:125.2": {"pli": "Taṇhā paṭikūladhammā kiṅkāraṇaṁ? ", "html": "{}"}, "pe8:125.3": {"pli": "Taṇhāvasena hi sattā dvīhi dhammehi sattā, kabaḷīkāre āhāre rasataṇhāya sattā, phasse sukhasaññāya sattā. ", "html": "{}"}, "pe8:125.4": {"pli": "Tatthāyasmā sāriputto kabaḷīkāre ca āhāre paṭikūlasaññī viharati, phasse ca dukkhasaññī viharati. ", "html": "{}"}, "pe8:125.5": {"pli": "Evaṁ paṭikūlesu ca appaṭikūlesu ca paṭikūlasaññī viharati. ", "html": "{}</p>"}, "pe8:126.1": {"pli": "Kathaṁ paṭikūlesu ca dhammesu appaṭikūlesu ca dhammesu appaṭikūlasaññī viharati? ", "html": "<p>{}"}, "pe8:126.2": {"pli": "Taṇhākkhayaṁ anuttaraṁ nibbānaṁ tathā bālaputhujjanānaṁ paṭikūlasaññā pahatasaññā ca. ", "html": "{}"}, "pe8:126.3": {"pli": "Tatthāyasmato sāriputtassa appaṭikūlasaññā abyāpādasaññā ca sāmaṁ paññāya passitvā evaṁ paṭikūlesu ca dhammesu appaṭikūlasaññī viharati. ", "html": "{}</p>"}, "pe8:127.1": {"pli": "Kathaṁ paṭikūlesu ca appaṭikūlesu ca dhammesu appaṭikūlasaññī viharati? ", "html": "<p>{}"}, "pe8:127.2": {"pli": "Tatiye ca nibbāne paṭikūlasaññino yasena ca kittini ca appaṭikūlasaññino. ", "html": "{}"}, "pe8:127.3": {"pli": "Tatthāyasmā sāriputto assādañca ādīnavañca nissaraṇañca yathābhūtaṁ sammāpaññāya paṭijānanto paṭikūlañca appaṭikūlañca dhammaṁ tadubhayaṁ abhinivajjayitvā appaṭikūlasaññī viharati. ", "html": "{}</p>"}, "pe8:128.1": {"pli": "Kathaṁ paṭikūlaṁ appaṭikūlañca dhammaṁ tadubhayaṁ abhinivajjayitvā upekkhako viharati? ", "html": "<p>{}"}, "pe8:128.2": {"pli": "Sato ca sampajāno ca, yañca samanupassati anunayo appaṭikūlo dhammo paṭigho ca paṭikūlo dhammo, tatthāyasmā sāriputto anunayassa paṭighappahīnattā upekkhako viharati sato sampajāno ca. ", "html": "{}"}, "pe8:128.3": {"pli": "Yañcassa samanupassati ayaṁ pañcavidhā anuttarā indriyabhāvanā. ", "html": "{}"}, "pe8:128.4": {"pli": "Ayaṁ suttaniddeso. ", "html": "{}</p>"}, "pe8:129.1": {"pli": "Tattha katamo desanāhāro? ", "html": "<p data-counter='1'>{}"}, "pe8:129.2": {"pli": "Imamhi sutte kiṁ desitabbaṁ? ", "html": "{}"}, "pe8:129.3": {"pli": "Tattha vuccate, imamhi sutte diṭṭhadhammasukhavihāro desito, tathā vimuttaṁ cittaṁ paccavekkhaṇā ca adhipaññādhammaṁ desitaṁ. ", "html": "{}</p>"}, "pe8:130.1": {"pli": "Tattha katamo vicayo? ", "html": "<p data-counter='2'>{}"}, "pe8:130.2": {"pli": "Ye kāye kāyānupassino viharanti, tesaṁ cittaṁ anunayappaṭighena na viharati anunayappaṭighena cābhiramamānassa cittaṁ samaggataṁ bhavissatīti bhāvanāya balametaṁ, ayaṁ vicayo hāro. ", "html": "{}</p>"}, "pe8:131.1": {"pli": "Tattha katamo yuttihāro? ", "html": "<p data-counter='3'>{}"}, "pe8:131.2": {"pli": "Kāyabhāvanāya ca cittabhāvanāya ca na kiñci sabrahmacārī atimaññissatīti. ", "html": "{}"}, "pe8:131.3": {"pli": "Atthi esā yutti, ayaṁ yuttihāro. ", "html": "{}</p>"}, "pe8:132.1": {"pli": "Tattha katamo padaṭṭhāno hāro? ", "html": "<p data-counter='4'>{}"}, "pe8:132.2": {"pli": "Kāyabhāvanāya paṭhamassa sati upaṭṭhānassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:132.3": {"pli": "Yā pathavīsamacittatā, sā aniccānupassanāya padaṭṭhānaṁ. ", "html": "{}</p>"}, "pe8:133.1": {"pli": "Tattha katamo lakkhaṇo? ", "html": "<p data-counter='5'>{}"}, "pe8:133.2": {"pli": "Yaṁ pathavīsamena cetasā viharati attānupassī pathavīsamena gihī viharati. ", "html": "{}"}, "pe8:133.3": {"pli": "Ko attho pathavīsamenāti? ", "html": "{}"}, "pe8:133.4": {"pli": "Yathā ye ca selopamatāya akammayuttā evameva pathavīsamo ayaṁ hiriyatāya. ", "html": "{}"}, "pe8:133.5": {"pli": "Ayaṁ lakkhaṇo. ", "html": "{}</p>"}, "pe8:134.1": {"pli": "Tattha katamo catubyūho hāro? ", "html": "<p data-counter='6'>{}"}, "pe8:134.2": {"pli": "Imamhi byākaraṇe ko tassa āyasmato adhippāyo? ", "html": "{}"}, "pe8:134.3": {"pli": "Ye keci arahantā indriyabhāvanaṁ ākaṅkhiyanti, te pathavīsamataṁ uppādayissantīti. ", "html": "{}"}, "pe8:134.4": {"pli": "Ayaṁ adhippāyo. ", "html": "{}</p>"}, "pe8:135.1": {"pli": "Tattha katamo āvaṭṭoti? ", "html": "<p data-counter='7'>{}"}, "pe8:135.2": {"pli": "Natthi āvaṭṭassa bhūmi. ", "html": "{}</p>"}, "pe8:136.1": {"pli": "Tattha katamo vibhatti? ", "html": "<p data-counter='8'>{}"}, "pe8:136.2": {"pli": "Yo kāyānupassī viharati, so pathavīsamacittataṁ paṭilabhissatīti na ekaṁsena. ", "html": "{}"}, "pe8:136.3": {"pli": "Kiṅkāraṇaṁ? ", "html": "{}"}, "pe8:136.4": {"pli": "Ye khaṇḍakādichinnakādino, na te pathavīsamacittataṁ paṭilabhanti. ", "html": "{}"}, "pe8:136.5": {"pli": "Sabbā kāyagatāsati sekkhabhāvanāya nibbānaṁ phalaṁ, ayaṁ vibhatti. ", "html": "{}</p>"}, "pe8:137.1": {"pli": "Tattha katamo parivattano hāro? ", "html": "<p data-counter='9'>{}"}, "pe8:137.2": {"pli": "Ye kāyānupassino viharissanti, tesaṁyeva kāyapaccayā uppajjeyya āsavā vighātapariḷāhā, ayaṁ parivattano hāro. ", "html": "{}</p>"}, "pe8:138.1": {"pli": "Tattha katamo otaraṇo? ", "html": "<p data-counter='12'>{}"}, "pe8:138.2": {"pli": "Pañcakkhandhā avitiṇṇā bāvīsatindriyāni, tathā yaṁ manindriyaṁ, taṁ manodhātu manāyatanañca. ", "html": "{}"}, "pe8:138.3": {"pli": "Yaṁ samādhindriyaṁ, taṁ dhammadhātu dhammāyatanañca. ", "html": "{}"}, "pe8:138.4": {"pli": "Ayaṁ otaraṇo hāro. ", "html": "{}</p>"}, "pe8:139.1": {"pli": "Tattha katamo sodhano hāro? ", "html": "<p data-counter='13'>{}"}, "pe8:139.2": {"pli": "Ye ca manasā cattāro bhāvetabbā, te sabbe bhāvitā yaṁ taṁ manena pahīne pattabbataṁ sabbattha etassa ca atthāya ārambho, so attho suddho. ", "html": "{}"}, "pe8:139.3": {"pli": "Ayaṁ sodhano hāro. ", "html": "{}</p>"}, "pe8:140.1": {"pli": "Tattha katamo adhiṭṭhāno? ", "html": "<p data-counter='14'>{}"}, "pe8:140.2": {"pli": "Ayaṁ samādhi ekattatāya paññatto, cha kāyā ekattatāya paññattā. ", "html": "{}"}, "pe8:140.3": {"pli": "Pañcindriyāni rūpīni rūpakāyo. ", "html": "{}"}, "pe8:140.4": {"pli": "Cha vedanākāyā vedanākāyo. ", "html": "{}"}, "pe8:140.5": {"pli": "Cha saññākāyā saññākāyo. ", "html": "{}"}, "pe8:140.6": {"pli": "Cha cetanākāyā cetanākāyo. ", "html": "{}"}, "pe8:140.7": {"pli": "Cha viññāṇakāyā viññāṇakāyo. ", "html": "{}"}, "pe8:140.8": {"pli": "Sabbepi ete dhammā dhammakāyotiyeva saṅkhaṁ gacchanti. ", "html": "{}"}, "pe8:140.9": {"pli": "Ayaṁ adhiṭṭhāno. ", "html": "{}</p>"}, "pe8:141.1": {"pli": "Parikkhāroti samāpattikosallañca vīthikosallañca hetu. ", "html": "<p data-counter='15'>{}"}, "pe8:141.2": {"pli": "Yañca gocarakosallaṁ yañca kallaṁ taṁ kosallaṁ paccayo. ", "html": "{}"}, "pe8:141.3": {"pli": "Vodānakosallaṁ hetu, kallaṁ paccayo. ", "html": "{}"}, "pe8:141.4": {"pli": "Sukhaṁ hetu, abyāpajjaṁ paccayo. ", "html": "{}"}, "pe8:141.5": {"pli": "Ayaṁ parikkhāro. ", "html": "{}</p>"}, "pe8:142.1": {"pli": "Tattha katamo samāropanoti? ", "html": "<p data-counter='16'>{}"}, "pe8:142.2": {"pli": "Yathā pathavī sucimpi nikkhīpante asucimpi nikkhitte tādiseyeva evaṁ kāyo manāpikehipi phassehi amanāpikehipi phassehi tādisoyeva paṭighasamphassena vā sukhāya vedanāya tādisaṁ yo cittaṁ. ", "html": "{}"}, "pe8:142.3": {"pli": "Idaṁ suttaṁ vibhattaṁ saopammaṁ ugghaṭitaññussa puggalassa vibhāgena. ", "html": "{}"}, "pe8:142.4": {"pli": "Tattha samāropanāya avakāso natthi. ", "html": "{}</p>"}, "pe8:143.1": {"pli": "Tattha katamaṁ suttaṁ saṅkilesabhāgiyaṁ? ", "html": "<p>{}"}, "pe8:143.2": {"pli": "Yato ca kusalehi dhammehi na virodhati, na vaḍḍhati, imaṁ ādīnavaṁ bhagavā deseti, tasmā channaṁ vivareyya, vivaṭaṁ nātivassati, tato ādīnavato vivareyyāti taṁ tīhi dhammehi nābhidhaṁsitāti asubhasaññāya rāgena nābhidhaṁsiyati. ", "html": "{}"}, "pe8:143.3": {"pli": "Mettāya dosena nābhidhaṁsiyati. ", "html": "{}"}, "pe8:143.4": {"pli": "Vipassanāya mohena nābhidhaṁsiyati. ", "html": "{}"}, "pe8:143.5": {"pli": "Evañcassa yo yo dhammo paṭipakkho tamhi tamhi dhamme paripūrissati. ", "html": "{}"}, "pe8:143.6": {"pli": "Yo tassa dhammassa akusalo dhammo paṭipakkho, tena nādhivāsiyati. ", "html": "{}</p>"}, "pe8:144.1": {"pli": "Aparo pariyāyo. ", "html": "<p>{}"}, "pe8:144.2": {"pli": "Ye ime dhammā attanā na sakkoti vuṭṭhānaṁ, te ete dhammā desitā. ", "html": "{}"}, "pe8:144.3": {"pli": "Channamativassatīti tehi vitakkaṁ yena ca sakkā puna desitaṁ cittaṁ vibhāvetuṁ pariyodāpetuṁ vivekaninnassa vivekapoṇassa vivekapabbhārassa vuddhiṁ virūḷhiṁ vepullataṁ āpajjati kusalesu dhammesu, seyyathāpi nāma uppalaṁ vā kumudaṁ vā padumaṁ vā udake sukkapakkhe cando yāvaratti yāvadivaso āgacchati, tassa vuddhiyeva pāṭikaṅkhitabbā, na parihāni, evaṁvidhaṁ taṁ cittaṁ nābhidhaṁsiyati. ", "html": "{}"}, "pe8:144.4": {"pli": "Aparopettha yo akūṭo asaṭho amāyāvī uju puriso yathābhūtaṁ attānaṁ āvikaroti. ", "html": "{}"}, "pe8:144.5": {"pli": "Tattha yo chādeti tassa akusalā dhammā cittaṁ anudhāvanti. ", "html": "{}"}, "pe8:144.6": {"pli": "Channamativassatīti yo pana hoti asaṭho akūṭo amāyāvī uju puriso yathābhūtaṁ attānaṁ āvikaroti. ", "html": "{}"}, "pe8:144.7": {"pli": "Tassa cittaṁ akusalehi dhammehi na viddhaṁsiyati, ayaṁ suttattho. ", "html": "{}</p>"}, "pe8:145.1": {"pli": "Tattha katamā desanā? ", "html": "<p data-counter='1'>{}"}, "pe8:145.2": {"pli": "Idha desitā dasa akusalakammapathā adhivassanatāya dasa kusalakammapathā anadhivassanatāya akusalehi na visujjhati. ", "html": "{}"}, "pe8:145.3": {"pli": "Yathā vuttaṁ bhagavatā “cittasaṅkilesā, bhikkhave, sattā saṅkilissantī”ti. ", "html": "{}</p>"}, "pe8:146.1": {"pli": "Tattha katamo vicayo? ", "html": "<p data-counter='2'>{}"}, "pe8:146.2": {"pli": "Yassevaṁ cittaṁ adhivāsiyati, tassa bujjhitassa yaṁ bhaveyya kūṭeyya, taṁ ānantariyenapi satthari vā guṇānukampanatāya, ayaṁ vicayo. ", "html": "{}</p>"}, "pe8:147.1": {"pli": "Tattha katamā yuttīti? ", "html": "<p data-counter='3'>{}"}, "pe8:147.2": {"pli": "Evaṁ anadhivasiyantaṁ cittaṁ vuṭṭhāti. ", "html": "{}"}, "pe8:147.3": {"pli": "Vuṭṭhitaṁ patiṭṭhahati kusalesu dhammesūti atthi esā yutti. ", "html": "{}</p>"}, "pe8:148.1": {"pli": "Padaṭṭhānanti channamativassatīti channaṁ asaṁvarānaṁ padaṭṭhānaṁ, vivaṭaṁ nātivassatīti achannaṁ saṁvaraṇānaṁ. ", "html": "<p data-counter='4'>{}"}, "pe8:148.2": {"pli": "Tasmā channaṁ vivareyya vivaṭaṁ nātivassatīti desanāya padaṭṭhānaṁ. ", "html": "{}</p>"}, "pe8:149.1": {"pli": "Lakkhaṇoti channamativassatīti ye keci vicittena channena ekalakkhaṇā dhammā sabbe te aviddhaṁsiyanti. ", "html": "<p data-counter='5'>{}"}, "pe8:149.2": {"pli": "Tasmā channaṁ vivareyya. ", "html": "{}"}, "pe8:149.3": {"pli": "Vivaṭaṁ nātivassatīti ye keci tena acchannena ekalakkhaṇā dhammā sabbe te nātivassantīti lakkhaṇo hāro. ", "html": "{}</p>"}, "pe8:150.1": {"pli": "Tattha katamo catubyūho hāro? ", "html": "<p data-counter='6'>{}"}, "pe8:150.2": {"pli": "Imamhi sutte bhagavato ko adhippāyo? ", "html": "{}"}, "pe8:150.3": {"pli": "Yesaṁ kesañci cittaṁ akusalā dhammā adhipaṭidesitā te yathādhammaṁ paṭikarissantīti ayaṁ tattha bhagavato adhippāyo. ", "html": "{}"}, "pe8:150.4": {"pli": "Ayaṁ catubyūho hāro. ", "html": "{}</p>"}, "pe8:151.1": {"pli": "Āvaṭṭoti yaṁ channaṁ taṁ duvidhaṁ kampamānaṁ samucchitabbo. ", "html": "<p data-counter='7'>{}"}, "pe8:151.2": {"pli": "Ānantariyasamādhīnaṁ. ", "html": "{}"}, "pe8:151.3": {"pli": "Tattha passaddhiyañca māno āsave vaḍḍheti, assaddhiyena ca pamādaṁ gacchati, pamādena onamati, unnaḷabhāvaṁ gacchati. ", "html": "{}"}, "pe8:151.4": {"pli": "Vuttañcetaṁ bhagavatā “unnaḷānaṁ pamattānaṁ tesaṁ vaḍḍhanti āsavā”ti cattāri tāni upādānāni, yāni cattāri upādānāni, te pañcupādānakkhandhā bhavanti. ", "html": "{}"}, "pe8:151.5": {"pli": "Imāni saccāni dukkhañca samudayo ca. ", "html": "{}"}, "pe8:151.6": {"pli": "Tasmā channaṁ vivareyyāti yena hetunā, te āsavā vaḍḍhanti. ", "html": "{}"}, "pe8:151.7": {"pli": "Tesaṁ pahīnattā āsavā pahīyante. ", "html": "{}"}, "pe8:151.8": {"pli": "Tattha appamādena assaddhiyaṁ pahīyati uddhaccakukkuccappahānena oḷārikatā tassa dve dhammā na samatho ca bhāvanā ca pāripūriṁ gacchanti. ", "html": "{}"}, "pe8:151.9": {"pli": "Yo tesaṁ āsavānaṁ khayo, ayaṁ nirodho. ", "html": "{}"}, "pe8:151.10": {"pli": "Imāni cattāri saccāni, ayaṁ āvaṭṭo. ", "html": "{}</p>"}, "pe8:152.1": {"pli": "Tattha katamo vibhatti hāro? ", "html": "<p>{}"}, "pe8:152.2": {"pli": "Channamativassatīti na ekaṁso. ", "html": "{}"}, "pe8:152.3": {"pli": "Kiṅkāraṇaṁ? ", "html": "{}"}, "pe8:152.4": {"pli": "Yassa assā nivattanā yathāpi sekkhānaṁ. ", "html": "{}"}, "pe8:152.5": {"pli": "Yathāvuttaṁ bhagavatā—", "html": "{}</p>"}, "pe8:153.1": {"pli": "“Kiñcāpi sekkho pakareyya pāpaṁ, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe8:153.2": {"pli": "Kāyena vācāya uda cetasā vā; ", "html": "<span class='verse-line'>{}</span>"}, "pe8:153.3": {"pli": "Abhabbo hi tassa pariguhanāya, ", "html": "<span class='verse-line'>{}</span>"}, "pe8:153.4": {"pli": "Abhabbatā diṭṭhapadassa hotī”ti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe8:154.1": {"pli": "Kiñcāpi tesaṁ nivāraṇaṁ cittaṁ hoti. ", "html": "<p data-counter='8'>{}"}, "pe8:154.2": {"pli": "Api tu appaccayā samāye ca te niddisitabbā, ayaṁ vibhattihāro. ", "html": "{}</p>"}, "pe8:155.1": {"pli": "Tattha katamo parivattano hāro. ", "html": "<p data-counter='9'>{}"}, "pe8:155.2": {"pli": "Channamativassatīti yassa ye dhammā sabbaṁ anavivaṭaṁ ativassiyati, vivaṭaṁ nātivassati, avaguṇantaṁ nātivassati. ", "html": "{}"}, "pe8:155.3": {"pli": "Ayaṁ parivattano hāro. ", "html": "{}</p>"}, "pe8:156.1": {"pli": "Tattha katamo vevacano hāro. ", "html": "<p data-counter='10'>{}"}, "pe8:156.2": {"pli": "Channanti āvutaṁ nivutaṁ pihitaṁ paṭikujjitaṁ sañchannaṁ parodhaṁ, vivaṭaṁ nātivassatīti yassa te dhammā pabbajjitā vinodaṁ nādhivassitā vantikatāti, ayaṁ vevacano hāro. ", "html": "{}</p>"}, "pe8:157.1": {"pli": "Tattha katamo paññatti hāro. ", "html": "<p data-counter='11'>{}"}, "pe8:157.2": {"pli": "Channamativassatīti kilesabhāgiyapaññattaṁ vivaṭaṁ nātivassatīti sadhammakiccaṁ yaṁ paṭipadā paññattiyā paññattaṁ, tasmā hi channaṁ vivareyyāti anusāsanapaññattiyā paññattaṁ, vivaṭaṁ nātivassatīti niddhānapaññattiyā paññattaṁ, ayaṁ paññatti hāro. ", "html": "{}</p>"}, "pe8:158.1": {"pli": "Tattha katamo otaraṇo hāro? ", "html": "<p data-counter='12'>{}"}, "pe8:158.2": {"pli": "Channamativassatīti tayo kilesā rāgo doso moho, te khandhesu saṅkhārakkhandho …pe… ", "html": "{}"}, "pe8:158.3": {"pli": "te purā yathā niddiṭṭhaṁ khandhadhātuāyatanesu, ayaṁ otaraṇo hāro. ", "html": "{}</p>"}, "pe8:159.1": {"pli": "Tattha katamo sodhano hāro? ", "html": "<p data-counter='13'>{}"}, "pe8:159.2": {"pli": "Yenārambhena idaṁ suttaṁ bhāsati so ārambho niyutto. ", "html": "{}</p>"}, "pe8:160.1": {"pli": "Adhiṭṭhānoti channamativassatīti ekattatāya paññattaṁ. ", "html": "<p data-counter='14'>{}"}, "pe8:160.2": {"pli": "Kiṅkāraṇaṁ? ", "html": "{}"}, "pe8:160.3": {"pli": "Idaṁ hi ativassatīti imassa ca ativassati evañca ativassatīti ayaṁ vemattatāya yā suṇasādhāraṇehi lakkhaṇehi paññāpiyati, sā ekattapaññatti. ", "html": "{}</p>"}, "pe8:161.1": {"pli": "Tattha katamo parikkhāro? ", "html": "<p data-counter='15'>{}"}, "pe8:161.2": {"pli": "Yañca taṁ ativassiyanti, tassa dve hetū dve paccayā akusalapasuteva vācakattābhirati ca. ", "html": "{}"}, "pe8:161.3": {"pli": "Ime dve ayonisomanasikāro ca kusalā dhammā vopasaggā ca, ime dve paccayā. ", "html": "{}</p>"}, "pe8:162.1": {"pli": "Tattha katamo samāropano? ", "html": "<p data-counter='16'>{}"}, "pe8:162.2": {"pli": "Channamativassatīti vemati passatīti channaṁ yaṁ pariggahituṁ yaṁ adesituṁ appassutaṁ yaṁ kathaṅkathā vibhūtena akusalamūlena yaṁ taṇhāya ca te vaḍḍhati dosāti sannitvā te appasakkhayena saṅkhārā. ", "html": "{}"}, "pe8:162.3": {"pli": "Saṅkhārapaccayā viññāṇaṁ yāva jarāmaraṇaṁ, ayaṁ samāropano. ", "html": "{}"}, "pe8:162.4": {"pli": "Yaṁ puna tathā desanā, tasseva akusalā dhammā vuddhiṁ virūḷhiṁ vepullatamāpajjati tassa saṅkhārā nirodhā, ayaṁ samāropano. ", "html": "{}</p>"}, "pe8:163.1": {"pli": "Cattāro puggalā tamo tamaparāyanoti …pe… ", "html": "<p data-counter='1'>{}"}, "pe8:163.2": {"pli": "tattha katamo vuccate tamo nāma? ", "html": "{}"}, "pe8:163.3": {"pli": "Yo tamo andhakāro, yathā vuttaṁ bhagavatā “yathā andhakāre tasmiṁ bhayānake sakampidhātupuriso na passati, evameva aññāṇato tamopanandhakāro pāpakasakammasavipākaṁ na saddho hoti”. ", "html": "{}"}, "pe8:163.4": {"pli": "Iti evaṁ lakkhaṇatā aññāṇaṁ tamo avijjā moho, yena sattā yathābhūtaṁ nappajānanti, iti vuccati tamoti. ", "html": "{}"}, "pe8:163.5": {"pli": "So tiṇṇaṁ cakkhūnaṁ tamo maṁsacakkhuno dibbacakkhuno paññācakkhuno, imesaṁ cakkhūnaṁ idha tamo niddisiyati aññāṇanti. ", "html": "{}"}, "pe8:163.6": {"pli": "Tattha katamaṁ aññāṇaṁ adassanaṁ? ", "html": "{}"}, "pe8:163.7": {"pli": "Atha nissaye yaṁ pubbante aññāṇaṁ aparante aññāṇaṁ pubbantāparante aññāṇaṁ hetumhi aññāṇaṁ paccayamhi aññāṇaṁ tassa aññāṇino samādhibhūtassa eso nissando. ", "html": "{}"}, "pe8:163.8": {"pli": "Yaṁ na jānāti idaṁ sevitabbaṁ idaṁ na manasikātabbanti. ", "html": "{}"}, "pe8:163.9": {"pli": "So tena tamena niddisiyati tamopi yathā vuccati. ", "html": "{}"}, "pe8:163.10": {"pli": "Mūḷhoti evaṁ cetanā. ", "html": "{}"}, "pe8:163.11": {"pli": "Tena tamena so puggalo vuccati. ", "html": "{}"}, "pe8:163.12": {"pli": "Tamoti so tena tamena asamūhatena asamucchinnena tapparamo bhavati tapparāyano, ayaṁ vuccati puggalo tamo tamaparāyanoti. ", "html": "{}"}, "pe8:163.13": {"pli": "Parāyanoyeva dhammo manasikātabbo so tamo dahati aññacittaṁ upaṭṭhapeti. ", "html": "{}"}, "pe8:163.14": {"pli": "Te cassa dhammā nijjhānakkhamanti. ", "html": "{}"}, "pe8:163.15": {"pli": "So sutamayāya paññāya samanupassati. ", "html": "{}</p>"}, "pe8:164.1": {"pli": "Tattha katamo tamo jotiparāyano? ", "html": "<p data-counter='2'>{}"}, "pe8:164.2": {"pli": "So tena paññāvasena iriyati evaṁ tasseva iriyantassa parāyano bhavati. ", "html": "{}"}, "pe8:164.3": {"pli": "Ayaṁ vuccate puggalo tamo jotiparāyano. ", "html": "{}</p>"}, "pe8:165.1": {"pli": "Tattha katamo puggalo joti jotiparāyano? ", "html": "<p data-counter='3'>{}"}, "pe8:165.2": {"pli": "Tattha vuccati joti nāma yaṁ tassa ce tamassa paṭipakkhena ye ca dhamme antamaso ñāṇāloko, so suṇadhammo puggalo tamo jotiparāyano, tattha vuccate, yoyaṁ puggalo tamo jotiparāyano, so yadi tathārūpaṁ kalyāṇamittaṁ paṭilabhati, yo naṁ akusalato ca nivāreti bhāvitakusalatāva bhāvī niyojetīti. ", "html": "{}"}, "pe8:165.3": {"pli": "Evañca saddhammaṁ deseti. ", "html": "{}"}, "pe8:165.4": {"pli": "Ime dhammā kusalā, ime dhammā akusalā. ", "html": "{}"}, "pe8:165.5": {"pli": "Ime dhammā sāvajjā, ime dhammā anavajjā. ", "html": "{}"}, "pe8:165.6": {"pli": "Ime dhammā sevitabbā, ime dhammā na sevitabbā. ", "html": "{}"}, "pe8:165.7": {"pli": "Ime dhammā bhajitabbā, ime dhammā na bhajitabbā. ", "html": "{}"}, "pe8:165.8": {"pli": "Ime dhammā upasampajja vihātabbā, ime dhammā na upasampajja vihātabbā. ", "html": "{}"}, "pe8:165.9": {"pli": "Ime dhammā manasikātabbā, ime dhammā na manasikātabbāti. ", "html": "{}"}, "pe8:165.10": {"pli": "Paccate saññāya yathā saññāyati satindriyāni, so evaṁ pajānāti. ", "html": "{}"}, "pe8:165.11": {"pli": "Ime dhammā kusalā, ime dhammā akusalā. ", "html": "{}"}, "pe8:165.12": {"pli": "Ime dhammā sāvajjā, ime dhammā anavajjā. ", "html": "{}"}, "pe8:165.13": {"pli": "Ime dhammā sevitabbā, ime dhammā na sevitabbā. ", "html": "{}"}, "pe8:165.14": {"pli": "Ime dhammā bhāvetabbā, ime dhammā na bhāvetabbā. ", "html": "{}"}, "pe8:165.15": {"pli": "Ime dhammā upasampajja vihātabbā, ime dhammā na upasampajja vihātabbā. ", "html": "{}"}, "pe8:165.16": {"pli": "Ime dhammā manasikātabbā, ime dhammā na manasikātabbāti. ", "html": "{}"}, "pe8:165.17": {"pli": "So te dhamme susuyyati, sotaṁ odahati, aññaṁ cittaṁ upaṭṭhapeti, te cassa dhammā nijjhānakkhamanti, so sutamayāya paññāya samannāgato so tena paccayavasena iriyati evaṁ tasseva iriyanti tapparamo bhavati tapparāyano. ", "html": "{}"}, "pe8:165.18": {"pli": "Ayaṁ vuccate puggalo tamo tamaparāyano. ", "html": "{}</p>"}, "pe8:166.1": {"pli": "Tattha katamo puggalo joti tamaparāyano? ", "html": "<p data-counter='4'>{}"}, "pe8:166.2": {"pli": "Joti nāma yā tasseva tamassa paṭipakkhena ye dhammā antamaso ñāṇāloko, so puna dhammo. ", "html": "{}"}, "pe8:166.3": {"pli": "Katamā uccate? ", "html": "{}"}, "pe8:166.4": {"pli": "Paññāyato paṇḍitoti vuccate, so evaṁ pajānāti. ", "html": "{}"}, "pe8:166.5": {"pli": "Ime dhammā kusalā, ime dhammā akusalā. ", "html": "{}"}, "pe8:166.6": {"pli": "Ime dhammā sāvajjā, ime dhammā anavajjā. ", "html": "{}"}, "pe8:166.7": {"pli": "Ime dhammā sevitabbā, ime dhammā na sevitabbā. ", "html": "{}"}, "pe8:166.8": {"pli": "Ime dhammā bhāvitabbā, ime dhammā na bhāvitabbā. ", "html": "{}"}, "pe8:166.9": {"pli": "Ime dhammā upasampajja vihātabbā, ime dhammā na upasampajja vihātabbā. ", "html": "{}"}, "pe8:166.10": {"pli": "Ime dhammā manasikātabbā, ime dhammā na manasikātabbā. ", "html": "{}"}, "pe8:166.11": {"pli": "Idha pana pāpamittasaṁsevano pāpamittavasānugo akusale dhamme abhivaḍḍheti, kusale dhamme pajahati. ", "html": "{}"}, "pe8:166.12": {"pli": "So tena pamādena paccayasaññā amanasikatvā assatiasampajaññaṁ āsevati. ", "html": "{}"}, "pe8:166.13": {"pli": "Tayā yo paṭipakkho tamo, so pavaḍḍheti. ", "html": "{}"}, "pe8:166.14": {"pli": "So tamābhibhūto parāyano tamaparamo ceva bhavati. ", "html": "{}"}, "pe8:166.15": {"pli": "Ayaṁ vuccati puggalo joti tamaparāyano. ", "html": "{}</p>"}, "pe8:167.1": {"pli": "Tattha katamo puggalo joti jotiparāyano? ", "html": "<p>{}"}, "pe8:167.2": {"pli": "Tattha vuccate soyaṁ puggalo kalyāṇamittassa sannissito bhavati sakkā saṁyogī kusalaṁ gavesī, so kalyāṇamitte upasaṅkamitvā paripucchati, paripañhayati—", "html": "{}"}, "pe8:167.3": {"pli": "kiṁ kusalaṁ, kiṁ akusalaṁ? ", "html": "{}"}, "pe8:167.4": {"pli": "Kiṁ sāvajjaṁ, kiṁ anavajjaṁ? ", "html": "{}"}, "pe8:167.5": {"pli": "Kiṁ sevitabbaṁ, kiṁ na sevitabbaṁ? ", "html": "{}"}, "pe8:167.6": {"pli": "Kiṁ bhāvitabbaṁ, kiṁ na bhāvitabbaṁ? ", "html": "{}"}, "pe8:167.7": {"pli": "Kiṁ upasampajja vihātabbaṁ, kiṁ na upasampajja vihātabbaṁ? ", "html": "{}"}, "pe8:167.8": {"pli": "Kiṁ manasikātabbaṁ, kiṁ na manasikātabbaṁ? ", "html": "{}"}, "pe8:167.9": {"pli": "Kathaṁ saṅkileso hoti, kathaṁ vodānaṁ hoti? ", "html": "{}"}, "pe8:167.10": {"pli": "Kathaṁ pavatti hoti, kathaṁ nivatti hoti? ", "html": "{}"}, "pe8:167.11": {"pli": "Kathaṁ bandho hoti, kathaṁ mokkho hoti? ", "html": "{}"}, "pe8:167.12": {"pli": "Kathaṁ sakkāyasamudayo hoti, kathaṁ sakkāyanirodho hoti? ", "html": "{}"}, "pe8:167.13": {"pli": "So ettha desitaṁ yathā upaṭṭhitaṁ tathā sampaṭipajjanto so evaṁ pajānāti. ", "html": "{}"}, "pe8:167.14": {"pli": "Ime dhammā kusalā, ime dhammā akusalā. ", "html": "{}"}, "pe8:167.15": {"pli": "Evaṁ …pe… ", "html": "{}"}, "pe8:167.16": {"pli": "yāva kathaṁ sakkāyasamudayo hoti, kathaṁ sakkāyanirodho hotīti vitthārena kātabbaṁ. ", "html": "{}"}, "pe8:167.17": {"pli": "So te dhamme adhipāṭikaṅkhāti evaṁ lakkhaṇaṁ ñāṇaṁ vijjā ālokaṁ vaḍḍheti. ", "html": "{}"}, "pe8:167.18": {"pli": "So puggalo tapparamo bhavati tapparāyano, ayaṁ vuccate puggalo joti jotiparāyano. ", "html": "{}</p>"}, "pe8:168.1": {"pli": "Tattha katamo puggalo tamo tamaparāyano? ", "html": "<p>{}"}, "pe8:168.2": {"pli": "Yo akusalaṁ dhammaṁ dīpeti. ", "html": "{}"}, "pe8:168.3": {"pli": "Taṁ bhāvanāya hīnāsu gatīsu upapattiṁ dasseti, tapparamo bhavati tapparāyano. ", "html": "{}"}, "pe8:168.4": {"pli": "Ayaṁ vuccate puggalo tamo tamaparāyano. ", "html": "{}</p>"}, "pe8:169.1": {"pli": "Tattha yo puggalo tamo jotiparāyano? ", "html": "<p>{}"}, "pe8:169.2": {"pli": "So tamena akusalassa kammassa vipākaṁ dasseti. ", "html": "{}"}, "pe8:169.3": {"pli": "Tameti yaṁ cakkhu kalyāṇamittassa yena akusale dhamme pajahati, kusale dhamme abhivaḍḍhati. ", "html": "{}</p>"}, "pe8:170.1": {"pli": "Tattha yo ca paṇītāsu gatīsu upapattiṁ dasseti, tapparamo tena vuccate tamo jotiparāyano. ", "html": "<p>{}</p>"}, "pe8:171.1": {"pli": "Tattha yo puggalo joti tamaparāyano? ", "html": "<p>{}"}, "pe8:171.2": {"pli": "Kusalassa kammavipākaṁ dasseti. ", "html": "{}"}, "pe8:171.3": {"pli": "Yaṁ cakkhu pāpamittasaṁsaggena pāpamittupasevena pāpamittavasānugo akusalaṁ dhammaṁ abhivaḍḍhati, taṁ bhāvanāya hīnāsu gatīsu upapattiṁ dasseti. ", "html": "{}"}, "pe8:171.4": {"pli": "Tapparamo tena vuccate joti tamaparāyano. ", "html": "{}</p>"}, "pe8:172.1": {"pli": "Tattha yo puggalo joti jotiparāyano so jotitā pabhātā yāva paṇītāsu gatīsu upapattiṁ dasseti. ", "html": "<p>{}"}, "pe8:172.2": {"pli": "Tapparamo tenāha joti jotiparāyano. ", "html": "{}</p>"}, "pe8:173.1": {"pli": "Jotitamaparāyanena dasa akusalānaṁ kammānaṁ udayaṁ dasseti. ", "html": "<p>{}"}, "pe8:173.2": {"pli": "Tamena puggalena akusalānaṁ kammānaṁ vipākaṁ dasseti. ", "html": "{}"}, "pe8:173.3": {"pli": "Na akusalānaṁ dhammānaṁ vipākaṁ dasseti. ", "html": "{}"}, "pe8:173.4": {"pli": "Tamena aṭṭha micchattāni dasseti. ", "html": "{}"}, "pe8:173.5": {"pli": "Jotinā aṭṭha sammattāni dasseti. ", "html": "{}"}, "pe8:173.6": {"pli": "Jotinā tamaparāyanena dasa akusalakammapathe dasseti. ", "html": "{}"}, "pe8:173.7": {"pli": "Jotinā paṇītattaṁ dasseti. ", "html": "{}"}, "pe8:173.8": {"pli": "Tamena jotiparāyanena atapanīyaṁ dhammaṁ dasseti. ", "html": "{}"}, "pe8:173.9": {"pli": "Jotinā tamaparāyanena tapanīyaṁ dhammaṁ dasseti. ", "html": "{}"}, "pe8:173.10": {"pli": "Ayaṁ suttattho. ", "html": "{}</p>"}, "pe8:174.1": {"pli": "Tattha katamo desanā hāro? ", "html": "<p data-counter='1'>{}"}, "pe8:174.2": {"pli": "Imamhi sutte kiṁ desitaṁ? ", "html": "{}"}, "pe8:174.3": {"pli": "Tattha vuccate imamhi sutte kusalākusalā dhammā desitā. ", "html": "{}"}, "pe8:174.4": {"pli": "Kusalākusalānañca dhammānaṁ vipāko desito. ", "html": "{}"}, "pe8:174.5": {"pli": "Hīnappaṇītānañca sattānaṁ gati nānākāraṇaṁ desitaṁ. ", "html": "{}"}, "pe8:174.6": {"pli": "Ayaṁ desanā hāro. ", "html": "{}</p>"}, "pe8:175.1": {"pli": "Tattha katamo vicayo hāro? ", "html": "<p data-counter='2–3'>{}"}, "pe8:175.2": {"pli": "Akusalassa kammassa yo vipākaṁ paccanubhoti. ", "html": "{}"}, "pe8:175.3": {"pli": "Tattha ṭhito akusale dhamme uppādiyati vicayantaṁ yujjati. ", "html": "{}"}, "pe8:175.4": {"pli": "Kusalassa kammassa yo vipākaṁ paccanubhoti. ", "html": "{}"}, "pe8:175.5": {"pli": "Tattha ṭhito kusale dhamme uppādiyati vicayantaṁ yujjati. ", "html": "{}"}, "pe8:175.6": {"pli": "Ayaṁ vicayo yutti ca. ", "html": "{}</p>"}, "pe8:176.1": {"pli": "Tattha katamo padaṭṭhāno hāro? ", "html": "<p data-counter='4'>{}"}, "pe8:176.2": {"pli": "Yo puggalo joti, so paccavekkhaṇāya padaṭṭhānaṁ. ", "html": "{}"}, "pe8:176.3": {"pli": "Yo puggalo tamo, so tamādinnaṁ vānupassanāya padaṭṭhānanti dasseti. ", "html": "{}"}, "pe8:176.4": {"pli": "Tamena jotiparāyanena appamādassa padaṭṭhānaṁ dasseti, tamo avijjāya ca diṭṭhiyā ca padaṭṭhānaṁ dasseti. ", "html": "{}"}, "pe8:176.5": {"pli": "Jotinā tamaparāyanena pamādassa ca diṭṭhiyā ca padaṭṭhānaṁ dasseti. ", "html": "{}"}, "pe8:176.6": {"pli": "Ayaṁ padaṭṭhāno. ", "html": "{}</p>"}, "pe8:177.1": {"pli": "Tattha katamo lakkhaṇo hāro? ", "html": "<p data-counter='5'>{}"}, "pe8:177.2": {"pli": "Tamena tamaparāyanena tamoti avijjāya niddiṭṭhāya sabbakilesadhammā niddiṭṭhā honti. ", "html": "{}"}, "pe8:177.3": {"pli": "Tamena jotiparāyanena jotivijjāya niddiṭṭhāya sabbe bodhipakkhiyadhammā niddiṭṭhā honti. ", "html": "{}"}, "pe8:177.4": {"pli": "Jotitamaparāyanena pamādo niddiṭṭho hoti. ", "html": "{}"}, "pe8:177.5": {"pli": "Tamena jotiparāyanena appamādo niddiṭṭho hoti. ", "html": "{}"}, "pe8:177.6": {"pli": "Ayaṁ lakkhaṇo hāro. ", "html": "{}</p>"}, "pe8:178.1": {"pli": "Tattha katamo catubyūho hāro? ", "html": "<p data-counter='6'>{}"}, "pe8:178.2": {"pli": "Imamhi sutte bhagavato ko adhippāyo? ", "html": "{}"}, "pe8:178.3": {"pli": "Ye sattā nīcakulino, na te imaṁ sutvā kusale dhamme samādāya vattissanti. ", "html": "{}"}, "pe8:178.4": {"pli": "Ye sattā uccakulino, te imaṁ dhammadesanaṁ sutvā bhiyyoso mattāya kusale dhamme samādāya vattissantīti. ", "html": "{}"}, "pe8:178.5": {"pli": "Ayaṁ catubyūho hāro. ", "html": "{}"}, "pe8:178.6": {"pli": "Bhūmiyaṁ upadeso. ", "html": "{}</p>"}, "pe8:179.1": {"pli": "Tattha katamo āvaṭṭo hāro? ", "html": "<p data-counter='7'>{}"}, "pe8:179.2": {"pli": "Yā avijjāto pabhūti taṇhā, ayaṁ samudayo. ", "html": "{}"}, "pe8:179.3": {"pli": "Yo tamo tamaparāyano, idaṁ dukkhaṁ. ", "html": "{}"}, "pe8:179.4": {"pli": "Imāni dve saccāni dukkhañca samudayo ca joti yena suttena dhammena paññāpiyati, so dhammo paññindriyassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:179.5": {"pli": "Tena amohena tīṇi kusalamūlāni pāripūriṁ gacchanti saggassa padaṭṭhānaṁ. ", "html": "{}</p>"}, "pe8:180.1": {"pli": "Tattha katamā vibhatti? ", "html": "<p data-counter='8–9'>{}"}, "pe8:180.2": {"pli": "Tamo tamaparāyanoti na ekaṁsena. ", "html": "{}"}, "pe8:180.3": {"pli": "Kiṅkāraṇaṁ? ", "html": "{}"}, "pe8:180.4": {"pli": "Atthi tamo ca bhavo aparāpariyavedanīyena ca kusalena jotinā puggalena sahopattibhāve. ", "html": "{}"}, "pe8:180.5": {"pli": "Atthi joti ca bhavo aparāpariyavedanīyena ca akusalena tamena puggalena sahopattibhāve parivattanā tamesu paṭipakkhoti jotinā tamaparāyano. ", "html": "{}</p>"}, "pe8:181.1": {"pli": "Tattha katamo vevacano? ", "html": "<p data-counter='10'>{}"}, "pe8:181.2": {"pli": "Yo tamo, so evaṁ attabyāpādāya paṭipanno, so assaddhāya bālo akusalo abyatto anādīnavadassī. ", "html": "{}"}, "pe8:181.3": {"pli": "Yo joti, so attahitāya paṭipanno paṇḍito kusalo byatto ādīnavadassī. ", "html": "{}"}, "pe8:181.4": {"pli": "Ayaṁ vevacano. ", "html": "{}</p>"}, "pe8:182.1": {"pli": "Tattha katamā paññatti? ", "html": "<p data-counter='11'>{}"}, "pe8:182.2": {"pli": "So puggalo vipākapaññattiyā paññāpiyati, akusale pariyādinnatā paññāpiyati. ", "html": "{}"}, "pe8:182.3": {"pli": "Jotikusaladhammupapattipaññattiyā paññāpiyati kusaladhammavipākapaññattiyā cāti. ", "html": "{}</p>"}, "pe8:183.1": {"pli": "Otaraṇoti ye avijjāpaccayā saṅkhārā yañca jarāmaraṇaṁ yā ca avijjā, taṁ padaṭṭhānaṁ, niddesena vijjuppādo avijjānirodho yo yāva jarāmaraṇanirodho, ime dve dhammā saṅkhārakkhandhapariyāpannā. ", "html": "<p data-counter='12'>{}"}, "pe8:183.2": {"pli": "Dhammadhātu dhammāyatanañca padaṭṭhānaṁ niddesena dhātūsu. ", "html": "{}</p>"}, "pe8:184.1": {"pli": "Tattha katamo sodhano? ", "html": "<p data-counter='13'>{}"}, "pe8:184.2": {"pli": "Imassa suttassa desitassa ārambho. ", "html": "{}</p>"}, "pe8:185.1": {"pli": "Adhiṭṭhānoti tamoti bhagavā bravīti, na ekaṁ puggalaṁ deseti. ", "html": "<p data-counter='14'>{}"}, "pe8:185.2": {"pli": "Yāvatā sattānaṁ gati, tattha ye duccaritadhammena upapannā, te bahulādhivacanena tamo niddisati. ", "html": "{}"}, "pe8:185.3": {"pli": "Yā joti sabbasattesu kusaladhammopapatti sabbaṁ taṁ jotīti abhilapati ayamekatā paccayo yonisomanasikārapaññatti catunnaṁ mahābhūtānaṁ puggalānaṁ. ", "html": "{}</p>"}, "pe8:186.1": {"pli": "Tattha katamo parikkhāro? ", "html": "<p data-counter='15'>{}"}, "pe8:186.2": {"pli": "Akusalassa pāpamittatā paccayo, ayoniso manasikāro hetu. ", "html": "{}"}, "pe8:186.3": {"pli": "Kusalassa kalyāṇamittatā paccayo, yoniso manasikāro hetu. ", "html": "{}</p>"}, "pe8:187.1": {"pli": "Tattha katamā samāropanāti? ", "html": "<p data-counter='16'>{}"}, "pe8:187.2": {"pli": "Idhekacco nīce kule paccājāto hotīti nīce kule paccājāto rūpesu saddesu gandhesu rasesu phassesu, so upapanno sabbamhi mānussake upabhogaparibhoge. ", "html": "{}"}, "pe8:187.3": {"pli": "Joti paṇītesu kusalesu upapanno sabbamhi mānussake upabhogaparibhoge upapannoti. ", "html": "{}</p>"}, "pe8:188.1": {"pli": "Tattha katamaṁ saṅkilesabhāgiyaṁ nibbedhabhāgiyaṁ ca suttaṁ? ", "html": "<p>{}"}, "pe8:188.2": {"pli": "Na taṁ daḷhaṁ bandhanamāhu dhīrāti gāthā. ", "html": "{}"}, "pe8:188.3": {"pli": "Kena kāraṇena taṁ bandhanaṁ daḷhaṁ? ", "html": "{}"}, "pe8:188.4": {"pli": "Catūhi kāraṇehi issariyena sakkā mocetuṁ dhanena vā aññena vā yācanāya vā parāyanena vā. ", "html": "{}"}, "pe8:188.5": {"pli": "Yesu ca ayaṁ rāgo maṇikuṇḍalesu puttesu dāresu ca yā apekkhā, idamassa cetasikabandhanaṁ. ", "html": "{}"}, "pe8:188.6": {"pli": "Taṁ na sakkā issariyena vā dhanena vā aññena vā yācanāya vā parāyanena vā mocetuṁ. ", "html": "{}"}, "pe8:188.7": {"pli": "Na ca tattha koci atthi pāṭibhogo. ", "html": "{}"}, "pe8:188.8": {"pli": "Iminā bandhanato mocayitthāti devo vā manusso vā tadidaṁ bandhanaṁ rāgānusayena ca chasu bāhiresu ca āyatanesu bandhati. ", "html": "{}"}, "pe8:188.9": {"pli": "Rūpesu rūpataṇhā bandhati, yāva dhammesu dhammataṇhā. ", "html": "{}"}, "pe8:188.10": {"pli": "Yo idha loke bandho paralokasmiṁ bandho nīyati. ", "html": "{}"}, "pe8:188.11": {"pli": "So bandho jāyati, bandho mīyati. ", "html": "{}"}, "pe8:188.12": {"pli": "Bandho asmā lokā paraṁ lokaṁ gacchati. ", "html": "{}"}, "pe8:188.13": {"pli": "Na sakkā mocetuṁ aññatra ariyamaggena imañca bandhanaṁ. ", "html": "{}"}, "pe8:188.14": {"pli": "Maraṇabhāvañca upapattibhāvañca bhayato viditvā chandarāgaṁ pajahati. ", "html": "{}"}, "pe8:188.15": {"pli": "So imaṁ chandarāgaṁ pajahitvā atikkamati. ", "html": "{}"}, "pe8:188.16": {"pli": "Ayañca loko ito paraṁ dutiyo. ", "html": "{}</p>"}, "pe8:189.1": {"pli": "Tattha yaṁ bandhanāsaṅkhārānaṁ pahānaṁ idaṁ vuccati ubhayesu ṭhānesu vīriyaṁ, gandhaparivāto sumuni nopalimpati. ", "html": "<p>{}"}, "pe8:189.2": {"pli": "Tatheva pariggahesu puttesu dāresu ca avūḷho salloti tasseva taṇhāya pahānaṁ dasseti. ", "html": "{}"}, "pe8:189.3": {"pli": "Ayaṁ taṇhāmūlassa pahānā vare appamattoti kāmo pamādavattati pahānāya nekkhammābhirato appamādavihārī bhavati. ", "html": "{}"}, "pe8:189.4": {"pli": "Tassa āsayaṁ pahānāya neva imaṁ lokaṁ āsīsati na paralokaṁ. ", "html": "{}"}, "pe8:189.5": {"pli": "Na idhalokaṁ nissitaṁ, piyarūpaṁ sātarūpaṁ ākaṅkhati. ", "html": "{}"}, "pe8:189.6": {"pli": "Nāpi paralokaṁ nissitaṁ piyarūpaṁ sātarūpaṁ ākaṅkhati, tena vuccate “nāsīsate lokamimaṁ paraṁ lokañcā”ti. ", "html": "{}"}, "pe8:189.7": {"pli": "Yaṁ tassa pahānaṁ taṁ chedanaṁ aṭṭhakavaggiyesu muni niddiṭṭho. ", "html": "{}"}, "pe8:189.8": {"pli": "So idha virodho aṭṭhakavaggiyesu nāsīsanaṁ idha anāthā. ", "html": "{}"}, "pe8:189.9": {"pli": "Tathāyaṁ taṇhāya tassa pariggahassa vatthukāmassa ekagāthāya ete sabbe kāmā dassitā. ", "html": "{}"}, "pe8:189.10": {"pli": "Tena bhagavā deseti “etampi chetvāna paribbajanti anapekkhino sabbakāme pahāyā”ti. ", "html": "{}"}, "pe8:189.11": {"pli": "Imissā gāthāya dvidhā niddeso saṁsandananiddeso ca samayaniddeso ca, yathā ayaṁ gāthā saṅkilesabhāgiyañca nibbedhabhāgiyañca, evaṁ tāya gāthāya saṅkilesabhāgiyañca nibbedhabhāgiyañca visajjanā. ", "html": "{}"}, "pe8:189.12": {"pli": "Evaṁ gāthā sabbagāthāsu byākaraṇesu vā niddiṭṭhaṁ suttaṁ. ", "html": "{}</p>"}, "pe8:190.1": {"pli": "Tattha katamā desanā? ", "html": "<p data-counter='1'>{}"}, "pe8:190.2": {"pli": "Imaṁ suttaṁ kenādhippāyena desitaṁ. ", "html": "{}"}, "pe8:190.3": {"pli": "Ye rāgacaritā sattā, te kāme pajahissantīti ayaṁ tattha bhagavato adhippāyo. ", "html": "{}</p>"}, "pe8:191.1": {"pli": "Tattha katamo vicayo? ", "html": "<p data-counter='2'>{}"}, "pe8:191.2": {"pli": "Yassa dasavatthukā kilesā uttiṇṇā vantā viditā. ", "html": "{}"}, "pe8:191.3": {"pli": "Katame dasavidhāti, kilesakāmā ca orambhāgiyauddhambhāgiyā ca saṁyojanā dasavatthukāni āyatanāni, ayaṁ vicayo. ", "html": "{}</p>"}, "pe8:192.1": {"pli": "Tattha katamā yutti? ", "html": "<p data-counter='3'>{}"}, "pe8:192.2": {"pli": "Ye sārattā te gāḷhabandhanena bandhanti atthi esā yutti. ", "html": "{}</p>"}, "pe8:193.1": {"pli": "Tattha katamo padaṭṭhāno? ", "html": "<p data-counter='4'>{}"}, "pe8:193.2": {"pli": "Sāratto maṇikuṇḍalesu mamaṅkārassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:193.3": {"pli": "Apekkhāti atītavatthussa sarāgassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:193.4": {"pli": "Etampi chetvāti bhāvanāya padaṭṭhānaṁ. ", "html": "{}</p>"}, "pe8:194.1": {"pli": "Tattha katamo lakkhaṇo? ", "html": "<p data-counter='5'>{}"}, "pe8:194.2": {"pli": "Sārattacitto maṇikuṇḍalesu yo ahaṅkāre visatto mamaṅkāre visatto, yo puttadāre sāratto. ", "html": "{}"}, "pe8:194.3": {"pli": "Khettavatthusmiṁ sāratto. ", "html": "{}"}, "pe8:194.4": {"pli": "Ayaṁ lakkhaṇo hāro. ", "html": "{}</p>"}, "pe8:195.1": {"pli": "Tattha katamo catubyūho hāro? ", "html": "<p data-counter='6'>{}"}, "pe8:195.2": {"pli": "Idha sutte bhagavato ko adhippāyo. ", "html": "{}"}, "pe8:195.3": {"pli": "Ye nibbānena chandikā bhavissanti, te puttadāre taṇhaṁ pajahissanti. ", "html": "{}"}, "pe8:195.4": {"pli": "Ayaṁ tattha bhagavato adhippāyo. ", "html": "{}"}, "pe8:195.5": {"pli": "Imāni cattāri saccāni. ", "html": "{}</p>"}, "pe8:196.1": {"pli": "Tattha katamo āvaṭṭo? ", "html": "<p data-counter='7'>{}"}, "pe8:196.2": {"pli": "Yā puttadāre taṇhā, ayaṁ samudayo. ", "html": "{}"}, "pe8:196.3": {"pli": "Ye upādinnakkhandhā, te ye ca bāhiresu rūpesu rūpapariggaho, idaṁ dukkhaṁ, yaṁ tattha chedanīyaṁ, ayaṁ nirodho. ", "html": "{}"}, "pe8:196.4": {"pli": "Yena bhijjati, ayaṁ maggo. ", "html": "{}</p>"}, "pe8:197.1": {"pli": "Vibhattīti natthi vibhattiyā bhūmi. ", "html": "<p data-counter='8'>{}</p>"}, "pe8:198.1": {"pli": "Parivattanoti paṭipakkho niddiṭṭho. ", "html": "<p data-counter='9'>{}</p>"}, "pe8:199.1": {"pli": "Tattha katamo vevacano? ", "html": "<p data-counter='10'>{}"}, "pe8:199.2": {"pli": "Niddiṭṭho vevacano. ", "html": "{}</p>"}, "pe8:200.1": {"pli": "Tattha katamo otaraṇo? ", "html": "<p data-counter='12'>{}"}, "pe8:200.2": {"pli": "Atthi taṇhā eko satto otiṇṇo tappaccayā viññāṇaṁ yāva jarāmaraṇaṁ. ", "html": "{}"}, "pe8:200.3": {"pli": "Yā tattha vedanā, ayaṁ avijjā vijjuppādā avijjānirodho yāva jarāmaraṇanirodho. ", "html": "{}</p>"}, "pe8:201.1": {"pli": "Tattha katamo sodhano? ", "html": "<p data-counter='13'>{}"}, "pe8:201.2": {"pli": "Suddho gāthāya ārambho. ", "html": "{}</p>"}, "pe8:202.1": {"pli": "Tattha katamo adhiṭṭhāno? ", "html": "<p data-counter='14'>{}"}, "pe8:202.2": {"pli": "Na taṁ daḷhaṁ bandhanamāhu dhīrāti ekattatāya paññattā, na vemattatāya. ", "html": "{}"}, "pe8:202.3": {"pli": "Cattāro rāgā kāmarāgo rūparāgo bhavarāgo diṭṭhirāgo cāti ekattatāya paññattā. ", "html": "{}</p>"}, "pe8:203.1": {"pli": "Tattha katamo parikkhāro? ", "html": "<p data-counter='15'>{}"}, "pe8:203.2": {"pli": "Yesaṁ rāgo maṇikuṇḍalesu tassa subhasaññā hetu, anubyañjanaso ca nimittaggāhitā paccayo. ", "html": "{}"}, "pe8:203.3": {"pli": "Yāya te chinnāni tassa asubhasaññā hetu, nimittaggahaṇaanubyañjanaggahaṇavinodanaṁ paccayo. ", "html": "{}</p>"}, "pe8:204.1": {"pli": "Tattha katamo samāropano? ", "html": "<p data-counter='16'>{}"}, "pe8:204.2": {"pli": "Sāratto maṇikuṇḍalesu sammūḷhavidho duṭṭhātipi etampi chetvāna paribbajantīti taṁ pariññātatthaṁ parivajjitatthaṁ pajahitā, ayaṁ samāropano. ", "html": "{}</p>"}, "pe8:205.1": {"pli": "Yaṁ cetasikaṁ yaṁ pakappitaṁ vitthārena paccayo, yaṁ vā cetasikaṁ kāyikaṁ cetasikaṁ kammaṁ. ", "html": "<p>{}"}, "pe8:205.2": {"pli": "Kiṅkāraṇā? ", "html": "{}"}, "pe8:205.3": {"pli": "Cetasikā hi cetanā manokammāti vuccate, sā cetanākammaṁ, yaṁ cetasikaṁ imaṁ kāyikañca vācasikañca imāni tīṇi kammāni niddiṭṭhāni. ", "html": "{}"}, "pe8:205.4": {"pli": "Kāyakammaṁ vacīkammañca tāni kusalāni piyaṁ kāyena ca vācāya ca ārabhati parāmasati, ayaṁ vuccati sīlabbataparāmāso. ", "html": "{}"}, "pe8:205.5": {"pli": "Saṅkappanā te tividhā saṅkhārā puññamayā apuññamayā āneñjamayā, tappaccayā viññāṇaṁ te ārammaṇametaṁ hoti viññāṇassa ṭhitiyā. ", "html": "{}"}, "pe8:205.6": {"pli": "Yā subhasaññā sukhasaññā attasaññā ca. ", "html": "{}"}, "pe8:205.7": {"pli": "Idaṁ cetasikaṁ. ", "html": "{}"}, "pe8:205.8": {"pli": "Yaṁ rūpūpagaṁ viññāṇaṁ tiṭṭhati rūpārammaṇaṁ rūpapatiṭṭhitaṁ nandūpasecanaṁ vuddhiṁ virūḷhiṁ vepullataṁ gacchati, ayaṁ saṅkappanā, iti yaṁ viññāṇaṭṭhitīsu ṭhitaṁ paṭhamābhinibbattiārammaṇavasena upādānaṁ, idaṁ vuccati cetasikanti. ", "html": "{}</p>"}, "pe8:206.1": {"pli": "Tattha ṭhitassa arūpassa yā nikanti ajjhosānaṁ, idampi sakampitaṁ manāpikesu rūpesu piyarūpasātarūpesu ābhogo, idaṁ cetasikaṁ. ", "html": "<p>{}"}, "pe8:206.2": {"pli": "Yaṁ ceteti sattesu manāpikesu abhijjhākāyagantho paṭighānusayesu byāpādakāyagantho sabbe cattāro ganthā, ayaṁ pañcasu kāmaguṇesu paṭhamābhinipāto cittassa yā cetanā yassa tattha assādānupassissa anekā pāpakā akusalā dhammā cittaṁ arūpavatiyo honti. ", "html": "{}"}, "pe8:206.3": {"pli": "Puggalo rāgānubandhibhūto tehi kilesakāmehi yathā kāmakaraṇīyo, ayaṁ vuccate kāmesu pakappanā. ", "html": "{}"}, "pe8:206.4": {"pli": "Evaṁ sabbe cattāro oghā. ", "html": "{}"}, "pe8:206.5": {"pli": "Yaṁ tehi kāmehi saṁyutto viharati bhāvito ajjhosanno, ayaṁ cetanā. ", "html": "{}"}, "pe8:206.6": {"pli": "Yassa tathāyaṁ avītarāgassa adhigatapemassa tassa vipariṇāmaññathābhāvā uppajjanti sokaparidevadukkhadomanassupāyāsā dukkhānuparivattitaṁ viññāṇaṁ hoti saritassa vayadhammasamuppādo cittaṁ pariyādiyati, idaṁ vuccati pakappitanti. ", "html": "{}</p>"}, "pe8:207.1": {"pli": "Ekamekassa ceteti ca pakappeti ca viññāṇassa ṭhiti yā hoti, sā ca ṭhiti dvidhā ārammaṇaṭṭhiti ca āhāraṭṭhiti ca. ", "html": "<p>{}"}, "pe8:207.2": {"pli": "Tattha yā ārammaṇaṭṭhiti, ayaṁ nāmarūpassa paccayo. ", "html": "{}"}, "pe8:207.3": {"pli": "Yā āhāraṭṭhiti yā punabbhavābhinibbattikā ṭhiti yā ca ponobhavikā ṭhiti, ayaṁ vuccati ārammaṇaṁ. ", "html": "{}"}, "pe8:207.4": {"pli": "Taṁ hoti viññāṇassa ṭhitiyā tassa viññāṇapaccayā nāmarūpaṁ yāva jarāmaraṇañca ceteti, atha ca puna patthayate yato na ponobhavikā anāgatavatthumhi, ayaṁ paṭipakkho niddiṭṭho. ", "html": "{}"}, "pe8:207.5": {"pli": "Na ceteti na patthayati atha ca dūsetīti duvidho niddeso. ", "html": "{}"}, "pe8:207.6": {"pli": "Assa pubbe hoti taṁ cetasikaṁ taṁ pakappitaṁ asamūhataṁ tappaccayā, ayaṁ viññāṇassa ṭhiti hoti. ", "html": "{}</p>"}, "pe8:208.1": {"pli": "Atha vā tassa anusayā āvibhavanti tappaccayā tassa punabbhavo nibbattati. ", "html": "<p>{}"}, "pe8:208.2": {"pli": "Atha vā naṁ saṅkiyate appetu āgāre vā, sukhumā vā santi vā na saṅkiyate kāme taṁ evaṁ niccesupi āgāresu jāto hoti. ", "html": "{}"}, "pe8:208.3": {"pli": "Taṁ nayati yaṁ no kappetuṁ evaṁ saṅkhārā cetitā pakappitā ca ārammaṇabhūtā honti, yā ca cetanā yā ca pakappanā yañca vatthu nibbattaṁ, ubhopi ete ārammaṇaṁ viññāṇassa tathā cetanāya ca saṅkappanāya ca patthanāya ca bhūtā sattā ceteti ca saṅkappeti ca. ", "html": "{}"}, "pe8:208.4": {"pli": "Yaṁ gavesanā na ca ceteti na ca saṅkappeti. ", "html": "{}"}, "pe8:208.5": {"pli": "Katame ca sattā bhūtā? ", "html": "{}"}, "pe8:208.6": {"pli": "Ye ca tanujātaaṇḍajāpi aṇḍakā anubhinnā saṁsedajā na ca sambhinnā ime bhūtā. ", "html": "{}"}, "pe8:208.7": {"pli": "Katame sambhavesino gabbhagatā aṇḍagatā saṁsaranto ime na ceteti na pattheti na ca saṅkappeti. ", "html": "{}"}, "pe8:208.8": {"pli": "Anusaye na ca punabbhavo nibbattīti? ", "html": "{}"}, "pe8:208.9": {"pli": "Ye bhūtā sattā ye sambhavesino, te thāvarā. ", "html": "{}"}, "pe8:208.10": {"pli": "Ye vā sato cetenti patthenti ca ye thāvarā. ", "html": "{}"}, "pe8:208.11": {"pli": "Te na ca cetenti, na ca patthenti, na ca saṅkappenti, anusayena ca saṁsaranti. ", "html": "{}</p>"}, "pe8:209.1": {"pli": "Aparo pariyāyo. ", "html": "<p>{}"}, "pe8:209.2": {"pli": "Ye ariyapuggalā sekkhā, tattha te na ca cetenti, na ca saṅkappenti, anusayena puna uppajjanti. ", "html": "{}</p>"}, "pe8:210.1": {"pli": "Aparo pariyāyo. ", "html": "<p>{}"}, "pe8:210.2": {"pli": "Sukhumā pāṇā bhūmigatā udakagatā cakkhuno āpāthaṁ nāgacchanti, te na ca cetenti, na ca saṅkappenti, anusayena ca saṁsaranti. ", "html": "{}</p>"}, "pe8:211.1": {"pli": "Aparo pariyāyo. ", "html": "<p>{}"}, "pe8:211.2": {"pli": "Bāhikā sabbe bhikkhū abhimānikā, te na ca cetenti, na ca patthayanti, anusayena ca saṁsaranti, na ca cetenti, na ca saṅkappenti, na ca anusenti. ", "html": "{}"}, "pe8:211.3": {"pli": "Ārammaṇampetaṁ na hoti viññāṇassa ṭhitiyā. ", "html": "{}</p>"}, "pe8:212.1": {"pli": "Na ca cetetīti pariyuṭṭhānasamugghātaṁ dasseti. ", "html": "<p>{}"}, "pe8:212.2": {"pli": "Na ca anusetīti anusayasamugghātaṁ dasseti. ", "html": "{}"}, "pe8:212.3": {"pli": "Na ca cetetīti oḷārikānaṁ kilesānaṁ pahānaṁ dasseti. ", "html": "{}"}, "pe8:212.4": {"pli": "Na ca anusetīti sukhumānaṁ kilesānaṁ pahānaṁ dasseti. ", "html": "{}"}, "pe8:212.5": {"pli": "Na ca cetetīti yena bhūmi ca na ca patthayantīti sakadāgāmī anāgāmī, na ca anusetīti arahaṁ, na ca cetetīti sīlakkhandhassa paṭipakkhena pahānaṁ dasseti, na ca patthayatīti samādhikkhandhassa paṭipakkhena pahānaṁ dasseti, na ca anusayatīti paññākkhandhassa paṭipakkhena pahānaṁ dasseti, na ca cetetīti apuññamayānaṁ saṅkhārānaṁ pahānaṁ dasseti, na ca patthayatīti puññamayānaṁ saṅkhārānaṁ pahānaṁ dasseti, na ca anusetīti āneñjamayānaṁ saṅkhārānaṁ pahānaṁ dasseti, na ca cetetīti anaññātaññassāmītindriyaṁ, na ca patthayatīti aññindriyaṁ, na ca anusayatīti aññātāvino indriyaṁ. ", "html": "{}"}, "pe8:212.6": {"pli": "Na ca cetetīti mudukā indriyabhāvanā, na ca patthayatīti majjhaindriyabhāvanā, na ca anusetīti adhimattā indriyabhāvanā. ", "html": "{}"}, "pe8:212.7": {"pli": "Ayaṁ suttattho. ", "html": "{}</p>"}, "pe8:213.1": {"pli": "Tattha katamā desanā? ", "html": "<p data-counter='1'>{}"}, "pe8:213.2": {"pli": "Idha sutte cattāri saccāni desitāni. ", "html": "{}</p>"}, "pe8:214.1": {"pli": "Yañca cetayitaṁ yañca pakappitaṁ atthi etaṁ ārammaṇaṁ cittaṁ patiṭṭhati vicinati yujjati. ", "html": "<p data-counter='2–3'>{}"}, "pe8:214.2": {"pli": "Na ca cetetīti na ca patthayatīti atthi evaṁ ārammaṇaṁ anusaye viññāṇamiti viciniyati yujjati na ca ceteti na ca patthayati. ", "html": "{}"}, "pe8:214.3": {"pli": "Anusayappahānā viññāṇaṭṭhitiṁ na gavesanti, viciyantaṁ yujjati. ", "html": "{}"}, "pe8:214.4": {"pli": "Ayaṁ yuttivicayo. ", "html": "{}</p>"}, "pe8:215.1": {"pli": "Tattha katamo padaṭṭhāno? ", "html": "<p data-counter='4'>{}"}, "pe8:215.2": {"pli": "Cetanā pariyuṭṭhānaṁ cetanāpariyuṭṭhānassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:215.3": {"pli": "Saṅkappanaṁ upādānassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:215.4": {"pli": "Anusayo pariyuṭṭhānassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:215.5": {"pli": "Tesaṁ chandarāgavināsāya bhāvanā bhavarāgassa pahānaṁ. ", "html": "{}</p>"}, "pe8:216.1": {"pli": "Tattha katamo lakkhaṇo? ", "html": "<p data-counter='5'>{}"}, "pe8:216.2": {"pli": "Yaṁ cetasikanti vedayitaṁ pakappitaṁ uggahitaṁ viññātaṁ tabbiññāṇaṁ ārammaṇampi paccayopi. ", "html": "{}</p>"}, "pe8:217.1": {"pli": "Tattha katamo catubyūho? ", "html": "<p data-counter='6'>{}"}, "pe8:217.2": {"pli": "Idha sutte bhagavato ko adhippāyo? ", "html": "{}"}, "pe8:217.3": {"pli": "Ye punabbhavaṁ na icchanti, te na cetayissanti na ca patthayissantīti, ayaṁ adhippāyo. ", "html": "{}</p>"}, "pe8:218.1": {"pli": "Āvaṭṭoti yā ca cetanā patthanā ca anusayo ca viññāṇaṭṭhitipahānā ca, imāni dve saccāni. ", "html": "<p data-counter='7'>{}</p>"}, "pe8:219.1": {"pli": "Vibhattīti natthi vibhattiyā bhūmi. ", "html": "<p data-counter='8'>{}</p>"}, "pe8:220.1": {"pli": "Parivattanā pana paṭipakkhaṁ suttaṁ. ", "html": "<p data-counter='9'>{}</p>"}, "pe8:221.1": {"pli": "Tattha katamo vevacano? ", "html": "<p data-counter='10'>{}"}, "pe8:221.2": {"pli": "Cetanā rūpasañcetanā yāva dhammasañcetanā. ", "html": "{}"}, "pe8:221.3": {"pli": "Yo anusayo, te satta anusayā. ", "html": "{}</p>"}, "pe8:222.1": {"pli": "Paññattīti cetanāpariyuṭṭhānaṁ paññattiyā paññattā. ", "html": "<p data-counter='11'>{}"}, "pe8:222.2": {"pli": "Saṅkappanaṁ upādānapaññattiyā paññattaṁ. ", "html": "{}"}, "pe8:222.3": {"pli": "Anusayo hetupaññattiyā paññatto. ", "html": "{}"}, "pe8:222.4": {"pli": "Viññāṇaṭṭhiti upapattihetupaññattiyā paññattā. ", "html": "{}"}, "pe8:222.5": {"pli": "Cetanā saṅkappanā anusayo samucchedo chandarāgavinayapaññattiyā paññatto. ", "html": "{}"}, "pe8:222.6": {"pli": "Paṭhame keci dvīhi parivattakehi paṭiccasamuppādo idappaccayatāya majjhapaññatti. ", "html": "{}</p>"}, "pe8:223.1": {"pli": "Otaraṇoti dvīhi parivattakehi dukkhañca samudayo ca majjhimakehi maggo ca nirodho ca. ", "html": "<p data-counter='12'>{}</p>"}, "pe8:224.1": {"pli": "Sodhanoti sutte suttassa ārambho. ", "html": "<p data-counter='13'>{}</p>"}, "pe8:225.1": {"pli": "Adhiṭṭhānoti yañcetayitaṁ sabbaṁ adhiṭṭhānena ekattāya paññattaṁ. ", "html": "<p data-counter='14'>{}"}, "pe8:225.2": {"pli": "Saṅkappitanti upādānekattāya paññattaṁ. ", "html": "{}"}, "pe8:225.3": {"pli": "Viññāṇaṁ ekattāya paññattaṁ. ", "html": "{}</p>"}, "pe8:226.1": {"pli": "Parikkhāroti subhañca ārammaṇaṁ ayoniso manasikāro cetanā hetupaccayatāya paccayo. ", "html": "<p data-counter='15'>{}"}, "pe8:226.2": {"pli": "Viññāṇassa patiṭṭhāno dhammo ārammaṇapaccayatāya paccayo. ", "html": "{}"}, "pe8:226.3": {"pli": "Tassa manasikāro hetupaccayatāya paccayo. ", "html": "{}</p>"}, "pe8:227.1": {"pli": "Tattha katamo samāropano? ", "html": "<p data-counter='16'>{}"}, "pe8:227.2": {"pli": "Idaṁ suttaṁ saññitaṁ tattha ceteti visajjanā iti niddisitabbā. ", "html": "{}"}, "pe8:227.3": {"pli": "Tassa diṭṭhiyā viññāṇapaccayā nāmarūpaṁ yāva jarāmaraṇaṁ, ayaṁ samāropano. ", "html": "{}</p>"}, "pe8:228.1": {"pli": "Ārammaṇametaṁ na hoti viññāṇassa ṭhitiyā, viññāṇanirodhā nāmarūpanirodho, nāmarūpanirodhā yāva jarāmaraṇanirodho. ", "html": "<p>{}</p>"}, "pe8:229.1": {"pli": "Tattha katamaṁ saṅkilesabhāgiyañca nibbedhabhāgiyañca asekkhabhāgiyañca suttaṁ? ", "html": "<p>{}"}, "pe8:229.2": {"pli": "Ayaṁ loko santāpajāto yāva ye hi keci samaṇā vā brāhmaṇā vā bhavena bhavassa vippamokkhamāhaṁsu. ", "html": "{}"}, "pe8:229.3": {"pli": "Saṅkilesabhāgiyaṁ upadhiṁ hi paṭicca dukkhamidaṁ sambhoti, yā tā pana taṇhā pahīyanti, bhavaṁ nābhinandatīti nibbedhassa nibbutassa bhikkhuno anupādāya punabbhavo na hoti. ", "html": "{}"}, "pe8:229.4": {"pli": "Upaccagā sabbabhavāni tādīti asekkhabhāgiyaṁ. ", "html": "{}</p>"}, "pe8:230.1": {"pli": "Tattha santāpajātoti rāgajo santāpo dosajo mohajoti. ", "html": "<p>{}"}, "pe8:230.2": {"pli": "Tesaṁ sattānaṁ ṭhānaṁ dasseti. ", "html": "{}"}, "pe8:230.3": {"pli": "Loko santāpajātoti phasso tividho sukhavedanīyo dukkhavedanīyo adukkhamasukhavedanīyo. ", "html": "{}"}, "pe8:230.4": {"pli": "Tattha sukhavedanīyo phasso rāgasantāpo, dukkhavedanīyo dosasantāpo, adukkhamasukhavedanīyo mohasantāpo. ", "html": "{}"}, "pe8:230.5": {"pli": "Yathā ca bhagavā āha paṭhamakassa valāhakassa gomagge yehi gahapatiputta rāgajehi dosajehi mohajehi santāpehi dukkhaṁ supati, te mama santāpā na santi. ", "html": "{}</p>"}, "pe8:231.1": {"pli": "Rogaṁ vadati attatoti tehi santāpehi santāpito tividhaṁ vipallāsaṁ paṭilabhati saññāvipallāsaṁ cittavipallāsaṁ diṭṭhivipallāsaṁ. ", "html": "<p>{}"}, "pe8:231.2": {"pli": "Tattha asubhe subhanti saññāvipallāso. ", "html": "{}"}, "pe8:231.3": {"pli": "Dukkhe sukhanti cittavipallāso. ", "html": "{}"}, "pe8:231.4": {"pli": "Anicce niccanti anattani attāti diṭṭhivipallāso. ", "html": "{}</p>"}, "pe8:232.1": {"pli": "Yathā cittassa vipallāso saññādiṭṭhite tividhā vitakkā—", "html": "<p>{}"}, "pe8:232.2": {"pli": "cittavitakko vipallāso saññāvitakko vipallāso diṭṭhivitakko vipallāsopi. ", "html": "{}"}, "pe8:232.3": {"pli": "Tattha avijjā vipallāso gocarā gatipateyyabhūmi, yathā hi taṁ sañjānāti yathā vijānāti yathā sañjānāti ca vijānāti ca. ", "html": "{}"}, "pe8:232.4": {"pli": "Yathā khanti ceteti ime cattāro vipallāsā sattā yehi catubbidhaṁ attabhāvavatthuṁ rogabhūtaṁ gaṇḍabhūtaṁ “attā”ti vadanti. ", "html": "{}"}, "pe8:232.5": {"pli": "Rogaṁ vadati attatoti ayaṁ āvaṭṭo. ", "html": "{}"}, "pe8:232.6": {"pli": "Yena yena hi maññati tato taṁ hoti aññathāti subhanti maññati na tathā hoti. ", "html": "{}"}, "pe8:232.7": {"pli": "Evaṁ sukhanti niccaṁ attāti so aññathā bhavameva santaṁ anāgataṁ bhavaṁ patthayati, tena vuccati “bhavarāgo”ti. ", "html": "{}"}, "pe8:232.8": {"pli": "Bhavamevābhinandati, yaṁ abhinandati, taṁ dukkhanti pañcakkhandhe niddisiyati. ", "html": "{}"}, "pe8:232.9": {"pli": "Yañca tappaccayā sokaparidevadukkhaṁ tassa hi bhāvessati. ", "html": "{}"}, "pe8:232.10": {"pli": "Ettāvatā saṅkileso hoti. ", "html": "{}"}, "pe8:232.11": {"pli": "Pahānatthaṁ kho pana brahmacariyaṁ vussati. ", "html": "{}"}, "pe8:232.12": {"pli": "Tiṇṇaṁ santāpānaṁ chandarāgavinayo hoti. ", "html": "{}</p>"}, "pe8:233.1": {"pli": "Upadhiṁ hi paṭicca dukkhamidaṁ bhavatīti ye bhavamevābhinandanti yassa bhāvessati, taṁ dukkhaṁ tassa dukkhassa pahānamāha. ", "html": "<p>{}"}, "pe8:233.2": {"pli": "Sabbaso upādānañca yaṁ natthi dukkhassa sambhavoti cattāro vipallāsā yathā niddiṭṭhaupādānamāha. ", "html": "{}"}, "pe8:233.3": {"pli": "Tassa paṭhamo vipallāso kāmupādānaṁ, dutiyaṁ diṭṭhupādānaṁ, tatiyaṁ sīlabbatupādānaṁ, catutthaṁ attavādupādānaṁ, tesaṁ yo khayo natthi dukkhassa sambhavo upadhi nidānaṁ dukkhanirodhamāha. ", "html": "{}"}, "pe8:233.4": {"pli": "Evametaṁ yathābhūtaṁ sammappaññāya passato vibhavataṇhā na hoti. ", "html": "{}"}, "pe8:233.5": {"pli": "Vibhavaṁ nābhinandatīti dassanabhūmiṁ manteti sabbaso taṇhakkhayaṁ nibbānanti dve vimuttiyo katheti rāgavirāgañca avijjāvirāgañca. ", "html": "{}"}, "pe8:233.6": {"pli": "Tassa bhikkhunoti anupādisesanibbānadhātuṁ manteti. ", "html": "{}"}, "pe8:233.7": {"pli": "Ayaṁ suttassa atthaniddeso. ", "html": "{}</p>"}, "pe8:234.1": {"pli": "Tattha katamo vicayo? ", "html": "<p data-counter='2–3'>{}"}, "pe8:234.2": {"pli": "Yassa yattha pariḷāheti tassa pariḍayhantassa so yathābhūtaṁ natthi nibbindati ca, ayaṁ vicayo ca yutti ca. ", "html": "{}</p>"}, "pe8:235.1": {"pli": "Padaṭṭhāno rāgajo pariḷāho sukhindriyassa domanassindriyassa ca padaṭṭhānaṁ. ", "html": "<p data-counter='4'>{}"}, "pe8:235.2": {"pli": "Dosajo pariḷāho sukhindriyassa domanassindriyassa ca padaṭṭhānaṁ. ", "html": "{}"}, "pe8:235.3": {"pli": "Mohajo pariḷāho upekkhindriyassa domanassindriyassa ca padaṭṭhānaṁ. ", "html": "{}</p>"}, "pe8:236.1": {"pli": "Tattha katamo lakkhaṇo hāro? ", "html": "<p data-counter='5'>{}"}, "pe8:236.2": {"pli": "Phassapareto vedanāpareto saññāparetopi saṅkhāraparetopi yena yena maññati yadi subhanimittena yadi sukhanimittena yadi niccanimittena yadi attanimittena asubhe subhanti maññati, evaṁ sabbaṁ rāgaje pariḷāhe vutte cattāro pariḷāhā vuttā bhavanti. ", "html": "{}"}, "pe8:236.3": {"pli": "Rāgajo dosajo mohajo diṭṭhijo ca rāgaṁ vadāmīti attato vadati. ", "html": "{}"}, "pe8:236.4": {"pli": "Sabbāni pannarasa padāni aniccaṁ dukkhanti. ", "html": "{}</p>"}, "pe8:237.1": {"pli": "Tattha katamo catubyūho? ", "html": "<p data-counter='6'>{}"}, "pe8:237.2": {"pli": "Idha sutte bhagavato ko adhippāyo? ", "html": "{}"}, "pe8:237.3": {"pli": "Ye pariḷāhena na acchanti te bhavaṁ nābhinandanti. ", "html": "{}"}, "pe8:237.4": {"pli": "Ye bhavaṁ nābhinandanti, te parinibbāyissanti. ", "html": "{}"}, "pe8:237.5": {"pli": "Ayaṁ adhippāyo. ", "html": "{}</p>"}, "pe8:238.1": {"pli": "Tattha katamo āvaṭṭo? ", "html": "<p data-counter='7'>{}"}, "pe8:238.2": {"pli": "Saṅkilesabhāgiyena dukkhañca samudayañca niddisati. ", "html": "{}"}, "pe8:238.3": {"pli": "Nibbedhabhāgiyena maggañca nirodhañca. ", "html": "{}</p>"}, "pe8:239.1": {"pli": "Tattha katamā vibhatti? ", "html": "<p data-counter='8'>{}"}, "pe8:239.2": {"pli": "Santāpajāto rogajāto rogaṁ vadati attato taṁ na ekaṁsena hoti amanasikārā santāpajāto kho na ca rogaṁ attato vadati. ", "html": "{}</p>"}, "pe8:240.1": {"pli": "Tattha katamo parivattano? ", "html": "<p data-counter='9'>{}"}, "pe8:240.2": {"pli": "Pakkhapaṭipakkhanidassanatthaṁ bhūmi parivattanāya. ", "html": "{}</p>"}, "pe8:241.1": {"pli": "Tattha katamo vevacano hāro? ", "html": "<p data-counter='10'>{}"}, "pe8:241.2": {"pli": "Rogañca attato vadati sallaṁ attato vadati. ", "html": "{}"}, "pe8:241.3": {"pli": "Pannarasa padāni sabbāni vattabbāni. ", "html": "{}</p>"}, "pe8:242.1": {"pli": "Tattha katamā paññatti? ", "html": "<p data-counter='11'>{}"}, "pe8:242.2": {"pli": "Santāpajātoti domanassapadaṭṭhānaṁ. ", "html": "{}"}, "pe8:242.3": {"pli": "Sabbe vacanapaññattiyā paññapeti. ", "html": "{}"}, "pe8:242.4": {"pli": "Rogaṁ vadati attato vipallāso saṅkilesapaññattiyā paññapeti. ", "html": "{}"}, "pe8:242.5": {"pli": "Yaṁ nābhinandati, taṁ dukkhanti vipallāsanikkhepapaññattiyā paññattā. ", "html": "{}"}, "pe8:242.6": {"pli": "Te akatasattā lokā majjhena vemattatāya paññattā. ", "html": "{}</p>"}, "pe8:243.1": {"pli": "Tattha katamo otaraṇo? ", "html": "<p data-counter='12'>{}"}, "pe8:243.2": {"pli": "Santāpajātoti tīṇi akusalamūlāni, te saṅkhārā saṅkhārakkhandhapariyāpannā, dhātūsu dhammadhātu, āyatanesu dhammāyatanaṁ. ", "html": "{}"}, "pe8:243.3": {"pli": "Indriyesu itthindriyaṁ purisindriyañca padaṭṭhānaṁ. ", "html": "{}</p>"}, "pe8:244.1": {"pli": "Tattha katamo sodhano? ", "html": "<p data-counter='13'>{}"}, "pe8:244.2": {"pli": "Suddho suttassa ārambho. ", "html": "{}</p>"}, "pe8:245.1": {"pli": "Tattha katamo adhiṭṭhāno hāro? ", "html": "<p data-counter='14'>{}"}, "pe8:245.2": {"pli": "Pariḷāhoti ye sattā lokā ekattapaññattiyā paññattā, te akatasattā lokā majjhena vemattatāya paññattā. ", "html": "{}</p>"}, "pe8:246.1": {"pli": "Tattha katamo parikkhāro? ", "html": "<p data-counter='15'>{}"}, "pe8:246.2": {"pli": "Santāpajātoti ayoniso manasikāro hetu, vipallāsañca paccayo. ", "html": "{}"}, "pe8:246.3": {"pli": "Tattha dvīhi dhammehi attā abhiniviṭṭhā cittañca cetasikañca dhamme ubhayāni tassa viparītena parāmasato. ", "html": "{}"}, "pe8:246.4": {"pli": "Aparo pariyāyo, cetasikehi dhammehi attasaññā anattasaññā samugghāteti. ", "html": "{}"}, "pe8:246.5": {"pli": "Aparo pariyāyo. ", "html": "{}"}, "pe8:246.6": {"pli": "Aniccasaññā cetasikesu dhammesu, na tu attasaññā. ", "html": "{}"}, "pe8:246.7": {"pli": "Idaṁ vuccati cittanti vā manoti vā viññāṇanti vā idaṁ dīgharattaṁ abbhuggataṁ etaṁ mama, esohamasmi, eso me attāti. ", "html": "{}"}, "pe8:246.8": {"pli": "Tattha cetasikā dhammānupassanā esāpi dhammasaññā. ", "html": "{}"}, "pe8:246.9": {"pli": "Tassa ko hetu, ko paccayo? ", "html": "{}"}, "pe8:246.10": {"pli": "Ahaṅkāro hetu, mamaṅkāro paccayo. ", "html": "{}</p>"}, "pe8:247.1": {"pli": "Tattha katamo samāropano? ", "html": "<p>{}"}, "pe8:247.2": {"pli": "Ayaṁ loko santāpajātoti akusalaṁ manteti viññāṇaṁ nāmarūpassa paccayo yāva jarāmaraṇanti, ayaṁ samāropano. ", "html": "{}</p>"}, "pe8:248.1": {"pli": "Evametaṁ yathābhūtaṁ, sammappaññāya passati akusalamūlānaṁ pahānaṁ. ", "html": "<p data-counter='16'>{}"}, "pe8:248.2": {"pli": "Tattha avijjānirodho avijjānirodhā yāva jarāmaraṇanirodho, ayaṁ samāropano. ", "html": "{}</p>"}, "pe8:249.1": {"pli": "Cattāro puggalā—", "html": "<p>{}"}, "pe8:249.2": {"pli": "anusotagāmī paṭisotagāmī ṭhitatto, tiṇṇo pāraṅgato thale tiṭṭhati brāhmaṇoti. ", "html": "{}</p>"}, "pe8:250.1": {"pli": "Tattha yo anusotagāmī ayaṁ kāme sevati. ", "html": "<p>{}"}, "pe8:250.2": {"pli": "Pāpañca kammaṁ karoti yāva kāme paṭisevati. ", "html": "{}"}, "pe8:250.3": {"pli": "Idaṁ lobho akusalamūlaṁ, soyeva taṇhā, so tehi kāmehi vuyhati anusotagāmīti vuccati. ", "html": "{}"}, "pe8:250.4": {"pli": "Yo puggalo tāhi gamito tappaccayā tassa hetu akusalakammaṁ karoti kāyena ca vācāya ca, ayaṁ vuccati pāpakammaṁ karotīti. ", "html": "{}"}, "pe8:250.5": {"pli": "Tassa tīṇi sotāni sakkāyadiṭṭhi vicikicchā sīlabbataparāmāso. ", "html": "{}"}, "pe8:250.6": {"pli": "Imehi tīhi sotehi tividhadhātuyaṁ uppajjati kāmadhātuyaṁ rūpadhātuyaṁ arūpadhātuyaṁ. ", "html": "{}"}, "pe8:250.7": {"pli": "Tena paṭipakkhena yo kāme na paṭisevati. ", "html": "{}"}, "pe8:250.8": {"pli": "Yo sīlavataṁ na parāmasati. ", "html": "{}"}, "pe8:250.9": {"pli": "Yo sakkāyadiṭṭhīnaṁ pahānāya kāmesu yathābhūtaṁ ādīnavaṁ passati. ", "html": "{}"}, "pe8:250.10": {"pli": "Yena ca te dhamme paṭisevati. ", "html": "{}"}, "pe8:250.11": {"pli": "Yañca tappaccayā tiṭṭhati brāhmaṇoti arahaṁ kira. ", "html": "{}"}, "pe8:250.12": {"pli": "Tattha arahaṁ tassa pāraṅgato hoti, pāraṅgatassa thale tiṭṭhati sopādisesā nibbānadhātu. ", "html": "{}"}, "pe8:250.13": {"pli": "Anusotagāminīti dassanappahātabbānaṁ saṁyojanānaṁ appahānamāha. ", "html": "{}"}, "pe8:250.14": {"pli": "Paṭisotagāminīti phale diṭṭhekaṭṭhānañca kilesānaṁ pahānamāha, ṭhitattena pañcannaṁ orambhāgiyānaṁ saṁyojanānaṁ pahānamāha. ", "html": "{}"}, "pe8:250.15": {"pli": "Tattha anusotagāminā maggarūpimāha. ", "html": "{}"}, "pe8:250.16": {"pli": "Paṭisotagāminā ṭhitattena ca maggamitimāha. ", "html": "{}"}, "pe8:250.17": {"pli": "Pāraṅgatena sāvakā asekkhā ca sammāsambuddhā ca vuttā. ", "html": "{}"}, "pe8:250.18": {"pli": "Anusotagāminā sakkāyasamudayagāminiṁ paṭipadamāha. ", "html": "{}"}, "pe8:250.19": {"pli": "Paṭisotagāminā ṭhitattena sakkāyanirodhagāminiṁ paṭipadamāha. ", "html": "{}"}, "pe8:250.20": {"pli": "Pāraṅgatena dasa asekkhā arahantā dhammā vuttā. ", "html": "{}"}, "pe8:250.21": {"pli": "Ayaṁ suttattho. ", "html": "{}</p>"}, "pe8:251.1": {"pli": "Tattha katamā desanā? ", "html": "<p data-counter='1'>{}"}, "pe8:251.2": {"pli": "Imasmiṁ hi sutte cattāri ariyasaccāni desitāni. ", "html": "{}"}, "pe8:251.3": {"pli": "Tedhātukalokasamatikkamanañca. ", "html": "{}</p>"}, "pe8:252.1": {"pli": "Tattha katamo vicayo hāro? ", "html": "<p data-counter='2'>{}"}, "pe8:252.2": {"pli": "Yo kāme paṭisevati pāpaṁ kareyyāti yo ca kāme na paṭisevati so pāpakammaṁ na kareyyāti yo ca imehi dvīhi bhūmīhi uttiṇṇo pāraṅgatoti yā vīmaṁsā ayaṁ vicayo. ", "html": "{}</p>"}, "pe8:253.1": {"pli": "Yuttīti yujjati suttesu, nāyujjatīti yā vīmaṁsāya, ayaṁ yutti. ", "html": "<p data-counter='3'>{}</p>"}, "pe8:254.1": {"pli": "Padaṭṭhānoti anusotagāminā sattannaṁ saṁyojanānaṁ padaṭṭhānaṁ. ", "html": "<p data-counter='4'>{}"}, "pe8:254.2": {"pli": "Akusalassa kiriyā akusalassa mūlānaṁ padaṭṭhānaṁ. ", "html": "{}"}, "pe8:254.3": {"pli": "Paṭisotagāminā yathābhūtadassanassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:254.4": {"pli": "Ṭhitattena asaṁhāriyāya padaṭṭhānaṁ. ", "html": "{}"}, "pe8:254.5": {"pli": "Pāraṅgatoti kadāci bhūmiyā padaṭṭhānaṁ. ", "html": "{}</p>"}, "pe8:255.1": {"pli": "Tattha katamo lakkhaṇo hāro? ", "html": "<p data-counter='5'>{}"}, "pe8:255.2": {"pli": "Yo anusotaṁ gacchati taṇhāvasena. ", "html": "{}"}, "pe8:255.3": {"pli": "Sabbesampi kilesānaṁ vasena gacchati. ", "html": "{}"}, "pe8:255.4": {"pli": "Yo paṭisotaṁ vāyamati. ", "html": "{}"}, "pe8:255.5": {"pli": "Taṇhāya sabbesampi so kilesānaṁ vāyamati paṭisotaṁ. ", "html": "{}"}, "pe8:255.6": {"pli": "Yo attanā ṭhito kāyenapi so ṭhito vācācittenapi so ṭhito. ", "html": "{}"}, "pe8:255.7": {"pli": "Ayaṁ lakkhaṇo hāro. ", "html": "{}</p>"}, "pe8:256.1": {"pli": "Tattha katamo catubyūho? ", "html": "<p data-counter='6'>{}"}, "pe8:256.2": {"pli": "Idha sutte bhagavato ko adhippāyo? ", "html": "{}"}, "pe8:256.3": {"pli": "Ye anusotagāminiyā paṭipadāya nābhiramissanti, te paṭisotaṁ vāyamissantīti yāva kadāci bhūmiyaṁ, ayaṁ adhippāyo. ", "html": "{}</p>"}, "pe8:257.1": {"pli": "Āvaṭṭoti idha sutte cattāri suttāni desitāni. ", "html": "<p data-counter='7'>{}</p>"}, "pe8:258.1": {"pli": "Tattha katamo vibhatti hāro? ", "html": "<p data-counter='8'>{}"}, "pe8:258.2": {"pli": "Yo kāme paṭisevati pāpañca kammaṁ karoti. ", "html": "{}"}, "pe8:258.3": {"pli": "So anusotagāmīti na ekaṁsena sotāpannopi kāme paṭisevati. ", "html": "{}"}, "pe8:258.4": {"pli": "Taṁ bhāgiyañca pāpakammaṁ karoti. ", "html": "{}"}, "pe8:258.5": {"pli": "Kiñcāpi sekkhopi kareyya pāpaṁ yathā sutte niddiṭṭho na ca so anusotagāmī, idaṁ vibhajjabyākaraṇīyaṁ. ", "html": "{}"}, "pe8:258.6": {"pli": "Na ca kāme paṭisevati na ca pāpakammaṁ karoti paṭisotagāmī na ca ekaṁsena sabbe bāhirako kāmesu vītarāgo na ca kāme paṭisevati, tena ca pāpakammaṁ karoti anusotagāmī paṭisotagāmī, ayaṁ vibhatti. ", "html": "{}</p>"}, "pe8:259.1": {"pli": "Tattha katamo parivattano hāro? ", "html": "<p data-counter='9'>{}"}, "pe8:259.2": {"pli": "Niddiṭṭho paṭipakkho. ", "html": "{}</p>"}, "pe8:260.1": {"pli": "Vevacanoti kāmesu vatthukāmāpi kilesakāmāpi rūpasaddagandharasaphassaputtadāradāsakammakaraporisañca pariggahā. ", "html": "<p data-counter='10'>{}</p>"}, "pe8:261.1": {"pli": "Paññattīti sabbe puthujjanā ekattāya paññattā. ", "html": "<p data-counter='11'>{}"}, "pe8:261.2": {"pli": "Anusotagāmīti kilesasamudācārapaññattiyā paññattā. ", "html": "{}"}, "pe8:261.3": {"pli": "Ye pana sekkhā puggalā, te nibbānapaññattiyā paññattā. ", "html": "{}"}, "pe8:261.4": {"pli": "Ye pana anāgāmī, te asaṁhāriya paññattiyā paññattā, ayaṁ paññatti. ", "html": "{}</p>"}, "pe8:262.1": {"pli": "Otaraṇoti yo anusotagāmī, so dukkhaṁ. ", "html": "<p data-counter='12'>{}"}, "pe8:262.2": {"pli": "Ye tassa dhammā, te dukkhassa samudayo. ", "html": "{}"}, "pe8:262.3": {"pli": "Yaṁ rūpaṁ, ayaṁ rūpakkhandho, evaṁ pañcapi khandhā paṭiccasamuppādo, te kilesā saṅkhārakkhandhapariyāpannā dhammāyatanaṁ dhammadhātu indriyesu ca paññattā. ", "html": "{}</p>"}, "pe8:263.1": {"pli": "Sodhanoti yenārambhena idaṁ suttaṁ desitaṁ, so ārambho sabbo suddho. ", "html": "<p data-counter='13'>{}</p>"}, "pe8:264.1": {"pli": "Adhiṭṭhānoti paṭisotagāminā sabbe sotāpannā ekattena vā niddiṭṭhā rāgānusayapaṭisotagāmino sekkhāva maggo ca sekkho ca puggalo ṭhitattoti. ", "html": "<p>{}</p>"}, "pe8:265.1": {"pli": "Vītarāgo ekattāya paññatto. ", "html": "<p data-counter='14'>{}"}, "pe8:265.2": {"pli": "Pāraṅgatoti sabbe arahanto sabbe paccekabuddhā sammāsambuddhā ca ekattāya paññattā. ", "html": "{}</p>"}, "pe8:266.1": {"pli": "Parikkhāroti anusotagāmino pāpamittapaccayo kāmapariyuṭṭhānaṁ hetu. ", "html": "<p data-counter='15'>{}"}, "pe8:266.2": {"pli": "Paṭisotagāmino dve hetū dve paccayā ca yāva sammādiṭṭhiyā uppādāyadiṭṭhi, tassa paṭiladdhamaggo hetu ārambho paccayo kāyiko cetasikassa koṭṭhāso ca. ", "html": "{}</p>"}, "pe8:267.1": {"pli": "Samāropanoti vibhatti idaṁ suttaṁ natthi samāropanāya bhūmi. ", "html": "<p data-counter='16'>{}</p>"}, "pe8:268.1": {"pli": "Pañcānisaṁsā sotānugatānaṁ dhammānaṁ yāva diṭṭhiyā suppaṭividdhānaṁ suttaṁ vitthārena kātabbaṁ. ", "html": "<p>{}"}, "pe8:268.2": {"pli": "Yuñjato ghaṭentassa vāyamato gilāno maraṇakāle devabhūto paccekabodhiṁ pāpuṇāti. ", "html": "{}"}, "pe8:268.3": {"pli": "Sotānugatāti saddhammassavanena kataṁ hoti. ", "html": "{}"}, "pe8:268.4": {"pli": "Na ca adhipaññādhammavipassanāya tassa cittaṁ tasitaṁ hoti, na ca anibbiddhattaṁ, idaṁ ca suttaṁ pañcannaṁ puggalānaṁ desitaṁ, saddhānusārino mudindriyassa tikkhindriyassa ca dhammānusārino tikkhindriyassa mudindriyassa ca. ", "html": "{}"}, "pe8:268.5": {"pli": "Yo pana mohacarito puggalo na sakkoti yuñjituṁ ghaṭituṁ vāyamituṁ yathābhūtaṁ yathāsamādhikā vimutti taṁ khaṇaṁ taṁ layaṁ taṁ muhuttaṁ phalaṁ dasseti. ", "html": "{}"}, "pe8:268.6": {"pli": "Sādhu parihāyati paro taṁ duyhati, no tu sukhaavipākinī bhavati. ", "html": "{}"}, "pe8:268.7": {"pli": "Tassa diṭṭheyeva ca dhamme upapajjaaparāpariyavedanīyaṁ. ", "html": "{}"}, "pe8:268.8": {"pli": "Tattha yo puggalo dhammānusārī tassa yadi sotānugatā dhammā honti so yuñjanto pāpuṇāti. ", "html": "{}"}, "pe8:268.9": {"pli": "Yo dhammānusārī mudindriyo, so gilāno pāpuṇāti. ", "html": "{}"}, "pe8:268.10": {"pli": "Yo saddhānusārī tikkhindriyo, so maraṇakālasamaye pāpuṇāti. ", "html": "{}"}, "pe8:268.11": {"pli": "Yo mudindriyo, so devabhūto pāpuṇāti. ", "html": "{}"}, "pe8:268.12": {"pli": "Yadā devabhūto na pāpuṇāti, na so teneva dhammarāgena tāya dhammanandiyā paccekabodhiṁ pāpuṇāti. ", "html": "{}"}, "pe8:268.13": {"pli": "Yo sotānugatesu yuñjati ghaṭeti vāyamati, so pubbāpannena visesaṁ sañjānāti, sañjānanto pāpuṇāti. ", "html": "{}"}, "pe8:268.14": {"pli": "Sace pana gilānassa manasikāro hoti, tattha yuñjanto pāpuṇāti. ", "html": "{}"}, "pe8:268.15": {"pli": "Sace panassa maraṇakāle saṁviggo hoti, tattha yuñjanto pāpuṇāti. ", "html": "{}"}, "pe8:268.16": {"pli": "Sace pana na katthaci saṁvego hoti, tassa devabhūtassa sukhino dhammabhūtā pādā evaṁ avilapati. ", "html": "{}"}, "pe8:268.17": {"pli": "So evaṁ jānāti “ayaṁ so dhammavinayo yattha mayaṁ pubbe manussabhūtā brahmacariyaṁ carimhā”ti. ", "html": "{}"}, "pe8:268.18": {"pli": "Atha devabhūto pāpuṇāti. ", "html": "{}"}, "pe8:268.19": {"pli": "Dibbesu vā pañcasu kāmaguṇesu ajjhosito hoti pamādavihārī, so tena kusalamūlena paccekabodhiṁ pāpuṇāti. ", "html": "{}</p>"}, "pe8:269.1": {"pli": "Yā paratoghosena vacasā suparicitā, ayaṁ sutamayī paññā. ", "html": "<p>{}"}, "pe8:269.2": {"pli": "Ye pana dhammā honti manasā anupekkhitā, ayaṁ cintāmayī paññā. ", "html": "{}"}, "pe8:269.3": {"pli": "Yaṁ diṭṭhiyā suppaṭividdhā, ayaṁ bhāvanāmayī paññā. ", "html": "{}"}, "pe8:269.4": {"pli": "Yaṁ sotānugatā vacasā paricitā honti, so ca diṭṭheyeva dhamme parinibbāyī, ayaṁ arahaṁ puggalo. ", "html": "{}"}, "pe8:269.5": {"pli": "Yo upapajjati devabhūto pāpuṇāti, tattha ca parinibbāyati, ayaṁ anāgāmī. ", "html": "{}"}, "pe8:269.6": {"pli": "Yo tena kusalamūlena paccekabodhiṁ pāpuṇāti, ayaṁ pubbayogasambhārasambhūto puggalo. ", "html": "{}</p>"}, "pe8:270.1": {"pli": "Sotānugatā dhammāti paṭhamaṁ vimuttāyatanaṁ, vacasā paricitāti dutiyaṁ tatiyañca vimuttāyatanaṁ, manasā anupekkhitāti catutthaṁ vimuttāyatanaṁ diṭṭhiyā suppaṭividdhāti pañcamaṁ vimuttāyatanaṁ. ", "html": "<p>{}</p>"}, "pe8:271.1": {"pli": "Sotānugatāya vimuttiyā vacasā yā vācā suppaṭividdhā anupubbadhammassa sotena sutvā sīlakkhandhe paripūreti, manasā anupekkhitā samādhikkhandhaṁ paripūreti, diṭṭhiyā suppaṭividdhā paññākkhandhaṁ paripūreti. ", "html": "<p>{}</p>"}, "pe8:272.1": {"pli": "Sotānugatā dhammā bahussutā hontīti vitthārena kātabbaṁ. ", "html": "<p>{}"}, "pe8:272.2": {"pli": "Idaṁ paṭhamaṁ saddhāpadānaṁ manasā anupekkhitāti paṭisallānabahulo viharati, vitthārena kātabbaṁ. ", "html": "{}"}, "pe8:272.3": {"pli": "Idaṁ dutiyaṁ saddhāpadānaṁ diṭṭhiyā suppaṭividdhāti anāsavā cetovimuttiyā nāparaṁ itthattāyāti pajānātīti. ", "html": "{}"}, "pe8:272.4": {"pli": "Idaṁ tatiyaṁ saddhāpadānaṁ. ", "html": "{}</p>"}, "pe8:273.1": {"pli": "Sotānugatā dhammāti sekkhaṁ satthā dasseti. ", "html": "<p>{}"}, "pe8:273.2": {"pli": "Manasā anupekkhitāti arahattaṁ satthā dasseti. ", "html": "{}"}, "pe8:273.3": {"pli": "Diṭṭhiyā suppaṭividdhāti tathāgataṁ arahantaṁ sammāsambuddhaṁ satthā dasseti. ", "html": "{}</p>"}, "pe8:274.1": {"pli": "Sotānugatā dhammāti kāmānaṁ nissaraṇaṁ dasseti. ", "html": "<p>{}"}, "pe8:274.2": {"pli": "Manasā anupekkhitāti rūpadhātuyā nissaraṇaṁ dasseti. ", "html": "{}"}, "pe8:274.3": {"pli": "Diṭṭhiyā suppaṭividdhāti tedhātukānaṁ nissaraṇaṁ dasseti. ", "html": "{}"}, "pe8:274.4": {"pli": "Ayaṁ suttattho. ", "html": "{}</p>"}, "pe8:275.1": {"pli": "Tattha katamo desanāhāro? ", "html": "<p>{}"}, "pe8:275.2": {"pli": "Imamhi sutte tayo esanā desitā sotānugatehi dhammehi vacasā paricitehi kāmesanāya samathamaggo. ", "html": "{}"}, "pe8:275.3": {"pli": "Diṭṭhiyā suppaṭividdhehi brahmacariyesanāya samathamaggo. ", "html": "{}</p>"}, "pe8:276.1": {"pli": "Vicayoti yathā suttaṁ manasikaronto vicinanto sutamayipaññaṁ paṭilabhati. ", "html": "<p>{}"}, "pe8:276.2": {"pli": "Yathā ca so manasikarotīti yathā sutadhammā tadā cintāmayipaññaṁ paṭilabhati. ", "html": "{}"}, "pe8:276.3": {"pli": "Yathā diṭṭheva dhamme manasikaroti tadā bhāvanāmayipaññaṁ paṭilabhati. ", "html": "{}"}, "pe8:276.4": {"pli": "Ayaṁ vicayo. ", "html": "{}</p>"}, "pe8:277.1": {"pli": "Sutena sutamayipaññaṁ paṭilabhati. ", "html": "<p>{}"}, "pe8:277.2": {"pli": "Cintāya cintāmayipaññaṁ bhāvanāya bhāvanāmayipaññaṁ paṭilabhati. ", "html": "{}"}, "pe8:277.3": {"pli": "Atthi esā yutti. ", "html": "{}</p>"}, "pe8:278.1": {"pli": "Padaṭṭhānoti sotānugatā dhammāti dhammassavanassa padaṭṭhānaṁ. ", "html": "<p>{}"}, "pe8:278.2": {"pli": "Vacasā paricitāti yuñjanāya padaṭṭhānaṁ. ", "html": "{}"}, "pe8:278.3": {"pli": "Manasā anupekkhitāti dhammānudhammāya vipassanāya padaṭṭhānaṁ. ", "html": "{}"}, "pe8:278.4": {"pli": "Diṭṭhiyā anupekkhitāti paññāyapi anupekkhitā diṭṭhiyāpi anupekkhitā. ", "html": "{}</p>"}, "pe8:279.1": {"pli": "Catubyūhoti imamhi sutte bhagavato ko adhippāyo? ", "html": "<p>{}"}, "pe8:279.2": {"pli": "Ye imāhi dvīhi paññāhi samannāgatā tehi …. ", "html": "{}</p>"}, "pe8:280.1": {"pli": "Sa nibbutoti maggaphalaṁ anupādisesañca nibbānadhātuṁ manteti, dānena oḷārikānaṁ kilesānaṁ pahānaṁ manteti. ", "html": "<p>{}"}, "pe8:280.2": {"pli": "Sīlena majjhimānaṁ, paññāya sukhumakilesānaṁ manteti, rāgadosamohakkhayā sa nibbutoti katā ca bhūmi. ", "html": "{}</p>"}, "pe8:281.1": {"pli": "“Dadato puññaṁ pavaḍḍhati, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "pe8:281.2": {"pli": "Saṁyamato veraṁ na cīyati”. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe8:282.1": {"pli": "Kusalo ca jahāti pāpakanti maggo vutto. ", "html": "<p>{}"}, "pe8:282.2": {"pli": "Rāgadosamohakkhayā sa nibbutoti maggaphalamāha. ", "html": "{}</p>"}, "pe8:283.1": {"pli": "Dadato puññaṁ pavaḍḍhati, saṁyamatoti tīhi padehi lokikaṁ kusalamūlaṁ vuttaṁ. ", "html": "<p>{}"}, "pe8:283.2": {"pli": "Rāgadosamohakkhayā sa nibbutoti lokuttaraṁ kusalamūlaṁ vuttaṁ. ", "html": "{}</p>"}, "pe8:284.1": {"pli": "Dadato puññaṁ pavaḍḍhati, saṁyamato veraṁ na cīyatīti puthujjanabhūmiṁ manteti. ", "html": "<p>{}"}, "pe8:284.2": {"pli": "Kusalo ca jahāti pāpakanti sekkhabhūmiṁ manteti. ", "html": "{}"}, "pe8:284.3": {"pli": "Rāgadosamohakkhayā sa nibbutoti asekkhabhūmi vuttā. ", "html": "{}</p>"}, "pe8:285.1": {"pli": "Dadato puññaṁ pavaḍḍhati, saṁyamato veraṁ na cīyatīti magganiyā paṭipadā vuttā. ", "html": "<p>{}"}, "pe8:285.2": {"pli": "Kusalo ca jahāti pāpakanti sekkhavimutti. ", "html": "{}"}, "pe8:285.3": {"pli": "Rāgadosamohakkhayā sa nibbutoti asekkhavimutti. ", "html": "{}</p>"}, "pe8:286.1": {"pli": "Dadato puññaṁ pavaḍḍhati, saṁyamato veraṁ na cīyatīti dānakathaṁ sīlakathaṁ maggakathaṁ lokikānaṁ dhammānaṁ desanamāha. ", "html": "<p>{}"}, "pe8:286.2": {"pli": "Kusalo ca jahāti pāpakanti loke ādīnavānupassanā. ", "html": "{}"}, "pe8:286.3": {"pli": "Rāgadosamohakkhayā sa nibbutoti sāmukkaṁsikāya dhammadesanāyapi paṭividdhā. ", "html": "{}</p>"}, "pe8:287.1": {"pli": "Dadato puññaṁ pavaḍḍhatīti pāṇānaṁ abhayadānena pāṇātipātā veramaṇisattānaṁ abhayaṁ deti. ", "html": "<p>{}"}, "pe8:287.2": {"pli": "Evaṁ sabbāni sikkhāpadāni kātabbāni. ", "html": "{}"}, "pe8:287.3": {"pli": "Saṁyamato veraṁ na cīyatīti sīle patiṭṭhāya cittaṁ saṁyameti, tassa saṁyamato pāripūriṁ gacchati. ", "html": "{}"}, "pe8:287.4": {"pli": "Rāgadosamohakkhayā sa nibbutoti dve vimuttiyo. ", "html": "{}"}, "pe8:287.5": {"pli": "Ayaṁ suttaniddeso. ", "html": "{}</p>"}, "pe8:288.1": {"pli": "Tattha katamā desanā? ", "html": "<p data-counter='1'>{}"}, "pe8:288.2": {"pli": "Imamhi sutte kiṁ desitaṁ? ", "html": "{}"}, "pe8:288.3": {"pli": "Dve sugatiyo devā ca manussā ca, dibbā ca pañcakāmaguṇā, mānussakā ca. ", "html": "{}"}, "pe8:288.4": {"pli": "Dvīhi padehi niddeso. ", "html": "{}"}, "pe8:288.5": {"pli": "Dadato puññaṁ pavaḍḍhati, saṁyamato veraṁ na cīyati, kusalo ca jahāti pāpakanti maggo vutto. ", "html": "{}"}, "pe8:288.6": {"pli": "Rāgadosamohakkhayā sa nibbutoti dve nibbānadhātuyo desitā sopādisesā ca anupādisesā ca. ", "html": "{}"}, "pe8:288.7": {"pli": "Ayaṁ desanā. ", "html": "{}</p>"}, "pe8:289.1": {"pli": "Vicayoti dadato puññaṁ pavaḍḍhatīti iminā paṭhamena padena dānamayikapuññakiriyavatthu vuttaṁ. ", "html": "<p data-counter='2–6'>{}"}, "pe8:289.2": {"pli": "Tenassa ānantariyānaṁ kusalānaṁ dhammānaṁ. ", "html": "{}"}, "pe8:289.3": {"pli": "Dutiyena padena … yanti, niyyānikaṁ sāsananti, ayaṁ adhippāyo. ", "html": "{}</p>"}, "pe8:290.1": {"pli": "Assavanena ca amanasikārena ca appaṭivedhena ca sakkāyasamudayagāminī paṭipadā vuttā. ", "html": "<p data-counter='7'>{}"}, "pe8:290.2": {"pli": "Savanena ca manasikārena ca paṭivedhena ca sakkāyanirodhagāminī paṭipadā vuttā. ", "html": "{}"}, "pe8:290.3": {"pli": "Ayaṁ āvaṭṭo. ", "html": "{}</p>"}, "pe8:291.1": {"pli": "Vibhattīti ekaṁsabyākaraṇīyo. ", "html": "<p data-counter='8'>{}"}, "pe8:291.2": {"pli": "Natthi tattha vibhattiyā bhūmi. ", "html": "{}"}, "pe8:291.3": {"pli": "Parivattanāti ye pañcānisaṁsā, te pañcādinā paṭipakkhena teneva diṭṭheva dhamme pāpuṇāti, taṁ upapajjamānā aparo pariyāyo. ", "html": "{}</p>"}, "pe8:292.1": {"pli": "Vevacananti sotānugatā dhammāti yaṁ suttaṁ diṭṭhampi paññindriyaṁ viññattampi diṭṭhiyā suppaṭividdhampi vibhāvitampi. ", "html": "<p data-counter='9'>{}</p>"}, "pe8:293.1": {"pli": "Paññattīti sotānugatādhammāti desanā avijjāpaññattiyā paññattaṁ. ", "html": "<p data-counter='11'>{}"}, "pe8:293.2": {"pli": "Manasikāro pāmojjapaññattiyā paññatto, diṭṭhadhammāpi ānisaṁsapaññattiyā paññattā. ", "html": "{}</p>"}, "pe8:294.1": {"pli": "Otaraṇoti tisso paññā vacasā paricitesu sutamayī paññā manasā anupekkhitesu cintāmayī paññā diṭṭhiyā suppaṭividdhāsu bhāvanāmayī paññā. ", "html": "<p data-counter='12'>{}"}, "pe8:294.2": {"pli": "Imāni ariyasaccāni indriyāni vijjuppādā avijjānirodho paṭiccasamuppādo indriyesu tīṇi indriyāni, āyatanesu dhammāyatanapariyāpannā dhātūsu dhammadhātupariyāpannāti. ", "html": "{}</p>"}, "pe8:295.1": {"pli": "Sodhanoti yo ārambho suttassa paveso niyutto. ", "html": "<p data-counter='13'>{}</p>"}, "pe8:296.1": {"pli": "Adhiṭṭhānoti pañcānisaṁsāti vemattatāya paññattā ānisaṁsā sotā anugatāti vemattatāya ariyavohāro paññatto, dhamme ca savananti ekattatāya paññattaṁ. ", "html": "<p data-counter='14'>{}</p>"}, "pe8:297.1": {"pli": "Parikkhāroti dhammassavanassa payirupāsanā paccayo, saddhā hetu. ", "html": "<p data-counter='15'>{}"}, "pe8:297.2": {"pli": "Manasā anupekkhitāti atthappaṭisaṁveditā paccayo, dhammappaṭisaṁveditā hetu, diṭṭhiyā suppaṭividdhāti saddhammassavanañca manasikāro ca paccayo, sutamayī cintāmayī paññā hetu. ", "html": "{}</p>"}, "pe8:298.1": {"pli": "Samāropanoti vibhattaṁ suttaṁ aparo pariyāyo nibbatti bale natthi. ", "html": "<p data-counter='16'>{}"}, "pe8:298.2": {"pli": "Tattha samāropanāya bhūmi. ", "html": "{}</p>"}, "pe8:299.1": {"pli": "Tattha katamaṁ vāsanābhāgiyañca nibbedhabhāgiyañca suttaṁ? ", "html": "<p data-counter='1–2'>{}"}, "pe8:299.2": {"pli": "Dadato puññaṁ pavaḍḍhatīti gāthā. ", "html": "{}"}, "pe8:299.3": {"pli": "Dadatoti dānamayikapuññakiriyavatthu vuttaṁ. ", "html": "{}"}, "pe8:299.4": {"pli": "Saṁyamato veraṁ na cīyatīti sīlamayikapuññakiriyavatthu vuttaṁ. ", "html": "{}"}, "pe8:299.5": {"pli": "Kusalo ca jahāti pāpakanti lobhassa ca mohassa ca byāpādassa ca pahānamāha. ", "html": "{}"}, "pe8:299.6": {"pli": "Rāgadosamohakkhayā sa nibbutoti lobhassa ca mohassa ca byāpādassa ca chandarāgavinayamāhāti. ", "html": "{}"}, "pe8:299.7": {"pli": "Dadato puññaṁ pavaḍḍhatīti gāthā alobho kusalamūlaṁ bhavati. ", "html": "{}"}, "pe8:299.8": {"pli": "Saṁyamato veraṁ na cīyatīti adoso kusalamūlaṁ bhavati. ", "html": "{}"}, "pe8:299.9": {"pli": "Saṁyamato veraṁ na cīyatīti averā asapattā abyāpādatāya sadā. ", "html": "{}"}, "pe8:299.10": {"pli": "Kusalo ca jahāti pāpakanti ñāṇuppādā aññāṇanirodho. ", "html": "{}"}, "pe8:299.11": {"pli": "Catutthapadena rāgadosamohakkhayena rāgavirāgā cetovimuttimohakkhayena avijjāvirāgā paññāvimutti, ayaṁ vicayo. ", "html": "{}</p>"}, "pe8:300.1": {"pli": "Yuttīti dāne ṭhito ubhayaṁ hi paripūreti. ", "html": "<p data-counter='3'>{}"}, "pe8:300.2": {"pli": "Macchariyañca pajahati. ", "html": "{}"}, "pe8:300.3": {"pli": "Puññañca pavaḍḍhati. ", "html": "{}"}, "pe8:300.4": {"pli": "Atthi esā yutti. ", "html": "{}</p>"}, "pe8:301.1": {"pli": "Padaṭṭhānanti dadato puññaṁ pavaḍḍhatīti cāgādhiṭṭhānassa padaṭṭhānaṁ. ", "html": "<p data-counter='4'>{}"}, "pe8:301.2": {"pli": "Saṁyamato veraṁ na cīyatīti paññādhiṭṭhānassa padaṭṭhānaṁ kusalo ca jahāti pāpakanti saccādhiṭṭhānassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:301.3": {"pli": "Rāgadosamohakkhayā sa nibbutoti upasamādhiṭṭhānassa padaṭṭhānaṁ. ", "html": "{}"}, "pe8:301.4": {"pli": "Ayaṁ padaṭṭhāno. ", "html": "{}</p>"}, "pe8:302.1": {"pli": "Tattha katamo lakkhaṇo? ", "html": "<p data-counter='5'>{}"}, "pe8:302.2": {"pli": "Dadato puññaṁ pavaḍḍhati saṁyamato veraṁ na cīyati. ", "html": "{}"}, "pe8:302.3": {"pli": "Dadatopi veraṁ na kariyāti kusalo ca jahāti pāpakaṁ rāgadosamohakkhayā sa nibbuto rūpakkhayāpi vedanakkhayāpi, yena rūpena diṭṭhaṁ, tena tathāgato paññapento paññapeyya rūpassa khayā virāganirodhāti evaṁ pañcakkhandhā. ", "html": "{}</p>"}, "pe8:303.1": {"pli": "Catubyūho idha bhagavato ko adhippāyo? ", "html": "<p data-counter='6'>{}"}, "pe8:303.2": {"pli": "Ye mahābhogānaṁ patthayissanti? ", "html": "{}"}, "pe8:303.3": {"pli": "Te dānaṁ dassanti parissayapahānāya, ye averābhichandakā, te pañca verāni pajahissanti, ye kusalābhichandakā, te aṭṭhaṅgikaṁ maggaṁ bhāvessanti aṭṭhannaṁ micchattānaṁ pahānāya. ", "html": "{}"}, "pe8:303.4": {"pli": "Ye nibbāyitukāmā, te rāgadosamohaṁ pajahissantīti ayaṁ bhagavato adhippāyo. ", "html": "{}</p>"}, "pe8:304.1": {"pli": "Āvaṭṭoti yañca adadato macchariyaṁ yañca asaṁyamato veraṁ yañca akusalassa pāpassa appahānaṁ, ayaṁ dukkhaniddeso na samudayo. ", "html": "<p data-counter='7'>{}"}, "pe8:304.2": {"pli": "Alobhena ca adosena ca amohena ca kusalena imāni tīṇi kusalamūlāni. ", "html": "{}"}, "pe8:304.3": {"pli": "Tesaṁ paccayo aṭṭha sammattāni, ayaṁ maggo. ", "html": "{}"}, "pe8:304.4": {"pli": "Tesaṁ rāgadosamohānaṁ khayā, ayaṁ nirodho. ", "html": "{}</p>"}, "pe8:305.1": {"pli": "Vibhattīti dadato puññaṁ pavaḍḍhatīti na ekaṁsena yo rājadaṇḍabhayena deti, yo ca akappiyassa paribhogena sīlavantesu deti, na tassa puññaṁ pavaḍḍhatīti so cetaṁ dānaṁ akusalena deti, daṇḍadānaṁ satthadānaṁ apuññamayaṁ pavaḍḍhati, na puññaṁ. ", "html": "<p data-counter='8'>{}"}, "pe8:305.2": {"pli": "Saṁyamato veraṁ na cīyatīti na ekaṁsena kiṅkāraṇaṁ yañca yo padaṁ diṭṭhadhammikaṁ passati yadi mama rājāno gahetvā hatthaṁ vā chindeyya …pe… ", "html": "{}"}, "pe8:305.3": {"pli": "na tena saṁyamena veraṁ na karoti. ", "html": "{}"}, "pe8:305.4": {"pli": "Yo tu evaṁ samādiyati pāṇātipātassa pāpako vipākoti, diṭṭhe yeva dhamme abhisamparāye ca evaṁ sabbassa akusalassa hetuto ārati. ", "html": "{}"}, "pe8:305.5": {"pli": "Iminā saṁyamena veraṁ na cīyati. ", "html": "{}</p>"}, "pe8:306.1": {"pli": "Parivattanāti dadato puññaṁ pavaḍḍhatīti adadato puññaṁ na pavaḍḍhati. ", "html": "<p data-counter='9'>{}"}, "pe8:306.2": {"pli": "Yaṁ dānamayaṁ, taṁ saṁyamato veraṁ na cīyati, asaṁyamato veraṁ karīyati. ", "html": "{}"}, "pe8:306.3": {"pli": "Kusalo ca jahāti pāpakaṁ akusalo na jahāti. ", "html": "{}"}, "pe8:306.4": {"pli": "Rāgadosamohakkhayā sanibbutoti dūtaṁ pesetvā paṇītaṁ pesetvāpi na pakkosāmi, so sayameva pana mahābhikkhusaṅghaparivāro amhākaṁ vasanaṭṭhānaṁ sampatto amhehi ca santhāgārasālā kāritā, ettha mayaṁ dasabalaṁ ānetvā maṅgalaṁ bhaṇāpemāti cintetvā upasaṅkamiṁsu. ", "html": "{}"}, "pe8:306.5": {"pli": "Yena santhāgāraṁ tenupasaṅkamiṁsūti taṁ divasaṁ kira santhāgāre cittakammaṁ niṭṭhāpetvā aṭṭakā muttamattā honti. ", "html": "{}"}, "pe8:306.6": {"pli": "Buddhā nāma araññajjhāsayā araññārāmā antogāme vaseyyuṁ vā no vāti tasmā bhagavato manaṁ jānitvāva paṭijaggissāmāti cintetvā te bhagavantaṁ upasaṅkamiṁsu. ", "html": "{}"}, "pe8:306.7": {"pli": "Idāni pana manaṁ labhitvā paṭijaggitukāmā yena santhāgāraṁ, tenupasaṅkamiṁsu. ", "html": "{}"}, "pe8:306.8": {"pli": "Sabbasantharinti yathā sabbaṁ santhataṁ hoti evaṁ yena bhagavā tenupasaṅkamiṁsūti. ", "html": "{}"}, "pe8:306.9": {"pli": "Ettha pana te mallarājāno santhāgāraṁ paṭijaggitvā nagaravīthiyopi sammajjāpetvā dhaje ussāpetvā suvaṇṇaghaṭikadaliyo ca ṭhapāpetvā sakalanagaraṁ dīpamālāhi vippakiṇṇatārakaṁ viya katvā khīrapake dārake khīraṁ pāyetha, dahare kumāre lahuṁ lahuṁ bhojāpetvā sayāpetha, uccāsaddaṁ mākari, ajja ekarattiṁ satthā antogāmeva vasissati, buddhā nāma appasaddakāmā hontīti bheriṁ carāpetvā sayaṁ daṇḍakadīpikā ādāya yena bhagavā tenupasaṅkamiṁsu. ", "html": "{}"}, "pe8:306.10": {"pli": "Bhagavantaṁyeva purakkhatvāti bhagavantaṁ purato katvā, tattha bhagavā bhikkhūnañceva upāsakānañca majjhe nisinno ativiya virocati. ", "html": "{}"}, "pe8:306.11": {"pli": "Samantapāsādiko suvaṇṇavaṇṇo abhirūpo dassanīyo puratthimakāyato suvaṇṇavaṇṇā rasmi uṭṭhahitvā gaganatale asītihatthaṁ ṭhānaṁ gaṇhāti. ", "html": "{}"}, "pe8:306.12": {"pli": "Pacchimakāyato dakkhiṇahatthato vāmahatthato suvaṇṇavaṇṇā heṭṭhā pādatalehi pavāḷavaṇṇarasmi uṭṭhahitvā ghanapathaviyaṁ asītihatthaṁ ṭhānaṁ gaṇhāti, evaṁ samantā asītihatthamattaṁ ṭhānaṁ chabbaṇṇabuddharasmiyo vijjotamānā vitaṇḍamānā vidhāvanti, sabbe disābhāgā suvaṇṇacampakapupphehi vikiriyamānā viya suvaṇṇaghaṭato nikkhantasuvaṇṇarasadhārāhi siñcamānā viya pasāritasuvaṇṇapaṭaparikkhittā viya verambhavātasamuṭṭhitakiṁsukakiṁsukārakaṇikārapupphacuṇṇasamokiṇṇā viya vippakasantaṁ asītianubyañjanabyāmappabhā dvattiṁsavaralakkhaṇasamujjalaṁ sarīraṁ samuggatatārakaṁ viya gaganatalaṁ vikasitamiva padumavanaṁ sabbaphāliphullo viya yojanasatiko pāricchattako paṭipāṭiyā ṭhapitānaṁ dvattiṁsacandānaṁ dvattiṁsasūriyānaṁ dvattiṁsacakkavattīnaṁ dvattiṁsadevarājānaṁ dvattiṁsamahābrahmānaṁ nibbuto asekkhassa natthi nibbuti. ", "html": "{}</p>"}, "pe8:307.1": {"pli": "Vevacananti dadato puññaṁ pavaḍḍhati, anumodatopi puññaṁ pavaḍḍhati. ", "html": "<p data-counter='10'>{}"}, "pe8:307.2": {"pli": "Cittassa samādahatopi veyyāvaccakiriyāyapi puññaṁ pavaḍḍhatīti. ", "html": "{}</p>"}, "pe8:308.1": {"pli": "Paññattīti dadato puññaṁ pavaḍḍhati, alobhassa paṭinissayaghātapaññattiyā paññattaṁ. ", "html": "<p data-counter='11'>{}"}, "pe8:308.2": {"pli": "Saṁyamato veraṁ na cīyatīti adosassa paṭinissayaghātapaññattiyā paññattaṁ kusalo ca jahāti pāpakanti amohassa paṭinissayaghātapaññattiyā paññattaṁ. ", "html": "{}</p>"}, "pe8:309.1": {"pli": "Otaraṇoti pañcasu indriyesu dadato puññaṁ pavaḍḍhati, saṁyamato veraṁ na cīyati saṁyamena sīlakkhandho. ", "html": "<p data-counter='12'>{}"}, "pe8:309.2": {"pli": "Otiṇṇo chasu indriyesu saṁvaro, ayaṁ samādhikkhandho, yaṁ kusalo ca jahāti pāpakaṁ, ayaṁ paññākkhandho, rāgadosamohakkhayā sa nibbutoti vimuttikkhandho. ", "html": "{}"}, "pe8:309.3": {"pli": "Dhātūsu dhammadhātu, āyatanesu manāyatanaṁ. ", "html": "{}</p>"}, "pe8:310.1": {"pli": "Sodhanoti yenārambhena idaṁ suttaṁ desitaṁ so ārambho suddho. ", "html": "<p data-counter='13'>{}</p>"}, "pe8:311.1": {"pli": "Adhiṭṭhāno dānanti ekattatāya paññattaṁ. ", "html": "<p data-counter='14'>{}"}, "pe8:311.2": {"pli": "Cāgo pariccāgo dhammadānaṁ āmisadānaṁ, aṭṭha dānāni vitthārena kātabbāni, ayaṁ vemattatā. ", "html": "{}"}, "pe8:311.3": {"pli": "Na ca dadato ekattapaññattiyā paññattaṁ. ", "html": "{}"}, "pe8:311.4": {"pli": "Khantī anavajjanti paññattiyā paññattaṁ. ", "html": "{}"}, "pe8:311.5": {"pli": "Rāgadosamohakkhayā sa nibbutoti rodhavīriyapaññattiyā paññattā. ", "html": "{}</p>"}, "pe8:312.1": {"pli": "Parikkhāroti dānassa pāmojjaṁ paccayo, alobho hetu. ", "html": "<p data-counter='15'>{}"}, "pe8:312.2": {"pli": "Saṁyamato yoniso manasikāro hetu, pariccāgo paccayo. ", "html": "{}"}, "pe8:312.3": {"pli": "Kusalo ca jahāti pāpakanti yathābhūtadassanaṁ paccayo, ñāṇappaṭilābho hetu. ", "html": "{}"}, "pe8:312.4": {"pli": "Rāgadosamohakkhayā sa nibbutoti parato ca ghoso ajjhattañca yoniso manasikāro maggo ca hetu ca paccayo ca. ", "html": "{}</p>"}, "pe8:313.1": {"pli": "Samāropanoti dadato puññaṁ pavaḍḍhatīti gāthā tassa sīlampi vaḍḍhati. ", "html": "<p data-counter='16'>{}"}, "pe8:313.2": {"pli": "Saṁyamopi vaḍḍhati. ", "html": "{}"}, "pe8:313.3": {"pli": "Saṁyamato veraṁ na cīyatīti. ", "html": "{}"}, "pe8:313.4": {"pli": "Aññepi kilesā na cīyanti yepissa tappaccayā uppajjeyyuṁ āsavā vighātā, tepissa na uppajjanti. ", "html": "{}"}, "pe8:313.5": {"pli": "Rāgadosamohakkhayā sa nibbutoti rāgadosassāpi khayā rāgānusayassapi khayā dosassa mohassāpi sa nibbutoti sopādisesā nibbānadhātu anupādisesāpi. ", "html": "{}"}, "pe8:313.6": {"pli": "Ayaṁ samāropano. ", "html": "{}</p>"}, "pe8:313.7": {"pli": "Therassa mahākaccāyanassa peṭakopadese hārassa sampātabhūmi samattā. ", "html": "<p class='endsutta'>{}</p></article>"}}, "pe9": {"pe9:0.1": {"pli": "Peṭakopadesa ", "html": "<article id='pe9'><header><ul><li class='division'>{}</li></ul>"}, "pe9:0.2": {"pli": "Suttavebhaṅgiya ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "pe9:1.1": {"pli": "Pubbā koṭi na paññāyati avijjāya ca bhavataṇhāya ca. ", "html": "<p>{}"}, "pe9:1.2": {"pli": "Tattha avijjānīvaraṇānaṁ taṇhāsaṁyojanānaṁ sattānaṁ pubbakoṭi na paññāyati. ", "html": "{}"}, "pe9:1.3": {"pli": "Tattha ye sattā taṇhāsaṁyojanā, te ajjhosānabahulā mandavipassakā. ", "html": "{}"}, "pe9:1.4": {"pli": "Ye pana ussannadiṭṭhikā sattā, te vipassanābahulā mandajjhosānā. ", "html": "{}</p>"}, "pe9:2.1": {"pli": "Tattha taṇhācaritā sattā sattasaññābhiniviṭṭhā anuppādavayadassino. ", "html": "<p>{}"}, "pe9:2.2": {"pli": "Te pañcasu khandhesu attānaṁ samanupassanti “rūpavantaṁ vā attānaṁ, attani vā rūpaṁ, rūpasmiṁ vā attānan”ti. ", "html": "{}"}, "pe9:2.3": {"pli": "Evaṁ pañcakkhandhā. ", "html": "{}"}, "pe9:2.4": {"pli": "Aññehi khandhehi attānaṁ samanupassanti tassa ussannadiṭṭhikā sattā vipassamānā khandhe ujuṁ attato samanupassanti. ", "html": "{}"}, "pe9:2.5": {"pli": "Te rūpaṁ attako samanupassanti. ", "html": "{}"}, "pe9:2.6": {"pli": "Yaṁ rūpaṁ, so attā. ", "html": "{}"}, "pe9:2.7": {"pli": "Yo ahaṁ, taṁ rūpaṁ. ", "html": "{}"}, "pe9:2.8": {"pli": "So rūpavināsaṁ passati, ayaṁ ucchedavādī. ", "html": "{}"}, "pe9:2.9": {"pli": "Iti pañcannaṁ khandhānaṁ paṭhamābhinipātā sakkāyadiṭṭhiyo pañca ucchedaṁ bhajanti “taṁ jīvaṁ taṁ sarīran”ti. ", "html": "{}"}, "pe9:2.10": {"pli": "Ekamekamhi khandhe tīhi padehi pacchimakehi sassataṁ bhajati “aññaṁ jīvaṁ aññaṁ sarīran”ti. ", "html": "{}"}, "pe9:2.11": {"pli": "Ito bahiddhāte pabbajitā taṇhācaritā kāmasukhallikānuyogamanuyuttā viharanti. ", "html": "{}"}, "pe9:2.12": {"pli": "Tena ye ca nissandena diṭṭhicaritā attakilamathānuyogamanuyuttā viharanti. ", "html": "{}"}, "pe9:2.13": {"pli": "Tenayeva diṭṭhisukhena ettāvatā bāhirako payogo. ", "html": "{}</p>"}, "pe9:3.1": {"pli": "Tattha diṭṭhicaritā sattā ye ariyadhammavinayaṁ otaranti, te dhammānusārino honti. ", "html": "<p>{}"}, "pe9:3.2": {"pli": "Ye taṇhācaritā sattā ariyaṁ dhammavinayaṁ otaranti, te saddhānusārino honti. ", "html": "{}</p>"}, "pe9:4.1": {"pli": "Tattha ye diṭṭhicaritā sattā, te kāmesu dosadiṭṭhī, na ca ye kāmesu anusayā samūhatā, te attakilamathānuyogamanuyuttā viharanti. ", "html": "<p>{}"}, "pe9:4.2": {"pli": "Tesaṁ satthā dhammaṁ deseti. ", "html": "{}"}, "pe9:4.3": {"pli": "Añño vā sāvako kāmehi natthi atthoti te ca pubbeyeva kāmehi anatthikā iti kāme appakasirena paṭinissajjanti. ", "html": "{}"}, "pe9:4.4": {"pli": "Te cetasikena dukkhena anajjhositā. ", "html": "{}"}, "pe9:4.5": {"pli": "Tena vuccati “sukhā paṭipadā”ti. ", "html": "{}"}, "pe9:4.6": {"pli": "Ye pana taṇhācaritā sattā, te kāmesu ajjhositā, tesaṁ satthā vā dhammaṁ deseti. ", "html": "{}"}, "pe9:4.7": {"pli": "Aññataro vā bhikkhu kāmehi natthi atthoti, te piyarūpaṁ dukkhena paṭinissajjanti. ", "html": "{}"}, "pe9:4.8": {"pli": "Tena vuccati “dukkhā paṭipadā”ti. ", "html": "{}"}, "pe9:4.9": {"pli": "Iti ime sabbasattā dvīsu paṭipadāsu samosaraṇaṁ gacchanti dukkhāyañca sukhāyañca. ", "html": "{}</p>"}, "pe9:5.1": {"pli": "Tattha ye diṭṭhicaritā sattā, te dvidhā mudindriyā ca tikkhindriyā ca. ", "html": "<p>{}"}, "pe9:5.2": {"pli": "Tattha ye diṭṭhicaritā sattā tikkhindriyā sukhena paṭinissajjanti, khippañca abhisamenti, tena vuccati “khippābhiññā sukhā paṭipadā”ti. ", "html": "{}"}, "pe9:5.3": {"pli": "Tattha ye diṭṭhicaritā sattā mudindriyā paṭhamaṁ tikkhindriyaṁ upādāya dandhataraṁ abhisamenti, te sukhena paṭinissajjanti, dandhañca abhisamenti. ", "html": "{}"}, "pe9:5.4": {"pli": "Tena vuccati “sukhā paṭipadā dandhābhiññā”ti. ", "html": "{}"}, "pe9:5.5": {"pli": "Tattha taṇhācaritā sattā dvidhā tikkhindriyā ca mudindriyā ca. ", "html": "{}"}, "pe9:5.6": {"pli": "Tattha ye taṇhācaritā sattā tikkhindriyā dukkhena paṭinissajjanti, khippañca abhisamenti. ", "html": "{}"}, "pe9:5.7": {"pli": "Tena vuccati “dukkhā paṭipadā khippābhiññā”ti. ", "html": "{}"}, "pe9:5.8": {"pli": "Tattha ye taṇhācaritā sattā mudindriyā paṭhamaṁ tikkhindriyaṁ upādāya dandhataraṁ abhisamenti, te dukkhena paṭinissajjanti, dandhañca abhisamenti. ", "html": "{}"}, "pe9:5.9": {"pli": "Tena vuccati “dukkhā paṭipadā dandhābhiññā”ti. ", "html": "{}"}, "pe9:5.10": {"pli": "Imā catasso paṭipadāyo apañcamā achaṭṭhā. ", "html": "{}"}, "pe9:5.11": {"pli": "Ye hi keci nibbutā nibbāyissanti vā imāhi catūhi paṭipadāhi anaññāhi ayaṁ paṭipadācatukkena kilese niddisati. ", "html": "{}"}, "pe9:5.12": {"pli": "Yā catukkamaggena ariyadhammesu niddisitabbā, ayaṁ vuccati sīhavikkīḷito nāma nayo. ", "html": "{}</p>"}, "pe9:6.1": {"pli": "Tatrime cattāro āhārā. ", "html": "<p>{}"}, "pe9:6.2": {"pli": "Cattāro vipallāsā upādānā yogā ganthā āsavā oghā sallā viññāṇaṭṭhitiyo agatigamanāti, evaṁ imāni sabbāni dasa padāni. ", "html": "{}"}, "pe9:6.3": {"pli": "Ayaṁ suttassa saṁsandanā. ", "html": "{}</p>"}, "pe9:7.1": {"pli": "Cattāro āhārā. ", "html": "<p>{}"}, "pe9:7.2": {"pli": "Tattha yo ca kabaḷīkāro āhāro yo ca phasso āhāro, ime taṇhācaritena pahātabbā. ", "html": "{}"}, "pe9:7.3": {"pli": "Tattha yo ca manosañcetanāhāro yo ca viññāṇāhāro, ime diṭṭhicaritena pahātabbā. ", "html": "{}</p>"}, "pe9:8.1": {"pli": "Paṭhamo āhāro paṭhamo vipallāso, dutiyo āhāro dutiyo vipallāso, tatiyo āhāro tatiyo vipallāso, catuttho āhāro catuttho vipallāso. ", "html": "<p>{}"}, "pe9:8.2": {"pli": "Ime cattāro vipallāsā apañcamā achaṭṭhā. ", "html": "{}"}, "pe9:8.3": {"pli": "Idañca pamāṇā cattāro āhārā. ", "html": "{}</p>"}, "pe9:9.1": {"pli": "Tattha paṭhame vipallāse ṭhito kāme upādiyati, idaṁ kāmupādānaṁ. ", "html": "<p>{}"}, "pe9:9.2": {"pli": "Dutiye vipallāse ṭhito anāgataṁ bhavaṁ upādiyati, idaṁ sīlabbatupādānaṁ. ", "html": "{}"}, "pe9:9.3": {"pli": "Tatiye vipallāse ṭhito viparīto diṭṭhiṁ upādiyati, idaṁ diṭṭhupādānaṁ. ", "html": "{}"}, "pe9:9.4": {"pli": "Catutthe vipallāse ṭhito khandhe attato upādiyati, idaṁ attavādupādānaṁ. ", "html": "{}</p>"}, "pe9:10.1": {"pli": "Tattha kāmupādāne ṭhito kāme abhijjhāyati ganthati, ayaṁ abhijjhākāyagantho. ", "html": "<p>{}"}, "pe9:10.2": {"pli": "Sīlabbatupādāne ṭhito byāpādaṁ ganthati, ayaṁ byāpādakāyagantho. ", "html": "{}"}, "pe9:10.3": {"pli": "Diṭṭhupādāne ṭhito parāmāsaṁ ganthati, ayaṁ parāmāsakāyagantho. ", "html": "{}"}, "pe9:10.4": {"pli": "Attavādupādāne ṭhito papañcanto ganthati, ayaṁ idaṁsaccābhiniveso kāyagantho. ", "html": "{}</p>"}, "pe9:11.1": {"pli": "Tassa ganthitā kilesā āsavanti. ", "html": "<p>{}"}, "pe9:11.2": {"pli": "Kiñci pana vuccati vippaṭisāro. ", "html": "{}"}, "pe9:11.3": {"pli": "Ye vippaṭisārā te anusayā. ", "html": "{}"}, "pe9:11.4": {"pli": "Tattha abhijjhākāyaganthena kāmāsavo, byāpādakāyaganthena bhavāsavo, parāmāsakāyaganthena diṭṭhāsavo, idaṁ saccābhinivesakāyaganthena avijjāsavo. ", "html": "{}</p>"}, "pe9:12.1": {"pli": "Te cattāro āsavā vepullabhāvaṁ gatā oghā honti, tena vuccanti “oghā”ti. ", "html": "<p>{}"}, "pe9:12.2": {"pli": "Tattha kāmāsavo kāmogho, bhavāsavo bhavogho, avijjāsavo avijjogho, diṭṭhāsavo diṭṭhogho. ", "html": "{}</p>"}, "pe9:13.1": {"pli": "Te cattāro oghā āsayamanupaviṭṭhā anusayasahagatā vuccanti. ", "html": "<p>{}"}, "pe9:13.2": {"pli": "Sallāti hadayamāhacca tiṭṭhantā. ", "html": "{}"}, "pe9:13.3": {"pli": "Tattha kāmogho rāgasallaṁ, bhavogho dosasallaṁ, avijjogho mohasallaṁ, diṭṭhogho diṭṭhisallaṁ. ", "html": "{}</p>"}, "pe9:14.1": {"pli": "Imehi catūhi sallehi pariyādinnaṁ viññāṇaṁ catūsu dhammesu tiṭṭhati rūpe vedanāya saññāya saṅkhāresu. ", "html": "<p>{}"}, "pe9:14.2": {"pli": "Imā catasso viññāṇaṭṭhitiyo. ", "html": "{}"}, "pe9:14.3": {"pli": "Tattha rāgasallena nandūpasecanaṁ rūpūpagaṁ viññāṇaṁ tiṭṭhati. ", "html": "{}"}, "pe9:14.4": {"pli": "Dosasallena vedanūpagaṁ mohasallena saññūpagaṁ diṭṭhisallena nandūpasecanaṁ saṅkhārūpagaṁ viññāṇaṁ tiṭṭhati. ", "html": "{}</p>"}, "pe9:15.1": {"pli": "Catūhi viññāṇaṭṭhitīhi catubbidhaṁ agatiṁ gacchanti chandā dosā bhayā mohā. ", "html": "<p>{}"}, "pe9:15.2": {"pli": "Rāgena chandā agatiṁ gacchati, dosena dosā agatiṁ gacchati, mohena mohā agatiṁ gacchati, diṭṭhiyā bhayā agatiṁ gacchati. ", "html": "{}"}, "pe9:15.3": {"pli": "Iti idañca kammaṁ ime ca kilesā. ", "html": "{}"}, "pe9:15.4": {"pli": "Ayaṁ saṁsārassa hetu. ", "html": "{}</p>"}, "pe9:16.1": {"pli": "Tatthimā catasso disā kabaḷīkārāhāro “asubhe subhan”ti vipallāso kāmupādānaṁ kāmayogo abhijjhākāyagantho kāmāsavo kāmogho rāgasallaṁ rūpūpagā viññāṇaṭṭhiti chandā agatigamanaṁ. ", "html": "<p>{}"}, "pe9:16.2": {"pli": "Ayaṁ paṭhamā disā. ", "html": "{}</p>"}, "pe9:17.1": {"pli": "Phasso āhāro “dukkhe sukhan”ti vipallāso sīlabbatupādānaṁ bhavayogobyāpādo kāyagantho bhavāsavo bhavogho dosasallaṁ vedanūpagā viññāṇaṭṭhiti dosā agatigamanaṁ, ayaṁ dutiyā disā. ", "html": "<p>{}</p>"}, "pe9:18.1": {"pli": "Manosañcetanāhāro “anattani attā”ti vipallāso diṭṭhupādānaṁ diṭṭhiyogo parāmāsakāyagantho diṭṭhāsavo diṭṭhogho diṭṭhisallaṁ saññūpagā viññāṇaṭṭhiti bhayā agatigamanaṁ. ", "html": "<p>{}"}, "pe9:18.2": {"pli": "Ayaṁ tatiyā disā. ", "html": "{}</p>"}, "pe9:19.1": {"pli": "Viññāṇāhāro “anicce niccan”ti vipallāso attavādupādānaṁ avijjāyogo idaṁsaccābhiniveso kāyagantho avijjāsavo avijjogho mohasallaṁ saṅkhārūpagā viññāṇaṭṭhiti mohā agatigamanaṁ, ayaṁ catutthī disā. ", "html": "<p>{}"}, "pe9:19.2": {"pli": "Iti imesaṁ dasannaṁ suttānaṁ paṭhamena padena paṭhamāya disāya ālokanaṁ. ", "html": "{}"}, "pe9:19.3": {"pli": "Ayaṁ vuccati disālokanā. ", "html": "{}</p>"}, "pe9:20.1": {"pli": "Catūhi vipallāsehi akusalapakkhe disāvilokanā kilesaṁ saṁyojetvā ayaṁ akusalapakkhe disāvilokanāya bhūmi pañcannaṁ dasannaṁ suttānaṁ yāni paṭhamāni padāni imesaṁ dhammānaṁ ko attho? ", "html": "<p>{}"}, "pe9:20.2": {"pli": "Eko attho, byañjanameva nānaṁ. ", "html": "{}"}, "pe9:20.3": {"pli": "Evaṁ dutiyā evaṁ tatiyā evaṁ catutthī. ", "html": "{}"}, "pe9:20.4": {"pli": "Ayaṁ paṭhamā saṁsandanā. ", "html": "{}</p>"}, "pe9:21.1": {"pli": "Iminā peyyālena sabbe kilesā catūsu padesu pakkhipitabbā. ", "html": "<p>{}"}, "pe9:21.2": {"pli": "Tato kusalapakkhe catasso paṭipadā cattāri jhānāni cattāro satipaṭṭhānā cattāro vihārā dibbo brahmā ariyo āneñjo cattāro sammappadhānā cattāro acchariyā abbhutadhammā cattāro adhiṭṭhānā cattāro samādhayo chandasamādhi vīriyasamādhi cittasamādhi vīmaṁsāsamādhi. ", "html": "{}"}, "pe9:21.3": {"pli": "Cattāro dhammā sukhabhāgiyā nāññatra bojjhaṅgā nāññatra tapasā nāññatindriyasaṁvarā nāññatra sabbanissaggā cattāri appamāṇāni. ", "html": "{}</p>"}, "pe9:22.1": {"pli": "Tattha dukkhā paṭipadā dandhābhiññā bhāviyamānā bahulīkariyamānā paṭhamaṁ jhānaṁ paripūreti, paṭhamaṁ jhānaṁ paripuṇṇaṁ paṭhamaṁ satipaṭṭhānaṁ paripūreti, paṭhamaṁ satipaṭṭhānaṁ paripuṇṇaṁ paṭhamaṁ vihāraṁ paripūreti, paṭhamo vihāro paripuṇṇo paṭhamaṁ sammappadhānaṁ paripūreti, paṭhamaṁ sammappadhānaṁ paripuṇṇaṁ paṭhamaṁ acchariyaṁ abbhutadhammaṁ paripūreti, paṭhamo acchariyo abbhuto dhammo paripuṇṇo paṭhamaṁ adhiṭṭhānaṁ paripūreti, paṭhamaṁ adhiṭṭhānaṁ paripuṇṇaṁ chandasamādhiṁ paripūreti, chandasamādhi paripuṇṇo indriyasaṁvaraṁ paripūreti, indriyasaṁvaro paripuṇṇo paṭhamaṁ mettāappamāṇaṁ paripūreti. ", "html": "<p>{}"}, "pe9:22.2": {"pli": "Evaṁ yāva sabbanissaggo catutthaṁ appamāṇaṁ paripūreti. ", "html": "{}</p>"}, "pe9:23.1": {"pli": "Tattha paṭhamā ca paṭipadā paṭhamañca jhānaṁ paṭhamañca satipaṭṭhānaṁ paṭhamo ca vihāro paṭhamañca sammappadhānaṁ paṭhamo ca acchariyo abbhuto dhammo saccādhiṭṭhānañca chandasamādhi ca indriyasaṁvaro ca mettā ca appamāṇaṁ. ", "html": "<p>{}"}, "pe9:23.2": {"pli": "Ayaṁ paṭhamā disā. ", "html": "{}</p>"}, "pe9:24.1": {"pli": "Dukkhā ca paṭipadā khippābhiññā dutiyaṁ jhānaṁ dutiyañca satipaṭṭhānaṁ dutiyo ca vihāro dutiyañca sammappadhānaṁ dutiyo ca acchariyo abbhuto dhammo cāgādhiṭṭhānaṁ cittasamādhi cattāro iddhipādā karuṇā ca appamāṇaṁ, ayaṁ dutiyā disā. ", "html": "<p>{}</p>"}, "pe9:25.1": {"pli": "Sukhā ca paṭipadā dandhābhiññā tatiyañca jhānaṁ tatiyañca satipaṭṭhānaṁ tatiyo ca vihāro tatiyañca sammappadhānaṁ tatiyo ca acchariyo abbhuto dhammo paññādhiṭṭhānañca vīriyasamādhi ca bojjhaṅgā ca muditā ca appamāṇaṁ. ", "html": "<p>{}"}, "pe9:25.2": {"pli": "Ayaṁ tatiyā disā. ", "html": "{}</p>"}, "pe9:26.1": {"pli": "Sukhā ca paṭipadā khippābhiññā catutthaṁ jhānaṁ catutthañca satipaṭṭhānaṁ catuttho ca vihāro catutthañca sammappadhānaṁ catuttho ca acchariyo abbhuto dhammo upasamādhiṭṭhānañca vīmaṁsāsamādhi ca sabbanissaggo ca upekkhā appamāṇañca. ", "html": "<p>{}"}, "pe9:26.2": {"pli": "Ayaṁ catutthī disā. ", "html": "{}"}, "pe9:26.3": {"pli": "Imāsaṁ catassannaṁ disānaṁ ālokanā. ", "html": "{}"}, "pe9:26.4": {"pli": "Ayaṁ vuccati disālokano nāma nayo. ", "html": "{}</p>"}, "pe9:27.1": {"pli": "Tatthāyaṁ yojanā. ", "html": "<p>{}"}, "pe9:27.2": {"pli": "Cattāro ca āhārā catasso ca paṭipadā, cattāro ca vipallāsā cattāro ca satipaṭṭhānā, cattāri ca upādānāni cattāri ca jhānāni cattāro ca yogā vihārā ca, ganthā ca sammappadhānā ca, āsavā ca acchariyā abbhutadhammā ca, oghā ca adhiṭṭhānāni ca, sallā ca samādhayo, viññāṇaṭṭhitiyo cattāro ca sukhabhāgiyā dhammā, cattāri ca agatigamanāni cattāri ca appamāṇāni iti kusalākusalānaṁ paṭipakkhavasena yojanā, ayaṁ vuccati disālokano nayo. ", "html": "{}</p>"}, "pe9:28.1": {"pli": "Tassa cattāri sāmaññaphalāni pariyosānaṁ, yo ca dhammo kusalākusalaniddese paṭhamo disāniddeso, imassa sotāpattiphalaṁ pariyosānaṁ dutiyaṁ sakadāgāmiphalaṁ, tatiyaṁ anāgāmiphalaṁ, catutthaṁ arahattaphalaṁ. ", "html": "<p>{}</p>"}, "pe9:29.1": {"pli": "Tattha katamo tipukkhalo nayo? ", "html": "<p>{}"}, "pe9:29.2": {"pli": "Ye ca dukkhāya paṭipadāya dandhābhiññāya khippābhiññāya ca niyyanti dve puggalā, ye ca sukhāya paṭipadāya dandhābhiññāya khippābhiññāya ca niyyanti dve puggalā. ", "html": "{}</p>"}, "pe9:30.1": {"pli": "Imesaṁ catunnaṁ puggalānaṁ yo puggalo sukhāya paṭipadāya dandhābhiññāya niyyāti, yo ca puggalo dukkhāya paṭipadāya khippābhiññāya niyyāti. ", "html": "<p>{}"}, "pe9:30.2": {"pli": "Ime dve puggalā bhavanti. ", "html": "{}"}, "pe9:30.3": {"pli": "Tattha yo sukhāya paṭipadāya khippābhiññāya niyyāti, ayaṁ ugghaṭitaññū. ", "html": "{}"}, "pe9:30.4": {"pli": "Yo pacchimo puggalo sādhāraṇo, ayaṁ vipañcitaññū. ", "html": "{}"}, "pe9:30.5": {"pli": "Yo puggalo dandhābhiññāya dukkhāya paṭipadāya niyyāti, ayaṁ neyyo. ", "html": "{}"}, "pe9:30.6": {"pli": "Ime cattāro bhavitvā tīṇi honti, tattha ugghaṭitaññussa samathapubbaṅgamā vipassanā, neyyassa vipassanāpubbaṅgamo samatho, vipañcitaññussa samathavipassanā yuganaddhā. ", "html": "{}"}, "pe9:30.7": {"pli": "Ugghaṭitaññussa mudukā desanā, neyyassa tikkhā desanā, vipañcitaññussa tikkhamudukā desanā. ", "html": "{}</p>"}, "pe9:31.1": {"pli": "Ugghaṭitaññussa adhipaññāsikkhā, neyyassa adhicittasikkhā, vipañcitaññussa adhisīlasikkhā. ", "html": "<p>{}"}, "pe9:31.2": {"pli": "Iti imesaṁ puggalānaṁ catūhi paṭipadāhi niyyānaṁ. ", "html": "{}</p>"}, "pe9:32.1": {"pli": "Tattha ayaṁ saṅkileso, tīṇi akusalamūlāni tayo phassā tisso vedanā tayo upavicārā tayo saṅkilesā tayo vitakkā tayo pariḷāhā tīṇi saṅkhatalakkhaṇāni tisso dukkhatāti. ", "html": "<p>{}</p>"}, "pe9:33.1": {"pli": "Tīṇi akusalamūlānīti lobho akusalamūlaṁ, doso akusalamūlaṁ, moho akusalamūlaṁ. ", "html": "<p>{}"}, "pe9:33.2": {"pli": "Tayo phassāti sukhavedanīyo phasso, dukkhavedanīyo phasso, adukkhamasukhavedanīyo phasso. ", "html": "{}"}, "pe9:33.3": {"pli": "Tisso vedanāti sukhā vedanā dukkhā vedanā adukkhamasukhā vedanā. ", "html": "{}"}, "pe9:33.4": {"pli": "Tayo upavicārāti somanassopavicāro domanassopavicāro upekkhopavicāro. ", "html": "{}"}, "pe9:33.5": {"pli": "Tayo saṅkilesāti rāgo doso moho. ", "html": "{}"}, "pe9:33.6": {"pli": "Tayo vitakkāti kāmavitakko byāpādavitakko vihiṁsāvitakko. ", "html": "{}"}, "pe9:33.7": {"pli": "Tayo pariḷāhāti rāgajo dosajo mohajo. ", "html": "{}"}, "pe9:33.8": {"pli": "Tīṇi saṅkhatalakkhaṇānīti uppādo ṭhiti vayo. ", "html": "{}"}, "pe9:33.9": {"pli": "Tisso dukkhatāti dukkhadukkhatā vipariṇāmadukkhatā saṅkhatadukkhatā. ", "html": "{}</p>"}, "pe9:34.1": {"pli": "Tattha lobho akusalamūlaṁ kuto samuṭṭhitaṁ? ", "html": "<p>{}"}, "pe9:34.2": {"pli": "Tividhaṁ ārammaṇaṁ manāpikaṁ amanāpikaṁ upekkhāṭhāniyañca. ", "html": "{}"}, "pe9:34.3": {"pli": "Tattha manāpikena ārammaṇena lobho akusalamūlaṁ samuṭṭhahati. ", "html": "{}"}, "pe9:34.4": {"pli": "Iti manāpikā ārammaṇā sukhavedanīyo phasso, sukhavedanīyaṁ phassaṁ paṭicca uppajjate sukhavedanā, sukhavedanaṁ paṭicca uppajjate somanassūpavicāro, somanassūpavicāraṁ paṭicca uppajjate rāgo, rāgaṁ paṭicca uppajjate kāmavitakko, kāmavitakkaṁ paṭicca uppajjate rāgajo pariḷāho rāgajaṁ pariḷāhaṁ paṭicca uppajjate uppādo saṅkhatalakkhaṇo, uppādaṁ saṅkhatalakkhaṇaṁ paṭicca uppajjate vipariṇāmadukkhatā. ", "html": "{}</p>"}, "pe9:35.1": {"pli": "Doso akusalamūlaṁ kuto samuṭṭhitaṁ? ", "html": "<p>{}"}, "pe9:35.2": {"pli": "Amanāpikena ārammaṇena doso akusalamūlaṁ samuṭṭhitaṁ. ", "html": "{}"}, "pe9:35.3": {"pli": "Iti amanāpikā ārammaṇā dukkhavedanīyo phasso, dukkhavedanīyaṁ phassaṁ paṭicca uppajjate dukkhavedanā, dukkhavedanaṁ paṭicca uppajjate domanassūpavicāro, domanassūpavicāraṁ paṭicca uppajjate doso, dosaṁ paṭicca uppajjate byāpādavitakko, byāpādavitakkaṁ paṭicca uppajjate dosajo pariḷāho, dosajaṁ pariḷāhaṁ paṭicca uppajjate ṭhitassa aññathattaṁ saṅkhatalakkhaṇaṁ, ṭhitassa aññathattaṁ saṅkhatalakkhaṇaṁ paṭicca uppajjate dukkhadukkhatā vedanā. ", "html": "{}</p>"}, "pe9:36.1": {"pli": "Moho akusalamūlaṁ kuto samuṭṭhitaṁ? ", "html": "<p>{}"}, "pe9:36.2": {"pli": "Upekkhāṭhāniyena ārammaṇena moho akusalamūlaṁ samuṭṭhitaṁ. ", "html": "{}"}, "pe9:36.3": {"pli": "Iti upekkhāṭhāniyā ārammaṇā adukkhamasukhavedanīyo phasso, adukkhamasukhavedanīyaṁ phassaṁ paṭicca uppajjate adukkhamasukhā vedanā, adukkhamasukhavedanaṁ paṭicca uppajjate upekkhūpavicāro, upekkhūpavicāraṁ paṭicca uppajjate moho, mohaṁ paṭicca uppajjate vihiṁsāvitakko, vihiṁsāvitakkaṁ paṭicca uppajjate mohajo pariḷāho, mohajaṁ pariḷāhaṁ paṭicca uppajjate vayo saṅkhatalakkhaṇaṁ, vayaṁ saṅkhatalakkhaṇaṁ paṭicca uppajjate saṅkhatadukkhatā, iti ayaṁ tiṇṇaṁ kilesānaṁ niddeso, ayaṁ vuccate kusalapakkhe tipukkhalo nayo. ", "html": "{}</p>"}, "pe9:37.1": {"pli": "Iti tīṇi akusalamūlāni na catutthāni na pañcamāni, tayo phassāti tisso vedanā yāva saṅkhatadukkhatāti, yo koci akusalapakkho, sabbo so tīsu akusalamūlesu samosarati. ", "html": "<p>{}</p>"}, "pe9:38.1": {"pli": "Tattha katamo kusalapakkho? ", "html": "<p>{}"}, "pe9:38.2": {"pli": "Tīṇi kusalamūlāni, tisso paññā sutamayī paññā cintāmayī paññā bhāvanāmayī paññā. ", "html": "{}"}, "pe9:38.3": {"pli": "Tayo samādhī savitakkasavicāro …pe… ", "html": "{}"}, "pe9:38.4": {"pli": "tisso sikkhā adhisīlasikkhā …pe… ", "html": "{}"}, "pe9:38.5": {"pli": "sikkhā. ", "html": "{}"}, "pe9:38.6": {"pli": "Tīṇi nimittāni samathanimittaṁ paggahanimittaṁ upekkhānimittaṁ. ", "html": "{}"}, "pe9:38.7": {"pli": "Tayo vitakkā nekkhammavitakko …pe… ", "html": "{}"}, "pe9:38.8": {"pli": "avihiṁsāvitakko. ", "html": "{}"}, "pe9:38.9": {"pli": "Tīṇi indriyāni anaññātaññassāmītindriyanti vitthāro. ", "html": "{}"}, "pe9:38.10": {"pli": "Tayo upavicārā nekkhammūpavicāro abyāpādūpavicāro avihiṁsūpavicāro. ", "html": "{}"}, "pe9:38.11": {"pli": "Tisso esanā kāmesanā bhavesanā brahmacariyesanā. ", "html": "{}"}, "pe9:38.12": {"pli": "Tayo khandhā sīlakkhandho samādhikkhandho paññākkhandho. ", "html": "{}</p>"}, "pe9:39.1": {"pli": "Tattha yaṁ alobho kusalamūlaṁ, taṁ sutamayipaññaṁ paripūreti, sutamayī paññā paripuṇṇā savitakkaṁ savicāraṁ samādhiṁ paripūreti, savitakko savicāro samādhi paripuṇṇo adhicittasikkhaṁ paripūreti, adhicittasikkhā paripuṇṇā samathanimittaṁ paripūreti, samathanimittaṁ paripuṇṇaṁ nekkhammavitakkaṁ paripūreti, nekkhammavitakko paripuṇṇo anaññātaññassāmītindriyaṁ paripūreti, anaññātaññassāmītindriyaṁ paripuṇṇaṁ nekkhammūpavicāraṁ paripūreti, nekkhammūpavicāro paripuṇṇo kāmesanaṁ pajahati, kāmesanappahānaṁ samādhikkhandhaṁ paripūreti. ", "html": "<p>{}</p>"}, "pe9:40.1": {"pli": "Adoso kusalamūlaṁ cintāmayipaññaṁ paripūreti, cintāmayī paññā paripuṇṇā avitakkavicāramattaṁ samādhiṁ paripūreti. ", "html": "<p>{}"}, "pe9:40.2": {"pli": "Avitakkavicāramatto samādhi paripuṇṇo adhisīlasikkhaṁ paripūreti, adhisīlasikkhā paripuṇṇā upekkhānimittaṁ paripūreti, upekkhānimittaṁ paripuṇṇaṁ abyāpādavitakkaṁ paripūreti, abyāpādavitakko paripuṇṇo aññindriyaṁ paripūreti, aññindriyaṁ paripuṇṇaṁ abyāpādūpavicāraṁ paripūreti, abyāpādūpavicāro paripuṇṇo bhavesanaṁ pajahati, bhavesanappahānaṁ sīlakkhandhaṁ paripūreti. ", "html": "{}</p>"}, "pe9:41.1": {"pli": "Amoho kusalamūlaṁ bhāvanāmayipaññaṁ paripūreti, bhāvanāmayī paññā paripuṇṇā avitakkaavicāraṁ samādhiṁ paripūreti, avitakko avicāro samādhi paripuṇṇo adhipaññāsikkhaṁ paripūreti, adhipaññāsikkhā paripuṇṇā paggahanimittaṁ paripūreti, paggahanimittaṁ paripuṇṇaṁ aññātāvino indriyaṁ paripūreti, aññātāvino indriyaṁ paripuṇṇaṁ avihiṁsūpavicāraṁ paripūreti, avihiṁsūpavicāro paripuṇṇo brahmacariyesanaṁ paripūreti, brahmacariyesanā paripuṇṇā paññākkhandhaṁ paripūreti. ", "html": "<p>{}</p>"}, "pe9:42.1": {"pli": "Iti ime tayo dhammā kusalapakkhikā sabbe kusalā dhammā tīhi tikaniddesehi niddisiyanti tīṇi vimokkhamukhāni tassa pariyosānaṁ. ", "html": "<p>{}"}, "pe9:42.2": {"pli": "Tattha paṭhamena appaṇihitaṁ, dutiyena suññataṁ, tatiyena animittaṁ. ", "html": "{}"}, "pe9:42.3": {"pli": "Ayaṁ vuccati dutiyo tipukkhalo nāma nayo. ", "html": "{}</p>"}, "pe9:43.1": {"pli": "Tattha ye ime tayo puggalā ugghaṭitaññū vipañcitaññū neyyoti. ", "html": "<p>{}"}, "pe9:43.2": {"pli": "Imesaṁ tiṇṇaṁ puggalānaṁ ye ca puggalā sukhāya paṭipadāya khippābhiññāya, sukhāya paṭipadāya dandhābhiññāya ca niyyanti, te dve puggalā. ", "html": "{}"}, "pe9:43.3": {"pli": "Ye ca dve puggalā dukkhāya paṭipadāya khippābhiññāya, dukkhāya paṭipadāya dandhābhiññāya ca niyyanti, ime cattāro tena visesena dve bhavanti diṭṭhicarito ca taṇhācarito ca. ", "html": "{}"}, "pe9:43.4": {"pli": "Ime cattāro bhavitvā tayo bhavanti, tayo bhavitvā dve bhavanti. ", "html": "{}"}, "pe9:43.5": {"pli": "Imesaṁ dvinnaṁ puggalānaṁ ayaṁ saṅkileso, avijjā ca taṇhā ca, ahirikañca anottappañca, assati ca asampajaññañca, nīvaraṇāni ca saṁyojanāni ca, ajjhosānañca abhiniveso ca, ahaṅkāro ca mamaṅkāro ca, assaddhiyañca dovacassañca, kosajjañca ayoniso ca manasikāro, vicikicchā ca abhijjhā ca, asaddhammassavanañca asamāpatti ca. ", "html": "{}</p>"}, "pe9:44.1": {"pli": "Tattha avijjā ca ahirikañca assati ca nīvaraṇāni ca ajjhosānañca ahaṅkāro ca assaddhiyañca kosajjañca vicikicchā ca asaddhammassavanañca, ayaṁ ekā disā. ", "html": "<p>{}</p>"}, "pe9:45.1": {"pli": "Taṇhā ca anottappañca asampajaññañca saṁyojanāni ca abhiniveso ca mamaṅkāro ca dovacassatā ca ayoniso manasikāro ca abhijjhā ca asamāpatti ca, ayaṁ dutiyā disā. ", "html": "<p>{}"}, "pe9:45.2": {"pli": "Dasannaṁ dukānaṁ dasa padāni paṭhamāni kātabbāni. ", "html": "{}"}, "pe9:45.3": {"pli": "Saṅkhittena atthaṁ ñāpenti paṭipakkhe kaṇhapakkhassa sabbesaṁ dukānaṁ dasa padāni dutiyakāni, ayaṁ dutiyā disā. ", "html": "{}</p>"}, "pe9:46.1": {"pli": "Iti akusalānaṁ dhammānaṁ dukkhaniddeso, ayaṁ samudayo. ", "html": "<p>{}"}, "pe9:46.2": {"pli": "Yaṁ taṁ dhammaṁ ajjhāvasati nāmañca rūpañca idaṁ dukkhaṁ iti ayañca samudayo, idañca dukkhaṁ, imāni dve saccāni dukkhañca samudayo ca nandiyāvaṭṭassa nayassa paṭhamaniddeso. ", "html": "{}</p>"}, "pe9:47.1": {"pli": "Tattha katamo kusalapakkho? ", "html": "<p>{}"}, "pe9:47.2": {"pli": "Samatho ca vipassanā ca, vijjā ca caraṇañca, sati ca sampajaññañca, hirī ca ottappañca, ahaṅkārappahānañca mamaṅkārappahānañca, sammāvāyāmo ca yoniso ca manasikāro, sammāsati ca sammāsamādhi ca, paññā ca nibbidā ca, samāpatti ca saddhammassavanañca, somanassañca dhammānudhammappaṭipatti ca. ", "html": "{}</p>"}, "pe9:48.1": {"pli": "Tattha samatho ca vijjā ca sati ca hirī ca ahaṅkārappahānañca sammāvāyāmo ca sammāsati ca paññā ca samāpatti ca somanassañca, ime dhammā ekā disā. ", "html": "<p>{}"}, "pe9:48.2": {"pli": "Vipassanā ca caraṇañca sampajaññañca ottappañca mamaṅkārappahānañca yoniso manasikāro ca sammāsamādhi ca nibbidā ca saddhammassavanañca dhammānudhammappaṭipatti ca, ayaṁ dutiyā disā. ", "html": "{}"}, "pe9:48.3": {"pli": "Iti kusalapakkhe ca akusalapakkhe ca nandiyāvaṭṭassa pana nayassa catasso disā. ", "html": "{}</p>"}, "pe9:49.1": {"pli": "Tāsu yāni akusalapakkhassa paṭhamāni padāni akusalāni kusalehi pahānaṁ gacchanti, tāni kusalapakkhe dutiyehi padehi pahānaṁ gacchanti. ", "html": "<p>{}"}, "pe9:49.2": {"pli": "Tesaṁ pahānā rāgavirāgā cetovimutti yāni akusalapakkhassa dutiyāni akusalapadāni pahānaṁ gacchanti, tāni kusalapakkhassa paṭhamehi padehi pahānaṁ gacchanti. ", "html": "{}"}, "pe9:49.3": {"pli": "Tesaṁ pahānā avijjāvirāgā paññāvimutti pariyosānaṁ. ", "html": "{}"}, "pe9:49.4": {"pli": "Imesaṁ tiṇṇaṁ nayānaṁ paṭhamo nayo sīhavikkīḷito nāma. ", "html": "{}"}, "pe9:49.5": {"pli": "Aṭṭha padāni cattāri ca kusalāni cattāri ca akusalāni imāni aṭṭha padāni mūlapadāni, atthanayena dutiyo tipukkhalo. ", "html": "{}"}, "pe9:49.6": {"pli": "So chahi dhammehi neti kusalamūlāni ca neti akusalamūlāni ca, iti imāni cha padāni purimakāni ca aṭṭha mūlapadāni imāni cuddasa padāni aṭṭhārasannaṁ mūlapadānaṁ. ", "html": "{}"}, "pe9:49.7": {"pli": "Tattha yo pacchimako nayo nandiyāvaṭṭo, so catūhi dhammehi neti. ", "html": "{}"}, "pe9:49.8": {"pli": "Avijjāya ca taṇhāya ca samathena ca vipassanāya ca, ime cattāro dhammā imāni aṭṭhārasa mūlapadāni tīsu nayesu niddiṭṭhāni. ", "html": "{}</p>"}, "pe9:50.1": {"pli": "Tattha yāni nava padāni kusalāni, tattha sabbaṁ kusalaṁ samosarati. ", "html": "<p>{}"}, "pe9:50.2": {"pli": "Tesañca navannaṁ mūlānaṁ cattāri padāni sīhavikkīḷitanaye tīṇi tipukkhale dve nandiyāvaṭṭe, iccete kusalassa pakkhā. ", "html": "{}"}, "pe9:50.3": {"pli": "Tattha yāni nava padāni kusalāni, tattha sabbaṁ kusalaṁ yujjati. ", "html": "{}"}, "pe9:50.4": {"pli": "Tattha sīhavikkīḷite naye cattāri padāni tīṇi tipukkhale dve nandiyāvaṭṭe imāni nava padāni kusalāni niddiṭṭhāni. ", "html": "{}</p>"}, "pe9:51.1": {"pli": "Tattha yāni nandiyāvaṭṭe naye cattāri padāni, tattha aṭṭhārasa mūlapadāni samosaranti. ", "html": "<p>{}"}, "pe9:51.2": {"pli": "Yathā kathaṁ, samatho ca alobho ca adoso ca asubhasaññā ca dukkhasaññā ca imāni kusalapakkhe pañca padāni samathaṁ bhajanti. ", "html": "{}"}, "pe9:51.3": {"pli": "Vipassanā ca amoho ca aniccasaññā ca anattasaññā ca imāni cattāri padāni vipassanaṁ bhajanti. ", "html": "{}"}, "pe9:51.4": {"pli": "Imāni nava padāni kusalāni dvīsu padesu yojitāni, tattha akusalapakkhe navannaṁ akusalamūlapadānaṁ yā ca taṇhā yo ca lobho yo ca doso yā ca subhasaññā yā ca sukhasaññā, imāni pañca padāni taṇhaṁ bhajanti. ", "html": "{}"}, "pe9:51.5": {"pli": "Yā ca avijjā yo ca moho yā ca niccasaññā yā ca attasaññā, imāni cattāri padāni avijjaṁ bhajanti. ", "html": "{}"}, "pe9:51.6": {"pli": "Etāni nava padāni akusalāni susaṅkhittāni. ", "html": "{}"}, "pe9:51.7": {"pli": "Iti tayo nayā ekaṁ nayaṁ na paviṭṭhā. ", "html": "{}"}, "pe9:51.8": {"pli": "Evaṁ aṭṭhārasa mūlapadāni nandiyāvaṭṭanaye niddisitabbāni. ", "html": "{}</p>"}, "pe9:52.1": {"pli": "Kathaṁ aṭṭhārasa mūlapadāni, tipukkhale naye yujjanti? ", "html": "<p>{}"}, "pe9:52.2": {"pli": "Navannaṁ padānaṁ kusalānaṁ, vipassanā ca amoho ca aniccasaññā ca anattasaññā ca, imāni cattāri padāni; ", "html": "{}"}, "pe9:52.3": {"pli": "amoho ca samatho ca alobho ca asubhasaññā ca, imāni cattāri padāni; ", "html": "{}"}, "pe9:52.4": {"pli": "lobho ca doso ca, evaṁ imāni nava padāni tīsu kusalesu yojetabbāni. ", "html": "{}"}, "pe9:52.5": {"pli": "Tattha navannaṁ padānaṁ akusalānaṁ taṇhā ca lobho ca subhasaññā ca sukhasaññā ca, imāni cattāri padāni lobho akusalamūlaṁ; ", "html": "{}"}, "pe9:52.6": {"pli": "avijjā ca moho ca niccasaññā ca attasaññā ca ayaṁ moho ayaṁ doso, ye ca imāni nava padāni tīsu akusalesu yojitāni. ", "html": "{}"}, "pe9:52.7": {"pli": "Evaṁ aṭṭhārasa mūlapadāni kusalamūlesu ca yojetvā tipukkhalena nayena niddisitabbāni. ", "html": "{}</p>"}, "pe9:53.1": {"pli": "Kathaṁ aṭṭhārasa mūlapadāni sīhavikkīḷite naye yujjanti? ", "html": "<p>{}"}, "pe9:53.2": {"pli": "Taṇhā ca subhasaññā ca, ayaṁ paṭhamo vipallāso. ", "html": "{}"}, "pe9:53.3": {"pli": "Lobho ca sukhasaññā ca, ayaṁ dutiyo vipallāso. ", "html": "{}"}, "pe9:53.4": {"pli": "Avijjā ca niccasaññā ca, ayaṁ tatiyo vipallāso. ", "html": "{}"}, "pe9:53.5": {"pli": "Moho ca attasaññā ca, ayaṁ catuttho vipallāso. ", "html": "{}"}, "pe9:53.6": {"pli": "Iti nava padāni akusalamūlāni catūsu padesu yojitāni. ", "html": "{}"}, "pe9:53.7": {"pli": "Tattha navannaṁ mūlapadānaṁ kusalānaṁ samatho ca asubhasaññā ca, idaṁ paṭhamaṁ satipaṭṭhānaṁ. ", "html": "{}"}, "pe9:53.8": {"pli": "Alobho ca dukkhasaññā ca, idaṁ dutiyaṁ satipaṭṭhānaṁ. ", "html": "{}"}, "pe9:53.9": {"pli": "Vipassanā ca aniccasaññā ca, idaṁ tatiyaṁ satipaṭṭhānaṁ. ", "html": "{}"}, "pe9:53.10": {"pli": "Amoho ca anattasaññā ca, idaṁ catutthaṁ satipaṭṭhānaṁ. ", "html": "{}"}, "pe9:53.11": {"pli": "Imāni aṭṭhārasa mūlapadāni sīhavikkīḷitanayaṁ anupaviṭṭhāni. ", "html": "{}"}, "pe9:53.12": {"pli": "Imesaṁ tiṇṇaṁ nayānaṁ yā bhūmi ca yo rāgo ca yo doso ca ekaṁ nayaṁ pavisati. ", "html": "{}"}, "pe9:53.13": {"pli": "Ekassa nayassa akusale vā dhamme kusale vā dhamme viññāte paṭipakkho anvesitabbo, paṭipakkhe anvesitvā so nayo niddisitabbo, tamhi naye niddiṭṭho. ", "html": "{}"}, "pe9:53.14": {"pli": "Yathā ekamhi naye sabbe nayā paviṭṭhā tathā niddisitabbā. ", "html": "{}"}, "pe9:53.15": {"pli": "Ekamhi ca naye aṭṭhārasa mūlapadāni paviṭṭhāni, tamhi dhamme viññāte sabbe dhammā viññātā honti. ", "html": "{}"}, "pe9:53.16": {"pli": "Imesaṁ tiṇṇaṁ nayānaṁ sīhavikkīḷitanayassa cattāri phalāni pariyosānaṁ. ", "html": "{}"}, "pe9:53.17": {"pli": "Paṭhamāya disāya paṭhamaṁ phalaṁ, dutiyāya disāya dutiyaṁ phalaṁ, tatiyāya disāya tatiyaṁ phalaṁ, catutthāya disāya catutthaṁ phalaṁ. ", "html": "{}"}, "pe9:53.18": {"pli": "Tipukkhalassa nayassa tīṇi vimokkhamukhāni pariyosānaṁ. ", "html": "{}"}, "pe9:53.19": {"pli": "Paṭhamāya disāya appaṇihitaṁ, dutiyāya disāya suññataṁ, tatiyāya disāya animittaṁ. ", "html": "{}"}, "pe9:53.20": {"pli": "Nandiyāvaṭṭassa nayassa rāgavirāgā cetovimutti avijjāvirāgā ca paññāvimutti pariyosānaṁ. ", "html": "{}"}, "pe9:53.21": {"pli": "Paṭhamāya disāya rāgavirāgā cetovimutti, dutiyāya disāya avijjāvirāgā paññāvimutti. ", "html": "{}"}, "pe9:53.22": {"pli": "Ime tayo nayā imesaṁ tiṇṇaṁ nayānaṁ aṭṭhārasannaṁ mūlapadānaṁ ālokanā, ayaṁ vuccati disālokano nayo. ", "html": "{}"}, "pe9:53.23": {"pli": "Āloketvāna jānāti “ayaṁ dhammo imaṁ dhammaṁ bhajatī”ti sammā yojanā. ", "html": "{}"}, "pe9:53.24": {"pli": "Kusalapakkhe akusalapakkhe ca ayaṁ nayo aṅkuso nāma. ", "html": "{}"}, "pe9:53.25": {"pli": "Ime pañca nayā. ", "html": "{}</p>"}, "pe9:54.1": {"pli": "Tatthimā uddānagāthā ", "html": "<p class='uddana-intro'>{}</p>"}, "pe9:55.1": {"pli": "Taṇhā ca avijjāpi ca, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "pe9:55.2": {"pli": "Lobho doso tatheva moho ca; ", "html": "<span class='verse-line'>{}</span>"}, "pe9:55.3": {"pli": "Cattāro ca vipallāsā, ", "html": "<span class='verse-line'>{}</span>"}, "pe9:55.4": {"pli": "Kilesabhūmī nava padāni. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe9:56.1": {"pli": "Ye ca satipaṭṭhānā, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe9:56.2": {"pli": "Samatho ca vipassanā kusalamūlā; ", "html": "<span class='verse-line'>{}</span>"}, "pe9:56.3": {"pli": "Etaṁ sabbaṁ kusalaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe9:56.4": {"pli": "Indriyabhūmī nava padāni. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe9:57.1": {"pli": "Sabbakusalaṁ navahi padehi yujjati, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe9:57.2": {"pli": "Navahi ceva akusalaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "pe9:57.3": {"pli": "Ete te mūlapadā, ", "html": "<span class='verse-line'>{}</span>"}, "pe9:57.4": {"pli": "Ubhato aṭṭhārasa padāni. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe9:58.1": {"pli": "Taṇhā ceva avijjā ca, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe9:58.2": {"pli": "Samatho ca vipassanā; ", "html": "<span class='verse-line'>{}</span>"}, "pe9:58.3": {"pli": "Yo neti sabbesu yogayutto, ", "html": "<span class='verse-line'>{}</span>"}, "pe9:58.4": {"pli": "Ayaṁ nayo nandiyāvaṭṭo. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe9:59.1": {"pli": "Yaṁ kusalamūlehi, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe9:59.2": {"pli": "Nayati kusalaakusalamūlehi; ", "html": "<span class='verse-line'>{}</span>"}, "pe9:59.3": {"pli": "Bhūtaṁ tathaṁ avitathaṁ, ", "html": "<span class='verse-line'>{}</span>"}, "pe9:59.4": {"pli": "Tipukkhalaṁ taṁ nayaṁ āhu. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe9:60.1": {"pli": "So neti vipallāsehi, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe9:60.2": {"pli": "Kilesaindriyehi ca; ", "html": "<span class='verse-line'>{}</span>"}, "pe9:60.3": {"pli": "Dhamme taṁ nayaṁ vinayamāhu, ", "html": "<span class='verse-line'>{}</span>"}, "pe9:60.4": {"pli": "Sīhavikkīḷitaṁ nāma. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe9:61.1": {"pli": "Veyyākaraṇe vutte, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe9:61.2": {"pli": "Kusalatāhi akusalatāhi ca; ", "html": "<span class='verse-line'>{}</span>"}, "pe9:61.3": {"pli": "Tayo ālokayati, ", "html": "<span class='verse-line'>{}</span>"}, "pe9:61.4": {"pli": "Ayaṁ nayo disālocano nāma. ", "html": "<span class='verse-line'>{}</span></p>"}, "pe9:62.1": {"pli": "Oloketvā disālocanena, ", "html": "<p><span class='verse-line'>{}</span>"}, "pe9:62.2": {"pli": "Ukkhipiya yaṁ samāneti; ", "html": "<span class='verse-line'>{}</span>"}, "pe9:62.3": {"pli": "Sabbe kusalākusale, ", "html": "<span class='verse-line'>{}</span>"}, "pe9:62.4": {"pli": "Ayaṁ nayo aṅkuso nāma. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "pe9:63.1": {"pli": "Nayasamuṭṭhānaṁ. ", "html": "<p class='endsection'>{}</p>"}, "pe9:63.2": {"pli": "Peṭakopadese mahākaccāyanassa therassa ", "html": "<p class='endsutta'>{}</p>"}, "pe9:63.3": {"pli": "suttavibhaṅgassa dassanaṁ samattaṁ. ", "html": "<p class='endsutta'>{}</p>"}, "pe9:64.1": {"pli": "Yāni catukkāni akusalāni kusalāni ca sīhavikkīḷite naye niddiṭṭhāni, tikāni kusalāni ca akusalāni ca tipukkhale naye niddiṭṭhāni, dukāni kusalāni ca akusalāni ca nandiyāvaṭṭe naye niddiṭṭhāni. ", "html": "<p><span class='verse-line'>{}</span>"}, "pe9:64.2": {"pli": "Yesu dvīsu dhammesu kusalesu so attho tikesu vibhajjamānassa bhavabhūmi, atha ca sabbo ca attho tīhi byañjanehi niddisati. ", "html": "{}"}, "pe9:64.3": {"pli": "Tattakāni vuccati. ", "html": "{}"}, "pe9:64.4": {"pli": "Yo attho catūhi padehi aṭṭhavīsatibhāgehi natthibhūmi niddisituṁ, avacarantova catūhi padehi niddisati. ", "html": "{}"}, "pe9:64.5": {"pli": "Iti yaṁ yathāniddiṭṭhassa avikosanā idaṁ pamāṇaṁ. ", "html": "{}"}, "pe9:64.6": {"pli": "Yathā sabbe samādhayo tīsu samādhīsu pariyesitabbā, savitakkasavicāre avitakkavicāramatte avitakkaavicāre idaṁ pamāṇaṁ, natthi catuttho samādhi. ", "html": "{}"}, "pe9:64.7": {"pli": "Tathā tisso paññā cintāmayī sutamayī bhāvanāmayī sabbāsu paññāsu niddisati, natthi catutthī paññā na cintāmayī na sutamayī na bhāvanāmayī, paññā nāssa atthi imesaṁ dhammānaṁ yā avikkhepanā, idaṁ vuccati pamāṇanti. ", "html": "{}</p>"}, "pe9:65.1": {"pli": "Therassa mahākaccāyanassa jambuvanavāsino peṭakopadeso samatto. ", "html": "<p class='endsutta'>{}</p>"}, "pe9:66.1": {"pli": "Peṭakopadesapakaraṇaṁ niṭṭhitaṁ. ", "html": "<p class='endbook'>{}</p></article>"}}}};