window.SUTTA_DB['sutta_kn_ud'] = {"id": "ud", "title": "Heartfelt Sayings", "structure": {"ud": {"ud-bodhivagga": ["ud1.1", "ud1.2", "ud1.3", "ud1.4", "ud1.5", "ud1.6", "ud1.7", "ud1.8", "ud1.9", "ud1.10"], "ud-mucalindavagga": ["ud2.1", "ud2.2", "ud2.3", "ud2.4", "ud2.5", "ud2.6", "ud2.7", "ud2.8", "ud2.9", "ud2.10"], "ud-nandavagga": ["ud3.1", "ud3.2", "ud3.3", "ud3.4", "ud3.5", "ud3.6", "ud3.7", "ud3.8", "ud3.9", "ud3.10"], "ud-meghiyavagga": ["ud4.1", "ud4.2", "ud4.3", "ud4.4", "ud4.5", "ud4.6", "ud4.7", "ud4.8", "ud4.9", "ud4.10"], "ud-sonavagga": ["ud5.1", "ud5.2", "ud5.3", "ud5.4", "ud5.5", "ud5.6", "ud5.7", "ud5.8", "ud5.9", "ud5.10"], "ud-jaccandhavagga": ["ud6.1", "ud6.2", "ud6.3", "ud6.4", "ud6.5", "ud6.6", "ud6.7", "ud6.8", "ud6.9", "ud6.10"], "ud-culavagga": ["ud7.1", "ud7.2", "ud7.3", "ud7.4", "ud7.5", "ud7.6", "ud7.7", "ud7.8", "ud7.9", "ud7.10"], "ud-pataligamiyavagga": ["ud8.1", "ud8.2", "ud8.3", "ud8.4", "ud8.5", "ud8.6", "ud8.7", "ud8.8", "ud8.9", "ud8.10"]}}, "meta": {"ud": {"type": "branch", "acronym": "Ud", "translated_title": "Heartfelt Sayings", "original_title": "Udāna", "blurb": "Eighty short discourses in mixed prose and verse. These take the form of a simple prose narrative, which serves to frame an inspired saying, usually in verse. The <i lang='pi' translate='no'>udāna</i> and <i lang='pi' translate='no'>dhammapada</i> literature appear to overlap, and can be considered as one genre. The Dhammapada verses also have associated narratives, but these are found in the commentary. There are some indications that some of the narratives in the Udāna may be a little later than most of the material in the four main <i lang='pi' translate='no'>nikāyas</i>.", "author_uid": null, "scroll_target": null}, "ud-bodhivagga": {"type": "branch", "acronym": "", "translated_title": "The Chapter on Awakening", "original_title": "Bodhivagga", "blurb": "This chapter begins with a series of discourses set immediately after the Buddha’s awakening, which are shared with the first chapter of the Khandhakas. It establishes the fundamental thesis of Buddhism: that awakening is possible through one’s own efforts. Each discourse deals with the idea of the true “brahmin”, defined not according to hereditary caste, but freedom from defilements.", "author_uid": null, "scroll_target": null}, "ud1.1": {"type": "leaf", "acronym": "Ud 1.1", "translated_title": "Upon Awakening (1st)", "original_title": "Paṭhamabodhisutta", "blurb": "The Buddha, soon after awakening, contemplates specific conditionality and dependent co-arising.", "author_uid": "sujato", "scroll_target": null}, "ud1.2": {"type": "leaf", "acronym": "Ud 1.2", "translated_title": "Upon Awakening (2nd)", "original_title": "Dutiyabodhisutta", "blurb": "The Buddha, soon after awakening, contemplates specific conditionality and dependent co-arising.", "author_uid": "sujato", "scroll_target": null}, "ud1.3": {"type": "leaf", "acronym": "Ud 1.3", "translated_title": "Upon Awakening (3rd)", "original_title": "Tatiyabodhisutta", "blurb": "The Buddha, soon after awakening, contemplates specific conditionality and dependent co-arising.", "author_uid": "sujato", "scroll_target": null}, "ud1.4": {"type": "leaf", "acronym": "Ud 1.4", "translated_title": "The Mystic Syllable Huṁ", "original_title": "Huṁhuṅkasutta", "blurb": "A brahmin who recites the mystic syllable huṁ asks the Buddha who is the true brahmin.", "author_uid": "sujato", "scroll_target": null}, "ud1.5": {"type": "leaf", "acronym": "Ud 1.5", "translated_title": "The Brahmin", "original_title": "Brāhmaṇasutta", "blurb": "The Buddha redefines the term “brahmin” to mean anyone, regardless of birth, who is awakened.", "author_uid": "sujato", "scroll_target": null}, "ud1.6": {"type": "leaf", "acronym": "Ud 1.6", "translated_title": "With Mahākassapa", "original_title": "Mahākassapasutta", "blurb": "Ven. Mahā Kassapa chooses to go for alms among the poor rather than among the devas.", "author_uid": "sujato", "scroll_target": null}, "ud1.7": {"type": "leaf", "acronym": "Ud 1.7", "translated_title": "At Ajakalāpaka", "original_title": "Ajakalāpakasutta", "blurb": "An awakened person has gone beyond fear.", "author_uid": "sujato", "scroll_target": null}, "ud1.8": {"type": "leaf", "acronym": "Ud 1.8", "translated_title": "With Saṅgāmaji", "original_title": "Saṅgāmajisutta", "blurb": "A monk’s former wife tries to use their child to lure him back into the lay life.", "author_uid": "sujato", "scroll_target": null}, "ud1.9": {"type": "leaf", "acronym": "Ud 1.9", "translated_title": "Dreadlocked Ascetics", "original_title": "Jaṭilasutta", "blurb": "The mind is cleansed and purified, not by ablutions, but by truth and rectitude.", "author_uid": "sujato", "scroll_target": null}, "ud1.10": {"type": "leaf", "acronym": "Ud 1.10", "translated_title": "With Bāhiya", "original_title": "Bāhiyasutta", "blurb": "The ascetic Bāhiya becomes an arahant after receiving a brief teaching about adding nothing to sensory experience.", "author_uid": "sujato", "scroll_target": null}, "ud-mucalindavagga": {"type": "branch", "acronym": "", "translated_title": "The Chapter with Mucalinda", "original_title": "Mucalindavagga", "blurb": "The Buddha’s awakening has shown him a spiritual happiness that is better than any worldly happiness. This chapter focusses on the emotional dimension of enlightenment.", "author_uid": null, "scroll_target": null}, "ud2.1": {"type": "leaf", "acronym": "Ud 2.1", "translated_title": "With Mucalinda", "original_title": "Mucalindasutta", "blurb": "Shortly after the Buddha’s awakening, a dragon protects him from a storm.", "author_uid": "sujato", "scroll_target": null}, "ud2.2": {"type": "leaf", "acronym": "Ud 2.2", "translated_title": "Kings", "original_title": "Rājasutta", "blurb": "When monks have gathered, they shouldn’t spend their time gossiping about kings.", "author_uid": "sujato", "scroll_target": null}, "ud2.3": {"type": "leaf", "acronym": "Ud 2.3", "translated_title": "A Stick", "original_title": "Daṇḍasutta", "blurb": "A lesson to young boys: If you don’t want to suffer from pain, don’t inflict pain on other beings.", "author_uid": "sujato", "scroll_target": null}, "ud2.4": {"type": "leaf", "acronym": "Ud 2.4", "translated_title": "Esteem", "original_title": "Sakkārasutta", "blurb": "On how to deal with abusive language.", "author_uid": "sujato", "scroll_target": null}, "ud2.5": {"type": "leaf", "acronym": "Ud 2.5", "translated_title": "A Lay Follower", "original_title": "Upāsakasutta", "blurb": "The drawbacks of having.", "author_uid": "sujato", "scroll_target": null}, "ud2.6": {"type": "leaf", "acronym": "Ud 2.6", "translated_title": "The Pregnant Woman", "original_title": "Gabbhinīsutta", "blurb": "More drawbacks of having.", "author_uid": "sujato", "scroll_target": null}, "ud2.7": {"type": "leaf", "acronym": "Ud 2.7", "translated_title": "An Only Son", "original_title": "Ekaputtakasutta", "blurb": "The sorrow that comes from having those who are dear.", "author_uid": "sujato", "scroll_target": null}, "ud2.8": {"type": "leaf", "acronym": "Ud 2.8", "translated_title": "Suppavāsā", "original_title": "Suppavāsāsutta", "blurb": "After a long and difficult pregnancy, Suppavāsā invites the Buddha and the Sangha for a meal.", "author_uid": "sujato", "scroll_target": null}, "ud2.9": {"type": "leaf", "acronym": "Ud 2.9", "translated_title": "With Visākhā", "original_title": "Visākhāsutta", "blurb": "The drawbacks of having to do business with kings.", "author_uid": "sujato", "scroll_target": null}, "ud2.10": {"type": "leaf", "acronym": "Ud 2.10", "translated_title": "With Bhaddiya", "original_title": "Bhaddiyasutta", "blurb": "A former king, now monk, exclaims over the bliss of solitude.", "author_uid": "sujato", "scroll_target": null}, "ud-nandavagga": {"type": "branch", "acronym": "", "translated_title": "The Chapter with Nanda", "original_title": "Nandavagga", "blurb": "The discourses in this chapter focus on equanimity in the face of pleasure and pain. There are several examples of monastic conduct of poise and balance.", "author_uid": null, "scroll_target": null}, "ud3.1": {"type": "leaf", "acronym": "Ud 3.1", "translated_title": "Born of the Fruits of Deeds", "original_title": "Kammavipākajasutta", "blurb": "A monk meditates in pain engendered by past kamma but without being struck down by it.", "author_uid": "sujato", "scroll_target": null}, "ud3.2": {"type": "leaf", "acronym": "Ud 3.2", "translated_title": "With Nanda", "original_title": "Nandasutta", "blurb": "The Buddha makes a deal with his step-brother: Stay a monk, and you’ll be rewarded with nymphs in the next life.", "author_uid": "sujato", "scroll_target": null}, "ud3.3": {"type": "leaf", "acronym": "Ud 3.3", "translated_title": "With Yasoja", "original_title": "Yasojasutta", "blurb": "A group of monks, rebuked by the Buddha, take it as an encouragement to reach awakening.", "author_uid": "sujato", "scroll_target": null}, "ud3.4": {"type": "leaf", "acronym": "Ud 3.4", "translated_title": "With Sāriputta", "original_title": "Sāriputtasutta", "blurb": "Ven. Sāriputta meditates, his mind as solid as rock.", "author_uid": "sujato", "scroll_target": null}, "ud3.5": {"type": "leaf", "acronym": "Ud 3.5", "translated_title": "With Mahāmoggallāna", "original_title": "Mahāmoggallānasutta", "blurb": "Ven. Mahā Moggallāna meditates.", "author_uid": "sujato", "scroll_target": null}, "ud3.6": {"type": "leaf", "acronym": "Ud 3.6", "translated_title": "With Pilindavaccha", "original_title": "Pilindavacchasutta", "blurb": "Some old habits die hard.", "author_uid": "sujato", "scroll_target": null}, "ud3.7": {"type": "leaf", "acronym": "Ud 3.7", "translated_title": "Sakka’s Heartfelt Saying", "original_title": "Sakkudānasutta", "blurb": "A deva-king disguises himself to give alms to Ven. Mahā Kassapa.", "author_uid": "sujato", "scroll_target": null}, "ud3.8": {"type": "leaf", "acronym": "Ud 3.8", "translated_title": "One Who Eats Only Almsfood", "original_title": "Piṇḍapātikasutta", "blurb": "The Buddha rebukes monks who are chatting about the agreeable things that one can encounter on almsround.", "author_uid": "sujato", "scroll_target": null}, "ud3.9": {"type": "leaf", "acronym": "Ud 3.9", "translated_title": "Professions", "original_title": "Sippasutta", "blurb": "More lessons in what monks should and shouldn’t talk about.", "author_uid": "sujato", "scroll_target": null}, "ud3.10": {"type": "leaf", "acronym": "Ud 3.10", "translated_title": "The World", "original_title": "Lokasutta", "blurb": "Shortly after awakening, the Buddha contemplates future rebirth.", "author_uid": "sujato", "scroll_target": null}, "ud-meghiyavagga": {"type": "branch", "acronym": "", "translated_title": "The Chapter with Meghiya", "original_title": "Meghiyavagga", "blurb": "If a practitioner fails to achieve such balance, their undisciplined mind leads to harming themselves and others.", "author_uid": null, "scroll_target": null}, "ud4.1": {"type": "leaf", "acronym": "Ud 4.1", "translated_title": "With Meghiya", "original_title": "Meghiyasutta", "blurb": "A monk leaves the Buddha to go into solitude, only to find his mind overcome by unskillful thoughts.", "author_uid": "sujato", "scroll_target": null}, "ud4.2": {"type": "leaf", "acronym": "Ud 4.2", "translated_title": "Restless", "original_title": "Uddhatasutta", "blurb": "The need to protect body and mind.", "author_uid": "sujato", "scroll_target": null}, "ud4.3": {"type": "leaf", "acronym": "Ud 4.3", "translated_title": "The Cowherd", "original_title": "Gopālakasutta", "blurb": "A cowherd is murdered after presenting a meal to the Buddha and the Sangha.", "author_uid": "sujato", "scroll_target": null}, "ud4.4": {"type": "leaf", "acronym": "Ud 4.4", "translated_title": "The Spirit’s Blow", "original_title": "Yakkhapahārasutta", "blurb": "A spirit gives Ven. Sāriputta a blow on the head.", "author_uid": "sujato", "scroll_target": null}, "ud4.5": {"type": "leaf", "acronym": "Ud 4.5", "translated_title": "A Bull Elephant", "original_title": "Nāgasutta", "blurb": "The Buddha, hemmed in by his followers, goes into seclusion.", "author_uid": "sujato", "scroll_target": null}, "ud4.6": {"type": "leaf", "acronym": "Ud 4.6", "translated_title": "The Alms-Gatherer", "original_title": "Piṇḍolasutta", "blurb": "Ven. Piṇḍola Bhāradvāja meditates.", "author_uid": "sujato", "scroll_target": null}, "ud4.7": {"type": "leaf", "acronym": "Ud 4.7", "translated_title": "With Sāriputta", "original_title": "Sāriputtasutta", "blurb": "Ven. Sāriputta meditates.", "author_uid": "sujato", "scroll_target": null}, "ud4.8": {"type": "leaf", "acronym": "Ud 4.8", "translated_title": "With Sundarī", "original_title": "Sundarīsutta", "blurb": "Wanderers of other sects kill a woman and blame her murder on the Sangha.", "author_uid": "sujato", "scroll_target": null}, "ud4.9": {"type": "leaf", "acronym": "Ud 4.9", "translated_title": "With Upasena", "original_title": "Upasenasutta", "blurb": "Ven. Upasena Vaṅgantaputta contemplates: “Fortunate has been my life; fortunate will be my death.”", "author_uid": "sujato", "scroll_target": null}, "ud4.10": {"type": "leaf", "acronym": "Ud 4.10", "translated_title": "The Peacefulness of Sāriputta", "original_title": "Sāriputtaupasamasutta", "blurb": "Ven. Sāriputta meditates.", "author_uid": "sujato", "scroll_target": null}, "ud-sonavagga": {"type": "branch", "acronym": "", "translated_title": "The Chapter with Soṇa", "original_title": "Soṇavagga", "blurb": "Broadening the focus from the individual practitioner, this chapter deals with the “all”, and shows how the Dhamma is inclusive of all beings, from kings to little fish.", "author_uid": null, "scroll_target": null}, "ud5.1": {"type": "leaf", "acronym": "Ud 5.1", "translated_title": "Who Is More Dear?", "original_title": "Piyatarasutta", "blurb": "King Pasenadi asks his queen, “Is there anyone dearer to you than yourself?”", "author_uid": "sujato", "scroll_target": null}, "ud5.2": {"type": "leaf", "acronym": "Ud 5.2", "translated_title": "Short-lived", "original_title": "Appāyukasutta", "blurb": "Ven. Ānanda comments on how the Buddha’s mother died shortly after his birth.", "author_uid": "sujato", "scroll_target": null}, "ud5.3": {"type": "leaf", "acronym": "Ud 5.3", "translated_title": "With Suppabuddha the Leper", "original_title": "Suppabuddhakuṭṭhisutta", "blurb": "A leper becomes a stream-enterer, dies, and is reborn as a deva.", "author_uid": "sujato", "scroll_target": null}, "ud5.4": {"type": "leaf", "acronym": "Ud 5.4", "translated_title": "The Boys", "original_title": "Kumārakasutta", "blurb": "A lesson to young boys: If you don’t want to suffer from pain, don’t inflict pain on other beings.", "author_uid": "sujato", "scroll_target": null}, "ud5.5": {"type": "leaf", "acronym": "Ud 5.5", "translated_title": "Sabbath", "original_title": "Uposathasutta", "blurb": "Ven. Mahā Moggallāna expels a sham monk from a meeting of the Sangha.", "author_uid": "sujato", "scroll_target": null}, "ud5.6": {"type": "leaf", "acronym": "Ud 5.6", "translated_title": "With Soṇa", "original_title": "Soṇasutta", "blurb": "A young man in a remote part of India is able to ordain only after many delays.", "author_uid": "sujato", "scroll_target": null}, "ud5.7": {"type": "leaf", "acronym": "Ud 5.7", "translated_title": "With Revata the Doubter", "original_title": "Kaṅkhārevatasutta", "blurb": "Ven. Revata meditates.", "author_uid": "sujato", "scroll_target": null}, "ud5.8": {"type": "leaf", "acronym": "Ud 5.8", "translated_title": "Schism in the Saṅgha", "original_title": "Saṁghabhedasutta", "blurb": "Devadatta announces that he will cause a split in the Sangha.", "author_uid": "sujato", "scroll_target": null}, "ud5.9": {"type": "leaf", "acronym": "Ud 5.9", "translated_title": "Teasing", "original_title": "Sadhāyamānasutta", "blurb": "A group of youths jeer at the monks.", "author_uid": "sujato", "scroll_target": null}, "ud5.10": {"type": "leaf", "acronym": "Ud 5.10", "translated_title": "With Cūḷapanthaka", "original_title": "Cūḷapanthakasutta", "blurb": "Ven. Cūḷa Panthaka meditates.", "author_uid": "sujato", "scroll_target": null}, "ud-jaccandhavagga": {"type": "branch", "acronym": "", "translated_title": "The Chapter on Blind From Birth", "original_title": "Jaccandhavagga", "blurb": "Returning to stories about the Buddha’s life, this chapter skips ahead to near the end. Meanwhile, it reminds us of those who have lost their way.", "author_uid": null, "scroll_target": null}, "ud6.1": {"type": "leaf", "acronym": "Ud 6.1", "translated_title": "Surrendering the Life Force", "original_title": "Āyusaṅkhārossajjanasutta", "blurb": "The Buddha relinquishes the forces that will keep him living.", "author_uid": "sujato", "scroll_target": null}, "ud6.2": {"type": "leaf", "acronym": "Ud 6.2", "translated_title": "Seven Matted-Hair Ascetics", "original_title": "Sattajaṭilasutta", "blurb": "How to know another person’s character.", "author_uid": "sujato", "scroll_target": null}, "ud6.3": {"type": "leaf", "acronym": "Ud 6.3", "translated_title": "The Buddha’s Reviewing", "original_title": "Paccavekkhaṇasutta", "blurb": "The Buddha reflects on the unskillful qualities that he has abandoned.", "author_uid": "sujato", "scroll_target": null}, "ud6.4": {"type": "leaf", "acronym": "Ud 6.4", "translated_title": "Followers of Various Other Religions (1st)", "original_title": "Paṭhamanānātitthiyasutta", "blurb": "The blind people and the elephant.", "author_uid": "sujato", "scroll_target": null}, "ud6.5": {"type": "leaf", "acronym": "Ud 6.5", "translated_title": "Followers of Various Other Religions (2nd)", "original_title": "Dutiyanānātitthiyasutta", "blurb": "Wanderers of other sects dispute over the self, pleasure, pain, and the nature of the world.", "author_uid": "sujato", "scroll_target": null}, "ud6.6": {"type": "leaf", "acronym": "Ud 6.6", "translated_title": "Followers of Various Other Religions (3rd)", "original_title": "Tatiyanānātitthiyasutta", "blurb": "Wanderers of other sects dispute over the self, pleasure, pain, and the nature of the world.", "author_uid": "sujato", "scroll_target": null}, "ud6.7": {"type": "leaf", "acronym": "Ud 6.7", "translated_title": "With Subhūti", "original_title": "Subhūtisutta", "blurb": "Ven. Subhūti meditates.", "author_uid": "sujato", "scroll_target": null}, "ud6.8": {"type": "leaf", "acronym": "Ud 6.8", "translated_title": "The Courtesan", "original_title": "Gaṇikāsutta", "blurb": "Two factions fight over a courtesan.", "author_uid": "sujato", "scroll_target": null}, "ud6.9": {"type": "leaf", "acronym": "Ud 6.9", "translated_title": "Hastening By", "original_title": "Upātidhāvantisutta", "blurb": "Insects fly into the flames of lamps set out at night.", "author_uid": "sujato", "scroll_target": null}, "ud6.10": {"type": "leaf", "acronym": "Ud 6.10", "translated_title": "Arising", "original_title": "Uppajjantisutta", "blurb": "Other sectarians shine only as long as a Buddha hasn’t appeared in the world.", "author_uid": "sujato", "scroll_target": null}, "ud-culavagga": {"type": "branch", "acronym": "", "translated_title": "The Lesser Chapter", "original_title": "Cūḷavagga", "blurb": "This chapter returns to the awakening of the Buddha, and employing water-based imagery, it reinforces the power of the Dhamma to carry us across the flood.", "author_uid": null, "scroll_target": null}, "ud7.1": {"type": "leaf", "acronym": "Ud 7.1", "translated_title": "Bhaddiya the Dwarf (1st)", "original_title": "Paṭhamalakuṇḍakabhaddiyasutta", "blurb": "A dwarf becomes an arahant.", "author_uid": "sujato", "scroll_target": null}, "ud7.2": {"type": "leaf", "acronym": "Ud 7.2", "translated_title": "Bhaddiya the Dwarf (2nd)", "original_title": "Dutiyalakuṇḍakabhaddiyasutta", "blurb": "Ven. Sāriputta doesn’t realize that his listener has already become an arahant.", "author_uid": "sujato", "scroll_target": null}, "ud7.3": {"type": "leaf", "acronym": "Ud 7.3", "translated_title": "Clinging (1st)", "original_title": "Paṭhamasattasutta", "blurb": "Attachment to sensual pleasures keeps you from crossing over the flood.", "author_uid": "sujato", "scroll_target": null}, "ud7.4": {"type": "leaf", "acronym": "Ud 7.4", "translated_title": "Clinging (2nd)", "original_title": "Dutiyasattasutta", "blurb": "Attachment to sensual pleasures keeps you trapped like a fish.", "author_uid": "sujato", "scroll_target": null}, "ud7.5": {"type": "leaf", "acronym": "Ud 7.5", "translated_title": "Another Discourse with Bhaddiya the Dwarf", "original_title": "Aparalakuṇḍakabhaddiyasutta", "blurb": "Inner and outer beauty.", "author_uid": "sujato", "scroll_target": null}, "ud7.6": {"type": "leaf", "acronym": "Ud 7.6", "translated_title": "The Ending of Craving", "original_title": "Taṇhāsaṅkhayasutta", "blurb": "Ven. Aññata Koṇḍañña meditates.", "author_uid": "sujato", "scroll_target": null}, "ud7.7": {"type": "leaf", "acronym": "Ud 7.7", "translated_title": "The Ending of Proliferation", "original_title": "Papañcakhayasutta", "blurb": "The Buddha contemplates his own abandoning of the perceptions and categories of proliferation.", "author_uid": "sujato", "scroll_target": null}, "ud7.8": {"type": "leaf", "acronym": "Ud 7.8", "translated_title": "Kaccāna", "original_title": "Kaccānasutta", "blurb": "Ven. Mahā Kaccāyana meditates.", "author_uid": "sujato", "scroll_target": null}, "ud7.9": {"type": "leaf", "acronym": "Ud 7.9", "translated_title": "The Well", "original_title": "Udapānasutta", "blurb": "Wanderers of other sects try to keep the Buddha from drinking the water in a well.", "author_uid": "sujato", "scroll_target": null}, "ud7.10": {"type": "leaf", "acronym": "Ud 7.10", "translated_title": "King Udena", "original_title": "Utenasutta", "blurb": "Five hundred awakened women die in a fire.", "author_uid": "sujato", "scroll_target": null}, "ud-pataligamiyavagga": {"type": "branch", "acronym": "", "translated_title": "The Chapter with the Pāṭali Villagers", "original_title": "Pāṭaligāmiyavagga", "blurb": "The final chapter deals with the Buddha’s final passing away and the crisis of faith that this created. It begins with series of solemn declarations on Nibbana and ends with the spectacular passing of the monk Dabba. ", "author_uid": null, "scroll_target": null}, "ud8.1": {"type": "leaf", "acronym": "Ud 8.1", "translated_title": "About Extinguishment (1st)", "original_title": "Paṭhamanibbānapaṭisaṁyuttasutta", "blurb": "The nature of extinguishment.", "author_uid": "sujato", "scroll_target": null}, "ud8.2": {"type": "leaf", "acronym": "Ud 8.2", "translated_title": "About Extinguishment (2nd)", "original_title": "Dutiyanibbānapaṭisaṁyuttasutta", "blurb": "On seeing extinguishment.", "author_uid": "sujato", "scroll_target": null}, "ud8.3": {"type": "leaf", "acronym": "Ud 8.3", "translated_title": "About Extinguishment (3rd)", "original_title": "Tatiyanibbānapaṭisaṁyuttasutta", "blurb": "The escape from conditions is areal.", "author_uid": "sujato", "scroll_target": null}, "ud8.4": {"type": "leaf", "acronym": "Ud 8.4", "translated_title": "About Extinguishment (4th)", "original_title": "Catutthanibbānapaṭisaṁyuttasutta", "blurb": "Extinguishment as independence.", "author_uid": "sujato", "scroll_target": null}, "ud8.5": {"type": "leaf", "acronym": "Ud 8.5", "translated_title": "With Cunda", "original_title": "Cundasutta", "blurb": "The Buddha’s last meal.", "author_uid": "sujato", "scroll_target": null}, "ud8.6": {"type": "leaf", "acronym": "Ud 8.6", "translated_title": "The Layfolk of Pāṭali Village", "original_title": "Pāṭaligāmiyasutta", "blurb": "The rewards of virtue and of dedicating merit to the devas.", "author_uid": "sujato", "scroll_target": null}, "ud8.7": {"type": "leaf", "acronym": "Ud 8.7", "translated_title": "A Fork in the Road", "original_title": "Dvidhāpathasutta", "blurb": "One of the Buddha’s attendants disobeys him.", "author_uid": "sujato", "scroll_target": null}, "ud8.8": {"type": "leaf", "acronym": "Ud 8.8", "translated_title": "With Visākhā", "original_title": "Visākhāsutta", "blurb": "Lady Visākhā wishes for many grandchildren.", "author_uid": "sujato", "scroll_target": null}, "ud8.9": {"type": "leaf", "acronym": "Ud 8.9", "translated_title": "With Dabba (1st)", "original_title": "Paṭhamadabbasutta", "blurb": "Ven. Dabba Mallaputta performs a miracle on his death.", "author_uid": "sujato", "scroll_target": null}, "ud8.10": {"type": "leaf", "acronym": "Ud 8.10", "translated_title": "Dabba (2nd)", "original_title": "Dutiyadabbasutta", "blurb": "There’s no destination to describe for those rightly released.", "author_uid": "sujato", "scroll_target": null}}, "content": {"ud1.1": {"ud1.1:0.1": {"pli": "Udāna 1.1 ", "eng": "Heartfelt Sayings 1.1 ", "html": "<article id='ud1.1'><header><ul><li class='division'>{}</li></ul>"}, "ud1.1:0.2": {"pli": "Paṭhamabodhisutta ", "eng": "Upon Awakening (1st) ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud1.1:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud1.1:1.2": {"pli": "ekaṁ samayaṁ bhagavā uruvelāyaṁ viharati najjā nerañjarāya tīre bodhirukkhamūle paṭhamābhisambuddho. ", "eng": "At one time, when he was first awakened, the Buddha was staying in Uruvelā at the root of the tree of awakening on the bank of the Nerañjarā River. ", "html": "{}"}, "ud1.1:1.3": {"pli": "Tena kho pana samayena bhagavā sattāhaṁ ekapallaṅkena nisinno hoti vimuttisukhapaṭisaṁvedī. ", "eng": "There the Buddha sat cross-legged for seven days without moving, experiencing the bliss of freedom. ", "html": "{}"}, "ud1.1:1.4": {"pli": "Atha kho bhagavā tassa sattāhassa accayena tamhā samādhimhā vuṭṭhahitvā rattiyā paṭhamaṁ yāmaṁ paṭiccasamuppādaṁ anulomaṁ sādhukaṁ manasākāsi: ", "eng": "When seven days had passed, the Buddha emerged from that state of immersion. In the first part of the night, he carefully applied the mind to dependent origination in forward order: ", "html": "{}</p>"}, "ud1.1:2.1": {"pli": "“Iti imasmiṁ sati idaṁ hoti, imassuppādā idaṁ uppajjati, yadidaṁ—", "eng": "“When this exists, this comes to be; due to the arising of this, this arises. That is: ", "html": "<p>{}"}, "ud1.1:2.2": {"pli": "avijjāpaccayā saṅkhārā, ", "eng": "Ignorance is a requirement for choices. ", "html": "{}"}, "ud1.1:2.3": {"pli": "saṅkhārapaccayā viññāṇaṁ, ", "eng": "Choices are a requirement for consciousness. ", "html": "{}"}, "ud1.1:2.4": {"pli": "viññāṇapaccayā nāmarūpaṁ, ", "eng": "Consciousness is a requirement for name and form. ", "html": "{}"}, "ud1.1:2.5": {"pli": "nāmarūpapaccayā saḷāyatanaṁ, ", "eng": "Name and form are requirements for the six sense fields. ", "html": "{}"}, "ud1.1:2.6": {"pli": "saḷāyatanapaccayā phasso, ", "eng": "The six sense fields are requirements for contact. ", "html": "{}"}, "ud1.1:2.7": {"pli": "phassapaccayā vedanā, ", "eng": "Contact is a requirement for feeling. ", "html": "{}"}, "ud1.1:2.8": {"pli": "vedanāpaccayā taṇhā, ", "eng": "Feeling is a requirement for craving. ", "html": "{}"}, "ud1.1:2.9": {"pli": "taṇhāpaccayā upādānaṁ, ", "eng": "Craving is a requirement for grasping. ", "html": "{}"}, "ud1.1:2.10": {"pli": "upādānapaccayā bhavo, ", "eng": "Grasping is a requirement for continued existence. ", "html": "{}"}, "ud1.1:2.11": {"pli": "bhavapaccayā jāti, ", "eng": "Continued existence is a requirement for rebirth. ", "html": "{}"}, "ud1.1:2.12": {"pli": "jātipaccayā jarāmaraṇaṁ sokaparidevadukkhadomanassupāyāsā sambhavanti. ", "eng": "Rebirth is a requirement for old age and death, sorrow, lamentation, pain, sadness, and distress to come to be. ", "html": "{}"}, "ud1.1:2.13": {"pli": "Evametassa kevalassa dukkhakkhandhassa samudayo hotī”ti. ", "eng": "That is how this entire mass of suffering originates.” ", "html": "{}</p>"}, "ud1.1:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud1.1:4.1": {"pli": "“Yadā have pātubhavanti dhammā, ", "eng": "“When things become clear ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud1.1:4.2": {"pli": "Ātāpino jhāyato brāhmaṇassa; ", "eng": "to the keen, meditating brahmin, ", "html": "<span class='verse-line'>{}</span>"}, "ud1.1:4.3": {"pli": "Athassa kaṅkhā vapayanti sabbā, ", "eng": "his doubts are dispelled, ", "html": "<span class='verse-line'>{}</span>"}, "ud1.1:4.4": {"pli": "Yato pajānāti sahetudhamman”ti. ", "eng": "since he understands each thing and its cause.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud1.1:4.5": {"pli": "Paṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud1.2": {"ud1.2:0.1": {"pli": "Udāna 1.2 ", "eng": "Heartfelt Sayings 1.2 ", "html": "<article id='ud1.2'><header><ul><li class='division'>{}</li></ul>"}, "ud1.2:0.2": {"pli": "Dutiyabodhisutta ", "eng": "Upon Awakening (2nd) ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud1.2:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud1.2:1.2": {"pli": "ekaṁ samayaṁ bhagavā uruvelāyaṁ viharati najjā nerañjarāya tīre bodhirukkhamūle paṭhamābhisambuddho. ", "eng": "At one time, when he was first awakened, the Buddha was staying in Uruvelā at the root of the tree of awakening on the bank of the Nerañjarā River. ", "html": "{}"}, "ud1.2:1.3": {"pli": "Tena kho pana samayena bhagavā sattāhaṁ ekapallaṅkena nisinno hoti vimuttisukhapaṭisaṁvedī. ", "eng": "There the Buddha sat cross-legged for seven days without moving, experiencing the bliss of freedom. ", "html": "{}"}, "ud1.2:1.4": {"pli": "Atha kho bhagavā tassa sattāhassa accayena tamhā samādhimhā vuṭṭhahitvā rattiyā majjhimaṁ yāmaṁ paṭiccasamuppādaṁ paṭilomaṁ sādhukaṁ manasākāsi: ", "eng": "When seven days had passed, the Buddha emerged from that state of immersion. In the second part of the night, he carefully applied the mind to dependent origination in reverse order: ", "html": "{}</p>"}, "ud1.2:2.1": {"pli": "“Iti imasmiṁ asati idaṁ na hoti, imassa nirodhā idaṁ nirujjhati, yadidaṁ—", "eng": "“When this doesn’t exist, this doesn’t come to be; due to the cessation of this, this ceases. That is: ", "html": "<p>{}"}, "ud1.2:2.2": {"pli": "avijjānirodhā saṅkhāranirodho, ", "eng": "When ignorance ceases, choices cease. ", "html": "{}"}, "ud1.2:2.3": {"pli": "saṅkhāranirodhā viññāṇanirodho, ", "eng": "When choices cease, consciousness ceases. ", "html": "{}"}, "ud1.2:2.4": {"pli": "viññāṇanirodhā nāmarūpanirodho, ", "eng": "When consciousness ceases, name and form cease. ", "html": "{}"}, "ud1.2:2.5": {"pli": "nāmarūpanirodhā saḷāyatananirodho, ", "eng": "When name and form cease, the six sense fields cease. ", "html": "{}"}, "ud1.2:2.6": {"pli": "saḷāyatananirodhā phassanirodho, ", "eng": "When the six sense fields cease, contact ceases. ", "html": "{}"}, "ud1.2:2.7": {"pli": "phassanirodhā vedanānirodho, ", "eng": "When contact ceases, feeling ceases. ", "html": "{}"}, "ud1.2:2.8": {"pli": "vedanānirodhā taṇhānirodho, ", "eng": "When feeling ceases, craving ceases. ", "html": "{}"}, "ud1.2:2.9": {"pli": "taṇhānirodhā upādānanirodho, ", "eng": "When craving ceases, grasping ceases. ", "html": "{}"}, "ud1.2:2.10": {"pli": "upādānanirodhā bhavanirodho, ", "eng": "When grasping ceases, continued existence ceases. ", "html": "{}"}, "ud1.2:2.11": {"pli": "bhavanirodhā jātinirodho, ", "eng": "When continued existence ceases, rebirth ceases. ", "html": "{}"}, "ud1.2:2.12": {"pli": "jātinirodhā jarāmaraṇaṁ sokaparidevadukkhadomanassupāyāsā nirujjhanti. ", "eng": "When rebirth ceases, old age and death, sorrow, lamentation, pain, sadness, and distress cease. ", "html": "{}"}, "ud1.2:2.13": {"pli": "Evametassa kevalassa dukkhakkhandhassa nirodho hotī”ti. ", "eng": "That is how this entire mass of suffering ceases.” ", "html": "{}</p>"}, "ud1.2:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud1.2:4.1": {"pli": "“Yadā have pātubhavanti dhammā, ", "eng": "“When things become clear ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud1.2:4.2": {"pli": "Ātāpino jhāyato brāhmaṇassa; ", "eng": "to the keen, meditating brahmin, ", "html": "<span class='verse-line'>{}</span>"}, "ud1.2:4.3": {"pli": "Athassa kaṅkhā vapayanti sabbā, ", "eng": "his doubts are dispelled, ", "html": "<span class='verse-line'>{}</span>"}, "ud1.2:4.4": {"pli": "Yato khayaṁ paccayānaṁ avedī”ti. ", "eng": "since he’s known the end of conditions.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud1.2:4.5": {"pli": "Dutiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud1.3": {"ud1.3:0.1": {"pli": "Udāna 1.3 ", "eng": "Heartfelt Sayings 1.3 ", "html": "<article id='ud1.3'><header><ul><li class='division'>{}</li></ul>"}, "ud1.3:0.2": {"pli": "Tatiyabodhisutta ", "eng": "Upon Awakening (3rd) ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud1.3:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud1.3:1.2": {"pli": "ekaṁ samayaṁ bhagavā uruvelāyaṁ viharati najjā nerañjarāya tīre bodhirukkhamūle paṭhamābhisambuddho. ", "eng": "At one time, when he was first awakened, the Buddha was staying in Uruvelā at the root of the tree of awakening on the bank of the Nerañjarā River. ", "html": "{}"}, "ud1.3:1.3": {"pli": "Tena kho pana samayena bhagavā sattāhaṁ ekapallaṅkena nisinno hoti vimuttisukhapaṭisaṁvedī. ", "eng": "There the Buddha sat cross-legged for seven days without moving, experiencing the bliss of freedom. ", "html": "{}"}, "ud1.3:1.4": {"pli": "Atha kho bhagavā tassa sattāhassa accayena tamhā samādhimhā vuṭṭhahitvā rattiyā pacchimaṁ yāmaṁ paṭiccasamuppādaṁ anulomapaṭilomaṁ sādhukaṁ manasākāsi: ", "eng": "When seven days had passed, the Buddha emerged from that state of immersion. In the last watch, he carefully applied the mind to dependent origination in forward and reverse order: ", "html": "{}</p>"}, "ud1.3:2.1": {"pli": "“Iti imasmiṁ sati idaṁ hoti, imassuppādā idaṁ uppajjati, ", "eng": "“When this exists, this comes to be; due to the arising of this, this arises. ", "html": "<p>{}"}, "ud1.3:2.2": {"pli": "imasmiṁ asati idaṁ na hoti, imassa nirodhā idaṁ nirujjhati; yadidaṁ—", "eng": "When this doesn’t exist, this doesn’t come to be; due to the cessation of this, this ceases. That is: ", "html": "{}"}, "ud1.3:2.3": {"pli": "avijjāpaccayā saṅkhārā, ", "eng": "Ignorance is a requirement for choices. ", "html": "{}"}, "ud1.3:2.4": {"pli": "saṅkhārapaccayā viññāṇaṁ, ", "eng": "Choices are a requirement for consciousness. ", "html": "{}"}, "ud1.3:2.5": {"pli": "viññāṇapaccayā nāmarūpaṁ, ", "eng": "Consciousness is a requirement for name and form. ", "html": "{}"}, "ud1.3:2.6": {"pli": "nāmarūpapaccayā saḷāyatanaṁ, ", "eng": "Name and form are requirements for the six sense fields. ", "html": "{}"}, "ud1.3:2.7": {"pli": "saḷāyatanapaccayā phasso, ", "eng": "The six sense fields are requirements for contact. ", "html": "{}"}, "ud1.3:2.8": {"pli": "phassapaccayā vedanā, ", "eng": "Contact is a requirement for feeling. ", "html": "{}"}, "ud1.3:2.9": {"pli": "vedanāpaccayā taṇhā, ", "eng": "Feeling is a requirement for craving. ", "html": "{}"}, "ud1.3:2.10": {"pli": "taṇhāpaccayā upādānaṁ, ", "eng": "Craving is a requirement for grasping. ", "html": "{}"}, "ud1.3:2.11": {"pli": "upādānapaccayā bhavo, ", "eng": "Grasping is a requirement for continued existence. ", "html": "{}"}, "ud1.3:2.12": {"pli": "bhavapaccayā jāti, ", "eng": "Continued existence is a requirement for rebirth. ", "html": "{}"}, "ud1.3:2.13": {"pli": "jātipaccayā jarāmaraṇaṁ sokaparidevadukkhadomanassupāyāsā sambhavanti. ", "eng": "Rebirth is a requirement for old age and death, sorrow, lamentation, pain, sadness, and distress to come to be. ", "html": "{}"}, "ud1.3:2.14": {"pli": "Evametassa kevalassa dukkhakkhandhassa samudayo hoti. ", "eng": "That is how this entire mass of suffering originates. ", "html": "{}</p>"}, "ud1.3:3.1": {"pli": "Avijjāya tveva asesavirāganirodhā saṅkhāranirodho, ", "eng": "When ignorance fades away and ceases with nothing left over, choices cease. ", "html": "<p>{}"}, "ud1.3:3.2": {"pli": "saṅkhāranirodhā viññāṇanirodho, ", "eng": "When choices cease, consciousness ceases. ", "html": "{}"}, "ud1.3:3.3": {"pli": "viññāṇanirodhā nāmarūpanirodho, ", "eng": "When consciousness ceases, name and form cease. ", "html": "{}"}, "ud1.3:3.4": {"pli": "nāmarūpanirodhā saḷāyatananirodho, ", "eng": "When name and form cease, the six sense fields cease. ", "html": "{}"}, "ud1.3:3.5": {"pli": "saḷāyatananirodhā phassanirodho, ", "eng": "When the six sense fields cease, contact ceases. ", "html": "{}"}, "ud1.3:3.6": {"pli": "phassanirodhā vedanānirodho, ", "eng": "When contact ceases, feeling ceases. ", "html": "{}"}, "ud1.3:3.7": {"pli": "vedanānirodhā taṇhānirodho, ", "eng": "When feeling ceases, craving ceases. ", "html": "{}"}, "ud1.3:3.8": {"pli": "taṇhānirodhā upādānanirodho, ", "eng": "When craving ceases, grasping ceases. ", "html": "{}"}, "ud1.3:3.9": {"pli": "upādānanirodhā bhavanirodho, ", "eng": "When grasping ceases, continued existence ceases. ", "html": "{}"}, "ud1.3:3.10": {"pli": "bhavanirodhā jātinirodho, ", "eng": "When continued existence ceases, rebirth ceases. ", "html": "{}"}, "ud1.3:3.11": {"pli": "jātinirodhā jarāmaraṇaṁ sokaparidevadukkhadomanassupāyāsā nirujjhanti. ", "eng": "When rebirth ceases, old age and death, sorrow, lamentation, pain, sadness, and distress cease. ", "html": "{}"}, "ud1.3:3.12": {"pli": "Evametassa kevalassa dukkhakkhandhassa nirodho hotī”ti. ", "eng": "That is how this entire mass of suffering ceases.” ", "html": "{}</p>"}, "ud1.3:4.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud1.3:5.1": {"pli": "“Yadā have pātubhavanti dhammā, ", "eng": "“When things become clear ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud1.3:5.2": {"pli": "Ātāpino jhāyato brāhmaṇassa; ", "eng": "to the keen, meditating brahmin, ", "html": "<span class='verse-line'>{}</span>"}, "ud1.3:5.3": {"pli": "Vidhūpayaṁ tiṭṭhati mārasenaṁ, ", "eng": "he remains, scattering Māra’s army, ", "html": "<span class='verse-line'>{}</span>"}, "ud1.3:5.4": {"pli": "Sūriyova obhāsayamantalikkhan”ti. ", "eng": "as the sun lights up the sky.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud1.3:5.5": {"pli": "Tatiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud1.4": {"ud1.4:0.1": {"pli": "Udāna 1.4 ", "eng": "Heartfelt Sayings 1.4 ", "html": "<article id='ud1.4'><header><ul><li class='division'>{}</li></ul>"}, "ud1.4:0.2": {"pli": "Huṁhuṅkasutta ", "eng": "The Mystic Syllable Huṁ ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud1.4:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>", "comm": "This episode also appears at <a href='index.html?q=pli-tv-kd1#2.1.1'>Kd 1:2.1.1</a>. Sharing three distinctive terms—<i lang='pi' translate='no'>huṁ</i>, <i lang='pi' translate='no'>brahmavāda</i>, <i lang='pi' translate='no'>kāsāva</i>—with the Chāndogya Upaniṣad, it can be read as a rebuttal of the Chāndogya’s special emphasis on the mystic power of Vedic ritual utterances (<i lang='pi' translate='no'>sāman</i>). "}, "ud1.4:1.2": {"pli": "ekaṁ samayaṁ bhagavā uruvelāyaṁ viharati najjā nerañjarāya tīre ajapālanigrodhe paṭhamābhisambuddho. ", "eng": "At one time, when he was first awakened, the Buddha was staying in Uruvelā at the goatherd’s banyan tree on the bank of the Nerañjarā River. ", "html": "{}"}, "ud1.4:1.3": {"pli": "Tena kho pana samayena bhagavā sattāhaṁ ekapallaṅkena nisinno hoti vimuttisukhapaṭisaṁvedī. ", "eng": "There the Buddha sat cross-legged for seven days without moving, experiencing the bliss of freedom. ", "html": "{}"}, "ud1.4:1.4": {"pli": "Atha kho bhagavā tassa sattāhassa accayena tamhā samādhimhā vuṭṭhāsi. ", "eng": "When seven days had passed, the Buddha emerged from that state of immersion. ", "html": "{}</p>"}, "ud1.4:2.1": {"pli": "Atha kho aññataro huṁhuṅkajātiko brāhmaṇo yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavatā saddhiṁ sammodi. ", "eng": "Then a certain brahmin, one of those who murmured the mystic syllable <i lang='pi' translate='no'>huṁ</i>, <i lang='pi' translate='no'>huṁ</i>, went up to the Buddha and exchanged greetings with him. ", "html": "<p>{}", "comm": "<i lang='pi' translate='no'>Huṁhuṅkajātiko brāhmaṇo</i> is literally, “a brahmin of the <i lang='pi' translate='no'>huṁ</i>, <i lang='pi' translate='no'>huṁ</i> sort”. The significance of this is revealed in Chāndogya Upaniṣad 1.13, which lists twelve mystic syllables (<i lang='pi' translate='no'>stobha</i>) that are uttered during the recitation of the <i lang='pi' translate='no'>sāman</i> hymn. The thirteenth and final syllable is <i lang='pi' translate='no'>huṁ</i>, said to be variable and “indistinct” (<i lang='pi' translate='no'>anirukta</i>). It contains the last and most closed-off of the cardinal vowels (<i lang='pi' translate='no'>u</i>), which creates an indistinct mumble, reflecting the undefined nature of the primordial divinity. Its significance is underlined by the fact that it is called it the <i lang='pi' translate='no'>upaniṣad</i>, the “hidden connection” (1.14). <i lang='pi' translate='no'>Huṁ</i> is found fairly often in later Hindu texts, even receiving its own Hūṅkāra Tantra; occasionally we find the redoubled form as here (eg. Bṛhatkathāślokasaṁgraha 21.146). It made its way into such Tibetan texts as the Ḍākārṇava Tantra, where it is said to be recited at the end, reflecting its phonetic quality exactly as in the Upaniṣad. It is recognizable today as the final syllable in the mantra, <i lang='pi' translate='no'>om mani padme hum</i>. "}, "ud1.4:2.2": {"pli": "Sammodanīyaṁ kathaṁ sāraṇīyaṁ vītisāretvā ekamantaṁ aṭṭhāsi. Ekamantaṁ ṭhito kho so brāhmaṇo bhagavantaṁ etadavoca: ", "eng": "When the greetings and polite conversation were over, he stood to one side and said, ", "html": "{}"}, "ud1.4:2.3": {"pli": "“kittāvatā nu kho, bho gotama, brāhmaṇo hoti, katame ca pana brāhmaṇakaraṇā dhammā”ti? ", "eng": "“Worthy Gotama, how do you define a brahmin? And what are the things that make one a brahmin?” ", "html": "{}</p>"}, "ud1.4:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud1.4:4.1": {"pli": "“Yo brāhmaṇo bāhitapāpadhammo, ", "eng": "“Any brahmin who has banished bad qualities—", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>", "comm": "This pun is explained at <a href='index.html?q=mn39#24.2'>MN 39:24.2</a>. "}, "ud1.4:4.2": {"pli": "Nihuṁhuṅko nikkasāvo yatatto; ", "eng": "not murmuring the mystic syllable <i lang='pi' translate='no'>huṁ</i>, <j>unstained, self-controlled, ", "html": "<span class='verse-line'>{}</span>", "comm": "“Unstained” is from <i lang='pi' translate='no'>kasāva</i>, which occurs in the same sense at Chāndogya Upaniṣad 7.26.2 (<i lang='pi' translate='no'>mṛditakaṣāyāya</i>). "}, "ud1.4:4.3": {"pli": "Vedantagū vūsitabrahmacariyo, ", "eng": "a complete knowledge master <j>who has completed the spiritual journey—", "html": "<span class='verse-line'>{}</span>", "comm": "The “knowledge” in <i lang='pi' translate='no'>vedagū</i> is the Vedas in Brahmanism and the three knowledges in Buddhism. It is not an attested term in Brahmanical texts. | For <i lang='pi' translate='no'>vūsitabrahmacariyo</i> (“completed the spiritual journey”) compare <i lang='pi' translate='no'>uṣitvā brahmacaryaṁ</i> at Bṛhadāraṇyaka Upaniṣad 5.2.1. "}, "ud1.4:4.4": {"pli": "Dhammena so brahmavādaṁ vadeyya; ", "eng": "they may legitimately proclaim the divine doctrine; ", "html": "<span class='verse-line'>{}</span>", "comm": "This, the only occurrence of <i lang='pi' translate='no'>brahmavāda</i> in Pali, harks back to the brahmin’s role as a reciter of the <i lang='pi' translate='no'>sāman</i>. In Brahmanical texts it has the sense of “orthodox Vedic doctrine” (Atharva Veda 11.3.26a, 15.1.8a; Bṛhadāraṇyaka Upaniṣad 14.7.3.1; Chāndogya Upaniṣad 2.24.1), comparable to the sense of Theravāda (“doctrine of the elders”). Here the Buddha, as so often, adapts the meaning to his own ends. "}, "ud1.4:4.5": {"pli": "Yassussadā natthi kuhiñci loke”ti. ", "eng": "who have no pretensions about anything in the world.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>", "comm": "“Pretensions” renders <i lang='pi' translate='no'>ussadā</i>. This line is found in the earlier Aṭṭhavagga (<a href='index.html?q=snp4.3#4.4'>Snp 4.3:4.4</a> and note), from where I suspect it has been imported. Here it dangles as the last line of the only five-line stanza in the Udāna, following a line that is already grammatically complete (the demonstrative <i lang='pi' translate='no'>so</i> answers the relative <i lang='pi' translate='no'>yo</i> in the opening line). Perhaps it was added to downplay the importance of the term <i lang='pi' translate='no'>brahmavāda</i>. "}, "ud1.4:4.6": {"pli": "Catutthaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud1.5": {"ud1.5:0.1": {"pli": "Udāna 1.5 ", "eng": "Heartfelt Sayings 1.5 ", "html": "<article id='ud1.5'><header><ul><li class='division'>{}</li></ul>"}, "ud1.5:0.2": {"pli": "Brāhmaṇasutta ", "eng": "The Brahmin ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud1.5:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud1.5:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud1.5:1.3": {"pli": "Tena kho pana samayena āyasmā ca sāriputto āyasmā ca mahāmoggallāno āyasmā ca mahākassapo āyasmā ca mahākaccāno āyasmā ca mahākoṭṭhiko āyasmā ca mahākappino āyasmā ca mahācundo āyasmā ca anuruddho āyasmā ca revato āyasmā ca nando yena bhagavā tenupasaṅkamiṁsu. ", "eng": "Now at that time a number of senior monks approached the Buddha—Venerables Sāriputta, Mahāmoggallāna, Mahākassapa, Mahākaccāna, Mahākoṭṭhita, Mahākappina, Mahācunda, Anuruddha, Revata, and Nanda. ", "html": "{}</p>", "comm": "A similar list of great disciples is found in the prelude to the Ānāpānassatisutta at <a href='index.html?q=mn118#1.3'>MN 118:1.3</a>. The only difference is that there the final person is Ānanda rather than Nanda. It is tempting to follow the BJT and PTS in reading Ānanda here as well, but the purpose of this sutta is to extol all these monks as “brahmins”, which the sutta itself explains as meaning “with fetters ended”, namely arahants. But at this time, Ānanda was still a stream-enterer (<a href='index.html?q=dn16#5.13.2'>DN 16:5.13.2</a>), and would only attain arahantship after the Buddha’s passing away (<a href='index.html?q=pli-tv-kd21#1.6.1'>Kd 21:1.6.1</a>). Nanda, on the other hand, was indeed an arahant (<a href='index.html?q=ud3.2#14.3'>Ud 3.2:14.3</a>). "}, "ud1.5:2.1": {"pli": "Addasā kho bhagavā te āyasmante dūratova āgacchante; ", "eng": "The Buddha saw them coming off in the distance, ", "html": "<p>{}"}, "ud1.5:2.2": {"pli": "disvāna bhikkhū āmantesi: ", "eng": "and addressed the mendicants: ", "html": "{}"}, "ud1.5:2.3": {"pli": "“ete, bhikkhave, brāhmaṇā āgacchanti; ", "eng": "“These, mendicants, are brahmins coming! ", "html": "{}"}, "ud1.5:2.4": {"pli": "ete, bhikkhave, brāhmaṇā āgacchantī”ti. ", "eng": "These are brahmins coming!” ", "html": "{}"}, "ud1.5:2.5": {"pli": "Evaṁ vutte, aññataro brāhmaṇajātiko bhikkhu bhagavantaṁ etadavoca: ", "eng": "When he said this, a certain mendicant who was brahmin by birth asked the Buddha, ", "html": "{}"}, "ud1.5:2.6": {"pli": "“kittāvatā nu kho, bhante, brāhmaṇo hoti, katame ca pana brāhmaṇakaraṇā dhammā”ti? ", "eng": "“Sir, how do you define a brahmin? And what are the things that make one a brahmin?” ", "html": "{}</p>"}, "ud1.5:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud1.5:4.1": {"pli": "“Bāhitvā pāpake dhamme, ", "eng": "“Having banished bad qualities, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud1.5:4.2": {"pli": "ye caranti sadā satā; ", "eng": "those who live always mindful, ", "html": "<span class='verse-line'>{}</span>"}, "ud1.5:4.3": {"pli": "Khīṇasaṁyojanā buddhā, ", "eng": "with fetters ended, awakened, ", "html": "<span class='verse-line'>{}</span>"}, "ud1.5:4.4": {"pli": "te ve lokasmi brāhmaṇā”ti. ", "eng": "they are the world’s true brahmins.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud1.5:4.5": {"pli": "Pañcamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud1.6": {"ud1.6:0.1": {"pli": "Udāna 1.6 ", "eng": "Heartfelt Sayings 1.6 ", "html": "<article id='ud1.6'><header><ul><li class='division'>{}</li></ul>"}, "ud1.6:0.2": {"pli": "Mahākassapasutta ", "eng": "With Mahākassapa ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud1.6:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud1.6:1.2": {"pli": "ekaṁ samayaṁ bhagavā rājagahe viharati veḷuvane kalandakanivāpe. ", "eng": "At one time the Buddha was staying near Rājagaha, in the Bamboo Grove, the squirrels’ feeding ground. ", "html": "{}"}, "ud1.6:1.3": {"pli": "Tena kho pana samayena āyasmā mahākassapo pippaliguhāyaṁ viharati ābādhiko dukkhito bāḷhagilāno. ", "eng": "Now at that time Venerable Mahākassapa was staying in the Pipphali cave, and he was sick, suffering, gravely ill. ", "html": "{}"}, "ud1.6:1.4": {"pli": "Atha kho āyasmā mahākassapo aparena samayena tamhā ābādhā vuṭṭhāsi. ", "eng": "Then after some time he recovered from that illness. ", "html": "{}"}, "ud1.6:1.5": {"pli": "Atha kho āyasmato mahākassapassa tamhā ābādhā vuṭṭhitassa etadahosi: ", "eng": "It occurred to him, ", "html": "{}"}, "ud1.6:1.6": {"pli": "“yannūnāhaṁ rājagahaṁ piṇḍāya paviseyyan”ti. ", "eng": "“Why not enter Rājagaha for almsfood?” ", "html": "{}</p>"}, "ud1.6:2.1": {"pli": "Tena kho pana samayena pañcamattāni devatāsatāni ussukkaṁ āpannāni honti āyasmato mahākassapassa piṇḍapātapaṭilābhāya. ", "eng": "Now at that time five hundred deities were ready and eager for the chance to offer alms to Mahākassapa. ", "html": "<p>{}"}, "ud1.6:2.2": {"pli": "Atha kho āyasmā mahākassapo tāni pañcamattāni devatāsatāni paṭikkhipitvā pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya rājagahaṁ piṇḍāya pāvisi—", "eng": "But Mahākasspa turned down those deities. In the morning, he robed up, took his bowl and robe, and entered Rājagaha for alms. ", "html": "{}"}, "ud1.6:2.3": {"pli": "yena daliddavisikhā kapaṇavisikhā pesakāravisikhā. ", "eng": "He went to the streets of the poor, the destitute, and the embroiderers. ", "html": "{}"}, "ud1.6:2.4": {"pli": "Addasā kho bhagavā āyasmantaṁ mahākassapaṁ rājagahe piṇḍāya carantaṁ yena daliddavisikhā kapaṇavisikhā pesakāravisikhā. ", "eng": "The Buddha saw him wandering for alms in the streets of the poor, the destitute, and the embroiderers. ", "html": "{}</p>"}, "ud1.6:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud1.6:4.1": {"pli": "“Anaññaposimaññātaṁ, ", "eng": "“The stranger, providing for no other, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>", "comm": "Compare <a href='index.html?q=ud1.8'>Ud 1.8</a>. "}, "ud1.6:4.2": {"pli": "dantaṁ sāre patiṭṭhitaṁ; ", "eng": "tamed, consolidated in the core, ", "html": "<span class='verse-line'>{}</span>"}, "ud1.6:4.3": {"pli": "Khīṇāsavaṁ vantadosaṁ, ", "eng": "with defilements ended and flaws purged: ", "html": "<span class='verse-line'>{}</span>"}, "ud1.6:4.4": {"pli": "tamahaṁ brūmi brāhmaṇan”ti. ", "eng": "that’s who I declare a brahmin.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud1.6:4.5": {"pli": "Chaṭṭhaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud1.7": {"ud1.7:0.1": {"pli": "Udāna 1.7 ", "eng": "Heartfelt Sayings 1.7 ", "html": "<article id='ud1.7'><header><ul><li class='division'>{}</li></ul>"}, "ud1.7:0.2": {"pli": "Ajakalāpakasutta ", "eng": "At Ajakalāpaka ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud1.7:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud1.7:1.2": {"pli": "ekaṁ samayaṁ bhagavā pāvāyaṁ viharati ajakalāpake cetiye, ajakalāpakassa yakkhassa bhavane. ", "eng": "At one time the Buddha was staying in Pāvā at the Ajakalāpaka Shrine, the haunt of the native spirit Ajakalāpaka. ", "html": "{}"}, "ud1.7:1.3": {"pli": "Tena kho pana samayena bhagavā rattandhakāratimisāyaṁ abbhokāse nisinno hoti; devo ca ekamekaṁ phusāyati. ", "eng": "Now at that time the Buddha was meditating in the open during the dark of night, while a gentle rain drizzled down. ", "html": "{}"}, "ud1.7:1.4": {"pli": "Atha kho ajakalāpako yakkho bhagavato bhayaṁ chambhitattaṁ lomahaṁsaṁ uppādetukāmo yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavato avidūre tikkhattuṁ “akkulo pakkulo”ti akkulapakkulikaṁ akāsi: ", "eng": "Then Ajakalāpaka, wanting to make the Buddha feel fear, terror, and goosebumps, approached him, and while not far away yelled three times, “Grr! Arrgh!” Then he added, ", "html": "{}"}, "ud1.7:1.5": {"pli": "“eso te, samaṇa, pisāco”ti. ", "eng": "“Now that’s a goblin for you, ascetic!” ", "html": "{}</p>"}, "ud1.7:2.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud1.7:3.1": {"pli": "“Yadā sakesu dhammesu, ", "eng": "“When a brahmin gets over ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud1.7:3.2": {"pli": "pāragū hoti brāhmaṇo; ", "eng": "their own issues, ", "html": "<span class='verse-line'>{}</span>"}, "ud1.7:3.3": {"pli": "Atha etaṁ pisācañca, ", "eng": "they transcend this goblin ", "html": "<span class='verse-line'>{}</span>"}, "ud1.7:3.4": {"pli": "pakkulañcātivattatī”ti. ", "eng": "and his grunts.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud1.7:3.5": {"pli": "Sattamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud1.8": {"ud1.8:0.1": {"pli": "Udāna 1.8 ", "eng": "Heartfelt Sayings 1.8 ", "html": "<article id='ud1.8'><header><ul><li class='division'>{}</li></ul>"}, "ud1.8:0.2": {"pli": "Saṅgāmajisutta ", "eng": "With Saṅgāmaji ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud1.8:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud1.8:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud1.8:1.3": {"pli": "Tena kho pana samayena āyasmā saṅgāmaji sāvatthiṁ anuppatto hoti bhagavantaṁ dassanāya. ", "eng": "Now at that time Venerable Saṅgāmaji had arrived at Sāvatthī to see the Buddha. ", "html": "{}"}, "ud1.8:1.4": {"pli": "Assosi kho āyasmato saṅgāmajissa purāṇadutiyikā: ", "eng": "His former wife heard ", "html": "{}"}, "ud1.8:1.5": {"pli": "“ayyo kira saṅgāmaji sāvatthiṁ anuppatto”ti. ", "eng": "that he had arrived, ", "html": "{}"}, "ud1.8:1.6": {"pli": "Sā dārakaṁ ādāya jetavanaṁ agamāsi. ", "eng": "and went to the Jetavana, taking their boy. ", "html": "{}</p>"}, "ud1.8:2.1": {"pli": "Tena kho pana samayena āyasmā saṅgāmaji aññatarasmiṁ rukkhamūle divāvihāraṁ nisinno hoti. ", "eng": "Now at that time Venerable Saṅgāmaji was sitting at the root of a tree for the day’s meditation. ", "html": "<p>{}"}, "ud1.8:2.2": {"pli": "Atha kho āyasmato saṅgāmajissa purāṇadutiyikā yenāyasmā saṅgāmaji tenupasaṅkami; upasaṅkamitvā āyasmantaṁ saṅgāmajiṁ etadavoca: ", "eng": "Then his former wife went up to him and said, ", "html": "{}"}, "ud1.8:2.3": {"pli": "“khuddaputtañhi, samaṇa, posa man”ti. ", "eng": "“Ascetic, please raise this little boy.” ", "html": "{}", "comm": "Read <i lang='pi' translate='no'>naṁ</i> throughout. "}, "ud1.8:2.4": {"pli": "Evaṁ vutte, āyasmā saṅgāmaji tuṇhī ahosi. ", "eng": "When she said this, Saṅgāmaji kept silent. ", "html": "{}</p>"}, "ud1.8:3.1": {"pli": "Dutiyampi kho āyasmato saṅgāmajissa purāṇadutiyikā āyasmantaṁ saṅgāmajiṁ etadavoca: ", "eng": "For a second time she said, ", "html": "<p>{}"}, "ud1.8:3.2": {"pli": "“khuddaputtañhi, samaṇa, posa man”ti. ", "eng": "“Ascetic, please raise this little boy.” ", "html": "{}"}, "ud1.8:3.3": {"pli": "Dutiyampi kho āyasmā saṅgāmaji tuṇhī ahosi. ", "eng": "For a second time, Saṅgāmaji kept silent. ", "html": "{}</p>"}, "ud1.8:4.1": {"pli": "Tatiyampi kho āyasmato saṅgāmajissa purāṇadutiyikā āyasmantaṁ saṅgāmajiṁ etadavoca: ", "eng": "For a third time she said, ", "html": "<p>{}"}, "ud1.8:4.2": {"pli": "“khuddaputtañhi, samaṇa, posa man”ti. ", "eng": "“Ascetic, please raise this little boy.” ", "html": "{}"}, "ud1.8:4.3": {"pli": "Tatiyampi kho āyasmā saṅgāmaji tuṇhī ahosi. ", "eng": "For a third time, Saṅgāmaji kept silent. ", "html": "{}</p>"}, "ud1.8:5.1": {"pli": "Atha kho āyasmato saṅgāmajissa purāṇadutiyikā taṁ dārakaṁ āyasmato saṅgāmajissa purato nikkhipitvā pakkāmi: ", "eng": "Then she put down the boy in front of Saṅgāmaji, saying, ", "html": "<p>{}"}, "ud1.8:5.2": {"pli": "“eso te, samaṇa, putto; posa nan”ti. ", "eng": "“This is your child, ascetic. Please raise him.” ", "html": "{}</p>"}, "ud1.8:6.1": {"pli": "Atha kho āyasmā saṅgāmaji taṁ dārakaṁ neva olokesi nāpi ālapi. ", "eng": "But Saṅgāmaji neither looked at the boy nor spoke to him. ", "html": "<p>{}"}, "ud1.8:6.2": {"pli": "Atha kho āyasmato saṅgāmajissa purāṇadutiyikā avidūraṁ gantvā apalokentī addasa āyasmantaṁ saṅgāmajiṁ taṁ dārakaṁ neva olokentaṁ nāpi ālapantaṁ, disvānassā etadahosi: ", "eng": "Then his former wife went a little distance away. Looking back, she saw Saṅgāmaji ignoring the boy, and thought, ", "html": "{}"}, "ud1.8:6.3": {"pli": "“na cāyaṁ samaṇo puttenapi atthiko”ti. ", "eng": "“This ascetic doesn’t even want his child.” ", "html": "{}", "comm": "<i lang='pi' translate='no'>Atthiko</i> is tricky to translate due to the vagueness of the base term. Ānandajoti has “need”, while Ṭhānissaro has “care about”. But it is usually used in the sense of “want”, as a synonym of <i lang='pi' translate='no'>gavesī</i>, etc., or one who desires a kingdom. "}, "ud1.8:6.4": {"pli": "Tato paṭinivattitvā dārakaṁ ādāya pakkāmi. ", "eng": "She returned to pick up the boy, then left. ", "html": "{}"}, "ud1.8:6.5": {"pli": "Addasā kho bhagavā dibbena cakkhunā visuddhena atikkantamānusakena āyasmato saṅgāmajissa purāṇadutiyikāya evarūpaṁ vippakāraṁ. ", "eng": "With clairvoyance that is purified and superhuman, the Buddha saw how Saṅgāmaji’s former wife went back for the child. ", "html": "{}</p>", "comm": "For <i lang='pi' translate='no'>vippakāraṁ</i>, Ānandajoti has “bad manners”, while Ṭhānissaro has “misbehaving”. Neither of these seems justified. PTS dictionary gives only commentarial citations for this word, but it also occurs at <a href='index.html?q=pli-tv-pvr15#37.7'>Pvr 15:37.7</a>, where it is defined as changes in posture. It seems the Buddha merely observed that the former wife had changed her mind and gone back. Otherwise, the sutta passes no judgment on her behavior. "}, "ud1.8:7.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud1.8:8.1": {"pli": "“Āyantiṁ nābhinandati, ", "eng": "“When she came he was not glad, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud1.8:8.2": {"pli": "pakkamantiṁ na socati; ", "eng": "when she left he did not grieve. ", "html": "<span class='verse-line'>{}</span>"}, "ud1.8:8.3": {"pli": "Saṅgā saṅgāmajiṁ muttaṁ, ", "eng": "Victorious in battle, freed from chains, ", "html": "<span class='verse-line'>{}</span>"}, "ud1.8:8.4": {"pli": "tamahaṁ brūmi brāhmaṇan”ti. ", "eng": "that’s who I declare a brahmin.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud1.8:8.5": {"pli": "Aṭṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud1.9": {"ud1.9:0.1": {"pli": "Udāna 1.9 ", "eng": "Heartfelt Sayings 1.9 ", "html": "<article id='ud1.9'><header><ul><li class='division'>{}</li></ul>"}, "ud1.9:0.2": {"pli": "Jaṭilasutta ", "eng": "Dreadlocked Ascetics ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud1.9:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud1.9:1.2": {"pli": "ekaṁ samayaṁ bhagavā gayāyaṁ viharati gayāsīse. ", "eng": "At one time the Buddha was staying near Gayā on Gayā Head. ", "html": "{}"}, "ud1.9:1.3": {"pli": "Tena kho pana samayena sambahulā jaṭilā sītāsu hemantikāsu rattīsu antaraṭṭhake himapātasamaye gayāyaṁ ummujjantipi nimujjantipi, ummujjanimujjampi karonti osiñcantipi, aggimpi juhanti: ", "eng": "Now at that time—during the cold spell when the snow falls in the January winter—many dreadlocked ascetics plunged in and out of the Gayā river. Plunging and showering, they served the sacred flame, thinking: ", "html": "{}"}, "ud1.9:1.4": {"pli": "“iminā suddhī”ti. ", "eng": "“Through this there is purity.” ", "html": "{}</p>"}, "ud1.9:2.1": {"pli": "Addasā kho bhagavā te sambahule jaṭile sītāsu hemantikāsu rattīsu antaraṭṭhake himapātasamaye gayāyaṁ ummujjantepi nimujjantepi ummujjanimujjampi karonte osiñcantepi aggimpi juhante: ", "eng": "The Buddha saw them plunging in and out. ", "html": "<p>{}"}, "ud1.9:2.2": {"pli": "“iminā suddhī”ti. ", "html": "{}</p>"}, "ud1.9:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud1.9:4.1": {"pli": "“Na udakena sucī hoti, ", "eng": "“Purity doesn’t come from water, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud1.9:4.2": {"pli": "bahvettha nhāyatī jano; ", "eng": "no matter how many people bathe there. ", "html": "<span class='verse-line'>{}</span>"}, "ud1.9:4.3": {"pli": "Yamhi saccañca dhammo ca, ", "eng": "One who has truth and principle, ", "html": "<span class='verse-line'>{}</span>"}, "ud1.9:4.4": {"pli": "so sucī so ca brāhmaṇo”ti. ", "eng": "they are pure, they are brahmins.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud1.9:4.5": {"pli": "Navamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud1.10": {"ud1.10:0.1": {"pli": "Udāna 1.10 ", "eng": "Heartfelt Sayings 1.10 ", "html": "<article id='ud1.10'><header><ul><li class='division'>{}</li></ul>"}, "ud1.10:0.2": {"pli": "Bāhiyasutta ", "eng": "With Bāhiya ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud1.10:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud1.10:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud1.10:1.3": {"pli": "Tena kho pana samayena bāhiyo dārucīriyo suppārake paṭivasati samuddatīre sakkato garukato mānito pūjito apacito lābhī cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhārānaṁ. ", "eng": "Now at that time Bāhiya of the Bark Cloth was residing by Suppāraka on the ocean shore, where he was honored, respected, revered, venerated, and esteemed. And he received robes, almsfood, lodgings, and medicines and supplies for the sick. ", "html": "{}", "comm": "Suppāraka is modern Nallasopara near Mumbai on India’s west coast. "}, "ud1.10:1.4": {"pli": "Atha kho bāhiyassa dārucīriyassa rahogatassa paṭisallīnassa evaṁ cetaso parivitakko udapādi: ", "eng": "Then as he was in private retreat this thought came to his mind, ", "html": "{}"}, "ud1.10:1.5": {"pli": "“ye kho keci loke arahanto vā arahattamaggaṁ vā samāpannā, ahaṁ tesaṁ aññataro”ti. ", "eng": "“I am one of those in the world who are perfected or on the path to perfection.” ", "html": "{}</p>"}, "ud1.10:2.1": {"pli": "Atha kho bāhiyassa dārucīriyassa purāṇasālohitā devatā anukampikā atthakāmā bāhiyassa dārucīriyassa cetasā cetoparivitakkamaññāya yena bāhiyo dārucīriyo tenupasaṅkami; upasaṅkamitvā bāhiyaṁ dārucīriyaṁ etadavoca: ", "eng": "Then a deity who was a former relative of Bāhiya, having sympathy and wanting what’s best for him, approached him and said: ", "html": "<p>{}"}, "ud1.10:2.2": {"pli": "“neva kho tvaṁ, bāhiya, arahā, nāpi arahattamaggaṁ vā samāpanno. ", "eng": "“Bāhiya, you’re not a perfected one, nor on the path to perfection. ", "html": "{}"}, "ud1.10:2.3": {"pli": "Sāpi te paṭipadā natthi yāya tvaṁ arahā vā assa arahattamaggaṁ vā samāpanno”ti. ", "eng": "You don’t have the practice by which you might become a perfected one or one on the path to perfection.” ", "html": "{}</p>"}, "ud1.10:3.1": {"pli": "“Atha ke carahi sadevake loke arahanto vā arahattamaggaṁ vā samāpannā”ti? ", "eng": "“So who then are those in the world who are perfected or on the path to perfection?” ", "html": "<p>{}"}, "ud1.10:3.2": {"pli": "“Atthi, bāhiya, uttaresu janapadesu sāvatthī nāma nagaraṁ. ", "eng": "“In the northern lands there is a city named Sāvatthī. ", "html": "{}"}, "ud1.10:3.3": {"pli": "Tattha so bhagavā etarahi viharati arahaṁ sammāsambuddho. ", "eng": "There that Blessed One is now staying, the perfected one, the fully awakened Buddha. ", "html": "{}"}, "ud1.10:3.4": {"pli": "So hi, bāhiya, bhagavā arahā ceva arahattāya ca dhammaṁ desetī”ti. ", "eng": "He is a perfected one and teaches the Dhamma for the sake of perfection.” ", "html": "{}</p>"}, "ud1.10:4.1": {"pli": "Atha kho bāhiyo dārucīriyo tāya devatāya saṁvejito tāvadeva suppārakamhā pakkāmi. ", "eng": "Impelled by that deity, Bāhiya left Suppāraka right away. ", "html": "<p>{}"}, "ud1.10:4.2": {"pli": "Sabbattha ekarattiparivāsena yena sāvatthī jetavanaṁ anāthapiṇḍikassa ārāmo tenupasaṅkami. ", "eng": "Sojourning no more than a single night in any place, he made his way to Anāthapiṇḍika’s Monastery in the Jeta Grove at Sāvatthī. ", "html": "{}"}, "ud1.10:4.3": {"pli": "Tena kho pana samayena sambahulā bhikkhū abbhokāse caṅkamanti. ", "eng": "At that time several mendicants were walking mindfully in the open air. ", "html": "{}"}, "ud1.10:4.4": {"pli": "Atha kho bāhiyo dārucīriyo yena te bhikkhū tenupasaṅkami; upasaṅkamitvā te bhikkhū etadavoca: ", "eng": "Bāhiya approached them and said, ", "html": "{}"}, "ud1.10:4.5": {"pli": "“kahaṁ nu kho, bhante, etarahi bhagavā viharati arahaṁ sammāsambuddho? ", "eng": "“Sirs, where is the Blessed One at present, the perfected one, the fully awakened Buddha? ", "html": "{}"}, "ud1.10:4.6": {"pli": "Dassanakāmamhā mayaṁ taṁ bhagavantaṁ arahantaṁ sammāsambuddhan”ti. ", "eng": "For I want to see him.” ", "html": "{}"}, "ud1.10:4.7": {"pli": "“Antaragharaṁ paviṭṭho kho, bāhiya, bhagavā piṇḍāyā”ti. ", "eng": "“He has entered an inhabited area for almsfood, Bāhiya.” ", "html": "{}</p>"}, "ud1.10:5.1": {"pli": "Atha kho bāhiyo dārucīriyo taramānarūpo jetavanā nikkhamitvā sāvatthiṁ pavisitvā addasa bhagavantaṁ sāvatthiyaṁ piṇḍāya carantaṁ pāsādikaṁ pasādanīyaṁ santindriyaṁ santamānasaṁ uttamadamathasamathamanuppattaṁ dantaṁ guttaṁ yatindriyaṁ nāgaṁ. ", "eng": "Then Bāhiya rushed out of the Jeta Grove and entered Sāvatthī, where he saw the Buddha walking for alms. He was impressive and inspiring, with peaceful faculties and heart, attained to the highest self-control and serenity, like an elephant with tamed, guarded, and controlled faculties. ", "html": "<p>{}"}, "ud1.10:5.2": {"pli": "Disvāna yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavato pāde sirasā nipatitvā bhagavantaṁ etadavoca: ", "eng": "Bāhiya went up to the Buddha, bowed down with his head at the Buddha’s feet, and said, ", "html": "{}"}, "ud1.10:5.3": {"pli": "“desetu me, bhante, bhagavā dhammaṁ; desetu sugato dhammaṁ, ", "eng": "“Sir, let the Blessed One teach me the Dhamma! Let the Holy One teach me the Dhamma! ", "html": "{}"}, "ud1.10:5.4": {"pli": "yaṁ mamassa dīgharattaṁ hitāya sukhāyā”ti. ", "eng": "That would be for my lasting welfare and happiness.” ", "html": "{}"}, "ud1.10:5.5": {"pli": "Evaṁ vutte, bhagavā bāhiyaṁ dārucīriyaṁ etadavoca: ", "eng": "The Buddha said this: ", "html": "{}"}, "ud1.10:5.6": {"pli": "“akālo kho tāva, bāhiya, antaragharaṁ paviṭṭhamhā piṇḍāyā”ti. ", "eng": "“It’s not the time, Bāhiya, so long as I have entered an inhabited area for almsfood.” ", "html": "{}</p>"}, "ud1.10:6.1": {"pli": "Dutiyampi kho bāhiyo dārucīriyo bhagavantaṁ etadavoca: ", "eng": "For a second time, Bāhiya said, ", "html": "<p>{}"}, "ud1.10:6.2": {"pli": "“dujjānaṁ kho panetaṁ, bhante, bhagavato vā jīvitantarāyānaṁ, mayhaṁ vā jīvitantarāyānaṁ. ", "eng": "“But you never know, sir, when life is at risk, either the Buddha’s or my own. ", "html": "{}"}, "ud1.10:6.3": {"pli": "Desetu me, bhante, bhagavā dhammaṁ; ", "eng": "Let the Blessed One teach me the Dhamma! Let the Holy One teach me the Dhamma! ", "html": "{}"}, "ud1.10:6.4": {"pli": "desetu sugato dhammaṁ, yaṁ mamassa dīgharattaṁ hitāya sukhāyā”ti. ", "eng": "That would be for my lasting welfare and happiness.” ", "html": "{}"}, "ud1.10:6.5": {"pli": "Dutiyampi kho bhagavā bāhiyaṁ dārucīriyaṁ etadavoca: ", "eng": "For a second time, the Buddha said, ", "html": "{}"}, "ud1.10:6.6": {"pli": "“akālo kho tāva, bāhiya, antaragharaṁ paviṭṭhamhā piṇḍāyā”ti. ", "eng": "“It’s not the time, Bāhiya, so long as I have entered an inhabited area for almsfood.” ", "html": "{}</p>"}, "ud1.10:7.1": {"pli": "Tatiyampi kho bāhiyo dārucīriyo bhagavantaṁ etadavoca: ", "eng": "For a third time, Bāhiya said, ", "html": "<p>{}"}, "ud1.10:7.2": {"pli": "“dujjānaṁ kho panetaṁ, bhante, bhagavato vā jīvitantarāyānaṁ, mayhaṁ vā jīvitantarāyānaṁ. ", "eng": "“But you never know, sir, when life is at risk, either the Buddha’s or my own. ", "html": "{}"}, "ud1.10:7.3": {"pli": "Desetu me, bhante, bhagavā dhammaṁ; ", "eng": "Let the Blessed One teach me the Dhamma! Let the Holy One teach me the Dhamma! ", "html": "{}"}, "ud1.10:7.4": {"pli": "desetu sugato dhammaṁ, yaṁ mamassa dīgharattaṁ hitāya sukhāyā”ti. ", "eng": "That would be for my lasting welfare and happiness.” ", "html": "{}</p>"}, "ud1.10:8.1": {"pli": "“Tasmātiha te, bāhiya, evaṁ sikkhitabbaṁ: ", "eng": "“In that case, Bāhiya, you should train like this: ", "html": "<p>{}"}, "ud1.10:8.2": {"pli": "‘diṭṭhe diṭṭhamattaṁ bhavissati, sute sutamattaṁ bhavissati, mute mutamattaṁ bhavissati, viññāte viññātamattaṁ bhavissatī’ti. ", "eng": "‘In the seen will be merely the seen; in the heard will be merely the heard; in the thought will be merely the thought; in the known will be merely the known.’ ", "html": "{}"}, "ud1.10:8.3": {"pli": "Evañhi te, bāhiya, sikkhitabbaṁ. ", "eng": "That’s how you should train. ", "html": "{}"}, "ud1.10:8.4": {"pli": "Yato kho te, bāhiya, diṭṭhe diṭṭhamattaṁ bhavissati, sute sutamattaṁ bhavissati, mute mutamattaṁ bhavissati, viññāte viññātamattaṁ bhavissati, ", "eng": "When you have trained in this way, ", "html": "{}"}, "ud1.10:8.5": {"pli": "tato tvaṁ, bāhiya, na tena. ", "eng": "you won’t be ‘by that’. ", "html": "{}"}, "ud1.10:8.6": {"pli": "Yato tvaṁ, bāhiya, na tena; ", "eng": "When you’re not ‘by that’, ", "html": "{}"}, "ud1.10:8.7": {"pli": "tato tvaṁ, bāhiya, na tattha. ", "eng": "you won’t be ‘in that’. ", "html": "{}"}, "ud1.10:8.8": {"pli": "Yato tvaṁ, bāhiya, na tattha, ", "eng": "When you’re not ‘in that’, ", "html": "{}"}, "ud1.10:8.9": {"pli": "tato tvaṁ, bāhiya, nevidha na huraṁ na ubhayamantarena. ", "eng": "you won’t be in this life or the next or between the two. ", "html": "{}"}, "ud1.10:8.10": {"pli": "Esevanto dukkhassā”ti. ", "eng": "Just this is the end of suffering.” ", "html": "{}</p>"}, "ud1.10:9.1": {"pli": "Atha kho bāhiyassa dārucīriyassa bhagavato imāya saṅkhittāya dhammadesanāya tāvadeva anupādāya āsavehi cittaṁ vimucci. ", "eng": "Then, due to this brief Dhamma teaching of the Buddha, Bāhiya’s mind was right away freed from defilements by not grasping. ", "html": "<p>{}</p>"}, "ud1.10:10.1": {"pli": "Atha kho bhagavā bāhiyaṁ dārucīriyaṁ iminā saṅkhittena ovādena ovaditvā pakkāmi. ", "eng": "And when the Buddha had given Bāhiya this brief advice he left. ", "html": "<p>{}"}, "ud1.10:10.2": {"pli": "Atha kho acirapakkantassa bhagavato bāhiyaṁ dārucīriyaṁ gāvī taruṇavacchā adhipatitvā jīvitā voropesi. ", "eng": "But soon after the Buddha had left, a cow with a baby calf charged at Bāhiya and took his life. ", "html": "{}</p>"}, "ud1.10:11.1": {"pli": "Atha kho bhagavā sāvatthiyaṁ piṇḍāya caritvā pacchābhattaṁ piṇḍapātapaṭikkanto sambahulehi bhikkhūhi saddhiṁ nagaramhā nikkhamitvā addasa bāhiyaṁ dārucīriyaṁ kālaṅkataṁ; ", "eng": "Then the Buddha wandered for alms in Sāvatthī. After the meal, on his return from almsround, he exited the city together with several mendicants and saw that Bāhiya had passed away. ", "html": "<p>{}"}, "ud1.10:11.2": {"pli": "disvāna bhikkhū āmantesi: ", "eng": "He said to the monks, ", "html": "{}"}, "ud1.10:11.3": {"pli": "“gaṇhatha, bhikkhave, bāhiyassa dārucīriyassa sarīrakaṁ; mañcakaṁ āropetvā nīharitvā jhāpetha; thūpañcassa karotha. ", "eng": "“Mendicants, pick up Bāhiya’s corpse. Having lifted it onto a cot and carried it, cremate it and build a monument. ", "html": "{}"}, "ud1.10:11.4": {"pli": "Sabrahmacārī vo, bhikkhave, kālaṅkato”ti. ", "eng": "Mendicants, one of your spiritual companions has passed away.” ", "html": "{}</p>"}, "ud1.10:12.1": {"pli": "“Evaṁ, bhante”ti kho te bhikkhū bhagavato paṭissutvā bāhiyassa dārucīriyassa sarīrakaṁ mañcakaṁ āropetvā nīharitvā jhāpetvā thūpañcassa katvā yena bhagavā tenupasaṅkamiṁsu; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdiṁsu. ", "eng": "“Yes, sir,” replied those mendicants. They did as the Buddha asked, then returned to the Buddha ", "html": "<p>{}"}, "ud1.10:12.2": {"pli": "Ekamantaṁ nisinnā kho te bhikkhū bhagavantaṁ etadavocuṁ: ", "eng": "and said, ", "html": "{}"}, "ud1.10:12.3": {"pli": "“daḍḍhaṁ, bhante, bāhiyassa dārucīriyassa sarīraṁ, thūpo cassa kato. ", "eng": "“Sir, Bāhiya’s corpse has been cremated and a monument built for him. ", "html": "{}"}, "ud1.10:12.4": {"pli": "Tassa kā gati, ko abhisamparāyo”ti? ", "eng": "Where has he been reborn in his next life?” ", "html": "{}"}, "ud1.10:12.5": {"pli": "“Paṇḍito, bhikkhave, bāhiyo dārucīriyo paccapādi dhammassānudhammaṁ; na ca maṁ dhammādhikaraṇaṁ vihesesi. ", "eng": "“Mendicants, Bāhiya was astute. He practiced in line with the teaching, and did not trouble me about the teachings. ", "html": "{}"}, "ud1.10:12.6": {"pli": "Parinibbuto, bhikkhave, bāhiyo dārucīriyo”ti. ", "eng": "Bāhiya of the Bark Cloth has become fully quenched.” ", "html": "{}</p>"}, "ud1.10:13.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud1.10:14.1": {"pli": "“Yattha āpo ca pathavī, ", "eng": "“Where water and earth, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>", "comm": "Compare similar lines at <a href='index.html?q=sn1.27#2.1'>SN 1.27:2.1</a>; and a similar idea at <a href='index.html?q=ud8.1#3.1'>Ud 8.1:3.1</a>. "}, "ud1.10:14.2": {"pli": "tejo vāyo na gādhati; ", "eng": "fire and air have no footing—", "html": "<span class='verse-line'>{}</p></span>"}, "ud1.10:14.3": {"pli": "Na tattha sukkā jotanti, ", "eng": "there no star does shine, ", "html": "<p><span class='verse-line'>{}</span>", "comm": "Compare Kaṭha Upaniṣad 2.2.15: “There the sun shines not, nor the moon and stars”. "}, "ud1.10:14.4": {"pli": "ādicco nappakāsati; ", "eng": "nor does the sun shed its light; ", "html": "<span class='verse-line'>{}</span>"}, "ud1.10:14.5": {"pli": "Na tattha candimā bhāti, ", "eng": "there the moon glows not, ", "html": "<span class='verse-line'>{}</span>"}, "ud1.10:14.6": {"pli": "tamo tattha na vijjati. ", "eng": "yet no darkness is found. ", "html": "<span class='verse-line'>{}</span></p>"}, "ud1.10:15.1": {"pli": "Yadā ca attanāvedi, ", "eng": "And when a sage, a brahmin, finds understanding ", "html": "<p><span class='verse-line'>{}</span>"}, "ud1.10:15.2": {"pli": "muni monena brāhmaṇo; ", "eng": "through their own sagacity, ", "html": "<span class='verse-line'>{}</span>"}, "ud1.10:15.3": {"pli": "Atha rūpā arūpā ca, ", "eng": "then, from forms and formless, ", "html": "<span class='verse-line'>{}</span>"}, "ud1.10:15.4": {"pli": "sukhadukkhā pamuccatī”ti. ", "eng": "from pleasure and pain they are released.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud1.10:15.5": {"pli": "Dasamaṁ. ", "html": "<p class='endsutta'>{}</p>"}, "ud1.10:16.1": {"pli": "Ayampi udāno vutto bhagavatā iti me sutanti. ", "eng": "This too is a heartfelt saying that was spoken by the Blessed One: that is what I heard. ", "html": "<p>{}</p>", "comm": "This tag is unique to the Udāna, and seems to be a relic of an earlier oral organization. It is virtually identical to the tag concluding each discourse of the Itivuttaka, except there we find <i lang='pi' translate='no'>attha</i> instead of <i lang='pi' translate='no'>udāna</i>. "}, "ud1.10:16.2": {"pli": "Bodhivaggo paṭhamo. ", "html": "<p class='endvagga'>{}</p>"}, "ud1.10:17.0": {"pli": "Tassuddānaṁ ", "html": "<p class='uddana-intro'>{}</p>"}, "ud1.10:17.1": {"pli": "Tayo bodhī ca huṁhuṅko, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "ud1.10:17.2": {"pli": "brāhmaṇā kassapena ca; ", "html": "<span class='verse-line'>{}</span>"}, "ud1.10:17.3": {"pli": "Aja saṅgāma jaṭilā, ", "html": "<span class='verse-line'>{}</span>"}, "ud1.10:17.4": {"pli": "bāhiyenāti te dasāti. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}}, "ud2.1": {"ud2.1:0.1": {"pli": "Udāna 2.1 ", "eng": "Heartfelt Sayings 2.1 ", "html": "<article id='ud2.1'><header><ul><li class='division'>{}</li></ul>"}, "ud2.1:0.2": {"pli": "Mucalindasutta ", "eng": "With Mucalinda ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud2.1:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud2.1:1.2": {"pli": "ekaṁ samayaṁ bhagavā uruvelāyaṁ viharati najjā nerañjarāya tīre mucalindamūle paṭhamābhisambuddho. ", "eng": "At one time, when he was first awakened, the Buddha was staying in Uruvelā at the root of the Mucalinda tree on the bank of the Nerañjarā River. ", "html": "{}"}, "ud2.1:1.3": {"pli": "Tena kho pana samayena bhagavā sattāhaṁ ekapallaṅkena nisinno hoti vimuttisukhapaṭisaṁvedī. ", "eng": "There the Buddha sat cross-legged for seven days without moving, experiencing the bliss of freedom. ", "html": "{}</p>"}, "ud2.1:2.1": {"pli": "Tena kho pana samayena mahā akālamegho udapādi sattāhavaddalikā sītavātaduddinī. ", "eng": "Just then a great storm blew up out of season, bringing seven days of precipitation, with cold winds and overcast skies. ", "html": "<p>{}"}, "ud2.1:2.2": {"pli": "Atha kho mucalindo nāgarājā sakabhavanā nikkhamitvā bhagavato kāyaṁ sattakkhattuṁ bhogehi parikkhipitvā uparimuddhani mahantaṁ phaṇaṁ vihacca aṭṭhāsi: ", "eng": "Mucalinda, the dragon king, came out from his abode, encircled the Buddha’s body with seven coils and spread his large hood over his head, thinking, ", "html": "{}"}, "ud2.1:2.3": {"pli": "“mā bhagavantaṁ sītaṁ, mā bhagavantaṁ uṇhaṁ, mā bhagavantaṁ ḍaṁsamakasavātātapasarīsapasamphasso”ti. ", "eng": "“May the Buddha not be hot or cold, nor be bothered by flies, mosquitoes, wind, sun, or reptiles.” ", "html": "{}</p>"}, "ud2.1:3.1": {"pli": "Atha kho bhagavā tassa sattāhassa accayena tamhā samādhimhā vuṭṭhāsi. ", "eng": "When seven days had passed, the Buddha emerged from that state of immersion. ", "html": "<p>{}"}, "ud2.1:3.2": {"pli": "Atha kho mucalindo nāgarājā viddhaṁ vigatavalāhakaṁ devaṁ viditvā bhagavato kāyā bhoge viniveṭhetvā sakavaṇṇaṁ paṭisaṁharitvā māṇavakavaṇṇaṁ abhinimminitvā bhagavato purato aṭṭhāsi pañjaliko bhagavantaṁ namassamāno. ", "eng": "When he knew the heavens were clear and cloudless, Mucalinda unwrapped his coils from the Buddha’s body. Hiding his own form, he manifested in the form of a brahmin youth. He stood in front of the Buddha, venerating him with joined palms. ", "html": "{}</p>"}, "ud2.1:4.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud2.1:5.1": {"pli": "“Sukho viveko tuṭṭhassa, ", "eng": "“Seclusion is happiness for the contented ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud2.1:5.2": {"pli": "sutadhammassa passato; ", "eng": "who see the teaching they have learned. ", "html": "<span class='verse-line'>{}</span>"}, "ud2.1:5.3": {"pli": "Abyāpajjaṁ sukhaṁ loke, ", "eng": "Kindness for the world is happiness ", "html": "<span class='verse-line'>{}</span>"}, "ud2.1:5.4": {"pli": "pāṇabhūtesu saṁyamo. ", "eng": "for one who’d not harm a living creature. ", "html": "<span class='verse-line'>{}</span></p>"}, "ud2.1:6.1": {"pli": "Sukhā virāgatā loke, ", "eng": "Dispassion for the world is happiness ", "html": "<p><span class='verse-line'>{}</span>"}, "ud2.1:6.2": {"pli": "kāmānaṁ samatikkamo; ", "eng": "for one who has gone beyond sensual pleasures. ", "html": "<span class='verse-line'>{}</span>"}, "ud2.1:6.3": {"pli": "Asmimānassa yo vinayo, ", "eng": "But dispelling the conceit ‘I am’ ", "html": "<span class='verse-line'>{}</span>"}, "ud2.1:6.4": {"pli": "etaṁ ve paramaṁ sukhan”ti. ", "eng": "is truly the ultimate happiness.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud2.1:6.5": {"pli": "Paṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud2.2": {"ud2.2:0.1": {"pli": "Udāna 2.2 ", "eng": "Heartfelt Sayings 2.2 ", "html": "<article id='ud2.2'><header><ul><li class='division'>{}</li></ul>"}, "ud2.2:0.2": {"pli": "Rājasutta ", "eng": "Kings ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud2.2:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud2.2:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud2.2:1.3": {"pli": "Tena kho pana samayena sambahulānaṁ bhikkhūnaṁ pacchābhattaṁ piṇḍapātapaṭikkantānaṁ upaṭṭhānasālāyaṁ sannisinnānaṁ sannipatitānaṁ ayamantarākathā udapādi: ", "eng": "Now at that time, after the meal, on return from almsround, several mendicants sat together in the assembly hall and this discussion came up among them: ", "html": "{}"}, "ud2.2:1.4": {"pli": "“ko nu kho, āvuso, imesaṁ dvinnaṁ rājūnaṁ mahaddhanataro vā mahābhogataro vā mahākosataro vā mahāvijitataro vā mahāvāhanataro vā mahabbalataro vā mahiddhikataro vā mahānubhāvataro vā rājā vā māgadho seniyo bimbisāro, rājā vā pasenadi kosalo”ti? ", "eng": "“Which of these two kings has greater wealth, riches, treasury, dominion, vehicles, forces, might, and power: King Seniya Bimbisāra of Magadha or King Pasenadi of Kosala?” ", "html": "{}"}, "ud2.2:1.5": {"pli": "Ayañcarahi tesaṁ bhikkhūnaṁ antarākathā hoti vippakatā. ", "eng": "At that point the conversation among those mendicants was left unfinished. ", "html": "{}</p>"}, "ud2.2:2.1": {"pli": "Atha kho bhagavā sāyanhasamayaṁ paṭisallānā vuṭṭhito yenupaṭṭhānasālā tenupasaṅkami; upasaṅkamitvā paññatte āsane nisīdi. ", "eng": "Then in the late afternoon, the Buddha came out of retreat, went to the assembly hall, sat down on the seat spread out, ", "html": "<p>{}"}, "ud2.2:2.2": {"pli": "Nisajja kho bhagavā bhikkhū āmantesi: ", "eng": "and addressed the mendicants: ", "html": "{}"}, "ud2.2:2.3": {"pli": "“kāya nuttha, bhikkhave, etarahi kathāya sannisinnā sannipatitā, kā ca pana vo antarākathā vippakatā”ti? ", "eng": "“Mendicants, what were you sitting talking about just now? What conversation was left unfinished?” ", "html": "{}</p>"}, "ud2.2:3.1": {"pli": "“Idha, bhante, amhākaṁ pacchābhattaṁ piṇḍapātapaṭikkantānaṁ upaṭṭhānasālāyaṁ sannisinnānaṁ sannipatitānaṁ ayamantarākathā udapādi: ", "eng": "So the mendicants told him what they had been talking about when the Buddha arrived. The Buddha said, ", "html": "<p>{}"}, "ud2.2:3.2": {"pli": "‘ko nu kho, āvuso, imesaṁ dvinnaṁ rājūnaṁ mahaddhanataro vā mahābhogataro vā mahākosataro vā mahāvijitataro vā mahāvāhanataro vā mahabbalataro vā mahiddhikataro vā mahānubhāvataro vā rājā vā māgadho seniyo bimbisāro, rājā vā pasenadi kosalo’ti? ", "html": "{}"}, "ud2.2:3.3": {"pli": "Ayaṁ kho no, bhante, antarākathā vippakatā, atha bhagavā anuppatto”ti. ", "html": "{}</p>"}, "ud2.2:4.1": {"pli": "“Na khvetaṁ, bhikkhave, tumhākaṁ patirūpaṁ kulaputtānaṁ saddhā agārasmā anagāriyaṁ pabbajitānaṁ yaṁ tumhe evarūpiṁ kathaṁ katheyyātha. ", "eng": "“Mendicants, it is not appropriate for you gentlemen who have gone forth out of faith from the lay life to homelessness to talk about such things. ", "html": "<p>{}"}, "ud2.2:4.2": {"pli": "Sannipatitānaṁ vo, bhikkhave, dvayaṁ karaṇīyaṁ—", "eng": "When you’re sitting together you should do one of two things: ", "html": "{}"}, "ud2.2:4.3": {"pli": "dhammī vā kathā ariyo vā tuṇhībhāvo”ti. ", "eng": "discuss the teachings or keep noble silence.” ", "html": "{}</p>"}, "ud2.2:5.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud2.2:6.1": {"pli": "“Yañca kāmasukhaṁ loke, ", "eng": "“Neither the pleasures of the senses, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud2.2:6.2": {"pli": "yañcidaṁ diviyaṁ sukhaṁ; ", "eng": "nor even divine happiness, ", "html": "<span class='verse-line'>{}</span>"}, "ud2.2:6.3": {"pli": "Taṇhakkhayasukhassete, ", "eng": "is worth even a sixteenth part ", "html": "<span class='verse-line'>{}</span>"}, "ud2.2:6.4": {"pli": "kalaṁ nāgghanti soḷasin”ti. ", "eng": "of the happiness of craving’s end.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud2.2:6.5": {"pli": "Dutiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud2.3": {"ud2.3:0.1": {"pli": "Udāna 2.3 ", "eng": "Heartfelt Sayings 2.3 ", "html": "<article id='ud2.3'><header><ul><li class='division'>{}</li></ul>"}, "ud2.3:0.2": {"pli": "Daṇḍasutta ", "eng": "A Stick ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud2.3:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud2.3:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud2.3:1.3": {"pli": "Tena kho pana samayena sambahulā kumārakā antarā ca sāvatthiṁ antarā ca jetavanaṁ ahiṁ daṇḍena hananti. ", "eng": "Now at that time, between Sāvatthī and the Jeta Grove, several boys were hitting a snake with a stick. ", "html": "{}"}, "ud2.3:1.4": {"pli": "Atha kho bhagavā pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya sāvatthiṁ piṇḍāya pāvisi. ", "eng": "Then the Buddha robed up in the morning and, taking his bowl and robe, entered Sāvatthī for alms. ", "html": "{}"}, "ud2.3:1.5": {"pli": "Addasā kho bhagavā sambahule kumārake antarā ca sāvatthiṁ antarā ca jetavanaṁ ahiṁ daṇḍena hanante. ", "eng": "He saw the boys hitting the snake. ", "html": "{}</p>"}, "ud2.3:2.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud2.3:3.1": {"pli": "“Sukhakāmāni bhūtāni, ", "eng": "“Creatures love happiness, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud2.3:3.2": {"pli": "yo daṇḍena vihiṁsati; ", "eng": "so if you harm them with a stick ", "html": "<span class='verse-line'>{}</span>"}, "ud2.3:3.3": {"pli": "Attano sukhamesāno, ", "eng": "in search of your own happiness, ", "html": "<span class='verse-line'>{}</span>"}, "ud2.3:3.4": {"pli": "pecca so na labhate sukhaṁ. ", "eng": "after death you’ll find no happiness. ", "html": "<span class='verse-line'>{}</span></p>"}, "ud2.3:4.1": {"pli": "Sukhakāmāni bhūtāni, ", "eng": "Creatures love happiness, ", "html": "<p><span class='verse-line'>{}</span>"}, "ud2.3:4.2": {"pli": "yo daṇḍena na hiṁsati; ", "eng": "so if you don’t harm them with a stick ", "html": "<span class='verse-line'>{}</span>"}, "ud2.3:4.3": {"pli": "Attano sukhamesāno, ", "eng": "in search of your own happiness, ", "html": "<span class='verse-line'>{}</span>"}, "ud2.3:4.4": {"pli": "pecca so labhate sukhan”ti. ", "eng": "after death you will find happiness.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud2.3:4.5": {"pli": "Tatiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud2.4": {"ud2.4:0.1": {"pli": "Udāna 2.4 ", "eng": "Heartfelt Sayings 2.4 ", "html": "<article id='ud2.4'><header><ul><li class='division'>{}</li></ul>"}, "ud2.4:0.2": {"pli": "Sakkārasutta ", "eng": "Esteem ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud2.4:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud2.4:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud2.4:1.3": {"pli": "Tena kho pana samayena bhagavā sakkato hoti garukato mānito pūjito apacito, lābhī cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhārānaṁ. ", "eng": "Now at that time the Buddha was honored, respected, revered, venerated, and esteemed. And he received robes, almsfood, lodgings, and medicines and supplies for the sick. ", "html": "{}"}, "ud2.4:1.4": {"pli": "Bhikkhusaṅghopi sakkato hoti garukato mānito pūjito apacito, lābhī cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhārānaṁ. ", "eng": "And the mendicant Saṅgha was also honored, respected, revered, venerated, and esteemed. And they received robes, almsfood, lodgings, and medicines and supplies for the sick. ", "html": "{}"}, "ud2.4:1.5": {"pli": "Aññatitthiyā pana paribbājakā asakkatā honti agarukatā amānitā apūjitā anapacitā, na lābhino cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhārānaṁ. ", "eng": "But the wanderers who followed other religions were not honored, respected, revered, venerated, and esteemed. And they didn’t receive robes, almsfood, lodgings, and medicines and supplies for the sick. ", "html": "{}"}, "ud2.4:1.6": {"pli": "Atha kho te aññatitthiyā paribbājakā bhagavato sakkāraṁ asahamānā bhikkhusaṅghassa ca, gāme ca araññe ca bhikkhū disvā asabbhāhi pharusāhi vācāhi akkosanti paribhāsanti rosenti vihesenti. ", "eng": "Then those wanderers who followed other religions, unable to bear the esteem of the mendicant Sangha, abused, attacked, harassed, and troubled the mendicants in the village and the wilderness. ", "html": "{}</p>"}, "ud2.4:2.1": {"pli": "Atha kho sambahulā bhikkhū yena bhagavā tenupasaṅkamiṁsu; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdiṁsu. Ekamantaṁ nisinnā kho te bhikkhū bhagavantaṁ etadavocuṁ: ", "eng": "Then several mendicants went up to the Buddha, bowed, sat down to one side, and told him what had happened. ", "html": "<p>{}"}, "ud2.4:2.2": {"pli": "“etarahi, bhante, bhagavā sakkato garukato mānito pūjito apacito, lābhī cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhārānaṁ. ", "html": "{}"}, "ud2.4:2.3": {"pli": "Bhikkhusaṅghopi sakkato garukato mānito pūjito apacito, lābhī cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhārānaṁ. ", "html": "{}"}, "ud2.4:2.4": {"pli": "Aññatitthiyā pana paribbājakā asakkatā agarukatā amānitā apūjitā anapacitā, na lābhino cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhārānaṁ. ", "html": "{}"}, "ud2.4:2.5": {"pli": "Atha kho te, bhante, aññatitthiyā paribbājakā bhagavato sakkāraṁ asahamānā bhikkhusaṅghassa ca, gāme ca araññe ca bhikkhū disvā asabbhāhi pharusāhi vācāhi akkosanti paribhāsanti rosenti vihesantī”ti. ", "html": "{}</p>"}, "ud2.4:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud2.4:4.1": {"pli": "“Gāme araññe sukhadukkhaphuṭṭho, ", "eng": "“When struck by pleasure and pain <j>in the village or wilderness, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud2.4:4.2": {"pli": "Nevattato no parato dahetha; ", "eng": "regard it not as self or alien. ", "html": "<span class='verse-line'>{}</span>", "comm": "The suttas vary in how they treat <i lang='pi' translate='no'>parato</i> (“as alien”). Here one is urged to disidentify from seeing both “as self” and “as alien”, whereas elsewhere seeing things “as alien” is equivalent to seeing them as not-self (<a href='index.html?q=thag20.1#22.2'>Thag 20.1:22.2</a>, <a href='index.html?q=mn74#9.1'>MN 74:9.1</a>). "}, "ud2.4:4.3": {"pli": "Phusanti phassā upadhiṁ paṭicca, ", "eng": "Contacts strike because of attachment; ", "html": "<span class='verse-line'>{}</span>"}, "ud2.4:4.4": {"pli": "Nirūpadhiṁ kena phuseyyu phassā”ti. ", "eng": "how would contacts strike one free of attachment?” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud2.4:4.5": {"pli": "Catutthaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud2.5": {"ud2.5:0.1": {"pli": "Udāna 2.5 ", "eng": "Heartfelt Sayings 2.5 ", "html": "<article id='ud2.5'><header><ul><li class='division'>{}</li></ul>"}, "ud2.5:0.2": {"pli": "Upāsakasutta ", "eng": "A Lay Follower ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud2.5:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud2.5:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud2.5:1.3": {"pli": "Tena kho pana samayena aññataro icchānaṅgalako upāsako sāvatthiṁ anuppatto hoti kenacideva karaṇīyena. ", "eng": "Now at that time a certain lay follower from Icchānaṅgalaka arrived at Sāvatthī on some business. ", "html": "{}"}, "ud2.5:1.4": {"pli": "Atha kho so upāsako sāvatthiyaṁ taṁ karaṇīyaṁ tīretvā yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. ", "eng": "Having concluded his business in Sāvatthī he went to see the Buddha, bowed, and sat down to one side. ", "html": "{}"}, "ud2.5:1.5": {"pli": "Ekamantaṁ nisinnaṁ kho taṁ upāsakaṁ bhagavā etadavoca: ", "eng": "The Buddha said this to him: ", "html": "{}"}, "ud2.5:1.6": {"pli": "“cirassaṁ kho tvaṁ, upāsaka, imaṁ pariyāyamakāsi yadidaṁ idhāgamanāyā”ti. ", "eng": "“It’s been a long time, lay follower, since you took the opportunity to come here.” ", "html": "{}</p>"}, "ud2.5:2.1": {"pli": "“Cirapaṭikāhaṁ, bhante, bhagavantaṁ dassanāya upasaṅkamitukāmo, api cāhaṁ kehici kehici kiccakaraṇīyehi byāvaṭo. Evāhaṁ nāsakkhiṁ bhagavantaṁ dassanāya upasaṅkamitun”ti. ", "eng": "“For a long time I’ve wanted to come and see the Buddha, but I wasn’t able, being prevented by my many duties and responsibilities.” ", "html": "<p>{}</p>"}, "ud2.5:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud2.5:4.1": {"pli": "“Sukhaṁ vata tassa na hoti kiñci, ", "eng": "“One who has nothing is happy indeed, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud2.5:4.2": {"pli": "Saṅkhātadhammassa bahussutassa; ", "eng": "a learned person who has appraised the teaching. ", "html": "<span class='verse-line'>{}</span>"}, "ud2.5:4.3": {"pli": "Sakiñcanaṁ passa vihaññamānaṁ, ", "eng": "See how troubled are those with attachments, ", "html": "<span class='verse-line'>{}</span>"}, "ud2.5:4.4": {"pli": "Jano janasmiṁ paṭibandharūpo”ti. ", "eng": "a person bound tight to people.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud2.5:4.5": {"pli": "Pañcamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud2.6": {"ud2.6:0.1": {"pli": "Udāna 2.6 ", "eng": "Heartfelt Sayings 2.6 ", "html": "<article id='ud2.6'><header><ul><li class='division'>{}</li></ul>"}, "ud2.6:0.2": {"pli": "Gabbhinīsutta ", "eng": "The Pregnant Woman ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud2.6:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud2.6:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud2.6:1.3": {"pli": "Tena kho pana samayena aññatarassa paribbājakassa daharamāṇavikā pajāpati hoti gabbhinī upavijaññā. ", "eng": "Now at that time a certain wanderer had a young brahmin wife who was pregnant and about to give birth. ", "html": "{}"}, "ud2.6:1.4": {"pli": "Atha kho sā paribbājikā taṁ paribbājakaṁ etadavoca: ", "eng": "She said to him, ", "html": "{}"}, "ud2.6:1.5": {"pli": "“gaccha tvaṁ, brāhmaṇa, telaṁ āhara, yaṁ me vijātāya bhavissatī”ti. ", "eng": "“Go, brahmin, bring oil for my delivery.” ", "html": "{}</p>"}, "ud2.6:2.1": {"pli": "Evaṁ vutte, so paribbājako taṁ paribbājikaṁ etadavoca: ", "eng": "The wanderer said, ", "html": "<p>{}"}, "ud2.6:2.2": {"pli": "“kuto panāhaṁ, bhoti, telaṁ āharāmī”ti? ", "eng": "“But where, my dear, can I get oil?” ", "html": "{}"}, "ud2.6:2.3": {"pli": "Dutiyampi kho sā paribbājikā taṁ paribbājakaṁ etadavoca: ", "eng": "For a second time, she said, ", "html": "{}"}, "ud2.6:2.4": {"pli": "“gaccha tvaṁ, brāhmaṇa, telaṁ āhara, yaṁ me vijātāya bhavissatī”ti. ", "eng": "“Go, brahmin, bring oil for my delivery.” ", "html": "{}"}, "ud2.6:2.5": {"pli": "Dutiyampi kho so paribbājako taṁ paribbājikaṁ etadavoca: ", "eng": "For a second time, the wanderer said, ", "html": "{}"}, "ud2.6:2.6": {"pli": "“kuto panāhaṁ, bhoti, telaṁ āharāmī”ti? ", "eng": "“But where, my dear, can I get oil?” ", "html": "{}"}, "ud2.6:2.7": {"pli": "Tatiyampi kho sā paribbājikā taṁ paribbājakaṁ etadavoca: ", "eng": "For a third time, she said, ", "html": "{}"}, "ud2.6:2.8": {"pli": "“gaccha tvaṁ, brāhmaṇa, telaṁ āhara, yaṁ me vijātāya bhavissatī”ti. ", "eng": "“Go, brahmin, bring oil for my delivery.” ", "html": "{}</p>"}, "ud2.6:3.1": {"pli": "Tena kho pana samayena rañño pasenadissa kosalassa koṭṭhāgāre samaṇassa vā brāhmaṇassa vā sappissa vā telassa vā yāvadatthaṁ pātuṁ dīyati, no nīharituṁ. ", "eng": "Now at that time ghee and oil were being given away to any ascetic or brahmin at the storehouse of King Pasenadi of Kosala. But it was only to drink there, not to take away. ", "html": "<p>{}</p>"}, "ud2.6:4.1": {"pli": "Atha kho tassa paribbājakassa etadahosi: ", "eng": "Knowing this, the wanderer thought, ", "html": "<p>{}"}, "ud2.6:4.2": {"pli": "“rañño kho pana pasenadissa kosalassa koṭṭhāgāre samaṇassa vā brāhmaṇassa vā sappissa vā telassa vā yāvadatthaṁ pātuṁ dīyati, no nīharituṁ. ", "html": "{}"}, "ud2.6:4.3": {"pli": "Yannūnāhaṁ rañño pasenadissa kosalassa koṭṭhāgāraṁ gantvā telassa yāvadatthaṁ pivitvā gharaṁ āgantvā ucchadditvāna dadeyyaṁ, yaṁ imissā vijātāya bhavissatī”ti. ", "eng": "“Why don’t I go to the king’s storehouse, drink as much oil as I can, then come home and throw it up so it can be used for the delivery?” ", "html": "{}</p>"}, "ud2.6:5.1": {"pli": "Atha kho so paribbājako rañño pasenadissa kosalassa koṭṭhāgāraṁ gantvā telassa yāvadatthaṁ pivitvā gharaṁ āgantvā neva sakkoti uddhaṁ kātuṁ, na pana adho. ", "eng": "Then he did just that. But when he got home he was unable to either bring it up or pass it out. ", "html": "<p>{}"}, "ud2.6:5.2": {"pli": "So dukkhāhi tibbāhi kharāhi kaṭukāhi vedanāhi phuṭṭho āvaṭṭati parivaṭṭati. ", "eng": "He rolled to and fro, suffering painful, sharp, severe, acute feelings. ", "html": "{}</p>"}, "ud2.6:6.1": {"pli": "Atha kho bhagavā pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya sāvatthiṁ piṇḍāya pāvisi. ", "eng": "Then the Buddha robed up in the morning and, taking his bowl and robe, entered Sāvatthī for alms. ", "html": "<p>{}"}, "ud2.6:6.2": {"pli": "Addasā kho bhagavā taṁ paribbājakaṁ dukkhāhi tibbāhi kharāhi kaṭukāhi vedanāhi phuṭṭhaṁ āvaṭṭamānaṁ parivaṭṭamānaṁ. ", "eng": "He saw the wanderer in agony. ", "html": "{}</p>"}, "ud2.6:7.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud2.6:8.1": {"pli": "“Sukhino vata ye akiñcanā, ", "eng": "“Oh! How happy are those with nothing! ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud2.6:8.2": {"pli": "Vedaguno hi janā akiñcanā; ", "eng": "Hence knowledge masters are people with nothing. ", "html": "<span class='verse-line'>{}</span>"}, "ud2.6:8.3": {"pli": "Sakiñcanaṁ passa vihaññamānaṁ, ", "eng": "See how troubled are those with attachments, ", "html": "<span class='verse-line'>{}</span>"}, "ud2.6:8.4": {"pli": "Jano janasmiṁ paṭibandhacitto”ti. ", "eng": "a person bound tight to people.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud2.6:8.5": {"pli": "Chaṭṭhaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud2.7": {"ud2.7:0.1": {"pli": "Udāna 2.7 ", "eng": "Heartfelt Sayings 2.7 ", "html": "<article id='ud2.7'><header><ul><li class='division'>{}</li></ul>"}, "ud2.7:0.2": {"pli": "Ekaputtakasutta ", "eng": "An Only Son ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud2.7:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud2.7:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud2.7:1.3": {"pli": "Tena kho pana samayena aññatarassa upāsakassa ekaputtako piyo manāpo kālaṅkato hoti. ", "eng": "Now at that time a certain lay follower’s dear and beloved only child passed away. ", "html": "{}</p>"}, "ud2.7:2.1": {"pli": "Atha kho sambahulā upāsakā allavatthā allakesā divā divassa yena bhagavā tenupasaṅkamiṁsu; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdiṁsu. Ekamantaṁ nisinne kho te upāsake bhagavā etadavoca: ", "eng": "Then in the middle of the day several lay followers with wet clothes and hair went up to the Buddha, bowed, and sat down to one side. The Buddha said to them: ", "html": "<p>{}"}, "ud2.7:2.2": {"pli": "“kiṁ nu kho tumhe, upāsakā, allavatthā allakesā idhūpasaṅkamantā divā divassā”ti? ", "eng": "“Why, lay followers, have you come here in the middle of the day with wet clothes and hair?” ", "html": "{}</p>"}, "ud2.7:3.1": {"pli": "Evaṁ vutte, so upāsako bhagavantaṁ etadavoca: ", "eng": "The lay follower replied, ", "html": "<p>{}"}, "ud2.7:3.2": {"pli": "“mayhaṁ kho, bhante, ekaputtako piyo manāpo kālaṅkato. ", "eng": "“Sir, my dear and beloved only child has passed away. ", "html": "{}"}, "ud2.7:3.3": {"pli": "Tena mayaṁ allavatthā allakesā idhūpasaṅkamantā divā divassā”ti. ", "eng": "That’s why we came here in the middle of the day with wet clothes and hair.” ", "html": "{}</p>"}, "ud2.7:4.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud2.7:5.1": {"pli": "“Piyarūpassādagadhitāse, ", "eng": "“Hosts of gods and most human beings are bound ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>", "comm": "The PTS reading <i lang='pi' translate='no'>piyarūpāsāta</i> is tempting, and supported by the Udānavarga’s <i lang='pi' translate='no'>priyarūpasātagrathitā</i>. The commentary reads <i lang='pi' translate='no'>sukhavedanassādena</i>, which appears to support <i lang='pi' translate='no'>assāda</i>. But perhaps originally <i lang='pi' translate='no'>sāta</i> was glossed with <i lang='pi' translate='no'>sukhavedanā</i>, and <i lang='pi' translate='no'>assāda</i> was read back into the text by mistake. "}, "ud2.7:5.2": {"pli": "Devakāyā puthu manussā ca; ", "eng": "to what seems dear and pleasant. ", "html": "<span class='verse-line'>{}</span>"}, "ud2.7:5.3": {"pli": "Aghāvino parijunnā, ", "eng": "Miserable and exhausted, ", "html": "<span class='verse-line'>{}</span>"}, "ud2.7:5.4": {"pli": "Maccurājassa vasaṁ gacchanti. ", "eng": "they fall under the sway of the King of Death. ", "html": "<span class='verse-line'>{}</span></p>"}, "ud2.7:6.1": {"pli": "Ye ve divā ca ratto ca, ", "eng": "The diligent, who day and night ", "html": "<p><span class='verse-line'>{}</span>"}, "ud2.7:6.2": {"pli": "Appamattā jahanti piyarūpaṁ; ", "eng": "give up what seems pleasant, ", "html": "<span class='verse-line'>{}</span>"}, "ud2.7:6.3": {"pli": "Te ve khaṇanti aghamūlaṁ, ", "eng": "dig out the root of gloom—", "html": "<span class='verse-line'>{}</span>"}, "ud2.7:6.4": {"pli": "Maccuno āmisaṁ durativattan”ti. ", "eng": "Death’s bait so hard to escape.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud2.7:6.5": {"pli": "Sattamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud2.8": {"ud2.8:0.1": {"pli": "Udāna 2.8 ", "eng": "Heartfelt Sayings 2.8 ", "html": "<article id='ud2.8'><header><ul><li class='division'>{}</li></ul>"}, "ud2.8:0.2": {"pli": "Suppavāsāsutta ", "eng": "Suppavāsā ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud2.8:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud2.8:1.2": {"pli": "ekaṁ samayaṁ bhagavā kuṇḍikāyaṁ viharati kuṇḍadhānavane. ", "eng": "At one time the Buddha was staying near Kuṇḍiyā in the Kuṇḍadhāna Grove. ", "html": "{}"}, "ud2.8:1.3": {"pli": "Tena kho pana samayena suppavāsā koliyadhītā satta vassāni gabbhaṁ dhāreti. ", "eng": "Now at that time Suppavāsā the Koliyan had been pregnant for seven years, and in obstructed labor for seven days. ", "html": "{}", "comm": "<i lang='pi' translate='no'>Mūḷha</i> here has the sense of “gone astray” in reference to obstructed labor that prevents birth; see <a href='index.html?q=mn86#14.2'>MN 86:14.2</a>. "}, "ud2.8:1.4": {"pli": "Sattāhaṁ mūḷhagabbhā sā dukkhāhi tibbāhi kharāhi kaṭukāhi vedanāhi phuṭṭhā tīhi vitakkehi adhivāseti: ", "eng": "While suffering painful, sharp, severe, acute feelings, three thoughts helped her endure: ", "html": "{}"}, "ud2.8:1.5": {"pli": "“sammāsambuddho vata so bhagavā yo imassa evarūpassa dukkhassa pahānāya dhammaṁ deseti; ", "eng": "“Oh! The Blessed One is indeed a fully awakened Buddha, who teaches the Dhamma for giving up suffering such as this. ", "html": "{}"}, "ud2.8:1.6": {"pli": "suppaṭipanno vata tassa bhagavato sāvakasaṅgho yo imassa evarūpassa dukkhassa pahānāya paṭipanno; ", "eng": "Oh! The Saṅgha of the Buddha’s disciples is indeed practicing well, who practice for giving up suffering such as this. ", "html": "{}"}, "ud2.8:1.7": {"pli": "susukhaṁ vata taṁ nibbānaṁ yatthidaṁ evarūpaṁ dukkhaṁ na saṁvijjatī”ti. ", "eng": "Oh! Extinguishment is so very blissful, where such suffering as this is not found.” ", "html": "{}</p>"}, "ud2.8:2.1": {"pli": "Atha kho suppavāsā koliyadhītā sāmikaṁ āmantesi: ", "eng": "Then Suppavāsā addressed her husband, ", "html": "<p>{}"}, "ud2.8:2.2": {"pli": "“ehi tvaṁ, ayyaputta, yena bhagavā tenupasaṅkama; upasaṅkamitvā mama vacanena bhagavato pāde sirasā vandāhi; appābādhaṁ appātaṅkaṁ lahuṭṭhānaṁ balaṁ phāsuvihāraṁ puccha: ", "eng": "“Please, master, go to the Buddha, and in my name bow with your head to his feet. Ask him if he is healthy and well, nimble, strong, and living comfortably. ", "html": "{}"}, "ud2.8:2.3": {"pli": "‘suppavāsā, bhante, koliyadhītā bhagavato pāde sirasā vandati; appābādhaṁ appātaṅkaṁ lahuṭṭhānaṁ balaṁ phāsuvihāraṁ pucchatī’ti. ", "html": "{}"}, "ud2.8:2.4": {"pli": "Evañca vadehi: ", "eng": "And then say: ", "html": "{}"}, "ud2.8:2.5": {"pli": "‘suppavāsā, bhante, koliyadhītā satta vassāni gabbhaṁ dhāreti. ", "eng": "‘Suppavāsā the Koliyan has been pregnant for seven years, and in obstructed labor for seven days. ", "html": "{}"}, "ud2.8:2.6": {"pli": "Sattāhaṁ mūḷhagabbhā sā dukkhāhi tibbāhi kharāhi kaṭukāhi vedanāhi phuṭṭhā tīhi vitakkehi adhivāseti—", "eng": "While suffering painful, sharp, severe, acute feelings, three thoughts help her endure: ", "html": "{}"}, "ud2.8:2.7": {"pli": "sammāsambuddho vata so bhagavā yo imassa evarūpassa dukkhassa pahānāya dhammaṁ deseti; ", "eng": "“Oh! The Blessed One is indeed a fully awakened Buddha, who teaches the Dhamma for giving up suffering such as this. ", "html": "{}"}, "ud2.8:2.8": {"pli": "suppaṭipanno vata tassa bhagavato sāvakasaṅgho yo imassa evarūpassa dukkhassa pahānāya paṭipanno; ", "eng": "Oh! The Saṅgha of the Buddha’s disciples is indeed practicing well, who practice for giving up suffering such as this. ", "html": "{}"}, "ud2.8:2.9": {"pli": "susukhaṁ vata taṁ nibbānaṁ yatthidaṁ evarūpaṁ dukkhaṁ na saṁvijjatī’”ti. ", "eng": "Oh! Extinguishment is so very blissful, where such suffering as this is not found.”’” ", "html": "{}</p>"}, "ud2.8:3.1": {"pli": "“Paraman”ti kho so koliyaputto suppavāsāya koliyadhītāya paṭissutvā yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. ", "eng": "“Excellent idea,” he replied. He went to the Buddha and told him of his wife’s struggles. The Buddha said: ", "html": "<p>{}"}, "ud2.8:3.2": {"pli": "Ekamantaṁ nisinno kho koliyaputto bhagavantaṁ etadavoca: ", "html": "{}"}, "ud2.8:3.3": {"pli": "“suppavāsā, bhante, koliyadhītā bhagavato pāde sirasā vandati, appābādhaṁ appātaṅkaṁ lahuṭṭhānaṁ balaṁ phāsuvihāraṁ pucchati; ", "html": "{}"}, "ud2.8:3.4": {"pli": "evañca vadeti: ", "html": "{}"}, "ud2.8:3.5": {"pli": "‘suppavāsā, bhante, koliyadhītā satta vassāni gabbhaṁ dhāreti. ", "html": "{}"}, "ud2.8:3.6": {"pli": "Sattāhaṁ mūḷhagabbhā sā dukkhāhi tibbāhi kharāhi kaṭukāhi vedanāhi phuṭṭhā tīhi vitakkehi adhivāseti—", "html": "{}"}, "ud2.8:3.7": {"pli": "sammāsambuddho vata so bhagavā yo imassa evarūpassa dukkhassa pahānāya dhammaṁ deseti; ", "html": "{}"}, "ud2.8:3.8": {"pli": "suppaṭipanno vata tassa bhagavato sāvakasaṅgho yo imassa evarūpassa dukkhassa pahānāya paṭipanno; ", "html": "{}"}, "ud2.8:3.9": {"pli": "susukhaṁ vata nibbānaṁ yatthidaṁ evarūpaṁ dukkhaṁ na saṁvijjatī’”ti. ", "html": "{}</p>"}, "ud2.8:4.1": {"pli": "“Sukhinī hotu suppavāsā koliyadhītā; arogā arogaṁ puttaṁ vijāyatū”ti. ", "eng": "“May Suppavāsā the Koliyan be happy and healthy! May she give birth to a healthy child!” ", "html": "<p>{}"}, "ud2.8:4.2": {"pli": "Saha vacanā ca pana bhagavato suppavāsā koliyadhītā sukhinī arogā arogaṁ puttaṁ vijāyi. ", "eng": "As soon as he spoke, Suppavāsā, happy and healthy, gave birth to a healthy child. ", "html": "{}</p>"}, "ud2.8:5.1": {"pli": "“Evaṁ, bhante”ti kho so koliyaputto bhagavato bhāsitaṁ abhinanditvā anumoditvā uṭṭhāyāsanā bhagavantaṁ abhivādetvā padakkhiṇaṁ katvā yena sakaṁ gharaṁ tena paccāyāsi. ", "eng": "Saying “Yes, sir,” the Koliyan gentleman approved and agreed with what the Buddha said. He got up from his seat, bowed, and respectfully circled the Buddha, keeping him on his right. Then he returned to his own house. ", "html": "<p>{}"}, "ud2.8:5.2": {"pli": "Addasā kho so koliyaputto suppavāsaṁ koliyadhītaraṁ sukhiniṁ arogaṁ arogaṁ puttaṁ vijātaṁ. ", "eng": "He saw that his wife, happy and healthy, had given birth to a healthy child, ", "html": "{}"}, "ud2.8:5.3": {"pli": "Disvānassa etadahosi: ", "eng": "and thought, ", "html": "{}"}, "ud2.8:5.4": {"pli": "“acchariyaṁ vata bho, abbhutaṁ vata bho, tathāgatassa mahiddhikatā mahānubhāvatā, yatra hi nāmāyaṁ suppavāsā koliyadhītā saha vacanā ca pana bhagavato sukhinī arogā arogaṁ puttaṁ vijāyissatī”ti. ", "eng": "“Oh lord, how incredible, how amazing! The Realized One has such psychic power and might! For as soon as he spoke, Suppavāsā, happy and healthy, gave birth to a healthy child.” ", "html": "{}"}, "ud2.8:5.5": {"pli": "Attamano pamudito pītisomanassajāto ahosi. ", "eng": "He became uplifted and overjoyed, full of rapture and happiness. ", "html": "{}</p>"}, "ud2.8:6.1": {"pli": "Atha kho suppavāsā koliyadhītā sāmikaṁ āmantesi: ", "eng": "Then Suppavāsā addressed her husband, ", "html": "<p>{}"}, "ud2.8:6.2": {"pli": "“ehi tvaṁ, ayyaputta, yena bhagavā tenupasaṅkama; upasaṅkamitvā mama vacanena bhagavato pāde sirasā vandāhi: ", "eng": "“Please, master, go to the Buddha, and in my name bow with your head to his feet. ", "html": "{}"}, "ud2.8:6.3": {"pli": "‘suppavāsā, bhante, koliyadhītā bhagavato pāde sirasā vandatī’ti; ", "html": "{}"}, "ud2.8:6.4": {"pli": "evañca vadehi: ", "eng": "And then say, ", "html": "{}"}, "ud2.8:6.5": {"pli": "‘suppavāsā, bhante, koliyadhītā satta vassāni gabbhaṁ dhāreti. ", "eng": "‘Suppavāsā the Koliyan, who was pregnant for seven years, and in obstructed labor for seven days, ", "html": "{}"}, "ud2.8:6.6": {"pli": "Sattāhaṁ mūḷhagabbhā sā etarahi sukhinī arogā arogaṁ puttaṁ vijātā. ", "eng": "is now happy and healthy and has given birth to a healthy child. ", "html": "{}"}, "ud2.8:6.7": {"pli": "Sā sattāhaṁ buddhappamukhaṁ bhikkhusaṅghaṁ bhattena nimanteti. ", "eng": "She invites the mendicant Saṅgha headed by the Buddha to a meal for seven days. ", "html": "{}"}, "ud2.8:6.8": {"pli": "Adhivāsetu kira, bhante, bhagavā suppavāsāya koliyadhītāya satta bhattāni saddhiṁ bhikkhusaṅghenā’”ti. ", "eng": "Sir, might the Buddha please accept seven meals from Suppavāsā.’” ", "html": "{}</p>"}, "ud2.8:7.1": {"pli": "“Paraman”ti kho so koliyaputto suppavāsāya koliyadhītāya paṭissutvā yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. ", "eng": "“Excellent idea,” he replied. He went to the Buddha, told him the good news, and conveyed his wife’s invitation. ", "html": "<p>{}"}, "ud2.8:7.2": {"pli": "Ekamantaṁ nisinno kho so koliyaputto bhagavantaṁ etadavoca: ", "html": "{}</p>"}, "ud2.8:8.1": {"pli": "“Suppavāsā, bhante, koliyadhītā bhagavato pāde sirasā vandati; ", "html": "<p>{}"}, "ud2.8:8.2": {"pli": "evañca vadeti: ", "html": "{}"}, "ud2.8:8.3": {"pli": "‘suppavāsā, bhante, koliyadhītā satta vassāni gabbhaṁ dhāreti. ", "html": "{}"}, "ud2.8:8.4": {"pli": "Sattāhaṁ mūḷhagabbhā sā etarahi sukhinī arogā arogaṁ puttaṁ vijātā. ", "html": "{}"}, "ud2.8:8.5": {"pli": "Sā sattāhaṁ buddhappamukhaṁ bhikkhusaṅghaṁ bhattena nimanteti. ", "html": "{}"}, "ud2.8:8.6": {"pli": "Adhivāsetu kira, bhante, bhagavā suppavāsāya koliyadhītāya satta bhattāni saddhiṁ bhikkhusaṅghenā’”ti. ", "html": "{}</p>"}, "ud2.8:9.1": {"pli": "Tena kho pana samayena aññatarena upāsakena buddhappamukho bhikkhusaṅgho svātanāya bhattena nimantito hoti. ", "eng": "Now at that time a certain lay follower had already invited the Sangha of monks headed by the Buddha for the meal on the following day. ", "html": "<p>{}"}, "ud2.8:9.2": {"pli": "So ca upāsako āyasmato mahāmoggallānassa upaṭṭhāko hoti. ", "eng": "That lay follower was Venerable Mahāmoggallāna’s supporter. ", "html": "{}"}, "ud2.8:9.3": {"pli": "Atha kho bhagavā āyasmantaṁ mahāmoggallānaṁ āmantesi: ", "eng": "Then the Buddha addressed Venerable Mahāmoggallāna, ", "html": "{}"}, "ud2.8:9.4": {"pli": "“ehi tvaṁ, moggallāna, yena so upāsako tenupasaṅkama; upasaṅkamitvā taṁ upāsakaṁ evaṁ vadehi: ", "eng": "“Please, Moggallāna, go to that lay follower and say to him, ", "html": "{}"}, "ud2.8:9.5": {"pli": "‘suppavāsā, āvuso, koliyadhītā satta vassāni gabbhaṁ dhāresi. ", "eng": "‘Suppavāsā the Koliyan, who was pregnant for seven years, and in obstructed labor for seven days, ", "html": "{}"}, "ud2.8:9.6": {"pli": "Sattāhaṁ mūḷhagabbhā sā etarahi sukhinī arogā arogaṁ puttaṁ vijātā. ", "eng": "is now happy and healthy and has given birth to a healthy child. ", "html": "{}"}, "ud2.8:9.7": {"pli": "Sā sattāhaṁ buddhappamukhaṁ bhikkhusaṅghaṁ bhattena nimanteti. ", "eng": "She invites the mendicant Saṅgha headed by the Buddha to a meal for seven days. ", "html": "{}"}, "ud2.8:9.8": {"pli": "Karotu suppavāsā koliyadhītā satta bhattāni, pacchā tvaṁ karissasī’ti. ", "eng": "Let Suppavāsā make seven meals, afterwards you can make yours.’ ", "html": "{}"}, "ud2.8:9.9": {"pli": "Tuyheso upaṭṭhāko”ti. ", "eng": "He is your supporter.” ", "html": "{}</p>"}, "ud2.8:10.1": {"pli": "“Evaṁ, bhante”ti kho āyasmā mahāmoggallāno bhagavato paṭissutvā yena so upāsako tenupasaṅkami; upasaṅkamitvā taṁ upāsakaṁ etadavoca: ", "eng": "“Yes, sir,” replied Mahāmoggallāna. He went to that lay follower and conveyed the Buddha’s request. ", "html": "<p>{}"}, "ud2.8:10.2": {"pli": "“suppavāsā, āvuso, koliyadhītā satta vassāni gabbhaṁ dhāreti. ", "html": "{}"}, "ud2.8:10.3": {"pli": "Sattāhaṁ mūḷhagabbhā sā etarahi sukhinī arogā arogaṁ puttaṁ vijātā. ", "html": "{}"}, "ud2.8:10.4": {"pli": "Sā sattāhaṁ buddhappamukhaṁ bhikkhusaṅghaṁ bhattena nimanteti. ", "html": "{}"}, "ud2.8:10.5": {"pli": "Karotu suppavāsā koliyadhītā satta bhattāni, pacchā tvaṁ karissasī”ti. ", "html": "{}</p>"}, "ud2.8:11.1": {"pli": "“Sace me, bhante, ayyo mahāmoggallāno tiṇṇaṁ dhammānaṁ pāṭibhogo—", "eng": "“If, sir, Master Mahāmoggallāna can guarantee me three things—", "html": "<p>{}"}, "ud2.8:11.2": {"pli": "bhogānañca jīvitassa ca saddhāya ca, ", "eng": "wealth, life, and faith—", "html": "{}"}, "ud2.8:11.3": {"pli": "karotu suppavāsā koliyadhītā satta bhattāni, pacchāhaṁ karissāmī”ti. ", "eng": "then let Suppavāsā make seven meals, afterwards I shall make mine.” ", "html": "{}"}, "ud2.8:11.4": {"pli": "“Dvinnaṁ kho te ahaṁ, āvuso, dhammānaṁ pāṭibhogo—", "eng": "“I can guarantee you two things—", "html": "{}"}, "ud2.8:11.5": {"pli": "bhogānañca jīvitassa ca. ", "eng": "wealth and life. ", "html": "{}"}, "ud2.8:11.6": {"pli": "Saddhāya pana tvaṁyeva pāṭibhogo”ti. ", "eng": "But as for faith, you alone are the guarantor.” ", "html": "{}</p>"}, "ud2.8:12.1": {"pli": "“Sace me, bhante, ayyo mahāmoggallāno dvinnaṁ dhammānaṁ pāṭibhogo—", "eng": "“If, sir Master Mahāmoggallāna can guarantee me two things—", "html": "<p>{}"}, "ud2.8:12.2": {"pli": "bhogānañca jīvitassa ca, ", "eng": "wealth and life—", "html": "{}"}, "ud2.8:12.3": {"pli": "karotu suppavāsā koliyadhītā satta bhattāni, pacchāhaṁ karissāmī”ti. ", "eng": "then let Suppavāsā make seven meals, afterwards I shall make mine.” ", "html": "{}</p>"}, "ud2.8:13.1": {"pli": "Atha kho āyasmā mahāmoggallāno taṁ upāsakaṁ saññāpetvā yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ etadavoca: ", "eng": "Having persuaded that lay follower, Mahāmoggallāna went to the Buddha and said, ", "html": "<p>{}"}, "ud2.8:13.2": {"pli": "“saññatto, bhante, so upāsako mayā; ", "eng": "“I’ve persuaded the lay follower. ", "html": "{}"}, "ud2.8:13.3": {"pli": "karotu suppavāsā koliyadhītā satta bhattāni, pacchā so karissatī”ti. ", "eng": "Let Suppavāsā make seven meals, afterwards he shall make his.” ", "html": "{}</p>"}, "ud2.8:14.1": {"pli": "Atha kho suppavāsā koliyadhītā sattāhaṁ buddhappamukhaṁ bhikkhusaṅghaṁ paṇītena khādanīyena bhojanīyena sahatthā santappesi sampavāresi, ", "eng": "For seven days Suppavāsā served and satisfied the Buddha with her own hands with delicious fresh and cooked foods. ", "html": "<p>{}"}, "ud2.8:14.2": {"pli": "tañca dārakaṁ bhagavantaṁ vandāpesi sabbañca bhikkhusaṅghaṁ. ", "eng": "And she made her little boy bow to the Buddha and the mendicant Sangha. ", "html": "{}</p>"}, "ud2.8:15.1": {"pli": "Atha kho āyasmā sāriputto taṁ dārakaṁ etadavoca: ", "eng": "Then Sāriputta said to the boy, ", "html": "<p>{}"}, "ud2.8:15.2": {"pli": "“kacci te, dāraka, khamanīyaṁ, kacci yāpanīyaṁ, kacci na kiñci dukkhan”ti? ", "eng": "“I hope you’re keeping well, little boy; I hope you’re all right. I hope that you are not in pain.” ", "html": "{}"}, "ud2.8:15.3": {"pli": "“Kuto me, bhante sāriputta, khamanīyaṁ, kuto yāpanīyaṁ. ", "eng": "“How could I be keeping well? How could I be getting by? ", "html": "{}"}, "ud2.8:15.4": {"pli": "Satta me vassāni lohitakumbhiyaṁ vutthānī”ti. ", "eng": "For seven years I lived in a pot of blood.” ", "html": "{}</p>", "comm": "<i lang='pi' translate='no'>Lohitakumbhiya</i> is probably a play on the common phrase <i lang='pi' translate='no'>lohakumbhiya</i>, “copper pot”, which is commonly used in descriptions of hell. The boy, it seems, had an unusually rapid linguistic development. "}, "ud2.8:16.1": {"pli": "Atha kho suppavāsā koliyadhītā: ", "eng": "Then Suppavāsā, thinking, ", "html": "<p>{}"}, "ud2.8:16.2": {"pli": "“putto me dhammasenāpatinā saddhiṁ mantetī”ti attamanā pamuditā pītisomanassajātā ahosi. ", "eng": "“My child is conversing with the General of the Dhamma!” was uplifted and overjoyed, full of rapture and happiness. ", "html": "{}"}, "ud2.8:16.3": {"pli": "Atha kho bhagavā suppavāsaṁ koliyadhītaraṁ attamanaṁ pamuditaṁ pītisomanassajātaṁ viditvā suppavāsaṁ koliyadhītaraṁ etadavoca: ", "eng": "Knowing this, the Buddha said to her, ", "html": "{}"}, "ud2.8:16.4": {"pli": "“iccheyyāsi tvaṁ, suppavāse, aññampi evarūpaṁ puttan”ti? ", "eng": "“Would you like to have another child like this?” ", "html": "{}"}, "ud2.8:16.5": {"pli": "“Iccheyyāmahaṁ, bhagavā, aññānipi evarūpāni satta puttānī”ti. ", "eng": "“Sir, I would like to have seven more children like this!” ", "html": "{}</p>"}, "ud2.8:17.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud2.8:18.1": {"pli": "“Asātaṁ sātarūpena, ", "eng": "“Pain in the guise of pleasure, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud2.8:18.2": {"pli": "piyarūpena appiyaṁ; ", "eng": "the disliked in the guise of the liked, ", "html": "<span class='verse-line'>{}</span>"}, "ud2.8:18.3": {"pli": "Dukkhaṁ sukhassa rūpena, ", "eng": "suffering in the guise of happiness, ", "html": "<span class='verse-line'>{}</span>"}, "ud2.8:18.4": {"pli": "pamattamativattatī”ti. ", "eng": "overpower the negligent.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud2.8:18.5": {"pli": "Aṭṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud2.9": {"ud2.9:0.1": {"pli": "Udāna 2.9 ", "eng": "Heartfelt Sayings 2.9 ", "html": "<article id='ud2.9'><header><ul><li class='division'>{}</li></ul>"}, "ud2.9:0.2": {"pli": "Visākhāsutta ", "eng": "With Visākhā ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud2.9:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud2.9:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati pubbārāme migāramātupāsāde. ", "eng": "At one time the Buddha was staying near Sāvatthī in the stilt longhouse of Migāra’s mother in the Eastern Monastery. ", "html": "{}"}, "ud2.9:1.3": {"pli": "Tena kho pana samayena visākhāya migāramātuyā kocideva attho raññe pasenadimhi kosale paṭibaddho hoti. ", "eng": "Now at that time Visākhā was caught up in some business with King Pasenadi. ", "html": "{}"}, "ud2.9:1.4": {"pli": "Taṁ rājā pasenadi kosalo na yathādhippāyaṁ tīreti. ", "eng": "But the king’s settlement did not meet her expectations. ", "html": "{}</p>"}, "ud2.9:2.1": {"pli": "Atha kho visākhā migāramātā divā divassa yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. ", "eng": "Then, in the middle of the day, she went to the Buddha, bowed, and sat down. ", "html": "<p>{}"}, "ud2.9:2.2": {"pli": "Ekamantaṁ nisinnaṁ kho visākhaṁ migāramātaraṁ bhagavā etadavoca: ", "eng": "The Buddha said to her: ", "html": "{}"}, "ud2.9:2.3": {"pli": "“handa kuto nu tvaṁ, visākhe, āgacchasi divā divassā”ti? ", "eng": "“So, Visākhā, where are you coming from in the middle of the day?” ", "html": "{}"}, "ud2.9:2.4": {"pli": "“Idha me, bhante, kocideva attho raññe pasenadimhi kosale paṭibaddho; ", "eng": "“Sir, I am caught up in some business with King Pasenadi. ", "html": "{}"}, "ud2.9:2.5": {"pli": "taṁ rājā pasenadi kosalo na yathādhippāyaṁ tīretī”ti. ", "eng": "But the king’s settlement did not meet my expectations.” ", "html": "{}</p>"}, "ud2.9:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud2.9:4.1": {"pli": "“Sabbaṁ paravasaṁ dukkhaṁ, ", "eng": "“All under another’s control is suffering, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud2.9:4.2": {"pli": "sabbaṁ issariyaṁ sukhaṁ; ", "eng": "all under one’s own authority is pleasing; ", "html": "<span class='verse-line'>{}</span>"}, "ud2.9:4.3": {"pli": "Sādhāraṇe vihaññanti, ", "eng": "what’s shared is stressful for both parties, ", "html": "<span class='verse-line'>{}</span>"}, "ud2.9:4.4": {"pli": "yogā hi duratikkamā”ti. ", "eng": "for yokes are hard to escape.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud2.9:4.5": {"pli": "Navamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud2.10": {"ud2.10:0.1": {"pli": "Udāna 2.10 ", "eng": "Heartfelt Sayings 2.10 ", "html": "<article id='ud2.10'><header><ul><li class='division'>{}</li></ul>"}, "ud2.10:0.2": {"pli": "Bhaddiyasutta ", "eng": "With Bhaddiya ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud2.10:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud2.10:1.2": {"pli": "ekaṁ samayaṁ bhagavā anupiyāyaṁ viharati ambavane. ", "eng": "At one time the Buddha was staying near Anupiya in a mango grove. ", "html": "{}"}, "ud2.10:1.3": {"pli": "Tena kho pana samayena āyasmā bhaddiyo kāḷīgodhāya putto araññagatopi rukkhamūlagatopi suññāgāragatopi abhikkhaṇaṁ udānaṁ udānesi: ", "eng": "Now at that time, Venerable Bhaddiya son of Kāḷīgodhā, even in the wilderness, at the foot of a tree, or in an empty dwelling, frequently expressed this heartfelt sentiment: ", "html": "{}"}, "ud2.10:1.4": {"pli": "“aho sukhaṁ, aho sukhan”ti. ", "eng": "“Oh, what bliss! Oh, what bliss!” ", "html": "{}</p>"}, "ud2.10:2.1": {"pli": "Assosuṁ kho sambahulā bhikkhū āyasmato bhaddiyassa kāḷīgodhāya puttassa araññagatassapi rukkhamūlagatassapi suññāgāragatassapi abhikkhaṇaṁ udānaṁ udānentassa: ", "eng": "Several mendicants heard him ", "html": "<p>{}"}, "ud2.10:2.2": {"pli": "“aho sukhaṁ, aho sukhan”ti. ", "html": "{}"}, "ud2.10:2.3": {"pli": "Sutvāna nesaṁ etadahosi: ", "eng": "and thought, ", "html": "{}"}, "ud2.10:2.4": {"pli": "“nissaṁsayaṁ kho, āvuso, āyasmā bhaddiyo kāḷīgodhāya putto anabhirato brahmacariyaṁ carati, ", "eng": "“Without a doubt, Venerable Bhaddiya leads the spiritual life dissatisfied. ", "html": "{}"}, "ud2.10:2.5": {"pli": "yaṁsa pubbe agāriyabhūtassa rajjasukhaṁ, so tamanussaramāno araññagatopi rukkhamūlagatopi suññāgāragatopi abhikkhaṇaṁ udānaṁ udānesi: ", "eng": "It’s when recalling the pleasures of royalty he formerly enjoyed as a layperson that, even in the wilderness, at the foot of a tree, or in an empty dwelling, he frequently expresses this heartfelt sentiment: ", "html": "{}"}, "ud2.10:2.6": {"pli": "‘aho sukhaṁ, aho sukhan’”ti. ", "eng": "‘Oh, what bliss! Oh, what bliss!’” ", "html": "{}</p>"}, "ud2.10:3.1": {"pli": "Atha kho sambahulā bhikkhū yena bhagavā tenupasaṅkamiṁsu; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdiṁsu. Ekamantaṁ nisinnā kho te bhikkhū bhagavantaṁ etadavocuṁ: ", "eng": "Then those mendicants went up to the Buddha, bowed, sat down to one side, and told him what was happening. ", "html": "<p>{}"}, "ud2.10:3.2": {"pli": "“āyasmā, bhante, bhaddiyo kāḷīgodhāya putto araññagatopi rukkhamūlagatopi suññāgāragatopi abhikkhaṇaṁ udānaṁ udānesi: ", "html": "{}"}, "ud2.10:3.3": {"pli": "‘aho sukhaṁ, aho sukhan’ti. ", "html": "{}"}, "ud2.10:3.4": {"pli": "Nissaṁsayaṁ kho, bhante, āyasmā bhaddiyo kāḷīgodhāya putto anabhirato brahmacariyaṁ carati. ", "html": "{}"}, "ud2.10:3.5": {"pli": "Yaṁsa pubbe agāriyabhūtassa rajjasukhaṁ, so tamanussaramāno araññagatopi rukkhamūlagatopi suññāgāragatopi abhikkhaṇaṁ udānaṁ udānesi: ", "html": "{}"}, "ud2.10:3.6": {"pli": "‘aho sukhaṁ, aho sukhan’”ti. ", "html": "{}</p>"}, "ud2.10:4.1": {"pli": "Atha kho bhagavā aññataraṁ bhikkhuṁ āmantesi: ", "eng": "So the Buddha addressed one of the monks, ", "html": "<p>{}"}, "ud2.10:4.2": {"pli": "“ehi tvaṁ, bhikkhu, mama vacanena bhaddiyaṁ bhikkhuṁ āmantehi: ", "eng": "“Please, monk, in my name tell the mendicant Bhaddiya that ", "html": "{}"}, "ud2.10:4.3": {"pli": "‘satthā taṁ, āvuso bhaddiya, āmantetī’”ti. ", "eng": "the teacher summons him.” ", "html": "{}</p>"}, "ud2.10:5.1": {"pli": "“Evaṁ, bhante”ti kho so bhikkhu bhagavato paṭissutvā yenāyasmā bhaddiyo kāḷīgodhāya putto tenupasaṅkami; upasaṅkamitvā bhaddiyaṁ kāḷīgodhāya puttaṁ etadavoca: ", "eng": "“Yes, sir,” that monk replied. He went to Bhaddiya and said to him, ", "html": "<p>{}"}, "ud2.10:5.2": {"pli": "“satthā taṁ, āvuso bhaddiya, āmantetī”ti. ", "eng": "“Reverend Bhaddiya, the teacher summons you.” ", "html": "{}"}, "ud2.10:5.3": {"pli": "“Evamāvuso”ti kho āyasmā bhaddiyo kāḷīgodhāya putto tassa bhikkhuno paṭissutvā yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. Ekamantaṁ nisinnaṁ kho āyasmantaṁ bhaddiyaṁ kāḷīgodhāya puttaṁ bhagavā etadavoca: ", "eng": "“Yes, reverend,” Bhaddiya replied. He went to the Buddha, bowed, and sat down to one side. The Buddha said to him: ", "html": "{}</p>"}, "ud2.10:6.1": {"pli": "“Saccaṁ kira tvaṁ, bhaddiya, araññagatopi rukkhamūlagatopi suññāgāragatopi abhikkhaṇaṁ udānaṁ udānesi: ", "eng": "“Is it really true, Bhaddiya, that even in the wilderness, at the foot of a tree, or in an empty dwelling, you frequently express this heartfelt sentiment: ", "html": "<p>{}"}, "ud2.10:6.2": {"pli": "‘aho sukhaṁ, aho sukhan’”ti. ", "eng": "‘Oh, what bliss! Oh, what bliss!’?” ", "html": "{}"}, "ud2.10:6.3": {"pli": "“Evaṁ, bhante”ti. ", "eng": "“Yes, sir.” ", "html": "{}</p>"}, "ud2.10:7.1": {"pli": "“Kiṁ pana tvaṁ, bhaddiya, atthavasaṁ sampassamāno araññagatopi rukkhamūlagatopi suññāgāragatopi abhikkhaṇaṁ udānaṁ udānesi: ", "eng": "“But why do you say this?” ", "html": "<p>{}"}, "ud2.10:7.2": {"pli": "‘aho sukhaṁ, aho sukhan’”ti. ", "html": "{}"}, "ud2.10:7.3": {"pli": "“Pubbe me, bhante, agāriyabhūtassa rajjaṁ kārentassa antopi antepure rakkhā susaṁvihitā ahosi, bahipi antepure rakkhā susaṁvihitā ahosi, antopi nagare rakkhā susaṁvihitā ahosi, bahipi nagare rakkhā susaṁvihitā ahosi, antopi janapade rakkhā susaṁvihitā ahosi, bahipi janapade rakkhā susaṁvihitā ahosi. ", "eng": "“Formerly, as a layperson ruling the land, my guard was well organized within and without the royal compound, within and without the city, and within and without the country. ", "html": "{}"}, "ud2.10:7.4": {"pli": "So kho ahaṁ, bhante, evaṁ rakkhito gopito santo bhīto ubbiggo ussaṅkī utrāsī vihāsiṁ. ", "eng": "But although I was guarded and defended in this way, I remained fearful, scared, suspicious, and nervous. ", "html": "{}"}, "ud2.10:7.5": {"pli": "Etarahi kho panāhaṁ, bhante, araññagatopi rukkhamūlagatopi suññāgāragatopi eko abhīto anubbiggo anussaṅkī anutrāsī appossukko pannalomo paradattavutto, migabhūtena cetasā viharāmi. ", "eng": "But these days, even when alone in the wilderness, at the foot of a tree, or in an empty dwelling, I’m not fearful, scared, suspicious, or nervous. I live relaxed, unruffled, surviving on charity, my heart free as a wild deer. ", "html": "{}"}, "ud2.10:7.6": {"pli": "Imaṁ kho ahaṁ, bhante, atthavasaṁ sampassamāno araññagatopi rukkhamūlagatopi suññāgāragatopi abhikkhaṇaṁ udānaṁ udānesiṁ: ", "eng": "It is for this reason that, even in the wilderness, at the foot of a tree, or in an empty dwelling, I frequently expressed this heartfelt sentiment: ", "html": "{}"}, "ud2.10:7.7": {"pli": "‘aho sukhaṁ, aho sukhan’”ti. ", "eng": "‘Oh, what bliss! Oh, what bliss!’” ", "html": "{}</p>"}, "ud2.10:8.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud2.10:9.1": {"pli": "“Yassantarato na santi kopā, ", "eng": "“They who hide no anger within, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud2.10:9.2": {"pli": "Itibhavābhavatañca vītivatto; ", "eng": "passed beyond any kind of existence; ", "html": "<span class='verse-line'>{}</span>"}, "ud2.10:9.3": {"pli": "Taṁ vigatabhayaṁ sukhiṁ asokaṁ, ", "eng": "happy, free from fear and sorrow—", "html": "<span class='verse-line'>{}</span>"}, "ud2.10:9.4": {"pli": "Devā nānubhavanti dassanāyā”ti. ", "eng": "even the gods can’t see them.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud2.10:9.5": {"pli": "Dasamaṁ. ", "html": "<p class='endsutta'>{}</p>"}, "ud2.10:9.6": {"pli": "Mucalindavaggo dutiyo. ", "html": "<p class='endvagga'>{}</p>"}, "ud2.10:10.0": {"pli": "Tassuddānaṁ ", "html": "<p class='uddana-intro'>{}</p>"}, "ud2.10:10.1": {"pli": "Mucalindo rājā daṇḍena, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "ud2.10:10.2": {"pli": "sakkāro upāsakena ca; ", "html": "<span class='verse-line'>{}</span>"}, "ud2.10:10.3": {"pli": "Gabbhinī ekaputto ca, ", "html": "<span class='verse-line'>{}</span>"}, "ud2.10:10.4": {"pli": "suppavāsā visākhā ca; ", "html": "<span class='verse-line'>{}</span>"}, "ud2.10:10.5": {"pli": "Kāḷīgodhāya bhaddiyoti. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}}, "ud3.1": {"ud3.1:0.1": {"pli": "Udāna 3.1 ", "eng": "Heartfelt Sayings 3.1 ", "html": "<article id='ud3.1'><header><ul><li class='division'>{}</li></ul>"}, "ud3.1:0.2": {"pli": "Kammavipākajasutta ", "eng": "Born of the Fruits of Deeds ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud3.1:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud3.1:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud3.1:1.3": {"pli": "Tena kho pana samayena aññataro bhikkhu bhagavato avidūre nisinno hoti pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya purāṇakammavipākajaṁ dukkhaṁ tibbaṁ kharaṁ kaṭukaṁ vedanaṁ adhivāsento sato sampajāno avihaññamāno. ", "eng": "Now, at that time a certain mendicant was sitting not far from the Buddha, cross-legged, with his body straight. As a result of past deeds, he suffered painful, sharp, severe, and acute feelings, which he endured unbothered, with mindfulness and awareness. ", "html": "{}</p>"}, "ud3.1:2.1": {"pli": "Addasā kho bhagavā taṁ bhikkhuṁ avidūre nisinnaṁ pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya purāṇakammavipākajaṁ dukkhaṁ tibbaṁ kharaṁ kaṭukaṁ vedanaṁ adhivāsentaṁ sataṁ sampajānaṁ avihaññamānaṁ. ", "eng": "The Buddha saw him meditating and enduring that pain. ", "html": "<p>{}</p>"}, "ud3.1:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud3.1:4.1": {"pli": "“Sabbakammajahassa bhikkhuno, ", "eng": "“A mendicant who has left all deeds behind, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud3.1:4.2": {"pli": "Dhunamānassa pure kataṁ rajaṁ; ", "eng": "shaking off the dust of past deeds, ", "html": "<span class='verse-line'>{}</span>"}, "ud3.1:4.3": {"pli": "Amamassa ṭhitassa tādino, ", "eng": "unselfish, steady, unaffected, ", "html": "<span class='verse-line'>{}</span>"}, "ud3.1:4.4": {"pli": "Attho natthi janaṁ lapetave”ti. ", "eng": "has no need to complain.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud3.1:4.5": {"pli": "Paṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud3.2": {"ud3.2:0.1": {"pli": "Udāna 3.2 ", "eng": "Heartfelt Sayings 3.2 ", "html": "<article id='ud3.2'><header><ul><li class='division'>{}</li></ul>"}, "ud3.2:0.2": {"pli": "Nandasutta ", "eng": "With Nanda ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud3.2:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud3.2:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud3.2:1.3": {"pli": "Tena kho pana samayena āyasmā nando bhagavato bhātā mātucchāputto sambahulānaṁ bhikkhūnaṁ evamāroceti: ", "eng": "Now at that time Venerable Nanda, the Buddha’s brother and maternal cousin, informed several mendicants: ", "html": "{}"}, "ud3.2:1.4": {"pli": "“anabhirato ahaṁ, āvuso, brahmacariyaṁ carāmi; ", "eng": "“I lead the spiritual life dissatisfied. ", "html": "{}"}, "ud3.2:1.5": {"pli": "na sakkomi brahmacariyaṁ sandhāretuṁ, sikkhaṁ paccakkhāya hīnāyāvattissāmī”ti. ", "eng": "I am unable to keep up the spiritual life. I shall resign the training and return to a lesser life.” ", "html": "{}</p>"}, "ud3.2:2.1": {"pli": "Atha kho aññataro bhikkhu yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. Ekamantaṁ nisinno kho so bhikkhu bhagavantaṁ etadavoca: ", "eng": "Then a mendicant went up to the Buddha, bowed, sat down to one side, and told him what was happening. ", "html": "<p>{}"}, "ud3.2:2.2": {"pli": "“āyasmā, bhante, nando bhagavato bhātā mātucchāputto sambahulānaṁ bhikkhūnaṁ evamāroceti: ", "html": "{}"}, "ud3.2:2.3": {"pli": "‘anabhirato ahaṁ, āvuso, brahmacariyaṁ carāmi, na sakkomi brahmacariyaṁ sandhāretuṁ, sikkhaṁ paccakkhāya hīnāyāvattissāmī’”ti. ", "html": "{}</p>"}, "ud3.2:3.1": {"pli": "Atha kho bhagavā aññataraṁ bhikkhuṁ āmantesi: ", "eng": "So the Buddha addressed one of the monks, ", "html": "<p>{}"}, "ud3.2:3.2": {"pli": "“ehi tvaṁ, bhikkhu, mama vacanena nandaṁ bhikkhuṁ āmantehi: ", "eng": "“Please, monk, in my name tell the mendicant Nanda that ", "html": "{}"}, "ud3.2:3.3": {"pli": "‘satthā taṁ, āvuso nanda, āmantetī’”ti. ", "eng": "the teacher summons him.” ", "html": "{}"}, "ud3.2:3.4": {"pli": "“Evaṁ, bhante”ti kho so bhikkhu bhagavato paṭissutvā yenāyasmā nando tenupasaṅkami; upasaṅkamitvā āyasmantaṁ nandaṁ etadavoca: ", "eng": "“Yes, sir,” that monk replied. He went to Nanda and said to him, ", "html": "{}"}, "ud3.2:3.5": {"pli": "“satthā taṁ, āvuso nanda, āmantetī”ti. ", "eng": "“Reverend Nanda, the teacher summons you.” ", "html": "{}</p>"}, "ud3.2:4.1": {"pli": "“Evamāvuso”ti kho āyasmā nando tassa bhikkhuno paṭissutvā yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. Ekamantaṁ nisinnaṁ kho āyasmantaṁ nandaṁ bhagavā etadavoca: ", "eng": "“Yes, reverend,” Nanda replied. He went to the Buddha, bowed, and sat down to one side. The Buddha said to him: ", "html": "<p>{}</p>"}, "ud3.2:5.1": {"pli": "“Saccaṁ kira tvaṁ, nanda, sambahulānaṁ bhikkhūnaṁ evamārocesi: ", "eng": "“Is it really true, Nanda, that you informed several mendicants that ", "html": "<p>{}"}, "ud3.2:5.2": {"pli": "‘anabhirato ahaṁ, āvuso, brahmacariyaṁ carāmi, na sakkomi brahmacariyaṁ sandhāretuṁ, sikkhaṁ paccakkhāya hīnāyāvattissāmī’”ti? ", "eng": "you are unable to keep up the spiritual life; that you shall resign the training and return to a lesser life?” ", "html": "{}"}, "ud3.2:5.3": {"pli": "“Evaṁ, bhante”ti. ", "eng": "“Yes, sir,” he replied. ", "html": "{}</p>"}, "ud3.2:6.1": {"pli": "“Kissa pana tvaṁ, nanda, anabhirato brahmacariyaṁ carasi, na sakkosi brahmacariyaṁ sandhāretuṁ, sikkhaṁ paccakkhāya hīnāyāvattissasī”ti? ", "eng": "“But why are you so dissatisfied with the spiritual life?” ", "html": "<p>{}"}, "ud3.2:6.2": {"pli": "“Sākiyānī maṁ, bhante, janapadakalyāṇī gharā nikkhamantassa upaḍḍhullikhitehi kesehi apaloketvā maṁ etadavoca: ", "eng": "“As I left my house, sir, the finest lady of the Sakyan land, her hair half-combed, glanced at me and said, ", "html": "{}"}, "ud3.2:6.3": {"pli": "‘tuvaṭaṁ kho, ayyaputta, āgaccheyyāsī’ti. ", "eng": "‘Hurry back, master.’ ", "html": "{}"}, "ud3.2:6.4": {"pli": "So kho ahaṁ, bhante, tamanussaramāno anabhirato brahmacariyaṁ carāmi, na sakkomi brahmacariyaṁ sandhāretuṁ, sikkhaṁ paccakkhāya hīnāyāvattissāmī”ti. ", "eng": "Recalling that, I am dissatisfied and shall resign the training.” ", "html": "{}</p>"}, "ud3.2:7.1": {"pli": "Atha kho bhagavā āyasmantaṁ nandaṁ bāhāyaṁ gahetvā—seyyathāpi nāma balavā puriso samiñjitaṁ vā bāhaṁ pasāreyya, pasāritaṁ vā bāhaṁ samiñjeyya; evamevaṁ—jetavane antarahito devesu tāvatiṁsesu pāturahosi. ", "eng": "Then the Buddha took Nanda by the arm and, as easily as a strong person would extend or contract their arm, vanished from Jeta’s Grove and reappeared among the gods of the thirty-three. ", "html": "<p>{}</p>"}, "ud3.2:8.1": {"pli": "Tena kho pana samayena pañcamattāni accharāsatāni sakkassa devānamindassa upaṭṭhānaṁ āgatāni honti kakuṭapādāni. ", "eng": "Now at that time five hundred dove-footed nymphs had come to attend to Sakka, the lord of gods. ", "html": "<p>{}", "comm": "The image of the “dove-footed” nymphs is, so far as I know, unique. Doves have bright pink feet, evoking the rouged feet often mentioned as a sign of beauty. But doves in Vedic mythology are emissaries of  Nirṛti, the malign goddess of dissolution. The footprint of a dove in the fireplace is an omen of death (Rig Veda 10.165.4,  Āpastamba Gṛhyasūtra 8.23.9). Perhaps these delightful nymphs are the secret emissaries of Dissolution, their feet betraying the horrifying reality beneath the beguiling surface. "}, "ud3.2:8.2": {"pli": "Atha kho bhagavā āyasmantaṁ nandaṁ āmantesi: ", "eng": "Then the Buddha said to Nanda, ", "html": "{}"}, "ud3.2:8.3": {"pli": "“passasi no tvaṁ, nanda, imāni pañca accharāsatāni kakuṭapādānī”ti? ", "eng": "“Nanda, do you see these five hundred dove-footed nymphs?” ", "html": "{}"}, "ud3.2:8.4": {"pli": "“Evaṁ, bhante”ti. ", "eng": "“Yes, sir,” he replied. ", "html": "{}</p>"}, "ud3.2:9.1": {"pli": "“Taṁ kiṁ maññasi, nanda, katamā nu kho abhirūpatarā vā dassanīyatarā vā pāsādikatarā vā, sākiyānī vā janapadakalyāṇī, imāni vā pañca accharāsatāni kakuṭapādānī”ti? ", "eng": "“What do you think, Nanda? Who is more attractive, good-looking, and lovely—the finest lady of the Sakyan land, or these five hundred dove-footed nymphs?” ", "html": "<p>{}"}, "ud3.2:9.2": {"pli": "“Seyyathāpi, bhante, paluṭṭhamakkaṭī kaṇṇanāsacchinnā; ", "eng": "“Compared to these five hundred dove-footed nymphs, the finest lady of the Sakyan land is like a deformed monkey with its ears and nose cut off. ", "html": "{}"}, "ud3.2:9.3": {"pli": "evamevaṁ kho, bhante, sākiyānī janapadakalyāṇī imesaṁ pañcannaṁ accharāsatānaṁ upanidhāya saṅkhyampi nopeti kalabhāgampi nopeti upanidhimpi nopeti. ", "eng": "She doesn’t count, there’s no comparison, she’s not worth a fraction. ", "html": "{}"}, "ud3.2:9.4": {"pli": "Atha kho imāni pañca accharāsatāni abhirūpatarāni ceva dassanīyatarāni ca pāsādikatarāni cā”ti. ", "eng": "These five hundred dove-footed nymphs are far more attractive, good-looking, and lovely.” ", "html": "{}</p>"}, "ud3.2:10.1": {"pli": "“Abhirama, nanda, abhirama, nanda. ", "eng": "“Rejoice, Nanda, rejoice! ", "html": "<p>{}"}, "ud3.2:10.2": {"pli": "Ahaṁ te pāṭibhogo pañcannaṁ accharāsatānaṁ paṭilābhāya kakuṭapādānan”ti. ", "eng": "I guarantee you five hundred dove-footed nymphs.” ", "html": "{}"}, "ud3.2:10.3": {"pli": "“Sace me, bhante, bhagavā pāṭibhogo pañcannaṁ accharāsatānaṁ paṭilābhāya kakuṭapādānaṁ, abhiramissāmahaṁ, bhante, bhagavati brahmacariye”ti. ", "eng": "“If, sir, you guarantee me five hundred dove-footed nymphs, I shall happily lead the spiritual life under the Buddha.” ", "html": "{}</p>"}, "ud3.2:11.1": {"pli": "Atha kho bhagavā āyasmantaṁ nandaṁ bāhāyaṁ gahetvā—seyyathāpi nāma balavā puriso samiñjitaṁ vā bāhaṁ pasāreyya, pasāritaṁ vā bāhaṁ samiñjeyya; evamevaṁ—devesu tāvatiṁsesu antarahito jetavane pāturahosi. ", "eng": "Then the Buddha took Nanda by the arm and, as easily as a strong person would extend or contract their arm, vanished from the gods of the thirty-three and reappeared at Jeta’s Grove. ", "html": "<p>{}</p>"}, "ud3.2:12.1": {"pli": "Assosuṁ kho bhikkhū: ", "eng": "The mendicants heard, ", "html": "<p>{}"}, "ud3.2:12.2": {"pli": "“āyasmā kira nando bhagavato bhātā mātucchāputto accharānaṁ hetu brahmacariyaṁ carati; ", "eng": "“It seems Venerable Nanda—who is both the Buddha’s half-brother and maternal cousin—leads the spiritual life for the sake of nymphs. ", "html": "{}"}, "ud3.2:12.3": {"pli": "bhagavā kirassa pāṭibhogo pañcannaṁ accharāsatānaṁ paṭilābhāya kakuṭapādānan”ti. ", "eng": "And it seems that the Buddha guaranteed him five hundred dove-footed nymphs.” ", "html": "{}</p>"}, "ud3.2:13.1": {"pli": "Atha kho āyasmato nandassa sahāyakā bhikkhū āyasmantaṁ nandaṁ bhatakavādena ca upakkitakavādena ca samudācaranti: ", "eng": "Monks who were his friends accused him of being a hireling and a lackey, ", "html": "<p>{}"}, "ud3.2:13.2": {"pli": "“bhatako kirāyasmā nando upakkitako kirāyasmā nando accharānaṁ hetu brahmacariyaṁ carati; ", "eng": "“It seems Nanda is a hireling, it seems he is a lackey: he leads the spiritual life for the sake of nymphs. ", "html": "{}"}, "ud3.2:13.3": {"pli": "bhagavā kirassa pāṭibhogo pañcannaṁ accharāsatānaṁ paṭilābhāya kakuṭapādānan”ti. ", "eng": "And it seems that the Buddha guaranteed him five hundred dove-footed nymphs.” ", "html": "{}</p>"}, "ud3.2:14.1": {"pli": "Atha kho āyasmā nando sahāyakānaṁ bhikkhūnaṁ bhatakavādena ca upakkitakavādena ca aṭṭīyamāno harāyamāno jigucchamāno eko vūpakaṭṭho appamatto ātāpī pahitatto viharanto nacirasseva—yassatthāya kulaputtā sammadeva agārasmā anagāriyaṁ pabbajanti, tadanuttaraṁ—brahmacariyapariyosānaṁ diṭṭheva dhamme sayaṁ abhiññā sacchikatvā upasampajja vihāsi. ", "eng": "Then Nanda—embarrassed, ashamed, and disgusted at being called a hireling and a lackey—living alone, withdrawn, diligent, keen, and resolute, soon realized the supreme end of the spiritual path in this very life. He lived having achieved with his own insight the goal for which gentlemen rightly go forth from the lay life to homelessness. ", "html": "<p>{}"}, "ud3.2:14.2": {"pli": "“Khīṇā jāti, vusitaṁ brahmacariyaṁ, kataṁ karaṇīyaṁ, nāparaṁ itthattāyā”ti abbhaññāsi. ", "eng": "He understood: “Rebirth is ended; the spiritual journey has been completed; what had to be done has been done; there is nothing further for this place.” ", "html": "{}"}, "ud3.2:14.3": {"pli": "Aññataro kho panāyasmā nando arahataṁ ahosi. ", "eng": "Venerable Nanda became one of the perfected. ", "html": "{}</p>"}, "ud3.2:15.1": {"pli": "Atha kho aññatarā devatā abhikkantāya rattiyā abhikkantavaṇṇā kevalakappaṁ jetavanaṁ obhāsetvā yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ aṭṭhāsi. Ekamantaṁ ṭhitā kho sā devatā bhagavantaṁ etadavoca: ", "eng": "Then, late at night, a glorious deity, lighting up the entire Jeta’s Grove, went up to the Buddha, bowed, stood to one side, and said to him: ", "html": "<p>{}"}, "ud3.2:15.2": {"pli": "“āyasmā, bhante, nando bhagavato bhātā mātucchāputto āsavānaṁ khayā anāsavaṁ cetovimuttiṁ paññāvimuttiṁ diṭṭheva dhamme sayaṁ abhiññā sacchikatvā upasampajja viharatī”ti. ", "eng": "“Sir, Venerable Nanda—who is both the Buddha’s half-brother and maternal cousin—has realized the undefiled freedom of heart and freedom by wisdom in this very life. He lives having realized it with his own insight due to the ending of defilements.” ", "html": "{}"}, "ud3.2:15.3": {"pli": "Bhagavatopi kho ñāṇaṁ udapādi: ", "eng": "And the knowledge also came to the Buddha: ", "html": "{}"}, "ud3.2:15.4": {"pli": "“nando āsavānaṁ khayā anāsavaṁ cetovimuttiṁ paññāvimuttiṁ diṭṭheva dhamme sayaṁ abhiññā sacchikatvā upasampajja viharatī”ti. ", "eng": "“Nanda has realized the undefiled freedom of heart and freedom by wisdom in this very life. He lives having realized it with his own insight due to the ending of defilements.” ", "html": "{}</p>"}, "ud3.2:16.1": {"pli": "Atha kho āyasmā nando tassā rattiyā accayena yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. Ekamantaṁ nisinno kho āyasmā nando bhagavantaṁ etadavoca: ", "eng": "Then, when the night had passed, Nanda went to the Buddha, bowed, sat down to one side, and said to him, ", "html": "<p>{}"}, "ud3.2:16.2": {"pli": "“yaṁ me, bhante, bhagavā pāṭibhogo pañcannaṁ accharāsatānaṁ paṭilābhāya kakuṭapādānaṁ, muñcāmahaṁ, bhante, bhagavantaṁ etasmā paṭissavā”ti. ", "eng": "“Worthy sir, you guaranteed me five hundred dove-footed nymphs. I release you from that promise.” ", "html": "{}"}, "ud3.2:16.3": {"pli": "“Mayāpi kho tvaṁ, nanda, cetasā ceto paricca vidito: ", "eng": "“Nanda, I encompassed your mind and knew that ", "html": "{}"}, "ud3.2:16.4": {"pli": "‘nando āsavānaṁ khayā anāsavaṁ cetovimuttiṁ paññāvimuttiṁ diṭṭheva dhamme sayaṁ abhiññā sacchikatvā upasampajja viharatī’ti. ", "eng": "you had realized the undefiled freedom of heart and freedom by wisdom. ", "html": "{}"}, "ud3.2:16.5": {"pli": "Devatāpi me etamatthaṁ ārocesi: ", "eng": "And deities also told me about this. ", "html": "{}"}, "ud3.2:16.6": {"pli": "‘āyasmā, bhante, nando bhagavato bhātā mātucchāputto āsavānaṁ khayā anāsavaṁ cetovimuttiṁ paññāvimuttiṁ diṭṭheva dhamme sayaṁ abhiññā sacchikatvā upasampajja viharatī’ti. ", "html": "{}"}, "ud3.2:16.7": {"pli": "Yadeva kho te, nanda, anupādāya āsavehi cittaṁ vimuttaṁ, athāhaṁ mutto etasmā paṭissavā”ti. ", "eng": "As soon as your mind was freed from defilements by not grasping, I was released from that promise.” ", "html": "{}</p>"}, "ud3.2:17.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud3.2:18.1": {"pli": "“Yassa nittiṇṇo paṅko, ", "eng": "“The mendicant who has crossed over the bog, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud3.2:18.2": {"pli": "Maddito kāmakaṇḍako; ", "eng": "who has crushed the thorn of sensuality, ", "html": "<span class='verse-line'>{}</span>"}, "ud3.2:18.3": {"pli": "Mohakkhayaṁ anuppatto, ", "eng": "who has reached the end of delusion, ", "html": "<span class='verse-line'>{}</span>"}, "ud3.2:18.4": {"pli": "Sukhadukkhesu na vedhatī sa bhikkhū”ti. ", "eng": "trembles not at pleasure and pain.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud3.2:18.5": {"pli": "Dutiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud3.3": {"ud3.3:0.1": {"pli": "Udāna 3.3 ", "eng": "Heartfelt Sayings 3.3 ", "html": "<article id='ud3.3'><header><ul><li class='division'>{}</li></ul>"}, "ud3.3:0.2": {"pli": "Yasojasutta ", "eng": "With Yasoja ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud3.3:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud3.3:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud3.3:1.3": {"pli": "Tena kho pana samayena yasojappamukhāni pañcamattāni bhikkhusatāni sāvatthiṁ anuppattāni honti bhagavantaṁ dassanāya. ", "eng": "Now at that time five hundred mendicants headed by Yasoja arrived at Sāvatthī to see the Buddha. ", "html": "{}"}, "ud3.3:1.4": {"pli": "Tedha kho āgantukā bhikkhū nevāsikehi bhikkhūhi saddhiṁ paṭisammodamānā senāsanāni paññāpayamānā pattacīvarāni paṭisāmayamānā uccāsaddā mahāsaddā ahesuṁ. ", "eng": "At that, those visiting mendicants, while exchanging pleasantries with the resident mendicants, preparing their lodgings, and putting away their bowls and robes, made a colossal racket. ", "html": "{}</p>", "comm": "The narrative employs a unique scheme in introducing the repetitions: <i lang='pi' translate='no'>tedha</i>, <i lang='pi' translate='no'>tete</i>, <i lang='pi' translate='no'>teme</i>. "}, "ud3.3:2.1": {"pli": "Atha kho bhagavā āyasmantaṁ ānandaṁ āmantesi: ", "eng": "Then the Buddha said to Venerable Ānanda, ", "html": "<p>{}"}, "ud3.3:2.2": {"pli": "“ke panete, ānanda, uccāsaddā mahāsaddā kevaṭṭā maññe macchavilope”ti? ", "eng": "“Ānanda, who’s making that colossal racket? You’d think it was fishermen hauling in a catch!” ", "html": "{}"}, "ud3.3:2.3": {"pli": "“Etāni, bhante, yasojappamukhāni pañcamattāni bhikkhusatāni sāvatthiṁ anuppattāni bhagavantaṁ dassanāya. ", "eng": "“Sir, those five hundred mendicants headed by Yasoja have arrived at Sāvatthī to see the Buddha. ", "html": "{}"}, "ud3.3:2.4": {"pli": "Tete āgantukā bhikkhū nevāsikehi bhikkhūhi saddhiṁ paṭisammodamānā senāsanāni paññāpayamānā pattacīvarāni paṭisāmayamānā uccāsaddā mahāsaddā”ti. ", "eng": "It’s those visiting mendicants who, while exchanging pleasantries with the resident mendicants, preparing their lodgings, and putting away their bowls and robes, made a colossal racket.” ", "html": "{}"}, "ud3.3:2.5": {"pli": "“Tenahānanda, mama vacanena te bhikkhū āmantehi: ", "eng": "“Well then, Ānanda, in my name tell those mendicants that ", "html": "{}"}, "ud3.3:2.6": {"pli": "‘satthā āyasmante āmantetī’”ti. ", "eng": "the teacher summons them.” ", "html": "{}</p>"}, "ud3.3:3.1": {"pli": "“Evaṁ, bhante”ti kho āyasmā ānando bhagavato paṭissutvā yena te bhikkhū tenupasaṅkami; upasaṅkamitvā te bhikkhū etadavoca: ", "eng": "“Yes, sir,” Ānanda replied. He went to those mendicants and said, ", "html": "<p>{}"}, "ud3.3:3.2": {"pli": "“satthā āyasmante āmantetī”ti. ", "eng": "“Venerables, the teacher summons you.” ", "html": "{}"}, "ud3.3:3.3": {"pli": "“Evamāvuso”ti kho te bhikkhū āyasmato ānandassa paṭissutvā yena bhagavā tenupasaṅkamiṁsu; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdiṁsu. Ekamantaṁ nisinne kho te bhikkhū bhagavā etadavoca: ", "eng": "“Yes, reverend,” replied those mendicants. Then they rose from their seats and went to the Buddha, bowed, and sat down to one side. The Buddha said to them, ", "html": "{}</p>"}, "ud3.3:4.1": {"pli": "“Kiṁ nu tumhe, bhikkhave, uccāsaddā mahāsaddā, kevaṭṭā maññe macchavilope”ti? ", "eng": "“Mendicants, what’s with that colossal racket? You’d think it was fishermen hauling in a catch!” ", "html": "<p>{}"}, "ud3.3:4.2": {"pli": "Evaṁ vutte, āyasmā yasojo bhagavantaṁ etadavoca: ", "eng": "When he said this, Venerable Yasoja said to the Buddha, ", "html": "{}"}, "ud3.3:4.3": {"pli": "“imāni, bhante, pañcamattāni bhikkhusatāni sāvatthiṁ anuppattāni bhagavantaṁ dassanāya. ", "eng": "“Sir, these five hundred mendicants have arrived at Sāvatthī to see the Buddha. ", "html": "{}"}, "ud3.3:4.4": {"pli": "Teme āgantukā bhikkhū nevāsikehi bhikkhūhi saddhiṁ paṭisammodamānā senāsanāni paññāpayamānā pattacīvarāni paṭisāmayamānā uccāsaddā mahāsaddā”ti. ", "eng": "It’s these visiting mendicants who, while exchanging pleasantries with the resident mendicants, preparing their lodgings, and putting away their bowls and robes, made a colossal racket.” ", "html": "{}"}, "ud3.3:4.5": {"pli": "“Gacchatha, bhikkhave, paṇāmemi vo; na vo mama santike vatthabban”ti. ", "eng": "“Go away, mendicants, I dismiss you. You are not to stay in my presence.” ", "html": "{}</p>"}, "ud3.3:5.1": {"pli": "“Evaṁ, bhante”ti kho te bhikkhū bhagavato paṭissutvā uṭṭhāyāsanā bhagavantaṁ abhivādetvā padakkhiṇaṁ katvā senāsanaṁ saṁsāmetvā pattacīvaramādāya yena vajjī tena cārikaṁ pakkamiṁsu. ", "eng": "“Yes, sir,” replied those mendicants. They got up from their seats, bowed, and respectfully circled the Buddha, keeping him on their right. They set their lodgings in order and left, taking their bowls and robes. ", "html": "<p>{}"}, "ud3.3:5.2": {"pli": "Vajjīsu anupubbena cārikaṁ caramānā yena vaggumudā nadī tenupasaṅkamiṁsu; upasaṅkamitvā vaggumudāya nadiyā tīre paṇṇakuṭiyo karitvā vassaṁ upagacchiṁsu. ", "eng": "Traveling stage by stage in the land of the Vajjis, they arrived at the Vaggumudā River. They built leaf huts near the riverbank and there they entered the rainy season. ", "html": "{}</p>"}, "ud3.3:6.1": {"pli": "Atha kho āyasmā yasojo vassūpagato bhikkhū āmantesi: ", "eng": "Then Venerable Yasoja, having entered the rainy season, addressed the mendicants: ", "html": "<p>{}"}, "ud3.3:6.2": {"pli": "“bhagavatā mayaṁ, āvuso, paṇāmitā atthakāmena hitesinā, anukampakena anukampaṁ upādāya. ", "eng": "“Out of sympathy, reverends, the Buddha dismissed us, wanting what’s best for us. ", "html": "{}"}, "ud3.3:6.3": {"pli": "Handa mayaṁ, āvuso, tathā vihāraṁ kappema yathā no viharataṁ bhagavā attamano assā”ti. ", "eng": "Come, let us live in such a way that the Buddha would be pleased with us.” ", "html": "{}"}, "ud3.3:6.4": {"pli": "“Evamāvuso”ti kho te bhikkhū āyasmato yasojassa paccassosuṁ. ", "eng": "“Yes, reverend,” they replied. ", "html": "{}"}, "ud3.3:6.5": {"pli": "Atha kho te bhikkhū vūpakaṭṭhā appamattā ātāpino pahitattā viharantā tenevantaravassena sabbeva tisso vijjā sacchākaṁsu. ", "eng": "Then those mendicants, living alone, withdrawn, diligent, keen, and resolute, all realized the three knowledges in that same rainy season. ", "html": "{}</p>"}, "ud3.3:7.1": {"pli": "Atha kho bhagavā sāvatthiyaṁ yathābhirantaṁ viharitvā yena vesālī tena cārikaṁ pakkāmi. ", "eng": "When the Buddha had stayed in Sāvatthī as long as he pleased, he set out for Vesālī. ", "html": "<p>{}"}, "ud3.3:7.2": {"pli": "Anupubbena cārikaṁ caramāno yena vesālī tadavasari. ", "eng": "Traveling stage by stage, he arrived at Vesālī, ", "html": "{}"}, "ud3.3:7.3": {"pli": "Tatra sudaṁ bhagavā vesāliyaṁ viharati mahāvane kūṭāgārasālāyaṁ. ", "eng": "where he stayed in the hall with the peaked roof. ", "html": "{}</p>"}, "ud3.3:8.1": {"pli": "Atha kho bhagavā vaggumudātīriyānaṁ bhikkhūnaṁ cetasā ceto paricca manasi karitvā āyasmantaṁ ānandaṁ āmantesi: ", "eng": "Then, having applied his mind to encompassing the minds of the mendicants staying on the bank of the river Vaggumudā, the Buddha said to Venerable Ānanda, ", "html": "<p>{}"}, "ud3.3:8.2": {"pli": "“ālokajātā viya me, ānanda, esā disā, obhāsajātā viya me, ānanda, esā disā; ", "eng": "“A light, it appears to me, has arisen in this quarter, Ānanda; a brightness has arisen. ", "html": "{}"}, "ud3.3:8.3": {"pli": "yassaṁ disāyaṁ vaggumudātīriyā bhikkhū viharanti gantuṁ appaṭikūlāsi me manasi kātuṁ. ", "eng": "I’m not put off at the thought of going to where the mendicants are staying on the bank of the river Vaggumudā. ", "html": "{}", "comm": "Here the Buddha invents the concept of “faint praise”. "}, "ud3.3:8.4": {"pli": "Pahiṇeyyāsi tvaṁ, ānanda, vaggumudātīriyānaṁ bhikkhūnaṁ santike dūtaṁ: ", "eng": "Send a message to those mendicants: ", "html": "{}"}, "ud3.3:8.5": {"pli": "‘satthā āyasmante āmanteti, satthā āyasmantānaṁ dassanakāmo’”ti. ", "eng": "‘Venerables, the teacher summons you. He wants to see you.’” ", "html": "{}</p>"}, "ud3.3:9.1": {"pli": "“Evaṁ, bhante”ti kho āyasmā ānando bhagavato paṭissutvā yena aññataro bhikkhu tenupasaṅkami; upasaṅkamitvā taṁ bhikkhuṁ etadavoca: ", "eng": "“Yes, sir,” Ānanda replied. He went to one of the mendicants and said, ", "html": "<p>{}"}, "ud3.3:9.2": {"pli": "“ehi tvaṁ, āvuso, yena vaggumudātīriyā bhikkhū tenupasaṅkama; upasaṅkamitvā vaggumudātīriye bhikkhū evaṁ vadehi: ", "eng": "“Please, Reverend, go to the mendicants staying on the bank of the river Vaggumudā and say to them, ", "html": "{}"}, "ud3.3:9.3": {"pli": "‘satthā āyasmante āmanteti, satthā āyasmantānaṁ dassanakāmo’”ti. ", "eng": "‘Venerables, the teacher summons you. He wants to see you.’” ", "html": "{}</p>"}, "ud3.3:10.1": {"pli": "“Evamāvuso”ti kho so bhikkhu āyasmato ānandassa paṭissutvā—", "eng": "“Yes, reverend,” replied that mendicant. ", "html": "<p>{}"}, "ud3.3:10.2": {"pli": "seyyathāpi nāma balavā puriso samiñjitaṁ vā bāhaṁ pasāreyya, pasāritaṁ vā bāhaṁ samiñjeyya; evamevaṁ—mahāvane kūṭāgārasālāyaṁ antarahito vaggumudāya nadiyā tīre tesaṁ bhikkhūnaṁ purato pāturahosi. ", "eng": "Then, as easily as a strong person would extend or contract their arm, he vanished from the Great Wood, in the hall with the peaked roof, and reappeared in front of those mendicants on the bank of the river Vaggumudā. ", "html": "{}"}, "ud3.3:10.3": {"pli": "Atha kho so bhikkhu vaggumudātīriye bhikkhū etadavoca: ", "eng": "Then he said to those mendicants, ", "html": "{}"}, "ud3.3:10.4": {"pli": "“satthā āyasmante āmanteti, satthā āyasmantānaṁ dassanakāmo”ti. ", "eng": "“Venerables, the teacher summons you. He wants to see you.” ", "html": "{}</p>"}, "ud3.3:11.1": {"pli": "“Evamāvuso”ti kho te bhikkhū tassa bhikkhuno paṭissutvā senāsanaṁ saṁsāmetvā pattacīvaramādāya—", "eng": "“Yes, reverend,” replied those mendicants. They set their lodgings in order and took their bowls and robes. ", "html": "<p>{}"}, "ud3.3:11.2": {"pli": "seyyathāpi nāma balavā puriso samiñjitaṁ vā bāhaṁ pasāreyya, pasāritaṁ vā bāhaṁ samiñjeyya; evamevaṁ—vaggumudāya nadiyā tīre antarahitā mahāvane kūṭāgārasālāyaṁ bhagavato sammukhe pāturahesuṁ. ", "eng": "Then, as easily as a strong person would extend or contract their arm, they vanished from the bank of the river Vaggumudā, and reappeared in the presence of the Buddha in the Great Wood, in the hall with the peaked roof. ", "html": "{}"}, "ud3.3:11.3": {"pli": "Tena kho pana samayena bhagavā āneñjena samādhinā nisinno hoti. ", "eng": "But at that time the Buddha was sitting immersed in imperturbable meditation. ", "html": "{}"}, "ud3.3:11.4": {"pli": "Atha kho tesaṁ bhikkhūnaṁ etadahosi: ", "eng": "Then those mendicants thought, ", "html": "{}"}, "ud3.3:11.5": {"pli": "“katamena nu kho bhagavā vihārena etarahi viharatī”ti? ", "eng": "“What kind of meditation is the Buddha practicing right now?” ", "html": "{}"}, "ud3.3:11.6": {"pli": "Atha kho tesaṁ bhikkhūnaṁ etadahosi: ", "eng": "They thought, ", "html": "{}"}, "ud3.3:11.7": {"pli": "“āneñjena kho bhagavā vihārena etarahi viharatī”ti. ", "eng": "“He is practicing the imperturbable meditation.” ", "html": "{}"}, "ud3.3:11.8": {"pli": "Sabbeva āneñjasamādhinā nisīdiṁsu. ", "eng": "They all sat in imperturbable meditation. ", "html": "{}</p>"}, "ud3.3:12.1": {"pli": "Atha kho āyasmā ānando abhikkantāya rattiyā, nikkhante paṭhame yāme, uṭṭhāyāsanā ekaṁsaṁ uttarāsaṅgaṁ karitvā yena bhagavā tenañjaliṁ paṇāmetvā bhagavantaṁ etadavoca: ", "eng": "And then, as the night was getting late, in the first watch of the night, Venerable Ānanda got up from his seat, arranged his robe over one shoulder, raised his joined palms toward the Buddha and said, ", "html": "<p>{}"}, "ud3.3:12.2": {"pli": "“abhikkantā, bhante, ratti; nikkhanto paṭhamo yāmo; ciranisinnā āgantukā bhikkhū; ", "eng": "“Sir, the night is getting late. It is the first watch of the night, and the visiting mendicants have been sitting long. ", "html": "{}"}, "ud3.3:12.3": {"pli": "paṭisammodatu, bhante, bhagavā āgantukehi bhikkhūhī”ti. ", "eng": "Sir, please greet the visiting mendicants.” ", "html": "{}"}, "ud3.3:12.4": {"pli": "Evaṁ vutte, bhagavā tuṇhī ahosi. ", "eng": "But the Buddha kept silent. ", "html": "{}</p>"}, "ud3.3:13.1": {"pli": "Dutiyampi kho āyasmā ānando abhikkantāya rattiyā, nikkhante majjhime yāme, uṭṭhāyāsanā ekaṁsaṁ uttarāsaṅgaṁ karitvā yena bhagavā tenañjaliṁ paṇāmetvā bhagavantaṁ etadavoca: ", "eng": "For a second time, as the night was getting late, in the middle watch of the night, Ānanda got up from his seat, arranged his robe over one shoulder, raised his joined palms toward the Buddha and said, ", "html": "<p>{}"}, "ud3.3:13.2": {"pli": "“abhikkantā, bhante, ratti; ", "eng": "“Sir, the night is getting late. It is the second watch of the night, and the visiting mendicants have been sitting long. ", "html": "{}"}, "ud3.3:13.3": {"pli": "nikkhanto majjhimo yāmo; ", "html": "{}"}, "ud3.3:13.4": {"pli": "ciranisinnā āgantukā bhikkhū; ", "html": "{}"}, "ud3.3:13.5": {"pli": "paṭisammodatu, bhante, bhagavā āgantukehi bhikkhūhī”ti. ", "eng": "Sir, please greet the visiting mendicants.” ", "html": "{}"}, "ud3.3:13.6": {"pli": "Dutiyampi kho bhagavā tuṇhī ahosi. ", "eng": "But for a second time the Buddha kept silent. ", "html": "{}</p>"}, "ud3.3:14.1": {"pli": "Tatiyampi kho āyasmā ānando abhikkantāya rattiyā, nikkhante pacchime yāme, uddhaste aruṇe, nandimukhiyā rattiyā uṭṭhāyāsanā ekaṁsaṁ uttarāsaṅgaṁ karitvā yena bhagavā tenañjaliṁ paṇāmetvā bhagavantaṁ etadavoca: ", "eng": "For a third time, as the night was getting late, in the last watch of the night, as dawn stirred, bringing joy to the night, Ānanda got up from his seat, arranged his robe over one shoulder, raised his joined palms toward the Buddha and said, ", "html": "<p>{}"}, "ud3.3:14.2": {"pli": "“abhikkantā, bhante, ratti; ", "eng": "“Sir, the night is getting late. It is the last watch of the night; dawn stirs, bringing joy to the night, and the visiting mendicants have been sitting long. ", "html": "{}"}, "ud3.3:14.3": {"pli": "nikkhanto pacchimo yāmo; ", "html": "{}"}, "ud3.3:14.4": {"pli": "uddhasto aruṇo; ", "html": "{}"}, "ud3.3:14.5": {"pli": "nandimukhī ratti; ", "html": "{}"}, "ud3.3:14.6": {"pli": "ciranisinnā āgantukā bhikkhū; ", "html": "{}"}, "ud3.3:14.7": {"pli": "paṭisammodatu, bhante, bhagavā, āgantukehi bhikkhūhī”ti. ", "eng": "Sir, please greet the visiting mendicants.” ", "html": "{}</p>"}, "ud3.3:15.1": {"pli": "Atha kho bhagavā tamhā samādhimhā vuṭṭhahitvā āyasmantaṁ ānandaṁ āmantesi: ", "eng": "Then the Buddha emerged from that immersion and addressed Ānanda, ", "html": "<p>{}"}, "ud3.3:15.2": {"pli": "“sace kho tvaṁ, ānanda, jāneyyāsi ettakampi te nappaṭibhāseyya. ", "eng": "“If you’d known, Ānanda, you wouldn’t have said so much. ", "html": "{}", "comm": "Ānandajoti has “about them” for <i lang='pi' translate='no'>te</i> here, but in parallel phrases we find <i lang='pi' translate='no'>no</i>, so this must be the second person singular pronoun. "}, "ud3.3:15.3": {"pli": "Ahañca, ānanda, imāni ca pañca bhikkhusatāni sabbeva āneñjasamādhinā nisīdimhā”ti. ", "eng": "Both I and these five hundred mendicants have been sitting in imperturbable meditation.” ", "html": "{}</p>"}, "ud3.3:16.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud3.3:17.1": {"pli": "“Yassa jito kāmakaṇḍako, ", "eng": "“A mendicant who has beaten <j>the thorn of sensuality—", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud3.3:17.2": {"pli": "Akkoso ca vadho ca bandhanañca; ", "eng": "and abuse, killing, and caging—", "html": "<span class='verse-line'>{}</span>"}, "ud3.3:17.3": {"pli": "Pabbatova so ṭhito anejo, ", "eng": "steady as a mountain, imperturbable, ", "html": "<span class='verse-line'>{}</span>"}, "ud3.3:17.4": {"pli": "Sukhadukkhesu na vedhatī sa bhikkhū”ti. ", "eng": "trembles not at pleasure and pain.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud3.3:17.5": {"pli": "Tatiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud3.4": {"ud3.4:0.1": {"pli": "Udāna 3.4 ", "eng": "Heartfelt Sayings 3.4 ", "html": "<article id='ud3.4'><header><ul><li class='division'>{}</li></ul>"}, "ud3.4:0.2": {"pli": "Sāriputtasutta ", "eng": "With Sāriputta ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud3.4:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud3.4:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud3.4:1.3": {"pli": "Tena kho pana samayena āyasmā sāriputto bhagavato avidūre nisinno hoti pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya parimukhaṁ satiṁ upaṭṭhapetvā. ", "eng": "Now at that time Venerable Sāriputta was sitting not far from the Buddha, cross-legged, his body set straight, and mindfulness established in his presence. ", "html": "{}"}, "ud3.4:1.4": {"pli": "Addasā kho bhagavā āyasmantaṁ sāriputtaṁ avidūre nisinnaṁ pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya parimukhaṁ satiṁ upaṭṭhapetvā. ", "eng": "The Buddha saw him meditating there. ", "html": "{}</p>"}, "ud3.4:2.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud3.4:3.1": {"pli": "“Yathāpi pabbato selo, ", "eng": "“As a rocky mountain ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud3.4:3.2": {"pli": "acalo suppatiṭṭhito; ", "eng": "is unwavering and well grounded, ", "html": "<span class='verse-line'>{}</span>"}, "ud3.4:3.3": {"pli": "Evaṁ mohakkhayā bhikkhu, ", "eng": "so when delusion ends, ", "html": "<span class='verse-line'>{}</span>"}, "ud3.4:3.4": {"pli": "pabbatova na vedhatī”ti. ", "eng": "a monk, like a mountain, doesn’t tremble.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud3.4:3.5": {"pli": "Catutthaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud3.5": {"ud3.5:0.1": {"pli": "Udāna 3.5 ", "eng": "Heartfelt Sayings 3.5 ", "html": "<article id='ud3.5'><header><ul><li class='division'>{}</li></ul>"}, "ud3.5:0.2": {"pli": "Mahāmoggallānasutta ", "eng": "With Mahāmoggallāna ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud3.5:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud3.5:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud3.5:1.3": {"pli": "Tena kho pana samayena āyasmā mahāmoggallāno bhagavato avidūre nisinno hoti pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya kāyagatāya satiyā ajjhattaṁ sūpaṭṭhitāya. ", "eng": "Now at that time Venerable Mahāmoggallāna was sitting not far from the Buddha, cross-legged, with his body straight and mindfulness of the body well-established in himself. ", "html": "{}"}, "ud3.5:1.4": {"pli": "Addasā kho bhagavā āyasmantaṁ mahāmoggallānaṁ avidūre nisinnaṁ pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya kāyagatāya satiyā ajjhattaṁ sūpaṭṭhitāya. ", "eng": "The Buddha saw him meditating there. ", "html": "{}</p>"}, "ud3.5:2.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud3.5:3.1": {"pli": "“Sati kāyagatā upaṭṭhitā, ", "eng": "“With mindfulness of the body established, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud3.5:3.2": {"pli": "Chasu phassāyatanesu saṁvuto; ", "eng": "restrained in the six fields of contact, ", "html": "<span class='verse-line'>{}</span>"}, "ud3.5:3.3": {"pli": "Satataṁ bhikkhu samāhito, ", "eng": "a mendicant always immersed in samādhi ", "html": "<span class='verse-line'>{}</span>"}, "ud3.5:3.4": {"pli": "Jaññā nibbānamattano”ti. ", "eng": "would know extinguishment in themselves.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud3.5:3.5": {"pli": "Pañcamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud3.6": {"ud3.6:0.1": {"pli": "Udāna 3.6 ", "eng": "Heartfelt Sayings 3.6 ", "html": "<article id='ud3.6'><header><ul><li class='division'>{}</li></ul>"}, "ud3.6:0.2": {"pli": "Pilindavacchasutta ", "eng": "With Pilindavaccha ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud3.6:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud3.6:1.2": {"pli": "ekaṁ samayaṁ bhagavā rājagahe viharati veḷuvane kalandakanivāpe. ", "eng": "At one time the Buddha was staying near Rājagaha, in the Bamboo Grove, the squirrels’ feeding ground. ", "html": "{}"}, "ud3.6:1.3": {"pli": "Tena kho pana samayena āyasmā pilindavaccho bhikkhū vasalavādena samudācarati. ", "eng": "Now at that time Venerable Pilindavaccha addressed the mendicants as “lowlifes”. ", "html": "{}"}, "ud3.6:1.4": {"pli": "Atha kho sambahulā bhikkhū yena bhagavā tenupasaṅkamiṁsu; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdiṁsu. Ekamantaṁ nisinnā kho te bhikkhū bhagavantaṁ etadavocuṁ: ", "eng": "Then several mendicants went up to the Buddha, bowed, sat down to one side, and said to him, ", "html": "{}"}, "ud3.6:1.5": {"pli": "“āyasmā, bhante, pilindavaccho bhikkhū vasalavādena samudācaratī”ti. ", "eng": "“Sir, Venerable Pilindavaccha addresses the mendicants as ‘lowlifes’.” ", "html": "{}</p>"}, "ud3.6:2.1": {"pli": "Atha kho bhagavā aññataraṁ bhikkhuṁ āmantesi: ", "eng": "So the Buddha addressed one of the monks, ", "html": "<p>{}"}, "ud3.6:2.2": {"pli": "“ehi tvaṁ, bhikkhu, mama vacanena pilindavacchaṁ bhikkhuṁ āmantehi: ", "eng": "“Please, monk, in my name tell the mendicant Pilindavaccha that ", "html": "{}"}, "ud3.6:2.3": {"pli": "‘satthā taṁ, āvuso pilindavaccha, āmantetī’”ti. ", "eng": "the Teacher summons him.” ", "html": "{}"}, "ud3.6:2.4": {"pli": "“Evaṁ, bhante”ti kho so bhikkhu bhagavato paṭissutvā yenāyasmā pilindavaccho tenupasaṅkami; upasaṅkamitvā āyasmantaṁ pilindavacchaṁ etadavoca: ", "eng": "“Yes, sir,” that monk replied. He went to Pilindavaccha and said to him, ", "html": "{}"}, "ud3.6:2.5": {"pli": "“satthā taṁ, āvuso pilindavaccha, āmantetī”ti. ", "eng": "“Reverend Pilindavaccha, the teacher summons you.” ", "html": "{}</p>"}, "ud3.6:3.1": {"pli": "“Evamāvuso”ti kho āyasmā pilindavaccho tassa bhikkhuno paṭissutvā yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. Ekamantaṁ nisinnaṁ kho āyasmantaṁ pilindavacchaṁ bhagavā etadavoca: ", "eng": "“Yes, reverend,” Pilindavaccha replied. He went to the Buddha, bowed, and sat down to one side. The Buddha said to him: ", "html": "<p>{}"}, "ud3.6:3.2": {"pli": "“saccaṁ kira tvaṁ, vaccha, bhikkhū vasalavādena samudācarasī”ti? ", "eng": "“Is it really true, Vaccha, that you addressed the mendicants as ‘lowlifes’?” ", "html": "{}"}, "ud3.6:3.3": {"pli": "“Evaṁ, bhante”ti. ", "eng": "“Yes, sir,” he replied. ", "html": "{}</p>"}, "ud3.6:4.1": {"pli": "Atha kho bhagavā āyasmato pilindavacchassa pubbenivāsaṁ manasi karitvā bhikkhū āmantesi: ", "eng": "Then, having applied his mind to Pilindavaccha’s past lives, the Buddha said to the mendicants, ", "html": "<p>{}"}, "ud3.6:4.2": {"pli": "“mā kho tumhe, bhikkhave, vacchassa bhikkhuno ujjhāyittha. ", "eng": "“Mendicants, don’t complain about the mendicant Vaccha. ", "html": "{}"}, "ud3.6:4.3": {"pli": "Na, bhikkhave, vaccho dosantaro bhikkhū vasalavādena samudācarati. ", "eng": "He doesn’t address the mendicants as ‘lowlifes’ out of hate. ", "html": "{}"}, "ud3.6:4.4": {"pli": "Vacchassa, bhikkhave, bhikkhuno pañca jātisatāni abbokiṇṇāni brāhmaṇakule paccājātāni. ", "eng": "For five hundred lives without interruption he was reborn in a brahmin family. ", "html": "{}"}, "ud3.6:4.5": {"pli": "So tassa vasalavādo dīgharattaṁ samudāciṇṇo. ", "eng": "For a long time, he has addressed people as ‘lowlife’. ", "html": "{}"}, "ud3.6:4.6": {"pli": "Tenāyaṁ vaccho bhikkhū vasalavādena samudācaratī”ti. ", "eng": "That’s why he addresses the mendicants as ‘lowlifes’.” ", "html": "{}</p>"}, "ud3.6:5.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud3.6:6.1": {"pli": "“Yamhi na māyā vasatī na māno, ", "eng": "“In whom dwells no deceit or conceit, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud3.6:6.2": {"pli": "Yo vītalobho amamo nirāso; ", "eng": "rid of greed, unselfish, with no need for hope, ", "html": "<span class='verse-line'>{}</span>"}, "ud3.6:6.3": {"pli": "Paṇunnakodho abhinibbutatto, ", "eng": "with anger cast aside, quenched: ", "html": "<span class='verse-line'>{}</span>"}, "ud3.6:6.4": {"pli": "So brāhmaṇo so samaṇo sa bhikkhū”ti. ", "eng": "they are a brahmin, an ascetic, a mendicant.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud3.6:6.5": {"pli": "Chaṭṭhaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud3.7": {"ud3.7:0.1": {"pli": "Udāna 3.7 ", "eng": "Heartfelt Sayings 3.7 ", "html": "<article id='ud3.7'><header><ul><li class='division'>{}</li></ul>"}, "ud3.7:0.2": {"pli": "Sakkudānasutta ", "eng": "Sakka’s Heartfelt Saying ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud3.7:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud3.7:1.2": {"pli": "ekaṁ samayaṁ bhagavā rājagahe viharati veḷuvane kalandakanivāpe. ", "eng": "At one time the Buddha was staying near Rājagaha, in the Bamboo Grove, the squirrels’ feeding ground. ", "html": "{}"}, "ud3.7:1.3": {"pli": "Tena kho pana samayena āyasmā mahākassapo pippaliguhāyaṁ viharati, sattāhaṁ ekapallaṅkena nisinno hoti aññataraṁ samādhiṁ samāpajjitvā. ", "eng": "Now at that time Venerable Mahākassapa was staying in the Pipphali cave. Having entered a certain state of immersion, he sat cross-legged for seven days without moving. ", "html": "{}"}, "ud3.7:1.4": {"pli": "Atha kho āyasmā mahākassapo tassa sattāhassa accayena tamhā samādhimhā vuṭṭhāsi. ", "eng": "When seven days had passed, Mahākassapa emerged from that state of immersion. ", "html": "{}"}, "ud3.7:1.5": {"pli": "Atha kho āyasmato mahākassapassa tamhā samādhimhā vuṭṭhitassa etadahosi: ", "eng": "It occurred to him, ", "html": "{}"}, "ud3.7:1.6": {"pli": "“yannūnāhaṁ rājagahaṁ piṇḍāya paviseyyan”ti. ", "eng": "“Why not enter Rājagaha for almsfood?” ", "html": "{}</p>"}, "ud3.7:2.1": {"pli": "Tena kho pana samayena pañcamattāni devatāsatāni ussukkaṁ āpannāni honti āyasmato mahākassapassa piṇḍapātapaṭilābhāya. ", "eng": "Now at that time five hundred deities were ready and eager for the chance to offer alms to Mahākassapa. ", "html": "<p>{}"}, "ud3.7:2.2": {"pli": "Atha kho āyasmā mahākassapo tāni pañcamattāni devatāsatāni paṭikkhipitvā pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya rājagahaṁ piṇḍāya pāvisi. ", "eng": "But Mahākasspa turned down those deities. In the morning, he robed up, took his bowl and robe, and entered Rājagaha for alms. ", "html": "{}</p>"}, "ud3.7:2.3": {"pli": "Tena kho pana samayena sakko devānamindo āyasmato mahākassapassa piṇḍapātaṁ dātukāmo hoti. ", "eng": "Now at that time Sakka, lord of gods, wished to give alms to Mahākassapa. ", "html": "<p>{}"}, "ud3.7:3.2": {"pli": "Pesakāravaṇṇaṁ abhinimminitvā tantaṁ vināti. Sujā asurakaññā tasaraṁ pūreti. ", "eng": "Having manifested in the appearance of an embroiderer, he worked the loom while the titan maiden Sujā fed the shuttle. ", "html": "{}", "comm": "Sujā is Sakka’s wife (<a href='index.html?q=sn11.12#6.1'>SN 11.12:6.1</a>). "}, "ud3.7:3.3": {"pli": "Atha kho āyasmā mahākassapo rājagahe sapadānaṁ piṇḍāya caramāno yena sakkassa devānamindassa nivesanaṁ tenupasaṅkami. ", "eng": "Then, as Mahākassapa wandered indiscriminately for almsfood in Rājagaha, he approached Sakka’s house. ", "html": "{}"}, "ud3.7:3.4": {"pli": "Addasā kho sakko devānamindo āyasmantaṁ mahākassapaṁ dūratova āgacchantaṁ. ", "eng": "Seeing Mahākassapa coming off in the distance, ", "html": "{}"}, "ud3.7:3.5": {"pli": "Disvāna gharā nikkhamitvā paccuggantvā hatthato pattaṁ gahetvā gharaṁ pavisitvā ghaṭiyā odanaṁ uddharitvā pattaṁ pūretvā āyasmato mahākassapassa adāsi. ", "eng": "Sakka came out of his house, greeted him, and took the bowl from his hand. He re-entered the house and filled the bowl with rice from the pot. ", "html": "{}"}, "ud3.7:3.6": {"pli": "So ahosi piṇḍapāto anekasūpo anekabyañjano anekarasabyañjano. ", "eng": "That almsfood had many tasty soups and sauces. ", "html": "{}", "comm": "I’m not sure if <i lang='pi' translate='no'>anekarasabyañjano</i> is correct, although the word does occur at <a href='index.html?q=pv13#15.4'>Pv 13:15.4</a> and <a href='index.html?q=pv27#12.4'>Pv 27:12.4</a>. The commentary, while not definitive, suggests <i lang='pi' translate='no'>anekasūpabyañjano</i> (<i lang='pi' translate='no'>anekehi sūpehi ceva byañjanehi ca</i>), which would be more idiomatic. However it is not attested. "}, "ud3.7:3.7": {"pli": "Atha kho āyasmato mahākassapassa etadahosi: ", "eng": "Then it occurred to Mahākassapa, ", "html": "{}"}, "ud3.7:3.8": {"pli": "“ko nu kho ayaṁ satto yassāyaṁ evarūpo iddhānubhāvo”ti? ", "eng": "“Now, what being is this who has such psychic power?” ", "html": "{}"}, "ud3.7:3.9": {"pli": "Atha kho āyasmato mahākassapassa etadahosi: ", "eng": "It occurred to him, ", "html": "{}"}, "ud3.7:3.10": {"pli": "“sakko kho ayaṁ devānamindo”ti. ", "eng": "“This is Sakka, lord of gods.” ", "html": "{}"}, "ud3.7:3.11": {"pli": "Iti viditvā sakkaṁ devānamindaṁ etadavoca: ", "eng": "Knowing this, he said to Sakka, ", "html": "{}"}, "ud3.7:3.12": {"pli": "“kataṁ kho te idaṁ, kosiya; ", "eng": "“This is your doing, Kosiya; ", "html": "{}"}, "ud3.7:3.13": {"pli": "mā punapi evarūpamakāsī”ti. ", "eng": "don’t do such a thing again.” ", "html": "{}"}, "ud3.7:3.14": {"pli": "“Amhākampi, bhante kassapa, puññena attho; ", "eng": "“But Honorable Kassapa, we too need merit! ", "html": "{}"}, "ud3.7:3.15": {"pli": "amhākampi puññena karaṇīyan”ti. ", "eng": "We too ought to make merit.” ", "html": "{}</p>"}, "ud3.7:4.1": {"pli": "Atha kho sakko devānamindo āyasmantaṁ mahākassapaṁ abhivādetvā padakkhiṇaṁ katvā vehāsaṁ abbhuggantvā ākāse antalikkhe tikkhattuṁ udānaṁ udānesi: ", "eng": "Then Sakka bowed and respectfully circled Mahākassapa, keeping him on his right. Then he rose into the air and, sitting cross-legged in midair, expressed this heartfelt sentiment three times: ", "html": "<p>{}"}, "ud3.7:4.2": {"pli": "“aho dānaṁ paramadānaṁ kassape suppatiṭṭhitaṁ. ", "eng": "“Oh the gift, the best gift is well established in Kassapa! ", "html": "{}"}, "ud3.7:4.3": {"pli": "Aho dānaṁ paramadānaṁ kassape suppatiṭṭhitaṁ. ", "eng": "Oh the gift, the best gift is well established in Kassapa! ", "html": "{}"}, "ud3.7:4.4": {"pli": "Aho dānaṁ paramadānaṁ kassape suppatiṭṭhitan”ti. ", "eng": "Oh the gift, the best gift is well established in Kassapa!” ", "html": "{}"}, "ud3.7:4.5": {"pli": "Assosi kho bhagavā dibbāya sotadhātuyā visuddhāya atikkantamānusikāya sakkassa devānamindassa vehāsaṁ abbhuggantvā ākāse antalikkhe tikkhattuṁ udānaṁ udānentassa: ", "eng": "With clairaudience that is purified and superhuman, the Buddha heard Sakka express this heartfelt sentiment while sitting in the sky. ", "html": "{}"}, "ud3.7:4.6": {"pli": "“aho dānaṁ paramadānaṁ kassape suppatiṭṭhitaṁ. ", "html": "{}"}, "ud3.7:4.7": {"pli": "Aho dānaṁ paramadānaṁ kassape suppatiṭṭhitaṁ. ", "html": "{}"}, "ud3.7:4.8": {"pli": "Aho dānaṁ paramadānaṁ kassape suppatiṭṭhitan”ti. ", "html": "{}</p>"}, "ud3.7:5.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud3.7:6.1": {"pli": "“Piṇḍapātikassa bhikkhuno, ", "eng": "“A mendicant who relies on alms, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud3.7:6.2": {"pli": "Attabharassa anaññaposino; ", "eng": "self-supported, providing for no other; ", "html": "<span class='verse-line'>{}</span>"}, "ud3.7:6.3": {"pli": "Devā pihayanti tādino, ", "eng": "the unaffected one is envied by even the gods, ", "html": "<span class='verse-line'>{}</span>"}, "ud3.7:6.4": {"pli": "Upasantassa sadā satīmato”ti. ", "eng": "calm and ever mindful.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud3.7:6.5": {"pli": "Sattamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud3.8": {"ud3.8:0.1": {"pli": "Udāna 3.8 ", "eng": "Heartfelt Sayings 3.8 ", "html": "<article id='ud3.8'><header><ul><li class='division'>{}</li></ul>"}, "ud3.8:0.2": {"pli": "Piṇḍapātikasutta ", "eng": "One Who Eats Only Almsfood ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud3.8:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud3.8:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud3.8:1.3": {"pli": "Tena kho pana samayena sambahulānaṁ bhikkhūnaṁ pacchābhattaṁ piṇḍapātapaṭikkantānaṁ karerimaṇḍalamāḷe sannisinnānaṁ sannipatitānaṁ ayamantarākathā udapādi: ", "eng": "Now at that time, after the meal, on return from almsround, several mendicants sat together in the pavilion by the kareri tree and this discussion came up among them: ", "html": "{}</p>"}, "ud3.8:2.1": {"pli": "“Piṇḍapātiko, āvuso, bhikkhu piṇḍāya caranto labhati kālena kālaṁ manāpike cakkhunā rūpe passituṁ, labhati kālena kālaṁ manāpike sotena sadde sotuṁ, labhati kālena kālaṁ manāpike ghānena gandhe ghāyituṁ, labhati kālena kālaṁ manāpike jivhāya rase sāyituṁ, labhati kālena kālaṁ manāpike kāyena phoṭṭhabbe phusituṁ. ", "eng": "“Reverends, when a mendicant who eats only almsfood is wandering for alms, from time to time they get to see pleasing sights, hear pleasing sounds, smell pleasing smells, taste pleasing tastes, and encounter pleasing touches. ", "html": "<p>{}"}, "ud3.8:2.2": {"pli": "Piṇḍapātiko, āvuso, bhikkhu sakkato garukato mānito pūjito apacito piṇḍāya carati. ", "eng": "They wander for alms being honored, respected, revered, venerated, and esteemed. ", "html": "{}"}, "ud3.8:2.3": {"pli": "Handāvuso, mayampi piṇḍapātikā homa. ", "eng": "Come, we too should eat only almsfood. ", "html": "{}"}, "ud3.8:2.4": {"pli": "Mayampi lacchāma kālena kālaṁ manāpike cakkhunā rūpe passituṁ, mayampi lacchāma kālena kālaṁ manāpike sotena sadde sotuṁ, mayampi lacchāma kālena kālaṁ manāpike ghānena gandhe ghāyituṁ, mayampi lacchāma kālena kālaṁ manāpike jivhāya rase sāyituṁ, mayampi lacchāma kālena kālaṁ manāpike kāyena phoṭṭhabbe phusituṁ; ", "eng": "From time to time we too will get to see pleasing sights, hear pleasing sounds, smell pleasing smells, taste pleasing tastes, and encounter pleasing touches. ", "html": "{}"}, "ud3.8:2.5": {"pli": "mayampi sakkatā garukatā mānitā pūjitā apacitā piṇḍāya carissāmā”ti. ", "eng": "We too shall wander for alms being honored, respected, revered, venerated, and esteemed.” ", "html": "{}"}, "ud3.8:2.6": {"pli": "Ayañcarahi tesaṁ bhikkhūnaṁ antarākathā hoti vippakatā. ", "eng": "At that point the conversation among those mendicants was left unfinished. ", "html": "{}</p>"}, "ud3.8:3.1": {"pli": "Atha kho bhagavā sāyanhasamayaṁ paṭisallānā vuṭṭhito yena karerimaṇḍalamāḷo tenupasaṅkami; upasaṅkamitvā paññatte āsane nisīdi. ", "eng": "Then in the late afternoon, the Buddha came out of retreat and went to the pavilion by the kareri tree, where he sat on the seat spread out ", "html": "<p>{}"}, "ud3.8:3.2": {"pli": "Nisajja kho bhagavā bhikkhū āmantesi: ", "eng": "and addressed the mendicants: ", "html": "{}"}, "ud3.8:3.3": {"pli": "“kāya nuttha, bhikkhave, etarahi kathāya sannisinnā, kā ca pana vo antarākathā vippakatā”ti? ", "eng": "“Mendicants, what were you sitting talking about just now? What conversation was left unfinished?” ", "html": "{}</p>"}, "ud3.8:4.1": {"pli": "“Idha, bhante, amhākaṁ pacchābhattaṁ piṇḍapātapaṭikkantānaṁ karerimaṇḍalamāḷe sannisinnānaṁ sannipatitānaṁ ayamantarākathā udapādi: ", "eng": "So the mendicants told him what they had been talking about. The Buddha said, ", "html": "<p>{}</p>"}, "ud3.8:5.1": {"pli": "‘Piṇḍapātiko, āvuso, bhikkhu piṇḍāya caranto labhati kālena kālaṁ manāpike cakkhunā rūpe passituṁ, labhati kālena kālaṁ manāpike sotena sadde sotuṁ, labhati kālena kālaṁ manāpike ghānena gandhe ghāyituṁ, labhati kālena kālaṁ manāpike jivhāya rase sāyituṁ, labhati kālena kālaṁ manāpike kāyena phoṭṭhabbe phusituṁ. ", "html": "<p>{}"}, "ud3.8:5.2": {"pli": "Piṇḍapātiko, āvuso, bhikkhu sakkato garukato mānito pūjito apacito piṇḍāya carati. ", "html": "{}"}, "ud3.8:5.3": {"pli": "Handāvuso, mayampi piṇḍapātikā homa. ", "html": "{}"}, "ud3.8:5.4": {"pli": "Mayampi lacchāma kālena kālaṁ manāpike cakkhunā rūpe passituṁ …pe… ", "html": "{}"}, "ud3.8:5.5": {"pli": "kāyena phoṭṭhabbe phusituṁ. ", "html": "{}"}, "ud3.8:5.6": {"pli": "Mayampi sakkatā garukatā mānitā pūjitā apacitā piṇḍāya carissāmā’ti. ", "html": "{}"}, "ud3.8:5.7": {"pli": "Ayaṁ kho no, bhante, antarākathā vippakatā, atha bhagavā anuppatto”ti. ", "html": "{}</p>"}, "ud3.8:6.1": {"pli": "“Na khvetaṁ, bhikkhave, tumhākaṁ patirūpaṁ kulaputtānaṁ saddhā agārasmā anagāriyaṁ pabbajitānaṁ yaṁ tumhe evarūpiṁ kathaṁ katheyyātha. ", "eng": "“Mendicants, it is not appropriate for you gentlemen who have gone forth out of faith from the lay life to homelessness to talk about such things. ", "html": "<p>{}"}, "ud3.8:6.2": {"pli": "Sannipatitānaṁ vo, bhikkhave, dvayaṁ karaṇīyaṁ—", "eng": "When you’re sitting together you should do one of two things: ", "html": "{}"}, "ud3.8:6.3": {"pli": "dhammī vā kathā ariyo vā tuṇhībhāvo”ti. ", "eng": "discuss the teachings or keep noble silence.” ", "html": "{}</p>"}, "ud3.8:7.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud3.8:8.1": {"pli": "“Piṇḍapātikassa bhikkhuno, ", "eng": "“A mendicant who relies on alms, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud3.8:8.2": {"pli": "Attabharassa anaññaposino; ", "eng": "self-supported, providing for no other; ", "html": "<span class='verse-line'>{}</span>"}, "ud3.8:8.3": {"pli": "Devā pihayanti tādino, ", "eng": "the unaffected one is envied by even the gods, ", "html": "<span class='verse-line'>{}</span>"}, "ud3.8:8.4": {"pli": "No ce saddasilokanissito”ti. ", "eng": "but not if they’re after popularity and reputation.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud3.8:8.5": {"pli": "Aṭṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud3.9": {"ud3.9:0.1": {"pli": "Udāna 3.9 ", "eng": "Heartfelt Sayings 3.9 ", "html": "<article id='ud3.9'><header><ul><li class='division'>{}</li></ul>"}, "ud3.9:0.2": {"pli": "Sippasutta ", "eng": "Professions ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud3.9:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud3.9:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud3.9:1.3": {"pli": "Tena kho pana samayena sambahulānaṁ bhikkhūnaṁ pacchābhattaṁ piṇḍapātapaṭikkantānaṁ maṇḍalamāḷe sannisinnānaṁ sannipatitānaṁ ayamantarākathā udapādi: ", "eng": "Now at that time, after the meal, on return from almsround, several mendicants sat together in the pavilion and this discussion came up among them: ", "html": "{}"}, "ud3.9:1.4": {"pli": "“ko nu kho, āvuso, sippaṁ jānāti? ", "eng": "“Who knows a craft? ", "html": "{}"}, "ud3.9:1.5": {"pli": "Ko kiṁ sippaṁ sikkhi? ", "eng": "Who is studying which craft? ", "html": "{}"}, "ud3.9:1.6": {"pli": "Kataraṁ sippaṁ sippānaṁ aggan”ti? ", "eng": "Which is the best craft?” ", "html": "{}</p>"}, "ud3.9:2.1": {"pli": "Tatthekacce evamāhaṁsu: ", "eng": "In answer, some said that ", "html": "<p>{}"}, "ud3.9:2.2": {"pli": "“hatthisippaṁ sippānaṁ aggan”ti. ", "eng": "elephant-craft is the best of crafts. ", "html": "{}"}, "ud3.9:2.3": {"pli": "Ekacce evamāhaṁsu: ", "eng": "Others said that the best craft is ", "html": "{}"}, "ud3.9:2.4": {"pli": "“assasippaṁ sippānaṁ aggan”ti. ", "eng": "horse-craft, ", "html": "{}"}, "ud3.9:2.5": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:2.6": {"pli": "“rathasippaṁ sippānaṁ aggan”ti. ", "eng": "or chariot-craft, ", "html": "{}"}, "ud3.9:2.7": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:2.8": {"pli": "“dhanusippaṁ sippānaṁ aggan”ti. ", "eng": "or archery, ", "html": "{}"}, "ud3.9:2.9": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:2.10": {"pli": "“tharusippaṁ sippānaṁ aggan”ti. ", "eng": "or swordsmanship, ", "html": "{}"}, "ud3.9:2.11": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:2.12": {"pli": "“muddāsippaṁ sippānaṁ aggan”ti. ", "eng": "or arithmetic, ", "html": "{}"}, "ud3.9:2.13": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:2.14": {"pli": "“gaṇanāsippaṁ sippānaṁ aggan”ti. ", "eng": "or accounting, ", "html": "{}"}, "ud3.9:2.15": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:2.16": {"pli": "“saṅkhānasippaṁ sippānaṁ aggan”ti. ", "eng": "or calculating, ", "html": "{}"}, "ud3.9:2.17": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:2.18": {"pli": "“lekhāsippaṁ sippānaṁ aggan”ti. ", "eng": "or writing, ", "html": "{}"}, "ud3.9:2.19": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:2.20": {"pli": "“kāveyyasippaṁ sippānaṁ aggan”ti. ", "eng": "or poetry, ", "html": "{}"}, "ud3.9:2.21": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:2.22": {"pli": "“lokāyatasippaṁ sippānaṁ aggan”ti. ", "eng": "or cosmology, ", "html": "{}"}, "ud3.9:2.23": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:2.24": {"pli": "“khattavijjāsippaṁ sippānaṁ aggan”ti. ", "eng": "or geomancy. ", "html": "{}"}, "ud3.9:2.25": {"pli": "Ayañcarahi tesaṁ bhikkhūnaṁ antarākathā hoti vippakatā. ", "eng": "At that point the conversation among those mendicants was left unfinished. ", "html": "{}</p>"}, "ud3.9:3.1": {"pli": "Atha kho bhagavā sāyanhasamayaṁ paṭisallānā vuṭṭhito yena maṇḍalamāḷo tenupasaṅkami; upasaṅkamitvā paññatte āsane nisīdi. ", "eng": "Then in the late afternoon, the Buddha came out of retreat and went to the assembly hall. He sat down on the seat spread out, ", "html": "<p>{}"}, "ud3.9:3.2": {"pli": "Nisajja kho bhagavā bhikkhū āmantesi: ", "eng": "and addressed the mendicants: ", "html": "{}"}, "ud3.9:3.3": {"pli": "“kāya nuttha, bhikkhave, etarahi kathāya sannisinnā, kā ca pana vo antarākathā vippakatā”ti? ", "eng": "“Mendicants, what were you sitting talking about just now? What conversation was left unfinished?” ", "html": "{}</p>"}, "ud3.9:4.1": {"pli": "“Idha, bhante, amhākaṁ pacchābhattaṁ piṇḍapātapaṭikkantānaṁ maṇḍalamāḷe sannisinnānaṁ ayamantarākathā udapādi: ", "eng": "So the mendicants told him what they had been talking about when the Buddha arrived. The Buddha said, ", "html": "<p>{}"}, "ud3.9:4.2": {"pli": "‘ko nu kho, āvuso, sippaṁ jānāti? ", "html": "{}"}, "ud3.9:4.3": {"pli": "Ko kiṁ sippaṁ sikkhi? ", "html": "{}"}, "ud3.9:4.4": {"pli": "Kataraṁ sippaṁ sippānaṁ aggan’ti? ", "html": "{}</p>"}, "ud3.9:5.1": {"pli": "Tatthekacce evamāhaṁsu: ", "html": "<p>{}"}, "ud3.9:5.2": {"pli": "‘hatthisippaṁ sippānaṁ aggan’ti. ", "html": "{}"}, "ud3.9:5.3": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:5.4": {"pli": "‘assasippaṁ sippānaṁ aggan’ti. ", "html": "{}"}, "ud3.9:5.5": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:5.6": {"pli": "‘rathasippaṁ sippānaṁ aggan’ti. ", "html": "{}"}, "ud3.9:5.7": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:5.8": {"pli": "‘dhanusippaṁ sippānaṁ aggan’ti. ", "html": "{}"}, "ud3.9:5.9": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:5.10": {"pli": "‘tharusippaṁ sippānaṁ aggan’ti. ", "html": "{}"}, "ud3.9:5.11": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:5.12": {"pli": "‘muddāsippaṁ sippānaṁ aggan’ti. ", "html": "{}"}, "ud3.9:5.13": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:5.14": {"pli": "‘gaṇanāsippaṁ sippānaṁ aggan’ti. ", "html": "{}"}, "ud3.9:5.15": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:5.16": {"pli": "‘saṅkhānasippaṁ sippānaṁ aggan’ti. ", "html": "{}"}, "ud3.9:5.17": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:5.18": {"pli": "‘lekhāsippaṁ sippānaṁ aggan’ti. ", "html": "{}"}, "ud3.9:5.19": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:5.20": {"pli": "‘kāveyyasippaṁ sippānaṁ aggan’ti. ", "html": "{}"}, "ud3.9:5.21": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:5.22": {"pli": "‘lokāyatasippaṁ sippānaṁ aggan’ti. ", "html": "{}"}, "ud3.9:5.23": {"pli": "Ekacce evamāhaṁsu: ", "html": "{}"}, "ud3.9:5.24": {"pli": "‘khattavijjāsippaṁ sippānaṁ aggan’ti. ", "html": "{}"}, "ud3.9:5.25": {"pli": "Ayaṁ kho no, bhante, antarākathā hoti vippakatā, atha bhagavā anuppatto”ti. ", "html": "{}</p>"}, "ud3.9:6.1": {"pli": "“Na khvetaṁ, bhikkhave, tumhākaṁ patirūpaṁ kulaputtānaṁ saddhā agārasmā anagāriyaṁ pabbajitānaṁ yaṁ tumhe evarūpiṁ kathaṁ katheyyātha. ", "eng": "“Mendicants, it is not appropriate for you gentlemen who have gone forth out of faith from the lay life to homelessness to talk about such things. ", "html": "<p>{}"}, "ud3.9:6.2": {"pli": "Sannipatitānaṁ vo, bhikkhave, dvayaṁ karaṇīyaṁ—", "eng": "When you’re sitting together you should do one of two things: ", "html": "{}"}, "ud3.9:6.3": {"pli": "dhammī vā kathā ariyo vā tuṇhībhāvo”ti. ", "eng": "discuss the teachings or keep noble silence.” ", "html": "{}</p>"}, "ud3.9:7.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud3.9:8.1": {"pli": "“Asippajīvī lahu atthakāmo, ", "eng": "“Living without a craft, light, wanting what is best, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud3.9:8.2": {"pli": "Yatindriyo sabbadhi vippamutto; ", "eng": "with senses controlled, everywhere free; ", "html": "<span class='verse-line'>{}</span>"}, "ud3.9:8.3": {"pli": "Anokasārī amamo nirāso, ", "eng": "a migrant with no bastion, unselfish, with no need for hope, ", "html": "<span class='verse-line'>{}</span>"}, "ud3.9:8.4": {"pli": "Hitvā mānaṁ ekacaro sa bhikkhū”ti. ", "eng": "having given up conceit, wandering alone: <j>that is a mendicant.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud3.9:8.5": {"pli": "Navamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud3.10": {"ud3.10:0.1": {"pli": "Udāna 3.10 ", "eng": "Heartfelt Sayings 3.10 ", "html": "<article id='ud3.10'><header><ul><li class='division'>{}</li></ul>"}, "ud3.10:0.2": {"pli": "Lokasutta ", "eng": "The World ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud3.10:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud3.10:1.2": {"pli": "ekaṁ samayaṁ bhagavā uruvelāyaṁ viharati najjā nerañjarāya tīre bodhirukkhamūle paṭhamābhisambuddho. ", "eng": "At one time, when he was first awakened, the Buddha was staying in Uruvelā at the root of the tree of awakening on the bank of the Nerañjarā River. ", "html": "{}"}, "ud3.10:1.3": {"pli": "Tena kho pana samayena bhagavā sattāhaṁ ekapallaṅkena nisinno hoti vimuttisukhapaṭisaṁvedī. ", "eng": "There the Buddha sat cross-legged for seven days without moving, experiencing the bliss of freedom. ", "html": "{}</p>"}, "ud3.10:2.1": {"pli": "Atha kho bhagavā tassa sattāhassa accayena tamhā samādhimhā vuṭṭhahitvā buddhacakkhunā lokaṁ volokesi. ", "eng": "When seven days had passed, the Buddha emerged from that state of immersion and surveyed the world with the eye of a Buddha. ", "html": "<p>{}"}, "ud3.10:2.2": {"pli": "Addasā kho bhagavā buddhacakkhunā volokento satte anekehi santāpehi santappamāne, anekehi ca pariḷāhehi pariḍayhamāne—", "eng": "He saw sentient beings tormented with many torments, and burning with many fevers ", "html": "{}"}, "ud3.10:2.3": {"pli": "rāgajehipi, dosajehipi, mohajehipi. ", "eng": "born of greed, hate, and delusion. ", "html": "{}</p>"}, "ud3.10:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud3.10:4.1": {"pli": "“Ayaṁ loko santāpajāto, ", "eng": "“This world, born in torment, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud3.10:4.2": {"pli": "Phassapareto rogaṁ vadati attato; ", "eng": "overcome by contact, speaks of disease as the self. ", "html": "<span class='verse-line'>{}</span>"}, "ud3.10:4.3": {"pli": "Yena yena hi maññati, ", "eng": "For whatever it thinks it is, ", "html": "<span class='verse-line'>{}</span>"}, "ud3.10:4.4": {"pli": "Tato taṁ hoti aññathā. ", "eng": "it turns out to be something else. ", "html": "<span class='verse-line'>{}</span></p>"}, "ud3.10:5.1": {"pli": "Aññathābhāvī bhavasatto loko, ", "eng": "The world is attached to continued existence, <j>overcome by continued existence, ", "html": "<p><span class='verse-line'>{}</span>"}, "ud3.10:5.2": {"pli": "Bhavapareto bhavamevābhinandati; ", "eng": "taking pleasure only in continued existence, <j>yet it becomes something else. ", "html": "<span class='verse-line'>{}</span>"}, "ud3.10:5.3": {"pli": "Yadabhinandati taṁ bhayaṁ, ", "eng": "What it enjoys, that is the fear; ", "html": "<span class='verse-line'>{}</span>"}, "ud3.10:5.4": {"pli": "Yassa bhāyati taṁ dukkhaṁ; ", "eng": "what it fears, that is the suffering. ", "html": "<span class='verse-line'>{}</span>"}, "ud3.10:5.5": {"pli": "Bhavavippahānāya kho, ", "eng": "But this spiritual life is led ", "html": "<span class='verse-line'>{}</span>"}, "ud3.10:5.6": {"pli": "Panidaṁ brahmacariyaṁ vussati. ", "eng": "in order to give up continued existence. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud3.10:6.1": {"pli": "‘Ye hi keci samaṇā vā brāhmaṇā vā bhavena bhavassa vippamokkhamāhaṁsu, sabbe te avippamuttā bhavasmā’ti vadāmi. ", "eng": "Of the ascetics and brahmins who say that through continued existence one is freed from continued existence, none are themselves freed from continued existence, I say. ", "html": "<p>{}"}, "ud3.10:6.2": {"pli": "‘Ye vā pana keci samaṇā vā brāhmaṇā vā vibhavena bhavassa nissaraṇamāhaṁsu, sabbe te anissaṭā bhavasmā’ti vadāmi. ", "eng": "Of the ascetics and brahmins who say that through nonexistence one escapes from existence, none have themselves escaped from existence, I say. ", "html": "{}</p>"}, "ud3.10:7.1": {"pli": "Upadhiñhi paṭicca dukkhamidaṁ sambhoti, sabbupādānakkhayā natthi dukkhassa sambhavo. ", "eng": "For this suffering originates dependent on all attachment. With the ending of all grasping there is no origination of suffering. ", "html": "<p>{}", "comm": "Thai editions read <i lang='pi' translate='no'>sabbupadhiṁ hi</i>, which is supported by the Udānavarga. "}, "ud3.10:7.2": {"pli": "Lokamimaṁ passa; ", "eng": "Just look at this world! ", "html": "{}"}, "ud3.10:7.3": {"pli": "puthū avijjāya paretā bhūtā bhūtaratā aparimuttā; ", "eng": "Mired in all sorts of ignorance, beings in love with being are not released from continued existence. ", "html": "{}", "comm": "Accepting <i lang='pi' translate='no'>bhavā aparimuttā</i> from the Buddha Jayanthi edition. For <i lang='pi' translate='no'>puthu</i>, the commentary says <i lang='pi' translate='no'>bahū, visuṁ visuṁ vā</i>. "}, "ud3.10:7.4": {"pli": "ye hi keci bhavā sabbadhi sabbatthatāya sabbe te bhavā aniccā dukkhā vipariṇāmadhammāti. ", "eng": "Whatever states of continued existence there are—everywhere, all over—all are impermanent, suffering, and perishable. ", "html": "{}</p>"}, "ud3.10:8.1": {"pli": "Evametaṁ yathābhūtaṁ, ", "eng": "One who sees truly like this, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud3.10:8.2": {"pli": "Sammappaññāya passato; ", "eng": "with right wisdom, ", "html": "<span class='verse-line'>{}</span>"}, "ud3.10:8.3": {"pli": "Bhavataṇhā pahīyati, ", "eng": "gives up craving for continued existence, ", "html": "<span class='verse-line'>{}</span>"}, "ud3.10:8.4": {"pli": "Vibhavaṁ nābhinandati; ", "eng": "while not looking forward to nonexistence. ", "html": "<span class='verse-line'>{}</span>"}, "ud3.10:8.5": {"pli": "Sabbaso taṇhānaṁ khayā, ", "eng": "Extinguishment comes <j>from the ending of all cravings; ", "html": "<span class='verse-line'>{}</span>"}, "ud3.10:8.6": {"pli": "Asesavirāganirodho nibbānaṁ. ", "eng": "fading away and cessation with nothing left over. ", "html": "<span class='verse-line'>{}</span></p>"}, "ud3.10:9.1": {"pli": "Tassa nibbutassa bhikkhuno, ", "eng": "There is no further existence ", "html": "<p><span class='verse-line'>{}</span>"}, "ud3.10:9.2": {"pli": "Anupādā punabbhavo na hoti; ", "eng": "for that mendicant extinguished without grasping. ", "html": "<span class='verse-line'>{}</span>"}, "ud3.10:9.3": {"pli": "Abhibhūto māro vijitasaṅgāmo, ", "eng": "Victorious in battle, such a one has defeated Māra; ", "html": "<span class='verse-line'>{}</span>"}, "ud3.10:9.4": {"pli": "Upaccagā sabbabhavāni tādī”ti. ", "eng": "they’ve gone beyond all states of existence.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud3.10:9.5": {"pli": "Dasamaṁ. ", "html": "<p class='endsutta'>{}</p>"}, "ud3.10:9.6": {"pli": "Nandavaggo tatiyo. ", "html": "<p class='endvagga'>{}</p>"}, "ud3.10:10.0": {"pli": "Tassuddānaṁ ", "html": "<p class='uddana-intro'>{}</p>"}, "ud3.10:10.1": {"pli": "Kammaṁ nando yasojo ca, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "ud3.10:10.2": {"pli": "sāriputto ca kolito; ", "html": "<span class='verse-line'>{}</span>"}, "ud3.10:10.3": {"pli": "Pilindo kassapo piṇḍo, ", "html": "<span class='verse-line'>{}</span>"}, "ud3.10:10.4": {"pli": "sippaṁ lokena te dasāti. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}}, "ud4.1": {"ud4.1:0.1": {"pli": "Udāna 4.1 ", "eng": "Heartfelt Sayings 4.1 ", "html": "<article id='ud4.1'><header><ul><li class='division'>{}</li></ul>"}, "ud4.1:0.2": {"pli": "Meghiyasutta ", "eng": "With Meghiya ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud4.1:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud4.1:1.2": {"pli": "ekaṁ samayaṁ bhagavā cālikāyaṁ viharati cālike pabbate. ", "eng": "At one time the Buddha was staying near Cālikā, on the Cālikā mountain. ", "html": "{}"}, "ud4.1:1.3": {"pli": "Tena kho pana samayena āyasmā meghiyo bhagavato upaṭṭhāko hoti. ", "eng": "Now, at that time Venerable Meghiya was the Buddha’s attendant. ", "html": "{}"}, "ud4.1:1.4": {"pli": "Atha kho āyasmā meghiyo yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ aṭṭhāsi. Ekamantaṁ ṭhito kho āyasmā meghiyo bhagavantaṁ etadavoca: ", "eng": "Then Venerable Meghiya went up to the Buddha, bowed, stood to one side, and said to him, ", "html": "{}"}, "ud4.1:1.5": {"pli": "“icchāmahaṁ, bhante, jantugāmaṁ piṇḍāya pavisitun”ti. ", "eng": "“Sir, I’d like to enter Jantu village for alms.” ", "html": "{}"}, "ud4.1:1.6": {"pli": "“Yassadāni tvaṁ, meghiya, kālaṁ maññasī”ti. ", "eng": "“Please, Meghiya, go at your convenience.” ", "html": "{}</p>"}, "ud4.1:2.1": {"pli": "Atha kho āyasmā meghiyo pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya jantugāmaṁ piṇḍāya pāvisi. ", "eng": "Then Meghiya robed up in the morning and, taking his bowl and robe, entered Jantu village for alms. ", "html": "<p>{}"}, "ud4.1:2.2": {"pli": "Jantugāme piṇḍāya caritvā pacchābhattaṁ piṇḍapātapaṭikkanto yena kimikāḷāya nadiyā tīraṁ tenupasaṅkami. ", "eng": "After the meal, on his return from almsround in Jantu village, he went to the shore of Kimikālā river. ", "html": "{}"}, "ud4.1:2.3": {"pli": "Addasā kho āyasmā meghiyo kimikāḷāya nadiyā tīre jaṅghāvihāraṁ anucaṅkamamāno anuvicaramāno ambavanaṁ pāsādikaṁ manuññaṁ ramaṇīyaṁ. ", "eng": "As he was going for a walk along the shore of the river he saw a lovely and delightful mango grove. ", "html": "{}"}, "ud4.1:2.4": {"pli": "Disvānassa etadahosi: ", "eng": "When he saw this he thought, ", "html": "{}"}, "ud4.1:2.5": {"pli": "“pāsādikaṁ vatidaṁ ambavanaṁ manuññaṁ ramaṇīyaṁ. Alaṁ vatidaṁ kulaputtassa padhānatthikassa padhānāya. ", "eng": "“Oh, this mango grove is lovely and delightful! This is good enough for striving for a gentleman wanting to strive. ", "html": "{}"}, "ud4.1:2.6": {"pli": "Sace maṁ bhagavā anujāneyya, āgaccheyyāhaṁ imaṁ ambavanaṁ padhānāyā”ti. ", "eng": "If the Buddha allows me, I’ll come back to this mango grove to meditate.” ", "html": "{}</p>"}, "ud4.1:3.1": {"pli": "Atha kho āyasmā meghiyo yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. ", "eng": "Then Venerable Meghiya went up to the Buddha, bowed, sat down to one side, and told him what had happened, adding, ", "html": "<p>{}"}, "ud4.1:3.2": {"pli": "Ekamantaṁ nisinno kho āyasmā meghiyo bhagavantaṁ etadavoca: ", "html": "{}</p>"}, "ud4.1:4.1": {"pli": "“Idhāhaṁ, bhante, pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya jantugāmaṁ piṇḍāya pāvisiṁ. ", "html": "<p>{}"}, "ud4.1:4.2": {"pli": "Jantugāme piṇḍāya caritvā pacchābhattaṁ piṇḍapātapaṭikkanto yena kimikāḷāya nadiyā tīraṁ tenupasaṅkamiṁ. ", "html": "{}"}, "ud4.1:4.3": {"pli": "Addasaṁ kho ahaṁ, bhante, kimikāḷāya nadiyā tīre jaṅghāvihāraṁ anucaṅkamamāno anuvicaramāno ambavanaṁ pāsādikaṁ manuññaṁ ramaṇīyaṁ. ", "html": "{}"}, "ud4.1:4.4": {"pli": "Disvāna me etadahosi: ", "html": "{}"}, "ud4.1:4.5": {"pli": "‘pāsādikaṁ vatidaṁ ambavanaṁ manuññaṁ ramaṇīyaṁ. ", "html": "{}"}, "ud4.1:4.6": {"pli": "Alaṁ vatidaṁ kulaputtassa padhānatthikassa padhānāya. ", "html": "{}"}, "ud4.1:4.7": {"pli": "Sace maṁ bhagavā anujāneyya, āgaccheyyāhaṁ imaṁ ambavanaṁ padhānāyā’ti. ", "html": "{}"}, "ud4.1:4.8": {"pli": "Sace maṁ, bhante, bhagavā anujānāti, gaccheyyāhaṁ taṁ ambavanaṁ padhānāyā”ti. ", "eng": "“If the Buddha allows me, I’ll go back to that mango grove to meditate.” ", "html": "{}</p>"}, "ud4.1:5.1": {"pli": "Evaṁ vutte, bhagavā āyasmantaṁ meghiyaṁ etadavoca: ", "eng": "When he had spoken, the Buddha said to him, ", "html": "<p>{}"}, "ud4.1:5.2": {"pli": "“āgamehi tāva, meghiya, ekakamhi tāva, yāva aññopi koci bhikkhu āgacchatī”ti. ", "eng": "“We’re alone, Meghiya. Wait until another mendicant comes.” ", "html": "{}</p>"}, "ud4.1:6.1": {"pli": "Dutiyampi kho āyasmā meghiyo bhagavantaṁ etadavoca: ", "eng": "For a second time Meghiya said to the Buddha, ", "html": "<p>{}"}, "ud4.1:6.2": {"pli": "“bhagavato, bhante, natthi kiñci uttari karaṇīyaṁ, natthi katassa vā paticayo. ", "eng": "“Sir, the Buddha has nothing more to do, and nothing that needs improvement. ", "html": "{}"}, "ud4.1:6.3": {"pli": "Mayhaṁ kho pana, bhante, atthi uttari karaṇīyaṁ, atthi katassa paticayo. ", "eng": "But I have. ", "html": "{}"}, "ud4.1:6.4": {"pli": "Sace maṁ bhagavā anujānāti, gaccheyyāhaṁ taṁ ambavanaṁ padhānāyā”ti. ", "eng": "If you allow me, I’ll go back to that mango grove to meditate.” ", "html": "{}"}, "ud4.1:6.5": {"pli": "Dutiyampi kho bhagavā āyasmantaṁ meghiyaṁ etadavoca: ", "eng": "For a second time the Buddha said, ", "html": "{}"}, "ud4.1:6.6": {"pli": "“āgamehi tāva, meghiya, ekakamhi tāva, yāva aññopi koci bhikkhu āgacchatī”ti. ", "eng": "“We’re alone, Meghiya. Wait until another mendicant comes.” ", "html": "{}</p>"}, "ud4.1:7.1": {"pli": "Tatiyampi kho āyasmā meghiyo bhagavantaṁ etadavoca: ", "eng": "For a third time Meghiya said to the Buddha, ", "html": "<p>{}"}, "ud4.1:7.2": {"pli": "“bhagavato, bhante, natthi kiñci uttari karaṇīyaṁ, natthi katassa vā paticayo. ", "eng": "“Sir, the Buddha has nothing more to do, and nothing that needs improvement. ", "html": "{}"}, "ud4.1:7.3": {"pli": "Mayhaṁ kho pana, bhante, atthi uttari karaṇīyaṁ, atthi katassa paticayo. ", "eng": "But I have. ", "html": "{}"}, "ud4.1:7.4": {"pli": "Sace maṁ bhagavā anujānāti, gaccheyyāhaṁ taṁ ambavanaṁ padhānāyā”ti. ", "eng": "If you allow me, I’ll go back to that mango grove to meditate.” ", "html": "{}"}, "ud4.1:7.5": {"pli": "“Padhānanti kho, meghiya, vadamānaṁ kinti vadeyyāma? ", "eng": "“Meghiya, since you speak of meditation, what can I say? ", "html": "{}"}, "ud4.1:7.6": {"pli": "Yassadāni tvaṁ, meghiya, kālaṁ maññasī”ti. ", "eng": "Please, Meghiya, go at your convenience.” ", "html": "{}</p>"}, "ud4.1:8.1": {"pli": "Atha kho āyasmā meghiyo uṭṭhāyāsanā bhagavantaṁ abhivādetvā padakkhiṇaṁ katvā yena taṁ ambavanaṁ tenupasaṅkami; upasaṅkamitvā taṁ ambavanaṁ ajjhogāhetvā aññatarasmiṁ rukkhamūle divāvihāraṁ nisīdi. ", "eng": "Then Meghiya got up from his seat, bowed, and respectfully circled the Buddha, keeping him on his right. Then he went to that mango grove, and, having plunged deep into it, sat at the root of a certain tree for the day’s meditation. ", "html": "<p>{}"}, "ud4.1:8.2": {"pli": "Atha kho āyasmato meghiyassa tasmiṁ ambavane viharantassa yebhuyyena tayo pāpakā akusalā vitakkā samudācaranti, seyyathidaṁ—", "eng": "But while Meghiya was meditating in that mango grove he was beset mostly by three kinds of bad, unskillful thoughts, namely, ", "html": "{}"}, "ud4.1:8.3": {"pli": "kāmavitakko, byāpādavitakko, vihiṁsāvitakko. ", "eng": "sensual, malicious, and cruel thoughts. ", "html": "{}</p>"}, "ud4.1:9.1": {"pli": "Atha kho āyasmato meghiyassa etadahosi: ", "eng": "Then he thought, ", "html": "<p>{}"}, "ud4.1:9.2": {"pli": "“acchariyaṁ vata bho, abbhutaṁ vata bho. ", "eng": "“Oh lord, how incredible, how amazing! ", "html": "{}"}, "ud4.1:9.3": {"pli": "Saddhāya ca vatamhā agārasmā anagāriyaṁ pabbajitā. ", "eng": "I’ve gone forth out of faith from the lay life to homelessness, ", "html": "{}"}, "ud4.1:9.4": {"pli": "Atha ca panimehi tīhi pāpakehi akusalehi vitakkehi anvāsattā, seyyathidaṁ—", "eng": "but I’m still harassed by these three kinds of bad, unskillful thoughts: ", "html": "{}"}, "ud4.1:9.5": {"pli": "kāmavitakkena, byāpādavitakkena, vihiṁsāvitakkena”. ", "eng": "sensual, malicious, and cruel thoughts.” ", "html": "{}</p>"}, "ud4.1:10.1": {"pli": "Atha kho āyasmā meghiyo sāyanhasamayaṁ paṭisallānā vuṭṭhito yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. Ekamantaṁ nisinno kho āyasmā meghiyo bhagavantaṁ etadavoca: ", "eng": "Then in the late afternoon, Venerable Meghiya came out of retreat and went to the Buddha. He bowed, sat down to one side, and told the Buddha what had happened. ", "html": "<p>{}"}, "ud4.1:10.2": {"pli": "“idha mayhaṁ, bhante, tasmiṁ ambavane viharantassa yebhuyyena tayo pāpakā akusalā vitakkā samudācaranti, seyyathidaṁ—", "html": "{}"}, "ud4.1:10.3": {"pli": "kāmavitakko, byāpādavitakko, vihiṁsāvitakko. ", "html": "{}"}, "ud4.1:10.4": {"pli": "Tassa mayhaṁ, bhante, etadahosi: ", "html": "{}"}, "ud4.1:10.5": {"pli": "‘acchariyaṁ vata bho, abbhutaṁ vata bho. ", "html": "{}"}, "ud4.1:10.6": {"pli": "Saddhāya ca vatamhā agārasmā anagāriyaṁ pabbajitā. ", "html": "{}"}, "ud4.1:10.7": {"pli": "Atha ca panimehi tīhi pāpakehi akusalehi vitakkehi anvāsattā, seyyathidaṁ—", "html": "{}"}, "ud4.1:10.8": {"pli": "kāmavitakkena, byāpādavitakkena, vihiṁsāvitakkena’”. ", "html": "{}</p>"}, "ud4.1:11.1": {"pli": "“Aparipakkāya, meghiya, cetovimuttiyā pañca dhammā paripākāya saṁvattanti. ", "eng": "“Meghiya, when the heart’s release is not ripe, five things help it ripen. ", "html": "<p>{}"}, "ud4.1:11.2": {"pli": "Katame pañca? ", "eng": "What five? ", "html": "{}</p>"}, "ud4.1:12.1": {"pli": "Idha, meghiya, bhikkhu kalyāṇamitto hoti kalyāṇasahāyo kalyāṇasampavaṅko. ", "eng": "Firstly, a mendicant has good friends, companions, and associates. ", "html": "<p>{}"}, "ud4.1:12.2": {"pli": "Aparipakkāya, meghiya, cetovimuttiyā ayaṁ paṭhamo dhammo paripākāya saṁvattati. ", "eng": "This is the first thing … ", "html": "{}</p>"}, "ud4.1:13.1": {"pli": "Puna caparaṁ, meghiya, bhikkhu sīlavā hoti, pātimokkhasaṁvarasaṁvuto viharati ācāragocarasampanno, aṇumattesu vajjesu bhayadassāvī, samādāya sikkhati sikkhāpadesu. ", "eng": "Furthermore, a mendicant is ethical, restrained in the monastic code, conducting themselves well and resorting for alms in suitable places. Seeing danger in the slightest fault, they keep the rules they’ve undertaken. ", "html": "<p>{}"}, "ud4.1:13.2": {"pli": "Aparipakkāya, meghiya, cetovimuttiyā ayaṁ dutiyo dhammo paripākāya saṁvattati. ", "eng": "This is the second thing … ", "html": "{}</p>"}, "ud4.1:14.1": {"pli": "Puna caparaṁ, meghiya, bhikkhu yāyaṁ kathā abhisallekhikā cetovivaraṇasappāyā ekantanibbidāya virāgāya nirodhāya upasamāya abhiññāya sambodhāya nibbānāya saṁvattati, seyyathidaṁ—appicchakathā, santuṭṭhikathā, pavivekakathā, asaṁsaggakathā, vīriyārambhakathā, sīlakathā, samādhikathā, paññākathā, vimuttikathā, vimuttiñāṇadassanakathā; evarūpāya kathāya nikāmalābhī hoti akicchalābhī akasiralābhī. ", "eng": "Furthermore, a mendicant gets to take part in talk about self-effacement that helps open the heart and leads solely to disillusionment, dispassion, cessation, peace, insight, awakening, and extinguishment when they want, without trouble or difficulty. That is, talk about fewness of wishes, contentment, seclusion, aloofness, arousing energy, ethics, immersion, wisdom, freedom, and the knowledge and vision of freedom.’ ", "html": "<p>{}"}, "ud4.1:14.2": {"pli": "Aparipākāya, meghiya, cetovimuttiyā ayaṁ tatiyo dhammo paripākāya saṁvattati. ", "eng": "This is the third thing … ", "html": "{}</p>"}, "ud4.1:15.1": {"pli": "Puna caparaṁ, meghiya, bhikkhu āraddhavīriyo viharati, akusalānaṁ dhammānaṁ pahānāya, kusalānaṁ dhammānaṁ upasampadāya, thāmavā daḷhaparakkamo anikkhittadhuro kusalesu dhammesu. ", "eng": "Furthermore, a mendicant lives with energy roused up for giving up unskillful qualities and embracing skillful qualities. They are strong, staunchly vigorous, not slacking off when it comes to developing skillful qualities. ", "html": "<p>{}"}, "ud4.1:15.2": {"pli": "Aparipakkāya, meghiya, cetovimuttiyā ayaṁ catuttho dhammo paripākāya saṁvattati. ", "eng": "This is the fourth thing … ", "html": "{}</p>"}, "ud4.1:16.1": {"pli": "Puna caparaṁ, meghiya, bhikkhu paññavā hoti udayatthagāminiyā paññāya samannāgato ariyāya nibbedhikāya sammā dukkhakkhayagāminiyā. ", "eng": "Furthermore, a mendicant is wise. They have the wisdom of arising and passing away which is noble, penetrative, and leads to the complete ending of suffering. ", "html": "<p>{}"}, "ud4.1:16.2": {"pli": "Aparipakkāya, meghiya, cetovimuttiyā ayaṁ pañcamo dhammo paripākāya saṁvattati. ", "eng": "This is the fifth thing that, when the heart’s release is not ripe, helps it ripen. ", "html": "{}"}, "ud4.1:16.3": {"pli": "Aparipakkāya, meghiya, cetovimuttiyā ime pañca dhammā paripākāya saṁvattanti. ", "eng": "These are the five things that, when the heart’s release is not ripe, help it ripen. ", "html": "{}</p>"}, "ud4.1:17.1": {"pli": "Kalyāṇamittassetaṁ, meghiya, bhikkhuno pāṭikaṅkhaṁ kalyāṇasahāyassa kalyāṇasampavaṅkassa yaṁ sīlavā bhavissati, pātimokkhasaṁvarasaṁvuto viharissati, ācāragocarasampanno, aṇumattesu vajjesu bhayadassāvī, samādāya sikkhissati sikkhāpadesu. ", "eng": "A mendicant with good friends, companions, and associates can expect to be ethical … ", "html": "<p>{}</p>"}, "ud4.1:18.1": {"pli": "Kalyāṇamittassetaṁ, meghiya, bhikkhuno pāṭikaṅkhaṁ kalyāṇasahāyassa kalyāṇasampavaṅkassa yaṁ yāyaṁ kathā abhisallekhikā cetovivaraṇasappāyā ekantanibbidāya virāgāya nirodhāya upasamāya abhiññāya sambodhāya nibbānāya saṁvattati, seyyathidaṁ—appicchakathā, santuṭṭhikathā, pavivekakathā, asaṁsaggakathā, vīriyārambhakathā, sīlakathā, samādhikathā, paññākathā, vimuttikathā, vimuttiñāṇadassanakathā; evarūpāya kathāya nikāmalābhī bhavissati akicchalābhī akasiralābhī. ", "eng": "A mendicant with good friends, companions, and associates can expect to take part in talk about self-effacement that helps open the heart … ", "html": "<p>{}</p>"}, "ud4.1:19.1": {"pli": "Kalyāṇamittassetaṁ, meghiya, bhikkhuno pāṭikaṅkhaṁ kalyāṇasahāyassa kalyāṇasampavaṅkassa yaṁ āraddhavīriyo viharissati akusalānaṁ dhammānaṁ pahānāya, kusalānaṁ dhammānaṁ upasampadāya, thāmavā daḷhaparakkamo anikkhittadhuro kusalesu dhammesu. ", "eng": "A mendicant with good friends, companions, and associates can expect to be energetic … ", "html": "<p>{}</p>"}, "ud4.1:20.1": {"pli": "Kalyāṇamittassetaṁ, meghiya, bhikkhuno pāṭikaṅkhaṁ kalyāṇasahāyassa kalyāṇasampavaṅkassa yaṁ paññavā bhavissati, udayatthagāminiyā paññāya samannāgato ariyāya nibbedhikāya sammā dukkhakkhayagāminiyā. ", "eng": "A mendicant with good friends, companions, and associates can expect to be wise … ", "html": "<p>{}</p>"}, "ud4.1:21.1": {"pli": "Tena ca pana, meghiya, bhikkhunā imesu pañcasu dhammesu patiṭṭhāya cattāro dhammā uttari bhāvetabbā—", "eng": "But then, a mendicant grounded on these five things should develop four further things. ", "html": "<p>{}"}, "ud4.1:21.2": {"pli": "asubhā bhāvetabbā rāgassa pahānāya, mettā bhāvetabbā byāpādassa pahānāya, ānāpānassati bhāvetabbā vitakkupacchedāya, aniccasaññā bhāvetabbā asmimānasamugghātāya. ", "eng": "They should develop the perception of ugliness to give up greed, love to give up hate, mindfulness of breathing to cut off thinking, and perception of impermanence to uproot the conceit ‘I am’. ", "html": "{}"}, "ud4.1:21.3": {"pli": "Aniccasaññino hi, meghiya, anattasaññā saṇṭhāti, ", "eng": "When you perceive impermanence, the perception of not-self becomes stabilized. ", "html": "{}"}, "ud4.1:21.4": {"pli": "anattasaññī asmimānasamugghātaṁ pāpuṇāti diṭṭheva dhamme nibbānan”ti. ", "eng": "Perceiving not-self, you uproot the conceit ‘I am’ and attain extinguishment in this very life.” ", "html": "{}</p>"}, "ud4.1:22.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud4.1:23.1": {"pli": "“Khuddā vitakkā sukhumā vitakkā, ", "eng": "“Lesser thoughts and subtle thoughts ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud4.1:23.2": {"pli": "Anugatā manaso uppilāvā; ", "eng": "arise, springing up in the mind. ", "html": "<span class='verse-line'>{}</span>", "comm": "Following variant <i lang='pi' translate='no'>anuggatā</i>. Udānavarga here has <i lang='pi' translate='no'>samudgatāṁ</i> (“arisen”), which makes for an easier reading. "}, "ud4.1:23.3": {"pli": "Ete avidvā manaso vitakke, ", "eng": "Not understanding these thoughts, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.1:23.4": {"pli": "Hurā huraṁ dhāvati bhantacitto. ", "eng": "one with mind astray runs from one thing to the next. ", "html": "<span class='verse-line'>{}</span></p>", "comm": "The idiom <i lang='pi' translate='no'>hurā huraṁ</i> is literally “from here to there”. It is explained by the commentary as meaning both from one sense stimulus to the other, and from one life to the next. "}, "ud4.1:24.1": {"pli": "Ete ca vidvā manaso vitakke, ", "eng": "Having understood these thoughts ", "html": "<p><span class='verse-line'>{}</span>"}, "ud4.1:24.2": {"pli": "Ātāpiyo saṁvaratī satīmā; ", "eng": "that arise, springing up in the mind, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.1:24.3": {"pli": "Anugate manaso uppilāve, ", "eng": "an awakened one—keen, restrained, and mindful—", "html": "<span class='verse-line'>{}</span>"}, "ud4.1:24.4": {"pli": "Asesamete pajahāsi buddho”ti. ", "eng": "has given them up without remainder.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud4.1:24.5": {"pli": "Paṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud4.2": {"ud4.2:0.1": {"pli": "Udāna 4.2 ", "eng": "Heartfelt Sayings 4.2 ", "html": "<article id='ud4.2'><header><ul><li class='division'>{}</li></ul>"}, "ud4.2:0.2": {"pli": "Uddhatasutta ", "eng": "Restless ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud4.2:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud4.2:1.2": {"pli": "ekaṁ samayaṁ bhagavā kusinārāyaṁ viharati upavattane mallānaṁ sālavane. ", "eng": "At one time the Buddha was staying in the sal forest of the Mallas at Upavattana near Kusinārā. ", "html": "{}"}, "ud4.2:1.3": {"pli": "Tena kho pana samayena sambahulā bhikkhū bhagavato avidūre araññakuṭikāyaṁ viharanti uddhatā unnaḷā capalā mukharā vikiṇṇavācā muṭṭhassatino asampajānā asamāhitā vibbhantacittā pākatindriyā. ", "eng": "Now at that time several mendicants were staying not far from the Buddha in a wilderness hut. They were restless, insolent, fickle, scurrilous, loose-tongued, unmindful, lacking situational awareness and immersion, with straying minds and undisciplined faculties. ", "html": "{}</p>"}, "ud4.2:2.1": {"pli": "Addasā kho bhagavā te sambahule bhikkhū avidūre araññakuṭikāyaṁ viharante uddhate unnaḷe capale mukhare vikiṇṇavāce muṭṭhassatino asampajāne asamāhite vibbhantacitte pākatindriye. ", "eng": "The Buddha saw those mendicants staying nearby. ", "html": "<p>{}</p>"}, "ud4.2:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud4.2:4.1": {"pli": "“Arakkhitena kāyena, ", "eng": "“Unguarded in body, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud4.2:4.2": {"pli": "micchādiṭṭhihatena ca; ", "eng": "ruined by wrong view, ", "html": "<span class='verse-line'>{}</span>", "comm": "The variant <i lang='pi' translate='no'>micchādiṭṭhigatena</i> is the more obvious reading. However, <i lang='pi' translate='no'>micchādiṭṭhihatā</i> appears at <a href='index.html?q=an4.49'>AN 4.49</a> without variants. Commentary accepts <i lang='pi' translate='no'>hata</i> in both places. Here it glosses <i lang='pi' translate='no'>sassatādimicchābhinivesadūsitena</i>. "}, "ud4.2:4.3": {"pli": "Thinamiddhābhibhūtena, ", "eng": "overcome with dullness and drowsiness, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.2:4.4": {"pli": "vasaṁ mārassa gacchati. ", "eng": "you fall under Māra’s sway. ", "html": "<span class='verse-line'>{}</span></p>"}, "ud4.2:5.1": {"pli": "Tasmā rakkhitacittassa, ", "eng": "That’s why you should guard the mind, ", "html": "<p><span class='verse-line'>{}</span>", "comm": "Commentary: <i lang='pi' translate='no'>rakkhitacitto assa</i>, literally “be one of guarded mind”. "}, "ud4.2:5.2": {"pli": "sammāsaṅkappagocaro; ", "eng": "with right thoughts your habitat, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.2:5.3": {"pli": "Sammādiṭṭhipurekkhāro, ", "eng": "and right view at the fore. ", "html": "<span class='verse-line'>{}</span>"}, "ud4.2:5.4": {"pli": "ñatvāna udayabbayaṁ; ", "eng": "Having understood rise and fall, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.2:5.5": {"pli": "Thinamiddhābhibhū bhikkhu, ", "eng": "a mendicant who has overcome <j>dullness and drowsiness ", "html": "<span class='verse-line'>{}</span>"}, "ud4.2:5.6": {"pli": "sabbā duggatiyo jahe”ti. ", "eng": "would abandon all bad destinies.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud4.2:5.7": {"pli": "Dutiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud4.3": {"ud4.3:0.1": {"pli": "Udāna 4.3 ", "eng": "Heartfelt Sayings 4.3 ", "html": "<article id='ud4.3'><header><ul><li class='division'>{}</li></ul>"}, "ud4.3:0.2": {"pli": "Gopālakasutta ", "eng": "The Cowherd ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud4.3:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud4.3:1.2": {"pli": "ekaṁ samayaṁ bhagavā kosalesu cārikaṁ carati mahatā bhikkhusaṅghena saddhiṁ. ", "eng": "At one time the Buddha was wandering in the land of the Kosalans together with a large Saṅgha of mendicants. ", "html": "{}"}, "ud4.3:1.3": {"pli": "Atha kho bhagavā maggā okkamma yena aññataraṁ rukkhamūlaṁ tenupasaṅkami; upasaṅkamitvā paññatte āsane nisīdi. ", "eng": "And then the Buddha left the road, went to the root of a certain tree, and sat down on the seat spread out. ", "html": "{}</p>"}, "ud4.3:2.1": {"pli": "Atha kho aññataro gopālako yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. ", "eng": "Then a certain cowherd went up to the Buddha, bowed, and sat down to one side. ", "html": "<p>{}"}, "ud4.3:2.2": {"pli": "Ekamantaṁ nisinnaṁ kho taṁ gopālakaṁ bhagavā dhammiyā kathāya sandassesi samādapesi samuttejesi sampahaṁsesi. ", "eng": "The Buddha educated, encouraged, fired up, and inspired him with a Dhamma talk. ", "html": "{}</p>"}, "ud4.3:3.1": {"pli": "Atha kho so gopālako bhagavatā dhammiyā kathāya sandassito samādapito samuttejito sampahaṁsito bhagavantaṁ etadavoca: ", "eng": "Then the cowherd said to the Buddha, ", "html": "<p>{}"}, "ud4.3:3.2": {"pli": "“adhivāsetu me, bhante, bhagavā svātanāya bhattaṁ saddhiṁ bhikkhusaṅghenā”ti. ", "eng": "“Sir, may the Buddha together with the mendicant Saṅgha please accept tomorrow’s meal from me.” ", "html": "{}"}, "ud4.3:3.3": {"pli": "Adhivāsesi bhagavā tuṇhībhāvena. ", "eng": "The Buddha consented with silence. ", "html": "{}"}, "ud4.3:3.4": {"pli": "Atha kho so gopālako bhagavato adhivāsanaṁ viditvā uṭṭhāyāsanā bhagavantaṁ abhivādetvā padakkhiṇaṁ katvā pakkāmi. ", "eng": "The cowherd got up from his seat, circumambulated the Buddha with his right side toward him, and left. ", "html": "{}</p>"}, "ud4.3:4.1": {"pli": "Atha kho so gopālako tassā rattiyā accayena sake nivesane pahūtaṁ appodakapāyāsaṁ paṭiyādāpetvā navañca sappiṁ bhagavato kālaṁ ārocesi: ", "eng": "And when the night had passed the cowherd had plenty of thick milk-rice prepared in his own home, with fresh ghee. Then he had the Buddha informed of the time, saying, ", "html": "<p>{}"}, "ud4.3:4.2": {"pli": "“kālo, bhante, niṭṭhitaṁ bhattan”ti. ", "eng": "“Sir, it’s time. The meal is ready.” ", "html": "{}</p>"}, "ud4.3:5.1": {"pli": "Atha kho bhagavā pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya saddhiṁ bhikkhusaṅghena yena tassa gopālakassa nivesanaṁ tenupasaṅkami; upasaṅkamitvā paññatte āsane nisīdi. ", "eng": "The Buddha robed up, took his bowl and robe and, together with the Sangha of monks, went to the house of that cowherd, where he sat down on the prepared seat in the dining hall. ", "html": "<p>{}"}, "ud4.3:5.2": {"pli": "Atha kho so gopālako buddhappamukhaṁ bhikkhusaṅghaṁ appodakapāyāsena navena ca sappinā sahatthā santappesi sampavāresi. ", "eng": "Then the cowherd served and satisfied the mendicant Saṅgha headed by the Buddha with his own hands with a thick milk-rice and fresh ghee. ", "html": "{}"}, "ud4.3:5.3": {"pli": "Atha kho so gopālako bhagavantaṁ bhuttāviṁ onītapattapāṇiṁ aññataraṁ nīcaṁ āsanaṁ gahetvā ekamantaṁ nisīdi. ", "eng": "When the Buddha had eaten and washed his hand and bowl, the cowherd took a low seat and sat to one side. ", "html": "{}"}, "ud4.3:5.4": {"pli": "Ekamantaṁ nisinnaṁ kho taṁ gopālakaṁ bhagavā dhammiyā kathāya sandassetvā samādapetvā samuttejetvā sampahaṁsetvā uṭṭhāyāsanā pakkāmi. ", "eng": "The Buddha then instructed, inspired, and gladdened him with a teaching, after which he got up and left. ", "html": "{}"}, "ud4.3:5.5": {"pli": "Atha kho acirapakkantassa bhagavato taṁ gopālakaṁ aññataro puriso sīmantarikāya jīvitā voropesi. ", "eng": "But soon after the Buddha had left, the cowherd was killed by a certain man in the gap between village perimeters. ", "html": "{}</p>"}, "ud4.3:6.1": {"pli": "Atha kho sambahulā bhikkhū yena bhagavā tenupasaṅkamiṁsu; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdiṁsu. Ekamantaṁ nisinnā kho te bhikkhū bhagavantaṁ etadavocuṁ: ", "eng": "Then several mendicants went up to the Buddha, bowed, sat down to one side, and told him what had happened. ", "html": "<p>{}"}, "ud4.3:6.2": {"pli": "“yena, bhante, gopālakena ajja buddhappamukho bhikkhusaṅgho appodakapāyāsena navena ca sappinā sahatthā santappito sampavārito so kira, bhante, gopālako aññatarena purisena sīmantarikāya jīvitā voropito”ti. ", "html": "{}</p>"}, "ud4.3:7.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud4.3:8.1": {"pli": "“Diso disaṁ yaṁ taṁ kayirā, ", "eng": "“A wrongly directed mind ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud4.3:8.2": {"pli": "verī vā pana verinaṁ; ", "eng": "would do you more harm ", "html": "<span class='verse-line'>{}</span>"}, "ud4.3:8.3": {"pli": "Micchāpaṇihitaṁ cittaṁ, ", "eng": "than a hater to the hated, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.3:8.4": {"pli": "pāpiyo naṁ tato kare”ti. ", "eng": "or an enemy to their foe.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud4.3:8.5": {"pli": "Tatiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud4.4": {"ud4.4:0.1": {"pli": "Udāna 4.4 ", "eng": "Heartfelt Sayings 4.4 ", "html": "<article id='ud4.4'><header><ul><li class='division'>{}</li></ul>"}, "ud4.4:0.2": {"pli": "Yakkhapahārasutta ", "eng": "The Spirit’s Blow ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud4.4:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud4.4:1.2": {"pli": "ekaṁ samayaṁ bhagavā rājagahe viharati veḷuvane kalandakanivāpe. ", "eng": "At one time the Buddha was staying near Rājagaha, in the Bamboo Grove, the squirrels’ feeding ground. ", "html": "{}"}, "ud4.4:1.3": {"pli": "Tena kho pana samayena āyasmā ca sāriputto āyasmā ca mahāmoggallāno kapotakandarāyaṁ viharanti. ", "eng": "At that time Venerables Sāriputta and Moggallāna were staying near the pigeons’ alcove. ", "html": "{}"}, "ud4.4:1.4": {"pli": "Tena kho pana samayena āyasmā sāriputto juṇhāya rattiyā navoropitehi kesehi abbhokāse nisinno hoti aññataraṁ samādhiṁ samāpajjitvā. ", "eng": "Now at that time Venerable Sāriputta was sitting outdoors in the moonlight, his head freshly shaven, having entered a certain state of immersion. ", "html": "{}</p>"}, "ud4.4:2.1": {"pli": "Tena kho pana samayena dve yakkhā sahāyakā uttarāya disāya dakkhiṇaṁ disaṁ gacchanti kenacideva karaṇīyena. ", "eng": "Now at that time two native spirits who were friends were on their way from the north to the south on some business. ", "html": "<p>{}"}, "ud4.4:2.2": {"pli": "Addasaṁsu kho te yakkhā āyasmantaṁ sāriputtaṁ juṇhāya rattiyā navoropitehi kesehi abbhokāse nisinnaṁ. ", "eng": "They saw Sāriputta meditating there. ", "html": "{}"}, "ud4.4:2.3": {"pli": "Disvāna eko yakkho dutiyaṁ yakkhaṁ etadavoca: ", "eng": "One of the spirits said to the other, ", "html": "{}"}, "ud4.4:2.4": {"pli": "“paṭibhāti maṁ, samma, imassa samaṇassa sīse pahāraṁ dātun”ti. ", "eng": "“I feel inspired, friend, to give this ascetic a blow on the head!” ", "html": "{}"}, "ud4.4:2.5": {"pli": "Evaṁ vutte, so yakkho taṁ yakkhaṁ etadavoca: ", "eng": "The other spirit replied, ", "html": "{}"}, "ud4.4:2.6": {"pli": "“alaṁ, samma, mā samaṇaṁ āsādesi. ", "eng": "“Enough, friend, don’t hit the ascetic! ", "html": "{}"}, "ud4.4:2.7": {"pli": "Uḷāro so, samma, samaṇo mahiddhiko mahānubhāvo”ti. ", "eng": "That is an eminent ascetic, powerful and mighty!” ", "html": "{}</p>"}, "ud4.4:3.1": {"pli": "Dutiyampi kho so yakkho taṁ yakkhaṁ etadavoca: ", "eng": "For a second time the first spirit said to the other, ", "html": "<p>{}"}, "ud4.4:3.2": {"pli": "“paṭibhāti maṁ, samma, imassa samaṇassa sīse pahāraṁ dātun”ti. ", "eng": "“I feel inspired, friend, to give this ascetic a blow on the head!” ", "html": "{}"}, "ud4.4:3.3": {"pli": "Dutiyampi kho so yakkho taṁ yakkhaṁ etadavoca: ", "eng": "For a second time, the other spirit replied, ", "html": "{}"}, "ud4.4:3.4": {"pli": "“alaṁ, samma, mā samaṇaṁ āsādesi. ", "eng": "“Enough, friend, don’t hit the ascetic! ", "html": "{}"}, "ud4.4:3.5": {"pli": "Uḷāro so, samma, samaṇo mahiddhiko mahānubhāvo”ti. ", "eng": "That is an eminent ascetic, powerful and mighty!” ", "html": "{}"}, "ud4.4:3.6": {"pli": "Tatiyampi kho so yakkho taṁ yakkhaṁ etadavoca: ", "eng": "For a third time the first spirit said to the other, ", "html": "{}"}, "ud4.4:3.7": {"pli": "“paṭibhāti maṁ, samma, imassa samaṇassa sīse pahāraṁ dātun”ti. ", "eng": "“I feel inspired, friend, to give this ascetic a blow on the head!” ", "html": "{}"}, "ud4.4:3.8": {"pli": "Tatiyampi kho so yakkho taṁ yakkhaṁ etadavoca: ", "eng": "For a third time, the other spirit replied, ", "html": "{}"}, "ud4.4:3.9": {"pli": "“alaṁ, samma, mā samaṇaṁ āsādesi. ", "eng": "“Enough, friend, don’t hit the ascetic! ", "html": "{}"}, "ud4.4:3.10": {"pli": "Uḷāro so, samma, samaṇo mahiddhiko mahānubhāvo”ti. ", "eng": "That is an eminent ascetic, powerful and mighty!” ", "html": "{}</p>"}, "ud4.4:4.1": {"pli": "Atha kho so yakkho taṁ yakkhaṁ anādiyitvā āyasmato sāriputtattherassa sīse pahāraṁ adāsi. ", "eng": "Ignoring his friend, the first spirit struck Sāriputta. ", "html": "<p>{}"}, "ud4.4:4.2": {"pli": "Tāva mahā pahāro ahosi, api tena pahārena sattaratanaṁ vā aḍḍhaṭṭhamaratanaṁ vā nāgaṁ osādeyya, mahantaṁ vā pabbatakūṭaṁ padāleyya. ", "eng": "The blow was so strong it would have felled a bull elephant seven or seven and a half cubits tall, or split apart a great mountain peak. ", "html": "{}"}, "ud4.4:4.3": {"pli": "Atha ca pana so yakkho “ḍayhāmi ḍayhāmī”ti vatvā tattheva mahānirayaṁ apatāsi. ", "eng": "But then the spirit, crying out, “I burn, I burn!” fell into the Great Hell right there. ", "html": "{}</p>"}, "ud4.4:5.1": {"pli": "Addasā kho āyasmā mahāmoggallāno dibbena cakkhunā visuddhena atikkantamānusakena tena yakkhena āyasmato sāriputtattherassa sīse pahāraṁ dīyamānaṁ. ", "eng": "With clairvoyance that is purified and superhuman, Venerable Moggallāna saw that spirit striking Venerable Sāriputta. ", "html": "<p>{}"}, "ud4.4:5.2": {"pli": "Disvā yena āyasmā sāriputto tenupasaṅkami; upasaṅkamitvā āyasmantaṁ sāriputtaṁ etadavoca: ", "eng": "He approached him and said, ", "html": "{}"}, "ud4.4:5.3": {"pli": "“kacci te, āvuso, khamanīyaṁ, kacci yāpanīyaṁ, kacci na kiñci dukkhan”ti? ", "eng": "“I hope you’re keeping well, reverend; I hope you’re all right. I hope that you are not in pain.” ", "html": "{}"}, "ud4.4:5.4": {"pli": "“Khamanīyaṁ me, āvuso moggallāna, yāpanīyaṁ me, āvuso moggallāna; ", "eng": "“I am keeping well, Reverend Moggallāna; ", "html": "{}"}, "ud4.4:5.5": {"pli": "api ca me sīsaṁ thokaṁ dukkhan”ti. ", "eng": "but my head does hurt a little.” ", "html": "{}</p>"}, "ud4.4:6.1": {"pli": "“Acchariyaṁ, āvuso sāriputta, abbhutaṁ, āvuso sāriputta. ", "eng": "“It’s incredible, Reverend Sāriputta, it’s amazing! ", "html": "<p>{}"}, "ud4.4:6.2": {"pli": "Yāva mahiddhiko āyasmā sāriputto mahānubhāvo. ", "eng": "How mighty and powerful is Venerable Sāriputta! ", "html": "{}"}, "ud4.4:6.3": {"pli": "Idha te, āvuso sāriputta, aññataro yakkho sīse pahāraṁ adāsi. ", "eng": "Just now, a native spirit struck you on the head. ", "html": "{}"}, "ud4.4:6.4": {"pli": "Tāva mahā pahāro ahosi, api tena pahārena sattaratanaṁ vā aḍḍhaṭṭhamaratanaṁ vā nāgaṁ osādeyya, mahantaṁ vā pabbatakūṭaṁ padāleyya, atha ca panāyasmā sāriputto evamāha: ", "eng": "The blow was so strong it would have felled a bull elephant seven or seven and a half cubits tall, or split apart a great mountain peak. ", "html": "{}"}, "ud4.4:6.5": {"pli": "‘khamanīyaṁ me, āvuso moggallāna, yāpanīyaṁ me, āvuso moggallāna; ", "eng": "Yet you say, ‘I am keeping well, Reverend Moggallāna; ", "html": "{}"}, "ud4.4:6.6": {"pli": "api ca me sīsaṁ thokaṁ dukkhan’”ti. ", "eng": "but my head does hurt a little.’” ", "html": "{}</p>"}, "ud4.4:7.1": {"pli": "“Acchariyaṁ, āvuso moggallāna, abbhutaṁ, āvuso moggallāna. ", "eng": "“It’s incredible, Reverend Moggallāna, it’s amazing! ", "html": "<p>{}"}, "ud4.4:7.2": {"pli": "Yāva mahiddhiko āyasmā mahāmoggallāno mahānubhāvo yatra hi nāma yakkhampi passissati. ", "eng": "How mighty and powerful is Venerable Moggallāna, in that he can even see a native spirit! ", "html": "{}"}, "ud4.4:7.3": {"pli": "Mayaṁ panetarahi paṁsupisācakampi na passāmā”ti. ", "eng": "Whereas I can’t even see a mud-goblin right now.” ", "html": "{}</p>"}, "ud4.4:8.1": {"pli": "Assosi kho bhagavā dibbāya sotadhātuyā visuddhāya atikkantamānusikāya tesaṁ ubhinnaṁ mahānāgānaṁ imaṁ evarūpaṁ kathāsallāpaṁ. ", "eng": "With clairaudience that is purified and superhuman, the Buddha heard that discussion between those two spiritual giants. ", "html": "<p>{}</p>"}, "ud4.4:9.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud4.4:10.1": {"pli": "“Yassa selūpamaṁ cittaṁ, ", "eng": "“One whose mind is like a rock, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud4.4:10.2": {"pli": "ṭhitaṁ nānupakampati; ", "eng": "steady, never trembling, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.4:10.3": {"pli": "Virattaṁ rajanīyesu, ", "eng": "free of desire for desirable things, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.4:10.4": {"pli": "kopaneyye na kuppati; ", "eng": "not getting annoyed when things are annoying: ", "html": "<span class='verse-line'>{}</span>"}, "ud4.4:10.5": {"pli": "Yassevaṁ bhāvitaṁ cittaṁ, ", "eng": "from where will suffering strike one ", "html": "<span class='verse-line'>{}</span>"}, "ud4.4:10.6": {"pli": "kuto taṁ dukkhamessatī”ti. ", "eng": "whose mind is developed like this?” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud4.4:10.7": {"pli": "Catutthaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud4.5": {"ud4.5:0.1": {"pli": "Udāna 4.5 ", "eng": "Heartfelt Sayings 4.5 ", "html": "<article id='ud4.5'><header><ul><li class='division'>{}</li></ul>"}, "ud4.5:0.2": {"pli": "Nāgasutta ", "eng": "A Bull Elephant ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud4.5:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud4.5:1.2": {"pli": "ekaṁ samayaṁ bhagavā kosambiyaṁ viharati ghositārāme. ", "eng": "At one time the Buddha was staying near Kosambī, in Ghosita’s Monastery. ", "html": "{}"}, "ud4.5:1.3": {"pli": "Tena kho pana samayena bhagavā ākiṇṇo viharati bhikkhūhi bhikkhunīhi upāsakehi upāsikāhi rājūhi rājamahāmattehi titthiyehi titthiyasāvakehi. ", "eng": "Now at that time Buddha lived crowded by monks, nuns, laymen, and laywomen; by rulers and their chief ministers, and monastics of other religions and their disciples. ", "html": "{}"}, "ud4.5:1.4": {"pli": "Ākiṇṇo dukkhaṁ na phāsu viharati. ", "eng": "Crowded, he lived in suffering and discomfort. ", "html": "{}"}, "ud4.5:1.5": {"pli": "Atha kho bhagavato etadahosi: ", "eng": "Then he thought, ", "html": "{}"}, "ud4.5:1.6": {"pli": "“ahaṁ kho etarahi ākiṇṇo viharāmi bhikkhūhi bhikkhunīhi upāsakehi upāsikāhi rājūhi rājamahāmattehi titthiyehi titthiyasāvakehi. ", "eng": "“These days I live crowded by monks, nuns, laymen, and laywomen; by rulers and their chief ministers, and monastics of other religions and their disciples. ", "html": "{}"}, "ud4.5:1.7": {"pli": "Ākiṇṇo dukkhaṁ na phāsu viharāmi. ", "eng": "Crowded, I live in suffering and discomfort. ", "html": "{}"}, "ud4.5:1.8": {"pli": "Yannūnāhaṁ eko gaṇasmā vūpakaṭṭho vihareyyan”ti. ", "eng": "Why don’t I live alone, withdrawn from the group?” ", "html": "{}</p>"}, "ud4.5:2.1": {"pli": "Atha kho bhagavā pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya kosambiṁ piṇḍāya pāvisi. ", "eng": "Then the Buddha robed up in the morning and, taking his bowl and robe, entered Kosambī for alms. ", "html": "<p>{}"}, "ud4.5:2.2": {"pli": "Kosambiyaṁ piṇḍāya caritvā pacchābhattaṁ piṇḍapātapaṭikkanto sāmaṁ senāsanaṁ saṁsāmetvā pattacīvaramādāya anāmantetvā upaṭṭhākaṁ anapaloketvā bhikkhusaṅghaṁ eko adutiyo yena pālileyyakaṁ tena cārikaṁ pakkāmi. ", "eng": "After the meal, on his return from almsround, he set his lodgings in order himself. Taking his bowl and robe, without informing his attendants or taking leave of the mendicant Saṅgha, he set out to go wandering alone towards Pārileyyaka, with no companion. ", "html": "{}"}, "ud4.5:2.3": {"pli": "Anupubbena cārikaṁ caramāno yena pālileyyakaṁ tadavasari. ", "eng": "When he eventually arrived, ", "html": "{}"}, "ud4.5:2.4": {"pli": "Tatra sudaṁ bhagavā pālileyyake viharati rakkhitavanasaṇḍe bhaddasālamūle. ", "eng": "he stayed in a protected forest grove, at the foot of a sacred sal tree. ", "html": "{}</p>"}, "ud4.5:3.1": {"pli": "Aññataropi kho hatthināgo ākiṇṇo viharati hatthīhi hatthinīhi hatthikalabhehi hatthicchāpehi. Chinnaggāni ceva tiṇāni khādati, obhaggobhaggañcassa sākhābhaṅgaṁ khādanti, āvilāni ca pānīyāni pivati, ogāhā cassa uttiṇṇassa hatthiniyo kāyaṁ upanighaṁsantiyo gacchanti. ", "eng": "A certain bull elephant was also living crowded by other males, females, younglings, and cubs. He ate the grass they’d trampled, and they ate the broken branches he dragged down. He drank muddy water, and after his bath the female elephants bumped into him. ", "html": "<p>{}"}, "ud4.5:3.2": {"pli": "Ākiṇṇo dukkhaṁ na phāsu viharati. ", "eng": "Crowded, he lived in suffering and discomfort. ", "html": "{}"}, "ud4.5:3.3": {"pli": "Atha kho tassa hatthināgassa etadahosi: ", "eng": "Then he thought, ", "html": "{}"}, "ud4.5:3.4": {"pli": "“ahaṁ kho etarahi ākiṇṇo viharāmi hatthīhi hatthinīhi hatthikalabhehi hatthicchāpehi, chinnaggāni ceva tiṇāni khādāmi, obhaggobhaggañca me sākhābhaṅgaṁ khādanti, āvilāni ca pānīyāni pivāmi, ogāhā ca me uttiṇṇassa hatthiniyo kāyaṁ upanighaṁsantiyo gacchanti, ākiṇṇo dukkhaṁ na phāsu viharāmi. ", "eng": "“These days I live crowded by other males, females, younglings, and cubs. I eat the grass they’ve trampled, and they eat the broken branches I’ve dragged down. I drink muddy water, and after my bath the female elephants bump into me. Crowded, I live in suffering and discomfort. ", "html": "{}"}, "ud4.5:3.5": {"pli": "Yannūnāhaṁ eko gaṇasmā vūpakaṭṭho vihareyyan”ti. ", "eng": "Why don’t I live alone, withdrawn from the herd?” ", "html": "{}</p>"}, "ud4.5:4.1": {"pli": "Atha kho so hatthināgo yūthā apakkamma yena pālileyyakaṁ rakkhitavanasaṇḍo bhaddasālamūlaṁ yena bhagavā tenupasaṅkami. ", "eng": "So he left the herd and went to Pārileyyaka, where he approached the Buddha in the protected forest grove at the foot of a sacred sal tree. ", "html": "<p>{}"}, "ud4.5:4.2": {"pli": "Tatra sudaṁ so hatthināgo yasmiṁ padese bhagavā viharati taṁ padesaṁ appaharitaṁ karoti, soṇḍāya ca bhagavato pānīyaṁ paribhojanīyaṁ upaṭṭhāpeti. ", "eng": "There he attended on the Buddha, clearing the vegetation from the place where the Buddha stayed, and using his trunk to set out water for drinking and washing. ", "html": "{}</p>"}, "ud4.5:5.1": {"pli": "Atha kho bhagavato rahogatassa paṭisallīnassa evaṁ cetaso parivitakko udapādi: ", "eng": "Then as the Buddha was in private retreat this thought came to his mind, ", "html": "<p>{}"}, "ud4.5:5.2": {"pli": "“ahaṁ kho pubbe ākiṇṇo vihāsiṁ bhikkhūhi bhikkhunīhi upāsakehi upāsikāhi rājūhi rājamahāmattehi titthiyehi titthiyasāvakehi, ākiṇṇo dukkhaṁ na phāsu vihāsiṁ. ", "eng": "“Formerly I lived crowded by monks, nuns, laymen, and laywomen; by rulers and their chief ministers, and monastics of other religions and their disciples. Crowded, I live in suffering and discomfort. ", "html": "{}"}, "ud4.5:5.3": {"pli": "Somhi etarahi anākiṇṇo viharāmi bhikkhūhi bhikkhunīhi upāsakehi upāsikāhi rājūhi rājamahāmattehi titthiyehi titthiyasāvakehi, anākiṇṇo sukhaṁ phāsu viharāmī”ti. ", "eng": "But now I live uncrowded by monks, nuns, laymen, and laywomen; by rulers and their chief ministers, and monastics of other religions and their disciples. Being uncrowded, I live in happiness and comfort.” ", "html": "{}</p>"}, "ud4.5:6.1": {"pli": "Tassapi kho hatthināgassa evaṁ cetaso parivitakko udapādi: ", "eng": "And to the bull elephant also this thought came to mind, ", "html": "<p>{}"}, "ud4.5:6.2": {"pli": "“ahaṁ kho pubbe ākiṇṇo vihāsiṁ hatthīhi hatthinīhi hatthikalabhehi hatthicchāpehi, chinnaggāni ceva tiṇāni khādiṁ, obhaggobhaggañca me sākhābhaṅgaṁ khādiṁsu, āvilāni ca pānīyāni apāyiṁ, ogāhā ca me uttiṇṇassa hatthiniyo kāyaṁ upanighaṁsantiyo agamaṁsu, ākiṇṇo dukkhaṁ na phāsu vihāsiṁ. ", "eng": "“Formerly I lived crowded by other males, females, younglings, and cubs. I ate the grass they’d trampled, and they ate the broken branches I’d dragged down. I drank muddy water, and after my bath the female elephants bumped into me. Crowded, I lived in suffering and discomfort. ", "html": "{}"}, "ud4.5:6.3": {"pli": "Somhi etarahi anākiṇṇo viharāmi hatthīhi hatthinīhi hatthikalabhehi hatthicchāpehi, acchinnaggāni ceva tiṇāni khādāmi, obhaggobhaggañca me sākhābhaṅgaṁ na khādanti, anāvilāni ca pānīyāni pivāmi, ogāhā ca me uttiṇṇassa hatthiniyo na kāyaṁ upanighaṁsantiyo gacchanti, anākiṇṇo sukhaṁ phāsu viharāmī”ti. ", "eng": "But now I live uncrowded by other males, females, younglings, and cubs. I eat untrampled grass, and other elephants don’t eat the broken branches I have dragged down. I don’t drink muddy water, and the female elephants don’t bump into me after my bath. Being uncrowded, I live in happiness and comfort.” ", "html": "{}</p>"}, "ud4.5:7.1": {"pli": "Atha kho bhagavā attano ca pavivekaṁ viditvā tassa ca hatthināgassa cetasā cetoparivitakkamaññāya tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding his own seclusion and knowing that elephant’s train of thought, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud4.5:8.1": {"pli": "“Etaṁ nāgassa nāgena, ", "eng": "“The giant elephant, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud4.5:8.2": {"pli": "īsādantassa hatthino; ", "eng": "with tusks like chariot-poles, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.5:8.3": {"pli": "Sameti cittaṁ cittena, ", "eng": "agrees heart to heart with the spiritual giant, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.5:8.4": {"pli": "yadeko ramatī mano”ti. ", "eng": "since each finds joy in the woods alone.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>", "comm": "Commentary reads <i lang='pi' translate='no'>vane araññe</i>, so text’s <i lang='pi' translate='no'>mano</i> must be wrong. "}, "ud4.5:8.5": {"pli": "Pañcamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud4.6": {"ud4.6:0.1": {"pli": "Udāna 4.6 ", "eng": "Heartfelt Sayings 4.6 ", "html": "<article id='ud4.6'><header><ul><li class='division'>{}</li></ul>"}, "ud4.6:0.2": {"pli": "Piṇḍolasutta ", "eng": "The Alms-Gatherer ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud4.6:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud4.6:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud4.6:1.3": {"pli": "Tena kho pana samayena āyasmā piṇḍolabhāradvājo bhagavato avidūre nisinno hoti pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya āraññiko piṇḍapātiko paṁsukūliko tecīvariko appiccho santuṭṭho pavivitto asaṁsaṭṭho āraddhavīriyo dhutavādo adhicittamanuyutto. ", "eng": "Now at that time Venerable Bhāradvāja the Alms-Gatherer was sitting not far from the Buddha, cross-legged, with his body straight. He was one who lived in the wilderness, ate only almsfood, wore rag robes, and owned just three robes. He was of few wishes, content, secluded, aloof, and energetic, an advocate of austerities, dedicated to the higher mind. ", "html": "{}</p>"}, "ud4.6:2.1": {"pli": "Addasā kho bhagavā āyasmantaṁ piṇḍolabhāradvājaṁ avidūre nisinnaṁ pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya āraññikaṁ piṇḍapātikaṁ paṁsukūlikaṁ tecīvarikaṁ appicchaṁ santuṭṭhaṁ pavivittaṁ asaṁsaṭṭhaṁ āraddhavīriyaṁ dhutavādaṁ adhicittamanuyuttaṁ. ", "eng": "The Buddha saw him meditating there. ", "html": "<p>{}</p>"}, "ud4.6:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud4.6:4.1": {"pli": "“Anūpavādo anūpaghāto, ", "eng": "“Not speaking ill nor doing harm; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud4.6:4.2": {"pli": "Pātimokkhe ca saṁvaro; ", "eng": "restraint in the monastic code; ", "html": "<span class='verse-line'>{}</span>"}, "ud4.6:4.3": {"pli": "Mattaññutā ca bhattasmiṁ, ", "eng": "moderation in eating; ", "html": "<span class='verse-line'>{}</span>"}, "ud4.6:4.4": {"pli": "Pantañca sayanāsanaṁ; ", "eng": "staying in remote lodgings; ", "html": "<span class='verse-line'>{}</span>"}, "ud4.6:4.5": {"pli": "Adhicitte ca āyogo, ", "eng": "commitment to the higher mind—", "html": "<span class='verse-line'>{}</span>"}, "ud4.6:4.6": {"pli": "Etaṁ buddhāna sāsanan”ti. ", "eng": "this is the instruction of the Buddhas.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud4.6:4.7": {"pli": "Chaṭṭhaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud4.7": {"ud4.7:0.1": {"pli": "Udāna 4.7 ", "eng": "Heartfelt Sayings 4.7 ", "html": "<article id='ud4.7'><header><ul><li class='division'>{}</li></ul>"}, "ud4.7:0.2": {"pli": "Sāriputtasutta ", "eng": "With Sāriputta ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud4.7:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud4.7:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud4.7:1.3": {"pli": "Tena kho pana samayena āyasmā sāriputto bhagavato avidūre nisinno hoti pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya appiccho santuṭṭho pavivitto asaṁsaṭṭho āraddhavīriyo adhicittamanuyutto. ", "eng": "Now at that time Venerable Sāriputta was sitting not far from the Buddha, cross-legged, with his body straight. He was of few wishes, content, secluded, aloof, energetic, dedicated to the higher mind. ", "html": "{}</p>"}, "ud4.7:2.1": {"pli": "Addasā kho bhagavā āyasmantaṁ sāriputtaṁ avidūre nisinnaṁ pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya appicchaṁ santuṭṭhaṁ pavivittaṁ asaṁsaṭṭhaṁ āraddhavīriyaṁ adhicittamanuyuttaṁ. ", "eng": "The Buddha saw him meditating there. ", "html": "<p>{}</p>"}, "ud4.7:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud4.7:4.1": {"pli": "“Adhicetaso appamajjato, ", "eng": "“A sage of higher consciousness, diligent, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud4.7:4.2": {"pli": "Munino monapathesu sikkhato; ", "eng": "training in the ways of sagacity: ", "html": "<span class='verse-line'>{}</span>"}, "ud4.7:4.3": {"pli": "Sokā na bhavanti tādino, ", "eng": "there are no sorrows for the unaffected, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.7:4.4": {"pli": "Upasantassa sadā satīmato”ti. ", "eng": "calm and ever mindful.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud4.7:4.5": {"pli": "Sattamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud4.8": {"ud4.8:0.1": {"pli": "Udāna 4.8 ", "eng": "Heartfelt Sayings 4.8 ", "html": "<article id='ud4.8'><header><ul><li class='division'>{}</li></ul>"}, "ud4.8:0.2": {"pli": "Sundarīsutta ", "eng": "With Sundarī ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud4.8:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud4.8:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud4.8:1.3": {"pli": "Tena kho pana samayena bhagavā sakkato hoti garukato mānito pūjito apacito lābhī cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhārānaṁ. ", "eng": "Now at that time the Buddha was honored, respected, revered, venerated, and esteemed. And he received robes, almsfood, lodgings, and medicines and supplies for the sick. ", "html": "{}"}, "ud4.8:1.4": {"pli": "Bhikkhusaṅghopi sakkato hoti garukato mānito pūjito apacito lābhī cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhārānaṁ. ", "eng": "And the mendicant Saṅgha was also honored, respected, revered, venerated, and esteemed. And they received robes, almsfood, lodgings, and medicines and supplies for the sick. ", "html": "{}"}, "ud4.8:1.5": {"pli": "Aññatitthiyā pana paribbājakā asakkatā honti agarukatā amānitā apūjitā anapacitā na lābhino cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhārānaṁ. ", "eng": "But the wanderers who followed other religions were not honored, respected, revered, venerated, and esteemed. And they didn’t receive robes, almsfood, lodgings, and medicines and supplies for the sick. ", "html": "{}</p>"}, "ud4.8:2.1": {"pli": "Atha kho te aññatitthiyā paribbājakā bhagavato sakkāraṁ asahamānā bhikkhusaṅghassa ca yena sundarī paribbājikā tenupasaṅkamiṁsu; upasaṅkamitvā sundariṁ paribbājikaṁ etadavocuṁ: ", "eng": "Then those wanderers who followed other religions, unable to bear the esteem of the mendicant Sangha, approached the female wanderer Sundarī and said, ", "html": "<p>{}"}, "ud4.8:2.2": {"pli": "“ussahasi tvaṁ, bhagini, ñātīnaṁ atthaṁ kātun”ti? ", "eng": "“Sister, are you able to do something for the welfare of your kin?” ", "html": "{}", "comm": "Ānandajoti has “venture”, Ṭhānissaro has “dare”, but a similar phrase at <a href='index.html?q=pli-tv-kd8#12.1.5'>Kd 8:12.1.5</a> clearly has the weaker sense of “able”, per Ireland and commentary (<i lang='pi' translate='no'>ussahasīti sakkosi</i>). "}, "ud4.8:2.3": {"pli": "“Kyāhaṁ, ayyā, karomi? ", "eng": "“What can I do, masters? ", "html": "{}"}, "ud4.8:2.4": {"pli": "Kiṁ mayā na sakkā kātuṁ? ", "eng": "How can I help? ", "html": "{}", "comm": "Accepting the variant <i lang='pi' translate='no'>kiṁ mayā sakkā</i>, which is found in all similar passages. Reading, as Ṭhānissaro and Ānandajoti do, “what can I <em>not</em> do” is pleasingly dramatic, but not very idiomatic for Pali. Far more likely a simple reinforcement is meant. Commentary ignores this phrase, implying it thought there was nothing unusual. "}, "ud4.8:2.5": {"pli": "Jīvitampi me pariccattaṁ ñātīnaṁ atthāyā”ti. ", "eng": "I’d even give my life for the welfare of my kin.” ", "html": "{}</p>"}, "ud4.8:3.1": {"pli": "“Tena hi, bhagini, abhikkhaṇaṁ jetavanaṁ gacchāhī”ti. ", "eng": "“Well then, sister, frequently visit Jeta’s Grove.” ", "html": "<p>{}"}, "ud4.8:3.2": {"pli": "“Evaṁ, ayyā”ti kho sundarī paribbājikā tesaṁ aññatitthiyānaṁ paribbājakānaṁ paṭissutvā abhikkhaṇaṁ jetavanaṁ agamāsi. ", "eng": "“Yes, masters,” she replied, and did as they asked. ", "html": "{}</p>"}, "ud4.8:4.1": {"pli": "Yadā te aññiṁsu aññatitthiyā paribbājakā: ", "eng": "When those wanderers knew ", "html": "<p>{}"}, "ud4.8:4.2": {"pli": "“vodiṭṭhā kho sundarī paribbājikā bahujanena abhikkhaṇaṁ jetavanaṁ gacchantī”ti. ", "eng": "that Sundarī had been clearly seen by many people frequently visiting Jeta’s Grove, ", "html": "{}"}, "ud4.8:4.3": {"pli": "Atha naṁ jīvitā voropetvā tattheva jetavanassa parikhākūpe nikkhipitvā yena rājā pasenadi kosalo tenupasaṅkamiṁsu; upasaṅkamitvā rājānaṁ pasenadiṁ kosalaṁ etadavocuṁ: ", "eng": "they killed her and dumped her in the ditch around Jeta’s Grove. Then they went to see King Pasenadi of Kosala and said to him, ", "html": "{}"}, "ud4.8:4.4": {"pli": "“yā sā, mahārāja, sundarī paribbājikā; sā no na dissatī”ti. ", "eng": "“Great king, we cannot find the female wanderer Sundarī.” ", "html": "{}"}, "ud4.8:4.5": {"pli": "“Kattha pana tumhe āsaṅkathā”ti? ", "eng": "“But where do you suspect she is?” ", "html": "{}"}, "ud4.8:4.6": {"pli": "“Jetavane, mahārājā”ti. ", "eng": "“At Jeta’s Grove, great king.” ", "html": "{}"}, "ud4.8:4.7": {"pli": "“Tena hi jetavanaṁ vicinathā”ti. ", "eng": "“Well then, search Jeta’s Grove.” ", "html": "{}</p>"}, "ud4.8:5.1": {"pli": "Atha kho te aññatitthiyā paribbājakā jetavanaṁ vicinitvā yathānikkhittaṁ parikhākūpā uddharitvā mañcakaṁ āropetvā sāvatthiṁ pavesetvā rathiyāya rathiyaṁ siṅghāṭakena siṅghāṭakaṁ upasaṅkamitvā manusse ujjhāpesuṁ: ", "eng": "So the wanderers searched Jeta’s Grove. They pulled her body up from the ditch where they had dumped it, and lifted it on a bier. Having entered Sāvatthī, they went from street to street and from square to square, complaining to people: ", "html": "<p>{}</p>"}, "ud4.8:6.1": {"pli": "“Passathāyyā samaṇānaṁ sakyaputtiyānaṁ kammaṁ. ", "eng": "“See the deed of the ascetics who follow the Sakyan! ", "html": "<p>{}"}, "ud4.8:6.2": {"pli": "Alajjino ime samaṇā sakyaputtiyā dussīlā pāpadhammā musāvādino abrahmacārino. ", "eng": "Shameless are these ascetics who follow the Sakyan, immoral and wicked. They are liars and unchaste. ", "html": "{}"}, "ud4.8:6.3": {"pli": "Ime hi nāma dhammacārino samacārino brahmacārino saccavādino sīlavanto kalyāṇadhammā paṭijānissanti. ", "eng": "Sure, they claim to be of principled and moral conduct, to be chaste, truthful, ethical, and of good character. ", "html": "{}", "comm": "The idiom <i lang='pi' translate='no'>ime hi nāma</i> appears mildly emphatic in a disparaging way. Compare <i lang='pi' translate='no'>kathaṁ hi nāma</i> just below. "}, "ud4.8:6.4": {"pli": "Natthi imesaṁ sāmaññaṁ, natthi imesaṁ brahmaññaṁ. ", "eng": "But they have no asceticism, no spirituality. ", "html": "{}"}, "ud4.8:6.5": {"pli": "Naṭṭhaṁ imesaṁ sāmaññaṁ, naṭṭhaṁ imesaṁ brahmaññaṁ. ", "eng": "Asceticism and spirituality are lost to them! ", "html": "{}"}, "ud4.8:6.6": {"pli": "Kuto imesaṁ sāmaññaṁ, kuto imesaṁ brahmaññaṁ? ", "eng": "Where is their asceticism, where their spirituality? ", "html": "{}"}, "ud4.8:6.7": {"pli": "Apagatā ime sāmaññā, apagatā ime brahmaññā. ", "eng": "They have abandoned asceticism and spirituality! ", "html": "{}"}, "ud4.8:6.8": {"pli": "Kathañhi nāma puriso purisakiccaṁ karitvā itthiṁ jīvitā voropessatī”ti. ", "eng": "How on earth can a man, having done a man’s business, kill a woman!” ", "html": "{}</p>"}, "ud4.8:7.1": {"pli": "Tena kho pana samayena sāvatthiyaṁ manussā bhikkhū disvā asabbhāhi pharusāhi vācāhi akkosanti paribhāsanti rosanti vihesanti: ", "eng": "Then at that time when the people of Sāvatthī saw the mendicants they abused and insulted them with rude, harsh words: ", "html": "<p>{}</p>"}, "ud4.8:8.1": {"pli": "“Alajjino ime samaṇā sakyaputtiyā dussīlā pāpadhammā musāvādino abrahmacārino. ", "eng": "“Shameless are these ascetics who follow the Sakyan, immoral, wicked, liars, and fake chastes. ", "html": "<p>{}"}, "ud4.8:8.2": {"pli": "Ime hi nāma dhammacārino samacārino brahmacārino saccavādino sīlavanto kalyāṇadhammā paṭijānissanti. ", "eng": "Sure, they claim to be of principled and moral conduct, to be chaste, truthful, ethical, and of good character. ", "html": "{}"}, "ud4.8:8.3": {"pli": "Natthi imesaṁ sāmaññaṁ, natthi imesaṁ brahmaññaṁ. ", "eng": "But they have no asceticism, no spirituality. ", "html": "{}"}, "ud4.8:8.4": {"pli": "Naṭṭhaṁ imesaṁ sāmaññaṁ, naṭṭhaṁ imesaṁ brahmaññaṁ. ", "eng": "Asceticism and spirituality are lost to them! ", "html": "{}"}, "ud4.8:8.5": {"pli": "Kuto imesaṁ sāmaññaṁ, kuto imesaṁ brahmaññaṁ? ", "eng": "Where is their asceticism, where their spirituality? ", "html": "{}"}, "ud4.8:8.6": {"pli": "Apagatā ime sāmaññā, apagatā ime brahmaññā. ", "eng": "They have abandoned asceticism and spirituality! ", "html": "{}"}, "ud4.8:8.7": {"pli": "Kathañhi nāma puriso purisakiccaṁ karitvā itthiṁ jīvitā voropessatī”ti. ", "eng": "How on earth can a man, having done a man’s business, kill a woman!” ", "html": "{}</p>"}, "ud4.8:9.1": {"pli": "Atha kho sambahulā bhikkhū pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya sāvatthiṁ piṇḍāya pāvisiṁsu. ", "eng": "Then several mendicants robed up in the morning and, taking their bowls and robes, entered Sāvatthī for alms. ", "html": "<p>{}"}, "ud4.8:9.2": {"pli": "Sāvatthiyaṁ piṇḍāya caritvā pacchābhattaṁ piṇḍapātapaṭikkantā yena bhagavā tenupasaṅkamiṁsu; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdiṁsu. Ekamantaṁ nisinnā kho te bhikkhū bhagavantaṁ etadavocuṁ: ", "eng": "Then, after the meal, when they returned from almsround, they went up to the Buddha, bowed, sat down to one side, and told him what was happening. ", "html": "{}</p>"}, "ud4.8:10.1": {"pli": "“Etarahi, bhante, sāvatthiyaṁ manussā bhikkhū disvā asabbhāhi pharusāhi vācāhi akkosanti paribhāsanti rosanti vihesanti: ", "html": "<p>{}"}, "ud4.8:10.2": {"pli": "‘alajjino ime samaṇā sakyaputtiyā dussīlā pāpadhammā musāvādino abrahmacārino. ", "html": "{}"}, "ud4.8:10.3": {"pli": "Ime hi nāma dhammacārino samacārino brahmacārino saccavādino sīlavanto kalyāṇadhammā paṭijānissanti. ", "html": "{}"}, "ud4.8:10.4": {"pli": "Natthi imesaṁ sāmaññaṁ, natthi imesaṁ brahmaññaṁ. ", "html": "{}"}, "ud4.8:10.5": {"pli": "Naṭṭhaṁ imesaṁ sāmaññaṁ, naṭṭhaṁ imesaṁ brahmaññaṁ. ", "html": "{}"}, "ud4.8:10.6": {"pli": "Kuto imesaṁ sāmaññaṁ, kuto imesaṁ brahmaññaṁ? ", "html": "{}"}, "ud4.8:10.7": {"pli": "Apagatā ime sāmaññā, apagatā ime brahmaññā. ", "html": "{}"}, "ud4.8:10.8": {"pli": "Kathañhi nāma puriso purisakiccaṁ karitvā itthiṁ jīvitā voropessatī’”ti. ", "html": "{}</p>"}, "ud4.8:11.1": {"pli": "“Neso, bhikkhave, saddo ciraṁ bhavissati sattāhameva bhavissati. Sattāhassa accayena antaradhāyissati. ", "eng": "“That rumor, mendicants, won’t last long. It will only be seven days, then it will vanish. ", "html": "<p>{}"}, "ud4.8:11.2": {"pli": "Tena hi, bhikkhave, ye manussā bhikkhū disvā asabbhāhi pharusāhi vācāhi akkosanti paribhāsanti rosanti vihesanti, te tumhe imāya gāthāya paṭicodetha: ", "eng": "So you may respond to those critics with this verse: ", "html": "{}</p>"}, "ud4.8:12.1": {"pli": "‘Abhūtavādī nirayaṁ upeti, ", "eng": "A liar goes to hell, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud4.8:12.2": {"pli": "Yo vāpi katvā na karomi cāha; ", "eng": "as does one who denies what they did. ", "html": "<span class='verse-line'>{}</span>"}, "ud4.8:12.3": {"pli": "Ubhopi te pecca samā bhavanti, ", "eng": "Both are equal in the hereafter, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.8:12.4": {"pli": "Nihīnakammā manujā paratthā’”ti. ", "eng": "those men of base deeds.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud4.8:13.1": {"pli": "Atha kho te bhikkhū bhagavato santike imaṁ gāthaṁ pariyāpuṇitvā ye manussā bhikkhū disvā asabbhāhi pharusāhi vācāhi akkosanti paribhāsanti rosanti vihesanti te imāya gāthāya paṭicodenti: ", "eng": "The mendicants memorized that verse in the Buddha’s presence, then used it to respond to those critics. ", "html": "<p>{}</p>"}, "ud4.8:14.1": {"pli": "“Abhūtavādī nirayaṁ upeti, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud4.8:14.2": {"pli": "Yo vāpi katvā na karomi cāha; ", "html": "<span class='verse-line'>{}</span>"}, "ud4.8:14.3": {"pli": "Ubhopi te pecca samā bhavanti, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.8:14.4": {"pli": "Nihīnakammā manujā paratthā”ti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud4.8:15.1": {"pli": "Manussānaṁ etadahosi: ", "eng": "People thought, ", "html": "<p>{}"}, "ud4.8:15.2": {"pli": "“akārakā ime samaṇā sakyaputtiyā, nayimehi kataṁ, sapantime samaṇā sakyaputtiyā”ti. ", "eng": "“These ascetics who follow the Sakyan didn’t do it, it was not done by them, they swear it.” ", "html": "{}"}, "ud4.8:15.3": {"pli": "Neva so saddo ciraṁ ahosi. Sattāhameva ahosi. Sattāhassa accayena antaradhāyi. ", "eng": "That rumor didn’t last long. It was seven days, then it vanished. ", "html": "{}</p>"}, "ud4.8:16.1": {"pli": "Atha kho sambahulā bhikkhū yena bhagavā tenupasaṅkamiṁsu; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdiṁsu. Ekamantaṁ nisinnā kho te bhikkhū bhagavato etadavocuṁ: ", "eng": "Then several mendicants went up to the Buddha, bowed, sat down to one side, and said to him, ", "html": "<p>{}</p>"}, "ud4.8:17.1": {"pli": "“Acchariyaṁ, bhante, abbhutaṁ, bhante. ", "eng": "“It’s incredible, sir, it’s amazing! ", "html": "<p>{}"}, "ud4.8:17.2": {"pli": "Yāva subhāsitañcidaṁ, bhante, bhagavatā: ", "eng": "How well said this was by the Buddha: ", "html": "{}"}, "ud4.8:17.3": {"pli": "‘neso, bhikkhave, saddo ciraṁ bhavissati. Sattāhameva bhavissati. Sattāhassa accayena antaradhāyissatī’ti. ", "eng": "‘That rumor, mendicants, won’t last long. It will only be seven days, then it will vanish.’ ", "html": "{}"}, "ud4.8:17.4": {"pli": "Antarahito so, bhante, saddo”ti. ", "eng": "That rumor has vanished, sir.” ", "html": "{}</p>"}, "ud4.8:18.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud4.8:19.1": {"pli": "“Tudanti vācāya janā asaññatā, ", "eng": "“People out of control stab with words, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud4.8:19.2": {"pli": "Sarehi saṅgāmagataṁva kuñjaraṁ; ", "eng": "like they stab a tusker in battle with darts. ", "html": "<span class='verse-line'>{}</span>"}, "ud4.8:19.3": {"pli": "Sutvāna vākyaṁ pharusaṁ udīritaṁ, ", "eng": "When they hear a harsh word spoken, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.8:19.4": {"pli": "Adhivāsaye bhikkhu aduṭṭhacitto”ti. ", "eng": "a mendicant should endure with no anger in heart.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud4.8:19.5": {"pli": "Aṭṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud4.9": {"ud4.9:0.1": {"pli": "Udāna 4.9 ", "eng": "Heartfelt Sayings 4.9 ", "html": "<article id='ud4.9'><header><ul><li class='division'>{}</li></ul>"}, "ud4.9:0.2": {"pli": "Upasenasutta ", "eng": "With Upasena ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud4.9:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud4.9:1.2": {"pli": "ekaṁ samayaṁ bhagavā rājagahe viharati veḷuvane kalandakanivāpe. ", "eng": "At one time the Buddha was staying near Rājagaha, in the Bamboo Grove, the squirrels’ feeding ground. ", "html": "{}"}, "ud4.9:1.3": {"pli": "Atha kho āyasmato upasenassa vaṅgantaputtassa rahogatassa paṭisallīnassa evaṁ cetaso parivitakko udapādi: ", "eng": "Then as Venerable Upasena son of Vaṅgantā was in private retreat this thought came to his mind: ", "html": "{}"}, "ud4.9:1.4": {"pli": "“lābhā vata me, suladdhaṁ vata me, ", "eng": "“I’m so fortunate, so very fortunate! ", "html": "{}"}, "ud4.9:1.5": {"pli": "satthā ca me bhagavā arahaṁ sammāsambuddho; ", "eng": "My Teacher is the Blessed One, perfected, a fully awakened Buddha. ", "html": "{}"}, "ud4.9:1.6": {"pli": "svākkhāte camhi dhammavinaye agārasmā anagāriyaṁ pabbajito; ", "eng": "Well explained is the teaching and training in which I have gone forth from the lay life to homelessness. ", "html": "{}"}, "ud4.9:1.7": {"pli": "sabrahmacārino ca me sīlavanto kalyāṇadhammā; ", "eng": "My spiritual companions are ethical and of good character. ", "html": "{}"}, "ud4.9:1.8": {"pli": "sīlesu camhi paripūrakārī; ", "eng": "I have fulfilled the precepts. ", "html": "{}"}, "ud4.9:1.9": {"pli": "susamāhito camhi ekaggacitto; ", "eng": "My mind is unified and serene. ", "html": "{}"}, "ud4.9:1.10": {"pli": "arahā camhi khīṇāsavo; ", "eng": "I am a perfected one who has ended the defilements. ", "html": "{}"}, "ud4.9:1.11": {"pli": "mahiddhiko camhi mahānubhāvo. ", "eng": "I am of great might and power. ", "html": "{}"}, "ud4.9:1.12": {"pli": "Bhaddakaṁ me jīvitaṁ, bhaddakaṁ maraṇan”ti. ", "eng": "My life has been good, and my death will be good.” ", "html": "{}</p>"}, "ud4.9:2.1": {"pli": "Atha kho bhagavā āyasmato upasenassa vaṅgantaputtassa cetasā cetoparivitakkamaññāya tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, knowing that Venerable Upasena’s train of thought, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud4.9:3.1": {"pli": "“Yaṁ jīvitaṁ na tapati, ", "eng": "“One who has no guilt in life, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud4.9:3.2": {"pli": "maraṇante na socati; ", "eng": "nor grief at facing death: ", "html": "<span class='verse-line'>{}</span>"}, "ud4.9:3.3": {"pli": "Sa ve diṭṭhapado dhīro, ", "eng": "that attentive one who has seen the truth, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.9:3.4": {"pli": "sokamajjhe na socati. ", "eng": "grieves not among the grieving. ", "html": "<span class='verse-line'>{}</span></p>"}, "ud4.9:4.1": {"pli": "Ucchinnabhavataṇhassa, ", "eng": "For the mendicant with peaceful mind, ", "html": "<p><span class='verse-line'>{}</span>"}, "ud4.9:4.2": {"pli": "santacittassa bhikkhuno; ", "eng": "who has cut off craving for continued existence, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.9:4.3": {"pli": "Vikkhīṇo jātisaṁsāro, ", "eng": "transmigration through births is finished, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.9:4.4": {"pli": "natthi tassa punabbhavo”ti. ", "eng": "there’ll be no more future lives for them.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud4.9:4.5": {"pli": "Navamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud4.10": {"ud4.10:0.1": {"pli": "Udāna 4.10 ", "eng": "Heartfelt Sayings 4.10 ", "html": "<article id='ud4.10'><header><ul><li class='division'>{}</li></ul>"}, "ud4.10:0.2": {"pli": "Sāriputtaupasamasutta ", "eng": "The Peacefulness of Sāriputta ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud4.10:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud4.10:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud4.10:1.3": {"pli": "Tena kho pana samayena āyasmā sāriputto bhagavato avidūre nisinno hoti pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya attano upasamaṁ paccavekkhamāno. ", "eng": "Now at that time Venerable Sāriputta was sitting not far from the Buddha, cross-legged, with his body straight, reviewing his own peacefulness. ", "html": "{}</p>"}, "ud4.10:2.1": {"pli": "Addasā kho bhagavā āyasmantaṁ sāriputtaṁ avidūre nisinnaṁ pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya attano upasamaṁ paccavekkhamānaṁ. ", "eng": "The Buddha saw him meditating there. ", "html": "<p>{}</p>"}, "ud4.10:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud4.10:4.1": {"pli": "“Upasantasantacittassa, ", "eng": "“With mind at peace, so full of peace, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud4.10:4.2": {"pli": "netticchinnassa bhikkhuno; ", "eng": "for a mendicant who has cut the cord, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.10:4.3": {"pli": "Vikkhīṇo jātisaṁsāro, ", "eng": "transmigration through births is finished: ", "html": "<span class='verse-line'>{}</span>"}, "ud4.10:4.4": {"pli": "mutto so mārabandhanā”ti. ", "eng": "they’re freed from Māra’s bonds.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud4.10:4.5": {"pli": "Dasamaṁ. ", "html": "<p class='endsutta'>{}</p>"}, "ud4.10:4.6": {"pli": "Meghiyavaggo catuttho. ", "html": "<p class='endvagga'>{}</p>"}, "ud4.10:5.0": {"pli": "Tassuddānaṁ ", "html": "<p class='uddana-intro'>{}</p>"}, "ud4.10:5.1": {"pli": "Meghiyo uddhatā gopālo, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "ud4.10:5.2": {"pli": "yakkho nāgena pañcamaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "ud4.10:5.3": {"pli": "Piṇḍolo sāriputto ca, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.10:5.4": {"pli": "sundarī bhavati aṭṭhamaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "ud4.10:5.5": {"pli": "Upaseno vaṅgantaputto, ", "html": "<span class='verse-line'>{}</span>"}, "ud4.10:5.6": {"pli": "sāriputto ca te dasāti. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}}, "ud5.1": {"ud5.1:0.1": {"pli": "Udāna 5.1 ", "eng": "Heartfelt Sayings 5.1 ", "html": "<article id='ud5.1'><header><ul><li class='division'>{}</li></ul>"}, "ud5.1:0.2": {"pli": "Piyatarasutta ", "eng": "Who Is More Dear? ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud5.1:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud5.1:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud5.1:1.3": {"pli": "Tena kho pana samayena rājā pasenadi kosalo mallikāya deviyā saddhiṁ uparipāsādavaragato hoti. ", "eng": "Now at that time King Pasenadi of Kosala was upstairs in the royal longhouse together with Queen Mallikā. ", "html": "{}"}, "ud5.1:1.4": {"pli": "Atha kho rājā pasenadi kosalo mallikaṁ deviṁ etadavoca: ", "eng": "Then King Pasenadi addressed Queen Mallikā, ", "html": "{}"}, "ud5.1:1.5": {"pli": "“atthi nu kho te, mallike, kocañño attanā piyataro”ti? ", "eng": "“Mallikā, is there anyone more dear to you than yourself?” ", "html": "{}</p>"}, "ud5.1:2.1": {"pli": "“Natthi kho me, mahārāja, kocañño attanā piyataro. ", "eng": "“No, great king, there isn’t. ", "html": "<p>{}"}, "ud5.1:2.2": {"pli": "Tuyhaṁ pana, mahārāja, atthañño koci attanā piyataro”ti? ", "eng": "But is there anyone more dear to you than yourself?” ", "html": "{}"}, "ud5.1:2.3": {"pli": "“Mayhampi kho, mallike, natthañño koci attanā piyataro”ti. ", "eng": "“For me also, Mallikā, there’s no-one.” ", "html": "{}</p>"}, "ud5.1:3.1": {"pli": "Atha kho rājā pasenadi kosalo pāsādā orohitvā yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. Ekamantaṁ nisinno kho rājā pasenadi kosalo bhagavantaṁ etadavoca: ", "eng": "Then King Pasenadi of Kosala came downstairs from the stilt longhouse, went to the Buddha, bowed, sat down to one side, and told him what had happened. ", "html": "<p>{}</p>"}, "ud5.1:4.1": {"pli": "“Idhāhaṁ, bhante, mallikāya deviyā saddhiṁ uparipāsādavaragato mallikaṁ deviṁ etadavocaṁ: ", "html": "<p>{}"}, "ud5.1:4.2": {"pli": "‘atthi nu kho te, mallike, kocañño attanā piyataro’ti? ", "html": "{}"}, "ud5.1:4.3": {"pli": "Evaṁ vutte, mallikā devī maṁ etadavoca: ", "html": "{}"}, "ud5.1:4.4": {"pli": "‘natthi kho me, mahārāja, kocañño attanā piyataro. ", "html": "{}"}, "ud5.1:4.5": {"pli": "Tuyhaṁ pana, mahārāja, atthañño koci attanā piyataro’ti? ", "html": "{}"}, "ud5.1:4.6": {"pli": "Evaṁ vutte, ahaṁ, bhante, mallikaṁ deviṁ etadavocaṁ: ", "html": "{}"}, "ud5.1:4.7": {"pli": "‘mayhampi kho, mallike, natthañño koci attanā piyataro’”ti. ", "html": "{}</p>"}, "ud5.1:5.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud5.1:6.1": {"pli": "“Sabbā disā anuparigamma cetasā, ", "eng": "“Having explored every quarter with the mind, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud5.1:6.2": {"pli": "Nevajjhagā piyataramattanā kvaci; ", "eng": "one finds no-one dearer than oneself. ", "html": "<span class='verse-line'>{}</span>"}, "ud5.1:6.3": {"pli": "Evaṁ piyo puthu attā paresaṁ, ", "eng": "Likewise for others, each holds themselves dear; ", "html": "<span class='verse-line'>{}</span>"}, "ud5.1:6.4": {"pli": "Tasmā na hiṁse paramattakāmo”ti. ", "eng": "so one who desires self-knowledge <j>would harm no other.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud5.1:6.5": {"pli": "Paṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud5.2": {"ud5.2:0.1": {"pli": "Udāna 5.2 ", "eng": "Heartfelt Sayings 5.2 ", "html": "<article id='ud5.2'><header><ul><li class='division'>{}</li></ul>"}, "ud5.2:0.2": {"pli": "Appāyukasutta ", "eng": "Short-lived ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud5.2:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud5.2:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud5.2:1.3": {"pli": "Atha kho āyasmā ānando sāyanhasamayaṁ paṭisallānā vuṭṭhito yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. Ekamantaṁ nisinno kho āyasmā ānando bhagavantaṁ etadavoca: ", "eng": "Then in the late afternoon, Venerable Ānanda came out of retreat and went to the Buddha. He bowed, sat down to one side, and said to him: ", "html": "{}"}, "ud5.2:1.4": {"pli": "“acchariyaṁ, bhante, abbhutaṁ, bhante. ", "eng": "“It’s incredible, sir, it’s amazing! ", "html": "{}"}, "ud5.2:1.5": {"pli": "Yāva appāyukā hi, bhante, bhagavato mātā ahosi, sattāhajāte bhagavati bhagavato mātā kālamakāsi, tusitaṁ kāyaṁ upapajjī”ti. ", "eng": "How short-lived was the Blessed One’s mother! For seven days after the Blessed One was born, his mother passed away and was reborn in the host of joyful gods.” ", "html": "{}</p>", "comm": "Here I use “Blessed One” rather than my usual “Buddha” for <i lang='pi' translate='no'>bhagavā</i>. Normally the difference is immaterial, as it is simply the most common way to address the Buddha. In this case, however, the passage Ānanda is referring to uses <i lang='pi' translate='no'>bodhisatta</i>, and it is quite striking that he changes it here. "}, "ud5.2:2.1": {"pli": "“Evametaṁ, ānanda, appāyukā hi, ānanda, bodhisattamātaro honti. ", "eng": "“That’s so true, Ānanda! For the mothers of beings intent of awakening are short-lived. ", "html": "<p>{}", "comm": "The Buddha switches back to <i lang='pi' translate='no'>bodhisatta</i>, and depersonalizes it by using the plural. "}, "ud5.2:2.2": {"pli": "Sattāhajātesu bodhisattesu bodhisattamātaro kālaṁ karonti, tusitaṁ kāyaṁ upapajjantī”ti. ", "eng": "Seven days after the beings intent on awakening are born, their mothers pass away and are reborn in the host of joyful gods.” ", "html": "{}</p>"}, "ud5.2:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud5.2:4.1": {"pli": "“Ye keci bhūtā bhavissanti ye vāpi, ", "eng": "“Whether born or to be born, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud5.2:4.2": {"pli": "Sabbe gamissanti pahāya dehaṁ; ", "eng": "all depart, leaving the body behind. ", "html": "<span class='verse-line'>{}</span>"}, "ud5.2:4.3": {"pli": "Taṁ sabbajāniṁ kusalo viditvā, ", "eng": "The skillful, understanding that all is lost, ", "html": "<span class='verse-line'>{}</span>"}, "ud5.2:4.4": {"pli": "Ātāpiyo brahmacariyaṁ careyyā”ti. ", "eng": "would keenly practice the spiritual life.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud5.2:4.5": {"pli": "Dutiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud5.3": {"ud5.3:0.1": {"pli": "Udāna 5.3 ", "eng": "Heartfelt Sayings 5.3 ", "html": "<article id='ud5.3'><header><ul><li class='division'>{}</li></ul>"}, "ud5.3:0.2": {"pli": "Suppabuddhakuṭṭhisutta ", "eng": "With Suppabuddha the Leper ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud5.3:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud5.3:1.2": {"pli": "ekaṁ samayaṁ bhagavā rājagahe viharati veḷuvane kalandakanivāpe. ", "eng": "At one time the Buddha was staying near Rājagaha, in the Bamboo Grove, the squirrels’ feeding ground. ", "html": "{}"}, "ud5.3:1.3": {"pli": "Tena kho pana samayena rājagahe suppabuddho nāma kuṭṭhī ahosi—", "eng": "At that time in Rājagaha there was a leper called Suppabuddha. ", "html": "{}"}, "ud5.3:1.4": {"pli": "manussadaliddo, manussakapaṇo, manussavarāko. ", "eng": "He was a poor person, destitute, and pitiful. ", "html": "{}"}, "ud5.3:1.5": {"pli": "Tena kho pana samayena bhagavā mahatiyā parisāya parivuto dhammaṁ desento nisinno hoti. ", "eng": "Now, at that time the Buddha was teaching Dhamma, surrounded by a large assembly. ", "html": "{}</p>"}, "ud5.3:2.1": {"pli": "Addasā kho suppabuddho kuṭṭhī taṁ mahājanakāyaṁ dūratova sannipatitaṁ. ", "eng": "Suppabuddha saw the crowd seated off in the distance ", "html": "<p>{}"}, "ud5.3:2.2": {"pli": "Disvānassa etadahosi: ", "eng": "and thought, ", "html": "{}"}, "ud5.3:2.3": {"pli": "“nissaṁsayaṁ kho ettha kiñci khādanīyaṁ vā bhojanīyaṁ vā bhājīyati. ", "eng": "“Surely some fresh and cooked foods will be distributed there. ", "html": "{}"}, "ud5.3:2.4": {"pli": "Yannūnāhaṁ yena so mahājanakāyo tenupasaṅkameyyaṁ. ", "eng": "Why don’t I approach the crowd? ", "html": "{}"}, "ud5.3:2.5": {"pli": "Appeva nāmettha kiñci khādanīyaṁ vā bhojanīyaṁ vā labheyyan”ti. ", "eng": "Hopefully I’ll get something to eat.” ", "html": "{}</p>"}, "ud5.3:3.1": {"pli": "Atha kho suppabuddho kuṭṭhī yena so mahājanakāyo tenupasaṅkami. ", "eng": "So he approached the crowd ", "html": "<p>{}"}, "ud5.3:3.2": {"pli": "Addasā kho suppabuddho kuṭṭhī bhagavantaṁ mahatiyā parisāya parivutaṁ dhammaṁ desentaṁ nisinnaṁ. ", "eng": "where he saw the Buddha teaching Dhamma, surrounded by a large assembly. ", "html": "{}"}, "ud5.3:3.3": {"pli": "Disvānassa etadahosi: ", "eng": "When he saw this he thought, ", "html": "{}"}, "ud5.3:3.4": {"pli": "“na kho ettha kiñci khādanīyaṁ vā bhojanīyaṁ vā bhājīyati. ", "eng": "“There’s no food being distributed here. ", "html": "{}"}, "ud5.3:3.5": {"pli": "Samaṇo ayaṁ gotamo parisati dhammaṁ deseti. ", "eng": "The ascetic Gotama is teaching Dhamma in an assembly. ", "html": "{}"}, "ud5.3:3.6": {"pli": "Yannūnāhampi dhammaṁ suṇeyyan”ti. ", "eng": "Why don’t I also listen to the teaching?” ", "html": "{}"}, "ud5.3:3.7": {"pli": "Tattheva ekamantaṁ nisīdi: ", "eng": "Right there he sat down to one side, thinking, ", "html": "{}"}, "ud5.3:3.8": {"pli": "“ahampi dhammaṁ sossāmī”ti. ", "eng": "“I also will listen to the teaching.” ", "html": "{}</p>"}, "ud5.3:4.1": {"pli": "Atha kho bhagavā sabbāvantaṁ parisaṁ cetasā ceto paricca manasākāsi ", "eng": "Then the Buddha focused on encompassing the minds of everyone in the assembly, thinking, ", "html": "<p>{}"}, "ud5.3:4.2": {"pli": "“ko nu kho idha bhabbo dhammaṁ viññātun”ti? ", "eng": "“Who here is capable of understanding the teaching?” ", "html": "{}"}, "ud5.3:4.3": {"pli": "Addasā kho bhagavā suppabuddhaṁ kuṭṭhiṁ tassaṁ parisāyaṁ nisinnaṁ. ", "eng": "He saw Suppabuddha sitting in the assembly, ", "html": "{}", "comm": "It’s interesting to note that, despite the perhaps derogatory sense of <i lang='pi' translate='no'>kuṭṭhi</i> (“leper”), there is no question that Suppabuddha sits in the assembly and is not physically exlcuded. "}, "ud5.3:4.4": {"pli": "Disvānassa etadahosi: ", "eng": "and thought, ", "html": "{}"}, "ud5.3:4.5": {"pli": "“ayaṁ kho idha bhabbo dhammaṁ viññātun”ti. ", "eng": "“He is capable of understanding the teaching.” ", "html": "{}"}, "ud5.3:4.6": {"pli": "Suppabuddhaṁ kuṭṭhiṁ ārabbha anupubbiṁ kathaṁ kathesi, seyyathidaṁ—", "eng": "He gave a step by step talk especially for Suppabuddha ", "html": "{}"}, "ud5.3:4.7": {"pli": "dānakathaṁ sīlakathaṁ saggakathaṁ; kāmānaṁ ādīnavaṁ okāraṁ saṅkilesaṁ; nekkhamme ānisaṁsaṁ pakāsesi. ", "eng": "on giving, ethical conduct, and heaven. He explained the drawbacks of sensual pleasures, so sordid and corrupt, and the benefit of renunciation. ", "html": "{}"}, "ud5.3:4.8": {"pli": "Yadā bhagavā aññāsi suppabuddhaṁ kuṭṭhiṁ kallacittaṁ muducittaṁ vinīvaraṇacittaṁ udaggacittaṁ pasannacittaṁ, atha yā buddhānaṁ sāmukkaṁsikā dhammadesanā taṁ pakāsesi—", "eng": "When the Buddha knew that Suppabuddha’s mind was ready, supple, without hindrances, elated, and confident, he revealed the teaching unique to the Buddhas: ", "html": "{}"}, "ud5.3:4.9": {"pli": "dukkhaṁ, samudayaṁ, nirodhaṁ, maggaṁ. ", "eng": "suffering, its origin, its cessation, and the path. ", "html": "{}"}, "ud5.3:4.10": {"pli": "Seyyathāpi nāma suddhaṁ vatthaṁ apagatakāḷakaṁ sammadeva rajanaṁ paṭiggaṇheyya; ", "eng": "Just as a clean cloth rid of stains would properly absorb dye, ", "html": "{}"}, "ud5.3:4.11": {"pli": "evamevaṁ suppabuddhassa kuṭṭhissa tasmiṁyeva āsane virajaṁ vītamalaṁ dhammacakkhuṁ udapādi: ", "eng": "in that very seat the stainless, immaculate vision of the Dhamma arose in Suppabuddha: ", "html": "{}"}, "ud5.3:4.12": {"pli": "“yaṁ kiñci samudayadhammaṁ sabbaṁ taṁ nirodhadhamman”ti. ", "eng": "“Everything that has a beginning has an end.” ", "html": "{}</p>"}, "ud5.3:5.1": {"pli": "Atha kho suppabuddho kuṭṭhī diṭṭhadhammo pattadhammo viditadhammo pariyogāḷhadhammo tiṇṇavicikiccho vigatakathaṅkatho vesārajjappatto aparappaccayo satthu sāsane uṭṭhāyāsanā yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. Ekamantaṁ nisinno kho suppabuddho kuṭṭhī bhagavantaṁ etadavoca: ", "eng": "Then Suppabuddha saw, attained, understood, and fathomed the Dhamma. He went beyond doubt, got rid of indecision, and became self-assured and independent of others regarding the Teacher’s instructions. He rose from his seat and went to the Buddha, bowed, sat down to one side, and said: ", "html": "<p>{}</p>"}, "ud5.3:6.1": {"pli": "“Abhikkantaṁ, bhante, abhikkantaṁ, bhante. ", "eng": "“Excellent, sir! Excellent! ", "html": "<p>{}"}, "ud5.3:6.2": {"pli": "Seyyathāpi, bhante, nikkujjitaṁ vā ukkujjeyya, paṭicchannaṁ vā vivareyya, mūḷhassa vā maggaṁ ācikkheyya, andhakāre vā telapajjotaṁ dhāreyya: ‘cakkhumanto rūpāni dakkhantī’ti; evamevaṁ bhagavatā anekapariyāyena dhammo pakāsito. ", "eng": "As if he were righting the overturned, or revealing the hidden, or pointing out the path to the lost, or lighting a lamp in the dark so people with clear eyes can see what’s there, the Buddha has made the teaching clear in many ways. ", "html": "{}"}, "ud5.3:6.3": {"pli": "Esāhaṁ, bhante, bhagavantaṁ saraṇaṁ gacchāmi dhammañca bhikkhusaṅghañca. ", "eng": "I go for refuge to the Buddha, to the teaching, and to the mendicant Saṅgha. ", "html": "{}"}, "ud5.3:6.4": {"pli": "Upāsakaṁ maṁ bhagavā dhāretu ajjatagge pāṇupetaṁ saraṇaṁ gatan”ti. ", "eng": "From this day forth, may the Buddha remember me as a lay follower who has gone for refuge for life.” ", "html": "{}</p>"}, "ud5.3:7.1": {"pli": "Atha kho suppabuddho kuṭṭhī bhagavatā dhammiyā kathāya sandassito samādapito samuttejito sampahaṁsito bhagavatā bhāsitaṁ abhinanditvā anumoditvā uṭṭhāyāsanā bhagavantaṁ abhivādetvā padakkhiṇaṁ katvā pakkāmi. ", "eng": "After Suppabuddha had been educated, encouraged, fired up, and inspired with a Dhamma talk by the Buddha, he got up from his seat, bowed, and respectfully circled the Buddha before leaving. ", "html": "<p>{}"}, "ud5.3:7.2": {"pli": "Atha kho acirapakkantaṁ suppabuddhaṁ kuṭṭhiṁ gāvī taruṇavacchā adhipatitvā jīvitā voropesi. ", "eng": "But soon after Suppabuddha had left, a cow with a baby calf charged at him and took his life. ", "html": "{}</p>"}, "ud5.3:8.1": {"pli": "Atha kho sambahulā bhikkhū yena bhagavā tenupasaṅkamiṁsu; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdiṁsu. ", "eng": "Then several mendicants went up to the Buddha, bowed, sat down to one side, and said, ", "html": "<p>{}"}, "ud5.3:8.2": {"pli": "Ekamantaṁ nisinnā kho te bhikkhū bhagavantaṁ etadavocuṁ: ", "html": "{}"}, "ud5.3:8.3": {"pli": "“yo so, bhante, suppabuddho nāma kuṭṭhī bhagavatā dhammiyā kathāya sandassito samādapito samuttejito sampahaṁsito, so kālaṅkato. ", "eng": "“The leper named Suppabuddha, after being educated, encouraged, fired up, and inspired with a Dhamma talk by the Buddha, has passed away. ", "html": "{}"}, "ud5.3:8.4": {"pli": "Tassa kā gati, ko abhisamparāyo”ti? ", "eng": "Where has he been reborn in his next life?” ", "html": "{}</p>"}, "ud5.3:9.1": {"pli": "“Paṇḍito, bhikkhave, suppabuddho kuṭṭhī; paccapādi dhammassānudhammaṁ; na ca maṁ dhammādhikaraṇaṁ vihesesi. ", "eng": "“Mendicants, Suppabuddha was astute. He practiced in line with the teaching, and did not trouble me about the teachings. ", "html": "<p>{}"}, "ud5.3:9.2": {"pli": "Suppabuddho, bhikkhave, kuṭṭhī tiṇṇaṁ saṁyojanānaṁ parikkhayā sotāpanno avinipātadhammo niyato sambodhiparāyaṇo”ti. ", "eng": "With the ending of three fetters, Suppabuddha is a stream-enterer, not liable to be reborn in the underworld, destined for awakening.” ", "html": "{}</p>"}, "ud5.3:10.1": {"pli": "Evaṁ vutte, aññataro bhikkhu bhagavantaṁ etadavoca: ", "eng": "When he said this, one of the mendicants said to the Buddha, ", "html": "<p>{}"}, "ud5.3:10.2": {"pli": "“ko nu kho, bhante, hetu, ko paccayo yena suppabuddho kuṭṭhī ahosi—", "eng": "“What is the cause, sir, what is the reason why Suppabuddha became a leper, ", "html": "{}"}, "ud5.3:10.3": {"pli": "manussadaliddo, manussakapaṇo, manussavarāko”ti? ", "eng": "a poor person, destitute, and pitiful?” ", "html": "{}</p>"}, "ud5.3:11.1": {"pli": "“Bhūtapubbaṁ, bhikkhave, suppabuddho kuṭṭhī imasmiṁyeva rājagahe seṭṭhiputto ahosi. ", "eng": "“Once upon a time, mendicants, Suppabuddha used to be a financier’s son right here in Rājagaha. ", "html": "<p>{}"}, "ud5.3:11.2": {"pli": "So uyyānabhūmiṁ niyyanto addasa tagarasikhiṁ paccekabuddhaṁ nagaraṁ piṇḍāya pavisantaṁ. ", "eng": "On his way to visit a park, he saw Tagarasikhī, an Independent Buddha, entering the city for alms. ", "html": "{}"}, "ud5.3:11.3": {"pli": "Disvānassa etadahosi: ", "eng": "When he saw this he thought, ", "html": "{}"}, "ud5.3:11.4": {"pli": "‘kvāyaṁ kuṭṭhī kuṭṭhicīvarena vicaratī’ti? ", "eng": "“Who is this leper wandering about with his leper’s robe?” ", "html": "{}"}, "ud5.3:11.5": {"pli": "Niṭṭhubhitvā apasabyato karitvā pakkāmi. ", "eng": "Before leaving, he spat on the ground and turned his left side to Tagarasikhī. ", "html": "{}", "comm": "At Taittirīya Brāhmaṇa 3.2.9b, a titan renders the sacrificial ground impure by spitting on it. "}, "ud5.3:11.6": {"pli": "So tassa kammassa vipākena bahūni vassasatāni bahūni vassasahassāni bahūni vassasatasahassāni niraye paccittha. ", "eng": "As a result of that deed he burned in hell for many years, for many hundreds, many thousands, many hundreds of thousands of years. ", "html": "{}"}, "ud5.3:11.7": {"pli": "Tasseva kammassa vipākāvasesena imasmiṁyeva rājagahe kuṭṭhī ahosi manussadaliddo, manussakapaṇo, manussavarāko. ", "eng": "And as a residual result of that same deed, he became a leper right here in Rājagaha, a poor person, destitute and pitiful. ", "html": "{}"}, "ud5.3:11.8": {"pli": "So tathāgatappaveditaṁ dhammavinayaṁ āgamma saddhaṁ samādiyi sīlaṁ samādiyi sutaṁ samādiyi cāgaṁ samādiyi paññaṁ samādiyi. ", "eng": "But owing to faith in the teaching and training proclaimed by the Realized One, he undertook ethics, learning, generosity, and wisdom. ", "html": "{}"}, "ud5.3:11.9": {"pli": "So tathāgatappaveditaṁ dhammavinayaṁ āgamma saddhaṁ samādiyitvā sīlaṁ samādiyitvā sutaṁ samādiyitvā cāgaṁ samādiyitvā paññaṁ samādiyitvā kāyassa bhedā paraṁ maraṇā sugatiṁ saggaṁ lokaṁ upapanno devānaṁ tāvatiṁsānaṁ sahabyataṁ. ", "eng": "After undertaking these things, when his body broke up, after death, he was reborn in a good place, a heavenly realm, in the company of the gods of the thirty-three. ", "html": "{}"}, "ud5.3:11.10": {"pli": "So tattha aññe deve atirocati vaṇṇena ceva yasasā cā”ti. ", "eng": "There he outshines the other gods in beauty and glory.” ", "html": "{}</p>"}, "ud5.3:12.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud5.3:13.1": {"pli": "“Cakkhumā visamānīva, ", "eng": "“As a clear-eyed man would avoid rough paths, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud5.3:13.2": {"pli": "vijjamāne parakkame; ", "eng": "so long as strength is found; ", "html": "<span class='verse-line'>{}</span>"}, "ud5.3:13.3": {"pli": "Paṇḍito jīvalokasmiṁ, ", "eng": "an astute person in the living world, ", "html": "<span class='verse-line'>{}</span>"}, "ud5.3:13.4": {"pli": "pāpāni parivajjaye”ti. ", "eng": "would shun bad deeds.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud5.3:13.5": {"pli": "Tatiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud5.4": {"ud5.4:0.1": {"pli": "Udāna 5.4 ", "eng": "Heartfelt Sayings 5.4 ", "html": "<article id='ud5.4'><header><ul><li class='division'>{}</li></ul>"}, "ud5.4:0.2": {"pli": "Kumārakasutta ", "eng": "The Boys ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud5.4:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud5.4:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud5.4:1.3": {"pli": "Tena kho pana samayena sambahulā kumārakā antarā ca sāvatthiṁ antarā ca jetavanaṁ macchake bādhenti. ", "eng": "Now at that time, between Sāvatthī and the Jeta Grove, several boys were tormenting some fish. ", "html": "{}</p>"}, "ud5.4:2.1": {"pli": "Atha kho bhagavā pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya sāvatthiṁ piṇḍāya pāvisi. ", "eng": "Then the Buddha robed up in the morning and, taking his bowl and robe, entered Sāvatthī for alms. ", "html": "<p>{}"}, "ud5.4:2.2": {"pli": "Addasā kho bhagavā te sambahule kumārake antarā ca sāvatthiṁ antarā ca jetavanaṁ macchake bādhente. ", "eng": "He saw the boys tormenting the fish. ", "html": "{}"}, "ud5.4:2.3": {"pli": "Disvāna yena te kumārakā tenupasaṅkami; upasaṅkamitvā te kumārake etadavoca: ", "eng": "He went to them and said, ", "html": "{}"}, "ud5.4:2.4": {"pli": "“bhāyatha vo, tumhe kumārakā, dukkhassa, appiyaṁ vo dukkhan”ti? ", "eng": "“Boys, do you fear pain? Do you dislike pain?” ", "html": "{}"}, "ud5.4:2.5": {"pli": "“Evaṁ, bhante, bhāyāma mayaṁ, bhante, dukkhassa, appiyaṁ no dukkhan”ti. ", "eng": "“Yes, sir,” they replied. “We dislike pain.” ", "html": "{}</p>"}, "ud5.4:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud5.4:4.1": {"pli": "“Sace bhāyatha dukkhassa, ", "eng": "“If you fear pain, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud5.4:4.2": {"pli": "sace vo dukkhamappiyaṁ; ", "eng": "if you dislike pain, ", "html": "<span class='verse-line'>{}</span>"}, "ud5.4:4.3": {"pli": "Mākattha pāpakaṁ kammaṁ, ", "eng": "don’t do bad deeds ", "html": "<span class='verse-line'>{}</span>"}, "ud5.4:4.4": {"pli": "āvi vā yadi vā raho. ", "eng": "either openly or in secret. ", "html": "<span class='verse-line'>{}</span></p>"}, "ud5.4:5.1": {"pli": "Sace ca pāpakaṁ kammaṁ, ", "eng": "If you should do a bad deed, ", "html": "<p><span class='verse-line'>{}</span>"}, "ud5.4:5.2": {"pli": "karissatha karotha vā; ", "eng": "now or in the future, ", "html": "<span class='verse-line'>{}</span>"}, "ud5.4:5.3": {"pli": "Na vo dukkhā pamutyatthi, ", "eng": "you won’t be freed from suffering, ", "html": "<span class='verse-line'>{}</span>"}, "ud5.4:5.4": {"pli": "upeccapi palāyatan”ti. ", "eng": "though you fly away and flee.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud5.4:5.5": {"pli": "Catutthaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud5.5": {"ud5.5:0.1": {"pli": "Udāna 5.5 ", "eng": "Heartfelt Sayings 5.5 ", "html": "<article id='ud5.5'><header><ul><li class='division'>{}</li></ul>"}, "ud5.5:0.2": {"pli": "Uposathasutta ", "eng": "Sabbath ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud5.5:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud5.5:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati pubbārāme migāramātupāsāde. ", "eng": "At one time the Buddha was staying near Sāvatthī in the stilt longhouse of Migāra’s mother in the Eastern Monastery. ", "html": "{}"}, "ud5.5:1.3": {"pli": "Tena kho pana samayena bhagavā tadahuposathe bhikkhusaṅghaparivuto nisinno hoti. ", "eng": "Now, at that time it was the sabbath, and the Buddha was sitting surrounded by the Saṅgha of monks. ", "html": "{}</p>"}, "ud5.5:2.1": {"pli": "Atha kho āyasmā ānando abhikkantāya rattiyā, nikkhante paṭhame yāme, uṭṭhāyāsanā ekaṁsaṁ uttarāsaṅgaṁ karitvā yena bhagavā tenañjaliṁ paṇāmetvā bhagavantaṁ etadavoca: ", "eng": "And then, as the night was getting late, in the first watch of the night, Venerable Ānanda got up from his seat, arranged his robe over one shoulder, raised his joined palms toward the Buddha and said, ", "html": "<p>{}"}, "ud5.5:2.2": {"pli": "“abhikkantā, bhante, ratti; nikkhanto paṭhamo yāmo; ciranisinno bhikkhusaṅgho; ", "eng": "“Sir, the night is getting late. It is the first watch of the night, and the Saṅgha of monks has been sitting long. ", "html": "{}"}, "ud5.5:2.3": {"pli": "uddisatu, bhante, bhagavā bhikkhūnaṁ pātimokkhan”ti. ", "eng": "Please, sir, may the Buddha recite the monastic code to the monks.” ", "html": "{}"}, "ud5.5:2.4": {"pli": "Evaṁ vutte, bhagavā tuṇhī ahosi. ", "eng": "But when he said this, the Buddha kept silent. ", "html": "{}</p>"}, "ud5.5:3.1": {"pli": "Dutiyampi kho āyasmā ānando abhikkantāya rattiyā, nikkhante majjhime yāme, uṭṭhāyāsanā ekaṁsaṁ uttarāsaṅgaṁ karitvā yena bhagavā tenañjaliṁ paṇāmetvā bhagavantaṁ etadavoca: ", "eng": "For a second time, as the night was getting late, in the middle watch of the night, Ānanda got up from his seat, arranged his robe over one shoulder, raised his joined palms toward the Buddha and said, ", "html": "<p>{}"}, "ud5.5:3.2": {"pli": "“abhikkantā, bhante, ratti; nikkhanto majjhimo yāmo; ciranisinno bhikkhusaṅgho; ", "eng": "“Sir, the night is getting late. It is the middle watch of the night, and the Saṅgha of monks has been sitting long. ", "html": "{}"}, "ud5.5:3.3": {"pli": "uddisatu, bhante, bhagavā bhikkhūnaṁ pātimokkhan”ti. ", "eng": "Please, sir, may the Buddha recite the monastic code to the monks.” ", "html": "{}"}, "ud5.5:3.4": {"pli": "Dutiyampi kho bhagavā tuṇhī ahosi. ", "eng": "But for a second time the Buddha kept silent. ", "html": "{}</p>"}, "ud5.5:4.1": {"pli": "Tatiyampi kho āyasmā ānando abhikkantāya rattiyā, nikkhante pacchime yāme, uddhaste aruṇe, nandimukhiyā rattiyā uṭṭhāyāsanā ekaṁsaṁ uttarāsaṅgaṁ karitvā yena bhagavā tenañjaliṁ paṇāmetvā bhagavantaṁ etadavoca: ", "eng": "For a third time, as the night was getting late, in the last watch of the night, as dawn stirred, bringing joy to the night, Ānanda got up from his seat, arranged his robe over one shoulder, raised his joined palms toward the Buddha and said, ", "html": "<p>{}"}, "ud5.5:4.2": {"pli": "“abhikkantā, bhante, ratti; nikkhanto pacchimo yāmo; uddhasto aruṇo; nandimukhī ratti; ciranisinno bhikkhusaṅgho; ", "eng": "“Sir, the night is getting late. It is the last watch of the night and dawn stirs, bringing joy to the night. ", "html": "{}"}, "ud5.5:4.3": {"pli": "uddisatu, bhante, bhagavā bhikkhūnaṁ pātimokkhan”ti. ", "eng": "Please, sir, may the Buddha recite the monastic code to the monks.” ", "html": "{}"}, "ud5.5:4.4": {"pli": "“Aparisuddhā, ānanda, parisā”ti. ", "eng": "“Ānanda, the assembly is not pure.” ", "html": "{}</p>"}, "ud5.5:5.1": {"pli": "Atha kho āyasmato mahāmoggallānassa etadahosi: ", "eng": "Then Venerable Mahāmoggallāna thought, ", "html": "<p>{}"}, "ud5.5:5.2": {"pli": "“kaṁ nu kho bhagavā puggalaṁ sandhāya evamāha: ", "eng": "“Who is the Buddha talking about?” ", "html": "{}"}, "ud5.5:5.3": {"pli": "‘aparisuddhā, ānanda, parisā’”ti? ", "html": "{}"}, "ud5.5:5.4": {"pli": "Atha kho āyasmā mahāmoggallāno sabbāvantaṁ bhikkhusaṅghaṁ cetasā ceto paricca manasākāsi. ", "eng": "Then he focused on encompassing the minds of everyone in the Saṅgha. ", "html": "{}"}, "ud5.5:5.5": {"pli": "Addasā kho āyasmā mahāmoggallāno taṁ puggalaṁ dussīlaṁ pāpadhammaṁ asuciṁ saṅkassarasamācāraṁ paṭicchannakammantaṁ assamaṇaṁ samaṇapaṭiññaṁ abrahmacāriṁ brahmacāripaṭiññaṁ antopūtiṁ avassutaṁ kasambujātaṁ majjhe bhikkhusaṅghassa nisinnaṁ. ", "eng": "He saw that unethical individual, of bad qualities, filthy, with suspicious behavior, underhand, no true ascetic or spiritual practitioner—though claiming to be one—rotten inside, festering, and depraved, sitting in the middle of the Saṅgha. ", "html": "{}"}, "ud5.5:5.6": {"pli": "Disvāna uṭṭhāyāsanā yena so puggalo tenupasaṅkami; upasaṅkamitvā taṁ puggalaṁ etadavoca: ", "eng": "When he saw him he got up from his seat, went up to him and said, ", "html": "{}"}, "ud5.5:5.7": {"pli": "“uṭṭhehi, āvuso, diṭṭhosi bhagavatā; ", "eng": "“Get up, reverend. The Buddha has seen you. ", "html": "{}"}, "ud5.5:5.8": {"pli": "natthi te bhikkhūhi saddhiṁ saṁvāso”ti. ", "eng": "You can’t live in communion with the monks.” ", "html": "{}"}, "ud5.5:5.9": {"pli": "Evaṁ vutte, so puggalo tuṇhī ahosi. ", "eng": "But when he said this, that individual kept silent. ", "html": "{}</p>"}, "ud5.5:6.1": {"pli": "Dutiyampi kho āyasmā mahāmoggallāno taṁ puggalaṁ etadavoca: ", "eng": "For a second time and a third time, ", "html": "<p>{}"}, "ud5.5:6.2": {"pli": "“uṭṭhehi, āvuso, diṭṭhosi bhagavatā; ", "eng": "he asked that monk to leave. ", "html": "{}"}, "ud5.5:6.3": {"pli": "natthi te bhikkhūhi saddhiṁ saṁvāso”ti. ", "html": "{}"}, "ud5.5:6.4": {"pli": "Dutiyampi kho …pe… ", "html": "{}"}, "ud5.5:6.5": {"pli": "tatiyampi kho so puggalo tuṇhī ahosi. ", "eng": "But for a third time that individual kept silent. ", "html": "{}</p>"}, "ud5.5:7.1": {"pli": "Atha kho āyasmā mahāmoggallāno taṁ puggalaṁ bāhāyaṁ gahetvā bahidvārakoṭṭhakā nikkhāmetvā sūcighaṭikaṁ datvā yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ etadavoca: ", "eng": "Then Venerable Mahāmoggallāna took that individual by the arm, ejected him out the gate, and bolted the door. Then he went up to the Buddha, and said to him, ", "html": "<p>{}"}, "ud5.5:7.2": {"pli": "“nikkhāmito, bhante, so puggalo mayā. ", "eng": "“I have ejected that individual. ", "html": "{}"}, "ud5.5:7.3": {"pli": "Parisuddhā parisā. ", "eng": "The assembly is pure. ", "html": "{}"}, "ud5.5:7.4": {"pli": "Uddisatu, bhante, bhagavā bhikkhūnaṁ pātimokkhan”ti. ", "eng": "Please, sir, may the Buddha recite the monastic code to the monks.” ", "html": "{}"}, "ud5.5:7.5": {"pli": "“Acchariyaṁ, moggallāna, abbhutaṁ, moggallāna. ", "eng": "“It’s incredible, Moggallāna, it’s amazing, ", "html": "{}"}, "ud5.5:7.6": {"pli": "Yāva bāhāgahaṇāpi nāma so moghapuriso āgamessatī”ti. ", "eng": "how that futile man waited to be taken by the arm!” ", "html": "{}</p>"}, "ud5.5:8.1": {"pli": "Atha kho bhagavā bhikkhū āmantesi: ", "eng": "Then the Buddha said to the monks, ", "html": "<p>{}"}, "ud5.5:8.2": {"pli": "“na dānāhaṁ, bhikkhave, ito paraṁ uposathaṁ karissāmi, pātimokkhaṁ uddisissāmi. ", "eng": "“From this day forth, monks, I will not perform the sabbath or recite the monastic code. ", "html": "{}"}, "ud5.5:8.3": {"pli": "Tumheva dāni, bhikkhave, ito paraṁ uposathaṁ kareyyātha, pātimokkhaṁ uddiseyyātha. ", "eng": "Now you should perform the sabbath and recite the monastic code. ", "html": "{}"}, "ud5.5:8.4": {"pli": "Aṭṭhānametaṁ, bhikkhave, anavakāso yaṁ tathāgato aparisuddhāya parisāya uposathaṁ kareyya, pātimokkhaṁ uddiseyya. ", "eng": "It’s impossible, monks, it can’t happen that a Realized One could recite the monastic code in an impure assembly. ", "html": "{}</p>"}, "ud5.5:9.1": {"pli": "Aṭṭhime, bhikkhave, mahāsamudde acchariyā abbhutā dhammā, ye disvā disvā asurā mahāsamudde abhiramanti. ", "eng": "Seeing these eight incredible and amazing things the titans love the ocean. ", "html": "<p>{}"}, "ud5.5:9.2": {"pli": "Katame aṭṭha? ", "eng": "What eight? ", "html": "{}</p>"}, "ud5.5:10.1": {"pli": "Mahāsamuddo, bhikkhave, anupubbaninno anupubbapoṇo anupubbapabbhāro, na āyatakeneva papāto. ", "eng": "The ocean gradually slants, slopes, and inclines, with no abrupt precipice. ", "html": "<p>{}"}, "ud5.5:10.2": {"pli": "Yampi, bhikkhave, mahāsamuddo anupubbaninno anupubbapoṇo anupubbapabbhāro na āyatakeneva papāto; ", "html": "{}"}, "ud5.5:10.3": {"pli": "ayaṁ, bhikkhave, mahāsamudde paṭhamo acchariyo abbhuto dhammo, yaṁ disvā disvā asurā mahāsamudde abhiramanti. ", "eng": "This is the first thing the titans love about the ocean. ", "html": "{}</p>"}, "ud5.5:11.1": {"pli": "Puna caparaṁ, bhikkhave, mahāsamuddo ṭhitadhammo velaṁ nātivattati. ", "eng": "Furthermore, the ocean is consistent and doesn’t overflow its boundaries. ", "html": "<p>{}"}, "ud5.5:11.2": {"pli": "Yampi, bhikkhave, mahāsamuddo ṭhitadhammo velaṁ nātivattati; ", "html": "{}"}, "ud5.5:11.3": {"pli": "ayaṁ, bhikkhave, mahāsamudde dutiyo acchariyo abbhuto dhammo, yaṁ disvā disvā asurā mahāsamudde abhiramanti. ", "eng": "This is the second thing the titans love about the ocean. ", "html": "{}</p>"}, "ud5.5:12.1": {"pli": "Puna caparaṁ, bhikkhave, mahāsamuddo na matena kuṇapena saṁvasati. Yaṁ hoti mahāsamudde mataṁ kuṇapaṁ taṁ khippameva tīraṁ vāheti, thalaṁ ussāreti. ", "eng": "Furthermore, the ocean doesn’t accommodate a carcass, but quickly carries it to the shore and strands it on the beach. ", "html": "<p>{}"}, "ud5.5:12.2": {"pli": "Yampi, bhikkhave, mahāsamuddo na matena kuṇapena saṁvasati, yaṁ hoti mahāsamudde mataṁ kuṇapaṁ taṁ khippameva tīraṁ vāheti thalaṁ ussāreti; ", "html": "{}"}, "ud5.5:12.3": {"pli": "ayaṁ, bhikkhave, mahāsamudde tatiyo acchariyo abbhuto dhammo, yaṁ disvā disvā asurā mahāsamudde abhiramanti. ", "eng": "This is the third thing the titans love about the ocean. ", "html": "{}</p>"}, "ud5.5:13.1": {"pli": "Puna caparaṁ, bhikkhave, yā kāci mahānadiyo, seyyathidaṁ—gaṅgā yamunā aciravatī sarabhū mahī, tā mahāsamuddaṁ patvā jahanti purimāni nāmagottāni; ‘mahāsamuddo’tveva saṅkhaṁ gacchanti. ", "eng": "Furthermore, when they reach the ocean, all the great rivers—that is, the Ganges, Yamunā, Aciravatī, Sarabhū, and Mahī—lose their names and clans and are simply reckoned as ‘the ocean’. ", "html": "<p>{}"}, "ud5.5:13.2": {"pli": "Yampi, bhikkhave, yā kāci mahānadiyo, seyyathidaṁ—gaṅgā yamunā aciravatī sarabhū mahī tā mahāsamuddaṁ patvā jahanti purimāni nāmagottāni, ‘mahāsamuddo’tveva saṅkhaṁ gacchanti; ", "html": "{}"}, "ud5.5:13.3": {"pli": "ayaṁ, bhikkhave, mahāsamudde catuttho acchariyo abbhuto dhammo, yaṁ disvā disvā asurā mahāsamudde abhiramanti. ", "eng": "This is the fourth thing the titans love about the ocean. ", "html": "{}</p>"}, "ud5.5:14.1": {"pli": "Puna caparaṁ, bhikkhave, yā ca loke savantiyo mahāsamuddaṁ appenti, yā ca antalikkhā dhārā papatanti, na tena mahāsamuddassa ūnattaṁ vā pūrattaṁ vā paññāyati. ", "eng": "Furthermore, for all the world’s streams that reach it, and the showers that fall from the sky, the ocean never empties or fills up. ", "html": "<p>{}"}, "ud5.5:14.2": {"pli": "Yampi, bhikkhave, yā ca loke savantiyo mahāsamuddaṁ appenti, yā ca antalikkhā dhārā papatanti, na tena mahāsamuddassa ūnattaṁ vā pūrattaṁ vā paññāyati; ", "html": "{}"}, "ud5.5:14.3": {"pli": "ayaṁ, bhikkhave, mahāsamudde pañcamo acchariyo abbhuto dhammo, yaṁ disvā disvā asurā mahāsamudde abhiramanti. ", "eng": "This is the fifth thing the titans love about the ocean. ", "html": "{}</p>"}, "ud5.5:15.1": {"pli": "Puna caparaṁ, bhikkhave, mahāsamuddo ekaraso loṇaraso. ", "eng": "Furthermore, the ocean has just one taste, the taste of salt. ", "html": "<p>{}"}, "ud5.5:15.2": {"pli": "Yampi, bhikkhave, mahāsamuddo ekaraso loṇaraso; ", "html": "{}"}, "ud5.5:15.3": {"pli": "ayaṁ, bhikkhave, mahāsamudde chaṭṭho acchariyo abbhuto dhammo, yaṁ disvā disvā asurā mahāsamudde abhiramanti. ", "eng": "This is the sixth thing the titans love about the ocean. ", "html": "{}</p>"}, "ud5.5:16.1": {"pli": "Puna caparaṁ, bhikkhave, mahāsamuddo bahuratano anekaratano. Tatrimāni ratanāni, seyyathidaṁ—muttā maṇi veḷuriyo saṅkho silā pavāḷaṁ rajataṁ jātarūpaṁ lohitaṅgo masāragallaṁ. ", "eng": "Furthermore, the ocean is full of many kinds of treasures, such as pearls, gems, beryl, conch, quartz, coral, silver, native gold, rubies, and emeralds. ", "html": "<p>{}"}, "ud5.5:16.2": {"pli": "Yampi, bhikkhave, mahāsamuddo bahuratano anekaratano, tatrimāni ratanāni, seyyathidaṁ—muttā maṇi veḷuriyo saṅkho silā pavāḷaṁ rajataṁ jātarūpaṁ lohitaṅgo masāragallaṁ; ", "html": "{}"}, "ud5.5:16.3": {"pli": "ayaṁ, bhikkhave, mahāsamudde sattamo acchariyo abbhuto dhammo, yaṁ disvā disvā asurā mahāsamudde abhiramanti. ", "eng": "This is the seventh thing the titans love about the ocean. ", "html": "{}</p>"}, "ud5.5:17.1": {"pli": "Puna caparaṁ, bhikkhave, mahāsamuddo mahataṁ bhūtānaṁ āvāso. Tatrime bhūtā—timi timiṅgalo timitimiṅgalo asurā nāgā gandhabbā. Santi mahāsamudde yojanasatikāpi attabhāvā, dviyojanasatikāpi attabhāvā, tiyojanasatikāpi attabhāvā, catuyojanasatikāpi attabhāvā, pañcayojanasatikāpi attabhāvā. ", "eng": "Furthermore, many great beings live in the ocean, such as leviathans, leviathan-gulpers, leviathan-gulper-gulpers, titans, dragons, and centaurs. In the ocean there are life-forms a hundred leagues long, or even two hundred, three hundred, four hundred, or five hundred leagues long. ", "html": "<p>{}"}, "ud5.5:17.2": {"pli": "Yampi, bhikkhave, mahāsamuddo mahataṁ bhūtānaṁ āvāso, tatrime bhūtā—timi timiṅgalo timitimiṅgalo asurā nāgā gandhabbā, santi mahāsamudde yojanasatikāpi attabhāvā dviyojanasatikāpi attabhāvā …pe… pañcayojanasatikāpi attabhāvā; ", "html": "{}"}, "ud5.5:17.3": {"pli": "ayaṁ, bhikkhave, mahāsamudde aṭṭhamo acchariyo abbhuto dhammo, yaṁ disvā disvā asurā mahāsamudde abhiramanti. ", "eng": "This is the eighth thing the titans love about the ocean. ", "html": "{}"}, "ud5.5:17.4": {"pli": "Ime kho, bhikkhave, aṭṭha mahāsamudde acchariyā abbhutā dhammā ye disvā disvā asurā mahāsamudde abhiramanti. ", "eng": "Seeing these eight incredible and amazing things the titans love the ocean. ", "html": "{}</p>"}, "ud5.5:18.1": {"pli": "Evamevaṁ kho, bhikkhave, imasmiṁ dhammavinaye aṭṭha acchariyā abbhutā dhammā, ye disvā disvā bhikkhū imasmiṁ dhammavinaye abhiramanti. ", "eng": "In the same way, seeing eight incredible and amazing things, mendicants, the mendicants love this teaching and training. ", "html": "<p>{}"}, "ud5.5:18.2": {"pli": "Katame aṭṭha? ", "eng": "What eight? ", "html": "{}</p>"}, "ud5.5:19.1": {"pli": "Seyyathāpi, bhikkhave, mahāsamuddo anupubbaninno anupubbapoṇo anupubbapabbhāro, na āyatakeneva papāto; ", "eng": "The ocean gradually slants, slopes, and inclines, with no abrupt precipice. ", "html": "<p>{}"}, "ud5.5:19.2": {"pli": "evamevaṁ kho, bhikkhave, imasmiṁ dhammavinaye anupubbasikkhā anupubbakiriyā anupubbapaṭipadā, na āyatakeneva aññāpaṭivedho. ", "eng": "In the same way in this teaching and training the penetration to enlightenment comes from gradual training, progress, and practice, not abruptly. ", "html": "{}"}, "ud5.5:19.3": {"pli": "Yampi, bhikkhave, imasmiṁ dhammavinaye anupubbasikkhā anupubbakiriyā anupubbapaṭipadā, na āyatakeneva aññāpaṭivedho; ", "html": "{}"}, "ud5.5:19.4": {"pli": "ayaṁ, bhikkhave, imasmiṁ dhammavinaye paṭhamo acchariyo abbhuto dhammo, yaṁ disvā disvā bhikkhū imasmiṁ dhammavinaye abhiramanti. ", "eng": "This is the first thing the mendicants love about this teaching and training. ", "html": "{}</p>"}, "ud5.5:20.1": {"pli": "Seyyathāpi, bhikkhave, mahāsamuddo ṭhitadhammo velaṁ nātivattati; ", "eng": "The ocean is consistent and doesn’t overflow its boundaries. ", "html": "<p>{}"}, "ud5.5:20.2": {"pli": "evamevaṁ kho, bhikkhave, yaṁ mayā sāvakānaṁ sikkhāpadaṁ paññattaṁ taṁ mama sāvakā jīvitahetupi nātikkamanti. ", "eng": "In the same way, when a training rule is laid down for my disciples they wouldn’t break it even for the sake of their own life. ", "html": "{}"}, "ud5.5:20.3": {"pli": "Yampi, bhikkhave, mayā sāvakānaṁ sikkhāpadaṁ paññattaṁ taṁ mama sāvakā jīvitahetupi nātikkamanti; ", "html": "{}"}, "ud5.5:20.4": {"pli": "ayaṁ, bhikkhave, imasmiṁ dhammavinaye dutiyo acchariyo abbhuto dhammo, yaṁ disvā disvā bhikkhū imasmiṁ dhammavinaye abhiramanti. ", "eng": "This is the second thing the mendicants love about this teaching and training. ", "html": "{}</p>"}, "ud5.5:21.1": {"pli": "Seyyathāpi, bhikkhave, mahāsamuddo na matena kuṇapena saṁvasati; yaṁ hoti mahāsamudde mataṁ kuṇapaṁ taṁ khippameva tīraṁ vāheti, thalaṁ ussāreti; ", "eng": "The ocean doesn’t accommodate a carcass, but quickly carries it to the shore and strands it on the beach. ", "html": "<p>{}"}, "ud5.5:21.2": {"pli": "evamevaṁ kho, bhikkhave, yo so puggalo dussīlo pāpadhammo asuci saṅkassarasamācāro paṭicchannakammanto assamaṇo samaṇapaṭiñño abrahmacārī brahmacāripaṭiñño antopūti avassuto kasambujāto, na tena saṅgho saṁvasati; atha kho naṁ khippameva sannipatitvā ukkhipati. Kiñcāpi so hoti majjhe bhikkhusaṅghassa nisinno, atha kho so ārakāva saṅghamhā, saṅgho ca tena. ", "eng": "In the same way, the Saṅgha doesn’t accommodate an individual who is unethical, of bad qualities, filthy, with suspicious behavior, underhand, no true ascetic or spiritual practitioner—though claiming to be one—rotten inside, festering, and depraved. But they quickly gather and expel them. Even if such an individual is sitting in the middle of the Saṅgha, they’re far from the Saṅgha, and the Saṅgha is far from them. ", "html": "{}"}, "ud5.5:21.3": {"pli": "Yampi, bhikkhave, yo so puggalo dussīlo pāpadhammo asuci saṅkassarasamācāro paṭicchannakammanto assamaṇo samaṇapaṭiñño abrahmacārī brahmacāripaṭiñño antopūti avassuto kasambujāto, na tena saṅgho saṁvasati; khippameva naṁ sannipatitvā ukkhipati. Kiñcāpi so hoti majjhe bhikkhusaṅghassa nisinno, atha kho so ārakāva saṅghamhā, saṅgho ca tena; ", "html": "{}"}, "ud5.5:21.4": {"pli": "ayaṁ, bhikkhave, imasmiṁ dhammavinaye tatiyo acchariyo abbhuto dhammo, yaṁ disvā disvā bhikkhū imasmiṁ dhammavinaye abhiramanti. ", "eng": "This is the third thing the mendicants love about this teaching and training. ", "html": "{}</p>"}, "ud5.5:22.1": {"pli": "Seyyathāpi, bhikkhave, yā kāci mahānadiyo, seyyathidaṁ—gaṅgā yamunā aciravatī sarabhū mahī tā mahāsamuddaṁ patvā jahanti purimāni nāmagottāni, ‘mahāsamuddo’tveva saṅkhaṁ gacchanti; ", "eng": "Furthermore, when they reach the ocean, all the great rivers—that is, the Ganges, Yamunā, Aciravatī, Sarabhū, and Mahī—lose their names and clans and are simply reckoned as ‘the ocean’. ", "html": "<p>{}"}, "ud5.5:22.2": {"pli": "evamevaṁ kho, bhikkhave, cattāro vaṇṇā—khattiyā, brāhmaṇā, vessā, suddā te tathāgatappavedite dhammavinaye agārasmā anagāriyaṁ pabbajitvā jahanti purimāni nāmagottāni, ‘samaṇā sakyaputtiyā’tveva saṅkhaṁ gacchanti. ", "eng": "In the same way, when they go forth from the lay life to homelessness, all four classes—aristocrats, brahmins, peasants, and menials—lose their former names and clans and are simply reckoned as ‘ascetics who follow the Sakyan’. ", "html": "{}"}, "ud5.5:22.3": {"pli": "Yampi, bhikkhave, cattāro vaṇṇā—khattiyā, brāhmaṇā, vessā, suddā te tathāgatappavedite dhammavinaye agārasmā anagāriyaṁ pabbajitvā jahanti purimāni nāmagottāni, ‘samaṇā sakyaputtiyā’tveva saṅkhaṁ gacchanti; ", "html": "{}"}, "ud5.5:22.4": {"pli": "ayaṁ, bhikkhave, imasmiṁ dhammavinaye catuttho acchariyo abbhuto dhammo, yaṁ disvā disvā bhikkhū imasmiṁ dhammavinaye abhiramanti. ", "eng": "This is the fourth thing the mendicants love about this teaching and training. ", "html": "{}</p>"}, "ud5.5:23.1": {"pli": "Seyyathāpi, bhikkhave, yā ca loke savantiyo mahāsamuddaṁ appenti, yā ca antalikkhā dhārā papatanti, na tena mahāsamuddassa ūnattaṁ vā pūrattaṁ vā paññāyati; ", "eng": "For all the world’s streams that reach it, and the showers that fall from the sky, the ocean never empties or fills up. ", "html": "<p>{}"}, "ud5.5:23.2": {"pli": "evamevaṁ kho, bhikkhave, bahū cepi bhikkhū anupādisesāya nibbānadhātuyā parinibbāyanti, na tena nibbānadhātuyā ūnattaṁ vā pūrattaṁ vā paññāyati. ", "eng": "In the same way, though several mendicants become fully extinguished in the element of extinguishment with no residue, the element of extinguishment never empties or fills up. ", "html": "{}"}, "ud5.5:23.3": {"pli": "Yampi, bhikkhave, bahū cepi bhikkhū anupādisesāya nibbānadhātuyā parinibbāyanti, na tena nibbānadhātuyā ūnattaṁ vā pūrattaṁ vā paññāyati; ", "html": "{}"}, "ud5.5:23.4": {"pli": "ayaṁ, bhikkhave, imasmiṁ dhammavinaye pañcamo acchariyo abbhuto dhammo, yaṁ disvā disvā bhikkhū imasmiṁ dhammavinaye abhiramanti. ", "eng": "This is the fifth thing the mendicants love about this teaching and training. ", "html": "{}</p>"}, "ud5.5:24.1": {"pli": "Seyyathāpi, bhikkhave, mahāsamuddo ekaraso loṇaraso; ", "eng": "The ocean has just one taste, the taste of salt. ", "html": "<p>{}"}, "ud5.5:24.2": {"pli": "evamevaṁ kho, bhikkhave, ayaṁ dhammavinayo ekaraso vimuttiraso. ", "eng": "In the same way, this teaching and training has one taste, the taste of freedom. ", "html": "{}"}, "ud5.5:24.3": {"pli": "Yampi, bhikkhave, ayaṁ dhammavinayo ekaraso vimuttiraso; ", "html": "{}"}, "ud5.5:24.4": {"pli": "ayaṁ, bhikkhave, imasmiṁ dhammavinaye chaṭṭho acchariyo abbhuto dhammo, yaṁ disvā disvā bhikkhū imasmiṁ dhammavinaye abhiramanti. ", "eng": "This is the sixth thing the mendicants love about this teaching and training. ", "html": "{}</p>"}, "ud5.5:25.1": {"pli": "Seyyathāpi, bhikkhave, mahāsamuddo bahuratano anekaratano, tatrimāni ratanāni, seyyathidaṁ—muttā maṇi veḷuriyo saṅkho silā pavāḷaṁ rajataṁ jātarūpaṁ lohitaṅgo masāragallaṁ; ", "eng": "The ocean is full of many kinds of treasures, such as pearls, gems, beryl, conch, quartz, coral, silver, native gold, rubies, and emeralds. ", "html": "<p>{}"}, "ud5.5:25.2": {"pli": "evamevaṁ kho, bhikkhave, ayaṁ dhammavinayo bahuratano anekaratano; tatrimāni ratanāni, seyyathidaṁ—cattāro satipaṭṭhānā, cattāro sammappadhānā, cattāro iddhipādā, pañcindriyāni, pañca balāni, satta bojjhaṅgā, ariyo aṭṭhaṅgiko maggo. ", "eng": "In the same way, this teaching and training is full of many kinds of treasures, such as the four kinds of mindfulness meditation, the four right efforts, the four bases of psychic power, the five faculties, the five powers, the seven awakening factors, and the noble eightfold path. ", "html": "{}"}, "ud5.5:25.3": {"pli": "Yampi, bhikkhave, ayaṁ dhammavinayo bahuratano anekaratano, tatrimāni ratanāni, seyyathidaṁ—cattāro satipaṭṭhānā, cattāro sammappadhānā, cattāro iddhipādā, pañcindriyāni, pañca balāni, satta bojjhaṅgā, ariyo aṭṭhaṅgiko maggo; ", "html": "{}"}, "ud5.5:25.4": {"pli": "ayaṁ, bhikkhave, imasmiṁ dhammavinaye sattamo acchariyo abbhuto dhammo, yaṁ disvā disvā bhikkhū imasmiṁ dhammavinaye abhiramanti. ", "eng": "This is the seventh thing the mendicants love about this teaching and training. ", "html": "{}</p>"}, "ud5.5:26.1": {"pli": "Seyyathāpi, bhikkhave, mahāsamuddo mahataṁ bhūtānaṁ āvāso, tatrime bhūtā—timi timiṅgalo timitimiṅgalo asurā nāgā gandhabbā, santi mahāsamudde yojanasatikāpi attabhāvā dviyojanasatikāpi attabhāvā tiyojanasatikāpi attabhāvā catuyojanasatikāpi attabhāvā pañcayojanasatikāpi attabhāvā; ", "eng": "Many great beings live in the ocean, such as leviathans, leviathan-gulpers, leviathan-gulper-gulpers, titans, dragons, and centaurs. In the ocean there are life-forms a hundred leagues long, or even two hundred, three hundred, four hundred, or five hundred leagues long. ", "html": "<p>{}"}, "ud5.5:26.2": {"pli": "evamevaṁ kho, bhikkhave, ayaṁ dhammavinayo mahataṁ bhūtānaṁ āvāso;tatrime bhūtā—sotāpanno, sotāpattiphalasacchikiriyāya paṭipanno, sakadāgāmī, sakadāgāmiphalasacchikiriyāya paṭipanno, anāgāmī, anāgāmiphalasacchikiriyāya paṭipanno, arahā, arahattāya paṭipanno. ", "eng": "In the same way, great beings live in this teaching and training, and these are those beings. The stream-enterer and the one practicing to realize the fruit of stream-entry. The once-returner and the one practicing to realize the fruit of once-return. The non-returner and the one practicing to realize the fruit of non-return. The perfected one, and the one practicing for perfection. ", "html": "{}"}, "ud5.5:26.3": {"pli": "Yampi, bhikkhave, ayaṁ dhammavinayo mahataṁ bhūtānaṁ āvāso, tatrime bhūtā—sotāpanno, sotāpattiphalasacchikiriyāya paṭipanno, sakadāgāmī, sakadāgāmiphalasacchikiriyāya paṭipanno, anāgāmī, anāgāmiphalasacchikiriyāya paṭipanno, arahā, arahattāya paṭipanno; ", "html": "{}"}, "ud5.5:26.4": {"pli": "ayaṁ, bhikkhave, imasmiṁ dhammavinaye aṭṭhamo acchariyo abbhuto dhammo, yaṁ disvā disvā bhikkhū imasmiṁ dhammavinaye abhiramanti. ", "eng": "This is the eighth thing the mendicants love about this teaching and training. ", "html": "{}"}, "ud5.5:26.5": {"pli": "Ime kho, bhikkhave, imasmiṁ dhammavinaye aṭṭha acchariyā abbhutā dhammā, ye disvā disvā bhikkhū imasmiṁ dhammavinaye abhiramantī”ti. ", "eng": "Seeing these eight incredible and amazing things, the mendicants love this teaching and training.” ", "html": "{}</p>"}, "ud5.5:27.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud5.5:28.1": {"pli": "“Channamativassati, ", "eng": "“The rain saturates things that are covered up; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud5.5:28.2": {"pli": "vivaṭaṁ nātivassati; ", "eng": "it doesn’t saturate things that are open. ", "html": "<span class='verse-line'>{}</span>"}, "ud5.5:28.3": {"pli": "Tasmā channaṁ vivaretha, ", "eng": "Therefore you should open up a covered thing, ", "html": "<span class='verse-line'>{}</span>"}, "ud5.5:28.4": {"pli": "evaṁ taṁ nātivassatī”ti. ", "eng": "so the rain will not saturate it.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud5.5:28.5": {"pli": "Pañcamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud5.6": {"ud5.6:0.1": {"pli": "Udāna 5.6 ", "eng": "Heartfelt Sayings 5.6 ", "html": "<article id='ud5.6'><header><ul><li class='division'>{}</li></ul>"}, "ud5.6:0.2": {"pli": "Soṇasutta ", "eng": "With Soṇa ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud5.6:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud5.6:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud5.6:1.3": {"pli": "Tena kho pana samayena āyasmā mahākaccāno avantīsu viharati kuraraghare pavatte pabbate. ", "eng": "Now at that time Venerable Mahākaccāna was staying in the land of the Avantis near Kuraraghara on Steep Mountain. ", "html": "{}"}, "ud5.6:1.4": {"pli": "Tena kho pana samayena soṇo upāsako kuṭikaṇṇo āyasmato mahākaccānassa upaṭṭhāko hoti. ", "eng": "And the layman Soṇa of the Sharp Ears was Mahākaccāna’s attendant. ", "html": "{}</p>"}, "ud5.6:2.1": {"pli": "Atha kho soṇassa upāsakassa kuṭikaṇṇassa rahogatassa paṭisallīnassa evaṁ cetaso parivitakko udapādi: ", "eng": "Then as Soṇa was in private retreat this thought came to his mind, ", "html": "<p>{}"}, "ud5.6:2.2": {"pli": "“yathā yathā kho ayyo mahākaccāno dhammaṁ deseti nayidaṁ sukaraṁ agāraṁ ajjhāvasatā ekantaparipuṇṇaṁ ekantaparisuddhaṁ saṅkhalikhitaṁ brahmacariyaṁ carituṁ. ", "eng": "“As I understand Master Mahākaccāna’s teachings, it’s not easy for someone living at home to lead the spiritual life utterly full and pure, like a polished shell. ", "html": "{}"}, "ud5.6:2.3": {"pli": "Yannūnāhaṁ kesamassuṁ ohāretvā kāsāyāni vatthāni acchādetvā agārasmā anagāriyaṁ pabbajeyyan”ti. ", "eng": "Why don’t I shave off my hair and beard, dress in ocher robes, and go forth from lay life to homelessness?” ", "html": "{}</p>"}, "ud5.6:3.1": {"pli": "Atha kho soṇo upāsako kuṭikaṇṇo yenāyasmā mahākaccāno tenupasaṅkami; upasaṅkamitvā āyasmantaṁ mahākaccānaṁ abhivādetvā ekamantaṁ nisīdi. ", "eng": "Then Soṇa went up to Mahākaccāna, bowed, sat down to one side, and told him what he was thinking. Then he said, ", "html": "<p>{}"}, "ud5.6:3.2": {"pli": "Ekamantaṁ nisinno kho soṇo upāsako kuṭikaṇṇo āyasmantaṁ mahākaccānaṁ etadavoca: ", "html": "{}</p>"}, "ud5.6:4.1": {"pli": "“Idha mayhaṁ, bhante, rahogatassa paṭisallīnassa evaṁ cetaso parivitakko udapādi: ", "html": "<p>{}"}, "ud5.6:4.2": {"pli": "‘yathā yathā kho ayyo mahākaccāno dhammaṁ deseti nayidaṁ sukaraṁ agāraṁ ajjhāvasatā ekantaparipuṇṇaṁ ekantaparisuddhaṁ saṅkhalikhitaṁ brahmacariyaṁ carituṁ. ", "html": "{}"}, "ud5.6:4.3": {"pli": "Yannūnāhaṁ kesamassuṁ ohāretvā kāsāyāni vatthāni acchādetvā agārasmā anagāriyaṁ pabbajeyyan’ti. ", "html": "{}"}, "ud5.6:4.4": {"pli": "Pabbājetu maṁ, bhante, ayyo mahākaccāno”ti. ", "eng": "“May Master Mahākaccāna please give me the going forth!” ", "html": "{}</p>"}, "ud5.6:5.1": {"pli": "Evaṁ vutte, āyasmā mahākaccāno soṇaṁ upāsakaṁ kuṭikaṇṇaṁ etadavoca: ", "eng": "When this was said, Mahākaccāna said to him, ", "html": "<p>{}"}, "ud5.6:5.2": {"pli": "“dukkaraṁ kho, soṇa, yāvajīvaṁ ekabhattaṁ ekaseyyaṁ brahmacariyaṁ. ", "eng": "“It’s hard to lead the spiritual life as long as you live, eating in one part of the day and sleeping alone. ", "html": "{}"}, "ud5.6:5.3": {"pli": "Iṅgha tvaṁ, soṇa, tattheva āgārikabhūto samāno buddhānaṁ sāsanaṁ anuyuñja kālayuttaṁ ekabhattaṁ ekaseyyaṁ brahmacariyan”ti. ", "eng": "Come now, Soṇa, while remaining a layperson just as you are, devote yourself to the instructions of the Buddhas, leading the spiritual life at suitable times, eating in one part of the day and sleeping alone.” ", "html": "{}"}, "ud5.6:5.4": {"pli": "Atha kho soṇassa upāsakassa kuṭikaṇṇassa yo ahosi pabbajjābhisaṅkhāro so paṭipassambhi. ", "eng": "Then Soṇa’s aspiration to go forth died down. ", "html": "{}</p>"}, "ud5.6:6.1": {"pli": "Dutiyampi kho …pe… ", "eng": "For a second time, while in private retreat the thought came to Soṇa that he should go forth, but the outcome was the same. ", "html": "<p>{}"}, "ud5.6:6.2": {"pli": "dutiyampi kho āyasmā mahākaccāno soṇaṁ upāsakaṁ kuṭikaṇṇaṁ etadavoca: ", "html": "{}"}, "ud5.6:6.3": {"pli": "“dukkaraṁ kho, soṇa, yāvajīvaṁ ekabhattaṁ ekaseyyaṁ brahmacariyaṁ. ", "html": "{}"}, "ud5.6:6.4": {"pli": "Iṅgha tvaṁ, soṇa, tattheva āgārikabhūto samāno buddhānaṁ sāsanaṁ anuyuñja kālayuttaṁ ekabhattaṁ ekaseyyaṁ brahmacariyan”ti. ", "html": "{}"}, "ud5.6:6.5": {"pli": "Dutiyampi kho soṇassa upāsakassa kuṭikaṇṇassa yo ahosi pabbajjābhisaṅkhāro so paṭipassambhi. ", "html": "{}</p>"}, "ud5.6:7.1": {"pli": "Tatiyampi kho soṇassa upāsakassa kuṭikaṇṇassa rahogatassa paṭisallīnassa evaṁ cetaso parivitakko udapādi: ", "eng": "For a third time, as Soṇa was in private retreat this thought came to his mind, ", "html": "<p>{}"}, "ud5.6:7.2": {"pli": "“yathā yathā kho ayyo mahākaccāno dhammaṁ deseti nayidaṁ sukaraṁ agāraṁ ajjhāvasatā ekantaparipuṇṇaṁ ekantaparisuddhaṁ saṅkhalikhitaṁ brahmacariyaṁ carituṁ. ", "eng": "“As I understand Master Mahākaccāna’s teachings, it’s not easy for someone living at home to lead the spiritual life utterly full and pure, like a polished shell. ", "html": "{}"}, "ud5.6:7.3": {"pli": "Yannūnāhaṁ kesamassuṁ ohāretvā kāsāyāni vatthāni acchādetvā agārasmā anagāriyaṁ pabbajeyyan”ti. ", "eng": "Why don’t I shave off my hair and beard, dress in ocher robes, and go forth from lay life to homelessness?” ", "html": "{}"}, "ud5.6:7.4": {"pli": "Tatiyampi kho soṇo upāsako kuṭikaṇṇo yenāyasmā mahākaccāno tenupasaṅkami; upasaṅkamitvā āyasmantaṁ mahākaccānaṁ abhivādetvā ekamantaṁ nisīdi. ", "eng": "For a third time, Soṇa went up to Mahākaccāna, bowed, sat down to one side, and told him what he was thinking. Then he said, ", "html": "{}"}, "ud5.6:7.5": {"pli": "Ekamantaṁ nisinno kho soṇo upāsako kuṭikaṇṇo āyasmantaṁ mahākaccānaṁ etadavoca: ", "html": "{}</p>"}, "ud5.6:8.1": {"pli": "“Idha mayhaṁ, bhante, rahogatassa paṭisallīnassa evaṁ cetaso parivitakko udapādi: ", "html": "<p>{}"}, "ud5.6:8.2": {"pli": "‘yathā yathā kho ayyo mahākaccāno dhammaṁ deseti nayidaṁ sukaraṁ agāraṁ ajjhāvasatā ekantaparipuṇṇaṁ ekantaparisuddhaṁ saṅkhalikhitaṁ brahmacariyaṁ carituṁ. ", "html": "{}"}, "ud5.6:8.3": {"pli": "Yannūnāhaṁ kesamassuṁ ohāretvā kāsāyāni vatthāni acchādetvā agārasmā anagāriyaṁ pabbajeyyan’ti. ", "html": "{}"}, "ud5.6:8.4": {"pli": "Pabbājetu maṁ, bhante, ayyo mahākaccāno”ti. ", "eng": "“May Master Mahākaccāna please give me the going forth!” ", "html": "{}</p>"}, "ud5.6:9.1": {"pli": "Atha kho āyasmā mahākaccāno soṇaṁ upāsakaṁ kuṭikaṇṇaṁ pabbājesi. ", "eng": "Then Mahākaccāna gave Soṇa the going forth. ", "html": "<p>{}"}, "ud5.6:9.2": {"pli": "Tena kho pana samayena avantidakkhiṇāpatho appabhikkhuko hoti. ", "eng": "Now at that time the southern region, including Avanti, was short of monks. ", "html": "{}"}, "ud5.6:9.3": {"pli": "Atha kho āyasmā mahākaccāno tiṇṇaṁ vassānaṁ accayena kicchena kasirena tato tato dasavaggaṁ bhikkhusaṅghaṁ sannipātetvā āyasmantaṁ soṇaṁ upasampādesi. ", "eng": "It took three years and much struggle and difficulty before Venerable Mahākaccāyana was able to assemble from here and there a Sangha consisting of ten monks and give Venerable Soṇa the full ordination. ", "html": "{}</p>"}, "ud5.6:10.1": {"pli": "Atha kho āyasmato soṇassa vassaṁvuṭṭhassa rahogatassa paṭisallīnassa evaṁ cetaso parivitakko udapādi: ", "eng": "Then as Venerable Soṇa was in private retreat this thought came to his mind, ", "html": "<p>{}"}, "ud5.6:10.2": {"pli": "“na kho me so bhagavā sammukhā diṭṭho, api ca sutoyeva me: ", "eng": "“I have not personally seen the Buddha. I have only heard reports that ", "html": "{}"}, "ud5.6:10.3": {"pli": "‘so bhagavā īdiso ca īdiso cā’ti. ", "eng": "that Blessed One is like this or like that. ", "html": "{}"}, "ud5.6:10.4": {"pli": "Sace maṁ upajjhāyo anujāneyya, gaccheyyāhaṁ taṁ bhagavantaṁ dassanāya arahantaṁ sammāsambuddhan”ti. ", "eng": "If my mentor allows, I should go to see that Blessed One, the perfected one, the fully awakened Buddha.” ", "html": "{}</p>"}, "ud5.6:11.1": {"pli": "Atha kho āyasmā soṇo sāyanhasamayaṁ paṭisallānā vuṭṭhito yenāyasmā mahākaccāno tenupasaṅkami; upasaṅkamitvā āyasmantaṁ mahākaccānaṁ abhivādetvā ekamantaṁ nisīdi. ", "eng": "Then in the late afternoon, Soṇa came out of retreat, went up to Mahākaccāna, bowed, sat down to one side, and told him what he was thinking. Then Mahākaccāna said, ", "html": "<p>{}"}, "ud5.6:11.2": {"pli": "Ekamantaṁ nisinno kho āyasmā soṇo āyasmantaṁ mahākaccānaṁ etadavoca: ", "html": "{}</p>"}, "ud5.6:12.1": {"pli": "“Idha mayhaṁ, bhante, rahogatassa paṭisallīnassa evaṁ cetaso parivitakko udapādi: ", "html": "<p>{}"}, "ud5.6:12.2": {"pli": "‘na kho me so bhagavā sammukhā diṭṭho, api ca sutoyeva me—", "html": "{}"}, "ud5.6:12.3": {"pli": "so bhagavā īdiso ca īdiso cā’ti. ", "html": "{}"}, "ud5.6:12.4": {"pli": "Sace maṁ upajjhāyo anujāneyya, gaccheyyāhaṁ taṁ bhagavantaṁ dassanāya arahantaṁ sammāsambuddhan”ti. ", "html": "{}"}, "ud5.6:12.5": {"pli": "(…) ", "html": "{}</p>"}, "ud5.6:13.1": {"pli": "“Sādhu sādhu, soṇa; ", "eng": "“Good, good, Soṇa! ", "html": "<p>{}"}, "ud5.6:13.2": {"pli": "gaccha tvaṁ, soṇa, taṁ bhagavantaṁ dassanāya arahantaṁ sammāsambuddhaṁ. ", "eng": "Go to see the Blessed One, the perfected one, the fully awakened Buddha. ", "html": "{}"}, "ud5.6:13.3": {"pli": "Dakkhissasi tvaṁ, soṇa, taṁ bhagavantaṁ pāsādikaṁ pasādanīyaṁ santindriyaṁ santamānasaṁ uttamadamathasamathamanuppattaṁ dantaṁ guttaṁ yatindriyaṁ nāgaṁ. ", "eng": "You will see that Blessed One who is impressive and inspiring, with peaceful faculties and heart, attained to the highest self-control and serenity, like an elephant with tamed, guarded, and controlled faculties. ", "html": "{}"}, "ud5.6:13.4": {"pli": "Disvāna mama vacanena bhagavato pāde sirasā vandāhi, appābādhaṁ appātaṅkaṁ lahuṭṭhānaṁ balaṁ phāsuvihāraṁ puccha: ", "eng": "On seeing him, in my name bow with your head to his feet. Ask him if he is healthy and well, nimble, strong, and living comfortably, saying, ", "html": "{}"}, "ud5.6:13.5": {"pli": "‘upajjhāyo me, bhante, āyasmā mahākaccāno bhagavato pāde sirasā vandati, appābādhaṁ appātaṅkaṁ lahuṭṭhānaṁ balaṁ phāsuvihāraṁ pucchatī’”ti. ", "eng": "‘Sir, my mentor Venerable Mahākaccāna bows with his head to your feet. He asks if you are healthy and well, nimble, strong, and living comfortably.’” ", "html": "{}</p>"}, "ud5.6:14.1": {"pli": "“Evaṁ, bhante”ti kho āyasmā soṇo āyasmato mahākaccānassa bhāsitaṁ abhinanditvā anumoditvā uṭṭhāyāsanā āyasmantaṁ mahākaccānaṁ abhivādetvā padakkhiṇaṁ katvā senāsanaṁ saṁsāmetvā pattacīvaramādāya yena sāvatthi tena cārikaṁ pakkāmi. ", "eng": "Saying, “Yes, sir,” Soṇa welcomed and agreed with Mahākaccāna’s words. He got up from his seat, bowed, and respectfully circled Mahākaccāna, keeping him on his right. Then he set his lodgings in order and, taking his bowl and robe, set out for Sāvatthī. ", "html": "<p>{}"}, "ud5.6:14.2": {"pli": "Anupubbena cārikaṁ caramāno yena sāvatthi jetavanaṁ anāthapiṇḍikassa ārāmo, yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. ", "eng": "Eventually he came to Sāvatthī and Jeta’s Grove. He went up to the Buddha, bowed, and sat down to one side. ", "html": "{}"}, "ud5.6:14.3": {"pli": "Ekamantaṁ nisinno kho āyasmā soṇo bhagavantaṁ etadavoca: ", "eng": "Soṇa said to the Buddha, ", "html": "{}"}, "ud5.6:14.4": {"pli": "“upajjhāyo me, bhante, āyasmā mahākaccāno bhagavato pāde sirasā vandati, appābādhaṁ appātaṅkaṁ lahuṭṭhānaṁ balaṁ phāsuvihāraṁ pucchatī”ti. ", "eng": "“Sir, my mentor Venerable Mahākaccāna bows with his head to your feet. He asks if you are healthy and well, nimble, strong, and living comfortably.” ", "html": "{}</p>"}, "ud5.6:15.1": {"pli": "“Kacci, bhikkhu, khamanīyaṁ, kacci yāpanīyaṁ, kaccisi appakilamathena addhānaṁ āgato, na ca piṇḍakena kilantosī”ti? ", "eng": "“I hope you’re keeping well, mendicant; I hope you’re all right. And I hope you have arrived from your journey unwearied, having had no trouble getting almsfood.” ", "html": "<p>{}"}, "ud5.6:15.2": {"pli": "“Khamanīyaṁ, bhagavā, yāpanīyaṁ, bhagavā, appakilamathena cāhaṁ, bhante, addhānaṁ āgato, na ca piṇḍakena kilantomhī”ti. ", "eng": "“I’m keeping well, Blessed One; I’m all right. And I have arrived from my journey unwearied, having had no trouble getting almsfood.” ", "html": "{}</p>"}, "ud5.6:16.1": {"pli": "Atha kho bhagavā āyasmantaṁ ānandaṁ āmantesi: ", "eng": "Then the Buddha said to Venerable Ānanda, ", "html": "<p>{}"}, "ud5.6:16.2": {"pli": "“imassānanda, āgantukassa bhikkhuno senāsanaṁ paññāpehī”ti. ", "eng": "“Prepare lodgings for this visiting mendicant.” ", "html": "{}"}, "ud5.6:16.3": {"pli": "Atha kho āyasmato ānandassa etadahosi: ", "eng": "Then Venerable Ānanda thought, ", "html": "{}"}, "ud5.6:16.4": {"pli": "“yassa kho maṁ bhagavā āṇāpeti: ", "eng": "“When the Buddha orders me ", "html": "{}"}, "ud5.6:16.5": {"pli": "‘imassānanda, āgantukassa bhikkhuno senāsanaṁ paññāpehī’ti, icchati bhagavā tena bhikkhunā saddhiṁ ekavihāre vatthuṁ, icchati bhagavā āyasmatā soṇena saddhiṁ ekavihāre vatthun”ti. ", "eng": "to prepare lodgings for a specific mendicant, he wishes to stay in the same dwelling with that mendicant. The Buddha wishes to stay together with Venerable Soṇa.” ", "html": "{}"}, "ud5.6:16.6": {"pli": "Yasmiṁ vihāre bhagavā viharati, tasmiṁ vihāre āyasmato soṇassa senāsanaṁ paññāpesi. ", "eng": "He prepared lodgings for Soṇa in the same dwelling where the Buddha was staying. ", "html": "{}</p>"}, "ud5.6:17.1": {"pli": "Atha kho bhagavā bahudeva rattiṁ abbhokāse nisajjāya vītināmetvā pāde pakkhāletvā vihāraṁ pāvisi. ", "eng": "The Buddha spent much of the night sitting meditation in the open. Then he got up from his seat, washed his feet and entered the dwelling. ", "html": "<p>{}"}, "ud5.6:17.2": {"pli": "Āyasmāpi kho soṇo bahudeva rattiṁ abbhokāse nisajjāya vītināmetvā pāde pakkhāletvā vihāraṁ pāvisi. ", "eng": "Venerable Soṇa did the same. ", "html": "{}"}, "ud5.6:17.3": {"pli": "Atha kho bhagavā rattiyā paccūsasamayaṁ paccuṭṭhāya āyasmantaṁ soṇaṁ ajjhesi: ", "eng": "Then the Buddha rose at the crack of dawn and addressed Soṇa, ", "html": "{}"}, "ud5.6:17.4": {"pli": "“paṭibhātu taṁ, bhikkhu, dhammo bhāsitun”ti. ", "eng": "“Speak some Dhamma, mendicant, as you feel inspired.” ", "html": "{}</p>"}, "ud5.6:18.1": {"pli": "“Evaṁ, bhante”ti kho āyasmā soṇo bhagavato paṭissutvā soḷasa aṭṭhakavaggikāni sabbāneva sarena abhaṇi. ", "eng": "“Yes, sir,” replied Soṇa. He intoned all sixteen discourses in the Chapter of the Eights. ", "html": "<p>{}", "comm": "The Vinaya version of this passage omits “sixteen” (<a href='index.html?q=pli-tv-kd5#13.9.5'>Kd 5:13.9.5</a>). | <i lang='pi' translate='no'>Sarena abhaṇi</i> (“intoned”) means “recited with tones”, apparently a reference to a chanting style that made use of or echoed the Vedic tonal system (<i lang='pi' translate='no'>svara</i>). It was allowed for mendicants at <a href='index.html?q=pli-tv-kd15#3.2.3'>Kd 15:3.2.3</a>; see too <a href='index.html?q=pli-tv-kd22#1.10.4'>Kd 22:1.10.4</a>. Contrast <i lang='pi' translate='no'>gītassara</i> (“singing sound”, <a href='index.html?q=an5.209'>AN 5.209</a>), which is not allowed as it is a sensuous song (<a href='index.html?q=dn21#1.6.2'>DN 21:1.6.2</a>). "}, "ud5.6:18.2": {"pli": "Atha kho bhagavā āyasmato soṇassa sarabhaññapariyosāne abbhanumodi: ", "eng": "When Soṇa finished his intoning, the Buddha applauded, saying, ", "html": "{}"}, "ud5.6:18.3": {"pli": "“sādhu sādhu, bhikkhu, suggahitāni te, bhikkhu, soḷasa aṭṭhakavaggikāni sumanasikatāni sūpadhāritāni, kalyāṇiyāsi vācāya samannāgato vissaṭṭhāya anelagaḷāya atthassa viññāpaniyā. ", "eng": "“Good, good, mendicant! You have learned the sixteen discourses of the Chapter of the Eights well, you have applied the mind and remembered it well. You are a good speaker, with a polished, clear, and articulate voice that expresses the meaning. ", "html": "{}"}, "ud5.6:18.4": {"pli": "Kati vassosi tvaṁ, bhikkhū”ti? ", "eng": "How many rains have you been ordained, mendicant?” ", "html": "{}"}, "ud5.6:18.5": {"pli": "“Ekavasso ahaṁ, bhagavā”ti. ", "eng": "“I have one rains, Blessed One.” ", "html": "{}"}, "ud5.6:18.6": {"pli": "“Kissa pana tvaṁ, bhikkhu, evaṁ ciraṁ akāsī”ti? ", "eng": "“But why did it take you so long to make it?” ", "html": "{}"}, "ud5.6:18.7": {"pli": "“Ciraṁ diṭṭho me, bhante, kāmesu ādīnavo; ", "eng": "“Sir, I have long seen the drawbacks of sensual pleasures, ", "html": "{}"}, "ud5.6:18.8": {"pli": "api ca sambādho gharāvāso bahukicco bahukaraṇīyo”ti. ", "eng": "yet living in a house is cramped, with many duties and much to do.” ", "html": "{}</p>"}, "ud5.6:19.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud5.6:20.1": {"pli": "“Disvā ādīnavaṁ loke, ", "eng": "“Seeing the danger of the world, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud5.6:20.2": {"pli": "ñatvā dhammaṁ nirūpadhiṁ; ", "eng": "I understood the truth without attachments. ", "html": "<span class='verse-line'>{}</span>"}, "ud5.6:20.3": {"pli": "Ariyo na ramatī pāpe, ", "eng": "The Noble One does not delight in evil, ", "html": "<span class='verse-line'>{}</span>"}, "ud5.6:20.4": {"pli": "pāpe na ramatī sucī”ti. ", "eng": "the Pure One does not delight in evil.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud5.6:20.5": {"pli": "Chaṭṭhaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud5.7": {"ud5.7:0.1": {"pli": "Udāna 5.7 ", "eng": "Heartfelt Sayings 5.7 ", "html": "<article id='ud5.7'><header><ul><li class='division'>{}</li></ul>"}, "ud5.7:0.2": {"pli": "Kaṅkhārevatasutta ", "eng": "With Revata the Doubter ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud5.7:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud5.7:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud5.7:1.3": {"pli": "Tena kho pana samayena āyasmā kaṅkhārevato bhagavato avidūre nisinno hoti pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya attano kaṅkhāvitaraṇavisuddhiṁ paccavekkhamāno. ", "eng": "Now at that time Venerable Revata the Doubter was sitting not far from the Buddha, cross-legged, with his body straight, reviewing his own purification by traversing doubt. ", "html": "{}</p>"}, "ud5.7:2.1": {"pli": "Addasā kho bhagavā āyasmantaṁ kaṅkhārevataṁ avidūre nisinnaṁ pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya attano kaṅkhāvitaraṇavisuddhiṁ paccavekkhamānaṁ. ", "eng": "The Buddha saw him meditating there. ", "html": "<p>{}</p>"}, "ud5.7:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud5.7:4.1": {"pli": "“Yā kāci kaṅkhā idha vā huraṁ vā, ", "eng": "“Any doubts about this life or the next, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud5.7:4.2": {"pli": "Sakavediyā vā paravediyā vā; ", "eng": "about one’s own experiences or those of another: ", "html": "<span class='verse-line'>{}</span>"}, "ud5.7:4.3": {"pli": "Ye jhāyino tā pajahanti sabbā, ", "eng": "those who meditate give them all up, ", "html": "<span class='verse-line'>{}</span>"}, "ud5.7:4.4": {"pli": "Ātāpino brahmacariyaṁ carantā”ti. ", "eng": "keenly practicing the spiritual life.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud5.7:4.5": {"pli": "Sattamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud5.8": {"ud5.8:0.1": {"pli": "Udāna 5.8 ", "eng": "Heartfelt Sayings 5.8 ", "html": "<article id='ud5.8'><header><ul><li class='division'>{}</li></ul>"}, "ud5.8:0.2": {"pli": "Saṅghabhedasutta ", "eng": "Schism in the Saṅgha ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud5.8:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud5.8:1.2": {"pli": "ekaṁ samayaṁ bhagavā rājagahe viharati veḷuvane kalandakanivāpe. ", "eng": "At one time the Buddha was staying near Rājagaha, in the Bamboo Grove, the squirrels’ feeding ground. ", "html": "{}"}, "ud5.8:1.3": {"pli": "Tena kho pana samayena āyasmā ānando tadahuposathe pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya rājagahaṁ piṇḍāya pāvisi. ", "eng": "Now at that time Venerable Ānanda robed up in the morning and, taking his bowl and robe, entered Rājagaha for alms. ", "html": "{}</p>"}, "ud5.8:2.1": {"pli": "Addasā kho devadatto āyasmantaṁ ānandaṁ rājagahe piṇḍāya carantaṁ. ", "eng": "Devadatta saw him wandering for alms, ", "html": "<p>{}"}, "ud5.8:2.2": {"pli": "Disvāna yenāyasmā ānando tenupasaṅkami; upasaṅkamitvā āyasmantaṁ ānandaṁ etadavoca: ", "eng": "so he went up to him and said, ", "html": "{}"}, "ud5.8:2.3": {"pli": "“ajjatagge dānāhaṁ, āvuso ānanda, aññatreva bhagavatā aññatra bhikkhusaṅghā uposathaṁ karissāmi saṅghakammāni cā”ti. ", "eng": "“From this day forth, Reverend Ānanda, I shall perform the sabbath and legal proceedings of the Saṅgha apart from the Buddha and the Saṅgha of mendicants.” ", "html": "{}</p>"}, "ud5.8:3.1": {"pli": "Atha kho āyasmā ānando rājagahe piṇḍāya caritvā pacchābhattaṁ piṇḍapātapaṭikkanto yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. ", "eng": "Then Ānanda wandered for alms in Rājagaha. After the meal, on his return from almsround, he went to the Buddha, bowed, sat down to one side, ", "html": "<p>{}"}, "ud5.8:3.2": {"pli": "Ekamantaṁ nisinno kho āyasmā ānando bhagavantaṁ etadavoca: ", "eng": "and told him what had just happened, adding: ", "html": "{}</p>"}, "ud5.8:4.1": {"pli": "“Idhāhaṁ, bhante, pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya rājagahaṁ piṇḍāya pāvisiṁ. ", "html": "<p>{}"}, "ud5.8:4.2": {"pli": "Addasā kho maṁ, bhante, devadatto rājagahe piṇḍāya carantaṁ. ", "html": "{}"}, "ud5.8:4.3": {"pli": "Disvāna yenāhaṁ tenupasaṅkami; upasaṅkamitvā maṁ etadavoca: ", "html": "{}"}, "ud5.8:4.4": {"pli": "‘ajjatagge dānāhaṁ, āvuso ānanda, aññatreva bhagavatā aññatra bhikkhusaṅghā uposathaṁ karissāmi saṅghakammāni cā’ti. ", "html": "{}"}, "ud5.8:4.5": {"pli": "Ajja, bhante, devadatto saṅghaṁ bhindissati, uposathañca karissati saṅghakammāni cā”ti. ", "eng": "“Today, sir, Devadatta will split the Saṅgha. He will perform the sabbath and legal proceedings of the Saṅgha.” ", "html": "{}</p>"}, "ud5.8:5.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud5.8:6.1": {"pli": "“Sukaraṁ sādhunā sādhu, ", "eng": "“It’s easy for the good to do good, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud5.8:6.2": {"pli": "Sādhu pāpena dukkaraṁ; ", "eng": "but for the bad, doing good is hard. ", "html": "<span class='verse-line'>{}</span>"}, "ud5.8:6.3": {"pli": "Pāpaṁ pāpena sukaraṁ, ", "eng": "For the bad, it’s easy to do bad, ", "html": "<span class='verse-line'>{}</span>"}, "ud5.8:6.4": {"pli": "Pāpamariyehi dukkaran”ti. ", "eng": "but for the noble ones, doing bad is hard.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud5.8:6.5": {"pli": "Aṭṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud5.9": {"ud5.9:0.1": {"pli": "Udāna 5.9 ", "eng": "Heartfelt Sayings 5.9 ", "html": "<article id='ud5.9'><header><ul><li class='division'>{}</li></ul>"}, "ud5.9:0.2": {"pli": "Sadhāyamānasutta ", "eng": "Teasing ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud5.9:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud5.9:1.2": {"pli": "ekaṁ samayaṁ bhagavā kosalesu cārikaṁ carati mahatā bhikkhusaṅghena saddhiṁ. ", "eng": "At one time the Buddha was wandering in the land of the Kosalans together with a large Saṅgha of mendicants. ", "html": "{}"}, "ud5.9:1.3": {"pli": "Tena kho pana samayena sambahulā māṇavakā bhagavato avidūre sadhāyamānarūpā atikkamanti. ", "eng": "Now at that time several young students were passing by not far from the Buddha in a teasing manner. ", "html": "{}"}, "ud5.9:1.4": {"pli": "Addasā kho bhagavā sambahule māṇavake avidūre sadhāyamānarūpe atikkante. ", "eng": "The Buddha saw them. ", "html": "{}</p>"}, "ud5.9:2.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud5.9:3.1": {"pli": "“Parimuṭṭhā paṇḍitābhāsā, ", "eng": "“Dolts pretending to be astute, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud5.9:3.2": {"pli": "vācāgocarabhāṇino; ", "eng": "they talk, their words out of bounds. ", "html": "<span class='verse-line'>{}</span>"}, "ud5.9:3.3": {"pli": "Yāvicchanti mukhāyāmaṁ, ", "eng": "They blab at will, their mouths agape, ", "html": "<span class='verse-line'>{}</span>"}, "ud5.9:3.4": {"pli": "yena nītā na taṁ vidū”ti. ", "eng": "and no-one knows what leads them on.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud5.9:3.5": {"pli": "Navamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud5.10": {"ud5.10:0.1": {"pli": "Udāna 5.10 ", "eng": "Heartfelt Sayings 5.10 ", "html": "<article id='ud5.10'><header><ul><li class='division'>{}</li></ul>"}, "ud5.10:0.2": {"pli": "Cūḷapanthakasutta ", "eng": "With Cūḷapanthaka ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud5.10:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud5.10:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud5.10:1.3": {"pli": "Tena kho pana samayena āyasmā cūḷapanthako bhagavato avidūre nisinno hoti pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya parimukhaṁ satiṁ upaṭṭhapetvā. ", "eng": "Now at that time Venerable Cūḷapanthaka was sitting not far from the Buddha, cross-legged, his body set straight, and mindfulness established in his presence. ", "html": "{}</p>"}, "ud5.10:2.1": {"pli": "Addasā kho bhagavā āyasmantaṁ cūḷapanthakaṁ avidūre nisinnaṁ pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya parimukhaṁ satiṁ upaṭṭhapetvā. ", "eng": "The Buddha saw him meditating there. ", "html": "<p>{}</p>"}, "ud5.10:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud5.10:4.1": {"pli": "“Ṭhitena kāyena ṭhitena cetasā, ", "eng": "“Steady in body, steady in mind, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud5.10:4.2": {"pli": "Tiṭṭhaṁ nisinno uda vā sayāno; ", "eng": "standing, sitting or lying down: ", "html": "<span class='verse-line'>{}</span>"}, "ud5.10:4.3": {"pli": "Etaṁ satiṁ bhikkhu adhiṭṭhahāno, ", "eng": "a mendicant focusing on this mindfulness ", "html": "<span class='verse-line'>{}</span>"}, "ud5.10:4.4": {"pli": "Labhetha pubbāpariyaṁ visesaṁ; ", "eng": "gains an ever higher distinction. ", "html": "<span class='verse-line'>{}</span>"}, "ud5.10:4.5": {"pli": "Laddhāna pubbāpariyaṁ visesaṁ, ", "eng": "And when they have done so, ", "html": "<span class='verse-line'>{}</span>"}, "ud5.10:4.6": {"pli": "Adassanaṁ maccurājassa gacche”ti. ", "eng": "they vanish from the King of Death.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud5.10:4.7": {"pli": "Dasamaṁ. ", "html": "<p class='endsutta'>{}</p>"}, "ud5.10:4.8": {"pli": "Soṇavaggo pañcamo. ", "html": "<p class='endvagga'>{}</p>"}, "ud5.10:5.0": {"pli": "Tassuddānaṁ ", "html": "<p class='uddana-intro'>{}</p>"}, "ud5.10:5.1": {"pli": "Piyo appāyukā kuṭṭhī, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "ud5.10:5.2": {"pli": "kumārakā uposatho; ", "html": "<span class='verse-line'>{}</span>"}, "ud5.10:5.3": {"pli": "Soṇo ca revato bhedo, ", "html": "<span class='verse-line'>{}</span>"}, "ud5.10:5.4": {"pli": "sadhāya panthakena cāti. ", "html": "<span class='verse-line'>{}</span></p></blockquote></article>"}}, "ud6.1": {"ud6.1:0.1": {"pli": "Udāna 6.1 ", "eng": "Heartfelt Sayings 6.1 ", "html": "<article id='ud6.1'><header><ul><li class='division'>{}</li></ul>"}, "ud6.1:0.2": {"pli": "Āyusaṅkhārossajjanasutta ", "eng": "Surrendering the Life Force ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud6.1:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud6.1:1.2": {"pli": "ekaṁ samayaṁ bhagavā vesāliyaṁ viharati mahāvane kūṭāgārasālāyaṁ. ", "eng": "At one time the Buddha was staying near Vesālī, at the Great Wood, in the hall with the peaked roof. ", "html": "{}"}, "ud6.1:1.3": {"pli": "Atha kho bhagavā pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya vesāliṁ piṇḍāya pāvisi. ", "eng": "Then the Buddha robed up in the morning and, taking his bowl and robe, entered Vesālī for alms. ", "html": "{}"}, "ud6.1:1.4": {"pli": "Vesāliyaṁ piṇḍāya caritvā pacchābhattaṁ piṇḍapātapaṭikkanto āyasmantaṁ ānandaṁ āmantesi: ", "eng": "Then, after the meal, on his return from almsround, he addressed Venerable Ānanda: ", "html": "{}"}, "ud6.1:1.5": {"pli": "“gaṇhāhi, ānanda, nisīdanaṁ. ", "eng": "“Ānanda, get your sitting cloth. ", "html": "{}"}, "ud6.1:1.6": {"pli": "Yena cāpālaṁ cetiyaṁ tenupasaṅkamissāma divāvihārāyā”ti. ", "eng": "Let’s go to the Cāpāla Shrine for the day’s meditation.” ", "html": "{}</p>"}, "ud6.1:2.1": {"pli": "“Evaṁ, bhante”ti kho āyasmā ānando bhagavato paṭissutvā nisīdanaṁ ādāya bhagavantaṁ piṭṭhito piṭṭhito anubandhi. ", "eng": "“Yes, sir,” replied Ānanda. Taking his sitting cloth he followed behind the Buddha. ", "html": "<p>{}"}, "ud6.1:2.2": {"pli": "Atha kho bhagavā yena cāpālaṁ cetiyaṁ tenupasaṅkami; upasaṅkamitvā paññatte āsane nisīdi. ", "eng": "Then the Buddha went up to the Cāpāla Shrine, where he sat on the seat spread out. ", "html": "{}"}, "ud6.1:2.3": {"pli": "Nisajja kho bhagavā āyasmantaṁ ānandaṁ āmantesi: ", "eng": "When he was seated he said to Venerable Ānanda: ", "html": "{}</p>"}, "ud6.1:3.1": {"pli": "“Ramaṇīyā, ānanda, vesālī; ramaṇīyaṁ udenaṁ cetiyaṁ; ramaṇīyaṁ gotamakaṁ cetiyaṁ; ramaṇīyaṁ sattambaṁ cetiyaṁ; ramaṇīyaṁ bahuputtaṁ cetiyaṁ; ramaṇīyaṁ sārandadaṁ cetiyaṁ; ramaṇīyaṁ cāpālaṁ cetiyaṁ. ", "eng": "“Ānanda, Vesālī is lovely. And the Udena, Gotamaka, Seven Maidens, Many Sons, Sārandada, and Cāpāla Shrines are all lovely. ", "html": "<p>{}"}, "ud6.1:3.2": {"pli": "Yassa kassaci, ānanda, cattāro iddhipādā bhāvitā bahulīkatā yānīkatā vatthukatā anuṭṭhitā paricitā susamāraddhā, so ākaṅkhamāno (…) kappaṁ vā tiṭṭheyya kappāvasesaṁ vā. ", "eng": "Whoever has developed and cultivated the four bases of psychic power—made them a vehicle and a basis, kept them up, consolidated them, and properly implemented them—may, if they wish, live for the proper lifespan or what’s left of it. ", "html": "{}", "comm": "Normally <i lang='pi' translate='no'>kappa</i> as a period of time means “eon”, but the late canonical texts Kathāvatthu (<a href='https://suttacentral.net/kv11.5'>Kv 11.5</a>) and Milindapañha (<a href='https://suttacentral.net/mil5.1.10'>Mil 5.1.10</a>) argue that it means the “lifespan”, an interpretation followed by the commentaries. Support for this comes from <a href='index.html?q=dn26#28.3'>DN 26:28.3</a>, which says that “long life” for a mendicant is the four bases of psychic power which enable you to remain for the <i lang='pi' translate='no'>kappa</i>. Here it is surely talking about a full lifespan. Underlying this is the idea that in different epochs the “proper lifespan” varies; in the Buddha’s day it was one hundred years (<a href='index.html?q=dn14#1.7.7'>DN 14:1.7.7</a>). We can resolve the problem if we read <i lang='pi' translate='no'>kappa</i> here, not as a period of time, but as “proper, fitting”, i.e. the “proper” lifespan of a hundred years. "}, "ud6.1:3.3": {"pli": "Tathāgatassa kho, ānanda, cattāro iddhipādā bhāvitā bahulīkatā yānīkatā vatthukatā anuṭṭhitā paricitā susamāraddhā. Ākaṅkhamāno, ānanda, tathāgato kappaṁ vā tiṭṭheyya kappāvasesaṁ vā”ti. ", "eng": "The Realized One has developed and cultivated the four bases of psychic power, made them a vehicle and a basis, kept them up, consolidated them, and properly implemented them. If he wished, the Realized One could live for the proper lifespan or what’s left of it.” ", "html": "{}</p>"}, "ud6.1:4.1": {"pli": "Evampi kho āyasmā ānando bhagavatā oḷārike nimitte kayiramāne, oḷārike obhāse kayiramāne, nāsakkhi paṭivijjhituṁ; ", "eng": "But Ānanda didn’t get it, even though the Buddha dropped such an obvious hint, such a clear sign. ", "html": "<p>{}"}, "ud6.1:4.2": {"pli": "na bhagavantaṁ yāci: ", "eng": "He didn’t beg the Buddha, ", "html": "{}"}, "ud6.1:4.3": {"pli": "“tiṭṭhatu, bhante, bhagavā kappaṁ; tiṭṭhatu sugato kappaṁ bahujanahitāya bahujanasukhāya lokānukampāya atthāya hitāya sukhāya devamanussānan”ti, yathā taṁ mārena pariyuṭṭhitacitto. ", "eng": "“Sir, may the Blessed One please remain for the proper lifespan! May the Holy One please remain for the proper lifespan! That would be for the welfare and happiness of the people, out of sympathy for the world, for the benefit, welfare, and happiness of gods and humans.” For his mind was as if possessed by Māra. ", "html": "{}"}, "ud6.1:4.4": {"pli": "Dutiyampi kho …pe… ", "eng": "For a second time … ", "html": "{}"}, "ud6.1:4.5": {"pli": "tatiyampi kho bhagavā āyasmantaṁ ānandaṁ āmantesi: ", "eng": "and for a third time, the Buddha said to Ānanda: ", "html": "{}</p>"}, "ud6.1:5.1": {"pli": "“Ramaṇīyā, ānanda, vesālī; ramaṇīyaṁ udenaṁ cetiyaṁ; ramaṇīyaṁ gotamakaṁ cetiyaṁ; ramaṇīyaṁ sattambaṁ cetiyaṁ; ramaṇīyaṁ bahuputtaṁ cetiyaṁ; ramaṇīyaṁ sārandadaṁ cetiyaṁ; ramaṇīyaṁ cāpālaṁ cetiyaṁ. ", "eng": "“Ānanda, Vesālī is lovely. And the Udena, Gotamaka, Seven Maidens, Many Sons, Sārandada, and Cāpāla Shrines are all lovely. ", "html": "<p>{}"}, "ud6.1:5.2": {"pli": "Yassa kassaci, ānanda, cattāro iddhipādā bhāvitā bahulīkatā yānīkatā vatthukatā anuṭṭhitā paricitā susamāraddhā, so ākaṅkhamāno kappaṁ vā tiṭṭheyya kappāvasesaṁ vā. ", "eng": "Whoever has developed and cultivated the four bases of psychic power—made them a vehicle and a basis, kept them up, consolidated them, and properly implemented them—may, if they wish, live for the proper lifespan or what’s left of it. ", "html": "{}"}, "ud6.1:5.3": {"pli": "Tathāgatassa kho, ānanda, cattāro iddhipādā bhāvitā bahulīkatā yānīkatā vatthukatā anuṭṭhitā paricitā susamāraddhā. Ākaṅkhamāno, ānanda, tathāgato kappaṁ vā tiṭṭheyya kappāvasesaṁ vā”ti. ", "eng": "The Realized One has developed and cultivated the four bases of psychic power, made them a vehicle and a basis, kept them up, consolidated them, and properly implemented them. If he wished, the Realized One could live for the proper lifespan or what’s left of it.” ", "html": "{}</p>"}, "ud6.1:6.1": {"pli": "Evampi kho āyasmā ānando bhagavatā oḷārike nimitte kayiramāne, oḷārike obhāse kayiramāne, nāsakkhi paṭivijjhituṁ; ", "eng": "But Ānanda didn’t get it, even though the Buddha dropped such an obvious hint, such a clear sign. ", "html": "<p>{}"}, "ud6.1:6.2": {"pli": "na bhagavantaṁ yāci: ", "eng": "He didn’t beg the Buddha, ", "html": "{}"}, "ud6.1:6.3": {"pli": "“tiṭṭhatu, bhante, bhagavā kappaṁ; tiṭṭhatu sugato kappaṁ bahujanahitāya bahujanasukhāya lokānukampāya atthāya hitāya sukhāya devamanussānan”ti, yathā taṁ mārena pariyuṭṭhitacitto. ", "eng": "“Sir, may the Blessed One please remain for the proper lifespan! May the Holy One please remain for the proper lifespan! That would be for the welfare and happiness of the people, out of sympathy for the world, for the benefit, welfare, and happiness of gods and humans.” For his mind was as if possessed by Māra. ", "html": "{}</p>"}, "ud6.1:7.1": {"pli": "Atha kho bhagavā āyasmantaṁ ānandaṁ āmantesi: ", "eng": "Then the Buddha said to Venerable Ānanda, ", "html": "<p>{}"}, "ud6.1:7.2": {"pli": "“gaccha tvaṁ, ānanda, yassadāni kālaṁ maññasī”ti. ", "eng": "“Go now, Ānanda, at your convenience.” ", "html": "{}"}, "ud6.1:7.3": {"pli": "“Evaṁ, bhante”ti kho āyasmā ānando bhagavato paṭissutvā uṭṭhāyāsanā bhagavantaṁ abhivādetvā padakkhiṇaṁ katvā avidūre aññatarasmiṁ rukkhamūle nisīdi. ", "eng": "“Yes, sir,” replied Ānanda. He rose from his seat, bowed, and respectfully circled the Buddha, keeping him on his right, before sitting at the root of a tree close by. ", "html": "{}</p>"}, "ud6.1:8.1": {"pli": "Atha kho māro pāpimā, acirapakkante āyasmante ānande, yena bhagavā tenupasaṅkami; upasaṅkamitvā ekamantaṁ aṭṭhāsi. Ekamantaṁ ṭhito kho māro pāpimā bhagavantaṁ etadavoca: ", "eng": "And then, not long after Ānanda had left, Māra the Wicked went up to the Buddha, stood to one side, and said to him: ", "html": "<p>{}</p>"}, "ud6.1:9.1": {"pli": "“Parinibbātu dāni, bhante, bhagavā; parinibbātu sugato; parinibbānakālo dāni, bhante, bhagavato. ", "eng": "“May the Blessed One now be fully extinguished! May the Holy One now be fully extinguished! Now is the time for the full extinguishment of the Buddha. ", "html": "<p>{}"}, "ud6.1:9.2": {"pli": "Bhāsitā kho panesā, bhante, bhagavatā vācā: ", "eng": "Sir, you once made this statement: ", "html": "{}"}, "ud6.1:9.3": {"pli": "‘na tāvāhaṁ, pāpima, parinibbāyissāmi yāva me bhikkhū na sāvakā bhavissanti viyattā vinītā visāradā bahussutā dhammadharā dhammānudhammappaṭipannā sāmīcippaṭipannā anudhammacārino, sakaṁ ācariyakaṁ uggahetvā ācikkhissanti desessanti paññapessanti paṭṭhapessanti vivarissanti vibhajissanti uttānīkarissanti uppannaṁ parappavādaṁ sahadhammena suniggahitaṁ niggahetvā sappāṭihāriyaṁ dhammaṁ desessantī’ti. ", "eng": "‘Wicked One, I shall not be fully extinguished until I have monk disciples who are competent, educated, assured, learned, have memorized the teachings, and practice in line with the teaching. Not until they practice properly, living in line with the teaching. Not until they’ve learned their own tradition, and explain, teach, assert, establish, disclose, analyze, and make it clear. Not until they can legitimately and completely refute the doctrines of others that come up, and teach with a demonstrable basis.’ ", "html": "{}"}, "ud6.1:9.4": {"pli": "Etarahi kho pana, bhante, bhikkhū bhagavato sāvakā viyattā vinītā visāradā bahussutā dhammadharā dhammānudhammappaṭipannā sāmīcippaṭipannā anudhammacārino sakaṁ ācariyakaṁ uggahetvā ācikkhanti desenti paññapenti paṭṭhapenti vivaranti vibhajanti uttānīkaronti uppannaṁ parappavādaṁ sahadhammena suniggahitaṁ niggahetvā sappāṭihāriyaṁ dhammaṁ desenti. ", "eng": "Today you do have such monk disciples. ", "html": "{}"}, "ud6.1:9.5": {"pli": "Parinibbātu dāni, bhante, bhagavā; parinibbātu sugato; parinibbānakālo dāni, bhante, bhagavato. ", "eng": "May the Blessed One now be fully extinguished! May the Holy One now be fully extinguished! Now is the time for the full extinguishment of the Buddha. ", "html": "{}</p>"}, "ud6.1:10.1": {"pli": "Bhāsitā kho panesā, bhante, bhagavatā vācā: ", "eng": "Sir, you once made this statement: ", "html": "<p>{}"}, "ud6.1:10.2": {"pli": "‘na tāvāhaṁ, pāpima, parinibbāyissāmi yāva me bhikkhuniyo na sāvikā bhavissanti viyattā vinītā visāradā bahussutā dhammadharā dhammānudhammappaṭipannā sāmīcippaṭipannā anudhammacāriniyo sakaṁ ācariyakaṁ uggahetvā ācikkhissanti desessanti paññapessanti paṭṭhapessanti vivarissanti vibhajissanti uttānīkarissanti uppannaṁ parappavādaṁ sahadhammena suniggahitaṁ niggahetvā sappāṭihāriyaṁ dhammaṁ desessantī’ti. ", "eng": "‘Wicked One, I shall not be fully extinguished until I have nun disciples who are competent, educated, assured, learned …’ … ", "html": "{}"}, "ud6.1:10.3": {"pli": "Etarahi kho pana, bhante, bhikkhuniyo bhagavato sāvikā viyattā vinītā visāradā bahussutā dhammadharā dhammānudhammappaṭipannā sāmīcippaṭipannā anudhammacāriniyo sakaṁ ācariyakaṁ uggahetvā ācikkhanti desenti paññapenti paṭṭhapenti vivaranti vibhajanti uttānīkaronti uppannaṁ parappavādaṁ sahadhammena suniggahitaṁ niggahetvā sappāṭihāriyaṁ dhammaṁ desenti. ", "eng": "Today you do have such nun disciples. ", "html": "{}"}, "ud6.1:10.4": {"pli": "Parinibbātu dāni, bhante, bhagavā; parinibbātu sugato; parinibbānakālo dāni, bhante, bhagavato. ", "html": "{}</p>"}, "ud6.1:11.1": {"pli": "Bhāsitā kho panesā, bhante, bhagavatā vācā: ", "html": "<p>{}"}, "ud6.1:11.2": {"pli": "‘na tāvāhaṁ, pāpima, parinibbāyissāmi yāva me upāsakā na sāvakā bhavissanti viyattā vinītā visāradā bahussutā dhammadharā dhammānudhammappaṭipannā sāmīcippaṭipannā anudhammacārino sakaṁ ācariyakaṁ uggahetvā ācikkhissanti desessanti paññapessanti paṭṭhapessanti vivarissanti vibhajissanti uttānīkarissanti uppannaṁ parappavādaṁ sahadhammena suniggahitaṁ niggahetvā sappāṭihāriyaṁ dhammaṁ desessantī’ti. ", "eng": "‘Wicked One, I shall not be fully extinguished until I have layman disciples who are competent, educated, assured, learned …’ ", "html": "{}"}, "ud6.1:11.3": {"pli": "Etarahi kho pana, bhante, upāsakā bhagavato sāvakā viyattā vinītā visāradā bahussutā dhammadharā dhammānudhammappaṭipannā sāmīcippaṭipannā anudhammacārino sakaṁ ācariyakaṁ uggahetvā ācikkhanti desenti paññapenti paṭṭhapenti vivaranti vibhajanti uttānīkaronti uppannaṁ parappavādaṁ sahadhammena suniggahitaṁ niggahetvā sappāṭihāriyaṁ dhammaṁ desenti. ", "eng": "Today you do have such layman disciples. ", "html": "{}"}, "ud6.1:11.4": {"pli": "Parinibbātu dāni, bhante, bhagavā; parinibbātu sugato; parinibbānakālo dāni, bhante, bhagavato. ", "html": "{}</p>"}, "ud6.1:12.1": {"pli": "Bhāsitā kho panesā, bhante, bhagavatā vācā: ", "html": "<p>{}"}, "ud6.1:12.2": {"pli": "‘na tāvāhaṁ, pāpima, parinibbāyissāmi yāva me upāsikā na sāvikā bhavissanti viyattā vinītā visāradā bahussutā dhammadharā dhammānudhammappaṭipannā sāmīcippaṭipannā anudhammacāriniyo sakaṁ ācariyakaṁ uggahetvā ācikkhissanti desessanti paññapessanti paṭṭhapessanti vivarissanti vibhajissanti uttānīkarissanti uppannaṁ parappavādaṁ sahadhammena suniggahitaṁ niggahetvā sappāṭihāriyaṁ dhammaṁ desessantī’ti. ", "eng": "‘Wicked One, I shall not be fully extinguished until I have laywoman disciples who are competent, educated, assured, learned …’ ", "html": "{}"}, "ud6.1:12.3": {"pli": "Etarahi kho pana, bhante, upāsikā bhagavato sāvikā viyattā vinītā visāradā bahussutā dhammadharā dhammānudhammappaṭipannā sāmīcippaṭipannā anudhammacāriniyo sakaṁ ācariyakaṁ uggahetvā ācikkhanti desenti paññapenti paṭṭhapenti vivaranti vibhajanti uttānīkaronti uppannaṁ parappavādaṁ sahadhammena suniggahitaṁ niggahetvā sappāṭihāriyaṁ dhammaṁ desenti. ", "eng": "Today you do have such laywoman disciples. ", "html": "{}"}, "ud6.1:12.4": {"pli": "Parinibbātu dāni, bhante, bhagavā; parinibbātu sugato; parinibbānakālo dāni, bhante, bhagavato. ", "eng": "May the Blessed One now be fully extinguished! May the Holy One now be fully extinguished! Now is the time for the full extinguishment of the Buddha. ", "html": "{}</p>"}, "ud6.1:13.1": {"pli": "Bhāsitā kho panesā, bhante, bhagavatā vācā: ", "eng": "Sir, you once made this statement: ", "html": "<p>{}"}, "ud6.1:13.2": {"pli": "‘na tāvāhaṁ, pāpima, parinibbāyissāmi yāva me idaṁ brahmacariyaṁ na iddhañceva bhavissati phītañca vitthārikaṁ bāhujaññaṁ puthubhūtaṁ yāva devamanussehi suppakāsitan’ti. ", "eng": "‘I shall not be fully extinguished until my spiritual path is successful and prosperous, extensive, popular, widespread, and well proclaimed wherever there are gods and humans.’ ", "html": "{}"}, "ud6.1:13.3": {"pli": "Etarahi kho pana, bhante, bhagavato brahmacariyaṁ iddhañceva phītañca vitthārikaṁ bāhujaññaṁ puthubhūtaṁ yāva devamanussehi suppakāsitaṁ. ", "eng": "Today your spiritual path is successful and prosperous, extensive, popular, widespread, and well proclaimed wherever there are gods and humans. ", "html": "{}"}, "ud6.1:13.4": {"pli": "Parinibbātu dāni, bhante, bhagavā; parinibbātu sugato; parinibbānakālo dāni, bhante, bhagavato”ti. ", "eng": "May the Blessed One now be fully extinguished! May the Holy One now be fully extinguished! Now is the time for the full extinguishment of the Buddha. ", "html": "{}</p>"}, "ud6.1:14.1": {"pli": "Evaṁ vutte, bhagavā māraṁ pāpimantaṁ etadavoca: ", "eng": "When this was said, the Buddha said to Māra, ", "html": "<p>{}"}, "ud6.1:14.2": {"pli": "“appossukko tvaṁ, pāpima, hohi. Na ciraṁ tathāgatassa parinibbānaṁ bhavissati. ", "eng": "“Relax, Wicked One. The full extinguishment of the Realized One will be soon. ", "html": "{}"}, "ud6.1:14.3": {"pli": "Ito tiṇṇaṁ māsānaṁ accayena tathāgato parinibbāyissatī”ti. ", "eng": "Three months from now the Realized One will be fully extinguished.” ", "html": "{}</p>"}, "ud6.1:15.1": {"pli": "Atha kho bhagavā cāpāle cetiye sato sampajāno āyusaṅkhāraṁ ossaji. ", "eng": "So at the Cāpāla Shrine the Buddha, mindful and aware, surrendered the life force. ", "html": "<p>{}"}, "ud6.1:15.2": {"pli": "Ossaṭṭhe ca bhagavatā āyusaṅkhāre mahābhūmicālo ahosi bhiṁsanako lomahaṁso, devadundubhiyo ca phaliṁsu. ", "eng": "When he did so there was a great earthquake, awe-inspiring and hair-raising, and thunder cracked the sky. ", "html": "{}</p>"}, "ud6.1:16.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud6.1:17.1": {"pli": "“Tulamatulañca sambhavaṁ, ", "eng": "“Comparing the incomparable <j>with the creation of prolonged life, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>", "comm": "For this difficult verse, I generally follow Bhikkhu Bodhi’s long discussion in <em>Connected Discourses of the Buddha</em>, note 255 on the Mahāvagga. <i lang='pi' translate='no'>Sambhava</i> means “production, creation”, and here I think it refers to the prolonging of life which the Buddha had just rejected. "}, "ud6.1:17.2": {"pli": "Bhavasaṅkhāramavassaji muni; ", "eng": "the sage surrendered the life force. ", "html": "<span class='verse-line'>{}</span>", "comm": "<i lang='pi' translate='no'>Bhavasaṅkhāra</i> here is equivalent to <i lang='pi' translate='no'>āyusaṅkhāra</i> and <i lang='pi' translate='no'>jīvitasaṅkhāra</i>. "}, "ud6.1:17.3": {"pli": "Ajjhattarato samāhito, ", "eng": "Happy inside, serene, ", "html": "<span class='verse-line'>{}</span>"}, "ud6.1:17.4": {"pli": "Abhindi kavacamivattasambhavan”ti. ", "eng": "he shattered self-creation like a suit of armor.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>", "comm": "<i lang='pi' translate='no'>Attasambhava</i> here refers back to <i lang='pi' translate='no'>sambhava</i> in the first line. "}, "ud6.1:17.5": {"pli": "Paṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud6.2": {"ud6.2:0.1": {"pli": "Udāna 6.2 ", "eng": "Heartfelt Sayings 6.2 ", "html": "<article id='ud6.2'><header><ul><li class='division'>{}</li></ul>"}, "ud6.2:0.2": {"pli": "Sattajaṭilasutta ", "eng": "Seven Matted-Hair Ascetics ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud6.2:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud6.2:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati pubbārāme migāramātupāsāde. ", "eng": "At one time the Buddha was staying near Sāvatthī in the stilt longhouse of Migāra’s mother in the Eastern Monastery. ", "html": "{}", "comm": "The same narrative introduces different verses at <a href='index.html?q=sn3.11'>SN 3.11</a>. "}, "ud6.2:1.3": {"pli": "Tena kho pana samayena bhagavā sāyanhasamayaṁ paṭisallānā vuṭṭhito bahidvārakoṭṭhake nisinno hoti. ", "eng": "Then in the late afternoon, the Buddha came out of retreat and sat outside the gate. ", "html": "{}"}, "ud6.2:1.4": {"pli": "Atha kho rājā pasenadi kosalo yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. ", "eng": "Then King Pasenadi of Kosala went up to the Buddha, bowed, and sat down to one side. ", "html": "{}</p>"}, "ud6.2:2.1": {"pli": "Tena kho pana samayena satta ca jaṭilā, satta ca nigaṇṭhā, satta ca acelakā, satta ca ekasāṭakā, satta ca paribbājakā, parūḷhakacchanakhalomā khārivividhamādāya bhagavato avidūre atikkamanti. ", "eng": "Now at that time seven matted-hair ascetics, seven Jain ascetics, seven naked ascetics, seven one-cloth ascetics, and seven wanderers passed by not far from the Buddha. Their armpits and bodies were hairy, and their nails were long; and they carried their pack with shoulder-poles. ", "html": "<p>{}</p>", "comm": "“Matted-hair ascetics” (<i lang='pi' translate='no'>jaṭilā</i>) were Vedic ascetics practicing bathing and worshiping the sacred flame (<a href='index.html?q=ud1.9'>Ud 1.9</a>; see note on <a href='index.html?q=mn40#5.10'>MN 40:5.10</a>). | “Jain ascetics” (<i lang='pi' translate='no'>nigaṇṭhā</i>) were followers of Mahāvīra. | The next two types might be the two main subsets of Jains. Some Jains were “one-cloth ascetics” (see <a href='index.html?q=an6.57#4.1'>AN 6.57:4.1</a>); this group came to be known as Śvetāmbara. Others were “naked ascetics” (<i lang='pi' translate='no'>acelakā</i>; see Ācārāṅga Sūtra 1.6.2, 1.7.7; Uttarādhyayana Sūtra 2); these came to be known as Digambara. On the other hand, Ājīvakas may be naked. | “Wanderers (<i lang='pi' translate='no'>paribbājakā</i>) were homeless religious renunciates—male or female—who wandered seeking alms (see note on <a href='index.html?q=dn1#1.1.3'>DN 1:1.1.3</a>). | For <i lang='pi' translate='no'>khāri</i> (“pack”) see also <a href='index.html?q=sn7.9#14.1'>SN 7.9:14.1</a>. "}, "ud6.2:3.1": {"pli": "Addasā kho rājā pasenadi kosalo te satta ca jaṭile, satta ca nigaṇṭhe, satta ca acelake, satta ca ekasāṭake, satta ca paribbājake, parūḷhakacchanakhalome khārivividhamādāya bhagavato avidūre atikkamante. ", "eng": "King Pasenadi saw them passing by. ", "html": "<p>{}"}, "ud6.2:3.2": {"pli": "Disvāna uṭṭhāyāsanā ekaṁsaṁ uttarāsaṅgaṁ karitvā dakkhiṇajāṇumaṇḍalaṁ pathaviyaṁ nihantvā yena te satta ca jaṭilā, satta ca nigaṇṭhā, satta ca acelakā, satta ca ekasāṭakā, satta ca paribbājakā, tenañjaliṁ paṇāmetvā tikkhattuṁ nāmaṁ sāvesi: ", "eng": "He got up from his seat, arranged his robe over one shoulder, knelt with his right knee on the ground, raised his joined palms toward those various ascetics, and pronounced his name three times: ", "html": "{}"}, "ud6.2:3.3": {"pli": "“rājāhaṁ, bhante, pasenadi kosalo; ", "eng": "“Sirs, I am Pasenadi, king of Kosala! ", "html": "{}"}, "ud6.2:3.4": {"pli": "rājāhaṁ, bhante, pasenadi kosalo; ", "eng": "I am Pasenadi, king of Kosala! ", "html": "{}"}, "ud6.2:3.5": {"pli": "rājāhaṁ, bhante, pasenadi kosalo”ti. ", "eng": "I am Pasenadi, king of Kosala!” ", "html": "{}</p>"}, "ud6.2:4.1": {"pli": "Atha kho rājā pasenadi kosalo acirapakkantesu tesu sattasu ca jaṭilesu, sattasu ca nigaṇṭhesu, sattasu ca acelakesu, sattasu ca ekasāṭakesu, sattasu ca paribbājakesu, yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. Ekamantaṁ nisinno kho rājā pasenadi kosalo bhagavantaṁ etadavoca: ", "eng": "Then, soon after those ascetics had left, King Pasenadi went up to the Buddha, bowed, sat down to one side, and said to him, ", "html": "<p>{}"}, "ud6.2:4.2": {"pli": "“ye kho, bhante, loke arahanto vā arahattamaggaṁ vā samāpannā ete tesaṁ aññatare”ti. ", "eng": "“Sir, these are among those in the world who are perfected or who are on the path to perfection!” ", "html": "{}</p>", "comm": "This sentence is phrased as a statement in all editions here, and in the parallel passage at <a href='index.html?q=ud6.2#4.2'>Ud 6.2:4.2</a> all except the PTS edition, where it is a question. The commentary in both places confirms it is meant as a statement (<i lang='pi' translate='no'>“ime arahanto”ti vutte</i>). Pasenadi is testing the Buddha. "}, "ud6.2:5.1": {"pli": "“Dujjānaṁ kho etaṁ, mahārāja, tayā gihinā kāmabhoginā puttasambādhasayanaṁ ajjhāvasantena kāsikacandanaṁ paccanubhontena mālāgandhavilepanaṁ dhārayantena jātarūparajataṁ sādiyantena—ime vā arahanto, ime vā arahattamaggaṁ samāpannāti. ", "eng": "“Great king, as a layman enjoying sensual pleasures, living at home with your children, using sandalwood imported from Kāsi, wearing garlands, fragrance, and makeup, and accepting gold and currency, it’s hard for you to know who is perfected or on the path to perfection. ", "html": "<p>{}</p>"}, "ud6.2:6.1": {"pli": "Saṁvāsena kho, mahārāja, sīlaṁ veditabbaṁ. Tañca kho dīghena addhunā na ittaraṁ, manasikarotā no amanasikarotā, paññavatā no duppaññena. ", "eng": "You can get to know a person’s ethics by living with them. But only after a long time, not casually; only when attentive, not when inattentive; and only by the wise, not the witless. ", "html": "<p>{}"}, "ud6.2:6.2": {"pli": "Saṁvohārena kho, mahārāja, soceyyaṁ veditabbaṁ. Tañca kho dīghena addhunā na ittaraṁ, manasikarotā no amanasikarotā, paññavatā no duppaññena. ", "eng": "You can get to know a person’s purity by dealing with them. … ", "html": "{}"}, "ud6.2:6.3": {"pli": "Āpadāsu kho, mahārāja, thāmo veditabbo. So ca kho dīghena addhunā na ittaraṁ, manasikarotā no amanasikarotā, paññavatā no duppaññena. ", "eng": "You can get to know a person’s resilience in times of trouble. … ", "html": "{}"}, "ud6.2:6.4": {"pli": "Sākacchāya kho, mahārāja, paññā veditabbā. Sā ca kho dīghena addhunā na ittaraṁ, manasikarotā no amanasikarotā, paññavatā no duppaññenā”ti. ", "eng": "You can get to know a person’s wisdom by discussion. But only after a long time, not casually; only when attentive, not when inattentive; and only by the wise, not the witless.” ", "html": "{}</p>"}, "ud6.2:7.1": {"pli": "“Acchariyaṁ, bhante, abbhutaṁ, bhante. ", "eng": "“It’s incredible, sir, it’s amazing, ", "html": "<p>{}"}, "ud6.2:7.2": {"pli": "Yāva subhāsitañcidaṁ, bhante, bhagavatā: ", "eng": "how well said this was by the Buddha. … ", "html": "{}"}, "ud6.2:7.3": {"pli": "‘dujjānaṁ kho etaṁ, mahārāja, tayā gihinā puttasambādhasayanaṁ ajjhāvasantena kāsikacandanaṁ paccanubhontena mālāgandhavilepanaṁ dhārayantena jātarūparajataṁ sādiyantena—ime vā arahanto, ime vā arahattamaggaṁ samāpannāti. ", "html": "{}"}, "ud6.2:7.4": {"pli": "Saṁvāsena kho, mahārāja, sīlaṁ veditabbaṁ …pe… ", "html": "{}"}, "ud6.2:7.5": {"pli": "sākacchāya kho, mahārāja, paññā veditabbā. ", "html": "{}"}, "ud6.2:7.6": {"pli": "Sā ca kho dīghena addhunā na ittaraṁ, manasikarotā no amanasikarotā, paññavatā no duppaññenā’”ti. ", "html": "{}</p>"}, "ud6.2:8.1": {"pli": "“Ete, bhante, mama purisā corā ocarakā janapadaṁ ocaritvā gacchanti. ", "eng": "Sir, these are my spies, my undercover agents returning after spying on the country. ", "html": "<p>{}", "comm": "The use of ascetics for spies, which is still common today, is detailed by Kauṭilya in his Arthaśāstra. Those seeking a job can imitate bald (<i lang='pi' translate='no'>muṇḍa</i>) or matted-hair (<i lang='pi' translate='no'>jaṭila</i>) ascetics, pretending to live on a handful of greens but secretly eating what they like (as here). Other fake ascetics or spies disguised as merchants then acclaim them to build reputation (as Pasenadi here). When people seek prophecies, he foretells that those loyal to the king shall prosper and those who oppose him will suffer. The king’s minister then ensures that such prophecies come true (1.11.13, 1.13.15). Female mendicants are especially useful for manipulating the wives of officials (5.1.19, 5.1.50, 12.2.20), or officials regarding their wives (11.1.52). Ascetic spies are taught to manipulate loyalties with morality tales (1.14) and to fool enemy kings by faking magical powers (13.2; cp. Devadatta using magic to seduce Ajātasattu at <a href='index.html?q=pli-tv-kd17#2.1.1'>Kd 17:2.1.1</a>). They conduct assassinations by tricking targets into consuming poisons in the guise of a ritual (5.1.33) or love-potions (11.1.40), or he may first instigate rape, then accuse the rapist (11.1.48). Princes may also don the guise of such ascetics in order to escape a coup, while forest ascetics misdirect pursuers (7.17.50). Such spies earn a solid middle income comparable to soldiers, superintendents, teachers, or accountants (5.3.22–3). "}, "ud6.2:8.2": {"pli": "Tehi paṭhamaṁ ociṇṇaṁ ahaṁ pacchā osārissāmi. ", "eng": "I shall later make use of what they first spied out. ", "html": "{}", "comm": "Readings for <i lang='pi' translate='no'>osāpayissāmi</i> are highly uncertain, and the sentence may be corrupt. Udāna commentary glosses <i lang='pi' translate='no'>paṭipajjissāmi karissāmi</i>, which suggests Sanskrit <i lang='pi' translate='no'>apāśri</i>, “employ, practice, use”. "}, "ud6.2:8.3": {"pli": "Idāni te, bhante, taṁ rajojallaṁ pavāhetvā sunhātā suvilittā kappitakesamassū odātavatthavasanā pañcahi kāmaguṇehi samappitā samaṅgibhūtā paricāressantī”ti. ", "eng": "Now—when they have rinsed off the dust and dirt, and are nicely bathed and anointed, with hair and beard dressed, and dressed in white—they will amuse themselves, supplied and provided with the five kinds of sensual stimulation.” ", "html": "{}</p>"}, "ud6.2:9.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud6.2:10.1": {"pli": "“Na vāyameyya sabbattha, ", "eng": "“Don’t strive in every situation, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud6.2:10.2": {"pli": "nāññassa puriso siyā; ", "eng": "don’t become another’s man. ", "html": "<span class='verse-line'>{}</span>"}, "ud6.2:10.3": {"pli": "Nāññaṁ nissāya jīveyya, ", "eng": "Don’t live depending on another, ", "html": "<span class='verse-line'>{}</span>"}, "ud6.2:10.4": {"pli": "dhammena na vaṇiṁ care”ti. ", "eng": "and don’t use the teaching to make money.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>", "comm": "The commentary says, “Don’t teach the Dhamma for the sake of money, etc.” "}, "ud6.2:10.5": {"pli": "Dutiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud6.3": {"ud6.3:0.1": {"pli": "Udāna 6.3 ", "eng": "Heartfelt Sayings 6.3 ", "html": "<article id='ud6.3'><header><ul><li class='division'>{}</li></ul>"}, "ud6.3:0.2": {"pli": "Paccavekkhaṇasutta ", "eng": "The Buddha’s Reviewing ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud6.3:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud6.3:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud6.3:1.3": {"pli": "Tena kho pana samayena bhagavā attano aneke pāpake akusale dhamme pahīne paccavekkhamāno nisinno hoti, aneke ca kusale dhamme bhāvanāpāripūriṁ gate. ", "eng": "Now at that time the Buddha was sitting reviewing his own giving up of many bad, unskillful qualities, and the many skillful qualities he had fully developed. ", "html": "{}</p>"}, "ud6.3:2.1": {"pli": "Atha kho bhagavā attano aneke pāpake akusale dhamme pahīne viditvā aneke ca kusale dhamme bhāvanāpāripūriṁ gate tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, knowing the many bad, unskillful qualities that he had given up and the many skillful qualities he had fully developed, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud6.3:3.1": {"pli": "“Ahu pubbe tadā nāhu, ", "eng": "“What was before then was not; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud6.3:3.2": {"pli": "nāhu pubbe tadā ahu; ", "eng": "what before was not then was. ", "html": "<span class='verse-line'>{}</span>"}, "ud6.3:3.3": {"pli": "Na cāhu na ca bhavissati, ", "eng": "It never was, nor will it be, ", "html": "<span class='verse-line'>{}</span>"}, "ud6.3:3.4": {"pli": "na cetarahi vijjatī”ti. ", "eng": "nor is it found today.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud6.3:3.5": {"pli": "Tatiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud6.4": {"ud6.4:0.1": {"pli": "Udāna 6.4 ", "eng": "Heartfelt Sayings 6.4 ", "html": "<article id='ud6.4'><header><ul><li class='division'>{}</li></ul>"}, "ud6.4:0.2": {"pli": "Paṭhamanānātitthiyasutta ", "eng": "Followers of Various Other Religions (1st) ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud6.4:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud6.4:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud6.4:1.3": {"pli": "Tena kho pana samayena sambahulā nānātitthiyasamaṇabrāhmaṇaparibbājakā sāvatthiyaṁ paṭivasanti nānādiṭṭhikā nānākhantikā nānārucikā nānādiṭṭhinissayanissitā. ", "eng": "Now at that time several ascetics, brahmins, and wanderers who followed various other religions were residing in Sāvatthī, holding different views, beliefs, and opinions, relying on different views. ", "html": "{}</p>"}, "ud6.4:2.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "eng": "There were some ascetics and brahmins who had this doctrine and view: ", "html": "<p>{}"}, "ud6.4:2.2": {"pli": "“sassato loko, idameva saccaṁ moghamaññan”ti. ", "eng": "“The cosmos is eternal. This is the only truth, anything else is futile.” ", "html": "{}</p>"}, "ud6.4:3.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "eng": "Others held views such as the following, each regarding their own view as true and others as silly. ", "html": "<p>{}"}, "ud6.4:3.2": {"pli": "“asassato loko, idameva saccaṁ moghamaññan”ti. ", "eng": "“The cosmos is not eternal.” ", "html": "{}</p>"}, "ud6.4:4.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.4:4.2": {"pli": "“antavā loko, idameva saccaṁ moghamaññan”ti. ", "eng": "“The cosmos is finite.” ", "html": "{}</p>"}, "ud6.4:5.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.4:5.2": {"pli": "“anantavā loko, idameva saccaṁ moghamaññan”ti. ", "eng": "“The cosmos is infinite.” ", "html": "{}</p>"}, "ud6.4:6.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.4:6.2": {"pli": "“taṁ jīvaṁ taṁ sarīraṁ, idameva saccaṁ moghamaññan”ti. ", "eng": "“The soul and the body are one and the same.” ", "html": "{}</p>"}, "ud6.4:7.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.4:7.2": {"pli": "“aññaṁ jīvaṁ aññaṁ sarīraṁ, idameva saccaṁ moghamaññan”ti. ", "eng": "“The soul is one thing, the body another.” ", "html": "{}</p>"}, "ud6.4:8.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.4:8.2": {"pli": "“hoti tathāgato paraṁ maraṇā, idameva saccaṁ moghamaññan”ti. ", "eng": "“A realized one still exists after death.” ", "html": "{}</p>"}, "ud6.4:9.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.4:9.2": {"pli": "“na hoti tathāgato paraṁ maraṇā, idameva saccaṁ moghamaññan”ti. ", "eng": "“A realized one no longer exists after death.” ", "html": "{}</p>"}, "ud6.4:10.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.4:10.2": {"pli": "“hoti ca na ca hoti tathāgato paraṁ maraṇā, idameva saccaṁ moghamaññan”ti. ", "eng": "“A realized one both still exists and no longer exists after death.” ", "html": "{}</p>"}, "ud6.4:11.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.4:11.2": {"pli": "“neva hoti na na hoti tathāgato paraṁ maraṇā, idameva saccaṁ moghamaññan”ti. ", "eng": "“A realized one neither still exists nor no longer exists after death.” ", "html": "{}</p>"}, "ud6.4:12.1": {"pli": "Te bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṁ mukhasattīhi vitudantā viharanti: ", "eng": "They were fighting, quarreling, and disputing, continually wounding each other with barbed words: ", "html": "<p>{}"}, "ud6.4:12.2": {"pli": "“ediso dhammo, nediso dhammo; ", "eng": "“Such is Truth, such is not Truth! ", "html": "{}"}, "ud6.4:12.3": {"pli": "nediso dhammo, ediso dhammo”ti. ", "eng": "Such is not Truth, such is Truth!” ", "html": "{}</p>"}, "ud6.4:13.1": {"pli": "Atha kho sambahulā bhikkhū pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya sāvatthiṁ piṇḍāya pāvisiṁsu. ", "eng": "Then several mendicants robed up in the morning and, taking their bowls and robes, entered Sāvatthī for alms. ", "html": "<p>{}"}, "ud6.4:13.2": {"pli": "Sāvatthiyaṁ piṇḍāya caritvā pacchābhattaṁ piṇḍapātapaṭikkantā yena bhagavā tenupasaṅkamiṁsu; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdiṁsu. ", "eng": "Then, after the meal, when they returned from almsround, they went up to the Buddha, bowed, sat down to one side, and told him what was happening. The Buddha said: ", "html": "{}"}, "ud6.4:13.3": {"pli": "Ekamantaṁ nisinnā kho te bhikkhū bhagavantaṁ etadavocuṁ: ", "html": "{}</p>"}, "ud6.4:14.1": {"pli": "“Idha, bhante, sambahulā nānātitthiyasamaṇabrāhmaṇaparibbājakā sāvatthiyaṁ paṭivasanti nānādiṭṭhikā nānākhantikā nānārucikā nānādiṭṭhinissayanissitā—", "html": "<p>{}</p>"}, "ud6.4:15.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.4:15.2": {"pli": "‘sassato loko, idameva saccaṁ moghamaññan’ti …pe… ", "html": "{}"}, "ud6.4:15.3": {"pli": "te bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṁ mukhasattīhi vitudantā viharanti: ", "html": "{}"}, "ud6.4:15.4": {"pli": "‘ediso dhammo, nediso dhammo; ", "html": "{}"}, "ud6.4:15.5": {"pli": "nediso dhammo, ediso dhammo’”ti. ", "html": "{}</p>"}, "ud6.4:16.1": {"pli": "“Aññatitthiyā, bhikkhave, paribbājakā andhā acakkhukā; ", "eng": "“The wanderers of other religions are blind and sightless. ", "html": "<p>{}"}, "ud6.4:16.2": {"pli": "atthaṁ na jānanti, anatthaṁ na jānanti, dhammaṁ na jānanti, adhammaṁ na jānanti. ", "eng": "They don’t understand what is beneficial or what is not beneficial, nor what is the truth and what is not the truth. ", "html": "{}"}, "ud6.4:16.3": {"pli": "Te atthaṁ ajānantā anatthaṁ ajānantā dhammaṁ ajānantā adhammaṁ ajānantā bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṁ mukhasattīhi vitudantā viharanti: ", "eng": "That’s why they are fighting, quarreling, and disputing, continually wounding each other with barbed words. ", "html": "{}"}, "ud6.4:16.4": {"pli": "‘ediso dhammo, nediso dhammo; ", "html": "{}"}, "ud6.4:16.5": {"pli": "nediso dhammo, ediso dhammo’ti. ", "html": "{}</p>"}, "ud6.4:17.1": {"pli": "Bhūtapubbaṁ, bhikkhave, imissāyeva sāvatthiyā aññataro rājā ahosi. ", "eng": "Once upon a time, mendicants, right here in Sāvatthī there was a certain king. ", "html": "<p>{}"}, "ud6.4:17.2": {"pli": "Atha kho, bhikkhave, so rājā aññataraṁ purisaṁ āmantesi: ", "eng": "Then the king addressed a man, ", "html": "{}"}, "ud6.4:17.3": {"pli": "‘ehi tvaṁ, ambho purisa, yāvatakā sāvatthiyā jaccandhā te sabbe ekajjhaṁ sannipātehī’ti. ", "eng": "‘Please, worthy man, gather all those blind from birth throughout Sāvatthī and bring them together in one place.’ ", "html": "{}"}, "ud6.4:17.4": {"pli": "‘Evaṁ, devā’ti kho, bhikkhave, so puriso tassa rañño paṭissutvā yāvatakā sāvatthiyā jaccandhā te sabbe gahetvā yena so rājā tenupasaṅkami; upasaṅkamitvā taṁ rājānaṁ etadavoca: ", "eng": "‘Yes, Your Majesty,’ that man replied. He did as the king asked, then said to him, ", "html": "{}"}, "ud6.4:17.5": {"pli": "‘sannipātitā kho te, deva, yāvatakā sāvatthiyā jaccandhā’ti. ", "eng": "‘Your Majesty, the blind people throughout Sāvatthī have been gathered.’ ", "html": "{}"}, "ud6.4:17.6": {"pli": "‘Tena hi, bhaṇe, jaccandhānaṁ hatthiṁ dassehī’ti. ", "eng": "‘Well then, my man, show them an elephant.’ ", "html": "{}"}, "ud6.4:17.7": {"pli": "‘Evaṁ, devā’ti kho, bhikkhave, so puriso tassa rañño paṭissutvā jaccandhānaṁ hatthiṁ dassesi—", "eng": "‘Yes, Your Majesty,’ that man replied. He did as the king asked. ", "html": "{}</p>"}, "ud6.4:18.1": {"pli": "Ekaccānaṁ jaccandhānaṁ hatthissa sīsaṁ dassesi: ", "eng": "To some of the blind people he showed the elephant’s head, saying, ", "html": "<p>{}"}, "ud6.4:18.2": {"pli": "‘ediso, jaccandhā, hatthī’ti. ", "eng": "‘Here is the elephant.’ ", "html": "{}"}, "ud6.4:18.3": {"pli": "Ekaccānaṁ jaccandhānaṁ hatthissa kaṇṇaṁ dassesi: ", "eng": "To some of them he showed the elephant’s ear, saying, ", "html": "{}"}, "ud6.4:18.4": {"pli": "‘ediso, jaccandhā, hatthī’ti. ", "eng": "‘Here is the elephant.’ ", "html": "{}"}, "ud6.4:18.5": {"pli": "Ekaccānaṁ jaccandhānaṁ hatthissa dantaṁ dassesi: ", "eng": "To some of them he showed the elephant’s tusk, saying, ", "html": "{}"}, "ud6.4:18.6": {"pli": "‘ediso, jaccandhā, hatthī’ti. ", "eng": "‘Here is the elephant.’ ", "html": "{}"}, "ud6.4:18.7": {"pli": "Ekaccānaṁ jaccandhānaṁ hatthissa soṇḍaṁ dassesi: ", "eng": "To some of them he showed the elephant’s trunk, saying, ", "html": "{}"}, "ud6.4:18.8": {"pli": "‘ediso, jaccandhā, hatthī’ti. ", "eng": "‘Here is the elephant.’ ", "html": "{}"}, "ud6.4:18.9": {"pli": "Ekaccānaṁ jaccandhānaṁ hatthissa kāyaṁ dassesi: ", "eng": "To some of them he showed the elephant’s flank, saying, ", "html": "{}"}, "ud6.4:18.10": {"pli": "‘ediso, jaccandhā, hatthī’ti. ", "eng": "‘Here is the elephant.’ ", "html": "{}"}, "ud6.4:18.11": {"pli": "Ekaccānaṁ jaccandhānaṁ hatthissa pādaṁ dassesi: ", "eng": "To some of them he showed the elephant’s leg, saying, ", "html": "{}"}, "ud6.4:18.12": {"pli": "‘ediso, jaccandhā, hatthī’ti. ", "eng": "‘Here is the elephant.’ ", "html": "{}"}, "ud6.4:18.13": {"pli": "Ekaccānaṁ jaccandhānaṁ hatthissa satthiṁ dassesi: ", "eng": "To some of them he showed the elephant’s thigh, saying, ", "html": "{}"}, "ud6.4:18.14": {"pli": "‘ediso, jaccandhā, hatthī’ti. ", "eng": "‘Here is the elephant.’ ", "html": "{}"}, "ud6.4:18.15": {"pli": "Ekaccānaṁ jaccandhānaṁ hatthissa naṅguṭṭhaṁ dassesi: ", "eng": "To some of them he showed the elephant’s tail, saying, ", "html": "{}"}, "ud6.4:18.16": {"pli": "‘ediso, jaccandhā, hatthī’ti. ", "eng": "‘Here is the elephant.’ ", "html": "{}"}, "ud6.4:18.17": {"pli": "Ekaccānaṁ jaccandhānaṁ hatthissa vāladhiṁ dassesi: ", "eng": "To some of them he showed the tip of the elephant’s tail, saying, ", "html": "{}"}, "ud6.4:18.18": {"pli": "‘ediso, jaccandhā, hatthī’ti. ", "eng": "‘Here is the elephant.’ ", "html": "{}</p>"}, "ud6.4:19.1": {"pli": "Atha kho, bhikkhave, so puriso jaccandhānaṁ hatthiṁ dassetvā yena so rājā tenupasaṅkami; upasaṅkamitvā taṁ rājānaṁ etadavoca: ", "eng": "Then he approached the king and said, ", "html": "<p>{}"}, "ud6.4:19.2": {"pli": "‘diṭṭho kho tehi, deva, jaccandhehi hatthī; ", "eng": "‘Your Majesty, the blind people have been shown the elephant. ", "html": "{}"}, "ud6.4:19.3": {"pli": "yassadāni kālaṁ maññasī’ti. ", "eng": "Please go at your convenience.’ ", "html": "{}</p>"}, "ud6.4:20.1": {"pli": "Atha kho, bhikkhave, so rājā yena te jaccandhā tenupasaṅkami; upasaṅkamitvā te jaccandhe etadavoca: ", "eng": "Then the king went up to the blind people and said, ", "html": "<p>{}"}, "ud6.4:20.2": {"pli": "‘diṭṭho vo, jaccandhā, hatthī’ti? ", "eng": "‘Have you seen the elephant?’ ", "html": "{}"}, "ud6.4:20.3": {"pli": "‘Evaṁ, deva, diṭṭho no hatthī’ti. ", "eng": "‘Yes, Your Majesty, we have been shown the elephant.’ ", "html": "{}"}, "ud6.4:20.4": {"pli": "‘Vadetha, jaccandhā, kīdiso hatthī’ti? ", "eng": "‘Then tell us, what kind of thing is an elephant?’ ", "html": "{}</p>"}, "ud6.4:21.1": {"pli": "Yehi, bhikkhave, jaccandhehi hatthissa sīsaṁ diṭṭhaṁ ahosi, te evamāhaṁsu: ", "eng": "The blind people who had been shown the elephant’s head said, ", "html": "<p>{}"}, "ud6.4:21.2": {"pli": "‘ediso, deva, hatthī seyyathāpi kumbho’ti. ", "eng": "‘Your Majesty, an elephant is like a pot.’ ", "html": "{}</p>"}, "ud6.4:22.1": {"pli": "Yehi, bhikkhave, jaccandhehi hatthissa kaṇṇo diṭṭho ahosi, te evamāhaṁsu: ", "eng": "Those who had been shown the ear said, ", "html": "<p>{}"}, "ud6.4:22.2": {"pli": "‘ediso, deva, hatthī seyyathāpi suppo’ti. ", "eng": "‘An elephant is like a winnowing fan.’ ", "html": "{}</p>"}, "ud6.4:23.1": {"pli": "Yehi, bhikkhave, jaccandhehi hatthissa danto diṭṭho ahosi, te evamāhaṁsu: ", "eng": "Those who had been shown the tusk said, ", "html": "<p>{}"}, "ud6.4:23.2": {"pli": "‘ediso, deva, hatthī seyyathāpi khīlo’ti. ", "eng": "‘An elephant is like a ploughshare.’ ", "html": "{}</p>"}, "ud6.4:24.1": {"pli": "Yehi, bhikkhave, jaccandhehi hatthissa soṇḍo diṭṭho ahosi, te evamāhaṁsu: ", "eng": "Those who had been shown the trunk said, ", "html": "<p>{}"}, "ud6.4:24.2": {"pli": "‘ediso, deva, hatthī seyyathāpi naṅgalīsā’ti. ", "eng": "‘An elephant is like a plough-pole.’ ", "html": "{}</p>"}, "ud6.4:25.1": {"pli": "Yehi, bhikkhave, jaccandhehi hatthissa kāyo diṭṭho ahosi, te evamāhaṁsu: ", "eng": "Those who had been shown the flank said, ", "html": "<p>{}"}, "ud6.4:25.2": {"pli": "‘ediso, deva, hatthī seyyathāpi koṭṭho’ti. ", "eng": "‘An elephant is like a storehouse.’ ", "html": "{}</p>"}, "ud6.4:26.1": {"pli": "Yehi, bhikkhave, jaccandhehi hatthissa pādo diṭṭho ahosi, te evamāhaṁsu: ", "eng": "Those who had been shown the leg said, ", "html": "<p>{}"}, "ud6.4:26.2": {"pli": "‘ediso, deva, hatthī seyyathāpi thūṇo’ti. ", "eng": "‘An elephant is like a pillar.’ ", "html": "{}</p>"}, "ud6.4:27.1": {"pli": "Yehi, bhikkhave, jaccandhehi hatthissa satthi diṭṭho ahosi, te evamāhaṁsu: ", "eng": "Those who had been shown the thigh said, ", "html": "<p>{}"}, "ud6.4:27.2": {"pli": "‘ediso, deva, hatthī seyyathāpi udukkhalo’ti. ", "eng": "‘An elephant is like a mortar.’ ", "html": "{}</p>"}, "ud6.4:28.1": {"pli": "Yehi, bhikkhave, jaccandhehi hatthissa naṅguṭṭhaṁ diṭṭhaṁ ahosi, te evamāhaṁsu: ", "eng": "Those who had been shown the tail said, ", "html": "<p>{}"}, "ud6.4:28.2": {"pli": "‘ediso, deva, hatthī seyyathāpi musalo’ti. ", "eng": "‘An elephant is like a pestle.’ ", "html": "{}</p>"}, "ud6.4:29.1": {"pli": "Yehi, bhikkhave, jaccandhehi hatthissa vāladhi diṭṭho ahosi, te evamāhaṁsu: ", "eng": "Those who had been shown the tip of the tail said, ", "html": "<p>{}"}, "ud6.4:29.2": {"pli": "‘ediso, deva, hatthī seyyathāpi sammajjanī’ti. ", "eng": "‘An elephant is like a broom.’ ", "html": "{}</p>"}, "ud6.4:30.1": {"pli": "Te ‘ediso hatthī, nediso hatthī; ", "eng": "Saying, ‘Such is an elephant, not such! ", "html": "<p>{}"}, "ud6.4:30.2": {"pli": "nediso hatthī, ediso hatthī’ti aññamaññaṁ muṭṭhīhi saṁsumbhiṁsu. ", "eng": "Such is not an elephant, such is!’ they punched each other with their fists. ", "html": "{}"}, "ud6.4:30.3": {"pli": "Tena ca pana, bhikkhave, so rājā attamano ahosi. ", "eng": "At that, the king was pleased. ", "html": "{}</p>"}, "ud6.4:31.1": {"pli": "Evamevaṁ kho, bhikkhave, aññatitthiyā paribbājakā andhā acakkhukā. ", "eng": "In the same way, mendicants, the wanderers of other religions are blind and sightless. ", "html": "<p>{}"}, "ud6.4:31.2": {"pli": "Te atthaṁ na jānanti anatthaṁ na jānanti, dhammaṁ na jānanti adhammaṁ na jānanti. ", "eng": "They don’t understand what is beneficial or what is not beneficial, nor what is the truth and what is not the truth. ", "html": "{}"}, "ud6.4:31.3": {"pli": "Te atthaṁ ajānantā anatthaṁ ajānantā, dhammaṁ ajānantā adhammaṁ ajānantā bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṁ mukhasattīhi vitudantā viharanti: ", "eng": "That’s why they are fighting, quarreling, and disputing, continually wounding each other with barbed words. ", "html": "{}"}, "ud6.4:31.4": {"pli": "‘ediso dhammo, nediso dhammo; ", "eng": "‘Such is Truth, such is not! ", "html": "{}"}, "ud6.4:31.5": {"pli": "nediso dhammo, ediso dhammo’”ti. ", "eng": "Such is not Truth, such is!’” ", "html": "{}</p>"}, "ud6.4:32.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud6.4:33.1": {"pli": "“Imesu kira sajjanti, ", "eng": "“Some ascetics and brahmins, it seems, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud6.4:33.2": {"pli": "eke samaṇabrāhmaṇā; ", "eng": "cling to these things. ", "html": "<span class='verse-line'>{}</span>"}, "ud6.4:33.3": {"pli": "Viggayha naṁ vivadanti, ", "eng": "Arguing, they dispute, ", "html": "<span class='verse-line'>{}</span>"}, "ud6.4:33.4": {"pli": "janā ekaṅgadassino”ti. ", "eng": "the folk who see just one part.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud6.4:33.5": {"pli": "Catutthaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud6.5": {"ud6.5:0.1": {"pli": "Udāna 6.5 ", "eng": "Heartfelt Sayings 6.5 ", "html": "<article id='ud6.5'><header><ul><li class='division'>{}</li></ul>"}, "ud6.5:0.2": {"pli": "Dutiyanānātitthiyasutta ", "eng": "Followers of Various Other Religions (2nd) ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud6.5:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud6.5:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud6.5:1.3": {"pli": "Tena kho pana samayena sambahulā nānātitthiyasamaṇabrāhmaṇaparibbājakā sāvatthiyaṁ paṭivasanti nānādiṭṭhikā nānākhantikā nānārucikā nānādiṭṭhinissayanissitā. ", "eng": "Now at that time several ascetics, brahmins, and wanderers who followed various other religions were residing in Sāvatthī, holding different views, beliefs, and opinions, relying on different views. ", "html": "{}</p>"}, "ud6.5:2.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "eng": "There were some ascetics and brahmins who had this doctrine and view: ", "html": "<p>{}"}, "ud6.5:2.2": {"pli": "“sassato attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“The self and the cosmos are eternal. This is the only truth, anything else is futile.” ", "html": "{}</p>"}, "ud6.5:3.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "eng": "Others held views such as the following, each regarding their own view as true and others as silly. ", "html": "<p>{}"}, "ud6.5:3.2": {"pli": "“asassato attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“The self and the cosmos are not eternal.” ", "html": "{}</p>"}, "ud6.5:4.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.5:4.2": {"pli": "“sassato ca asassato ca attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“The self and the cosmos are both eternal and not eternal.” ", "html": "{}</p>"}, "ud6.5:5.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.5:5.2": {"pli": "“neva sassato nāsassato attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“The self and the cosmos are neither eternal nor not eternal.” ", "html": "{}</p>"}, "ud6.5:6.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.5:6.2": {"pli": "“sayaṅkato attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“The self and the cosmos are made by oneself.” ", "html": "{}</p>"}, "ud6.5:7.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.5:7.2": {"pli": "“paraṅkato attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“The self and the cosmos are made by another.” ", "html": "{}</p>"}, "ud6.5:8.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.5:8.2": {"pli": "“sayaṅkato ca paraṅkato ca attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“The self and the cosmos are made by both oneself and another.” ", "html": "{}</p>"}, "ud6.5:9.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.5:9.2": {"pli": "“asayaṅkāro aparaṅkāro adhiccasamuppanno attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“The self and the cosmos have arisen anomalously, not made by oneself or another.” ", "html": "{}</p>"}, "ud6.5:10.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.5:10.2": {"pli": "“sassataṁ sukhadukkhaṁ attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“Pleasure and pain are eternal, and the self and the cosmos.” ", "html": "{}</p>", "comm": "<i lang='pi' translate='no'>Attā ca loko ca</i> here and following is missing from the parallel passage in <a href='index.html?q=dn29#36.9'>DN 29:36.9</a>. I assume it has been inserted in error and translate accordingly. "}, "ud6.5:11.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.5:11.2": {"pli": "“asassataṁ sukhadukkhaṁ attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“Pleasure and pain are not eternal, and the self and the cosmos.” ", "html": "{}</p>"}, "ud6.5:12.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.5:12.2": {"pli": "“sassatañca asassatañca sukhadukkhaṁ attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“Pleasure and pain are both eternal and not eternal, and the self and the cosmos.” ", "html": "{}</p>"}, "ud6.5:13.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.5:13.2": {"pli": "“neva sassataṁ nāsassataṁ sukhadukkhaṁ attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“Pleasure and pain are neither eternal nor not eternal, and the self and the cosmos.” ", "html": "{}</p>"}, "ud6.5:14.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.5:14.2": {"pli": "“sayaṅkataṁ sukhadukkhaṁ attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“Pleasure and pain are made by oneself, and the self and the cosmos.” ", "html": "{}</p>"}, "ud6.5:15.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.5:15.2": {"pli": "“paraṅkataṁ sukhadukkhaṁ attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“Pleasure and pain are made by another, and the self and the cosmos.” ", "html": "{}</p>"}, "ud6.5:16.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.5:16.2": {"pli": "“sayaṅkatañca paraṅkatañca sukhadukkhaṁ attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“Pleasure and pain are made by both oneself and another, and the self and the cosmos.” ", "html": "{}</p>"}, "ud6.5:17.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.5:17.2": {"pli": "“asayaṅkāraṁ aparaṅkāraṁ adhiccasamuppannaṁ sukhadukkhaṁ attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“Pleasure and pain have arisen anomalously, not made by oneself or another, and the self and the cosmos.” ", "html": "{}</p>"}, "ud6.5:18.1": {"pli": "Te bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṁ mukhasattīhi vitudantā viharanti: ", "eng": "They were fighting, quarreling, and disputing, continually wounding each other with barbed words: ", "html": "<p>{}"}, "ud6.5:18.2": {"pli": "“ediso dhammo, nediso dhammo; ", "eng": "“Such is Truth, such is not Truth! ", "html": "{}"}, "ud6.5:18.3": {"pli": "nediso dhammo, ediso dhammo”ti. ", "eng": "Such is not Truth, such is Truth!” ", "html": "{}</p>"}, "ud6.5:19.1": {"pli": "Atha kho sambahulā bhikkhū pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya sāvatthiṁ piṇḍāya pāvisiṁsu. ", "eng": "Then several mendicants robed up in the morning and, taking their bowls and robes, entered Sāvatthī for alms. ", "html": "<p>{}"}, "ud6.5:19.2": {"pli": "Sāvatthiyaṁ piṇḍāya caritvā pacchābhattaṁ piṇḍapātapaṭikkantā yena bhagavā tenupasaṅkamiṁsu; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdiṁsu. ", "eng": "Then, after the meal, when they returned from almsround, they went up to the Buddha, bowed, sat down to one side, and told him what was happening. ", "html": "{}"}, "ud6.5:19.3": {"pli": "Ekamantaṁ nisinnā kho te bhikkhū bhagavantaṁ etadavocuṁ: ", "html": "{}</p>"}, "ud6.5:20.1": {"pli": "“Idha, bhante, sambahulā nānātitthiyasamaṇabrāhmaṇaparibbājakā sāvatthiyaṁ paṭivasanti nānādiṭṭhikā nānākhantikā nānārucikā nānādiṭṭhinissayanissitā. ", "html": "<p>{}</p>"}, "ud6.5:21.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.5:21.2": {"pli": "‘sassato attā ca loko ca, idameva saccaṁ moghamaññan’ti …pe… ", "html": "{}"}, "ud6.5:21.3": {"pli": "te bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṁ mukhasattīhi vitudantā viharanti: ", "html": "{}"}, "ud6.5:21.4": {"pli": "‘ediso dhammo, nediso dhammo; ", "html": "{}"}, "ud6.5:21.5": {"pli": "nediso dhammo, ediso dhammo’”ti. ", "html": "{}</p>"}, "ud6.5:22.1": {"pli": "“Aññatitthiyā, bhikkhave, paribbājakā andhā acakkhukā; ", "html": "<p>{}"}, "ud6.5:22.2": {"pli": "atthaṁ na jānanti anatthaṁ na jānanti, dhammaṁ na jānanti adhammaṁ na jānanti. ", "html": "{}"}, "ud6.5:22.3": {"pli": "Te atthaṁ ajānantā anatthaṁ ajānantā, dhammaṁ ajānantā adhammaṁ ajānantā bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṁ mukhasattīhi vitudantā viharanti: ", "html": "{}"}, "ud6.5:22.4": {"pli": "‘ediso dhammo, nediso dhammo; ", "html": "{}"}, "ud6.5:22.5": {"pli": "nediso dhammo, ediso dhammo’”ti. ", "html": "{}</p>"}, "ud6.5:23.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud6.5:24.1": {"pli": "“Imesu kira sajjanti, ", "eng": "“Some ascetics and brahmins, it seems, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud6.5:24.2": {"pli": "eke samaṇabrāhmaṇā; ", "eng": "cling to these things. ", "html": "<span class='verse-line'>{}</span>"}, "ud6.5:24.3": {"pli": "Antarāva visīdanti, ", "eng": "They founder in mid-stream, ", "html": "<span class='verse-line'>{}</span>"}, "ud6.5:24.4": {"pli": "appatvāva tamogadhan”ti. ", "eng": "without reaching that firm footing.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud6.5:24.5": {"pli": "Pañcamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud6.6": {"ud6.6:0.1": {"pli": "Udāna 6.6 ", "eng": "Heartfelt Sayings 6.6 ", "html": "<article id='ud6.6'><header><ul><li class='division'>{}</li></ul>"}, "ud6.6:0.2": {"pli": "Tatiyanānātitthiyasutta ", "eng": "Followers of Various Other Religions (3rd) ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud6.6:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud6.6:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud6.6:1.3": {"pli": "Tena kho pana samayena sambahulā nānātitthiyasamaṇabrāhmaṇaparibbājakā sāvatthiyaṁ paṭivasanti nānādiṭṭhikā nānākhantikā nānārucikā nānādiṭṭhinissayanissitā. ", "eng": "Now at that time several ascetics, brahmins, and wanderers who followed various other religions were residing in Sāvatthī, holding different views, beliefs, and opinions, relying on different views. ", "html": "{}</p>"}, "ud6.6:2.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "eng": "There were some ascetics and brahmins who had this doctrine and view: ", "html": "<p>{}"}, "ud6.6:2.2": {"pli": "“sassato attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“The self and the cosmos are eternal. This is the only truth, anything else is futile.” ", "html": "{}</p>"}, "ud6.6:3.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "eng": "Others held views such as the following, each regarding their own view as true and others as silly. ", "html": "<p>{}"}, "ud6.6:3.2": {"pli": "“asassato attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“The self and the cosmos are not eternal.” ", "html": "{}</p>"}, "ud6.6:4.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.6:4.2": {"pli": "“sassato ca asassato ca attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“The self and the cosmos are both eternal and not eternal.” ", "html": "{}</p>"}, "ud6.6:5.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.6:5.2": {"pli": "“neva sassato nāsassato attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“The self and the cosmos are neither eternal nor not eternal.” ", "html": "{}</p>"}, "ud6.6:6.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.6:6.2": {"pli": "“sayaṅkato attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“The self and the cosmos are made by oneself.” ", "html": "{}</p>"}, "ud6.6:7.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.6:7.2": {"pli": "“paraṅkato attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“The self and the cosmos are made by another.” ", "html": "{}</p>"}, "ud6.6:8.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.6:8.2": {"pli": "“sayaṅkato ca paraṅkato ca attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“The self and the cosmos are made by both oneself and another.” ", "html": "{}</p>"}, "ud6.6:9.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.6:9.2": {"pli": "“asayaṅkāro aparaṅkāro adhiccasamuppanno attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“The self and the cosmos have arisen anomalously, not made by oneself or another.” ", "html": "{}</p>"}, "ud6.6:10.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.6:10.2": {"pli": "“sassataṁ sukhadukkhaṁ attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“Pleasure and pain are eternal, and the self and the cosmos.” ", "html": "{}</p>"}, "ud6.6:11.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.6:11.2": {"pli": "“asassataṁ sukhadukkhaṁ attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“Pleasure and pain are not eternal, and the self and the cosmos.” ", "html": "{}</p>"}, "ud6.6:12.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.6:12.2": {"pli": "“sassatañca asassatañca sukhadukkhaṁ attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“Pleasure and pain are both eternal and not eternal, and the self and the cosmos.” ", "html": "{}</p>"}, "ud6.6:13.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.6:13.2": {"pli": "“neva sassataṁ nāsassataṁ sukhadukkhaṁ attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“Pleasure and pain are neither eternal nor not eternal, and the self and the cosmos.” ", "html": "{}</p>"}, "ud6.6:14.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.6:14.2": {"pli": "“sayaṅkataṁ sukhadukkhaṁ attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“Pleasure and pain are made by oneself, and the self and the cosmos.” ", "html": "{}</p>"}, "ud6.6:15.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.6:15.2": {"pli": "“paraṅkataṁ sukhadukkhaṁ attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“Pleasure and pain are made by another, and the self and the cosmos.” ", "html": "{}</p>"}, "ud6.6:16.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.6:16.2": {"pli": "“sayaṅkatañca paraṅkatañca sukhadukkhaṁ attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“Pleasure and pain are made by both oneself and another, and the self and the cosmos.” ", "html": "{}</p>"}, "ud6.6:17.1": {"pli": "Santi paneke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.6:17.2": {"pli": "“asayaṅkāraṁ aparaṅkāraṁ adhiccasamuppannaṁ sukhadukkhaṁ attā ca loko ca, idameva saccaṁ moghamaññan”ti. ", "eng": "“Pleasure and pain have arisen anomalously, not made by oneself or another, and the self and the cosmos.” ", "html": "{}</p>"}, "ud6.6:18.1": {"pli": "Te bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṁ mukhasattīhi vitudantā viharanti: ", "eng": "They were fighting, quarreling, and disputing, continually wounding each other with barbed words: ", "html": "<p>{}"}, "ud6.6:18.2": {"pli": "“ediso dhammo, nediso dhammo; ", "eng": "‘Such is Truth, such is not! ", "html": "{}"}, "ud6.6:18.3": {"pli": "nediso dhammo, ediso dhammo”ti. ", "eng": "Such is not Truth, such is Truth!” ", "html": "{}</p>"}, "ud6.6:19.1": {"pli": "Atha kho sambahulā bhikkhū pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya sāvatthiṁ piṇḍāya pāvisiṁsu. ", "eng": "Then several mendicants robed up in the morning and, taking their bowls and robes, entered Sāvatthī for alms. ", "html": "<p>{}"}, "ud6.6:19.2": {"pli": "Sāvatthiyaṁ piṇḍāya caritvā pacchābhattaṁ piṇḍapātapaṭikkantā yena bhagavā tenupasaṅkamiṁsu; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdiṁsu. ", "eng": "Then, after the meal, when they returned from almsround, they went up to the Buddha, bowed, sat down to one side, and told him what was happening. ", "html": "{}"}, "ud6.6:19.3": {"pli": "Ekamantaṁ nisinnā kho te bhikkhū bhagavantaṁ etadavocuṁ: ", "html": "{}</p>"}, "ud6.6:20.1": {"pli": "“Idha, bhante, sambahulā nānātitthiyasamaṇabrāhmaṇaparibbājakā sāvatthiyaṁ paṭivasanti nānādiṭṭhikā nānākhantikā nānārucikā nānādiṭṭhinissayanissitā. ", "html": "<p>{}</p>"}, "ud6.6:21.1": {"pli": "Santeke samaṇabrāhmaṇā evaṁvādino evaṁdiṭṭhino: ", "html": "<p>{}"}, "ud6.6:21.2": {"pli": "‘sassato attā ca loko ca, idameva saccaṁ moghamaññan’ti …pe… ", "html": "{}"}, "ud6.6:21.3": {"pli": "te bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṁ mukhasattīhi vitudantā viharanti: ", "html": "{}"}, "ud6.6:21.4": {"pli": "‘ediso dhammo, nediso dhammo; ", "html": "{}"}, "ud6.6:21.5": {"pli": "nediso dhammo, ediso dhammo’”ti. ", "html": "{}</p>"}, "ud6.6:22.1": {"pli": "“Aññatitthiyā, bhikkhave, paribbājakā andhā acakkhukā. ", "html": "<p>{}"}, "ud6.6:22.2": {"pli": "Te atthaṁ na jānanti anatthaṁ na jānanti, dhammaṁ na jānanti adhammaṁ na jānanti. ", "html": "{}"}, "ud6.6:22.3": {"pli": "Te atthaṁ ajānantā anatthaṁ ajānantā, dhammaṁ ajānantā adhammaṁ ajānantā bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṁ mukhasattīhi vitudantā viharanti: ", "html": "{}"}, "ud6.6:22.4": {"pli": "‘ediso dhammo, nediso dhammo; ", "html": "{}"}, "ud6.6:22.5": {"pli": "nediso dhammo, ediso dhammo’”ti. ", "html": "{}</p>"}, "ud6.6:23.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud6.6:24.1": {"pli": "“Ahaṅkārapasutāyaṁ pajā, ", "eng": "“Folk are fixated on the I-maker, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud6.6:24.2": {"pli": "Paraṅkārūpasaṁhitā; ", "eng": "which is tied up with the other-maker. ", "html": "<span class='verse-line'>{}</span>"}, "ud6.6:24.3": {"pli": "Etadeke nābbhaññaṁsu, ", "eng": "There are some who do not realize this, ", "html": "<span class='verse-line'>{}</span>"}, "ud6.6:24.4": {"pli": "Na naṁ sallanti addasuṁ. ", "eng": "they do not see the dart. ", "html": "<span class='verse-line'>{}</span></p>"}, "ud6.6:25.1": {"pli": "Etañca sallaṁ paṭikacca passato, ", "eng": "But when they see this dart, ", "html": "<p><span class='verse-line'>{}</span>"}, "ud6.6:25.2": {"pli": "Ahaṁ karomīti na tassa hoti; ", "eng": "they do not think, ‘I make it’, ", "html": "<span class='verse-line'>{}</span>"}, "ud6.6:25.3": {"pli": "Paro karotīti na tassa hoti. ", "eng": "nor ‘another makes it’. ", "html": "<span class='verse-line'>{}</span></p>"}, "ud6.6:26.1": {"pli": "Mānupetā ayaṁ pajā, ", "eng": "These folk are caught up in conceit, ", "html": "<p><span class='verse-line'>{}</span>"}, "ud6.6:26.2": {"pli": "Mānaganthā mānavinibaddhā; ", "eng": "tied by conceit, shackled by conceit. ", "html": "<span class='verse-line'>{}</span>"}, "ud6.6:26.3": {"pli": "Diṭṭhīsu sārambhakathā, ", "eng": "Vehemently defending their views, ", "html": "<span class='verse-line'>{}</span>"}, "ud6.6:26.4": {"pli": "Saṁsāraṁ nātivattatī”ti. ", "eng": "they don’t escape transmigration.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud6.6:26.5": {"pli": "Chaṭṭhaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud6.7": {"ud6.7:0.1": {"pli": "Udāna 6.7 ", "eng": "Heartfelt Sayings 6.7 ", "html": "<article id='ud6.7'><header><ul><li class='division'>{}</li></ul>"}, "ud6.7:0.2": {"pli": "Subhūtisutta ", "eng": "With Subhūti ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud6.7:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud6.7:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud6.7:1.3": {"pli": "Tena kho pana samayena āyasmā subhūti bhagavato avidūre nisinno hoti pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya avitakkaṁ samādhiṁ samāpajjitvā. ", "eng": "Now at that time Venerable Subhūti was sitting not far from the Buddha, cross-legged, with his body straight, having attained the immersion free of placing the mind. ", "html": "{}</p>"}, "ud6.7:2.1": {"pli": "Addasā kho bhagavā āyasmantaṁ subhūtiṁ avidūre nisinnaṁ pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya avitakkaṁ samādhiṁ samāpannaṁ. ", "eng": "The Buddha saw him meditating there. ", "html": "<p>{}</p>"}, "ud6.7:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud6.7:4.1": {"pli": "“Yassa vitakkā vidhūpitā, ", "eng": "“In whom mental vibrations are cleared away, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud6.7:4.2": {"pli": "Ajjhattaṁ suvikappitā asesā; ", "eng": "internally clipped off entirely, ", "html": "<span class='verse-line'>{}</span>"}, "ud6.7:4.3": {"pli": "Taṁ saṅgamaticca arūpasaññī, ", "eng": "perceiving the formless, beyond chains, ", "html": "<span class='verse-line'>{}</span>"}, "ud6.7:4.4": {"pli": "Catuyogātigato na jātu metī”ti. ", "eng": "having overcome the four yokes, <j>they never return.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>", "comm": "For “they never return” (<i lang='pi' translate='no'>na jātu’m’eti</i>) there is a variant reading “go to rebirth” (<i lang='pi' translate='no'>jātim eti</i>) in <a href='index.html?q=sag#sag32'>Śarīrārthagāthā 32</a>, which  here is very close to the Pali. The commentary also acknowledges this variant: <i lang='pi' translate='no'>‘Na jāti metī’tipi paṭhanti, so evattho</i>. "}, "ud6.7:4.5": {"pli": "Sattamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud6.8": {"ud6.8:0.1": {"pli": "Udāna 6.8 ", "eng": "Heartfelt Sayings 6.8 ", "html": "<article id='ud6.8'><header><ul><li class='division'>{}</li></ul>"}, "ud6.8:0.2": {"pli": "Gaṇikāsutta ", "eng": "The Courtesan ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud6.8:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud6.8:1.2": {"pli": "ekaṁ samayaṁ bhagavā rājagahe viharati veḷuvane kalandakanivāpe. ", "eng": "At one time the Buddha was staying near Rājagaha, in the Bamboo Grove, the squirrels’ feeding ground. ", "html": "{}"}, "ud6.8:1.3": {"pli": "Tena kho pana samayena rājagahe dve pūgā aññatarissā gaṇikāya sārattā honti paṭibaddhacittā; ", "eng": "Now at that time two gangs were both hopelessly in love with a certain courtesan. ", "html": "{}"}, "ud6.8:1.4": {"pli": "bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṁ pāṇīhipi upakkamanti, leḍḍūhipi upakkamanti, daṇḍehipi upakkamanti, satthehipi upakkamanti. ", "eng": "Fighting, quarreling, and disputing, they attacked each other with fists, stones, rods, and swords, ", "html": "{}"}, "ud6.8:1.5": {"pli": "Te tattha maraṇampi nigacchanti maraṇamattampi dukkhaṁ. ", "eng": "resulting in death and deadly pain. ", "html": "{}</p>"}, "ud6.8:2.1": {"pli": "Atha kho sambahulā bhikkhū pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya rājagahaṁ piṇḍāya pāvisiṁsu. ", "eng": "Then several mendicants robed up in the morning and, taking their bowls and robes, entered Rājagaha for alms. ", "html": "<p>{}"}, "ud6.8:2.2": {"pli": "Rājagahe piṇḍāya caritvā pacchābhattaṁ piṇḍapātapaṭikkantā yena bhagavā tenupasaṅkamiṁsu; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdiṁsu. ", "eng": "Then, after the meal, when they returned from almsround, they went up to the Buddha, bowed, sat down to one side, and told him what was happening. ", "html": "{}"}, "ud6.8:2.3": {"pli": "Ekamantaṁ nisinnā kho te bhikkhū bhagavantaṁ etadavocuṁ: ", "html": "{}</p>"}, "ud6.8:3.1": {"pli": "“Idha, bhante, rājagahe dve pūgā aññatarissā gaṇikāya sārattā paṭibaddhacittā; ", "html": "<p>{}"}, "ud6.8:3.2": {"pli": "bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṁ pāṇīhipi upakkamanti, leḍḍūhipi upakkamanti, daṇḍehipi upakkamanti, satthehipi upakkamanti. ", "html": "{}"}, "ud6.8:3.3": {"pli": "Te tattha maraṇampi nigacchanti maraṇamattampi dukkhan”ti. ", "html": "{}</p>"}, "ud6.8:4.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud6.8:5.1": {"pli": "“Yañca pattaṁ yañca pattabbaṁ, ubhayametaṁ rajānukiṇṇaṁ, āturassānusikkhato. ", "eng": "“What has been attained and what is to be attained are both strewn over with dust for that one training while still sick. ", "html": "<p>{}"}, "ud6.8:5.2": {"pli": "Ye ca sikkhāsārā sīlabbataṁ jīvitaṁ brahmacariyaṁ upaṭṭhānasārā, ayameko anto. ", "eng": "Those for whom the training is the essence, or precepts and observances, chastity, and service as the essence: this is one extreme. ", "html": "{}"}, "ud6.8:5.3": {"pli": "Ye ca evaṁvādino: ", "eng": "Those who say, ", "html": "{}"}, "ud6.8:5.4": {"pli": "‘natthi kāmesu doso’ti, ayaṁ dutiyo anto. ", "eng": "‘There’s nothing wrong with sensual pleasures’: this is the second extreme. ", "html": "{}"}, "ud6.8:5.5": {"pli": "Iccete ubho antā kaṭasivaḍḍhanā, kaṭasiyo diṭṭhiṁ vaḍḍhenti. ", "eng": "Thus these two extremes swell the charnel grounds, while the charnel grounds swell wrong view. ", "html": "{}"}, "ud6.8:5.6": {"pli": "Etete ubho ante anabhiññāya olīyanti eke, atidhāvanti eke. ", "eng": "Not realizing these two extremes, some get stuck and some overreach. ", "html": "{}"}, "ud6.8:5.7": {"pli": "Ye ca kho te abhiññāya tatra ca nāhesuṁ, tena ca nāmaññiṁsu, vaṭṭaṁ tesaṁ natthi paññāpanāyā”ti. ", "eng": "Those who realize these things, who were not found there, who did not conceive by that, there is no cycle of rebirths to be found.” ", "html": "{}</p>"}, "ud6.8:5.8": {"pli": "Aṭṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud6.9": {"ud6.9:0.1": {"pli": "Udāna 6.9 ", "eng": "Heartfelt Sayings 6.9 ", "html": "<article id='ud6.9'><header><ul><li class='division'>{}</li></ul>"}, "ud6.9:0.2": {"pli": "Upātidhāvantisutta ", "eng": "Hastening By ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud6.9:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud6.9:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud6.9:1.3": {"pli": "Tena kho pana samayena bhagavā rattandhakāratimisāyaṁ abbhokāse nisinno hoti telappadīpesu jhāyamānesu. ", "eng": "Now at that time the Buddha was meditating in the open during the dark of night, while oil lamps were burning. ", "html": "{}</p>"}, "ud6.9:2.1": {"pli": "Tena kho pana samayena sambahulā adhipātakā tesu telappadīpesu āpātaparipātaṁ anayaṁ āpajjanti, byasanaṁ āpajjanti, anayabyasanaṁ āpajjanti. ", "eng": "And many moths were falling down and crashing down into the lamps, coming to grief and ruin. ", "html": "<p>{}"}, "ud6.9:2.2": {"pli": "Addasā kho bhagavā te sambahule adhipātake tesu telappadīpesu āpātaparipātaṁ anayaṁ āpajjante, byasanaṁ āpajjante, anayabyasanaṁ āpajjante. ", "eng": "The Buddha saw the moths coming to grief. ", "html": "{}</p>"}, "ud6.9:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud6.9:4.1": {"pli": "“Upātidhāvanti na sāramenti, ", "eng": "“Hastening by, they miss the essence, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud6.9:4.2": {"pli": "Navaṁ navaṁ bandhanaṁ brūhayanti; ", "eng": "sprouting ever more new bonds. ", "html": "<span class='verse-line'>{}</span>"}, "ud6.9:4.3": {"pli": "Patanti pajjotamivādhipātakā, ", "eng": "Like moths falling in the flame, ", "html": "<span class='verse-line'>{}</span>"}, "ud6.9:4.4": {"pli": "Diṭṭhe sute itiheke niviṭṭhā”ti. ", "eng": "some have become fixed in what is seen or heard.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud6.9:4.5": {"pli": "Navamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud6.10": {"ud6.10:0.1": {"pli": "Udāna 6.10 ", "eng": "Heartfelt Sayings 6.10 ", "html": "<article id='ud6.10'><header><ul><li class='division'>{}</li></ul>"}, "ud6.10:0.2": {"pli": "Uppajjantisutta ", "eng": "Arising ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud6.10:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud6.10:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud6.10:1.3": {"pli": "Atha kho āyasmā ānando yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. ", "eng": "Then Venerable Ānanda went up to the Buddha, bowed, sat down to one side, and said to him: ", "html": "{}"}, "ud6.10:1.4": {"pli": "Ekamantaṁ nisinno kho āyasmā ānando bhagavantaṁ etadavoca: ", "html": "{}</p>"}, "ud6.10:2.1": {"pli": "“Yāvakīvañca, bhante, tathāgatā loke nuppajjanti arahanto sammāsambuddhā tāva aññatitthiyā paribbājakā sakkatā honti garukatā mānitā pūjitā apacitā lābhino cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhārānaṁ. ", "eng": "“Sir, so long as the Realized Ones, the perfected ones, the fully awakened Buddhas do not arise in the world, the wanderers of other religions are honored, respected, revered, venerated, and esteemed. And they receive robes, almsfood, lodgings, and medicines and supplies for the sick. ", "html": "<p>{}"}, "ud6.10:2.2": {"pli": "Yato ca kho, bhante, tathāgatā loke uppajjanti arahanto sammāsambuddhā atha aññatitthiyā paribbājakā asakkatā honti agarukatā amānitā apūjitā anapacitā na lābhino cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhārānaṁ. ", "eng": "But when the Realized Ones do arise in the world, the wanderers of other religions are no longer honored, respected, revered, venerated, and esteemed. And they do not receive robes, almsfood, lodgings, and medicines and supplies for the sick. ", "html": "{}"}, "ud6.10:2.3": {"pli": "Bhagavāyeva dāni, bhante, sakkato hoti garukato mānito pūjito apacito lābhī cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhārānaṁ, bhikkhusaṅgho cā”ti. ", "eng": "Now only the Buddha is honored, respected, revered, venerated, and esteemed. And he receives robes, almsfood, lodgings, and medicines and supplies for the sick.” ", "html": "{}</p>"}, "ud6.10:3.1": {"pli": "“Evametaṁ, ānanda, yāvakīvañca, ānanda, tathāgatā loke nuppajjanti arahanto sammāsambuddhā tāva aññatitthiyā paribbājakā sakkatā honti garukatā mānitā pūjitā apacitā lābhino cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhārānaṁ. ", "eng": "“That’s so true, Ānanda. So long as the Realized Ones do not arise in the world, the wanderers of other religions are honored, respected, revered, venerated, and esteemed. And they receive robes, almsfood, lodgings, and medicines and supplies for the sick. ", "html": "<p>{}"}, "ud6.10:3.2": {"pli": "Yato ca kho, ānanda, tathāgatā loke uppajjanti arahanto sammāsambuddhā atha aññatitthiyā paribbājakā asakkatā honti agarukatā amānitā apūjitā anapacitā na lābhino cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhārānaṁ. ", "eng": "But when the Realized Ones do arise in the world, the wanderers of other religions are no longer honored in this way. ", "html": "{}"}, "ud6.10:3.3": {"pli": "Tathāgatova dāni sakkato hoti garukato mānito pūjito apacito lābhī cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhārānaṁ, bhikkhusaṅgho cā”ti. ", "eng": "Now only the Realized One is honored, respected, revered, venerated, and esteemed. And he receives robes, almsfood, lodgings, and medicines and supplies for the sick. And so does the mendicant Saṅgha.” ", "html": "{}</p>"}, "ud6.10:4.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud6.10:5.1": {"pli": "“Obhāsati tāva so kimi, ", "eng": "“The glow-worm shines so long ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud6.10:5.2": {"pli": "Yāva na unnamate pabhaṅkaro; ", "eng": "as the beacon of the sun does not rise. ", "html": "<span class='verse-line'>{}</span>"}, "ud6.10:5.3": {"pli": "Sa verocanamhi uggate, ", "eng": "But when the sun has come up, ", "html": "<span class='verse-line'>{}</span>"}, "ud6.10:5.4": {"pli": "Hatappabho hoti na cāpi bhāsati. ", "eng": "that light is erased and shines no more. ", "html": "<span class='verse-line'>{}</span></p>"}, "ud6.10:6.1": {"pli": "Evaṁ obhāsitameva takkikānaṁ, ", "eng": "So too the sophists shine bright ", "html": "<p><span class='verse-line'>{}</span>"}, "ud6.10:6.2": {"pli": "Yāva sammāsambuddhā loke nuppajjanti; ", "eng": "so long as the Buddhas don’t arise in the world. ", "html": "<span class='verse-line'>{}</span>"}, "ud6.10:6.3": {"pli": "Na takkikā sujjhanti na cāpi sāvakā, ", "eng": "The sophists are not purified, nor are their disciples. ", "html": "<span class='verse-line'>{}</span>"}, "ud6.10:6.4": {"pli": "Duddiṭṭhī na dukkhā pamuccare”ti. ", "eng": "Having bad views, they are not freed from suffering.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud6.10:6.5": {"pli": "Dasamaṁ. ", "html": "<p class='endsutta'>{}</p>"}, "ud6.10:7.0": {"pli": "Tassuddānaṁ ", "html": "<p class='uddana-intro'>{}</p>"}, "ud6.10:7.1": {"pli": "Āyujaṭilavekkhaṇā, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "ud6.10:7.2": {"pli": "tayo titthiyā subhūti; ", "html": "<span class='verse-line'>{}</span>"}, "ud6.10:7.3": {"pli": "Gaṇikā upāti navamo, ", "html": "<span class='verse-line'>{}</span>"}, "ud6.10:7.4": {"pli": "uppajjanti ca te dasāti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud6.10:7.5": {"pli": "Jaccandhavaggo chaṭṭho. ", "html": "<p class='endvagga'>{}</p></article>"}}, "ud7.1": {"ud7.1:0.1": {"pli": "Udāna 7.1 ", "eng": "Heartfelt Sayings 7.1 ", "html": "<article id='ud7.1'><header><ul><li class='division'>{}</li></ul>"}, "ud7.1:0.2": {"pli": "Paṭhamalakuṇḍakabhaddiyasutta ", "eng": "Bhaddiya the Dwarf (1st) ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud7.1:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud7.1:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud7.1:1.3": {"pli": "Tena kho pana samayena āyasmā sāriputto āyasmantaṁ lakuṇḍakabhaddiyaṁ anekapariyāyena dhammiyā kathāya sandasseti samādapeti samuttejeti sampahaṁseti. ", "eng": "Now at that time Venerable Sāriputta was educating, encouraging, firing up, and inspiring Venerable Bhaddiya the Dwarf in many ways with a Dhamma talk. ", "html": "{}</p>"}, "ud7.1:2.1": {"pli": "Atha kho āyasmato lakuṇḍakabhaddiyassa āyasmatā sāriputtena anekapariyāyena dhammiyā kathāya sandassiyamānassa samādapiyamānassa samuttejiyamānassa sampahaṁsiyamānassa anupādāya āsavehi cittaṁ vimucci. ", "eng": "Then after being taught like this Bhaddiya’s mind was freed from defilements by not grasping. ", "html": "<p>{}</p>"}, "ud7.1:3.1": {"pli": "Addasā kho bhagavā āyasmantaṁ lakuṇḍakabhaddiyaṁ āyasmatā sāriputtena anekapariyāyena dhammiyā kathāya sandassiyamānaṁ samādapiyamānaṁ samuttejiyamānaṁ sampahaṁsiyamānaṁ anupādāya āsavehi cittaṁ vimuttaṁ. ", "eng": "The Buddha saw what had happened. ", "html": "<p>{}</p>"}, "ud7.1:4.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud7.1:5.1": {"pli": "“Uddhaṁ adho sabbadhi vippamutto, ", "eng": "“Above, below, everywhere free, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud7.1:5.2": {"pli": "Ayaṁhamasmīti anānupassī; ", "eng": "not contemplating ‘I am this’. ", "html": "<span class='verse-line'>{}</span>"}, "ud7.1:5.3": {"pli": "Evaṁ vimutto udatāri oghaṁ, ", "eng": "Freed like this, he has crossed the flood ", "html": "<span class='verse-line'>{}</span>"}, "ud7.1:5.4": {"pli": "Atiṇṇapubbaṁ apunabbhavāyā”ti. ", "eng": "not crossed before, so as to not be reborn.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud7.1:5.5": {"pli": "Paṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud7.2": {"ud7.2:0.1": {"pli": "Udāna 7.2 ", "eng": "Heartfelt Sayings 7.2 ", "html": "<article id='ud7.2'><header><ul><li class='division'>{}</li></ul>"}, "ud7.2:0.2": {"pli": "Dutiyalakuṇḍakabhaddiyasutta ", "eng": "Bhaddiya the Dwarf (2nd) ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud7.2:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud7.2:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud7.2:1.3": {"pli": "Tena kho pana samayena āyasmā sāriputto āyasmantaṁ lakuṇḍakabhaddiyaṁ sekhaṁ maññamāno bhiyyoso mattāya anekapariyāyena dhammiyā kathāya sandasseti samādapeti samuttejeti sampahaṁseti. ", "eng": "Now at that time Venerable Sāriputta was educating, encouraging, firing up, and inspiring Venerable Bhaddiya the Dwarf in even more ways with a Dhamma talk, thinking that he was still a trainee. ", "html": "{}</p>"}, "ud7.2:2.1": {"pli": "Addasā kho bhagavā āyasmantaṁ sāriputtaṁ āyasmantaṁ lakuṇḍakabhaddiyaṁ sekhaṁ maññamānaṁ bhiyyoso mattāya anekapariyāyena dhammiyā kathāya sandassentaṁ samādapentaṁ samuttejentaṁ sampahaṁsentaṁ. ", "eng": "The Buddha saw what was happening. ", "html": "<p>{}</p>"}, "ud7.2:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud7.2:4.1": {"pli": "“Acchecchi vaṭṭaṁ byagā nirāsaṁ, ", "eng": "“They’ve cut the cycle, gone to the wishless; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud7.2:4.2": {"pli": "Visukkhā saritā na sandati; ", "eng": "the streams are dried, they flow no more. ", "html": "<span class='verse-line'>{}</span>"}, "ud7.2:4.3": {"pli": "Chinnaṁ vaṭṭaṁ na vattati, ", "eng": "Cut, the cycle no longer spins. ", "html": "<span class='verse-line'>{}</span>"}, "ud7.2:4.4": {"pli": "Esevanto dukkhassā”ti. ", "eng": "Just this is the end of suffering.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud7.2:4.5": {"pli": "Dutiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud7.3": {"ud7.3:0.1": {"pli": "Udāna 7.3 ", "eng": "Heartfelt Sayings 7.3 ", "html": "<article id='ud7.3'><header><ul><li class='division'>{}</li></ul>"}, "ud7.3:0.2": {"pli": "Paṭhamasattasutta ", "eng": "Clinging (1st) ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud7.3:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud7.3:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud7.3:1.3": {"pli": "Tena kho pana samayena sāvatthiyā manussā yebhuyyena kāmesu ativelaṁ sattā (…) rattā giddhā gathitā mucchitā ajjhosannā sammattakajātā kāmesu viharanti. ", "eng": "Now at that time most of the people in Sāvatthī overly clung to sensual pleasures. Lustful, greedy, tied, infatuated, they lived completely addicted to sensual pleasures. ", "html": "{}</p>"}, "ud7.3:2.1": {"pli": "Atha kho sambahulā bhikkhū pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya sāvatthiyaṁ piṇḍāya pāvisiṁsu. ", "eng": "Then several mendicants robed up in the morning and, taking their bowls and robes, entered Sāvatthī for alms. ", "html": "<p>{}"}, "ud7.3:2.2": {"pli": "Sāvatthiyaṁ piṇḍāya caritvā pacchābhattaṁ piṇḍapātapaṭikkantā yena bhagavā tenupasaṅkamiṁsu; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdiṁsu. ", "eng": "Then, after the meal, when they returned from almsround, they went up to the Buddha, bowed, sat down to one side, and told him what was happening. ", "html": "{}"}, "ud7.3:2.3": {"pli": "Ekamantaṁ nisinnā kho te bhikkhū bhagavantaṁ etadavocuṁ: ", "html": "{}"}, "ud7.3:2.4": {"pli": "“idha, bhante, sāvatthiyā manussā yebhuyyena kāmesu ativelaṁ sattā rattā giddhā gathitā mucchitā ajjhosannā sammattakajātā kāmesu viharantī”ti. ", "html": "{}</p>"}, "ud7.3:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud7.3:4.1": {"pli": "“Kāmesu sattā kāmasaṅgasattā, ", "eng": "“Clinging to sensual pleasures, <j>to the chains of the senses, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud7.3:4.2": {"pli": "Saṁyojane vajjamapassamānā; ", "eng": "blind to the faults of the fetters, ", "html": "<span class='verse-line'>{}</span>"}, "ud7.3:4.3": {"pli": "Na hi jātu saṁyojanasaṅgasattā, ", "eng": "clinging to the chain of the fetters, ", "html": "<span class='verse-line'>{}</span>"}, "ud7.3:4.4": {"pli": "Oghaṁ tareyyuṁ vipulaṁ mahantan”ti. ", "eng": "they’ll never cross the flood so vast.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud7.3:4.5": {"pli": "Tatiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud7.4": {"ud7.4:0.1": {"pli": "Udāna 7.4 ", "eng": "Heartfelt Sayings 7.4 ", "html": "<article id='ud7.4'><header><ul><li class='division'>{}</li></ul>"}, "ud7.4:0.2": {"pli": "Dutiyasattasutta ", "eng": "Clinging (2nd) ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud7.4:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud7.4:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud7.4:1.3": {"pli": "Tena kho pana samayena sāvatthiyā manussā yebhuyyena kāmesu sattā (…) rattā giddhā gathitā mucchitā ajjhosannā andhīkatā sammattakajātā kāmesu viharanti. ", "eng": "Now at that time most of the people in Sāvatthī clung to sensual pleasures. Lustful, greedy, tied, infatuated, attached, and blinded, they lived completely addicted to sensual pleasures. ", "html": "{}</p>"}, "ud7.4:2.1": {"pli": "Atha kho bhagavā pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya sāvatthiṁ piṇḍāya pāvisi. ", "eng": "Then the Buddha robed up in the morning and, taking his bowl and robe, entered Sāvatthī for alms. ", "html": "<p>{}"}, "ud7.4:2.2": {"pli": "Addasā kho bhagavā sāvatthiyā te manusse yebhuyyena kāmesu satte ratte giddhe gathite mucchite ajjhopanne andhīkate sammattakajāte kāmesu viharante. ", "eng": "He saw how attached the humans were. ", "html": "{}</p>"}, "ud7.4:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud7.4:4.1": {"pli": "“Kāmandhā jālasañchannā, ", "eng": "“Blinded by sensual pleasures, wrapped in a net, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud7.4:4.2": {"pli": "Taṇhāchadanachāditā; ", "eng": "they are smothered over by craving; ", "html": "<span class='verse-line'>{}</span>"}, "ud7.4:4.3": {"pli": "Pamattabandhunā baddhā, ", "eng": "bound by the kinsman of the negligent, ", "html": "<span class='verse-line'>{}</span>"}, "ud7.4:4.4": {"pli": "Macchāva kumināmukhe; ", "eng": "like a fish caught in a funnel-net trap. ", "html": "<span class='verse-line'>{}</span>"}, "ud7.4:4.5": {"pli": "Jarāmaraṇamanventi, ", "eng": "They chase old age and death, ", "html": "<span class='verse-line'>{}</span>"}, "ud7.4:4.6": {"pli": "Vaccho khīrapakova mātaran”ti. ", "eng": "like a suckling calf its mother.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud7.4:4.7": {"pli": "Catutthaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud7.5": {"ud7.5:0.1": {"pli": "Udāna 7.5 ", "eng": "Heartfelt Sayings 7.5 ", "html": "<article id='ud7.5'><header><ul><li class='division'>{}</li></ul>"}, "ud7.5:0.2": {"pli": "Aparalakuṇḍakabhaddiyasutta ", "eng": "Another Discourse with Bhaddiya the Dwarf ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud7.5:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud7.5:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud7.5:1.3": {"pli": "Tena kho pana samayena āyasmā lakuṇḍakabhaddiyo sambahulānaṁ bhikkhūnaṁ piṭṭhito piṭṭhito yena bhagavā tenupasaṅkami. ", "eng": "Then Venerable Bhaddiya the Dwarf, closely following several mendicants, approached the Buddha. ", "html": "{}</p>"}, "ud7.5:2.1": {"pli": "Addasā kho bhagavā āyasmantaṁ lakuṇḍakabhaddiyaṁ dūratova sambahulānaṁ bhikkhūnaṁ piṭṭhito piṭṭhito āgacchantaṁ dubbaṇṇaṁ duddasikaṁ okoṭimakaṁ yebhuyyena bhikkhūnaṁ paribhūtarūpaṁ. ", "eng": "The Buddha saw Venerable Bhaddiya coming off in the distance—ugly, unsightly, deformed, and despised by most of the mendicants. ", "html": "<p>{}"}, "ud7.5:2.2": {"pli": "Disvāna bhikkhū āmantesi: ", "eng": "The Buddha addressed the mendicants: ", "html": "{}</p>"}, "ud7.5:3.1": {"pli": "“Passatha no tumhe, bhikkhave, etaṁ bhikkhuṁ dūratova sambahulānaṁ bhikkhūnaṁ piṭṭhito piṭṭhito āgacchantaṁ dubbaṇṇaṁ duddasikaṁ okoṭimakaṁ yebhuyyena bhikkhūnaṁ paribhūtarūpan”ti? ", "eng": "“Mendicants, do you see this monk coming—ugly, unsightly, deformed, and despised by most of the mendicants?” ", "html": "<p>{}"}, "ud7.5:3.2": {"pli": "“Evaṁ, bhante”ti. ", "eng": "“Yes, sir.” ", "html": "{}</p>"}, "ud7.5:4.1": {"pli": "“Eso, bhikkhave, bhikkhu mahiddhiko mahānubhāvo. Na ca sā samāpatti sulabharūpā yā tena bhikkhunā asamāpannapubbā. ", "eng": "“That mendicant is very mighty and powerful. It’s not easy to find an attainment that he has not already attained. ", "html": "<p>{}"}, "ud7.5:4.2": {"pli": "Yassa catthāya kulaputtā sammadeva agārasmā anagāriyaṁ pabbajanti, tadanuttaraṁ—brahmacariyapariyosānaṁ diṭṭheva dhamme sayaṁ abhiññā sacchikatvā upasampajja viharatī”ti. ", "eng": "And he has realized the supreme end of the spiritual path in this very life. He lives having achieved with his own insight the goal for which gentlemen rightly go forth from the lay life to homelessness.” ", "html": "{}</p>"}, "ud7.5:5.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud7.5:6.1": {"pli": "“Nelaṅgo setapacchādo, ", "eng": "“With flawless wheel and white canopy, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud7.5:6.2": {"pli": "ekāro vattatī ratho; ", "eng": "the one-spoke chariot rolls on. ", "html": "<span class='verse-line'>{}</span>"}, "ud7.5:6.3": {"pli": "Anīghaṁ passa āyantaṁ, ", "eng": "See it come, untroubled, ", "html": "<span class='verse-line'>{}</span>"}, "ud7.5:6.4": {"pli": "chinnasotaṁ abandhanan”ti. ", "eng": "with stream cut, unbound.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud7.5:6.5": {"pli": "Pañcamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud7.6": {"ud7.6:0.1": {"pli": "Udāna 7.6 ", "eng": "Heartfelt Sayings 7.6 ", "html": "<article id='ud7.6'><header><ul><li class='division'>{}</li></ul>"}, "ud7.6:0.2": {"pli": "Taṇhāsaṅkhayasutta ", "eng": "The Ending of Craving ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud7.6:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud7.6:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud7.6:1.3": {"pli": "Tena kho pana samayena āyasmā aññāsikoṇḍañño bhagavato avidūre nisinno hoti pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya taṇhāsaṅkhayavimuttiṁ paccavekkhamāno. ", "eng": "Now at that time Venerable Koṇḍañña Who Understood was sitting not far from the Buddha, cross-legged, with his body straight, reviewing the freedom through the ending of craving. ", "html": "{}</p>"}, "ud7.6:2.1": {"pli": "Addasā kho bhagavā āyasmantaṁ aññāsikoṇḍaññaṁ avidūre nisinnaṁ pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya taṇhāsaṅkhayavimuttiṁ paccavekkhamānaṁ. ", "eng": "The Buddha saw him meditating there. ", "html": "<p>{}</p>"}, "ud7.6:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud7.6:4.1": {"pli": "“Yassa mūlaṁ chamā natthi, ", "eng": "“There is no root or ground or leaves for them, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud7.6:4.2": {"pli": "paṇṇā natthi kuto latā; ", "eng": "so where would creepers sprout from? ", "html": "<span class='verse-line'>{}</span>"}, "ud7.6:4.3": {"pli": "Taṁ dhīraṁ bandhanā muttaṁ, ", "eng": "That attentive one is released from bonds: ", "html": "<span class='verse-line'>{}</span>"}, "ud7.6:4.4": {"pli": "ko taṁ ninditumarahati; ", "eng": "who is worthy to criticize them? ", "html": "<span class='verse-line'>{}</span>"}, "ud7.6:4.5": {"pli": "Devāpi naṁ pasaṁsanti, ", "eng": "Even the gods praise them, ", "html": "<span class='verse-line'>{}</span>"}, "ud7.6:4.6": {"pli": "brahmunāpi pasaṁsito”ti. ", "eng": "and by the Divinity, too, they’re praised.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud7.6:4.7": {"pli": "Chaṭṭhaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud7.7": {"ud7.7:0.1": {"pli": "Udāna 7.7 ", "eng": "Heartfelt Sayings 7.7 ", "html": "<article id='ud7.7'><header><ul><li class='division'>{}</li></ul>"}, "ud7.7:0.2": {"pli": "Papañcakhayasutta ", "eng": "The Ending of Proliferation ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud7.7:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud7.7:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud7.7:1.3": {"pli": "Tena kho pana samayena bhagavā attano papañcasaññāsaṅkhāpahānaṁ paccavekkhamāno nisinno hoti. ", "eng": "Now at that time the Buddha was sitting reviewing his own giving up of the judgments driven by the proliferation of perceptions. ", "html": "{}</p>", "comm": "“Judgment” (<i lang='pi' translate='no'>saṅkhā</i>) refers to the way we “appraise” or “assess” ourselves, especially in relation to others (cf. <a href='index.html?q=mn1#3.3'>MN 1:3.3</a>, <a href='index.html?q=dn1#1.3.2'>DN 1:1.3.2</a>). | “Proliferation” (<i lang='pi' translate='no'>papañca</i>) is the compulsion of the mind to spread out in endless inner commentary that hides reality. | “Perceptions” refer to the underlying tendencies that cause us to interpret the present in terms of the past. See <a href='index.html?q=mn18'>MN 18</a> for a detailed explanation. "}, "ud7.7:2.1": {"pli": "Atha kho bhagavā attano papañcasaññāsaṅkhāpahānaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding his own giving up of the judgments driven by the proliferation of perceptions, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud7.7:3.1": {"pli": "“Yassa papañcā ṭhiti ca natthi, ", "eng": "“There is no proliferation remaining in those ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud7.7:3.2": {"pli": "Sandānaṁ palighañca vītivatto; ", "eng": "who have passed beyond the halter and cross-bar. ", "html": "<span class='verse-line'>{}</span>"}, "ud7.7:3.3": {"pli": "Taṁ nittaṇhaṁ muniṁ carantaṁ, ", "eng": "The sage who lives without craving ", "html": "<span class='verse-line'>{}</span>"}, "ud7.7:3.4": {"pli": "Nāvajānāti sadevakopi loko”ti. ", "eng": "is never scorned by the world with its gods.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud7.7:3.5": {"pli": "Sattamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud7.8": {"ud7.8:0.1": {"pli": "Udāna 7.8 ", "eng": "Heartfelt Sayings 7.8 ", "html": "<article id='ud7.8'><header><ul><li class='division'>{}</li></ul>"}, "ud7.8:0.2": {"pli": "Kaccānasutta ", "eng": "Kaccāna ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud7.8:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud7.8:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud7.8:1.3": {"pli": "Tena kho pana samayena āyasmā mahākaccāno bhagavato avidūre nisinno hoti pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya kāyagatāya satiyā ajjhattaṁ parimukhaṁ sūpaṭṭhitāya. ", "eng": "Now at that time Venerable Mahākaccāna was sitting not far from the Buddha, cross-legged, with his body straight and mindfulness of the body well-established internally in his presence. ", "html": "{}</p>"}, "ud7.8:2.1": {"pli": "Addasā kho bhagavā āyasmantaṁ mahākaccānaṁ avidūre nisinnaṁ pallaṅkaṁ ābhujitvā ujuṁ kāyaṁ paṇidhāya kāyagatāya satiyā ajjhattaṁ parimukhaṁ sūpaṭṭhitāya. ", "eng": "The Buddha saw him meditating there. ", "html": "<p>{}</p>"}, "ud7.8:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud7.8:4.1": {"pli": "“Yassa siyā sabbadā sati, ", "eng": "“Their mindfulness would always ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud7.8:4.2": {"pli": "Satataṁ kāyagatā upaṭṭhitā; ", "eng": "be established in the body, constant: ", "html": "<span class='verse-line'>{}</span>"}, "ud7.8:4.3": {"pli": "No cassa no ca me siyā, ", "eng": "‘It might not be, and it might not be mine, ", "html": "<span class='verse-line'>{}</span>"}, "ud7.8:4.4": {"pli": "Na bhavissati na ca me bhavissati; ", "eng": "It will not be, and it will not be mine.’ ", "html": "<span class='verse-line'>{}</span>"}, "ud7.8:4.5": {"pli": "Anupubbavihāri tattha so, ", "eng": "Meditating stage by stage on that, ", "html": "<span class='verse-line'>{}</span>"}, "ud7.8:4.6": {"pli": "Kāleneva tare visattikan”ti. ", "eng": "in time they’d cross over clinging.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud7.8:4.7": {"pli": "Aṭṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud7.9": {"ud7.9:0.1": {"pli": "Udāna 7.9 ", "eng": "Heartfelt Sayings 7.9 ", "html": "<article id='ud7.9'><header><ul><li class='division'>{}</li></ul>"}, "ud7.9:0.2": {"pli": "Udapānasutta ", "eng": "The Well ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud7.9:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud7.9:1.2": {"pli": "ekaṁ samayaṁ bhagavā mallesu cārikaṁ caramāno mahatā bhikkhusaṅghena saddhiṁ yena thūṇaṁ nāma mallānaṁ brāhmaṇagāmo tadavasari. ", "eng": "At one time the Buddha was wandering in the land of the Mallas together with a large Saṅgha of mendicants when he arrived at a brahmin town of the Mallas named Thūṇa. ", "html": "{}"}, "ud7.9:1.3": {"pli": "Assosuṁ kho thūṇeyyakā brāhmaṇagahapatikā: ", "eng": "The brahmins and householders of Thūṇa heard: ", "html": "{}"}, "ud7.9:1.4": {"pli": "“samaṇo khalu, bho, gotamo sakyaputto sakyakulā pabbajito mallesu cārikaṁ caramāno mahatā bhikkhusaṅghena saddhiṁ thūṇaṁ anuppatto”ti. ", "eng": "“It seems the ascetic Gotama—a Sakyan, gone forth from a Sakyan family—while wandering in the land of the Mallas has arrived at Thūṇa, together with a large Saṅgha of mendicants.” ", "html": "{}"}, "ud7.9:1.5": {"pli": "(…) Udapānaṁ tiṇassa ca bhusassa ca yāva mukhato pūresuṁ: ", "eng": "They filled the well with grass and hay right to the top, thinking, ", "html": "{}"}, "ud7.9:1.6": {"pli": "“mā te muṇḍakā samaṇakā pānīyaṁ apaṁsū”ti. ", "eng": "“Don’t let these shavelings, these fake ascetics drink the water.” ", "html": "{}</p>"}, "ud7.9:2.1": {"pli": "Atha kho bhagavā maggā okkamma yena rukkhamūlaṁ tenupasaṅkami; upasaṅkamitvā paññatte āsane nisīdi. ", "eng": "And then the Buddha left the road, went to the root of a tree, and sat down on the seat spread out. ", "html": "<p>{}"}, "ud7.9:2.2": {"pli": "Nisajja kho bhagavā āyasmantaṁ ānandaṁ āmantesi: ", "eng": "When he was seated he said to Venerable Ānanda: ", "html": "{}"}, "ud7.9:2.3": {"pli": "“iṅgha me tvaṁ, ānanda, etamhā udapānā pānīyaṁ āharā”ti. ", "eng": "“Please, Ānanda, fetch me some water from that well.” ", "html": "{}</p>"}, "ud7.9:3.1": {"pli": "Evaṁ vutte, āyasmā ānando bhagavantaṁ etadavoca: ", "eng": "When he said this, Venerable Ānanda said to the Buddha, ", "html": "<p>{}"}, "ud7.9:3.2": {"pli": "“idāni so, bhante, udapāno thūṇeyyakehi brāhmaṇagahapatikehi tiṇassa ca bhusassa ca yāva mukhato pūrito: ", "eng": "“Just now, sir, the brahimns and householders of Thūṇa filled the well with grass and hay right to the top, thinking, ", "html": "{}"}, "ud7.9:3.3": {"pli": "‘mā te muṇḍakā samaṇakā pānīyaṁ apaṁsū’”ti. ", "eng": "‘Don’t let these shavelings, these fake ascetics drink the water.’” ", "html": "{}</p>"}, "ud7.9:4.1": {"pli": "Dutiyampi kho …pe… ", "eng": "For a second time, ", "html": "<p>{}"}, "ud7.9:4.2": {"pli": "tatiyampi kho bhagavā āyasmantaṁ ānandaṁ āmantesi: ", "eng": "and for a third time, the Buddha said to Ānanda: ", "html": "{}"}, "ud7.9:4.3": {"pli": "“iṅgha me tvaṁ, ānanda, etamhā udapānā pānīyaṁ āharā”ti. ", "eng": "“Please, Ānanda, fetch me some water from that well.” ", "html": "{}"}, "ud7.9:4.4": {"pli": "“Evaṁ, bhante”ti kho āyasmā ānando bhagavato paṭissutvā pattaṁ gahetvā yena so udapāno tenupasaṅkami. ", "eng": "“Yes, sir,” replied Ānanda. Taking his bowl he went to the well. ", "html": "{}"}, "ud7.9:4.5": {"pli": "Atha kho so udapāno āyasmante ānande upasaṅkamante sabbaṁ taṁ tiṇañca bhusañca mukhato ovamitvā acchassa udakassa anāvilassa vippasannassa yāva mukhato pūrito vissandanto maññe aṭṭhāsi. ", "eng": "As he approached the well, all the grass and hay erupted out of the well-mouth. The water stood transparent, unclouded, and clear right up to the top, seeming to overflow. ", "html": "{}</p>"}, "ud7.9:5.1": {"pli": "Atha kho āyasmato ānandassa etadahosi: ", "eng": "Then Venerable Ānanda thought, ", "html": "<p>{}"}, "ud7.9:5.2": {"pli": "“acchariyaṁ vata bho, abbhutaṁ vata bho, tathāgatassa mahiddhikatā mahānubhāvatā. ", "eng": "“Oh lord, how incredible, how amazing! The Realized One has such psychic power and might! ", "html": "{}"}, "ud7.9:5.3": {"pli": "Ayañhi so udapāno mayi upasaṅkamante sabbaṁ taṁ tiṇañca bhusañca mukhato ovamitvā acchassa udakassa anāvilassa vippasannassa yāva mukhato pūrito vissandanto maññe ṭhito”ti. ", "eng": "For when I approached this well, all the grass and hay erupted out of the well-mouth. The water stood transparent, unclouded, and clear right up to the top, seeming to overflow.” ", "html": "{}"}, "ud7.9:5.4": {"pli": "Pattena pānīyaṁ ādāya yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ etadavoca: ", "eng": "Gathering a bowl of drinking water he went back to the Buddha, and said to him, ", "html": "{}"}, "ud7.9:5.5": {"pli": "“acchariyaṁ, bhante, abbhutaṁ, bhante, tathāgatassa mahiddhikatā mahānubhāvatā. ", "eng": "“It’s incredible, sir, it’s amazing! The Realized One has such psychic power and might! ", "html": "{}"}, "ud7.9:5.6": {"pli": "Ayañhi so, bhante, udapāno mayi upasaṅkamante sabbaṁ taṁ tiṇañca bhusañca mukhato ovamitvā acchassa udakassa anāvilassa vippasannassa yāva mukhato pūrito vissandanto maññe aṭṭhāsi. ", "eng": "For when I approached that well, all the grass and hay erupted out of the well-mouth. The water stood transparent, unclouded, and clear right up to the top, seeming to overflow. ", "html": "{}"}, "ud7.9:5.7": {"pli": "Pivatu bhagavā pānīyaṁ, pivatu sugato pānīyan”ti. ", "eng": "Drink the water, Blessed One! Drink the water, Holy One!” ", "html": "{}</p>"}, "ud7.9:6.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud7.9:7.1": {"pli": "“Kiṁ kayirā udapānena, ", "eng": "“What difference would a well make ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud7.9:7.2": {"pli": "Āpā ce sabbadā siyuṁ; ", "eng": "if water is there all the time? ", "html": "<span class='verse-line'>{}</span>"}, "ud7.9:7.3": {"pli": "Taṇhāya mūlato chetvā, ", "eng": "Having cut off craving at the root, ", "html": "<span class='verse-line'>{}</span>"}, "ud7.9:7.4": {"pli": "Kissa pariyesanaṁ care”ti. ", "eng": "who would go out on a quest?” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud7.9:7.5": {"pli": "Navamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud7.10": {"ud7.10:0.1": {"pli": "Udāna 7.10 ", "eng": "Heartfelt Sayings 7.10 ", "html": "<article id='ud7.10'><header><ul><li class='division'>{}</li></ul>"}, "ud7.10:0.2": {"pli": "Utenasutta ", "eng": "King Udena ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud7.10:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud7.10:1.2": {"pli": "ekaṁ samayaṁ bhagavā kosambiyaṁ viharati ghositārāme. ", "eng": "At one time the Buddha was staying near Kosambī, in Ghosita’s Monastery. ", "html": "{}"}, "ud7.10:1.3": {"pli": "Tena kho pana samayena rañño utenassa uyyānagatassa antepuraṁ daḍḍhaṁ hoti, pañca ca itthisatāni kālaṅkatāni honti sāmāvatīpamukhāni. ", "eng": "Now at that time, while King Udena was visiting a park, his royal compound burned down. Five hundred women died, with Queen Sāmāvatī at their head. ", "html": "{}</p>"}, "ud7.10:2.1": {"pli": "Atha kho sambahulā bhikkhū pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya kosambiṁ piṇḍāya pāvisiṁsu. ", "eng": "Then several mendicants robed up in the morning and, taking their bowls and robes, entered Kosambī for alms. ", "html": "<p>{}"}, "ud7.10:2.2": {"pli": "Kosambiyaṁ piṇḍāya caritvā pacchābhattaṁ piṇḍapātapaṭikkantā yena bhagavā tenupasaṅkamiṁsu; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdiṁsu. ", "eng": "Then, after the meal, when they returned from almsround, they went up to the Buddha, bowed, sat down to one side, and told him had happened. They asked the Buddha, ", "html": "{}"}, "ud7.10:2.3": {"pli": "Ekamantaṁ nisinnā kho te bhikkhū bhagavantaṁ etadavocuṁ: ", "html": "{}"}, "ud7.10:2.4": {"pli": "“idha, bhante, rañño utenassa uyyānagatassa antepuraṁ daḍḍhaṁ, pañca ca itthisatāni kālaṅkatāni sāmāvatīpamukhāni. ", "html": "{}"}, "ud7.10:2.5": {"pli": "Tāsaṁ, bhante, upāsikānaṁ kā gati ko abhisamparāyo”ti? ", "eng": "“Sir, where have those laywomen been reborn in the next life?” ", "html": "{}</p>"}, "ud7.10:3.1": {"pli": "“Santettha, bhikkhave, upāsikāyo sotāpannā, santi sakadāgāminiyo, santi anāgāminiyo. ", "eng": "“Among those laywomen there were stream-enterers, once-returners, and non-returners. ", "html": "<p>{}"}, "ud7.10:3.2": {"pli": "Sabbā tā, bhikkhave, upāsikāyo anipphalā kālaṅkatā”ti. ", "eng": "None of those laywomen died without some fruit of the practice.” ", "html": "{}</p>"}, "ud7.10:4.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud7.10:5.1": {"pli": "“Mohasambandhano loko, ", "eng": "“The world is caught up in delusion, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud7.10:5.2": {"pli": "bhabbarūpova dissati; ", "eng": "but is looked on as making sense. ", "html": "<span class='verse-line'>{}</span>"}, "ud7.10:5.3": {"pli": "Upadhibandhano bālo, ", "eng": "The fool caught up in attachment ", "html": "<span class='verse-line'>{}</span>"}, "ud7.10:5.4": {"pli": "tamasā parivārito; ", "eng": "is surrounded by darkness. ", "html": "<span class='verse-line'>{}</span>"}, "ud7.10:5.5": {"pli": "Sassatoriva khāyati, ", "eng": "It seems as if eternal, ", "html": "<span class='verse-line'>{}</span>"}, "ud7.10:5.6": {"pli": "passato natthi kiñcanan”ti. ", "eng": "but for one who sees, there is nothing.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud7.10:5.7": {"pli": "Dasamaṁ. ", "html": "<p class='endsection'>{}</p>"}, "ud7.10:6.0": {"pli": "Tassuddānaṁ ", "html": "<p class='uddana-intro'>{}</p>"}, "ud7.10:6.1": {"pli": "Dve bhaddiyā dve ca sattā, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "ud7.10:6.2": {"pli": "lakuṇḍako taṇhākkhayo; ", "html": "<span class='verse-line'>{}</span>"}, "ud7.10:6.3": {"pli": "Papañcakhayo ca kaccāno, ", "html": "<span class='verse-line'>{}</span>"}, "ud7.10:6.4": {"pli": "udapānañca utenoti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud7.10:6.5": {"pli": "Cūḷavaggo sattamo. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud8.1": {"ud8.1:0.1": {"pli": "Udāna 8.1 ", "eng": "Heartfelt Sayings 8.1 ", "html": "<article id='ud8.1'><header><ul><li class='division'>{}</li></ul>"}, "ud8.1:0.2": {"pli": "Paṭhamanibbānapaṭisaṁyuttasutta ", "eng": "About Extinguishment (1st) ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud8.1:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud8.1:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud8.1:1.3": {"pli": "Tena kho pana samayena bhagavā bhikkhū nibbānapaṭisaṁyuttāya dhammiyā kathāya sandasseti samādapeti samuttejeti sampahaṁseti. ", "eng": "Now at that time the Buddha was educating, encouraging, firing up, and inspiring the mendicants with a Dhamma talk about extinguishment. ", "html": "{}"}, "ud8.1:1.4": {"pli": "Tedha bhikkhū aṭṭhiṁ katvā manasi katvā sabbaṁ cetaso samannāharitvā ohitasotā dhammaṁ suṇanti. ", "eng": "And those mendicants were paying attention, applying the mind, concentrating wholeheartedly, and actively listening. ", "html": "{}</p>"}, "ud8.1:2.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud8.1:3.1": {"pli": "“Atthi, bhikkhave, tadāyatanaṁ, yattha neva pathavī, na āpo, na tejo, na vāyo, na ākāsānañcāyatanaṁ, na viññāṇañcāyatanaṁ, na ākiñcaññāyatanaṁ, na nevasaññānāsaññāyatanaṁ, nāyaṁ loko, na paraloko, na ubho candimasūriyā. ", "eng": "“There is, mendicants, that dimension where there is no earth, no water, no fire, no wind; no dimension of infinite space, no dimension of infinite consciousness, no dimension of nothingness, no dimension of neither perception nor non-perception; no this world, no other world, no moon or sun. ", "html": "<p>{}"}, "ud8.1:3.2": {"pli": "Tatrāpāhaṁ, bhikkhave, neva āgatiṁ vadāmi, na gatiṁ, na ṭhitiṁ, na cutiṁ, na upapattiṁ; ", "eng": "There, mendicants, I say there is no coming or going or remaining or passing away or reappearing. ", "html": "{}"}, "ud8.1:3.3": {"pli": "appatiṭṭhaṁ, appavattaṁ, anārammaṇamevetaṁ. ", "eng": "It is not established, does not proceed, and has no support. ", "html": "{}"}, "ud8.1:3.4": {"pli": "Esevanto dukkhassā”ti. ", "eng": "Just this is the end of suffering.” ", "html": "{}</p>"}, "ud8.1:3.5": {"pli": "Paṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud8.2": {"ud8.2:0.1": {"pli": "Udāna 8.2 ", "eng": "Heartfelt Sayings 8.2 ", "html": "<article id='ud8.2'><header><ul><li class='division'>{}</li></ul>"}, "ud8.2:0.2": {"pli": "Dutiyanibbānapaṭisaṁyuttasutta ", "eng": "About Extinguishment (2nd) ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud8.2:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud8.2:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud8.2:1.3": {"pli": "Tena kho pana samayena bhagavā bhikkhū nibbānapaṭisaṁyuttāya dhammiyā kathāya sandasseti samādapeti samuttejeti sampahaṁseti. ", "eng": "Now at that time the Buddha was educating, encouraging, firing up, and inspiring the mendicants with a Dhamma talk about extinguishment. ", "html": "{}"}, "ud8.2:1.4": {"pli": "Tedha bhikkhū aṭṭhiṁ katvā manasi katvā sabbaṁ cetaso samannāharitvā ohitasotā dhammaṁ suṇanti. ", "eng": "And those mendicants were paying attention, applying the mind, concentrating wholeheartedly, and actively listening. ", "html": "{}</p>"}, "ud8.2:2.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud8.2:3.1": {"pli": "“Duddasaṁ anataṁ nāma, ", "eng": "“It’s hard to see what they call the ‘uninclined’, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud8.2:3.2": {"pli": "na hi saccaṁ sudassanaṁ; ", "eng": "for the truth is not easy to see. ", "html": "<span class='verse-line'>{}</span>"}, "ud8.2:3.3": {"pli": "Paṭividdhā taṇhā jānato, ", "eng": "For one who has penetrated craving, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.2:3.4": {"pli": "passato natthi kiñcanan”ti. ", "eng": "who knows and sees, there is nothing.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud8.2:3.5": {"pli": "Dutiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud8.3": {"ud8.3:0.1": {"pli": "Udāna 8.3 ", "eng": "Heartfelt Sayings 8.3 ", "html": "<article id='ud8.3'><header><ul><li class='division'>{}</li></ul>"}, "ud8.3:0.2": {"pli": "Tatiyanibbānapaṭisaṁyuttasutta ", "eng": "About Extinguishment (3rd) ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud8.3:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud8.3:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud8.3:1.3": {"pli": "Tena kho pana samayena bhagavā bhikkhū nibbānapaṭisaṁyuttāya dhammiyā kathāya sandasseti samādapeti samuttejeti sampahaṁseti. ", "eng": "Now at that time the Buddha was educating, encouraging, firing up, and inspiring the mendicants with a Dhamma talk about extinguishment. ", "html": "{}"}, "ud8.3:1.4": {"pli": "Tedha bhikkhū aṭṭhiṁ katvā, manasi katvā, sabbaṁ cetaso samannāharitvā, ohitasotā dhammaṁ suṇanti. ", "eng": "And those mendicants were paying attention, applying the mind, concentrating wholeheartedly, and actively listening. ", "html": "{}</p>"}, "ud8.3:2.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud8.3:3.1": {"pli": "“Atthi, bhikkhave, ajātaṁ abhūtaṁ akataṁ asaṅkhataṁ. ", "eng": "“There is, mendicants, that which is free of rebirth, free of what has been produced, made, and conditioned. ", "html": "<p>{}"}, "ud8.3:3.2": {"pli": "No cetaṁ, bhikkhave, abhavissa ajātaṁ abhūtaṁ akataṁ asaṅkhataṁ, nayidha jātassa bhūtassa katassa saṅkhatassa nissaraṇaṁ paññāyetha. ", "eng": "If there were nothing free of rebirth, free of what has been produced, made, and conditioned, then you would find no escape here from rebirth, from what has been produced, made, and conditioned. ", "html": "{}"}, "ud8.3:3.3": {"pli": "Yasmā ca kho, bhikkhave, atthi ajātaṁ abhūtaṁ akataṁ asaṅkhataṁ, tasmā jātassa bhūtassa katassa saṅkhatassa nissaraṇaṁ paññāyatī”ti. ", "eng": "But since there is that which is free of rebirth, free of what has been produced, made, and conditioned, an escape is found from rebirth, from what has been produced, made, and conditioned.” ", "html": "{}</p>"}, "ud8.3:3.4": {"pli": "Tatiyaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud8.4": {"ud8.4:0.1": {"pli": "Udāna 8.4 ", "eng": "Heartfelt Sayings 8.4 ", "html": "<article id='ud8.4'><header><ul><li class='division'>{}</li></ul>"}, "ud8.4:0.2": {"pli": "Catutthanibbānapaṭisaṁyuttasutta ", "eng": "About Extinguishment (4th) ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud8.4:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud8.4:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud8.4:1.3": {"pli": "Tena kho pana samayena bhagavā bhikkhū nibbānapaṭisaṁyuttāya dhammiyā kathāya sandasseti samādapeti samuttejeti sampahaṁseti. ", "eng": "Now at that time the Buddha was educating, encouraging, firing up, and inspiring the mendicants with a Dhamma talk about extinguishment. ", "html": "{}"}, "ud8.4:1.4": {"pli": "Tedha bhikkhū aṭṭhiṁ katvā manasi katvā sabbaṁ cetaso samannāharitvā ohitasotā dhammaṁ suṇanti. ", "eng": "And those mendicants were paying attention, applying the mind, concentrating wholeheartedly, and actively listening. ", "html": "{}</p>"}, "ud8.4:2.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud8.4:3.1": {"pli": "“Nissitassa calitaṁ, anissitassa calitaṁ natthi. ", "eng": "“For the dependent there is agitation. For the independent there’s no agitation. ", "html": "<p>{}", "comm": "This passage is quoted at <a href='index.html?q=mn144#11.3'>MN 144:11.3</a> and <a href='index.html?q=sn35.87#11.3'>SN 35.87:11.3</a>, and referred to at <a href='index.html?q=snp3.12#44.3'>Snp 3.12:44.3</a>. "}, "ud8.4:3.2": {"pli": "Calite asati passaddhi, ", "eng": "When there’s no agitation there is tranquility. ", "html": "{}"}, "ud8.4:3.3": {"pli": "passaddhiyā sati nati na hoti. ", "eng": "When there is tranquility there’s no inclination. ", "html": "{}"}, "ud8.4:3.4": {"pli": "Natiyā asati āgatigati na hoti. ", "eng": "When there’s no inclination, there’s no coming and going. ", "html": "{}"}, "ud8.4:3.5": {"pli": "Āgatigatiyā asati cutūpapāto na hoti. ", "eng": "When there’s no coming and going, there’s no passing away and reappearing. ", "html": "{}"}, "ud8.4:3.6": {"pli": "Cutūpapāte asati nevidha na huraṁ na ubhayamantarena. ", "eng": "When there’s no passing away and reappearing there’s no this life or the next or between the two. ", "html": "{}"}, "ud8.4:3.7": {"pli": "Esevanto dukkhassā”ti. ", "eng": "Just this is the end of suffering.” ", "html": "{}</p>"}, "ud8.4:3.8": {"pli": "Catutthaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud8.5": {"ud8.5:0.1": {"pli": "Udāna 8.5 ", "eng": "Heartfelt Sayings 8.5 ", "html": "<article id='ud8.5'><header><ul><li class='division'>{}</li></ul>"}, "ud8.5:0.2": {"pli": "Cundasutta ", "eng": "With Cunda ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud8.5:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud8.5:1.2": {"pli": "ekaṁ samayaṁ bhagavā mallesu cārikaṁ caramāno mahatā bhikkhusaṅghena saddhiṁ yena pāvā tadavasari. ", "eng": "At one time the Buddha was wandering in the land of the Mallas together with a large Saṅgha when he arrived at Pāvā. ", "html": "{}"}, "ud8.5:1.3": {"pli": "Tatra sudaṁ bhagavā pāvāyaṁ viharati cundassa kammāraputtassa ambavane. ", "eng": "There he stayed in Cunda the smith’s mango grove. ", "html": "{}</p>"}, "ud8.5:2.1": {"pli": "Assosi kho cundo kammāraputto: “bhagavā kira mallesu cārikaṁ caramāno mahatā bhikkhusaṅghena saddhiṁ pāvaṁ anuppatto pāvāyaṁ viharati mayhaṁ ambavane”ti. ", "eng": "Cunda heard that the Buddha had arrived and was staying in his mango grove. ", "html": "<p>{}"}, "ud8.5:2.2": {"pli": "Atha kho cundo kammāraputto yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. ", "eng": "Then he went to the Buddha, bowed, and sat down to one side. ", "html": "{}"}, "ud8.5:2.3": {"pli": "Ekamantaṁ nisinnaṁ kho cundaṁ kammāraputtaṁ bhagavā dhammiyā kathāya sandassesi samādapesi samuttejesi sampahaṁsesi. ", "eng": "The Buddha educated, encouraged, fired up, and inspired him with a Dhamma talk. ", "html": "{}"}, "ud8.5:2.4": {"pli": "Atha kho cundo kammāraputto bhagavatā dhammiyā kathāya sandassito samādapito samuttejito sampahaṁsito bhagavantaṁ etadavoca: ", "eng": "Then Cunda said to the Buddha, ", "html": "{}"}, "ud8.5:2.5": {"pli": "“adhivāsetu me, bhante, bhagavā svātanāya bhattaṁ saddhiṁ bhikkhusaṅghenā”ti. ", "eng": "“Sir, may the Buddha together with the mendicant Saṅgha please accept tomorrow’s meal from me.” ", "html": "{}"}, "ud8.5:2.6": {"pli": "Adhivāsesi bhagavā tuṇhībhāvena. ", "eng": "The Buddha consented with silence. ", "html": "{}</p>"}, "ud8.5:3.1": {"pli": "Atha kho cundo kammāraputto bhagavato adhivāsanaṁ viditvā uṭṭhāyāsanā bhagavantaṁ abhivādetvā padakkhiṇaṁ katvā pakkāmi. ", "eng": "Then, knowing that the Buddha had consented, Cunda got up from his seat, bowed, and respectfully circled the Buddha, keeping him on his right, before leaving. ", "html": "<p>{}"}, "ud8.5:3.2": {"pli": "Atha kho cundo kammāraputto tassā rattiyā accayena sake nivesane paṇītaṁ khādanīyaṁ bhojanīyaṁ paṭiyādāpetvā pahūtañca sūkaramaddavaṁ bhagavato kālaṁ ārocāpesi: ", "eng": "And when the night had passed Cunda had delicious fresh and cooked foods prepared in his own home, and plenty of pork on the turn. Then he had the Buddha informed of the time, saying, ", "html": "{}"}, "ud8.5:3.3": {"pli": "“kālo, bhante, niṭṭhitaṁ bhattan”ti. ", "eng": "“Sir, it’s time. The meal is ready.” ", "html": "{}</p>"}, "ud8.5:4.1": {"pli": "Atha kho bhagavā pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya saddhiṁ bhikkhusaṅghena yena cundassa kammāraputtassa nivesanaṁ tenupasaṅkami; upasaṅkamitvā paññatte āsane nisīdi. ", "eng": "Then the Buddha robed up in the morning and, taking his bowl and robe, went to the home of Cunda together with the mendicant Saṅgha, where he sat on the seat spread out ", "html": "<p>{}"}, "ud8.5:4.2": {"pli": "Nisajja kho bhagavā cundaṁ kammāraputtaṁ āmantesi: ", "eng": "and addressed Cunda, ", "html": "{}"}, "ud8.5:4.3": {"pli": "“yaṁ te, cunda, sūkaramaddavaṁ paṭiyattaṁ tena maṁ parivisa, ", "eng": "“Cunda, please serve me with the pork on the turn that you’ve prepared. ", "html": "{}"}, "ud8.5:4.4": {"pli": "yaṁ panaññaṁ khādanīyaṁ bhojanīyaṁ paṭiyattaṁ tena bhikkhusaṅghaṁ parivisā”ti. ", "eng": "And serve the mendicant Saṅgha with the other foods.” ", "html": "{}"}, "ud8.5:4.5": {"pli": "“Evaṁ, bhante”ti kho cundo kammāraputto bhagavato paṭissutvā yaṁ ahosi sūkaramaddavaṁ paṭiyattaṁ tena bhagavantaṁ parivisi; ", "eng": "“Yes, sir,” replied Cunda, and did as he was asked. ", "html": "{}"}, "ud8.5:4.6": {"pli": "yaṁ panaññaṁ khādanīyaṁ bhojanīyaṁ paṭiyattaṁ tena bhikkhusaṅghaṁ parivisi. ", "html": "{}</p>"}, "ud8.5:5.1": {"pli": "Atha kho bhagavā cundaṁ kammāraputtaṁ āmantesi: ", "eng": "Then the Buddha addressed Cunda, ", "html": "<p>{}"}, "ud8.5:5.2": {"pli": "“yaṁ te, cunda, sūkaramaddavaṁ avasiṭṭhaṁ taṁ sobbhe nikhaṇāhi. ", "eng": "“Cunda, any pork on the turn that’s left over, you should bury it in a pit. ", "html": "{}"}, "ud8.5:5.3": {"pli": "Nāhaṁ taṁ, cunda, passāmi sadevake loke samārake sabrahmake sassamaṇabrāhmaṇiyā pajāya sadevamanussāya yassa taṁ paribhuttaṁ sammā pariṇāmaṁ gaccheyya aññatra tathāgatassā”ti. ", "eng": "I don’t see anyone in this world—with its gods, Māras, and Divinities, this population with its ascetics and brahmins, its gods and humans—who could properly digest it except for the Realized One.” ", "html": "{}"}, "ud8.5:5.4": {"pli": "“Evaṁ, bhante”ti kho cundo kammāraputto bhagavato paṭissutvā yaṁ ahosi sūkaramaddavaṁ avasiṭṭhaṁ taṁ sobbhe nikhaṇitvā yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. ", "eng": "“Yes, sir,” replied Cunda. He did as he was asked, then came back to the Buddha, bowed, and sat down to one side. ", "html": "{}"}, "ud8.5:5.5": {"pli": "Ekamantaṁ nisinnaṁ kho cundaṁ kammāraputtaṁ bhagavā dhammiyā kathāya sandassetvā samādapetvā samuttejetvā sampahaṁsetvā uṭṭhāyāsanā pakkāmi. ", "eng": "Then the Buddha educated, encouraged, fired up, and inspired him with a Dhamma talk, after which he got up from his seat and left. ", "html": "{}</p>"}, "ud8.5:6.1": {"pli": "Atha kho bhagavato cundassa kammāraputtassa bhattaṁ bhuttāvissa kharo ābādho uppajji. Lohitapakkhandikā pabāḷhā vedanā vattanti māraṇantikā. ", "eng": "After the Buddha had eaten Cunda’s meal, he fell severely ill with bloody dysentery, struck by dreadful pains, close to death. ", "html": "<p>{}"}, "ud8.5:6.2": {"pli": "Tatra sudaṁ bhagavā sato sampajāno adhivāsesi avihaññamāno. ", "eng": "But he endured unbothered, with mindfulness and situational awareness. ", "html": "{}</p>"}, "ud8.5:7.1": {"pli": "Atha kho bhagavā āyasmantaṁ ānandaṁ āmantesi: ", "eng": "Then the Buddha said to Venerable Ānanda, ", "html": "<p>{}"}, "ud8.5:7.2": {"pli": "“āyāmānanda, yena kusinārā tenupasaṅkamissāmā”ti. ", "eng": "“Come, Ānanda, let’s go to Kusinārā.” ", "html": "{}"}, "ud8.5:7.3": {"pli": "“Evaṁ, bhante”ti kho āyasmā ānando bhagavato paccassosi. ", "eng": "“Yes, sir,” Ānanda replied. ", "html": "{}</p>"}, "ud8.5:8.1": {"pli": "Cundassa bhattaṁ bhuñjitvā, ", "eng": "I’ve heard that after eating ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud8.5:8.2": {"pli": "kammārassāti me sutaṁ; ", "eng": "the meal of Cunda the smith, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.5:8.3": {"pli": "Ābādhaṁ samphusī dhīro, ", "eng": "the attentive one fell severely ill, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.5:8.4": {"pli": "pabāḷhaṁ māraṇantikaṁ. ", "eng": "with pains, close to death. ", "html": "<span class='verse-line'>{}</span></p>"}, "ud8.5:9.1": {"pli": "Bhuttassa ca sūkaramaddavena, ", "eng": "A severe sickness struck the Teacher ", "html": "<p><span class='verse-line'>{}</span>"}, "ud8.5:9.2": {"pli": "Byādhippabāḷho udapādi satthuno; ", "eng": "who had eaten the pork on the turn. ", "html": "<span class='verse-line'>{}</span>"}, "ud8.5:9.3": {"pli": "Viriccamāno bhagavā avoca, ", "eng": "While still purging the Buddha said: ", "html": "<span class='verse-line'>{}</span>"}, "ud8.5:9.4": {"pli": "“Gacchāmahaṁ kusināraṁ nagaran”ti. ", "eng": "“I’ll go to the citadel of Kusinārā.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud8.5:10.1": {"pli": "Atha kho bhagavā maggā okkamma yena aññataraṁ rukkhamūlaṁ tenupasaṅkami; upasaṅkamitvā āyasmantaṁ ānandaṁ āmantesi: ", "eng": "Then the Buddha left the road and went to the root of a certain tree, where he addressed Ānanda, ", "html": "<p>{}"}, "ud8.5:10.2": {"pli": "“iṅgha me tvaṁ, ānanda, catugguṇaṁ saṅghāṭiṁ paññāpehi; kilantosmi, ānanda, nisīdissāmī”ti. ", "eng": "“Please, Ānanda, fold my outer robe in four and spread it out for me. I am tired and will sit down.” ", "html": "{}"}, "ud8.5:10.3": {"pli": "“Evaṁ, bhante”ti kho āyasmā ānando bhagavato paṭissutvā catugguṇaṁ saṅghāṭiṁ paññāpesi. ", "eng": "“Yes, sir,” replied Ānanda, and did as he was asked. ", "html": "{}"}, "ud8.5:10.4": {"pli": "Nisīdi bhagavā paññatte āsane. ", "eng": "The Buddha sat on the seat spread out, ", "html": "{}"}, "ud8.5:10.5": {"pli": "Nisajja kho bhagavā āyasmantaṁ ānandaṁ āmantesi: ", "eng": "and said to Venerable Ānanda, ", "html": "{}"}, "ud8.5:10.6": {"pli": "“iṅgha me tvaṁ, ānanda, pānīyaṁ āhara; pipāsitosmi, ānanda, pivissāmī”ti. ", "eng": "“Please, Ānanda, fetch me some water. I am thirsty and will drink.” ", "html": "{}</p>"}, "ud8.5:11.1": {"pli": "Evaṁ vutte, āyasmā ānando bhagavantaṁ etadavoca: ", "eng": "When he said this, Venerable Ānanda said to the Buddha, ", "html": "<p>{}"}, "ud8.5:11.2": {"pli": "“idāni, bhante, pañcamattāni sakaṭasatāni atikkantāni. Taṁ cakkacchinnaṁ udakaṁ parittaṁ luḷitaṁ āvilaṁ sandati. ", "eng": "“Sir, just now around five hundred carts have passed by. The shallow water has been churned up by their wheels, and it flows cloudy and murky. ", "html": "{}"}, "ud8.5:11.3": {"pli": "Ayaṁ, bhante, kukudhā nadī avidūre acchodakā sātodakā sītodakā setodakā supatitthā ramaṇīyā. ", "eng": "The Kakutthā river is not far away, with clear, sweet, cool water, clean, with smooth banks, delightful. ", "html": "{}"}, "ud8.5:11.4": {"pli": "Ettha bhagavā pānīyañca pivissati gattāni ca sītīkarissatī”ti. ", "eng": "There the Buddha can drink and cool his limbs.” ", "html": "{}</p>"}, "ud8.5:12.1": {"pli": "Dutiyampi kho …pe… ", "eng": "For a second time, ", "html": "<p>{}"}, "ud8.5:12.2": {"pli": "tatiyampi kho bhagavā āyasmantaṁ ānandaṁ āmantesi: ", "eng": "and a third time, the Buddha said to Ānanda, ", "html": "{}"}, "ud8.5:12.3": {"pli": "“iṅgha me tvaṁ, ānanda, pānīyaṁ āhara; pipāsitosmi, ānanda, pivissāmī”ti. ", "eng": "“Please, Ānanda, fetch me some water. I am thirsty and will drink.” ", "html": "{}"}, "ud8.5:12.4": {"pli": "“Evaṁ, bhante”ti kho āyasmā ānando bhagavato paṭissutvā pattaṁ gahetvā yena sā nadī tenupasaṅkami. ", "eng": "“Yes, sir,” replied Ānanda. Taking his bowl he went to the river. ", "html": "{}"}, "ud8.5:12.5": {"pli": "Atha kho sā nadī cakkacchinnā parittā luḷitā āvilā sandamānā āyasmante ānande upasaṅkamante acchā vippasannā anāvilā sandati. ", "eng": "Now, though the shallow water in that river had been churned up by wheels, and flowed cloudy and murky, when Ānanda approached it flowed transparent, clear, and unclouded. ", "html": "{}</p>"}, "ud8.5:13.1": {"pli": "Atha kho āyasmato ānandassa etadahosi: ", "eng": "Then Venerable Ānanda thought, ", "html": "<p>{}"}, "ud8.5:13.2": {"pli": "“acchariyaṁ vata bho, abbhutaṁ vata bho, tathāgatassa mahiddhikatā mahānubhāvatā. ", "eng": "“Oh lord, how incredible, how amazing! The Realized One has such psychic power and might! ", "html": "{}"}, "ud8.5:13.3": {"pli": "Ayañhi sā nadī cakkacchinnā parittā luḷitā āvilā sandamānā mayi upasaṅkamante acchā vippasannā anāvilā sandatī”ti. ", "eng": "For though the shallow water in that river had been churned up by wheels, and flowed cloudy and murky, when I approached it flowed transparent, clear, and unclouded.” ", "html": "{}"}, "ud8.5:13.4": {"pli": "Pattena pānīyaṁ ādāya yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ etadavoca: ", "eng": "Gathering a bowl of drinking water he went back to the Buddha, and said to him, ", "html": "{}"}, "ud8.5:13.5": {"pli": "“acchariyaṁ, bhante, abbhutaṁ, bhante, tathāgatassa mahiddhikatā mahānubhāvatā. ", "eng": "“It’s incredible, sir, it’s amazing! The Realized One has such psychic power and might! ", "html": "{}"}, "ud8.5:13.6": {"pli": "Ayañhi sā, bhante, nadī cakkacchinnā parittā luḷitā āvilā sandamānā mayi upasaṅkamante acchā vippasannā anāvilā sandati. ", "eng": "For though the shallow water in that river had been churned up by wheels, and flowed cloudy and murky, when I approached it flowed transparent, clear, and unclouded. ", "html": "{}"}, "ud8.5:13.7": {"pli": "Pivatu bhagavā pānīyaṁ, pivatu sugato pānīyan”ti. ", "eng": "Drink the water, Blessed One! Drink the water, Holy One!” ", "html": "{}</p>"}, "ud8.5:14.1": {"pli": "Atha kho bhagavā pānīyaṁ apāyi. ", "eng": "So the Buddha drank the water. ", "html": "<p>{}"}, "ud8.5:14.2": {"pli": "Atha kho bhagavā mahatā bhikkhusaṅghena saddhiṁ yena kukudhā nadī tenupasaṅkami; upasaṅkamitvā kukudhaṁ nadiṁ ajjhogāhetvā nhatvā ca pivitvā ca paccuttaritvā yena ambavanaṁ tenupasaṅkami. ", "eng": "Then the Buddha together with a large Saṅgha of mendicants went to the Kakutthā River. He plunged into the river and bathed and drank. ", "html": "{}"}, "ud8.5:14.3": {"pli": "Upasaṅkamitvā āyasmantaṁ cundakaṁ āmantesi: ", "eng": "And when he had emerged, he went to the mango grove, where he addressed Venerable Cundaka, ", "html": "{}"}, "ud8.5:14.4": {"pli": "“iṅgha me tvaṁ, cundaka, catugguṇaṁ saṅghāṭiṁ paññāpehi; kilantosmi, cundaka, nipajjissāmī”ti. ", "eng": "“Please, Cundaka, fold my outer robe in four and spread it out for me. I am tired and will lie down.” ", "html": "{}</p>"}, "ud8.5:15.1": {"pli": "“Evaṁ, bhante”ti kho āyasmā cundako bhagavato paṭissutvā catugguṇaṁ saṅghāṭiṁ paññāpesi. ", "eng": "“Yes, sir,” replied Cundaka, and did as he was asked. ", "html": "<p>{}"}, "ud8.5:15.2": {"pli": "Atha kho bhagavā dakkhiṇena passena sīhaseyyaṁ kappesi pāde pādaṁ accādhāya sato sampajāno uṭṭhānasaññaṁ manasi karitvā. ", "eng": "And then the Buddha laid down in the lion’s posture—on the right side, placing one foot on top of the other—mindful and aware, and focused on the time of getting up. ", "html": "{}"}, "ud8.5:15.3": {"pli": "Āyasmā pana cundako tattheva bhagavato purato nisīdi. ", "eng": "But Cundaka sat down right there in front of the Buddha. ", "html": "{}</p>"}, "ud8.5:16.1": {"pli": "Gantvāna buddho nadikaṁ kukudhaṁ, ", "eng": "Having gone to Kakutthā Creek, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud8.5:16.2": {"pli": "Acchodakaṁ sātudakaṁ vippasannaṁ; ", "eng": "whose water was transparent, sweet, and clear, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.5:16.3": {"pli": "Ogāhi satthā sukilantarūpo, ", "eng": "the Teacher, being tired, plunged in, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.5:16.4": {"pli": "Tathāgato appaṭimodha loke. ", "eng": "the Realized One, without compare in the world. ", "html": "<span class='verse-line'>{}</span></p>"}, "ud8.5:17.1": {"pli": "Nhatvā ca pivitvā cudatāri satthā, ", "eng": "And after bathing and drinking the Teacher emerged. ", "html": "<p><span class='verse-line'>{}</span>"}, "ud8.5:17.2": {"pli": "Purakkhato bhikkhugaṇassa majjhe; ", "eng": "Before the group of mendicants, in the middle, <j>the Buddha, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.5:17.3": {"pli": "Satthā pavattā bhagavā idha dhamme, ", "eng": "the Teacher who rolled forth <j>the present dispensation, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.5:17.4": {"pli": "Upāgami ambavanaṁ mahesi; ", "eng": "the great seer went to the mango grove. ", "html": "<span class='verse-line'>{}</span>"}, "ud8.5:17.5": {"pli": "Āmantayi cundakaṁ nāma bhikkhuṁ, ", "eng": "He addressed the mendicant named Cundaka: ", "html": "<span class='verse-line'>{}</span>"}, "ud8.5:17.6": {"pli": "“Catugguṇaṁ santhara me nipajjaṁ”. ", "eng": "“Spread out my folded robe so I can lie down.” ", "html": "<span class='verse-line'>{}</span></p>"}, "ud8.5:18.1": {"pli": "So codito bhāvitattena cundo, ", "eng": "The evolved one urged Cunda, ", "html": "<p><span class='verse-line'>{}</span>"}, "ud8.5:18.2": {"pli": "Catugguṇaṁ santhari khippameva; ", "eng": "who quickly spread the folded robe. ", "html": "<span class='verse-line'>{}</span>"}, "ud8.5:18.3": {"pli": "Nipajji satthā sukilantarūpo, ", "eng": "The Teacher lay down so tired, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.5:18.4": {"pli": "Cundopi tattha pamukhe nisīdīti. ", "eng": "while Cunda sat there before him. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud8.5:19.1": {"pli": "Atha kho bhagavā āyasmantaṁ ānandaṁ āmantesi: ", "eng": "Then the Buddha said to Venerable Ānanda, ", "html": "<p>{}"}, "ud8.5:19.2": {"pli": "“siyā kho panānanda, cundassa kammāraputtassa koci vippaṭisāraṁ upadaheyya: ", "eng": "“Now it may happen, Ānanda, that others may give rise to some regret for Cunda the smith: ", "html": "{}"}, "ud8.5:19.3": {"pli": "‘tassa te, āvuso cunda, alābhā, tassa te dulladdhaṁ yassa te tathāgato pacchimaṁ piṇḍapātaṁ bhuñjitvā parinibbuto’ti. ", "eng": "‘It’s your loss, friend Cunda, it’s your misfortune, in that the Realized One was fully quenched after eating his last almsmeal from you.’ ", "html": "{}"}, "ud8.5:19.4": {"pli": "Cundassānanda, kammāraputtassa evaṁ vippaṭisāro paṭivinodetabbo—", "eng": "You should get rid of remorse in Cunda the smith like this: ", "html": "{}</p>"}, "ud8.5:20.1": {"pli": "Tassa te, āvuso cunda, lābhā, tassa te suladdhaṁ ", "eng": "‘You’re fortunate, friend Cunda, you’re so very fortunate, ", "html": "<p>{}"}, "ud8.5:20.2": {"pli": "yassa te tathāgato pacchimaṁ piṇḍapātaṁ paribhuñjitvā parinibbuto. ", "eng": "in that the Realized One was fully quenched after eating his last almsmeal from you. ", "html": "{}"}, "ud8.5:20.3": {"pli": "Sammukhā metaṁ, āvuso cunda, bhagavato sutaṁ, sammukhā paṭiggahitaṁ—", "eng": "I have heard and learned this in the presence of the Buddha. ", "html": "{}"}, "ud8.5:20.4": {"pli": "dveme piṇḍapātā samasamaphalā samasamavipākā ativiya aññehi piṇḍapātehi mahapphalatarā ca mahānisaṁsatarā ca. ", "eng": "There are two almsmeal offerings that have identical fruit and result, and are more fruitful and beneficial than other almsmeal offerings. ", "html": "{}"}, "ud8.5:20.5": {"pli": "Katame dve? ", "eng": "What two? ", "html": "{}"}, "ud8.5:20.6": {"pli": "Yañca piṇḍapātaṁ paribhuñjitvā tathāgato anuttaraṁ sammāsambodhiṁ abhisambujjhati, yañca piṇḍapātaṁ paribhuñjitvā anupādisesāya nibbānadhātuyā parinibbāyati. ", "eng": "The almsmeal after eating which a Realized One awakens to the supreme perfect awakening; and the almsmeal after eating which he becomes fully extinguished in the element of extinguishment with no residue. ", "html": "{}"}, "ud8.5:20.7": {"pli": "Ime dve piṇḍapātā samasamaphalā samasamavipākā ativiya aññehi piṇḍapātehi mahapphalatarā ca mahānisaṁsatarā ca. ", "eng": "These two almsmeal offerings have identical fruit and result, and are more fruitful and beneficial than other almsmeal offerings. ", "html": "{}</p>"}, "ud8.5:21.1": {"pli": "‘Āyusaṁvattanikaṁ āyasmatā cundena kammāraputtena kammaṁ upacitaṁ, vaṇṇasaṁvattanikaṁ āyasmatā cundena kammāraputtena kammaṁ upacitaṁ, sukhasaṁvattanikaṁ āyasmatā cundena kammāraputtena kammaṁ upacitaṁ, saggasaṁvattanikaṁ āyasmatā cundena kammāraputtena kammaṁ upacitaṁ, yasasaṁvattanikaṁ āyasmatā cundena kammāraputtena kammaṁ upacitaṁ, ādhipateyyasaṁvattanikaṁ āyasmatā cundena kammāraputtena kammaṁ upacitan’ti. ", "eng": "You’ve accumulated a deed that leads to long life, beauty, happiness, glory, heaven, and sovereignty.’ ", "html": "<p>{}"}, "ud8.5:21.2": {"pli": "Cundassānanda, kammāraputtassa evaṁ vippaṭisāro paṭivinodetabbo”ti. ", "eng": "You should dispel remorse in Cunda the smith like this.” ", "html": "{}</p>"}, "ud8.5:22.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud8.5:23.1": {"pli": "“Dadato puññaṁ pavaḍḍhati, ", "eng": "“A giver’s merit grows; ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud8.5:23.2": {"pli": "Saṁyamato veraṁ na cīyati; ", "eng": "enmity doesn’t build up when you have self-control. ", "html": "<span class='verse-line'>{}</span>"}, "ud8.5:23.3": {"pli": "Kusalo ca jahāti pāpakaṁ, ", "eng": "A skillful person gives up bad things—", "html": "<span class='verse-line'>{}</span>"}, "ud8.5:23.4": {"pli": "Rāgadosamohakkhayā sanibbuto”ti. ", "eng": "with the end of greed, hate, and delusion, <j>they’re quenched.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud8.5:23.5": {"pli": "Pañcamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud8.6": {"ud8.6:0.1": {"pli": "Udāna 8.6 ", "eng": "Heartfelt Sayings 8.6 ", "html": "<article id='ud8.6'><header><ul><li class='division'>{}</li></ul>"}, "ud8.6:0.2": {"pli": "Pāṭaligāmiyasutta ", "eng": "The Layfolk of Pāṭali Village ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud8.6:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud8.6:1.2": {"pli": "ekaṁ samayaṁ bhagavā magadhesu cārikaṁ caramāno mahatā bhikkhusaṅghena saddhiṁ yena pāṭaligāmo tadavasari. ", "eng": "At one time the Buddha was wandering in the land of the Magadhans together with a large Saṅgha of mendicants when he arrived at the village of Pāṭali. ", "html": "{}"}, "ud8.6:1.3": {"pli": "Assosuṁ kho pāṭaligāmiyā upāsakā: ", "eng": "The lay followers of Pāṭali Village heard that he had arrived. ", "html": "{}"}, "ud8.6:1.4": {"pli": "“bhagavā kira magadhesu cārikaṁ caramāno mahatā bhikkhusaṅghena saddhiṁ pāṭaligāmaṁ anuppatto”ti. ", "html": "{}"}, "ud8.6:1.5": {"pli": "Atha kho pāṭaligāmiyā upāsakā yena bhagavā tenupasaṅkamiṁsu; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdiṁsu. ", "eng": "So they went to see him, bowed, sat down to one side, and said to him, ", "html": "{}"}, "ud8.6:1.6": {"pli": "Ekamantaṁ nisinnā kho pāṭaligāmiyā upāsakā bhagavantaṁ etadavocuṁ: ", "html": "{}"}, "ud8.6:1.7": {"pli": "“adhivāsetu no, bhante, bhagavā āvasathāgāran”ti. ", "eng": "“Sir, please consent to come to our guest house.” ", "html": "{}"}, "ud8.6:1.8": {"pli": "Adhivāsesi bhagavā tuṇhībhāvena. ", "eng": "The Buddha consented with silence. ", "html": "{}</p>"}, "ud8.6:2.1": {"pli": "Atha kho pāṭaligāmiyā upāsakā bhagavato adhivāsanaṁ viditvā uṭṭhāyāsanā bhagavantaṁ abhivādetvā padakkhiṇaṁ katvā yenāvasathāgāraṁ tenupasaṅkamiṁsu; upasaṅkamitvā sabbasanthariṁ āvasathāgāraṁ santharitvā āsanāni paññāpetvā udakamaṇikaṁ patiṭṭhāpetvā telappadīpaṁ āropetvā yena bhagavā tenupasaṅkamiṁsu; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ aṭṭhaṁsu. Ekamantaṁ ṭhitā kho pāṭaligāmiyā upāsakā bhagavantaṁ etadavocuṁ: ", "eng": "Then, knowing that the Buddha had consented, the lay followers of Pāṭali Village got up from their seat, bowed, and respectfully circled the Buddha, keeping him on their right. Then they went to the guest house, where they spread carpets all over, prepared seats, set up a water jar, and placed an oil lamp. Then they went back to the Buddha, bowed, stood to one side, and told him of their preparations, saying: ", "html": "<p>{}"}, "ud8.6:2.2": {"pli": "“sabbasantharisanthataṁ, bhante, āvasathāgāraṁ; āsanāni paññattāni; udakamaṇiko patiṭṭhāpito telappadīpo āropito. ", "html": "{}"}, "ud8.6:2.3": {"pli": "Yassadāni, bhante, bhagavā kālaṁ maññatī”ti. ", "eng": "“Please, sir, come at your convenience.” ", "html": "{}</p>"}, "ud8.6:3.1": {"pli": "Atha kho bhagavā nivāsetvā pattacīvaramādāya saddhiṁ bhikkhusaṅghena yena āvasathāgāraṁ tenupasaṅkami; upasaṅkamitvā pāde pakkhāletvā āvasathāgāraṁ pavisitvā majjhimaṁ thambhaṁ nissāya puratthābhimukho nisīdi. ", "eng": "In the morning, the Buddha robed up and, taking his bowl and robe, went to the guest house together with the Saṅgha of mendicants. Having washed his feet he entered the guest house and sat against the central column facing east. ", "html": "<p>{}"}, "ud8.6:3.2": {"pli": "Bhikkhusaṅghopi kho pāde pakkhāletvā āvasathāgāraṁ pavisitvā pacchimaṁ bhittiṁ nissāya puratthābhimukho nisīdi bhagavantaṁyeva purakkhatvā. ", "eng": "The Saṅgha of mendicants also washed their feet, entered the guest house, and sat against the west wall facing east, with the Buddha right in front of them. ", "html": "{}"}, "ud8.6:3.3": {"pli": "Pāṭaligāmiyāpi kho upāsakā pāde pakkhāletvā āvasathāgāraṁ pavisitvā puratthimaṁ bhittiṁ nissāya pacchimābhimukhā nisīdiṁsu bhagavantaṁyeva purakkhatvā. ", "eng": "The lay followers of Pāṭali Village also washed their feet, entered the guest house, and sat against the east wall facing west, with the Buddha right in front of them. ", "html": "{}"}, "ud8.6:3.4": {"pli": "Atha kho bhagavā pāṭaligāmiye upāsake āmantesi: ", "eng": "Then the Buddha addressed them: ", "html": "{}</p>"}, "ud8.6:4.1": {"pli": "“Pañcime, gahapatayo, ādīnavā dussīlassa sīlavipattiyā. ", "eng": "“Householders, there are these five drawbacks for an unethical person because of their failure in ethics. ", "html": "<p>{}"}, "ud8.6:4.2": {"pli": "Katame pañca? ", "eng": "What five? ", "html": "{}"}, "ud8.6:4.3": {"pli": "Idha, gahapatayo, dussīlo sīlavipanno pamādādhikaraṇaṁ mahatiṁ bhogajāniṁ nigacchati. ", "eng": "Firstly, an unethical person loses great wealth on account of negligence. ", "html": "{}"}, "ud8.6:4.4": {"pli": "Ayaṁ paṭhamo ādīnavo dussīlassa sīlavipattiyā. ", "eng": "This is the first drawback. ", "html": "{}</p>"}, "ud8.6:5.1": {"pli": "Puna caparaṁ, gahapatayo, dussīlassa sīlavipannassa pāpako kittisaddo abbhuggacchati. ", "eng": "Furthermore, an unethical person gets a bad reputation. ", "html": "<p>{}"}, "ud8.6:5.2": {"pli": "Ayaṁ dutiyo ādīnavo dussīlassa sīlavipattiyā. ", "eng": "This is the second drawback. ", "html": "{}</p>"}, "ud8.6:6.1": {"pli": "Puna caparaṁ, gahapatayo, dussīlo sīlavipanno yaññadeva parisaṁ upasaṅkamati—yadi khattiyaparisaṁ, yadi brāhmaṇaparisaṁ, yadi gahapatiparisaṁ, yadi samaṇaparisaṁ—avisārado upasaṅkamati maṅkubhūto. ", "eng": "Furthermore, an unethical person enters any kind of assembly timid and embarrassed, whether it’s an assembly of aristocrats, brahmins, householders, or ascetics. ", "html": "<p>{}"}, "ud8.6:6.2": {"pli": "Ayaṁ tatiyo ādīnavo dussīlassa sīlavipattiyā. ", "eng": "This is the third drawback. ", "html": "{}</p>"}, "ud8.6:7.1": {"pli": "Puna caparaṁ, gahapatayo, dussīlo sīlavipanno sammūḷho kālaṁ karoti. ", "eng": "Furthermore, an unethical person feels lost when they die. ", "html": "<p>{}"}, "ud8.6:7.2": {"pli": "Ayaṁ catuttho ādīnavo dussīlassa sīlavipattiyā. ", "eng": "This is the fourth drawback. ", "html": "{}</p>"}, "ud8.6:8.1": {"pli": "Puna caparaṁ, gahapatayo, dussīlo sīlavipanno kāyassa bhedā paraṁ maraṇā apāyaṁ duggatiṁ vinipātaṁ nirayaṁ upapajjati. ", "eng": "Furthermore, an unethical person, when their body breaks up, after death, is reborn in a place of loss, a bad place, the underworld, hell. ", "html": "<p>{}"}, "ud8.6:8.2": {"pli": "Ayaṁ pañcamo ādīnavo dussīlassa sīlavipattiyā. ", "eng": "This is the fifth drawback. ", "html": "{}"}, "ud8.6:8.3": {"pli": "Ime kho, gahapatayo, pañca ādīnavā dussīlassa sīlavipattiyā. ", "eng": "These are the five drawbacks for an unethical person because of their failure in ethics. ", "html": "{}</p>"}, "ud8.6:9.1": {"pli": "Pañcime, gahapatayo, ānisaṁsā sīlavato sīlasampadāya. ", "eng": "There are these five benefits for an ethical person because of their accomplishment in ethics. ", "html": "<p>{}"}, "ud8.6:9.2": {"pli": "Katame pañca? ", "eng": "What five? ", "html": "{}"}, "ud8.6:9.3": {"pli": "Idha, gahapatayo, sīlavā sīlasampanno appamādādhikaraṇaṁ mahantaṁ bhogakkhandhaṁ adhigacchati. ", "eng": "Firstly, an ethical person gains great wealth on account of diligence. ", "html": "{}"}, "ud8.6:9.4": {"pli": "Ayaṁ paṭhamo ānisaṁso sīlavato sīlasampadāya. ", "eng": "This is the first benefit. ", "html": "{}</p>"}, "ud8.6:10.1": {"pli": "Puna caparaṁ, gahapatayo, sīlavato sīlasampannassa kalyāṇo kittisaddo abbhuggacchati. ", "eng": "Furthermore, an ethical person gets a good reputation. ", "html": "<p>{}"}, "ud8.6:10.2": {"pli": "Ayaṁ dutiyo ānisaṁso sīlavato sīlasampadāya. ", "eng": "This is the second benefit. ", "html": "{}</p>"}, "ud8.6:11.1": {"pli": "Puna caparaṁ, gahapatayo, sīlavā sīlasampanno yaññadeva parisaṁ upasaṅkamati—yadi khattiyaparisaṁ, yadi brāhmaṇaparisaṁ, yadi gahapatiparisaṁ, yadi samaṇaparisaṁ—visārado upasaṅkamati amaṅkubhūto. ", "eng": "Furthermore, an ethical person enters any kind of assembly bold and self-assured, whether it’s an assembly of aristocrats, brahmins, householders, or ascetics. ", "html": "<p>{}"}, "ud8.6:11.2": {"pli": "Ayaṁ tatiyo ānisaṁso sīlavato sīlasampadāya. ", "eng": "This is the third benefit. ", "html": "{}</p>"}, "ud8.6:12.1": {"pli": "Puna caparaṁ, gahapatayo, sīlavā sīlasampanno asammūḷho kālaṁ karoti. ", "eng": "Furthermore, an ethical person dies not feeling lost. ", "html": "<p>{}"}, "ud8.6:12.2": {"pli": "Ayaṁ catuttho ānisaṁso sīlavato sīlasampadāya. ", "eng": "This is the fourth benefit. ", "html": "{}</p>"}, "ud8.6:13.1": {"pli": "Puna caparaṁ, gahapatayo, sīlavā sīlasampanno kāyassa bhedā paraṁ maraṇā sugatiṁ saggaṁ lokaṁ upapajjati. ", "eng": "Furthermore, when an ethical person’s body breaks up, after death, they’re reborn in a good place, a heavenly realm. ", "html": "<p>{}"}, "ud8.6:13.2": {"pli": "Ayaṁ pañcamo ānisaṁso sīlavato sīlasampadāya. ", "eng": "This is the fifth benefit. ", "html": "{}"}, "ud8.6:13.3": {"pli": "Ime kho, gahapatayo, pañca ānisaṁsā sīlavato sīlasampadāyā”ti. ", "eng": "These are the five benefits for an ethical person because of their accomplishment in ethics.” ", "html": "{}</p>"}, "ud8.6:14.1": {"pli": "Atha kho bhagavā pāṭaligāmiye upāsake bahudeva rattiṁ dhammiyā kathāya sandassetvā samādapetvā samuttejetvā sampahaṁsetvā uyyojesi: ", "eng": "The Buddha spent much of the night educating, encouraging, firing up, and inspiring the lay followers of Pāṭali Village with a Dhamma talk. Then he sent them off, ", "html": "<p>{}"}, "ud8.6:14.2": {"pli": "“abhikkantā kho, gahapatayo, ratti; yassadāni tumhe kālaṁ maññathā”ti ", "eng": "“The night is getting late, householders. Please go at your convenience.” ", "html": "{}"}, "ud8.6:14.3": {"pli": "Atha kho pāṭaligāmiyā upāsakā bhagavato bhāsitaṁ abhinanditvā anumoditvā uṭṭhāyāsanā bhagavantaṁ abhivādetvā padakkhiṇaṁ katvā pakkamiṁsu. ", "eng": "And then the lay followers of Pāṭali Village approved and agreed with what the Buddha said. They got up from their seat, bowed, and respectfully circled the Buddha, keeping him on their right, before leaving. ", "html": "{}"}, "ud8.6:14.4": {"pli": "Atha kho bhagavā acirapakkantesu pāṭaligāmiyesu upāsakesu suññāgāraṁ pāvisi. ", "eng": "Soon after they left the Buddha entered a private cubicle. ", "html": "{}</p>"}, "ud8.6:15.1": {"pli": "Tena kho pana samayena sunidhavassakārā magadhamahāmattā pāṭaligāme nagaraṁ māpenti vajjīnaṁ paṭibāhāya. ", "eng": "Now at that time the Magadhan ministers Sunidha and Vassakāra were building a citadel at Pāṭali Village to keep the Vajjis out. ", "html": "<p>{}"}, "ud8.6:15.2": {"pli": "Tena kho pana samayena sambahulā devatāyo sahassasahasseva pāṭaligāme vatthūni pariggaṇhanti. ", "eng": "At that time thousands of deities were taking possession of building sites in Pāṭali Village. ", "html": "{}"}, "ud8.6:15.3": {"pli": "Yasmiṁ padese mahesakkhā devatā vatthūni pariggaṇhanti mahesakkhānaṁ tattha raññaṁ rājamahāmattānaṁ cittāni namanti nivesanāni māpetuṁ. ", "eng": "Illustrious rulers or royal chief ministers inclined to build houses at sites possessed by illustrious deities. ", "html": "{}"}, "ud8.6:15.4": {"pli": "Yasmiṁ padese majjhimā devatā vatthūni pariggaṇhanti majjhimānaṁ tattha raññaṁ rājamahāmattānaṁ cittāni namanti nivesanāni māpetuṁ. ", "eng": "Middling rulers or royal chief ministers inclined to build houses at sites possessed by middling deities. ", "html": "{}"}, "ud8.6:15.5": {"pli": "Yasmiṁ padese nīcā devatā vatthūni pariggaṇhanti nīcānaṁ tattha raññaṁ rājamahāmattānaṁ cittāni namanti nivesanāni māpetuṁ. ", "eng": "Lesser rulers or royal chief ministers inclined to build houses at sites possessed by lesser deities. ", "html": "{}</p>"}, "ud8.6:16.1": {"pli": "Addasā kho bhagavā dibbena cakkhunā visuddhena atikkantamānusakena tā devatāyo sahassasahasseva pāṭaligāme vatthūni pariggaṇhantiyo. ", "eng": "With clairvoyance that is purified and superhuman, the Buddha saw those deities taking possession of building sites in Pāṭali Village, ", "html": "<p>{}"}, "ud8.6:16.2": {"pli": "Yasmiṁ padese mahesakkhā devatā vatthūni pariggaṇhanti, mahesakkhānaṁ tattha raññaṁ rājamahāmattānaṁ cittāni namanti nivesanāni māpetuṁ. ", "eng": "and the people building houses in accord with the station of the deities. ", "html": "{}"}, "ud8.6:16.3": {"pli": "Yasmiṁ padese majjhimā devatā vatthūni pariggaṇhanti, majjhimānaṁ tattha raññaṁ rājamahāmattānaṁ cittāni namanti nivesanāni māpetuṁ. ", "html": "{}"}, "ud8.6:16.4": {"pli": "Yasmiṁ padese nīcā devatā vatthūni pariggaṇhanti, nīcānaṁ tattha raññaṁ rājamahāmattānaṁ cittāni namanti nivesanāni māpetuṁ. ", "html": "{}"}, "ud8.6:16.5": {"pli": "Atha kho bhagavā tassā rattiyā paccūsasamaye paccuṭṭhāya āyasmantaṁ ānandaṁ āmantesi: ", "eng": "The Buddha rose at the crack of dawn and addressed Ānanda, ", "html": "{}</p>"}, "ud8.6:17.1": {"pli": "“Ke nu kho, ānanda, pāṭaligāme nagaraṁ māpentī”ti. ", "eng": "“Ānanda, who is building a citadel at Pāṭali Village?” ", "html": "<p>{}"}, "ud8.6:17.2": {"pli": "“Sunidhavassakārā, bhante, magadhamahāmattā pāṭaligāme nagaraṁ māpenti vajjīnaṁ paṭibāhāyā”ti. ", "eng": "“Sir, the Magadhan chief ministers Sunidha and Vassakāra are building a citadel to keep the Vajjis out.” ", "html": "{}"}, "ud8.6:17.3": {"pli": "“Seyyathāpi, ānanda, devehi tāvatiṁsehi saddhiṁ mantetvā; ", "eng": "“It’s as if they were building the citadel in consultation with the gods of the thirty-three. ", "html": "{}"}, "ud8.6:17.4": {"pli": "evamevaṁ kho, ānanda, sunidhavassakārā magadhamahāmattā pāṭaligāme nagaraṁ māpenti vajjīnaṁ paṭibāhāya. ", "html": "{}"}, "ud8.6:17.5": {"pli": "Idhāhaṁ, ānanda, addasaṁ dibbena cakkhunā visuddhena atikkantamānusakena sambahulā devatāyo sahassasahasseva pāṭaligāme vatthūni pariggaṇhantiyo. ", "eng": "With clairvoyance that is purified and superhuman, I saw those deities taking possession of building sites. ", "html": "{}"}, "ud8.6:17.6": {"pli": "Yasmiṁ padese mahesakkhā devatā vatthūni pariggaṇhanti mahesakkhānaṁ tattha raññaṁ rājamahāmattānaṁ cittāni namanti nivesanāni māpetuṁ. ", "eng": "Illustrious rulers or royal chief ministers inclined to build houses at sites possessed by illustrious deities. ", "html": "{}"}, "ud8.6:17.7": {"pli": "Yasmiṁ padese majjhimā devatā vatthūni pariggaṇhanti majjhimānaṁ tattha raññaṁ rājamahāmattānaṁ cittāni namanti nivesanāni māpetuṁ. ", "eng": "Middling rulers or royal chief ministers inclined to build houses at sites possessed by middling deities. ", "html": "{}"}, "ud8.6:17.8": {"pli": "Yasmiṁ padese nīcā devatā vatthūni pariggaṇhanti nīcānaṁ tattha raññaṁ rājamahāmattānaṁ cittāni namanti nivesanāni māpetuṁ. ", "eng": "Lesser rulers or royal chief ministers inclined to build houses at sites possessed by lesser deities. ", "html": "{}"}, "ud8.6:17.9": {"pli": "Yāvatā, ānanda, ariyaṁ āyatanaṁ yāvatā vaṇippatho idaṁ agganagaraṁ bhavissati pāṭaliputtaṁ puṭabhedanaṁ. ", "eng": "As far as the civilized region extends, as far as the trading zone extends, this will be the chief city: the Pāṭaliputta trade center. ", "html": "{}"}, "ud8.6:17.10": {"pli": "Pāṭaliputtassa kho, ānanda, tayo antarāyā bhavissanti—", "eng": "But Pāṭaliputta will face three threats: ", "html": "{}"}, "ud8.6:17.11": {"pli": "aggito vā udakato vā mithubhedato vā”ti. ", "eng": "from fire, flood, and dissension.” ", "html": "{}</p>"}, "ud8.6:18.1": {"pli": "Atha kho sunidhavassakārā magadhamahāmattā yena bhagavā tenupasaṅkamiṁsu; upasaṅkamitvā bhagavatā saddhiṁ sammodiṁsu. ", "eng": "Then the Magadhan ministers Sunidha and Vassakāra approached the Buddha, and exchanged greetings with him. ", "html": "<p>{}"}, "ud8.6:18.2": {"pli": "Sammodanīyaṁ kathaṁ sāraṇīyaṁ vītisāretvā ekamantaṁ aṭṭhaṁsu. ", "eng": "When the greetings and polite conversation were over, they stood to one side and said, ", "html": "{}"}, "ud8.6:18.3": {"pli": "Ekamantaṁ ṭhitā kho sunidhavassakārā magadhamahāmattā bhagavantaṁ etadavocuṁ: ", "html": "{}"}, "ud8.6:18.4": {"pli": "“adhivāsetu no bhavaṁ gotamo ajjatanāya bhattaṁ saddhiṁ bhikkhusaṅghenā”ti. ", "eng": "“Would the worthy Gotama together with the mendicant Saṅgha please accept today’s meal from me?” ", "html": "{}"}, "ud8.6:18.5": {"pli": "Adhivāsesi bhagavā tuṇhībhāvena. ", "html": "{}</p>"}, "ud8.6:19.1": {"pli": "Atha kho sunidhavassakārā magadhamahāmattā bhagavato adhivāsanaṁ viditvā yena sako āvasatho tenupasaṅkamiṁsu; upasaṅkamitvā sake āvasathe paṇītaṁ khādanīyaṁ bhojanīyaṁ paṭiyādāpetvā bhagavato kālaṁ ārocesuṁ: ", "eng": "Then, knowing that the Buddha had consented, they went to their own guest house, where they had delicious fresh and cooked foods prepared. Then they had the Buddha informed of the time, saying, ", "html": "<p>{}"}, "ud8.6:19.2": {"pli": "“kālo, bho gotama, niṭṭhitaṁ bhattan”ti. ", "eng": "“It’s time, worthy Gotama, the meal is ready.” ", "html": "{}</p>"}, "ud8.6:20.1": {"pli": "Atha kho bhagavā pubbaṇhasamayaṁ nivāsetvā pattacīvaramādāya saddhiṁ bhikkhusaṅghena yena sunidhavassakārānaṁ magadhamahāmattānaṁ āvasatho tenupasaṅkami; upasaṅkamitvā paññatte āsane nisīdi. ", "eng": "Then the Buddha robed up in the morning and, taking his bowl and robe, went to their guest house together with the mendicant Saṅgha, where he sat on the seat spread out. ", "html": "<p>{}"}, "ud8.6:20.2": {"pli": "Atha kho sunidhavassakārā magadhamahāmattā buddhappamukhaṁ bhikkhusaṅghaṁ paṇītena khādanīyena bhojanīyena sahatthā santappesuṁ sampavāresuṁ. ", "eng": "Then Sunidha and Vassakāra served and satisfied the mendicant Saṅgha headed by the Buddha with their own hands with delicious fresh and cooked foods. ", "html": "{}</p>"}, "ud8.6:21.1": {"pli": "Atha kho sunidhavassakārā magadhamahāmattā bhagavantaṁ bhuttāviṁ onītapattapāṇiṁ aññataraṁ nīcaṁ āsanaṁ gahetvā ekamantaṁ nisīdiṁsu. ", "eng": "When the Buddha had eaten and washed his hand and bowl, Sunidha and Vassakāra took a low seat and sat to one side. ", "html": "<p>{}"}, "ud8.6:21.2": {"pli": "Ekamantaṁ nisinne kho sunidhavassakāre magadhamahāmatte bhagavā imāhi gāthāhi anumodi: ", "eng": "The Buddha expressed his appreciation with these verses: ", "html": "{}</p>"}, "ud8.6:22.1": {"pli": "“Yasmiṁ padese kappeti, ", "eng": "“In the place he makes his dwelling, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud8.6:22.2": {"pli": "vāsaṁ paṇḍitajātiyo; ", "eng": "having fed the astute ", "html": "<span class='verse-line'>{}</span>"}, "ud8.6:22.3": {"pli": "Sīlavantettha bhojetvā, ", "eng": "and the virtuous here, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.6:22.4": {"pli": "saññate brahmacārayo. ", "eng": "the restrained spiritual practitioners, ", "html": "<span class='verse-line'>{}</span></p>"}, "ud8.6:23.1": {"pli": "Yā tattha devatā āsuṁ, ", "eng": "he should dedicate an offering ", "html": "<p><span class='verse-line'>{}</span>"}, "ud8.6:23.2": {"pli": "tāsaṁ dakkhiṇamādise; ", "eng": "to the deities there. ", "html": "<span class='verse-line'>{}</span>"}, "ud8.6:23.3": {"pli": "Tā pūjitā pūjayanti, ", "eng": "Venerated, they venerate him; ", "html": "<span class='verse-line'>{}</span>"}, "ud8.6:23.4": {"pli": "mānitā mānayanti naṁ. ", "eng": "honored, they honor him. ", "html": "<span class='verse-line'>{}</span></p>"}, "ud8.6:24.1": {"pli": "Tato naṁ anukampanti, ", "eng": "After that they have sympathy for him, ", "html": "<p><span class='verse-line'>{}</span>"}, "ud8.6:24.2": {"pli": "mātā puttaṁva orasaṁ; ", "eng": "like a mother for the child at her breast. ", "html": "<span class='verse-line'>{}</span>"}, "ud8.6:24.3": {"pli": "Devatānukampito poso, ", "eng": "A man beloved of the deities ", "html": "<span class='verse-line'>{}</span>"}, "ud8.6:24.4": {"pli": "sadā bhadrāni passatī”ti. ", "eng": "always sees nice things.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud8.6:25.1": {"pli": "Atha kho bhagavā sunidhavassakārānaṁ magadhamahāmattānaṁ imāhi gāthāhi anumoditvā uṭṭhāyāsanā pakkāmi. ", "eng": "When the Buddha had expressed his appreciation to Sunidha and Vassakāra with these verses, he got up from his seat and left. ", "html": "<p>{}</p>"}, "ud8.6:26.1": {"pli": "Tena kho pana samayena sunidhavassakārā magadhamahāmattā bhagavantaṁ piṭṭhito piṭṭhito anubandhā honti: ", "eng": "Sunidha and Vassakāra followed behind the Buddha, thinking, ", "html": "<p>{}"}, "ud8.6:26.2": {"pli": "“yenajja samaṇo gotamo dvārena nikkhamissati taṁ gotamadvāraṁ nāma bhavissati. ", "eng": "“The gate through which the ascetic Gotama exits today shall be named the Gotama Gate. ", "html": "{}"}, "ud8.6:26.3": {"pli": "Yena titthena gaṅgaṁ nadiṁ tarissati taṁ gotamatitthaṁ nāma bhavissatī”ti. ", "eng": "The ford at which he crosses the Ganges River shall be named the Gotama Ford.” ", "html": "{}</p>"}, "ud8.6:27.1": {"pli": "Atha kho bhagavā yena dvārena nikkhami taṁ gotamadvāraṁ nāma ahosi. ", "eng": "Then the gate through which the Buddha exited was named the Gotama Gate. ", "html": "<p>{}"}, "ud8.6:27.2": {"pli": "Atha kho bhagavā yena gaṅgā nadī tenupasaṅkami. ", "eng": "Then the Buddha came to the Ganges River. ", "html": "{}"}, "ud8.6:27.3": {"pli": "Tena kho pana samayena gaṅgā nadī pūrā hoti samatittikā kākapeyyā. ", "eng": "Now at that time the Ganges was full to the brim so a crow could drink from it. ", "html": "{}"}, "ud8.6:27.4": {"pli": "Appekacce manussā nāvaṁ pariyesanti, appekacce uḷumpaṁ pariyesanti, appekacce kullaṁ bandhanti apārā pāraṁ gantukāmā. ", "eng": "Wanting to cross from the near to the far shore, some people were seeking a boat, some a dinghy, while some were tying up a raft. ", "html": "{}"}, "ud8.6:27.5": {"pli": "Atha kho bhagavā—seyyathāpi nāma balavā puriso samiñjitaṁ vā bāhaṁ pasāreyya, pasāritaṁ vā bāhaṁ samiñjeyya; evamevaṁ—gaṅgāya nadiyā orimatīre antarahito pārimatīre paccuṭṭhāsi saddhiṁ bhikkhusaṅghena. ", "eng": "But, as easily as a strong person would extend or contract their arm, the Buddha, together with the mendicant Saṅgha, vanished from the near shore and landed on the far shore. ", "html": "{}</p>"}, "ud8.6:28.1": {"pli": "Addasā kho bhagavā te manusse appekacce nāvaṁ pariyesante, appekacce uḷumpaṁ pariyesante, appekacce kullaṁ bandhante apārā pāraṁ gantukāme. ", "eng": "He saw all those humans wanting to cross over. ", "html": "<p>{}</p>"}, "ud8.6:29.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud8.6:30.1": {"pli": "“Ye taranti aṇṇavaṁ saraṁ, ", "eng": "“Those who cross a deluge or stream ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud8.6:30.2": {"pli": "Setuṁ katvāna visajja pallalāni; ", "eng": "have built a bridge and left the marshes behind. ", "html": "<span class='verse-line'>{}</span>"}, "ud8.6:30.3": {"pli": "Kullañhi jano pabandhati, ", "eng": "While some people are still tying a raft, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.6:30.4": {"pli": "Tiṇṇā medhāvino janā”ti. ", "eng": "intelligent people have crossed over.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud8.6:30.5": {"pli": "Chaṭṭhaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud8.7": {"ud8.7:0.1": {"pli": "Udāna 8.7 ", "eng": "Heartfelt Sayings 8.7 ", "html": "<article id='ud8.7'><header><ul><li class='division'>{}</li></ul>"}, "ud8.7:0.2": {"pli": "Dvidhāpathasutta ", "eng": "A Fork in the Road ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud8.7:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud8.7:1.2": {"pli": "ekaṁ samayaṁ bhagavā kosalesu addhānamaggappaṭipanno hoti āyasmatā nāgasamālena pacchāsamaṇena. ", "eng": "At one time the Buddha was traveling along a road in the Kosalan lands with Venerable Nāgasamāla as his second monk. ", "html": "{}"}, "ud8.7:1.3": {"pli": "Addasā kho āyasmā nāgasamālo antarāmagge dvidhāpathaṁ. ", "eng": "Nāgasamāla saw a fork in the road ", "html": "{}"}, "ud8.7:1.4": {"pli": "Disvāna bhagavantaṁ etadavoca: ", "eng": "and said to the Buddha, ", "html": "{}"}, "ud8.7:1.5": {"pli": "“ayaṁ, bhante, bhagavā pantho; ", "eng": "“Sir, this is the road, ", "html": "{}"}, "ud8.7:1.6": {"pli": "iminā gacchāmā”ti. ", "eng": "let us go this way.” ", "html": "{}"}, "ud8.7:1.7": {"pli": "Evaṁ vutte, bhagavā āyasmantaṁ nāgasamālaṁ etadavoca: ", "eng": "But when he said this the Buddha responded, ", "html": "{}"}, "ud8.7:1.8": {"pli": "“ayaṁ, nāgasamāla, pantho; ", "eng": "“Nāgasamāla, this is the road, ", "html": "{}"}, "ud8.7:1.9": {"pli": "iminā gacchāmā”ti. ", "eng": "let us go this way.” ", "html": "{}</p>"}, "ud8.7:2.1": {"pli": "Dutiyampi kho …pe… ", "eng": "For a second time, ", "html": "<p>{}"}, "ud8.7:2.2": {"pli": "tatiyampi kho āyasmā nāgasamālo bhagavantaṁ etadavoca: ", "eng": "and a third time Nāgasamāla said to the Buddha, ", "html": "{}"}, "ud8.7:2.3": {"pli": "“ayaṁ, bhante, bhagavā pantho; ", "eng": "“Sir, this is the road, ", "html": "{}"}, "ud8.7:2.4": {"pli": "iminā gacchāmā”ti. ", "eng": "let us go this way.” ", "html": "{}"}, "ud8.7:2.5": {"pli": "Tatiyampi kho bhagavā āyasmantaṁ nāgasamālaṁ etadavoca: ", "eng": "And for a third time the Buddha responded, ", "html": "{}"}, "ud8.7:2.6": {"pli": "“ayaṁ, nāgasamāla, pantho; ", "eng": "“Nāgasamāla, this is the road, ", "html": "{}"}, "ud8.7:2.7": {"pli": "iminā gacchāmā”ti. ", "eng": "let us go this way.” ", "html": "{}"}, "ud8.7:2.8": {"pli": "Atha kho āyasmā nāgasamālo bhagavato pattacīvaraṁ tattheva chamāyaṁ nikkhipitvā pakkāmi: ", "eng": "Then Nāgasamāla put the Buddha’s bowl and robes down on the ground right there and left, saying, ", "html": "{}"}, "ud8.7:2.9": {"pli": "“idaṁ, bhante, bhagavato pattacīvaran”ti. ", "eng": "“Sir, here are your bowl and robes.” ", "html": "{}</p>"}, "ud8.7:3.1": {"pli": "Atha kho āyasmato nāgasamālassa tena panthena gacchantassa antarāmagge corā nikkhamitvā hatthehi ca pādehi ca ākoṭesuṁ pattañca bhindiṁsu saṅghāṭiñca vipphālesuṁ. ", "eng": "Then as Nāgasamāla was going down that road, he was set upon by bandits who struck him with fists and feet, broke his bowl, and tore up his outer robe. ", "html": "<p>{}"}, "ud8.7:3.2": {"pli": "Atha kho āyasmā nāgasamālo bhinnena pattena vipphālitāya saṅghāṭiyā yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. ", "eng": "Then Nāgasamāla—with his bowl broken and his outer robe torn—went to the Buddha and told him what had happened. ", "html": "{}"}, "ud8.7:3.3": {"pli": "Ekamantaṁ nisinno kho āyasmā nāgasamālo bhagavantaṁ etadavoca: ", "html": "{}"}, "ud8.7:3.4": {"pli": "“idha mayhaṁ, bhante, tena panthena gacchantassa antarāmagge corā nikkhamitvā hatthehi ca pādehi ca ākoṭesuṁ, pattañca bhindiṁsu, saṅghāṭiñca vipphālesun”ti. ", "html": "{}</p>"}, "ud8.7:4.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud8.7:5.1": {"pli": "“Saddhiṁ caramekato vasaṁ, ", "eng": "“Walking together, dwelling as one, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud8.7:5.2": {"pli": "Misso aññajanena vedagū; ", "eng": "the knowledge master mixes with foolish folk. ", "html": "<span class='verse-line'>{}</span>"}, "ud8.7:5.3": {"pli": "Vidvā pajahāti pāpakaṁ, ", "eng": "Knowing this, they give up wickedness, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.7:5.4": {"pli": "Koñco khīrapakova ninnagan”ti. ", "eng": "like a milk-drinking heron the water.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud8.7:5.5": {"pli": "Sattamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud8.8": {"ud8.8:0.1": {"pli": "Udāna 8.8 ", "eng": "Heartfelt Sayings 8.8 ", "html": "<article id='ud8.8'><header><ul><li class='division'>{}</li></ul>"}, "ud8.8:0.2": {"pli": "Visākhāsutta ", "eng": "With Visākhā ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud8.8:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud8.8:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati pubbārāme migāramātupāsāde. ", "eng": "At one time the Buddha was staying near Sāvatthī in the stilt longhouse of Migāra’s mother in the Eastern Monastery. ", "html": "{}"}, "ud8.8:1.3": {"pli": "Tena kho pana samayena visākhāya migāramātuyā nattā kālaṅkatā hoti piyā manāpā. ", "eng": "Now at that time the dear and beloved granddaughter of Visākhā Migāra’s Mother had just passed away. ", "html": "{}"}, "ud8.8:1.4": {"pli": "Atha kho visākhā migāramātā allavatthā allakesā divā divassa yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. ", "eng": "Then, in the middle of the day, Visākhā with wet clothes and hair went to the Buddha, bowed, and sat down. ", "html": "{}"}, "ud8.8:1.5": {"pli": "Ekamantaṁ nisinnaṁ kho visākhaṁ migāramātaraṁ bhagavā etadavoca: ", "eng": "The Buddha said to her, ", "html": "{}</p>"}, "ud8.8:2.1": {"pli": "“Handa kuto nu tvaṁ, visākhe, āgacchasi allavatthā allakesā idhūpasaṅkantā divā divassā”ti? ", "eng": "“So, Visākhā, where are you coming from in the middle of the day with wet clothes and hair?” ", "html": "<p>{}"}, "ud8.8:2.2": {"pli": "“Nattā me, bhante, piyā manāpā kālaṅkatā. ", "eng": "“Sir, my beloved granddaughter has just passed away. ", "html": "{}"}, "ud8.8:2.3": {"pli": "Tenāhaṁ allavatthā allakesā idhūpasaṅkantā divā divassā”ti. ", "eng": "That’s why I came here in the middle of the day with wet clothes and hair.” ", "html": "{}"}, "ud8.8:2.4": {"pli": "“Iccheyyāsi tvaṁ, visākhe, yāvatikā sāvatthiyā manussā tāvatike putte ca nattāro cā”ti? ", "eng": "“Visākhā, would you like as many children and grandchildren as there are people in the whole of Sāvatthī?” ", "html": "{}"}, "ud8.8:2.5": {"pli": "“Iccheyyāhaṁ, bhagavā, yāvatikā sāvatthiyā manussā tāvatike putte ca nattāro cā”ti. ", "eng": "“I would, sir.” ", "html": "{}</p>"}, "ud8.8:3.1": {"pli": "“Kīvabahukā pana, visākhe, sāvatthiyā manussā devasikaṁ kālaṁ karontī”ti? ", "eng": "“But Visākhā, how many people pass away each day in Sāvatthī?” ", "html": "<p>{}"}, "ud8.8:3.2": {"pli": "“Dasapi, bhante, sāvatthiyā manussā devasikaṁ kālaṁ karonti; ", "eng": "“Every day, sir, there are ten people passing away in Sāvatthī. ", "html": "{}"}, "ud8.8:3.3": {"pli": "navapi, bhante … ", "eng": "Or else there are nine, ", "html": "{}"}, "ud8.8:3.4": {"pli": "aṭṭhapi, bhante … ", "eng": "eight, ", "html": "{}"}, "ud8.8:3.5": {"pli": "sattapi, bhante … ", "eng": "seven, ", "html": "{}"}, "ud8.8:3.6": {"pli": "chapi, bhante … ", "eng": "six, ", "html": "{}"}, "ud8.8:3.7": {"pli": "pañcapi, bhante … ", "eng": "five, ", "html": "{}"}, "ud8.8:3.8": {"pli": "cattāropi, bhante … ", "eng": "four, ", "html": "{}"}, "ud8.8:3.9": {"pli": "tīṇipi, bhante … ", "eng": "three, ", "html": "{}"}, "ud8.8:3.10": {"pli": "dvepi, bhante, sāvatthiyā manussā devasikaṁ kālaṁ karonti. ", "eng": "two, ", "html": "{}"}, "ud8.8:3.11": {"pli": "Ekopi, bhante, sāvatthiyā manusso devasikaṁ kālaṁ karoti. ", "eng": "or at least one person who passes away every day in Sāvatthī. ", "html": "{}"}, "ud8.8:3.12": {"pli": "Avivittā, bhante, sāvatthi manussehi kālaṁ karontehī”ti. ", "eng": "Sāvatthī is never without someone passing away.” ", "html": "{}</p>"}, "ud8.8:4.1": {"pli": "“Taṁ kiṁ maññasi, visākhe, api nu tvaṁ kadāci karahaci anallavatthā vā bhaveyyāsi anallakesā vā”ti? ", "eng": "“What do you think, Visākhā? Would there ever be a time when your clothes and hair were not wet?” ", "html": "<p>{}"}, "ud8.8:4.2": {"pli": "“No hetaṁ, bhante. ", "eng": "“No, sir. ", "html": "{}"}, "ud8.8:4.3": {"pli": "Alaṁ me, bhante, tāva bahukehi puttehi ca nattārehi cā”ti. ", "eng": "Enough, sir, with so many children and grandchildren.” ", "html": "{}</p>"}, "ud8.8:5.1": {"pli": "“Yesaṁ kho, visākhe, sataṁ piyāni, sataṁ tesaṁ dukkhāni; ", "eng": "“Those who have a hundred loved ones, Visākhā, have a hundred sufferings. ", "html": "<p>{}"}, "ud8.8:5.2": {"pli": "yesaṁ navuti piyāni, navuti tesaṁ dukkhāni; ", "eng": "Those who have ninety loved ones, ", "html": "{}"}, "ud8.8:5.3": {"pli": "yesaṁ asīti piyāni, asīti tesaṁ dukkhāni; ", "eng": "or eighty, ", "html": "{}"}, "ud8.8:5.4": {"pli": "yesaṁ sattati piyāni, sattati tesaṁ dukkhāni; ", "eng": "seventy, ", "html": "{}"}, "ud8.8:5.5": {"pli": "yesaṁ saṭṭhi piyāni, saṭṭhi tesaṁ dukkhāni; ", "eng": "sixty, ", "html": "{}"}, "ud8.8:5.6": {"pli": "yesaṁ paññāsaṁ piyāni, paññāsaṁ tesaṁ dukkhāni; ", "eng": "fifty, ", "html": "{}"}, "ud8.8:5.7": {"pli": "yesaṁ cattārīsaṁ piyāni, cattārīsaṁ tesaṁ dukkhāni, ", "eng": "forty, ", "html": "{}"}, "ud8.8:5.8": {"pli": "yesaṁ tiṁsaṁ piyāni, tiṁsaṁ tesaṁ dukkhāni; ", "eng": "thirty, ", "html": "{}"}, "ud8.8:5.9": {"pli": "yesaṁ vīsati piyāni, vīsati tesaṁ dukkhāni, ", "eng": "twenty, ", "html": "{}"}, "ud8.8:5.10": {"pli": "yesaṁ dasa piyāni, dasa tesaṁ dukkhāni; ", "eng": "ten, ", "html": "{}"}, "ud8.8:5.11": {"pli": "yesaṁ nava piyāni, nava tesaṁ dukkhāni; ", "eng": "nine, ", "html": "{}"}, "ud8.8:5.12": {"pli": "yesaṁ aṭṭha piyāni, aṭṭha tesaṁ dukkhāni; ", "eng": "eight, ", "html": "{}"}, "ud8.8:5.13": {"pli": "yesaṁ satta piyāni, satta tesaṁ dukkhāni; ", "eng": "seven, ", "html": "{}"}, "ud8.8:5.14": {"pli": "yesaṁ cha piyāni, cha tesaṁ dukkhāni; ", "eng": "six, ", "html": "{}"}, "ud8.8:5.15": {"pli": "yesaṁ pañca piyāni, pañca tesaṁ dukkhāni; ", "eng": "five, ", "html": "{}"}, "ud8.8:5.16": {"pli": "yesaṁ cattāri piyāni, cattāri tesaṁ dukkhāni; ", "eng": "four, ", "html": "{}"}, "ud8.8:5.17": {"pli": "yesaṁ tīṇi piyāni, tīṇi tesaṁ dukkhāni; ", "eng": "three, ", "html": "{}"}, "ud8.8:5.18": {"pli": "yesaṁ dve piyāni, dve tesaṁ dukkhāni; ", "eng": "two, ", "html": "{}"}, "ud8.8:5.19": {"pli": "yesaṁ ekaṁ piyaṁ, ekaṁ tesaṁ dukkhaṁ; ", "eng": "or one loved one have one suffering. ", "html": "{}"}, "ud8.8:5.20": {"pli": "yesaṁ natthi piyaṁ, natthi tesaṁ dukkhaṁ, asokā te virajā anupāyāsāti vadāmī”ti. ", "eng": "Those who have no loved ones have no suffering. They are free of sorrow, stains, and anguish I say.” ", "html": "{}</p>"}, "ud8.8:6.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud8.8:7.1": {"pli": "“Ye keci sokā paridevitā vā, ", "eng": "“All the sorrows and lamentations ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud8.8:7.2": {"pli": "Dukkhā va lokasmimanekarūpā; ", "eng": "and the countless forms of suffering in the world ", "html": "<span class='verse-line'>{}</span>"}, "ud8.8:7.3": {"pli": "Piyaṁ paṭiccappabhavanti ete, ", "eng": "occur because of those that we love; ", "html": "<span class='verse-line'>{}</span>"}, "ud8.8:7.4": {"pli": "Piye asante na bhavanti ete. ", "eng": "without loved ones they do not occur. ", "html": "<span class='verse-line'>{}</span></p>"}, "ud8.8:8.1": {"pli": "Tasmā hi te sukhino vītasokā, ", "eng": "That’s why those who have no loved ones <j>at all in the world ", "html": "<p><span class='verse-line'>{}</span>"}, "ud8.8:8.2": {"pli": "Yesaṁ piyaṁ natthi kuhiñci loke; ", "eng": "are happy and free of grief. ", "html": "<span class='verse-line'>{}</span>"}, "ud8.8:8.3": {"pli": "Tasmā asokaṁ virajaṁ patthayāno, ", "eng": "So aspiring to the sorrowless and stainless, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.8:8.4": {"pli": "Piyaṁ na kayirātha kuhiñci loke”ti. ", "eng": "have no loved ones in the world at all.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud8.8:8.5": {"pli": "Aṭṭhamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud8.9": {"ud8.9:0.1": {"pli": "Udāna 8.9 ", "eng": "Heartfelt Sayings 8.9 ", "html": "<article id='ud8.9'><header><ul><li class='division'>{}</li></ul>"}, "ud8.9:0.2": {"pli": "Paṭhamadabbasutta ", "eng": "With Dabba (1st) ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud8.9:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud8.9:1.2": {"pli": "ekaṁ samayaṁ bhagavā rājagahe viharati veḷuvane kalandakanivāpe. ", "eng": "At one time the Buddha was staying near Rājagaha, in the Bamboo Grove, the squirrels’ feeding ground. ", "html": "{}"}, "ud8.9:1.3": {"pli": "Atha kho āyasmā dabbo mallaputto yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ nisīdi. ", "eng": "Then Venerable Dabba the Mallian went up to the Buddha, bowed, sat down to one side, and said to him: ", "html": "{}"}, "ud8.9:1.4": {"pli": "Ekamantaṁ nisinno kho āyasmā dabbo mallaputto bhagavantaṁ etadavoca: ", "html": "{}"}, "ud8.9:1.5": {"pli": "“parinibbānakālo me dāni, sugatā”ti. ", "eng": "“Holy One, it is the time for my full extinguishment.” ", "html": "{}"}, "ud8.9:1.6": {"pli": "“Yassadāni tvaṁ, dabba, kālaṁ maññasī”ti. ", "eng": "“Please, Dabba, do as you see fit.” ", "html": "{}</p>"}, "ud8.9:2.1": {"pli": "Atha kho āyasmā dabbo mallaputto uṭṭhāyāsanā bhagavantaṁ abhivādetvā padakkhiṇaṁ katvā vehāsaṁ abbhuggantvā ākāse antalikkhe pallaṅkena nisīditvā tejodhātuṁ samāpajjitvā vuṭṭhahitvā parinibbāyi. ", "eng": "Then Dabba rose from his seat, bowed and respectfully circled the Buddha, keeping him on his right. Then he rose into the air and, sitting cross-legged in midair, entered and withdrew from the fire element before becoming fully extinguished. ", "html": "<p>{}</p>"}, "ud8.9:3.1": {"pli": "Atha kho āyasmato dabbassa mallaputtassa vehāsaṁ abbhuggantvā ākāse antalikkhe pallaṅkena nisīditvā tejodhātuṁ samāpajjitvā vuṭṭhahitvā parinibbutassa sarīrassa jhāyamānassa ḍayhamānassa neva chārikā paññāyittha na masi. ", "eng": "Then when he was fully quenched while sitting cross-legged in midair, his body burning and combusting left neither ashes nor soot to be found. ", "html": "<p>{}"}, "ud8.9:3.2": {"pli": "Seyyathāpi nāma sappissa vā telassa vā jhāyamānassa ḍayhamānassa neva chārikā paññāyati na masi; ", "eng": "It’s like when ghee or oil blaze and burn, and neither ashes nor soot are found. ", "html": "{}"}, "ud8.9:3.3": {"pli": "evamevaṁ āyasmato dabbassa mallaputtassa vehāsaṁ abbhuggantvā ākāse antalikkhe pallaṅkena nisīditvā tejodhātuṁ samāpajjitvā vuṭṭhahitvā parinibbutassa sarīrassa jhāyamānassa ḍayhamānassa neva chārikā paññāyittha na masīti. ", "html": "{}</p>"}, "ud8.9:4.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud8.9:5.1": {"pli": "“Abhedi kāyo nirodhi saññā, ", "eng": "“The body is broken up, perception has ceased, ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud8.9:5.2": {"pli": "Vedanā sītibhaviṁsu sabbā; ", "eng": "all feelings have become cool; ", "html": "<span class='verse-line'>{}</span>"}, "ud8.9:5.3": {"pli": "Vūpasamiṁsu saṅkhārā, ", "eng": "choices are stilled, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.9:5.4": {"pli": "Viññāṇaṁ atthamāgamā”ti. ", "eng": "and consciousness come to an end.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud8.9:5.5": {"pli": "Navamaṁ. ", "html": "<p class='endsutta'>{}</p></article>"}}, "ud8.10": {"ud8.10:0.1": {"pli": "Udāna 8.10 ", "eng": "Heartfelt Sayings 8.10 ", "html": "<article id='ud8.10'><header><ul><li class='division'>{}</li></ul>"}, "ud8.10:0.2": {"pli": "Dutiyadabbasutta ", "eng": "Dabba (2nd) ", "html": "<h1 class='sutta-title'>{}</h1></header>"}, "ud8.10:1.1": {"pli": "Evaṁ me sutaṁ—", "eng": "So I have heard. ", "html": "<p><span class='evam'>{}</span>"}, "ud8.10:1.2": {"pli": "ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. ", "eng": "At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. ", "html": "{}"}, "ud8.10:1.3": {"pli": "Tatra kho bhagavā bhikkhū āmantesi: ", "eng": "There the Buddha addressed the mendicants, ", "html": "{}"}, "ud8.10:1.4": {"pli": "“bhikkhavo”ti. ", "eng": "“Mendicants!” ", "html": "{}"}, "ud8.10:1.5": {"pli": "“Bhadante”ti te bhikkhū bhagavato paccassosuṁ. ", "eng": "“Venerable sir,” they replied. ", "html": "{}"}, "ud8.10:1.6": {"pli": "Bhagavā etadavoca: ", "eng": "The Buddha said this: ", "html": "{}</p>"}, "ud8.10:2.1": {"pli": "“Dabbassa, bhikkhave, mallaputtassa vehāsaṁ abbhuggantvā ākāse antalikkhe pallaṅkena nisīditvā tejodhātuṁ samāpajjitvā vuṭṭhahitvā parinibbutassa sarīrassa jhāyamānassa ḍayhamānassa neva chārikā paññāyittha na masi. ", "eng": "“Mendicants, when Dabba the Mallian rose into the air and, sitting cross-legged in midair, entered and withdrew from the fire element before becoming fully quenched, his body burning and combusting left neither ashes nor soot to be found. ", "html": "<p>{}"}, "ud8.10:2.2": {"pli": "Seyyathāpi nāma sappissa vā telassa vā jhāyamānassa ḍayhamānassa neva chārikā paññāyati na masi; ", "eng": "It’s like when ghee or oil blaze and burn, and neither ashes nor soot are found. ", "html": "{}"}, "ud8.10:2.3": {"pli": "evamevaṁ kho, bhikkhave, dabbassa mallaputtassa vehāsaṁ abbhuggantvā ākāse antalikkhe pallaṅkena nisīditvā tejodhātuṁ samāpajjitvā vuṭṭhahitvā parinibbutassa sarīrassa jhāyamānassa ḍayhamānassa neva chārikā paññāyittha na masī”ti. ", "eng": "In the same way, when Dabba the Mallian rose into the air and, sitting cross-legged in midair, entered and withdrew from the fire element before becoming fully quenched, his body burning and combusting left neither ashes nor soot to be found.” ", "html": "{}</p>"}, "ud8.10:3.1": {"pli": "Atha kho bhagavā etamatthaṁ viditvā tāyaṁ velāyaṁ imaṁ udānaṁ udānesi: ", "eng": "Then, understanding this matter, on that occasion the Buddha expressed this heartfelt sentiment: ", "html": "<p>{}</p>"}, "ud8.10:4.1": {"pli": "“Ayoghanahatasseva, ", "eng": "“Just as the glow of iron struck ", "html": "<blockquote class='gatha'><p><span class='verse-line'>{}</span>"}, "ud8.10:4.2": {"pli": "jalato jātavedaso; ", "eng": "by a hammer in the fire of Jātaveda ", "html": "<span class='verse-line'>{}</span>"}, "ud8.10:4.3": {"pli": "Anupubbūpasantassa, ", "eng": "gradually dissipates, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.10:4.4": {"pli": "yathā na ñāyate gati. ", "eng": "its destiny unknown; ", "html": "<span class='verse-line'>{}</span></p>"}, "ud8.10:5.1": {"pli": "Evaṁ sammāvimuttānaṁ, ", "eng": "in the same way, for the rightly released, ", "html": "<p><span class='verse-line'>{}</span>"}, "ud8.10:5.2": {"pli": "kāmabandhoghatārinaṁ; ", "eng": "who have crossed the flood of sensual bonds, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.10:5.3": {"pli": "Paññāpetuṁ gati natthi, ", "eng": "and attained unshakable happiness, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.10:5.4": {"pli": "pattānaṁ acalaṁ sukhan”ti. ", "eng": "their destiny cannot be found.” ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud8.10:5.5": {"pli": "Dasamaṁ. ", "html": "<p class='endsutta'>{}</p>"}, "ud8.10:5.6": {"pli": "Pāṭaligāmiyavaggo aṭṭhamo. ", "html": "<p class='endvagga'>{}</p>"}, "ud8.10:6.0": {"pli": "Tassuddānaṁ ", "html": "<p class='uddana-intro'>{}</p>"}, "ud8.10:6.1": {"pli": "Nibbānā caturo vuttā, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "ud8.10:6.2": {"pli": "cundo pāṭaligāmiyā; ", "html": "<span class='verse-line'>{}</span>"}, "ud8.10:6.3": {"pli": "Dvidhāpatho visākhā ca, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.10:6.4": {"pli": "dabbena saha te dasāti. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud8.10:7.0": {"pli": "Udāne vaggānamuddānaṁ ", "html": "<p class='uddana-intro'>{}</p>"}, "ud8.10:7.1": {"pli": "Vaggamidaṁ paṭhamaṁ varabodhi, ", "html": "<blockquote class='uddanagatha'><p><span class='verse-line'>{}</span>"}, "ud8.10:7.2": {"pli": "Vaggamidaṁ dutiyaṁ mucalindo; ", "html": "<span class='verse-line'>{}</span>"}, "ud8.10:7.3": {"pli": "Nandakavaggavaro tatiyo tu, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.10:7.4": {"pli": "Meghiyavaggavaro ca catuttho. ", "html": "<span class='verse-line'>{}</span></p>"}, "ud8.10:8.1": {"pli": "Pañcamavaggavarantidha soṇo, ", "html": "<p><span class='verse-line'>{}</span>"}, "ud8.10:8.2": {"pli": "Chaṭṭhamavaggavaranti jaccandho; ", "html": "<span class='verse-line'>{}</span>"}, "ud8.10:8.3": {"pli": "Sattamavaggavaranti ca cūḷo, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.10:8.4": {"pli": "Pāṭaligāmiyamaṭṭhamavaggo. ", "html": "<span class='verse-line'>{}</span></p>"}, "ud8.10:9.1": {"pli": "Asītimanūnakasuttavaraṁ, ", "html": "<p><span class='verse-line'>{}</span>"}, "ud8.10:9.2": {"pli": "Vaggamidaṭṭhakaṁ suvibhattaṁ; ", "html": "<span class='verse-line'>{}</span>"}, "ud8.10:9.3": {"pli": "Dassitaṁ cakkhumatā vimalena, ", "html": "<span class='verse-line'>{}</span>"}, "ud8.10:9.4": {"pli": "Addhā hi taṁ udānamitīdamāhu. ", "html": "<span class='verse-line'>{}</span></p></blockquote>"}, "ud8.10:9.5": {"pli": "Udānapāḷi niṭṭhitā. ", "eng": "The Heartfelt Sayings are finished. ", "html": "<p class='endbook'>{}</p></article>"}}}};