window.SUTTA_DB = window.SUTTA_DB || {};
Object.assign(window.SUTTA_DB, {
  "bv1": {
    "previous": null,
    "next": "bv2",
    "content": "<article id='bv1'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv1:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv1:0.2'><span class='pli'>1. Ratanacaṅkamanakaṇḍa </span></span></h1></header>\n<p class='namo'><span class='segment' id='1.1' data-uid='bv1:1.1'><span class='pli'>Namo tassa Bhagavato Arahato Sammāsambuddhassa. </span></span></p>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv1:2.1'><span class='pli'>Brahmā ca lokādhipatī sahampatī, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv1:2.2'><span class='pli'>Katañjalī anadhivaraṁ ayācatha; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv1:2.3'><span class='pli'>“Santīdha sattāpparajakkhajātikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv1:2.4'><span class='pli'>Desehi dhammaṁ anukampimaṁ pajaṁ”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv1:3.1'><span class='pli'>Sampannavijjācaraṇassa tādino, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv1:3.2'><span class='pli'>Jutindharassantimadehadhārino; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv1:3.3'><span class='pli'>Tathāgatassappaṭipuggalassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv1:3.4'><span class='pli'>Uppajji kāruññatā sabbasatte. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv1:4.1'><span class='pli'>“Na hete jānanti sadevamānusā, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv1:4.2'><span class='pli'>Buddho ayaṁ kīdisako naruttamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv1:4.3'><span class='pli'>Iddhibalaṁ paññābalañca kīdisaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv1:4.4'><span class='pli'>Buddhabalaṁ lokahitassa kīdisaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv1:5.1'><span class='pli'>Na hete jānanti sadevamānusā, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv1:5.2'><span class='pli'>Buddho ayaṁ edisako naruttamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv1:5.3'><span class='pli'>Iddhibalaṁ paññābalañca edisaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv1:5.4'><span class='pli'>Buddhabalaṁ lokahitassa edisaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv1:6.1'><span class='pli'>Handāhaṁ dassayissāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv1:6.2'><span class='pli'>buddhabalamanuttaraṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv1:6.3'><span class='pli'>Caṅkamaṁ māpayissāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv1:6.4'><span class='pli'>nabhe ratanamaṇḍitaṁ”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv1:7.1'><span class='pli'>Bhummā mahārājikā tāvatiṁsā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv1:7.2'><span class='pli'>Yāmā ca devā tusitā ca nimmitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv1:7.3'><span class='pli'>Paranimmitā yepi ca brahmakāyikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv1:7.4'><span class='pli'>Ānanditā vipulamakaṁsu ghosaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv1:8.1'><span class='pli'>Obhāsitā ca pathavī sadevakā, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv1:8.2'><span class='pli'>Puthū ca lokantarikā asaṁvutā; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv1:8.3'><span class='pli'>Tamo ca tibbo vihato tadā ahu, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv1:8.4'><span class='pli'>Disvāna accherakaṁ pāṭihīraṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv1:9.1'><span class='pli'>Sadevagandhabbamanussarakkhase, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv1:9.2'><span class='pli'>Ābhā uḷārā vipulā ajāyatha; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv1:9.3'><span class='pli'>Imasmiṁ loke parasmiñcobhayasmiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv1:9.4'><span class='pli'>Adho ca uddhaṁ tiriyañca vitthataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv1:10.1'><span class='pli'>Sattuttamo anadhivaro vināyako, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv1:10.2'><span class='pli'>Satthā ahū devamanussapūjito; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv1:10.3'><span class='pli'>Mahānubhāvo satapuññalakkhaṇo, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv1:10.4'><span class='pli'>Dassesi accherakaṁ pāṭihīraṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv1:11.1'><span class='pli'>So yācito devavarena cakkhumā, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv1:11.2'><span class='pli'>Atthaṁ samekkhitvā tadā naruttamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv1:11.3'><span class='pli'>Caṅkamaṁ māpayi lokanāyako, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv1:11.4'><span class='pli'>Suniṭṭhitaṁ sabbaratananimmitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv1:12.1'><span class='pli'>Iddhī ca ādesanānusāsanī, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv1:12.2'><span class='pli'>Tipāṭihīre bhagavā vasī ahu; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv1:12.3'><span class='pli'>Caṅkamaṁ māpayi lokanāyako, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv1:12.4'><span class='pli'>Suniṭṭhitaṁ sabbaratananimmitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv1:13.1'><span class='pli'>Dasasahassīlokadhātuyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv1:13.2'><span class='pli'>Sinerupabbatuttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv1:13.3'><span class='pli'>Thambheva dassesi paṭipāṭiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv1:13.4'><span class='pli'>Caṅkame ratanāmaye. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv1:14.1'><span class='pli'>Dasasahassī atikkamma, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv1:14.2'><span class='pli'>caṅkamaṁ māpayī jino; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv1:14.3'><span class='pli'>Sabbasoṇṇamayā passe, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv1:14.4'><span class='pli'>caṅkame ratanāmaye. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv1:15.1'><span class='pli'>Tulāsaṅghāṭānuvaggā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv1:15.2'><span class='pli'>sovaṇṇaphalakatthatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv1:15.3'><span class='pli'>Vedikā sabbasovaṇṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv1:15.4'><span class='pli'>dubhato passesu nimmitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv1:16.1'><span class='pli'>Maṇimuttāvālikākiṇṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv1:16.2'><span class='pli'>nimmito ratanāmayo; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv1:16.3'><span class='pli'>Obhāseti disā sabbā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv1:16.4'><span class='pli'>sataraṁsīva uggato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv1:17.1'><span class='pli'>Tasmiṁ caṅkamane dhīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv1:17.2'><span class='pli'>dvattiṁsavaralakkhaṇo; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv1:17.3'><span class='pli'>Virocamāno sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv1:17.4'><span class='pli'>caṅkame caṅkamī jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv1:18.1'><span class='pli'>Dibbaṁ mandāravaṁ pupphaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv1:18.2'><span class='pli'>padumaṁ pārichattakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv1:18.3'><span class='pli'>Caṅkamane okiranti, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv1:18.4'><span class='pli'>sabbe devā samāgatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv1:19.1'><span class='pli'>Passanti taṁ devasaṅghā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv1:19.2'><span class='pli'>dasasahassī pamoditā; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv1:19.3'><span class='pli'>Namassamānā nipatanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv1:19.4'><span class='pli'>tuṭṭhahaṭṭhā pamoditā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv1:20.1'><span class='pli'>Tāvatiṁsā ca yāmā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv1:20.2'><span class='pli'>tusitā cāpi devatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv1:20.3'><span class='pli'>Nimmānaratino devā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv1:20.4'><span class='pli'>ye devā vasavattino; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.5' data-uid='bv1:20.5'><span class='pli'>Udaggacittā sumanā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.6' data-uid='bv1:20.6'><span class='pli'>passanti lokanāyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv1:21.1'><span class='pli'>Sadevagandhabbamanussarakkhasā, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv1:21.2'><span class='pli'>Nāgā supaṇṇā atha vāpi kinnarā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv1:21.3'><span class='pli'>Passanti taṁ lokahitānukampakaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv1:21.4'><span class='pli'>Nabheva accuggatacandamaṇḍalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv1:22.1'><span class='pli'>Ābhassarā subhakiṇhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv1:22.2'><span class='pli'>Vehapphalā akaniṭṭhā ca devatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv1:22.3'><span class='pli'>Susuddhasukkavatthavasanā, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv1:22.4'><span class='pli'>Tiṭṭhanti pañjalīkatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv1:23.1'><span class='pli'>Muñcanti pupphaṁ pana pañcavaṇṇikaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv1:23.2'><span class='pli'>Mandāravaṁ candanacuṇṇamissitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv1:23.3'><span class='pli'>Bhamenti celāni ca ambare tadā, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv1:23.4'><span class='pli'>“Aho jino lokahitānukampako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv1:24.1'><span class='pli'>Tuvaṁ satthā ca ketū ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv1:24.2'><span class='pli'>dhajo yūpo ca pāṇinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv1:24.3'><span class='pli'>Parāyaṇo patiṭṭhā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv1:24.4'><span class='pli'>dīpo ca dvipaduttamo”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv1:25.1'><span class='pli'>Dasasahassīlokadhātuyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv1:25.2'><span class='pli'>Devatāyo mahiddhikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv1:25.3'><span class='pli'>Parivāretvā namassanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv1:25.4'><span class='pli'>Tuṭṭhahaṭṭhā pamoditā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv1:26.1'><span class='pli'>Devatā devakaññā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv1:26.2'><span class='pli'>pasannā tuṭṭhamānasā; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv1:26.3'><span class='pli'>Pañcavaṇṇikapupphehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv1:26.4'><span class='pli'>pūjayanti narāsabhaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv1:27.1'><span class='pli'>Passanti taṁ devasaṅghā, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv1:27.2'><span class='pli'>pasannā tuṭṭhamānasā; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv1:27.3'><span class='pli'>Pañcavaṇṇikapupphehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv1:27.4'><span class='pli'>pūjayanti narāsabhaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='bv1:28.1'><span class='pli'>“Aho acchariyaṁ loke, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv1:28.2'><span class='pli'>abbhutaṁ lomahaṁsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv1:28.3'><span class='pli'>Na medisaṁ bhūtapubbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv1:28.4'><span class='pli'>accheraṁ lomahaṁsanaṁ”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='bv1:29.1'><span class='pli'>Sakasakamhi bhavane, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='bv1:29.2'><span class='pli'>nisīditvāna devatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='bv1:29.3'><span class='pli'>Hasanti tā mahāhasitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='bv1:29.4'><span class='pli'>disvānaccherakaṁ nabhe. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='bv1:30.1'><span class='pli'>Ākāsaṭṭhā ca bhūmaṭṭhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='bv1:30.2'><span class='pli'>tiṇapanthanivāsino; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='bv1:30.3'><span class='pli'>Katañjalī namassanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='bv1:30.4'><span class='pli'>tuṭṭhahaṭṭhā pamoditā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='bv1:31.1'><span class='pli'>Yepi dīghāyukā nāgā, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='bv1:31.2'><span class='pli'>puññavanto mahiddhikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='bv1:31.3'><span class='pli'>Pamoditā namassanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='bv1:31.4'><span class='pli'>pūjayanti naruttamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='bv1:32.1'><span class='pli'>Saṅgītiyo pavattenti, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='bv1:32.2'><span class='pli'>ambare anilañjase; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='bv1:32.3'><span class='pli'>Cammanaddhāni vādenti, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='bv1:32.4'><span class='pli'>disvānaccherakaṁ nabhe. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='bv1:33.1'><span class='pli'>Saṅkhā ca paṇavā ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='bv1:33.2'><span class='pli'>athopi ḍiṇḍimā bahū; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='bv1:33.3'><span class='pli'>Antalikkhasmiṁ vajjanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='bv1:33.4'><span class='pli'>disvānaccherakaṁ nabhe. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='34.1' data-uid='bv1:34.1'><span class='pli'>“Abbhuto vata no ajja, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='bv1:34.2'><span class='pli'>uppajji lomahaṁsano; </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='bv1:34.3'><span class='pli'>Dhuvamatthasiddhiṁ labhāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.4' data-uid='bv1:34.4'><span class='pli'>khaṇo no paṭipādito”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='35.1' data-uid='bv1:35.1'><span class='pli'>Buddhoti tesaṁ sutvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.2' data-uid='bv1:35.2'><span class='pli'>pīti uppajji tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='35.3' data-uid='bv1:35.3'><span class='pli'>Buddho buddhoti kathayantā, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.4' data-uid='bv1:35.4'><span class='pli'>tiṭṭhanti pañjalīkatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='36.1' data-uid='bv1:36.1'><span class='pli'>Hiṅkārā sādhukārā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.2' data-uid='bv1:36.2'><span class='pli'>ukkuṭṭhi sampahaṁsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='36.3' data-uid='bv1:36.3'><span class='pli'>Pajā ca vividhā gagane, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.4' data-uid='bv1:36.4'><span class='pli'>vattenti pañjalīkatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='37.1' data-uid='bv1:37.1'><span class='pli'>Gāyanti seḷenti ca vādayanti ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.2' data-uid='bv1:37.2'><span class='pli'>Bhujāni pothenti ca naccayanti ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='37.3' data-uid='bv1:37.3'><span class='pli'>Muñcanti pupphaṁ pana pañcavaṇṇikaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.4' data-uid='bv1:37.4'><span class='pli'>Mandāravaṁ candanacuṇṇamissitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='38.1' data-uid='bv1:38.1'><span class='pli'>“Yathā tuyhaṁ mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.2' data-uid='bv1:38.2'><span class='pli'>pādesu cakkalakkhaṇaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='38.3' data-uid='bv1:38.3'><span class='pli'>Dhajavajirapaṭākā, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.4' data-uid='bv1:38.4'><span class='pli'>vaḍḍhamānaṅkusācitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='39.1' data-uid='bv1:39.1'><span class='pli'>Rūpe sīle samādhimhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.2' data-uid='bv1:39.2'><span class='pli'>paññāya ca asādiso; </span></span></span>\n<span class='verse-line'><span class='segment' id='39.3' data-uid='bv1:39.3'><span class='pli'>Vimuttiyā asamasamo, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.4' data-uid='bv1:39.4'><span class='pli'>dhammacakkappavattane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='40.1' data-uid='bv1:40.1'><span class='pli'>Dasanāgabalaṁ kāye, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.2' data-uid='bv1:40.2'><span class='pli'>tuyhaṁ pākatikaṁ balaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='40.3' data-uid='bv1:40.3'><span class='pli'>Iddhibalena asamo, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.4' data-uid='bv1:40.4'><span class='pli'>dhammacakkappavattane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='41.1' data-uid='bv1:41.1'><span class='pli'>Evaṁ sabbaguṇūpetaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.2' data-uid='bv1:41.2'><span class='pli'>sabbaṅgasamupāgataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='41.3' data-uid='bv1:41.3'><span class='pli'>Mahāmuniṁ kāruṇikaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.4' data-uid='bv1:41.4'><span class='pli'>lokanāthaṁ namassatha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='42.1' data-uid='bv1:42.1'><span class='pli'>Abhivādanaṁ thomanañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.2' data-uid='bv1:42.2'><span class='pli'>vandanañca pasaṁsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='42.3' data-uid='bv1:42.3'><span class='pli'>Namassanañca pūjañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.4' data-uid='bv1:42.4'><span class='pli'>sabbaṁ arahasī tuvaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='43.1' data-uid='bv1:43.1'><span class='pli'>Ye keci loke vandaneyyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.2' data-uid='bv1:43.2'><span class='pli'>vandanaṁ arahanti ye; </span></span></span>\n<span class='verse-line'><span class='segment' id='43.3' data-uid='bv1:43.3'><span class='pli'>Sabbaseṭṭho mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.4' data-uid='bv1:43.4'><span class='pli'>sadiso te na vijjati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='44.1' data-uid='bv1:44.1'><span class='pli'>Sāriputto mahāpañño, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.2' data-uid='bv1:44.2'><span class='pli'>samādhijjhānakovido; </span></span></span>\n<span class='verse-line'><span class='segment' id='44.3' data-uid='bv1:44.3'><span class='pli'>Gijjhakūṭe ṭhitoyeva, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.4' data-uid='bv1:44.4'><span class='pli'>passati lokanāyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='45.1' data-uid='bv1:45.1'><span class='pli'>Suphullaṁ sālarājaṁva, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.2' data-uid='bv1:45.2'><span class='pli'>candaṁva gagane yathā; </span></span></span>\n<span class='verse-line'><span class='segment' id='45.3' data-uid='bv1:45.3'><span class='pli'>Majjhanhikeva sūriyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.4' data-uid='bv1:45.4'><span class='pli'>olokesi narāsabhaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='46.1' data-uid='bv1:46.1'><span class='pli'>Jalantaṁ dīparukkhaṁva, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.2' data-uid='bv1:46.2'><span class='pli'>Taruṇasūriyaṁva uggataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='46.3' data-uid='bv1:46.3'><span class='pli'>Byāmappabhānurañjitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.4' data-uid='bv1:46.4'><span class='pli'>Dhīraṁ passati lokanāyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='47.1' data-uid='bv1:47.1'><span class='pli'>Pañcannaṁ bhikkhusatānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.2' data-uid='bv1:47.2'><span class='pli'>katakiccāna tādinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='47.3' data-uid='bv1:47.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.4' data-uid='bv1:47.4'><span class='pli'>khaṇena sannipātayi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='48.1' data-uid='bv1:48.1'><span class='pli'>Lokappasādanaṁ nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.2' data-uid='bv1:48.2'><span class='pli'>pāṭihīraṁ nidassayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='48.3' data-uid='bv1:48.3'><span class='pli'>Amhepi tattha gantvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.4' data-uid='bv1:48.4'><span class='pli'>vandissāma mayaṁ jinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='49.1' data-uid='bv1:49.1'><span class='pli'>Etha sabbe gamissāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.2' data-uid='bv1:49.2'><span class='pli'>pucchissāma mayaṁ jinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='49.3' data-uid='bv1:49.3'><span class='pli'>Kaṅkhaṁ vinodayissāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.4' data-uid='bv1:49.4'><span class='pli'>passitvā lokanāyakaṁ”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='50.1' data-uid='bv1:50.1'><span class='pli'>Sādhūti te paṭissutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.2' data-uid='bv1:50.2'><span class='pli'>nipakā saṁvutindriyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='50.3' data-uid='bv1:50.3'><span class='pli'>Pattacīvaramādāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.4' data-uid='bv1:50.4'><span class='pli'>taramānā upāgamuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='51.1' data-uid='bv1:51.1'><span class='pli'>Khīṇāsavehi vimalehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.2' data-uid='bv1:51.2'><span class='pli'>dantehi uttame dame; </span></span></span>\n<span class='verse-line'><span class='segment' id='51.3' data-uid='bv1:51.3'><span class='pli'>Sāriputto mahāpañño, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.4' data-uid='bv1:51.4'><span class='pli'>iddhiyā upasaṅkami. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='52.1' data-uid='bv1:52.1'><span class='pli'>Tehi bhikkhūhi parivuto, </span></span></span>\n<span class='verse-line'><span class='segment' id='52.2' data-uid='bv1:52.2'><span class='pli'>sāriputto mahāgaṇī; </span></span></span>\n<span class='verse-line'><span class='segment' id='52.3' data-uid='bv1:52.3'><span class='pli'>Laḷanto devova gagane, </span></span></span>\n<span class='verse-line'><span class='segment' id='52.4' data-uid='bv1:52.4'><span class='pli'>iddhiyā upasaṅkami. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='53.1' data-uid='bv1:53.1'><span class='pli'>Ukkāsitañca khipitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='53.2' data-uid='bv1:53.2'><span class='pli'>ajjhupekkhiya subbatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='53.3' data-uid='bv1:53.3'><span class='pli'>Sagāravā sappatissā, </span></span></span>\n<span class='verse-line'><span class='segment' id='53.4' data-uid='bv1:53.4'><span class='pli'>sambuddhaṁ upasaṅkamuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='54.1' data-uid='bv1:54.1'><span class='pli'>Upasaṅkamitvā passanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='54.2' data-uid='bv1:54.2'><span class='pli'>sayambhuṁ lokanāyakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='54.3' data-uid='bv1:54.3'><span class='pli'>Nabhe accuggataṁ dhīraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='54.4' data-uid='bv1:54.4'><span class='pli'>candaṁva gagane yathā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='55.1' data-uid='bv1:55.1'><span class='pli'>Jalantaṁ dīparukkhaṁva, </span></span></span>\n<span class='verse-line'><span class='segment' id='55.2' data-uid='bv1:55.2'><span class='pli'>vijjuṁva gagane yathā; </span></span></span>\n<span class='verse-line'><span class='segment' id='55.3' data-uid='bv1:55.3'><span class='pli'>Majjhanhikeva sūriyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='55.4' data-uid='bv1:55.4'><span class='pli'>passanti lokanāyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='56.1' data-uid='bv1:56.1'><span class='pli'>Pañcabhikkhusatā sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='56.2' data-uid='bv1:56.2'><span class='pli'>passanti lokanāyakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='56.3' data-uid='bv1:56.3'><span class='pli'>Rahadamiva vippasannaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='56.4' data-uid='bv1:56.4'><span class='pli'>suphullaṁ padumaṁ yathā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='57.1' data-uid='bv1:57.1'><span class='pli'>Añjaliṁ paggahetvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='57.2' data-uid='bv1:57.2'><span class='pli'>tuṭṭhahaṭṭhā pamoditā; </span></span></span>\n<span class='verse-line'><span class='segment' id='57.3' data-uid='bv1:57.3'><span class='pli'>Namassamānā nipatanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='57.4' data-uid='bv1:57.4'><span class='pli'>satthuno cakkalakkhaṇe. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='58.1' data-uid='bv1:58.1'><span class='pli'>Sāriputto mahāpañño, </span></span></span>\n<span class='verse-line'><span class='segment' id='58.2' data-uid='bv1:58.2'><span class='pli'>koraṇḍasamasādiso; </span></span></span>\n<span class='verse-line'><span class='segment' id='58.3' data-uid='bv1:58.3'><span class='pli'>Samādhijjhānakusalo, </span></span></span>\n<span class='verse-line'><span class='segment' id='58.4' data-uid='bv1:58.4'><span class='pli'>vandate lokanāyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='59.1' data-uid='bv1:59.1'><span class='pli'>Gajjitā kālameghova, </span></span></span>\n<span class='verse-line'><span class='segment' id='59.2' data-uid='bv1:59.2'><span class='pli'>nīluppalasamasādiso; </span></span></span>\n<span class='verse-line'><span class='segment' id='59.3' data-uid='bv1:59.3'><span class='pli'>Iddhibalena asamo, </span></span></span>\n<span class='verse-line'><span class='segment' id='59.4' data-uid='bv1:59.4'><span class='pli'>moggallāno mahiddhiko. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='60.1' data-uid='bv1:60.1'><span class='pli'>Mahākassapopi ca thero, </span></span></span>\n<span class='verse-line'><span class='segment' id='60.2' data-uid='bv1:60.2'><span class='pli'>uttattakanakasannibho; </span></span></span>\n<span class='verse-line'><span class='segment' id='60.3' data-uid='bv1:60.3'><span class='pli'>Dhutaguṇe agganikkhitto, </span></span></span>\n<span class='verse-line'><span class='segment' id='60.4' data-uid='bv1:60.4'><span class='pli'>thomito satthuvaṇṇito. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='61.1' data-uid='bv1:61.1'><span class='pli'>Dibbacakkhūnaṁ yo aggo, </span></span></span>\n<span class='verse-line'><span class='segment' id='61.2' data-uid='bv1:61.2'><span class='pli'>anuruddho mahāgaṇī; </span></span></span>\n<span class='verse-line'><span class='segment' id='61.3' data-uid='bv1:61.3'><span class='pli'>Ñātiseṭṭho bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='61.4' data-uid='bv1:61.4'><span class='pli'>avidūreva tiṭṭhati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='62.1' data-uid='bv1:62.1'><span class='pli'>Āpattianāpattiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='62.2' data-uid='bv1:62.2'><span class='pli'>satekicchāya kovido; </span></span></span>\n<span class='verse-line'><span class='segment' id='62.3' data-uid='bv1:62.3'><span class='pli'>Vinaye agganikkhitto, </span></span></span>\n<span class='verse-line'><span class='segment' id='62.4' data-uid='bv1:62.4'><span class='pli'>upāli satthuvaṇṇito. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='63.1' data-uid='bv1:63.1'><span class='pli'>Sukhumanipuṇatthapaṭividdho, </span></span></span>\n<span class='verse-line'><span class='segment' id='63.2' data-uid='bv1:63.2'><span class='pli'>Kathikānaṁ pavaro gaṇī; </span></span></span>\n<span class='verse-line'><span class='segment' id='63.3' data-uid='bv1:63.3'><span class='pli'>Isi mantāniyā putto, </span></span></span>\n<span class='verse-line'><span class='segment' id='63.4' data-uid='bv1:63.4'><span class='pli'>Puṇṇo nāmāti vissuto. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='64.1' data-uid='bv1:64.1'><span class='pli'>Etesaṁ cittamaññāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='64.2' data-uid='bv1:64.2'><span class='pli'>opammakusalo muni; </span></span></span>\n<span class='verse-line'><span class='segment' id='64.3' data-uid='bv1:64.3'><span class='pli'>Kaṅkhacchedo mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='64.4' data-uid='bv1:64.4'><span class='pli'>kathesi attano guṇaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='65.1' data-uid='bv1:65.1'><span class='pli'>“Cattāro te asaṅkheyyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='65.2' data-uid='bv1:65.2'><span class='pli'>koṭi yesaṁ na nāyati; </span></span></span>\n<span class='verse-line'><span class='segment' id='65.3' data-uid='bv1:65.3'><span class='pli'>Sattakāyo ca ākāso, </span></span></span>\n<span class='verse-line'><span class='segment' id='65.4' data-uid='bv1:65.4'><span class='pli'>cakkavāḷā canantakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='65.5' data-uid='bv1:65.5'><span class='pli'>Buddhañāṇaṁ appameyyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='65.6' data-uid='bv1:65.6'><span class='pli'>na sakkā ete vijānituṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='66.1' data-uid='bv1:66.1'><span class='pli'>Kimetaṁ acchariyaṁ loke, </span></span></span>\n<span class='verse-line'><span class='segment' id='66.2' data-uid='bv1:66.2'><span class='pli'>yaṁ me iddhivikubbanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='66.3' data-uid='bv1:66.3'><span class='pli'>Aññe bahū acchariyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='66.4' data-uid='bv1:66.4'><span class='pli'>abbhutā lomahaṁsanā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='67.1' data-uid='bv1:67.1'><span class='pli'>Yadāhaṁ tusite kāye, </span></span></span>\n<span class='verse-line'><span class='segment' id='67.2' data-uid='bv1:67.2'><span class='pli'>santusito nāmahaṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='67.3' data-uid='bv1:67.3'><span class='pli'>Dasasahassī samāgamma, </span></span></span>\n<span class='verse-line'><span class='segment' id='67.4' data-uid='bv1:67.4'><span class='pli'>yācanti pañjalī mamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='68.1' data-uid='bv1:68.1'><span class='pli'>‘Kālo kho te mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='68.2' data-uid='bv1:68.2'><span class='pli'>uppajja mātukucchiyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='68.3' data-uid='bv1:68.3'><span class='pli'>Sadevakaṁ tārayanto, </span></span></span>\n<span class='verse-line'><span class='segment' id='68.4' data-uid='bv1:68.4'><span class='pli'>bujjhassu amataṁ padaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='69.1' data-uid='bv1:69.1'><span class='pli'>Tusitā kāyā cavitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='69.2' data-uid='bv1:69.2'><span class='pli'>yadā okkami kucchiyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='69.3' data-uid='bv1:69.3'><span class='pli'>Dasasahassīlokadhātu, </span></span></span>\n<span class='verse-line'><span class='segment' id='69.4' data-uid='bv1:69.4'><span class='pli'>kampittha dharaṇī tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='70.1' data-uid='bv1:70.1'><span class='pli'>Yadāhaṁ mātukucchito, </span></span></span>\n<span class='verse-line'><span class='segment' id='70.2' data-uid='bv1:70.2'><span class='pli'>sampajānova nikkhamiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='70.3' data-uid='bv1:70.3'><span class='pli'>Sādhukāraṁ pavattenti, </span></span></span>\n<span class='verse-line'><span class='segment' id='70.4' data-uid='bv1:70.4'><span class='pli'>dasasahassī pakampatha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='71.1' data-uid='bv1:71.1'><span class='pli'>Okkantiṁ me samo natthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='71.2' data-uid='bv1:71.2'><span class='pli'>jātito abhinikkhame; </span></span></span>\n<span class='verse-line'><span class='segment' id='71.3' data-uid='bv1:71.3'><span class='pli'>Sambodhiyaṁ ahaṁ seṭṭho, </span></span></span>\n<span class='verse-line'><span class='segment' id='71.4' data-uid='bv1:71.4'><span class='pli'>dhammacakkappavattane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='72.1' data-uid='bv1:72.1'><span class='pli'>Aho acchariyaṁ loke, </span></span></span>\n<span class='verse-line'><span class='segment' id='72.2' data-uid='bv1:72.2'><span class='pli'>buddhānaṁ guṇamahantatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='72.3' data-uid='bv1:72.3'><span class='pli'>Dasasahassīlokadhātu, </span></span></span>\n<span class='verse-line'><span class='segment' id='72.4' data-uid='bv1:72.4'><span class='pli'>chappakāraṁ pakampatha; </span></span></span>\n<span class='verse-line'><span class='segment' id='72.5' data-uid='bv1:72.5'><span class='pli'>Obhāso ca mahā āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='72.6' data-uid='bv1:72.6'><span class='pli'>accheraṁ lomahaṁsanaṁ”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='73.1' data-uid='bv1:73.1'><span class='pli'>Bhagavā tamhi samaye, </span></span></span>\n<span class='verse-line'><span class='segment' id='73.2' data-uid='bv1:73.2'><span class='pli'>lokajeṭṭho narāsabho; </span></span></span>\n<span class='verse-line'><span class='segment' id='73.3' data-uid='bv1:73.3'><span class='pli'>Sadevakaṁ dassayanto, </span></span></span>\n<span class='verse-line'><span class='segment' id='73.4' data-uid='bv1:73.4'><span class='pli'>iddhiyā caṅkamī jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='74.1' data-uid='bv1:74.1'><span class='pli'>Caṅkame caṅkamantova, </span></span></span>\n<span class='verse-line'><span class='segment' id='74.2' data-uid='bv1:74.2'><span class='pli'>kathesi lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='74.3' data-uid='bv1:74.3'><span class='pli'>Antarā na nivatteti, </span></span></span>\n<span class='verse-line'><span class='segment' id='74.4' data-uid='bv1:74.4'><span class='pli'>catuhatthe caṅkame yathā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='75.1' data-uid='bv1:75.1'><span class='pli'>Sāriputto mahāpañño, </span></span></span>\n<span class='verse-line'><span class='segment' id='75.2' data-uid='bv1:75.2'><span class='pli'>samādhijjhānakovido; </span></span></span>\n<span class='verse-line'><span class='segment' id='75.3' data-uid='bv1:75.3'><span class='pli'>Paññāya pāramippatto, </span></span></span>\n<span class='verse-line'><span class='segment' id='75.4' data-uid='bv1:75.4'><span class='pli'>pucchati lokanāyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='76.1' data-uid='bv1:76.1'><span class='pli'>“Kīdiso te mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='76.2' data-uid='bv1:76.2'><span class='pli'>abhinīhāro naruttama; </span></span></span>\n<span class='verse-line'><span class='segment' id='76.3' data-uid='bv1:76.3'><span class='pli'>Kamhi kāle tayā dhīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='76.4' data-uid='bv1:76.4'><span class='pli'>patthitā bodhimuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='77.1' data-uid='bv1:77.1'><span class='pli'>Dānaṁ sīlañca nekkhammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='77.2' data-uid='bv1:77.2'><span class='pli'>paññāvīriyañca kīdisaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='77.3' data-uid='bv1:77.3'><span class='pli'>Khantisaccamadhiṭṭhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='77.4' data-uid='bv1:77.4'><span class='pli'>mettupekkhā ca kīdisā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='78.1' data-uid='bv1:78.1'><span class='pli'>Dasa pāramī tayā dhīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='78.2' data-uid='bv1:78.2'><span class='pli'>kīdisī lokanāyaka; </span></span></span>\n<span class='verse-line'><span class='segment' id='78.3' data-uid='bv1:78.3'><span class='pli'>Kathaṁ upapāramī puṇṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='78.4' data-uid='bv1:78.4'><span class='pli'>paramatthapāramī kathaṁ”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='79.1' data-uid='bv1:79.1'><span class='pli'>Tassa puṭṭho viyākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='79.2' data-uid='bv1:79.2'><span class='pli'>karavīkamadhuragiro; </span></span></span>\n<span class='verse-line'><span class='segment' id='79.3' data-uid='bv1:79.3'><span class='pli'>Nibbāpayanto hadayaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='79.4' data-uid='bv1:79.4'><span class='pli'>hāsayanto sadevakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='80.1' data-uid='bv1:80.1'><span class='pli'>Atītabuddhānaṁ jinānaṁ desitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='80.2' data-uid='bv1:80.2'><span class='pli'>Nikīlitaṁ buddhaparamparāgataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='80.3' data-uid='bv1:80.3'><span class='pli'>Pubbenivāsānugatāya buddhiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='80.4' data-uid='bv1:80.4'><span class='pli'>Pakāsayī lokahitaṁ sadevake. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='81.1' data-uid='bv1:81.1'><span class='pli'>“Pītipāmojjajananaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='81.2' data-uid='bv1:81.2'><span class='pli'>sokasallavinodanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='81.3' data-uid='bv1:81.3'><span class='pli'>Sabbasampattipaṭilābhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='81.4' data-uid='bv1:81.4'><span class='pli'>cittīkatvā suṇātha me. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='82.1' data-uid='bv1:82.1'><span class='pli'>Madanimmadanaṁ sokanudaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='82.2' data-uid='bv1:82.2'><span class='pli'>Saṁsāraparimocanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='82.3' data-uid='bv1:82.3'><span class='pli'>Sabbadukkhakkhayaṁ maggaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='82.4' data-uid='bv1:82.4'><span class='pli'>Sakkaccaṁ paṭipajjathā”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='83.1' data-uid='bv1:83.1'><span class='pli'>Ratanacaṅkamanakaṇḍo niṭṭhito. </span></span></p></article>\n"
  },
  "bv2": {
    "previous": "bv1",
    "next": "bv3",
    "content": "<article id='bv2'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv2:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv2:0.2'><span class='pli'>2 Sumedhapatthanākathā </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv2:1.1'><span class='pli'>“Kappe ca satasahasse, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv2:1.2'><span class='pli'>caturo ca asaṅkhiye; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv2:1.3'><span class='pli'>Amaraṁ nāma nagaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv2:1.4'><span class='pli'>dassaneyyaṁ manoramaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv2:2.1'><span class='pli'>Dasahi saddehi avivittaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv2:2.2'><span class='pli'>Annapānasamāyutaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv2:2.3'><span class='pli'>Hatthisaddaṁ assasaddaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv2:2.4'><span class='pli'>Bherisaṅkharathāni ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.5' data-uid='bv2:2.5'><span class='pli'>Khādatha pivatha ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.6' data-uid='bv2:2.6'><span class='pli'>Annapānena ghositaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv2:3.1'><span class='pli'>Nagaraṁ sabbaṅgasampannaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv2:3.2'><span class='pli'>sabbakammamupāgataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv2:3.3'><span class='pli'>Sattaratanasampannaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv2:3.4'><span class='pli'>nānājanasamākulaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.5' data-uid='bv2:3.5'><span class='pli'>Samiddhaṁ devanagaraṁva, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.6' data-uid='bv2:3.6'><span class='pli'>āvāsaṁ puññakamminaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv2:4.1'><span class='pli'>Nagare amaravatiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv2:4.2'><span class='pli'>sumedho nāma brāhmaṇo; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv2:4.3'><span class='pli'>Anekakoṭisannicayo, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv2:4.4'><span class='pli'>pahūtadhanadhaññavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv2:5.1'><span class='pli'>Ajjhāyako mantadharo, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv2:5.2'><span class='pli'>tiṇṇaṁ vedāna pāragū; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv2:5.3'><span class='pli'>Lakkhaṇe itihāse ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv2:5.4'><span class='pli'>sadhamme pāramiṁ gato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv2:6.1'><span class='pli'>Rahogato nisīditvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv2:6.2'><span class='pli'>evaṁ cintesahaṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv2:6.3'><span class='pli'>‘Dukkho punabbhavo nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv2:6.4'><span class='pli'>sarīrassa ca bhedanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv2:7.1'><span class='pli'>Jātidhammo jarādhammo, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv2:7.2'><span class='pli'>Byādhidhammo sahaṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv2:7.3'><span class='pli'>Ajaraṁ amataṁ khemaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv2:7.4'><span class='pli'>Pariyesissāmi nibbutiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv2:8.1'><span class='pli'>Yaṁnūnimaṁ pūtikāyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv2:8.2'><span class='pli'>nānākuṇapapūritaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv2:8.3'><span class='pli'>Chaḍḍayitvāna gaccheyyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv2:8.4'><span class='pli'>anapekkho anatthiko. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv2:9.1'><span class='pli'>Atthi hehiti so maggo, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv2:9.2'><span class='pli'>na so sakkā na hetuye; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv2:9.3'><span class='pli'>Pariyesissāmi taṁ maggaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv2:9.4'><span class='pli'>bhavato parimuttiyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv2:10.1'><span class='pli'>Yathāpi dukkhe vijjante, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv2:10.2'><span class='pli'>sukhaṁ nāmapi vijjati; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv2:10.3'><span class='pli'>Evaṁ bhave vijjamāne, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv2:10.4'><span class='pli'>vibhavopi icchitabbako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv2:11.1'><span class='pli'>Yathāpi uṇhe vijjante, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv2:11.2'><span class='pli'>aparaṁ vijjati sītalaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv2:11.3'><span class='pli'>Evaṁ tividhaggi vijjante, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv2:11.4'><span class='pli'>nibbānaṁ icchitabbakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv2:12.1'><span class='pli'>Yathāpi pāpe vijjante, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv2:12.2'><span class='pli'>kalyāṇamapi vijjati; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv2:12.3'><span class='pli'>Evamevaṁ jāti vijjante, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv2:12.4'><span class='pli'>ajātipicchitabbakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv2:13.1'><span class='pli'>Yathā gūthagato puriso, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv2:13.2'><span class='pli'>taḷākaṁ disvāna pūritaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv2:13.3'><span class='pli'>Na gavesati taṁ taḷākaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv2:13.4'><span class='pli'>na doso taḷākassa so. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv2:14.1'><span class='pli'>Evaṁ kilesamaladhovaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv2:14.2'><span class='pli'>vijjante amatantaḷe; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv2:14.3'><span class='pli'>Na gavesati taṁ taḷākaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv2:14.4'><span class='pli'>na doso amatantaḷe. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv2:15.1'><span class='pli'>Yathā arīhi pariruddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv2:15.2'><span class='pli'>vijjante gamanampathe; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv2:15.3'><span class='pli'>Na palāyati so puriso, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv2:15.4'><span class='pli'>na doso añjasassa so. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv2:16.1'><span class='pli'>Evaṁ kilesapariruddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv2:16.2'><span class='pli'>vijjamāne sive pathe; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv2:16.3'><span class='pli'>Na gavesati taṁ maggaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv2:16.4'><span class='pli'>na doso sivamañjase. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv2:17.1'><span class='pli'>Yathāpi byādhito puriso, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv2:17.2'><span class='pli'>vijjamāne tikicchake; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv2:17.3'><span class='pli'>Na tikicchāpeti taṁ byādhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv2:17.4'><span class='pli'>na doso so tikicchake. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv2:18.1'><span class='pli'>Evaṁ kilesabyādhīhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv2:18.2'><span class='pli'>Dukkhito paripīḷito; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv2:18.3'><span class='pli'>Na gavesati taṁ ācariyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv2:18.4'><span class='pli'>Na doso so vināyake. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv2:19.1'><span class='pli'>Yathāpi kuṇapaṁ puriso, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv2:19.2'><span class='pli'>kaṇṭhe baddhaṁ jigucchiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv2:19.3'><span class='pli'>Mocayitvāna gaccheyya, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv2:19.4'><span class='pli'>sukhī serī sayaṁvasī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv2:20.1'><span class='pli'>Tathevimaṁ pūtikāyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv2:20.2'><span class='pli'>nānākuṇapasañcayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv2:20.3'><span class='pli'>Chaḍḍayitvāna gaccheyyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv2:20.4'><span class='pli'>anapekkho anatthiko. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv2:21.1'><span class='pli'>Yathā uccāraṭṭhānamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv2:21.2'><span class='pli'>karīsaṁ naranāriyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv2:21.3'><span class='pli'>Chaḍḍayitvāna gacchanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv2:21.4'><span class='pli'>anapekkhā anatthikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv2:22.1'><span class='pli'>Evamevāhaṁ imaṁ kāyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv2:22.2'><span class='pli'>nānākuṇapapūritaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv2:22.3'><span class='pli'>Chaḍḍayitvāna gacchissaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv2:22.4'><span class='pli'>vaccaṁ katvā yathā kuṭiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv2:23.1'><span class='pli'>Yathāpi jajjaraṁ nāvaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv2:23.2'><span class='pli'>paluggaṁ udagāhiniṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv2:23.3'><span class='pli'>Sāmī chaḍḍetvā gacchanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv2:23.4'><span class='pli'>anapekkhā anatthikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv2:24.1'><span class='pli'>Evamevāhaṁ imaṁ kāyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv2:24.2'><span class='pli'>navacchiddaṁ dhuvassavaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv2:24.3'><span class='pli'>Chaḍḍayitvāna gacchissaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv2:24.4'><span class='pli'>jiṇṇanāvaṁva sāmikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv2:25.1'><span class='pli'>Yathāpi puriso corehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv2:25.2'><span class='pli'>gacchanto bhaṇḍamādiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv2:25.3'><span class='pli'>Bhaṇḍacchedabhayaṁ disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv2:25.4'><span class='pli'>chaḍḍayitvāna gacchati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv2:26.1'><span class='pli'>Evamevaṁ ayaṁ kāyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv2:26.2'><span class='pli'>mahācorasamo viya; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv2:26.3'><span class='pli'>Pahāyimaṁ gamissāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv2:26.4'><span class='pli'>kusalacchedanā bhayā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv2:27.1'><span class='pli'>Evāhaṁ cintayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv2:27.2'><span class='pli'>nekakoṭisataṁ dhanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv2:27.3'><span class='pli'>Nāthānāthānaṁ datvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv2:27.4'><span class='pli'>himavantamupāgamiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='bv2:28.1'><span class='pli'>Himavantassāvidūre, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv2:28.2'><span class='pli'>dhammiko nāma pabbato; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv2:28.3'><span class='pli'>Assamo sukato mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv2:28.4'><span class='pli'>paṇṇasālā sumāpitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='bv2:29.1'><span class='pli'>Caṅkamaṁ tattha māpesiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='bv2:29.2'><span class='pli'>pañcadosavivajjitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='bv2:29.3'><span class='pli'>Aṭṭhaguṇasamupetaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='bv2:29.4'><span class='pli'>abhiññābalamāhariṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='bv2:30.1'><span class='pli'>Sāṭakaṁ pajahiṁ tattha, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='bv2:30.2'><span class='pli'>navadosamupāgataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='bv2:30.3'><span class='pli'>Vākacīraṁ nivāsesiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='bv2:30.4'><span class='pli'>dvādasaguṇamupāgataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='bv2:31.1'><span class='pli'>Aṭṭhadosasamākiṇṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='bv2:31.2'><span class='pli'>pajahiṁ paṇṇasālakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='bv2:31.3'><span class='pli'>Upāgamiṁ rukkhamūlaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='bv2:31.4'><span class='pli'>guṇe dasahupāgataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='bv2:32.1'><span class='pli'>Vāpitaṁ ropitaṁ dhaññaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='bv2:32.2'><span class='pli'>pajahiṁ niravasesato; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='bv2:32.3'><span class='pli'>Anekaguṇasampannaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='bv2:32.4'><span class='pli'>pavattaphalamādiyiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='bv2:33.1'><span class='pli'>Tatthappadhānaṁ padahiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='bv2:33.2'><span class='pli'>nisajjaṭṭhānacaṅkame; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='bv2:33.3'><span class='pli'>Abbhantaramhi sattāhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='bv2:33.4'><span class='pli'>abhiññābalapāpuṇiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='34.1' data-uid='bv2:34.1'><span class='pli'>Evaṁ me siddhippattassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='bv2:34.2'><span class='pli'>vasībhūtassa sāsane; </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='bv2:34.3'><span class='pli'>Dīpaṅkaro nāma jino, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.4' data-uid='bv2:34.4'><span class='pli'>uppajji lokanāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='35.1' data-uid='bv2:35.1'><span class='pli'>Uppajjante ca jāyante, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.2' data-uid='bv2:35.2'><span class='pli'>bujjhante dhammadesane; </span></span></span>\n<span class='verse-line'><span class='segment' id='35.3' data-uid='bv2:35.3'><span class='pli'>Caturo nimitte nāddasaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.4' data-uid='bv2:35.4'><span class='pli'>jhānaratisamappito. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='36.1' data-uid='bv2:36.1'><span class='pli'>Paccantadesavisaye, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.2' data-uid='bv2:36.2'><span class='pli'>nimantetvā tathāgataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='36.3' data-uid='bv2:36.3'><span class='pli'>Tassa āgamanaṁ maggaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.4' data-uid='bv2:36.4'><span class='pli'>sodhenti tuṭṭhamānasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='37.1' data-uid='bv2:37.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.2' data-uid='bv2:37.2'><span class='pli'>nikkhamitvā sakassamā; </span></span></span>\n<span class='verse-line'><span class='segment' id='37.3' data-uid='bv2:37.3'><span class='pli'>Dhunanto vākacīrāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.4' data-uid='bv2:37.4'><span class='pli'>gacchāmi ambare tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='38.1' data-uid='bv2:38.1'><span class='pli'>Vedajātaṁ janaṁ disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.2' data-uid='bv2:38.2'><span class='pli'>tuṭṭhahaṭṭhaṁ pamoditaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='38.3' data-uid='bv2:38.3'><span class='pli'>Orohitvāna gaganā, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.4' data-uid='bv2:38.4'><span class='pli'>manusse pucchi tāvade. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='39.1' data-uid='bv2:39.1'><span class='pli'>‘Tuṭṭhahaṭṭho pamudito, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.2' data-uid='bv2:39.2'><span class='pli'>vedajāto mahājano; </span></span></span>\n<span class='verse-line'><span class='segment' id='39.3' data-uid='bv2:39.3'><span class='pli'>Kassa sodhīyati maggo, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.4' data-uid='bv2:39.4'><span class='pli'>añjasaṁ vaṭumāyanaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='40.1' data-uid='bv2:40.1'><span class='pli'>Te me puṭṭhā viyākaṁsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.2' data-uid='bv2:40.2'><span class='pli'>‘buddho loke anuttaro; </span></span></span>\n<span class='verse-line'><span class='segment' id='40.3' data-uid='bv2:40.3'><span class='pli'>Dīpaṅkaro nāma jino, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.4' data-uid='bv2:40.4'><span class='pli'>uppajji lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='40.5' data-uid='bv2:40.5'><span class='pli'>Tassa sodhīyati maggo, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.6' data-uid='bv2:40.6'><span class='pli'>añjasaṁ vaṭumāyanaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='41.1' data-uid='bv2:41.1'><span class='pli'>Buddhotivacanaṁ sutvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.2' data-uid='bv2:41.2'><span class='pli'>pīti uppajji tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='41.3' data-uid='bv2:41.3'><span class='pli'>Buddho buddhoti kathayanto, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.4' data-uid='bv2:41.4'><span class='pli'>somanassaṁ pavedayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='42.1' data-uid='bv2:42.1'><span class='pli'>Tattha ṭhatvā vicintesiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.2' data-uid='bv2:42.2'><span class='pli'>tuṭṭho saṁviggamānaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='42.3' data-uid='bv2:42.3'><span class='pli'>‘Idha bījāni ropissaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.4' data-uid='bv2:42.4'><span class='pli'>khaṇo ve mā upaccagā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='43.1' data-uid='bv2:43.1'><span class='pli'>‘Yadi buddhassa sodhetha, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.2' data-uid='bv2:43.2'><span class='pli'>ekokāsaṁ dadātha me; </span></span></span>\n<span class='verse-line'><span class='segment' id='43.3' data-uid='bv2:43.3'><span class='pli'>Ahampi sodhayissāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.4' data-uid='bv2:43.4'><span class='pli'>añjasaṁ vaṭumāyanaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='44.1' data-uid='bv2:44.1'><span class='pli'>Adaṁsu te mamokāsaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.2' data-uid='bv2:44.2'><span class='pli'>sodhetuṁ añjasaṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='44.3' data-uid='bv2:44.3'><span class='pli'>Buddho buddhoti cintento, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.4' data-uid='bv2:44.4'><span class='pli'>maggaṁ sodhemahaṁ tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='45.1' data-uid='bv2:45.1'><span class='pli'>Aniṭṭhite mamokāse, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.2' data-uid='bv2:45.2'><span class='pli'>dīpaṅkaro mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='45.3' data-uid='bv2:45.3'><span class='pli'>Catūhi satasahassehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.4' data-uid='bv2:45.4'><span class='pli'>chaḷabhiññehi tādihi; </span></span></span>\n<span class='verse-line'><span class='segment' id='45.5' data-uid='bv2:45.5'><span class='pli'>Khīṇāsavehi vimalehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.6' data-uid='bv2:45.6'><span class='pli'>paṭipajji añjasaṁ jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='46.1' data-uid='bv2:46.1'><span class='pli'>Paccuggamanā vattanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.2' data-uid='bv2:46.2'><span class='pli'>vajjanti bheriyo bahū; </span></span></span>\n<span class='verse-line'><span class='segment' id='46.3' data-uid='bv2:46.3'><span class='pli'>Āmoditā naramarū, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.4' data-uid='bv2:46.4'><span class='pli'>sādhukāraṁ pavattayuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='47.1' data-uid='bv2:47.1'><span class='pli'>Devā manusse passanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.2' data-uid='bv2:47.2'><span class='pli'>manussāpi ca devatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='47.3' data-uid='bv2:47.3'><span class='pli'>Ubhopi te pañjalikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.4' data-uid='bv2:47.4'><span class='pli'>anuyanti tathāgataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='48.1' data-uid='bv2:48.1'><span class='pli'>Devā dibbehi turiyehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.2' data-uid='bv2:48.2'><span class='pli'>manussā mānusehi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='48.3' data-uid='bv2:48.3'><span class='pli'>Ubhopi te vajjayantā, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.4' data-uid='bv2:48.4'><span class='pli'>anuyanti tathāgataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='49.1' data-uid='bv2:49.1'><span class='pli'>Dibbaṁ mandāravaṁ pupphaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.2' data-uid='bv2:49.2'><span class='pli'>padumaṁ pārichattakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='49.3' data-uid='bv2:49.3'><span class='pli'>Disodisaṁ okiranti, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.4' data-uid='bv2:49.4'><span class='pli'>ākāsanabhagatā marū. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='50.1' data-uid='bv2:50.1'><span class='pli'>Dibbaṁ candanacuṇṇañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.2' data-uid='bv2:50.2'><span class='pli'>varagandhañca kevalaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='50.3' data-uid='bv2:50.3'><span class='pli'>Disodisaṁ okiranti, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.4' data-uid='bv2:50.4'><span class='pli'>ākāsanabhagatā marū. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='51.1' data-uid='bv2:51.1'><span class='pli'>Campakaṁ saralaṁ nīpaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.2' data-uid='bv2:51.2'><span class='pli'>nāgapunnāgaketakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='51.3' data-uid='bv2:51.3'><span class='pli'>Disodisaṁ ukkhipanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.4' data-uid='bv2:51.4'><span class='pli'>bhūmitalagatā narā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='52.1' data-uid='bv2:52.1'><span class='pli'>Kese muñcitvāhaṁ tattha, </span></span></span>\n<span class='verse-line'><span class='segment' id='52.2' data-uid='bv2:52.2'><span class='pli'>vākacīrañca cammakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='52.3' data-uid='bv2:52.3'><span class='pli'>Kalale pattharitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='52.4' data-uid='bv2:52.4'><span class='pli'>avakujjo nipajjahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='53.1' data-uid='bv2:53.1'><span class='pli'>‘Akkamitvāna maṁ buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='53.2' data-uid='bv2:53.2'><span class='pli'>saha sissehi gacchatu; </span></span></span>\n<span class='verse-line'><span class='segment' id='53.3' data-uid='bv2:53.3'><span class='pli'>Mā naṁ kalale akkamittha, </span></span></span>\n<span class='verse-line'><span class='segment' id='53.4' data-uid='bv2:53.4'><span class='pli'>hitāya me bhavissati’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='54.1' data-uid='bv2:54.1'><span class='pli'>Pathaviyaṁ nipannassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='54.2' data-uid='bv2:54.2'><span class='pli'>evaṁ me āsi cetaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='54.3' data-uid='bv2:54.3'><span class='pli'>‘Icchamāno ahaṁ ajja, </span></span></span>\n<span class='verse-line'><span class='segment' id='54.4' data-uid='bv2:54.4'><span class='pli'>kilese jhāpaye mama. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='55.1' data-uid='bv2:55.1'><span class='pli'>Kiṁ me aññātavesena, </span></span></span>\n<span class='verse-line'><span class='segment' id='55.2' data-uid='bv2:55.2'><span class='pli'>dhammaṁ sacchikatenidha; </span></span></span>\n<span class='verse-line'><span class='segment' id='55.3' data-uid='bv2:55.3'><span class='pli'>Sabbaññutaṁ pāpuṇitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='55.4' data-uid='bv2:55.4'><span class='pli'>buddho hessaṁ sadevake. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='56.1' data-uid='bv2:56.1'><span class='pli'>Kiṁ me ekena tiṇṇena, </span></span></span>\n<span class='verse-line'><span class='segment' id='56.2' data-uid='bv2:56.2'><span class='pli'>purisena thāmadassinā; </span></span></span>\n<span class='verse-line'><span class='segment' id='56.3' data-uid='bv2:56.3'><span class='pli'>Sabbaññutaṁ pāpuṇitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='56.4' data-uid='bv2:56.4'><span class='pli'>santāressaṁ sadevakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='57.1' data-uid='bv2:57.1'><span class='pli'>Iminā me adhikārena, </span></span></span>\n<span class='verse-line'><span class='segment' id='57.2' data-uid='bv2:57.2'><span class='pli'>katena purisuttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='57.3' data-uid='bv2:57.3'><span class='pli'>Sabbaññutaṁ pāpuṇitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='57.4' data-uid='bv2:57.4'><span class='pli'>tāremi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='58.1' data-uid='bv2:58.1'><span class='pli'>Saṁsārasotaṁ chinditvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='58.2' data-uid='bv2:58.2'><span class='pli'>viddhaṁsetvā tayo bhave; </span></span></span>\n<span class='verse-line'><span class='segment' id='58.3' data-uid='bv2:58.3'><span class='pli'>Dhammanāvaṁ samāruyha, </span></span></span>\n<span class='verse-line'><span class='segment' id='58.4' data-uid='bv2:58.4'><span class='pli'>santāressaṁ sadevakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='59.1' data-uid='bv2:59.1'><span class='pli'>Manussattaṁ liṅgasampatti, </span></span></span>\n<span class='verse-line'><span class='segment' id='59.2' data-uid='bv2:59.2'><span class='pli'>hetu satthāradassanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='59.3' data-uid='bv2:59.3'><span class='pli'>Pabbajjā guṇasampatti, </span></span></span>\n<span class='verse-line'><span class='segment' id='59.4' data-uid='bv2:59.4'><span class='pli'>adhikāro ca chandatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='59.5' data-uid='bv2:59.5'><span class='pli'>Aṭṭhadhammasamodhānā, </span></span></span>\n<span class='verse-line'><span class='segment' id='59.6' data-uid='bv2:59.6'><span class='pli'>abhinīhāro samijjhati’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='60.1' data-uid='bv2:60.1'><span class='pli'>Dīpaṅkaro lokavidū, </span></span></span>\n<span class='verse-line'><span class='segment' id='60.2' data-uid='bv2:60.2'><span class='pli'>āhutīnaṁ paṭiggaho; </span></span></span>\n<span class='verse-line'><span class='segment' id='60.3' data-uid='bv2:60.3'><span class='pli'>Ussīsake maṁ ṭhatvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='60.4' data-uid='bv2:60.4'><span class='pli'>idaṁ vacanamabravi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='61.1' data-uid='bv2:61.1'><span class='pli'>‘Passatha imaṁ tāpasaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='61.2' data-uid='bv2:61.2'><span class='pli'>jaṭilaṁ uggatāpanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='61.3' data-uid='bv2:61.3'><span class='pli'>Aparimeyyito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='61.4' data-uid='bv2:61.4'><span class='pli'>buddho loke bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='62.1' data-uid='bv2:62.1'><span class='pli'>Ahu kapilavhayā rammā, </span></span></span>\n<span class='verse-line'><span class='segment' id='62.2' data-uid='bv2:62.2'><span class='pli'>nikkhamitvā tathāgato; </span></span></span>\n<span class='verse-line'><span class='segment' id='62.3' data-uid='bv2:62.3'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='62.4' data-uid='bv2:62.4'><span class='pli'>katvā dukkarakārikaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='63.1' data-uid='bv2:63.1'><span class='pli'>Ajapālarukkhamūlasmiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='63.2' data-uid='bv2:63.2'><span class='pli'>nisīditvā tathāgato; </span></span></span>\n<span class='verse-line'><span class='segment' id='63.3' data-uid='bv2:63.3'><span class='pli'>Tattha pāyāsaṁ paggayha, </span></span></span>\n<span class='verse-line'><span class='segment' id='63.4' data-uid='bv2:63.4'><span class='pli'>nerañjaramupehiti. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='64.1' data-uid='bv2:64.1'><span class='pli'>Nerañjarāya tīramhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='64.2' data-uid='bv2:64.2'><span class='pli'>pāyāsaṁ ada so jino; </span></span></span>\n<span class='verse-line'><span class='segment' id='64.3' data-uid='bv2:64.3'><span class='pli'>Paṭiyattavaramaggena, </span></span></span>\n<span class='verse-line'><span class='segment' id='64.4' data-uid='bv2:64.4'><span class='pli'>bodhimūlamupehiti. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='65.1' data-uid='bv2:65.1'><span class='pli'>Tato padakkhiṇaṁ katvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='65.2' data-uid='bv2:65.2'><span class='pli'>bodhimaṇḍaṁ anuttaro; </span></span></span>\n<span class='verse-line'><span class='segment' id='65.3' data-uid='bv2:65.3'><span class='pli'>Assattharukkhamūlamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='65.4' data-uid='bv2:65.4'><span class='pli'>bujjhissati mahāyaso. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='66.1' data-uid='bv2:66.1'><span class='pli'>Imassa janikā mātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='66.2' data-uid='bv2:66.2'><span class='pli'>māyā nāma bhavissati; </span></span></span>\n<span class='verse-line'><span class='segment' id='66.3' data-uid='bv2:66.3'><span class='pli'>Pitā suddhodano nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='66.4' data-uid='bv2:66.4'><span class='pli'>ayaṁ hessati gotamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='67.1' data-uid='bv2:67.1'><span class='pli'>Anāsavā vītarāgā, </span></span></span>\n<span class='verse-line'><span class='segment' id='67.2' data-uid='bv2:67.2'><span class='pli'>santacittā samāhitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='67.3' data-uid='bv2:67.3'><span class='pli'>Kolito upatisso ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='67.4' data-uid='bv2:67.4'><span class='pli'>aggā hessanti sāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='67.5' data-uid='bv2:67.5'><span class='pli'>Ānando nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='67.6' data-uid='bv2:67.6'><span class='pli'>upaṭṭhissatimaṁ jinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='68.1' data-uid='bv2:68.1'><span class='pli'>Khemā uppalavaṇṇā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='68.2' data-uid='bv2:68.2'><span class='pli'>aggā hessanti sāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='68.3' data-uid='bv2:68.3'><span class='pli'>Anāsavā vītarāgā, </span></span></span>\n<span class='verse-line'><span class='segment' id='68.4' data-uid='bv2:68.4'><span class='pli'>santacittā samāhitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='68.5' data-uid='bv2:68.5'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='68.6' data-uid='bv2:68.6'><span class='pli'>assatthoti pavuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='69.1' data-uid='bv2:69.1'><span class='pli'>Citto ca hatthāḷavako, </span></span></span>\n<span class='verse-line'><span class='segment' id='69.2' data-uid='bv2:69.2'><span class='pli'>aggā hessantupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='69.3' data-uid='bv2:69.3'><span class='pli'>Uttarā nandamātā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='69.4' data-uid='bv2:69.4'><span class='pli'>aggā hessantupaṭṭhikā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='70.1' data-uid='bv2:70.1'><span class='pli'>Idaṁ sutvāna vacanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='70.2' data-uid='bv2:70.2'><span class='pli'>asamassa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='70.3' data-uid='bv2:70.3'><span class='pli'>Āmoditā naramarū, </span></span></span>\n<span class='verse-line'><span class='segment' id='70.4' data-uid='bv2:70.4'><span class='pli'>buddhabījaṁ kira ayaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='71.1' data-uid='bv2:71.1'><span class='pli'>Ukkuṭṭhisaddā vattanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='71.2' data-uid='bv2:71.2'><span class='pli'>apphoṭenti hasanti ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='71.3' data-uid='bv2:71.3'><span class='pli'>Katañjalī namassanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='71.4' data-uid='bv2:71.4'><span class='pli'>dasasahassī sadevakā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='72.1' data-uid='bv2:72.1'><span class='pli'>‘Yadimassa lokanāthassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='72.2' data-uid='bv2:72.2'><span class='pli'>virajjhissāma sāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='72.3' data-uid='bv2:72.3'><span class='pli'>Anāgatamhi addhāne, </span></span></span>\n<span class='verse-line'><span class='segment' id='72.4' data-uid='bv2:72.4'><span class='pli'>hessāma sammukhā imaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='73.1' data-uid='bv2:73.1'><span class='pli'>Yathā manussā nadiṁ tarantā, </span></span></span>\n<span class='verse-line'><span class='segment' id='73.2' data-uid='bv2:73.2'><span class='pli'>Paṭititthaṁ virajjhiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='73.3' data-uid='bv2:73.3'><span class='pli'>Heṭṭhātitthe gahetvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='73.4' data-uid='bv2:73.4'><span class='pli'>Uttaranti mahānadiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='74.1' data-uid='bv2:74.1'><span class='pli'>Evamevaṁ mayaṁ sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='74.2' data-uid='bv2:74.2'><span class='pli'>yadi muñcāmimaṁ jinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='74.3' data-uid='bv2:74.3'><span class='pli'>Anāgatamhi addhāne, </span></span></span>\n<span class='verse-line'><span class='segment' id='74.4' data-uid='bv2:74.4'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='75.1' data-uid='bv2:75.1'><span class='pli'>Dīpaṅkaro lokavidū, </span></span></span>\n<span class='verse-line'><span class='segment' id='75.2' data-uid='bv2:75.2'><span class='pli'>āhutīnaṁ paṭiggaho; </span></span></span>\n<span class='verse-line'><span class='segment' id='75.3' data-uid='bv2:75.3'><span class='pli'>Mama kammaṁ pakittetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='75.4' data-uid='bv2:75.4'><span class='pli'>dakkhiṇaṁ pādamuddhari. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='76.1' data-uid='bv2:76.1'><span class='pli'>Ye tatthāsuṁ jinaputtā, </span></span></span>\n<span class='verse-line'><span class='segment' id='76.2' data-uid='bv2:76.2'><span class='pli'>Padakkhiṇamakaṁsu maṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='76.3' data-uid='bv2:76.3'><span class='pli'>Devā manussā asurā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='76.4' data-uid='bv2:76.4'><span class='pli'>Abhivādetvāna pakkamuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='77.1' data-uid='bv2:77.1'><span class='pli'>Dassanaṁ me atikkante, </span></span></span>\n<span class='verse-line'><span class='segment' id='77.2' data-uid='bv2:77.2'><span class='pli'>sasaṅghe lokanāyake; </span></span></span>\n<span class='verse-line'><span class='segment' id='77.3' data-uid='bv2:77.3'><span class='pli'>Sayanā vuṭṭhahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='77.4' data-uid='bv2:77.4'><span class='pli'>pallaṅkaṁ ābhujiṁ tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='78.1' data-uid='bv2:78.1'><span class='pli'>Sukhena sukhito homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='78.2' data-uid='bv2:78.2'><span class='pli'>pāmojjena pamodito; </span></span></span>\n<span class='verse-line'><span class='segment' id='78.3' data-uid='bv2:78.3'><span class='pli'>Pītiyā ca abhissanno, </span></span></span>\n<span class='verse-line'><span class='segment' id='78.4' data-uid='bv2:78.4'><span class='pli'>pallaṅkaṁ ābhujiṁ tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='79.1' data-uid='bv2:79.1'><span class='pli'>Pallaṅkena nisīditvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='79.2' data-uid='bv2:79.2'><span class='pli'>evaṁ cintesahaṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='79.3' data-uid='bv2:79.3'><span class='pli'>‘Vasībhūto ahaṁ jhāne, </span></span></span>\n<span class='verse-line'><span class='segment' id='79.4' data-uid='bv2:79.4'><span class='pli'>abhiññāsu pāramiṅgato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='80.1' data-uid='bv2:80.1'><span class='pli'>Sahassiyamhi lokamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='80.2' data-uid='bv2:80.2'><span class='pli'>isayo natthi me samā; </span></span></span>\n<span class='verse-line'><span class='segment' id='80.3' data-uid='bv2:80.3'><span class='pli'>Asamo iddhidhammesu, </span></span></span>\n<span class='verse-line'><span class='segment' id='80.4' data-uid='bv2:80.4'><span class='pli'>alabhiṁ īdisaṁ sukhaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='81.1' data-uid='bv2:81.1'><span class='pli'>Pallaṅkābhujane mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='81.2' data-uid='bv2:81.2'><span class='pli'>dasasahassādhivāsino; </span></span></span>\n<span class='verse-line'><span class='segment' id='81.3' data-uid='bv2:81.3'><span class='pli'>Mahānādaṁ pavattesuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='81.4' data-uid='bv2:81.4'><span class='pli'>‘dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='82.1' data-uid='bv2:82.1'><span class='pli'>Yā pubbe bodhisattānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='82.2' data-uid='bv2:82.2'><span class='pli'>pallaṅkavaramābhuje; </span></span></span>\n<span class='verse-line'><span class='segment' id='82.3' data-uid='bv2:82.3'><span class='pli'>Nimittāni padissanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='82.4' data-uid='bv2:82.4'><span class='pli'>tāni ajja padissare. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='83.1' data-uid='bv2:83.1'><span class='pli'>Sītaṁ byapagataṁ hoti, </span></span></span>\n<span class='verse-line'><span class='segment' id='83.2' data-uid='bv2:83.2'><span class='pli'>uṇhañca upasammati; </span></span></span>\n<span class='verse-line'><span class='segment' id='83.3' data-uid='bv2:83.3'><span class='pli'>Tāni ajja padissanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='83.4' data-uid='bv2:83.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='84.1' data-uid='bv2:84.1'><span class='pli'>Dasasahassī lokadhātū, </span></span></span>\n<span class='verse-line'><span class='segment' id='84.2' data-uid='bv2:84.2'><span class='pli'>nissaddā honti nirākulā; </span></span></span>\n<span class='verse-line'><span class='segment' id='84.3' data-uid='bv2:84.3'><span class='pli'>Tāni ajja padissanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='84.4' data-uid='bv2:84.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='85.1' data-uid='bv2:85.1'><span class='pli'>Mahāvātā na vāyanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='85.2' data-uid='bv2:85.2'><span class='pli'>na sandanti savantiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='85.3' data-uid='bv2:85.3'><span class='pli'>Tāni ajja padissanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='85.4' data-uid='bv2:85.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='86.1' data-uid='bv2:86.1'><span class='pli'>Thalajā dakajā pupphā, </span></span></span>\n<span class='verse-line'><span class='segment' id='86.2' data-uid='bv2:86.2'><span class='pli'>sabbe pupphanti tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='86.3' data-uid='bv2:86.3'><span class='pli'>Tepajja pupphitā sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='86.4' data-uid='bv2:86.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='87.1' data-uid='bv2:87.1'><span class='pli'>Latā vā yadi vā rukkhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='87.2' data-uid='bv2:87.2'><span class='pli'>phalabhārā honti tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='87.3' data-uid='bv2:87.3'><span class='pli'>Tepajja phalitā sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='87.4' data-uid='bv2:87.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='88.1' data-uid='bv2:88.1'><span class='pli'>Ākāsaṭṭhā ca bhūmaṭṭhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='88.2' data-uid='bv2:88.2'><span class='pli'>ratanā jotanti tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='88.3' data-uid='bv2:88.3'><span class='pli'>Tepajja ratanā jotanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='88.4' data-uid='bv2:88.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='89.1' data-uid='bv2:89.1'><span class='pli'>Mānussakā ca dibbā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='89.2' data-uid='bv2:89.2'><span class='pli'>turiyā vajjanti tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='89.3' data-uid='bv2:89.3'><span class='pli'>Tepajjubho abhiravanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='89.4' data-uid='bv2:89.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='90.1' data-uid='bv2:90.1'><span class='pli'>Vicitrapupphā gaganā, </span></span></span>\n<span class='verse-line'><span class='segment' id='90.2' data-uid='bv2:90.2'><span class='pli'>abhivassanti tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='90.3' data-uid='bv2:90.3'><span class='pli'>Tepi ajja pavassanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='90.4' data-uid='bv2:90.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='91.1' data-uid='bv2:91.1'><span class='pli'>Mahāsamuddo ābhujati, </span></span></span>\n<span class='verse-line'><span class='segment' id='91.2' data-uid='bv2:91.2'><span class='pli'>dasasahassī pakampati; </span></span></span>\n<span class='verse-line'><span class='segment' id='91.3' data-uid='bv2:91.3'><span class='pli'>Tepajjubho abhiravanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='91.4' data-uid='bv2:91.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='92.1' data-uid='bv2:92.1'><span class='pli'>Nirayepi dasasahasse, </span></span></span>\n<span class='verse-line'><span class='segment' id='92.2' data-uid='bv2:92.2'><span class='pli'>aggī nibbanti tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='92.3' data-uid='bv2:92.3'><span class='pli'>Tepajja nibbutā aggī, </span></span></span>\n<span class='verse-line'><span class='segment' id='92.4' data-uid='bv2:92.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='93.1' data-uid='bv2:93.1'><span class='pli'>Vimalo hoti sūriyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='93.2' data-uid='bv2:93.2'><span class='pli'>sabbā dissanti tārakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='93.3' data-uid='bv2:93.3'><span class='pli'>Tepi ajja padissanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='93.4' data-uid='bv2:93.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='94.1' data-uid='bv2:94.1'><span class='pli'>Anovaṭṭhena udakaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='94.2' data-uid='bv2:94.2'><span class='pli'>mahiyā ubbhijji tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='94.3' data-uid='bv2:94.3'><span class='pli'>Tampajjubbhijjate mahiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='94.4' data-uid='bv2:94.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='95.1' data-uid='bv2:95.1'><span class='pli'>Tārāgaṇā virocanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='95.2' data-uid='bv2:95.2'><span class='pli'>nakkhattā gaganamaṇḍale; </span></span></span>\n<span class='verse-line'><span class='segment' id='95.3' data-uid='bv2:95.3'><span class='pli'>Visākhā candimāyuttā, </span></span></span>\n<span class='verse-line'><span class='segment' id='95.4' data-uid='bv2:95.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='96.1' data-uid='bv2:96.1'><span class='pli'>Bilāsayā darīsayā, </span></span></span>\n<span class='verse-line'><span class='segment' id='96.2' data-uid='bv2:96.2'><span class='pli'>nikkhamanti sakāsayā; </span></span></span>\n<span class='verse-line'><span class='segment' id='96.3' data-uid='bv2:96.3'><span class='pli'>Tepajja āsayā chuddhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='96.4' data-uid='bv2:96.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='97.1' data-uid='bv2:97.1'><span class='pli'>Na honti aratī sattānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='97.2' data-uid='bv2:97.2'><span class='pli'>santuṭṭhā honti tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='97.3' data-uid='bv2:97.3'><span class='pli'>Tepajja sabbe santuṭṭhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='97.4' data-uid='bv2:97.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='98.1' data-uid='bv2:98.1'><span class='pli'>Rogā tadupasammanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='98.2' data-uid='bv2:98.2'><span class='pli'>jighacchā ca vinassati; </span></span></span>\n<span class='verse-line'><span class='segment' id='98.3' data-uid='bv2:98.3'><span class='pli'>Tāni ajja padissanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='98.4' data-uid='bv2:98.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='99.1' data-uid='bv2:99.1'><span class='pli'>Rāgo tadā tanu hoti, </span></span></span>\n<span class='verse-line'><span class='segment' id='99.2' data-uid='bv2:99.2'><span class='pli'>doso moho vinassati; </span></span></span>\n<span class='verse-line'><span class='segment' id='99.3' data-uid='bv2:99.3'><span class='pli'>Tepajja vigatā sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='99.4' data-uid='bv2:99.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='100.1' data-uid='bv2:100.1'><span class='pli'>Bhayaṁ tadā na bhavati, </span></span></span>\n<span class='verse-line'><span class='segment' id='100.2' data-uid='bv2:100.2'><span class='pli'>ajjapetaṁ padissati; </span></span></span>\n<span class='verse-line'><span class='segment' id='100.3' data-uid='bv2:100.3'><span class='pli'>Tena liṅgena jānāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='100.4' data-uid='bv2:100.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='101.1' data-uid='bv2:101.1'><span class='pli'>Rajonuddhaṁsati uddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='101.2' data-uid='bv2:101.2'><span class='pli'>ajjapetaṁ padissati; </span></span></span>\n<span class='verse-line'><span class='segment' id='101.3' data-uid='bv2:101.3'><span class='pli'>Tena liṅgena jānāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='101.4' data-uid='bv2:101.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='102.1' data-uid='bv2:102.1'><span class='pli'>Aniṭṭhagandho pakkamati, </span></span></span>\n<span class='verse-line'><span class='segment' id='102.2' data-uid='bv2:102.2'><span class='pli'>dibbagandho pavāyati; </span></span></span>\n<span class='verse-line'><span class='segment' id='102.3' data-uid='bv2:102.3'><span class='pli'>Sopajja vāyati gandho, </span></span></span>\n<span class='verse-line'><span class='segment' id='102.4' data-uid='bv2:102.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='103.1' data-uid='bv2:103.1'><span class='pli'>Sabbe devā padissanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='103.2' data-uid='bv2:103.2'><span class='pli'>ṭhapayitvā arūpino; </span></span></span>\n<span class='verse-line'><span class='segment' id='103.3' data-uid='bv2:103.3'><span class='pli'>Tepajja sabbe dissanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='103.4' data-uid='bv2:103.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='104.1' data-uid='bv2:104.1'><span class='pli'>Yāvatā nirayā nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='104.2' data-uid='bv2:104.2'><span class='pli'>sabbe dissanti tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='104.3' data-uid='bv2:104.3'><span class='pli'>Tepajja sabbe dissanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='104.4' data-uid='bv2:104.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='105.1' data-uid='bv2:105.1'><span class='pli'>Kuṭṭā kavāṭā selā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='105.2' data-uid='bv2:105.2'><span class='pli'>na hontāvaraṇā tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='105.3' data-uid='bv2:105.3'><span class='pli'>Ākāsabhūtā tepajja, </span></span></span>\n<span class='verse-line'><span class='segment' id='105.4' data-uid='bv2:105.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='106.1' data-uid='bv2:106.1'><span class='pli'>Cutī ca upapatti ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='106.2' data-uid='bv2:106.2'><span class='pli'>khaṇe tasmiṁ na vijjati; </span></span></span>\n<span class='verse-line'><span class='segment' id='106.3' data-uid='bv2:106.3'><span class='pli'>Tānipajja padissanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='106.4' data-uid='bv2:106.4'><span class='pli'>dhuvaṁ buddho bhavissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='107.1' data-uid='bv2:107.1'><span class='pli'>Daḷhaṁ paggaṇha vīriyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='107.2' data-uid='bv2:107.2'><span class='pli'>mā nivatta abhikkama; </span></span></span>\n<span class='verse-line'><span class='segment' id='107.3' data-uid='bv2:107.3'><span class='pli'>Mayampetaṁ vijānāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='107.4' data-uid='bv2:107.4'><span class='pli'>dhuvaṁ buddho bhavissasi’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='108.1' data-uid='bv2:108.1'><span class='pli'>Buddhassa vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='108.2' data-uid='bv2:108.2'><span class='pli'>dasasahassīna cūbhayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='108.3' data-uid='bv2:108.3'><span class='pli'>Tuṭṭhahaṭṭho pamodito, </span></span></span>\n<span class='verse-line'><span class='segment' id='108.4' data-uid='bv2:108.4'><span class='pli'>evaṁ cintesahaṁ tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='109.1' data-uid='bv2:109.1'><span class='pli'>‘Advejjhavacanā buddhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='109.2' data-uid='bv2:109.2'><span class='pli'>Amoghavacanā jinā; </span></span></span>\n<span class='verse-line'><span class='segment' id='109.3' data-uid='bv2:109.3'><span class='pli'>Vitathaṁ natthi buddhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='109.4' data-uid='bv2:109.4'><span class='pli'>Dhuvaṁ buddho bhavāmahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='110.1' data-uid='bv2:110.1'><span class='pli'>Yathā khittaṁ nabhe leḍḍu, </span></span></span>\n<span class='verse-line'><span class='segment' id='110.2' data-uid='bv2:110.2'><span class='pli'>Dhuvaṁ patati bhūmiyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='110.3' data-uid='bv2:110.3'><span class='pli'>Tatheva buddhaseṭṭhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='110.4' data-uid='bv2:110.4'><span class='pli'>Vacanaṁ dhuvasassataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='110.5' data-uid='bv2:110.5'><span class='pli'>Vitathaṁ natthi buddhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='110.6' data-uid='bv2:110.6'><span class='pli'>Dhuvaṁ buddho bhavāmahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='111.1' data-uid='bv2:111.1'><span class='pli'>Yathāpi sabbasattānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='111.2' data-uid='bv2:111.2'><span class='pli'>Maraṇaṁ dhuvasassataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='111.3' data-uid='bv2:111.3'><span class='pli'>Tatheva buddhaseṭṭhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='111.4' data-uid='bv2:111.4'><span class='pli'>Vacanaṁ dhuvasassataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='111.5' data-uid='bv2:111.5'><span class='pli'>Vitathaṁ natthi buddhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='111.6' data-uid='bv2:111.6'><span class='pli'>Dhuvaṁ buddho bhavāmahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='112.1' data-uid='bv2:112.1'><span class='pli'>Yathā rattikkhaye patte, </span></span></span>\n<span class='verse-line'><span class='segment' id='112.2' data-uid='bv2:112.2'><span class='pli'>Sūriyuggamanaṁ dhuvaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='112.3' data-uid='bv2:112.3'><span class='pli'>Tatheva buddhaseṭṭhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='112.4' data-uid='bv2:112.4'><span class='pli'>Vacanaṁ dhuvasassataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='112.5' data-uid='bv2:112.5'><span class='pli'>Vitathaṁ natthi buddhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='112.6' data-uid='bv2:112.6'><span class='pli'>Dhuvaṁ buddho bhavāmahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='113.1' data-uid='bv2:113.1'><span class='pli'>Yathā nikkhantasayanassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='113.2' data-uid='bv2:113.2'><span class='pli'>Sīhassa nadanaṁ dhuvaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='113.3' data-uid='bv2:113.3'><span class='pli'>Tatheva buddhaseṭṭhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='113.4' data-uid='bv2:113.4'><span class='pli'>Vacanaṁ dhuvasassataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='113.5' data-uid='bv2:113.5'><span class='pli'>Vitathaṁ natthi buddhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='113.6' data-uid='bv2:113.6'><span class='pli'>Dhuvaṁ buddho bhavāmahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='114.1' data-uid='bv2:114.1'><span class='pli'>Yathā āpannasattānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='114.2' data-uid='bv2:114.2'><span class='pli'>Bhāramoropanaṁ dhuvaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='114.3' data-uid='bv2:114.3'><span class='pli'>Tatheva buddhaseṭṭhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='114.4' data-uid='bv2:114.4'><span class='pli'>Vacanaṁ dhuvasassataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='114.5' data-uid='bv2:114.5'><span class='pli'>Vitathaṁ natthi buddhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='114.6' data-uid='bv2:114.6'><span class='pli'>Dhuvaṁ buddho bhavāmahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='115.1' data-uid='bv2:115.1'><span class='pli'>Handa buddhakare dhamme, </span></span></span>\n<span class='verse-line'><span class='segment' id='115.2' data-uid='bv2:115.2'><span class='pli'>vicināmi ito cito; </span></span></span>\n<span class='verse-line'><span class='segment' id='115.3' data-uid='bv2:115.3'><span class='pli'>Uddhaṁ adho dasa disā, </span></span></span>\n<span class='verse-line'><span class='segment' id='115.4' data-uid='bv2:115.4'><span class='pli'>yāvatā dhammadhātuyā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='116.1' data-uid='bv2:116.1'><span class='pli'>Vicinanto tadā dakkhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='116.2' data-uid='bv2:116.2'><span class='pli'>paṭhamaṁ <b>dānapāramiṁ</b>; </span></span></span>\n<span class='verse-line'><span class='segment' id='116.3' data-uid='bv2:116.3'><span class='pli'>Pubbakehi mahesīhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='116.4' data-uid='bv2:116.4'><span class='pli'>anuciṇṇaṁ mahāpathaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='117.1' data-uid='bv2:117.1'><span class='pli'>‘Imaṁ tvaṁ paṭhamaṁ tāva, </span></span></span>\n<span class='verse-line'><span class='segment' id='117.2' data-uid='bv2:117.2'><span class='pli'>daḷhaṁ katvā samādiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='117.3' data-uid='bv2:117.3'><span class='pli'>Dānapāramitaṁ gaccha, </span></span></span>\n<span class='verse-line'><span class='segment' id='117.4' data-uid='bv2:117.4'><span class='pli'>yadi bodhiṁ pattumicchasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='118.1' data-uid='bv2:118.1'><span class='pli'>Yathāpi kumbho sampuṇṇo, </span></span></span>\n<span class='verse-line'><span class='segment' id='118.2' data-uid='bv2:118.2'><span class='pli'>yassa kassaci adhokato; </span></span></span>\n<span class='verse-line'><span class='segment' id='118.3' data-uid='bv2:118.3'><span class='pli'>Vamatevudakaṁ nissesaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='118.4' data-uid='bv2:118.4'><span class='pli'>na tattha parirakkhati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='119.1' data-uid='bv2:119.1'><span class='pli'>Tatheva yācake disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='119.2' data-uid='bv2:119.2'><span class='pli'>hīnamukkaṭṭhamajjhime; </span></span></span>\n<span class='verse-line'><span class='segment' id='119.3' data-uid='bv2:119.3'><span class='pli'>Dadāhi dānaṁ nissesaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='119.4' data-uid='bv2:119.4'><span class='pli'>kumbho viya adhokato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='120.1' data-uid='bv2:120.1'><span class='pli'>Na hete ettakāyeva, </span></span></span>\n<span class='verse-line'><span class='segment' id='120.2' data-uid='bv2:120.2'><span class='pli'>buddhadhammā bhavissare; </span></span></span>\n<span class='verse-line'><span class='segment' id='120.3' data-uid='bv2:120.3'><span class='pli'>Aññepi vicinissāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='120.4' data-uid='bv2:120.4'><span class='pli'>ye dhammā bodhipācanā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='121.1' data-uid='bv2:121.1'><span class='pli'>Vicinanto tadā dakkhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='121.2' data-uid='bv2:121.2'><span class='pli'>dutiyaṁ <b>sīlapāramiṁ</b>; </span></span></span>\n<span class='verse-line'><span class='segment' id='121.3' data-uid='bv2:121.3'><span class='pli'>Pubbakehi mahesīhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='121.4' data-uid='bv2:121.4'><span class='pli'>āsevitanisevitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='122.1' data-uid='bv2:122.1'><span class='pli'>‘Imaṁ tvaṁ dutiyaṁ tāva, </span></span></span>\n<span class='verse-line'><span class='segment' id='122.2' data-uid='bv2:122.2'><span class='pli'>daḷhaṁ katvā samādiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='122.3' data-uid='bv2:122.3'><span class='pli'>Sīlapāramitaṁ gaccha, </span></span></span>\n<span class='verse-line'><span class='segment' id='122.4' data-uid='bv2:122.4'><span class='pli'>yadi bodhiṁ pattumicchasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='123.1' data-uid='bv2:123.1'><span class='pli'>Yathāpi camarī vālaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='123.2' data-uid='bv2:123.2'><span class='pli'>kismiñci paṭilaggitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='123.3' data-uid='bv2:123.3'><span class='pli'>Upeti maraṇaṁ tattha, </span></span></span>\n<span class='verse-line'><span class='segment' id='123.4' data-uid='bv2:123.4'><span class='pli'>na vikopeti vāladhiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='124.1' data-uid='bv2:124.1'><span class='pli'>Tatheva tvaṁ catūsu bhūmīsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='124.2' data-uid='bv2:124.2'><span class='pli'>Sīlāni paripūraya; </span></span></span>\n<span class='verse-line'><span class='segment' id='124.3' data-uid='bv2:124.3'><span class='pli'>Parirakkha sabbadā sīlaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='124.4' data-uid='bv2:124.4'><span class='pli'>Camarī viya vāladhiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='125.1' data-uid='bv2:125.1'><span class='pli'>Na hete ettakāyeva, </span></span></span>\n<span class='verse-line'><span class='segment' id='125.2' data-uid='bv2:125.2'><span class='pli'>buddhadhammā bhavissare; </span></span></span>\n<span class='verse-line'><span class='segment' id='125.3' data-uid='bv2:125.3'><span class='pli'>Aññepi vicinissāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='125.4' data-uid='bv2:125.4'><span class='pli'>ye dhammā bodhipācanā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='126.1' data-uid='bv2:126.1'><span class='pli'>Vicinanto tadā dakkhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='126.2' data-uid='bv2:126.2'><span class='pli'>tatiyaṁ <b>nekkhammapāramiṁ</b>; </span></span></span>\n<span class='verse-line'><span class='segment' id='126.3' data-uid='bv2:126.3'><span class='pli'>Pubbakehi mahesīhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='126.4' data-uid='bv2:126.4'><span class='pli'>āsevitanisevitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='127.1' data-uid='bv2:127.1'><span class='pli'>‘Imaṁ tvaṁ tatiyaṁ tāva, </span></span></span>\n<span class='verse-line'><span class='segment' id='127.2' data-uid='bv2:127.2'><span class='pli'>daḷhaṁ katvā samādiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='127.3' data-uid='bv2:127.3'><span class='pli'>Nekkhammapāramitaṁ gaccha, </span></span></span>\n<span class='verse-line'><span class='segment' id='127.4' data-uid='bv2:127.4'><span class='pli'>yadi bodhiṁ pattumicchasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='128.1' data-uid='bv2:128.1'><span class='pli'>Yathā andughare puriso, </span></span></span>\n<span class='verse-line'><span class='segment' id='128.2' data-uid='bv2:128.2'><span class='pli'>ciravuttho dukhaṭṭito; </span></span></span>\n<span class='verse-line'><span class='segment' id='128.3' data-uid='bv2:128.3'><span class='pli'>Na tattha rāgaṁ janesi, </span></span></span>\n<span class='verse-line'><span class='segment' id='128.4' data-uid='bv2:128.4'><span class='pli'>muttiṁyeva gavesati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='129.1' data-uid='bv2:129.1'><span class='pli'>Tatheva tvaṁ sabbabhave, </span></span></span>\n<span class='verse-line'><span class='segment' id='129.2' data-uid='bv2:129.2'><span class='pli'>passa andughare viya; </span></span></span>\n<span class='verse-line'><span class='segment' id='129.3' data-uid='bv2:129.3'><span class='pli'>Nekkhammābhimukho hohi, </span></span></span>\n<span class='verse-line'><span class='segment' id='129.4' data-uid='bv2:129.4'><span class='pli'>bhavato parimuttiyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='130.1' data-uid='bv2:130.1'><span class='pli'>Na hete ettakāyeva, </span></span></span>\n<span class='verse-line'><span class='segment' id='130.2' data-uid='bv2:130.2'><span class='pli'>buddhadhammā bhavissare; </span></span></span>\n<span class='verse-line'><span class='segment' id='130.3' data-uid='bv2:130.3'><span class='pli'>Aññepi vicinissāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='130.4' data-uid='bv2:130.4'><span class='pli'>ye dhammā bodhipācanā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='131.1' data-uid='bv2:131.1'><span class='pli'>Vicinanto tadā dakkhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='131.2' data-uid='bv2:131.2'><span class='pli'>catutthaṁ <b>paññāpāramiṁ</b>; </span></span></span>\n<span class='verse-line'><span class='segment' id='131.3' data-uid='bv2:131.3'><span class='pli'>Pubbakehi mahesīhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='131.4' data-uid='bv2:131.4'><span class='pli'>āsevitanisevitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='132.1' data-uid='bv2:132.1'><span class='pli'>‘Imaṁ tvaṁ catutthaṁ tāva, </span></span></span>\n<span class='verse-line'><span class='segment' id='132.2' data-uid='bv2:132.2'><span class='pli'>daḷhaṁ katvā samādiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='132.3' data-uid='bv2:132.3'><span class='pli'>Paññāpāramitaṁ gaccha, </span></span></span>\n<span class='verse-line'><span class='segment' id='132.4' data-uid='bv2:132.4'><span class='pli'>yadi bodhiṁ pattumicchasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='133.1' data-uid='bv2:133.1'><span class='pli'>Yathāpi bhikkhu bhikkhanto, </span></span></span>\n<span class='verse-line'><span class='segment' id='133.2' data-uid='bv2:133.2'><span class='pli'>hīnamukkaṭṭhamajjhime; </span></span></span>\n<span class='verse-line'><span class='segment' id='133.3' data-uid='bv2:133.3'><span class='pli'>Kulāni na vivajjento, </span></span></span>\n<span class='verse-line'><span class='segment' id='133.4' data-uid='bv2:133.4'><span class='pli'>evaṁ labhati yāpanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='134.1' data-uid='bv2:134.1'><span class='pli'>Tatheva tvaṁ sabbakālaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='134.2' data-uid='bv2:134.2'><span class='pli'>paripucchaṁ budhaṁ janaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='134.3' data-uid='bv2:134.3'><span class='pli'>Paññāpāramitaṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='134.4' data-uid='bv2:134.4'><span class='pli'>sambodhiṁ pāpuṇissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='135.1' data-uid='bv2:135.1'><span class='pli'>Na hete ettakāyeva, </span></span></span>\n<span class='verse-line'><span class='segment' id='135.2' data-uid='bv2:135.2'><span class='pli'>buddhadhammā bhavissare; </span></span></span>\n<span class='verse-line'><span class='segment' id='135.3' data-uid='bv2:135.3'><span class='pli'>Aññepi vicinissāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='135.4' data-uid='bv2:135.4'><span class='pli'>ye dhammā bodhipācanā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='136.1' data-uid='bv2:136.1'><span class='pli'>Vicinanto tadā dakkhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='136.2' data-uid='bv2:136.2'><span class='pli'>pañcamaṁ <b>vīriyapāramiṁ</b>; </span></span></span>\n<span class='verse-line'><span class='segment' id='136.3' data-uid='bv2:136.3'><span class='pli'>Pubbakehi mahesīhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='136.4' data-uid='bv2:136.4'><span class='pli'>āsevitanisevitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='137.1' data-uid='bv2:137.1'><span class='pli'>‘Imaṁ tvaṁ pañcamaṁ tāva, </span></span></span>\n<span class='verse-line'><span class='segment' id='137.2' data-uid='bv2:137.2'><span class='pli'>daḷhaṁ katvā samādiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='137.3' data-uid='bv2:137.3'><span class='pli'>Vīriyapāramitaṁ gaccha, </span></span></span>\n<span class='verse-line'><span class='segment' id='137.4' data-uid='bv2:137.4'><span class='pli'>yadi bodhiṁ pattumicchasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='138.1' data-uid='bv2:138.1'><span class='pli'>Yathāpi sīho migarājā, </span></span></span>\n<span class='verse-line'><span class='segment' id='138.2' data-uid='bv2:138.2'><span class='pli'>nisajjaṭṭhānacaṅkame; </span></span></span>\n<span class='verse-line'><span class='segment' id='138.3' data-uid='bv2:138.3'><span class='pli'>Alīnavīriyo hoti, </span></span></span>\n<span class='verse-line'><span class='segment' id='138.4' data-uid='bv2:138.4'><span class='pli'>paggahitamano sadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='139.1' data-uid='bv2:139.1'><span class='pli'>Tatheva tvaṁ sabbabhave, </span></span></span>\n<span class='verse-line'><span class='segment' id='139.2' data-uid='bv2:139.2'><span class='pli'>paggaṇha vīriyaṁ daḷhaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='139.3' data-uid='bv2:139.3'><span class='pli'>Vīriyapāramitaṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='139.4' data-uid='bv2:139.4'><span class='pli'>sambodhiṁ pāpuṇissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='140.1' data-uid='bv2:140.1'><span class='pli'>Na hete ettakāyeva, </span></span></span>\n<span class='verse-line'><span class='segment' id='140.2' data-uid='bv2:140.2'><span class='pli'>buddhadhammā bhavissare; </span></span></span>\n<span class='verse-line'><span class='segment' id='140.3' data-uid='bv2:140.3'><span class='pli'>Aññepi vicinissāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='140.4' data-uid='bv2:140.4'><span class='pli'>ye dhammā bodhipācanā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='141.1' data-uid='bv2:141.1'><span class='pli'>Vicinanto tadā dakkhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='141.2' data-uid='bv2:141.2'><span class='pli'>chaṭṭhamaṁ <b>khantipāramiṁ</b>; </span></span></span>\n<span class='verse-line'><span class='segment' id='141.3' data-uid='bv2:141.3'><span class='pli'>Pubbakehi mahesīhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='141.4' data-uid='bv2:141.4'><span class='pli'>āsevitanisevitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='142.1' data-uid='bv2:142.1'><span class='pli'>‘Imaṁ tvaṁ chaṭṭhamaṁ tāva, </span></span></span>\n<span class='verse-line'><span class='segment' id='142.2' data-uid='bv2:142.2'><span class='pli'>daḷhaṁ katvā samādiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='142.3' data-uid='bv2:142.3'><span class='pli'>Tattha advejjhamānaso, </span></span></span>\n<span class='verse-line'><span class='segment' id='142.4' data-uid='bv2:142.4'><span class='pli'>sambodhiṁ pāpuṇissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='143.1' data-uid='bv2:143.1'><span class='pli'>Yathāpi pathavī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='143.2' data-uid='bv2:143.2'><span class='pli'>sucimpi asucimpi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='143.3' data-uid='bv2:143.3'><span class='pli'>Sabbaṁ sahati nikkhepaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='143.4' data-uid='bv2:143.4'><span class='pli'>na karoti paṭighaṁ tayā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='144.1' data-uid='bv2:144.1'><span class='pli'>Tatheva tvampi sabbesaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='144.2' data-uid='bv2:144.2'><span class='pli'>sammānāvamānakkhamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='144.3' data-uid='bv2:144.3'><span class='pli'>Khantipāramitaṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='144.4' data-uid='bv2:144.4'><span class='pli'>sambodhiṁ pāpuṇissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='145.1' data-uid='bv2:145.1'><span class='pli'>Na hete ettakāyeva, </span></span></span>\n<span class='verse-line'><span class='segment' id='145.2' data-uid='bv2:145.2'><span class='pli'>buddhadhammā bhavissare; </span></span></span>\n<span class='verse-line'><span class='segment' id='145.3' data-uid='bv2:145.3'><span class='pli'>Aññepi vicinissāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='145.4' data-uid='bv2:145.4'><span class='pli'>ye dhammā bodhipācanā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='146.1' data-uid='bv2:146.1'><span class='pli'>Vicinanto tadā dakkhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='146.2' data-uid='bv2:146.2'><span class='pli'>sattamaṁ <b>saccapāramiṁ</b>; </span></span></span>\n<span class='verse-line'><span class='segment' id='146.3' data-uid='bv2:146.3'><span class='pli'>Pubbakehi mahesīhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='146.4' data-uid='bv2:146.4'><span class='pli'>āsevitanisevitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='147.1' data-uid='bv2:147.1'><span class='pli'>‘Imaṁ tvaṁ sattamaṁ tāva, </span></span></span>\n<span class='verse-line'><span class='segment' id='147.2' data-uid='bv2:147.2'><span class='pli'>daḷhaṁ katvā samādiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='147.3' data-uid='bv2:147.3'><span class='pli'>Tattha advejjhavacano, </span></span></span>\n<span class='verse-line'><span class='segment' id='147.4' data-uid='bv2:147.4'><span class='pli'>sambodhiṁ pāpuṇissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='148.1' data-uid='bv2:148.1'><span class='pli'>Yathāpi osadhī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='148.2' data-uid='bv2:148.2'><span class='pli'>tulābhūtā sadevake; </span></span></span>\n<span class='verse-line'><span class='segment' id='148.3' data-uid='bv2:148.3'><span class='pli'>Samaye utuvasse vā, </span></span></span>\n<span class='verse-line'><span class='segment' id='148.4' data-uid='bv2:148.4'><span class='pli'>na vokkamati vīthito. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='149.1' data-uid='bv2:149.1'><span class='pli'>Tatheva tvampi saccesu, </span></span></span>\n<span class='verse-line'><span class='segment' id='149.2' data-uid='bv2:149.2'><span class='pli'>mā vokkama hi vīthito; </span></span></span>\n<span class='verse-line'><span class='segment' id='149.3' data-uid='bv2:149.3'><span class='pli'>Saccapāramitaṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='149.4' data-uid='bv2:149.4'><span class='pli'>sambodhiṁ pāpuṇissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='150.1' data-uid='bv2:150.1'><span class='pli'>Na hete ettakāyeva, </span></span></span>\n<span class='verse-line'><span class='segment' id='150.2' data-uid='bv2:150.2'><span class='pli'>buddhadhammā bhavissare; </span></span></span>\n<span class='verse-line'><span class='segment' id='150.3' data-uid='bv2:150.3'><span class='pli'>Aññepi vicinissāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='150.4' data-uid='bv2:150.4'><span class='pli'>ye dhammā bodhipācanā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='151.1' data-uid='bv2:151.1'><span class='pli'>Vicinanto tadā dakkhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='151.2' data-uid='bv2:151.2'><span class='pli'>Aṭṭhamaṁ <b>adhiṭṭhānapāramiṁ</b>; </span></span></span>\n<span class='verse-line'><span class='segment' id='151.3' data-uid='bv2:151.3'><span class='pli'>Pubbakehi mahesīhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='151.4' data-uid='bv2:151.4'><span class='pli'>Āsevitanisevitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='152.1' data-uid='bv2:152.1'><span class='pli'>‘Imaṁ tvaṁ aṭṭhamaṁ tāva, </span></span></span>\n<span class='verse-line'><span class='segment' id='152.2' data-uid='bv2:152.2'><span class='pli'>daḷhaṁ katvā samādiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='152.3' data-uid='bv2:152.3'><span class='pli'>Tattha tvaṁ acalo hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='152.4' data-uid='bv2:152.4'><span class='pli'>sambodhiṁ pāpuṇissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='153.1' data-uid='bv2:153.1'><span class='pli'>Yathāpi pabbato selo, </span></span></span>\n<span class='verse-line'><span class='segment' id='153.2' data-uid='bv2:153.2'><span class='pli'>acalo suppatiṭṭhito; </span></span></span>\n<span class='verse-line'><span class='segment' id='153.3' data-uid='bv2:153.3'><span class='pli'>Na kampati bhusavātehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='153.4' data-uid='bv2:153.4'><span class='pli'>sakaṭṭhāneva tiṭṭhati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='154.1' data-uid='bv2:154.1'><span class='pli'>Tatheva tvampi adhiṭṭhāne, </span></span></span>\n<span class='verse-line'><span class='segment' id='154.2' data-uid='bv2:154.2'><span class='pli'>Sabbadā acalo bhava; </span></span></span>\n<span class='verse-line'><span class='segment' id='154.3' data-uid='bv2:154.3'><span class='pli'>Adhiṭṭhānapāramitaṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='154.4' data-uid='bv2:154.4'><span class='pli'>Sambodhiṁ pāpuṇissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='155.1' data-uid='bv2:155.1'><span class='pli'>Na hete ettakāyeva, </span></span></span>\n<span class='verse-line'><span class='segment' id='155.2' data-uid='bv2:155.2'><span class='pli'>buddhadhammā bhavissare; </span></span></span>\n<span class='verse-line'><span class='segment' id='155.3' data-uid='bv2:155.3'><span class='pli'>Aññepi vicinissāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='155.4' data-uid='bv2:155.4'><span class='pli'>ye dhammā bodhipācanā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='156.1' data-uid='bv2:156.1'><span class='pli'>Vicinanto tadā dakkhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='156.2' data-uid='bv2:156.2'><span class='pli'>navamaṁ <b>mettāpāramiṁ</b>; </span></span></span>\n<span class='verse-line'><span class='segment' id='156.3' data-uid='bv2:156.3'><span class='pli'>Pubbakehi mahesīhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='156.4' data-uid='bv2:156.4'><span class='pli'>āsevitanisevitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='157.1' data-uid='bv2:157.1'><span class='pli'>‘Imaṁ tvaṁ navamaṁ tāva, </span></span></span>\n<span class='verse-line'><span class='segment' id='157.2' data-uid='bv2:157.2'><span class='pli'>daḷhaṁ katvā samādiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='157.3' data-uid='bv2:157.3'><span class='pli'>Mettāya asamo hohi, </span></span></span>\n<span class='verse-line'><span class='segment' id='157.4' data-uid='bv2:157.4'><span class='pli'>yadi bodhiṁ pattumicchasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='158.1' data-uid='bv2:158.1'><span class='pli'>Yathāpi udakaṁ nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='158.2' data-uid='bv2:158.2'><span class='pli'>kalyāṇe pāpake jane; </span></span></span>\n<span class='verse-line'><span class='segment' id='158.3' data-uid='bv2:158.3'><span class='pli'>Samaṁ pharati sītena, </span></span></span>\n<span class='verse-line'><span class='segment' id='158.4' data-uid='bv2:158.4'><span class='pli'>pavāheti rajomalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='159.1' data-uid='bv2:159.1'><span class='pli'>Tatheva tvaṁ hitāhite, </span></span></span>\n<span class='verse-line'><span class='segment' id='159.2' data-uid='bv2:159.2'><span class='pli'>samaṁ mettāya bhāvaya; </span></span></span>\n<span class='verse-line'><span class='segment' id='159.3' data-uid='bv2:159.3'><span class='pli'>Mettāpāramitaṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='159.4' data-uid='bv2:159.4'><span class='pli'>sambodhiṁ pāpuṇissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='160.1' data-uid='bv2:160.1'><span class='pli'>Na hete ettakāyeva, </span></span></span>\n<span class='verse-line'><span class='segment' id='160.2' data-uid='bv2:160.2'><span class='pli'>buddhadhammā bhavissare; </span></span></span>\n<span class='verse-line'><span class='segment' id='160.3' data-uid='bv2:160.3'><span class='pli'>Aññepi vicinissāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='160.4' data-uid='bv2:160.4'><span class='pli'>ye dhammā bodhipācanā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='161.1' data-uid='bv2:161.1'><span class='pli'>Vicinanto tadā dakkhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='161.2' data-uid='bv2:161.2'><span class='pli'>dasamaṁ <b>upekkhāpāramiṁ</b>; </span></span></span>\n<span class='verse-line'><span class='segment' id='161.3' data-uid='bv2:161.3'><span class='pli'>Pubbakehi mahesīhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='161.4' data-uid='bv2:161.4'><span class='pli'>āsevitanisevitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='162.1' data-uid='bv2:162.1'><span class='pli'>‘Imaṁ tvaṁ dasamaṁ tāva, </span></span></span>\n<span class='verse-line'><span class='segment' id='162.2' data-uid='bv2:162.2'><span class='pli'>daḷhaṁ katvā samādiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='162.3' data-uid='bv2:162.3'><span class='pli'>Tulābhūto daḷho hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='162.4' data-uid='bv2:162.4'><span class='pli'>sambodhiṁ pāpuṇissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='163.1' data-uid='bv2:163.1'><span class='pli'>Yathāpi pathavī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='163.2' data-uid='bv2:163.2'><span class='pli'>nikkhittaṁ asuciṁ suciṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='163.3' data-uid='bv2:163.3'><span class='pli'>Upekkhati ubhopete, </span></span></span>\n<span class='verse-line'><span class='segment' id='163.4' data-uid='bv2:163.4'><span class='pli'>kopānunayavajjitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='164.1' data-uid='bv2:164.1'><span class='pli'>Tatheva tvaṁ sukhadukkhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='164.2' data-uid='bv2:164.2'><span class='pli'>tulābhūto sadā bhava; </span></span></span>\n<span class='verse-line'><span class='segment' id='164.3' data-uid='bv2:164.3'><span class='pli'>Upekkhāpāramitaṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='164.4' data-uid='bv2:164.4'><span class='pli'>sambodhiṁ pāpuṇissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='165.1' data-uid='bv2:165.1'><span class='pli'>Ettakāyeva te loke, </span></span></span>\n<span class='verse-line'><span class='segment' id='165.2' data-uid='bv2:165.2'><span class='pli'>ye dhammā bodhipācanā; </span></span></span>\n<span class='verse-line'><span class='segment' id='165.3' data-uid='bv2:165.3'><span class='pli'>Tatuddhaṁ natthi aññatra, </span></span></span>\n<span class='verse-line'><span class='segment' id='165.4' data-uid='bv2:165.4'><span class='pli'>daḷhaṁ tattha patiṭṭhaha’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='166.1' data-uid='bv2:166.1'><span class='pli'>Ime dhamme sammasato, </span></span></span>\n<span class='verse-line'><span class='segment' id='166.2' data-uid='bv2:166.2'><span class='pli'>sabhāvasarasalakkhaṇe; </span></span></span>\n<span class='verse-line'><span class='segment' id='166.3' data-uid='bv2:166.3'><span class='pli'>Dhammatejena vasudhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='166.4' data-uid='bv2:166.4'><span class='pli'>dasasahassī pakampatha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='167.1' data-uid='bv2:167.1'><span class='pli'>Calatī ravatī pathavī, </span></span></span>\n<span class='verse-line'><span class='segment' id='167.2' data-uid='bv2:167.2'><span class='pli'>ucchuyantaṁva pīḷitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='167.3' data-uid='bv2:167.3'><span class='pli'>Telayante yathā cakkaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='167.4' data-uid='bv2:167.4'><span class='pli'>evaṁ kampati medanī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='168.1' data-uid='bv2:168.1'><span class='pli'>Yāvatā parisā āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='168.2' data-uid='bv2:168.2'><span class='pli'>buddhassa parivesane; </span></span></span>\n<span class='verse-line'><span class='segment' id='168.3' data-uid='bv2:168.3'><span class='pli'>Pavedhamānā sā tattha, </span></span></span>\n<span class='verse-line'><span class='segment' id='168.4' data-uid='bv2:168.4'><span class='pli'>mucchitā seti bhūmiyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='169.1' data-uid='bv2:169.1'><span class='pli'>Ghaṭānekasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='169.2' data-uid='bv2:169.2'><span class='pli'>kumbhīnañca satā bahū; </span></span></span>\n<span class='verse-line'><span class='segment' id='169.3' data-uid='bv2:169.3'><span class='pli'>Sañcuṇṇamathitā tattha, </span></span></span>\n<span class='verse-line'><span class='segment' id='169.4' data-uid='bv2:169.4'><span class='pli'>aññamaññaṁ paghaṭṭitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='170.1' data-uid='bv2:170.1'><span class='pli'>Ubbiggā tasitā bhītā, </span></span></span>\n<span class='verse-line'><span class='segment' id='170.2' data-uid='bv2:170.2'><span class='pli'>bhantā byathitamānasā; </span></span></span>\n<span class='verse-line'><span class='segment' id='170.3' data-uid='bv2:170.3'><span class='pli'>Mahājanā samāgamma, </span></span></span>\n<span class='verse-line'><span class='segment' id='170.4' data-uid='bv2:170.4'><span class='pli'>dīpaṅkaramupāgamuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='171.1' data-uid='bv2:171.1'><span class='pli'>‘Kiṁ bhavissati lokassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='171.2' data-uid='bv2:171.2'><span class='pli'>kalyāṇamatha pāpakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='171.3' data-uid='bv2:171.3'><span class='pli'>Sabbo upadduto loko, </span></span></span>\n<span class='verse-line'><span class='segment' id='171.4' data-uid='bv2:171.4'><span class='pli'>taṁ vinodehi cakkhuma’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='172.1' data-uid='bv2:172.1'><span class='pli'>Tesaṁ tadā saññapesi, </span></span></span>\n<span class='verse-line'><span class='segment' id='172.2' data-uid='bv2:172.2'><span class='pli'>dīpaṅkaro mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='172.3' data-uid='bv2:172.3'><span class='pli'>‘Visaṭṭhā hotha mā bhetha, </span></span></span>\n<span class='verse-line'><span class='segment' id='172.4' data-uid='bv2:172.4'><span class='pli'>imasmiṁ pathavikampane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='173.1' data-uid='bv2:173.1'><span class='pli'>Yamahaṁ ajja byākāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='173.2' data-uid='bv2:173.2'><span class='pli'>buddho loke bhavissati; </span></span></span>\n<span class='verse-line'><span class='segment' id='173.3' data-uid='bv2:173.3'><span class='pli'>Eso sammasati dhammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='173.4' data-uid='bv2:173.4'><span class='pli'>pubbakaṁ jinasevitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='174.1' data-uid='bv2:174.1'><span class='pli'>Tassa sammasato dhammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='174.2' data-uid='bv2:174.2'><span class='pli'>buddhabhūmiṁ asesato; </span></span></span>\n<span class='verse-line'><span class='segment' id='174.3' data-uid='bv2:174.3'><span class='pli'>Tenāyaṁ kampitā pathavī, </span></span></span>\n<span class='verse-line'><span class='segment' id='174.4' data-uid='bv2:174.4'><span class='pli'>dasasahassī sadevake’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='175.1' data-uid='bv2:175.1'><span class='pli'>Buddhassa vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='175.2' data-uid='bv2:175.2'><span class='pli'>mano nibbāyi tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='175.3' data-uid='bv2:175.3'><span class='pli'>Sabbe maṁ upasaṅkamma, </span></span></span>\n<span class='verse-line'><span class='segment' id='175.4' data-uid='bv2:175.4'><span class='pli'>punāpi abhivandisuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='176.1' data-uid='bv2:176.1'><span class='pli'>Samādiyitvā buddhaguṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='176.2' data-uid='bv2:176.2'><span class='pli'>daḷhaṁ katvāna mānasaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='176.3' data-uid='bv2:176.3'><span class='pli'>Dīpaṅkaraṁ namassitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='176.4' data-uid='bv2:176.4'><span class='pli'>āsanā vuṭṭhahiṁ tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='177.1' data-uid='bv2:177.1'><span class='pli'>Dibbaṁ mānusakaṁ pupphaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='177.2' data-uid='bv2:177.2'><span class='pli'>devā mānusakā ubho; </span></span></span>\n<span class='verse-line'><span class='segment' id='177.3' data-uid='bv2:177.3'><span class='pli'>Samokiranti pupphehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='177.4' data-uid='bv2:177.4'><span class='pli'>vuṭṭhahantassa āsanā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='178.1' data-uid='bv2:178.1'><span class='pli'>Vedayanti ca te sotthiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='178.2' data-uid='bv2:178.2'><span class='pli'>devā mānusakā ubho; </span></span></span>\n<span class='verse-line'><span class='segment' id='178.3' data-uid='bv2:178.3'><span class='pli'>‘Mahantaṁ patthitaṁ tuyhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='178.4' data-uid='bv2:178.4'><span class='pli'>taṁ labhassu yathicchitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='179.1' data-uid='bv2:179.1'><span class='pli'>Sabbītiyo vivajjantu, </span></span></span>\n<span class='verse-line'><span class='segment' id='179.2' data-uid='bv2:179.2'><span class='pli'>Soko rogo vinassatu; </span></span></span>\n<span class='verse-line'><span class='segment' id='179.3' data-uid='bv2:179.3'><span class='pli'>Mā te bhavantvantarāyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='179.4' data-uid='bv2:179.4'><span class='pli'>Phusa khippaṁ bodhimuttamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='180.1' data-uid='bv2:180.1'><span class='pli'>Yathāpi samaye patte, </span></span></span>\n<span class='verse-line'><span class='segment' id='180.2' data-uid='bv2:180.2'><span class='pli'>pupphanti pupphino dumā; </span></span></span>\n<span class='verse-line'><span class='segment' id='180.3' data-uid='bv2:180.3'><span class='pli'>Tatheva tvaṁ mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='180.4' data-uid='bv2:180.4'><span class='pli'>buddhañāṇena pupphasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='181.1' data-uid='bv2:181.1'><span class='pli'>Yathā ye keci sambuddhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='181.2' data-uid='bv2:181.2'><span class='pli'>pūrayuṁ dasa pāramī; </span></span></span>\n<span class='verse-line'><span class='segment' id='181.3' data-uid='bv2:181.3'><span class='pli'>Tatheva tvaṁ mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='181.4' data-uid='bv2:181.4'><span class='pli'>pūraya dasa pāramī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='182.1' data-uid='bv2:182.1'><span class='pli'>Yathā ye keci sambuddhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='182.2' data-uid='bv2:182.2'><span class='pli'>bodhimaṇḍamhi bujjhare; </span></span></span>\n<span class='verse-line'><span class='segment' id='182.3' data-uid='bv2:182.3'><span class='pli'>Tatheva tvaṁ mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='182.4' data-uid='bv2:182.4'><span class='pli'>bujjhassu jinabodhiyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='183.1' data-uid='bv2:183.1'><span class='pli'>Yathā ye keci sambuddhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='183.2' data-uid='bv2:183.2'><span class='pli'>dhammacakkaṁ pavattayuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='183.3' data-uid='bv2:183.3'><span class='pli'>Tatheva tvaṁ mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='183.4' data-uid='bv2:183.4'><span class='pli'>dhammacakkaṁ pavattaya. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='184.1' data-uid='bv2:184.1'><span class='pli'>Puṇṇamāye yathā cando, </span></span></span>\n<span class='verse-line'><span class='segment' id='184.2' data-uid='bv2:184.2'><span class='pli'>parisuddho virocati; </span></span></span>\n<span class='verse-line'><span class='segment' id='184.3' data-uid='bv2:184.3'><span class='pli'>Tatheva tvaṁ puṇṇamano, </span></span></span>\n<span class='verse-line'><span class='segment' id='184.4' data-uid='bv2:184.4'><span class='pli'>viroca dasasahassiyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='185.1' data-uid='bv2:185.1'><span class='pli'>Rāhumutto yathā sūriyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='185.2' data-uid='bv2:185.2'><span class='pli'>tāpena atirocati; </span></span></span>\n<span class='verse-line'><span class='segment' id='185.3' data-uid='bv2:185.3'><span class='pli'>Tatheva lokā muñcitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='185.4' data-uid='bv2:185.4'><span class='pli'>viroca siriyā tuvaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='186.1' data-uid='bv2:186.1'><span class='pli'>Yathā yā kāci nadiyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='186.2' data-uid='bv2:186.2'><span class='pli'>osaranti mahodadhiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='186.3' data-uid='bv2:186.3'><span class='pli'>Evaṁ sadevakā lokā, </span></span></span>\n<span class='verse-line'><span class='segment' id='186.4' data-uid='bv2:186.4'><span class='pli'>osarantu tavantike’. </span></span></span></p>\n<p data-counter='2681'><span class='verse-line'><span class='segment' id='187.1' data-uid='bv2:187.1'><span class='pli'>Tehi thutappasattho so, </span></span></span>\n<span class='verse-line'><span class='segment' id='187.2' data-uid='bv2:187.2'><span class='pli'>Dasa dhamme samādiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='187.3' data-uid='bv2:187.3'><span class='pli'>Te dhamme paripūrento, </span></span></span>\n<span class='verse-line'><span class='segment' id='187.4' data-uid='bv2:187.4'><span class='pli'>Pavanaṁ pāvisī tadā”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='188.1' data-uid='bv2:188.1'><span class='pli'>Sumedhapatthanākathā niṭṭhitā. </span></span></p></article>\n"
  },
  "bv3": {
    "previous": "bv2",
    "next": "bv4",
    "content": "<article id='bv3'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv3:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv3:0.2'><span class='pli'>3. Dīpaṅkarabuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv3:1.1'><span class='pli'>“Tadā te bhojayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv3:1.2'><span class='pli'>sasaṅghaṁ lokanāyakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv3:1.3'><span class='pli'>Upagacchuṁ saraṇaṁ tassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv3:1.4'><span class='pli'>dīpaṅkarassa satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv3:2.1'><span class='pli'>Saraṇāgamane kañci, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv3:2.2'><span class='pli'>nivesesi tathāgato; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv3:2.3'><span class='pli'>Kañci pañcasu sīlesu, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv3:2.4'><span class='pli'>sīle dasavidhe paraṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv3:3.1'><span class='pli'>Kassaci deti sāmaññaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv3:3.2'><span class='pli'>caturo phalamuttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv3:3.3'><span class='pli'>Kassaci asame dhamme, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv3:3.4'><span class='pli'>deti so paṭisambhidā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv3:4.1'><span class='pli'>Kassaci varasamāpattiyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv3:4.2'><span class='pli'>Aṭṭha deti narāsabho; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv3:4.3'><span class='pli'>Tisso kassaci vijjāyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv3:4.4'><span class='pli'>Chaḷabhiññā pavecchati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv3:5.1'><span class='pli'>Tena yogena janakāyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv3:5.2'><span class='pli'>ovadati mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv3:5.3'><span class='pli'>Tena vitthārikaṁ āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv3:5.4'><span class='pli'>lokanāthassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv3:6.1'><span class='pli'>Mahāhanusabhakkhandho, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv3:6.2'><span class='pli'>dīpaṅkarassa nāmako; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv3:6.3'><span class='pli'>Bahū jane tārayati, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv3:6.4'><span class='pli'>parimoceti duggatiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv3:7.1'><span class='pli'>Bodhaneyyaṁ janaṁ disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv3:7.2'><span class='pli'>satasahassepi yojane; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv3:7.3'><span class='pli'>Khaṇena upagantvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv3:7.4'><span class='pli'>bodheti taṁ mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv3:8.1'><span class='pli'>Paṭhamābhisamaye buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv3:8.2'><span class='pli'>koṭisatamabodhayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv3:8.3'><span class='pli'>Dutiyābhisamaye nātho, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv3:8.4'><span class='pli'>navutikoṭimabodhayi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv3:9.1'><span class='pli'>Yadā ca devabhavanamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv3:9.2'><span class='pli'>buddho dhammamadesayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv3:9.3'><span class='pli'>Navutikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv3:9.4'><span class='pli'>tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv3:10.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv3:10.2'><span class='pli'>dīpaṅkarassa satthuno; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv3:10.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv3:10.4'><span class='pli'>paṭhamo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv3:11.1'><span class='pli'>Puna nāradakūṭamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv3:11.2'><span class='pli'>pavivekagate jine; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv3:11.3'><span class='pli'>Khīṇāsavā vītamalā, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv3:11.4'><span class='pli'>samiṁsu satakoṭiyo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv3:12.1'><span class='pli'>Yamhi kāle mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv3:12.2'><span class='pli'>sudassanasiluccaye; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv3:12.3'><span class='pli'>Navakoṭisahassehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv3:12.4'><span class='pli'>pavāresi mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv3:13.1'><span class='pli'>Dasavīsasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv3:13.2'><span class='pli'>dhammābhisamayo ahu; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv3:13.3'><span class='pli'>Ekadvinnaṁ abhisamayā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv3:13.4'><span class='pli'>gaṇanāto asaṅkhiyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv3:14.1'><span class='pli'>Vitthārikaṁ bāhujaññaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv3:14.2'><span class='pli'>iddhaṁ phītaṁ ahū tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv3:14.3'><span class='pli'>Dīpaṅkarassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv3:14.4'><span class='pli'>sāsanaṁ suvisodhitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv3:15.1'><span class='pli'>Cattāri satasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv3:15.2'><span class='pli'>chaḷabhiññā mahiddhikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv3:15.3'><span class='pli'>Dīpaṅkaraṁ lokaviduṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv3:15.4'><span class='pli'>parivārenti sabbadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv3:16.1'><span class='pli'>Ye keci tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv3:16.2'><span class='pli'>jahanti mānusaṁ bhavaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv3:16.3'><span class='pli'>Apattamānasā sekhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv3:16.4'><span class='pli'>garahitā bhavanti te. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv3:17.1'><span class='pli'>Supupphitaṁ pāvacanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv3:17.2'><span class='pli'>arahantehi tādihi; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv3:17.3'><span class='pli'>Khīṇāsavehi vimalehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv3:17.4'><span class='pli'>upasobhati sabbadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv3:18.1'><span class='pli'>Nagaraṁ rammavatī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv3:18.2'><span class='pli'>sudevo nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv3:18.3'><span class='pli'>Sumedhā nāma janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv3:18.4'><span class='pli'>dīpaṅkarassa satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv3:19.1'><span class='pli'>Dasavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv3:19.2'><span class='pli'>agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv3:19.3'><span class='pli'>Haṁsā koñcā mayūrā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv3:19.4'><span class='pli'>tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv3:20.1'><span class='pli'>Tīṇi satasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv3:20.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv3:20.3'><span class='pli'>Padumā nāma sā nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv3:20.4'><span class='pli'>usabhakkhandho atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv3:21.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv3:21.2'><span class='pli'>hatthiyānena nikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv3:21.3'><span class='pli'>Anūnadasamāsāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv3:21.4'><span class='pli'>padhāne padahī jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv3:22.1'><span class='pli'>Padhānacāraṁ caritvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv3:22.2'><span class='pli'>abujjhi mānasaṁ muni; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv3:22.3'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv3:22.4'><span class='pli'>dīpaṅkaro mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv3:23.1'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv3:23.2'><span class='pli'>nandārāme sirīghare; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv3:23.3'><span class='pli'>Nisinno sirīsamūlamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv3:23.4'><span class='pli'>akā titthiyamaddanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv3:24.1'><span class='pli'>Sumaṅgalo ca tisso ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv3:24.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv3:24.3'><span class='pli'>Sāgato nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv3:24.4'><span class='pli'>dīpaṅkarassa satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv3:25.1'><span class='pli'>Nandā ceva sunandā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv3:25.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv3:25.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv3:25.4'><span class='pli'>pipphalīti pavuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv3:26.1'><span class='pli'>Tapussabhallikā nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv3:26.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv3:26.3'><span class='pli'>Sirimā koṇā upaṭṭhikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv3:26.4'><span class='pli'>dīpaṅkarassa satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv3:27.1'><span class='pli'>Asītihatthamubbedho, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv3:27.2'><span class='pli'>dīpaṅkaro mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv3:27.3'><span class='pli'>Sobhati dīparukkhova, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv3:27.4'><span class='pli'>sālarājāva phullito. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='bv3:28.1'><span class='pli'>Satasahassavassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv3:28.2'><span class='pli'>āyu tassa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv3:28.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv3:28.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='bv3:29.1'><span class='pli'>Jotayitvāna saddhammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='bv3:29.2'><span class='pli'>santāretvā mahājanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='bv3:29.3'><span class='pli'>Jalitvā aggikkhandhova, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='bv3:29.4'><span class='pli'>nibbuto so sasāvako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='bv3:30.1'><span class='pli'>Sā ca iddhi so ca yaso, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='bv3:30.2'><span class='pli'>Tāni ca pādesu cakkaratanāni; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='bv3:30.3'><span class='pli'>Sabbaṁ tamantarahitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='bv3:30.4'><span class='pli'>Nanu rittā sabbasaṅkhārā. </span></span></span></p>\n<p data-counter='2991'><span class='verse-line'><span class='segment' id='31.1' data-uid='bv3:31.1'><span class='pli'>Dīpaṅkaro jino satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='bv3:31.2'><span class='pli'>Nandārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='bv3:31.3'><span class='pli'>Tatthevassa jinathūpo, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='bv3:31.4'><span class='pli'>Chattiṁsubbedhayojano”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='32.1' data-uid='bv3:32.1'><span class='pli'>Dīpaṅkarassa bhagavato vaṁso paṭhamo. </span></span></p></article>\n"
  },
  "bv4": {
    "previous": "bv3",
    "next": "bv5",
    "content": "<article id='bv4'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv4:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv4:0.2'><span class='pli'>4. Koṇḍaññabuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv4:1.1'><span class='pli'>“Dīpaṅkarassa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv4:1.2'><span class='pli'>Koṇḍañño nāma nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv4:1.3'><span class='pli'>Anantatejo amitayaso, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv4:1.4'><span class='pli'>Appameyyo durāsado. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv4:2.1'><span class='pli'>Dharaṇūpamo khamanena, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv4:2.2'><span class='pli'>sīlena sāgarūpamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv4:2.3'><span class='pli'>Samādhinā merūpamo, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv4:2.4'><span class='pli'>ñāṇena gaganūpamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv4:3.1'><span class='pli'>Indriyabalabojjhaṅga- </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv4:3.2'><span class='pli'>maggasaccappakāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv4:3.3'><span class='pli'>Pakāsesi sadā buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv4:3.4'><span class='pli'>hitāya sabbapāṇinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv4:4.1'><span class='pli'>Dhammacakkaṁ pavattente, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv4:4.2'><span class='pli'>koṇḍaññe lokanāyake; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv4:4.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv4:4.4'><span class='pli'>paṭhamābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv4:5.1'><span class='pli'>Tato parampi desente, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv4:5.2'><span class='pli'>naramarūnaṁ samāgame; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv4:5.3'><span class='pli'>Navutikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv4:5.4'><span class='pli'>dutiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv4:6.1'><span class='pli'>Titthiye abhimaddanto, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv4:6.2'><span class='pli'>yadā dhammamadesayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv4:6.3'><span class='pli'>Asītikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv4:6.4'><span class='pli'>tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv4:7.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv4:7.2'><span class='pli'>koṇḍaññassa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv4:7.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv4:7.4'><span class='pli'>santacittāna tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv4:8.1'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv4:8.2'><span class='pli'>paṭhamo āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv4:8.3'><span class='pli'>Dutiyo koṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv4:8.4'><span class='pli'>tatiyo navutikoṭinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv4:9.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv4:9.2'><span class='pli'>vijitāvī nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv4:9.3'><span class='pli'>Samuddaṁ antamantena, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv4:9.4'><span class='pli'>issariyaṁ vattayāmahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv4:10.1'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv4:10.2'><span class='pli'>vimalānaṁ mahesinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv4:10.3'><span class='pli'>Saha lokagganāthena, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv4:10.4'><span class='pli'>paramannena tappayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv4:11.1'><span class='pli'>Sopi maṁ buddho byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv4:11.2'><span class='pli'>koṇḍañño lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv4:11.3'><span class='pli'>‘Aparimeyyito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv4:11.4'><span class='pli'>buddho loke bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv4:12.1'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv4:12.2'><span class='pli'>katvā dukkarakārikaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv4:12.3'><span class='pli'>Assatthamūle sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv4:12.4'><span class='pli'>bujjhissati mahāyaso. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv4:13.1'><span class='pli'>Imassa janikā mātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv4:13.2'><span class='pli'>māyā nāma bhavissati; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv4:13.3'><span class='pli'>Pitā suddhodano nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv4:13.4'><span class='pli'>ayaṁ hessati gotamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv4:14.1'><span class='pli'>Kolito upatisso ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv4:14.2'><span class='pli'>aggā hessanti sāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv4:14.3'><span class='pli'>Ānando nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv4:14.4'><span class='pli'>upaṭṭhissatimaṁ jinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv4:15.1'><span class='pli'>Khemā uppalavaṇṇā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv4:15.2'><span class='pli'>aggā hessanti sāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv4:15.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv4:15.4'><span class='pli'>assatthoti pavuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv4:16.1'><span class='pli'>Citto ca hatthāḷavako, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv4:16.2'><span class='pli'>aggā hessantupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv4:16.3'><span class='pli'>Nandamātā ca uttarā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv4:16.4'><span class='pli'>aggā hessantupaṭṭhikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.5' data-uid='bv4:16.5'><span class='pli'>Āyu vassasataṁ tassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.6' data-uid='bv4:16.6'><span class='pli'>gotamassa yasassino’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv4:17.1'><span class='pli'>Idaṁ sutvāna vacanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv4:17.2'><span class='pli'>asamassa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv4:17.3'><span class='pli'>Āmoditā naramarū, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv4:17.4'><span class='pli'>‘buddhabījaṁ kira ayaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv4:18.1'><span class='pli'>Ukkuṭṭhisaddā vattanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv4:18.2'><span class='pli'>apphoṭenti hasanti ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv4:18.3'><span class='pli'>Katañjalī namassanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv4:18.4'><span class='pli'>dasasahassidevatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv4:19.1'><span class='pli'>‘Yadimassa lokanāthassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv4:19.2'><span class='pli'>virajjhissāma sāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv4:19.3'><span class='pli'>Anāgatamhi addhāne, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv4:19.4'><span class='pli'>hessāma sammukhā imaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv4:20.1'><span class='pli'>Yathā manussā nadiṁ tarantā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv4:20.2'><span class='pli'>Paṭititthaṁ virajjhiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv4:20.3'><span class='pli'>Heṭṭhātitthe gahetvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv4:20.4'><span class='pli'>Uttaranti mahānadiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv4:21.1'><span class='pli'>Evamevaṁ mayaṁ sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv4:21.2'><span class='pli'>yadi muñcāmimaṁ jinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv4:21.3'><span class='pli'>Anāgatamhi addhāne, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv4:21.4'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv4:22.1'><span class='pli'>Tassāhaṁ vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv4:22.2'><span class='pli'>bhiyyo cittaṁ pasādayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv4:22.3'><span class='pli'>Tameva atthaṁ sādhento, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv4:22.4'><span class='pli'>mahārajjaṁ jine adaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.5' data-uid='bv4:22.5'><span class='pli'>Mahārajjaṁ daditvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.6' data-uid='bv4:22.6'><span class='pli'>pabbajiṁ tassa santike. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv4:23.1'><span class='pli'>Suttantaṁ vinayañcāpi, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv4:23.2'><span class='pli'>navaṅgaṁ satthusāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv4:23.3'><span class='pli'>Sabbaṁ pariyāpuṇitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv4:23.4'><span class='pli'>sobhayiṁ jinasāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv4:24.1'><span class='pli'>Tatthappamatto viharanto, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv4:24.2'><span class='pli'>nisajjaṭṭhānacaṅkame; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv4:24.3'><span class='pli'>Abhiññāpāramiṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv4:24.4'><span class='pli'>brahmalokamagañchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv4:25.1'><span class='pli'>Nagaraṁ rammavatī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv4:25.2'><span class='pli'>sunando nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv4:25.3'><span class='pli'>Sujātā nāma janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv4:25.4'><span class='pli'>koṇḍaññassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv4:26.1'><span class='pli'>Dasavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv4:26.2'><span class='pli'>agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv4:26.3'><span class='pli'>Suci suruci subho ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv4:26.4'><span class='pli'>tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv4:27.1'><span class='pli'>Tīṇi satasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv4:27.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv4:27.3'><span class='pli'>Rucidevī nāma nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv4:27.4'><span class='pli'>vijitaseno atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='bv4:28.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv4:28.2'><span class='pli'>rathayānena nikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv4:28.3'><span class='pli'>Anūnadasamāsāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv4:28.4'><span class='pli'>padhānaṁ padahī jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='bv4:29.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='bv4:29.2'><span class='pli'>koṇḍañño dvipaduttamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='bv4:29.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='bv4:29.4'><span class='pli'>devānaṁ nagaruttame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='bv4:30.1'><span class='pli'>Bhaddo ceva subhaddo ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='bv4:30.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='bv4:30.3'><span class='pli'>Anuruddho nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='bv4:30.4'><span class='pli'>koṇḍaññassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='bv4:31.1'><span class='pli'>Tissā ca upatissā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='bv4:31.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='bv4:31.3'><span class='pli'>Sālakalyāṇiko bodhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='bv4:31.4'><span class='pli'>koṇḍaññassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='bv4:32.1'><span class='pli'>Soṇo ca upasoṇo ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='bv4:32.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='bv4:32.3'><span class='pli'>Nandā ceva sirīmā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='bv4:32.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='bv4:33.1'><span class='pli'>So aṭṭhāsīti hatthāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='bv4:33.2'><span class='pli'>accuggato mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='bv4:33.3'><span class='pli'>Sobhate uḷurājāva, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='bv4:33.4'><span class='pli'>sūriyo majjhanhike yathā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='34.1' data-uid='bv4:34.1'><span class='pli'>Vassasatasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='bv4:34.2'><span class='pli'>āyu vijjati tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='bv4:34.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.4' data-uid='bv4:34.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='35.1' data-uid='bv4:35.1'><span class='pli'>Khīṇāsavehi vimalehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.2' data-uid='bv4:35.2'><span class='pli'>vicittā āsi medanī; </span></span></span>\n<span class='verse-line'><span class='segment' id='35.3' data-uid='bv4:35.3'><span class='pli'>Yathā gaganamuḷūhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.4' data-uid='bv4:35.4'><span class='pli'>evaṁ so upasobhatha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='36.1' data-uid='bv4:36.1'><span class='pli'>Tepi nāgā appameyyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.2' data-uid='bv4:36.2'><span class='pli'>asaṅkhobbhā durāsadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='36.3' data-uid='bv4:36.3'><span class='pli'>Vijjupātaṁva dassetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.4' data-uid='bv4:36.4'><span class='pli'>nibbutā te mahāyasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='37.1' data-uid='bv4:37.1'><span class='pli'>Sā ca atuliyā jinassa iddhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.2' data-uid='bv4:37.2'><span class='pli'>Ñāṇaparibhāvito ca samādhi; </span></span></span>\n<span class='verse-line'><span class='segment' id='37.3' data-uid='bv4:37.3'><span class='pli'>Sabbaṁ tamantarahitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.4' data-uid='bv4:37.4'><span class='pli'>Nanu rittā sabbasaṅkhārā. </span></span></span></p>\n<p data-counter='3371'><span class='verse-line'><span class='segment' id='38.1' data-uid='bv4:38.1'><span class='pli'>Koṇḍañño pavaro buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.2' data-uid='bv4:38.2'><span class='pli'>Candārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='38.3' data-uid='bv4:38.3'><span class='pli'>Tattheva cetiyo citto, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.4' data-uid='bv4:38.4'><span class='pli'>Satta yojanamussito”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='39.1' data-uid='bv4:39.1'><span class='pli'>Koṇḍaññassa bhagavato vaṁso dutiyo. </span></span></p></article>\n"
  },
  "bv5": {
    "previous": "bv4",
    "next": "bv6",
    "content": "<article id='bv5'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv5:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv5:0.2'><span class='pli'>5. Maṅgalabuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv5:1.1'><span class='pli'>“Koṇḍaññassa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv5:1.2'><span class='pli'>maṅgalo nāma nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv5:1.3'><span class='pli'>Tamaṁ loke nihantvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv5:1.4'><span class='pli'>dhammokkamabhidhārayi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv5:2.1'><span class='pli'>Atulāsi pabhā tassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv5:2.2'><span class='pli'>jinehaññehi uttariṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv5:2.3'><span class='pli'>Candasūriyapabhaṁ hantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv5:2.4'><span class='pli'>dasasahassī virocati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv5:3.1'><span class='pli'>Sopi buddho pakāsesi, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv5:3.2'><span class='pli'>caturo saccavaruttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv5:3.3'><span class='pli'>Te te saccarasaṁ pītvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv5:3.4'><span class='pli'>vinodenti mahātamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv5:4.1'><span class='pli'>Patvāna bodhimatulaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv5:4.2'><span class='pli'>paṭhame dhammadesane; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv5:4.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv5:4.4'><span class='pli'>dhammābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv5:5.1'><span class='pli'>Surindadevabhavane, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv5:5.2'><span class='pli'>buddho dhammamadesayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv5:5.3'><span class='pli'>Tadā koṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv5:5.4'><span class='pli'>dutiyo samayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv5:6.1'><span class='pli'>Yadā sunando cakkavattī, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv5:6.2'><span class='pli'>sambuddhaṁ upasaṅkami; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv5:6.3'><span class='pli'>Tadā āhani sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv5:6.4'><span class='pli'>dhammabheriṁ varuttamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv5:7.1'><span class='pli'>Sunandassānucarā janatā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv5:7.2'><span class='pli'>Tadāsuṁ navutikoṭiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv5:7.3'><span class='pli'>Sabbepi te niravasesā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv5:7.4'><span class='pli'>Ahesuṁ ehi bhikkhukā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv5:8.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv5:8.2'><span class='pli'>maṅgalassa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv5:8.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv5:8.4'><span class='pli'>paṭhamo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv5:9.1'><span class='pli'>Dutiyo koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv5:9.2'><span class='pli'>Tatiyo navutikoṭinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv5:9.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv5:9.4'><span class='pli'>Tadā āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv5:10.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv5:10.2'><span class='pli'>surucī nāma brāhmaṇo; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv5:10.3'><span class='pli'>Ajjhāyako mantadharo, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv5:10.4'><span class='pli'>tiṇṇaṁ vedāna pāragū. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv5:11.1'><span class='pli'>Tamahaṁ upasaṅkamma, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv5:11.2'><span class='pli'>Saraṇaṁ gantvāna satthuno; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv5:11.3'><span class='pli'>Sambuddhappamukhaṁ saṅghaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv5:11.4'><span class='pli'>Gandhamālena pūjayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.5' data-uid='bv5:11.5'><span class='pli'>Pūjetvā gandhamālena, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.6' data-uid='bv5:11.6'><span class='pli'>Gavapānena tappayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv5:12.1'><span class='pli'>Sopi maṁ buddho byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv5:12.2'><span class='pli'>maṅgalo dvipaduttamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv5:12.3'><span class='pli'>‘Aparimeyyito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv5:12.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv5:13.1'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv5:13.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv5:13.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv5:14.1'><span class='pli'>Tassāpi vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv5:14.2'><span class='pli'>bhiyyo cittaṁ pasādayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv5:14.3'><span class='pli'>Uttariṁ vatamadhiṭṭhāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv5:14.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv5:15.1'><span class='pli'>Tadā pītimanubrūhanto, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv5:15.2'><span class='pli'>sambodhivarapattiyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv5:15.3'><span class='pli'>Buddhe datvāna maṁ gehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv5:15.4'><span class='pli'>pabbajiṁ tassa santike. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv5:16.1'><span class='pli'>Suttantaṁ vinayañcāpi, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv5:16.2'><span class='pli'>navaṅgaṁ satthusāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv5:16.3'><span class='pli'>Sabbaṁ pariyāpuṇitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv5:16.4'><span class='pli'>sobhayiṁ jinasāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv5:17.1'><span class='pli'>Tatthappamatto viharanto, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv5:17.2'><span class='pli'>brahmaṁ bhāvetva bhāvanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv5:17.3'><span class='pli'>Abhiññāpāramiṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv5:17.4'><span class='pli'>brahmalokamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv5:18.1'><span class='pli'>Uttaraṁ nāma nagaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv5:18.2'><span class='pli'>uttaro nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv5:18.3'><span class='pli'>Uttarā nāma janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv5:18.4'><span class='pli'>maṅgalassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv5:19.1'><span class='pli'>Navavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv5:19.2'><span class='pli'>agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv5:19.3'><span class='pli'>Yasavā sucimā sirīmā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv5:19.4'><span class='pli'>tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv5:20.1'><span class='pli'>Samatiṁsasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv5:20.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv5:20.3'><span class='pli'>Yasavatī nāma nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv5:20.4'><span class='pli'>sīvalo nāma atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv5:21.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv5:21.2'><span class='pli'>assayānena nikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv5:21.3'><span class='pli'>Anūnaaṭṭhamāsāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv5:21.4'><span class='pli'>padhānaṁ padahī jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv5:22.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv5:22.2'><span class='pli'>maṅgalo nāma nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv5:22.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv5:22.4'><span class='pli'>vane sirīvaruttame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv5:23.1'><span class='pli'>Sudevo dhammaseno ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv5:23.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv5:23.3'><span class='pli'>Pālito nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv5:23.4'><span class='pli'>maṅgalassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv5:24.1'><span class='pli'>Sīvalā ca asokā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv5:24.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv5:24.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv5:24.4'><span class='pli'>nāgarukkhoti vuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv5:25.1'><span class='pli'>Nando ceva visākho ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv5:25.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv5:25.3'><span class='pli'>Anulā ceva sutanā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv5:25.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv5:26.1'><span class='pli'>Aṭṭhāsīti ratanāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv5:26.2'><span class='pli'>accuggato mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv5:26.3'><span class='pli'>Tato niddhāvatī raṁsī, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv5:26.4'><span class='pli'>anekasatasahassiyo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv5:27.1'><span class='pli'>Navutivassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv5:27.2'><span class='pli'>āyu vijjati tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv5:27.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv5:27.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='bv5:28.1'><span class='pli'>Yathāpi sāgare ūmī, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv5:28.2'><span class='pli'>na sakkā tā gaṇetuye; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv5:28.3'><span class='pli'>Tatheva sāvakā tassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv5:28.4'><span class='pli'>na sakkā te gaṇetuye. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='bv5:29.1'><span class='pli'>Yāva aṭṭhāsi sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='bv5:29.2'><span class='pli'>maṅgalo lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='bv5:29.3'><span class='pli'>Na tassa sāsane atthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='bv5:29.4'><span class='pli'>sakilesamaraṇaṁ tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='bv5:30.1'><span class='pli'>Dhammokkaṁ dhārayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='bv5:30.2'><span class='pli'>santāretvā mahājanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='bv5:30.3'><span class='pli'>Jalitvā dhūmaketūva, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='bv5:30.4'><span class='pli'>nibbuto so mahāyaso. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='bv5:31.1'><span class='pli'>Saṅkhārānaṁ sabhāvatthaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='bv5:31.2'><span class='pli'>dassayitvā sadevake; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='bv5:31.3'><span class='pli'>Jalitvā aggikkhandhova, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='bv5:31.4'><span class='pli'>sūriyo atthaṅgato yathā. </span></span></span></p>\n<p data-counter='3691'><span class='verse-line'><span class='segment' id='32.1' data-uid='bv5:32.1'><span class='pli'>Uyyāne vassare nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='bv5:32.2'><span class='pli'>Buddho nibbāyi maṅgalo; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='bv5:32.3'><span class='pli'>Tatthevassa jinathūpo, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='bv5:32.4'><span class='pli'>Tiṁsayojanamuggato”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='33.1' data-uid='bv5:33.1'><span class='pli'>Maṅgalassa bhagavato vaṁso tatiyo. </span></span></p></article>\n"
  },
  "bv6": {
    "previous": "bv5",
    "next": "bv7",
    "content": "<article id='bv6'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv6:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv6:0.2'><span class='pli'>6. Sumanabuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv6:1.1'><span class='pli'>“Maṅgalassa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv6:1.2'><span class='pli'>sumano nāma nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv6:1.3'><span class='pli'>Sabbadhammehi asamo, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv6:1.4'><span class='pli'>sabbasattānamuttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv6:2.1'><span class='pli'>Tadā amatabheriṁ so, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv6:2.2'><span class='pli'>āhanī mekhale pure; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv6:2.3'><span class='pli'>Dhammasaṅkhasamāyuttaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv6:2.4'><span class='pli'>navaṅgaṁ jinasāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv6:3.1'><span class='pli'>Nijjinitvā kilese so, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv6:3.2'><span class='pli'>patvā sambodhimuttamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv6:3.3'><span class='pli'>Māpesi nagaraṁ satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv6:3.4'><span class='pli'>saddhammapuravaruttamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv6:4.1'><span class='pli'>Nirantaraṁ akuṭilaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv6:4.2'><span class='pli'>ujuṁ vipulavitthataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv6:4.3'><span class='pli'>Māpesi so mahāvīthiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv6:4.4'><span class='pli'>satipaṭṭhānavaruttamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv6:5.1'><span class='pli'>Phale cattāri sāmaññe, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv6:5.2'><span class='pli'>catasso paṭisambhidā; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv6:5.3'><span class='pli'>Chaḷabhiññāṭṭhasamāpattī, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv6:5.4'><span class='pli'>pasāresi tattha vīthiyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv6:6.1'><span class='pli'>Ye appamattā akhilā, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv6:6.2'><span class='pli'>hirivīriyehupāgatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv6:6.3'><span class='pli'>Te te ime guṇavare, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv6:6.4'><span class='pli'>ādiyanti yathā sukhaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv6:7.1'><span class='pli'>Evametena yogena, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv6:7.2'><span class='pli'>uddharanto mahājanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv6:7.3'><span class='pli'>Bodhesi paṭhamaṁ satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv6:7.4'><span class='pli'>koṭisatasahassiyo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv6:8.1'><span class='pli'>Yamhi kāle mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv6:8.2'><span class='pli'>ovadī titthiye gaṇe; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv6:8.3'><span class='pli'>Koṭisahassābhisamiṁsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv6:8.4'><span class='pli'>dutiye dhammadesane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv6:9.1'><span class='pli'>Yadā devā manussā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv6:9.2'><span class='pli'>samaggā ekamānasā; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv6:9.3'><span class='pli'>Nirodhapañhaṁ pucchiṁsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv6:9.4'><span class='pli'>saṁsayañcāpi mānasaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv6:10.1'><span class='pli'>Tadāpi dhammadesane, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv6:10.2'><span class='pli'>nirodhaparidīpane; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv6:10.3'><span class='pli'>Navutikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv6:10.4'><span class='pli'>tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv6:11.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv6:11.2'><span class='pli'>sumanassa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv6:11.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv6:11.4'><span class='pli'>santacittāna tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv6:12.1'><span class='pli'>Vassaṁvutthassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv6:12.2'><span class='pli'>abhighuṭṭhe pavāraṇe; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv6:12.3'><span class='pli'>Koṭisatasahassehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv6:12.4'><span class='pli'>pavāresi tathāgato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv6:13.1'><span class='pli'>Tatoparaṁ sannipāte, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv6:13.2'><span class='pli'>vimale kañcanapabbate; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv6:13.3'><span class='pli'>Navutikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv6:13.4'><span class='pli'>dutiyo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv6:14.1'><span class='pli'>Yadā sakko devarājā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv6:14.2'><span class='pli'>buddhadassanupāgami; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv6:14.3'><span class='pli'>Asītikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv6:14.4'><span class='pli'>tatiyo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv6:15.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv6:15.2'><span class='pli'>nāgarājā mahiddhiko; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv6:15.3'><span class='pli'>Atulo nāma nāmena, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv6:15.4'><span class='pli'>ussannakusalasañcayo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv6:16.1'><span class='pli'>Tadāhaṁ nāgabhavanā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv6:16.2'><span class='pli'>nikkhamitvā sañātibhi; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv6:16.3'><span class='pli'>Nāgānaṁ dibbaturiyehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv6:16.4'><span class='pli'>sasaṅghaṁ jinamupaṭṭhahiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv6:17.1'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv6:17.2'><span class='pli'>annapānena tappayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv6:17.3'><span class='pli'>Paccekadussayugaṁ datvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv6:17.4'><span class='pli'>saraṇaṁ tamupāgamiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv6:18.1'><span class='pli'>Sopi maṁ buddho byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv6:18.2'><span class='pli'>sumano lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv6:18.3'><span class='pli'>‘Aparimeyyito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv6:18.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv6:19.1'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv6:19.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv6:19.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv6:20.1'><span class='pli'>Tassāpi vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv6:20.2'><span class='pli'>bhiyyo cittaṁ pasādayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv6:20.3'><span class='pli'>Uttariṁ vatamadhiṭṭhāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv6:20.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv6:21.1'><span class='pli'>Nagaraṁ mekhalaṁ nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv6:21.2'><span class='pli'>sudatto nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv6:21.3'><span class='pli'>Sirimā nāma janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv6:21.4'><span class='pli'>sumanassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv6:22.1'><span class='pli'>Navavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv6:22.2'><span class='pli'>agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv6:22.3'><span class='pli'>Cando sucando vaṭaṁso ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv6:22.4'><span class='pli'>tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv6:23.1'><span class='pli'>Tesaṭṭhisatasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv6:23.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv6:23.3'><span class='pli'>Vaṭaṁsikā nāma nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv6:23.4'><span class='pli'>anūpamo nāma atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv6:24.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv6:24.2'><span class='pli'>hatthiyānena nikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv6:24.3'><span class='pli'>Anūnadasamāsāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv6:24.4'><span class='pli'>padhānaṁ padahī jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv6:25.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv6:25.2'><span class='pli'>sumano lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv6:25.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv6:25.4'><span class='pli'>mekhale puramuttame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv6:26.1'><span class='pli'>Saraṇo bhāvitatto ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv6:26.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv6:26.3'><span class='pli'>Udeno nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv6:26.4'><span class='pli'>sumanassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv6:27.1'><span class='pli'>Soṇā ca upasoṇā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv6:27.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv6:27.3'><span class='pli'>Sopi buddho amitayaso, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv6:27.4'><span class='pli'>nāgamūle abujjhatha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='bv6:28.1'><span class='pli'>Varuṇo ceva saraṇo ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv6:28.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv6:28.3'><span class='pli'>Cālā ca upacālā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv6:28.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='bv6:29.1'><span class='pli'>Uccattanena so buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='bv6:29.2'><span class='pli'>navutihatthamuggato; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='bv6:29.3'><span class='pli'>Kañcanagghiyasaṅkāso, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='bv6:29.4'><span class='pli'>dasasahassī virocati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='bv6:30.1'><span class='pli'>Navutivassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='bv6:30.2'><span class='pli'>āyu vijjati tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='bv6:30.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='bv6:30.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='bv6:31.1'><span class='pli'>Tāraṇīye tārayitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='bv6:31.2'><span class='pli'>bodhanīye ca bodhayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='bv6:31.3'><span class='pli'>Parinibbāyi sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='bv6:31.4'><span class='pli'>uḷurājāva atthami. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='bv6:32.1'><span class='pli'>Te ca khīṇāsavā bhikkhū, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='bv6:32.2'><span class='pli'>so ca buddho asādiso; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='bv6:32.3'><span class='pli'>Atulappabhaṁ dassayitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='bv6:32.4'><span class='pli'>nibbutā ye mahāyasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='bv6:33.1'><span class='pli'>Tañca ñāṇaṁ atuliyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='bv6:33.2'><span class='pli'>Tāni ca atulāni ratanāni; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='bv6:33.3'><span class='pli'>Sabbaṁ tamantarahitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='bv6:33.4'><span class='pli'>Nanu rittā sabbasaṅkhārā. </span></span></span></p>\n<p data-counter='4031'><span class='verse-line'><span class='segment' id='34.1' data-uid='bv6:34.1'><span class='pli'>Sumano yasadharo buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='bv6:34.2'><span class='pli'>Aṅgārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='bv6:34.3'><span class='pli'>Tattheva tassa jinathūpo, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.4' data-uid='bv6:34.4'><span class='pli'>Catuyojanamuggato”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='35.1' data-uid='bv6:35.1'><span class='pli'>Sumanassa bhagavato vaṁso catuttho. </span></span></p></article>\n"
  },
  "bv7": {
    "previous": "bv6",
    "next": "bv8",
    "content": "<article id='bv7'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv7:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv7:0.2'><span class='pli'>7. Revatabuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv7:1.1'><span class='pli'>Sumanassa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv7:1.2'><span class='pli'>revato nāma nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv7:1.3'><span class='pli'>Anūpamo asadiso, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv7:1.4'><span class='pli'>atulo uttamo jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv7:2.1'><span class='pli'>Sopi dhammaṁ pakāsesi, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv7:2.2'><span class='pli'>brahmunā abhiyācito; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv7:2.3'><span class='pli'>Khandhadhātuvavatthānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv7:2.4'><span class='pli'>appavattaṁ bhavābhave. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv7:3.1'><span class='pli'>Tassābhisamayā tīṇi, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv7:3.2'><span class='pli'>ahesuṁ dhammadesane; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv7:3.3'><span class='pli'>Gaṇanāya na vattabbo, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv7:3.4'><span class='pli'>paṭhamābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv7:4.1'><span class='pli'>Yadā arindamaṁ rājaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv7:4.2'><span class='pli'>vinesi revato muni; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv7:4.3'><span class='pli'>Tadā koṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv7:4.4'><span class='pli'>dutiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv7:5.1'><span class='pli'>Sattāhaṁ paṭisallānā, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv7:5.2'><span class='pli'>vuṭṭhahitvā narāsabho; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv7:5.3'><span class='pli'>Koṭisataṁ naramarūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv7:5.4'><span class='pli'>vinesi uttame phale. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv7:6.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv7:6.2'><span class='pli'>revatassa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv7:6.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv7:6.4'><span class='pli'>suvimuttāna tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv7:7.1'><span class='pli'>Atikkantā gaṇanapathaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv7:7.2'><span class='pli'>paṭhamaṁ ye samāgatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv7:7.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv7:7.4'><span class='pli'>dutiyo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv7:8.1'><span class='pli'>Yopi paññāya asamo, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv7:8.2'><span class='pli'>tassa cakkānuvattako; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv7:8.3'><span class='pli'>So tadā byādhito āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv7:8.4'><span class='pli'>patto jīvitasaṁsayaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv7:9.1'><span class='pli'>Tassa gilānapucchāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv7:9.2'><span class='pli'>ye tadā upagatā munī; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv7:9.3'><span class='pli'>Koṭisahassā arahanto, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv7:9.4'><span class='pli'>tatiyo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv7:10.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv7:10.2'><span class='pli'>atidevo nāma brāhmaṇo; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv7:10.3'><span class='pli'>Upagantvā revataṁ buddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv7:10.4'><span class='pli'>saraṇaṁ tassa gañchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv7:11.1'><span class='pli'>Tassa sīlaṁ samādhiñca, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv7:11.2'><span class='pli'>paññāguṇamanuttamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv7:11.3'><span class='pli'>Thomayitvā yathāthāmaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv7:11.4'><span class='pli'>uttarīyamadāsahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv7:12.1'><span class='pli'>Sopi maṁ buddho byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv7:12.2'><span class='pli'>revato lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv7:12.3'><span class='pli'>“Aparimeyyito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv7:12.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv7:13.1'><span class='pli'>‘Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv7:13.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv7:13.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv7:14.1'><span class='pli'>Tassāpi vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv7:14.2'><span class='pli'>bhiyyo cittaṁ pasādayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv7:14.3'><span class='pli'>Uttariṁ vatamadhiṭṭhāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv7:14.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv7:15.1'><span class='pli'>Tadāpi taṁ buddhadhammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv7:15.2'><span class='pli'>saritvā anubrūhayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv7:15.3'><span class='pli'>Āharissāmi taṁ dhammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv7:15.4'><span class='pli'>yaṁ mayhaṁ abhipatthitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv7:16.1'><span class='pli'>Nagaraṁ sudhaññavatī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv7:16.2'><span class='pli'>Vipulo nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv7:16.3'><span class='pli'>Vipulā nāma janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv7:16.4'><span class='pli'>Revatassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv7:17.1'><span class='pli'>Cha ca vassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv7:17.2'><span class='pli'>agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv7:17.3'><span class='pli'>Sudassano ratanagghi, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv7:17.4'><span class='pli'>āveḷo ca vibhūsito; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.5' data-uid='bv7:17.5'><span class='pli'>Puññakammābhinibbattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.6' data-uid='bv7:17.6'><span class='pli'>tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv7:18.1'><span class='pli'>Tettiṁsa ca sahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv7:18.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv7:18.3'><span class='pli'>Sudassanā nāma nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv7:18.4'><span class='pli'>varuṇo nāma atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv7:19.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv7:19.2'><span class='pli'>rathayānena nikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv7:19.3'><span class='pli'>Anūnasattamāsāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv7:19.4'><span class='pli'>padhānaṁ padahī jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv7:20.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv7:20.2'><span class='pli'>revato lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv7:20.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv7:20.4'><span class='pli'>varuṇārāme sirīghare. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv7:21.1'><span class='pli'>Varuṇo brahmadevo ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv7:21.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv7:21.3'><span class='pli'>Sambhavo nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv7:21.4'><span class='pli'>revatassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv7:22.1'><span class='pli'>Bhaddā ceva subhaddā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv7:22.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv7:22.3'><span class='pli'>Sopi buddho asamasamo, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv7:22.4'><span class='pli'>nāgamūle abujjhatha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv7:23.1'><span class='pli'>Padumo kuñjaro ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv7:23.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv7:23.3'><span class='pli'>Sirīmā ceva yasavatī, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv7:23.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv7:24.1'><span class='pli'>Uccattanena so buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv7:24.2'><span class='pli'>asītihatthamuggato; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv7:24.3'><span class='pli'>Obhāseti disā sabbā, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv7:24.4'><span class='pli'>indaketuva uggato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv7:25.1'><span class='pli'>Tassa sarīre nibbattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv7:25.2'><span class='pli'>pabhāmālā anuttarā; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv7:25.3'><span class='pli'>Divā vā yadi vā rattiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv7:25.4'><span class='pli'>samantā pharati yojanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv7:26.1'><span class='pli'>Saṭṭhivassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv7:26.2'><span class='pli'>āyu vijjati tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv7:26.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv7:26.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv7:27.1'><span class='pli'>Dassayitvā buddhabalaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv7:27.2'><span class='pli'>amataṁ loke pakāsayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv7:27.3'><span class='pli'>Nibbāyi anupādāno, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv7:27.4'><span class='pli'>yathaggupādānasaṅkhayā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='bv7:28.1'><span class='pli'>So ca kāyo ratananibho, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv7:28.2'><span class='pli'>so ca dhammo asādiso; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv7:28.3'><span class='pli'>Sabbaṁ tamantarahitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv7:28.4'><span class='pli'>nanu rittā sabbasaṅkhārā. </span></span></span></p>\n<p data-counter='4321'><span class='verse-line'><span class='segment' id='29.1' data-uid='bv7:29.1'><span class='pli'>Revato yasadharo buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='bv7:29.2'><span class='pli'>Nibbuto so mahāpure; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='bv7:29.3'><span class='pli'>Dhātuvitthārikaṁ āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='bv7:29.4'><span class='pli'>Tesu tesu padesato”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='30.1' data-uid='bv7:30.1'><span class='pli'>Revatassa bhagavato vaṁso pañcamo. </span></span></p></article>\n"
  },
  "bv8": {
    "previous": "bv7",
    "next": "bv9",
    "content": "<article id='bv8'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv8:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv8:0.2'><span class='pli'>8. Sobhitabuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv8:1.1'><span class='pli'>“Revatassa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv8:1.2'><span class='pli'>sobhito nāma nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv8:1.3'><span class='pli'>Samāhito santacitto, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv8:1.4'><span class='pli'>asamo appaṭipuggalo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv8:2.1'><span class='pli'>So jino sakagehamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv8:2.2'><span class='pli'>mānasaṁ vinivattayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv8:2.3'><span class='pli'>Patvāna kevalaṁ bodhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv8:2.4'><span class='pli'>dhammacakkaṁ pavattayi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv8:3.1'><span class='pli'>Yāva heṭṭhā avīcito, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv8:3.2'><span class='pli'>bhavaggā cāpi uddhato; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv8:3.3'><span class='pli'>Etthantare ekaparisā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv8:3.4'><span class='pli'>ahosi dhammadesane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv8:4.1'><span class='pli'>Tāya parisāya sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv8:4.2'><span class='pli'>dhammacakkaṁ pavattayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv8:4.3'><span class='pli'>Gaṇanāya na vattabbo, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv8:4.4'><span class='pli'>paṭhamābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv8:5.1'><span class='pli'>Tato parampi desente, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv8:5.2'><span class='pli'>marūnañca samāgame; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv8:5.3'><span class='pli'>Navutikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv8:5.4'><span class='pli'>dutiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv8:6.1'><span class='pli'>Punāparaṁ rājaputto, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv8:6.2'><span class='pli'>jayaseno nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv8:6.3'><span class='pli'>Ārāmaṁ ropayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv8:6.4'><span class='pli'>buddhe niyyādayī tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv8:7.1'><span class='pli'>Tassa yāgaṁ pakittento, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv8:7.2'><span class='pli'>dhammaṁ desesi cakkhumā; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv8:7.3'><span class='pli'>Tadā koṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv8:7.4'><span class='pli'>tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv8:8.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv8:8.2'><span class='pli'>sobhitassa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv8:8.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv8:8.4'><span class='pli'>santacittāna tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv8:9.1'><span class='pli'>Uggato nāma so rājā, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv8:9.2'><span class='pli'>dānaṁ deti naruttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv8:9.3'><span class='pli'>Tamhi dāne samāgañchuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv8:9.4'><span class='pli'>arahantā satakoṭiyo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv8:10.1'><span class='pli'>Punāparaṁ puragaṇo, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv8:10.2'><span class='pli'>deti dānaṁ naruttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv8:10.3'><span class='pli'>Tadā navutikoṭīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv8:10.4'><span class='pli'>dutiyo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv8:11.1'><span class='pli'>Devaloke vasitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv8:11.2'><span class='pli'>yadā orohatī jino; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv8:11.3'><span class='pli'>Tadā asītikoṭīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv8:11.4'><span class='pli'>tatiyo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv8:12.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv8:12.2'><span class='pli'>sujāto nāma brāhmaṇo; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv8:12.3'><span class='pli'>Tadā sasāvakaṁ buddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv8:12.4'><span class='pli'>annapānena tappayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv8:13.1'><span class='pli'>Sopi maṁ buddho byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv8:13.2'><span class='pli'>sobhito lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv8:13.3'><span class='pli'>‘Aparimeyyito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv8:13.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv8:14.1'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv8:14.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv8:14.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv8:15.1'><span class='pli'>Tassāpi vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv8:15.2'><span class='pli'>haṭṭho saṁviggamānaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv8:15.3'><span class='pli'>Tamevatthamanuppattiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv8:15.4'><span class='pli'>uggaṁ dhitimakāsahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv8:16.1'><span class='pli'>Sudhammaṁ nāma nagaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv8:16.2'><span class='pli'>sudhammo nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv8:16.3'><span class='pli'>Sudhammā nāma janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv8:16.4'><span class='pli'>sobhitassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv8:17.1'><span class='pli'>Navavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv8:17.2'><span class='pli'>agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv8:17.3'><span class='pli'>Kumudo nāḷino padumo, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv8:17.4'><span class='pli'>tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv8:18.1'><span class='pli'>Sattatiṁsasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv8:18.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv8:18.3'><span class='pli'>Maṇilā nāma sā nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv8:18.4'><span class='pli'>sīho nāmāsi atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv8:19.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv8:19.2'><span class='pli'>pāsādenābhinikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv8:19.3'><span class='pli'>Sattāhaṁ padhānacāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv8:19.4'><span class='pli'>caritvā purisuttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv8:20.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv8:20.2'><span class='pli'>sobhito lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv8:20.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv8:20.4'><span class='pli'>sudhammuyyānamuttame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv8:21.1'><span class='pli'>Asamo ca sunetto ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv8:21.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv8:21.3'><span class='pli'>Anomo nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv8:21.4'><span class='pli'>sobhitassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv8:22.1'><span class='pli'>Nakulā ca sujātā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv8:22.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv8:22.3'><span class='pli'>Bujjhamāno ca so buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv8:22.4'><span class='pli'>nāgamūle abujjhatha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv8:23.1'><span class='pli'>Rammo ceva sudatto ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv8:23.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv8:23.3'><span class='pli'>Nakulā ceva cittā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv8:23.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv8:24.1'><span class='pli'>Aṭṭhapaṇṇāsaratanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv8:24.2'><span class='pli'>accuggato mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv8:24.3'><span class='pli'>Obhāseti disā sabbā, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv8:24.4'><span class='pli'>sataraṁsīva uggato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv8:25.1'><span class='pli'>Yathā suphullaṁ pavanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv8:25.2'><span class='pli'>nānāgandhehi dhūpitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv8:25.3'><span class='pli'>Tatheva tassa pāvacanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv8:25.4'><span class='pli'>sīlagandhehi dhūpitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv8:26.1'><span class='pli'>Yathāpi sāgaro nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv8:26.2'><span class='pli'>dassanena atappiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv8:26.3'><span class='pli'>Tatheva tassa pāvacanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv8:26.4'><span class='pli'>savanena atappiyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv8:27.1'><span class='pli'>Navutivassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv8:27.2'><span class='pli'>āyu vijjati tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv8:27.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv8:27.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='bv8:28.1'><span class='pli'>Ovādaṁ anusiṭṭhiñca, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv8:28.2'><span class='pli'>datvāna sesake jane; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv8:28.3'><span class='pli'>Hutāsanova tāpetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv8:28.4'><span class='pli'>nibbuto so sasāvako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='bv8:29.1'><span class='pli'>So ca buddho asamasamo, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='bv8:29.2'><span class='pli'>tepi sāvakā balappattā; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='bv8:29.3'><span class='pli'>Sabbaṁ tamantarahitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='bv8:29.4'><span class='pli'>nanu rittā sabbasaṅkhārā. </span></span></span></p>\n<p data-counter='4621'><span class='verse-line'><span class='segment' id='30.1' data-uid='bv8:30.1'><span class='pli'>Sobhito varasambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='bv8:30.2'><span class='pli'>Sīhārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='bv8:30.3'><span class='pli'>Dhātuvitthārikaṁ āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='bv8:30.4'><span class='pli'>Tesu tesu padesato”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='31.1' data-uid='bv8:31.1'><span class='pli'>Sobhitassa bhagavato vaṁso chaṭṭho. </span></span></p></article>\n"
  },
  "bv9": {
    "previous": "bv8",
    "next": "bv10",
    "content": "<article id='bv9'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv9:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv9:0.2'><span class='pli'>9 Anomadassībuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv9:1.1'><span class='pli'>“Sobhitassa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv9:1.2'><span class='pli'>Sambuddho dvipaduttamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv9:1.3'><span class='pli'>Anomadassī amitayaso, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv9:1.4'><span class='pli'>Tejassī duratikkamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv9:2.1'><span class='pli'>So chetvā bandhanaṁ sabbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv9:2.2'><span class='pli'>viddhaṁsetvā tayo bhave; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv9:2.3'><span class='pli'>Anivattigamanaṁ maggaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv9:2.4'><span class='pli'>desesi devamānuse. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv9:3.1'><span class='pli'>Sāgarova asaṅkhobbho, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv9:3.2'><span class='pli'>pabbatova durāsado; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv9:3.3'><span class='pli'>Ākāsova ananto so, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv9:3.4'><span class='pli'>sālarājāva phullito. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv9:4.1'><span class='pli'>Dassanenapi taṁ buddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv9:4.2'><span class='pli'>tositā honti pāṇino; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv9:4.3'><span class='pli'>Byāharantaṁ giraṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv9:4.4'><span class='pli'>amataṁ pāpuṇanti te. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv9:5.1'><span class='pli'>Dhammābhisamayo tassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv9:5.2'><span class='pli'>Iddho phīto tadā ahu; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv9:5.3'><span class='pli'>Koṭisatāni abhisamiṁsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv9:5.4'><span class='pli'>Paṭhame dhammadesane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv9:6.1'><span class='pli'>Tato paraṁ abhisamaye, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv9:6.2'><span class='pli'>Vassante dhammavuṭṭhiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv9:6.3'><span class='pli'>Asītikoṭiyobhisamiṁsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv9:6.4'><span class='pli'>Dutiye dhammadesane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv9:7.1'><span class='pli'>Tatoparañhi vassante, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv9:7.2'><span class='pli'>tappayante ca pāṇinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv9:7.3'><span class='pli'>Aṭṭhasattatikoṭīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv9:7.4'><span class='pli'>tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv9:8.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv9:8.2'><span class='pli'>tassāpi ca mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv9:8.3'><span class='pli'>Abhiññābalappattānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv9:8.4'><span class='pli'>pupphitānaṁ vimuttiyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv9:9.1'><span class='pli'>Aṭṭhasatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv9:9.2'><span class='pli'>sannipāto tadā ahu; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv9:9.3'><span class='pli'>Pahīnamadamohānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv9:9.4'><span class='pli'>santacittāna tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv9:10.1'><span class='pli'>Sattasatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv9:10.2'><span class='pli'>dutiyo āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv9:10.3'><span class='pli'>Anaṅgaṇānaṁ virajānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv9:10.4'><span class='pli'>upasantāna tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv9:11.1'><span class='pli'>Channaṁ satasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv9:11.2'><span class='pli'>tatiyo āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv9:11.3'><span class='pli'>Abhiññābalappattānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv9:11.4'><span class='pli'>nibbutānaṁ tapassinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv9:12.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv9:12.2'><span class='pli'>yakkho āsiṁ mahiddhiko; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv9:12.3'><span class='pli'>Nekānaṁ yakkhakoṭīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv9:12.4'><span class='pli'>vasavattimhi issaro. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv9:13.1'><span class='pli'>Tadāpi taṁ buddhavaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv9:13.2'><span class='pli'>upagantvā mahesinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv9:13.3'><span class='pli'>Annapānena tappesiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv9:13.4'><span class='pli'>sasaṅghaṁ lokanāyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv9:14.1'><span class='pli'>Sopi maṁ tadā byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv9:14.2'><span class='pli'>visuddhanayano muni; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv9:14.3'><span class='pli'>‘Aparimeyyito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv9:14.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv9:15.1'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv9:15.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv9:15.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv9:16.1'><span class='pli'>Tassāpi vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv9:16.2'><span class='pli'>haṭṭho saṁviggamānaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv9:16.3'><span class='pli'>Uttariṁ vatamadhiṭṭhāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv9:16.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv9:17.1'><span class='pli'>Nagaraṁ candavatī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv9:17.2'><span class='pli'>yasavā nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv9:17.3'><span class='pli'>Mātā yasodharā nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv9:17.4'><span class='pli'>anomadassissa satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv9:18.1'><span class='pli'>Dasavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv9:18.2'><span class='pli'>agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv9:18.3'><span class='pli'>Sirī upasirī vaḍḍho, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv9:18.4'><span class='pli'>tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv9:19.1'><span class='pli'>Tevīsatisahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv9:19.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv9:19.3'><span class='pli'>Sirimā nāma sā nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv9:19.4'><span class='pli'>upavāṇo nāma atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv9:20.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv9:20.2'><span class='pli'>sivikāyābhinikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv9:20.3'><span class='pli'>Anūnadasamāsāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv9:20.4'><span class='pli'>padhānaṁ padahī jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv9:21.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv9:21.2'><span class='pli'>anomadassī mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv9:21.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv9:21.4'><span class='pli'>uyyāne so sudassane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv9:22.1'><span class='pli'>Nisabho ca anomo ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv9:22.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv9:22.3'><span class='pli'>Varuṇo nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv9:22.4'><span class='pli'>anomadassissa satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv9:23.1'><span class='pli'>Sundarī ca sumanā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv9:23.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv9:23.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv9:23.4'><span class='pli'>ajjunoti pavuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv9:24.1'><span class='pli'>Nandivaḍḍho sirivaḍḍho, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv9:24.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv9:24.3'><span class='pli'>Uppalā ceva padumā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv9:24.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv9:25.1'><span class='pli'>Aṭṭhapaṇṇāsaratanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv9:25.2'><span class='pli'>accuggato mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv9:25.3'><span class='pli'>Pabhā niddhāvatī tassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv9:25.4'><span class='pli'>sataraṁsīva uggato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv9:26.1'><span class='pli'>Vassasatasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv9:26.2'><span class='pli'>āyu vijjati tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv9:26.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv9:26.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv9:27.1'><span class='pli'>Supupphitaṁ pāvacanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv9:27.2'><span class='pli'>arahantehi tādihi; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv9:27.3'><span class='pli'>Vītarāgehi vimalehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv9:27.4'><span class='pli'>sobhittha jinasāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='bv9:28.1'><span class='pli'>So ca satthā amitayaso, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv9:28.2'><span class='pli'>Yugāni tāni atuliyāni; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv9:28.3'><span class='pli'>Sabbaṁ tamantarahitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv9:28.4'><span class='pli'>Nanu rittā sabbasaṅkhārā. </span></span></span></p>\n<p data-counter='4911'><span class='verse-line'><span class='segment' id='29.1' data-uid='bv9:29.1'><span class='pli'>Anomadassī jino satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='bv9:29.2'><span class='pli'>Dhammārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='bv9:29.3'><span class='pli'>Tatthevassa jinathūpo, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='bv9:29.4'><span class='pli'>Ubbedho pañcavīsatī”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='30.1' data-uid='bv9:30.1'><span class='pli'>Anomadassissa bhagavato vaṁso sattamo. </span></span></p></article>\n"
  },
  "bv10": {
    "previous": "bv9",
    "next": "bv11",
    "content": "<article id='bv10'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv10:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv10:0.2'><span class='pli'>10. Padumabuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv10:1.1'><span class='pli'>“Anomadassissa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv10:1.2'><span class='pli'>Sambuddho dvipaduttamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv10:1.3'><span class='pli'>Padumo nāma nāmena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv10:1.4'><span class='pli'>Asamo appaṭipuggalo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv10:2.1'><span class='pli'>Tassāpi asamaṁ sīlaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv10:2.2'><span class='pli'>samādhipi anantako; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv10:2.3'><span class='pli'>Asaṅkheyyaṁ ñāṇavaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv10:2.4'><span class='pli'>vimuttipi anūpamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv10:3.1'><span class='pli'>Tassāpi atulatejassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv10:3.2'><span class='pli'>dhammacakkappavattane; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv10:3.3'><span class='pli'>Abhisamayā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv10:3.4'><span class='pli'>mahātamapavāhanā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv10:4.1'><span class='pli'>Paṭhamābhisamaye buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv10:4.2'><span class='pli'>koṭisatamabodhayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv10:4.3'><span class='pli'>Dutiyābhisamaye dhīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv10:4.4'><span class='pli'>navutikoṭimabodhayi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv10:5.1'><span class='pli'>Yadā ca padumo buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv10:5.2'><span class='pli'>ovadī sakamatrajaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv10:5.3'><span class='pli'>Tadā asītikoṭīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv10:5.4'><span class='pli'>tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv10:6.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv10:6.2'><span class='pli'>padumassa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv10:6.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv10:6.4'><span class='pli'>paṭhamo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv10:7.1'><span class='pli'>Kathinatthārasamaye, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv10:7.2'><span class='pli'>uppanne kathinacīvare; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv10:7.3'><span class='pli'>Dhammasenāpatitthāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv10:7.4'><span class='pli'>bhikkhū sibbiṁsu cīvaraṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv10:8.1'><span class='pli'>Tadā te vimalā bhikkhū, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv10:8.2'><span class='pli'>chaḷabhiññā mahiddhikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv10:8.3'><span class='pli'>Tīṇi satasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv10:8.4'><span class='pli'>samiṁsu aparājitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv10:9.1'><span class='pli'>Punāparaṁ so narāsabho, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv10:9.2'><span class='pli'>pavane vāsaṁ upāgami; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv10:9.3'><span class='pli'>Tadā samāgamo āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv10:9.4'><span class='pli'>dvinnaṁ satasahassinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv10:10.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv10:10.2'><span class='pli'>sīho āsiṁ migādhibhū; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv10:10.3'><span class='pli'>Vivekamanubrūhantaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv10:10.4'><span class='pli'>pavane addasaṁ jinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv10:11.1'><span class='pli'>Vanditvā sirasā pāde, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv10:11.2'><span class='pli'>katvāna taṁ padakkhiṇaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv10:11.3'><span class='pli'>Tikkhattuṁ abhināditvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv10:11.4'><span class='pli'>sattāhaṁ jinamupaṭṭhahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv10:12.1'><span class='pli'>Sattāhaṁ varasamāpattiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv10:12.2'><span class='pli'>Vuṭṭhahitvā tathāgato; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv10:12.3'><span class='pli'>Manasā cintayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv10:12.4'><span class='pli'>Koṭibhikkhū samānayi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv10:13.1'><span class='pli'>Tadāpi so mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv10:13.2'><span class='pli'>tesaṁ majjhe viyākari; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv10:13.3'><span class='pli'>‘Aparimeyyito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv10:13.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv10:14.1'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv10:14.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv10:14.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv10:15.1'><span class='pli'>Tassāpi vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv10:15.2'><span class='pli'>bhiyyo cittaṁ pasādayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv10:15.3'><span class='pli'>Uttariṁ vatamadhiṭṭhāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv10:15.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv10:16.1'><span class='pli'>Campakaṁ nāma nagaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv10:16.2'><span class='pli'>asamo nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv10:16.3'><span class='pli'>Asamā nāma janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv10:16.4'><span class='pli'>padumassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv10:17.1'><span class='pli'>Dasavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv10:17.2'><span class='pli'>agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv10:17.3'><span class='pli'>Nandā-vasu-yasuttarā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv10:17.4'><span class='pli'>tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv10:18.1'><span class='pli'>Tettiṁsa ca sahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv10:18.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv10:18.3'><span class='pli'>Uttarā nāma sā nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv10:18.4'><span class='pli'>rammo nāmāsi atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv10:19.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv10:19.2'><span class='pli'>rathayānena nikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv10:19.3'><span class='pli'>Anūnaaṭṭhamāsāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv10:19.4'><span class='pli'>padhānaṁ padahī jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv10:20.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv10:20.2'><span class='pli'>padumo lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv10:20.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv10:20.4'><span class='pli'>dhanañcuyyānamuttame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv10:21.1'><span class='pli'>Sālo ca upasālo ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv10:21.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv10:21.3'><span class='pli'>Varuṇo nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv10:21.4'><span class='pli'>padumassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv10:22.1'><span class='pli'>Rādhā ceva surādhā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv10:22.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv10:22.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv10:22.4'><span class='pli'>mahāsoṇoti vuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv10:23.1'><span class='pli'>Bhiyyo ceva asamo ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv10:23.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv10:23.3'><span class='pli'>Rucī ca nandarāmā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv10:23.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv10:24.1'><span class='pli'>Aṭṭhapaṇṇāsaratanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv10:24.2'><span class='pli'>accuggato mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv10:24.3'><span class='pli'>Pabhā niddhāvatī tassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv10:24.4'><span class='pli'>asamā sabbaso disā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv10:25.1'><span class='pli'>Candappabhā sūriyappabhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv10:25.2'><span class='pli'>ratanaggimaṇippabhā; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv10:25.3'><span class='pli'>Sabbāpi tā hatā honti, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv10:25.4'><span class='pli'>patvā jinapabhuttamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv10:26.1'><span class='pli'>Vassasatasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv10:26.2'><span class='pli'>āyu vijjati tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv10:26.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv10:26.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv10:27.1'><span class='pli'>Paripakkamānase satte, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv10:27.2'><span class='pli'>bodhayitvā asesato; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv10:27.3'><span class='pli'>Sesake anusāsitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv10:27.4'><span class='pli'>nibbuto so sasāvako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='bv10:28.1'><span class='pli'>Uragova tacaṁ jiṇṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv10:28.2'><span class='pli'>vaddhapattaṁva pādapo; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv10:28.3'><span class='pli'>Jahitvā sabbasaṅkhāre, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv10:28.4'><span class='pli'>nibbuto so yathā sikhī. </span></span></span></p>\n<p data-counter='5201'><span class='verse-line'><span class='segment' id='29.1' data-uid='bv10:29.1'><span class='pli'>Padumo jinavaro satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='bv10:29.2'><span class='pli'>Dhammārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='bv10:29.3'><span class='pli'>Dhātuvitthārikaṁ āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='bv10:29.4'><span class='pli'>Tesu tesu padesato”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='30.1' data-uid='bv10:30.1'><span class='pli'>Padumassa bhagavato vaṁso aṭṭhamo. </span></span></p></article>\n"
  },
  "bv11": {
    "previous": "bv10",
    "next": "bv12",
    "content": "<article id='bv11'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv11:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv11:0.2'><span class='pli'>11. Nāradabuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv11:1.1'><span class='pli'>“Padumassa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv11:1.2'><span class='pli'>sambuddho dvipaduttamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv11:1.3'><span class='pli'>Nārado nāma nāmena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv11:1.4'><span class='pli'>asamo appaṭipuggalo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv11:2.1'><span class='pli'>So buddho cakkavattissa, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv11:2.2'><span class='pli'>jeṭṭho dayitaoraso; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv11:2.3'><span class='pli'>Āmukkamālābharaṇo, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv11:2.4'><span class='pli'>uyyānaṁ upasaṅkami. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv11:3.1'><span class='pli'>Tatthāsi rukkho yasavipulo, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv11:3.2'><span class='pli'>Abhirūpo brahā suci; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv11:3.3'><span class='pli'>Tamajjhapatvā upanisīdi, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv11:3.4'><span class='pli'>Mahāsoṇassa heṭṭhato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv11:4.1'><span class='pli'>Tattha ñāṇavaruppajji, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv11:4.2'><span class='pli'>anantaṁ vajirūpamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv11:4.3'><span class='pli'>Tena vicini saṅkhāre, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv11:4.4'><span class='pli'>ukkujjamavakujjakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv11:5.1'><span class='pli'>Tattha sabbakilesāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv11:5.2'><span class='pli'>asesamabhivāhayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv11:5.3'><span class='pli'>Pāpuṇī kevalaṁ bodhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv11:5.4'><span class='pli'>buddhañāṇe ca cuddasa. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv11:6.1'><span class='pli'>Pāpuṇitvāna sambodhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv11:6.2'><span class='pli'>dhammacakkaṁ pavattayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv11:6.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv11:6.4'><span class='pli'>paṭhamābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv11:7.1'><span class='pli'>Mahādoṇaṁ nāgarājaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv11:7.2'><span class='pli'>vinayanto mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv11:7.3'><span class='pli'>Pāṭiheraṁ tadākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv11:7.4'><span class='pli'>dassayanto sadevake. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv11:8.1'><span class='pli'>Tadā devamanussānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv11:8.2'><span class='pli'>tamhi dhammappakāsane; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv11:8.3'><span class='pli'>Navutikoṭisahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv11:8.4'><span class='pli'>tariṁsu sabbasaṁsayaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv11:9.1'><span class='pli'>Yamhi kāle mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv11:9.2'><span class='pli'>ovadī sakamatrajaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv11:9.3'><span class='pli'>Asītikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv11:9.4'><span class='pli'>tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv11:10.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv11:10.2'><span class='pli'>nāradassa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv11:10.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv11:10.4'><span class='pli'>paṭhamo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv11:11.1'><span class='pli'>Yadā buddho buddhaguṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv11:11.2'><span class='pli'>sanidānaṁ pakāsayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv11:11.3'><span class='pli'>Navutikoṭisahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv11:11.4'><span class='pli'>samiṁsu vimalā tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv11:12.1'><span class='pli'>Yadā verocano nāgo, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv11:12.2'><span class='pli'>dānaṁ dadāti satthuno; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv11:12.3'><span class='pli'>Tadā samiṁsu jinaputtā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv11:12.4'><span class='pli'>asītisatasahassiyo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv11:13.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv11:13.2'><span class='pli'>jaṭilo uggatāpano; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv11:13.3'><span class='pli'>Antalikkhacaro āsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv11:13.4'><span class='pli'>pañcābhiññāsu pāragū. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv11:14.1'><span class='pli'>Tadāpāhaṁ asamasamaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv11:14.2'><span class='pli'>sasaṅghaṁ saparijjanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv11:14.3'><span class='pli'>Annapānena tappetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv11:14.4'><span class='pli'>candanenābhipūjayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv11:15.1'><span class='pli'>Sopi maṁ tadā byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv11:15.2'><span class='pli'>nārado lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv11:15.3'><span class='pli'>‘Aparimeyyito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv11:15.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv11:16.1'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv11:16.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv11:16.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv11:17.1'><span class='pli'>Tassāpi vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv11:17.2'><span class='pli'>bhiyyo hāsetva mānasaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv11:17.3'><span class='pli'>Adhiṭṭhahiṁ vataṁ uggaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv11:17.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv11:18.1'><span class='pli'>Nagaraṁ dhaññavatī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv11:18.2'><span class='pli'>sudevo nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv11:18.3'><span class='pli'>Anomā nāma janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv11:18.4'><span class='pli'>nāradassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv11:19.1'><span class='pli'>Navavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv11:19.2'><span class='pli'>agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv11:19.3'><span class='pli'>Jito vijitābhirāmo, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv11:19.4'><span class='pli'>tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv11:20.1'><span class='pli'>Ticattārīsasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv11:20.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv11:20.3'><span class='pli'>Vijitasenā nāma nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv11:20.4'><span class='pli'>nanduttaro nāma atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv11:21.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv11:21.2'><span class='pli'>Padasā gamanena nikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv11:21.3'><span class='pli'>Sattāhaṁ padhānacāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv11:21.4'><span class='pli'>Acarī purisuttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv11:22.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv11:22.2'><span class='pli'>nārado lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv11:22.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv11:22.4'><span class='pli'>dhanañcuyyānamuttame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv11:23.1'><span class='pli'>Bhaddasālo jitamitto, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv11:23.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv11:23.3'><span class='pli'>Vāseṭṭho nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv11:23.4'><span class='pli'>nāradassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv11:24.1'><span class='pli'>Uttarā phaggunī ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv11:24.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv11:24.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv11:24.4'><span class='pli'>mahāsoṇoti vuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv11:25.1'><span class='pli'>Uggarindo vasabho ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv11:25.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv11:25.3'><span class='pli'>Indāvarī ca vaṇḍī ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv11:25.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv11:26.1'><span class='pli'>Aṭṭhāsītiratanāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv11:26.2'><span class='pli'>accuggato mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv11:26.3'><span class='pli'>Kañcanagghiyasaṅkāso, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv11:26.4'><span class='pli'>dasasahassī virocati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv11:27.1'><span class='pli'>Tassa byāmappabhā kāyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv11:27.2'><span class='pli'>niddhāvati disodisaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv11:27.3'><span class='pli'>Nirantaraṁ divārattiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv11:27.4'><span class='pli'>yojanaṁ pharate sadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='bv11:28.1'><span class='pli'>Na keci tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv11:28.2'><span class='pli'>samantā yojane janā; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv11:28.3'><span class='pli'>Ukkāpadīpe ujjālenti, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv11:28.4'><span class='pli'>buddharaṁsīhi otthaṭā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='bv11:29.1'><span class='pli'>Navutivassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='bv11:29.2'><span class='pli'>āyu vijjati tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='bv11:29.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='bv11:29.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='bv11:30.1'><span class='pli'>Yathā uḷūhi gaganaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='bv11:30.2'><span class='pli'>vicittaṁ upasobhati; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='bv11:30.3'><span class='pli'>Tatheva sāsanaṁ tassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='bv11:30.4'><span class='pli'>arahantehi sobhati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='bv11:31.1'><span class='pli'>Saṁsārasotaṁ taraṇāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='bv11:31.2'><span class='pli'>sesake paṭipannake; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='bv11:31.3'><span class='pli'>Dhammasetuṁ daḷhaṁ katvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='bv11:31.4'><span class='pli'>nibbuto so narāsabho. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='bv11:32.1'><span class='pli'>Sopi buddho asamasamo, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='bv11:32.2'><span class='pli'>Tepi khīṇāsavā atulatejā; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='bv11:32.3'><span class='pli'>Sabbaṁ tamantarahitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='bv11:32.4'><span class='pli'>Nanu rittā sabbasaṅkhārā. </span></span></span></p>\n<p data-counter='5531'><span class='verse-line'><span class='segment' id='33.1' data-uid='bv11:33.1'><span class='pli'>Nārado jinavasabho, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='bv11:33.2'><span class='pli'>Nibbuto sudassane pure; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='bv11:33.3'><span class='pli'>Tatthevassa thūpavaro, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='bv11:33.4'><span class='pli'>Catuyojanamuggato”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='34.1' data-uid='bv11:34.1'><span class='pli'>Nāradassa bhagavato vaṁso navamo. </span></span></p></article>\n"
  },
  "bv12": {
    "previous": "bv11",
    "next": "bv13",
    "content": "<article id='bv12'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv12:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv12:0.2'><span class='pli'>12. Padumuttarabuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv12:1.1'><span class='pli'>“Nāradassa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv12:1.2'><span class='pli'>sambuddho dvipaduttamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv12:1.3'><span class='pli'>Padumuttaro nāma jino, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv12:1.4'><span class='pli'>akkhobbho sāgarūpamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv12:2.1'><span class='pli'>Maṇḍakappova so āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv12:2.2'><span class='pli'>yamhi buddho ajāyatha; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv12:2.3'><span class='pli'>Ussannakusalā janatā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv12:2.4'><span class='pli'>tamhi kappe ajāyatha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv12:3.1'><span class='pli'>Padumuttarassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv12:3.2'><span class='pli'>Paṭhame dhammadesane; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv12:3.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv12:3.4'><span class='pli'>Dhammābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv12:4.1'><span class='pli'>Tato parampi vassante, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv12:4.2'><span class='pli'>Tappayante ca pāṇine; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv12:4.3'><span class='pli'>Sattatiṁsasatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv12:4.4'><span class='pli'>Dutiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv12:5.1'><span class='pli'>Yamhi kāle mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv12:5.2'><span class='pli'>ānandaṁ upasaṅkami; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv12:5.3'><span class='pli'>Pitusantikaṁ upagantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv12:5.4'><span class='pli'>āhanī amatadundubhiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv12:6.1'><span class='pli'>Āhate amatabherimhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv12:6.2'><span class='pli'>vassante dhammavuṭṭhiyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv12:6.3'><span class='pli'>Paññāsasatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv12:6.4'><span class='pli'>tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv12:7.1'><span class='pli'>Ovādako viññāpako, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv12:7.2'><span class='pli'>tārako sabbapāṇinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv12:7.3'><span class='pli'>Desanākusalo buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv12:7.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv12:8.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv12:8.2'><span class='pli'>padumuttarassa satthuno; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv12:8.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv12:8.4'><span class='pli'>paṭhamo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv12:9.1'><span class='pli'>Yadā buddho asamasamo, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv12:9.2'><span class='pli'>vasi vebhārapabbate; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv12:9.3'><span class='pli'>Navutikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv12:9.4'><span class='pli'>dutiyo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv12:10.1'><span class='pli'>Puna cārikaṁ pakkante, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv12:10.2'><span class='pli'>gāmanigamaraṭṭhato; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv12:10.3'><span class='pli'>Asītikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv12:10.4'><span class='pli'>tatiyo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv12:11.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv12:11.2'><span class='pli'>Jaṭilo nāma raṭṭhiko; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv12:11.3'><span class='pli'>Sambuddhappamukhaṁ saṅghaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv12:11.4'><span class='pli'>Sabhattaṁ dussamadāsahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv12:12.1'><span class='pli'>Sopi maṁ buddho byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv12:12.2'><span class='pli'>saṅghamajjhe nisīdiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv12:12.3'><span class='pli'>‘Satasahassito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv12:12.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv12:13.1'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv12:13.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv12:13.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv12:14.1'><span class='pli'>Tassāpi vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv12:14.2'><span class='pli'>uttariṁ vatamadhiṭṭhahiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv12:14.3'><span class='pli'>Akāsiṁ uggadaḷhaṁ dhitiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv12:14.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv12:15.1'><span class='pli'>Byāhatā titthiyā sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv12:15.2'><span class='pli'>Vimanā dummanā tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv12:15.3'><span class='pli'>Na tesaṁ keci paricaranti, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv12:15.4'><span class='pli'>Raṭṭhato nicchubhanti te. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv12:16.1'><span class='pli'>Sabbe tattha samāgantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv12:16.2'><span class='pli'>upagacchuṁ buddhasantike; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv12:16.3'><span class='pli'>Tuvaṁ nātho mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv12:16.4'><span class='pli'>saraṇaṁ hohi cakkhuma. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv12:17.1'><span class='pli'>Anukampako kāruṇiko, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv12:17.2'><span class='pli'>hitesī sabbapāṇinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv12:17.3'><span class='pli'>Sampatte titthiye sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv12:17.4'><span class='pli'>pañcasīle patiṭṭhapi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv12:18.1'><span class='pli'>Evaṁ nirākulaṁ āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv12:18.2'><span class='pli'>suññataṁ titthiyehi taṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv12:18.3'><span class='pli'>Vicittaṁ arahantehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv12:18.4'><span class='pli'>vasībhūtehi tādihi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv12:19.1'><span class='pli'>Nagaraṁ haṁsavatī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv12:19.2'><span class='pli'>ānando nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv12:19.3'><span class='pli'>Sujātā nāma janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv12:19.4'><span class='pli'>padumuttarassa satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv12:20.1'><span class='pli'>Dasavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv12:20.2'><span class='pli'>Agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv12:20.3'><span class='pli'>Naravāhano yaso vasavattī, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv12:20.4'><span class='pli'>Tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv12:21.1'><span class='pli'>Ticattārīsasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv12:21.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv12:21.3'><span class='pli'>Vasudattā nāma nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv12:21.4'><span class='pli'>uttamo nāma atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv12:22.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv12:22.2'><span class='pli'>pāsādenābhinikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv12:22.3'><span class='pli'>Sattāhaṁ padhānacāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv12:22.4'><span class='pli'>acarī purisuttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv12:23.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv12:23.2'><span class='pli'>padumuttaro vināyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv12:23.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv12:23.4'><span class='pli'>mithiluyyānamuttame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv12:24.1'><span class='pli'>Devalo ca sujāto ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv12:24.2'><span class='pli'>Ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv12:24.3'><span class='pli'>Sumano nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv12:24.4'><span class='pli'>Padumuttarassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv12:25.1'><span class='pli'>Amitā ca asamā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv12:25.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv12:25.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv12:25.4'><span class='pli'>salaloti pavuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv12:26.1'><span class='pli'>Vitiṇṇo ceva tisso ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv12:26.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv12:26.3'><span class='pli'>Haṭṭhā ceva vicittā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv12:26.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv12:27.1'><span class='pli'>Aṭṭhapaṇṇāsaratanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv12:27.2'><span class='pli'>accuggato mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv12:27.3'><span class='pli'>Kañcanagghiyasaṅkāso, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv12:27.4'><span class='pli'>dvattiṁsavaralakkhaṇo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='bv12:28.1'><span class='pli'>Kuṭṭā kavāṭā bhittī ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv12:28.2'><span class='pli'>rukkhā nagasiluccayā; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv12:28.3'><span class='pli'>Na tassāvaraṇaṁ atthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv12:28.4'><span class='pli'>samantā dvādasayojane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='bv12:29.1'><span class='pli'>Vassasatasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='bv12:29.2'><span class='pli'>āyu vijjati tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='bv12:29.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='bv12:29.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='bv12:30.1'><span class='pli'>Santāretvā bahujanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='bv12:30.2'><span class='pli'>chinditvā sabbasaṁsayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='bv12:30.3'><span class='pli'>Jalitvā aggikkhandhova, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='bv12:30.4'><span class='pli'>nibbuto so sasāvako. </span></span></span></p>\n<p data-counter='5841'><span class='verse-line'><span class='segment' id='31.1' data-uid='bv12:31.1'><span class='pli'>Padumuttaro jino buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='bv12:31.2'><span class='pli'>Nandārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='bv12:31.3'><span class='pli'>Tatthevassa thūpavaro, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='bv12:31.4'><span class='pli'>Dvādasubbedhayojano”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='32.1' data-uid='bv12:32.1'><span class='pli'>Padumuttarassa bhagavato vaṁso dasamo. </span></span></p></article>\n"
  },
  "bv13": {
    "previous": "bv12",
    "next": "bv14",
    "content": "<article id='bv13'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv13:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv13:0.2'><span class='pli'>13. Sumedhabuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv13:1.1'><span class='pli'>“Padumuttarassa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv13:1.2'><span class='pli'>Sumedho nāma nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv13:1.3'><span class='pli'>Durāsado uggatejo, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv13:1.4'><span class='pli'>Sabbalokuttamo muni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv13:2.1'><span class='pli'>Pasannanetto sumukho, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv13:2.2'><span class='pli'>brahā uju patāpavā; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv13:2.3'><span class='pli'>Hitesī sabbasattānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv13:2.4'><span class='pli'>bahū mocesi bandhanā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv13:3.1'><span class='pli'>Yadā buddho pāpuṇitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv13:3.2'><span class='pli'>kevalaṁ bodhimuttamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv13:3.3'><span class='pli'>Sudassanamhi nagare, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv13:3.4'><span class='pli'>dhammacakkaṁ pavattayi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv13:4.1'><span class='pli'>Tassāpi abhisamayā tīṇi, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv13:4.2'><span class='pli'>Ahesuṁ dhammadesane; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv13:4.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv13:4.4'><span class='pli'>Paṭhamābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv13:5.1'><span class='pli'>Punāparaṁ kumbhakaṇṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv13:5.2'><span class='pli'>yakkhaṁ so damayī jino; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv13:5.3'><span class='pli'>Navutikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv13:5.4'><span class='pli'>dutiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv13:6.1'><span class='pli'>Punāparaṁ amitayaso, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv13:6.2'><span class='pli'>catusaccaṁ pakāsayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv13:6.3'><span class='pli'>Asītikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv13:6.4'><span class='pli'>tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv13:7.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv13:7.2'><span class='pli'>sumedhassa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv13:7.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv13:7.4'><span class='pli'>santacittāna tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv13:8.1'><span class='pli'>Sudassanaṁ nāma nagaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv13:8.2'><span class='pli'>upagañchi jino yadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv13:8.3'><span class='pli'>Tadā khīṇāsavā bhikkhū, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv13:8.4'><span class='pli'>samiṁsu satakoṭiyo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv13:9.1'><span class='pli'>Punāparaṁ devakūṭe, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv13:9.2'><span class='pli'>bhikkhūnaṁ kathinatthate; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv13:9.3'><span class='pli'>Tadā navutikoṭīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv13:9.4'><span class='pli'>dutiyo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv13:10.1'><span class='pli'>Punāparaṁ dasabalo, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv13:10.2'><span class='pli'>yadā carati cārikaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv13:10.3'><span class='pli'>Tadā asītikoṭīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv13:10.4'><span class='pli'>tatiyo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv13:11.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv13:11.2'><span class='pli'>uttaro nāma māṇavo; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv13:11.3'><span class='pli'>Asītikoṭiyo mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv13:11.4'><span class='pli'>ghare sannicitaṁ dhanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv13:12.1'><span class='pli'>Kevalaṁ sabbaṁ datvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv13:12.2'><span class='pli'>sasaṅghe lokanāyake; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv13:12.3'><span class='pli'>Saraṇaṁ tassupagañchiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv13:12.4'><span class='pli'>pabbajjañcābhirocayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv13:13.1'><span class='pli'>Sopi maṁ buddho byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv13:13.2'><span class='pli'>karonto anumodanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv13:13.3'><span class='pli'>‘Tiṁsakappasahassamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv13:13.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv13:14.1'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv13:14.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv13:14.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv13:15.1'><span class='pli'>Tassāpi vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv13:15.2'><span class='pli'>bhiyyo cittaṁ pasādayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv13:15.3'><span class='pli'>Uttariṁ vatamadhiṭṭhāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv13:15.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv13:16.1'><span class='pli'>Suttantaṁ vinayañcāpi, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv13:16.2'><span class='pli'>navaṅgaṁ satthusāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv13:16.3'><span class='pli'>Sabbaṁ pariyāpuṇitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv13:16.4'><span class='pli'>sobhayiṁ jinasāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv13:17.1'><span class='pli'>Tatthappamatto viharanto, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv13:17.2'><span class='pli'>Nisajjaṭṭhānacaṅkame; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv13:17.3'><span class='pli'>Abhiññāsu pāramiṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv13:17.4'><span class='pli'>Brahmalokamagañchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv13:18.1'><span class='pli'>Sudassanaṁ nāma nagaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv13:18.2'><span class='pli'>sudatto nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv13:18.3'><span class='pli'>Sudattā nāma janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv13:18.4'><span class='pli'>sumedhassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv13:19.1'><span class='pli'>Navavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv13:19.2'><span class='pli'>Agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv13:19.3'><span class='pli'>Sucandakañcanasirivaḍḍhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv13:19.4'><span class='pli'>Tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv13:20.1'><span class='pli'>Tisoḷasasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv13:20.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv13:20.3'><span class='pli'>Sumanā nāma sā nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv13:20.4'><span class='pli'>punabbasu nāma atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv13:21.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv13:21.2'><span class='pli'>hatthiyānena nikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv13:21.3'><span class='pli'>Anūnakaṁ aḍḍhamāsaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv13:21.4'><span class='pli'>padhānaṁ padahī jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv13:22.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv13:22.2'><span class='pli'>sumedho lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv13:22.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv13:22.4'><span class='pli'>sudassanuyyānamuttame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv13:23.1'><span class='pli'>Saraṇo sabbakāmo ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv13:23.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv13:23.3'><span class='pli'>Sāgaro nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv13:23.4'><span class='pli'>sumedhassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv13:24.1'><span class='pli'>Rāmā ceva surāmā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv13:24.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv13:24.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv13:24.4'><span class='pli'>mahānīpoti vuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv13:25.1'><span class='pli'>Uruvelā yasavā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv13:25.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv13:25.3'><span class='pli'>Yasodharā sirimā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv13:25.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv13:26.1'><span class='pli'>Aṭṭhāsītiratanāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv13:26.2'><span class='pli'>accuggato mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv13:26.3'><span class='pli'>Obhāseti disā sabbā, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv13:26.4'><span class='pli'>cando tāragaṇe yathā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv13:27.1'><span class='pli'>Cakkavattimaṇī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv13:27.2'><span class='pli'>yathā tapati yojanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv13:27.3'><span class='pli'>Tatheva tassa ratanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv13:27.4'><span class='pli'>samantā pharati yojanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='bv13:28.1'><span class='pli'>Navutivassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv13:28.2'><span class='pli'>āyu vijjati tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv13:28.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv13:28.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='bv13:29.1'><span class='pli'>Tevijjachaḷabhiññehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='bv13:29.2'><span class='pli'>balappattehi tādihi; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='bv13:29.3'><span class='pli'>Samākulamidaṁ āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='bv13:29.4'><span class='pli'>arahantehi sādhuhi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='bv13:30.1'><span class='pli'>Tepi sabbe amitayasā, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='bv13:30.2'><span class='pli'>vippamuttā nirūpadhī; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='bv13:30.3'><span class='pli'>Ñāṇālokaṁ dassayitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='bv13:30.4'><span class='pli'>nibbutā te mahāyasā. </span></span></span></p>\n<p data-counter='6151'><span class='verse-line'><span class='segment' id='31.1' data-uid='bv13:31.1'><span class='pli'>Sumedho jinavaro buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='bv13:31.2'><span class='pli'>Medhārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='bv13:31.3'><span class='pli'>Dhātuvitthārikaṁ āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='bv13:31.4'><span class='pli'>Tesu tesu padesato”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='32.1' data-uid='bv13:32.1'><span class='pli'>Sumedhassa bhagavato vaṁso ekādasamo. </span></span></p></article>\n"
  },
  "bv14": {
    "previous": "bv13",
    "next": "bv15",
    "content": "<article id='bv14'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv14:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv14:0.2'><span class='pli'>14. Sujātabuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv14:1.1'><span class='pli'>“Tattheva maṇḍakappamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv14:1.2'><span class='pli'>sujāto nāma nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv14:1.3'><span class='pli'>Sīhahanusabhakkhandho, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv14:1.4'><span class='pli'>appameyyo durāsado. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv14:2.1'><span class='pli'>Candova vimalo suddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv14:2.2'><span class='pli'>sataraṁsīva patāpavā; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv14:2.3'><span class='pli'>Evaṁ sobhati sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv14:2.4'><span class='pli'>jalanto siriyā sadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv14:3.1'><span class='pli'>Pāpuṇitvāna sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv14:3.2'><span class='pli'>kevalaṁ bodhimuttamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv14:3.3'><span class='pli'>Sumaṅgalamhi nagare, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv14:3.4'><span class='pli'>dhammacakkaṁ pavattayi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv14:4.1'><span class='pli'>Desente pavaraṁ dhammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv14:4.2'><span class='pli'>Sujāte lokanāyake; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv14:4.3'><span class='pli'>Asītikoṭī abhisamiṁsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv14:4.4'><span class='pli'>Paṭhame dhammadesane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv14:5.1'><span class='pli'>Yadā sujāto amitayaso, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv14:5.2'><span class='pli'>Deve vassaṁ upāgami; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv14:5.3'><span class='pli'>Sattatiṁsasatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv14:5.4'><span class='pli'>Dutiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv14:6.1'><span class='pli'>Yadā sujāto asamasamo, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv14:6.2'><span class='pli'>Upagacchi pitusantikaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv14:6.3'><span class='pli'>Saṭṭhisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv14:6.4'><span class='pli'>Tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv14:7.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv14:7.2'><span class='pli'>sujātassa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv14:7.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv14:7.4'><span class='pli'>santacittāna tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv14:8.1'><span class='pli'>Abhiññābalappattānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv14:8.2'><span class='pli'>appattānaṁ bhavābhave; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv14:8.3'><span class='pli'>Saṭṭhisatasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv14:8.4'><span class='pli'>paṭhamaṁ sannipatiṁsu te. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv14:9.1'><span class='pli'>Punāparaṁ sannipāte, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv14:9.2'><span class='pli'>tidivorohaṇe jine; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv14:9.3'><span class='pli'>Paññāsasatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv14:9.4'><span class='pli'>dutiyo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv14:10.1'><span class='pli'>Upasaṅkamanto narāsabhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv14:10.2'><span class='pli'>Tassa yo aggasāvako; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv14:10.3'><span class='pli'>Catūhi satasahassehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv14:10.4'><span class='pli'>Sambuddhaṁ upasaṅkami. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv14:11.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv14:11.2'><span class='pli'>catudīpamhi issaro; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv14:11.3'><span class='pli'>Antalikkhacaro āsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv14:11.4'><span class='pli'>cakkavattī mahabbalo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv14:12.1'><span class='pli'>Loke acchariyaṁ disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv14:12.2'><span class='pli'>abbhutaṁ lomahaṁsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv14:12.3'><span class='pli'>Upagantvāna vandiṁ so, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv14:12.4'><span class='pli'>sujātaṁ lokanāyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv14:13.1'><span class='pli'>Catudīpe mahārajjaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv14:13.2'><span class='pli'>ratane satta uttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv14:13.3'><span class='pli'>Buddhe niyyādayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv14:13.4'><span class='pli'>pabbajiṁ tassa santike. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv14:14.1'><span class='pli'>Ārāmikā janapade, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv14:14.2'><span class='pli'>uṭṭhānaṁ paṭipiṇḍiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv14:14.3'><span class='pli'>Upanenti bhikkhusaṅghassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv14:14.4'><span class='pli'>paccayaṁ sayanāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv14:15.1'><span class='pli'>Sopi maṁ buddho byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv14:15.2'><span class='pli'>dasasahassimhi issaro; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv14:15.3'><span class='pli'>‘Tiṁsakappasahassamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv14:15.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv14:16.1'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv14:16.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv14:16.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv14:17.1'><span class='pli'>Tassāpi vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv14:17.2'><span class='pli'>bhiyyo hāsaṁ janesahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv14:17.3'><span class='pli'>Adhiṭṭhahiṁ vataṁ uggaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv14:17.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv14:18.1'><span class='pli'>Suttantaṁ vinayañcāpi, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv14:18.2'><span class='pli'>navaṅgaṁ satthusāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv14:18.3'><span class='pli'>Sabbaṁ pariyāpuṇitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv14:18.4'><span class='pli'>sobhayiṁ jinasāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv14:19.1'><span class='pli'>Tatthappamatto viharanto, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv14:19.2'><span class='pli'>brahmaṁ bhāvetva bhāvanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv14:19.3'><span class='pli'>Abhiññāpāramiṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv14:19.4'><span class='pli'>brahmalokamagañchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv14:20.1'><span class='pli'>Sumaṅgalaṁ nāma nagaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv14:20.2'><span class='pli'>uggato nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv14:20.3'><span class='pli'>Mātā pabhāvatī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv14:20.4'><span class='pli'>sujātassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv14:21.1'><span class='pli'>Navavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv14:21.2'><span class='pli'>agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv14:21.3'><span class='pli'>Sirī upasirī nando, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv14:21.4'><span class='pli'>tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv14:22.1'><span class='pli'>Tevīsatisahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv14:22.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv14:22.3'><span class='pli'>Sirinandā nāma nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv14:22.4'><span class='pli'>upaseno nāma atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv14:23.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv14:23.2'><span class='pli'>assayānena nikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv14:23.3'><span class='pli'>Anūnanavamāsāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv14:23.4'><span class='pli'>padhānaṁ padahī jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv14:24.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv14:24.2'><span class='pli'>sujāto lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv14:24.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv14:24.4'><span class='pli'>sumaṅgaluyyānamuttame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv14:25.1'><span class='pli'>Sudassano sudevo ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv14:25.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv14:25.3'><span class='pli'>Nārado nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv14:25.4'><span class='pli'>sujātassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv14:26.1'><span class='pli'>Nāgā ca nāgasamālā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv14:26.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv14:26.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv14:26.4'><span class='pli'>mahāveḷūti vuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv14:27.1'><span class='pli'>So ca rukkho ghanakkhandho, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv14:27.2'><span class='pli'>Acchiddo hoti pattiko; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv14:27.3'><span class='pli'>Uju vaṁso brahā hoti, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv14:27.4'><span class='pli'>Dassanīyo manoramo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='bv14:28.1'><span class='pli'>Ekakkhandho pavaḍḍhitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv14:28.2'><span class='pli'>tato sākhā pabhijjati; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv14:28.3'><span class='pli'>Yathā subaddho morahattho, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv14:28.4'><span class='pli'>evaṁ sobhati so dumo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='bv14:29.1'><span class='pli'>Na tassa kaṇṭakā honti, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='bv14:29.2'><span class='pli'>nāpi chiddaṁ mahā ahu; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='bv14:29.3'><span class='pli'>Vitthiṇṇasākho aviralo, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='bv14:29.4'><span class='pli'>sandacchāyo manoramo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='bv14:30.1'><span class='pli'>Sudatto ceva citto ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='bv14:30.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='bv14:30.3'><span class='pli'>Subhaddā ca padumā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='bv14:30.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='bv14:31.1'><span class='pli'>Paññāsaratano āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='bv14:31.2'><span class='pli'>uccattanena so jino; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='bv14:31.3'><span class='pli'>Sabbākāravarūpeto, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='bv14:31.4'><span class='pli'>sabbaguṇamupāgato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='bv14:32.1'><span class='pli'>Tassa pabhā asamasamā, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='bv14:32.2'><span class='pli'>niddhāvati samantato; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='bv14:32.3'><span class='pli'>Appamāṇo atuliyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='bv14:32.4'><span class='pli'>opammehi anūpamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='bv14:33.1'><span class='pli'>Navutivassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='bv14:33.2'><span class='pli'>āyu vijjati tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='bv14:33.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='bv14:33.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='34.1' data-uid='bv14:34.1'><span class='pli'>Yathāpi sāgare ūmī, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='bv14:34.2'><span class='pli'>gagane tārakā yathā; </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='bv14:34.3'><span class='pli'>Evaṁ tadā pāvacanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.4' data-uid='bv14:34.4'><span class='pli'>arahantehi cittitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='35.1' data-uid='bv14:35.1'><span class='pli'>So ca buddho asamasamo, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.2' data-uid='bv14:35.2'><span class='pli'>Guṇāni ca tāni atuliyāni; </span></span></span>\n<span class='verse-line'><span class='segment' id='35.3' data-uid='bv14:35.3'><span class='pli'>Sabbaṁ tamantarahitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.4' data-uid='bv14:35.4'><span class='pli'>Nanu rittā sabbasaṅkhārā. </span></span></span></p>\n<p data-counter='6511'><span class='verse-line'><span class='segment' id='36.1' data-uid='bv14:36.1'><span class='pli'>Sujāto jinavaro buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.2' data-uid='bv14:36.2'><span class='pli'>Silārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='36.3' data-uid='bv14:36.3'><span class='pli'>Tattheva tassa cetiyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.4' data-uid='bv14:36.4'><span class='pli'>Tīṇigāvutamuggato”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='37.1' data-uid='bv14:37.1'><span class='pli'>Sujātassa bhagavato vaṁso dvādasamo. </span></span></p></article>\n"
  },
  "bv15": {
    "previous": "bv14",
    "next": "bv16",
    "content": "<article id='bv15'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv15:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv15:0.2'><span class='pli'>15 Piyadassībuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv15:1.1'><span class='pli'>“Sujātassa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv15:1.2'><span class='pli'>sayambhū lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv15:1.3'><span class='pli'>Durāsado asamasamo, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv15:1.4'><span class='pli'>piyadassī mahāyaso. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv15:2.1'><span class='pli'>Sopi buddho amitayaso, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv15:2.2'><span class='pli'>ādiccova virocati; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv15:2.3'><span class='pli'>Sabbaṁ tamaṁ nihantvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv15:2.4'><span class='pli'>dhammacakkaṁ pavattayi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv15:3.1'><span class='pli'>Tassāpi atulatejassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv15:3.2'><span class='pli'>Ahesuṁ abhisamayā tayo; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv15:3.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv15:3.4'><span class='pli'>Paṭhamābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv15:4.1'><span class='pli'>Sudassano devarājā, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv15:4.2'><span class='pli'>micchādiṭṭhimarocayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv15:4.3'><span class='pli'>Tassa diṭṭhiṁ vinodento, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv15:4.4'><span class='pli'>satthā dhammamadesayi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv15:5.1'><span class='pli'>Janasannipāto atulo, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv15:5.2'><span class='pli'>mahāsannipatī tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv15:5.3'><span class='pli'>Navutikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv15:5.4'><span class='pli'>dutiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv15:6.1'><span class='pli'>Yadā doṇamukhaṁ hatthiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv15:6.2'><span class='pli'>vinesi narasārathi; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv15:6.3'><span class='pli'>Asītikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv15:6.4'><span class='pli'>tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv15:7.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv15:7.2'><span class='pli'>tassāpi piyadassino; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv15:7.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv15:7.4'><span class='pli'>paṭhamo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv15:8.1'><span class='pli'>Tato paraṁ navutikoṭī, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv15:8.2'><span class='pli'>samiṁsu ekato munī; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv15:8.3'><span class='pli'>Tatiye sannipātamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv15:8.4'><span class='pli'>asītikoṭiyo ahū. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv15:9.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv15:9.2'><span class='pli'>kassapo nāma brāhmaṇo; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv15:9.3'><span class='pli'>Ajjhāyako mantadharo, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv15:9.4'><span class='pli'>tiṇṇaṁ vedāna pāragū. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv15:10.1'><span class='pli'>Tassa dhammaṁ suṇitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv15:10.2'><span class='pli'>pasādaṁ janayiṁ ahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv15:10.3'><span class='pli'>Koṭisatasahassehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv15:10.4'><span class='pli'>saṅghārāmaṁ amāpayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv15:11.1'><span class='pli'>Tassa datvāna ārāmaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv15:11.2'><span class='pli'>haṭṭho saṁviggamānaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv15:11.3'><span class='pli'>Saraṇe pañca sīle ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv15:11.4'><span class='pli'>daḷhaṁ katvā samādiyiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv15:12.1'><span class='pli'>Sopi maṁ buddho byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv15:12.2'><span class='pli'>saṅghamajjhe nisīdiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv15:12.3'><span class='pli'>‘Aṭṭhārase kappasate, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv15:12.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv15:13.1'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv15:13.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv15:13.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv15:14.1'><span class='pli'>Tassāpi vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv15:14.2'><span class='pli'>bhiyyo cittaṁ pasādayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv15:14.3'><span class='pli'>Uttariṁ vatamadhiṭṭhāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv15:14.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv15:15.1'><span class='pli'>Sudhaññaṁ nāma nagaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv15:15.2'><span class='pli'>sudatto nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv15:15.3'><span class='pli'>Candā nāmāsi janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv15:15.4'><span class='pli'>piyadassissa satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv15:16.1'><span class='pli'>Navavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv15:16.2'><span class='pli'>Agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv15:16.3'><span class='pli'>Sunimmalavimalagiriguhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv15:16.4'><span class='pli'>Tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv15:17.1'><span class='pli'>Tettiṁsasahassāni ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv15:17.2'><span class='pli'>Nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv15:17.3'><span class='pli'>Vimalā nāma nārī ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv15:17.4'><span class='pli'>Kañcanāveḷo nāma atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv15:18.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv15:18.2'><span class='pli'>rathayānena nikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv15:18.3'><span class='pli'>Chamāsaṁ padhānacāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv15:18.4'><span class='pli'>acarī purisuttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv15:19.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv15:19.2'><span class='pli'>piyadassī mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv15:19.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv15:19.4'><span class='pli'>usabhuyyāne manorame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv15:20.1'><span class='pli'>Pālito sabbadassī ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv15:20.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv15:20.3'><span class='pli'>Sobhito nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv15:20.4'><span class='pli'>piyadassissa satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv15:21.1'><span class='pli'>Sujātā dhammadinnā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv15:21.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv15:21.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv15:21.4'><span class='pli'>kakudhoti pavuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv15:22.1'><span class='pli'>Sandhako dhammako ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv15:22.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv15:22.3'><span class='pli'>Visākhā dhammadinnā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv15:22.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv15:23.1'><span class='pli'>Sopi buddho amitayaso, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv15:23.2'><span class='pli'>dvattiṁsavaralakkhaṇo; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv15:23.3'><span class='pli'>Asītihatthamubbedho, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv15:23.4'><span class='pli'>sālarājāva dissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv15:24.1'><span class='pli'>Aggicandasūriyānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv15:24.2'><span class='pli'>natthi tādisikā pabhā; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv15:24.3'><span class='pli'>Yathā ahu pabhā tassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv15:24.4'><span class='pli'>asamassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv15:25.1'><span class='pli'>Tassāpi devadevassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv15:25.2'><span class='pli'>āyu tāvatakaṁ ahu; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv15:25.3'><span class='pli'>Navutivassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv15:25.4'><span class='pli'>loke aṭṭhāsi cakkhumā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv15:26.1'><span class='pli'>Sopi buddho asamasamo, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv15:26.2'><span class='pli'>Yugānipi tāni atuliyāni; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv15:26.3'><span class='pli'>Sabbaṁ tamantarahitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv15:26.4'><span class='pli'>Nanu rittā sabbasaṅkhārā. </span></span></span></p>\n<p data-counter='6781'><span class='verse-line'><span class='segment' id='27.1' data-uid='bv15:27.1'><span class='pli'>Piyadassī munivaro, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv15:27.2'><span class='pli'>Assatthārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv15:27.3'><span class='pli'>Tatthevassa jinathūpo, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv15:27.4'><span class='pli'>Tīṇiyojanamuggato”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='28.1' data-uid='bv15:28.1'><span class='pli'>Piyadassissa bhagavato vaṁso terasamo. </span></span></p></article>\n"
  },
  "bv16": {
    "previous": "bv15",
    "next": "bv17",
    "content": "<article id='bv16'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv16:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv16:0.2'><span class='pli'>16 Atthadassībuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv16:1.1'><span class='pli'>“Tattheva maṇḍakappamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv16:1.2'><span class='pli'>atthadassī mahāyaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv16:1.3'><span class='pli'>Mahātamaṁ nihantvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv16:1.4'><span class='pli'>patto sambodhimuttamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv16:2.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv16:2.2'><span class='pli'>dhammacakkaṁ pavattayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv16:2.3'><span class='pli'>Amatena tappayī lokaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv16:2.4'><span class='pli'>dasasahassisadevakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv16:3.1'><span class='pli'>Tassāpi lokanāthassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv16:3.2'><span class='pli'>Ahesuṁ abhisamayā tayo; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv16:3.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv16:3.4'><span class='pli'>Paṭhamābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv16:4.1'><span class='pli'>Yadā buddho atthadassī, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv16:4.2'><span class='pli'>carate devacārikaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv16:4.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv16:4.4'><span class='pli'>dutiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv16:5.1'><span class='pli'>Punāparaṁ yadā buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv16:5.2'><span class='pli'>desesi pitusantike; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv16:5.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv16:5.4'><span class='pli'>tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv16:6.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv16:6.2'><span class='pli'>tassāpi ca mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv16:6.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv16:6.4'><span class='pli'>santacittāna tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv16:7.1'><span class='pli'>Aṭṭhanavutisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv16:7.2'><span class='pli'>paṭhamo āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv16:7.3'><span class='pli'>Aṭṭhāsītisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv16:7.4'><span class='pli'>dutiyo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv16:8.1'><span class='pli'>Aṭṭhasattatisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv16:8.2'><span class='pli'>Tatiyo āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv16:8.3'><span class='pli'>Anupādā vimuttānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv16:8.4'><span class='pli'>Vimalānaṁ mahesinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv16:9.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv16:9.2'><span class='pli'>jaṭilo uggatāpano; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv16:9.3'><span class='pli'>Susīmo nāma nāmena, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv16:9.4'><span class='pli'>mahiyā seṭṭhasammato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv16:10.1'><span class='pli'>Dibbaṁ mandāravaṁ pupphaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv16:10.2'><span class='pli'>padumaṁ pārichattakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv16:10.3'><span class='pli'>Devalokāharitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv16:10.4'><span class='pli'>sambuddhamabhipūjayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv16:11.1'><span class='pli'>Sopi maṁ buddho byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv16:11.2'><span class='pli'>atthadassī mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv16:11.3'><span class='pli'>‘Aṭṭhārase kappasate, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv16:11.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv16:12.1'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv16:12.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv16:12.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv16:13.1'><span class='pli'>Tassāpi vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv16:13.2'><span class='pli'>haṭṭho saṁviggamānaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv16:13.3'><span class='pli'>Uttariṁ vatamadhiṭṭhāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv16:13.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv16:14.1'><span class='pli'>Sobhanaṁ nāma nagaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv16:14.2'><span class='pli'>sāgaro nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv16:14.3'><span class='pli'>Sudassanā nāma janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv16:14.4'><span class='pli'>atthadassissa satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv16:15.1'><span class='pli'>Dasavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv16:15.2'><span class='pli'>Agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv16:15.3'><span class='pli'>Amaragiri sugiri vāhanā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv16:15.4'><span class='pli'>Tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv16:16.1'><span class='pli'>Tettiṁsañca sahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv16:16.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv16:16.3'><span class='pli'>Visākhā nāma nārī ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv16:16.4'><span class='pli'>selo nāmāsi atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv16:17.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv16:17.2'><span class='pli'>assayānena nikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv16:17.3'><span class='pli'>Anūnaaṭṭhamāsāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv16:17.4'><span class='pli'>padhānaṁ padahī jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv16:18.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv16:18.2'><span class='pli'>atthadassī mahāyaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv16:18.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv16:18.4'><span class='pli'>anomuyyāne narāsabho. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv16:19.1'><span class='pli'>Santo ca upasanto ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv16:19.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv16:19.3'><span class='pli'>Abhayo nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv16:19.4'><span class='pli'>atthadassissa satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv16:20.1'><span class='pli'>Dhammā ceva sudhammā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv16:20.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv16:20.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv16:20.4'><span class='pli'>campakoti pavuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv16:21.1'><span class='pli'>Nakulo ca nisabho ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv16:21.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv16:21.3'><span class='pli'>Makilā ca sunandā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv16:21.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv16:22.1'><span class='pli'>Sopi buddho asamasamo, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv16:22.2'><span class='pli'>asītihatthamuggato; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv16:22.3'><span class='pli'>Sobhate sālarājāva, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv16:22.4'><span class='pli'>uḷurājāva pūrito. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv16:23.1'><span class='pli'>Tassa pākatikā raṁsī, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv16:23.2'><span class='pli'>anekasatakoṭiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv16:23.3'><span class='pli'>Uddhaṁ adho dasa disā, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv16:23.4'><span class='pli'>pharanti yojanaṁ sadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv16:24.1'><span class='pli'>Sopi buddho narāsabho, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv16:24.2'><span class='pli'>sabbasattuttamo muni; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv16:24.3'><span class='pli'>Vassasatasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv16:24.4'><span class='pli'>loke aṭṭhāsi cakkhumā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv16:25.1'><span class='pli'>Atulaṁ dassetvā obhāsaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv16:25.2'><span class='pli'>virocetvā sadevake; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv16:25.3'><span class='pli'>Sopi aniccataṁ patto, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv16:25.4'><span class='pli'>yathaggupādānasaṅkhayā. </span></span></span></p>\n<p data-counter='7041'><span class='verse-line'><span class='segment' id='26.1' data-uid='bv16:26.1'><span class='pli'>Atthadassī jinavaro, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv16:26.2'><span class='pli'>Anomārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv16:26.3'><span class='pli'>Dhātuvitthārikaṁ āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv16:26.4'><span class='pli'>Tesu tesu padesato”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='27.1' data-uid='bv16:27.1'><span class='pli'>Atthadassissa bhagavato vaṁso cuddasamo. </span></span></p></article>\n"
  },
  "bv17": {
    "previous": "bv16",
    "next": "bv18",
    "content": "<article id='bv17'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv17:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv17:0.2'><span class='pli'>17 Dhammadassībuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv17:1.1'><span class='pli'>“Tattheva maṇḍakappamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv17:1.2'><span class='pli'>dhammadassī mahāyaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv17:1.3'><span class='pli'>Tamandhakāraṁ vidhamitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv17:1.4'><span class='pli'>atirocati sadevake. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv17:2.1'><span class='pli'>Tassāpi atulatejassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv17:2.2'><span class='pli'>dhammacakkappavattane; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv17:2.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv17:2.4'><span class='pli'>paṭhamābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv17:3.1'><span class='pli'>Yadā buddho dhammadassī, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv17:3.2'><span class='pli'>vinesi sañjayaṁ isiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv17:3.3'><span class='pli'>Tadā navutikoṭīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv17:3.4'><span class='pli'>dutiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv17:4.1'><span class='pli'>Yadā sakko upāgañchi, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv17:4.2'><span class='pli'>sapariso vināyakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv17:4.3'><span class='pli'>Tadā asītikoṭīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv17:4.4'><span class='pli'>tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv17:5.1'><span class='pli'>Tassāpi devadevassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv17:5.2'><span class='pli'>sannipātā tayo ahuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv17:5.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv17:5.4'><span class='pli'>santacittāna tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv17:6.1'><span class='pli'>Yadā buddho dhammadassī, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv17:6.2'><span class='pli'>Saraṇe vassaṁ upāgami; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv17:6.3'><span class='pli'>Tadā koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv17:6.4'><span class='pli'>Paṭhamo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv17:7.1'><span class='pli'>Punāparaṁ yadā buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv17:7.2'><span class='pli'>devato eti mānusaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv17:7.3'><span class='pli'>Tadāpi satakoṭīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv17:7.4'><span class='pli'>dutiyo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv17:8.1'><span class='pli'>Punāparaṁ yadā buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv17:8.2'><span class='pli'>pakāsesi dhute guṇe; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv17:8.3'><span class='pli'>Tadā asītikoṭīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv17:8.4'><span class='pli'>tatiyo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv17:9.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv17:9.2'><span class='pli'>sakko āsiṁ purindado; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv17:9.3'><span class='pli'>Dibbena gandhamālena, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv17:9.4'><span class='pli'>tūriyenābhipūjayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv17:10.1'><span class='pli'>Sopi maṁ buddho byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv17:10.2'><span class='pli'>devamajjhe nisīdiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv17:10.3'><span class='pli'>‘Aṭṭhārase kappasate, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv17:10.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv17:11.1'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv17:11.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv17:11.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv17:12.1'><span class='pli'>Tassāpi vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv17:12.2'><span class='pli'>bhiyyo cittaṁ pasādayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv17:12.3'><span class='pli'>Uttariṁ vatamadhiṭṭhāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv17:12.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv17:13.1'><span class='pli'>Saraṇaṁ nāma nagaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv17:13.2'><span class='pli'>saraṇo nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv17:13.3'><span class='pli'>Sunandā nāma janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv17:13.4'><span class='pli'>dhammadassissa satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv17:14.1'><span class='pli'>Aṭṭhavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv17:14.2'><span class='pli'>Agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv17:14.3'><span class='pli'>Arajo virajo sudassano, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv17:14.4'><span class='pli'>Tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv17:15.1'><span class='pli'>Ticattārīsasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv17:15.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv17:15.3'><span class='pli'>Vicikoḷi nāma nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv17:15.4'><span class='pli'>atrajo puññavaḍḍhano. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv17:16.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv17:16.2'><span class='pli'>pāsādenābhinikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv17:16.3'><span class='pli'>Sattāhaṁ padhānacāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv17:16.4'><span class='pli'>acarī purisuttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv17:17.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv17:17.2'><span class='pli'>dhammadassī narāsabho; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv17:17.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv17:17.4'><span class='pli'>migadāye naruttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv17:18.1'><span class='pli'>Padumo phussadevo ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv17:18.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv17:18.3'><span class='pli'>Sunetto nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv17:18.4'><span class='pli'>dhammadassissa satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv17:19.1'><span class='pli'>Khemā ca saccanāmā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv17:19.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv17:19.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv17:19.4'><span class='pli'>bimbijāloti vuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv17:20.1'><span class='pli'>Subhaddo kaṭissaho ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv17:20.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv17:20.3'><span class='pli'>Sāḷiyā ca kaḷiyā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv17:20.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv17:21.1'><span class='pli'>Sopi buddho asamasamo, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv17:21.2'><span class='pli'>asītihatthamuggato; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv17:21.3'><span class='pli'>Atirocati tejena, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv17:21.4'><span class='pli'>dasasahassimhi dhātuyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv17:22.1'><span class='pli'>Suphullo sālarājāva, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv17:22.2'><span class='pli'>vijjūva gagane yathā; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv17:22.3'><span class='pli'>Majjhanhikeva sūriyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv17:22.4'><span class='pli'>evaṁ so upasobhatha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv17:23.1'><span class='pli'>Tassāpi atulatejassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv17:23.2'><span class='pli'>samakaṁ āsi jīvitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv17:23.3'><span class='pli'>Vassasatasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv17:23.4'><span class='pli'>loke aṭṭhāsi cakkhumā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv17:24.1'><span class='pli'>Obhāsaṁ dassayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv17:24.2'><span class='pli'>vimalaṁ katvāna sāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv17:24.3'><span class='pli'>Cavi candova gagane, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv17:24.4'><span class='pli'>nibbuto so sasāvako. </span></span></span></p>\n<p data-counter='7291'><span class='verse-line'><span class='segment' id='25.1' data-uid='bv17:25.1'><span class='pli'>Dhammadassī mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv17:25.2'><span class='pli'>Sālārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv17:25.3'><span class='pli'>Tatthevassa thūpavaro, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv17:25.4'><span class='pli'>Tīṇiyojanamuggato”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='26.1' data-uid='bv17:26.1'><span class='pli'>Dhammadassissa bhagavato vaṁso pannarasamo. </span></span></p></article>\n"
  },
  "bv18": {
    "previous": "bv17",
    "next": "bv19",
    "content": "<article id='bv18'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv18:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv18:0.2'><span class='pli'>18. Siddhatthabuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv18:1.1'><span class='pli'>“Dhammadassissa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv18:1.2'><span class='pli'>siddhattho nāma nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv18:1.3'><span class='pli'>Nihanitvā tamaṁ sabbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv18:1.4'><span class='pli'>sūriyo abbhuggato yathā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv18:2.1'><span class='pli'>Sopi patvāna sambodhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv18:2.2'><span class='pli'>santārento sadevakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv18:2.3'><span class='pli'>Abhivassi dhammameghena, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv18:2.4'><span class='pli'>nibbāpento sadevakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv18:3.1'><span class='pli'>Tassāpi atulatejassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv18:3.2'><span class='pli'>Ahesuṁ abhisamayā tayo; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv18:3.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv18:3.4'><span class='pli'>Paṭhamābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv18:4.1'><span class='pli'>Punāparaṁ bhīmarathe, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv18:4.2'><span class='pli'>yadā āhani dundubhiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv18:4.3'><span class='pli'>Tadā navutikoṭīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv18:4.4'><span class='pli'>dutiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv18:5.1'><span class='pli'>Yadā buddho dhammaṁ desesi, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv18:5.2'><span class='pli'>vebhāre so puruttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv18:5.3'><span class='pli'>Tadā navutikoṭīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv18:5.4'><span class='pli'>tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv18:6.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv18:6.2'><span class='pli'>tasmimpi dvipaduttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv18:6.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv18:6.4'><span class='pli'>santacittāna tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv18:7.1'><span class='pli'>Koṭisatānaṁ navutīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv18:7.2'><span class='pli'>asītiyāpi ca koṭinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv18:7.3'><span class='pli'>Ete āsuṁ tayo ṭhānā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv18:7.4'><span class='pli'>vimalānaṁ samāgame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv18:8.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv18:8.2'><span class='pli'>maṅgalo nāma tāpaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv18:8.3'><span class='pli'>Uggatejo duppasaho, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv18:8.4'><span class='pli'>abhiññābalasamāhito. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv18:9.1'><span class='pli'>Jambuto phalamānetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv18:9.2'><span class='pli'>siddhatthassa adāsahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv18:9.3'><span class='pli'>Paṭiggahetvā sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv18:9.4'><span class='pli'>idaṁ vacanamabravi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv18:10.1'><span class='pli'>‘Passatha imaṁ tāpasaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv18:10.2'><span class='pli'>jaṭilaṁ uggatāpanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv18:10.3'><span class='pli'>Catunnavutito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv18:10.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv18:11.1'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv18:11.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv18:11.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv18:12.1'><span class='pli'>Tassāpi vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv18:12.2'><span class='pli'>bhiyyo cittaṁ pasādayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv18:12.3'><span class='pli'>Uttariṁ vatamadhiṭṭhāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv18:12.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv18:13.1'><span class='pli'>Vebhāraṁ nāma nagaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv18:13.2'><span class='pli'>udeno nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv18:13.3'><span class='pli'>Suphassā nāma janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv18:13.4'><span class='pli'>siddhatthassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv18:14.1'><span class='pli'>Dasavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv18:14.2'><span class='pli'>agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv18:14.3'><span class='pli'>Kokāsuppalakokanadā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv18:14.4'><span class='pli'>tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv18:15.1'><span class='pli'>Tisoḷasasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv18:15.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv18:15.3'><span class='pli'>Somanassā nāma sā nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv18:15.4'><span class='pli'>anūpamo nāma atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv18:16.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv18:16.2'><span class='pli'>sivikāyābhinikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv18:16.3'><span class='pli'>Anūnadasamāsāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv18:16.4'><span class='pli'>padhānaṁ padahī jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv18:17.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv18:17.2'><span class='pli'>siddhattho lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv18:17.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv18:17.4'><span class='pli'>migadāye naruttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv18:18.1'><span class='pli'>Sambalo ca sumitto ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv18:18.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv18:18.3'><span class='pli'>Revato nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv18:18.4'><span class='pli'>siddhatthassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv18:19.1'><span class='pli'>Sīvalā ca surāmā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv18:19.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv18:19.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv18:19.4'><span class='pli'>kaṇikāroti vuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv18:20.1'><span class='pli'>Suppiyo ca samuddo ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv18:20.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv18:20.3'><span class='pli'>Rammā ceva surammā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv18:20.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv18:21.1'><span class='pli'>So buddho saṭṭhiratanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv18:21.2'><span class='pli'>ahosi nabhamuggato; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv18:21.3'><span class='pli'>Kañcanagghiyasaṅkāso, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv18:21.4'><span class='pli'>dasasahassī virocati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv18:22.1'><span class='pli'>Sopi buddho asamasamo, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv18:22.2'><span class='pli'>atulo appaṭipuggalo; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv18:22.3'><span class='pli'>Vassasatasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv18:22.4'><span class='pli'>loke aṭṭhāsi cakkhumā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv18:23.1'><span class='pli'>Vipulaṁ pabhaṁ dassayitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv18:23.2'><span class='pli'>pupphāpetvāna sāvake; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv18:23.3'><span class='pli'>Vilāsetvā samāpatyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv18:23.4'><span class='pli'>nibbuto so sasāvako. </span></span></span></p>\n<p data-counter='7531'><span class='verse-line'><span class='segment' id='24.1' data-uid='bv18:24.1'><span class='pli'>Siddhattho munivaro buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv18:24.2'><span class='pli'>Anomārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv18:24.3'><span class='pli'>Tatthevassa thūpavaro, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv18:24.4'><span class='pli'>Catuyojanamuggato”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='25.1' data-uid='bv18:25.1'><span class='pli'>Siddhatthassa bhagavato vaṁso soḷasamo. </span></span></p></article>\n"
  },
  "bv19": {
    "previous": "bv18",
    "next": "bv20",
    "content": "<article id='bv19'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv19:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv19:0.2'><span class='pli'>19. Tissabuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv19:1.1'><span class='pli'>“Siddhatthassa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv19:1.2'><span class='pli'>Asamo appaṭipuggalo; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv19:1.3'><span class='pli'>Anantatejo amitayaso, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv19:1.4'><span class='pli'>Tisso lokagganāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv19:2.1'><span class='pli'>Tamandhakāraṁ vidhamitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv19:2.2'><span class='pli'>obhāsetvā sadevakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv19:2.3'><span class='pli'>Anukampako mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv19:2.4'><span class='pli'>loke uppajji cakkhumā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv19:3.1'><span class='pli'>Tassāpi atulā iddhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv19:3.2'><span class='pli'>atulaṁ sīlaṁ samādhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv19:3.3'><span class='pli'>Sabbattha pāramiṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv19:3.4'><span class='pli'>dhammacakkaṁ pavattayi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv19:4.1'><span class='pli'>So buddho dasasahassimhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv19:4.2'><span class='pli'>Viññāpesi giraṁ suciṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv19:4.3'><span class='pli'>Koṭisatāni abhisamiṁsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv19:4.4'><span class='pli'>Paṭhame dhammadesane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv19:5.1'><span class='pli'>Dutiyo navutikoṭīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv19:5.2'><span class='pli'>tatiyo saṭṭhikoṭiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv19:5.3'><span class='pli'>Bandhanāto pamocesi, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv19:5.4'><span class='pli'>satte naramarū tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv19:6.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv19:6.2'><span class='pli'>tisse lokagganāyake; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv19:6.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv19:6.4'><span class='pli'>santacittāna tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv19:7.1'><span class='pli'>Khīṇāsavasatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv19:7.2'><span class='pli'>Paṭhamo āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv19:7.3'><span class='pli'>Navutisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv19:7.4'><span class='pli'>Dutiyo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv19:8.1'><span class='pli'>Asītisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv19:8.2'><span class='pli'>tatiyo āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv19:8.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv19:8.4'><span class='pli'>pupphitānaṁ vimuttiyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv19:9.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv19:9.2'><span class='pli'>sujāto nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv19:9.3'><span class='pli'>Mahābhogaṁ chaḍḍayitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv19:9.4'><span class='pli'>pabbajiṁ isipabbajaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv19:10.1'><span class='pli'>Mayi pabbajite sante, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv19:10.2'><span class='pli'>uppajji lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv19:10.3'><span class='pli'>Buddhoti saddaṁ sutvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv19:10.4'><span class='pli'>pīti me upapajjatha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv19:11.1'><span class='pli'>Dibbaṁ mandāravaṁ pupphaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv19:11.2'><span class='pli'>padumaṁ pārichattakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv19:11.3'><span class='pli'>Ubho hatthehi paggayha, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv19:11.4'><span class='pli'>dhunamāno upāgamiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv19:12.1'><span class='pli'>Catuvaṇṇaparivutaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv19:12.2'><span class='pli'>tissaṁ lokagganāyakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv19:12.3'><span class='pli'>Tamahaṁ pupphaṁ gahetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv19:12.4'><span class='pli'>matthake dhārayiṁ jinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv19:13.1'><span class='pli'>Sopi maṁ buddho byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv19:13.2'><span class='pli'>janamajjhe nisīdiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv19:13.3'><span class='pli'>‘Dvenavute ito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv19:13.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv19:14.1'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv19:14.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv19:14.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv19:15.1'><span class='pli'>Tassāpi vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv19:15.2'><span class='pli'>bhiyyo cittaṁ pasādayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv19:15.3'><span class='pli'>Uttariṁ vatamadhiṭṭhāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv19:15.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv19:16.1'><span class='pli'>Khemakaṁ nāma nagaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv19:16.2'><span class='pli'>janasandho nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv19:16.3'><span class='pli'>Padumā nāma janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv19:16.4'><span class='pli'>tissassa ca mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv19:17.1'><span class='pli'>Sattavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv19:17.2'><span class='pli'>Agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv19:17.3'><span class='pli'>Guhāsela nārisaya nisabhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv19:17.4'><span class='pli'>Tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv19:18.1'><span class='pli'>Samatiṁsasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv19:18.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv19:18.3'><span class='pli'>Subhaddānāmikā nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv19:18.4'><span class='pli'>ānando nāma atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv19:19.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv19:19.2'><span class='pli'>assayānena nikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv19:19.3'><span class='pli'>Anūnaaṭṭhamāsāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv19:19.4'><span class='pli'>padhānaṁ padahī jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv19:20.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv19:20.2'><span class='pli'>tisso lokagganāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv19:20.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv19:20.4'><span class='pli'>yasavatiyamuttame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv19:21.1'><span class='pli'>Brahmadevo udayo ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv19:21.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv19:21.3'><span class='pli'>Samaṅgo nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv19:21.4'><span class='pli'>tissassa ca mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv19:22.1'><span class='pli'>Phussā ceva sudattā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv19:22.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv19:22.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv19:22.4'><span class='pli'>asanoti pavuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv19:23.1'><span class='pli'>Sambalo ca sirimā ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv19:23.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv19:23.3'><span class='pli'>Kisāgotamī upasenā, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv19:23.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv19:24.1'><span class='pli'>So buddho saṭṭhiratano, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv19:24.2'><span class='pli'>ahu uccattane jino; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv19:24.3'><span class='pli'>Anūpamo asadiso, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv19:24.4'><span class='pli'>himavā viya dissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv19:25.1'><span class='pli'>Tassāpi atulatejassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv19:25.2'><span class='pli'>āyu āsi anuttaro; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv19:25.3'><span class='pli'>Vassasatasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv19:25.4'><span class='pli'>loke aṭṭhāsi cakkhumā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv19:26.1'><span class='pli'>Uttamaṁ pavaraṁ seṭṭhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv19:26.2'><span class='pli'>anubhotvā mahāyasaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv19:26.3'><span class='pli'>Jalitvā aggikkhandhova, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv19:26.4'><span class='pli'>nibbuto so sasāvako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv19:27.1'><span class='pli'>Valāhakova anilena, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv19:27.2'><span class='pli'>sūriyena viya ussavo; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv19:27.3'><span class='pli'>Andhakārova padīpena, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv19:27.4'><span class='pli'>nibbuto so sasāvako. </span></span></span></p>\n<p data-counter='7811'><span class='verse-line'><span class='segment' id='28.1' data-uid='bv19:28.1'><span class='pli'>Tisso jinavaro buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv19:28.2'><span class='pli'>Nandārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv19:28.3'><span class='pli'>Tatthevassa jinathūpo, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv19:28.4'><span class='pli'>Tīṇiyojanamuggato”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='29.1' data-uid='bv19:29.1'><span class='pli'>Tissassa bhagavato vaṁso sattarasamo. </span></span></p></article>\n"
  },
  "bv20": {
    "previous": "bv19",
    "next": "bv21",
    "content": "<article id='bv20'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv20:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv20:0.2'><span class='pli'>20. Phussabuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv20:1.1'><span class='pli'>“Tattheva maṇḍakappamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv20:1.2'><span class='pli'>ahu satthā anuttaro; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv20:1.3'><span class='pli'>Anūpamo asamasamo, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv20:1.4'><span class='pli'>phusso lokagganāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv20:2.1'><span class='pli'>Sopi sabbaṁ tamaṁ hantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv20:2.2'><span class='pli'>vijaṭetvā mahājaṭaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv20:2.3'><span class='pli'>Sadevakaṁ tappayanto, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv20:2.4'><span class='pli'>abhivassi amatambunā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv20:3.1'><span class='pli'>Dhammacakkaṁ pavattente, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv20:3.2'><span class='pli'>phusse nakkhattamaṅgale; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv20:3.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv20:3.4'><span class='pli'>paṭhamābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv20:4.1'><span class='pli'>Navutisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv20:4.2'><span class='pli'>dutiyābhisamayo ahu; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv20:4.3'><span class='pli'>Asītisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv20:4.4'><span class='pli'>tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv20:5.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv20:5.2'><span class='pli'>phussassāpi mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv20:5.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv20:5.4'><span class='pli'>santacittāna tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv20:6.1'><span class='pli'>Saṭṭhisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv20:6.2'><span class='pli'>paṭhamo āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv20:6.3'><span class='pli'>Paññāsasatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv20:6.4'><span class='pli'>dutiyo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv20:7.1'><span class='pli'>Cattārīsasatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv20:7.2'><span class='pli'>Tatiyo āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv20:7.3'><span class='pli'>Anupādā vimuttānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv20:7.4'><span class='pli'>Vocchinnapaṭisandhinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv20:8.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv20:8.2'><span class='pli'>vijitāvī nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv20:8.3'><span class='pli'>Chaḍḍayitvā mahārajjaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv20:8.4'><span class='pli'>pabbajiṁ tassa santike. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv20:9.1'><span class='pli'>Sopi maṁ buddho byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv20:9.2'><span class='pli'>phusso lokagganāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv20:9.3'><span class='pli'>‘Dvenavute ito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv20:9.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv20:10.1'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv20:10.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv20:10.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv20:11.1'><span class='pli'>Tassāpi vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv20:11.2'><span class='pli'>bhiyyo cittaṁ pasādayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv20:11.3'><span class='pli'>Uttariṁ vatamadhiṭṭhāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv20:11.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv20:12.1'><span class='pli'>Suttantaṁ vinayañcāpi, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv20:12.2'><span class='pli'>navaṅgaṁ satthusāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv20:12.3'><span class='pli'>Sabbaṁ pariyāpuṇitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv20:12.4'><span class='pli'>sobhayiṁ jinasāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv20:13.1'><span class='pli'>Tatthappamatto viharanto, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv20:13.2'><span class='pli'>brahmaṁ bhāvetva bhāvanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv20:13.3'><span class='pli'>Abhiññāsu pāramiṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv20:13.4'><span class='pli'>brahmalokamagañchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv20:14.1'><span class='pli'>Kāsikaṁ nāma nagaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv20:14.2'><span class='pli'>jayaseno nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv20:14.3'><span class='pli'>Sirimā nāma janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv20:14.4'><span class='pli'>phussassāpi mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv20:15.1'><span class='pli'>Navavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv20:15.2'><span class='pli'>Agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv20:15.3'><span class='pli'>Garuḷapakkha haṁsa suvaṇṇabhārā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv20:15.4'><span class='pli'>Tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv20:16.1'><span class='pli'>Tiṁsaitthisahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv20:16.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv20:16.3'><span class='pli'>Kisāgotamī nāma nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv20:16.4'><span class='pli'>anūpamo nāma atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv20:17.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv20:17.2'><span class='pli'>hatthiyānena nikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv20:17.3'><span class='pli'>Chamāsaṁ padhānacāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv20:17.4'><span class='pli'>acarī purisuttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv20:18.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv20:18.2'><span class='pli'>phusso lokagganāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv20:18.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv20:18.4'><span class='pli'>migadāye naruttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv20:19.1'><span class='pli'>Surakkhito dhammaseno, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv20:19.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv20:19.3'><span class='pli'>Sabhiyo nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv20:19.4'><span class='pli'>phussassāpi mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv20:20.1'><span class='pli'>Cālā ca upacālā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv20:20.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv20:20.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv20:20.4'><span class='pli'>āmaṇḍoti pavuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv20:21.1'><span class='pli'>Dhanañcayo visākho ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv20:21.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv20:21.3'><span class='pli'>Padumā ceva nāgā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv20:21.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv20:22.1'><span class='pli'>Aṭṭhapaṇṇāsaratanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv20:22.2'><span class='pli'>sopi accuggato muni; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv20:22.3'><span class='pli'>Sobhate sataraṁsīva, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv20:22.4'><span class='pli'>uḷurājāva pūrito. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv20:23.1'><span class='pli'>Navutivassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv20:23.2'><span class='pli'>āyu vijjati tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv20:23.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv20:23.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv20:24.1'><span class='pli'>Ovaditvā bahū satte, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv20:24.2'><span class='pli'>santāretvā bahū jane; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv20:24.3'><span class='pli'>Sopi satthā atulayaso, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv20:24.4'><span class='pli'>nibbuto so sasāvako. </span></span></span></p>\n<p data-counter='8061'><span class='verse-line'><span class='segment' id='25.1' data-uid='bv20:25.1'><span class='pli'>Phusso jinavaro satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv20:25.2'><span class='pli'>Senārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv20:25.3'><span class='pli'>Dhātuvitthārikaṁ āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv20:25.4'><span class='pli'>Tesu tesu padesato”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='26.1' data-uid='bv20:26.1'><span class='pli'>Phussassa bhagavato vaṁso aṭṭhārasamo. </span></span></p></article>\n"
  },
  "bv21": {
    "previous": "bv20",
    "next": "bv22",
    "content": "<article id='bv21'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv21:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv21:0.2'><span class='pli'>21 Vipassībuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv21:1.1'><span class='pli'>“Phussassa ca aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv21:1.2'><span class='pli'>sambuddho dvipaduttamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv21:1.3'><span class='pli'>Vipassī nāma nāmena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv21:1.4'><span class='pli'>loke uppajji cakkhumā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv21:2.1'><span class='pli'>Avijjaṁ sabbaṁ padāletvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv21:2.2'><span class='pli'>patto sambodhimuttamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv21:2.3'><span class='pli'>Dhammacakkaṁ pavattetuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv21:2.4'><span class='pli'>pakkāmi bandhumatīpuraṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv21:3.1'><span class='pli'>Dhammacakkaṁ pavattetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv21:3.2'><span class='pli'>ubho bodhesi nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv21:3.3'><span class='pli'>Gaṇanāya na vattabbo, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv21:3.4'><span class='pli'>paṭhamābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv21:4.1'><span class='pli'>Punāparaṁ amitayaso, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv21:4.2'><span class='pli'>tattha saccaṁ pakāsayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv21:4.3'><span class='pli'>Caturāsītisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv21:4.4'><span class='pli'>dutiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv21:5.1'><span class='pli'>Caturāsītisahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv21:5.2'><span class='pli'>sambuddhaṁ anupabbajuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv21:5.3'><span class='pli'>Tesamārāmapattānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv21:5.4'><span class='pli'>dhammaṁ desesi cakkhumā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv21:6.1'><span class='pli'>Sabbākārena bhāsato, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv21:6.2'><span class='pli'>sutvā upanisādino; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv21:6.3'><span class='pli'>Tepi dhammavaraṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv21:6.4'><span class='pli'>tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv21:7.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv21:7.2'><span class='pli'>vipassissa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv21:7.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv21:7.4'><span class='pli'>santacittāna tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv21:8.1'><span class='pli'>Aṭṭhasaṭṭhisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv21:8.2'><span class='pli'>Paṭhamo āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv21:8.3'><span class='pli'>Bhikkhusatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv21:8.4'><span class='pli'>Dutiyo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv21:9.1'><span class='pli'>Asītibhikkhusahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv21:9.2'><span class='pli'>tatiyo āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv21:9.3'><span class='pli'>Tattha bhikkhugaṇamajjhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv21:9.4'><span class='pli'>sambuddho atirocati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv21:10.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv21:10.2'><span class='pli'>nāgarājā mahiddhiko; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv21:10.3'><span class='pli'>Atulo nāma nāmena, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv21:10.4'><span class='pli'>puññavanto jutindharo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv21:11.1'><span class='pli'>Nekānaṁ nāgakoṭīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv21:11.2'><span class='pli'>parivāretvānahaṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv21:11.3'><span class='pli'>Vajjanto dibbaturiyehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv21:11.4'><span class='pli'>lokajeṭṭhaṁ upāgamiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv21:12.1'><span class='pli'>Upasaṅkamitvā sambuddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv21:12.2'><span class='pli'>Vipassiṁ lokanāyakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv21:12.3'><span class='pli'>Maṇimuttaratanakhacitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv21:12.4'><span class='pli'>Sabbābharaṇavibhūsitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.5' data-uid='bv21:12.5'><span class='pli'>Nimantetvā dhammarājassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.6' data-uid='bv21:12.6'><span class='pli'>Suvaṇṇapīṭhamadāsahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv21:13.1'><span class='pli'>Sopi maṁ buddho byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv21:13.2'><span class='pli'>saṅghamajjhe nisīdiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv21:13.3'><span class='pli'>‘Ekanavutito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv21:13.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv21:14.1'><span class='pli'>Ahu kapilavhayā rammā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv21:14.2'><span class='pli'>nikkhamitvā tathāgato; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv21:14.3'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv21:14.4'><span class='pli'>katvā dukkarakārikaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv21:15.1'><span class='pli'>Ajapālarukkhamūlasmiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv21:15.2'><span class='pli'>nisīditvā tathāgato; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv21:15.3'><span class='pli'>Tattha pāyāsaṁ paggayha, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv21:15.4'><span class='pli'>nerañjaramupehiti. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv21:16.1'><span class='pli'>Nerañjarāya tīramhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv21:16.2'><span class='pli'>pāyāsaṁ ada so jino; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv21:16.3'><span class='pli'>Paṭiyattavaramaggena, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv21:16.4'><span class='pli'>bodhimūlamupehiti. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv21:17.1'><span class='pli'>Tato padakkhiṇaṁ katvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv21:17.2'><span class='pli'>bodhimaṇḍaṁ anuttaro; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv21:17.3'><span class='pli'>Assatthamūle sambodhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv21:17.4'><span class='pli'>bujjhissati mahāyaso. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv21:18.1'><span class='pli'>Imassa janikā mātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv21:18.2'><span class='pli'>māyā nāma bhavissati; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv21:18.3'><span class='pli'>Pitā suddhodano nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv21:18.4'><span class='pli'>ayaṁ hessati gotamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv21:19.1'><span class='pli'>Anāsavā vītarāgā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv21:19.2'><span class='pli'>santacittā samāhitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv21:19.3'><span class='pli'>Kolito upatisso ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv21:19.4'><span class='pli'>aggā hessanti sāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.5' data-uid='bv21:19.5'><span class='pli'>Ānando nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.6' data-uid='bv21:19.6'><span class='pli'>upaṭṭhissatimaṁ jinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv21:20.1'><span class='pli'>Khemā uppalavaṇṇā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv21:20.2'><span class='pli'>aggā hessanti sāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv21:20.3'><span class='pli'>Anāsavā vītarāgā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv21:20.4'><span class='pli'>santacittā samāhitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.5' data-uid='bv21:20.5'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.6' data-uid='bv21:20.6'><span class='pli'>assatthoti pavuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv21:21.1'><span class='pli'>Citto ca hatthāḷavako, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv21:21.2'><span class='pli'>aggā hessantupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv21:21.3'><span class='pli'>Nandamātā ca uttarā, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv21:21.4'><span class='pli'>aggā hessantupaṭṭhikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.5' data-uid='bv21:21.5'><span class='pli'>Āyu vassasataṁ tassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.6' data-uid='bv21:21.6'><span class='pli'>gotamassa yasassino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv21:22.1'><span class='pli'>Idaṁ sutvāna vacanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv21:22.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv21:22.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv21:23.1'><span class='pli'>Tassāhaṁ vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv21:23.2'><span class='pli'>bhiyyo cittaṁ pasādayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv21:23.3'><span class='pli'>Uttariṁ vatamadhiṭṭhāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv21:23.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv21:24.1'><span class='pli'>Nagaraṁ bandhumatī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv21:24.2'><span class='pli'>bandhumā nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv21:24.3'><span class='pli'>Mātā bandhumatī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv21:24.4'><span class='pli'>vipassissa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv21:25.1'><span class='pli'>Aṭṭhavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv21:25.2'><span class='pli'>agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv21:25.3'><span class='pli'>Nando sunando sirimā, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv21:25.4'><span class='pli'>tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv21:26.1'><span class='pli'>Ticattārīsasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv21:26.2'><span class='pli'>Nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv21:26.3'><span class='pli'>Sudassanā nāma sā nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv21:26.4'><span class='pli'>Samavattakkhandho nāma atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv21:27.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv21:27.2'><span class='pli'>rathayānena nikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv21:27.3'><span class='pli'>Anūnaaṭṭhamāsāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv21:27.4'><span class='pli'>padhānaṁ padahī jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='bv21:28.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv21:28.2'><span class='pli'>vipassī lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv21:28.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv21:28.4'><span class='pli'>migadāye naruttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='bv21:29.1'><span class='pli'>Khaṇḍo ca tissanāmo ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='bv21:29.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='bv21:29.3'><span class='pli'>Asoko nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='bv21:29.4'><span class='pli'>vipassissa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='bv21:30.1'><span class='pli'>Candā ca candamittā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='bv21:30.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='bv21:30.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='bv21:30.4'><span class='pli'>pāṭalīti pavuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='bv21:31.1'><span class='pli'>Punabbasumitto nāgo ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='bv21:31.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='bv21:31.3'><span class='pli'>Sirimā uttarā ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='bv21:31.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='bv21:32.1'><span class='pli'>Asītihatthamubbedho, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='bv21:32.2'><span class='pli'>vipassī lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='bv21:32.3'><span class='pli'>Pabhā niddhāvati tassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='bv21:32.4'><span class='pli'>samantā sattayojane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='bv21:33.1'><span class='pli'>Asītivassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='bv21:33.2'><span class='pli'>āyu buddhassa tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='bv21:33.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='bv21:33.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='34.1' data-uid='bv21:34.1'><span class='pli'>Bahudevamanussānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='bv21:34.2'><span class='pli'>bandhanā parimocayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='bv21:34.3'><span class='pli'>Maggāmaggañca ācikkhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.4' data-uid='bv21:34.4'><span class='pli'>avasesaputhujjane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='35.1' data-uid='bv21:35.1'><span class='pli'>Ālokaṁ dassayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.2' data-uid='bv21:35.2'><span class='pli'>desetvā amataṁ padaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='35.3' data-uid='bv21:35.3'><span class='pli'>Jalitvā aggikkhandhova, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.4' data-uid='bv21:35.4'><span class='pli'>nibbuto so sasāvako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='36.1' data-uid='bv21:36.1'><span class='pli'>Iddhivaraṁ puññavaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.2' data-uid='bv21:36.2'><span class='pli'>lakkhaṇañca kusumitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='36.3' data-uid='bv21:36.3'><span class='pli'>Sabbaṁ tamantarahitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.4' data-uid='bv21:36.4'><span class='pli'>nanu rittā sabbasaṅkhārā. </span></span></span></p>\n<p data-counter='8431'><span class='verse-line'><span class='segment' id='37.1' data-uid='bv21:37.1'><span class='pli'>Vipassī jinavaro buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.2' data-uid='bv21:37.2'><span class='pli'>Sumittārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='37.3' data-uid='bv21:37.3'><span class='pli'>Tatthevassa thūpavaro, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.4' data-uid='bv21:37.4'><span class='pli'>Sattayojanamussito”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='38.1' data-uid='bv21:38.1'><span class='pli'>Vipassissa bhagavato vaṁso ekūnavīsatimo. </span></span></p></article>\n"
  },
  "bv22": {
    "previous": "bv21",
    "next": "bv23",
    "content": "<article id='bv22'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv22:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv22:0.2'><span class='pli'>22. Sikhībuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv22:1.1'><span class='pli'>“Vipassissa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv22:1.2'><span class='pli'>sambuddho dvipaduttamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv22:1.3'><span class='pli'>Sikhivhayo āsi jino, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv22:1.4'><span class='pli'>asamo appaṭipuggalo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv22:2.1'><span class='pli'>Mārasenaṁ pamadditvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv22:2.2'><span class='pli'>patto sambodhimuttamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv22:2.3'><span class='pli'>Dhammacakkaṁ pavattesi, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv22:2.4'><span class='pli'>anukampāya pāṇinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv22:3.1'><span class='pli'>Dhammacakkaṁ pavattente, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv22:3.2'><span class='pli'>sikhimhi jinapuṅgave; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv22:3.3'><span class='pli'>Koṭisatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv22:3.4'><span class='pli'>paṭhamābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv22:4.1'><span class='pli'>Aparampi dhammaṁ desente, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv22:4.2'><span class='pli'>gaṇaseṭṭhe naruttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv22:4.3'><span class='pli'>Navutikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv22:4.4'><span class='pli'>dutiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv22:5.1'><span class='pli'>Yamakapāṭihāriyañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv22:5.2'><span class='pli'>dassayante sadevake; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv22:5.3'><span class='pli'>Asītikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv22:5.4'><span class='pli'>tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv22:6.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv22:6.2'><span class='pli'>sikhissāpi mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv22:6.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv22:6.4'><span class='pli'>santacittāna tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv22:7.1'><span class='pli'>Bhikkhusatasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv22:7.2'><span class='pli'>paṭhamo āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv22:7.3'><span class='pli'>Asītibhikkhusahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv22:7.4'><span class='pli'>dutiyo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv22:8.1'><span class='pli'>Sattatibhikkhusahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv22:8.2'><span class='pli'>tatiyo āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv22:8.3'><span class='pli'>Anupalitto padumaṁva, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv22:8.4'><span class='pli'>toyamhi sampavaḍḍhitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv22:9.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv22:9.2'><span class='pli'>Arindamo nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv22:9.3'><span class='pli'>Sambuddhappamukhaṁ saṅghaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv22:9.4'><span class='pli'>Annapānena tappayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv22:10.1'><span class='pli'>Bahuṁ dussavaraṁ datvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv22:10.2'><span class='pli'>dussakoṭiṁ anappakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv22:10.3'><span class='pli'>Alaṅkataṁ hatthiyānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv22:10.4'><span class='pli'>sambuddhassa adāsahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv22:11.1'><span class='pli'>Hatthiyānaṁ nimminitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv22:11.2'><span class='pli'>kappiyaṁ upanāmayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv22:11.3'><span class='pli'>Pūrayiṁ mānasaṁ mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv22:11.4'><span class='pli'>niccaṁ daḷhamupaṭṭhitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv22:12.1'><span class='pli'>Sopi maṁ buddho byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv22:12.2'><span class='pli'>sikhī lokagganāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv22:12.3'><span class='pli'>‘Ekatiṁse ito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv22:12.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv22:13.1'><span class='pli'>Ahu kapilavhayā rammā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv22:13.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv22:13.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv22:14.1'><span class='pli'>Tassāhaṁ vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv22:14.2'><span class='pli'>bhiyyo cittaṁ pasādayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv22:14.3'><span class='pli'>Uttariṁ vatamadhiṭṭhāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv22:14.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv22:15.1'><span class='pli'>Nagaraṁ aruṇavatī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv22:15.2'><span class='pli'>Aruṇo nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv22:15.3'><span class='pli'>Pabhāvatī nāma janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv22:15.4'><span class='pli'>Sikhissāpi mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv22:16.1'><span class='pli'>Sattavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv22:16.2'><span class='pli'>agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv22:16.3'><span class='pli'>Sucandako giri vasabho, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv22:16.4'><span class='pli'>tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv22:17.1'><span class='pli'>Catuvīsasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv22:17.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv22:17.3'><span class='pli'>Sabbakāmā nāma nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv22:17.4'><span class='pli'>atulo nāma atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv22:18.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv22:18.2'><span class='pli'>hatthiyānena nikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv22:18.3'><span class='pli'>Aṭṭhamāsaṁ padhānacāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv22:18.4'><span class='pli'>acarī purisuttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv22:19.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv22:19.2'><span class='pli'>sikhī lokagganāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv22:19.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv22:19.4'><span class='pli'>migadāye naruttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv22:20.1'><span class='pli'>Abhibhū sambhavo ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv22:20.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv22:20.3'><span class='pli'>Khemaṅkaro nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv22:20.4'><span class='pli'>sikhissāpi mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv22:21.1'><span class='pli'>Sakhilā ca padumā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv22:21.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv22:21.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv22:21.4'><span class='pli'>puṇḍarīkoti vuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv22:22.1'><span class='pli'>Sirivaḍḍho ca nando ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv22:22.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv22:22.3'><span class='pli'>Cittā ceva suguttā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv22:22.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv22:23.1'><span class='pli'>Uccattanena so buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv22:23.2'><span class='pli'>sattatihatthamuggato; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv22:23.3'><span class='pli'>Kañcanagghiyasaṅkāso, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv22:23.4'><span class='pli'>dvattiṁsavaralakkhaṇo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv22:24.1'><span class='pli'>Tassāpi byāmappabhā kāyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv22:24.2'><span class='pli'>divārattiṁ nirantaraṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv22:24.3'><span class='pli'>Disodisaṁ niccharanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv22:24.4'><span class='pli'>tīṇiyojanaso pabhā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv22:25.1'><span class='pli'>Sattativassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv22:25.2'><span class='pli'>āyu tassa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv22:25.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv22:25.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv22:26.1'><span class='pli'>Dhammameghaṁ pavassetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv22:26.2'><span class='pli'>temayitvā sadevake; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv22:26.3'><span class='pli'>Khemantaṁ pāpayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv22:26.4'><span class='pli'>nibbuto so sasāvako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv22:27.1'><span class='pli'>Anubyañjanasampannaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv22:27.2'><span class='pli'>dvattiṁsavaralakkhaṇaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv22:27.3'><span class='pli'>Sabbaṁ tamantarahitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv22:27.4'><span class='pli'>nanu rittā sabbasaṅkhārā. </span></span></span></p>\n<p data-counter='8711'><span class='verse-line'><span class='segment' id='28.1' data-uid='bv22:28.1'><span class='pli'>Sikhī munivaro buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv22:28.2'><span class='pli'>Assārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv22:28.3'><span class='pli'>Tatthevassa thūpavaro, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv22:28.4'><span class='pli'>Tīṇiyojanamuggato”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='29.1' data-uid='bv22:29.1'><span class='pli'>Sikhissa bhagavato vaṁso vīsatimo. </span></span></p></article>\n"
  },
  "bv23": {
    "previous": "bv22",
    "next": "bv24",
    "content": "<article id='bv23'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv23:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv23:0.2'><span class='pli'>23. Vessabhūbuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv23:1.1'><span class='pli'>“Tattheva maṇḍakappamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv23:1.2'><span class='pli'>asamo appaṭipuggalo; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv23:1.3'><span class='pli'>Vessabhū nāma nāmena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv23:1.4'><span class='pli'>loke uppajji nāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv23:2.1'><span class='pli'>Ādittaṁ vata rāgaggi, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv23:2.2'><span class='pli'>taṇhānaṁ vijitaṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv23:2.3'><span class='pli'>Nāgova bandhanaṁ chetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv23:2.4'><span class='pli'>patto sambodhimuttamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv23:3.1'><span class='pli'>Dhammacakkaṁ pavattente, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv23:3.2'><span class='pli'>vessabhūlokanāyake; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv23:3.3'><span class='pli'>Asītikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv23:3.4'><span class='pli'>paṭhamābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv23:4.1'><span class='pli'>Pakkante cārikaṁ raṭṭhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv23:4.2'><span class='pli'>lokajeṭṭhe narāsabhe; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv23:4.3'><span class='pli'>Sattatikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv23:4.4'><span class='pli'>dutiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv23:5.1'><span class='pli'>Mahādiṭṭhiṁ vinodento, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv23:5.2'><span class='pli'>pāṭiheraṁ karoti so; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv23:5.3'><span class='pli'>Samāgatā naramarū, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv23:5.4'><span class='pli'>dasasahassī sadevake. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv23:6.1'><span class='pli'>Mahāacchariyaṁ disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv23:6.2'><span class='pli'>abbhutaṁ lomahaṁsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv23:6.3'><span class='pli'>Devā ceva manussā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv23:6.4'><span class='pli'>bujjhare saṭṭhikoṭiyo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv23:7.1'><span class='pli'>Sannipātā tayo āsuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv23:7.2'><span class='pli'>vessabhussa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv23:7.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv23:7.4'><span class='pli'>santacittāna tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv23:8.1'><span class='pli'>Asītibhikkhusahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv23:8.2'><span class='pli'>paṭhamo āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv23:8.3'><span class='pli'>Sattatibhikkhusahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv23:8.4'><span class='pli'>dutiyo āsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv23:9.1'><span class='pli'>Saṭṭhibhikkhusahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv23:9.2'><span class='pli'>tatiyo āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv23:9.3'><span class='pli'>Jarādibhayabhītānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv23:9.4'><span class='pli'>orasānaṁ mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv23:10.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv23:10.2'><span class='pli'>sudassano nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv23:10.3'><span class='pli'>Nimantetvā mahāvīraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv23:10.4'><span class='pli'>dānaṁ datvā mahārahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.5' data-uid='bv23:10.5'><span class='pli'>Annapānena vatthena, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.6' data-uid='bv23:10.6'><span class='pli'>sasaṅghaṁ jinapūjayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv23:11.1'><span class='pli'>Tassa buddhassa asamassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv23:11.2'><span class='pli'>cakkaṁ vattitamuttamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv23:11.3'><span class='pli'>Sutvāna paṇitaṁ dhammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv23:11.4'><span class='pli'>pabbajjamabhirocayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv23:12.1'><span class='pli'>Mahādānaṁ pavattetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv23:12.2'><span class='pli'>rattindivamatandito; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv23:12.3'><span class='pli'>Pabbajjaṁ guṇasampannaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv23:12.4'><span class='pli'>pabbajiṁ jinasantike. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv23:13.1'><span class='pli'>Ācāraguṇasampanno, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv23:13.2'><span class='pli'>vattasīlasamāhito; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv23:13.3'><span class='pli'>Sabbaññutaṁ gavesanto, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv23:13.4'><span class='pli'>ramāmi jinasāsane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv23:14.1'><span class='pli'>Saddhāpītiṁ upagantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv23:14.2'><span class='pli'>buddhaṁ vandāmi sattharaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv23:14.3'><span class='pli'>Pīti uppajjati mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv23:14.4'><span class='pli'>bodhiyāyeva kāraṇā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv23:15.1'><span class='pli'>Anivattamānasaṁ ñatvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv23:15.2'><span class='pli'>sambuddho etadabravi; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv23:15.3'><span class='pli'>‘Ekatiṁse ito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv23:15.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv23:16.1'><span class='pli'>Ahu kapilavhayā rammā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv23:16.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv23:16.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv23:17.1'><span class='pli'>Tassāhaṁ vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv23:17.2'><span class='pli'>bhiyyo cittaṁ pasādayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv23:17.3'><span class='pli'>Uttariṁ vatamadhiṭṭhāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv23:17.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv23:18.1'><span class='pli'>Anomaṁ nāma nagaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv23:18.2'><span class='pli'>suppatīto nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv23:18.3'><span class='pli'>Mātā yasavatī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv23:18.4'><span class='pli'>vessabhussa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv23:19.1'><span class='pli'>Cha ca vassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv23:19.2'><span class='pli'>Agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv23:19.3'><span class='pli'>Ruci suruci rativaḍḍhano, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv23:19.4'><span class='pli'>Tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv23:20.1'><span class='pli'>Anūnatiṁsasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv23:20.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv23:20.3'><span class='pli'>Sucittā nāma sā nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv23:20.4'><span class='pli'>suppabuddho nāma atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv23:21.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv23:21.2'><span class='pli'>sivikāyābhinikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv23:21.3'><span class='pli'>Chamāsaṁ padhānacāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv23:21.4'><span class='pli'>acarī purisuttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv23:22.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv23:22.2'><span class='pli'>vessabhūlokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv23:22.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv23:22.4'><span class='pli'>aruṇārāme naruttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv23:23.1'><span class='pli'>Soṇo ca uttaro ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv23:23.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv23:23.3'><span class='pli'>Upasanto nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv23:23.4'><span class='pli'>vessabhussa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv23:24.1'><span class='pli'>Rāmā ceva samālā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv23:24.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv23:24.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv23:24.4'><span class='pli'>mahāsāloti vuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv23:25.1'><span class='pli'>Sotthiko ceva rambho ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv23:25.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv23:25.3'><span class='pli'>Gotamī sirimā ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv23:25.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv23:26.1'><span class='pli'>Saṭṭhiratanamubbedho, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv23:26.2'><span class='pli'>hemayūpasamūpamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv23:26.3'><span class='pli'>Kāyā niccharati rasmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv23:26.4'><span class='pli'>rattiṁva pabbate sikhī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv23:27.1'><span class='pli'>Saṭṭhivassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv23:27.2'><span class='pli'>āyu tassa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv23:27.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv23:27.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='bv23:28.1'><span class='pli'>Dhammaṁ vitthārikaṁ katvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv23:28.2'><span class='pli'>vibhajitvā mahājanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv23:28.3'><span class='pli'>Dhammanāvaṁ ṭhapetvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv23:28.4'><span class='pli'>nibbuto so sasāvako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='bv23:29.1'><span class='pli'>Dassaneyyaṁ sabbajanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='bv23:29.2'><span class='pli'>vihāraṁ iriyāpathaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='bv23:29.3'><span class='pli'>Sabbaṁ tamantarahitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='bv23:29.4'><span class='pli'>nanu rittā sabbasaṅkhārā. </span></span></span></p>\n<p data-counter='9011'><span class='verse-line'><span class='segment' id='30.1' data-uid='bv23:30.1'><span class='pli'>Vessabhū jinavaro satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='bv23:30.2'><span class='pli'>Khemārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='bv23:30.3'><span class='pli'>Dhātuvitthārikaṁ āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='bv23:30.4'><span class='pli'>Tesu tesu padesato”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='31.1' data-uid='bv23:31.1'><span class='pli'>Vessabhussa bhagavato vaṁso ekavīsatimo. </span></span></p></article>\n"
  },
  "bv24": {
    "previous": "bv23",
    "next": "bv25",
    "content": "<article id='bv24'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv24:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv24:0.2'><span class='pli'>24. Kakusandhabuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv24:1.1'><span class='pli'>“Vessabhussa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv24:1.2'><span class='pli'>sambuddho dvipaduttamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv24:1.3'><span class='pli'>Kakusandho nāma nāmena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv24:1.4'><span class='pli'>appameyyo durāsado. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv24:2.1'><span class='pli'>Ugghāṭetvā sabbabhavaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv24:2.2'><span class='pli'>cariyāya pāramiṁ gato; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv24:2.3'><span class='pli'>Sīhova pañjaraṁ bhetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv24:2.4'><span class='pli'>patto sambodhimuttamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv24:3.1'><span class='pli'>Dhammacakkaṁ pavattente, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv24:3.2'><span class='pli'>Kakusandhe lokanāyake; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv24:3.3'><span class='pli'>Cattārīsakoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv24:3.4'><span class='pli'>Dhammābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv24:4.1'><span class='pli'>Antalikkhamhi ākāse, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv24:4.2'><span class='pli'>yamakaṁ katvā vikubbanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv24:4.3'><span class='pli'>Tiṁsakoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv24:4.4'><span class='pli'>bodhesi devamānuse. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv24:5.1'><span class='pli'>Naradevassa yakkhassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv24:5.2'><span class='pli'>catusaccappakāsane; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv24:5.3'><span class='pli'>Dhammābhisamayo tassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv24:5.4'><span class='pli'>gaṇanāto asaṅkhiyo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv24:6.1'><span class='pli'>Kakusandhassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv24:6.2'><span class='pli'>eko āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv24:6.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv24:6.4'><span class='pli'>santacittāna tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv24:7.1'><span class='pli'>Cattālīsasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv24:7.2'><span class='pli'>tadā āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv24:7.3'><span class='pli'>Dantabhūmimanuppattānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv24:7.4'><span class='pli'>āsavārigaṇakkhayā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv24:8.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv24:8.2'><span class='pli'>khemo nāmāsi khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv24:8.3'><span class='pli'>Tathāgate jinaputte, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv24:8.4'><span class='pli'>dānaṁ datvā anappakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv24:9.1'><span class='pli'>Pattañca cīvaraṁ datvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv24:9.2'><span class='pli'>añjanaṁ madhulaṭṭhikaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv24:9.3'><span class='pli'>Imetaṁ patthitaṁ sabbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv24:9.4'><span class='pli'>paṭiyādemi varaṁ varaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv24:10.1'><span class='pli'>Sopi maṁ buddho byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv24:10.2'><span class='pli'>kakusandho vināyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv24:10.3'><span class='pli'>‘Imamhi bhaddake kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv24:10.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv24:11.1'><span class='pli'>Ahu kapilavhayā rammā, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv24:11.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv24:11.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv24:12.1'><span class='pli'>Tassāpi vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv24:12.2'><span class='pli'>bhiyyo cittaṁ pasādayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv24:12.3'><span class='pli'>Uttariṁ vatamadhiṭṭhāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv24:12.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv24:13.1'><span class='pli'>Nagaraṁ khemāvatī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv24:13.2'><span class='pli'>khemo nāmāsahaṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv24:13.3'><span class='pli'>Sabbaññutaṁ gavesanto, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv24:13.4'><span class='pli'>pabbajiṁ tassa santike. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv24:14.1'><span class='pli'>Brāhmaṇo aggidatto ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv24:14.2'><span class='pli'>āsi buddhassa so pitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv24:14.3'><span class='pli'>Visākhā nāma janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv24:14.4'><span class='pli'>kakusandhassa satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv24:15.1'><span class='pli'>Vasate tattha kheme pure, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv24:15.2'><span class='pli'>sambuddhassa mahākulaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv24:15.3'><span class='pli'>Narānaṁ pavaraṁ seṭṭhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv24:15.4'><span class='pli'>jātimantaṁ mahāyasaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv24:16.1'><span class='pli'>Catuvassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv24:16.2'><span class='pli'>Agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv24:16.3'><span class='pli'>Kāmakāmavaṇṇakāmasuddhināmā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv24:16.4'><span class='pli'>Tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv24:17.1'><span class='pli'>Samattiṁsasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv24:17.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv24:17.3'><span class='pli'>Rocinī nāma sā nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv24:17.4'><span class='pli'>uttaro nāma atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv24:18.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv24:18.2'><span class='pli'>rathayānena nikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv24:18.3'><span class='pli'>Anūnaaṭṭhamāsāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv24:18.4'><span class='pli'>padhānaṁ padahī jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv24:19.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv24:19.2'><span class='pli'>kakusandho vināyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv24:19.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv24:19.4'><span class='pli'>migadāye naruttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv24:20.1'><span class='pli'>Vidhuro ca sañjīvo ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv24:20.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv24:20.3'><span class='pli'>Buddhijo nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv24:20.4'><span class='pli'>kakusandhassa satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv24:21.1'><span class='pli'>Sāmā ca campānāmā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv24:21.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv24:21.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv24:21.4'><span class='pli'>sirīsoti pavuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv24:22.1'><span class='pli'>Accuto ca sumano ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv24:22.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv24:22.3'><span class='pli'>Nandā ceva sunandā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv24:22.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv24:23.1'><span class='pli'>Cattālīsaratanāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv24:23.2'><span class='pli'>accuggato mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv24:23.3'><span class='pli'>Kanakappabhā niccharati, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv24:23.4'><span class='pli'>samantā dasayojanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv24:24.1'><span class='pli'>Cattālīsavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv24:24.2'><span class='pli'>Āyu tassa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv24:24.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv24:24.4'><span class='pli'>Tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv24:25.1'><span class='pli'>Dhammāpaṇaṁ pasāretvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv24:25.2'><span class='pli'>naranārīnaṁ sadevake; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv24:25.3'><span class='pli'>Naditvā sīhanādaṁva, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv24:25.4'><span class='pli'>nibbuto so sasāvako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv24:26.1'><span class='pli'>Aṭṭhaṅgavacanasampanno, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv24:26.2'><span class='pli'>acchiddāni nirantaraṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv24:26.3'><span class='pli'>Sabbaṁ tamantarahitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv24:26.4'><span class='pli'>nanu rittā sabbasaṅkhārā. </span></span></span></p>\n<p data-counter='9281'><span class='verse-line'><span class='segment' id='27.1' data-uid='bv24:27.1'><span class='pli'>Kakusandho jinavaro, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv24:27.2'><span class='pli'>Khemārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv24:27.3'><span class='pli'>Tatthevassa thūpavaro, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv24:27.4'><span class='pli'>Gāvutaṁ nabhamuggato”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='28.1' data-uid='bv24:28.1'><span class='pli'>Kakusandhassa bhagavato vaṁso dvāvīsatimo. </span></span></p></article>\n"
  },
  "bv25": {
    "previous": "bv24",
    "next": "bv26",
    "content": "<article id='bv25'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv25:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv25:0.2'><span class='pli'>25. Koṇāgamanabuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv25:1.1'><span class='pli'>“Kakusandhassa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv25:1.2'><span class='pli'>sambuddho dvipaduttamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv25:1.3'><span class='pli'>Koṇāgamano nāma jino, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv25:1.4'><span class='pli'>lokajeṭṭho narāsabho. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv25:2.1'><span class='pli'>Dasa dhamme pūrayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv25:2.2'><span class='pli'>kantāraṁ samatikkami; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv25:2.3'><span class='pli'>Pavāhiya malaṁ sabbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv25:2.4'><span class='pli'>patto sambodhimuttamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv25:3.1'><span class='pli'>Dhammacakkaṁ pavattente, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv25:3.2'><span class='pli'>koṇāgamananāyake; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv25:3.3'><span class='pli'>Tiṁsakoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv25:3.4'><span class='pli'>paṭhamābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv25:4.1'><span class='pli'>Pāṭihīraṁ karonte ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv25:4.2'><span class='pli'>paravādappamaddane; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv25:4.3'><span class='pli'>Vīsatikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv25:4.4'><span class='pli'>dutiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv25:5.1'><span class='pli'>Tato vikubbanaṁ katvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv25:5.2'><span class='pli'>jino devapuraṁ gato; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv25:5.3'><span class='pli'>Vasate tattha sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv25:5.4'><span class='pli'>silāya paṇḍukambale. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv25:6.1'><span class='pli'>Pakaraṇe satta desento, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv25:6.2'><span class='pli'>vassaṁ vasati so muni; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv25:6.3'><span class='pli'>Dasakoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv25:6.4'><span class='pli'>tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv25:7.1'><span class='pli'>Tassāpi devadevassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv25:7.2'><span class='pli'>eko āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv25:7.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv25:7.4'><span class='pli'>santacittāna tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv25:8.1'><span class='pli'>Tiṁsabhikkhusahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv25:8.2'><span class='pli'>tadā āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv25:8.3'><span class='pli'>Oghānamatikkantānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv25:8.4'><span class='pli'>bhijjitānañca maccuyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv25:9.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv25:9.2'><span class='pli'>pabbato nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv25:9.3'><span class='pli'>Mittāmaccehi sampanno, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv25:9.4'><span class='pli'>anantabalavāhano. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv25:10.1'><span class='pli'>Sambuddhadassanaṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv25:10.2'><span class='pli'>sutvā dhammamanuttaraṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv25:10.3'><span class='pli'>Nimantetvā sajinasaṅghaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv25:10.4'><span class='pli'>dānaṁ datvā yadicchakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv25:11.1'><span class='pli'>Pattuṇṇaṁ cīnapaṭṭañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv25:11.2'><span class='pli'>koseyyaṁ kambalampi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv25:11.3'><span class='pli'>Sovaṇṇapādukañceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv25:11.4'><span class='pli'>adāsiṁ satthusāvake. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv25:12.1'><span class='pli'>Sopi maṁ buddho byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv25:12.2'><span class='pli'>saṅghamajjhe nisīdiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv25:12.3'><span class='pli'>‘Imamhi bhaddake kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv25:12.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv25:13.1'><span class='pli'>Ahu kapilavhayā rammā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv25:13.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv25:13.3'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv25:14.1'><span class='pli'>Tassāpi vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv25:14.2'><span class='pli'>bhiyyo cittaṁ pasādayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv25:14.3'><span class='pli'>Uttariṁ vatamadhiṭṭhāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv25:14.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv25:15.1'><span class='pli'>Sabbaññutaṁ gavesanto, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv25:15.2'><span class='pli'>dānaṁ datvā naruttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv25:15.3'><span class='pli'>Ohāyāhaṁ mahārajjaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv25:15.4'><span class='pli'>pabbajiṁ jinasantike. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv25:16.1'><span class='pli'>Nagaraṁ sobhavatī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv25:16.2'><span class='pli'>sobho nāmāsi khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv25:16.3'><span class='pli'>Vasate tattha nagare, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv25:16.4'><span class='pli'>sambuddhassa mahākulaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv25:17.1'><span class='pli'>Brāhmaṇo yaññadatto ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv25:17.2'><span class='pli'>āsi buddhassa so pitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv25:17.3'><span class='pli'>Uttarā nāma janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv25:17.4'><span class='pli'>koṇāgamanassa satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv25:18.1'><span class='pli'>Tīṇi vassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv25:18.2'><span class='pli'>Agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv25:18.3'><span class='pli'>Tusitasantusitasantuṭṭhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv25:18.4'><span class='pli'>Tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv25:19.1'><span class='pli'>Anūnasoḷasasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv25:19.2'><span class='pli'>Nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv25:19.3'><span class='pli'>Rucigattā nāma nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv25:19.4'><span class='pli'>Satthavāho nāma atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv25:20.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv25:20.2'><span class='pli'>hatthiyānena nikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv25:20.3'><span class='pli'>Chamāsaṁ padhānacāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv25:20.4'><span class='pli'>acarī purisuttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv25:21.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv25:21.2'><span class='pli'>koṇāgamananāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv25:21.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv25:21.4'><span class='pli'>migadāye naruttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv25:22.1'><span class='pli'>Bhiyyaso uttaro nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv25:22.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv25:22.3'><span class='pli'>Sotthijo nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv25:22.4'><span class='pli'>koṇāgamanassa satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv25:23.1'><span class='pli'>Samuddā uttarā ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv25:23.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv25:23.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv25:23.4'><span class='pli'>udumbaroti pavuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv25:24.1'><span class='pli'>Uggo ca somadevo ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv25:24.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv25:24.3'><span class='pli'>Sīvalā ceva sāmā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv25:24.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv25:25.1'><span class='pli'>Uccattanena so buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv25:25.2'><span class='pli'>tiṁsahatthasamuggato; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv25:25.3'><span class='pli'>Ukkāmukhe yathā kambu, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv25:25.4'><span class='pli'>evaṁ raṁsīhi maṇḍito. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv25:26.1'><span class='pli'>Tiṁsavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv25:26.2'><span class='pli'>āyu buddhassa tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv25:26.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv25:26.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv25:27.1'><span class='pli'>Dhammacetiṁ samussetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv25:27.2'><span class='pli'>dhammadussavibhūsitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv25:27.3'><span class='pli'>Dhammapupphaguḷaṁ katvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv25:27.4'><span class='pli'>nibbuto so sasāvako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='bv25:28.1'><span class='pli'>Mahāvilāso tassa jano, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv25:28.2'><span class='pli'>siridhammappakāsano; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv25:28.3'><span class='pli'>Sabbaṁ tamantarahitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv25:28.4'><span class='pli'>nanu rittā sabbasaṅkhārā. </span></span></span></p>\n<p data-counter='9571'><span class='verse-line'><span class='segment' id='29.1' data-uid='bv25:29.1'><span class='pli'>Koṇāgamano sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='bv25:29.2'><span class='pli'>Pabbatārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='bv25:29.3'><span class='pli'>Dhātuvitthārikaṁ āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='bv25:29.4'><span class='pli'>Tesu tesu padesato”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='30.1' data-uid='bv25:30.1'><span class='pli'>Koṇāgamanassa bhagavato vaṁso tevīsatimo. </span></span></p></article>\n"
  },
  "bv26": {
    "previous": "bv25",
    "next": "bv27",
    "content": "<article id='bv26'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv26:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv26:0.2'><span class='pli'>26. Kassapabuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv26:1.1'><span class='pli'>“Koṇāgamanassa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv26:1.2'><span class='pli'>Sambuddho dvipaduttamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv26:1.3'><span class='pli'>Kassapo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv26:1.4'><span class='pli'>Dhammarājā pabhaṅkaro. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv26:2.1'><span class='pli'>Sañchaḍḍitaṁ kulamūlaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv26:2.2'><span class='pli'>bahvannapānabhojanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv26:2.3'><span class='pli'>Datvāna yācake dānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv26:2.4'><span class='pli'>pūrayitvāna mānasaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.5' data-uid='bv26:2.5'><span class='pli'>Usabhova āḷakaṁ bhetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.6' data-uid='bv26:2.6'><span class='pli'>patto sambodhimuttamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv26:3.1'><span class='pli'>Dhammacakkaṁ pavattente, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv26:3.2'><span class='pli'>kassape lokanāyake; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv26:3.3'><span class='pli'>Vīsakoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv26:3.4'><span class='pli'>paṭhamābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv26:4.1'><span class='pli'>Catumāsaṁ yadā buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv26:4.2'><span class='pli'>loke carati cārikaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv26:4.3'><span class='pli'>Dasakoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv26:4.4'><span class='pli'>dutiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv26:5.1'><span class='pli'>Yamakaṁ vikubbanaṁ katvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv26:5.2'><span class='pli'>ñāṇadhātuṁ pakittayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv26:5.3'><span class='pli'>Pañcakoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv26:5.4'><span class='pli'>tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv26:6.1'><span class='pli'>Sudhammā devapure ramme, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv26:6.2'><span class='pli'>tattha dhammaṁ pakittayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv26:6.3'><span class='pli'>Tīṇikoṭisahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv26:6.4'><span class='pli'>devānaṁ bodhayī jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv26:7.1'><span class='pli'>Naradevassa yakkhassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv26:7.2'><span class='pli'>apare dhammadesane; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv26:7.3'><span class='pli'>Etesānaṁ abhisamayā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv26:7.4'><span class='pli'>gaṇanāto asaṅkhiyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv26:8.1'><span class='pli'>Tassāpi devadevassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv26:8.2'><span class='pli'>eko āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv26:8.3'><span class='pli'>Khīṇāsavānaṁ vimalānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv26:8.4'><span class='pli'>santacittāna tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv26:9.1'><span class='pli'>Vīsabhikkhusahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv26:9.2'><span class='pli'>tadā āsi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv26:9.3'><span class='pli'>Atikkantabhavantānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv26:9.4'><span class='pli'>hirisīlena tādinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv26:10.1'><span class='pli'>Ahaṁ tadā māṇavako, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv26:10.2'><span class='pli'>jotipāloti vissuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv26:10.3'><span class='pli'>Ajjhāyako mantadharo, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv26:10.4'><span class='pli'>tiṇṇaṁ vedāna pāragū. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv26:11.1'><span class='pli'>Lakkhaṇe itihāse ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv26:11.2'><span class='pli'>sadhamme pāramiṁ gato; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv26:11.3'><span class='pli'>Bhūmantalikkhakusalo, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv26:11.4'><span class='pli'>katavijjo anāvayo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv26:12.1'><span class='pli'>Kassapassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv26:12.2'><span class='pli'>ghaṭīkāro nāmupaṭṭhāko; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv26:12.3'><span class='pli'>Sagāravo sappatisso, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv26:12.4'><span class='pli'>nibbuto tatiye phale. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv26:13.1'><span class='pli'>Ādāya maṁ ghaṭīkāro, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv26:13.2'><span class='pli'>upagañchi kassapaṁ jinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv26:13.3'><span class='pli'>Tassa dhammaṁ suṇitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv26:13.4'><span class='pli'>pabbajiṁ tassa santike. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv26:14.1'><span class='pli'>Āraddhavīriyo hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv26:14.2'><span class='pli'>vattāvattesu kovido; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv26:14.3'><span class='pli'>Na kvaci parihāyāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv26:14.4'><span class='pli'>pūresiṁ jinasāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv26:15.1'><span class='pli'>Yāvatā buddhabhaṇitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv26:15.2'><span class='pli'>navaṅgaṁ jinasāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv26:15.3'><span class='pli'>Sabbaṁ pariyāpuṇitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv26:15.4'><span class='pli'>sobhayiṁ jinasāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv26:16.1'><span class='pli'>Mama acchariyaṁ disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv26:16.2'><span class='pli'>sopi buddho viyākari; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv26:16.3'><span class='pli'>‘Imamhi bhaddake kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv26:16.4'><span class='pli'>ayaṁ buddho bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv26:17.1'><span class='pli'>Ahu kapilavhayā rammā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv26:17.2'><span class='pli'>nikkhamitvā tathāgato; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv26:17.3'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv26:17.4'><span class='pli'>katvā dukkarakārikaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv26:18.1'><span class='pli'>Ajapālarukkhamūle, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv26:18.2'><span class='pli'>nisīditvā tathāgato; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv26:18.3'><span class='pli'>Tattha pāyāsaṁ paggayha, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv26:18.4'><span class='pli'>nerañjaramupehiti. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv26:19.1'><span class='pli'>Nerañjarāya tīramhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv26:19.2'><span class='pli'>pāyāsaṁ paribhuñjiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv26:19.3'><span class='pli'>Paṭiyattavaramaggena, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv26:19.4'><span class='pli'>bodhimūlamupehiti. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv26:20.1'><span class='pli'>Tato padakkhiṇaṁ katvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv26:20.2'><span class='pli'>bodhimaṇḍaṁ anuttaro; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv26:20.3'><span class='pli'>Aparājitaṭṭhānamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv26:20.4'><span class='pli'>bodhipallaṅkamuttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.5' data-uid='bv26:20.5'><span class='pli'>Pallaṅkena nisīditvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.6' data-uid='bv26:20.6'><span class='pli'>bujjhissati mahāyaso. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv26:21.1'><span class='pli'>Imassa janikā mātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv26:21.2'><span class='pli'>māyā nāma bhavissati; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv26:21.3'><span class='pli'>Pitā suddhodano nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv26:21.4'><span class='pli'>ayaṁ hessati gotamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv26:22.1'><span class='pli'>Anāsavā vītarāgā, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv26:22.2'><span class='pli'>santacittā samāhitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv26:22.3'><span class='pli'>Kolito upatisso ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv26:22.4'><span class='pli'>aggā hessanti sāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.5' data-uid='bv26:22.5'><span class='pli'>Ānando nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.6' data-uid='bv26:22.6'><span class='pli'>upaṭṭhissatimaṁ jinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv26:23.1'><span class='pli'>Khemā uppalavaṇṇā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv26:23.2'><span class='pli'>aggā hessanti sāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv26:23.3'><span class='pli'>Anāsavā santacittā, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv26:23.4'><span class='pli'>vītarāgā samāhitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.5' data-uid='bv26:23.5'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.6' data-uid='bv26:23.6'><span class='pli'>assatthoti pavuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='bv26:24.1'><span class='pli'>Citto hatthāḷavako ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv26:24.2'><span class='pli'>aggā hessantupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv26:24.3'><span class='pli'>Nandamātā ca uttarā, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv26:24.4'><span class='pli'>aggā hessantupaṭṭhikā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='bv26:25.1'><span class='pli'>Idaṁ sutvāna vacanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='bv26:25.2'><span class='pli'>assamassa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='bv26:25.3'><span class='pli'>Āmoditā naramarū, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='bv26:25.4'><span class='pli'>buddhabījaṁ kira ayaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='bv26:26.1'><span class='pli'>Ukkuṭṭhisaddā pavattanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='bv26:26.2'><span class='pli'>apphoṭenti hasanti ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='bv26:26.3'><span class='pli'>Katañjalī namassanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='bv26:26.4'><span class='pli'>dasasahassī sadevakā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='bv26:27.1'><span class='pli'>‘Yadimassa lokanāthassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='bv26:27.2'><span class='pli'>virajjhissāma sāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='bv26:27.3'><span class='pli'>Anāgatamhi addhāne, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='bv26:27.4'><span class='pli'>hessāma sammukhā imaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='bv26:28.1'><span class='pli'>Yathā manussā nadiṁ tarantā, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='bv26:28.2'><span class='pli'>Paṭititthaṁ virajjhiya; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='bv26:28.3'><span class='pli'>Heṭṭhā titthe gahetvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='bv26:28.4'><span class='pli'>Uttaranti mahānadiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='bv26:29.1'><span class='pli'>Evamevaṁ mayaṁ sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='bv26:29.2'><span class='pli'>yadi muñcāmimaṁ jinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='bv26:29.3'><span class='pli'>Anāgatamhi addhāne, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='bv26:29.4'><span class='pli'>hessāma sammukhā imaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='bv26:30.1'><span class='pli'>Tassāpi vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='bv26:30.2'><span class='pli'>bhiyyo cittaṁ pasādayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='bv26:30.3'><span class='pli'>Uttariṁ vatamadhiṭṭhāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='bv26:30.4'><span class='pli'>dasapāramipūriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='bv26:31.1'><span class='pli'>Evamahaṁ saṁsaritvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='bv26:31.2'><span class='pli'>parivajjento anācaraṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='bv26:31.3'><span class='pli'>Dukkarañca kataṁ mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='bv26:31.4'><span class='pli'>bodhiyāyeva kāraṇā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='bv26:32.1'><span class='pli'>Nagaraṁ bārāṇasī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='bv26:32.2'><span class='pli'>kikī nāmāsi khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='bv26:32.3'><span class='pli'>Vasate tattha nagare, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='bv26:32.4'><span class='pli'>sambuddhassa mahākulaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='bv26:33.1'><span class='pli'>Brāhmaṇo brahmadattova, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='bv26:33.2'><span class='pli'>āsi buddhassa so pitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='bv26:33.3'><span class='pli'>Dhanavatī nāma janikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='bv26:33.4'><span class='pli'>kassapassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='34.1' data-uid='bv26:34.1'><span class='pli'>Duve vassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='bv26:34.2'><span class='pli'>agāraṁ ajjha so vasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='bv26:34.3'><span class='pli'>Haṁso yaso sirinando, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.4' data-uid='bv26:34.4'><span class='pli'>tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='35.1' data-uid='bv26:35.1'><span class='pli'>Tisoḷasasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.2' data-uid='bv26:35.2'><span class='pli'>Nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='35.3' data-uid='bv26:35.3'><span class='pli'>Sunandā nāma sā nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.4' data-uid='bv26:35.4'><span class='pli'>Vijitaseno nāma atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='36.1' data-uid='bv26:36.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.2' data-uid='bv26:36.2'><span class='pli'>pāsādenābhinikkhami; </span></span></span>\n<span class='verse-line'><span class='segment' id='36.3' data-uid='bv26:36.3'><span class='pli'>Sattāhaṁ padhānacāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.4' data-uid='bv26:36.4'><span class='pli'>acarī purisuttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='37.1' data-uid='bv26:37.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.2' data-uid='bv26:37.2'><span class='pli'>kassapo lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='37.3' data-uid='bv26:37.3'><span class='pli'>Vatti cakkaṁ mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.4' data-uid='bv26:37.4'><span class='pli'>migadāye naruttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='38.1' data-uid='bv26:38.1'><span class='pli'>Tisso ca bhāradvājo ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.2' data-uid='bv26:38.2'><span class='pli'>ahesuṁ aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='38.3' data-uid='bv26:38.3'><span class='pli'>Sabbamitto nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.4' data-uid='bv26:38.4'><span class='pli'>kassapassa mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='39.1' data-uid='bv26:39.1'><span class='pli'>Anuḷā uruvelā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.2' data-uid='bv26:39.2'><span class='pli'>ahesuṁ aggasāvikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='39.3' data-uid='bv26:39.3'><span class='pli'>Bodhi tassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.4' data-uid='bv26:39.4'><span class='pli'>nigrodhoti pavuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='40.1' data-uid='bv26:40.1'><span class='pli'>Sumaṅgalo ghaṭikāro ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.2' data-uid='bv26:40.2'><span class='pli'>ahesuṁ aggupaṭṭhakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='40.3' data-uid='bv26:40.3'><span class='pli'>Vicitasenā bhaddā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.4' data-uid='bv26:40.4'><span class='pli'>ahesuṁ aggupaṭṭhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='41.1' data-uid='bv26:41.1'><span class='pli'>Uccattanena so buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.2' data-uid='bv26:41.2'><span class='pli'>vīsatiratanuggato; </span></span></span>\n<span class='verse-line'><span class='segment' id='41.3' data-uid='bv26:41.3'><span class='pli'>Vijjulaṭṭhīva ākāse, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.4' data-uid='bv26:41.4'><span class='pli'>candova gahapūrito. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='42.1' data-uid='bv26:42.1'><span class='pli'>Vīsativassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.2' data-uid='bv26:42.2'><span class='pli'>āyu tassa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='42.3' data-uid='bv26:42.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.4' data-uid='bv26:42.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='43.1' data-uid='bv26:43.1'><span class='pli'>Dhammataḷākaṁ māpayitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.2' data-uid='bv26:43.2'><span class='pli'>sīlaṁ datvā vilepanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='43.3' data-uid='bv26:43.3'><span class='pli'>Dhammadussaṁ nivāsetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.4' data-uid='bv26:43.4'><span class='pli'>dhammamālaṁ vibhajjiya. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='44.1' data-uid='bv26:44.1'><span class='pli'>Dhammavimalamādāsaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.2' data-uid='bv26:44.2'><span class='pli'>ṭhapayitvā mahājane; </span></span></span>\n<span class='verse-line'><span class='segment' id='44.3' data-uid='bv26:44.3'><span class='pli'>Keci nibbānaṁ patthentā, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.4' data-uid='bv26:44.4'><span class='pli'>passantu me alaṅkaraṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='45.1' data-uid='bv26:45.1'><span class='pli'>Sīlakañcukaṁ datvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.2' data-uid='bv26:45.2'><span class='pli'>jhānakavacavammitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='45.3' data-uid='bv26:45.3'><span class='pli'>Dhammacammaṁ pārupitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.4' data-uid='bv26:45.4'><span class='pli'>datvā sannāhamuttamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='46.1' data-uid='bv26:46.1'><span class='pli'>Satiphalakaṁ datvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.2' data-uid='bv26:46.2'><span class='pli'>tikhiṇañāṇakuntimaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='46.3' data-uid='bv26:46.3'><span class='pli'>Dhammakhaggavaraṁ datvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.4' data-uid='bv26:46.4'><span class='pli'>sīlasaṁsaggamaddanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='47.1' data-uid='bv26:47.1'><span class='pli'>Tevijjābhūsanaṁ datvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.2' data-uid='bv26:47.2'><span class='pli'>āveḷaṁ caturo phale; </span></span></span>\n<span class='verse-line'><span class='segment' id='47.3' data-uid='bv26:47.3'><span class='pli'>Chaḷabhiññābharaṇaṁ datvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.4' data-uid='bv26:47.4'><span class='pli'>dhammapupphapiḷandhanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='48.1' data-uid='bv26:48.1'><span class='pli'>Saddhammapaṇḍaracchattaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.2' data-uid='bv26:48.2'><span class='pli'>datvā pāpanivāraṇaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='48.3' data-uid='bv26:48.3'><span class='pli'>Māpayitvābhayaṁ pupphaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.4' data-uid='bv26:48.4'><span class='pli'>nibbuto so sasāvako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='49.1' data-uid='bv26:49.1'><span class='pli'>Eso hi sammāsambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.2' data-uid='bv26:49.2'><span class='pli'>appameyyo durāsado; </span></span></span>\n<span class='verse-line'><span class='segment' id='49.3' data-uid='bv26:49.3'><span class='pli'>Eso hi dhammaratano, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.4' data-uid='bv26:49.4'><span class='pli'>svākkhāto ehipassiko. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='50.1' data-uid='bv26:50.1'><span class='pli'>Eso hi saṅgharatano, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.2' data-uid='bv26:50.2'><span class='pli'>suppaṭipanno anuttaro; </span></span></span>\n<span class='verse-line'><span class='segment' id='50.3' data-uid='bv26:50.3'><span class='pli'>Sabbaṁ tamantarahitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.4' data-uid='bv26:50.4'><span class='pli'>nanu rittā sabbasaṅkhārā. </span></span></span></p>\n<p data-counter='10081'><span class='verse-line'><span class='segment' id='51.1' data-uid='bv26:51.1'><span class='pli'>Mahākassapo jino satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.2' data-uid='bv26:51.2'><span class='pli'>Setabyārāmamhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='51.3' data-uid='bv26:51.3'><span class='pli'>Tatthevassa jinathūpo, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.4' data-uid='bv26:51.4'><span class='pli'>Yojanubbedhamuggato”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='52.1' data-uid='bv26:52.1'><span class='pli'>Kassapassa bhagavato vaṁso catuvīsatimo. </span></span></p></article>\n"
  },
  "bv27": {
    "previous": "bv26",
    "next": "bv28",
    "content": "<article id='bv27'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv27:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv27:0.2'><span class='pli'>27. Gotamabuddhavaṁsa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv27:1.1'><span class='pli'>“Ahametarahi sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv27:1.2'><span class='pli'>gotamo sakyavaḍḍhano; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv27:1.3'><span class='pli'>Padhānaṁ padahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv27:1.4'><span class='pli'>patto sambodhimuttamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv27:2.1'><span class='pli'>Brahmunā yācito santo, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv27:2.2'><span class='pli'>dhammacakkaṁ pavattayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv27:2.3'><span class='pli'>Aṭṭhārasannaṁ koṭīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv27:2.4'><span class='pli'>paṭhamābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv27:3.1'><span class='pli'>Tato parañca desente, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv27:3.2'><span class='pli'>naradevasamāgame; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv27:3.3'><span class='pli'>Gaṇanāya na vattabbo, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv27:3.4'><span class='pli'>dutiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv27:4.1'><span class='pli'>Idhevāhaṁ etarahi, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv27:4.2'><span class='pli'>ovadiṁ mama atrajaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv27:4.3'><span class='pli'>Gaṇanāya na vattabbo, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv27:4.4'><span class='pli'>tatiyābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv27:5.1'><span class='pli'>Ekosi sannipāto me, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv27:5.2'><span class='pli'>sāvakānaṁ mahesinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv27:5.3'><span class='pli'>Aḍḍhateḷasasatānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv27:5.4'><span class='pli'>bhikkhūnāsi samāgamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv27:6.1'><span class='pli'>Virocamāno vimalo, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv27:6.2'><span class='pli'>bhikkhusaṅghassa majjhago; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv27:6.3'><span class='pli'>Dadāmi patthitaṁ sabbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv27:6.4'><span class='pli'>maṇīva sabbakāmado. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv27:7.1'><span class='pli'>Phalamākaṅkhamānānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv27:7.2'><span class='pli'>bhavacchandajahesinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv27:7.3'><span class='pli'>Catusaccaṁ pakāsemi, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv27:7.4'><span class='pli'>anukampāya pāṇinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv27:8.1'><span class='pli'>Dasavīsasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv27:8.2'><span class='pli'>dhammābhisamayo ahu; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv27:8.3'><span class='pli'>Ekadvinnaṁ abhisamayo, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv27:8.4'><span class='pli'>gaṇanāto asaṅkhiyo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv27:9.1'><span class='pli'>Vitthārikaṁ bāhujaññaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv27:9.2'><span class='pli'>iddhaṁ phītaṁ suphullitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv27:9.3'><span class='pli'>Idha mayhaṁ sakyamunino, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv27:9.4'><span class='pli'>sāsanaṁ suvisodhitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv27:10.1'><span class='pli'>Anāsavā vītarāgā, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv27:10.2'><span class='pli'>santacittā samāhitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv27:10.3'><span class='pli'>Bhikkhūnekasatā sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv27:10.4'><span class='pli'>parivārenti maṁ sadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv27:11.1'><span class='pli'>Idāni ye etarahi, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv27:11.2'><span class='pli'>jahanti mānusaṁ bhavaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv27:11.3'><span class='pli'>Appattamānasā sekhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv27:11.4'><span class='pli'>te bhikkhū viññugarahitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv27:12.1'><span class='pli'>Ariyañca santhomayantā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv27:12.2'><span class='pli'>sadā dhammaratā janā; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv27:12.3'><span class='pli'>Bujjhissanti satimanto, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv27:12.4'><span class='pli'>saṁsārasaritaṁ gatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv27:13.1'><span class='pli'>Nagaraṁ kapilavatthu me, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv27:13.2'><span class='pli'>rājā suddhodano pitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv27:13.3'><span class='pli'>Mayhaṁ janettikā mātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv27:13.4'><span class='pli'>māyādevīti vuccati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv27:14.1'><span class='pli'>Ekūnatiṁsavassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv27:14.2'><span class='pli'>agāraṁ ajjhahaṁ vasiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv27:14.3'><span class='pli'>Rammo surammo subhako, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv27:14.4'><span class='pli'>tayo pāsādamuttamā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv27:15.1'><span class='pli'>Cattārīsasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv27:15.2'><span class='pli'>nāriyo samalaṅkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv27:15.3'><span class='pli'>Bhaddakañcanā nāma nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv27:15.4'><span class='pli'>rāhulo nāma atrajo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv27:16.1'><span class='pli'>Nimitte caturo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv27:16.2'><span class='pli'>assayānena nikkhamiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv27:16.3'><span class='pli'>Chabbassaṁ padhānacāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv27:16.4'><span class='pli'>acariṁ dukkaraṁ ahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv27:17.1'><span class='pli'>Bārāṇasiyaṁ isipatane, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv27:17.2'><span class='pli'>Cakkaṁ pavattitaṁ mayā; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv27:17.3'><span class='pli'>Ahaṁ gotamasambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv27:17.4'><span class='pli'>Saraṇaṁ sabbapāṇinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv27:18.1'><span class='pli'>Kolito upatisso ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv27:18.2'><span class='pli'>dve bhikkhū aggasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv27:18.3'><span class='pli'>Ānando nāmupaṭṭhāko, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv27:18.4'><span class='pli'>santikāvacaro mama; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.5' data-uid='bv27:18.5'><span class='pli'>Khemā uppalavaṇṇā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.6' data-uid='bv27:18.6'><span class='pli'>bhikkhunī aggasāvikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv27:19.1'><span class='pli'>Citto hatthāḷavako ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv27:19.2'><span class='pli'>aggupaṭṭhākupāsakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv27:19.3'><span class='pli'>Nandamātā ca uttarā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv27:19.4'><span class='pli'>aggupaṭṭhākupāsikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='bv27:20.1'><span class='pli'>Ahaṁ assatthamūlamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv27:20.2'><span class='pli'>patto sambodhimuttamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv27:20.3'><span class='pli'>Byāmappabhā sadā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv27:20.4'><span class='pli'>soḷasahatthamuggatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='bv27:21.1'><span class='pli'>Appaṁ vassasataṁ āyu, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='bv27:21.2'><span class='pli'>idānetarahi vijjati; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='bv27:21.3'><span class='pli'>Tāvatā tiṭṭhamānohaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='bv27:21.4'><span class='pli'>tāremi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='bv27:22.1'><span class='pli'>Ṭhapayitvāna dhammukkaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='bv27:22.2'><span class='pli'>pacchimaṁ janabodhanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='bv27:22.3'><span class='pli'>Ahampi nacirasseva, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='bv27:22.4'><span class='pli'>saddhiṁ sāvakasaṅghato; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.5' data-uid='bv27:22.5'><span class='pli'>Idheva parinibbissaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.6' data-uid='bv27:22.6'><span class='pli'>aggīvāhārasaṅkhayā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='bv27:23.1'><span class='pli'>Tāni ca atulatejāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='bv27:23.2'><span class='pli'>Imāni ca dasabalāni; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='bv27:23.3'><span class='pli'>Ayañca guṇadhāraṇo deho, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='bv27:23.4'><span class='pli'>Dvattiṁsavaralakkhaṇavicitto. </span></span></span></p>\n<p data-counter='10321'><span class='verse-line'><span class='segment' id='24.1' data-uid='bv27:24.1'><span class='pli'>Dasa disā pabhāsetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='bv27:24.2'><span class='pli'>Sataraṁsīva chappabhā; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='bv27:24.3'><span class='pli'>Sabbaṁ tamantarahissanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='bv27:24.4'><span class='pli'>Nanu rittā sabbasaṅkhārā”ti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='25.1' data-uid='bv27:25.1'><span class='pli'>Gotamassa bhagavato vaṁso pañcavīsatimo. </span></span></p></article>\n"
  },
  "bv28": {
    "previous": "bv27",
    "next": "bv29",
    "content": "<article id='bv28'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv28:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv28:0.2'><span class='pli'>28. Buddhapakiṇṇakakaṇḍa </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv28:1.1'><span class='pli'>Aparimeyyito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv28:1.2'><span class='pli'>caturo āsuṁ vināyakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv28:1.3'><span class='pli'>Taṇhaṅkaro medhaṅkaro, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv28:1.4'><span class='pli'>athopi saraṇaṅkaro; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.5' data-uid='bv28:1.5'><span class='pli'>Dīpaṅkaro ca sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.6' data-uid='bv28:1.6'><span class='pli'>ekakappamhi te jinā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv28:2.1'><span class='pli'>Dīpaṅkarassa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv28:2.2'><span class='pli'>koṇḍañño nāma nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv28:2.3'><span class='pli'>Ekova ekakappamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv28:2.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv28:3.1'><span class='pli'>Dīpaṅkarassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv28:3.2'><span class='pli'>koṇḍaññassa ca satthuno; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv28:3.3'><span class='pli'>Etesaṁ antarā kappā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv28:3.4'><span class='pli'>gaṇanāto asaṅkhiyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv28:4.1'><span class='pli'>Koṇḍaññassa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv28:4.2'><span class='pli'>maṅgalo nāma nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv28:4.3'><span class='pli'>Tesampi antarā kappā, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv28:4.4'><span class='pli'>gaṇanāto asaṅkhiyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv28:5.1'><span class='pli'>Maṅgalo ca sumano ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv28:5.2'><span class='pli'>revato sobhito muni; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv28:5.3'><span class='pli'>Tepi buddhā ekakappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv28:5.4'><span class='pli'>cakkhumanto pabhaṅkarā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv28:6.1'><span class='pli'>Sobhitassa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv28:6.2'><span class='pli'>anomadassī mahāyaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv28:6.3'><span class='pli'>Tesampi antarā kappā, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv28:6.4'><span class='pli'>gaṇanāto asaṅkhiyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv28:7.1'><span class='pli'>Anomadassī padumo, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv28:7.2'><span class='pli'>nārado cāpi nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv28:7.3'><span class='pli'>Tepi buddhā ekakappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv28:7.4'><span class='pli'>tamantakārakā munī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv28:8.1'><span class='pli'>Nāradassa aparena, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv28:8.2'><span class='pli'>Padumuttaro nāma nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv28:8.3'><span class='pli'>Ekakappamhi uppanno, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv28:8.4'><span class='pli'>Tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv28:9.1'><span class='pli'>Nāradassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv28:9.2'><span class='pli'>padumuttarassa satthuno; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv28:9.3'><span class='pli'>Tesampi antarā kappā, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv28:9.4'><span class='pli'>gaṇanāto asaṅkhiyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv28:10.1'><span class='pli'>Kappasatasahassamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv28:10.2'><span class='pli'>eko āsi mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv28:10.3'><span class='pli'>Padumuttaro lokavidū, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv28:10.4'><span class='pli'>āhutīnaṁ paṭiggaho. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv28:11.1'><span class='pli'>Tiṁsakappasahassamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv28:11.2'><span class='pli'>duve āsuṁ vināyakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv28:11.3'><span class='pli'>Sumedho ca sujāto ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv28:11.4'><span class='pli'>orato padumuttarā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv28:12.1'><span class='pli'>Aṭṭhārase kappasate, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv28:12.2'><span class='pli'>tayo āsuṁ vināyakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv28:12.3'><span class='pli'>Piyadassī atthadassī, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv28:12.4'><span class='pli'>dhammadassī ca nāyakā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv28:13.1'><span class='pli'>Orato ca sujātassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv28:13.2'><span class='pli'>sambuddhā dvipaduttamā; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv28:13.3'><span class='pli'>Ekakappamhi te buddhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv28:13.4'><span class='pli'>loke appaṭipuggalā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv28:14.1'><span class='pli'>Catunnavutito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv28:14.2'><span class='pli'>eko āsi mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv28:14.3'><span class='pli'>Siddhattho so lokavidū, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv28:14.4'><span class='pli'>sallakatto anuttaro. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv28:15.1'><span class='pli'>Dvenavute ito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv28:15.2'><span class='pli'>duve āsuṁ vināyakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv28:15.3'><span class='pli'>Tisso phusso ca sambuddhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv28:15.4'><span class='pli'>asamā appaṭipuggalā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv28:16.1'><span class='pli'>Ekanavutito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv28:16.2'><span class='pli'>vipassī nāma nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv28:16.3'><span class='pli'>Sopi buddho kāruṇiko, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv28:16.4'><span class='pli'>satte mocesi bandhanā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv28:17.1'><span class='pli'>Ekatiṁse ito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv28:17.2'><span class='pli'>duve āsuṁ vināyakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv28:17.3'><span class='pli'>Sikhī ca vessabhū ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv28:17.4'><span class='pli'>asamā appaṭipuggalā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='bv28:18.1'><span class='pli'>Imamhi bhaddake kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv28:18.2'><span class='pli'>tayo āsuṁ vināyakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv28:18.3'><span class='pli'>Kakusandho koṇāgamano, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv28:18.4'><span class='pli'>kassapo cāpi nāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='bv28:19.1'><span class='pli'>Ahametarahi sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='bv28:19.2'><span class='pli'>metteyyo cāpi hessati; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='bv28:19.3'><span class='pli'>Etepime pañca buddhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='bv28:19.4'><span class='pli'>dhīrā lokānukampakā. </span></span></span></p>\n<p data-counter='10521'><span class='verse-line'><span class='segment' id='20.1' data-uid='bv28:20.1'><span class='pli'>Etesaṁ dhammarājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='bv28:20.2'><span class='pli'>Aññesaṁnekakoṭinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='bv28:20.3'><span class='pli'>Ācikkhitvāna taṁ maggaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='bv28:20.4'><span class='pli'>Nibbutā te sasāvakāti. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='21.1' data-uid='bv28:21.1'><span class='pli'>Buddhapakiṇṇakakaṇḍaṁ niṭṭhitaṁ. </span></span></p></article>\n"
  },
  "bv29": {
    "previous": "bv28",
    "next": null,
    "content": "<article id='bv29'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='bv29:0.1'><span class='pli'>Buddhavaṁsa </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='bv29:0.2'><span class='pli'>29. Dhātubhājanīyakathā </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='bv29:1.1'><span class='pli'>Mahāgotamo jinavaro, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='bv29:1.2'><span class='pli'>kusināramhi nibbuto; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='bv29:1.3'><span class='pli'>Dhātuvitthārikaṁ āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='bv29:1.4'><span class='pli'>tesu tesu padesato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='bv29:2.1'><span class='pli'>Eko ajātasattussa, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='bv29:2.2'><span class='pli'>eko vesāliyā pure; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='bv29:2.3'><span class='pli'>Eko kapilavatthusmiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='bv29:2.4'><span class='pli'>eko ca allakappake. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='bv29:3.1'><span class='pli'>Eko ca rāmagāmamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='bv29:3.2'><span class='pli'>eko ca veṭhadīpake; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='bv29:3.3'><span class='pli'>Eko pāveyyake malle, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='bv29:3.4'><span class='pli'>eko ca kosinārake. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='bv29:4.1'><span class='pli'>Kumbhassa thūpaṁ kāresi, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='bv29:4.2'><span class='pli'>brāhmaṇo doṇasavhayo; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='bv29:4.3'><span class='pli'>Aṅgārathūpaṁ kāresuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='bv29:4.4'><span class='pli'>moriyā tuṭṭhamānasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='bv29:5.1'><span class='pli'>Aṭṭha sārīrikā thūpā, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='bv29:5.2'><span class='pli'>navamo kumbhacetiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='bv29:5.3'><span class='pli'>Aṅgārathūpo dasamo, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='bv29:5.4'><span class='pli'>tadāyeva patiṭṭhito. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='bv29:6.1'><span class='pli'>Uṇhīsaṁ catasso dāṭhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='bv29:6.2'><span class='pli'>akkhakā dve ca dhātuyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='bv29:6.3'><span class='pli'>Asambhinnā imā satta, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='bv29:6.4'><span class='pli'>sesā bhinnāva dhātuyo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='bv29:7.1'><span class='pli'>Mahantā muggamattā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='bv29:7.2'><span class='pli'>majjhimā bhinnataṇḍulā; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='bv29:7.3'><span class='pli'>Khuddakā sāsapamattā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='bv29:7.4'><span class='pli'>nānāvaṇṇā ca dhātuyo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='bv29:8.1'><span class='pli'>Mahantā suvaṇṇavaṇṇā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='bv29:8.2'><span class='pli'>muttavaṇṇā ca majjhimā; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='bv29:8.3'><span class='pli'>Khuddakā makulavaṇṇā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='bv29:8.4'><span class='pli'>soḷasadoṇamattikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='bv29:9.1'><span class='pli'>Mahantā pañca nāḷiyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='bv29:9.2'><span class='pli'>nāḷiyo pañca majjhimā; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='bv29:9.3'><span class='pli'>Khuddakā cha nāḷī ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='bv29:9.4'><span class='pli'>etā sabbāpi dhātuyo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='bv29:10.1'><span class='pli'>Uṇhīsaṁ sīhaḷe dīpe, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='bv29:10.2'><span class='pli'>brahmaloke ca vāmakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='bv29:10.3'><span class='pli'>Sīhaḷe dakkhiṇakkhañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='bv29:10.4'><span class='pli'>sabbāpetā patiṭṭhitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='bv29:11.1'><span class='pli'>Ekā dāṭhā tidasapure, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='bv29:11.2'><span class='pli'>ekā nāgapure ahu; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='bv29:11.3'><span class='pli'>Ekā gandhāravisaye, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='bv29:11.4'><span class='pli'>ekā kaliṅgarājino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='bv29:12.1'><span class='pli'>Cattālīsasamā dantā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='bv29:12.2'><span class='pli'>kesā lomā ca sabbaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='bv29:12.3'><span class='pli'>Devā hariṁsu ekekaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='bv29:12.4'><span class='pli'>cakkavāḷaparamparā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='bv29:13.1'><span class='pli'>Vajirāyaṁ bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='bv29:13.2'><span class='pli'>Patto daṇḍañca cīvaraṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='bv29:13.3'><span class='pli'>Nivāsanaṁ kulaghare, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='bv29:13.4'><span class='pli'>Paccattharaṇaṁ kapilavhaye. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='bv29:14.1'><span class='pli'>Pāṭaliputtapuramhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='bv29:14.2'><span class='pli'>karaṇaṁ kāyabandhanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='bv29:14.3'><span class='pli'>Campāyudakasāṭiyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='bv29:14.4'><span class='pli'>uṇṇalomañca kosale. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='bv29:15.1'><span class='pli'>Kāsāvaṁ brahmaloke ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='bv29:15.2'><span class='pli'>veṭhanaṁ tidase pure; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='bv29:15.3'><span class='pli'>Nisīdanaṁ avantīsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='bv29:15.4'><span class='pli'>raṭṭhe attharaṇaṁ tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='bv29:16.1'><span class='pli'>Araṇī ca mithilāyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='bv29:16.2'><span class='pli'>videhe parisāvanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='bv29:16.3'><span class='pli'>Vāsi sūcigharañcāpi, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='bv29:16.4'><span class='pli'>indapatthapure tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='bv29:17.1'><span class='pli'>Parikkhārā avasesā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='bv29:17.2'><span class='pli'>janapade aparantake; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='bv29:17.3'><span class='pli'>Paribhuttāni muninā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='bv29:17.4'><span class='pli'>akaṁsu manujā tadā. </span></span></span></p>\n<p data-counter='10701'><span class='verse-line'><span class='segment' id='18.1' data-uid='bv29:18.1'><span class='pli'>Dhātuvitthārikaṁ āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='bv29:18.2'><span class='pli'>Gotamassa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='bv29:18.3'><span class='pli'>Pāṇīnaṁ anukampāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='bv29:18.4'><span class='pli'>Ahu porāṇikaṁ tadāti. </span></span></span></p></blockquote></article>\n"
  }
});
