window.SUTTA_DB = window.SUTTA_DB || {};
Object.assign(window.SUTTA_DB, {
  "dhp1-20": {
    "previous": null,
    "next": "dhp21-32",
    "content": "<section class='range'  id='dhp1-20'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp1:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp1:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 1–20 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp1:0.3'><span class='pli'>Yamakavagga </span> <span class='eng'>1. Pairs </span></span></h1></header>\n<article id='dhp1'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp1:0.4'><span class='pli'>Cakkhupālattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp1:1'><span class='pli'>Manopubbaṅgamā dhammā, </span> <span class='eng'>Intention shapes experiences; </span> <span class='comment-marker' data-comment='<i lang=&#39;pi&#39; translate=&#39;no&#39;>Mano</i> here has its usual role as the active aspect of mind, effectively meaning <i lang=&#39;pi&#39; translate=&#39;no&#39;>kamma</i>. Translating as “mind” obscures the ethical reading in favor of an idealistic one, which given the remainder of the verse is surely not justified. '>*</span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp1:2'><span class='pli'>manoseṭṭhā manomayā; </span> <span class='eng'>intention is first, they’re made by intention. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp1:3'><span class='pli'>Manasā ce paduṭṭhena, </span> <span class='eng'>If with corrupt intent </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp1:4'><span class='pli'>bhāsati vā karoti vā; </span> <span class='eng'>you speak or act, </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp1:5'><span class='pli'>Tato naṁ dukkhamanveti, </span> <span class='eng'>suffering follows you, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp1:6'><span class='pli'>cakkaṁva vahato padaṁ. </span> <span class='eng'>like a wheel, the ox’s foot. </span></span></span></p></blockquote></article>\n<article id='dhp2'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp2:0'><span class='pli'>Maṭṭhakuṇḍalīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp2:1'><span class='pli'>Manopubbaṅgamā dhammā, </span> <span class='eng'>Intention shapes experiences; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp2:2'><span class='pli'>manoseṭṭhā manomayā; </span> <span class='eng'>intention is first, they’re made by intention. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp2:3'><span class='pli'>Manasā ce pasannena, </span> <span class='eng'>If with pure intent </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp2:4'><span class='pli'>bhāsati vā karoti vā; </span> <span class='eng'>you speak or act, </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp2:5'><span class='pli'>Tato naṁ sukhamanveti, </span> <span class='eng'>happiness follows you </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp2:6'><span class='pli'>chāyāva anapāyinī. </span> <span class='eng'>like a shadow that never leaves. </span></span></span></p></blockquote></article>\n<article id='dhp3'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp3:0'><span class='pli'>Tissattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp3:1'><span class='pli'>Akkocchi maṁ avadhi maṁ, </span> <span class='eng'>“They abused me, they hit me! </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp3:2'><span class='pli'>ajini maṁ ahāsi me; </span> <span class='eng'>They beat me, they robbed me!” </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp3:3'><span class='pli'>Ye ca taṁ upanayhanti, </span> <span class='eng'>For those who bear such a grudge, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp3:4'><span class='pli'>veraṁ tesaṁ na sammati. </span> <span class='eng'>hatred is never laid to rest. </span></span></span></p></blockquote></article>\n<article id='dhp4'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp4:1'><span class='pli'>Akkocchi maṁ avadhi maṁ, </span> <span class='eng'>“They abused me, they hit me! </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp4:2'><span class='pli'>ajini maṁ ahāsi me; </span> <span class='eng'>They beat me, they robbed me!” </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp4:3'><span class='pli'>Ye ca taṁ nupanayhanti, </span> <span class='eng'>For those who bear no such grudge, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp4:4'><span class='pli'>veraṁ tesūpasammati. </span> <span class='eng'>hatred is laid to rest. </span></span></span></p></blockquote></article>\n<article id='dhp5'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp5:0'><span class='pli'>Kāḷayakkhinīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp5:1'><span class='pli'>Na hi verena verāni, </span> <span class='eng'>For never is hatred </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp5:2'><span class='pli'>sammantīdha kudācanaṁ; </span> <span class='eng'>laid to rest by hate, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp5:3'><span class='pli'>Averena ca sammanti, </span> <span class='eng'>it’s laid to rest by love: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp5:4'><span class='pli'>esa dhammo sanantano. </span> <span class='eng'>this is an ancient teaching. </span></span></span></p></blockquote></article>\n<article id='dhp6'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp6:0'><span class='pli'>Kosambakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp6:1'><span class='pli'>Pare ca na vijānanti, </span> <span class='eng'>When others do not understand, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp6:2'><span class='pli'>mayamettha yamāmase; </span> <span class='eng'>let us, who do understand this, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp6:3'><span class='pli'>Ye ca tattha vijānanti, </span> <span class='eng'>restrain ourselves in this regard; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp6:4'><span class='pli'>tato sammanti medhagā. </span> <span class='eng'>for that is how conflicts are laid to rest. </span></span></span></p></blockquote></article>\n<article id='dhp7'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp7:0'><span class='pli'>Mahākāḷattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp7:1'><span class='pli'>Subhānupassiṁ viharantaṁ, </span> <span class='eng'>Those who contemplate the beautiful, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp7:2'><span class='pli'>indriyesu asaṁvutaṁ; </span> <span class='eng'>their faculties unrestrained, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp7:3'><span class='pli'>Bhojanamhi cāmattaññuṁ, </span> <span class='eng'>immoderate in eating, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp7:4'><span class='pli'>kusītaṁ hīnavīriyaṁ; </span> <span class='eng'>lazy, lacking energy: </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp7:5'><span class='pli'>Taṁ ve pasahati māro, </span> <span class='eng'>Māra strikes them down </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp7:6'><span class='pli'>vāto rukkhaṁva dubbalaṁ. </span> <span class='eng'>like the wind, a feeble tree. </span></span></span></p></blockquote></article>\n<article id='dhp8'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp8:1'><span class='pli'>Asubhānupassiṁ viharantaṁ, </span> <span class='eng'>Those who contemplate the ugly, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp8:2'><span class='pli'>Indriyesu susaṁvutaṁ; </span> <span class='eng'>their faculties well-restrained, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp8:3'><span class='pli'>Bhojanamhi ca mattaññuṁ, </span> <span class='eng'>eating in moderation, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp8:4'><span class='pli'>Saddhaṁ āraddhavīriyaṁ; </span> <span class='eng'>faithful and energetic: </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp8:5'><span class='pli'>Taṁ ve nappasahati māro, </span> <span class='eng'>Māra cannot strike them down, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp8:6'><span class='pli'>Vāto selaṁva pabbataṁ. </span> <span class='eng'>like the wind, a rocky mountain. </span></span></span></p></blockquote></article>\n<article id='dhp9'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp9:0'><span class='pli'>Devadattavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp9:1'><span class='pli'>Anikkasāvo kāsāvaṁ, </span> <span class='eng'>One who, not free of stains themselves, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp9:2'><span class='pli'>yo vatthaṁ paridahissati; </span> <span class='eng'>would wear the robe stained in ocher, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp9:3'><span class='pli'>Apeto damasaccena, </span> <span class='eng'>bereft of self-control and of truth: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp9:4'><span class='pli'>na so kāsāvamarahati. </span> <span class='eng'>they are not worthy of the ocher robe. </span></span></span></p></blockquote></article>\n<article id='dhp10'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp10:1'><span class='pli'>Yo ca vantakasāvassa, </span> <span class='eng'>One who’s purged all their stains, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp10:2'><span class='pli'>sīlesu susamāhito; </span> <span class='eng'>steady in ethics, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp10:3'><span class='pli'>Upeto damasaccena, </span> <span class='eng'>possessed of self-control and of truth, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp10:4'><span class='pli'>sa ve kāsāvamarahati. </span> <span class='eng'>they are truly worthy of the ocher robe. </span></span></span></p></blockquote></article>\n<article id='dhp11'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp11:0'><span class='pli'>Sāriputtattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp11:1'><span class='pli'>Asāre sāramatino, </span> <span class='eng'>Thinking the inessential is essential, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp11:2'><span class='pli'>sāre cāsāradassino; </span> <span class='eng'>seeing the essential as inessential; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp11:3'><span class='pli'>Te sāraṁ nādhigacchanti, </span> <span class='eng'>they don’t realize the essential, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp11:4'><span class='pli'>micchāsaṅkappagocarā. </span> <span class='eng'>for wrong thoughts are their habitat. </span></span></span></p></blockquote></article>\n<article id='dhp12'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp12:1'><span class='pli'>Sārañca sārato ñatvā, </span> <span class='eng'>Having known the essential as essential, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp12:2'><span class='pli'>asārañca asārato; </span> <span class='eng'>and the inessential as inessential; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp12:3'><span class='pli'>Te sāraṁ adhigacchanti, </span> <span class='eng'>they realize the essential, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp12:4'><span class='pli'>sammāsaṅkappagocarā. </span> <span class='eng'>for right thoughts are their habitat. </span></span></span></p></blockquote></article>\n<article id='dhp13'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp13:0'><span class='pli'>Nandattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp13:1'><span class='pli'>Yathā agāraṁ ducchannaṁ, </span> <span class='eng'>Just as rain seeps into </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp13:2'><span class='pli'>vuṭṭhī samativijjhati; </span> <span class='eng'>a poorly roofed house, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp13:3'><span class='pli'>Evaṁ abhāvitaṁ cittaṁ, </span> <span class='eng'>lust seeps into </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp13:4'><span class='pli'>rāgo samativijjhati. </span> <span class='eng'>an undeveloped mind. </span></span></span></p></blockquote></article>\n<article id='dhp14'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp14:1'><span class='pli'>Yathā agāraṁ suchannaṁ, </span> <span class='eng'>Just as rain doesn’t seep into </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp14:2'><span class='pli'>vuṭṭhī na samativijjhati; </span> <span class='eng'>a well roofed house, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp14:3'><span class='pli'>Evaṁ subhāvitaṁ cittaṁ, </span> <span class='eng'>lust doesn’t seep into </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp14:4'><span class='pli'>rāgo na samativijjhati. </span> <span class='eng'>a well developed mind. </span></span></span></p></blockquote></article>\n<article id='dhp15'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp15:0'><span class='pli'>Cundasūkarikavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp15:1'><span class='pli'>Idha socati pecca socati, </span> <span class='eng'>Here they grieve, hereafter they grieve, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp15:2'><span class='pli'>Pāpakārī ubhayattha socati; </span> <span class='eng'>an evildoer grieves in both places. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp15:3'><span class='pli'>So socati so vihaññati, </span> <span class='eng'>They grieve and fret, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp15:4'><span class='pli'>Disvā kammakiliṭṭhamattano. </span> <span class='eng'>seeing their own corrupt deeds. </span></span></span></p></blockquote></article>\n<article id='dhp16'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp16:0'><span class='pli'>Dhammikaupāsakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp16:1'><span class='pli'>Idha modati pecca modati, </span> <span class='eng'>Here they rejoice, hereafter they rejoice, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp16:2'><span class='pli'>Katapuñño ubhayattha modati; </span> <span class='eng'>one who does good rejoices in both places. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp16:3'><span class='pli'>So modati so pamodati, </span> <span class='eng'>They rejoice and celebrate, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp16:4'><span class='pli'>Disvā kammavisuddhimattano. </span> <span class='eng'>seeing their own pure deeds. </span></span></span></p></blockquote></article>\n<article id='dhp17'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp17:0'><span class='pli'>Devadattavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp17:1'><span class='pli'>Idha tappati pecca tappati, </span> <span class='eng'>Here they’re tormented, <j>hereafter they’re tormented, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp17:2'><span class='pli'>Pāpakārī ubhayattha tappati; </span> <span class='eng'>an evildoer is tormented in both places. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp17:3'><span class='pli'>“Pāpaṁ me katan”ti tappati, </span> <span class='eng'>They’re tormented <j>thinking of bad things they’ve done; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp17:4'><span class='pli'>Bhiyyo tappati duggatiṁ gato. </span> <span class='eng'>when gone to a bad place, <j>they’re tormented all the more. </span></span></span></p></blockquote></article>\n<article id='dhp18'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp18:0'><span class='pli'>Sumanadevīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp18:1'><span class='pli'>Idha nandati pecca nandati, </span> <span class='eng'>Here they delight, hereafter they delight, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp18:2'><span class='pli'>Katapuñño ubhayattha nandati; </span> <span class='eng'>one who does good delights in both places. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp18:3'><span class='pli'>“Puññaṁ me katan”ti nandati, </span> <span class='eng'>They delight thinking of good things they’ve done; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp18:4'><span class='pli'>Bhiyyo nandati suggatiṁ gato. </span> <span class='eng'>when gone to a good place, they delight all the more. </span></span></span></p></blockquote></article>\n<article id='dhp19'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp19:0'><span class='pli'>Dvesahāyakabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp19:1'><span class='pli'>Bahumpi ce saṁhita bhāsamāno, </span> <span class='eng'>Much though they may recite scripture, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp19:2'><span class='pli'>Na takkaro hoti naro pamatto; </span> <span class='eng'>if a negligent person does not apply them, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp19:3'><span class='pli'>Gopova gāvo gaṇayaṁ paresaṁ, </span> <span class='eng'>then, like a cowherd who counts the cattle of others, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp19:4'><span class='pli'>Na bhāgavā sāmaññassa hoti. </span> <span class='eng'>they miss out on the blessings of the ascetic life. </span></span></span></p></blockquote></article>\n<article id='dhp20'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp20:1'><span class='pli'>Appampi ce saṁhita bhāsamāno, </span> <span class='eng'>Little though they may recite scripture, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp20:2'><span class='pli'>Dhammassa hoti anudhammacārī; </span> <span class='eng'>if they live in line with the teaching, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp20:3'><span class='pli'>Rāgañca dosañca pahāya mohaṁ, </span> <span class='eng'>having given up greed, hate, and delusion, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp20:4'><span class='pli'>Sammappajāno suvimuttacitto; </span> <span class='eng'>with deep understanding and heart well freed, </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp20:5'><span class='pli'>Anupādiyāno idha vā huraṁ vā, </span> <span class='eng'>not grasping to this life or the next, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp20:6'><span class='pli'>Sa bhāgavā sāmaññassa hoti. </span> <span class='eng'>they share in the blessings of the ascetic life. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='7' data-uid='dhp20:7'><span class='pli'>Yamakavaggo paṭhamo. </span></span></p></article></section>\n",
    "acronym": "Dhp 1–20",
    "translated_title": "1. Pairs",
    "original_title": "Yamakavagga"
  },
  "dhp21-32": {
    "previous": "dhp1-20",
    "next": "dhp33-43",
    "content": "<section class='range'  id='dhp21-32'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp21:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp21:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 21–32 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp21:0.3'><span class='pli'>Appamādavagga </span> <span class='eng'>2. Diligence </span></span></h1></header>\n<article id='dhp21'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp21:0.4'><span class='pli'>Sāmāvatīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp21:1'><span class='pli'>Appamādo amatapadaṁ, </span> <span class='eng'>Heedfulness is the state free of death; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp21:2'><span class='pli'>pamādo maccuno padaṁ; </span> <span class='eng'>heedlessness is the state of death. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp21:3'><span class='pli'>Appamattā na mīyanti, </span> <span class='eng'>The heedful do not die, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp21:4'><span class='pli'>ye pamattā yathā matā. </span> <span class='eng'>while the heedless are like the dead. </span></span></span></p></blockquote></article>\n<article id='dhp22'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp22:1'><span class='pli'>Evaṁ visesato ñatvā, </span> <span class='eng'>Understanding this distinction </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp22:2'><span class='pli'>appamādamhi paṇḍitā; </span> <span class='eng'>when it comes to heedfulness, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp22:3'><span class='pli'>Appamāde pamodanti, </span> <span class='eng'>the astute rejoice in heedfulness, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp22:4'><span class='pli'>ariyānaṁ gocare ratā. </span> <span class='eng'>happy in the noble ones’ domain. </span></span></span></p></blockquote></article>\n<article id='dhp23'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp23:1'><span class='pli'>Te jhāyino sātatikā, </span> <span class='eng'>They who regularly meditate, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp23:2'><span class='pli'>niccaṁ daḷhaparakkamā; </span> <span class='eng'>always staunchly vigorous; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp23:3'><span class='pli'>Phusanti dhīrā nibbānaṁ, </span> <span class='eng'>the attentive realize extinguishment, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp23:4'><span class='pli'>yogakkhemaṁ anuttaraṁ. </span> <span class='eng'>the supreme sanctuary from the yoke. </span></span></span></p></blockquote></article>\n<article id='dhp24'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp24:0'><span class='pli'>Kumbaghosakaseṭṭhivatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp24:1'><span class='pli'>Uṭṭhānavato satīmato, </span> <span class='eng'>For the hard-working and mindful, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp24:2'><span class='pli'>Sucikammassa nisammakārino; </span> <span class='eng'>pure of deed and attentive, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp24:3'><span class='pli'>Saññatassa dhammajīvino, </span> <span class='eng'>restrained, living righteously, and diligent, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp24:4'><span class='pli'>Appamattassa yasobhivaḍḍhati. </span> <span class='eng'>their reputation only grows. </span></span></span></p></blockquote></article>\n<article id='dhp25'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp25:0'><span class='pli'>Cūḷapanthakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp25:1'><span class='pli'>Uṭṭhānenappamādena, </span> <span class='eng'>By hard work and diligence, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp25:2'><span class='pli'>saṁyamena damena ca; </span> <span class='eng'>by restraint and by self-control, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp25:3'><span class='pli'>Dīpaṁ kayirātha medhāvī, </span> <span class='eng'>a smart person would build an island </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp25:4'><span class='pli'>yaṁ ogho nābhikīrati. </span> <span class='eng'>that the floods cannot overflow. </span></span></span></p></blockquote></article>\n<article id='dhp26'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp26:0'><span class='pli'>Bālanakkhattasaṅghuṭṭhavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp26:1'><span class='pli'>Pamādamanuyuñjanti, </span> <span class='eng'>Fools and simpletons </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp26:2'><span class='pli'>bālā dummedhino janā; </span> <span class='eng'>devote themselves to negligence. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp26:3'><span class='pli'>Appamādañca medhāvī, </span> <span class='eng'>But the wise protect diligence </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp26:4'><span class='pli'>dhanaṁ seṭṭhaṁva rakkhati. </span> <span class='eng'>as their best treasure. </span></span></span></p></blockquote></article>\n<article id='dhp27'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp27:1'><span class='pli'>Mā pamādamanuyuñjetha, </span> <span class='eng'>Don’t devote yourself to negligence, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp27:2'><span class='pli'>mā kāmaratisanthavaṁ; </span> <span class='eng'>or delight in erotic intimacy. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp27:3'><span class='pli'>Appamatto hi jhāyanto, </span> <span class='eng'>For if you’re diligent and meditate, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp27:4'><span class='pli'>pappoti vipulaṁ sukhaṁ. </span> <span class='eng'>you’ll attain abundant happiness. </span></span></span></p></blockquote></article>\n<article id='dhp28'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp28:0'><span class='pli'>Mahākassapattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp28:1'><span class='pli'>Pamādaṁ appamādena, </span> <span class='eng'>When the astute dispel negligence </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp28:2'><span class='pli'>yadā nudati paṇḍito; </span> <span class='eng'>by means of diligence, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp28:3'><span class='pli'>Paññāpāsādamāruyha, </span> <span class='eng'>ascending the palace of wisdom, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp28:4'><span class='pli'>asoko sokiniṁ pajaṁ; </span> <span class='eng'>sorrowless, they behold this generation of sorrow, </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp28:5'><span class='pli'>Pabbataṭṭhova bhūmaṭṭhe, </span> <span class='eng'>as an attentive one on a mountain top </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp28:6'><span class='pli'>dhīro bāle avekkhati. </span> <span class='eng'>beholds the fools below. </span></span></span></p></blockquote></article>\n<article id='dhp29'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp29:0'><span class='pli'>Pamattāpamattadvesahāyakabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp29:1'><span class='pli'>Appamatto pamattesu, </span> <span class='eng'>Heedful among the heedless, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp29:2'><span class='pli'>suttesu bahujāgaro; </span> <span class='eng'>wide awake while others sleep—</span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp29:3'><span class='pli'>Abalassaṁva sīghasso, </span> <span class='eng'>a true sage leaves them behind, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp29:4'><span class='pli'>hitvā yāti sumedhaso. </span> <span class='eng'>like a swift horse passing a feeble. </span></span></span></p></blockquote></article>\n<article id='dhp30'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp30:0'><span class='pli'>Maghavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp30:1'><span class='pli'>Appamādena maghavā, </span> <span class='eng'>Maghavā became chief of the gods </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp30:2'><span class='pli'>devānaṁ seṭṭhataṁ gato; </span> <span class='eng'>by means of diligence. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp30:3'><span class='pli'>Appamādaṁ pasaṁsanti, </span> <span class='eng'>People praise diligence, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp30:4'><span class='pli'>pamādo garahito sadā. </span> <span class='eng'>while negligence is always deplored. </span></span></span></p></blockquote></article>\n<article id='dhp31'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp31:0'><span class='pli'>Aññatarabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp31:1'><span class='pli'>Appamādarato bhikkhu, </span> <span class='eng'>A mendicant who loves to be diligent, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp31:2'><span class='pli'>pamāde bhayadassi vā; </span> <span class='eng'>seeing fear in negligence—</span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp31:3'><span class='pli'>Saṁyojanaṁ aṇuṁ thūlaṁ, </span> <span class='eng'>advances like fire, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp31:4'><span class='pli'>ḍahaṁ aggīva gacchati. </span> <span class='eng'>burning up fetters big and small. </span></span></span></p></blockquote></article>\n<article id='dhp32'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp32:0'><span class='pli'>Nigamavāsitissattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp32:1'><span class='pli'>Appamādarato bhikkhu, </span> <span class='eng'>A mendicant who loves to be diligent, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp32:2'><span class='pli'>pamāde bhayadassi vā; </span> <span class='eng'>seeing fear in negligence—</span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp32:3'><span class='pli'>Abhabbo parihānāya, </span> <span class='eng'>such a one can’t decline, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp32:4'><span class='pli'>nibbānasseva santike. </span> <span class='eng'>and has drawn near to extinguishment. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp32:5'><span class='pli'>Appamādavaggo dutiyo. </span></span></p></article></section>\n",
    "acronym": "Dhp 21–32",
    "translated_title": "2. Diligence",
    "original_title": "Appamādavagga"
  },
  "dhp33-43": {
    "previous": "dhp21-32",
    "next": "dhp44-59",
    "content": "<section class='range'  id='dhp33-43'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp33:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp33:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 33–43 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp33:0.3'><span class='pli'>Cittavagga </span> <span class='eng'>3. The Mind </span></span></h1></header>\n<article id='dhp33'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp33:0.4'><span class='pli'>Meghiyattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp33:1'><span class='pli'>Phandanaṁ capalaṁ cittaṁ, </span> <span class='eng'>The mind quivers and shakes, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp33:2'><span class='pli'>dūrakkhaṁ dunnivārayaṁ; </span> <span class='eng'>hard to guard, hard to curb. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp33:3'><span class='pli'>Ujuṁ karoti medhāvī, </span> <span class='eng'>The discerning straighten it out, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp33:4'><span class='pli'>usukārova tejanaṁ. </span> <span class='eng'>like a fletcher straightens an arrow. </span></span></span></p></blockquote></article>\n<article id='dhp34'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp34:1'><span class='pli'>Vārijova thale khitto, </span> <span class='eng'>Like a fish pulled from the sea </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp34:2'><span class='pli'>okamokataubbhato; </span> <span class='eng'>and cast upon the shore, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp34:3'><span class='pli'>Pariphandatidaṁ cittaṁ, </span> <span class='eng'>this mind flounders about, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp34:4'><span class='pli'>māradheyyaṁ pahātave. </span> <span class='eng'>trying to throw off Māra’s dominion. </span></span></span></p></blockquote></article>\n<article id='dhp35'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp35:0'><span class='pli'>Aññatarabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp35:1'><span class='pli'>Dunniggahassa lahuno, </span> <span class='eng'>Hard to hold back, flighty, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp35:2'><span class='pli'>yatthakāmanipātino; </span> <span class='eng'>alighting where it will; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp35:3'><span class='pli'>Cittassa damatho sādhu, </span> <span class='eng'>it’s good to tame the mind; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp35:4'><span class='pli'>cittaṁ dantaṁ sukhāvahaṁ. </span> <span class='eng'>a tamed mind leads to bliss. </span></span></span></p></blockquote></article>\n<article id='dhp36'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp36:0'><span class='pli'>Aññataraukkaṇṭhitabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp36:1'><span class='pli'>Sududdasaṁ sunipuṇaṁ, </span> <span class='eng'>So hard to see, so subtle, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp36:2'><span class='pli'>yatthakāmanipātinaṁ; </span> <span class='eng'>alighting where it will; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp36:3'><span class='pli'>Cittaṁ rakkhetha medhāvī, </span> <span class='eng'>the discerning protect the mind, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp36:4'><span class='pli'>cittaṁ guttaṁ sukhāvahaṁ. </span> <span class='eng'>a guarded mind leads to bliss. </span></span></span></p></blockquote></article>\n<article id='dhp37'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp37:0'><span class='pli'>Saṅgharakkhitabhāgineyyattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp37:1'><span class='pli'>Dūraṅgamaṁ ekacaraṁ, </span> <span class='eng'>The mind travels far, wandering alone; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp37:2'><span class='pli'>asarīraṁ guhāsayaṁ; </span> <span class='eng'>incorporeal, it lies hidden in the heart. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp37:3'><span class='pli'>Ye cittaṁ saṁyamissanti, </span> <span class='eng'>Those who will restrain the mind </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp37:4'><span class='pli'>mokkhanti mārabandhanā. </span> <span class='eng'>are freed from Māra’s bonds. </span></span></span></p></blockquote></article>\n<article id='dhp38'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp38:0'><span class='pli'>Cittahatthattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp38:1'><span class='pli'>Anavaṭṭhitacittassa, </span> <span class='eng'>Those of unsteady mind, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp38:2'><span class='pli'>saddhammaṁ avijānato; </span> <span class='eng'>who don’t understand the true teaching, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp38:3'><span class='pli'>Pariplavapasādassa, </span> <span class='eng'>and whose confidence wavers, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp38:4'><span class='pli'>paññā na paripūrati. </span> <span class='eng'>do not perfect their wisdom. </span></span></span></p></blockquote></article>\n<article id='dhp39'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp39:1'><span class='pli'>Anavassutacittassa, </span> <span class='eng'>One whose mind is not festering, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp39:2'><span class='pli'>ananvāhatacetaso; </span> <span class='eng'>whose heart is undamaged, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp39:3'><span class='pli'>Puññapāpapahīnassa, </span> <span class='eng'>who’s given up right and wrong, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp39:4'><span class='pli'>natthi jāgarato bhayaṁ. </span> <span class='eng'>alert, has nothing to fear. </span></span></span></p></blockquote></article>\n<article id='dhp40'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp40:0'><span class='pli'>Pañcasatabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp40:1'><span class='pli'>Kumbhūpamaṁ kāyamimaṁ viditvā, </span> <span class='eng'>Knowing this body breaks like a pot, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp40:2'><span class='pli'>Nagarūpamaṁ cittamidaṁ ṭhapetvā; </span> <span class='eng'>and fortifying the mind like a citadel, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp40:3'><span class='pli'>Yodhetha māraṁ paññāvudhena, </span> <span class='eng'>attack Māra with the sword of wisdom, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp40:4'><span class='pli'>Jitañca rakkhe anivesano siyā. </span> <span class='eng'>guard your conquest, and never settle. </span></span></span></p></blockquote></article>\n<article id='dhp41'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp41:0'><span class='pli'>Pūtigattatissattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp41:1'><span class='pli'>Aciraṁ vatayaṁ kāyo, </span> <span class='eng'>All too soon this body </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp41:2'><span class='pli'>pathaviṁ adhisessati; </span> <span class='eng'>will lie upon the earth, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp41:3'><span class='pli'>Chuddho apetaviññāṇo, </span> <span class='eng'>bereft of consciousness, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp41:4'><span class='pli'>niratthaṁva kaliṅgaraṁ. </span> <span class='eng'>tossed aside like a worthless log. </span> <span class='comment-marker' data-comment='As the sacrificed beast “lies there useless as a block of wood” (<i lang=&#39;pi&#39; translate=&#39;no&#39;>dārveva tarhi bhūto ’narthyaḥ śete</i>, Śatapatha Brāhmaṇa 3.8.3.15). '>*</span></span></span></p></blockquote></article>\n<article id='dhp42'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp42:0'><span class='pli'>Nandagopālakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp42:1'><span class='pli'>Diso disaṁ yaṁ taṁ kayirā, </span> <span class='eng'>A wrongly directed mind </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp42:2'><span class='pli'>verī vā pana verinaṁ; </span> <span class='eng'>would do you more harm </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp42:3'><span class='pli'>Micchāpaṇihitaṁ cittaṁ, </span> <span class='eng'>than a hater to the hated, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp42:4'><span class='pli'>pāpiyo naṁ tato kare. </span> <span class='eng'>or an enemy to their foe. </span></span></span></p></blockquote></article>\n<article id='dhp43'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp43:0'><span class='pli'>Soreyyavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp43:1'><span class='pli'>Na taṁ mātā pitā kayirā, </span> <span class='eng'>A rightly directed mind </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp43:2'><span class='pli'>aññe vāpi ca ñātakā; </span> <span class='eng'>would do you more good </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp43:3'><span class='pli'>Sammāpaṇihitaṁ cittaṁ, </span> <span class='eng'>than your mother or father </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp43:4'><span class='pli'>seyyaso naṁ tato kare. </span> <span class='eng'>or any other relative. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp43:5'><span class='pli'>Cittavaggo tatiyo. </span></span></p></article></section>\n",
    "acronym": "Dhp 33–43",
    "translated_title": "3. The Mind",
    "original_title": "Cittavagga"
  },
  "dhp44-59": {
    "previous": "dhp33-43",
    "next": "dhp60-75",
    "content": "<section class='range'  id='dhp44-59'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp44:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp44:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 44–59 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp44:0.3'><span class='pli'>Pupphavagga </span> <span class='eng'>4. Flowers </span></span></h1></header>\n<article id='dhp44'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp44:0.4'><span class='pli'>Pathavikathāpasutapañcasatabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp44:1'><span class='pli'>Ko imaṁ pathaviṁ vicessati, </span> <span class='eng'>Who bestirs this earth, </span> <span class='comment-marker' data-comment='The verbs <i lang=&#39;pi&#39; translate=&#39;no&#39;>vicessati</i> and <i lang=&#39;pi&#39; translate=&#39;no&#39;>pacessati</i> in this verse appear to be unique. I take them to be from the Sanskrit <i lang=&#39;pi&#39; translate=&#39;no&#39;>ceṣṭ</i> (“move, explore”) rather than <i lang=&#39;pi&#39; translate=&#39;no&#39;>ci</i> (“gather”). They can then be read as present tense, which makes better sense of the verse. Sanskrit and Gandhari parallels have a variety of forms, all of which maintain the distinction between the <i lang=&#39;pi&#39; translate=&#39;no&#39;>vi</i> and <i lang=&#39;pi&#39; translate=&#39;no&#39;>pa</i> prefixes. | I take <i lang=&#39;pi&#39; translate=&#39;no&#39;>vicessati</i> in the sense “stir, agitate”, with reference to such passages as <a href=&#39;index.html?q=an7.69#15.1&#39;>AN 7.69:15.1</a>, where the noble one inspires the gods. The commentary, rather, suggests the sense “explore, investigate”. A variant reading <i lang=&#39;pi&#39; translate=&#39;no&#39;>vijessati</i> yields the sense “shall conquer”. '>*</span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp44:2'><span class='pli'>Yamalokañca imaṁ sadevakaṁ; </span> <span class='eng'>and the Yama realm with its gods? </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp44:3'><span class='pli'>Ko dhammapadaṁ sudesitaṁ, </span> <span class='eng'>Who sets out the well-taught word of truth, </span> <span class='comment-marker' data-comment='The verb <i lang=&#39;pi&#39; translate=&#39;no&#39;>pacessati</i> is exceedingly rare, but the late tantric text Sarvatathāgatādhiṣṭhāna Vyūhasūtra has <i lang=&#39;pi&#39; translate=&#39;no&#39;>praceṣṭavyaḥ</i>, evidently in the sense of “setting out” scents, flowers, etc. on a shrine. Compare the sense of “having prepared” (the offering for the fire, <i lang=&#39;pi&#39; translate=&#39;no&#39;>ceṣṭitvā</i>) at Bṛhadāraṇyaka Upaniṣad 6.4.19. '>*</span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp44:4'><span class='pli'>Kusalo pupphamiva pacessati. </span> <span class='eng'>as an expert a flower? </span></span></span></p></blockquote></article>\n<article id='dhp45'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp45:1'><span class='pli'>Sekho pathaviṁ vicessati, </span> <span class='eng'>A trainee bestirs this earth, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp45:2'><span class='pli'>Yamalokañca imaṁ sadevakaṁ; </span> <span class='eng'>and the Yama realm with its gods. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp45:3'><span class='pli'>Sekho dhammapadaṁ sudesitaṁ, </span> <span class='eng'>A trainee sets out the well-taught word of truth, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp45:4'><span class='pli'>Kusalo pupphamiva pacessati. </span> <span class='eng'>as an expert a flower. </span></span></span></p></blockquote></article>\n<article id='dhp46'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp46:0'><span class='pli'>Marīcikammaṭṭhānikabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp46:1'><span class='pli'>Pheṇūpamaṁ kāyamimaṁ viditvā, </span> <span class='eng'>Knowing this body’s like foam, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp46:2'><span class='pli'>Marīcidhammaṁ abhisambudhāno; </span> <span class='eng'>realizing it’s all just a mirage, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp46:3'><span class='pli'>Chetvāna mārassa papupphakāni, </span> <span class='eng'>and cutting off Māra’s blossoming, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp46:4'><span class='pli'>Adassanaṁ maccurājassa gacche. </span> <span class='eng'>vanish from the King of Death. </span></span></span></p></blockquote></article>\n<article id='dhp47'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp47:0'><span class='pli'>Viṭaṭūbhavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp47:1'><span class='pli'>Pupphāni heva pacinantaṁ, </span> <span class='eng'>As a mighty flood sweeps off a sleeping village, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp47:2'><span class='pli'>byāsattamanasaṁ naraṁ; </span> <span class='eng'>death steals away a man </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp47:3'><span class='pli'>Suttaṁ gāmaṁ mahoghova, </span> <span class='eng'>even as he gathers flowers, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp47:4'><span class='pli'>maccu ādāya gacchati. </span> <span class='eng'>his mind caught up in them. </span></span></span></p></blockquote></article>\n<article id='dhp48'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp48:0'><span class='pli'>Patipūjikakumārivatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp48:1'><span class='pli'>Pupphāni heva pacinantaṁ, </span> <span class='eng'>The terminator gains control of the man </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp48:2'><span class='pli'>byāsattamanasaṁ naraṁ; </span> <span class='eng'>who has not had his fill of pleasures, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp48:3'><span class='pli'>Atittaññeva kāmesu, </span> <span class='eng'>even as he gathers flowers, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp48:4'><span class='pli'>antako kurute vasaṁ. </span> <span class='eng'>his mind caught up in them. </span></span></span></p></blockquote></article>\n<article id='dhp49'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp49:0'><span class='pli'>Macchariyakosiyaseṭṭhivatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp49:1'><span class='pli'>Yathāpi bhamaro pupphaṁ, </span> <span class='eng'>A bee takes the nectar </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp49:2'><span class='pli'>vaṇṇagandhamaheṭhayaṁ; </span> <span class='eng'>and moves on, doing no damage </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp49:3'><span class='pli'>Paleti rasamādāya, </span> <span class='eng'>to the flower’s beauty and fragrance; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp49:4'><span class='pli'>evaṁ gāme munī care. </span> <span class='eng'>and that’s how a sage should walk in the village. </span></span></span></p></blockquote></article>\n<article id='dhp50'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp50:0'><span class='pli'>Pāveyyaājīvakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp50:1'><span class='pli'>Na paresaṁ vilomāni, </span> <span class='eng'>Don’t find fault with others, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp50:2'><span class='pli'>na paresaṁ katākataṁ; </span> <span class='eng'>with what they’ve done or left undone. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp50:3'><span class='pli'>Attanova avekkheyya, </span> <span class='eng'>You should only watch yourself, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp50:4'><span class='pli'>katāni akatāni ca. </span> <span class='eng'>what you’ve done or left undone. </span></span></span></p></blockquote></article>\n<article id='dhp51'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp51:0'><span class='pli'>Chattapāṇiupāsakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp51:1'><span class='pli'>Yathāpi ruciraṁ pupphaṁ, </span> <span class='eng'>Just like a glorious flower </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp51:2'><span class='pli'>vaṇṇavantaṁ agandhakaṁ; </span> <span class='eng'>that’s colorful but lacks fragrance; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp51:3'><span class='pli'>Evaṁ subhāsitā vācā, </span> <span class='eng'>eloquent speech is fruitless </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp51:4'><span class='pli'>aphalā hoti akubbato. </span> <span class='eng'>for one who does not act on it. </span></span></span></p></blockquote></article>\n<article id='dhp52'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp52:1'><span class='pli'>Yathāpi ruciraṁ pupphaṁ, </span> <span class='eng'>Just like a glorious flower </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp52:2'><span class='pli'>Vaṇṇavantaṁ sagandhakaṁ; </span> <span class='eng'>that’s both colorful and fragrant, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp52:3'><span class='pli'>Evaṁ subhāsitā vācā, </span> <span class='eng'>eloquent speech is fruitful </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp52:4'><span class='pli'>Saphalā hoti kubbato. </span> <span class='eng'>for one who acts on it. </span></span></span></p></blockquote></article>\n<article id='dhp53'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp53:0'><span class='pli'>Visākhāvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp53:1'><span class='pli'>Yathāpi puppharāsimhā, </span> <span class='eng'>Just as one would create many garlands </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp53:2'><span class='pli'>kayirā mālāguṇe bahū; </span> <span class='eng'>from a heap of flowers, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp53:3'><span class='pli'>Evaṁ jātena maccena, </span> <span class='eng'>when a person has come to be born, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp53:4'><span class='pli'>kattabbaṁ kusalaṁ bahuṁ. </span> <span class='eng'>they should do many skillful things. </span></span></span></p></blockquote></article>\n<article id='dhp54'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp54:0'><span class='pli'>Ānandattherapañhavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp54:1'><span class='pli'>Na pupphagandho paṭivātameti, </span> <span class='eng'>The fragrance of flowers doesn’t spread upwind, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp54:2'><span class='pli'>Na candanaṁ tagaramallikā vā; </span> <span class='eng'>nor sandalwood, pinwheel, or jasmine; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp54:3'><span class='pli'>Satañca gandho paṭivātameti, </span> <span class='eng'>but the fragrance of the good spreads upwind; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp54:4'><span class='pli'>Sabbā disā sappuriso pavāyati. </span> <span class='eng'>a true person’s virtue spreads in every direction. </span></span></span></p></blockquote></article>\n<article id='dhp55'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp55:1'><span class='pli'>Candanaṁ tagaraṁ vāpi, </span> <span class='eng'>Among all the fragrances—</span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp55:2'><span class='pli'>uppalaṁ atha vassikī; </span> <span class='eng'>sandalwood or pinwheel </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp55:3'><span class='pli'>Etesaṁ gandhajātānaṁ, </span> <span class='eng'>or lotus or jasmine—</span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp55:4'><span class='pli'>sīlagandho anuttaro. </span> <span class='eng'>the fragrance of virtue is supreme. </span></span></span></p></blockquote></article>\n<article id='dhp56'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp56:0'><span class='pli'>Mahākassapattherapiṇḍapātadinnavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp56:1'><span class='pli'>Appamatto ayaṁ gandho, </span> <span class='eng'>Faint is the fragrance </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp56:2'><span class='pli'>yāyaṁ tagaracandanī; </span> <span class='eng'>of sandal or pinwheel; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp56:3'><span class='pli'>Yo ca sīlavataṁ gandho, </span> <span class='eng'>but the fragrance of the virtuous </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp56:4'><span class='pli'>vāti devesu uttamo. </span> <span class='eng'>floats to the highest gods. </span></span></span></p></blockquote></article>\n<article id='dhp57'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp57:0'><span class='pli'>Godhikattheraparinibbānavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp57:1'><span class='pli'>Tesaṁ sampannasīlānaṁ, </span> <span class='eng'>For those accomplished in ethics, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp57:2'><span class='pli'>appamādavihārinaṁ; </span> <span class='eng'>meditating diligently, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp57:3'><span class='pli'>Sammadaññāvimuttānaṁ, </span> <span class='eng'>freed through the highest knowledge, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp57:4'><span class='pli'>māro maggaṁ na vindati. </span> <span class='eng'>Māra cannot find their path. </span></span></span></p></blockquote></article>\n<article id='dhp58'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp58:0'><span class='pli'>Garahadinnavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp58:1'><span class='pli'>Yathā saṅkāradhānasmiṁ, </span> <span class='eng'>From a heap of trash </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp58:2'><span class='pli'>ujjhitasmiṁ mahāpathe; </span> <span class='eng'>discarded on the highway, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp58:3'><span class='pli'>Padumaṁ tattha jāyetha, </span> <span class='eng'>a lotus might blossom, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp58:4'><span class='pli'>sucigandhaṁ manoramaṁ. </span> <span class='eng'>fragrant and delightful. </span></span></span></p></blockquote></article>\n<article id='dhp59'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp59:1'><span class='pli'>Evaṁ saṅkārabhūtesu, </span> <span class='eng'>So too, among those thought of as trash, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp59:2'><span class='pli'>andhabhūte puthujjane; </span> <span class='eng'>a disciple of the perfect Buddha </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp59:3'><span class='pli'>Atirocati paññāya, </span> <span class='eng'>outshines with their wisdom </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp59:4'><span class='pli'>sammāsambuddhasāvako. </span> <span class='eng'>the blind ordinary folk. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp59:5'><span class='pli'>Pupphavaggo catuttho. </span></span></p></article></section>\n",
    "acronym": "Dhp 44–59",
    "translated_title": "4. Flowers",
    "original_title": "Pupphavagga"
  },
  "dhp60-75": {
    "previous": "dhp44-59",
    "next": "dhp76-89",
    "content": "<section class='range'  id='dhp60-75'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp60:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp60:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 60–75 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp60:0.3'><span class='pli'>Bālavagga </span> <span class='eng'>5. The Fool </span></span></h1></header>\n<article id='dhp60'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp60:0.4'><span class='pli'>Aññatarapurisavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp60:1'><span class='pli'>Dīghā jāgarato ratti, </span> <span class='eng'>Long is the night for the wakeful; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp60:2'><span class='pli'>dīghaṁ santassa yojanaṁ; </span> <span class='eng'>long is the league for the weary; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp60:3'><span class='pli'>Dīgho bālāna saṁsāro, </span> <span class='eng'>long transmigrate the fools </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp60:4'><span class='pli'>saddhammaṁ avijānataṁ. </span> <span class='eng'>who don’t understand the true teaching. </span></span></span></p></blockquote></article>\n<article id='dhp61'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp61:0'><span class='pli'>Mahākassapasaddhivihārikavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp61:1'><span class='pli'>Carañce nādhigaccheyya, </span> <span class='eng'>If while wandering you find no partner </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp61:2'><span class='pli'>Seyyaṁ sadisamattano; </span> <span class='eng'>equal or better than yourself, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp61:3'><span class='pli'>Ekacariyaṁ daḷhaṁ kayirā, </span> <span class='eng'>then firmly resolve to wander alone—</span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp61:4'><span class='pli'>Natthi bāle sahāyatā. </span> <span class='eng'>there’s no fellowship with fools. </span></span></span></p></blockquote></article>\n<article id='dhp62'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp62:0'><span class='pli'>Ānandaseṭṭhivatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp62:1'><span class='pli'>Puttā matthi dhanaṁ matthi, </span> <span class='eng'>“Sons are mine, wealth is mine”—</span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp62:2'><span class='pli'>iti bālo vihaññati; </span> <span class='eng'>thus the fool frets. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp62:3'><span class='pli'>Attā hi attano natthi, </span> <span class='eng'>For even your self is not your own, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp62:4'><span class='pli'>kuto puttā kuto dhanaṁ. </span> <span class='eng'>let alone your sons or wealth. </span></span></span></p></blockquote></article>\n<article id='dhp63'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp63:0'><span class='pli'>Gaṇṭhibhedakacoravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp63:1'><span class='pli'>Yo bālo maññati bālyaṁ, </span> <span class='eng'>The fool who thinks they’re a fool </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp63:2'><span class='pli'>paṇḍito vāpi tena so; </span> <span class='eng'>is wise at least to that extent. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp63:3'><span class='pli'>Bālo ca paṇḍitamānī, </span> <span class='eng'>But the true fool is said to be one </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp63:4'><span class='pli'>sa ve “bālo”ti vuccati. </span> <span class='eng'>who imagines that they are wise. </span></span></span></p></blockquote></article>\n<article id='dhp64'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp64:0'><span class='pli'>Udāyittheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp64:1'><span class='pli'>Yāvajīvampi ce bālo, </span> <span class='eng'>Though a fool attends to the wise </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp64:2'><span class='pli'>paṇḍitaṁ payirupāsati; </span> <span class='eng'>even for the rest of their life, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp64:3'><span class='pli'>Na so dhammaṁ vijānāti, </span> <span class='eng'>they still don’t understand the teaching, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp64:4'><span class='pli'>dabbī sūparasaṁ yathā. </span> <span class='eng'>like a spoon the taste of the soup. </span></span></span></p></blockquote></article>\n<article id='dhp65'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp65:0'><span class='pli'>Tiṁsapāveyyakabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp65:1'><span class='pli'>Muhuttamapi ce viññū, </span> <span class='eng'>If a clever person attends to the wise </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp65:2'><span class='pli'>paṇḍitaṁ payirupāsati; </span> <span class='eng'>even just for an hour or so, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp65:3'><span class='pli'>Khippaṁ dhammaṁ vijānāti, </span> <span class='eng'>they swiftly understand the teaching, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp65:4'><span class='pli'>jivhā sūparasaṁ yathā. </span> <span class='eng'>like a tongue the taste of the soup. </span></span></span></p></blockquote></article>\n<article id='dhp66'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp66:0'><span class='pli'>Suppabuddhakuṭṭhivatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp66:1'><span class='pli'>Caranti bālā dummedhā, </span> <span class='eng'>Fools and simpletons behave </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp66:2'><span class='pli'>amitteneva attanā; </span> <span class='eng'>like their own worst enemies, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp66:3'><span class='pli'>Karontā pāpakaṁ kammaṁ, </span> <span class='eng'>doing wicked deeds </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp66:4'><span class='pli'>yaṁ hoti kaṭukapphalaṁ. </span> <span class='eng'>that ripen as bitter fruit. </span></span></span></p></blockquote></article>\n<article id='dhp67'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp67:0'><span class='pli'>Kassakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp67:1'><span class='pli'>Na taṁ kammaṁ kataṁ sādhu, </span> <span class='eng'>It’s not good to do a deed </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp67:2'><span class='pli'>yaṁ katvā anutappati; </span> <span class='eng'>that plagues you later on, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp67:3'><span class='pli'>Yassa assumukho rodaṁ, </span> <span class='eng'>for which you weep and wail, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp67:4'><span class='pli'>vipākaṁ paṭisevati. </span> <span class='eng'>as its effect stays with you. </span></span></span></p></blockquote></article>\n<article id='dhp68'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp68:0'><span class='pli'>Sumanamālākāravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp68:1'><span class='pli'>Tañca kammaṁ kataṁ sādhu, </span> <span class='eng'>It is good to do a deed </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp68:2'><span class='pli'>yaṁ katvā nānutappati; </span> <span class='eng'>that doesn’t plague you later on, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp68:3'><span class='pli'>Yassa patīto sumano, </span> <span class='eng'>that gladdens and cheers, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp68:4'><span class='pli'>vipākaṁ paṭisevati. </span> <span class='eng'>as its effect stays with you. </span></span></span></p></blockquote></article>\n<article id='dhp69'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp69:0'><span class='pli'>Uppalavaṇṇattherīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp69:1'><span class='pli'>Madhuṁvā maññati bālo, </span> <span class='eng'>The fool imagines that evil is sweet, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp69:2'><span class='pli'>yāva pāpaṁ na paccati; </span> <span class='eng'>so long as it has not yet ripened. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp69:3'><span class='pli'>Yadā ca paccati pāpaṁ, </span> <span class='eng'>But as soon as that evil ripens, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp69:4'><span class='pli'>atha dukkhaṁ nigacchati. </span> <span class='eng'>they fall into suffering. </span></span></span></p></blockquote></article>\n<article id='dhp70'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp70:0'><span class='pli'>Jambukattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp70:1'><span class='pli'>Māse māse kusaggena, </span> <span class='eng'>Month after month a fool may eat </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp70:2'><span class='pli'>bālo bhuñjeyya bhojanaṁ; </span> <span class='eng'>food from a grass-blade’s tip; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp70:3'><span class='pli'>Na so saṅkhātadhammānaṁ, </span> <span class='eng'>but they’ll never be worth a sixteenth part </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp70:4'><span class='pli'>kalaṁ agghati soḷasiṁ. </span> <span class='eng'>of one who has appraised the teaching. </span></span></span></p></blockquote></article>\n<article id='dhp71'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp71:0'><span class='pli'>Ahipetavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp71:1'><span class='pli'>Na hi pāpaṁ kataṁ kammaṁ, </span> <span class='eng'>For a wicked deed that has been done </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp71:2'><span class='pli'>Sajjukhīraṁva muccati; </span> <span class='eng'>does not curdle quickly like milk. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp71:3'><span class='pli'>Ḍahantaṁ bālamanveti, </span> <span class='eng'>Smoldering, it follows the fool, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp71:4'><span class='pli'>Bhasmacchannova pāvako. </span> <span class='eng'>like a fire smothered over with ash. </span></span></span></p></blockquote></article>\n<article id='dhp72'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp72:0'><span class='pli'>Saṭṭhikūṭapetavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp72:1'><span class='pli'>Yāvadeva anatthāya, </span> <span class='eng'>Whatever fame a fool may get, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp72:2'><span class='pli'>ñattaṁ bālassa jāyati; </span> <span class='eng'>it only gives rise to harm. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp72:3'><span class='pli'>Hanti bālassa sukkaṁsaṁ, </span> <span class='eng'>Whatever good features they have it ruins, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp72:4'><span class='pli'>muddhamassa vipātayaṁ. </span> <span class='eng'>and blows their head into bits. </span></span></span></p></blockquote></article>\n<article id='dhp73'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp73:0'><span class='pli'>Cittagahapativatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp73:1'><span class='pli'>Asantaṁ bhāvanamiccheyya, </span> <span class='eng'>They’d seek the esteem that they lack, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp73:2'><span class='pli'>Purekkhārañca bhikkhusu; </span> <span class='eng'>and status among the mendicants; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp73:3'><span class='pli'>Āvāsesu ca issariyaṁ, </span> <span class='eng'>authority over monasteries, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp73:4'><span class='pli'>Pūjaṁ parakulesu ca. </span> <span class='eng'>and honor among other families. </span></span></span></p></blockquote></article>\n<article id='dhp74'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp74:1'><span class='pli'>Mameva kata maññantu, </span> <span class='eng'>“Let both layfolk and renunciants think </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp74:2'><span class='pli'>gihī pabbajitā ubho; </span> <span class='eng'>the work was done by me alone. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp74:3'><span class='pli'>Mamevātivasā assu, </span> <span class='eng'>In anything at all that’s to be done, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp74:4'><span class='pli'>kiccākiccesu kismici; </span> <span class='eng'>let them fall under my sway alone.” </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp74:5'><span class='pli'>Iti bālassa saṅkappo, </span> <span class='eng'>So thinks the fool, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp74:6'><span class='pli'>icchā māno ca vaḍḍhati. </span> <span class='eng'>their greed and pride only growing. </span></span></span></p></blockquote></article>\n<article id='dhp75'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp75:0'><span class='pli'>Vanavāsitissasāmaṇeravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp75:1'><span class='pli'>Aññā hi lābhūpanisā, </span> <span class='eng'>For the means to profit and the path to quenching </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp75:2'><span class='pli'>aññā nibbānagāminī; </span> <span class='eng'>are two quite different things. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp75:3'><span class='pli'>Evametaṁ abhiññāya, </span> <span class='eng'>A mendicant disciple of the Buddha, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp75:4'><span class='pli'>bhikkhu buddhassa sāvako; </span> <span class='eng'>understanding what this really means, </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp75:5'><span class='pli'>Sakkāraṁ nābhinandeyya, </span> <span class='eng'>would never delight in honors, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp75:6'><span class='pli'>vivekamanubrūhaye. </span> <span class='eng'>but rather would foster seclusion. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='7' data-uid='dhp75:7'><span class='pli'>Bālavaggo pañcamo. </span></span></p></article></section>\n",
    "acronym": "Dhp 60–75",
    "translated_title": "5. Fools",
    "original_title": "Bālavagga"
  },
  "dhp76-89": {
    "previous": "dhp60-75",
    "next": "dhp90-99",
    "content": "<section class='range'  id='dhp76-89'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp76:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp76:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 76–89 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp76:0.3'><span class='pli'>Paṇḍitavagga </span> <span class='eng'>6. The Astute </span></span></h1></header>\n<article id='dhp76'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp76:0.4'><span class='pli'>Rādhattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp76:1'><span class='pli'>Nidhīnaṁva pavattāraṁ, </span> <span class='eng'>Regard one who sees your faults </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp76:2'><span class='pli'>yaṁ passe vajjadassinaṁ; </span> <span class='eng'>as a guide to a hidden treasure. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp76:3'><span class='pli'>Niggayhavādiṁ medhāviṁ, </span> <span class='eng'>Stay close to one so wise and astute </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp76:4'><span class='pli'>tādisaṁ paṇḍitaṁ bhaje; </span> <span class='eng'>who corrects you when you need it. </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp76:5'><span class='pli'>Tādisaṁ bhajamānassa, </span> <span class='eng'>Sticking close to such an impartial person, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp76:6'><span class='pli'>seyyo hoti na pāpiyo. </span> <span class='eng'>things get better, not worse. </span></span></span></p></blockquote></article>\n<article id='dhp77'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp77:0'><span class='pli'>Assajipunabbasukavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp77:1'><span class='pli'>Ovadeyyānusāseyya, </span> <span class='eng'>Advise and instruct; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp77:2'><span class='pli'>asabbhā ca nivāraye; </span> <span class='eng'>curb wickedness: </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp77:3'><span class='pli'>Satañhi so piyo hoti, </span> <span class='eng'>for you shall be loved by the good, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp77:4'><span class='pli'>asataṁ hoti appiyo. </span> <span class='eng'>and disliked by the bad. </span></span></span></p></blockquote></article>\n<article id='dhp78'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp78:0'><span class='pli'>Channattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp78:1'><span class='pli'>Na bhaje pāpake mitte, </span> <span class='eng'>Don’t mix with bad friends, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp78:2'><span class='pli'>na bhaje purisādhame; </span> <span class='eng'>nor with the worst of men. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp78:3'><span class='pli'>Bhajetha mitte kalyāṇe, </span> <span class='eng'>Mix with spiritual friends, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp78:4'><span class='pli'>bhajetha purisuttame. </span> <span class='eng'>and with the best of men. </span></span></span></p></blockquote></article>\n<article id='dhp79'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp79:0'><span class='pli'>Mahākappinattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp79:1'><span class='pli'>Dhammapīti sukhaṁ seti, </span> <span class='eng'>Through joy in the teaching you sleep at ease, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp79:2'><span class='pli'>vippasannena cetasā; </span> <span class='eng'>with clear and confident heart. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp79:3'><span class='pli'>Ariyappavedite dhamme, </span> <span class='eng'>An astute person always delights in the teaching </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp79:4'><span class='pli'>sadā ramati paṇḍito. </span> <span class='eng'>proclaimed by the Noble One. </span></span></span></p></blockquote></article>\n<article id='dhp80'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp80:0'><span class='pli'>Paṇḍitasāmaṇeravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp80:1'><span class='pli'>Udakañhi nayanti nettikā, </span> <span class='eng'>Irrigators guide water, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp80:2'><span class='pli'>Usukārā namayanti tejanaṁ; </span> <span class='eng'>fletchers straighten arrows, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp80:3'><span class='pli'>Dāruṁ namayanti tacchakā, </span> <span class='eng'>carpenters carve timber, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp80:4'><span class='pli'>Attānaṁ damayanti paṇḍitā. </span> <span class='eng'>the astute tame themselves. </span></span></span></p></blockquote></article>\n<article id='dhp81'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp81:0'><span class='pli'>Lakuṇḍakabhaddiyattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp81:1'><span class='pli'>Selo yathā ekaghano, </span> <span class='eng'>As the wind cannot stir </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp81:2'><span class='pli'>vātena na samīrati; </span> <span class='eng'>a solid mass of rock, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp81:3'><span class='pli'>Evaṁ nindāpasaṁsāsu, </span> <span class='eng'>so too blame and praise </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp81:4'><span class='pli'>na samiñjanti paṇḍitā. </span> <span class='eng'>do not affect the wise. </span></span></span></p></blockquote></article>\n<article id='dhp82'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp82:0'><span class='pli'>Kāṇamātāvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp82:1'><span class='pli'>Yathāpi rahado gambhīro, </span> <span class='eng'>Like a deep lake, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp82:2'><span class='pli'>vippasanno anāvilo; </span> <span class='eng'>clear and unclouded, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp82:3'><span class='pli'>Evaṁ dhammāni sutvāna, </span> <span class='eng'>so clear are the astute </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp82:4'><span class='pli'>vippasīdanti paṇḍitā. </span> <span class='eng'>when they hear the teachings. </span></span></span></p></blockquote></article>\n<article id='dhp83'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp83:0'><span class='pli'>Pañcasatabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp83:1'><span class='pli'>Sabbattha ve sappurisā cajanti, </span> <span class='eng'>True persons give up everything, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp83:2'><span class='pli'>Na kāmakāmā lapayanti santo; </span> <span class='eng'>they don’t cajole for the things they desire. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp83:3'><span class='pli'>Sukhena phuṭṭhā atha vā dukhena, </span> <span class='eng'>Though touched by sadness or happiness, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp83:4'><span class='pli'>Na uccāvacaṁ paṇḍitā dassayanti. </span> <span class='eng'>the astute appear neither depressed nor elated. </span></span></span></p></blockquote></article>\n<article id='dhp84'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp84:0'><span class='pli'>Dhammikattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp84:1'><span class='pli'>Na attahetu na parassa hetu, </span> <span class='eng'>Never wish for success by unjust means, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp84:2'><span class='pli'>Na puttamicche na dhanaṁ na raṭṭhaṁ; </span> <span class='eng'>for your own sake or that of another, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp84:3'><span class='pli'>Na iccheyya adhammena samiddhimattano, </span> <span class='eng'>desiring children, wealth, or nation; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp84:4'><span class='pli'>Sa sīlavā paññavā dhammiko siyā. </span> <span class='eng'>rather, be virtuous, wise, and just. </span></span></span></p></blockquote></article>\n<article id='dhp85'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp85:0'><span class='pli'>Dhammassavanattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp85:1'><span class='pli'>Appakā te manussesu, </span> <span class='eng'>Few are those among humans </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp85:2'><span class='pli'>ye janā pāragāmino; </span> <span class='eng'>who cross to the far shore. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp85:3'><span class='pli'>Athāyaṁ itarā pajā, </span> <span class='eng'>The rest just run around </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp85:4'><span class='pli'>tīramevānudhāvati. </span> <span class='eng'>on the near shore. </span></span></span></p></blockquote></article>\n<article id='dhp86'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp86:1'><span class='pli'>Ye ca kho sammadakkhāte, </span> <span class='eng'>When the teaching is well explained, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp86:2'><span class='pli'>dhamme dhammānuvattino; </span> <span class='eng'>those who practice accordingly </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp86:3'><span class='pli'>Te janā pāramessanti, </span> <span class='eng'>will cross over </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp86:4'><span class='pli'>maccudheyyaṁ suduttaraṁ. </span> <span class='eng'>Death’s dominion so hard to pass. </span></span></span></p></blockquote></article>\n<article id='dhp87'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp87:0'><span class='pli'>Pañcasataāgantukabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp87:1'><span class='pli'>Kaṇhaṁ dhammaṁ vippahāya, </span> <span class='eng'>Rid of dark qualities, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp87:2'><span class='pli'>Sukkaṁ bhāvetha paṇḍito; </span> <span class='eng'>an astute person should develop the bright. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp87:3'><span class='pli'>Okā anokamāgamma, </span> <span class='eng'>Leaving home behind </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp87:4'><span class='pli'>Viveke yattha dūramaṁ. </span> <span class='eng'>for the seclusion so hard to enjoy, </span></span></span></p></blockquote></article>\n<article id='dhp88'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp88:1'><span class='pli'>Tatrābhiratimiccheyya, </span> <span class='eng'>try to find satisfaction there, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp88:2'><span class='pli'>hitvā kāme akiñcano; </span> <span class='eng'>having left behind sensual pleasures. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp88:3'><span class='pli'>Pariyodapeyya attānaṁ, </span> <span class='eng'>Owning nothing, an astute person </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp88:4'><span class='pli'>cittaklesehi paṇḍito. </span> <span class='eng'>would cleanse themselves of mental corruptions. </span></span></span></p></blockquote></article>\n<article id='dhp89'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp89:1'><span class='pli'>Yesaṁ sambodhiyaṅgesu, </span> <span class='eng'>Those whose minds are rightly developed </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp89:2'><span class='pli'>sammā cittaṁ subhāvitaṁ; </span> <span class='eng'>in the awakening factors; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp89:3'><span class='pli'>Ādānapaṭinissagge, </span> <span class='eng'>who, letting go of attachments, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp89:4'><span class='pli'>anupādāya ye ratā; </span> <span class='eng'>delight in not grasping: </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp89:5'><span class='pli'>Khīṇāsavā jutimanto, </span> <span class='eng'>with defilements ended, brilliant, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp89:6'><span class='pli'>te loke parinibbutā. </span> <span class='eng'>they are quenched in this world. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='7' data-uid='dhp89:7'><span class='pli'>Paṇḍitavaggo chaṭṭho. </span></span></p></article></section>\n",
    "acronym": "Dhp 76–89",
    "translated_title": "6. The Astute",
    "original_title": "Paṇḍitavagga"
  },
  "dhp90-99": {
    "previous": "dhp76-89",
    "next": "dhp100-115",
    "content": "<section class='range'  id='dhp90-99'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp90:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp90:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 90–99 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp90:0.3'><span class='pli'>Arahantavagga </span> <span class='eng'>7. The Perfected Ones </span></span></h1></header>\n<article id='dhp90'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp90:0.4'><span class='pli'>Jīvakapañhavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp90:1'><span class='pli'>Gataddhino visokassa, </span> <span class='eng'>At journey’s end, rid of sorrow; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp90:2'><span class='pli'>vippamuttassa sabbadhi; </span> <span class='eng'>everywhere free, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp90:3'><span class='pli'>Sabbaganthappahīnassa, </span> <span class='eng'>all ties given up, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp90:4'><span class='pli'>pariḷāho na vijjati. </span> <span class='eng'>no fever is found in them. </span></span></span></p></blockquote></article>\n<article id='dhp91'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp91:0'><span class='pli'>Mahākassapattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp91:1'><span class='pli'>Uyyuñjanti satīmanto, </span> <span class='eng'>The mindful apply themselves; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp91:2'><span class='pli'>na nikete ramanti te; </span> <span class='eng'>they delight in no abode. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp91:3'><span class='pli'>Haṁsāva pallalaṁ hitvā, </span> <span class='eng'>Like a swan gone from the marsh, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp91:4'><span class='pli'>okamokaṁ jahanti te. </span> <span class='eng'>they leave home after home behind. </span></span></span></p></blockquote></article>\n<article id='dhp92'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp92:0'><span class='pli'>Belaṭṭhasīsattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp92:1'><span class='pli'>Yesaṁ sannicayo natthi, </span> <span class='eng'>Those with nothing stored up, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp92:2'><span class='pli'>ye pariññātabhojanā; </span> <span class='eng'>who have understood their food, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp92:3'><span class='pli'>Suññato animitto ca, </span> <span class='eng'>whose domain is the liberation </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp92:4'><span class='pli'>vimokkho yesaṁ gocaro; </span> <span class='eng'>of the signless and the empty: </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp92:5'><span class='pli'>Ākāseva sakuntānaṁ, </span> <span class='eng'>their path is hard to trace, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp92:6'><span class='pli'>gati tesaṁ durannayā. </span> <span class='eng'>like birds in the sky. </span></span></span></p></blockquote></article>\n<article id='dhp93'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp93:0'><span class='pli'>Anuruddhattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp93:1'><span class='pli'>Yassāsavā parikkhīṇā, </span> <span class='eng'>One whose defilements have ended; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp93:2'><span class='pli'>āhāre ca anissito; </span> <span class='eng'>who’s not attached to food; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp93:3'><span class='pli'>Suññato animitto ca, </span> <span class='eng'>whose domain is the liberation </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp93:4'><span class='pli'>vimokkho yassa gocaro; </span> <span class='eng'>of the signless and the empty: </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp93:5'><span class='pli'>Ākāseva sakuntānaṁ, </span> <span class='eng'>their track is hard to trace, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp93:6'><span class='pli'>padaṁ tassa durannayaṁ. </span> <span class='eng'>like birds in the sky. </span></span></span></p></blockquote></article>\n<article id='dhp94'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp94:0'><span class='pli'>Mahākaccāyanattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp94:1'><span class='pli'>Yassindriyāni samathaṅgatāni, </span> <span class='eng'>Whose faculties have become serene, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp94:2'><span class='pli'>Assā yathā sārathinā sudantā; </span> <span class='eng'>like horses tamed by a charioteer, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp94:3'><span class='pli'>Pahīnamānassa anāsavassa, </span> <span class='eng'>who has abandoned conceit and defilements; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp94:4'><span class='pli'>Devāpi tassa pihayanti tādino. </span> <span class='eng'>the unaffected one is envied by even the gods. </span></span></span></p></blockquote></article>\n<article id='dhp95'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp95:0'><span class='pli'>Sāriputtattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp95:1'><span class='pli'>Pathavisamo no virujjhati, </span> <span class='eng'>Undisturbed like the earth, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp95:2'><span class='pli'>Indakhilupamo tādi subbato; </span> <span class='eng'>true to their vows, steady as Indra’s pillar, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp95:3'><span class='pli'>Rahadova apetakaddamo, </span> <span class='eng'>like a lake clear of mud; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp95:4'><span class='pli'>Saṁsārā na bhavanti tādino. </span> <span class='eng'>such a one does not transmigrate. </span></span></span></p></blockquote></article>\n<article id='dhp96'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp96:0'><span class='pli'>Kosambivāsitissattherasāmaṇeravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp96:1'><span class='pli'>Santaṁ tassa manaṁ hoti, </span> <span class='eng'>Their mind is peaceful, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp96:2'><span class='pli'>santā vācā ca kamma ca; </span> <span class='eng'>peaceful are their speech and deeds. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp96:3'><span class='pli'>Sammadaññāvimuttassa, </span> <span class='eng'>Such a one is at peace, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp96:4'><span class='pli'>upasantassa tādino. </span> <span class='eng'>rightly freed through enlightenment. </span></span></span></p></blockquote></article>\n<article id='dhp97'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp97:0'><span class='pli'>Sāriputtattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp97:1'><span class='pli'>Assaddho akataññū ca, </span> <span class='eng'>Lacking faith, a house-breaker, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp97:2'><span class='pli'>sandhicchedo ca yo naro; </span> <span class='eng'>one who acknowledges nothing, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp97:3'><span class='pli'>Hatāvakāso vantāso, </span> <span class='eng'>purged of hope, they’ve wasted their chance: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp97:4'><span class='pli'>sa ve uttamaporiso. </span> <span class='eng'>that is indeed the supreme person! </span></span></span></p></blockquote></article>\n<article id='dhp98'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp98:0'><span class='pli'>Khadiravaniyarevatattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp98:1'><span class='pli'>Gāme vā yadi vāraññe, </span> <span class='eng'>Whether in village or wilderness, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp98:2'><span class='pli'>Ninne vā yadi vā thale; </span> <span class='eng'>in a valley or the uplands, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp98:3'><span class='pli'>Yattha arahanto viharanti, </span> <span class='eng'>wherever the perfected ones live </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp98:4'><span class='pli'>Taṁ bhūmirāmaṇeyyakaṁ. </span> <span class='eng'>is a delightful place. </span></span></span></p></blockquote></article>\n<article id='dhp99'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp99:0'><span class='pli'>Aññataraitthivatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp99:1'><span class='pli'>Ramaṇīyāni araññāni, </span> <span class='eng'>Delightful are the wildernesses </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp99:2'><span class='pli'>yattha na ramatī jano; </span> <span class='eng'>where no people delight. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp99:3'><span class='pli'>Vītarāgā ramissanti, </span> <span class='eng'>Those free of greed will delight there, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp99:4'><span class='pli'>na te kāmagavesino. </span> <span class='eng'>not those who seek sensual pleasures. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp99:5'><span class='pli'>Arahantavaggo sattamo. </span></span></p></article></section>\n",
    "acronym": "Dhp 90–99",
    "translated_title": "7. The Perfected Ones",
    "original_title": "Arahantavagga"
  },
  "dhp100-115": {
    "previous": "dhp90-99",
    "next": "dhp116-128",
    "content": "<section class='range'  id='dhp100-115'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp100:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp100:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 100–115 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp100:0.3'><span class='pli'>Sahassavagga </span> <span class='eng'>8. The Thousands </span></span></h1></header>\n<article id='dhp100'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp100:0.4'><span class='pli'>Tambadāṭhikacoraghātakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp100:1'><span class='pli'>Sahassamapi ce vācā, </span> <span class='eng'>Better than a thousand </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp100:2'><span class='pli'>anatthapadasaṁhitā; </span> <span class='eng'>meaningless sayings </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp100:3'><span class='pli'>Ekaṁ atthapadaṁ seyyo, </span> <span class='eng'>is a single meaningful saying, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp100:4'><span class='pli'>yaṁ sutvā upasammati. </span> <span class='eng'>hearing which brings you peace. </span></span></span></p></blockquote></article>\n<article id='dhp101'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp101:0'><span class='pli'>Bāhiyadārucīriyattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp101:1'><span class='pli'>Sahassamapi ce gāthā, </span> <span class='eng'>Better than a thousand </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp101:2'><span class='pli'>anatthapadasaṁhitā; </span> <span class='eng'>meaningless verses </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp101:3'><span class='pli'>Ekaṁ gāthāpadaṁ seyyo, </span> <span class='eng'>is a single meaningful verse, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp101:4'><span class='pli'>yaṁ sutvā upasammati. </span> <span class='eng'>hearing which brings you peace. </span></span></span></p></blockquote></article>\n<article id='dhp102'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp102:0'><span class='pli'>Kuṇḍalakesitherīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp102:1'><span class='pli'>Yo ca gāthā sataṁ bhāse, </span> <span class='eng'>Better than reciting </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp102:2'><span class='pli'>anatthapadasaṁhitā; </span> <span class='eng'>a hundred meaningless verses </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp102:3'><span class='pli'>Ekaṁ dhammapadaṁ seyyo, </span> <span class='eng'>is a single saying of Dhamma, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp102:4'><span class='pli'>yaṁ sutvā upasammati. </span> <span class='eng'>hearing which brings you peace. </span></span></span></p></blockquote></article>\n<article id='dhp103'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp103:1'><span class='pli'>Yo sahassaṁ sahassena, </span> <span class='eng'>The supreme conqueror is </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp103:2'><span class='pli'>saṅgāme mānuse jine; </span> <span class='eng'>not he who conquers a million men in battle, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp103:3'><span class='pli'>Ekañca jeyyamattānaṁ, </span> <span class='eng'>but he who conquers a single man: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp103:4'><span class='pli'>sa ve saṅgāmajuttamo. </span> <span class='eng'>himself. </span></span></span></p></blockquote></article>\n<article id='dhp104'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp104:0'><span class='pli'>Anatthapucchakabrāhmaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp104:1'><span class='pli'>Attā have jitaṁ seyyo, </span> <span class='eng'>It is surely better to conquer oneself </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp104:2'><span class='pli'>yā cāyaṁ itarā pajā; </span> <span class='eng'>than all those other folk. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp104:3'><span class='pli'>Attadantassa posassa, </span> <span class='eng'>When a person has tamed themselves, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp104:4'><span class='pli'>niccaṁ saññatacārino. </span> <span class='eng'>always living restrained, </span></span></span></p></blockquote></article>\n<article id='dhp105'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp105:1'><span class='pli'>Neva devo na gandhabbo, </span> <span class='eng'>no god nor centaur, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp105:2'><span class='pli'>na māro saha brahmunā; </span> <span class='eng'>nor Māra nor divinity, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp105:3'><span class='pli'>Jitaṁ apajitaṁ kayirā, </span> <span class='eng'>can undo the victory </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp105:4'><span class='pli'>tathārūpassa jantuno. </span> <span class='eng'>of such a personage. </span></span></span></p></blockquote></article>\n<article id='dhp106'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp106:0'><span class='pli'>Sāriputtattherassa mātulabrāhmaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp106:1'><span class='pli'>Māse māse sahassena, </span> <span class='eng'>Rather than a thousandfold sacrifice, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp106:2'><span class='pli'>yo yajetha sataṁ samaṁ; </span> <span class='eng'>every month for a full century, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp106:3'><span class='pli'>Ekañca bhāvitattānaṁ, </span> <span class='eng'>it’s better to honor for a single hour </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp106:4'><span class='pli'>muhuttamapi pūjaye; </span> <span class='eng'>one who has developed themselves. </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp106:5'><span class='pli'>Sāyeva pūjanā seyyo, </span> <span class='eng'>That offering is better </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp106:6'><span class='pli'>yañce vassasataṁ hutaṁ. </span> <span class='eng'>than the hundred year sacrifice. </span></span></span></p></blockquote></article>\n<article id='dhp107'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp107:0'><span class='pli'>Sāriputtattherassa bhāgineyyavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp107:1'><span class='pli'>Yo ca vassasataṁ jantu, </span> <span class='eng'>Rather than serve the sacred flame </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp107:2'><span class='pli'>aggiṁ paricare vane; </span> <span class='eng'>in the forest for a hundred years, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp107:3'><span class='pli'>Ekañca bhāvitattānaṁ, </span> <span class='eng'>it’s better to honor for a single hour </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp107:4'><span class='pli'>muhuttamapi pūjaye; </span> <span class='eng'>a personage who has developed themselves. </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp107:5'><span class='pli'>Sāyeva pūjanā seyyo, </span> <span class='eng'>That offering is better </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp107:6'><span class='pli'>yañce vassasataṁ hutaṁ. </span> <span class='eng'>than the hundred year sacrifice. </span></span></span></p></blockquote></article>\n<article id='dhp108'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp108:0'><span class='pli'>Sāriputtatherassa sahāyakabrāhmaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp108:1'><span class='pli'>Yaṁ kiñci yiṭṭhaṁ va hutaṁ va loke, </span> <span class='eng'>Whatever sacrifice or offering in the world </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp108:2'><span class='pli'>Saṁvaccharaṁ yajetha puññapekkho; </span> <span class='eng'>a seeker of merit may make for a year, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp108:3'><span class='pli'>Sabbampi taṁ na catubhāgameti, </span> <span class='eng'>none of it is worth a quarter </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp108:4'><span class='pli'>Abhivādanā ujjugatesu seyyo. </span> <span class='eng'>of bowing to the sincere. </span></span></span></p></blockquote></article>\n<article id='dhp109'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp109:0'><span class='pli'>Āyuvaḍḍhanakumāravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp109:1'><span class='pli'>Abhivādanasīlissa, </span> <span class='eng'>For one in the habit of bowing, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp109:2'><span class='pli'>niccaṁ vuḍḍhāpacāyino; </span> <span class='eng'>always honoring the elders, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp109:3'><span class='pli'>Cattāro dhammā vaḍḍhanti, </span> <span class='eng'>four blessings grow: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp109:4'><span class='pli'>āyu vaṇṇo sukhaṁ balaṁ. </span> <span class='eng'>lifespan, beauty, happiness, and strength. </span></span></span></p></blockquote></article>\n<article id='dhp110'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp110:0'><span class='pli'>Saṅkiccasāmaṇeravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp110:1'><span class='pli'>Yo ca vassasataṁ jīve, </span> <span class='eng'>Better to live a single day </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp110:2'><span class='pli'>dussīlo asamāhito; </span> <span class='eng'>ethical and absorbed in meditation </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp110:3'><span class='pli'>Ekāhaṁ jīvitaṁ seyyo, </span> <span class='eng'>than to live a hundred years </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp110:4'><span class='pli'>sīlavantassa jhāyino. </span> <span class='eng'>unethical and lacking immersion. </span></span></span></p></blockquote></article>\n<article id='dhp111'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp111:0'><span class='pli'>Khāṇukoṇḍaññattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp111:1'><span class='pli'>Yo ca vassasataṁ jīve, </span> <span class='eng'>Better to live a single day </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp111:2'><span class='pli'>duppañño asamāhito; </span> <span class='eng'>wise and absorbed in meditation </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp111:3'><span class='pli'>Ekāhaṁ jīvitaṁ seyyo, </span> <span class='eng'>than to live a hundred years </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp111:4'><span class='pli'>paññavantassa jhāyino. </span> <span class='eng'>witless and lacking immersion. </span></span></span></p></blockquote></article>\n<article id='dhp112'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp112:0'><span class='pli'>Sabbadāsattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp112:1'><span class='pli'>Yo ca vassasataṁ jīve, </span> <span class='eng'>Better to live a single day </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp112:2'><span class='pli'>kusīto hīnavīriyo; </span> <span class='eng'>energetic and strong, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp112:3'><span class='pli'>Ekāhaṁ jīvitaṁ seyyo, </span> <span class='eng'>than to live a hundred years </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp112:4'><span class='pli'>vīriyamārabhato daḷhaṁ. </span> <span class='eng'>lazy and lacking energy. </span></span></span></p></blockquote></article>\n<article id='dhp113'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp113:0'><span class='pli'>Paṭācārātherīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp113:1'><span class='pli'>Yo ca vassasataṁ jīve, </span> <span class='eng'>Better to live a single day </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp113:2'><span class='pli'>apassaṁ udayabbayaṁ; </span> <span class='eng'>seeing rise and fall </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp113:3'><span class='pli'>Ekāhaṁ jīvitaṁ seyyo, </span> <span class='eng'>than to live a hundred years </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp113:4'><span class='pli'>passato udayabbayaṁ. </span> <span class='eng'>blind to rise and fall. </span></span></span></p></blockquote></article>\n<article id='dhp114'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp114:0'><span class='pli'>Kisāgotamīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp114:1'><span class='pli'>Yo ca vassasataṁ jīve, </span> <span class='eng'>Better to live a single day </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp114:2'><span class='pli'>apassaṁ amataṁ padaṁ; </span> <span class='eng'>seeing the state free of death </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp114:3'><span class='pli'>Ekāhaṁ jīvitaṁ seyyo, </span> <span class='eng'>than to live a hundred years </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp114:4'><span class='pli'>passato amataṁ padaṁ. </span> <span class='eng'>blind to the state free of death. </span></span></span></p></blockquote></article>\n<article id='dhp115'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp115:0'><span class='pli'>Bahuputtikattherīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp115:1'><span class='pli'>Yo ca vassasataṁ jīve, </span> <span class='eng'>Better to live a single day </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp115:2'><span class='pli'>apassaṁ dhammamuttamaṁ; </span> <span class='eng'>seeing the supreme teaching </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp115:3'><span class='pli'>Ekāhaṁ jīvitaṁ seyyo, </span> <span class='eng'>than to live a hundred years </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp115:4'><span class='pli'>passato dhammamuttamaṁ. </span> <span class='eng'>blind to the supreme teaching. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp115:5'><span class='pli'>Sahassavaggo aṭṭhamo. </span></span></p></article></section>\n",
    "acronym": "Dhp 100–115",
    "translated_title": "8. The Thousands",
    "original_title": "Sahassavagga"
  },
  "dhp116-128": {
    "previous": "dhp100-115",
    "next": "dhp129-145",
    "content": "<section class='range'  id='dhp116-128'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp116:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp116:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 116–128 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp116:0.3'><span class='pli'>Pāpavagga </span> <span class='eng'>9. Wickedness </span></span></h1></header>\n<article id='dhp116'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp116:0.4'><span class='pli'>Cūḷekasāṭakabrāhmaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp116:1'><span class='pli'>Abhittharetha kalyāṇe, </span> <span class='eng'>Rush to do good, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp116:2'><span class='pli'>pāpā cittaṁ nivāraye; </span> <span class='eng'>shield your mind from evil; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp116:3'><span class='pli'>Dandhañhi karoto puññaṁ, </span> <span class='eng'>for when you’re slow to do good, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp116:4'><span class='pli'>pāpasmiṁ ramatī mano. </span> <span class='eng'>your thoughts delight in wickedness. </span></span></span></p></blockquote></article>\n<article id='dhp117'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp117:0'><span class='pli'>Seyyasakattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp117:1'><span class='pli'>Pāpañce puriso kayirā, </span> <span class='eng'>If you do something bad, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp117:2'><span class='pli'>Na naṁ kayirā punappunaṁ; </span> <span class='eng'>don’t do it again and again, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp117:3'><span class='pli'>Na tamhi chandaṁ kayirātha, </span> <span class='eng'>don’t set your heart on it, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp117:4'><span class='pli'>Dukkho pāpassa uccayo. </span> <span class='eng'>for piling up evil is suffering. </span></span></span></p></blockquote></article>\n<article id='dhp118'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp118:0'><span class='pli'>Lājadevadhītāvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp118:1'><span class='pli'>Puññañce puriso kayirā, </span> <span class='eng'>If you do something good, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp118:2'><span class='pli'>kayirā naṁ punappunaṁ; </span> <span class='eng'>do it again and again, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp118:3'><span class='pli'>Tamhi chandaṁ kayirātha, </span> <span class='eng'>set your heart on it, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp118:4'><span class='pli'>sukho puññassa uccayo. </span> <span class='eng'>for piling up goodness is joyful. </span></span></span></p></blockquote></article>\n<article id='dhp119'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp119:0'><span class='pli'>Anāthapiṇḍikaseṭṭhivatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp119:1'><span class='pli'>Pāpopi passati bhadraṁ, </span> <span class='eng'>Even the wicked see good things, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp119:2'><span class='pli'>Yāva pāpaṁ na paccati; </span> <span class='eng'>so long as their wickedness has not ripened. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp119:3'><span class='pli'>Yadā ca paccati pāpaṁ, </span> <span class='eng'>But as soon as that wickedness ripens, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp119:4'><span class='pli'>Atha pāpo pāpāni passati. </span> <span class='eng'>then the wicked see wicked things. </span></span></span></p></blockquote></article>\n<article id='dhp120'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp120:1'><span class='pli'>Bhadropi passati pāpaṁ, </span> <span class='eng'>Even the good see wicked things, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp120:2'><span class='pli'>Yāva bhadraṁ na paccati; </span> <span class='eng'>so long as their goodness has not ripened. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp120:3'><span class='pli'>Yadā ca paccati bhadraṁ, </span> <span class='eng'>But as soon as that goodness ripens, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp120:4'><span class='pli'>Atha bhadro bhadrāni passati. </span> <span class='eng'>then the good see good things. </span></span></span></p></blockquote></article>\n<article id='dhp121'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp121:0'><span class='pli'>Asaññataparikkhāravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp121:1'><span class='pli'>Māvamaññetha pāpassa, </span> <span class='eng'>Think not lightly of evil, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp121:2'><span class='pli'>na mantaṁ āgamissati; </span> <span class='eng'>that it won’t come back to you. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp121:3'><span class='pli'>Udabindunipātena, </span> <span class='eng'>The pot is filled with water </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp121:4'><span class='pli'>udakumbhopi pūrati; </span> <span class='eng'>falling drop by drop; </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp121:5'><span class='pli'>Bālo pūrati pāpassa, </span> <span class='eng'>the fool is filled with wickedness </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp121:6'><span class='pli'>thokaṁ thokampi ācinaṁ. </span> <span class='eng'>piled up bit by bit. </span></span></span></p></blockquote></article>\n<article id='dhp122'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp122:0'><span class='pli'>Biḷālapādakaseṭṭhivatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp122:1'><span class='pli'>Māvamaññetha puññassa, </span> <span class='eng'>Think not lightly of goodness, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp122:2'><span class='pli'>na mandaṁ āgamissati; </span> <span class='eng'>that it won’t come back to you. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp122:3'><span class='pli'>Udabindunipātena, </span> <span class='eng'>The pot is filled with water </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp122:4'><span class='pli'>udakumbhopi pūrati; </span> <span class='eng'>falling drop by drop; </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp122:5'><span class='pli'>Dhīro pūrati puññassa, </span> <span class='eng'>the attentive one is filled with goodness </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp122:6'><span class='pli'>thokaṁ thokampi ācinaṁ. </span> <span class='eng'>piled up bit by bit. </span></span></span></p></blockquote></article>\n<article id='dhp123'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp123:0'><span class='pli'>Mahādhanavāṇijavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp123:1'><span class='pli'>Vāṇijova bhayaṁ maggaṁ, </span> <span class='eng'>Avoid wickedness, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp123:2'><span class='pli'>appasattho mahaddhano; </span> <span class='eng'>as a merchant with rich cargo and small escort </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp123:3'><span class='pli'>Visaṁ jīvitukāmova, </span> <span class='eng'>would avoid a dangerous road, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp123:4'><span class='pli'>pāpāni parivajjaye. </span> <span class='eng'>or one who loves life would avoid drinking poison. </span></span></span></p></blockquote></article>\n<article id='dhp124'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp124:0'><span class='pli'>Kukkuṭamittanesādavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp124:1'><span class='pli'>Pāṇimhi ce vaṇo nāssa, </span> <span class='eng'>You can carry poison in your hand </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp124:2'><span class='pli'>hareyya pāṇinā visaṁ; </span> <span class='eng'>if it has no wound, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp124:3'><span class='pli'>Nābbaṇaṁ visamanveti, </span> <span class='eng'>for poison does not infect without a wound; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp124:4'><span class='pli'>natthi pāpaṁ akubbato. </span> <span class='eng'>nothing bad happens unless you do bad. </span></span></span></p></blockquote></article>\n<article id='dhp125'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp125:0'><span class='pli'>Kokasunakhaluddakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp125:1'><span class='pli'>Yo appaduṭṭhassa narassa dussati, </span> <span class='eng'>Whoever wrongs a man who has done no wrong, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp125:2'><span class='pli'>Suddhassa posassa anaṅgaṇassa; </span> <span class='eng'>a pure man who has not a blemish, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp125:3'><span class='pli'>Tameva bālaṁ pacceti pāpaṁ, </span> <span class='eng'>the evil backfires on the fool, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp125:4'><span class='pli'>Sukhumo rajo paṭivātaṁva khitto. </span> <span class='eng'>like fine dust thrown upwind. </span></span></span></p></blockquote></article>\n<article id='dhp126'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp126:0'><span class='pli'>Maṇikārakulūpakatissattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp126:1'><span class='pli'>Gabbhameke uppajjanti, </span> <span class='eng'>Some are born in a womb; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp126:2'><span class='pli'>nirayaṁ pāpakammino; </span> <span class='eng'>evil-doers go to hell; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp126:3'><span class='pli'>Saggaṁ sugatino yanti, </span> <span class='eng'>the virtuous go to heaven; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp126:4'><span class='pli'>parinibbanti anāsavā. </span> <span class='eng'>the undefiled are fully extinguished. </span></span></span></p></blockquote></article>\n<article id='dhp127'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp127:0'><span class='pli'>Tayojanavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp127:1'><span class='pli'>Na antalikkhe na samuddamajjhe, </span> <span class='eng'>Not in midair, nor mid-ocean, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp127:2'><span class='pli'>Na pabbatānaṁ vivaraṁ pavissa; </span> <span class='eng'>nor hiding in a mountain cleft; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp127:3'><span class='pli'>Na vijjatī so jagatippadeso, </span> <span class='eng'>you’ll find no place on the planet </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp127:4'><span class='pli'>Yatthaṭṭhito mucceyya pāpakammā. </span> <span class='eng'>to escape your wicked deeds. </span></span></span></p></blockquote></article>\n<article id='dhp128'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp128:0'><span class='pli'>Suppabuddhasakyavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp128:1'><span class='pli'>Na antalikkhe na samuddamajjhe, </span> <span class='eng'>Not in midair, nor mid-ocean, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp128:2'><span class='pli'>Na pabbatānaṁ vivaraṁ pavissa; </span> <span class='eng'>nor hiding in a mountain cleft; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp128:3'><span class='pli'>Na vijjatī so jagatippadeso, </span> <span class='eng'>you’ll find no place on the planet </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp128:4'><span class='pli'>Yatthaṭṭhitaṁ nappasaheyya maccu. </span> <span class='eng'>where you won’t be vanquished by death. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp128:5'><span class='pli'>Pāpavaggo navamo. </span></span></p></article></section>\n",
    "acronym": "Dhp 116–128",
    "translated_title": "9. Wickedness",
    "original_title": "Pāpavagga"
  },
  "dhp129-145": {
    "previous": "dhp116-128",
    "next": "dhp146-156",
    "content": "<section class='range'  id='dhp129-145'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp129:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp129:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 129–145 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp129:0.3'><span class='pli'>Daṇḍavagga </span> <span class='eng'>10. The Rod </span></span></h1></header>\n<article id='dhp129'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp129:0.4'><span class='pli'>Chabbaggiyabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp129:1'><span class='pli'>Sabbe tasanti daṇḍassa, </span> <span class='eng'>All tremble at the rod, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp129:2'><span class='pli'>sabbe bhāyanti maccuno; </span> <span class='eng'>all fear death. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp129:3'><span class='pli'>Attānaṁ upamaṁ katvā, </span> <span class='eng'>Treating others like oneself, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp129:4'><span class='pli'>na haneyya na ghātaye. </span> <span class='eng'>neither kill nor incite to kill. </span></span></span></p></blockquote></article>\n<article id='dhp130'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp130:0'><span class='pli'>Chabbaggiyavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp130:1'><span class='pli'>Sabbe tasanti daṇḍassa, </span> <span class='eng'>All tremble at the rod, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp130:2'><span class='pli'>sabbesaṁ jīvitaṁ piyaṁ; </span> <span class='eng'>all love life. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp130:3'><span class='pli'>Attānaṁ upamaṁ katvā, </span> <span class='eng'>Treating others like oneself, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp130:4'><span class='pli'>na haneyya na ghātaye. </span> <span class='eng'>neither kill nor incite to kill. </span></span></span></p></blockquote></article>\n<article id='dhp131'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp131:0'><span class='pli'>Sambahulakumārakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp131:1'><span class='pli'>Sukhakāmāni bhūtāni, </span> <span class='eng'>Creatures love happiness, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp131:2'><span class='pli'>yo daṇḍena vihiṁsati; </span> <span class='eng'>so if you harm them with a stick </span> <span class='comment-marker' data-comment='This verse parallels <a href=&#39;index.html?q=ud2.3&#39;>Ud 2.3</a> where it is clear an actual stick is meant. '>*</span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp131:3'><span class='pli'>Attano sukhamesāno, </span> <span class='eng'>in search of your own happiness, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp131:4'><span class='pli'>pecca so na labhate sukhaṁ. </span> <span class='eng'>after death you won’t find happiness. </span></span></span></p></blockquote></article>\n<article id='dhp132'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp132:1'><span class='pli'>Sukhakāmāni bhūtāni, </span> <span class='eng'>Creatures love happiness, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp132:2'><span class='pli'>yo daṇḍena na hiṁsati; </span> <span class='eng'>so if you don’t harm them with a stick </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp132:3'><span class='pli'>Attano sukhamesāno, </span> <span class='eng'>in search of your own happiness, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp132:4'><span class='pli'>pecca so labhate sukhaṁ. </span> <span class='eng'>after death you will find happiness. </span></span></span></p></blockquote></article>\n<article id='dhp133'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp133:0'><span class='pli'>Koṇḍadhānattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp133:1'><span class='pli'>Māvoca pharusaṁ kañci, </span> <span class='eng'>Don’t speak harshly, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp133:2'><span class='pli'>vuttā paṭivadeyyu taṁ; </span> <span class='eng'>they may speak harshly back. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp133:3'><span class='pli'>Dukkhā hi sārambhakathā, </span> <span class='eng'>For aggressive speech is painful, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp133:4'><span class='pli'>paṭidaṇḍā phuseyyu taṁ. </span> <span class='eng'>and the rod may spring back on you. </span></span></span></p></blockquote></article>\n<article id='dhp134'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp134:1'><span class='pli'>Sace neresi attānaṁ, </span> <span class='eng'>If you still yourself </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp134:2'><span class='pli'>kaṁso upahato yathā; </span> <span class='eng'>like a broken gong, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp134:3'><span class='pli'>Esa pattosi nibbānaṁ, </span> <span class='eng'>you reach extinguishment </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp134:4'><span class='pli'>sārambho te na vijjati. </span> <span class='eng'>and know no conflict. </span></span></span></p></blockquote></article>\n<article id='dhp135'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp135:0'><span class='pli'>Uposathikaitthīnaṁvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp135:1'><span class='pli'>Yathā daṇḍena gopālo, </span> <span class='eng'>As a cowherd drives the cows </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp135:2'><span class='pli'>gāvo pājeti gocaraṁ; </span> <span class='eng'>to pasture with the rod, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp135:3'><span class='pli'>Evaṁ jarā ca maccu ca, </span> <span class='eng'>so too old age and death </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp135:4'><span class='pli'>āyuṁ pājenti pāṇinaṁ. </span> <span class='eng'>drive life from living beings. </span></span></span></p></blockquote></article>\n<article id='dhp136'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp136:0'><span class='pli'>Ajagarapetavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp136:1'><span class='pli'>Atha pāpāni kammāni, </span> <span class='eng'>The fool does not understand </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp136:2'><span class='pli'>karaṁ bālo na bujjhati; </span> <span class='eng'>the evil that they do. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp136:3'><span class='pli'>Sehi kammehi dummedho, </span> <span class='eng'>But because of those deeds, that simpleton </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp136:4'><span class='pli'>aggidaḍḍhova tappati. </span> <span class='eng'>is tormented as if burnt by fire. </span></span></span></p></blockquote></article>\n<article id='dhp137'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp137:0'><span class='pli'>Mahāmoggallānattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp137:1'><span class='pli'>Yo daṇḍena adaṇḍesu, </span> <span class='eng'>One who violently attacks </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp137:2'><span class='pli'>appaduṭṭhesu dussati; </span> <span class='eng'>the peaceful and the innocent </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp137:3'><span class='pli'>Dasannamaññataraṁ ṭhānaṁ, </span> <span class='eng'>swiftly falls </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp137:4'><span class='pli'>khippameva nigacchati. </span> <span class='eng'>to one of ten bad states: </span></span></span></p></blockquote></article>\n<article id='dhp138'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp138:1'><span class='pli'>Vedanaṁ pharusaṁ jāniṁ, </span> <span class='eng'>harsh pain; loss; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp138:2'><span class='pli'>sarīrassa va bhedanaṁ; </span> <span class='eng'>the breakup of the body; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp138:3'><span class='pli'>Garukaṁ vāpi ābādhaṁ, </span> <span class='eng'>serious illness; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp138:4'><span class='pli'>cittakkhepaṁ va pāpuṇe. </span> <span class='eng'>mental distress; </span></span></span></p></blockquote></article>\n<article id='dhp139'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp139:1'><span class='pli'>Rājato vā upasaggaṁ, </span> <span class='eng'>hazards from rulers; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp139:2'><span class='pli'>Abbhakkhānaṁ va dāruṇaṁ; </span> <span class='eng'>vicious slander; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp139:3'><span class='pli'>Parikkhayaṁ va ñātīnaṁ, </span> <span class='eng'>loss of kin; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp139:4'><span class='pli'>Bhogānaṁ va pabhaṅguraṁ. </span> <span class='eng'>destruction of wealth; </span></span></span></p></blockquote></article>\n<article id='dhp140'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp140:1'><span class='pli'>Atha vāssa agārāni, </span> <span class='eng'>or else their home </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp140:2'><span class='pli'>aggi ḍahati pāvako; </span> <span class='eng'>is consumed by fire. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp140:3'><span class='pli'>Kāyassa bhedā duppañño, </span> <span class='eng'>When their body breaks up, that witless person </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp140:4'><span class='pli'>nirayaṁ sopapajjati. </span> <span class='eng'>is reborn in hell. </span></span></span></p></blockquote></article>\n<article id='dhp141'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp141:0'><span class='pli'>Bahubhaṇḍikabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp141:1'><span class='pli'>Na naggacariyā na jaṭā na paṅkā, </span> <span class='eng'>Not nudity, nor matted hair, nor mud, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp141:2'><span class='pli'>Nānāsakā thaṇḍilasāyikā vā; </span> <span class='eng'>nor fasting, nor lying on bare ground, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp141:3'><span class='pli'>Rajojallaṁ ukkuṭikappadhānaṁ, </span> <span class='eng'>nor wearing dust and dirt, or squatting on the heels, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp141:4'><span class='pli'>Sodhenti maccaṁ avitiṇṇakaṅkhaṁ. </span> <span class='eng'>will cleanse a mortal not free of doubt. </span></span></span></p></blockquote></article>\n<article id='dhp142'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp142:0'><span class='pli'>Santatimahāmattavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp142:1'><span class='pli'>Alaṅkato cepi samaṁ careyya, </span> <span class='eng'>Dressed up they may be, but if they live well—</span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp142:2'><span class='pli'>Santo danto niyato brahmacārī; </span> <span class='eng'>peaceful, tamed, committed to the spiritual path, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp142:3'><span class='pli'>Sabbesu bhūtesu nidhāya daṇḍaṁ, </span> <span class='eng'>having laid aside violence towards all creatures—</span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp142:4'><span class='pli'>So brāhmaṇo so samaṇo sa bhikkhu. </span> <span class='eng'>they are a brahmin, an ascetic, a mendicant. </span></span></span></p></blockquote></article>\n<article id='dhp143'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp143:0'><span class='pli'>Pilotikatissattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp143:1'><span class='pli'>Hirīnisedho puriso, </span> <span class='eng'>Can a person constrained by conscience </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp143:2'><span class='pli'>koci lokasmi vijjati; </span> <span class='eng'>be found in the world? </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp143:3'><span class='pli'>Yo niddaṁ apabodheti, </span> <span class='eng'>Who shies away from blame, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp143:4'><span class='pli'>asso bhadro kasāmiva. </span> <span class='eng'>like a fine horse from the whip? </span></span></span></p></blockquote></article>\n<article id='dhp144'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp144:1'><span class='pli'>Asso yathā bhadro kasāniviṭṭho, </span> <span class='eng'>Like a fine horse under the whip, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp144:2'><span class='pli'>Ātāpino saṁvegino bhavātha; </span> <span class='eng'>be keen and full of urgency. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp144:3'><span class='pli'>Saddhāya sīlena ca vīriyena ca, </span> <span class='eng'>With faith, ethics, and energy, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp144:4'><span class='pli'>Samādhinā dhammavinicchayena ca; </span> <span class='eng'>immersion, and investigation of principles, </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp144:5'><span class='pli'>Sampannavijjācaraṇā patissatā, </span> <span class='eng'>accomplished in knowledge and conduct, mindful, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp144:6'><span class='pli'>Jahissatha dukkhamidaṁ anappakaṁ. </span> <span class='eng'>give up this vast suffering. </span></span></span></p></blockquote></article>\n<article id='dhp145'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp145:0'><span class='pli'>Sukhasāmaṇeravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp145:1'><span class='pli'>Udakañhi nayanti nettikā, </span> <span class='eng'>While irrigators guide water, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp145:2'><span class='pli'>Usukārā namayanti tejanaṁ; </span> <span class='eng'>fletchers shape arrows, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp145:3'><span class='pli'>Dāruṁ namayanti tacchakā, </span> <span class='eng'>and carpenters carve timber—</span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp145:4'><span class='pli'>Attānaṁ damayanti subbatā. </span> <span class='eng'>those true to their vows tame themselves. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp145:5'><span class='pli'>Daṇḍavaggo dasamo. </span></span></p></article></section>\n",
    "acronym": "Dhp 129–145",
    "translated_title": "10. The Rod",
    "original_title": "Daṇḍavagga"
  },
  "dhp146-156": {
    "previous": "dhp129-145",
    "next": "dhp157-166",
    "content": "<section class='range'  id='dhp146-156'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp146:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp146:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 146–156 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp146:0.3'><span class='pli'>Jarāvagga </span> <span class='eng'>11. Old Age </span></span></h1></header>\n<article id='dhp146'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp146:0.4'><span class='pli'>Visākhāyasahāyikānaṁvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp146:1'><span class='pli'>Ko nu hāso kimānando, </span> <span class='eng'>What is joy, what is laughter, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp146:2'><span class='pli'>niccaṁ pajjalite sati; </span> <span class='eng'>when the flames are ever burning? </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp146:3'><span class='pli'>Andhakārena onaddhā, </span> <span class='eng'>Shrouded by darkness, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp146:4'><span class='pli'>padīpaṁ na gavesatha. </span> <span class='eng'>would you not seek a light? </span></span></span></p></blockquote></article>\n<article id='dhp147'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp147:0'><span class='pli'>Sirimāvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp147:1'><span class='pli'>Passa cittakataṁ bimbaṁ, </span> <span class='eng'>See this fancy puppet, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp147:2'><span class='pli'>arukāyaṁ samussitaṁ; </span> <span class='eng'>a body built of sores, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp147:3'><span class='pli'>Āturaṁ bahusaṅkappaṁ, </span> <span class='eng'>diseased, obsessed over, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp147:4'><span class='pli'>yassa natthi dhuvaṁ ṭhiti. </span> <span class='eng'>in which nothing lasts at all. </span></span></span></p></blockquote></article>\n<article id='dhp148'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp148:0'><span class='pli'>Uttarātherīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp148:1'><span class='pli'>Parijiṇṇamidaṁ rūpaṁ, </span> <span class='eng'>This body is decrepit and frail, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp148:2'><span class='pli'>roganīḷaṁ pabhaṅguraṁ; </span> <span class='eng'>a nest of disease. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp148:3'><span class='pli'>Bhijjati pūtisandeho, </span> <span class='eng'>This foul carcass falls apart, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp148:4'><span class='pli'>maraṇantañhi jīvitaṁ. </span> <span class='eng'>for life ends in death. </span></span></span></p></blockquote></article>\n<article id='dhp149'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp149:0'><span class='pli'>Sambahulaadhimānikabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp149:1'><span class='pli'>Yānimāni apatthāni, </span> <span class='eng'>These dove-grey bones </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp149:2'><span class='pli'>alābūneva sārade; </span> <span class='eng'>are tossed away like </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp149:3'><span class='pli'>Kāpotakāni aṭṭhīni, </span> <span class='eng'>dried gourds in the autumn—</span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp149:4'><span class='pli'>tāni disvāna kā rati. </span> <span class='eng'>what joy is there in such a sight? </span></span></span></p></blockquote></article>\n<article id='dhp150'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp150:0'><span class='pli'>Janapadakalyāṇīrūpanandātherīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp150:1'><span class='pli'>Aṭṭhīnaṁ nagaraṁ kataṁ, </span> <span class='eng'>In this city built of bones, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp150:2'><span class='pli'>maṁsalohitalepanaṁ; </span> <span class='eng'>plastered with flesh and blood, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp150:3'><span class='pli'>Yattha jarā ca maccu ca, </span> <span class='eng'>old age and death are stashed away, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp150:4'><span class='pli'>māno makkho ca ohito. </span> <span class='eng'>along with conceit and contempt. </span></span></span></p></blockquote></article>\n<article id='dhp151'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp151:0'><span class='pli'>Mallikādevīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp151:1'><span class='pli'>Jīranti ve rājarathā sucittā, </span> <span class='eng'>Fancy chariots of kings wear out, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp151:2'><span class='pli'>Atho sarīrampi jaraṁ upeti; </span> <span class='eng'>and even this body gets old. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp151:3'><span class='pli'>Satañca dhammo na jaraṁ upeti, </span> <span class='eng'>But the truth of the good never gets old—</span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp151:4'><span class='pli'>Santo have sabbhi pavedayanti. </span> <span class='eng'>so the good proclaim to the good. </span></span></span></p></blockquote></article>\n<article id='dhp152'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp152:0'><span class='pli'>Lāḷudāyītheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp152:1'><span class='pli'>Appassutāyaṁ puriso, </span> <span class='eng'>A person of little learning </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp152:2'><span class='pli'>balībaddhova jīrati; </span> <span class='eng'>ages like an ox—</span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp152:3'><span class='pli'>Maṁsāni tassa vaḍḍhanti, </span> <span class='eng'>their flesh grows, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp152:4'><span class='pli'>paññā tassa na vaḍḍhati. </span> <span class='eng'>but not their wisdom. </span></span></span></p></blockquote></article>\n<article id='dhp153'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp153:0'><span class='pli'>Udānavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp153:1'><span class='pli'>Anekajātisaṁsāraṁ, </span> <span class='eng'>Transmigrating through countless rebirths, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp153:2'><span class='pli'>sandhāvissaṁ anibbisaṁ; </span> <span class='eng'>I’ve journeyed without reward, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp153:3'><span class='pli'>Gahakāraṁ gavesanto, </span> <span class='eng'>searching for the house-builder; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp153:4'><span class='pli'>dukkhā jāti punappunaṁ. </span> <span class='eng'>painful is birth again and again. </span></span></span></p></blockquote></article>\n<article id='dhp154'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp154:1'><span class='pli'>Gahakāraka diṭṭhosi, </span> <span class='eng'>I’ve seen you, house-builder! </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp154:2'><span class='pli'>puna gehaṁ na kāhasi; </span> <span class='eng'>You won’t build a house again! </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp154:3'><span class='pli'>Sabbā te phāsukā bhaggā, </span> <span class='eng'>Your rafters are all broken, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp154:4'><span class='pli'>gahakūṭaṁ visaṅkhataṁ; </span> <span class='eng'>your roof-peak is demolished. </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp154:5'><span class='pli'>Visaṅkhāragataṁ cittaṁ, </span> <span class='eng'>My mind, set on demolition, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp154:6'><span class='pli'>taṇhānaṁ khayamajjhagā. </span> <span class='eng'>has reached the end of craving. </span></span></span></p></blockquote></article>\n<article id='dhp155'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp155:0'><span class='pli'>Mahādhanaseṭṭhiputtavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp155:1'><span class='pli'>Acaritvā brahmacariyaṁ, </span> <span class='eng'>When young they spurned the spiritual path </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp155:2'><span class='pli'>aladdhā yobbane dhanaṁ; </span> <span class='eng'>and failed to earn any wealth. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp155:3'><span class='pli'>Jiṇṇakoñcāva jhāyanti, </span> <span class='eng'>Now they brood like old cranes </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp155:4'><span class='pli'>khīṇamaccheva pallale. </span> <span class='eng'>in a pond bereft of fish. </span></span></span></p></blockquote></article>\n<article id='dhp156'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp156:1'><span class='pli'>Acaritvā brahmacariyaṁ, </span> <span class='eng'>When young they spurned the spiritual path </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp156:2'><span class='pli'>aladdhā yobbane dhanaṁ; </span> <span class='eng'>and failed to earn any wealth. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp156:3'><span class='pli'>Senti cāpātikhīṇāva, </span> <span class='eng'>Now they lie like spent arrows, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp156:4'><span class='pli'>purāṇāni anutthunaṁ. </span> <span class='eng'>bemoaning over things past. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp156:5'><span class='pli'>Jarāvaggo ekādasamo. </span></span></p></article></section>\n",
    "acronym": "Dhp 146–156",
    "translated_title": "11. Old Age",
    "original_title": "Jarāvagga"
  },
  "dhp157-166": {
    "previous": "dhp146-156",
    "next": "dhp167-178",
    "content": "<section class='range'  id='dhp157-166'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp157:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp157:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 157–166 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp157:0.3'><span class='pli'>Attavagga </span> <span class='eng'>12. The Self </span></span></h1></header>\n<article id='dhp157'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp157:0.4'><span class='pli'>Bodhirājakumāravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp157:1'><span class='pli'>Attānañce piyaṁ jaññā, </span> <span class='eng'>If you knew your self as beloved, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp157:2'><span class='pli'>rakkheyya naṁ surakkhitaṁ; </span> <span class='eng'>you’d look after it so well. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp157:3'><span class='pli'>Tiṇṇaṁ aññataraṁ yāmaṁ, </span> <span class='eng'>In one of the night’s three watches, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp157:4'><span class='pli'>paṭijaggeyya paṇḍito. </span> <span class='eng'>an astute person would remain alert. </span></span></span></p></blockquote></article>\n<article id='dhp158'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp158:0'><span class='pli'>Upanandasakyaputtattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp158:1'><span class='pli'>Attānameva paṭhamaṁ, </span> <span class='eng'>The astute would avoid being corrupted </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp158:2'><span class='pli'>patirūpe nivesaye; </span> <span class='eng'>by first grounding themselves </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp158:3'><span class='pli'>Athaññamanusāseyya, </span> <span class='eng'>in what is suitable, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp158:4'><span class='pli'>na kilisseyya paṇḍito. </span> <span class='eng'>and then instructing others. </span></span></span></p></blockquote></article>\n<article id='dhp159'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp159:0'><span class='pli'>Padhānikatissattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp159:1'><span class='pli'>Attānañce tathā kayirā, </span> <span class='eng'>If one were to treat oneself </span> <span class='comment-marker' data-comment='Accept Pali reading of <i lang=&#39;pi&#39; translate=&#39;no&#39;>attānaṁ</i> as accusative, but note the Prakrit variant <i lang=&#39;pi&#39; translate=&#39;no&#39;>attanā</i> in instrumental. '>*</span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp159:2'><span class='pli'>yathāññamanusāsati; </span> <span class='eng'>as one instructs another, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp159:3'><span class='pli'>Sudanto vata dametha, </span> <span class='eng'>the well-tamed indeed would tame: </span> <span class='comment-marker' data-comment='Read <i lang=&#39;pi&#39; translate=&#39;no&#39;>dametha</i> as third singular optative reflexive. '>*</span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp159:4'><span class='pli'>attā hi kira duddamo. </span> <span class='eng'>for the self, it seems, is hard to tame. </span></span></span></p></blockquote></article>\n<article id='dhp160'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp160:0'><span class='pli'>Kumārakassapamātuttherivatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp160:1'><span class='pli'>Attā hi attano nātho, </span> <span class='eng'>One is indeed the lord of oneself, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp160:2'><span class='pli'>ko hi nātho paro siyā; </span> <span class='eng'>for who else would be one’s lord? </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp160:3'><span class='pli'>Attanā hi sudantena, </span> <span class='eng'>By means of a well-tamed self, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp160:4'><span class='pli'>nāthaṁ labhati dullabhaṁ. </span> <span class='eng'>one gains a lord that’s rare indeed. </span></span></span></p></blockquote></article>\n<article id='dhp161'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp161:0'><span class='pli'>Mahākālaupāsakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp161:1'><span class='pli'>Attanā hi kataṁ pāpaṁ, </span> <span class='eng'>For the evil that is done by oneself, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp161:2'><span class='pli'>Attajaṁ attasambhavaṁ; </span> <span class='eng'>born and produced in oneself, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp161:3'><span class='pli'>Abhimatthati dummedhaṁ, </span> <span class='eng'>grinds down a simpleton, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp161:4'><span class='pli'>Vajiraṁvasmamayaṁ maṇiṁ. </span> <span class='eng'>as diamond grinds a lesser gem. </span></span></span></p></blockquote></article>\n<article id='dhp162'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp162:0'><span class='pli'>Devadattavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp162:1'><span class='pli'>Yassa accantadussilyaṁ, </span> <span class='eng'>One choked by immorality, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp162:2'><span class='pli'>māluvā sālamivotthataṁ; </span> <span class='eng'>as a sal tree by a creeper, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp162:3'><span class='pli'>Karoti so tathattānaṁ, </span> <span class='eng'>does to themselves </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp162:4'><span class='pli'>yathā naṁ icchatī diso. </span> <span class='eng'>what a foe only wishes. </span></span></span></p></blockquote></article>\n<article id='dhp163'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp163:0'><span class='pli'>Saṅghabhedaparisakkanavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp163:1'><span class='pli'>Sukarāni asādhūni, </span> <span class='eng'>It’s easy to do bad things </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp163:2'><span class='pli'>attano ahitāni ca; </span> <span class='eng'>harmful to oneself, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp163:3'><span class='pli'>Yaṁ ve hitañca sādhuñca, </span> <span class='eng'>but good things that are helpful </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp163:4'><span class='pli'>taṁ ve paramadukkaraṁ. </span> <span class='eng'>are the hardest things to do. </span></span></span></p></blockquote></article>\n<article id='dhp164'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp164:0'><span class='pli'>Kālattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp164:1'><span class='pli'>Yo sāsanaṁ arahataṁ, </span> <span class='eng'>On account of wicked views—</span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp164:2'><span class='pli'>ariyānaṁ dhammajīvinaṁ; </span> <span class='eng'>scorning the guidance </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp164:3'><span class='pli'>Paṭikkosati dummedho, </span> <span class='eng'>of the perfected ones, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp164:4'><span class='pli'>diṭṭhiṁ nissāya pāpikaṁ; </span> <span class='eng'>the noble ones living righteously—</span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp164:5'><span class='pli'>Phalāni kaṭṭhakasseva, </span> <span class='eng'>the idiot begets their own self’s demise, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp164:6'><span class='pli'>attaghātāya phallati. </span> <span class='eng'>like the bamboo bearing fruit. </span></span></span></p></blockquote></article>\n<article id='dhp165'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp165:0'><span class='pli'>Cūḷakālaupāsakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp165:1'><span class='pli'>Attanā hi kataṁ pāpaṁ, </span> <span class='eng'>For it is by oneself that evil’s done, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp165:2'><span class='pli'>attanā saṅkilissati; </span> <span class='eng'>one is corrupted by oneself. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp165:3'><span class='pli'>Attanā akataṁ pāpaṁ, </span> <span class='eng'>It’s by oneself that evil’s not done, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp165:4'><span class='pli'>attanāva visujjhati; </span> <span class='eng'>one is purified by oneself. </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp165:5'><span class='pli'>Suddhī asuddhi paccattaṁ, </span> <span class='eng'>Purity and impurity are personal matters, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp165:6'><span class='pli'>nāñño aññaṁ visodhaye. </span> <span class='eng'>no one can purify another. </span></span></span></p></blockquote></article>\n<article id='dhp166'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp166:0'><span class='pli'>Attadatthattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp166:1'><span class='pli'>Attadatthaṁ paratthena, </span> <span class='eng'>Never neglect what is good for yourself </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp166:2'><span class='pli'>bahunāpi na hāpaye; </span> <span class='eng'>for the sake of another, however great. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp166:3'><span class='pli'>Attadatthamabhiññāya, </span> <span class='eng'>Knowing well what is good for yourself, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp166:4'><span class='pli'>sadatthapasuto siyā. </span> <span class='eng'>be intent upon your heart’s goal. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp166:5'><span class='pli'>Attavaggo dvādasamo. </span></span></p></article></section>\n",
    "acronym": "Dhp 157–166",
    "translated_title": "12. The Self",
    "original_title": "Attavagga"
  },
  "dhp167-178": {
    "previous": "dhp157-166",
    "next": "dhp179-196",
    "content": "<section class='range'  id='dhp167-178'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp167:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp167:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 167–178 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp167:0.3'><span class='pli'>Lokavagga </span> <span class='eng'>13. The World </span></span></h1></header>\n<article id='dhp167'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp167:0.4'><span class='pli'>Daharabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp167:1'><span class='pli'>Hīnaṁ dhammaṁ na seveyya, </span> <span class='eng'>Don’t resort to lowly things, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp167:2'><span class='pli'>pamādena na saṁvase; </span> <span class='eng'>don’t abide in negligence, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp167:3'><span class='pli'>Micchādiṭṭhiṁ na seveyya, </span> <span class='eng'>don’t resort to wrong views, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp167:4'><span class='pli'>na siyā lokavaḍḍhano. </span> <span class='eng'>don’t perpetuate the world. </span></span></span></p></blockquote></article>\n<article id='dhp168'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp168:0'><span class='pli'>Suddhodanavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp168:1'><span class='pli'>Uttiṭṭhe nappamajjeyya, </span> <span class='eng'>Get up, don’t be heedless, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp168:2'><span class='pli'>dhammaṁ sucaritaṁ care; </span> <span class='eng'>live by principle, with good conduct. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp168:3'><span class='pli'>Dhammacārī sukhaṁ seti, </span> <span class='eng'>For one of good conduct sleeps at ease, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp168:4'><span class='pli'>asmiṁ loke paramhi ca. </span> <span class='eng'>in this world and the next. </span></span></span></p></blockquote></article>\n<article id='dhp169'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp169:1'><span class='pli'>Dhammaṁ care sucaritaṁ, </span> <span class='eng'>Live by principle, with good conduct, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp169:2'><span class='pli'>na naṁ duccaritaṁ care; </span> <span class='eng'>don’t conduct yourself badly. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp169:3'><span class='pli'>Dhammacārī sukhaṁ seti, </span> <span class='eng'>For one of good conduct sleeps at ease, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp169:4'><span class='pli'>asmiṁ loke paramhi ca. </span> <span class='eng'>in this world and the next. </span></span></span></p></blockquote></article>\n<article id='dhp170'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp170:0'><span class='pli'>Pañcasatavipassakabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp170:1'><span class='pli'>Yathā pubbuḷakaṁ passe, </span> <span class='eng'>Look upon the world </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp170:2'><span class='pli'>yathā passe marīcikaṁ; </span> <span class='eng'>as a bubble </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp170:3'><span class='pli'>Evaṁ lokaṁ avekkhantaṁ, </span> <span class='eng'>or a mirage, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp170:4'><span class='pli'>maccurājā na passati. </span> <span class='eng'>then the King of Death won’t see you. </span></span></span></p></blockquote></article>\n<article id='dhp171'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp171:0'><span class='pli'>Abhayarājakumāravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp171:1'><span class='pli'>Etha passathimaṁ lokaṁ, </span> <span class='eng'>Come, see this world decked out </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp171:2'><span class='pli'>cittaṁ rājarathūpamaṁ; </span> <span class='eng'>like a fancy royal chariot. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp171:3'><span class='pli'>Yattha bālā visīdanti, </span> <span class='eng'>Here fools founder, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp171:4'><span class='pli'>natthi saṅgo vijānataṁ. </span> <span class='eng'>but the discerning are not chained. </span></span></span></p></blockquote></article>\n<article id='dhp172'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp172:0'><span class='pli'>Sammajjanattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp172:1'><span class='pli'>Yo ca pubbe pamajjitvā, </span> <span class='eng'>He who once was heedless, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp172:2'><span class='pli'>pacchā so nappamajjati; </span> <span class='eng'>but turned to heedfulness, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp172:3'><span class='pli'>Somaṁ lokaṁ pabhāseti, </span> <span class='eng'>lights up the world </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp172:4'><span class='pli'>abbhā muttova candimā. </span> <span class='eng'>like the moon freed from clouds. </span></span></span></p></blockquote></article>\n<article id='dhp173'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp173:0'><span class='pli'>Aṅgulimālattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp173:1'><span class='pli'>Yassa pāpaṁ kataṁ kammaṁ, </span> <span class='eng'>Someone whose bad deed </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp173:2'><span class='pli'>kusalena pidhīyati; </span> <span class='eng'>is supplanted by the good, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp173:3'><span class='pli'>Somaṁ lokaṁ pabhāseti, </span> <span class='eng'>lights up the world, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp173:4'><span class='pli'>abbhā muttova candimā. </span> <span class='eng'>like the moon freed from clouds. </span></span></span></p></blockquote></article>\n<article id='dhp174'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp174:0'><span class='pli'>Pesakāradhītāvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp174:1'><span class='pli'>Andhabhūto ayaṁ loko, </span> <span class='eng'>Blind is the world, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp174:2'><span class='pli'>tanukettha vipassati; </span> <span class='eng'>few are those who clearly see. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp174:3'><span class='pli'>Sakuṇo jālamuttova, </span> <span class='eng'>Only a handful go to heaven, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp174:4'><span class='pli'>appo saggāya gacchati. </span> <span class='eng'>like a bird freed from a net. </span></span></span></p></blockquote></article>\n<article id='dhp175'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp175:0'><span class='pli'>Tiṁsabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp175:1'><span class='pli'>Haṁsādiccapathe yanti, </span> <span class='eng'>Swans fly by the sun’s path, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp175:2'><span class='pli'>ākāse yanti iddhiyā; </span> <span class='eng'>psychic sages fly through space. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp175:3'><span class='pli'>Nīyanti dhīrā lokamhā, </span> <span class='eng'>The attentive leave the world, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp175:4'><span class='pli'>jetvā māraṁ savāhiniṁ. </span> <span class='eng'>having vanquished Māra with his legions. </span></span></span></p></blockquote></article>\n<article id='dhp176'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp176:0'><span class='pli'>Ciñcamāṇavikāvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp176:1'><span class='pli'>Ekaṁ dhammaṁ atītassa, </span> <span class='eng'>When a personage, spurning the hereafter, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp176:2'><span class='pli'>musāvādissa jantuno; </span> <span class='eng'>transgresses in just one thing—</span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp176:3'><span class='pli'>Vitiṇṇaparalokassa, </span> <span class='eng'>lying—</span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp176:4'><span class='pli'>natthi pāpaṁ akāriyaṁ. </span> <span class='eng'>there is no evil they would not do. </span></span></span></p></blockquote></article>\n<article id='dhp177'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp177:0'><span class='pli'>Asadisadānavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp177:1'><span class='pli'>Na ve kadariyā devalokaṁ vajanti, </span> <span class='eng'>The miserly don’t ascend to heaven, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp177:2'><span class='pli'>Bālā have nappasaṁsanti dānaṁ; </span> <span class='eng'>it takes a fool to not praise giving. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp177:3'><span class='pli'>Dhīro ca dānaṁ anumodamāno, </span> <span class='eng'>The attentive celebrate giving, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp177:4'><span class='pli'>Teneva so hoti sukhī parattha. </span> <span class='eng'>and so find happiness in the hereafter. </span></span></span></p></blockquote></article>\n<article id='dhp178'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp178:0'><span class='pli'>Anāthapiṇḍikaputtakālavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp178:1'><span class='pli'>Pathabyā ekarajjena, </span> <span class='eng'>The fruit of stream-entry is better </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp178:2'><span class='pli'>saggassa gamanena vā; </span> <span class='eng'>than being the one king of the earth, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp178:3'><span class='pli'>Sabbalokādhipaccena, </span> <span class='eng'>than going to heaven, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp178:4'><span class='pli'>sotāpattiphalaṁ varaṁ. </span> <span class='eng'>than lordship over all the world. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp178:5'><span class='pli'>Lokavaggo terasamo. </span></span></p></article></section>\n",
    "acronym": "Dhp 167–178",
    "translated_title": "13. The World",
    "original_title": "Lokavagga"
  },
  "dhp179-196": {
    "previous": "dhp167-178",
    "next": "dhp197-208",
    "content": "<section class='range'  id='dhp179-196'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp179:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp179:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 179–196 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp179:0.3'><span class='pli'>Buddhavagga </span> <span class='eng'>14. The Buddhas </span></span></h1></header>\n<article id='dhp179'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp179:0.4'><span class='pli'>Māradhītaravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp179:1'><span class='pli'>Yassa jitaṁ nāvajīyati, </span> <span class='eng'>He whose victory may not be undone, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp179:2'><span class='pli'>Jitaṁ yassa noyāti koci loke; </span> <span class='eng'>a victory unrivaled in all the world; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp179:3'><span class='pli'>Taṁ buddhamanantagocaraṁ, </span> <span class='eng'>by what track would you trace that Buddha, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp179:4'><span class='pli'>Apadaṁ kena padena nessatha. </span> <span class='eng'>who leaves no track in his infinite range? </span></span></span></p></blockquote></article>\n<article id='dhp180'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp180:1'><span class='pli'>Yassa jālinī visattikā, </span> <span class='eng'>Of craving, the weaver, the clinger, he has none: </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp180:2'><span class='pli'>Taṇhā natthi kuhiñci netave; </span> <span class='eng'>so where can he be traced? </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp180:3'><span class='pli'>Taṁ buddhamanantagocaraṁ, </span> <span class='eng'>By what track would you trace that Buddha, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp180:4'><span class='pli'>Apadaṁ kena padena nessatha. </span> <span class='eng'>who leaves no track in his infinite range? </span></span></span></p></blockquote></article>\n<article id='dhp181'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp181:0'><span class='pli'>Devorohaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp181:1'><span class='pli'>Ye jhānapasutā dhīrā, </span> <span class='eng'>The attentive intent on absorption, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp181:2'><span class='pli'>nekkhammūpasame ratā; </span> <span class='eng'>who love the peace of renunciation, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp181:3'><span class='pli'>Devāpi tesaṁ pihayanti, </span> <span class='eng'>the Buddhas, ever mindful, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp181:4'><span class='pli'>sambuddhānaṁ satīmataṁ. </span> <span class='eng'>are envied by even the gods. </span></span></span></p></blockquote></article>\n<article id='dhp182'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp182:0'><span class='pli'>Erakapattanāgarājavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp182:1'><span class='pli'>Kiccho manussapaṭilābho, </span> <span class='eng'>It’s hard to gain a human birth; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp182:2'><span class='pli'>kicchaṁ maccāna jīvitaṁ; </span> <span class='eng'>the life of mortals is hard; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp182:3'><span class='pli'>Kicchaṁ saddhammassavanaṁ, </span> <span class='eng'>it’s hard to hear the true teaching; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp182:4'><span class='pli'>kiccho buddhānamuppādo. </span> <span class='eng'>the arising of Buddhas is hard. </span></span></span></p></blockquote></article>\n<article id='dhp183'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp183:0'><span class='pli'>Ānandattherapañhavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp183:1'><span class='pli'>Sabbapāpassa akaraṇaṁ, </span> <span class='eng'>Not to do any evil; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp183:2'><span class='pli'>kusalassa upasampadā; </span> <span class='eng'>to embrace the good; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp183:3'><span class='pli'>Sacittapariyodapanaṁ, </span> <span class='eng'>to purify one’s mind: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp183:4'><span class='pli'>etaṁ buddhāna sāsanaṁ. </span> <span class='eng'>this is the instruction of the Buddhas. </span></span></span></p></blockquote></article>\n<article id='dhp184'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp184:1'><span class='pli'>Khantī paramaṁ tapo titikkhā, </span> <span class='eng'>Patient acceptance is the ultimate fervor. </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp184:2'><span class='pli'>Nibbānaṁ paramaṁ vadanti buddhā; </span> <span class='eng'>Extinguishment is the ultimate, say the Buddhas. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp184:3'><span class='pli'>Na hi pabbajito parūpaghātī, </span> <span class='eng'>No true renunciate injures another, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp184:4'><span class='pli'>Na samaṇo hoti paraṁ viheṭhayanto. </span> <span class='eng'>nor does an ascetic hurt another. </span></span></span></p></blockquote></article>\n<article id='dhp185'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp185:1'><span class='pli'>Anūpavādo anūpaghāto, </span> <span class='eng'>Not speaking ill nor doing harm; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp185:2'><span class='pli'>Pātimokkhe ca saṁvaro; </span> <span class='eng'>restraint in the monastic code; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp185:3'><span class='pli'>Mattaññutā ca bhattasmiṁ, </span> <span class='eng'>moderation in eating; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp185:4'><span class='pli'>Pantañca sayanāsanaṁ; </span> <span class='eng'>staying in remote lodgings; </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp185:5'><span class='pli'>Adhicitte ca āyogo, </span> <span class='eng'>commitment to the higher mind—</span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp185:6'><span class='pli'>Etaṁ buddhāna sāsanaṁ. </span> <span class='eng'>this is the instruction of the Buddhas. </span></span></span></p></blockquote></article>\n<article id='dhp186'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp186:0'><span class='pli'>Anabhiratabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp186:1'><span class='pli'>Na kahāpaṇavassena, </span> <span class='eng'>Even if it were raining money, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp186:2'><span class='pli'>titti kāmesu vijjati; </span> <span class='eng'>you’d not be sated in sensual pleasures. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp186:3'><span class='pli'>Appassādā dukhā kāmā, </span> <span class='eng'>An astute person understands that sensual pleasures </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp186:4'><span class='pli'>iti viññāya paṇḍito. </span> <span class='eng'>offer little gratification and much suffering. </span></span></span></p></blockquote></article>\n<article id='dhp187'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp187:1'><span class='pli'>Api dibbesu kāmesu, </span> <span class='eng'>Thus they find no delight </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp187:2'><span class='pli'>ratiṁ so nādhigacchati; </span> <span class='eng'>even in celestial pleasures. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp187:3'><span class='pli'>Taṇhakkhayarato hoti, </span> <span class='eng'>A disciple of the fully awakened Buddha </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp187:4'><span class='pli'>sammāsambuddhasāvako. </span> <span class='eng'>delights in the ending of craving. </span></span></span></p></blockquote></article>\n<article id='dhp188'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp188:0'><span class='pli'>Aggidattabrāhmaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp188:1'><span class='pli'>Bahuṁ ve saraṇaṁ yanti, </span> <span class='eng'>So many go for refuge </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp188:2'><span class='pli'>pabbatāni vanāni ca; </span> <span class='eng'>to mountains and forest groves, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp188:3'><span class='pli'>Ārāmarukkhacetyāni, </span> <span class='eng'>to shrines in tended parks; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp188:4'><span class='pli'>manussā bhayatajjitā. </span> <span class='eng'>those people are driven by fear. </span></span></span></p></blockquote></article>\n<article id='dhp189'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp189:1'><span class='pli'>Netaṁ kho saraṇaṁ khemaṁ, </span> <span class='eng'>But such refuge is no sanctuary, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp189:2'><span class='pli'>netaṁ saraṇamuttamaṁ; </span> <span class='eng'>it is no supreme refuge. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp189:3'><span class='pli'>Netaṁ saraṇamāgamma, </span> <span class='eng'>By going to that refuge, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp189:4'><span class='pli'>sabbadukkhā pamuccati. </span> <span class='eng'>you’re not released from all suffering. </span></span></span></p></blockquote></article>\n<article id='dhp190'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp190:1'><span class='pli'>Yo ca buddhañca dhammañca, </span> <span class='eng'>One gone for refuge to the Buddha, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp190:2'><span class='pli'>saṅghañca saraṇaṁ gato; </span> <span class='eng'>to his teaching and to the Saṅgha, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp190:3'><span class='pli'>Cattāri ariyasaccāni, </span> <span class='eng'>sees the four noble truths </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp190:4'><span class='pli'>sammappaññāya passati. </span> <span class='eng'>with right understanding: </span></span></span></p></blockquote></article>\n<article id='dhp191'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp191:1'><span class='pli'>Dukkhaṁ dukkhasamuppādaṁ, </span> <span class='eng'>suffering, suffering’s origin, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp191:2'><span class='pli'>dukkhassa ca atikkamaṁ; </span> <span class='eng'>suffering’s transcendence, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp191:3'><span class='pli'>Ariyaṁ caṭṭhaṅgikaṁ maggaṁ, </span> <span class='eng'>and the noble eightfold path </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp191:4'><span class='pli'>dukkhūpasamagāminaṁ. </span> <span class='eng'>that leads to the stilling of suffering. </span></span></span></p></blockquote></article>\n<article id='dhp192'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp192:1'><span class='pli'>Etaṁ kho saraṇaṁ khemaṁ, </span> <span class='eng'>Such refuge is a sanctuary, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp192:2'><span class='pli'>etaṁ saraṇamuttamaṁ; </span> <span class='eng'>it is the supreme refuge. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp192:3'><span class='pli'>Etaṁ saraṇamāgamma, </span> <span class='eng'>By going to that refuge, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp192:4'><span class='pli'>sabbadukkhā pamuccati. </span> <span class='eng'>you’re released from all suffering. </span></span></span></p></blockquote></article>\n<article id='dhp193'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp193:0'><span class='pli'>Ānandattherapañhavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp193:1'><span class='pli'>Dullabho purisājañño, </span> <span class='eng'>It’s hard to find a thoroughbred man: </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp193:2'><span class='pli'>na so sabbattha jāyati; </span> <span class='eng'>they’re not born just anywhere. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp193:3'><span class='pli'>Yattha so jāyati dhīro, </span> <span class='eng'>A family where that attentive one is born </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp193:4'><span class='pli'>taṁ kulaṁ sukhamedhati. </span> <span class='eng'>prospers in happiness. </span></span></span></p></blockquote></article>\n<article id='dhp194'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp194:0'><span class='pli'>Sambahulabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp194:1'><span class='pli'>Sukho buddhānamuppādo, </span> <span class='eng'>Happy, the arising of Buddhas! </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp194:2'><span class='pli'>sukhā saddhammadesanā; </span> <span class='eng'>Happy, the teaching of Dhamma! </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp194:3'><span class='pli'>Sukhā saṅghassa sāmaggī, </span> <span class='eng'>Happy is the harmony of the Saṅgha, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp194:4'><span class='pli'>samaggānaṁ tapo sukho. </span> <span class='eng'>and the striving of the harmonious is happy. </span></span></span></p></blockquote></article>\n<article id='dhp195'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp195:0'><span class='pli'>Kassapadasabalassasuvaṇṇacetiyavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp195:1'><span class='pli'>Pūjārahe pūjayato, </span> <span class='eng'>When a person venerates the worthy—</span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp195:2'><span class='pli'>buddhe yadi va sāvake; </span> <span class='eng'>the Buddha or his disciple, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp195:3'><span class='pli'>Papañcasamatikkante, </span> <span class='eng'>who have transcended proliferation, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp195:4'><span class='pli'>tiṇṇasokapariddave. </span> <span class='eng'>and have left behind grief and lamentation, </span></span></span></p></blockquote></article>\n<article id='dhp196'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp196:1'><span class='pli'>Te tādise pūjayato, </span> <span class='eng'>quenched, fearing nothing from any quarter—</span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp196:2'><span class='pli'>nibbute akutobhaye; </span> <span class='eng'>the merit of one venerating such as these, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp196:3'><span class='pli'>Na sakkā puññaṁ saṅkhātuṁ, </span> <span class='eng'>cannot be calculated by anyone, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp196:4'><span class='pli'>imettamapi kenaci. </span> <span class='eng'>saying it is just this much. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp196:5'><span class='pli'>Buddhavaggo cuddasamo. </span></span></p>\n<p class='endsection'><span class='segment' id='6' data-uid='dhp196:6'><span class='pli'>Paṭhamabhāṇavāraṁ. </span></span></p></article></section>\n",
    "acronym": "Dhp 179–196",
    "translated_title": "14. The Buddhas",
    "original_title": "Buddhavagga"
  },
  "dhp197-208": {
    "previous": "dhp179-196",
    "next": "dhp209-220",
    "content": "<section class='range'  id='dhp197-208'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp197:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp197:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 197–208 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp197:0.3'><span class='pli'>Sukhavagga </span> <span class='eng'>15. Happiness </span></span></h1></header>\n<article id='dhp197'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp197:0.4'><span class='pli'>Ñātikalahavūpasamanavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp197:1'><span class='pli'>Susukhaṁ vata jīvāma, </span> <span class='eng'>Let us live so very happily, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp197:2'><span class='pli'>verinesu averino; </span> <span class='eng'>loving among the hostile. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp197:3'><span class='pli'>Verinesu manussesu, </span> <span class='eng'>Among hostile humans, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp197:4'><span class='pli'>viharāma averino. </span> <span class='eng'>let us live with love. </span></span></span></p></blockquote></article>\n<article id='dhp198'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp198:1'><span class='pli'>Susukhaṁ vata jīvāma, </span> <span class='eng'>Let us live so very happily, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp198:2'><span class='pli'>āturesu anāturā; </span> <span class='eng'>healthy among the ailing. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp198:3'><span class='pli'>Āturesu manussesu, </span> <span class='eng'>Among ailing humans </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp198:4'><span class='pli'>viharāma anāturā. </span> <span class='eng'>let us live healthily. </span></span></span></p></blockquote></article>\n<article id='dhp199'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp199:1'><span class='pli'>Susukhaṁ vata jīvāma, </span> <span class='eng'>Let us live so very happily, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp199:2'><span class='pli'>ussukesu anussukā; </span> <span class='eng'>content among the greedy. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp199:3'><span class='pli'>Ussukesu manussesu, </span> <span class='eng'>Among greedy humans, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp199:4'><span class='pli'>viharāma anussukā. </span> <span class='eng'>let us live content. </span></span></span></p></blockquote></article>\n<article id='dhp200'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp200:0'><span class='pli'>Māravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp200:1'><span class='pli'>Susukhaṁ vata jīvāma, </span> <span class='eng'>Let us live so very happily, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp200:2'><span class='pli'>yesaṁ no natthi kiñcanaṁ; </span> <span class='eng'>we who have nothing. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp200:3'><span class='pli'>Pītibhakkhā bhavissāma, </span> <span class='eng'>We shall feed on rapture, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp200:4'><span class='pli'>devā ābhassarā yathā. </span> <span class='eng'>like the gods of streaming radiance. </span></span></span></p></blockquote></article>\n<article id='dhp201'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp201:0'><span class='pli'>Kosalaraññoparājayavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp201:1'><span class='pli'>Jayaṁ veraṁ pasavati, </span> <span class='eng'>Victory breeds enmity; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp201:2'><span class='pli'>dukkhaṁ seti parājito; </span> <span class='eng'>the defeated sleep badly. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp201:3'><span class='pli'>Upasanto sukhaṁ seti, </span> <span class='eng'>The peaceful sleep at ease, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp201:4'><span class='pli'>hitvā jayaparājayaṁ. </span> <span class='eng'>having left victory and defeat behind. </span></span></span></p></blockquote></article>\n<article id='dhp202'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp202:0'><span class='pli'>Aññatarakuladārikāvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp202:1'><span class='pli'>Natthi rāgasamo aggi, </span> <span class='eng'>There is no fire like greed, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp202:2'><span class='pli'>Natthi dosasamo kali; </span> <span class='eng'>no crime like hate, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp202:3'><span class='pli'>Natthi khandhasamā dukkhā, </span> <span class='eng'>no suffering like the aggregates, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp202:4'><span class='pli'>Natthi santiparaṁ sukhaṁ. </span> <span class='eng'>no bliss beyond peace. </span></span></span></p></blockquote></article>\n<article id='dhp203'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp203:0'><span class='pli'>Ekaupāsakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp203:1'><span class='pli'>Jighacchāparamā rogā, </span> <span class='eng'>Hunger is the worst illness, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp203:2'><span class='pli'>saṅkhāraparamā dukhā; </span> <span class='eng'>conditions are the worst suffering. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp203:3'><span class='pli'>Etaṁ ñatvā yathābhūtaṁ, </span> <span class='eng'>For one who truly knows this, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp203:4'><span class='pli'>nibbānaṁ paramaṁ sukhaṁ. </span> <span class='eng'>extinguishment is the ultimate happiness. </span></span></span></p></blockquote></article>\n<article id='dhp204'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp204:0'><span class='pli'>Pasenadikosalavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp204:1'><span class='pli'>Ārogyaparamā lābhā, </span> <span class='eng'>Health is the ultimate blessing; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp204:2'><span class='pli'>Santuṭṭhiparamaṁ dhanaṁ; </span> <span class='eng'>contentment, the ultimate wealth; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp204:3'><span class='pli'>Vissāsaparamā ñāti, </span> <span class='eng'>trust is the ultimate family; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp204:4'><span class='pli'>Nibbānaṁ paramaṁ sukhaṁ. </span> <span class='eng'>extinguishment, the ultimate happiness. </span></span></span></p></blockquote></article>\n<article id='dhp205'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp205:0'><span class='pli'>Tissattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp205:1'><span class='pli'>Pavivekarasaṁ pitvā, </span> <span class='eng'>Having drunk the nectar of seclusion </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp205:2'><span class='pli'>rasaṁ upasamassa ca; </span> <span class='eng'>and the nectar of peace—</span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp205:3'><span class='pli'>Niddaro hoti nippāpo, </span> <span class='eng'>free of stress, free of evil, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp205:4'><span class='pli'>dhammapītirasaṁ pivaṁ. </span> <span class='eng'>drink the joyous nectar of truth. </span></span></span></p></blockquote></article>\n<article id='dhp206'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp206:0'><span class='pli'>Sakkavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp206:1'><span class='pli'>Sāhu dassanamariyānaṁ, </span> <span class='eng'>It’s good to see the noble ones, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp206:2'><span class='pli'>sannivāso sadā sukho; </span> <span class='eng'>staying with them is always good. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp206:3'><span class='pli'>Adassanena bālānaṁ, </span> <span class='eng'>Were you not to see fools, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp206:4'><span class='pli'>niccameva sukhī siyā. </span> <span class='eng'>you’d always be happy. </span></span></span></p></blockquote></article>\n<article id='dhp207'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp207:1'><span class='pli'>Bālasaṅgatacārī hi, </span> <span class='eng'>For one who consorts with fools </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp207:2'><span class='pli'>dīghamaddhāna socati; </span> <span class='eng'>grieves long. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp207:3'><span class='pli'>Dukkho bālehi saṁvāso, </span> <span class='eng'>Painful is living with fools, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp207:4'><span class='pli'>amitteneva sabbadā; </span> <span class='eng'>like being stuck with your enemy. </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp207:5'><span class='pli'>Dhīro ca sukhasaṁvāso, </span> <span class='eng'>Happy is living with an attentive one, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp207:6'><span class='pli'>ñātīnaṁva samāgamo. </span> <span class='eng'>like meeting with your kin. </span></span></span></p></blockquote>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp208:1'><span class='pli'>Tasmā hi—</span> <span class='eng'>Therefore: </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp208:2'><span class='pli'>Dhīrañca paññañca bahussutañca, </span> <span class='eng'>An attentive one, wise and learned, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp208:3'><span class='pli'>Dhorayhasīlaṁ vatavantamariyaṁ; </span> <span class='eng'>a behemoth of virtue, true to their vows, noble: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp208:4'><span class='pli'>Taṁ tādisaṁ sappurisaṁ sumedhaṁ, </span> <span class='eng'>follow a true and intelligent person such as this, </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp208:5'><span class='pli'>Bhajetha nakkhattapathaṁva candimā. </span> <span class='eng'>as the moon tracks the path of the stars. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='6' data-uid='dhp208:6'><span class='pli'>Sukhavaggo pannarasamo. </span></span></p></article></section>\n",
    "acronym": "Dhp 197–208",
    "translated_title": "15. Happiness",
    "original_title": "Sukhavagga"
  },
  "dhp209-220": {
    "previous": "dhp197-208",
    "next": "dhp221-234",
    "content": "<section class='range'  id='dhp209-220'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp209:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp209:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 209–220 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp209:0.3'><span class='pli'>Piyavagga </span> <span class='eng'>16. The Beloved </span></span></h1></header>\n<article id='dhp209'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp209:0.4'><span class='pli'>Tayojanapabbajitavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp209:1'><span class='pli'>Ayoge yuñjamattānaṁ, </span> <span class='eng'>Applying yourself where you ought not, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp209:2'><span class='pli'>yogasmiñca ayojayaṁ; </span> <span class='eng'>neglecting what you should be doing, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp209:3'><span class='pli'>Atthaṁ hitvā piyaggāhī, </span> <span class='eng'>forgetting your goal, you cling to what you hold dear, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp209:4'><span class='pli'>pihetattānuyoginaṁ. </span> <span class='eng'>jealous of those devoted to their heart’s goal. </span></span></span></p></blockquote></article>\n<article id='dhp210'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp210:1'><span class='pli'>Mā piyehi samāgañchi, </span> <span class='eng'>Don’t ever get too close </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp210:2'><span class='pli'>appiyehi kudācanaṁ; </span> <span class='eng'>to those you like or dislike. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp210:3'><span class='pli'>Piyānaṁ adassanaṁ dukkhaṁ, </span> <span class='eng'>For not seeing the liked is suffering, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp210:4'><span class='pli'>appiyānañca dassanaṁ. </span> <span class='eng'>and so is seeing the disliked. </span></span></span></p></blockquote></article>\n<article id='dhp211'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp211:1'><span class='pli'>Tasmā piyaṁ na kayirātha, </span> <span class='eng'>Therefore don’t hold anything dear, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp211:2'><span class='pli'>piyāpāyo hi pāpako; </span> <span class='eng'>for it’s bad to lose those you love. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp211:3'><span class='pli'>Ganthā tesaṁ na vijjanti, </span> <span class='eng'>No ties are found in they who </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp211:4'><span class='pli'>yesaṁ natthi piyāppiyaṁ. </span> <span class='eng'>hold nothing loved or loathed. </span></span></span></p></blockquote></article>\n<article id='dhp212'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp212:0'><span class='pli'>Aññatarakuṭumbikavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp212:1'><span class='pli'>Piyato jāyatī soko, </span> <span class='eng'>Sorrow springs from what we hold dear, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp212:2'><span class='pli'>piyato jāyatī bhayaṁ; </span> <span class='eng'>fear springs from what we hold dear; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp212:3'><span class='pli'>Piyato vippamuttassa, </span> <span class='eng'>one free from holding anything dear </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp212:4'><span class='pli'>natthi soko kuto bhayaṁ. </span> <span class='eng'>has no sorrow, let alone fear. </span></span></span></p></blockquote></article>\n<article id='dhp213'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp213:0'><span class='pli'>Visākhāvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp213:1'><span class='pli'>Pemato jāyatī soko, </span> <span class='eng'>Sorrow springs from attachment, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp213:2'><span class='pli'>pemato jāyatī bhayaṁ; </span> <span class='eng'>fear springs from attachment; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp213:3'><span class='pli'>Pemato vippamuttassa, </span> <span class='eng'>one free from attachment </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp213:4'><span class='pli'>natthi soko kuto bhayaṁ. </span> <span class='eng'>has no sorrow, let alone fear. </span></span></span></p></blockquote></article>\n<article id='dhp214'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp214:0'><span class='pli'>Licchavīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp214:1'><span class='pli'>Ratiyā jāyatī soko, </span> <span class='eng'>Sorrow springs from relishing, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp214:2'><span class='pli'>ratiyā jāyatī bhayaṁ; </span> <span class='eng'>fear springs from relishing; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp214:3'><span class='pli'>Ratiyā vippamuttassa, </span> <span class='eng'>one free from relishing </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp214:4'><span class='pli'>natthi soko kuto bhayaṁ. </span> <span class='eng'>has no sorrow, let alone fear. </span></span></span></p></blockquote></article>\n<article id='dhp215'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp215:0'><span class='pli'>Anitthigandhakumāravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp215:1'><span class='pli'>Kāmato jāyatī soko, </span> <span class='eng'>Sorrow springs from desire, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp215:2'><span class='pli'>kāmato jāyatī bhayaṁ; </span> <span class='eng'>fear springs from desire; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp215:3'><span class='pli'>Kāmato vippamuttassa, </span> <span class='eng'>one free from desire </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp215:4'><span class='pli'>natthi soko kuto bhayaṁ. </span> <span class='eng'>has no sorrow, let alone fear. </span></span></span></p></blockquote></article>\n<article id='dhp216'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp216:0'><span class='pli'>Aññatarabrāhmaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp216:1'><span class='pli'>Taṇhāya jāyatī soko, </span> <span class='eng'>Sorrow springs from craving, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp216:2'><span class='pli'>taṇhāya jāyatī bhayaṁ; </span> <span class='eng'>fear springs from craving; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp216:3'><span class='pli'>Taṇhāya vippamuttassa, </span> <span class='eng'>one free from craving </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp216:4'><span class='pli'>natthi soko kuto bhayaṁ. </span> <span class='eng'>has no sorrow, let alone fear. </span></span></span></p></blockquote></article>\n<article id='dhp217'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp217:0'><span class='pli'>Pañcasatadārakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp217:1'><span class='pli'>Sīladassanasampannaṁ, </span> <span class='eng'>One accomplished in virtue and vision, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp217:2'><span class='pli'>dhammaṭṭhaṁ saccavedinaṁ; </span> <span class='eng'>firmly principled, and truthful, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp217:3'><span class='pli'>Attano kamma kubbānaṁ, </span> <span class='eng'>doing oneself what ought be done: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp217:4'><span class='pli'>taṁ jano kurute piyaṁ. </span> <span class='eng'>that’s who the people love. </span></span></span></p></blockquote></article>\n<article id='dhp218'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp218:0'><span class='pli'>Ekaanāgāmittheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp218:1'><span class='pli'>Chandajāto anakkhāte, </span> <span class='eng'>One eager to realize the ineffable </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp218:2'><span class='pli'>Manasā ca phuṭo siyā; </span> <span class='eng'>would be filled with awareness. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp218:3'><span class='pli'>Kāmesu ca appaṭibaddhacitto, </span> <span class='eng'>Their mind not bound to pleasures of sense, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp218:4'><span class='pli'>Uddhaṁsototi vuccati. </span> <span class='eng'>they’re said to be heading upstream. </span></span></span></p></blockquote></article>\n<article id='dhp219'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp219:0'><span class='pli'>Nandiyavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp219:1'><span class='pli'>Cirappavāsiṁ purisaṁ, </span> <span class='eng'>When a man returns safely </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp219:2'><span class='pli'>dūrato sotthimāgataṁ; </span> <span class='eng'>after a long time spent abroad, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp219:3'><span class='pli'>Ñātimittā suhajjā ca, </span> <span class='eng'>family, friends, and loved ones </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp219:4'><span class='pli'>abhinandanti āgataṁ. </span> <span class='eng'>celebrate his return. </span></span></span></p></blockquote></article>\n<article id='dhp220'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp220:1'><span class='pli'>Tatheva katapuññampi, </span> <span class='eng'>Just so, when one who has done good </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp220:2'><span class='pli'>asmā lokā paraṁ gataṁ; </span> <span class='eng'>goes from this world to the next, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp220:3'><span class='pli'>Puññāni paṭigaṇhanti, </span> <span class='eng'>their good deeds receive them there, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp220:4'><span class='pli'>piyaṁ ñātīva āgataṁ. </span> <span class='eng'>as family welcomes home one they love. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp220:5'><span class='pli'>Piyavaggo soḷasamo. </span></span></p></article></section>\n",
    "acronym": "Dhp 209–220",
    "translated_title": "16. The Beloved",
    "original_title": "Piyavagga"
  },
  "dhp221-234": {
    "previous": "dhp209-220",
    "next": "dhp235-255",
    "content": "<section class='range'  id='dhp221-234'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp221:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp221:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 221–234 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp221:0.3'><span class='pli'>Kodhavagga </span> <span class='eng'>17. Anger </span></span></h1></header>\n<article id='dhp221'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp221:0.4'><span class='pli'>Rohinīkhattiyakaññāvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp221:1'><span class='pli'>Kodhaṁ jahe vippajaheyya mānaṁ, </span> <span class='eng'>Give up anger, get rid of conceit, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp221:2'><span class='pli'>Saṁyojanaṁ sabbamatikkameyya; </span> <span class='eng'>and escape every fetter. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp221:3'><span class='pli'>Taṁ nāmarūpasmimasajjamānaṁ, </span> <span class='eng'>Sufferings don’t befall one who has nothing, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp221:4'><span class='pli'>Akiñcanaṁ nānupatanti dukkhā. </span> <span class='eng'>not clinging to name and form. </span></span></span></p></blockquote></article>\n<article id='dhp222'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp222:0'><span class='pli'>Aññatarabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp222:1'><span class='pli'>Yo ve uppatitaṁ kodhaṁ, </span> <span class='eng'>When anger surges like a lurching chariot, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp222:2'><span class='pli'>rathaṁ bhantaṁva vāraye; </span> <span class='eng'>keep it in check. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp222:3'><span class='pli'>Tamahaṁ sārathiṁ brūmi, </span> <span class='eng'>That’s what I call a charioteer; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp222:4'><span class='pli'>rasmiggāho itaro jano. </span> <span class='eng'>others just hold the reins. </span></span></span></p></blockquote></article>\n<article id='dhp223'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp223:0'><span class='pli'>Uttarāupāsikavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp223:1'><span class='pli'>Akkodhena jine kodhaṁ, </span> <span class='eng'>Defeat anger with kindness, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp223:2'><span class='pli'>asādhuṁ sādhunā jine; </span> <span class='eng'>villainy with virtue, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp223:3'><span class='pli'>Jine kadariyaṁ dānena, </span> <span class='eng'>stinginess with giving, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp223:4'><span class='pli'>saccenālikavādinaṁ. </span> <span class='eng'>and lies with truth. </span></span></span></p></blockquote></article>\n<article id='dhp224'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp224:0'><span class='pli'>Mahāmoggallānapañhavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp224:1'><span class='pli'>Saccaṁ bhaṇe na kujjheyya, </span> <span class='eng'>Speak the truth, do not be angry, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp224:2'><span class='pli'>dajjā appampi yācito; </span> <span class='eng'>and give when asked, if only a little. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp224:3'><span class='pli'>Etehi tīhi ṭhānehi, </span> <span class='eng'>By these three means, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp224:4'><span class='pli'>gacche devāna santike. </span> <span class='eng'>you may enter the presence of the gods. </span></span></span></p></blockquote></article>\n<article id='dhp225'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp225:0'><span class='pli'>Buddhapitubrāhmaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp225:1'><span class='pli'>Ahiṁsakā ye munayo, </span> <span class='eng'>Those harmless sages, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp225:2'><span class='pli'>niccaṁ kāyena saṁvutā; </span> <span class='eng'>always restrained in body, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp225:3'><span class='pli'>Te yanti accutaṁ ṭhānaṁ, </span> <span class='eng'>go to the state that does not pass, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp225:4'><span class='pli'>yattha gantvā na socare. </span> <span class='eng'>where there is no sorrow. </span></span></span></p></blockquote></article>\n<article id='dhp226'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp226:0'><span class='pli'>Puṇṇadāsīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp226:1'><span class='pli'>Sadā jāgaramānānaṁ, </span> <span class='eng'>Always wakeful, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp226:2'><span class='pli'>ahorattānusikkhinaṁ; </span> <span class='eng'>practicing night and day, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp226:3'><span class='pli'>Nibbānaṁ adhimuttānaṁ, </span> <span class='eng'>focused only on extinguishment, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp226:4'><span class='pli'>atthaṁ gacchanti āsavā. </span> <span class='eng'>their defilements come to an end. </span></span></span></p></blockquote></article>\n<article id='dhp227'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp227:0'><span class='pli'>Atulaupāsakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp227:1'><span class='pli'>Porāṇametaṁ atula, </span> <span class='eng'>It’s always been this way, Atula, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp227:2'><span class='pli'>netaṁ ajjatanāmiva; </span> <span class='eng'>it’s not just today. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp227:3'><span class='pli'>Nindanti tuṇhimāsīnaṁ, </span> <span class='eng'>They blame you when you’re silent, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp227:4'><span class='pli'>nindanti bahubhāṇinaṁ; </span> <span class='eng'>they blame you when you speak a lot, </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp227:5'><span class='pli'>Mitabhāṇimpi nindanti, </span> <span class='eng'>and even when you speak just right: </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp227:6'><span class='pli'>natthi loke anindito. </span> <span class='eng'>no-one in the world escapes blame. </span></span></span></p></blockquote></article>\n<article id='dhp228'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp228:1'><span class='pli'>Na cāhu na ca bhavissati, </span> <span class='eng'>There never was, nor will be, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp228:2'><span class='pli'>na cetarahi vijjati; </span> <span class='eng'>nor is there today, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp228:3'><span class='pli'>Ekantaṁ nindito poso, </span> <span class='eng'>someone who is wholly praised </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp228:4'><span class='pli'>ekantaṁ vā pasaṁsito. </span> <span class='eng'>or wholly blamed. </span></span></span></p></blockquote></article>\n<article id='dhp229'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp229:1'><span class='pli'>Yañce viññū pasaṁsanti, </span> <span class='eng'>If, after watching them day in day out, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp229:2'><span class='pli'>anuvicca suve suve; </span> <span class='eng'>discerning people praise </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp229:3'><span class='pli'>Acchiddavuttiṁ medhāviṁ, </span> <span class='eng'>that sage of impeccable conduct, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp229:4'><span class='pli'>paññāsīlasamāhitaṁ. </span> <span class='eng'>endowed with ethics and wisdom; </span></span></span></p></blockquote></article>\n<article id='dhp230'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp230:1'><span class='pli'>Nikkhaṁ jambonadasseva, </span> <span class='eng'>like a pendant of Black Plum River gold, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp230:2'><span class='pli'>ko taṁ ninditumarahati; </span> <span class='eng'>who is worthy to criticize them? </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp230:3'><span class='pli'>Devāpi naṁ pasaṁsanti, </span> <span class='eng'>Even the gods praise them, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp230:4'><span class='pli'>brahmunāpi pasaṁsito. </span> <span class='eng'>and by the Divinity, too, they’re praised. </span></span></span></p></blockquote></article>\n<article id='dhp231'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp231:0'><span class='pli'>Chabbaggiyavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp231:1'><span class='pli'>Kāyappakopaṁ rakkheyya, </span> <span class='eng'>Guard against ill-tempered deeds, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp231:2'><span class='pli'>kāyena saṁvuto siyā; </span> <span class='eng'>be restrained in body. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp231:3'><span class='pli'>Kāyaduccaritaṁ hitvā, </span> <span class='eng'>Giving up bad bodily conduct, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp231:4'><span class='pli'>kāyena sucaritaṁ care. </span> <span class='eng'>conduct yourself well in body. </span></span></span></p></blockquote></article>\n<article id='dhp232'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp232:1'><span class='pli'>Vacīpakopaṁ rakkheyya, </span> <span class='eng'>Guard against ill-tempered words, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp232:2'><span class='pli'>vācāya saṁvuto siyā; </span> <span class='eng'>be restrained in speech. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp232:3'><span class='pli'>Vacīduccaritaṁ hitvā, </span> <span class='eng'>Giving up bad verbal conduct, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp232:4'><span class='pli'>vācāya sucaritaṁ care. </span> <span class='eng'>conduct yourself well in speech. </span></span></span></p></blockquote></article>\n<article id='dhp233'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp233:1'><span class='pli'>Manopakopaṁ rakkheyya, </span> <span class='eng'>Guard against ill-tempered thoughts, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp233:2'><span class='pli'>manasā saṁvuto siyā; </span> <span class='eng'>be restrained in mind. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp233:3'><span class='pli'>Manoduccaritaṁ hitvā, </span> <span class='eng'>Giving up bad mental conduct, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp233:4'><span class='pli'>manasā sucaritaṁ care. </span> <span class='eng'>conduct yourself well in mind. </span></span></span></p></blockquote></article>\n<article id='dhp234'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp234:1'><span class='pli'>Kāyena saṁvutā dhīrā, </span> <span class='eng'>An attentive one is restrained in body </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp234:2'><span class='pli'>atho vācāya saṁvutā; </span> <span class='eng'>restrained also in speech, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp234:3'><span class='pli'>Manasā saṁvutā dhīrā, </span> <span class='eng'>in thought, too, they are restrained: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp234:4'><span class='pli'>te ve suparisaṁvutā. </span> <span class='eng'>they are restrained in every way. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp234:5'><span class='pli'>Kodhavaggo sattarasamo. </span></span></p></article></section>\n",
    "acronym": "Dhp 221–234",
    "translated_title": "17. Anger",
    "original_title": "Kodhavagga"
  },
  "dhp235-255": {
    "previous": "dhp221-234",
    "next": "dhp256-272",
    "content": "<section class='range'  id='dhp235-255'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp235:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp235:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 235–255 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp235:0.3'><span class='pli'>Malavagga </span> <span class='eng'>18. Stains </span></span></h1></header>\n<article id='dhp235'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp235:0.4'><span class='pli'>Goghātakaputtavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp235:1'><span class='pli'>Paṇḍupalāsova dānisi, </span> <span class='eng'>Today you’re like a withered leaf, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp235:2'><span class='pli'>Yamapurisāpi ca te upaṭṭhitā; </span> <span class='eng'>Yama’s men await you. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp235:3'><span class='pli'>Uyyogamukhe ca tiṭṭhasi, </span> <span class='eng'>You stand at the departure gates, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp235:4'><span class='pli'>Pātheyyampi ca te na vijjati. </span> <span class='eng'>yet you have no supplies for the road. </span></span></span></p></blockquote></article>\n<article id='dhp236'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp236:1'><span class='pli'>So karohi dīpamattano, </span> <span class='eng'>Make an island of yourself! </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp236:2'><span class='pli'>Khippaṁ vāyama paṇḍito bhava; </span> <span class='eng'>Swiftly strive, learn to be wise! </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp236:3'><span class='pli'>Niddhantamalo anaṅgaṇo, </span> <span class='eng'>Purged of stains, flawless, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp236:4'><span class='pli'>Dibbaṁ ariyabhūmiṁ upehisi. </span> <span class='eng'>you’ll go to the heavenly realm of the noble ones. </span></span></span></p></blockquote></article>\n<article id='dhp237'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp237:1'><span class='pli'>Upanītavayo ca dānisi, </span> <span class='eng'>You’ve journeyed the stages of life, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp237:2'><span class='pli'>Sampayātosi yamassa santikaṁ; </span> <span class='eng'>and now you set out to meet Yama. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp237:3'><span class='pli'>Vāso te natthi antarā, </span> <span class='eng'>Along the way there’s nowhere to stay, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp237:4'><span class='pli'>Pātheyyampi ca te na vijjati. </span> <span class='eng'>yet you have no supplies for the road. </span></span></span></p></blockquote></article>\n<article id='dhp238'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp238:1'><span class='pli'>So karohi dīpamattano, </span> <span class='eng'>Make an island of yourself! </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp238:2'><span class='pli'>Khippaṁ vāyama paṇḍito bhava; </span> <span class='eng'>Swiftly strive, learn to be wise! </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp238:3'><span class='pli'>Niddhantamalo anaṅgaṇo, </span> <span class='eng'>Purged of stains, flawless, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp238:4'><span class='pli'>Na punaṁ jātijaraṁ upehisi. </span> <span class='eng'>you’ll not come again to rebirth and old age. </span></span></span></p></blockquote></article>\n<article id='dhp239'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp239:0'><span class='pli'>Aññatarabrāhmaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp239:1'><span class='pli'>Anupubbena medhāvī, </span> <span class='eng'>A smart person would purge </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp239:2'><span class='pli'>thokaṁ thokaṁ khaṇe khaṇe; </span> <span class='eng'>their own stains gradually, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp239:3'><span class='pli'>Kammāro rajatasseva, </span> <span class='eng'>bit by bit, moment by moment, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp239:4'><span class='pli'>niddhame malamattano. </span> <span class='eng'>like a smith smelting silver. </span></span></span></p></blockquote></article>\n<article id='dhp240'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp240:0'><span class='pli'>Tissattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp240:1'><span class='pli'>Ayasāva malaṁ samuṭṭhitaṁ, </span> <span class='eng'>It is the rust born on the iron </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp240:2'><span class='pli'>Tatuṭṭhāya tameva khādati; </span> <span class='eng'>that eats away the place it arose. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp240:3'><span class='pli'>Evaṁ atidhonacārinaṁ, </span> <span class='eng'>And so it is their own deeds </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp240:4'><span class='pli'>Sāni kammāni nayanti duggatiṁ. </span> <span class='eng'>that lead the overly-ascetic to a bad place. </span></span></span></p></blockquote></article>\n<article id='dhp241'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp241:0'><span class='pli'>Lāḷudāyīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp241:1'><span class='pli'>Asajjhāyamalā mantā, </span> <span class='eng'>Not rehearsing is the stain of hymns. </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp241:2'><span class='pli'>anuṭṭhānamalā gharā; </span> <span class='eng'>The stain of houses is neglect. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp241:3'><span class='pli'>Malaṁ vaṇṇassa kosajjaṁ, </span> <span class='eng'>Laziness is the stain of beauty. </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp241:4'><span class='pli'>pamādo rakkhato malaṁ. </span> <span class='eng'>A guard’s stain is negligence. </span></span></span></p></blockquote></article>\n<article id='dhp242'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp242:0'><span class='pli'>Aññatarakulaputtavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp242:1'><span class='pli'>Malitthiyā duccaritaṁ, </span> <span class='eng'>Misconduct is a woman’s stain. </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp242:2'><span class='pli'>maccheraṁ dadato malaṁ; </span> <span class='eng'>A giver’s stain is stinginess. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp242:3'><span class='pli'>Malā ve pāpakā dhammā, </span> <span class='eng'>Bad qualities are a stain </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp242:4'><span class='pli'>asmiṁ loke paramhi ca. </span> <span class='eng'>in this world and the next. </span></span></span></p></blockquote></article>\n<article id='dhp243'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp243:1'><span class='pli'>Tato malā malataraṁ, </span> <span class='eng'>But a worse stain than these </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp243:2'><span class='pli'>avijjā paramaṁ malaṁ; </span> <span class='eng'>is ignorance, the worst stain of all. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp243:3'><span class='pli'>Etaṁ malaṁ pahantvāna, </span> <span class='eng'>Having given up that stain, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp243:4'><span class='pli'>nimmalā hotha bhikkhavo. </span> <span class='eng'>be without stains, mendicants! </span></span></span></p></blockquote></article>\n<article id='dhp244'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp244:0'><span class='pli'>Cūḷasāribhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp244:1'><span class='pli'>Sujīvaṁ ahirikena, </span> <span class='eng'>Life is easy for the shameless. </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp244:2'><span class='pli'>kākasūrena dhaṁsinā; </span> <span class='eng'>With all the rude courage of a crow, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp244:3'><span class='pli'>Pakkhandinā pagabbhena, </span> <span class='eng'>they live pushy, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp244:4'><span class='pli'>saṅkiliṭṭhena jīvitaṁ. </span> <span class='eng'>rude, and corrupt. </span></span></span></p></blockquote></article>\n<article id='dhp245'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp245:1'><span class='pli'>Hirīmatā ca dujjīvaṁ, </span> <span class='eng'>Life is hard for the conscientious, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp245:2'><span class='pli'>niccaṁ sucigavesinā; </span> <span class='eng'>always seeking purity, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp245:3'><span class='pli'>Alīnenāppagabbhena, </span> <span class='eng'>neither clinging nor rude, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp245:4'><span class='pli'>suddhājīvena passatā. </span> <span class='eng'>pure of livelihood and discerning. </span></span></span></p></blockquote></article>\n<article id='dhp246'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp246:0'><span class='pli'>Pañcaupāsakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp246:1'><span class='pli'>Yo pāṇamatipāteti, </span> <span class='eng'>Take anyone in this world </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp246:2'><span class='pli'>musāvādañca bhāsati; </span> <span class='eng'>who kills living creatures, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp246:3'><span class='pli'>Loke adinnamādiyati, </span> <span class='eng'>speaks falsely, steals, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp246:4'><span class='pli'>paradārañca gacchati. </span> <span class='eng'>commits adultery, </span></span></span></p></blockquote></article>\n<article id='dhp247'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp247:1'><span class='pli'>Surāmerayapānañca, </span> <span class='eng'>and indulges in drinking </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp247:2'><span class='pli'>yo naro anuyuñjati; </span> <span class='eng'>beer and wine. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp247:3'><span class='pli'>Idheva meso lokasmiṁ, </span> <span class='eng'>Right here they dig up </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp247:4'><span class='pli'>mūlaṁ khaṇati attano. </span> <span class='eng'>the root of their own self. </span></span></span></p></blockquote></article>\n<article id='dhp248'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp248:1'><span class='pli'>Evaṁ bho purisa jānāhi, </span> <span class='eng'>Know this, good fellow: </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp248:2'><span class='pli'>pāpadhammā asaññatā; </span> <span class='eng'>they are unrestrained and wicked. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp248:3'><span class='pli'>Mā taṁ lobho adhammo ca, </span> <span class='eng'>Don’t let that greed and unrighteousness </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp248:4'><span class='pli'>ciraṁ dukkhāya randhayuṁ. </span> <span class='eng'>inflict pain on you for long. </span></span></span></p></blockquote></article>\n<article id='dhp249'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp249:0'><span class='pli'>Tissadaharavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp249:1'><span class='pli'>Dadāti ve yathāsaddhaṁ, </span> <span class='eng'>The people give according to their faith, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp249:2'><span class='pli'>yathāpasādanaṁ jano; </span> <span class='eng'>according to their confidence. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp249:3'><span class='pli'>Tattha yo ca maṅku bhavati, </span> <span class='eng'>If you get upset over that, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp249:4'><span class='pli'>paresaṁ pānabhojane; </span> <span class='eng'>over other’s food and drink, </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp249:5'><span class='pli'>Na so divā vā rattiṁ vā, </span> <span class='eng'>you’ll not, by day or by night, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp249:6'><span class='pli'>samādhimadhigacchati. </span> <span class='eng'>become immersed in samādhi. </span></span></span></p></blockquote></article>\n<article id='dhp250'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp250:1'><span class='pli'>Yassa cetaṁ samucchinnaṁ, </span> <span class='eng'>Those who have cut that out, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp250:2'><span class='pli'>mūlaghaccaṁ samūhataṁ; </span> <span class='eng'>dug it up at the root, eradicated it, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp250:3'><span class='pli'>Sa ve divā vā rattiṁ vā, </span> <span class='eng'>they will, by day or by night, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp250:4'><span class='pli'>samādhimadhigacchati. </span> <span class='eng'>become immersed in samādhi. </span></span></span></p></blockquote></article>\n<article id='dhp251'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp251:0'><span class='pli'>Pañcaupāsakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp251:1'><span class='pli'>Natthi rāgasamo aggi, </span> <span class='eng'>There is no fire like greed, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp251:2'><span class='pli'>natthi dosasamo gaho; </span> <span class='eng'>no crime like hate, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp251:3'><span class='pli'>Natthi mohasamaṁ jālaṁ, </span> <span class='eng'>no net like delusion, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp251:4'><span class='pli'>natthi taṇhāsamā nadī. </span> <span class='eng'>no river like craving. </span></span></span></p></blockquote></article>\n<article id='dhp252'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp252:0'><span class='pli'>Meṇḍakaseṭṭhivatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp252:1'><span class='pli'>Sudassaṁ vajjamaññesaṁ, </span> <span class='eng'>It’s easy to see the faults of others, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp252:2'><span class='pli'>attano pana duddasaṁ; </span> <span class='eng'>hard to see one’s own. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp252:3'><span class='pli'>Paresaṁ hi so vajjāni, </span> <span class='eng'>For the faults of others </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp252:4'><span class='pli'>opunāti yathā bhusaṁ; </span> <span class='eng'>are tossed high like hay, </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp252:5'><span class='pli'>Attano pana chādeti, </span> <span class='eng'>while one’s own are hidden, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp252:6'><span class='pli'>kaliṁva kitavā saṭho. </span> <span class='eng'>as a cheat hides a bad hand. </span></span></span></p></blockquote></article>\n<article id='dhp253'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp253:0'><span class='pli'>Ujjhānasaññittheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp253:1'><span class='pli'>Paravajjānupassissa, </span> <span class='eng'>When you look for the flaws of others, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp253:2'><span class='pli'>niccaṁ ujjhānasaññino; </span> <span class='eng'>always finding fault, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp253:3'><span class='pli'>Āsavā tassa vaḍḍhanti, </span> <span class='eng'>your defilements only grow, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp253:4'><span class='pli'>ārā so āsavakkhayā. </span> <span class='eng'>you’re far from ending defilements. </span></span></span></p></blockquote></article>\n<article id='dhp254'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp254:0'><span class='pli'>Subhaddaparibbājakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp254:1'><span class='pli'>Ākāseva padaṁ natthi, </span> <span class='eng'>In the atmosphere there is no track, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp254:2'><span class='pli'>samaṇo natthi bāhire; </span> <span class='eng'>there’s no true ascetic outside here. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp254:3'><span class='pli'>Papañcābhiratā pajā, </span> <span class='eng'>People enjoy proliferation, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp254:4'><span class='pli'>nippapañcā tathāgatā. </span> <span class='eng'>the Realized Ones are free of proliferation. </span></span></span></p></blockquote></article>\n<article id='dhp255'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp255:1'><span class='pli'>Ākāseva padaṁ natthi, </span> <span class='eng'>In the atmosphere there is no track, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp255:2'><span class='pli'>samaṇo natthi bāhire; </span> <span class='eng'>there’s no true ascetic outside here. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp255:3'><span class='pli'>Saṅkhārā sassatā natthi, </span> <span class='eng'>No conditions last forever, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp255:4'><span class='pli'>natthi buddhānamiñjitaṁ. </span> <span class='eng'>the Awakened Ones are not shaken. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp255:5'><span class='pli'>Malavaggo aṭṭhārasamo. </span></span></p></article></section>\n",
    "acronym": "Dhp 235–255",
    "translated_title": "18. Stains",
    "original_title": "Malavagga"
  },
  "dhp256-272": {
    "previous": "dhp235-255",
    "next": "dhp273-289",
    "content": "<section class='range'  id='dhp256-272'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp256:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp256:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 256–272 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp256:0.3'><span class='pli'>Dhammaṭṭhavagga </span> <span class='eng'>19. The Just </span></span></h1></header>\n<article id='dhp256'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp256:0.4'><span class='pli'>Vinicchayamahāmattavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp256:1'><span class='pli'>Na tena hoti dhammaṭṭho, </span> <span class='eng'>You don’t become just </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp256:2'><span class='pli'>yenatthaṁ sāhasā naye; </span> <span class='eng'>by passing hasty judgment. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp256:3'><span class='pli'>Yo ca atthaṁ anatthañca, </span> <span class='eng'>An astute person evaluates both </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp256:4'><span class='pli'>ubho niccheyya paṇḍito. </span> <span class='eng'>what is pertinent and what is irrelevant. </span></span></span></p></blockquote></article>\n<article id='dhp257'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp257:1'><span class='pli'>Asāhasena dhammena, </span> <span class='eng'>A wise one judges others without haste, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp257:2'><span class='pli'>samena nayatī pare; </span> <span class='eng'>justly and impartially; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp257:3'><span class='pli'>Dhammassa gutto medhāvī, </span> <span class='eng'>that guardian of the law </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp257:4'><span class='pli'>“dhammaṭṭho”ti pavuccati. </span> <span class='eng'>is said to be just. </span></span></span></p></blockquote></article>\n<article id='dhp258'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp258:0'><span class='pli'>Chabbaggiyavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp258:1'><span class='pli'>Na tena paṇḍito hoti, </span> <span class='eng'>You’re not an astute scholar </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp258:2'><span class='pli'>yāvatā bahu bhāsati; </span> <span class='eng'>just because you speak a lot. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp258:3'><span class='pli'>Khemī averī abhayo, </span> <span class='eng'>One who is secure, free of enmity and fear, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp258:4'><span class='pli'>“paṇḍito”ti pavuccati. </span> <span class='eng'>is said to be astute. </span></span></span></p></blockquote></article>\n<article id='dhp259'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp259:0'><span class='pli'>Ekudānakhīṇāsavavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp259:1'><span class='pli'>Na tāvatā dhammadharo, </span> <span class='eng'>You’re not one who has memorized the teaching </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp259:2'><span class='pli'>yāvatā bahu bhāsati; </span> <span class='eng'>just because you recite a lot. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp259:3'><span class='pli'>Yo ca appampi sutvāna, </span> <span class='eng'>Someone who directly sees the teaching </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp259:4'><span class='pli'>dhammaṁ kāyena passati; </span> <span class='eng'>after hearing only a little </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp259:5'><span class='pli'>Sa ve dhammadharo hoti, </span> <span class='eng'>is truly one who has memorized the teaching, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp259:6'><span class='pli'>yo dhammaṁ nappamajjati. </span> <span class='eng'>for they can never forget it. </span></span></span></p></blockquote></article>\n<article id='dhp260'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp260:0'><span class='pli'>Lakuṇḍakabhaddiyattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp260:1'><span class='pli'>Na tena thero so hoti, </span> <span class='eng'>You don’t become a senior </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp260:2'><span class='pli'>yenassa palitaṁ siro; </span> <span class='eng'>by getting some grey hairs; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp260:3'><span class='pli'>Paripakko vayo tassa, </span> <span class='eng'>for one ripe only in age, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp260:4'><span class='pli'>“moghajiṇṇo”ti vuccati. </span> <span class='eng'>is said to have aged in vain. </span></span></span></p></blockquote></article>\n<article id='dhp261'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp261:1'><span class='pli'>Yamhi saccañca dhammo ca, </span> <span class='eng'>One who is truthful and principled, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp261:2'><span class='pli'>ahiṁsā saṁyamo damo; </span> <span class='eng'>harmless, restrained, and self-controlled, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp261:3'><span class='pli'>Sa ve vantamalo dhīro, </span> <span class='eng'>attentive, purged of stains, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp261:4'><span class='pli'>“thero” iti pavuccati. </span> <span class='eng'>is said to be a senior. </span></span></span></p></blockquote></article>\n<article id='dhp262'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp262:0'><span class='pli'>Sambahulabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp262:1'><span class='pli'>Na vākkaraṇamattena, </span> <span class='eng'>Not by mere enunciation, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp262:2'><span class='pli'>vaṇṇapokkharatāya vā; </span> <span class='eng'>or a beautiful complexion </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp262:3'><span class='pli'>Sādhurūpo naro hoti, </span> <span class='eng'>does a person become holy, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp262:4'><span class='pli'>issukī maccharī saṭho. </span> <span class='eng'>if they’re jealous, stingy, and devious. </span></span></span></p></blockquote></article>\n<article id='dhp263'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp263:1'><span class='pli'>Yassa cetaṁ samucchinnaṁ, </span> <span class='eng'>But if they’ve cut that out, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp263:2'><span class='pli'>mūlaghaccaṁ samūhataṁ; </span> <span class='eng'>dug it up at the root, eradicated it, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp263:3'><span class='pli'>Sa vantadoso medhāvī, </span> <span class='eng'>that wise one, purged of vice, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp263:4'><span class='pli'>“sādhurūpo”ti vuccati. </span> <span class='eng'>is said to be holy. </span></span></span></p></blockquote></article>\n<article id='dhp264'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp264:0'><span class='pli'>Hatthakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp264:1'><span class='pli'>Na muṇḍakena samaṇo, </span> <span class='eng'>A liar and breaker of vows is no ascetic </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp264:2'><span class='pli'>abbato alikaṁ bhaṇaṁ; </span> <span class='eng'>just because they shave their head. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp264:3'><span class='pli'>Icchālobhasamāpanno, </span> <span class='eng'>How on earth can one be an ascetic </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp264:4'><span class='pli'>samaṇo kiṁ bhavissati. </span> <span class='eng'>who’s full of desire and greed? </span></span></span></p></blockquote></article>\n<article id='dhp265'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp265:1'><span class='pli'>Yo ca sameti pāpāni, </span> <span class='eng'>One who stops all wicked deeds, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp265:2'><span class='pli'>aṇuṁthūlāni sabbaso; </span> <span class='eng'>great and small, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp265:3'><span class='pli'>Samitattā hi pāpānaṁ, </span> <span class='eng'>because of stopping wicked deeds </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp265:4'><span class='pli'>“samaṇo”ti pavuccati. </span> <span class='eng'>is said to be an ascetic. </span></span></span></p></blockquote></article>\n<article id='dhp266'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp266:0'><span class='pli'>Aññatarabrāhmaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp266:1'><span class='pli'>Na tena bhikkhu so hoti, </span> <span class='eng'>You don’t become a mendicant </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp266:2'><span class='pli'>yāvatā bhikkhate pare; </span> <span class='eng'>just by begging from others. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp266:3'><span class='pli'>Vissaṁ dhammaṁ samādāya, </span> <span class='eng'>One who has undertaken domestic duties </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp266:4'><span class='pli'>bhikkhu hoti na tāvatā. </span> <span class='eng'>has not yet become a mendicant. </span></span></span></p></blockquote></article>\n<article id='dhp267'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp267:1'><span class='pli'>Yodha puññañca pāpañca, </span> <span class='eng'>But one living a spiritual life, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp267:2'><span class='pli'>bāhetvā brahmacariyavā; </span> <span class='eng'>who has banished both merit and evil, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp267:3'><span class='pli'>Saṅkhāya loke carati, </span> <span class='eng'>who wanders having appraised the world, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp267:4'><span class='pli'>sa ve “bhikkhū”ti vuccati. </span> <span class='eng'>is said to be a mendicant. </span></span></span></p></blockquote></article>\n<article id='dhp268'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp268:0'><span class='pli'>Titthiyavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp268:1'><span class='pli'>Na monena munī hoti, </span> <span class='eng'>You don’t become a sage by being sagelike, </span> <span class='comment-marker' data-comment='<i lang=&#39;pi&#39; translate=&#39;no&#39;>Mona</i> (“sagacity”, Sanskrit <i lang=&#39;pi&#39; translate=&#39;no&#39;>mauna</i>) is the abstract noun from <i lang=&#39;pi&#39; translate=&#39;no&#39;>muni</i> (‘sage”). Over time it acquired the sense “silence”, as a thoughtful man is silent, and it is explained as such here by the commentary as well as the Chinese translation (T 210:27.13 at T iv 586c19). However I have not found any early instances to support this. In Pali, silence is <i lang=&#39;pi&#39; translate=&#39;no&#39;>tuṇhī</i>; a “vow of silence”—forbidden to monastics—is <i lang=&#39;pi&#39; translate=&#39;no&#39;>mūgabbata</i> (<a href=&#39;index.html?q=pli-tv-kd4#1.12.6&#39;>Kd 4:1.12.6</a>). Nothing in the context here suggests a vow of silence. In the Rig Veda (7.56.8, 10.136) the <i lang=&#39;pi&#39; translate=&#39;no&#39;>muni</i> appears as an ecstatic, raving as if drunk, apparently from drugs (<i lang=&#39;pi&#39; translate=&#39;no&#39;>soma</i>, <i lang=&#39;pi&#39; translate=&#39;no&#39;>viṣa</i>), suggesting a sense of “mind-altered” or “mind-expanded”. Chāndogya Upaniṣad 8.5.2 refers to <i lang=&#39;pi&#39; translate=&#39;no&#39;>mauna</i> as the life of a student, characterized by <i lang=&#39;pi&#39; translate=&#39;no&#39;>manute</i> (“thinking”, “reflection”), so we can well translate “thoughtfulness”. Bṛhadāraṇyaka Upaniṣad 3.5.1 enjoins the student to become like a child, then reject both scholarship and childishness, thus becoming a “sage” (<i lang=&#39;pi&#39; translate=&#39;no&#39;>muni</i>). Then, rejecting sagacity and lack of sagacity they become a brahmin. This could easily have the Vedic sense of “mind-expanded (through drugs or meditation)”. A similar sense probably applies here. The verse enjoins “weighed” or rational thought as opposed to confused, perhaps “ecstatic” or “mind-altered” thought. '>*</span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp268:2'><span class='pli'>mūḷharūpo aviddasu; </span> <span class='eng'>while still confused and ignorant. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp268:3'><span class='pli'>Yo ca tulaṁva paggayha, </span> <span class='eng'>The astute one who holds the scales, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp268:4'><span class='pli'>varamādāya paṇḍito. </span> <span class='eng'>taking only the best, </span></span></span></p></blockquote></article>\n<article id='dhp269'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp269:1'><span class='pli'>Pāpāni parivajjeti, </span> <span class='eng'>and shunning the bad—</span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp269:2'><span class='pli'>sa munī tena so muni; </span> <span class='eng'>that is a sage, <j>and that is how one becomes a sage. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp269:3'><span class='pli'>Yo munāti ubho loke, </span> <span class='eng'>One who sagely weighs both in the world, </span> <span class='comment-marker' data-comment='These two lines recapitulate the previous lines. <i lang=&#39;pi&#39; translate=&#39;no&#39;>Munāti</i> (“sagely weighs”, literally “thinks, examines”) is punned with <i lang=&#39;pi&#39; translate=&#39;no&#39;>mināti</i> (“measure, weigh”) following the imagery of the scales. “Both” calls back to both good and evil. '>*</span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp269:4'><span class='pli'>“muni” tena pavuccati. </span> <span class='eng'>is thereby said to be a sage. </span></span></span></p></blockquote></article>\n<article id='dhp270'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp270:0'><span class='pli'>Bālisikavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp270:1'><span class='pli'>Na tena ariyo hoti, </span> <span class='eng'>You don’t become a noble one </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp270:2'><span class='pli'>yena pāṇāni hiṁsati; </span> <span class='eng'>by harming living beings. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp270:3'><span class='pli'>Ahiṁsā sabbapāṇānaṁ, </span> <span class='eng'>One harmless towards all living beings </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp270:4'><span class='pli'>“ariyo”ti pavuccati. </span> <span class='eng'>is said to be a noble one. </span></span></span></p></blockquote></article>\n<article id='dhp271'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp271:0'><span class='pli'>Sambahulasīlādisampannabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp271:1'><span class='pli'>Na sīlabbatamattena, </span> <span class='eng'>Not by precepts and observances, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp271:2'><span class='pli'>bāhusaccena vā pana; </span> <span class='eng'>nor by much learning, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp271:3'><span class='pli'>Atha vā samādhilābhena, </span> <span class='eng'>nor by meditative immersion, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp271:4'><span class='pli'>vivittasayanena vā. </span> <span class='eng'>nor by living in seclusion, </span></span></span></p></blockquote></article>\n<article id='dhp272'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp272:1'><span class='pli'>Phusāmi nekkhammasukhaṁ, </span> <span class='eng'>do I experience the bliss of renunciation </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp272:2'><span class='pli'>aputhujjanasevitaṁ; </span> <span class='eng'>not frequented by ordinary people. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp272:3'><span class='pli'>Bhikkhu vissāsamāpādi, </span> <span class='eng'>A mendicant cannot rest confident </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp272:4'><span class='pli'>appatto āsavakkhayaṁ. </span> <span class='eng'>without attaining the end of defilements. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp272:5'><span class='pli'>Dhammaṭṭhavaggo ekūnavīsatimo. </span></span></p></article></section>\n",
    "acronym": "Dhp 256–272",
    "translated_title": "19. The Just",
    "original_title": "Dhammaṭṭhavagga"
  },
  "dhp273-289": {
    "previous": "dhp256-272",
    "next": "dhp290-305",
    "content": "<section class='range'  id='dhp273-289'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp273:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp273:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 273–289 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp273:0.3'><span class='pli'>Maggavagga </span> <span class='eng'>20. The Path </span></span></h1></header>\n<article id='dhp273'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp273:0.4'><span class='pli'>Pañcasatabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp273:1'><span class='pli'>Maggānaṭṭhaṅgiko seṭṭho, </span> <span class='eng'>Of paths, the eightfold is the best; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp273:2'><span class='pli'>saccānaṁ caturo padā; </span> <span class='eng'>of truths, the four statements; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp273:3'><span class='pli'>Virāgo seṭṭho dhammānaṁ, </span> <span class='eng'>dispassion is the best of things, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp273:4'><span class='pli'>dvipadānañca cakkhumā. </span> <span class='eng'>and the Clear-eyed One is the best of humans. </span></span></span></p></blockquote></article>\n<article id='dhp274'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp274:1'><span class='pli'>Eseva maggo natthañño, </span> <span class='eng'><em>This</em> is the path, there is no other </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp274:2'><span class='pli'>Dassanassa visuddhiyā; </span> <span class='eng'>for the purification of vision. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp274:3'><span class='pli'>Etañhi tumhe paṭipajjatha, </span> <span class='eng'>You all must practice this, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp274:4'><span class='pli'>Mārassetaṁ pamohanaṁ. </span> <span class='eng'>it is the way to baffle Māra. </span></span></span></p></blockquote></article>\n<article id='dhp275'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp275:1'><span class='pli'>Etañhi tumhe paṭipannā, </span> <span class='eng'>When you all are practicing this, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp275:2'><span class='pli'>dukkhassantaṁ karissatha; </span> <span class='eng'>you will make an end of suffering. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp275:3'><span class='pli'>Akkhāto vo mayā maggo, </span> <span class='eng'>I have explained the path to you </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp275:4'><span class='pli'>aññāya sallakantanaṁ. </span> <span class='eng'>for extracting the thorn with wisdom. </span></span></span></p></blockquote></article>\n<article id='dhp276'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp276:1'><span class='pli'>Tumhehi kiccamātappaṁ, </span> <span class='eng'>You yourselves must do the work, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp276:2'><span class='pli'>akkhātāro tathāgatā; </span> <span class='eng'>the Realized Ones just show the way. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp276:3'><span class='pli'>Paṭipannā pamokkhanti, </span> <span class='eng'>Meditators practicing absorption </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp276:4'><span class='pli'>jhāyino mārabandhanā. </span> <span class='eng'>are released from Māra’s bonds. </span></span></span></p></blockquote></article>\n<article id='dhp277'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp277:0'><span class='pli'>Aniccalakkhaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp277:1'><span class='pli'>“Sabbe saṅkhārā aniccā”ti, </span> <span class='eng'>All conditions are impermanent—</span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp277:2'><span class='pli'>yadā paññāya passati; </span> <span class='eng'>when this is seen with wisdom, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp277:3'><span class='pli'>Atha nibbindati dukkhe, </span> <span class='eng'>one grows disillusioned with suffering: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp277:4'><span class='pli'>esa maggo visuddhiyā. </span> <span class='eng'>this is the path to purity. </span></span></span></p></blockquote></article>\n<article id='dhp278'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp278:0'><span class='pli'>Dukkhalakkhaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp278:1'><span class='pli'>“Sabbe saṅkhārā dukkhā”ti, </span> <span class='eng'>All conditions are suffering—</span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp278:2'><span class='pli'>yadā paññāya passati; </span> <span class='eng'>when this is seen with wisdom, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp278:3'><span class='pli'>Atha nibbindati dukkhe, </span> <span class='eng'>one grows disillusioned with suffering: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp278:4'><span class='pli'>esa maggo visuddhiyā. </span> <span class='eng'>this is the path to purity. </span></span></span></p></blockquote></article>\n<article id='dhp279'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp279:0'><span class='pli'>Anattalakkhaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp279:1'><span class='pli'>“Sabbe dhammā anattā”ti, </span> <span class='eng'>All things are not-self—</span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp279:2'><span class='pli'>yadā paññāya passati; </span> <span class='eng'>when this is seen with wisdom, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp279:3'><span class='pli'>Atha nibbindati dukkhe, </span> <span class='eng'>one grows disillusioned with suffering: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp279:4'><span class='pli'>esa maggo visuddhiyā. </span> <span class='eng'>this is the path to purity. </span></span></span></p></blockquote></article>\n<article id='dhp280'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp280:0'><span class='pli'>Padhānakammikatissattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp280:1'><span class='pli'>Uṭṭhānakālamhi anuṭṭhahāno, </span> <span class='eng'>They don’t get going when it’s time to start; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp280:2'><span class='pli'>Yuvā balī ālasiyaṁ upeto; </span> <span class='eng'>they’re young and strong, but given to sloth. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp280:3'><span class='pli'>Saṁsannasaṅkappamano kusīto, </span> <span class='eng'>Their mind depressed in sunken thought, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp280:4'><span class='pli'>Paññāya maggaṁ alaso na vindati. </span> <span class='eng'>lazy and slothful, they can’t discern the path. </span></span></span></p></blockquote></article>\n<article id='dhp281'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp281:0'><span class='pli'>Sūkarapetavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp281:1'><span class='pli'>Vācānurakkhī manasā susaṁvuto, </span> <span class='eng'>Guarded in speech, restrained in mind, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp281:2'><span class='pli'>Kāyena ca nākusalaṁ kayirā; </span> <span class='eng'>doing no unskillful bodily deed. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp281:3'><span class='pli'>Ete tayo kammapathe visodhaye, </span> <span class='eng'>Purify these three ways of performing deeds, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp281:4'><span class='pli'>Ārādhaye maggamisippaveditaṁ. </span> <span class='eng'>and win the path known to seers. </span></span></span></p></blockquote></article>\n<article id='dhp282'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp282:0'><span class='pli'>Poṭṭhilattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp282:1'><span class='pli'>Yogā ve jāyatī bhūri, </span> <span class='eng'>From meditation springs wisdom, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp282:2'><span class='pli'>ayogā bhūrisaṅkhayo; </span> <span class='eng'>without meditation, wisdom ends. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp282:3'><span class='pli'>Etaṁ dvedhāpathaṁ ñatvā, </span> <span class='eng'>Knowing these two paths—</span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp282:4'><span class='pli'>bhavāya vibhavāya ca; </span> <span class='eng'>of progress and decline—</span> <span class='comment-marker' data-comment='Here <i lang=&#39;pi&#39; translate=&#39;no&#39;>bhava</i> and <i lang=&#39;pi&#39; translate=&#39;no&#39;>vibhava</i> do not have the normal sense of “continued life” and “annihilation”. '>*</span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp282:5'><span class='pli'>Tathāttānaṁ niveseyya, </span> <span class='eng'>you should conduct yourself </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp282:6'><span class='pli'>yathā bhūri pavaḍḍhati. </span> <span class='eng'>so that wisdom grows. </span></span></span></p></blockquote></article>\n<article id='dhp283'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp283:0'><span class='pli'>Pañcamahallakabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp283:1'><span class='pli'>Vanaṁ chindatha mā rukkhaṁ, </span> <span class='eng'>Cut down the jungle, not just a tree; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp283:2'><span class='pli'>vanato jāyate bhayaṁ; </span> <span class='eng'>from the jungle springs fear. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp283:3'><span class='pli'>Chetvā vanañca vanathañca, </span> <span class='eng'>Having cut down jungle and snarl, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp283:4'><span class='pli'>nibbanā hotha bhikkhavo. </span> <span class='eng'>be free of jungles, mendicants! </span></span></span></p></blockquote></article>\n<article id='dhp284'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp284:1'><span class='pli'>Yāva hi vanatho na chijjati, </span> <span class='eng'>So long as the vine, no matter how small, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp284:2'><span class='pli'>Aṇumattopi narassa nārisu; </span> <span class='eng'>that ties a man to women is not cut, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp284:3'><span class='pli'>Paṭibaddhamanova tāva so, </span> <span class='eng'>his mind remains trapped, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp284:4'><span class='pli'>Vaccho khīrapakova mātari. </span> <span class='eng'>like a calf suckling its mother. </span></span></span></p></blockquote></article>\n<article id='dhp285'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp285:0'><span class='pli'>Suvaṇṇakārattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp285:1'><span class='pli'>Ucchinda sinehamattano, </span> <span class='eng'>Cut out affection for oneself, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp285:2'><span class='pli'>Kumudaṁ sāradikaṁva pāṇinā; </span> <span class='eng'>like plucking an autumn lotus. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp285:3'><span class='pli'>Santimaggameva brūhaya, </span> <span class='eng'>Foster only the path to peace, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp285:4'><span class='pli'>Nibbānaṁ sugatena desitaṁ. </span> <span class='eng'>the extinguishment the Holy One taught. </span></span></span></p></blockquote></article>\n<article id='dhp286'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp286:0'><span class='pli'>Mahādhanavāṇijavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp286:1'><span class='pli'>Idha vassaṁ vasissāmi, </span> <span class='eng'>“Here I will stay for the rains; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp286:2'><span class='pli'>idha hemantagimhisu; </span> <span class='eng'>here for winter, here the summer”; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp286:3'><span class='pli'>Iti bālo vicinteti, </span> <span class='eng'>thus the fool thinks, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp286:4'><span class='pli'>antarāyaṁ na bujjhati. </span> <span class='eng'>not realizing the danger. </span></span></span></p></blockquote></article>\n<article id='dhp287'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp287:0'><span class='pli'>Kisāgotamīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp287:1'><span class='pli'>Taṁ puttapasusammattaṁ, </span> <span class='eng'>As a mighty flood sweeps away a sleeping village, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp287:2'><span class='pli'>byāsattamanasaṁ naraṁ; </span> <span class='eng'>death steals away a man </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp287:3'><span class='pli'>Suttaṁ gāmaṁ mahoghova, </span> <span class='eng'>who dotes on children and cattle, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp287:4'><span class='pli'>maccu ādāya gacchati. </span> <span class='eng'>his mind caught up in them. </span></span></span></p></blockquote></article>\n<article id='dhp288'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp288:0'><span class='pli'>Paṭācārāvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp288:1'><span class='pli'>Na santi puttā tāṇāya, </span> <span class='eng'>Children provide you no shelter, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp288:2'><span class='pli'>na pitā nāpi bandhavā; </span> <span class='eng'>nor does father, nor relatives. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp288:3'><span class='pli'>Antakenādhipannassa, </span> <span class='eng'>When you’re seized by the terminator, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp288:4'><span class='pli'>natthi ñātīsu tāṇatā. </span> <span class='eng'>there’s no shelter in family. </span></span></span></p></blockquote></article>\n<article id='dhp289'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp289:1'><span class='pli'>Etamatthavasaṁ ñatvā, </span> <span class='eng'>Knowing the reason for this, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp289:2'><span class='pli'>paṇḍito sīlasaṁvuto; </span> <span class='eng'>astute, and ethically restrained, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp289:3'><span class='pli'>Nibbānagamanaṁ maggaṁ, </span> <span class='eng'>one would quickly clear the path </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp289:4'><span class='pli'>khippameva visodhaye. </span> <span class='eng'>that leads to extinguishment. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp289:5'><span class='pli'>Maggavaggo vīsatimo. </span></span></p></article></section>\n",
    "acronym": "Dhp 273–289",
    "translated_title": "20. The Path",
    "original_title": "Maggavagga"
  },
  "dhp290-305": {
    "previous": "dhp273-289",
    "next": "dhp306-319",
    "content": "<section class='range'  id='dhp290-305'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp290:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp290:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 290–305 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp290:0.3'><span class='pli'>Pakiṇṇakavagga </span> <span class='eng'>21. Miscellaneous </span></span></h1></header>\n<article id='dhp290'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp290:0.4'><span class='pli'>Attanopubbakammavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp290:1'><span class='pli'>Mattāsukhapariccāgā, </span> <span class='eng'>If by giving up material happiness </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp290:2'><span class='pli'>passe ce vipulaṁ sukhaṁ; </span> <span class='eng'>one sees abundant happiness, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp290:3'><span class='pli'>Caje mattāsukhaṁ dhīro, </span> <span class='eng'>the attentive would give up material happiness, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp290:4'><span class='pli'>sampassaṁ vipulaṁ sukhaṁ. </span> <span class='eng'>seeing the abundant happiness. </span></span></span></p></blockquote></article>\n<article id='dhp291'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp291:0'><span class='pli'>Kukkuṭaṇḍakhādikāvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp291:1'><span class='pli'>Paradukkhūpadhānena, </span> <span class='eng'>Seeking their own happiness </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp291:2'><span class='pli'>attano sukhamicchati; </span> <span class='eng'>by imposing suffering on others, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp291:3'><span class='pli'>Verasaṁsaggasaṁsaṭṭho, </span> <span class='eng'>living intimate with enmity, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp291:4'><span class='pli'>verā so na parimuccati. </span> <span class='eng'>they’re not freed from enmity. </span></span></span></p></blockquote></article>\n<article id='dhp292'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp292:0'><span class='pli'>Bhaddiyānaṁbhikkhūnaṁvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp292:1'><span class='pli'>Yañhi kiccaṁ apaviddhaṁ, </span> <span class='eng'>They dump what should be done, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp292:2'><span class='pli'>akiccaṁ pana karīyati; </span> <span class='eng'>and do what should not be done. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp292:3'><span class='pli'>Unnaḷānaṁ pamattānaṁ, </span> <span class='eng'>For the insolent and the negligent, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp292:4'><span class='pli'>tesaṁ vaḍḍhanti āsavā. </span> <span class='eng'>their defilements only grow. </span></span></span></p></blockquote></article>\n<article id='dhp293'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp293:1'><span class='pli'>Yesañca susamāraddhā, </span> <span class='eng'>Those that have properly undertaken </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp293:2'><span class='pli'>niccaṁ kāyagatā sati; </span> <span class='eng'>constant mindfulness of the body, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp293:3'><span class='pli'>Akiccaṁ te na sevanti, </span> <span class='eng'>don’t cultivate what should not be done, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp293:4'><span class='pli'>kicce sātaccakārino; </span> <span class='eng'>but always do what should be done. </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp293:5'><span class='pli'>Satānaṁ sampajānānaṁ, </span> <span class='eng'>Mindful and aware, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp293:6'><span class='pli'>atthaṁ gacchanti āsavā. </span> <span class='eng'>their defilements come to an end. </span></span></span></p></blockquote></article>\n<article id='dhp294'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp294:0'><span class='pli'>Lakuṇḍakabhaddiyavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp294:1'><span class='pli'>Mātaraṁ pitaraṁ hantvā, </span> <span class='eng'>Having slain mother and father, </span> <span class='comment-marker' data-comment='The following explanations are according to the commentary. “Mother” is craving, “father” is conceit. '>*</span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp294:2'><span class='pli'>rājāno dve ca khattiye; </span> <span class='eng'>and two aristocratic kings, </span> <span class='comment-marker' data-comment='The “two aristocratic kings” are eternalism and annihilationism. '>*</span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp294:3'><span class='pli'>Raṭṭhaṁ sānucaraṁ hantvā, </span> <span class='eng'>and having wiped out <j>the kingdom with its tax collector, </span> <span class='comment-marker' data-comment='The “kingdom” is the senses and their objects, while the “tax collector” is relishing and lust. '>*</span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp294:4'><span class='pli'>anīgho yāti brāhmaṇo. </span> <span class='eng'>the brahmin walks on untroubled. </span></span></span></p></blockquote></article>\n<article id='dhp295'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp295:1'><span class='pli'>Mātaraṁ pitaraṁ hantvā, </span> <span class='eng'>Having slain mother and father, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp295:2'><span class='pli'>rājāno dve ca sotthiye; </span> <span class='eng'>and two prosperous kings, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp295:3'><span class='pli'>Veyagghapañcamaṁ hantvā, </span> <span class='eng'>and a tiger as the fifth, </span> <span class='comment-marker' data-comment='The five hindrances with doubt as fifth. '>*</span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp295:4'><span class='pli'>anīgho yāti brāhmaṇo. </span> <span class='eng'>the brahmin walks on untroubled. </span></span></span></p></blockquote></article>\n<article id='dhp296'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp296:0'><span class='pli'>Dārusākaṭikaputtavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp296:1'><span class='pli'>Suppabuddhaṁ pabujjhanti, </span> <span class='eng'>The disciples of Gotama </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp296:2'><span class='pli'>sadā gotamasāvakā; </span> <span class='eng'>always wake up refreshed, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp296:3'><span class='pli'>Yesaṁ divā ca ratto ca, </span> <span class='eng'>who day and night </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp296:4'><span class='pli'>niccaṁ buddhagatā sati. </span> <span class='eng'>constantly recollect the Buddha. </span></span></span></p></blockquote></article>\n<article id='dhp297'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp297:1'><span class='pli'>Suppabuddhaṁ pabujjhanti, </span> <span class='eng'>The disciples of Gotama </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp297:2'><span class='pli'>sadā gotamasāvakā; </span> <span class='eng'>always wake up refreshed, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp297:3'><span class='pli'>Yesaṁ divā ca ratto ca, </span> <span class='eng'>who day and night </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp297:4'><span class='pli'>niccaṁ dhammagatā sati. </span> <span class='eng'>constantly recollect the teaching. </span></span></span></p></blockquote></article>\n<article id='dhp298'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp298:1'><span class='pli'>Suppabuddhaṁ pabujjhanti, </span> <span class='eng'>The disciples of Gotama </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp298:2'><span class='pli'>sadā gotamasāvakā; </span> <span class='eng'>always wake up refreshed, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp298:3'><span class='pli'>Yesaṁ divā ca ratto ca, </span> <span class='eng'>who day and night </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp298:4'><span class='pli'>niccaṁ saṅghagatā sati. </span> <span class='eng'>constantly recollect the Saṅgha. </span></span></span></p></blockquote></article>\n<article id='dhp299'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp299:1'><span class='pli'>Suppabuddhaṁ pabujjhanti, </span> <span class='eng'>The disciples of Gotama </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp299:2'><span class='pli'>sadā gotamasāvakā; </span> <span class='eng'>always wake up refreshed, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp299:3'><span class='pli'>Yesaṁ divā ca ratto ca, </span> <span class='eng'>who day and night </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp299:4'><span class='pli'>niccaṁ kāyagatā sati. </span> <span class='eng'>are constantly mindful of the body. </span></span></span></p></blockquote></article>\n<article id='dhp300'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp300:1'><span class='pli'>Suppabuddhaṁ pabujjhanti, </span> <span class='eng'>The disciples of Gotama </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp300:2'><span class='pli'>sadā gotamasāvakā; </span> <span class='eng'>always wake up refreshed, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp300:3'><span class='pli'>Yesaṁ divā ca ratto ca, </span> <span class='eng'>whose minds day and night </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp300:4'><span class='pli'>ahiṁsāya rato mano. </span> <span class='eng'>delight in harmlessness. </span></span></span></p></blockquote></article>\n<article id='dhp301'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp301:1'><span class='pli'>Suppabuddhaṁ pabujjhanti, </span> <span class='eng'>The disciples of Gotama </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp301:2'><span class='pli'>sadā gotamasāvakā; </span> <span class='eng'>always wake up refreshed, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp301:3'><span class='pli'>Yesaṁ divā ca ratto ca, </span> <span class='eng'>whose minds day and night </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp301:4'><span class='pli'>bhāvanāya rato mano. </span> <span class='eng'>delight in meditation. </span></span></span></p></blockquote></article>\n<article id='dhp302'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp302:0'><span class='pli'>Vajjiputtakabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp302:1'><span class='pli'>Duppabbajjaṁ durabhiramaṁ, </span> <span class='eng'>Going forth is hard, it’s hard to be happy; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp302:2'><span class='pli'>Durāvāsā gharā dukhā; </span> <span class='eng'>life at home is hard too, and painful, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp302:3'><span class='pli'>Dukkhosamānasaṁvāso, </span> <span class='eng'>it’s painful to stay when you’ve nothing in common. </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp302:4'><span class='pli'>Dukkhānupatitaddhagū; </span> <span class='eng'>A traveler is a prey to pain, </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp302:5'><span class='pli'>Tasmā na caddhagū siyā, </span> <span class='eng'>so don’t be a traveler, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp302:6'><span class='pli'>Na ca dukkhānupatito siyā. </span> <span class='eng'>don’t be prey to pain. </span></span></span></p></blockquote></article>\n<article id='dhp303'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp303:0'><span class='pli'>Cittagahapativatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp303:1'><span class='pli'>Saddho sīlena sampanno, </span> <span class='eng'>One who is faithful, accomplished in ethics, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp303:2'><span class='pli'>yasobhogasamappito; </span> <span class='eng'>blessed with fame and wealth, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp303:3'><span class='pli'>Yaṁ yaṁ padesaṁ bhajati, </span> <span class='eng'>is honored in whatever place </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp303:4'><span class='pli'>tattha tattheva pūjito. </span> <span class='eng'>they frequent. </span></span></span></p></blockquote></article>\n<article id='dhp304'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp304:0'><span class='pli'>Cūḷasubhaddāvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp304:1'><span class='pli'>Dūre santo pakāsenti, </span> <span class='eng'>The good shine from afar, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp304:2'><span class='pli'>himavantova pabbato; </span> <span class='eng'>like the Himalayan peaks, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp304:3'><span class='pli'>Asantettha na dissanti, </span> <span class='eng'>but the wicked are not seen, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp304:4'><span class='pli'>rattiṁ khittā yathā sarā. </span> <span class='eng'>like arrows scattered in the night. </span></span></span></p></blockquote></article>\n<article id='dhp305'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp305:0'><span class='pli'>Ekavihārittheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp305:1'><span class='pli'>Ekāsanaṁ ekaseyyaṁ, </span> <span class='eng'>Sitting alone, sleeping alone, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp305:2'><span class='pli'>eko caramatandito; </span> <span class='eng'>tirelessly wandering alone; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp305:3'><span class='pli'>Eko damayamattānaṁ, </span> <span class='eng'>one who tames themselves alone </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp305:4'><span class='pli'>vanante ramito siyā. </span> <span class='eng'>would delight within a forest. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp305:5'><span class='pli'>Pakiṇṇakavaggo ekavīsatimo. </span></span></p></article></section>\n",
    "acronym": "Dhp 290–305",
    "translated_title": "21. Miscellaneous",
    "original_title": "Pakiṇṇakavagga"
  },
  "dhp306-319": {
    "previous": "dhp290-305",
    "next": "dhp320-333",
    "content": "<section class='range'  id='dhp306-319'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp306:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp306:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 306–319 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp306:0.3'><span class='pli'>Nirayavagga </span> <span class='eng'>22. Hell </span></span></h1></header>\n<article id='dhp306'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp306:0.4'><span class='pli'>Sundarīparibbājikāvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp306:1'><span class='pli'>Abhūtavādī nirayaṁ upeti, </span> <span class='eng'>A liar goes to hell, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp306:2'><span class='pli'>Yo vāpi katvā na karomi cāha; </span> <span class='eng'>as does one who denies what they did. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp306:3'><span class='pli'>Ubhopi te pecca samā bhavanti, </span> <span class='eng'>Both are equal in the hereafter, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp306:4'><span class='pli'>Nihīnakammā manujā parattha. </span> <span class='eng'>those men of base deeds. </span></span></span></p></blockquote></article>\n<article id='dhp307'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp307:0'><span class='pli'>Duccaritaphalapīḷitavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp307:1'><span class='pli'>Kāsāvakaṇṭhā bahavo, </span> <span class='eng'>Many who wear a scrap of ocher cloth </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp307:2'><span class='pli'>pāpadhammā asaññatā; </span> <span class='eng'>are unrestrained and wicked. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp307:3'><span class='pli'>Pāpā pāpehi kammehi, </span> <span class='eng'>Being wicked, they are reborn in hell </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp307:4'><span class='pli'>nirayaṁ te upapajjare. </span> <span class='eng'>due to their bad deeds. </span></span></span></p></blockquote></article>\n<article id='dhp308'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp308:0'><span class='pli'>Vaggumudātīriyabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp308:1'><span class='pli'>Seyyo ayoguḷo bhutto, </span> <span class='eng'>It’d be better for the immoral and unrestrained </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp308:2'><span class='pli'>tatto aggisikhūpamo; </span> <span class='eng'>to eat an iron ball, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp308:3'><span class='pli'>Yañce bhuñjeyya dussīlo, </span> <span class='eng'>scorching, like a burning flame, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp308:4'><span class='pli'>raṭṭhapiṇḍamasaññato. </span> <span class='eng'>than to eat the nation’s alms. </span></span></span></p></blockquote></article>\n<article id='dhp309'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp309:0'><span class='pli'>Khemakaseṭṭhiputtavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp309:1'><span class='pli'>Cattāri ṭhānāni naro pamatto, </span> <span class='eng'>Four things befall a heedless man </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp309:2'><span class='pli'>Āpajjati paradārūpasevī; </span> <span class='eng'>who sleeps with another’s wife: </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp309:3'><span class='pli'>Apuññalābhaṁ na nikāmaseyyaṁ, </span> <span class='eng'>wickedness, poor sleep, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp309:4'><span class='pli'>Nindaṁ tatīyaṁ nirayaṁ catutthaṁ. </span> <span class='eng'>ill-repute, and rebirth in hell. </span></span></span></p></blockquote></article>\n<article id='dhp310'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp310:1'><span class='pli'>Apuññalābho ca gatī ca pāpikā, </span> <span class='eng'>He accrues wickedness and is reborn in a bad place, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp310:2'><span class='pli'>Bhītassa bhītāya ratī ca thokikā; </span> <span class='eng'>all so a frightened couple <j>may snatch a moment’s pleasure, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp310:3'><span class='pli'>Rājā ca daṇḍaṁ garukaṁ paṇeti, </span> <span class='eng'>for which rulers impose a heavy punishment. </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp310:4'><span class='pli'>Tasmā naro paradāraṁ na seve. </span> <span class='eng'>That’s why a man should not <j>sleep with another’s wife. </span></span></span></p></blockquote></article>\n<article id='dhp311'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp311:0'><span class='pli'>Dubbacabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp311:1'><span class='pli'>Kuso yathā duggahito, </span> <span class='eng'>When kusa grass is wrongly grasped </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp311:2'><span class='pli'>hatthamevānukantati; </span> <span class='eng'>it only cuts the hand. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp311:3'><span class='pli'>Sāmaññaṁ dupparāmaṭṭhaṁ, </span> <span class='eng'>So too, the ascetic life, when wrongly taken, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp311:4'><span class='pli'>nirayāyupakaḍḍhati. </span> <span class='eng'>drags you to hell. </span></span></span></p></blockquote></article>\n<article id='dhp312'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp312:1'><span class='pli'>Yaṁ kiñci sithilaṁ kammaṁ, </span> <span class='eng'>Any lax act, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp312:2'><span class='pli'>saṅkiliṭṭhañca yaṁ vataṁ; </span> <span class='eng'>any corrupt observance, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp312:3'><span class='pli'>Saṅkassaraṁ brahmacariyaṁ, </span> <span class='eng'>or suspicious spiritual life, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp312:4'><span class='pli'>na taṁ hoti mahapphalaṁ. </span> <span class='eng'>is not very fruitful. </span></span></span></p></blockquote></article>\n<article id='dhp313'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp313:1'><span class='pli'>Kayirā ce kayirāthenaṁ, </span> <span class='eng'>If one is to do what should be done, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp313:2'><span class='pli'>daḷhamenaṁ parakkame; </span> <span class='eng'>one should staunchly strive. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp313:3'><span class='pli'>Sithilo hi paribbājo, </span> <span class='eng'>For the life gone forth when laxly led </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp313:4'><span class='pli'>bhiyyo ākirate rajaṁ. </span> <span class='eng'>just stirs up dust all the more. </span></span></span></p></blockquote></article>\n<article id='dhp314'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp314:0'><span class='pli'>Issāpakataitthivatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp314:1'><span class='pli'>Akataṁ dukkaṭaṁ seyyo, </span> <span class='eng'>A bad deed is better left undone, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp314:2'><span class='pli'>pacchā tappati dukkaṭaṁ; </span> <span class='eng'>for it will plague you later on. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp314:3'><span class='pli'>Katañca sukataṁ seyyo, </span> <span class='eng'>A good deed is better done, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp314:4'><span class='pli'>yaṁ katvā nānutappati. </span> <span class='eng'>one that does not plague you. </span></span></span></p></blockquote></article>\n<article id='dhp315'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp315:0'><span class='pli'>Sambahulabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp315:1'><span class='pli'>Nagaraṁ yathā paccantaṁ, </span> <span class='eng'>As a frontier city </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp315:2'><span class='pli'>guttaṁ santarabāhiraṁ; </span> <span class='eng'>is guarded inside and out, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp315:3'><span class='pli'>Evaṁ gopetha attānaṁ, </span> <span class='eng'>so you should ward yourselves—</span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp315:4'><span class='pli'>khaṇo vo mā upaccagā; </span> <span class='eng'>don’t let the moment pass you by. </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp315:5'><span class='pli'>Khaṇātītā hi socanti, </span> <span class='eng'>For if you miss your moment </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp315:6'><span class='pli'>nirayamhi samappitā. </span> <span class='eng'>you’ll grieve when sent to hell. </span></span></span></p></blockquote></article>\n<article id='dhp316'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp316:0'><span class='pli'>Nigaṇṭhavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp316:1'><span class='pli'>Alajjitāye lajjanti, </span> <span class='eng'>Unashamed of what is shameful, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp316:2'><span class='pli'>lajjitāye na lajjare; </span> <span class='eng'>ashamed of what is not shameful; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp316:3'><span class='pli'>Micchādiṭṭhisamādānā, </span> <span class='eng'>beings who uphold wrong view </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp316:4'><span class='pli'>sattā gacchanti duggatiṁ. </span> <span class='eng'>go to a bad place. </span></span></span></p></blockquote></article>\n<article id='dhp317'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp317:1'><span class='pli'>Abhaye bhayadassino, </span> <span class='eng'>Seeing danger where there is none, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp317:2'><span class='pli'>bhaye cābhayadassino; </span> <span class='eng'>and blind to the actual danger, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp317:3'><span class='pli'>Micchādiṭṭhisamādānā, </span> <span class='eng'>beings who uphold wrong view </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp317:4'><span class='pli'>sattā gacchanti duggatiṁ. </span> <span class='eng'>go to a bad place. </span></span></span></p></blockquote></article>\n<article id='dhp318'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp318:0'><span class='pli'>Titthiyasāvakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp318:1'><span class='pli'>Avajje vajjamatino, </span> <span class='eng'>Seeing fault where there is none, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp318:2'><span class='pli'>vajje cāvajjadassino; </span> <span class='eng'>and blind to the actual fault, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp318:3'><span class='pli'>Micchādiṭṭhisamādānā, </span> <span class='eng'>beings who uphold wrong view </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp318:4'><span class='pli'>sattā gacchanti duggatiṁ. </span> <span class='eng'>go to a bad place. </span></span></span></p></blockquote></article>\n<article id='dhp319'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp319:1'><span class='pli'>Vajjañca vajjato ñatvā, </span> <span class='eng'>Knowing a fault as a fault </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp319:2'><span class='pli'>avajjañca avajjato; </span> <span class='eng'>and the faultless as faultless, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp319:3'><span class='pli'>Sammādiṭṭhisamādānā, </span> <span class='eng'>beings who uphold right view </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp319:4'><span class='pli'>sattā gacchanti suggatiṁ. </span> <span class='eng'>go to a good place. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp319:5'><span class='pli'>Nirayavaggo dvāvīsatimo. </span></span></p></article></section>\n",
    "acronym": "Dhp 306–319",
    "translated_title": "22. Hell",
    "original_title": "Nirayavagga"
  },
  "dhp320-333": {
    "previous": "dhp306-319",
    "next": "dhp334-359",
    "content": "<section class='range'  id='dhp320-333'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp320:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp320:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 320–333 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp320:0.3'><span class='pli'>Nāgavagga </span> <span class='eng'>23. Elephants </span></span></h1></header>\n<article id='dhp320'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp320:0.4'><span class='pli'>Attadantavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp320:1'><span class='pli'>Ahaṁ nāgova saṅgāme, </span> <span class='eng'>Like an elephant struck </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp320:2'><span class='pli'>cāpato patitaṁ saraṁ; </span> <span class='eng'>with arrows in battle, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp320:3'><span class='pli'>Ativākyaṁ titikkhissaṁ, </span> <span class='eng'>I shall withstand abuse, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp320:4'><span class='pli'>dussīlo hi bahujjano. </span> <span class='eng'>for so many folk are badly behaved. </span></span></span></p></blockquote></article>\n<article id='dhp321'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp321:1'><span class='pli'>Dantaṁ nayanti samitiṁ, </span> <span class='eng'>The well-tamed beast is the one led to the crowd; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp321:2'><span class='pli'>dantaṁ rājābhirūhati; </span> <span class='eng'>the tamed elephant’s the one the king mounts; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp321:3'><span class='pli'>Danto seṭṭho manussesu, </span> <span class='eng'>the tamed person who withstands abuse </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp321:4'><span class='pli'>yotivākyaṁ titikkhati. </span> <span class='eng'>is the best of human beings. </span></span></span></p></blockquote></article>\n<article id='dhp322'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp322:1'><span class='pli'>Varamassatarā dantā, </span> <span class='eng'>Those who have tamed themselves are better </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp322:2'><span class='pli'>ājānīyā ca sindhavā; </span> <span class='eng'>than fine tamed mules, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp322:3'><span class='pli'>Kuñjarā ca mahānāgā, </span> <span class='eng'>thoroughbreds from Sindh, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp322:4'><span class='pli'>attadanto tato varaṁ. </span> <span class='eng'>or giant tuskers. </span></span></span></p></blockquote></article>\n<article id='dhp323'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp323:0'><span class='pli'>Hatthācariyapubbakabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp323:1'><span class='pli'>Na hi etehi yānehi, </span> <span class='eng'>For not on those mounts </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp323:2'><span class='pli'>gaccheyya agataṁ disaṁ; </span> <span class='eng'>would you go to the untrodden place, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp323:3'><span class='pli'>Yathāttanā sudantena, </span> <span class='eng'>whereas, with the help of one <j>whose self is well tamed, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp323:4'><span class='pli'>danto dantena gacchati. </span> <span class='eng'>you go there, tamed by the tamed. </span></span></span></p></blockquote></article>\n<article id='dhp324'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp324:0'><span class='pli'>Parijiṇṇabrāhmaṇaputtavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp324:1'><span class='pli'>Dhanapālo nāma kuñjaro, </span> <span class='eng'>The tusker named Dhanapāla </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp324:2'><span class='pli'>Kaṭukabhedano dunnivārayo; </span> <span class='eng'>is musky in rut, hard to control. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp324:3'><span class='pli'>Baddho kabaḷaṁ na bhuñjati, </span> <span class='eng'>Bound, he eats not a morsel, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp324:4'><span class='pli'>Sumarati nāgavanassa kuñjaro. </span> <span class='eng'>for he misses the elephant forest. </span></span></span></p></blockquote></article>\n<article id='dhp325'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp325:0'><span class='pli'>Pasenadikosalavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp325:1'><span class='pli'>Middhī yadā hoti mahagghaso ca, </span> <span class='eng'>One who gets drowsy from overeating, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp325:2'><span class='pli'>Niddāyitā samparivattasāyī; </span> <span class='eng'>fond of sleep, rolling round the bed </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp325:3'><span class='pli'>Mahāvarāhova nivāpapuṭṭho, </span> <span class='eng'>like a great hog stuffed with grain: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp325:4'><span class='pli'>Punappunaṁ gabbhamupeti mando. </span> <span class='eng'>that dullard returns to the womb again and again. </span></span></span></p></blockquote></article>\n<article id='dhp326'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp326:0'><span class='pli'>Sānusāmaṇeravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp326:1'><span class='pli'>Idaṁ pure cittamacāri cārikaṁ, </span> <span class='eng'>In the past my mind wandered </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp326:2'><span class='pli'>Yenicchakaṁ yatthakāmaṁ yathāsukhaṁ; </span> <span class='eng'>how it wished, where it liked, as it pleased. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp326:3'><span class='pli'>Tadajjahaṁ niggahessāmi yoniso, </span> <span class='eng'>Now I’ll carefully guide it, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp326:4'><span class='pli'>Hatthippabhinnaṁ viya aṅkusaggaho. </span> <span class='eng'>as a trainer with a hook guides a rutting elephant. </span></span></span></p></blockquote></article>\n<article id='dhp327'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp327:0'><span class='pli'>Pāveyyakahatthivatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp327:1'><span class='pli'>Appamādaratā hotha, </span> <span class='eng'>Delight in diligence! </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp327:2'><span class='pli'>sacittamanurakkhatha; </span> <span class='eng'>Take good care of your mind! </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp327:3'><span class='pli'>Duggā uddharathattānaṁ, </span> <span class='eng'>Pull yourself out of this pit, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp327:4'><span class='pli'>paṅke sannova kuñjaro. </span> <span class='eng'>like an elephant sunk in a bog. </span></span></span></p></blockquote></article>\n<article id='dhp328'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp328:0'><span class='pli'>Sambahulabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp328:1'><span class='pli'>Sace labhetha nipakaṁ sahāyaṁ, </span> <span class='eng'>If you find an alert companion, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp328:2'><span class='pli'>Saddhiṁ caraṁ sādhuvihāridhīraṁ; </span> <span class='eng'>an attentive friend to live happily together, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp328:3'><span class='pli'>Abhibhuyya sabbāni parissayāni, </span> <span class='eng'>then, overcoming all adversities, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp328:4'><span class='pli'>Careyya tenattamano satīmā. </span> <span class='eng'>wander with them, joyful and mindful. </span></span></span></p></blockquote></article>\n<article id='dhp329'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp329:1'><span class='pli'>No ce labhetha nipakaṁ sahāyaṁ, </span> <span class='eng'>If you find no alert companion, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp329:2'><span class='pli'>Saddhiṁ caraṁ sādhuvihāridhīraṁ; </span> <span class='eng'>no attentive friend to live happily together, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp329:3'><span class='pli'>Rājāva raṭṭhaṁ vijitaṁ pahāya, </span> <span class='eng'>then, like a king who flees his conquered realm, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp329:4'><span class='pli'>Eko care mātaṅgaraññeva nāgo. </span> <span class='eng'>wander alone like a tusker in the wilds. </span></span></span></p></blockquote></article>\n<article id='dhp330'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp330:1'><span class='pli'>Ekassa caritaṁ seyyo, </span> <span class='eng'>It’s better to wander alone, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp330:2'><span class='pli'>Natthi bāle sahāyatā; </span> <span class='eng'>there’s no fellowship with fools. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp330:3'><span class='pli'>Eko care na ca pāpāni kayirā, </span> <span class='eng'>Wander alone and do no wrong, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp330:4'><span class='pli'>Appossukko mātaṅgaraññeva nāgo. </span> <span class='eng'>at ease like a tusker in the wilds. </span></span></span></p></blockquote></article>\n<article id='dhp331'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp331:0'><span class='pli'>Māravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp331:1'><span class='pli'>Atthamhi jātamhi sukhā sahāyā, </span> <span class='eng'>A friend in need is a blessing; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp331:2'><span class='pli'>Tuṭṭhī sukhā yā itarītarena; </span> <span class='eng'>it’s a blessing to be content with whatever; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp331:3'><span class='pli'>Puññaṁ sukhaṁ jīvitasaṅkhayamhi, </span> <span class='eng'>good deeds are a blessing at the end of life, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp331:4'><span class='pli'>Sabbassa dukkhassa sukhaṁ pahānaṁ. </span> <span class='eng'>and giving up all suffering is a blessing. </span></span></span></p></blockquote></article>\n<article id='dhp332'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp332:1'><span class='pli'>Sukhā matteyyatā loke, </span> <span class='eng'>In this world it’s a blessing to serve </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp332:2'><span class='pli'>atho petteyyatā sukhā; </span> <span class='eng'>one’s mother and one’s father. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp332:3'><span class='pli'>Sukhā sāmaññatā loke, </span> <span class='eng'>And it’s a blessing also to serve </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp332:4'><span class='pli'>atho brahmaññatā sukhā. </span> <span class='eng'>ascetics and brahmins. </span></span></span></p></blockquote></article>\n<article id='dhp333'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp333:1'><span class='pli'>Sukhaṁ yāva jarā sīlaṁ, </span> <span class='eng'>It’s a blessing to keep precepts until you grow old; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp333:2'><span class='pli'>sukhā saddhā patiṭṭhitā; </span> <span class='eng'>a blessing to be grounded in faith; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp333:3'><span class='pli'>Sukho paññāya paṭilābho, </span> <span class='eng'>the getting of wisdom’s a blessing; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp333:4'><span class='pli'>pāpānaṁ akaraṇaṁ sukhaṁ. </span> <span class='eng'>and it’s a blessing to avoid doing wrong. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp333:5'><span class='pli'>Nāgavaggo tevīsatimo. </span></span></p></article></section>\n",
    "acronym": "Dhp 320–333",
    "translated_title": "23. Elephants",
    "original_title": "Nāgavagga"
  },
  "dhp334-359": {
    "previous": "dhp320-333",
    "next": "dhp360-382",
    "content": "<section class='range'  id='dhp334-359'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp334:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp334:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 334–359 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp334:0.3'><span class='pli'>Taṇhāvagga </span> <span class='eng'>24. Craving </span></span></h1></header>\n<article id='dhp334'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp334:0.4'><span class='pli'>Kapilamacchavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp334:1'><span class='pli'>Manujassa pamattacārino, </span> <span class='eng'>When a man lives heedlessly, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp334:2'><span class='pli'>Taṇhā vaḍḍhati māluvā viya; </span> <span class='eng'>craving grows in them like a camel’s foot creeper. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp334:3'><span class='pli'>So plavatī hurā huraṁ, </span> <span class='eng'>They jump from one thing to the next, like a langur </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp334:4'><span class='pli'>Phalamicchaṁva vanasmi vānaro. </span> <span class='eng'>greedy for fruit in a forest grove. </span> <span class='comment-marker' data-comment='The <i lang=&#39;pi&#39; translate=&#39;no&#39;>vānara</i> is a type of human-like monkey often identified with the langur. See <a href=&#39;index.html?q=ja57&#39;>Ja 57</a>. '>*</span></span></span></p></blockquote></article>\n<article id='dhp335'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp335:1'><span class='pli'>Yaṁ esā sahate jammī, </span> <span class='eng'>Whoever is beaten by this wretched craving, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp335:2'><span class='pli'>taṇhā loke visattikā; </span> <span class='eng'>this attachment to the world, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp335:3'><span class='pli'>Sokā tassa pavaḍḍhanti, </span> <span class='eng'>their sorrow grows, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp335:4'><span class='pli'>abhivaṭṭhaṁva bīraṇaṁ. </span> <span class='eng'>like grass in the rain. </span></span></span></p></blockquote></article>\n<article id='dhp336'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp336:1'><span class='pli'>Yo cetaṁ sahate jammiṁ, </span> <span class='eng'>But whoever prevails over this wretched craving, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp336:2'><span class='pli'>taṇhaṁ loke duraccayaṁ; </span> <span class='eng'>so hard to get over in the world, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp336:3'><span class='pli'>Sokā tamhā papatanti, </span> <span class='eng'>their sorrows fall from them, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp336:4'><span class='pli'>udabinduva pokkharā. </span> <span class='eng'>like a drop from a lotus-leaf. </span></span></span></p></blockquote></article>\n<article id='dhp337'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp337:1'><span class='pli'>Taṁ vo vadāmi bhaddaṁ vo, </span> <span class='eng'>I say this to you, good people, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp337:2'><span class='pli'>yāvantettha samāgatā; </span> <span class='eng'>all those who have gathered here: </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp337:3'><span class='pli'>Taṇhāya mūlaṁ khaṇatha, </span> <span class='eng'>dig up the root of craving, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp337:4'><span class='pli'>usīratthova bīraṇaṁ; </span> <span class='eng'>as you’d dig up grass in search of roots. </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp337:5'><span class='pli'>Mā vo naḷaṁva sotova, </span> <span class='eng'>Don’t let Māra break you again and again, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp337:6'><span class='pli'>māro bhañji punappunaṁ. </span> <span class='eng'>like a stream breaking a reed. </span></span></span></p></blockquote></article>\n<article id='dhp338'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp338:0'><span class='pli'>Sūkarapotikāvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp338:1'><span class='pli'>Yathāpi mūle anupaddave daḷhe, </span> <span class='eng'>A tree grows back even when cut down, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp338:2'><span class='pli'>Chinnopi rukkho punareva rūhati; </span> <span class='eng'>so long as its roots are strong and undamaged; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp338:3'><span class='pli'>Evampi taṇhānusaye anūhate, </span> <span class='eng'>suffering springs up again and again, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp338:4'><span class='pli'>Nibbattatī dukkhamidaṁ punappunaṁ. </span> <span class='eng'>so long as the tendency to craving is not pulled out. </span></span></span></p></blockquote></article>\n<article id='dhp339'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp339:1'><span class='pli'>Yassa chattiṁsati sotā, </span> <span class='eng'>A person of low views </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp339:2'><span class='pli'>manāpasavanā bhusā; </span> <span class='eng'>in whom the thirty-six streams </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp339:3'><span class='pli'>Mahāvahanti duddiṭṭhiṁ, </span> <span class='eng'>that flow to pleasure are mighty, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp339:4'><span class='pli'>saṅkappā rāganissitā. </span> <span class='eng'>is swept away by lustful thoughts. </span></span></span></p></blockquote></article>\n<article id='dhp340'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp340:1'><span class='pli'>Savanti sabbadhi sotā, </span> <span class='eng'>The streams flow everywhere; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp340:2'><span class='pli'>latā uppajja tiṭṭhati; </span> <span class='eng'>a weed springs up and remains. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp340:3'><span class='pli'>Tañca disvā lataṁ jātaṁ, </span> <span class='eng'>Seeing this weed that has been born, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp340:4'><span class='pli'>mūlaṁ paññāya chindatha. </span> <span class='eng'>cut the root with wisdom. </span></span></span></p></blockquote></article>\n<article id='dhp341'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp341:1'><span class='pli'>Saritāni sinehitāni ca, </span> <span class='eng'>A personage’s joys </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp341:2'><span class='pli'>Somanassāni bhavanti jantuno; </span> <span class='eng'>flow from senses and cravings. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp341:3'><span class='pli'>Te sātasitā sukhesino, </span> <span class='eng'>Seekers of happiness, bent on pleasure, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp341:4'><span class='pli'>Te ve jātijarūpagā narā. </span> <span class='eng'>continue to be reborn and grow old. </span></span></span></p></blockquote></article>\n<article id='dhp342'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp342:1'><span class='pli'>Tasiṇāya purakkhatā pajā, </span> <span class='eng'>People governed by thirst, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp342:2'><span class='pli'>Parisappanti sasova bandhito; </span> <span class='eng'>crawl about like a trapped rabbit. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp342:3'><span class='pli'>Saṁyojanasaṅgasattakā, </span> <span class='eng'>Bound and fettered, for a long time </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp342:4'><span class='pli'>Dukkhamupenti punappunaṁ cirāya. </span> <span class='eng'>they return to pain time and again. </span></span></span></p></blockquote></article>\n<article id='dhp343'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp343:1'><span class='pli'>Tasiṇāya purakkhatā pajā, </span> <span class='eng'>People governed by thirst, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp343:2'><span class='pli'>Parisappanti sasova bandhito; </span> <span class='eng'>crawl about like a trapped rabbit. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp343:3'><span class='pli'>Tasmā tasiṇaṁ vinodaye, </span> <span class='eng'>That’s why one who longs for dispassion </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp343:4'><span class='pli'>Ākaṅkhanta virāgamattano. </span> <span class='eng'>should dispel thirst. </span></span></span></p></blockquote></article>\n<article id='dhp344'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp344:0'><span class='pli'>Vibbhantabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp344:1'><span class='pli'>Yo nibbanatho vanādhimutto, </span> <span class='eng'>Unsnarled, they set out for the jungle, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp344:2'><span class='pli'>Vanamutto vanameva dhāvati; </span> <span class='eng'>then they run right back to the jungle they left behind. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp344:3'><span class='pli'>Taṁ puggalametha passatha, </span> <span class='eng'>Just look at this individual! </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp344:4'><span class='pli'>Mutto bandhanameva dhāvati. </span> <span class='eng'>Freed, they run to bondage. </span></span></span></p></blockquote></article>\n<article id='dhp345'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp345:0'><span class='pli'>Bandhanāgāravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp345:1'><span class='pli'>Na taṁ daḷhaṁ bandhanamāhu dhīrā, </span> <span class='eng'>The attentive say that shackle is not strong </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp345:2'><span class='pli'>Yadāyasaṁ dārujapabbajañca; </span> <span class='eng'>that’s made of iron, wood, or knots. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp345:3'><span class='pli'>Sārattarattā maṇikuṇḍalesu, </span> <span class='eng'>But obsession with jeweled earrings, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp345:4'><span class='pli'>Puttesu dāresu ca yā apekkhā. </span> <span class='eng'>concern for your partners and children: </span></span></span></p></blockquote></article>\n<article id='dhp346'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp346:1'><span class='pli'>Etaṁ daḷhaṁ bandhanamāhu dhīrā, </span> <span class='eng'>this, say the attentive, is a strong shackle </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp346:2'><span class='pli'>Ohārinaṁ sithilaṁ duppamuñcaṁ; </span> <span class='eng'>dragging the indulgent down, hard to escape. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp346:3'><span class='pli'>Etampi chetvāna paribbajanti, </span> <span class='eng'>Having cut this one too they go forth, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp346:4'><span class='pli'>Anapekkhino kāmasukhaṁ pahāya. </span> <span class='eng'>unconcerned, having given up sensual pleasures. </span></span></span></p></blockquote></article>\n<article id='dhp347'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp347:0'><span class='pli'>Khemātherīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp347:1'><span class='pli'>Ye rāgarattānupatanti sotaṁ, </span> <span class='eng'>Besotted by lust they fall into the stream, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp347:2'><span class='pli'>Sayaṅkataṁ makkaṭakova jālaṁ; </span> <span class='eng'>like a spider caught in the web she wove. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp347:3'><span class='pli'>Etampi chetvāna vajanti dhīrā, </span> <span class='eng'>The attentive proceed, having cut this one too, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp347:4'><span class='pli'>Anapekkhino sabbadukkhaṁ pahāya. </span> <span class='eng'>unconcerned, having given up all suffering. </span></span></span></p></blockquote></article>\n<article id='dhp348'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp348:0'><span class='pli'>Uggasenavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp348:1'><span class='pli'>Muñca pure muñca pacchato, </span> <span class='eng'>Let go of the past, let go of the future, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp348:2'><span class='pli'>Majjhe muñca bhavassa pāragū; </span> <span class='eng'>let go of the present, having gone beyond rebirth. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp348:3'><span class='pli'>Sabbattha vimuttamānaso, </span> <span class='eng'>With your heart freed in every respect, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp348:4'><span class='pli'>Na punaṁ jātijaraṁ upehisi. </span> <span class='eng'>you’ll not come again to rebirth and old age. </span></span></span></p></blockquote></article>\n<article id='dhp349'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp349:0'><span class='pli'>Cūḷadhanuggahapaṇḍitavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp349:1'><span class='pli'>Vitakkamathitassa jantuno, </span> <span class='eng'>For a personage churned by thoughts, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp349:2'><span class='pli'>Tibbarāgassa subhānupassino; </span> <span class='eng'>very lustful, focusing on beauty, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp349:3'><span class='pli'>Bhiyyo taṇhā pavaḍḍhati, </span> <span class='eng'>their craving grows and grows, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp349:4'><span class='pli'>Esa kho daḷhaṁ karoti bandhanaṁ. </span> <span class='eng'>tying them with a stout bond. </span></span></span></p></blockquote></article>\n<article id='dhp350'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp350:1'><span class='pli'>Vitakkūpasame ca yo rato, </span> <span class='eng'>But one who loves to calm their thoughts, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp350:2'><span class='pli'>Asubhaṁ bhāvayate sadā sato; </span> <span class='eng'>developing perception of ugliness, ever mindful, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp350:3'><span class='pli'>Esa kho byanti kāhiti, </span> <span class='eng'>will surely eliminate that craving, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp350:4'><span class='pli'>Esa checchati mārabandhanaṁ. </span> <span class='eng'>cutting off the bonds of Māra. </span></span></span></p></blockquote></article>\n<article id='dhp351'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp351:0'><span class='pli'>Māravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp351:1'><span class='pli'>Niṭṭhaṅgato asantāsī, </span> <span class='eng'>One who is confident, unafraid, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp351:2'><span class='pli'>vītataṇho anaṅgaṇo; </span> <span class='eng'>rid of craving, free of blemish, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp351:3'><span class='pli'>Acchindi bhavasallāni, </span> <span class='eng'>having struck down the arrows flying to future lives, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp351:4'><span class='pli'>antimoyaṁ samussayo. </span> <span class='eng'>this bag of bones is their last. </span></span></span></p></blockquote></article>\n<article id='dhp352'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp352:1'><span class='pli'>Vītataṇho anādāno, </span> <span class='eng'>Rid of craving, free of grasping, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp352:2'><span class='pli'>Niruttipadakovido; </span> <span class='eng'>expert in the definition of terms, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp352:3'><span class='pli'>Akkharānaṁ sannipātaṁ, </span> <span class='eng'>knowing the correct </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp352:4'><span class='pli'>Jaññā pubbāparāni ca; </span> <span class='eng'>structure and sequence of syllables, </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp352:5'><span class='pli'>Sa ve “antimasārīro, </span> <span class='eng'>they are said to be one who bears their final body, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp352:6'><span class='pli'>Mahāpañño mahāpuriso”ti vuccati. </span> <span class='eng'>one of great wisdom, a great person. </span></span></span></p></blockquote></article>\n<article id='dhp353'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp353:0'><span class='pli'>Upakājīvakavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp353:1'><span class='pli'>Sabbābhibhū sabbavidūhamasmi, </span> <span class='eng'>I am the champion, the knower of all, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp353:2'><span class='pli'>Sabbesu dhammesu anūpalitto; </span> <span class='eng'>unsullied in the midst of all things. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp353:3'><span class='pli'>Sabbañjaho taṇhakkhaye vimutto, </span> <span class='eng'>I’ve given up all, freed in the ending of craving. </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp353:4'><span class='pli'>Sayaṁ abhiññāya kamuddiseyyaṁ. </span> <span class='eng'>Since I know for myself, whose follower should I be? </span></span></span></p></blockquote></article>\n<article id='dhp354'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp354:0'><span class='pli'>Sakkapañhavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp354:1'><span class='pli'>Sabbadānaṁ dhammadānaṁ jināti, </span> <span class='eng'>The gift of the teaching surmounts all other gifts; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp354:2'><span class='pli'>Sabbarasaṁ dhammaraso jināti; </span> <span class='eng'>the taste of the teaching surmounts all other tastes; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp354:3'><span class='pli'>Sabbaratiṁ dhammarati jināti, </span> <span class='eng'>the joy of the teaching surmounts all other joys; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp354:4'><span class='pli'>Taṇhakkhayo sabbadukkhaṁ jināti. </span> <span class='eng'>the ending of craving surmounts all suffering. </span></span></span></p></blockquote></article>\n<article id='dhp355'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp355:0'><span class='pli'>Aputtakaseṭṭhivatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp355:1'><span class='pli'>Hananti bhogā dummedhaṁ, </span> <span class='eng'>Riches ruin a simpleton, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp355:2'><span class='pli'>no ca pāragavesino; </span> <span class='eng'>but not a seeker of the far shore. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp355:3'><span class='pli'>Bhogataṇhāya dummedho, </span> <span class='eng'>From craving for wealth, a simpleton </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp355:4'><span class='pli'>hanti aññeva attanaṁ. </span> <span class='eng'>ruins themselves and others. </span></span></span></p></blockquote></article>\n<article id='dhp356'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp356:0'><span class='pli'>Aṅkuravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp356:1'><span class='pli'>Tiṇadosāni khettāni, </span> <span class='eng'>Weeds are the bane of crops, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp356:2'><span class='pli'>rāgadosā ayaṁ pajā; </span> <span class='eng'>but greed is these folk’s bane. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp356:3'><span class='pli'>Tasmā hi vītarāgesu, </span> <span class='eng'>That’s why a gift to one rid of greed </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp356:4'><span class='pli'>dinnaṁ hoti mahapphalaṁ. </span> <span class='eng'>is so very fruitful. </span></span></span></p></blockquote></article>\n<article id='dhp357'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp357:1'><span class='pli'>Tiṇadosāni khettāni, </span> <span class='eng'>Weeds are the bane of crops, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp357:2'><span class='pli'>dosadosā ayaṁ pajā; </span> <span class='eng'>but hate is these folk’s bane. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp357:3'><span class='pli'>Tasmā hi vītadosesu, </span> <span class='eng'>That’s why a gift to one rid of hate </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp357:4'><span class='pli'>dinnaṁ hoti mahapphalaṁ. </span> <span class='eng'>is so very fruitful. </span></span></span></p></blockquote></article>\n<article id='dhp358'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp358:1'><span class='pli'>Tiṇadosāni khettāni, </span> <span class='eng'>Weeds are the bane of crops, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp358:2'><span class='pli'>mohadosā ayaṁ pajā; </span> <span class='eng'>but delusion is these folk’s bane. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp358:3'><span class='pli'>Tasmā hi vītamohesu, </span> <span class='eng'>That’s why a gift to one rid of delusion </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp358:4'><span class='pli'>dinnaṁ hoti mahapphalaṁ. </span> <span class='eng'>is so very fruitful. </span></span></span></p></blockquote></article>\n<article id='dhp359'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp359:1'><span class='pli'>Tiṇadosāni khettāni, </span> <span class='eng'>Weeds are the bane of crops, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp359:2'><span class='pli'>icchādosā ayaṁ pajā; </span> <span class='eng'>but desire is these folk’s bane. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp359:3'><span class='pli'>Tasmā hi vigaticchesu, </span> <span class='eng'>That’s why a gift to one rid of desire </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp359:4'><span class='pli'>dinnaṁ hoti mahapphalaṁ. </span> <span class='eng'>is so very fruitful. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp359:5'><span class='pli'>Taṇhāvaggo catuvīsatimo. </span></span></p></article></section>\n",
    "acronym": "Dhp 334–359",
    "translated_title": "24. Craving",
    "original_title": "Taṇhāvagga"
  },
  "dhp360-382": {
    "previous": "dhp334-359",
    "next": "dhp383-423",
    "content": "<section class='range'  id='dhp360-382'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp360:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp360:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 360–382 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp360:0.3'><span class='pli'>Bhikkhuvagga </span> <span class='eng'>25. Mendicants </span></span></h1></header>\n<article id='dhp360'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp360:0.4'><span class='pli'>Pañcabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp360:1'><span class='pli'>Cakkhunā saṁvaro sādhu, </span> <span class='eng'>Restraint of the eye is good; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp360:2'><span class='pli'>sādhu sotena saṁvaro; </span> <span class='eng'>good is restraint of the ear; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp360:3'><span class='pli'>Ghānena saṁvaro sādhu, </span> <span class='eng'>restraint of the nose is good; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp360:4'><span class='pli'>sādhu jivhāya saṁvaro. </span> <span class='eng'>good is restraint of the tongue. </span></span></span></p></blockquote></article>\n<article id='dhp361'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp361:1'><span class='pli'>Kāyena saṁvaro sādhu, </span> <span class='eng'>Restraint of the body is good; </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp361:2'><span class='pli'>sādhu vācāya saṁvaro; </span> <span class='eng'>good is restraint of speech; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp361:3'><span class='pli'>Manasā saṁvaro sādhu, </span> <span class='eng'>restraint of mind is good; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp361:4'><span class='pli'>sādhu sabbattha saṁvaro; </span> <span class='eng'>everywhere, restraint is good. </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp361:5'><span class='pli'>Sabbattha saṁvuto bhikkhu, </span> <span class='eng'>The mendicant restrained everywhere </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp361:6'><span class='pli'>sabbadukkhā pamuccati. </span> <span class='eng'>is released from all suffering. </span></span></span></p></blockquote></article>\n<article id='dhp362'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp362:0'><span class='pli'>Haṁsaghātakabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp362:1'><span class='pli'>Hatthasaṁyato pādasaṁyato, </span> <span class='eng'>One restrained in hand and foot, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp362:2'><span class='pli'>Vācāsaṁyato saṁyatuttamo; </span> <span class='eng'>and in speech, the supreme restraint; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp362:3'><span class='pli'>Ajjhattarato samāhito, </span> <span class='eng'>happy inside, serene, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp362:4'><span class='pli'>Eko santusito tamāhu bhikkhuṁ. </span> <span class='eng'>solitary, content, I call a mendicant. </span></span></span></p></blockquote></article>\n<article id='dhp363'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp363:0'><span class='pli'>Kokālikavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp363:1'><span class='pli'>Yo mukhasaṁyato bhikkhu, </span> <span class='eng'>When a mendicant of restrained mouth, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp363:2'><span class='pli'>mantabhāṇī anuddhato; </span> <span class='eng'>thoughtful in counsel, not restless, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp363:3'><span class='pli'>Atthaṁ dhammañca dīpeti, </span> <span class='eng'>explains the text and its meaning, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp363:4'><span class='pli'>madhuraṁ tassa bhāsitaṁ. </span> <span class='eng'>their words are sweet. </span></span></span></p></blockquote></article>\n<article id='dhp364'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp364:0'><span class='pli'>Dhammārāmattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp364:1'><span class='pli'>Dhammārāmo dhammarato, </span> <span class='eng'>Delighting in the teaching, enjoying the teaching, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp364:2'><span class='pli'>dhammaṁ anuvicintayaṁ; </span> <span class='eng'>contemplating the teaching, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp364:3'><span class='pli'>Dhammaṁ anussaraṁ bhikkhu, </span> <span class='eng'>a mendicant who recollects the teaching </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp364:4'><span class='pli'>saddhammā na parihāyati. </span> <span class='eng'>doesn’t fall away from the true teaching. </span></span></span></p></blockquote></article>\n<article id='dhp365'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp365:0'><span class='pli'>Vipakkhasevakabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp365:1'><span class='pli'>Salābhaṁ nātimaññeyya, </span> <span class='eng'>A well-off mendicant ought not look down </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp365:2'><span class='pli'>nāññesaṁ pihayaṁ care; </span> <span class='eng'>on others, nor should they be envious. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp365:3'><span class='pli'>Aññesaṁ pihayaṁ bhikkhu, </span> <span class='eng'>A mendicant who envies others </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp365:4'><span class='pli'>samādhiṁ nādhigacchati. </span> <span class='eng'>does not achieve immersion. </span></span></span></p></blockquote></article>\n<article id='dhp366'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp366:1'><span class='pli'>Appalābhopi ce bhikkhu, </span> <span class='eng'>If a mendicant is poor in offerings, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp366:2'><span class='pli'>salābhaṁ nātimaññati; </span> <span class='eng'>the well-to-do ought not look down on them. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp366:3'><span class='pli'>Taṁ ve devā pasaṁsanti, </span> <span class='eng'>For the gods indeed praise them, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp366:4'><span class='pli'>suddhājīviṁ atanditaṁ. </span> <span class='eng'>who are tireless and pure of livelihood. </span></span></span></p></blockquote></article>\n<article id='dhp367'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp367:0'><span class='pli'>Pañcaggadāyakabrāhmaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp367:1'><span class='pli'>Sabbaso nāmarūpasmiṁ, </span> <span class='eng'>One who has no sense of ownership </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp367:2'><span class='pli'>yassa natthi mamāyitaṁ; </span> <span class='eng'>in the whole realm of name and form, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp367:3'><span class='pli'>Asatā ca na socati, </span> <span class='eng'>who does not grieve for that which is not, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp367:4'><span class='pli'>sa ve “bhikkhū”ti vuccati. </span> <span class='eng'>is said to be a mendicant. </span></span></span></p></blockquote></article>\n<article id='dhp368'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp368:0'><span class='pli'>Sambahulabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp368:1'><span class='pli'>Mettāvihārī yo bhikkhu, </span> <span class='eng'>A mendicant who meditates on love, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp368:2'><span class='pli'>pasanno buddhasāsane; </span> <span class='eng'>devoted to the Buddha’s teaching, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp368:3'><span class='pli'>Adhigacche padaṁ santaṁ, </span> <span class='eng'>would realize the peaceful state, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp368:4'><span class='pli'>saṅkhārūpasamaṁ sukhaṁ. </span> <span class='eng'>the blissful stilling of conditions. </span></span></span></p></blockquote></article>\n<article id='dhp369'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp369:1'><span class='pli'>Siñca bhikkhu imaṁ nāvaṁ, </span> <span class='eng'>Bail out this boat, mendicant! </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp369:2'><span class='pli'>sittā te lahumessati; </span> <span class='eng'>When bailed out it will float lightly. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp369:3'><span class='pli'>Chetvā rāgañca dosañca, </span> <span class='eng'>Having cut off desire and hate, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp369:4'><span class='pli'>tato nibbānamehisi. </span> <span class='eng'>you shall reach extinguishment. </span></span></span></p></blockquote></article>\n<article id='dhp370'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp370:1'><span class='pli'>Pañca chinde pañca jahe, </span> <span class='eng'>Five to cut, five to drop, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp370:2'><span class='pli'>pañca cuttari bhāvaye; </span> <span class='eng'>and five more to develop. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp370:3'><span class='pli'>Pañca saṅgātigo bhikkhu, </span> <span class='eng'>When a mendicant slips five chains </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp370:4'><span class='pli'>“oghatiṇṇo”ti vuccati. </span> <span class='eng'>they’re said to have crossed the flood. </span></span></span></p></blockquote></article>\n<article id='dhp371'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp371:1'><span class='pli'>Jhāya bhikkhu mā pamādo, </span> <span class='eng'>Practice absorption, don’t be negligent! </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp371:2'><span class='pli'>Mā te kāmaguṇe ramessu cittaṁ; </span> <span class='eng'>Don’t let the mind delight in the senses! </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp371:3'><span class='pli'>Mā lohaguḷaṁ gilī pamatto, </span> <span class='eng'>Don’t heedlessly swallow a hot iron ball! </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp371:4'><span class='pli'>Mā kandi “dukkhamidan”ti dayhamāno. </span> <span class='eng'>And when it burns, don’t cry, “Oh, the pain!” </span></span></span></p></blockquote></article>\n<article id='dhp372'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp372:1'><span class='pli'>Natthi jhānaṁ apaññassa, </span> <span class='eng'>No absorption for one without wisdom, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp372:2'><span class='pli'>paññā natthi ajhāyato; </span> <span class='eng'>no wisdom for one without absorption. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp372:3'><span class='pli'>Yamhi jhānañca paññā ca, </span> <span class='eng'>But one with absorption and wisdom—</span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp372:4'><span class='pli'>sa ve nibbānasantike. </span> <span class='eng'>they have truly drawn near to extinguishment. </span></span></span></p></blockquote></article>\n<article id='dhp373'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp373:1'><span class='pli'>Suññāgāraṁ paviṭṭhassa, </span> <span class='eng'>A mendicant who enters an empty hut </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp373:2'><span class='pli'>santacittassa bhikkhuno; </span> <span class='eng'>with mind at peace </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp373:3'><span class='pli'>Amānusī rati hoti, </span> <span class='eng'>finds a superhuman delight </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp373:4'><span class='pli'>sammā dhammaṁ vipassato. </span> <span class='eng'>as they rightly discern the Dhamma. </span></span></span></p></blockquote></article>\n<article id='dhp374'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp374:1'><span class='pli'>Yato yato sammasati, </span> <span class='eng'>Whenever they are mindful </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp374:2'><span class='pli'>khandhānaṁ udayabbayaṁ; </span> <span class='eng'>of the rise and fall of the aggregates, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp374:3'><span class='pli'>Labhatī pītipāmojjaṁ, </span> <span class='eng'>they feel rapture and joy: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp374:4'><span class='pli'>amataṁ taṁ vijānataṁ. </span> <span class='eng'>that is freedom from death for one who knows. </span></span></span></p></blockquote></article>\n<article id='dhp375'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp375:1'><span class='pli'>Tatrāyamādi bhavati, </span> <span class='eng'>This is the very start of the path </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp375:2'><span class='pli'>idha paññassa bhikkhuno; </span> <span class='eng'>for a wise mendicant: </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp375:3'><span class='pli'>Indriyagutti santuṭṭhi, </span> <span class='eng'>guarding the senses, contentment, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp375:4'><span class='pli'>pātimokkhe ca saṁvaro. </span> <span class='eng'>and restraint in the monastic code. </span></span></span></p></blockquote></article>\n<article id='dhp376'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp376:1'><span class='pli'>Mitte bhajassu kalyāṇe, </span> <span class='eng'>Mix with spiritual friends, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp376:2'><span class='pli'>suddhājīve atandite; </span> <span class='eng'>who are tireless and pure of livelihood. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp376:3'><span class='pli'>Paṭisanthāravutyassa, </span> <span class='eng'>Share what you have with others, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp376:4'><span class='pli'>ācārakusalo siyā; </span> <span class='eng'>being skillful in your conduct. </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp376:5'><span class='pli'>Tato pāmojjabahulo, </span> <span class='eng'>And when you’re full of joy, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp376:6'><span class='pli'>dukkhassantaṁ karissati. </span> <span class='eng'>you’ll make an end to suffering. </span></span></span></p></blockquote></article>\n<article id='dhp377'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp377:0'><span class='pli'>Pañcasatabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp377:1'><span class='pli'>Vassikā viya pupphāni, </span> <span class='eng'>As a jasmine sheds </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp377:2'><span class='pli'>maddavāni pamuñcati; </span> <span class='eng'>its withered flowers, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp377:3'><span class='pli'>Evaṁ rāgañca dosañca, </span> <span class='eng'>O mendicants, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp377:4'><span class='pli'>vippamuñcetha bhikkhavo. </span> <span class='eng'>shed greed and hate. </span></span></span></p></blockquote></article>\n<article id='dhp378'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp378:0'><span class='pli'>Santakāyattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp378:1'><span class='pli'>Santakāyo santavāco, </span> <span class='eng'>Calm in body, calm in speech, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp378:2'><span class='pli'>santavā susamāhito; </span> <span class='eng'>peaceful and serene; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp378:3'><span class='pli'>Vantalokāmiso bhikkhu, </span> <span class='eng'>a mendicant who’s spat out the world’s bait </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp378:4'><span class='pli'>“upasanto”ti vuccati. </span> <span class='eng'>is said to be one at peace. </span></span></span></p></blockquote></article>\n<article id='dhp379'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp379:0'><span class='pli'>Naṅgalakulattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp379:1'><span class='pli'>Attanā codayattānaṁ, </span> <span class='eng'>Urge yourself on, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp379:2'><span class='pli'>paṭimaṁsetha attanā; </span> <span class='eng'>reflect on yourself. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp379:3'><span class='pli'>So attagutto satimā, </span> <span class='eng'>A mendicant self-controlled and mindful </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp379:4'><span class='pli'>sukhaṁ bhikkhu vihāhisi. </span> <span class='eng'>will always dwell in happiness. </span></span></span></p></blockquote></article>\n<article id='dhp380'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp380:1'><span class='pli'>Attā hi attano nātho, </span> <span class='eng'>Self is indeed the lord of self, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp380:2'><span class='pli'>ko hi nātho paro siyā; </span> <span class='eng'>for who else would be one’s lord? </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp380:3'><span class='pli'>Attā hi attano gati, </span> <span class='eng'>Self is indeed the home of self, </span> <span class='comment-marker' data-comment='<i lang=&#39;pi&#39; translate=&#39;no&#39;>Gati</i> is glossed by the commentary as <i lang=&#39;pi&#39; translate=&#39;no&#39;>saraṇa</i> (“refuge”), which agrees with the Sanskrit Udānavarga’s <i lang=&#39;pi&#39; translate=&#39;no&#39;>śaraṇa</i>. '>*</span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp380:4'><span class='pli'>tasmā saṁyamamattānaṁ; </span> <span class='eng'>so restrain yourself, </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp380:5'><span class='pli'>Assaṁ bhadraṁva vāṇijo. </span> <span class='eng'>as a merchant his thoroughbred steed. </span></span></span></p></blockquote></article>\n<article id='dhp381'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp381:0'><span class='pli'>Vakkalittheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp381:1'><span class='pli'>Pāmojjabahulo bhikkhu, </span> <span class='eng'>A monk full of joy </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp381:2'><span class='pli'>pasanno buddhasāsane; </span> <span class='eng'>trusting in the Buddha’s teaching, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp381:3'><span class='pli'>Adhigacche padaṁ santaṁ, </span> <span class='eng'>would realize the peaceful state, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp381:4'><span class='pli'>saṅkhārūpasamaṁ sukhaṁ. </span> <span class='eng'>the blissful stilling of conditions. </span></span></span></p></blockquote></article>\n<article id='dhp382'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp382:0'><span class='pli'>Sumanasāmaṇeravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp382:1'><span class='pli'>Yo have daharo bhikkhu, </span> <span class='eng'>A young mendicant </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp382:2'><span class='pli'>yuñjati buddhasāsane; </span> <span class='eng'>devoted to the Buddha’s teaching, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp382:3'><span class='pli'>Somaṁ lokaṁ pabhāseti, </span> <span class='eng'>lights up the world, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp382:4'><span class='pli'>abbhā muttova candimā. </span> <span class='eng'>like the moon freed from clouds. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='5' data-uid='dhp382:5'><span class='pli'>Bhikkhuvaggo pañcavīsatimo. </span></span></p></article></section>\n",
    "acronym": "Dhp 360–382",
    "translated_title": "25. Mendicants",
    "original_title": "Bhikkhuvagga"
  },
  "dhp383-423": {
    "previous": "dhp360-382",
    "next": null,
    "content": "<section class='range'  id='dhp383-423'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='dhp383:0.1'><span class='pli'>Khuddakanikāya </span> <span class='eng'>Minor Collection </span></span></li>\n<li class='subdivision'><span class='segment' id='0.2' data-uid='dhp383:0.2'><span class='pli'>Dhammapada </span> <span class='eng'>Sayings of the Dhamma 383–423 </span></span></li></ul>\n<h1 class='range-title'><span class='segment' id='0.3' data-uid='dhp383:0.3'><span class='pli'>Brāhmaṇavagga </span> <span class='eng'>26. Brahmins </span></span></h1></header>\n<article id='dhp383'><h2 class='sutta-title'><span class='segment' id='0.4' data-uid='dhp383:0.4'><span class='pli'>Pasādabahulabrāhmaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp383:1'><span class='pli'>Chinda sotaṁ parakkamma, </span> <span class='eng'>Cut the stream, striving! </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp383:2'><span class='pli'>kāme panuda brāhmaṇa; </span> <span class='eng'>Cast aside sensual pleasures, brahmin. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp383:3'><span class='pli'>Saṅkhārānaṁ khayaṁ ñatvā, </span> <span class='eng'>Knowing the ending of conditions, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp383:4'><span class='pli'>akataññūsi brāhmaṇa. </span> <span class='eng'>know the uncreated, brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp384'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp384:0'><span class='pli'>Sambahulabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp384:1'><span class='pli'>Yadā dvayesu dhammesu, </span> <span class='eng'>When a brahmin has gone beyond </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp384:2'><span class='pli'>pāragū hoti brāhmaṇo; </span> <span class='eng'>dualistic phenomena, </span> <span class='comment-marker' data-comment='The commentary says this refers to the mastery of two things, serenity and discernment (cf. <a href=&#39;index.html?q=dn34#1.3.5&#39;>DN 34:1.3.5</a>). However, the term is not “two” but “duality” (<i lang=&#39;pi&#39; translate=&#39;no&#39;>dvaya</i>), suggesting a link to <a href=&#39;index.html?q=sn35.92#2.2&#39;>SN 35.92:2.2</a>, the duality of sense experience. '>*</span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp384:3'><span class='pli'>Athassa sabbe saṁyogā, </span> <span class='eng'>then they consciously </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp384:4'><span class='pli'>atthaṁ gacchanti jānato. </span> <span class='eng'>make an end of all fetters. </span></span></span></p></blockquote></article>\n<article id='dhp385'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp385:0'><span class='pli'>Māravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp385:1'><span class='pli'>Yassa pāraṁ apāraṁ vā, </span> <span class='eng'>One for whom there is no crossing over </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp385:2'><span class='pli'>pārāpāraṁ na vijjati; </span> <span class='eng'>or crossing back, or crossing over and back; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp385:3'><span class='pli'>Vītaddaraṁ visaṁyuttaṁ, </span> <span class='eng'>stress-free, detached, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp385:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp386'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp386:0'><span class='pli'>Aññatarabrāhmaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp386:1'><span class='pli'>Jhāyiṁ virajamāsīnaṁ, </span> <span class='eng'>Absorbed, rid of hopes, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp386:2'><span class='pli'>katakiccamanāsavaṁ; </span> <span class='eng'>their task completed, without defilements, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp386:3'><span class='pli'>Uttamatthamanuppattaṁ, </span> <span class='eng'>arrived at the highest goal: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp386:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp387'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp387:0'><span class='pli'>Ānandattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp387:1'><span class='pli'>Divā tapati ādicco, </span> <span class='eng'>The sun blazes by day, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp387:2'><span class='pli'>rattimābhāti candimā; </span> <span class='eng'>the moon radiates at night, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp387:3'><span class='pli'>Sannaddho khattiyo tapati, </span> <span class='eng'>the aristocrat shines in armor, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp387:4'><span class='pli'>jhāyī tapati brāhmaṇo; </span> <span class='eng'>and the brahmin shines in absorption. </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp387:5'><span class='pli'>Atha sabbamahorattiṁ, </span> <span class='eng'>But all day and all night, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp387:6'><span class='pli'>buddho tapati tejasā. </span> <span class='eng'>the Buddha blazes with glory. </span></span></span></p></blockquote></article>\n<article id='dhp388'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp388:0'><span class='pli'>Aññatarabrāhmaṇapabbajitavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp388:1'><span class='pli'>Bāhitapāpoti brāhmaṇo, </span> <span class='eng'>A brahmin’s so-called <j>since they’ve banished evil, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp388:2'><span class='pli'>Samacariyā samaṇoti vuccati; </span> <span class='eng'>an ascetic’s so-called <j>since they live a serene life. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp388:3'><span class='pli'>Pabbājayamattano malaṁ, </span> <span class='eng'>One who has renounced all stains </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp388:4'><span class='pli'>Tasmā “pabbajito”ti vuccati. </span> <span class='eng'>is said to be a “renunciant”. </span></span></span></p></blockquote></article>\n<article id='dhp389'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp389:0'><span class='pli'>Sāriputtattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp389:1'><span class='pli'>Na brāhmaṇassa pahareyya, </span> <span class='eng'>One should never strike a brahmin, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp389:2'><span class='pli'>nāssa muñcetha brāhmaṇo; </span> <span class='eng'>nor should a brahmin retaliate. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp389:3'><span class='pli'>Dhī brāhmaṇassa hantāraṁ, </span> <span class='eng'>Woe to the one who hurts a brahmin, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp389:4'><span class='pli'>tato dhī yassa muñcati. </span> <span class='eng'>and woe for the one who retaliates. </span></span></span></p></blockquote></article>\n<article id='dhp390'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp390:1'><span class='pli'>Na brāhmaṇassetadakiñci seyyo, </span> <span class='eng'>Nothing is better for a brahmin </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp390:2'><span class='pli'>Yadā nisedho manaso piyehi; </span> <span class='eng'>than to hold their mind back from attachment. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp390:3'><span class='pli'>Yato yato hiṁsamano nivattati, </span> <span class='eng'>From wherever a cruel wish recoils, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp390:4'><span class='pli'>Tato tato sammatimeva dukkhaṁ. </span> <span class='eng'>right there suffering subsides. </span></span></span></p></blockquote></article>\n<article id='dhp391'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp391:0'><span class='pli'>Mahāpajāpatigotamīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp391:1'><span class='pli'>Yassa kāyena vācāya, </span> <span class='eng'>Who does nothing wrong </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp391:2'><span class='pli'>manasā natthi dukkaṭaṁ; </span> <span class='eng'>by body, speech or mind, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp391:3'><span class='pli'>Saṁvutaṁ tīhi ṭhānehi, </span> <span class='eng'>restrained in these three respects, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp391:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp392'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp392:0'><span class='pli'>Sāriputtattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp392:1'><span class='pli'>Yamhā dhammaṁ vijāneyya, </span> <span class='eng'>You should graciously honor </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp392:2'><span class='pli'>sammāsambuddhadesitaṁ; </span> <span class='eng'>the one from whom you learn the Dhamma </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp392:3'><span class='pli'>Sakkaccaṁ taṁ namasseyya, </span> <span class='eng'>taught by the awakened Buddha, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp392:4'><span class='pli'>aggihuttaṁva brāhmaṇo. </span> <span class='eng'>as a brahmin honors the sacred flame. </span></span></span></p></blockquote></article>\n<article id='dhp393'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp393:0'><span class='pli'>Jaṭilabrāhmaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp393:1'><span class='pli'>Na jaṭāhi na gottena, </span> <span class='eng'>Not by matted hair or family, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp393:2'><span class='pli'>na jaccā hoti brāhmaṇo; </span> <span class='eng'>or birth is one a brahmin. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp393:3'><span class='pli'>Yamhi saccañca dhammo ca, </span> <span class='eng'>Those who are  truthful and principled: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp393:4'><span class='pli'>so sucī so ca brāhmaṇo. </span> <span class='eng'>they are pure, they are brahmins. </span></span></span></p></blockquote></article>\n<article id='dhp394'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp394:0'><span class='pli'>Kuhakabrāhmaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp394:1'><span class='pli'>Kiṁ te jaṭāhi dummedha, </span> <span class='eng'>Why the matted hair, you simpleton, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp394:2'><span class='pli'>kiṁ te ajinasāṭiyā; </span> <span class='eng'>and why the skin of deer? </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp394:3'><span class='pli'>Abbhantaraṁ te gahanaṁ, </span> <span class='eng'>The tangle is inside you, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp394:4'><span class='pli'>bāhiraṁ parimajjasi. </span> <span class='eng'>yet you polish up your outsides. </span></span></span></p></blockquote></article>\n<article id='dhp395'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp395:0'><span class='pli'>Kisāgotamīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp395:1'><span class='pli'>Paṁsukūladharaṁ jantuṁ, </span> <span class='eng'>A personage who wears robes of rags, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp395:2'><span class='pli'>kisaṁ dhamanisanthataṁ; </span> <span class='eng'>lean, their limbs showing veins, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp395:3'><span class='pli'>Ekaṁ vanasmiṁ jhāyantaṁ, </span> <span class='eng'>meditating alone in the forest, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp395:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp396'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp396:0'><span class='pli'>Ekabrāhmaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp396:1'><span class='pli'>Na cāhaṁ brāhmaṇaṁ brūmi, </span> <span class='eng'>I don’t call someone a brahmin </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp396:2'><span class='pli'>yonijaṁ mattisambhavaṁ; </span> <span class='eng'>after the mother’s womb they’re born from. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp396:3'><span class='pli'>Bhovādi nāma so hoti, </span> <span class='eng'>If they still have attachments, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp396:4'><span class='pli'>sace hoti sakiñcano; </span> <span class='eng'>they’re just someone who says “worthy”. </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp396:5'><span class='pli'>Akiñcanaṁ anādānaṁ, </span> <span class='eng'>Having nothing, taking nothing: </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp396:6'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp397'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp397:0'><span class='pli'>Uggasenaseṭṭhiputtavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp397:1'><span class='pli'>Sabbasaṁyojanaṁ chetvā, </span> <span class='eng'>Having cut off all fetters </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp397:2'><span class='pli'>yo ve na paritassati; </span> <span class='eng'>they have no anxiety. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp397:3'><span class='pli'>Saṅgātigaṁ visaṁyuttaṁ, </span> <span class='eng'>They’ve slipped their chains and are detached: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp397:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp398'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp398:0'><span class='pli'>Dvebrāhmaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp398:1'><span class='pli'>Chetvā naddhiṁ varattañca, </span> <span class='eng'>They’ve cut the strap and harness, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp398:2'><span class='pli'>sandānaṁ sahanukkamaṁ; </span> <span class='eng'>the halter and bridle too, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp398:3'><span class='pli'>Ukkhittapalighaṁ buddhaṁ, </span> <span class='eng'>with cross-bar lifted, they’re awakened: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp398:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp399'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp399:0'><span class='pli'>Akkosakabhāradvājavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp399:1'><span class='pli'>Akkosaṁ vadhabandhañca, </span> <span class='eng'>Abuse, killing, caging: </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp399:2'><span class='pli'>aduṭṭho yo titikkhati; </span> <span class='eng'>they withstand these without anger. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp399:3'><span class='pli'>Khantībalaṁ balānīkaṁ, </span> <span class='eng'>Patience is their powerful army: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp399:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp400'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp400:0'><span class='pli'>Sāriputtattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp400:1'><span class='pli'>Akkodhanaṁ vatavantaṁ, </span> <span class='eng'>Not irritable or pretentious, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp400:2'><span class='pli'>sīlavantaṁ anussadaṁ; </span> <span class='eng'>dutiful in precepts and observances, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp400:3'><span class='pli'>Dantaṁ antimasārīraṁ, </span> <span class='eng'>tamed, bearing their final body: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp400:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp401'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp401:0'><span class='pli'>Uppalavaṇṇāttherīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp401:1'><span class='pli'>Vāri pokkharapatteva, </span> <span class='eng'>Like water from a lotus leaf, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp401:2'><span class='pli'>āraggeriva sāsapo; </span> <span class='eng'>like a mustard seed off a pin-point, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp401:3'><span class='pli'>Yo na limpati kāmesu, </span> <span class='eng'>sensual pleasures slip off them: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp401:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp402'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp402:0'><span class='pli'>Aññatarabrāhmaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp402:1'><span class='pli'>Yo dukkhassa pajānāti, </span> <span class='eng'>They understand for themselves </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp402:2'><span class='pli'>idheva khayamattano; </span> <span class='eng'>the end of suffering in this life; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp402:3'><span class='pli'>Pannabhāraṁ visaṁyuttaṁ, </span> <span class='eng'>with burden put down, detached: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp402:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp403'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp403:0'><span class='pli'>Khemābhikkhunīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp403:1'><span class='pli'>Gambhīrapaññaṁ medhāviṁ, </span> <span class='eng'>Deep in wisdom, intelligent, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp403:2'><span class='pli'>maggāmaggassa kovidaṁ; </span> <span class='eng'>expert in the path and what is not the path; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp403:3'><span class='pli'>Uttamatthamanuppattaṁ, </span> <span class='eng'>arrived at the highest goal: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp403:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp404'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp404:0'><span class='pli'>Pabbhāravāsitissattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp404:1'><span class='pli'>Asaṁsaṭṭhaṁ gahaṭṭhehi, </span> <span class='eng'>Mixing with neither </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp404:2'><span class='pli'>anāgārehi cūbhayaṁ; </span> <span class='eng'>householders nor the homeless; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp404:3'><span class='pli'>Anokasārimappicchaṁ, </span> <span class='eng'>a migrant with no bastion, few in wishes: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp404:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp405'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp405:0'><span class='pli'>Aññatarabhikkhuvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp405:1'><span class='pli'>Nidhāya daṇḍaṁ bhūtesu, </span> <span class='eng'>They’ve laid aside violence </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp405:2'><span class='pli'>tasesu thāvaresu ca; </span> <span class='eng'>against creatures firm and frail; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp405:3'><span class='pli'>Yo na hanti na ghāteti, </span> <span class='eng'>not killing or making others kill: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp405:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp406'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp406:0'><span class='pli'>Sāmaṇerānaṁvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp406:1'><span class='pli'>Aviruddhaṁ viruddhesu, </span> <span class='eng'>Not fighting among those who fight, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp406:2'><span class='pli'>attadaṇḍesu nibbutaṁ; </span> <span class='eng'>quenched among those who have taken up arms, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp406:3'><span class='pli'>Sādānesu anādānaṁ, </span> <span class='eng'>not grasping among those who grasp: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp406:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp407'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp407:0'><span class='pli'>Mahāpanthakattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp407:1'><span class='pli'>Yassa rāgo ca doso ca, </span> <span class='eng'>They’ve discarded greed and hate, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp407:2'><span class='pli'>māno makkho ca pātito; </span> <span class='eng'>along with conceit and contempt, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp407:3'><span class='pli'>Sāsaporiva āraggā, </span> <span class='eng'>like a mustard seed off the point of a pin: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp407:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp408'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp408:0'><span class='pli'>Pilindavacchattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp408:1'><span class='pli'>Akakkasaṁ viññāpaniṁ, </span> <span class='eng'>The words they utter </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp408:2'><span class='pli'>giraṁ saccamudīraye; </span> <span class='eng'>are polished, informative, and true, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp408:3'><span class='pli'>Yāya nābhisaje kañci, </span> <span class='eng'>and don’t offend anyone: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp408:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp409'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp409:0'><span class='pli'>Aññatarattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp409:1'><span class='pli'>Yodha dīghaṁ va rassaṁ vā, </span> <span class='eng'>They don’t steal anything in the world, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp409:2'><span class='pli'>aṇuṁ thūlaṁ subhāsubhaṁ; </span> <span class='eng'>long or short, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp409:3'><span class='pli'>Loke adinnaṁ nādiyati, </span> <span class='eng'>fine or coarse, beautiful or ugly: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp409:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp410'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp410:0'><span class='pli'>Sāriputtattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp410:1'><span class='pli'>Āsā yassa na vijjanti, </span> <span class='eng'>They have no hope </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp410:2'><span class='pli'>asmiṁ loke paramhi ca; </span> <span class='eng'>in this world or the next; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp410:3'><span class='pli'>Nirāsāsaṁ visaṁyuttaṁ, </span> <span class='eng'>with no need for hope, detached: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp410:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp411'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp411:0'><span class='pli'>Mahāmoggallānattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp411:1'><span class='pli'>Yassālayā na vijjanti, </span> <span class='eng'>They have no clinging, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp411:2'><span class='pli'>aññāya akathaṅkathī; </span> <span class='eng'>knowledge has freed them of indecision, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp411:3'><span class='pli'>Amatogadhamanuppattaṁ, </span> <span class='eng'>they’ve arrived at the objective, freedom from death: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp411:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp412'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp412:0'><span class='pli'>Revatattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp412:1'><span class='pli'>Yodha puññañca pāpañca, </span> <span class='eng'>They’ve escaped clinging </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp412:2'><span class='pli'>ubho saṅgamupaccagā; </span> <span class='eng'>to both good and bad deeds; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp412:3'><span class='pli'>Asokaṁ virajaṁ suddhaṁ, </span> <span class='eng'>sorrowless, stainless, pure: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp412:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp413'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp413:0'><span class='pli'>Candābhattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp413:1'><span class='pli'>Candaṁva vimalaṁ suddhaṁ, </span> <span class='eng'>Pure as the spotless moon, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp413:2'><span class='pli'>vippasannamanāvilaṁ; </span> <span class='eng'>clear and undisturbed, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp413:3'><span class='pli'>Nandībhavaparikkhīṇaṁ, </span> <span class='eng'>they’ve ended relish for rebirth: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp413:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp414'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp414:0'><span class='pli'>Sīvalittheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp414:1'><span class='pli'>Yomaṁ palipathaṁ duggaṁ, </span> <span class='eng'>They’ve got past this grueling swamp </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp414:2'><span class='pli'>saṁsāraṁ mohamaccagā; </span> <span class='eng'>of delusion, transmigration. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp414:3'><span class='pli'>Tiṇṇo pāraṅgato jhāyī, </span> <span class='eng'>Meditating in stillness, free of indecision, </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp414:4'><span class='pli'>anejo akathaṅkathī; </span> <span class='eng'>they have crossed over to the far shore. </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp414:5'><span class='pli'>Anupādāya nibbuto, </span> <span class='eng'>They’re quenched by not grasping: </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp414:6'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp415'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp415:0'><span class='pli'>Sundarasamuddattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp415:1'><span class='pli'>Yodha kāme pahantvāna, </span> <span class='eng'>They’ve given up sensual stimulations, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp415:2'><span class='pli'>anāgāro paribbaje; </span> <span class='eng'>and have gone forth from lay life; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp415:3'><span class='pli'>Kāmabhavaparikkhīṇaṁ, </span> <span class='eng'>they’ve ended rebirth in the sensual realm: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp415:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp416'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp416:0'><span class='pli'>Jaṭilattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp416:1'><span class='pli'>Yodha taṇhaṁ pahantvāna, </span> <span class='eng'>They’ve given up craving, </span> <span class='comment-marker' data-comment='This verse and the next are identical. The commentary says they were spoken on two separate occasions. '>*</span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp416:2'><span class='pli'>anāgāro paribbaje; </span> <span class='eng'>and have gone forth from lay life; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp416:3'><span class='pli'>Taṇhābhavaparikkhīṇaṁ, </span> <span class='eng'>they’ve ended craving to be reborn: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp416:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote>\n<h2 class='sutta-title'><span class='segment' id='5.0' data-uid='dhp416:5.0'><span class='pli'>Jotikattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='5.1' data-uid='dhp416:5.1'><span class='pli'>Yodha taṇhaṁ pahantvāna, </span> <span class='eng'>They’ve given up craving, </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp416:6'><span class='pli'>anāgāro paribbaje; </span> <span class='eng'>and have gone forth from lay life; </span></span></span>\n<span class='verse-line'><span class='segment' id='7' data-uid='dhp416:7'><span class='pli'>Taṇhābhavaparikkhīṇaṁ, </span> <span class='eng'>they’ve ended craving to be reborn: </span></span></span>\n<span class='verse-line'><span class='segment' id='8' data-uid='dhp416:8'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp417'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp417:0'><span class='pli'>Naṭaputtakattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp417:1'><span class='pli'>Hitvā mānusakaṁ yogaṁ, </span> <span class='eng'>They’ve thrown off the human yoke, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp417:2'><span class='pli'>dibbaṁ yogaṁ upaccagā; </span> <span class='eng'>and slipped out of the heavenly yoke; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp417:3'><span class='pli'>Sabbayogavisaṁyuttaṁ, </span> <span class='eng'>unyoked from all yokes: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp417:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp418'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp418:0'><span class='pli'>Naṭaputtakattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp418:1'><span class='pli'>Hitvā ratiñca aratiñca, </span> <span class='eng'>Giving up desire and discontent, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp418:2'><span class='pli'>sītibhūtaṁ nirūpadhiṁ; </span> <span class='eng'>they’re cooled and free of attachments; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp418:3'><span class='pli'>Sabbalokābhibhuṁ vīraṁ, </span> <span class='eng'>a hero, master of the whole world: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp418:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp419'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp419:0'><span class='pli'>Vaṅgīsattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp419:1'><span class='pli'>Cutiṁ yo vedi sattānaṁ, </span> <span class='eng'>They know the passing away </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp419:2'><span class='pli'>upapattiñca sabbaso; </span> <span class='eng'>and rebirth of all beings; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp419:3'><span class='pli'>Asattaṁ sugataṁ buddhaṁ, </span> <span class='eng'>unattached, holy, awakened: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp419:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp420'><blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp420:1'><span class='pli'>Yassa gatiṁ na jānanti, </span> <span class='eng'>Gods, centaurs, and humans </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp420:2'><span class='pli'>devā gandhabbamānusā; </span> <span class='eng'>don’t know their destiny; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp420:3'><span class='pli'>Khīṇāsavaṁ arahantaṁ, </span> <span class='eng'>the perfected ones with defilements ended: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp420:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp421'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp421:0'><span class='pli'>Dhammadinnāttherīvatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp421:1'><span class='pli'>Yassa pure ca pacchā ca, </span> <span class='eng'>They have nothing before or after, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp421:2'><span class='pli'>majjhe ca natthi kiñcanaṁ; </span> <span class='eng'>or even in between. </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp421:3'><span class='pli'>Akiñcanaṁ anādānaṁ, </span> <span class='eng'>Having nothing, taking nothing: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp421:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp422'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp422:0'><span class='pli'>Aṅgulimālattheravatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp422:1'><span class='pli'>Usabhaṁ pavaraṁ vīraṁ, </span> <span class='eng'>Captain of the herd, excellent hero, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp422:2'><span class='pli'>mahesiṁ vijitāvinaṁ; </span> <span class='eng'>great seer and victor; </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp422:3'><span class='pli'>Anejaṁ nhātakaṁ buddhaṁ, </span> <span class='eng'>unstirred, washed, awakened: </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp422:4'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote></article>\n<article id='dhp423'><h2 class='sutta-title'><span class='segment' id='0' data-uid='dhp423:0'><span class='pli'>Devahitabrāhmaṇavatthu </span></span></h2>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1' data-uid='dhp423:1'><span class='pli'>Pubbenivāsaṁ yo vedi, </span> <span class='eng'>One who knows their past lives, </span></span></span>\n<span class='verse-line'><span class='segment' id='2' data-uid='dhp423:2'><span class='pli'>saggāpāyañca passati; </span> <span class='eng'>sees heaven and places of loss, </span></span></span>\n<span class='verse-line'><span class='segment' id='3' data-uid='dhp423:3'><span class='pli'>Atho jātikkhayaṁ patto, </span> <span class='eng'>and has attained the end of rebirth; </span></span></span>\n<span class='verse-line'><span class='segment' id='4' data-uid='dhp423:4'><span class='pli'>abhiññāvosito muni; </span> <span class='eng'>a sage of perfect insight </span></span></span>\n<span class='verse-line'><span class='segment' id='5' data-uid='dhp423:5'><span class='pli'>Sabbavositavosānaṁ, </span> <span class='eng'>at the summit of spiritual perfection: </span></span></span>\n<span class='verse-line'><span class='segment' id='6' data-uid='dhp423:6'><span class='pli'>tamahaṁ brūmi brāhmaṇaṁ. </span> <span class='eng'>that’s who I declare a brahmin. </span></span></span></p></blockquote>\n<p class='endvagga'><span class='segment' id='7' data-uid='dhp423:7'><span class='pli'>Brāhmaṇavaggo chabbīsatimo. </span></span></p>\n<div class='uddana'><p><span class='segment' id='8' data-uid='dhp423:8'><span class='pli'>Ettāvatā sabbapaṭhame yamakavagge cuddasa vatthūni, appamādavagge nava, cittavagge nava, pupphavagge dvādasa, bālavagge pannarasa, paṇḍitavagge ekādasa, arahantavagge dasa, sahassavagge cuddasa, pāpavagge dvādasa, daṇḍavagge ekādasa, jarāvagge nava, attavagge dasa, lokavagge ekādasa, buddhavagge nava, sukhavagge aṭṭha, piyavagge nava, kodhavagge aṭṭha, malavagge dvādasa, dhammaṭṭhavagge dasa, maggavagge dvādasa, pakiṇṇakavagge nava, nirayavagge nava, nāgavagge aṭṭha, taṇhāvagge dvādasa, bhikkhuvagge dvādasa, brāhmaṇavagge cattālīsāti pañcādhikāni tīṇi vatthusatāni. </span></span></p>\n<blockquote class='uddanagatha'><p><span class='verse-line'><span class='segment' id='9' data-uid='dhp423:9'><span class='pli'>Satevīsacatussatā, </span></span></span>\n<span class='verse-line'><span class='segment' id='10' data-uid='dhp423:10'><span class='pli'>catusaccavibhāvinā; </span></span></span>\n<span class='verse-line'><span class='segment' id='11' data-uid='dhp423:11'><span class='pli'>Satattayañca vatthūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12' data-uid='dhp423:12'><span class='pli'>pañcādhikaṁ samuṭṭhitāti. </span></span></span></p>\n<p class='uddana-intro'><span class='segment' id='13' data-uid='dhp423:13'><span class='pli'>Dhammapade vaggānamuddānaṁ </span></span></p></blockquote>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='14' data-uid='dhp423:14'><span class='pli'>Yamakappamādo cittaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15' data-uid='dhp423:15'><span class='pli'>pupphaṁ bālena paṇḍito; </span></span></span>\n<span class='verse-line'><span class='segment' id='16' data-uid='dhp423:16'><span class='pli'>Arahanto sahassañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='17' data-uid='dhp423:17'><span class='pli'>pāpaṁ daṇḍena te dasa. </span></span></span></p></blockquote>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='18' data-uid='dhp423:18'><span class='pli'>Jarā attā ca loko ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='19' data-uid='dhp423:19'><span class='pli'>buddho sukhaṁ piyena ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='20' data-uid='dhp423:20'><span class='pli'>Kodho malañca dhammaṭṭho, </span></span></span>\n<span class='verse-line'><span class='segment' id='21' data-uid='dhp423:21'><span class='pli'>maggavaggena vīsati. </span></span></span></p></blockquote>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='22' data-uid='dhp423:22'><span class='pli'>Pakiṇṇaṁ nirayo nāgo, </span></span></span>\n<span class='verse-line'><span class='segment' id='23' data-uid='dhp423:23'><span class='pli'>taṇhā bhikkhu ca brāhmaṇo; </span></span></span>\n<span class='verse-line'><span class='segment' id='24' data-uid='dhp423:24'><span class='pli'>Ete chabbīsati vaggā, </span></span></span>\n<span class='verse-line'><span class='segment' id='25' data-uid='dhp423:25'><span class='pli'>desitādiccabandhunā. </span></span></span></p>\n<p class='uddana-intro'><span class='segment' id='26' data-uid='dhp423:26'><span class='pli'>Gāthānamuddānaṁ </span></span></p></blockquote>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='27' data-uid='dhp423:27'><span class='pli'>Yamake vīsati gāthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='28' data-uid='dhp423:28'><span class='pli'>appamādamhi dvādasa; </span></span></span>\n<span class='verse-line'><span class='segment' id='29' data-uid='dhp423:29'><span class='pli'>Ekādasa cittavagge, </span></span></span>\n<span class='verse-line'><span class='segment' id='30' data-uid='dhp423:30'><span class='pli'>pupphavaggamhi soḷasa. </span></span></span></p></blockquote>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='31' data-uid='dhp423:31'><span class='pli'>Bāle ca soḷasa gāthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='32' data-uid='dhp423:32'><span class='pli'>paṇḍitamhi catuddasa; </span></span></span>\n<span class='verse-line'><span class='segment' id='33' data-uid='dhp423:33'><span class='pli'>Arahante dasa gāthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='34' data-uid='dhp423:34'><span class='pli'>sahasse honti soḷasa. </span></span></span></p></blockquote>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='35' data-uid='dhp423:35'><span class='pli'>Terasa pāpavaggamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='36' data-uid='dhp423:36'><span class='pli'>daṇḍamhi dasa satta ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='37' data-uid='dhp423:37'><span class='pli'>Ekādasa jarā vagge, </span></span></span>\n<span class='verse-line'><span class='segment' id='38' data-uid='dhp423:38'><span class='pli'>attavaggamhi tā dasa. </span></span></span></p></blockquote>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='39' data-uid='dhp423:39'><span class='pli'>Dvādasa lokavaggamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='40' data-uid='dhp423:40'><span class='pli'>buddhavaggamhi ṭhārasa; </span></span></span>\n<span class='verse-line'><span class='segment' id='41' data-uid='dhp423:41'><span class='pli'>Sukhe ca piyavagge ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='42' data-uid='dhp423:42'><span class='pli'>gāthāyo honti dvādasa. </span></span></span></p></blockquote>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='43' data-uid='dhp423:43'><span class='pli'>Cuddasa kodhavaggamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='44' data-uid='dhp423:44'><span class='pli'>malavaggekavīsati; </span></span></span>\n<span class='verse-line'><span class='segment' id='45' data-uid='dhp423:45'><span class='pli'>Sattarasa ca dhammaṭṭhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='46' data-uid='dhp423:46'><span class='pli'>maggavagge sattarasa. </span></span></span></p></blockquote>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='47' data-uid='dhp423:47'><span class='pli'>Pakiṇṇe soḷasa gāthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='48' data-uid='dhp423:48'><span class='pli'>niraye nāge ca cuddasa; </span></span></span>\n<span class='verse-line'><span class='segment' id='49' data-uid='dhp423:49'><span class='pli'>Chabbīsa taṇhāvaggamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='50' data-uid='dhp423:50'><span class='pli'>tevīsa bhikkhuvaggikā. </span></span></span></p></blockquote>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='51' data-uid='dhp423:51'><span class='pli'>Ekatālīsagāthāyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='52' data-uid='dhp423:52'><span class='pli'>brāhmaṇe vaggamuttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='53' data-uid='dhp423:53'><span class='pli'>Gāthāsatāni cattāri, </span></span></span>\n<span class='verse-line'><span class='segment' id='54' data-uid='dhp423:54'><span class='pli'>tevīsa ca punāpare; </span></span></span>\n<span class='verse-line'><span class='segment' id='55' data-uid='dhp423:55'><span class='pli'>Dhammapade nipātamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='56' data-uid='dhp423:56'><span class='pli'>desitādiccabandhunāti. </span></span></span></p></blockquote></div>\n<p class='endbook'><span class='segment' id='57' data-uid='dhp423:57'><span class='pli'>Dhammapadapāḷi samattā. </span> <span class='eng'>The Sayings of the Dhamma are completed. </span></span></p></article></section>\n",
    "acronym": "Dhp 383–423",
    "translated_title": "26. Brahmins",
    "original_title": "Brāhmaṇavagga"
  }
});
