// Source: kn/kp
window.SUTTA_DB = window.SUTTA_DB || {};
Object.assign(window.SUTTA_DB, {
  "kp1": {
    "previous": null,
    "next": "kp2",
    "content": "<article id='kp1'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='kp1:0.1'><span class='pli'>Khuddakapāṭha 1 </span> <span class='eng'>Basic Passages 1 </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='kp1:0.2'><span class='pli'>Saraṇattaya </span> <span class='eng'>The Three Refuges </span></span></h1></header>\n<p class='namo'><span class='segment' id='1.1' data-uid='kp1:1.1'><span class='pli'>Namo tassa Bhagavato Arahato Sammāsambuddhassa. </span> <span class='eng'>Homage to him, the blessed one, the perfected one, the fully awakened Buddha! </span></span></p>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='2.1' data-uid='kp1:2.1'><span class='pli'>Buddhaṁ saraṇaṁ gacchāmi, </span> <span class='eng'>I take refuge in the Buddha, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='kp1:2.2'><span class='pli'>Dhammaṁ saraṇaṁ gacchāmi, </span> <span class='eng'>I take refuge in the Teaching, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='kp1:2.3'><span class='pli'>Saṅghaṁ saraṇaṁ gacchāmi. </span> <span class='eng'>I take refuge in the Sangha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='kp1:3.1'><span class='pli'>Dutiyampi buddhaṁ saraṇaṁ gacchāmi, </span> <span class='eng'>For the second time I take refuge in the Buddha, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='kp1:3.2'><span class='pli'>Dutiyampi dhammaṁ saraṇaṁ gacchāmi, </span> <span class='eng'>for the second time I take refuge in the Teaching, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='kp1:3.3'><span class='pli'>Dutiyampi saṅghaṁ saraṇaṁ gacchāmi. </span> <span class='eng'>for the second time I take refuge in the Sangha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='kp1:4.1'><span class='pli'>Tatiyampi buddhaṁ saraṇaṁ gacchāmi, </span> <span class='eng'>For the third time I take refuge in the Buddha, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='kp1:4.2'><span class='pli'>Tatiyampi dhammaṁ saraṇaṁ gacchāmi, </span> <span class='eng'>for the third time I take refuge in the Teaching, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='kp1:4.3'><span class='pli'>Tatiyampi saṅghaṁ saraṇaṁ gacchāmi. </span> <span class='eng'>for the third time I take refuge in the Sangha. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='5.1' data-uid='kp1:5.1'><span class='pli'>Saraṇattayaṁ. </span></span></p></article>\n"
  },
  "kp2": {
    "previous": "kp1",
    "next": "kp3",
    "content": "<article id='kp2'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='kp2:0.1'><span class='pli'>Khuddakapāṭha 2 </span> <span class='eng'>Basic Passages 2 </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='kp2:0.2'><span class='pli'>Dasasikkhāpada </span> <span class='eng'>The Ten Precepts </span></span></h1></header>\n<p><span class='segment' id='1.1' data-uid='kp2:1.1'><span class='pli'>Pāṇātipātā veramaṇisikkhāpadaṁ samādiyāmi. </span> <span class='eng'>I undertake the precept to refrain from killing living creatures. </span></span></p>\n<p><span class='segment' id='2.1' data-uid='kp2:2.1'><span class='pli'>Adinnādānā veramaṇisikkhāpadaṁ samādiyāmi. </span> <span class='eng'>I undertake the precept to refrain from stealing. </span></span></p>\n<p><span class='segment' id='3.1' data-uid='kp2:3.1'><span class='pli'>Abrahmacariyā veramaṇisikkhāpadaṁ samādiyāmi. </span> <span class='eng'>I undertake the precept to refrain from sexual activity. </span></span></p>\n<p><span class='segment' id='4.1' data-uid='kp2:4.1'><span class='pli'>Musāvādā veramaṇisikkhāpadaṁ samādiyāmi. </span> <span class='eng'>I undertake the precept to refrain from lying. </span></span></p>\n<p><span class='segment' id='5.1' data-uid='kp2:5.1'><span class='pli'>Surāmerayamajjapamādaṭṭhānā veramaṇisikkhāpadaṁ samādiyāmi. </span> <span class='eng'>I undertake the precept to refrain from consuming beer, wine, and liquor intoxicants. </span></span></p>\n<p><span class='segment' id='6.1' data-uid='kp2:6.1'><span class='pli'>Vikālabhojanā veramaṇisikkhāpadaṁ samādiyāmi. </span> <span class='eng'>I undertake the precept to refrain from food at the wrong time. </span></span></p>\n<p><span class='segment' id='7.1' data-uid='kp2:7.1'><span class='pli'>Naccagītavāditavisūkadassanā veramaṇisikkhāpadaṁ samādiyāmi. </span> <span class='eng'>I undertake the precept to refrain from seeing shows of dancing, singing, and music . </span></span></p>\n<p><span class='segment' id='8.1' data-uid='kp2:8.1'><span class='pli'>Mālāgandhavilepanadhāraṇamaṇḍanavibhūsanaṭṭhānā veramaṇisikkhāpadaṁ samādiyāmi. </span> <span class='eng'>I undertake the precept to refrain from attiring and adorning myself with garlands, fragrance, and makeup. </span></span></p>\n<p><span class='segment' id='9.1' data-uid='kp2:9.1'><span class='pli'>Uccāsayanamahāsayanā veramaṇisikkhāpadaṁ samādiyāmi. </span> <span class='eng'>I undertake the precept to refrain from high and luxurious beds. </span></span></p>\n<p><span class='segment' id='10.1' data-uid='kp2:10.1'><span class='pli'>Jātarūparajatapaṭiggahaṇā veramaṇisikkhāpadaṁ samādiyāmi. </span> <span class='eng'>I undertake the precept to refrain from receiving gold and currency. </span></span></p>\n<p class='endsutta'><span class='segment' id='11.1' data-uid='kp2:11.1'><span class='pli'>Dasasikkhāpadaṁ. </span></span></p></article>\n"
  },
  "kp3": {
    "previous": "kp2",
    "next": "kp4",
    "content": "<article id='kp3'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='kp3:0.1'><span class='pli'>Khuddakapāṭha 3 </span> <span class='eng'>Basic Passages 3 </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='kp3:0.2'><span class='pli'>Dvattiṁsākāra </span> <span class='eng'>The Thirty-Two Parts of the Body </span></span></h1></header>\n<p><span class='segment' id='1.1' data-uid='kp3:1.1'><span class='pli'>Atthi imasmiṁ kāye—Kesā lomā nakhā dantā taco, maṁsaṁ nhāru aṭṭhi aṭṭhimiñjaṁ vakkaṁ, hadayaṁ yakanaṁ kilomakaṁ pihakaṁ papphāsaṁ, antaṁ antaguṇaṁ udariyaṁ karīsaṁ matthaluṅgaṁ, pittaṁ semhaṁ pubbo lohitaṁ sedo medo, assu vasā kheḷo siṅghāṇikā lasikā muttanti. </span> <span class='eng'>“In this body there is head hair, body hair, nails, teeth, skin, flesh, sinews, bones, bone marrow, kidneys, heart, liver, diaphragm, spleen, lungs, intestines, mesentery, undigested food, feces, brain, bile, phlegm, pus, blood, sweat, fat, tears, grease, saliva, snot, synovial fluid, urine.” </span></span></p>\n<p class='endsutta'><span class='segment' id='2.1' data-uid='kp3:2.1'><span class='pli'>Dvattiṁsākāraṁ. </span></span></p></article>\n"
  },
  "kp4": {
    "previous": "kp3",
    "next": "kp5",
    "content": "<article id='kp4'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='kp4:0.1'><span class='pli'>Khuddakapāṭha 4 </span> <span class='eng'>Basic Passages 4 </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='kp4:0.2'><span class='pli'>Kumārapañhā </span> <span class='eng'>The Boy’s Questions </span></span></h1></header>\n<p><span class='segment' id='1.1' data-uid='kp4:1.1'><span class='pli'>Ekaṁ nāma kiṁ? </span> <span class='eng'>What is the one? </span></span>\n<span class='segment' id='1.2' data-uid='kp4:1.2'><span class='pli'>Sabbe sattā āhāraṭṭhitikā. </span> <span class='eng'>All sentient beings are sustained by food. </span></span></p>\n<p><span class='segment' id='2.1' data-uid='kp4:2.1'><span class='pli'>Dve nāma kiṁ? </span> <span class='eng'>What is the two? </span></span>\n<span class='segment' id='2.2' data-uid='kp4:2.2'><span class='pli'>Nāmañca rūpañca. </span> <span class='eng'>Name and form. </span></span></p>\n<p><span class='segment' id='3.1' data-uid='kp4:3.1'><span class='pli'>Tīṇi nāma kiṁ? </span> <span class='eng'>What is the three? </span></span>\n<span class='segment' id='3.2' data-uid='kp4:3.2'><span class='pli'>Tisso vedanā. </span> <span class='eng'>Three feelings. </span></span></p>\n<p><span class='segment' id='4.1' data-uid='kp4:4.1'><span class='pli'>Cattāri nāma kiṁ? </span> <span class='eng'>What is the four? </span></span>\n<span class='segment' id='4.2' data-uid='kp4:4.2'><span class='pli'>Cattāri ariyasaccāni. </span> <span class='eng'>Four noble truths. </span></span></p>\n<p><span class='segment' id='5.1' data-uid='kp4:5.1'><span class='pli'>Pañca nāma kiṁ? </span> <span class='eng'>What is the five? </span></span>\n<span class='segment' id='5.2' data-uid='kp4:5.2'><span class='pli'>Pañcupādānakkhandhā. </span> <span class='eng'>Five grasping aggregates. </span></span></p>\n<p><span class='segment' id='6.1' data-uid='kp4:6.1'><span class='pli'>Cha nāma kiṁ? </span> <span class='eng'>What is the six? </span></span>\n<span class='segment' id='6.2' data-uid='kp4:6.2'><span class='pli'>Cha ajjhattikāni āyatanāni. </span> <span class='eng'>Six interior sense fields. </span></span></p>\n<p><span class='segment' id='7.1' data-uid='kp4:7.1'><span class='pli'>Satta nāma kiṁ? </span> <span class='eng'>What is the seven? </span></span>\n<span class='segment' id='7.2' data-uid='kp4:7.2'><span class='pli'>Satta bojjhaṅgā. </span> <span class='eng'>Seven awakening factors. </span></span></p>\n<p><span class='segment' id='8.1' data-uid='kp4:8.1'><span class='pli'>Aṭṭha nāma kiṁ? </span> <span class='eng'>What is the eight? </span></span>\n<span class='segment' id='8.2' data-uid='kp4:8.2'><span class='pli'>Ariyo aṭṭhaṅgiko maggo. </span> <span class='eng'>The noble eightfold path. </span></span></p>\n<p><span class='segment' id='9.1' data-uid='kp4:9.1'><span class='pli'>Nava nāma kiṁ? </span> <span class='eng'>What is the nine? </span></span>\n<span class='segment' id='9.2' data-uid='kp4:9.2'><span class='pli'>Nava sattāvāsā. </span> <span class='eng'>Nine abodes of sentient beings. </span></span></p>\n<p><span class='segment' id='10.1' data-uid='kp4:10.1'><span class='pli'>Dasa nāma kiṁ? </span> <span class='eng'>What is the ten? </span></span>\n<span class='segment' id='10.2' data-uid='kp4:10.2'><span class='pli'>Dasahaṅgehi samannāgato “arahā”ti vuccatīti. </span> <span class='eng'>One endowed with ten factors is called “perfected”. </span></span></p>\n<p class='endsutta'><span class='segment' id='11.1' data-uid='kp4:11.1'><span class='pli'>Kumārapañhā. </span></span></p></article>\n"
  },
  "kp5": {
    "previous": "kp4",
    "next": "kp6",
    "content": "<article id='kp5'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='kp5:0.1'><span class='pli'>Khuddakapāṭha 5 </span> <span class='eng'>Basic Passages 5 </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='kp5:0.2'><span class='pli'>Maṅgalasutta </span> <span class='eng'>Blessings </span></span></h1></header>\n<p><span class='segment' id='1.1' data-uid='kp5:1.1'><span class='pli'>Evaṁ me sutaṁ—</span> <span class='eng'>So I have heard. </span></span>\n<span class='segment' id='1.2' data-uid='kp5:1.2'><span class='pli'>ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. </span> <span class='eng'>At one time the Buddha was staying near Sāvatthī in Jeta’s Grove, Anāthapiṇḍika’s monastery. </span></span>\n<span class='segment' id='1.3' data-uid='kp5:1.3'><span class='pli'>Atha kho aññatarā devatā abhikkantāya rattiyā abhikkantavaṇṇā kevalakappaṁ jetavanaṁ obhāsetvā yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṁ abhivādetvā ekamantaṁ aṭṭhāsi. </span> <span class='eng'>Then, late at night, a glorious deity, lighting up the entire Jeta’s Grove, went up to the Buddha, bowed, and stood to one side. </span></span>\n<span class='segment' id='1.4' data-uid='kp5:1.4'><span class='pli'>Ekamantaṁ ṭhitā kho sā devatā bhagavantaṁ gāthāya ajjhabhāsi—</span> <span class='eng'>That deity addressed the Buddha in verse: </span></span></p>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='2.1' data-uid='kp5:2.1'><span class='pli'>“Bahū devā manussā ca, </span> <span class='eng'>“Many gods and humans </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='kp5:2.2'><span class='pli'>maṅgalāni acintayuṁ; </span> <span class='eng'>have thought about blessings </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='kp5:2.3'><span class='pli'>Ākaṅkhamānā sotthānaṁ, </span> <span class='eng'>desiring well-being: </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='kp5:2.4'><span class='pli'>brūhi maṅgalamuttamaṁ”. </span> <span class='eng'>declare the highest blessing.” </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='kp5:3.1'><span class='pli'>“Asevanā ca bālānaṁ, </span> <span class='eng'>“Not to fraternize with fools, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='kp5:3.2'><span class='pli'>paṇḍitānañca sevanā; </span> <span class='eng'>but to fraternize with the wise, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='kp5:3.3'><span class='pli'>Pūjā ca pūjaneyyānaṁ, </span> <span class='eng'>and honoring those worthy of honor: </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='kp5:3.4'><span class='pli'>etaṁ maṅgalamuttamaṁ. </span> <span class='eng'>this is the highest blessing. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='kp5:4.1'><span class='pli'>Patirūpadesavāso ca, </span> <span class='eng'>Living in a suitable region, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='kp5:4.2'><span class='pli'>pubbe ca katapuññatā; </span> <span class='eng'>having made merit in the past, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='kp5:4.3'><span class='pli'>Attasammāpaṇidhi ca, </span> <span class='eng'>being rightly resolved in oneself, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='kp5:4.4'><span class='pli'>etaṁ maṅgalamuttamaṁ. </span> <span class='eng'>this is the highest blessing. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='kp5:5.1'><span class='pli'>Bāhusaccañca sippañca, </span> <span class='eng'>Education and a craft, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='kp5:5.2'><span class='pli'>vinayo ca susikkhito; </span> <span class='eng'>discipline and training, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='kp5:5.3'><span class='pli'>Subhāsitā ca yā vācā, </span> <span class='eng'>and well-spoken speech: </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='kp5:5.4'><span class='pli'>etaṁ maṅgalamuttamaṁ. </span> <span class='eng'>this is the highest blessing. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='kp5:6.1'><span class='pli'>Mātāpituupaṭṭhānaṁ, </span> <span class='eng'>Caring for mother and father, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='kp5:6.2'><span class='pli'>puttadārassa saṅgaho; </span> <span class='eng'>kindness to children and partners, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='kp5:6.3'><span class='pli'>Anākulā ca kammantā, </span> <span class='eng'>and unstressful work: </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='kp5:6.4'><span class='pli'>etaṁ maṅgalamuttamaṁ. </span> <span class='eng'>this is the highest blessing. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='kp5:7.1'><span class='pli'>Dānañca dhammacariyā ca, </span> <span class='eng'>Giving and righteous conduct, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='kp5:7.2'><span class='pli'>ñātakānañca saṅgaho; </span> <span class='eng'>kindness to relatives, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='kp5:7.3'><span class='pli'>Anavajjāni kammāni, </span> <span class='eng'>blameless deeds: </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='kp5:7.4'><span class='pli'>etaṁ maṅgalamuttamaṁ. </span> <span class='eng'>this is the highest blessing. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='kp5:8.1'><span class='pli'>Āratī viratī pāpā, </span> <span class='eng'>Desisting and abstaining from evil, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='kp5:8.2'><span class='pli'>majjapānā ca saṁyamo; </span> <span class='eng'>avoiding drinking liquor, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='kp5:8.3'><span class='pli'>Appamādo ca dhammesu, </span> <span class='eng'>diligence in good qualities: </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='kp5:8.4'><span class='pli'>etaṁ maṅgalamuttamaṁ. </span> <span class='eng'>this is the highest blessing. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='kp5:9.1'><span class='pli'>Gāravo ca nivāto ca, </span> <span class='eng'>Respect and humility, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='kp5:9.2'><span class='pli'>santuṭṭhi ca kataññutā; </span> <span class='eng'>contentment and gratitude, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='kp5:9.3'><span class='pli'>Kālena dhammassavanaṁ, </span> <span class='eng'>and timely listening to the teaching: </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='kp5:9.4'><span class='pli'>etaṁ maṅgalamuttamaṁ. </span> <span class='eng'>this is the highest blessing. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='kp5:10.1'><span class='pli'>Khantī ca sovacassatā, </span> <span class='eng'>Patience, being easy to admonish, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='kp5:10.2'><span class='pli'>samaṇānañca dassanaṁ; </span> <span class='eng'>the sight of ascetics, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='kp5:10.3'><span class='pli'>Kālena dhammasākacchā, </span> <span class='eng'>and timely discussion of the teaching: </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='kp5:10.4'><span class='pli'>etaṁ maṅgalamuttamaṁ. </span> <span class='eng'>this is the highest blessing. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='kp5:11.1'><span class='pli'>Tapo ca brahmacariyañca, </span> <span class='eng'>Fervor and chastity </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='kp5:11.2'><span class='pli'>ariyasaccāna dassanaṁ; </span> <span class='eng'>seeing the noble truths, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='kp5:11.3'><span class='pli'>Nibbānasacchikiriyā ca, </span> <span class='eng'>and realization of extinguishment: </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='kp5:11.4'><span class='pli'>etaṁ maṅgalamuttamaṁ. </span> <span class='eng'>this is the highest blessing. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='kp5:12.1'><span class='pli'>Phuṭṭhassa lokadhammehi, </span> <span class='eng'>Though touched by worldly conditions, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='kp5:12.2'><span class='pli'>cittaṁ yassa na kampati; </span> <span class='eng'>their mind does not tremble; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='kp5:12.3'><span class='pli'>Asokaṁ virajaṁ khemaṁ, </span> <span class='eng'>sorrowless, stainless, secure: </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='kp5:12.4'><span class='pli'>etaṁ maṅgalamuttamaṁ. </span> <span class='eng'>this is the highest blessing. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='kp5:13.1'><span class='pli'>Etādisāni katvāna, </span> <span class='eng'>Having completed these things, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='kp5:13.2'><span class='pli'>Sabbattha maparājitā; </span> <span class='eng'>undefeated everywhere; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='kp5:13.3'><span class='pli'>Sabbattha sotthiṁ gacchanti, </span> <span class='eng'>everywhere they go in safety: </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='kp5:13.4'><span class='pli'>Taṁ tesaṁ maṅgalamuttaman”ti. </span> <span class='eng'>this is their highest blessing.” </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='14.1' data-uid='kp5:14.1'><span class='pli'>Maṅgalasuttaṁ. </span></span></p></article>\n"
  },
  "kp6": {
    "previous": "kp5",
    "next": "kp7",
    "content": "<article id='kp6'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='kp6:0.1'><span class='pli'>Khuddakapāṭha 6 </span> <span class='eng'>Basic Passages 6 </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='kp6:0.2'><span class='pli'>Ratanasutta </span> <span class='eng'>Gems </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='kp6:1.1'><span class='pli'>Yānīdha bhūtāni samāgatāni, </span> <span class='eng'>Whatever beings have gathered here, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='kp6:1.2'><span class='pli'>Bhummāni vā yāni va antalikkhe; </span> <span class='eng'>on the ground or in the sky, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='kp6:1.3'><span class='pli'>Sabbeva bhūtā sumanā bhavantu, </span> <span class='eng'>may beings all be of happy heart, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='kp6:1.4'><span class='pli'>Athopi sakkacca suṇantu bhāsitaṁ. </span> <span class='eng'>and listen carefully to what is said. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='kp6:2.1'><span class='pli'>Tasmā hi bhūtā nisāmetha sabbe, </span> <span class='eng'>So pay heed, all you beings, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='kp6:2.2'><span class='pli'>Mettaṁ karotha mānusiyā pajāya; </span> <span class='eng'>have love for humankind, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='kp6:2.3'><span class='pli'>Divā ca ratto ca haranti ye baliṁ, </span> <span class='eng'>who day and night bring offerings; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='kp6:2.4'><span class='pli'>Tasmā hi ne rakkhatha appamattā. </span> <span class='eng'>please protect them diligently. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='kp6:3.1'><span class='pli'>Yaṁ kiñci vittaṁ idha vā huraṁ vā, </span> <span class='eng'>There’s no wealth this life or the next, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='kp6:3.2'><span class='pli'>Saggesu vā yaṁ ratanaṁ paṇītaṁ; </span> <span class='eng'>no sublime gem in the heavens, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='kp6:3.3'><span class='pli'>Na no samaṁ atthi tathāgatena, </span> <span class='eng'>that equals the Realized One. </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='kp6:3.4'><span class='pli'>Idampi buddhe ratanaṁ paṇītaṁ; </span> <span class='eng'>This sublime gem is in the Buddha: </span></span></span>\n<span class='verse-line'><span class='segment' id='3.5' data-uid='kp6:3.5'><span class='pli'>Etena saccena suvatthi hotu. </span> <span class='eng'>by this truth, may you be well! </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='kp6:4.1'><span class='pli'>Khayaṁ virāgaṁ amataṁ paṇītaṁ, </span> <span class='eng'>Ending, dispassion, the undying, the sublime, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='kp6:4.2'><span class='pli'>Yadajjhagā sakyamunī samāhito; </span> <span class='eng'>attained by the Sakyan Sage immersed in samādhi; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='kp6:4.3'><span class='pli'>Na tena dhammena samatthi kiñci, </span> <span class='eng'>there is nothing equal to that Dhamma. </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='kp6:4.4'><span class='pli'>Idampi dhamme ratanaṁ paṇītaṁ; </span> <span class='eng'>This sublime gem is in the Dhamma: </span></span></span>\n<span class='verse-line'><span class='segment' id='4.5' data-uid='kp6:4.5'><span class='pli'>Etena saccena suvatthi hotu. </span> <span class='eng'>by this truth, may you be well! </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='kp6:5.1'><span class='pli'>Yaṁ buddhaseṭṭho parivaṇṇayī suciṁ, </span> <span class='eng'>The purity praised by the highest Buddha, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='kp6:5.2'><span class='pli'>Samādhimānantarikaññamāhu; </span> <span class='eng'>is said to be the “immersion with immediate fruit”; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='kp6:5.3'><span class='pli'>Samādhinā tena samo na vijjati, </span> <span class='eng'>no equal to that immersion is found. </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='kp6:5.4'><span class='pli'>Idampi dhamme ratanaṁ paṇītaṁ; </span> <span class='eng'>This sublime gem is in the Dhamma: </span></span></span>\n<span class='verse-line'><span class='segment' id='5.5' data-uid='kp6:5.5'><span class='pli'>Etena saccena suvatthi hotu. </span> <span class='eng'>by this truth, may you be well! </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='kp6:6.1'><span class='pli'>Ye puggalā aṭṭha sataṁ pasatthā, </span> <span class='eng'>The eight individual persons praised by the good </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='kp6:6.2'><span class='pli'>Cattāri etāni yugāni honti; </span> <span class='eng'>are the four pairs of the Holy One’s disciples; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='kp6:6.3'><span class='pli'>Te dakkhiṇeyyā sugatassa sāvakā, </span> <span class='eng'>they are worthy of religious donations, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='kp6:6.4'><span class='pli'>Etesu dinnāni mahapphalāni; </span> <span class='eng'>what’s given to them is very fruitful. </span></span></span>\n<span class='verse-line'><span class='segment' id='6.5' data-uid='kp6:6.5'><span class='pli'>Idampi saṅghe ratanaṁ paṇītaṁ, </span> <span class='eng'>This sublime gem is in the Saṅgha: </span></span></span>\n<span class='verse-line'><span class='segment' id='6.6' data-uid='kp6:6.6'><span class='pli'>Etena saccena suvatthi hotu. </span> <span class='eng'>by this truth, may you be well! </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='kp6:7.1'><span class='pli'>Ye suppayuttā manasā daḷhena, </span> <span class='eng'>Dedicated to Gotama’s dispensation, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='kp6:7.2'><span class='pli'>Nikkāmino gotamasāsanamhi; </span> <span class='eng'>strong-minded, free of sense desire, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='kp6:7.3'><span class='pli'>Te pattipattā amataṁ vigayha, </span> <span class='eng'>they’ve attained the goal, plunged into freedom from death, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='kp6:7.4'><span class='pli'>Laddhā mudhā nibbutiṁ bhuñjamānā; </span> <span class='eng'>and enjoy the quenching they’ve freely gained. </span></span></span>\n<span class='verse-line'><span class='segment' id='7.5' data-uid='kp6:7.5'><span class='pli'>Idampi saṅghe ratanaṁ paṇītaṁ, </span> <span class='eng'>This sublime gem is in the Saṅgha: </span></span></span>\n<span class='verse-line'><span class='segment' id='7.6' data-uid='kp6:7.6'><span class='pli'>Etena saccena suvatthi hotu. </span> <span class='eng'>by this truth, may you be well! </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='kp6:8.1'><span class='pli'>Yathindakhīlo pathavissito siyā, </span> <span class='eng'>As Indra’s pillar rooted in earth </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='kp6:8.2'><span class='pli'>Catubbhi vātehi asampakampiyo; </span> <span class='eng'>is not shaken by the four winds, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='kp6:8.3'><span class='pli'>Tathūpamaṁ sappurisaṁ vadāmi, </span> <span class='eng'>I say a true person is like this, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='kp6:8.4'><span class='pli'>Yo ariyasaccāni avecca passati; </span> <span class='eng'>who sees the noble truths in experience. </span></span></span>\n<span class='verse-line'><span class='segment' id='8.5' data-uid='kp6:8.5'><span class='pli'>Idampi saṅghe ratanaṁ paṇītaṁ, </span> <span class='eng'>This sublime gem is in the Saṅgha: </span></span></span>\n<span class='verse-line'><span class='segment' id='8.6' data-uid='kp6:8.6'><span class='pli'>Etena saccena suvatthi hotu. </span> <span class='eng'>by this truth, may you be well! </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='kp6:9.1'><span class='pli'>Ye ariyasaccāni vibhāvayanti, </span> <span class='eng'>Those who fathom the noble truths </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='kp6:9.2'><span class='pli'>Gambhīrapaññena sudesitāni; </span> <span class='eng'>taught by the one of deep wisdom, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='kp6:9.3'><span class='pli'>Kiñcāpi te honti bhusaṁ pamattā, </span> <span class='eng'>do not take an eighth life, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='kp6:9.4'><span class='pli'>Na te bhavaṁ aṭṭhamamādiyanti; </span> <span class='eng'>even if they are hugely negligent. </span></span></span>\n<span class='verse-line'><span class='segment' id='9.5' data-uid='kp6:9.5'><span class='pli'>Idampi saṅghe ratanaṁ paṇītaṁ, </span> <span class='eng'>This sublime gem is in the Saṅgha: </span></span></span>\n<span class='verse-line'><span class='segment' id='9.6' data-uid='kp6:9.6'><span class='pli'>Etena saccena suvatthi hotu. </span> <span class='eng'>by this truth, may you be well! </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='kp6:10.1'><span class='pli'>Sahāvassa dassanasampadāya, </span> <span class='eng'>When they attain to vision </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='kp6:10.2'><span class='pli'>Tayassu dhammā jahitā bhavanti; </span> <span class='eng'>they give up three things: </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='kp6:10.3'><span class='pli'>Sakkāyadiṭṭhī vicikicchitañca, </span> <span class='eng'>substantialist view, doubt, and any </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='kp6:10.4'><span class='pli'>Sīlabbataṁ vāpi yadatthi kiñci. </span> <span class='eng'>attachment to precepts and observances. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='kp6:11.1'><span class='pli'>Catūhapāyehi ca vippamutto, </span> <span class='eng'>They’re freed from the four places of loss, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='kp6:11.2'><span class='pli'>Chaccābhiṭhānāni abhabba kātuṁ; </span> <span class='eng'>and cannot do six things. </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='kp6:11.3'><span class='pli'>Idampi saṅghe ratanaṁ paṇītaṁ, </span> <span class='eng'>This sublime gem is in the Saṅgha: </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='kp6:11.4'><span class='pli'>Etena saccena suvatthi hotu. </span> <span class='eng'>by this truth, may you be well! </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='kp6:12.1'><span class='pli'>Kiñcāpi so kamma karoti pāpakaṁ, </span> <span class='eng'>Even if they do a bad deed </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='kp6:12.2'><span class='pli'>Kāyena vācā uda cetasā vā; </span> <span class='eng'>by body, speech, or mind, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='kp6:12.3'><span class='pli'>Abhabba so tassa paṭicchadāya, </span> <span class='eng'>they are unable to conceal it; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='kp6:12.4'><span class='pli'>Abhabbatā diṭṭhapadassa vuttā; </span> <span class='eng'>they say this inability applies to one who has seen the truth. </span></span></span>\n<span class='verse-line'><span class='segment' id='12.5' data-uid='kp6:12.5'><span class='pli'>Idampi saṅghe ratanaṁ paṇītaṁ, </span> <span class='eng'>This sublime gem is in the Saṅgha: </span></span></span>\n<span class='verse-line'><span class='segment' id='12.6' data-uid='kp6:12.6'><span class='pli'>Etena saccena suvatthi hotu. </span> <span class='eng'>by this truth, may you be well! </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='kp6:13.1'><span class='pli'>Vanappagumbe yatha phussitagge, </span> <span class='eng'>Like a tall forest tree crowned with flowers </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='kp6:13.2'><span class='pli'>Gimhānamāse paṭhamasmiṁ gimhe; </span> <span class='eng'>in the first month of summer; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='kp6:13.3'><span class='pli'>Tathūpamaṁ dhammavaraṁ adesayi, </span> <span class='eng'>that’s how he taught the superb Dhamma, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='kp6:13.4'><span class='pli'>Nibbānagāmiṁ paramaṁ hitāya; </span> <span class='eng'>leading to extinguishment, the ultimate benefit. </span></span></span>\n<span class='verse-line'><span class='segment' id='13.5' data-uid='kp6:13.5'><span class='pli'>Idampi buddhe ratanaṁ paṇītaṁ, </span> <span class='eng'>This sublime gem is in the Buddha: </span></span></span>\n<span class='verse-line'><span class='segment' id='13.6' data-uid='kp6:13.6'><span class='pli'>Etena saccena suvatthi hotu. </span> <span class='eng'>by this truth, may you be well! </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='kp6:14.1'><span class='pli'>Varo varaññū varado varāharo, </span> <span class='eng'>The superb, knower of the superb, giver of the superb, bringer of the superb; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='kp6:14.2'><span class='pli'>Anuttaro dhammavaraṁ adesayi; </span> <span class='eng'>taught the superb Dhamma supreme. </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='kp6:14.3'><span class='pli'>Idampi buddhe ratanaṁ paṇītaṁ, </span> <span class='eng'>This sublime gem is in the Buddha: </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='kp6:14.4'><span class='pli'>Etena saccena suvatthi hotu. </span> <span class='eng'>by this truth, may you be well! </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='kp6:15.1'><span class='pli'>Khīṇaṁ purāṇaṁ nava natthi sambhavaṁ, </span> <span class='eng'>The old is ended, nothing new is produced. </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='kp6:15.2'><span class='pli'>Virattacittāyatike bhavasmiṁ; </span> <span class='eng'>their minds have no desire for future rebirth. </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='kp6:15.3'><span class='pli'>Te khīṇabījā avirūḷhichandā, </span> <span class='eng'>Withered are the seeds, there’s no desire for growth, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='kp6:15.4'><span class='pli'>Nibbanti dhīrā yathāyaṁ padīpo; </span> <span class='eng'>those attentive ones are extinguished just like this lamp. </span></span></span>\n<span class='verse-line'><span class='segment' id='15.5' data-uid='kp6:15.5'><span class='pli'>Idampi saṅghe ratanaṁ paṇītaṁ, </span> <span class='eng'>This sublime gem is in the Saṅgha: </span></span></span>\n<span class='verse-line'><span class='segment' id='15.6' data-uid='kp6:15.6'><span class='pli'>Etena saccena suvatthi hotu. </span> <span class='eng'>by this truth, may you be well! </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='kp6:16.1'><span class='pli'>Yānīdha bhūtāni samāgatāni, </span> <span class='eng'>Whatever beings have gathered here, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='kp6:16.2'><span class='pli'>Bhummāni vā yāni va antalikkhe; </span> <span class='eng'>on the ground or in the sky: </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='kp6:16.3'><span class='pli'>Tathāgataṁ devamanussapūjitaṁ, </span> <span class='eng'>the Realized One is honored by gods and humans! </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='kp6:16.4'><span class='pli'>Buddhaṁ namassāma suvatthi hotu. </span> <span class='eng'>We bow to the Buddha! May you be safe! </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='kp6:17.1'><span class='pli'>Yānīdha bhūtāni samāgatāni, </span> <span class='eng'>Whatever beings have gathered here, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='kp6:17.2'><span class='pli'>Bhummāni vā yāni va antalikkhe; </span> <span class='eng'>on the ground or in the sky: </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='kp6:17.3'><span class='pli'>Tathāgataṁ devamanussapūjitaṁ, </span> <span class='eng'>the Realized One is honored by gods and humans! </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='kp6:17.4'><span class='pli'>Dhammaṁ namassāma suvatthi hotu. </span> <span class='eng'>We bow to the Dhamma! May you be safe! </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='kp6:18.1'><span class='pli'>Yānīdha bhūtāni samāgatāni, </span> <span class='eng'>Whatever beings have gathered here, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='kp6:18.2'><span class='pli'>Bhummāni vā yāni va antalikkhe; </span> <span class='eng'>on the ground or in the sky: </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='kp6:18.3'><span class='pli'>Tathāgataṁ devamanussapūjitaṁ, </span> <span class='eng'>the Realized One is honored by gods and humans! </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='kp6:18.4'><span class='pli'>Saṅghaṁ namassāma suvatthi hotūti. </span> <span class='eng'>We bow to the Saṅgha! May you be safe! </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='19.1' data-uid='kp6:19.1'><span class='pli'>Ratanasuttaṁ. </span></span></p></article>\n"
  },
  "kp7": {
    "previous": "kp6",
    "next": "kp8",
    "content": "<article id='kp7'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='kp7:0.1'><span class='pli'>Khuddakapāṭha 7 </span> <span class='eng'>Basic Passages 7 </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='kp7:0.2'><span class='pli'>Tirokuṭṭasutta </span> <span class='eng'>Outside the Walls </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='kp7:1.1'><span class='pli'>Tirokuṭṭesu tiṭṭhanti, </span> <span class='eng'>Outside the walls they stand and wait, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='kp7:1.2'><span class='pli'>sandhisiṅghāṭakesu ca; </span> <span class='eng'>at the junctions and the crossroads. </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='kp7:1.3'><span class='pli'>Dvārabāhāsu tiṭṭhanti, </span> <span class='eng'>Returning to their former homes </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='kp7:1.4'><span class='pli'>āgantvāna sakaṁ gharaṁ. </span> <span class='eng'>they wait beside the door posts. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='kp7:2.1'><span class='pli'>Pahūte annapānamhi, </span> <span class='eng'>But when lavish food and drink </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='kp7:2.2'><span class='pli'>khajjabhojje upaṭṭhite; </span> <span class='eng'>of many kinds is set out, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='kp7:2.3'><span class='pli'>Na tesaṁ koci sarati, </span> <span class='eng'>no-one remembers them at all, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='kp7:2.4'><span class='pli'>sattānaṁ kammapaccayā. </span> <span class='eng'>because of those beings’s deeds. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='kp7:3.1'><span class='pli'>Evaṁ dadanti ñātīnaṁ, </span> <span class='eng'>That’s why those who have compassion </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='kp7:3.2'><span class='pli'>ye honti anukampakā; </span> <span class='eng'>give to their relatives </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='kp7:3.3'><span class='pli'>Suciṁ paṇītaṁ kālena, </span> <span class='eng'>food and drink at the right time, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='kp7:3.4'><span class='pli'>kappiyaṁ pānabhojanaṁ. </span> <span class='eng'>that’s clean, delicious, and suitable. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='kp7:4.1'><span class='pli'>Idaṁ vo ñātīnaṁ hotu, </span> <span class='eng'>“May this be for our relatives! </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='kp7:4.2'><span class='pli'>sukhitā hontu ñātayo; </span> <span class='eng'>May our relatives be happy!” </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='kp7:4.3'><span class='pli'>Te ca tattha samāgantvā, </span> <span class='eng'>Those ghosts who have gathered there, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='kp7:4.4'><span class='pli'>ñātipetā samāgatā. </span> <span class='eng'>the departed relatives who have come </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='kp7:5.1'><span class='pli'>Pahūte annapānamhi, </span> <span class='eng'>for the lavish food and drink </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='kp7:5.2'><span class='pli'>sakkaccaṁ anumodare; </span> <span class='eng'>gratefully express appreciation: </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='kp7:5.3'><span class='pli'>Ciraṁ jīvantu no ñātī, </span> <span class='eng'>“May our relatives live long! </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='kp7:5.4'><span class='pli'>yesaṁ hetu labhāmase. </span> <span class='eng'>For those to whom we owe this gain, </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='kp7:6.1'><span class='pli'>Amhākañca katā pūjā, </span> <span class='eng'>who have given honor to us, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='kp7:6.2'><span class='pli'>dāyakā ca anipphalā; </span> <span class='eng'>it will not be fruitless for the donor.” </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='kp7:6.3'><span class='pli'>Na hi tattha kasī atthi, </span> <span class='eng'>There is no farming there, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='kp7:6.4'><span class='pli'>gorakkhettha na vijjati. </span> <span class='eng'>no cow pasture can be found; </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='kp7:7.1'><span class='pli'>Vaṇijjā tādisī natthi, </span> <span class='eng'>likewise there’s no trading, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='kp7:7.2'><span class='pli'>hiraññena kayākayaṁ; </span> <span class='eng'>and no commerce in gold coin. </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='kp7:7.3'><span class='pli'>Ito dinnena yāpenti, </span> <span class='eng'>The departed, the dead in that place </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='kp7:7.4'><span class='pli'>petā kālagatā tahiṁ. </span> <span class='eng'>live on what is given here. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='kp7:8.1'><span class='pli'>Unname udakaṁ vuṭṭhaṁ, </span> <span class='eng'>Just as water that rains on high </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='kp7:8.2'><span class='pli'>yathā ninnaṁ pavattati; </span> <span class='eng'>flows down to the plains, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='kp7:8.3'><span class='pli'>Evamevaṁ ito dinnaṁ, </span> <span class='eng'>so too what is given here </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='kp7:8.4'><span class='pli'>petānaṁ upakappati. </span> <span class='eng'>aids the departed ghosts. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='kp7:9.1'><span class='pli'>Yathā vārivahā pūrā, </span> <span class='eng'>Just as the rivers full </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='kp7:9.2'><span class='pli'>paripūrenti sāgaraṁ; </span> <span class='eng'>swell the ocean seas </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='kp7:9.3'><span class='pli'>Evamevaṁ ito dinnaṁ, </span> <span class='eng'>so too what is given here </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='kp7:9.4'><span class='pli'>petānaṁ upakappati. </span> <span class='eng'>aids the departed ghosts. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='kp7:10.1'><span class='pli'>Adāsi me akāsi me, </span> <span class='eng'>Thinking: “They gave to me, they did for me, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='kp7:10.2'><span class='pli'>ñāti mittā sakhā ca me; </span> <span class='eng'>they were my family, friend, comrade”, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='kp7:10.3'><span class='pli'>Petānaṁ dakkhiṇaṁ dajjā, </span> <span class='eng'>give offerings to departed kin, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='kp7:10.4'><span class='pli'>pubbe katamanussaraṁ. </span> <span class='eng'>remembering past deeds. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='kp7:11.1'><span class='pli'>Na hi ruṇṇaṁ vā soko vā, </span> <span class='eng'>For neither tears nor grief </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='kp7:11.2'><span class='pli'>yā caññā paridevanā; </span> <span class='eng'>or other lamentations </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='kp7:11.3'><span class='pli'>Na taṁ petānamatthāya, </span> <span class='eng'>are of any use to the departed, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='kp7:11.4'><span class='pli'>evaṁ tiṭṭhanti ñātayo. </span> <span class='eng'>so long as their relatives stay like this. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='kp7:12.1'><span class='pli'>Ayañca kho dakkhiṇā dinnā, </span> <span class='eng'>This offering that has been given, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='kp7:12.2'><span class='pli'>saṅghamhi suppatiṭṭhitā; </span> <span class='eng'>well placed in the Saṅgha, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='kp7:12.3'><span class='pli'>Dīgharattaṁ hitāyassa, </span> <span class='eng'>is for their lasting welfare, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='kp7:12.4'><span class='pli'>ṭhānaso upakappati. </span> <span class='eng'>and aids them right away. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='kp7:13.1'><span class='pli'>So ñātidhammo ca ayaṁ nidassito, </span> <span class='eng'>The relative’s duty has now been shown: </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='kp7:13.2'><span class='pli'>Petāna pūjā ca katā uḷārā; </span> <span class='eng'>how high honor to departed is performed, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='kp7:13.3'><span class='pli'>Balañca bhikkhūnamanuppadinnaṁ, </span> <span class='eng'>how the mendicants can be kept strong, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='kp7:13.4'><span class='pli'>Tumhehi puññaṁ pasutaṁ anappakanti. </span> <span class='eng'>and how no little merit is produced by you. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='14.1' data-uid='kp7:14.1'><span class='pli'>Tirokuṭṭasuttaṁ. </span></span></p></article>\n"
  },
  "kp8": {
    "previous": "kp7",
    "next": "kp9",
    "content": "<article id='kp8'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='kp8:0.1'><span class='pli'>Khuddakapāṭha 8 </span> <span class='eng'>Basic Passages 8 </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='kp8:0.2'><span class='pli'>Nidhikaṇḍasutta </span> <span class='eng'>A Treasure Trove </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='kp8:1.1'><span class='pli'>Nidhiṁ nidheti puriso, </span> <span class='eng'>A person stores away their savings </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='kp8:1.2'><span class='pli'>gambhīre odakantike; </span> <span class='eng'>in a deep trove by the water’s edge: </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='kp8:1.3'><span class='pli'>Atthe kicce samuppanne, </span> <span class='eng'>“When need arises </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='kp8:1.4'><span class='pli'>atthāya me bhavissati. </span> <span class='eng'>it will be there to help </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='kp8:2.1'><span class='pli'>Rājato vā duruttassa, </span> <span class='eng'>free me from rulers if I am slandered, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='kp8:2.2'><span class='pli'>corato pīḷitassa vā; </span> <span class='eng'>or from bandits if harassed, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='kp8:2.3'><span class='pli'>Iṇassa vā pamokkhāya, </span> <span class='eng'>or to release me from debt, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='kp8:2.4'><span class='pli'>dubbhikkhe āpadāsu vā; </span> <span class='eng'>or in case of famine or losses.” </span></span></span>\n<span class='verse-line'><span class='segment' id='2.5' data-uid='kp8:2.5'><span class='pli'>Etadatthāya lokasmiṁ, </span> <span class='eng'>What the world calls savings </span></span></span>\n<span class='verse-line'><span class='segment' id='2.6' data-uid='kp8:2.6'><span class='pli'>nidhi nāma nidhīyati. </span> <span class='eng'>get stored away for such reasons. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='kp8:3.1'><span class='pli'>Tāvassunihito santo, </span> <span class='eng'>But no matter how well stored away they are </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='kp8:3.2'><span class='pli'>gambhīre odakantike; </span> <span class='eng'>in a deep pit by the water’s edge, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='kp8:3.3'><span class='pli'>Na sabbo sabbadā eva, </span> <span class='eng'>all their savings will fail </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='kp8:3.4'><span class='pli'>tassa taṁ upakappati. </span> <span class='eng'>to aid them all the time. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='kp8:4.1'><span class='pli'>Nidhi vā ṭhānā cavati, </span> <span class='eng'>For perhaps those savings are removed from there, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='kp8:4.2'><span class='pli'>saññā vāssa vimuyhati; </span> <span class='eng'>or they forget what marks the site, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='kp8:4.3'><span class='pli'>Nāgā vā apanāmenti, </span> <span class='eng'>or dragons make off with them, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='kp8:4.4'><span class='pli'>yakkhā vāpi haranti naṁ. </span> <span class='eng'>or spirits carry them away, </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='kp8:5.1'><span class='pli'>Appiyā vāpi dāyādā, </span> <span class='eng'>or unloved heirs </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='kp8:5.2'><span class='pli'>uddharanti apassato; </span> <span class='eng'>secretly unearth them. </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='kp8:5.3'><span class='pli'>Yadā puññakkhayo hoti, </span> <span class='eng'>When their merit is used up, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='kp8:5.4'><span class='pli'>sabbametaṁ vinassati. </span> <span class='eng'>all of that will vanish. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='kp8:6.1'><span class='pli'>Yassa dānena sīlena, </span> <span class='eng'>But by giving and morality, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='kp8:6.2'><span class='pli'>saṁyamena damena ca; </span> <span class='eng'>restraint and self-control, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='kp8:6.3'><span class='pli'>Nidhī sunihito hoti, </span> <span class='eng'>a woman or man </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='kp8:6.4'><span class='pli'>itthiyā purisassa vā. </span> <span class='eng'>keeps their savings safe. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='kp8:7.1'><span class='pli'>Cetiyamhi ca saṅghe vā, </span> <span class='eng'>At a shrine or with the Saṅgha, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='kp8:7.2'><span class='pli'>puggale atithīsu vā; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='kp8:7.3'><span class='pli'>Mātari pitari cāpi, </span> <span class='eng'>with mother or father, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='kp8:7.4'><span class='pli'>atho jeṭṭhamhi bhātari. </span> <span class='eng'>or else an elder sibling, </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='kp8:8.1'><span class='pli'>Eso nidhi sunihito, </span> <span class='eng'>those savings are kept safe, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='kp8:8.2'><span class='pli'>ajeyyo anugāmiko; </span> <span class='eng'>they stay with you, undecaying. </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='kp8:8.3'><span class='pli'>Pahāya gamanīyesu, </span> <span class='eng'>We must go on leaving all behind, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='kp8:8.4'><span class='pli'>etaṁ ādāya gacchati. </span> <span class='eng'>only this you take when you go. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='kp8:9.1'><span class='pli'>Asādhāraṇamaññesaṁ, </span> <span class='eng'>You don’t have to share it with others, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='kp8:9.2'><span class='pli'>acorāharaṇo nidhi; </span> <span class='eng'>no thief makes off with your savings. </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='kp8:9.3'><span class='pli'>Kayirātha dhīro puññāni, </span> <span class='eng'>An attentive one would make merit, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='kp8:9.4'><span class='pli'>yo nidhi anugāmiko. </span> <span class='eng'>the savings that stay with you. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='kp8:10.1'><span class='pli'>Esa devamanussānaṁ, </span> <span class='eng'>Such savings grant every desire </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='kp8:10.2'><span class='pli'>sabbakāmadado nidhi; </span> <span class='eng'>of gods and humans too. </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='kp8:10.3'><span class='pli'>Yaṁ yadevābhipatthenti, </span> <span class='eng'>Whatever it is that they wish for </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='kp8:10.4'><span class='pli'>sabbametena labbhati. </span> <span class='eng'>through this they have it all. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='kp8:11.1'><span class='pli'>Suvaṇṇatā susaratā, </span> <span class='eng'>Good looks, a sweet voice, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='kp8:11.2'><span class='pli'>susaṇṭhānā surūpatā; </span> <span class='eng'>a good shape, and good appearance, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='kp8:11.3'><span class='pli'>Ādhipaccaparivāro, </span> <span class='eng'>leadership and followers: </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='kp8:11.4'><span class='pli'>sabbametena labbhati. </span> <span class='eng'>through this they have it all. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='kp8:12.1'><span class='pli'>Padesarajjaṁ issariyaṁ, </span> <span class='eng'>Sovereignty of a local kingdom, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='kp8:12.2'><span class='pli'>cakkavattisukhaṁ piyaṁ; </span> <span class='eng'>the happiness of a Wheel-Turning Monarch, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='kp8:12.3'><span class='pli'>Devarajjampi dibbesu, </span> <span class='eng'>even kingship in the heavens: </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='kp8:12.4'><span class='pli'>sabbametena labbhati. </span> <span class='eng'>through this they have it all. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='kp8:13.1'><span class='pli'>Mānussikā ca sampatti, </span> <span class='eng'>Human success, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='kp8:13.2'><span class='pli'>devaloke ca yā rati; </span> <span class='eng'>heavenly delight, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='kp8:13.3'><span class='pli'>Yā ca nibbānasampatti, </span> <span class='eng'>attaining extinguishment: </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='kp8:13.4'><span class='pli'>sabbametena labbhati. </span> <span class='eng'>through this they have it all. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='kp8:14.1'><span class='pli'>Mittasampadamāgamma, </span> <span class='eng'>Relying on having good friends, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='kp8:14.2'><span class='pli'>yonisova payuñjato; </span> <span class='eng'>rational application of effort, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='kp8:14.3'><span class='pli'>Vijjāvimuttivasībhāvo, </span> <span class='eng'>mastery of knowledge and freedom: </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='kp8:14.4'><span class='pli'>sabbametena labbhati. </span> <span class='eng'>through this they have it all. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='kp8:15.1'><span class='pli'>Paṭisambhidā vimokkhā ca, </span> <span class='eng'>Analytical knowledge, the liberations, </span> <span class='comment-marker' data-comment='The inclusion of <i lang=&#39;pi&#39; translate=&#39;no&#39;>paṭisambhidā</i> here with the late terms in the following lines reinforces the sense that this is a late term. Here it could refer either to the “analytical knowledge” of the elements (<a href=&#39;index.html?q=an1.595&#39;>AN 1.595</a>), or the “analytical knowledge” of the text(<a href=&#39;index.html?q=an5.95&#39;>AN 5.95</a>), in which case it is fourfold and should be translated as plural. '>*</span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='kp8:15.2'><span class='pli'>yā ca sāvakapāramī; </span> <span class='eng'>the perfections of the disciple, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='kp8:15.3'><span class='pli'>Paccekabodhi buddhabhūmi, </span> <span class='eng'>the plane of an independent Buddha: </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='kp8:15.4'><span class='pli'>sabbametena labbhati. </span> <span class='eng'>through this they have it all. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='kp8:16.1'><span class='pli'>Evaṁ mahatthikā esā, </span> <span class='eng'>This accomplishment in merit </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='kp8:16.2'><span class='pli'>yadidaṁ puññasampadā; </span> <span class='eng'>is so very beneficial. </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='kp8:16.3'><span class='pli'>Tasmā dhīrā pasaṁsanti, </span> <span class='eng'>That’s why the attentive and the astute </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='kp8:16.4'><span class='pli'>paṇḍitā katapuññatanti. </span> <span class='eng'>praise the making of merit. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='17.1' data-uid='kp8:17.1'><span class='pli'>Nidhikaṇḍasuttaṁ. </span></span></p></article>\n"
  },
  "kp9": {
    "previous": "kp8",
    "next": null,
    "content": "<article id='kp9'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='kp9:0.1'><span class='pli'>Khuddakapāṭha 9 </span> <span class='eng'>Basic Passages 9 </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.2' data-uid='kp9:0.2'><span class='pli'>Mettasutta </span> <span class='eng'>The Discourse on Love </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='kp9:1.1'><span class='pli'>Karaṇīyamatthakusalena, </span> <span class='eng'>Those who are skilled in the meaning of scripture </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='kp9:1.2'><span class='pli'>Yanta santaṁ padaṁ abhisamecca; </span> <span class='eng'>should practice like this so as to realize the state of peace. </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='kp9:1.3'><span class='pli'>Sakko ujū ca suhujū ca, </span> <span class='eng'>Let them be capable and upright, very upright, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='kp9:1.4'><span class='pli'>Sūvaco cassa mudu anatimānī. </span> <span class='eng'>easy to speak to, gentle and humble; </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='kp9:2.1'><span class='pli'>Santussako ca subharo ca, </span> <span class='eng'>content and unburdensome, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='kp9:2.2'><span class='pli'>Appakicco ca sallahukavutti; </span> <span class='eng'>unbusied, living lightly, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='kp9:2.3'><span class='pli'>Santindriyo ca nipako ca, </span> <span class='eng'>alert, with senses calmed, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='kp9:2.4'><span class='pli'>Appagabbho kulesvananugiddho. </span> <span class='eng'>courteous, not fawning on families. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='kp9:3.1'><span class='pli'>Na ca khuddamācare kiñci, </span> <span class='eng'>Let them not do the slightest thing </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='kp9:3.2'><span class='pli'>Yena viññū pare upavadeyyuṁ; </span> <span class='eng'>that others who are wise would blame. </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='kp9:3.3'><span class='pli'>Sukhino va khemino hontu, </span> <span class='eng'>May they be happy and safe! </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='kp9:3.4'><span class='pli'>Sabbasattā bhavantu sukhitattā. </span> <span class='eng'>May all beings be happy! </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='kp9:4.1'><span class='pli'>Ye keci pāṇabhūtatthi, </span> <span class='eng'>Whatever living creatures there are </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='kp9:4.2'><span class='pli'>Tasā vā thāvarā vanavasesā; </span> <span class='eng'>with not a one left out—</span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='kp9:4.3'><span class='pli'>Dīghā vā ye va mahantā, </span> <span class='eng'>frail or firm, long or large, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='kp9:4.4'><span class='pli'>Majjhimā rassakā aṇukathūlā. </span> <span class='eng'>medium, small, tiny or round, </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='kp9:5.1'><span class='pli'>Diṭṭhā vā ye va adiṭṭhā, </span> <span class='eng'>seen or unseen, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='kp9:5.2'><span class='pli'>Ye va dūre vasanti avidūre; </span> <span class='eng'>living far or near, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='kp9:5.3'><span class='pli'>Bhūtā va sambhavesī va, </span> <span class='eng'>those who have been born <j>and those about to be born—</span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='kp9:5.4'><span class='pli'>Sabbasattā bhavantu sukhitattā. </span> <span class='eng'>may all beings be happy! </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='kp9:6.1'><span class='pli'>Na paro paraṁ nikubbetha, </span> <span class='eng'>Let none deceive another, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='kp9:6.2'><span class='pli'>Nātimaññetha katthaci na kañci; </span> <span class='eng'>nor look down on anyone anywhere. </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='kp9:6.3'><span class='pli'>Byārosanā paṭighasañña, </span> <span class='eng'>Though provoked or aggrieved, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='kp9:6.4'><span class='pli'>Nāññamaññassa dukkhamiccheyya. </span> <span class='eng'>let them not wish pain on each other. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='kp9:7.1'><span class='pli'>Mātā yathā niyaṁ puttam </span> <span class='eng'>Even as a mother would protect with her life </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='kp9:7.2'><span class='pli'>Āyusā ekaputtamanurakkhe; </span> <span class='eng'>her child, her only child, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='kp9:7.3'><span class='pli'>Evampi sabbabhūtesu, </span> <span class='eng'>so too for all creatures </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='kp9:7.4'><span class='pli'>Mānasaṁ bhāvaye aparimāṇaṁ. </span> <span class='eng'>unfold a boundless heart. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='kp9:8.1'><span class='pli'>Mettañca sabbalokasmi, </span> <span class='eng'>With love for the whole world, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='kp9:8.2'><span class='pli'>Mānasaṁ bhāvaye aparimāṇaṁ; </span> <span class='eng'>unfold a boundless heart: </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='kp9:8.3'><span class='pli'>Uddhaṁ adho ca tiriyañca, </span> <span class='eng'>above, below, all round, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='kp9:8.4'><span class='pli'>Asambādhaṁ averamasapattaṁ. </span> <span class='eng'>unconstricted, without enmity or foe. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='kp9:9.1'><span class='pli'>Tiṭṭhaṁ caraṁ nisinno va, </span> <span class='eng'>When standing, walking, sitting, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='kp9:9.2'><span class='pli'>Sayāno yāvatāssa vitamiddho; </span> <span class='eng'>or lying down while yet unweary, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='kp9:9.3'><span class='pli'>Etaṁ satiṁ adhiṭṭheyya, </span> <span class='eng'>keep this ever in mind; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='kp9:9.4'><span class='pli'>Brahmametaṁ vihāramidhamāhu. </span> <span class='eng'>for this, they say, is a divine meditation in this life. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='kp9:10.1'><span class='pli'>Diṭṭhiñca anupaggamma, </span> <span class='eng'>Avoiding harmful views, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='kp9:10.2'><span class='pli'>Sīlavā dassanena sampanno; </span> <span class='eng'>virtuous, accomplished in insight, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='kp9:10.3'><span class='pli'>Kāmesu vinaya gedhaṁ, </span> <span class='eng'>with desire for sensual pleasures dispelled, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='kp9:10.4'><span class='pli'>Na hi jātuggabbhaseyya punaretīti. </span> <span class='eng'>they never return to a womb again. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='11.1' data-uid='kp9:11.1'><span class='pli'>Mettasuttaṁ. </span></span></p>\n<p class='endbook'><span class='segment' id='12.1' data-uid='kp9:12.1'><span class='pli'>Khuddakapāṭhapāḷi niṭṭhitā. </span></span></p></article>\n"
  }
});
