// Source: kn/thi-ap
window.SUTTA_DB = window.SUTTA_DB || {};
Object.assign(window.SUTTA_DB, {
  "thi-ap1": {
    "previous": null,
    "next": "thi-ap2",
    "content": "<article id='thi-ap1'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap1:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap1:0.2'><span class='pli'>Sumedhāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap1:0.3'><span class='pli'>Sumedhātherīapadāna </span></span></h1></header>\n<p class='namo'><span class='segment' id='1.1' data-uid='thi-ap1:1.1'><span class='pli'>Namo tassa Bhagavato Arahato Sammāsambuddhassa. </span></span></p>\n<p><span class='segment' id='2.1' data-uid='thi-ap1:2.1'><span class='pli'>Atha therikāpadānāni suṇātha—</span></span></p>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap1:3.1'><span class='pli'>“Bhagavati koṇāgamane, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap1:3.2'><span class='pli'>Saṅghārāmamhi navanivesanamhi; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap1:3.3'><span class='pli'>Sakhiyo tisso janiyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap1:3.4'><span class='pli'>Vihāradānaṁ adāsimha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap1:4.1'><span class='pli'>Dasakkhattuṁ satakkhattuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap1:4.2'><span class='pli'>Dasasatakkhattuṁ satānañca satakkhattuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap1:4.3'><span class='pli'>Devesu upapajjimha, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap1:4.4'><span class='pli'>Ko vādo mānuse bhave. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap1:5.1'><span class='pli'>Deve mahiddhikā ahumha, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap1:5.2'><span class='pli'>mānusakamhi ko vādo; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap1:5.3'><span class='pli'>Sattaratanamahesī, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap1:5.4'><span class='pli'>itthiratanaṁ ahaṁ bhaviṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap1:6.1'><span class='pli'>Idha sañcitakusalā, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap1:6.2'><span class='pli'>susamiddhakulappajā; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap1:6.3'><span class='pli'>Dhanañjānī ca khemā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap1:6.4'><span class='pli'>ahampi ca tayo janā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap1:7.1'><span class='pli'>Ārāmaṁ sukataṁ katvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap1:7.2'><span class='pli'>sabbāvayavamaṇḍitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap1:7.3'><span class='pli'>Buddhappamukhasaṅghassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap1:7.4'><span class='pli'>niyyādetvā samoditā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap1:8.1'><span class='pli'>Yattha yatthūpapajjāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap1:8.2'><span class='pli'>tassa kammassa vāhasā; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap1:8.3'><span class='pli'>Devesu aggataṁ pattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap1:8.4'><span class='pli'>manussesu tatheva ca. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap1:9.1'><span class='pli'>Imasmiṁyeva kappamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap1:9.2'><span class='pli'>brahmabandhu mahāyaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap1:9.3'><span class='pli'>Kassapo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap1:9.4'><span class='pli'>uppajji vadataṁ varo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap1:10.1'><span class='pli'>Upaṭṭhāko mahesissa, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap1:10.2'><span class='pli'>tadā āsi narissaro; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap1:10.3'><span class='pli'>Kāsirājā kikī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap1:10.4'><span class='pli'>bārāṇasipuruttame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap1:11.1'><span class='pli'>Tassāsuṁ satta dhītaro, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap1:11.2'><span class='pli'>rājakaññā sukhedhitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap1:11.3'><span class='pli'>Buddhopaṭṭhānaniratā, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap1:11.4'><span class='pli'>brahmacariyaṁ cariṁsu tā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap1:12.1'><span class='pli'>Tāsaṁ sahāyikā hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap1:12.2'><span class='pli'>sīlesu susamāhitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap1:12.3'><span class='pli'>Datvā dānāni sakkaccaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap1:12.4'><span class='pli'>agāreva vataṁ cariṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap1:13.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap1:13.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap1:13.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap1:13.4'><span class='pli'>tāvatiṁsūpagā ahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap1:14.1'><span class='pli'>Tato cutā yāmamagaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap1:14.2'><span class='pli'>tatohaṁ tusitaṁ gatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap1:14.3'><span class='pli'>Tato ca nimmānaratiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap1:14.4'><span class='pli'>vasavattipuraṁ tato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap1:15.1'><span class='pli'>Yattha yatthūpapajjāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap1:15.2'><span class='pli'>puññakammasamohitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap1:15.3'><span class='pli'>Tattha tattheva rājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap1:15.4'><span class='pli'>mahesittamakārayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap1:16.1'><span class='pli'>Tato cutā manussatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap1:16.2'><span class='pli'>rājūnaṁ cakkavattinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap1:16.3'><span class='pli'>Maṇḍalīnañca rājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap1:16.4'><span class='pli'>mahesittamakārayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap1:17.1'><span class='pli'>Sampattimanubhotvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap1:17.2'><span class='pli'>devesu mānusesu ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap1:17.3'><span class='pli'>Sabbattha sukhitā hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap1:17.4'><span class='pli'>nekajātīsu saṁsariṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap1:18.1'><span class='pli'>So hetu ca so pabhavo, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap1:18.2'><span class='pli'>Tammūlaṁ sāsane khamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap1:18.3'><span class='pli'>Paṭhamaṁ taṁ samodhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap1:18.4'><span class='pli'>Taṁ dhammaratāya nibbānaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap1:19.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap1:19.2'><span class='pli'>bhavā sabbe samūhatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap1:19.3'><span class='pli'>Nāgīva bandhanaṁ chetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap1:19.4'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap1:20.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap1:20.2'><span class='pli'>buddhaseṭṭhassa santike; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap1:20.3'><span class='pli'>Tisso vijjā anuppattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap1:20.4'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap1:21.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap1:21.2'><span class='pli'>vimokkhāpi ca aṭṭhime; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap1:21.3'><span class='pli'>Chaḷabhiññā sacchikatā, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap1:21.4'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='22.1' data-uid='thi-ap1:22.1'><span class='pli'>Itthaṁ sudaṁ sumedhā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='23.1' data-uid='thi-ap1:23.1'><span class='pli'>Sumedhātheriyāpadānaṁ paṭhamaṁ. </span></span></p></article>\n"
  },
  "thi-ap2": {
    "previous": "thi-ap1",
    "next": "thi-ap3",
    "content": "<article id='thi-ap2'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap2:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap2:0.2'><span class='pli'>Sumedhāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap2:0.3'><span class='pli'>2 Mekhalādāyikātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap2:1.1'><span class='pli'>“Siddhatthassa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap2:1.2'><span class='pli'>thūpakārāpikā ahuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap2:1.3'><span class='pli'>Mekhalikā mayā dinnā, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap2:1.4'><span class='pli'>navakammāya satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap2:2.1'><span class='pli'>Niṭṭhite ca mahāthūpe, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap2:2.2'><span class='pli'>mekhalaṁ punadāsahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap2:2.3'><span class='pli'>Lokanāthassa munino, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap2:2.4'><span class='pli'>pasannā sehi pāṇibhi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap2:3.1'><span class='pli'>Catunnavutito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap2:3.2'><span class='pli'>yaṁ mekhalamadaṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap2:3.3'><span class='pli'>Duggatiṁ nābhijānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap2:3.4'><span class='pli'>thūpakārassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap2:4.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap2:4.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap2:4.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap2:5.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap2:5.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap2:5.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap2:6.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap2:6.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap2:6.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='7.1' data-uid='thi-ap2:7.1'><span class='pli'>Itthaṁ sudaṁ mekhalādāyikā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='8.1' data-uid='thi-ap2:8.1'><span class='pli'>Mekhalādāyikātheriyāpadānaṁ dutiyaṁ. </span></span></p></article>\n"
  },
  "thi-ap3": {
    "previous": "thi-ap2",
    "next": "thi-ap4",
    "content": "<article id='thi-ap3'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap3:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap3:0.2'><span class='pli'>Sumedhāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap3:0.3'><span class='pli'>Maṇḍapadāyikātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap3:1.1'><span class='pli'>“Koṇāgamanabuddhassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap3:1.2'><span class='pli'>maṇḍapo kārito mayā; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap3:1.3'><span class='pli'>Dhuvaṁ ticīvaraṁdāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap3:1.4'><span class='pli'>buddhassa lokabandhuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap3:2.1'><span class='pli'>Yaṁ yaṁ janapadaṁ yāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap3:2.2'><span class='pli'>nigame rājadhāniyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap3:2.3'><span class='pli'>Sabbattha pūjitā homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap3:2.4'><span class='pli'>puññakammassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap3:3.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap3:3.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap3:3.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap3:4.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap3:4.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap3:4.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap3:5.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap3:5.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap3:5.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='6.1' data-uid='thi-ap3:6.1'><span class='pli'>Itthaṁ sudaṁ maṇḍapadāyikā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='7.1' data-uid='thi-ap3:7.1'><span class='pli'>Maṇḍapadāyikātheriyāpadānaṁ tatiyaṁ. </span></span></p></article>\n"
  },
  "thi-ap4": {
    "previous": "thi-ap3",
    "next": "thi-ap5",
    "content": "<article id='thi-ap4'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap4:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap4:0.2'><span class='pli'>Sumedhāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap4:0.3'><span class='pli'>4 Saṅkamanatthātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap4:1.1'><span class='pli'>“Vipassissa bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap4:1.2'><span class='pli'>lokajeṭṭhassa tādino; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap4:1.3'><span class='pli'>Rathiyaṁ paṭipannassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap4:1.4'><span class='pli'>tārayantassa pāṇino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap4:2.1'><span class='pli'>Gharato nikkhamitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap4:2.2'><span class='pli'>avakujjā nipajjahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap4:2.3'><span class='pli'>Anukampako lokanātho, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap4:2.4'><span class='pli'>sirasi akkamī mama. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap4:3.1'><span class='pli'>Akkamitvāna sirasi, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap4:3.2'><span class='pli'>agamā lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap4:3.3'><span class='pli'>Tena cittappasādena, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap4:3.4'><span class='pli'>tusitaṁ agamāsahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap4:4.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap4:4.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap4:4.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap4:5.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap4:5.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap4:5.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap4:6.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap4:6.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap4:6.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='7.1' data-uid='thi-ap4:7.1'><span class='pli'>Itthaṁ sudaṁ saṅkamanatthā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='8.1' data-uid='thi-ap4:8.1'><span class='pli'>Saṅkamanatthātheriyāpadānaṁ catutthaṁ. </span></span></p></article>\n"
  },
  "thi-ap5": {
    "previous": "thi-ap4",
    "next": "thi-ap6",
    "content": "<article id='thi-ap5'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap5:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap5:0.2'><span class='pli'>Sumedhāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap5:0.3'><span class='pli'>Naḷamālikātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap5:1.1'><span class='pli'>“Candabhāgānadītīre, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap5:1.2'><span class='pli'>ahosiṁ kinnarī tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap5:1.3'><span class='pli'>Addasaṁ virajaṁ buddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap5:1.4'><span class='pli'>sayambhuṁ aparājitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap5:2.1'><span class='pli'>Pasannacittā sumanā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap5:2.2'><span class='pli'>vedajātā katañjalī; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap5:2.3'><span class='pli'>Naḷamālaṁ gahetvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap5:2.4'><span class='pli'>sayambhuṁ abhipūjayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap5:3.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap5:3.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap5:3.3'><span class='pli'>Jahitvā kinnarīdehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap5:3.4'><span class='pli'>agacchiṁ tidasaṅgatiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap5:4.1'><span class='pli'>Chattiṁsadevarājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap5:4.2'><span class='pli'>mahesittamakārayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap5:4.3'><span class='pli'>Dasannaṁ cakkavattīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap5:4.4'><span class='pli'>mahesittamakārayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.5' data-uid='thi-ap5:4.5'><span class='pli'>Saṁvejetvāna me cittaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.6' data-uid='thi-ap5:4.6'><span class='pli'>pabbajiṁ anagāriyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap5:5.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap5:5.2'><span class='pli'>bhavā sabbe samūhatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap5:5.3'><span class='pli'>Sabbāsavā parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap5:5.4'><span class='pli'>natthi dāni punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap5:6.1'><span class='pli'>Catunnavutito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap5:6.2'><span class='pli'>yaṁ pupphamabhipūjayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap5:6.3'><span class='pli'>Duggatiṁ nābhijānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap5:6.4'><span class='pli'>pupphapūjāyidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap5:7.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap5:7.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap5:7.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap5:8.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap5:8.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap5:8.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap5:9.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap5:9.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap5:9.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='10.1' data-uid='thi-ap5:10.1'><span class='pli'>Itthaṁ sudaṁ naḷamālikā therī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='11.1' data-uid='thi-ap5:11.1'><span class='pli'>Naḷamālikātheriyāpadānaṁ pañcamaṁ. </span></span></p></article>\n"
  },
  "thi-ap6": {
    "previous": "thi-ap5",
    "next": "thi-ap7",
    "content": "<article id='thi-ap6'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap6:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap6:0.2'><span class='pli'>Sumedhāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap6:0.3'><span class='pli'>Ekapiṇḍapātadāyikātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap6:1.1'><span class='pli'>“Nagare bandhumatiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap6:1.2'><span class='pli'>bandhumā nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap6:1.3'><span class='pli'>Tassa rañño ahuṁ bhariyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap6:1.4'><span class='pli'>ekajjhaṁ cārayāmahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap6:2.1'><span class='pli'>Rahogatā nisīditvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap6:2.2'><span class='pli'>evaṁ cintesahaṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap6:2.3'><span class='pli'>‘Ādāya gamanīyaṁ hi, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap6:2.4'><span class='pli'>kusalaṁ natthi me kataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap6:3.1'><span class='pli'>Mahābhitāpaṁ kaṭukaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap6:3.2'><span class='pli'>ghorarūpaṁ sudāruṇaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap6:3.3'><span class='pli'>Nirayaṁ nūna gacchāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap6:3.4'><span class='pli'>ettha me natthi saṁsayo’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap6:4.1'><span class='pli'>Rājānaṁ upasaṅkamma, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap6:4.2'><span class='pli'>idaṁ vacanamabraviṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap6:4.3'><span class='pli'>‘Ekaṁ me samaṇaṁ dehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap6:4.4'><span class='pli'>bhojayissāmi khattiya’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap6:5.1'><span class='pli'>Adāsi me mahārājā, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap6:5.2'><span class='pli'>samaṇaṁ bhāvitindriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap6:5.3'><span class='pli'>Tassa pattaṁ gahetvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap6:5.4'><span class='pli'>paramannena pūrayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap6:6.1'><span class='pli'>Pūrayitvā paramannaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap6:6.2'><span class='pli'>gandhālepaṁ akāsahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap6:6.3'><span class='pli'>Jālena pidahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap6:6.4'><span class='pli'>vatthayugena chādayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap6:7.1'><span class='pli'>Ārammaṇaṁ mamaṁ etaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap6:7.2'><span class='pli'>sarāmi yāvajīvihaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap6:7.3'><span class='pli'>Tattha cittaṁ pasādetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap6:7.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap6:8.1'><span class='pli'>Tiṁsānaṁ devarājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap6:8.2'><span class='pli'>mahesittamakārayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap6:8.3'><span class='pli'>Manasā patthitaṁ mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap6:8.4'><span class='pli'>nibbattati yathicchitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap6:9.1'><span class='pli'>Vīsānaṁ cakkavattīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap6:9.2'><span class='pli'>mahesittamakārayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap6:9.3'><span class='pli'>Ocitattāva hutvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap6:9.4'><span class='pli'>saṁsarāmi bhavesvahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap6:10.1'><span class='pli'>Sabbabandhanamuttāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap6:10.2'><span class='pli'>apetā me upādikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap6:10.3'><span class='pli'>Sabbāsavā parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap6:10.4'><span class='pli'>natthi dāni punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap6:11.1'><span class='pli'>Ekanavutito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap6:11.2'><span class='pli'>yaṁ dānamadadiṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap6:11.3'><span class='pli'>Duggatiṁ nābhijānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap6:11.4'><span class='pli'>piṇḍapātassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap6:12.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap6:12.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap6:12.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap6:13.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap6:13.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap6:13.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap6:14.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap6:14.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap6:14.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='15.1' data-uid='thi-ap6:15.1'><span class='pli'>Itthaṁ sudaṁ ekapiṇḍapātadāyikā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='16.1' data-uid='thi-ap6:16.1'><span class='pli'>Ekapiṇḍapātadāyikātheriyāpadānaṁ chaṭṭhaṁ. </span></span></p></article>\n"
  },
  "thi-ap7": {
    "previous": "thi-ap6",
    "next": "thi-ap8",
    "content": "<article id='thi-ap7'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap7:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap7:0.2'><span class='pli'>Sumedhāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap7:0.3'><span class='pli'>7 Kaṭacchubhikkhādāyikātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap7:1.1'><span class='pli'>“Piṇḍacāraṁ carantassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap7:1.2'><span class='pli'>tissanāmassa satthuno; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap7:1.3'><span class='pli'>Kaṭacchubhikkhaṁ paggayha, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap7:1.4'><span class='pli'>buddhaseṭṭhassa dāsahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap7:2.1'><span class='pli'>Paṭiggahetvā sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap7:2.2'><span class='pli'>tisso lokagganāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap7:2.3'><span class='pli'>Vīthiyā saṇṭhito satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap7:2.4'><span class='pli'>akā me anumodanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap7:3.1'><span class='pli'>‘Kaṭacchubhikkhaṁ datvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap7:3.2'><span class='pli'>tāvatiṁsaṁ gamissasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap7:3.3'><span class='pli'>Chattiṁsadevarājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap7:3.4'><span class='pli'>mahesittaṁ karissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap7:4.1'><span class='pli'>Paññāsaṁ cakkavattīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap7:4.2'><span class='pli'>mahesittaṁ karissasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap7:4.3'><span class='pli'>Manasā patthitaṁ sabbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap7:4.4'><span class='pli'>paṭilacchasi sabbadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap7:5.1'><span class='pli'>Sampattiṁ anubhotvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap7:5.2'><span class='pli'>pabbajissasikiñcanā; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap7:5.3'><span class='pli'>Sabbāsave pariññāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap7:5.4'><span class='pli'>nibbāyissasināsavā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap7:6.1'><span class='pli'>Idaṁ vatvāna sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap7:6.2'><span class='pli'>tisso lokagganāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap7:6.3'><span class='pli'>Nabhaṁ abbhuggamī vīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap7:6.4'><span class='pli'>haṁsarājāva ambare. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap7:7.1'><span class='pli'>Sudinnaṁ me dānavaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap7:7.2'><span class='pli'>suyiṭṭhā yāgasampadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap7:7.3'><span class='pli'>Kaṭacchubhikkhaṁ datvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap7:7.4'><span class='pli'>pattāhaṁ acalaṁ padaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap7:8.1'><span class='pli'>Dvenavute ito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap7:8.2'><span class='pli'>yaṁ dānamadadiṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap7:8.3'><span class='pli'>Duggatiṁ nābhijānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap7:8.4'><span class='pli'>bhikkhādānassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap7:9.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap7:9.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap7:9.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap7:10.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap7:10.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap7:10.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap7:11.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap7:11.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap7:11.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='12.1' data-uid='thi-ap7:12.1'><span class='pli'>Itthaṁ sudaṁ kaṭacchubhikkhādāyikā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='13.1' data-uid='thi-ap7:13.1'><span class='pli'>Kaṭacchubhikkhādāyikātheriyāpadānaṁ sattamaṁ. </span></span></p></article>\n"
  },
  "thi-ap8": {
    "previous": "thi-ap7",
    "next": "thi-ap9",
    "content": "<article id='thi-ap8'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap8:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap8:0.2'><span class='pli'>Sumedhāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap8:0.3'><span class='pli'>Sattuppalamālikātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap8:1.1'><span class='pli'>“Nagare aruṇavatiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap8:1.2'><span class='pli'>aruṇo nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap8:1.3'><span class='pli'>Tassa rañño ahuṁ bhariyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap8:1.4'><span class='pli'>vāritaṁ vārayāmahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap8:2.1'><span class='pli'>Satta mālā gahetvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap8:2.2'><span class='pli'>uppalā devagandhikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap8:2.3'><span class='pli'>Nisajja pāsādavare, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap8:2.4'><span class='pli'>evaṁ cintesi tāvade. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap8:3.1'><span class='pli'>‘Kiṁ me imāhi mālāhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap8:3.2'><span class='pli'>sirasāropitāhi me; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap8:3.3'><span class='pli'>Varaṁ me buddhaseṭṭhassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap8:3.4'><span class='pli'>ñāṇamhi abhiropitaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap8:4.1'><span class='pli'>Sambuddhaṁ paṭimānentī, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap8:4.2'><span class='pli'>dvārāsanne nisīdahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap8:4.3'><span class='pli'>‘Yadā ehiti sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap8:4.4'><span class='pli'>pūjayissaṁ mahāmuniṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap8:5.1'><span class='pli'>Kakudho vilasantova, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap8:5.2'><span class='pli'>migarājāva kesarī; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap8:5.3'><span class='pli'>Bhikkhusaṅghena sahito, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap8:5.4'><span class='pli'>āgacchi vīthiyā jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap8:6.1'><span class='pli'>Buddhassa raṁsiṁ disvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap8:6.2'><span class='pli'>haṭṭhā saṁviggamānasā; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap8:6.3'><span class='pli'>Dvāraṁ avāpuritvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap8:6.4'><span class='pli'>buddhaseṭṭhamapūjayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap8:7.1'><span class='pli'>Satta uppalapupphāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap8:7.2'><span class='pli'>parikiṇṇāni ambare; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap8:7.3'><span class='pli'>Chadiṁ karonto buddhassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap8:7.4'><span class='pli'>matthake dhārayanti te. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap8:8.1'><span class='pli'>Udaggacittā sumanā, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap8:8.2'><span class='pli'>vedajātā katañjalī; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap8:8.3'><span class='pli'>Tattha cittaṁ pasādetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap8:8.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap8:9.1'><span class='pli'>Mahānelassa chādanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap8:9.2'><span class='pli'>dhārenti mama muddhani; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap8:9.3'><span class='pli'>Dibbagandhaṁ pavāyāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap8:9.4'><span class='pli'>sattuppalassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap8:10.1'><span class='pli'>Kadāci nīyamānāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap8:10.2'><span class='pli'>ñātisaṅghena me tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap8:10.3'><span class='pli'>Yāvatā parisā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap8:10.4'><span class='pli'>mahānelaṁ dharīyati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap8:11.1'><span class='pli'>Sattatidevarājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap8:11.2'><span class='pli'>mahesittamakārayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap8:11.3'><span class='pli'>Sabbattha issarā hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap8:11.4'><span class='pli'>saṁsarāmi bhavābhave. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap8:12.1'><span class='pli'>Tesaṭṭhi cakkavattīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap8:12.2'><span class='pli'>mahesittamakārayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap8:12.3'><span class='pli'>Sabbe mamanuvattanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap8:12.4'><span class='pli'>ādeyyavacanā ahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap8:13.1'><span class='pli'>Uppalasseva me vaṇṇo, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap8:13.2'><span class='pli'>gandho ceva pavāyati; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap8:13.3'><span class='pli'>Dubbaṇṇiyaṁ na jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap8:13.4'><span class='pli'>buddhapūjāyidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap8:14.1'><span class='pli'>Iddhipādesu kusalā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap8:14.2'><span class='pli'>bojjhaṅgabhāvanāratā; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap8:14.3'><span class='pli'>Abhiññāpāramippattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap8:14.4'><span class='pli'>buddhapūjāyidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap8:15.1'><span class='pli'>Satipaṭṭhānakusalā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap8:15.2'><span class='pli'>samādhijhānagocarā; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap8:15.3'><span class='pli'>Sammappadhānamanuyuttā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap8:15.4'><span class='pli'>buddhapūjāyidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap8:16.1'><span class='pli'>Vīriyaṁ me dhuradhorayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap8:16.2'><span class='pli'>yogakkhemādhivāhanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap8:16.3'><span class='pli'>Sabbāsavā parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap8:16.4'><span class='pli'>natthi dāni punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap8:17.1'><span class='pli'>Ekatiṁse ito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap8:17.2'><span class='pli'>yaṁ pupphamabhipūjayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap8:17.3'><span class='pli'>Duggatiṁ nābhijānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap8:17.4'><span class='pli'>buddhapūjāyidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap8:18.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap8:18.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap8:18.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap8:19.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap8:19.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap8:19.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap8:20.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap8:20.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap8:20.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='21.1' data-uid='thi-ap8:21.1'><span class='pli'>Itthaṁ sudaṁ sattuppalamālikā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='22.1' data-uid='thi-ap8:22.1'><span class='pli'>Sattuppalamālikātheriyāpadānaṁ aṭṭhamaṁ. </span></span></p></article>\n"
  },
  "thi-ap9": {
    "previous": "thi-ap8",
    "next": "thi-ap10",
    "content": "<article id='thi-ap9'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap9:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap9:0.2'><span class='pli'>Sumedhāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap9:0.3'><span class='pli'>Pañcadīpikātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap9:1.1'><span class='pli'>“Nagare haṁsavatiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap9:1.2'><span class='pli'>cārikī āsahaṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap9:1.3'><span class='pli'>Ārāmena ca ārāmaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap9:1.4'><span class='pli'>carāmi kusalatthikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap9:2.1'><span class='pli'>Kāḷapakkhamhi divase, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap9:2.2'><span class='pli'>addasaṁ bodhimuttamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap9:2.3'><span class='pli'>Tattha cittaṁ pasādetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap9:2.4'><span class='pli'>bodhimūle nisīdahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap9:3.1'><span class='pli'>Garucittaṁ upaṭṭhetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap9:3.2'><span class='pli'>sire katvāna añjaliṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap9:3.3'><span class='pli'>Somanassaṁ pavedetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap9:3.4'><span class='pli'>evaṁ cintesi tāvade. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap9:4.1'><span class='pli'>‘Yadi buddho amitaguṇo, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap9:4.2'><span class='pli'>asamappaṭipuggalo; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap9:4.3'><span class='pli'>Dassetu pāṭihīraṁ me, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap9:4.4'><span class='pli'>bodhi obhāsatu ayaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap9:5.1'><span class='pli'>Saha āvajjite mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap9:5.2'><span class='pli'>bodhi pajjali tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap9:5.3'><span class='pli'>Sabbasoṇṇamayā āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap9:5.4'><span class='pli'>disā sabbā virocati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap9:6.1'><span class='pli'>Sattarattindivaṁ tattha, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap9:6.2'><span class='pli'>bodhimūle nisīdahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap9:6.3'><span class='pli'>Sattame divase patte, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap9:6.4'><span class='pli'>dīpapūjaṁ akāsahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap9:7.1'><span class='pli'>Āsanaṁ parivāretvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap9:7.2'><span class='pli'>pañca dīpāni pajjaluṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap9:7.3'><span class='pli'>Yāva udeti sūriyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap9:7.4'><span class='pli'>dīpā me pajjaluṁ tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap9:8.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap9:8.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap9:8.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap9:8.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap9:9.1'><span class='pli'>Tattha me sukataṁ byamhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap9:9.2'><span class='pli'>pañcadīpāti vuccati; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap9:9.3'><span class='pli'>Saṭṭhiyojanamubbedhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap9:9.4'><span class='pli'>tiṁsayojanavitthataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap9:10.1'><span class='pli'>Asaṅkhiyāni dīpāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap9:10.2'><span class='pli'>parivāre jalanti me; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap9:10.3'><span class='pli'>Yāvatā devabhavanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap9:10.4'><span class='pli'>dīpālokena jotati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap9:11.1'><span class='pli'>Parammukhā nisīditvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap9:11.2'><span class='pli'>yadi icchāmi passituṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap9:11.3'><span class='pli'>Uddhaṁ adho ca tiriyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap9:11.4'><span class='pli'>sabbaṁ passāmi cakkhunā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap9:12.1'><span class='pli'>Yāvatā abhikaṅkhāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap9:12.2'><span class='pli'>daṭṭhuṁ sugataduggate; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap9:12.3'><span class='pli'>Tattha āvaraṇaṁ natthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap9:12.4'><span class='pli'>rukkhesu pabbatesu vā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap9:13.1'><span class='pli'>Asīti devarājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap9:13.2'><span class='pli'>mahesittamakārayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap9:13.3'><span class='pli'>Satānaṁ cakkavattīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap9:13.4'><span class='pli'>mahesittamakārayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap9:14.1'><span class='pli'>Yaṁ yaṁ yonupapajjāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap9:14.2'><span class='pli'>devattaṁ atha mānusaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap9:14.3'><span class='pli'>Dīpasatasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap9:14.4'><span class='pli'>parivāre jalanti me. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap9:15.1'><span class='pli'>Devalokā cavitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap9:15.2'><span class='pli'>uppajjiṁ mātukucchiyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap9:15.3'><span class='pli'>Mātukucchigatā santī, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap9:15.4'><span class='pli'>akkhi me na nimīlati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap9:16.1'><span class='pli'>Dīpasatasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap9:16.2'><span class='pli'>puññakammasamaṅgitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap9:16.3'><span class='pli'>Jalanti sūtikāgehe, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap9:16.4'><span class='pli'>pañcadīpānidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap9:17.1'><span class='pli'>Pacchime bhave sampatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap9:17.2'><span class='pli'>mānasaṁ vinivattayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap9:17.3'><span class='pli'>Ajarāmataṁ sītibhāvaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap9:17.4'><span class='pli'>nibbānaṁ phassayiṁ ahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap9:18.1'><span class='pli'>Jātiyā sattavassāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap9:18.2'><span class='pli'>arahattamapāpuṇiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap9:18.3'><span class='pli'>Upasampādayī buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap9:18.4'><span class='pli'>guṇamaññāya gotamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap9:19.1'><span class='pli'>Maṇḍape rukkhamūle vā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap9:19.2'><span class='pli'>pāsādesu guhāsu vā; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap9:19.3'><span class='pli'>Suññāgāre vasantiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap9:19.4'><span class='pli'>pañcadīpā jalanti me. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap9:20.1'><span class='pli'>Dibbacakkhuvisuddhaṁ me, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap9:20.2'><span class='pli'>samādhikusalā ahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap9:20.3'><span class='pli'>Abhiññāpāramippattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap9:20.4'><span class='pli'>pañcadīpānidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap9:21.1'><span class='pli'>Sabbavositavosānā, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap9:21.2'><span class='pli'>katakiccā anāsavā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap9:21.3'><span class='pli'>Pañcadīpā mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap9:21.4'><span class='pli'>pāde vandāmi cakkhuma. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap9:22.1'><span class='pli'>Satasahassito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap9:22.2'><span class='pli'>yaṁ dīpamadadiṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap9:22.3'><span class='pli'>Duggatiṁ nābhijānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap9:22.4'><span class='pli'>pañcadīpānidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap9:23.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap9:23.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap9:23.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap9:24.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap9:24.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap9:24.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='thi-ap9:25.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='thi-ap9:25.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='thi-ap9:25.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='26.1' data-uid='thi-ap9:26.1'><span class='pli'>Itthaṁ sudaṁ pañcadīpikā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='27.1' data-uid='thi-ap9:27.1'><span class='pli'>Pañcadīpikātheriyāpadānaṁ navamaṁ. </span></span></p></article>\n"
  },
  "thi-ap10": {
    "previous": "thi-ap9",
    "next": "thi-ap11",
    "content": "<article id='thi-ap10'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap10:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap10:0.2'><span class='pli'>Sumedhāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap10:0.3'><span class='pli'>Udakadāyikātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap10:1.1'><span class='pli'>“Nagare bandhumatiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap10:1.2'><span class='pli'>ahosiṁ udahārikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap10:1.3'><span class='pli'>Udahārena jīvāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap10:1.4'><span class='pli'>tena posemi dārake. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap10:2.1'><span class='pli'>Deyyadhammo ca me natthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap10:2.2'><span class='pli'>puññakkhette anuttare; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap10:2.3'><span class='pli'>Koṭṭhakaṁ upasaṅkamma, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap10:2.4'><span class='pli'>udakaṁ paṭṭhapesahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap10:3.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap10:3.2'><span class='pli'>tāvatiṁsamagacchahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap10:3.3'><span class='pli'>Tattha me sukataṁ byamhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap10:3.4'><span class='pli'>udahārena nimmitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap10:4.1'><span class='pli'>Accharānaṁ sahassassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap10:4.2'><span class='pli'>ahañhi pavarā tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap10:4.3'><span class='pli'>Dasaṭṭhānehi tā sabbā, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap10:4.4'><span class='pli'>abhibhomi sadā ahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap10:5.1'><span class='pli'>Paññāsaṁ devarājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap10:5.2'><span class='pli'>mahesittamakārayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap10:5.3'><span class='pli'>Vīsaticakkavattīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap10:5.4'><span class='pli'>mahesittamakārayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap10:6.1'><span class='pli'>Duve bhave saṁsarāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap10:6.2'><span class='pli'>devatte atha mānuse; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap10:6.3'><span class='pli'>Duggatiṁ nābhijānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap10:6.4'><span class='pli'>dakadānassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap10:7.1'><span class='pli'>Pabbatagge dumagge vā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap10:7.2'><span class='pli'>antalikkhe ca bhūmiyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap10:7.3'><span class='pli'>Yadā udakamicchāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap10:7.4'><span class='pli'>khippaṁ paṭilabhāmahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap10:8.1'><span class='pli'>Avuṭṭhikā disā natthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap10:8.2'><span class='pli'>santattā kuthitāpi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap10:8.3'><span class='pli'>Mama saṅkappamaññāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap10:8.4'><span class='pli'>mahāmegho pavassati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap10:9.1'><span class='pli'>Kadāci nīyamānāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap10:9.2'><span class='pli'>ñātisaṅghena me tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap10:9.3'><span class='pli'>Yadā icchāmahaṁ vassaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap10:9.4'><span class='pli'>mahāmegho ajāyatha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap10:10.1'><span class='pli'>Uṇhaṁ vā pariḷāho vā, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap10:10.2'><span class='pli'>sarīre me na vijjati; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap10:10.3'><span class='pli'>Kāye ca me rajo natthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap10:10.4'><span class='pli'>dakadānassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap10:11.1'><span class='pli'>Visuddhamanasā ajja, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap10:11.2'><span class='pli'>apetamanapāpikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap10:11.3'><span class='pli'>Sabbāsavaparikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap10:11.4'><span class='pli'>natthi dāni punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap10:12.1'><span class='pli'>Ekanavutito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap10:12.2'><span class='pli'>yaṁ dakaṁ adadiṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap10:12.3'><span class='pli'>Duggatiṁ nābhijānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap10:12.4'><span class='pli'>dakadānassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap10:13.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap10:13.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap10:13.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap10:14.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap10:14.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap10:14.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap10:15.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap10:15.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap10:15.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='16.1' data-uid='thi-ap10:16.1'><span class='pli'>Itthaṁ sudaṁ udakadāyikā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='17.1' data-uid='thi-ap10:17.1'><span class='pli'>Udakadāyikātheriyāpadānaṁ dasamaṁ. </span></span></p>\n<p class='endsection'><span class='segment' id='18.1' data-uid='thi-ap10:18.1'><span class='pli'>Sumedhāvaggo paṭhamo. </span></span></p>\n<p class='uddana-intro'><span class='segment' id='19.1' data-uid='thi-ap10:19.1'><span class='pli'>Tassuddānaṁ </span></span></p>\n<blockquote class='uddanagatha'><p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap10:20.1'><span class='pli'>Sumedhā mekhalādāyī, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap10:20.2'><span class='pli'>maṇḍapaṁ saṅkamaṁ dadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap10:20.3'><span class='pli'>Naḷamālī piṇḍadadā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap10:20.4'><span class='pli'>kaṭacchu uppalappadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap10:21.1'><span class='pli'>Dīpadā dakadā ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap10:21.2'><span class='pli'>gāthāyo gaṇitā iha; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap10:21.3'><span class='pli'>Ekagāthāsatañceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap10:21.4'><span class='pli'>tiṁsati ca taduttari. </span></span></span></p></blockquote></article>\n"
  },
  "thi-ap11": {
    "previous": "thi-ap10",
    "next": "thi-ap12",
    "content": "<article id='thi-ap11'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap11:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap11:0.2'><span class='pli'>Ekūposathikavagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap11:0.3'><span class='pli'>1 Ekūposathikātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap11:1.1'><span class='pli'>“Nagare bandhumatiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap11:1.2'><span class='pli'>bandhumā nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap11:1.3'><span class='pli'>Divase puṇṇamāya so, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap11:1.4'><span class='pli'>upavasi uposathaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap11:2.1'><span class='pli'>Ahaṁ tena samayena, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap11:2.2'><span class='pli'>kumbhadāsī ahaṁ tahiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap11:2.3'><span class='pli'>Disvā sarājakaṁ senaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap11:2.4'><span class='pli'>evāhaṁ cintayiṁ tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap11:3.1'><span class='pli'>‘Rājāpi rajjaṁ chaḍḍetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap11:3.2'><span class='pli'>upavasi uposathaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap11:3.3'><span class='pli'>Saphalaṁ nūna taṁ kammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap11:3.4'><span class='pli'>janakāyo pamodito’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap11:4.1'><span class='pli'>Yoniso paccavekkhitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap11:4.2'><span class='pli'>duggaccañca daliddataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap11:4.3'><span class='pli'>Mānasaṁ sampahaṁsitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap11:4.4'><span class='pli'>upavasiṁ uposathaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap11:5.1'><span class='pli'>Ahaṁ uposathaṁ katvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap11:5.2'><span class='pli'>sammāsambuddhasāsane; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap11:5.3'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap11:5.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap11:6.1'><span class='pli'>Tattha me sukataṁ byamhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap11:6.2'><span class='pli'>ubbhayojanamuggataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap11:6.3'><span class='pli'>Kūṭāgāravarūpetaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap11:6.4'><span class='pli'>mahāsanasubhūsitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap11:7.1'><span class='pli'>Accharā satasahassā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap11:7.2'><span class='pli'>upatiṭṭhanti maṁ sadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap11:7.3'><span class='pli'>Aññe deve atikkamma, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap11:7.4'><span class='pli'>atirocāmi sabbadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap11:8.1'><span class='pli'>Catusaṭṭhi devarājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap11:8.2'><span class='pli'>mahesittamakārayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap11:8.3'><span class='pli'>Tesaṭṭhi cakkavattīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap11:8.4'><span class='pli'>mahesittamakārayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap11:9.1'><span class='pli'>Suvaṇṇavaṇṇā hutvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap11:9.2'><span class='pli'>bhavesu saṁsarāmahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap11:9.3'><span class='pli'>Sabbattha pavarā homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap11:9.4'><span class='pli'>uposathassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap11:10.1'><span class='pli'>Hatthiyānaṁ assayānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap11:10.2'><span class='pli'>rathayānañca sīvikaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap11:10.3'><span class='pli'>Labhāmi sabbamevetaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap11:10.4'><span class='pli'>uposathassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap11:11.1'><span class='pli'>Soṇṇamayaṁ rūpimayaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap11:11.2'><span class='pli'>athopi phalikāmayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap11:11.3'><span class='pli'>Lohitaṅgamayañceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap11:11.4'><span class='pli'>sabbaṁ paṭilabhāmahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap11:12.1'><span class='pli'>Koseyyakambaliyāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap11:12.2'><span class='pli'>khomakappāsikāni ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap11:12.3'><span class='pli'>Mahagghāni ca vatthāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap11:12.4'><span class='pli'>sabbaṁ paṭilabhāmahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap11:13.1'><span class='pli'>Annaṁ pānaṁ khādanīyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap11:13.2'><span class='pli'>vatthasenāsanāni ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap11:13.3'><span class='pli'>Sabbametaṁ paṭilabhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap11:13.4'><span class='pli'>uposathassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap11:14.1'><span class='pli'>Varagandhañca mālañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap11:14.2'><span class='pli'>cuṇṇakañca vilepanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap11:14.3'><span class='pli'>Sabbametaṁ paṭilabhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap11:14.4'><span class='pli'>uposathassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap11:15.1'><span class='pli'>Kūṭāgārañca pāsādaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap11:15.2'><span class='pli'>maṇḍapaṁ hammiyaṁ guhaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap11:15.3'><span class='pli'>Sabbametaṁ paṭilabhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap11:15.4'><span class='pli'>uposathassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap11:16.1'><span class='pli'>Jātiyā sattavassāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap11:16.2'><span class='pli'>pabbajiṁ anagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap11:16.3'><span class='pli'>Aḍḍhamāse asampatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap11:16.4'><span class='pli'>arahattamapāpuṇiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap11:17.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap11:17.2'><span class='pli'>bhavā sabbe samūhatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap11:17.3'><span class='pli'>Sabbāsavā parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap11:17.4'><span class='pli'>natthi dāni punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap11:18.1'><span class='pli'>Ekanavutito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap11:18.2'><span class='pli'>yaṁ kammamakariṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap11:18.3'><span class='pli'>Duggatiṁ nābhijānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap11:18.4'><span class='pli'>uposathassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap11:19.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap11:19.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap11:19.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap11:20.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap11:20.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap11:20.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap11:21.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap11:21.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap11:21.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='22.1' data-uid='thi-ap11:22.1'><span class='pli'>Itthaṁ sudaṁ ekūposathikā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='23.1' data-uid='thi-ap11:23.1'><span class='pli'>Ekūposathikātheriyāpadānaṁ paṭhamaṁ. </span></span></p></article>\n"
  },
  "thi-ap12": {
    "previous": "thi-ap11",
    "next": "thi-ap13",
    "content": "<article id='thi-ap12'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap12:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap12:0.2'><span class='pli'>Ekūposathikavagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap12:0.3'><span class='pli'>2 Saḷalapupphikātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap12:1.1'><span class='pli'>“Candabhāgānadītīre, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap12:1.2'><span class='pli'>ahosiṁ kinnarī tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap12:1.3'><span class='pli'>Addasāhaṁ devadevaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap12:1.4'><span class='pli'>caṅkamantaṁ narāsabhaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap12:2.1'><span class='pli'>Ocinitvāna saḷalaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap12:2.2'><span class='pli'>buddhaseṭṭhassadāsahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap12:2.3'><span class='pli'>Upasiṅghi mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap12:2.4'><span class='pli'>saḷalaṁ devagandhikaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap12:3.1'><span class='pli'>Paṭiggahetvā sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap12:3.2'><span class='pli'>vipassī lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap12:3.3'><span class='pli'>Upasiṅghi mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap12:3.4'><span class='pli'>pekkhamānāya me tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap12:4.1'><span class='pli'>Añjaliṁ paggahetvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap12:4.2'><span class='pli'>vanditvā dvipaduttamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap12:4.3'><span class='pli'>Sakaṁ cittaṁ pasādetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap12:4.4'><span class='pli'>tato pabbatamāruhiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap12:5.1'><span class='pli'>Ekanavutito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap12:5.2'><span class='pli'>yaṁ pupphamadadiṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap12:5.3'><span class='pli'>Duggatiṁ nābhijānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap12:5.4'><span class='pli'>buddhapūjāyidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap12:6.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap12:6.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap12:6.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap12:7.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap12:7.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap12:7.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap12:8.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap12:8.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap12:8.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='9.1' data-uid='thi-ap12:9.1'><span class='pli'>Itthaṁ sudaṁ saḷalapupphikā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='10.1' data-uid='thi-ap12:10.1'><span class='pli'>Saḷalapupphikātheriyāpadānaṁ dutiyaṁ. </span></span></p></article>\n"
  },
  "thi-ap13": {
    "previous": "thi-ap12",
    "next": "thi-ap14",
    "content": "<article id='thi-ap13'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap13:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap13:0.2'><span class='pli'>Ekūposathikavagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap13:0.3'><span class='pli'>Modakadāyikātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap13:1.1'><span class='pli'>“Nagare bandhumatiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap13:1.2'><span class='pli'>kumbhadāsī ahosahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap13:1.3'><span class='pli'>Mama bhāgaṁ gahetvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap13:1.4'><span class='pli'>gacchaṁ udakahārikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap13:2.1'><span class='pli'>Panthamhi samaṇaṁ disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap13:2.2'><span class='pli'>santacittaṁ samāhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap13:2.3'><span class='pli'>Pasannacittā sumanā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap13:2.4'><span class='pli'>modake tīṇidāsahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap13:3.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap13:3.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap13:3.3'><span class='pli'>Ekanavutikappāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap13:3.4'><span class='pli'>vinipātaṁ nagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap13:4.1'><span class='pli'>Sampatti taṁ karitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap13:4.2'><span class='pli'>sabbaṁ anubhaviṁ ahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap13:4.3'><span class='pli'>Modake tīṇi datvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap13:4.4'><span class='pli'>pattāhaṁ acalaṁ padaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap13:5.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap13:5.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap13:5.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap13:6.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap13:6.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap13:6.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap13:7.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap13:7.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap13:7.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='8.1' data-uid='thi-ap13:8.1'><span class='pli'>Itthaṁ sudaṁ modakadāyikā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='9.1' data-uid='thi-ap13:9.1'><span class='pli'>Modakadāyikātheriyāpadānaṁ tatiyaṁ. </span></span></p></article>\n"
  },
  "thi-ap14": {
    "previous": "thi-ap13",
    "next": "thi-ap15",
    "content": "<article id='thi-ap14'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap14:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap14:0.2'><span class='pli'>Ekūposathikavagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap14:0.3'><span class='pli'>Ekāsanadāyikātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap14:1.1'><span class='pli'>“Nagare haṁsavatiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap14:1.2'><span class='pli'>ahosiṁ bālikā tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap14:1.3'><span class='pli'>Mātā ca me pitā ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap14:1.4'><span class='pli'>kammantaṁ agamaṁsu te. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap14:2.1'><span class='pli'>Majjhanhikamhi sūriye, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap14:2.2'><span class='pli'>addasaṁ samaṇaṁ ahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap14:2.3'><span class='pli'>Vīthiyā anugacchantaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap14:2.4'><span class='pli'>āsanaṁ paññapesahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap14:3.1'><span class='pli'>Gonakāvikatikāhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap14:3.2'><span class='pli'>paññapetvā mamāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap14:3.3'><span class='pli'>Pasannacittā sumanā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap14:3.4'><span class='pli'>idaṁ vacanamabraviṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap14:4.1'><span class='pli'>‘Santattā kuthitā bhūmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap14:4.2'><span class='pli'>sūro majjhanhike ṭhito; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap14:4.3'><span class='pli'>Mālutā ca na vāyanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap14:4.4'><span class='pli'>kālo cevettha mehiti. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap14:5.1'><span class='pli'>Paññattamāsanamidaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap14:5.2'><span class='pli'>tavatthāya mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap14:5.3'><span class='pli'>Anukampaṁ upādāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap14:5.4'><span class='pli'>nisīda mama āsane’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap14:6.1'><span class='pli'>Nisīdi tattha samaṇo, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap14:6.2'><span class='pli'>sudanto suddhamānaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap14:6.3'><span class='pli'>Tassa pattaṁ gahetvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap14:6.4'><span class='pli'>yathārandhaṁ adāsahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap14:7.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap14:7.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap14:7.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap14:7.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap14:8.1'><span class='pli'>Tattha me sukataṁ byamhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap14:8.2'><span class='pli'>āsanena sunimmitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap14:8.3'><span class='pli'>Saṭṭhiyojanamubbedhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap14:8.4'><span class='pli'>tiṁsayojanavitthataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap14:9.1'><span class='pli'>Soṇṇamayā maṇimayā, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap14:9.2'><span class='pli'>athopi phalikāmayā; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap14:9.3'><span class='pli'>Lohitaṅgamayā ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap14:9.4'><span class='pli'>pallaṅkā vividhā mama. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap14:10.1'><span class='pli'>Tūlikāvikatikāhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap14:10.2'><span class='pli'>kaṭṭissacittakāhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap14:10.3'><span class='pli'>Uddaekantalomī ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap14:10.4'><span class='pli'>pallaṅkā me susaṇṭhitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap14:11.1'><span class='pli'>Yadā icchāmi gamanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap14:11.2'><span class='pli'>hāsakhiḍḍasamappitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap14:11.3'><span class='pli'>Saha pallaṅkaseṭṭhena, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap14:11.4'><span class='pli'>gacchāmi mama patthitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap14:12.1'><span class='pli'>Asīti devarājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap14:12.2'><span class='pli'>mahesittamakārayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap14:12.3'><span class='pli'>Sattati cakkavattīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap14:12.4'><span class='pli'>mahesittamakārayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap14:13.1'><span class='pli'>Bhavābhave saṁsarantī, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap14:13.2'><span class='pli'>mahābhogaṁ labhāmahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap14:13.3'><span class='pli'>Bhoge me ūnatā natthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap14:13.4'><span class='pli'>ekāsanassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap14:14.1'><span class='pli'>Duve bhave saṁsarāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap14:14.2'><span class='pli'>devatte atha mānuse; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap14:14.3'><span class='pli'>Aññe bhave na jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap14:14.4'><span class='pli'>ekāsanassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap14:15.1'><span class='pli'>Duve kule pajāyāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap14:15.2'><span class='pli'>khattiye cāpi brāhmaṇe; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap14:15.3'><span class='pli'>Uccākulīnā sabbattha, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap14:15.4'><span class='pli'>ekāsanassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap14:16.1'><span class='pli'>Domanassaṁ na jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap14:16.2'><span class='pli'>cittasantāpanaṁ mama; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap14:16.3'><span class='pli'>Vevaṇṇiyaṁ na jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap14:16.4'><span class='pli'>ekāsanassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap14:17.1'><span class='pli'>Dhātiyo maṁ upaṭṭhanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap14:17.2'><span class='pli'>khujjā celāpikā bahū; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap14:17.3'><span class='pli'>Aṅkena aṅkaṁ gacchāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap14:17.4'><span class='pli'>ekāsanassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap14:18.1'><span class='pli'>Aññā nhāpenti bhojenti, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap14:18.2'><span class='pli'>aññā ramenti maṁ sadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap14:18.3'><span class='pli'>Aññā gandhaṁ vilimpanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap14:18.4'><span class='pli'>ekāsanassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap14:19.1'><span class='pli'>Maṇḍape rukkhamūle vā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap14:19.2'><span class='pli'>suññāgāre vasantiyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap14:19.3'><span class='pli'>Mama saṅkappamaññāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap14:19.4'><span class='pli'>pallaṅko upatiṭṭhati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap14:20.1'><span class='pli'>Ayaṁ pacchimako mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap14:20.2'><span class='pli'>carimo vattate bhavo; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap14:20.3'><span class='pli'>Ajjāpi rajjaṁ chaḍḍetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap14:20.4'><span class='pli'>pabbajiṁ anagāriyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap14:21.1'><span class='pli'>Satasahassito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap14:21.2'><span class='pli'>yaṁ dānamadadiṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap14:21.3'><span class='pli'>Duggatiṁ nābhijānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap14:21.4'><span class='pli'>ekāsanassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap14:22.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap14:22.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap14:22.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap14:23.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap14:23.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap14:23.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap14:24.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap14:24.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap14:24.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='25.1' data-uid='thi-ap14:25.1'><span class='pli'>Itthaṁ sudaṁ ekāsanadāyikā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='26.1' data-uid='thi-ap14:26.1'><span class='pli'>Ekāsanadāyikātheriyāpadānaṁ catutthaṁ. </span></span></p></article>\n"
  },
  "thi-ap15": {
    "previous": "thi-ap14",
    "next": "thi-ap16",
    "content": "<article id='thi-ap15'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap15:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap15:0.2'><span class='pli'>Ekūposathikavagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap15:0.3'><span class='pli'>Pañcadīpadāyikātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap15:1.1'><span class='pli'>“Nagare haṁsavatiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap15:1.2'><span class='pli'>cārikī āsahaṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap15:1.3'><span class='pli'>Ārāmena ca ārāmaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap15:1.4'><span class='pli'>carāmi kusalatthikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap15:2.1'><span class='pli'>Kāḷapakkhamhi divase, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap15:2.2'><span class='pli'>addasaṁ bodhimuttamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap15:2.3'><span class='pli'>Tattha cittaṁ pasādetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap15:2.4'><span class='pli'>bodhimūle nisīdahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap15:3.1'><span class='pli'>Garucittaṁ upaṭṭhetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap15:3.2'><span class='pli'>sire katvāna añjaliṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap15:3.3'><span class='pli'>Somanassaṁ pavedetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap15:3.4'><span class='pli'>evaṁ cintesi tāvade. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap15:4.1'><span class='pli'>‘Yadi buddho amitaguṇo, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap15:4.2'><span class='pli'>asamappaṭipuggalo; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap15:4.3'><span class='pli'>Dassetu pāṭihīraṁ me, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap15:4.4'><span class='pli'>bodhi obhāsatu ayaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap15:5.1'><span class='pli'>Saha āvajjite mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap15:5.2'><span class='pli'>bodhi pajjali tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap15:5.3'><span class='pli'>Sabbasoṇṇamayā āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap15:5.4'><span class='pli'>disā sabbā virocati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap15:6.1'><span class='pli'>Sattarattindivaṁ tattha, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap15:6.2'><span class='pli'>bodhimūle nisīdahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap15:6.3'><span class='pli'>Sattame divase patte, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap15:6.4'><span class='pli'>dīpapūjaṁ akāsahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap15:7.1'><span class='pli'>Āsanaṁ parivāretvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap15:7.2'><span class='pli'>pañca dīpāni pajjaluṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap15:7.3'><span class='pli'>Yāva udeti sūriyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap15:7.4'><span class='pli'>dīpā me pajjaluṁ tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap15:8.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap15:8.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap15:8.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap15:8.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap15:9.1'><span class='pli'>Tattha me sukataṁ byamhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap15:9.2'><span class='pli'>pañcadīpāti vuccati; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap15:9.3'><span class='pli'>Saṭṭhiyojanamubbedhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap15:9.4'><span class='pli'>tiṁsayojanavitthataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap15:10.1'><span class='pli'>Asaṅkhiyāni dīpāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap15:10.2'><span class='pli'>parivāre jaliṁsu me; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap15:10.3'><span class='pli'>Yāvatā devabhavanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap15:10.4'><span class='pli'>dīpālokena jotati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap15:11.1'><span class='pli'>Parammukhā nisīditvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap15:11.2'><span class='pli'>yadi icchāmi passituṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap15:11.3'><span class='pli'>Uddhaṁ adho ca tiriyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap15:11.4'><span class='pli'>sabbaṁ passāmi cakkhunā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap15:12.1'><span class='pli'>Yāvatā abhikaṅkhāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap15:12.2'><span class='pli'>daṭṭhuṁ sugataduggate; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap15:12.3'><span class='pli'>Tattha āvaraṇaṁ natthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap15:12.4'><span class='pli'>rukkhesu pabbatesu vā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap15:13.1'><span class='pli'>Asīti devarājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap15:13.2'><span class='pli'>mahesittamakārayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap15:13.3'><span class='pli'>Satānaṁ cakkavattīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap15:13.4'><span class='pli'>mahesittamakārayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap15:14.1'><span class='pli'>Yaṁ yaṁ yonupapajjāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap15:14.2'><span class='pli'>devattaṁ atha mānusaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap15:14.3'><span class='pli'>Dīpasatasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap15:14.4'><span class='pli'>parivāre jalanti me. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap15:15.1'><span class='pli'>Devalokā cavitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap15:15.2'><span class='pli'>uppajjiṁ mātukucchiyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap15:15.3'><span class='pli'>Mātukucchigatā santī, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap15:15.4'><span class='pli'>akkhi me na nimīlati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap15:16.1'><span class='pli'>Dīpasatasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap15:16.2'><span class='pli'>puññakammasamaṅgitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap15:16.3'><span class='pli'>Jalanti sūtikāgehe, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap15:16.4'><span class='pli'>pañcadīpānidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap15:17.1'><span class='pli'>Pacchime bhave sampatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap15:17.2'><span class='pli'>mānasaṁ vinivattayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap15:17.3'><span class='pli'>Ajarāmataṁ sītibhāvaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap15:17.4'><span class='pli'>nibbānaṁ phassayiṁ ahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap15:18.1'><span class='pli'>Jātiyā sattavassāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap15:18.2'><span class='pli'>arahattamapāpuṇiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap15:18.3'><span class='pli'>Upasampādayī buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap15:18.4'><span class='pli'>guṇamaññāya gotamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap15:19.1'><span class='pli'>Maṇḍape rukkhamūle vā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap15:19.2'><span class='pli'>suññāgāre vasantiyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap15:19.3'><span class='pli'>Sadā pajjalate dīpaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap15:19.4'><span class='pli'>pañcadīpānidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap15:20.1'><span class='pli'>Dibbacakkhuvisuddhaṁ me, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap15:20.2'><span class='pli'>samādhikusalā ahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap15:20.3'><span class='pli'>Abhiññāpāramippattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap15:20.4'><span class='pli'>pañcadīpānidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap15:21.1'><span class='pli'>Sabbavositavosānā, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap15:21.2'><span class='pli'>katakiccā anāsavā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap15:21.3'><span class='pli'>Pañcadīpā mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap15:21.4'><span class='pli'>pāde vandāmi cakkhuma. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap15:22.1'><span class='pli'>Satasahassito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap15:22.2'><span class='pli'>yaṁ dīpamadadiṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap15:22.3'><span class='pli'>Duggatiṁ nābhijānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap15:22.4'><span class='pli'>pañcadīpānidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap15:23.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap15:23.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap15:23.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap15:24.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap15:24.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap15:24.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='thi-ap15:25.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='thi-ap15:25.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='thi-ap15:25.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='26.1' data-uid='thi-ap15:26.1'><span class='pli'>Itthaṁ sudaṁ pañcadīpadāyikā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='27.1' data-uid='thi-ap15:27.1'><span class='pli'>Pañcadīpadāyikātheriyāpadānaṁ pañcamaṁ. </span></span></p></article>\n"
  },
  "thi-ap16": {
    "previous": "thi-ap15",
    "next": "thi-ap17",
    "content": "<article id='thi-ap16'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap16:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap16:0.2'><span class='pli'>Ekūposathikavagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap16:0.3'><span class='pli'>6 Naḷamālikātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap16:1.1'><span class='pli'>“Candabhāgānadītīre, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap16:1.2'><span class='pli'>ahosiṁ kinnarī tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap16:1.3'><span class='pli'>Addasaṁ virajaṁ buddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap16:1.4'><span class='pli'>sayambhuṁ aparājitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap16:2.1'><span class='pli'>Pasannacittā sumanā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap16:2.2'><span class='pli'>vedajātā katañjalī; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap16:2.3'><span class='pli'>Naḷamālaṁ gahetvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap16:2.4'><span class='pli'>sayambhuṁ abhipūjayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap16:3.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap16:3.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap16:3.3'><span class='pli'>Jahitvā kinnarīdehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap16:3.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap16:4.1'><span class='pli'>Chattiṁsadevarājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap16:4.2'><span class='pli'>mahesittamakārayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap16:4.3'><span class='pli'>Manasā patthitaṁ mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap16:4.4'><span class='pli'>nibbattati yathicchitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap16:5.1'><span class='pli'>Dasannaṁ cakkavattīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap16:5.2'><span class='pli'>mahesittamakārayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap16:5.3'><span class='pli'>Ocitattāva hutvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap16:5.4'><span class='pli'>saṁsarāmi bhavesvahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap16:6.1'><span class='pli'>Kusalaṁ vijjate mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap16:6.2'><span class='pli'>pabbajiṁ anagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap16:6.3'><span class='pli'>Pūjārahā ahaṁ ajja, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap16:6.4'><span class='pli'>sakyaputtassa sāsane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap16:7.1'><span class='pli'>Visuddhamanasā ajja, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap16:7.2'><span class='pli'>apetamanapāpikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap16:7.3'><span class='pli'>Sabbāsavā parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap16:7.4'><span class='pli'>natthi dāni punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap16:8.1'><span class='pli'>Catunnavutito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap16:8.2'><span class='pli'>yaṁ buddhamabhipūjayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap16:8.3'><span class='pli'>Duggatiṁ nābhijānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap16:8.4'><span class='pli'>naḷamālāyidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap16:9.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap16:9.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap16:9.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap16:10.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap16:10.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap16:10.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap16:11.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap16:11.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap16:11.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='12.1' data-uid='thi-ap16:12.1'><span class='pli'>Itthaṁ sudaṁ naḷamālikā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='13.1' data-uid='thi-ap16:13.1'><span class='pli'>Naḷamālikātheriyāpadānaṁ chaṭṭhaṁ. </span></span></p></article>\n"
  },
  "thi-ap17": {
    "previous": "thi-ap16",
    "next": "thi-ap18",
    "content": "<article id='thi-ap17'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap17:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap17:0.2'><span class='pli'>Ekūposathikavagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap17:0.3'><span class='pli'>Mahāpajāpatigotamītherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap17:1.1'><span class='pli'>Ekadā lokapajjoto, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap17:1.2'><span class='pli'>vesāliyaṁ mahāvane; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap17:1.3'><span class='pli'>Kūṭāgāre susālāyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap17:1.4'><span class='pli'>vasate narasārathi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap17:2.1'><span class='pli'>Tadā jinassa mātucchā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap17:2.2'><span class='pli'>mahāgotami bhikkhunī; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap17:2.3'><span class='pli'>Tahiṁ kate pure ramme, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap17:2.4'><span class='pli'>vasī bhikkhunupassaye. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap17:3.1'><span class='pli'>Bhikkhunīhi vimuttāhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap17:3.2'><span class='pli'>satehi saha pañcahi; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap17:3.3'><span class='pli'>Rahogatāya tassevaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap17:3.4'><span class='pli'>citassāsi vitakkitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap17:4.1'><span class='pli'>“Buddhassa parinibbānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap17:4.2'><span class='pli'>sāvakaggayugassa vā; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap17:4.3'><span class='pli'>Rāhulānandanandānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap17:4.4'><span class='pli'>nāhaṁ lacchāmi passituṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap17:5.1'><span class='pli'>Buddhassa parinibbānā, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap17:5.2'><span class='pli'>sāvakaggayugassa vā; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap17:5.3'><span class='pli'>Mahākassapanandānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap17:5.4'><span class='pli'>ānandarāhulāna ca. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap17:6.1'><span class='pli'>Paṭikaccāyusaṅkhāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap17:6.2'><span class='pli'>osajjitvāna nibbutiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap17:6.3'><span class='pli'>Gaccheyyaṁ lokanāthena, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap17:6.4'><span class='pli'>anuññātā mahesinā”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap17:7.1'><span class='pli'>Tathā pañcasatānampi, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap17:7.2'><span class='pli'>bhikkhunīnaṁ vitakkitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap17:7.3'><span class='pli'>Āsi khemādikānampi, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap17:7.4'><span class='pli'>etadeva vitakkitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap17:8.1'><span class='pli'>Bhūmicālo tadā āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap17:8.2'><span class='pli'>nāditā devadundubhī; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap17:8.3'><span class='pli'>Upassayādhivatthāyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap17:8.4'><span class='pli'>devatā sokapīḷitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap17:9.1'><span class='pli'>Vilapantā sukaruṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap17:9.2'><span class='pli'>tatthassūni pavattayuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap17:9.3'><span class='pli'>Mittā bhikkhuniyo tāhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap17:9.4'><span class='pli'>upagantvāna gotamiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap17:10.1'><span class='pli'>Nipacca sirasā pāde, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap17:10.2'><span class='pli'>idaṁ vacanamabravuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap17:10.3'><span class='pli'>Tattha toyalavāsittā, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap17:10.4'><span class='pli'>mayamayye rahogatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap17:11.1'><span class='pli'>Sā calā calitā bhūmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap17:11.2'><span class='pli'>nāditā devadundubhī; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap17:11.3'><span class='pli'>Paridevā ca suyyante, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap17:11.4'><span class='pli'>kimatthaṁ nūna gotamī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap17:12.1'><span class='pli'>Tadā avoca sā sabbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap17:12.2'><span class='pli'>yathāparivitakkitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap17:12.3'><span class='pli'>Tāyopi sabbā āhaṁsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap17:12.4'><span class='pli'>yathāparivitakkitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap17:13.1'><span class='pli'>“Yadi te rucitaṁ ayye, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap17:13.2'><span class='pli'>nibbānaṁ paramaṁ sivaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap17:13.3'><span class='pli'>Nibbāyissāma sabbāpi, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap17:13.4'><span class='pli'>buddhānuññāya subbate. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap17:14.1'><span class='pli'>Mayaṁ sahāva nikkhantā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap17:14.2'><span class='pli'>gharāpi ca bhavāpi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap17:14.3'><span class='pli'>Sahāyeva gamissāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap17:14.4'><span class='pli'>nibbānaṁ padamuttamaṁ”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap17:15.1'><span class='pli'>“Nibbānāya vajantīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap17:15.2'><span class='pli'>kiṁ vakkhāmī”ti sā vadaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap17:15.3'><span class='pli'>Saha sabbāhi niggañchi, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap17:15.4'><span class='pli'>bhikkhunīnilayā tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap17:16.1'><span class='pli'>“Upassaye yādhivatthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap17:16.2'><span class='pli'>devatā tā khamantu me; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap17:16.3'><span class='pli'>Bhikkhunīnilayassedaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap17:16.4'><span class='pli'>pacchimaṁ dassanaṁ mama. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap17:17.1'><span class='pli'>Na jarā maccu vā yattha, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap17:17.2'><span class='pli'>appiyehi samāgamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap17:17.3'><span class='pli'>Piyehi na viyogotthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap17:17.4'><span class='pli'>taṁ vajissaṁ asaṅkhataṁ”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap17:18.1'><span class='pli'>Avītarāgā taṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap17:18.2'><span class='pli'>vacanaṁ sugatorasā; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap17:18.3'><span class='pli'>Sokaṭṭā parideviṁsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap17:18.4'><span class='pli'>“aho no appapuññatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap17:19.1'><span class='pli'>Bhikkhunīnilayo suñño, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap17:19.2'><span class='pli'>bhūto tāhi vinā ayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap17:19.3'><span class='pli'>Pabhāte viya tārāyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap17:19.4'><span class='pli'>na dissanti jinorasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap17:20.1'><span class='pli'>Nibbānaṁ gotamī yāti, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap17:20.2'><span class='pli'>satehi saha pañcahi; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap17:20.3'><span class='pli'>Nadīsatehiva saha, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap17:20.4'><span class='pli'>gaṅgā pañcahi sāgaraṁ”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap17:21.1'><span class='pli'>Rathiyāya vajantiyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap17:21.2'><span class='pli'>disvā saddhā upāsikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap17:21.3'><span class='pli'>Gharā nikkhamma pādesu, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap17:21.4'><span class='pli'>nipacca idamabravuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap17:22.1'><span class='pli'>“Pasīdassu mahābhoge, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap17:22.2'><span class='pli'>anāthāyo vihāya no; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap17:22.3'><span class='pli'>Tayā na yuttā nibbātuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap17:22.4'><span class='pli'>icchaṭṭā vilapiṁsu tā”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap17:23.1'><span class='pli'>Tāsaṁ sokapahānatthaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap17:23.2'><span class='pli'>avoca madhuraṁ giraṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap17:23.3'><span class='pli'>“Ruditena alaṁ puttā, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='thi-ap17:23.4'><span class='pli'>hāsakāloyamajja vo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap17:24.1'><span class='pli'>Pariññātaṁ mayā dukkhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap17:24.2'><span class='pli'>dukkhahetu vivajjito; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap17:24.3'><span class='pli'>Nirodho me sacchikato, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='thi-ap17:24.4'><span class='pli'>maggo cāpi subhāvito. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='25.1' data-uid='thi-ap17:25.1'><span class='pli'>Paṭhamaṁ bhāṇavāraṁ. </span></span></p>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='26.1' data-uid='thi-ap17:26.1'><span class='pli'>Pariciṇṇo mayā satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='thi-ap17:26.2'><span class='pli'>kataṁ buddhassa sāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='thi-ap17:26.3'><span class='pli'>Ohito garuko bhāro, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='thi-ap17:26.4'><span class='pli'>bhavanetti samūhatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='thi-ap17:27.1'><span class='pli'>Yassatthāya pabbajitā, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='thi-ap17:27.2'><span class='pli'>agārasmānagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='thi-ap17:27.3'><span class='pli'>So me attho anuppatto, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='thi-ap17:27.4'><span class='pli'>sabbasaṁyojanakkhayo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='thi-ap17:28.1'><span class='pli'>Buddho tassa ca saddhammo, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='thi-ap17:28.2'><span class='pli'>anūno yāva tiṭṭhati; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='thi-ap17:28.3'><span class='pli'>Nibbātuṁ tāva kālo me, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='thi-ap17:28.4'><span class='pli'>mā maṁ socatha puttikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='thi-ap17:29.1'><span class='pli'>Koṇḍaññānandanandādī, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='thi-ap17:29.2'><span class='pli'>tiṭṭhanti rāhulo jino; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='thi-ap17:29.3'><span class='pli'>Sukhito sahito saṅgho, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='thi-ap17:29.4'><span class='pli'>hatadabbā ca titthiyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='thi-ap17:30.1'><span class='pli'>Okkākavaṁsassa yaso, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='thi-ap17:30.2'><span class='pli'>ussito māramaddano; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='thi-ap17:30.3'><span class='pli'>Nanu sampati kālo me, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='thi-ap17:30.4'><span class='pli'>nibbānatthāya puttikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='thi-ap17:31.1'><span class='pli'>Cirappabhuti yaṁ mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='thi-ap17:31.2'><span class='pli'>patthitaṁ ajja sijjhate; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='thi-ap17:31.3'><span class='pli'>Ānandabherikāloyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='thi-ap17:31.4'><span class='pli'>kiṁ vo assūhi puttikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='thi-ap17:32.1'><span class='pli'>Sace mayi dayā atthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='thi-ap17:32.2'><span class='pli'>yadi catthi kataññutā; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='thi-ap17:32.3'><span class='pli'>Saddhammaṭṭhitiyā sabbā, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='thi-ap17:32.4'><span class='pli'>karotha vīriyaṁ daḷhaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='thi-ap17:33.1'><span class='pli'>Thīnaṁ adāsi pabbajjaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='thi-ap17:33.2'><span class='pli'>sambuddho yācito mayā; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='thi-ap17:33.3'><span class='pli'>Tasmā yathāhaṁ nandissaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='thi-ap17:33.4'><span class='pli'>tathā tamanutiṭṭhatha”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='34.1' data-uid='thi-ap17:34.1'><span class='pli'>Tā evamanusāsitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='thi-ap17:34.2'><span class='pli'>bhikkhunīhi purakkhatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='thi-ap17:34.3'><span class='pli'>Upecca buddhaṁ vanditvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.4' data-uid='thi-ap17:34.4'><span class='pli'>idaṁ vacanamabravi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='35.1' data-uid='thi-ap17:35.1'><span class='pli'>“Ahaṁ sugata te mātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.2' data-uid='thi-ap17:35.2'><span class='pli'>tvañca vīra pitā mama; </span></span></span>\n<span class='verse-line'><span class='segment' id='35.3' data-uid='thi-ap17:35.3'><span class='pli'>Saddhammasukhada nātha, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.4' data-uid='thi-ap17:35.4'><span class='pli'>tayā jātāmhi gotama. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='36.1' data-uid='thi-ap17:36.1'><span class='pli'>Saṁvaddhitoyaṁ sugata, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.2' data-uid='thi-ap17:36.2'><span class='pli'>rūpakāyo mayā tava; </span></span></span>\n<span class='verse-line'><span class='segment' id='36.3' data-uid='thi-ap17:36.3'><span class='pli'>Anindito dhammakāyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.4' data-uid='thi-ap17:36.4'><span class='pli'>mama saṁvaddhito tayā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='37.1' data-uid='thi-ap17:37.1'><span class='pli'>Muhuttaṁ taṇhāsamaṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.2' data-uid='thi-ap17:37.2'><span class='pli'>khīraṁ tvaṁ pāyito mayā; </span></span></span>\n<span class='verse-line'><span class='segment' id='37.3' data-uid='thi-ap17:37.3'><span class='pli'>Tayāhaṁ santamaccantaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.4' data-uid='thi-ap17:37.4'><span class='pli'>dhammakhīrañhi pāyitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='38.1' data-uid='thi-ap17:38.1'><span class='pli'>Bandhanārakkhane mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.2' data-uid='thi-ap17:38.2'><span class='pli'>anaṇo tvaṁ mahāmune; </span></span></span>\n<span class='verse-line'><span class='segment' id='38.3' data-uid='thi-ap17:38.3'><span class='pli'>Puttakāmā thiyo yācaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.4' data-uid='thi-ap17:38.4'><span class='pli'>labhanti tādisaṁ sutaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='39.1' data-uid='thi-ap17:39.1'><span class='pli'>Mandhātādinarindānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.2' data-uid='thi-ap17:39.2'><span class='pli'>yā mātā sā bhavaṇṇave; </span></span></span>\n<span class='verse-line'><span class='segment' id='39.3' data-uid='thi-ap17:39.3'><span class='pli'>Nimuggāhaṁ tayā putta, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.4' data-uid='thi-ap17:39.4'><span class='pli'>tāritā bhavasāgarā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='40.1' data-uid='thi-ap17:40.1'><span class='pli'>Rañño mātā mahesīti, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.2' data-uid='thi-ap17:40.2'><span class='pli'>sulabhaṁ nāmamitthinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='40.3' data-uid='thi-ap17:40.3'><span class='pli'>Buddhamātāti yaṁ nāmaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.4' data-uid='thi-ap17:40.4'><span class='pli'>etaṁ paramadullabhaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='41.1' data-uid='thi-ap17:41.1'><span class='pli'>Tañca laddhaṁ mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.2' data-uid='thi-ap17:41.2'><span class='pli'>paṇidhānaṁ mamaṁ tayā; </span></span></span>\n<span class='verse-line'><span class='segment' id='41.3' data-uid='thi-ap17:41.3'><span class='pli'>Aṇukaṁ vā mahantaṁ vā, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.4' data-uid='thi-ap17:41.4'><span class='pli'>taṁ sabbaṁ pūritaṁ mayā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='42.1' data-uid='thi-ap17:42.1'><span class='pli'>Parinibbātumicchāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.2' data-uid='thi-ap17:42.2'><span class='pli'>vihāyemaṁ kaḷevaraṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='42.3' data-uid='thi-ap17:42.3'><span class='pli'>Anujānāhi me vīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.4' data-uid='thi-ap17:42.4'><span class='pli'>dukkhantakara nāyaka. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='43.1' data-uid='thi-ap17:43.1'><span class='pli'>Cakkaṅkusadhajākiṇṇe, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.2' data-uid='thi-ap17:43.2'><span class='pli'>pāde kamalakomale; </span></span></span>\n<span class='verse-line'><span class='segment' id='43.3' data-uid='thi-ap17:43.3'><span class='pli'>Pasārehi paṇāmaṁ te, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.4' data-uid='thi-ap17:43.4'><span class='pli'>karissaṁ puttauttame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='44.1' data-uid='thi-ap17:44.1'><span class='pli'>Suvaṇṇarāsisaṅkāsaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.2' data-uid='thi-ap17:44.2'><span class='pli'>sarīraṁ kuru pākaṭaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='44.3' data-uid='thi-ap17:44.3'><span class='pli'>Katvā dehaṁ sudiṭṭhaṁ te, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.4' data-uid='thi-ap17:44.4'><span class='pli'>santiṁ gacchāmi nāyaka”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='45.1' data-uid='thi-ap17:45.1'><span class='pli'>Dvattiṁsalakkhaṇūpetaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.2' data-uid='thi-ap17:45.2'><span class='pli'>suppabhālaṅkataṁ tanuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='45.3' data-uid='thi-ap17:45.3'><span class='pli'>Sañjhāghanāva bālakkaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.4' data-uid='thi-ap17:45.4'><span class='pli'>mātucchaṁ dassayī jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='46.1' data-uid='thi-ap17:46.1'><span class='pli'>Phullāravindasaṅkāse, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.2' data-uid='thi-ap17:46.2'><span class='pli'>taruṇādiccasappabhe; </span></span></span>\n<span class='verse-line'><span class='segment' id='46.3' data-uid='thi-ap17:46.3'><span class='pli'>Cakkaṅkite pādatale, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.4' data-uid='thi-ap17:46.4'><span class='pli'>tato sā sirasā pati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='47.1' data-uid='thi-ap17:47.1'><span class='pli'>“Paṇamāmi narādicca, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.2' data-uid='thi-ap17:47.2'><span class='pli'>ādiccakulaketukaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='47.3' data-uid='thi-ap17:47.3'><span class='pli'>Pacchime maraṇe mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.4' data-uid='thi-ap17:47.4'><span class='pli'>na taṁ ikkhāmahaṁ puno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='48.1' data-uid='thi-ap17:48.1'><span class='pli'>Itthiyo nāma lokagga, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.2' data-uid='thi-ap17:48.2'><span class='pli'>sabbadosākarā matā; </span></span></span>\n<span class='verse-line'><span class='segment' id='48.3' data-uid='thi-ap17:48.3'><span class='pli'>Yadi ko catthi doso me, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.4' data-uid='thi-ap17:48.4'><span class='pli'>khamassu karuṇākara. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='49.1' data-uid='thi-ap17:49.1'><span class='pli'>Itthikānañca pabbajjaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.2' data-uid='thi-ap17:49.2'><span class='pli'>haṁ taṁ yāciṁ punappunaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='49.3' data-uid='thi-ap17:49.3'><span class='pli'>Tattha ce atthi doso me, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.4' data-uid='thi-ap17:49.4'><span class='pli'>taṁ khamassu narāsabha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='50.1' data-uid='thi-ap17:50.1'><span class='pli'>Mayā bhikkhuniyo vīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.2' data-uid='thi-ap17:50.2'><span class='pli'>tavānuññāya sāsitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='50.3' data-uid='thi-ap17:50.3'><span class='pli'>Tatra ce atthi dunnītaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.4' data-uid='thi-ap17:50.4'><span class='pli'>taṁ khamassu khamādhipa”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='51.1' data-uid='thi-ap17:51.1'><span class='pli'>“Akkhante nāma khantabbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.2' data-uid='thi-ap17:51.2'><span class='pli'>kiṁ bhave guṇabhūsane; </span></span></span>\n<span class='verse-line'><span class='segment' id='51.3' data-uid='thi-ap17:51.3'><span class='pli'>Kimuttaraṁ te vakkhāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.4' data-uid='thi-ap17:51.4'><span class='pli'>nibbānāya vajantiyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='52.1' data-uid='thi-ap17:52.1'><span class='pli'>Suddhe anūne mama bhikkhusaṅghe, </span></span></span>\n<span class='verse-line'><span class='segment' id='52.2' data-uid='thi-ap17:52.2'><span class='pli'>Lokā ito nissarituṁ khamante; </span></span></span>\n<span class='verse-line'><span class='segment' id='52.3' data-uid='thi-ap17:52.3'><span class='pli'>Pabhātakāle byasanaṅgatānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='52.4' data-uid='thi-ap17:52.4'><span class='pli'>Disvāna niyyātiva candalekhā”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='53.1' data-uid='thi-ap17:53.1'><span class='pli'>Tadetarā bhikkhuniyo jinaggaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='53.2' data-uid='thi-ap17:53.2'><span class='pli'>Tārāva candānugatā sumeruṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='53.3' data-uid='thi-ap17:53.3'><span class='pli'>Padakkhiṇaṁ kacca nipacca pāde, </span></span></span>\n<span class='verse-line'><span class='segment' id='53.4' data-uid='thi-ap17:53.4'><span class='pli'>Ṭhitā mukhantaṁ samudikkhamānā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='54.1' data-uid='thi-ap17:54.1'><span class='pli'>“Na tittipubbaṁ tava dassanena, </span></span></span>\n<span class='verse-line'><span class='segment' id='54.2' data-uid='thi-ap17:54.2'><span class='pli'>Cakkhuṁ na sotaṁ tava bhāsitena; </span></span></span>\n<span class='verse-line'><span class='segment' id='54.3' data-uid='thi-ap17:54.3'><span class='pli'>Cittaṁ mamaṁ kevalamekameva, </span></span></span>\n<span class='verse-line'><span class='segment' id='54.4' data-uid='thi-ap17:54.4'><span class='pli'>Pappuyya taṁ dhammarasena titti. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='55.1' data-uid='thi-ap17:55.1'><span class='pli'>Nadato parisāyaṁ te, </span></span></span>\n<span class='verse-line'><span class='segment' id='55.2' data-uid='thi-ap17:55.2'><span class='pli'>vāditabbapahārino; </span></span></span>\n<span class='verse-line'><span class='segment' id='55.3' data-uid='thi-ap17:55.3'><span class='pli'>Ye te dakkhanti vadanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='55.4' data-uid='thi-ap17:55.4'><span class='pli'>dhaññā te narapuṅgava. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='56.1' data-uid='thi-ap17:56.1'><span class='pli'>Dīghaṅgulī tambanakhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='56.2' data-uid='thi-ap17:56.2'><span class='pli'>subhe āyatapaṇhike; </span></span></span>\n<span class='verse-line'><span class='segment' id='56.3' data-uid='thi-ap17:56.3'><span class='pli'>Ye pāde paṇamissanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='56.4' data-uid='thi-ap17:56.4'><span class='pli'>tepi dhaññā guṇandhara. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='57.1' data-uid='thi-ap17:57.1'><span class='pli'>Madhurāni pahaṭṭhāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='57.2' data-uid='thi-ap17:57.2'><span class='pli'>dosagghāni hitāni ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='57.3' data-uid='thi-ap17:57.3'><span class='pli'>Ye te vākyāni suyyanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='57.4' data-uid='thi-ap17:57.4'><span class='pli'>tepi dhaññā naruttama. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='58.1' data-uid='thi-ap17:58.1'><span class='pli'>Dhaññāhaṁ te mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='58.2' data-uid='thi-ap17:58.2'><span class='pli'>pādapūjanatapparā; </span></span></span>\n<span class='verse-line'><span class='segment' id='58.3' data-uid='thi-ap17:58.3'><span class='pli'>Tiṇṇasaṁsārakantārā, </span></span></span>\n<span class='verse-line'><span class='segment' id='58.4' data-uid='thi-ap17:58.4'><span class='pli'>suvākyena sirīmato”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='59.1' data-uid='thi-ap17:59.1'><span class='pli'>Tato sā anusāvetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='59.2' data-uid='thi-ap17:59.2'><span class='pli'>bhikkhusaṅghampi subbatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='59.3' data-uid='thi-ap17:59.3'><span class='pli'>Rāhulānandanande ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='59.4' data-uid='thi-ap17:59.4'><span class='pli'>vanditvā idamabravi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='60.1' data-uid='thi-ap17:60.1'><span class='pli'>“Āsīvisālayasame, </span></span></span>\n<span class='verse-line'><span class='segment' id='60.2' data-uid='thi-ap17:60.2'><span class='pli'>rogāvāse kaḷevare; </span></span></span>\n<span class='verse-line'><span class='segment' id='60.3' data-uid='thi-ap17:60.3'><span class='pli'>Nibbindā dukkhasaṅghāṭe, </span></span></span>\n<span class='verse-line'><span class='segment' id='60.4' data-uid='thi-ap17:60.4'><span class='pli'>jarāmaraṇagocare. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='61.1' data-uid='thi-ap17:61.1'><span class='pli'>Nānākalimalākiṇṇe, </span></span></span>\n<span class='verse-line'><span class='segment' id='61.2' data-uid='thi-ap17:61.2'><span class='pli'>parāyatte nirīhake; </span></span></span>\n<span class='verse-line'><span class='segment' id='61.3' data-uid='thi-ap17:61.3'><span class='pli'>Tena nibbātumicchāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='61.4' data-uid='thi-ap17:61.4'><span class='pli'>anumaññatha puttakā”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='62.1' data-uid='thi-ap17:62.1'><span class='pli'>Nando rāhulabhaddo ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='62.2' data-uid='thi-ap17:62.2'><span class='pli'>vītasokā nirāsavā; </span></span></span>\n<span class='verse-line'><span class='segment' id='62.3' data-uid='thi-ap17:62.3'><span class='pli'>Ṭhitācalaṭṭhiti thirā, </span></span></span>\n<span class='verse-line'><span class='segment' id='62.4' data-uid='thi-ap17:62.4'><span class='pli'>dhammatamanucintayuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='63.1' data-uid='thi-ap17:63.1'><span class='pli'>“Dhiratthu saṅkhataṁ lolaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='63.2' data-uid='thi-ap17:63.2'><span class='pli'>asāraṁ kadalūpamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='63.3' data-uid='thi-ap17:63.3'><span class='pli'>Māyāmarīcisadisaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='63.4' data-uid='thi-ap17:63.4'><span class='pli'>ittaraṁ anavaṭṭhitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='64.1' data-uid='thi-ap17:64.1'><span class='pli'>Yattha nāma jinassāyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='64.2' data-uid='thi-ap17:64.2'><span class='pli'>mātucchā buddhaposikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='64.3' data-uid='thi-ap17:64.3'><span class='pli'>Gotamī nidhanaṁ yāti, </span></span></span>\n<span class='verse-line'><span class='segment' id='64.4' data-uid='thi-ap17:64.4'><span class='pli'>aniccaṁ sabbasaṅkhataṁ”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='65.1' data-uid='thi-ap17:65.1'><span class='pli'>Ānando ca tadā sekho, </span></span></span>\n<span class='verse-line'><span class='segment' id='65.2' data-uid='thi-ap17:65.2'><span class='pli'>sokaṭṭo jinavacchalo; </span></span></span>\n<span class='verse-line'><span class='segment' id='65.3' data-uid='thi-ap17:65.3'><span class='pli'>Tatthassūni karonto so, </span></span></span>\n<span class='verse-line'><span class='segment' id='65.4' data-uid='thi-ap17:65.4'><span class='pli'>karuṇaṁ paridevati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='66.1' data-uid='thi-ap17:66.1'><span class='pli'>“Hā santiṁ gotamī yāti, </span></span></span>\n<span class='verse-line'><span class='segment' id='66.2' data-uid='thi-ap17:66.2'><span class='pli'>nūna buddhopi nibbutiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='66.3' data-uid='thi-ap17:66.3'><span class='pli'>Gacchati na cireneva, </span></span></span>\n<span class='verse-line'><span class='segment' id='66.4' data-uid='thi-ap17:66.4'><span class='pli'>aggiriva nirindhano”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='67.1' data-uid='thi-ap17:67.1'><span class='pli'>Evaṁ vilāpamānaṁ taṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='67.2' data-uid='thi-ap17:67.2'><span class='pli'>ānandaṁ āha gotamī; </span></span></span>\n<span class='verse-line'><span class='segment' id='67.3' data-uid='thi-ap17:67.3'><span class='pli'>Sutasāgaragambhīra- </span></span></span>\n<span class='verse-line'><span class='segment' id='67.4' data-uid='thi-ap17:67.4'><span class='pli'>buddhopaṭṭhānatappara. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='68.1' data-uid='thi-ap17:68.1'><span class='pli'>“Na yuttaṁ socituṁ putta, </span></span></span>\n<span class='verse-line'><span class='segment' id='68.2' data-uid='thi-ap17:68.2'><span class='pli'>hāsakāle upaṭṭhite; </span></span></span>\n<span class='verse-line'><span class='segment' id='68.3' data-uid='thi-ap17:68.3'><span class='pli'>Tayā me saraṇaṁ putta, </span></span></span>\n<span class='verse-line'><span class='segment' id='68.4' data-uid='thi-ap17:68.4'><span class='pli'>nibbānaṁ tamupāgataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='69.1' data-uid='thi-ap17:69.1'><span class='pli'>Tayā tāta samajjhiṭṭho, </span></span></span>\n<span class='verse-line'><span class='segment' id='69.2' data-uid='thi-ap17:69.2'><span class='pli'>pabbajjaṁ anujāni no; </span></span></span>\n<span class='verse-line'><span class='segment' id='69.3' data-uid='thi-ap17:69.3'><span class='pli'>Mā putta vimano hohi, </span></span></span>\n<span class='verse-line'><span class='segment' id='69.4' data-uid='thi-ap17:69.4'><span class='pli'>saphalo te parissamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='70.1' data-uid='thi-ap17:70.1'><span class='pli'>Yaṁ na diṭṭhaṁ purāṇehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='70.2' data-uid='thi-ap17:70.2'><span class='pli'>titthikācariyehipi; </span></span></span>\n<span class='verse-line'><span class='segment' id='70.3' data-uid='thi-ap17:70.3'><span class='pli'>Taṁ padaṁ sukumārīhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='70.4' data-uid='thi-ap17:70.4'><span class='pli'>sattavassāhi veditaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='71.1' data-uid='thi-ap17:71.1'><span class='pli'>Buddhasāsanapāleta, </span></span></span>\n<span class='verse-line'><span class='segment' id='71.2' data-uid='thi-ap17:71.2'><span class='pli'>pacchimaṁ dassanaṁ tava; </span></span></span>\n<span class='verse-line'><span class='segment' id='71.3' data-uid='thi-ap17:71.3'><span class='pli'>Tattha gacchāmahaṁ putta, </span></span></span>\n<span class='verse-line'><span class='segment' id='71.4' data-uid='thi-ap17:71.4'><span class='pli'>gato yattha na dissate. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='72.1' data-uid='thi-ap17:72.1'><span class='pli'>Kadāci dhammaṁ desento, </span></span></span>\n<span class='verse-line'><span class='segment' id='72.2' data-uid='thi-ap17:72.2'><span class='pli'>khipī lokagganāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='72.3' data-uid='thi-ap17:72.3'><span class='pli'>Tadāhaṁ āsīsavācaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='72.4' data-uid='thi-ap17:72.4'><span class='pli'>avocaṁ anukampikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='73.1' data-uid='thi-ap17:73.1'><span class='pli'>‘Ciraṁ jīva mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='73.2' data-uid='thi-ap17:73.2'><span class='pli'>kappaṁ tiṭṭha mahāmune; </span></span></span>\n<span class='verse-line'><span class='segment' id='73.3' data-uid='thi-ap17:73.3'><span class='pli'>Sabbalokassa atthāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='73.4' data-uid='thi-ap17:73.4'><span class='pli'>bhavassu ajarāmaro’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='74.1' data-uid='thi-ap17:74.1'><span class='pli'>Taṁ tathāvādiniṁ buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='74.2' data-uid='thi-ap17:74.2'><span class='pli'>mamaṁ so etadabravi; </span></span></span>\n<span class='verse-line'><span class='segment' id='74.3' data-uid='thi-ap17:74.3'><span class='pli'>‘Na hevaṁ vandiyā buddhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='74.4' data-uid='thi-ap17:74.4'><span class='pli'>yathā vandasi gotamī’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='75.1' data-uid='thi-ap17:75.1'><span class='pli'>‘Kathaṁ carahi sabbaññū, </span></span></span>\n<span class='verse-line'><span class='segment' id='75.2' data-uid='thi-ap17:75.2'><span class='pli'>vanditabbā tathāgatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='75.3' data-uid='thi-ap17:75.3'><span class='pli'>Kathaṁ avandiyā buddhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='75.4' data-uid='thi-ap17:75.4'><span class='pli'>taṁ me akkhāhi pucchito’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='76.1' data-uid='thi-ap17:76.1'><span class='pli'>‘Āraddhavīriye pahitatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='76.2' data-uid='thi-ap17:76.2'><span class='pli'>Niccaṁ daḷhaparakkame; </span></span></span>\n<span class='verse-line'><span class='segment' id='76.3' data-uid='thi-ap17:76.3'><span class='pli'>Samagge sāvake passa, </span></span></span>\n<span class='verse-line'><span class='segment' id='76.4' data-uid='thi-ap17:76.4'><span class='pli'>Etaṁ buddhānavandanaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='77.1' data-uid='thi-ap17:77.1'><span class='pli'>Tato upassayaṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='77.2' data-uid='thi-ap17:77.2'><span class='pli'>ekikāhaṁ vicintayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='77.3' data-uid='thi-ap17:77.3'><span class='pli'>‘Samaggaparisaṁ nātho, </span></span></span>\n<span class='verse-line'><span class='segment' id='77.4' data-uid='thi-ap17:77.4'><span class='pli'>rodhesi tibhavantago. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='78.1' data-uid='thi-ap17:78.1'><span class='pli'>Handāhaṁ parinibbissaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='78.2' data-uid='thi-ap17:78.2'><span class='pli'>mā vipattitamaddasaṁ’; </span></span></span>\n<span class='verse-line'><span class='segment' id='78.3' data-uid='thi-ap17:78.3'><span class='pli'>Evāhaṁ cintayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='78.4' data-uid='thi-ap17:78.4'><span class='pli'>disvāna isisattamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='79.1' data-uid='thi-ap17:79.1'><span class='pli'>Parinibbānakālaṁ me, </span></span></span>\n<span class='verse-line'><span class='segment' id='79.2' data-uid='thi-ap17:79.2'><span class='pli'>ārocesiṁ vināyakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='79.3' data-uid='thi-ap17:79.3'><span class='pli'>Tato so samanuññāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='79.4' data-uid='thi-ap17:79.4'><span class='pli'>‘kālaṁ jānāhi gotamī’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='80.1' data-uid='thi-ap17:80.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='80.2' data-uid='thi-ap17:80.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='80.3' data-uid='thi-ap17:80.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='81.1' data-uid='thi-ap17:81.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='81.2' data-uid='thi-ap17:81.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='81.3' data-uid='thi-ap17:81.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='82.1' data-uid='thi-ap17:82.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='82.2' data-uid='thi-ap17:82.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='82.3' data-uid='thi-ap17:82.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='83.1' data-uid='thi-ap17:83.1'><span class='pli'>“Thīnaṁ dhammābhisamaye, </span></span></span>\n<span class='verse-line'><span class='segment' id='83.2' data-uid='thi-ap17:83.2'><span class='pli'>ye bālā vimatiṁ gatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='83.3' data-uid='thi-ap17:83.3'><span class='pli'>Tesaṁ diṭṭhippahānatthaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='83.4' data-uid='thi-ap17:83.4'><span class='pli'>iddhiṁ dassehi gotamī”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='84.1' data-uid='thi-ap17:84.1'><span class='pli'>Tadā nipacca sambuddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='84.2' data-uid='thi-ap17:84.2'><span class='pli'>uppatitvāna ambaraṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='84.3' data-uid='thi-ap17:84.3'><span class='pli'>Iddhī anekā dassesi, </span></span></span>\n<span class='verse-line'><span class='segment' id='84.4' data-uid='thi-ap17:84.4'><span class='pli'>buddhānuññāya gotamī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='85.1' data-uid='thi-ap17:85.1'><span class='pli'>Ekikā bahudhā āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='85.2' data-uid='thi-ap17:85.2'><span class='pli'>bahukā cekikā tathā; </span></span></span>\n<span class='verse-line'><span class='segment' id='85.3' data-uid='thi-ap17:85.3'><span class='pli'>Āvibhāvaṁ tirobhāvaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='85.4' data-uid='thi-ap17:85.4'><span class='pli'>tirokuṭṭaṁ tironagaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='86.1' data-uid='thi-ap17:86.1'><span class='pli'>Asajjamānā agamā, </span></span></span>\n<span class='verse-line'><span class='segment' id='86.2' data-uid='thi-ap17:86.2'><span class='pli'>bhūmiyampi nimujjatha; </span></span></span>\n<span class='verse-line'><span class='segment' id='86.3' data-uid='thi-ap17:86.3'><span class='pli'>Abhijjamāne udake, </span></span></span>\n<span class='verse-line'><span class='segment' id='86.4' data-uid='thi-ap17:86.4'><span class='pli'>agañchi mahiyā yathā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='87.1' data-uid='thi-ap17:87.1'><span class='pli'>Sakuṇīva tathākāse, </span></span></span>\n<span class='verse-line'><span class='segment' id='87.2' data-uid='thi-ap17:87.2'><span class='pli'>pallaṅkena kamī tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='87.3' data-uid='thi-ap17:87.3'><span class='pli'>Vasaṁ vattesi kāyena, </span></span></span>\n<span class='verse-line'><span class='segment' id='87.4' data-uid='thi-ap17:87.4'><span class='pli'>yāva brahmanivesanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='88.1' data-uid='thi-ap17:88.1'><span class='pli'>Sineruṁ daṇḍaṁ katvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='88.2' data-uid='thi-ap17:88.2'><span class='pli'>chattaṁ katvā mahāmahiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='88.3' data-uid='thi-ap17:88.3'><span class='pli'>Samūlaṁ parivattetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='88.4' data-uid='thi-ap17:88.4'><span class='pli'>dhārayaṁ caṅkamī nabhe. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='89.1' data-uid='thi-ap17:89.1'><span class='pli'>Chassūrodayakāleva, </span></span></span>\n<span class='verse-line'><span class='segment' id='89.2' data-uid='thi-ap17:89.2'><span class='pli'>lokañcākāsi dhūmikaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='89.3' data-uid='thi-ap17:89.3'><span class='pli'>Yugante viya lokaṁ sā, </span></span></span>\n<span class='verse-line'><span class='segment' id='89.4' data-uid='thi-ap17:89.4'><span class='pli'>jālāmālākulaṁ akā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='90.1' data-uid='thi-ap17:90.1'><span class='pli'>Mucalindaṁ mahāselaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='90.2' data-uid='thi-ap17:90.2'><span class='pli'>merumūlanadantare; </span></span></span>\n<span class='verse-line'><span class='segment' id='90.3' data-uid='thi-ap17:90.3'><span class='pli'>Sāsapāriva sabbāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='90.4' data-uid='thi-ap17:90.4'><span class='pli'>ekenaggahi muṭṭhinā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='91.1' data-uid='thi-ap17:91.1'><span class='pli'>Aṅgulaggena chādesi, </span></span></span>\n<span class='verse-line'><span class='segment' id='91.2' data-uid='thi-ap17:91.2'><span class='pli'>bhākaraṁ sanisākaraṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='91.3' data-uid='thi-ap17:91.3'><span class='pli'>Candasūrasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='91.4' data-uid='thi-ap17:91.4'><span class='pli'>āveḷamiva dhārayi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='92.1' data-uid='thi-ap17:92.1'><span class='pli'>Catusāgaratoyāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='92.2' data-uid='thi-ap17:92.2'><span class='pli'>dhārayī ekapāṇinā; </span></span></span>\n<span class='verse-line'><span class='segment' id='92.3' data-uid='thi-ap17:92.3'><span class='pli'>Yugantajaladākāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='92.4' data-uid='thi-ap17:92.4'><span class='pli'>mahāvassaṁ pavassatha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='93.1' data-uid='thi-ap17:93.1'><span class='pli'>Cakkavattiṁ saparisaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='93.2' data-uid='thi-ap17:93.2'><span class='pli'>māpayī sā nabhattale; </span></span></span>\n<span class='verse-line'><span class='segment' id='93.3' data-uid='thi-ap17:93.3'><span class='pli'>Garuḷaṁ dviradaṁ sīhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='93.4' data-uid='thi-ap17:93.4'><span class='pli'>vinadantaṁ padassayi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='94.1' data-uid='thi-ap17:94.1'><span class='pli'>Ekikā abhinimmitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='94.2' data-uid='thi-ap17:94.2'><span class='pli'>Appameyyaṁ bhikkhunīgaṇaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='94.3' data-uid='thi-ap17:94.3'><span class='pli'>Puna antaradhāpetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='94.4' data-uid='thi-ap17:94.4'><span class='pli'>Ekikā munimabravi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='95.1' data-uid='thi-ap17:95.1'><span class='pli'>“Mātucchā te mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='95.2' data-uid='thi-ap17:95.2'><span class='pli'>tava sāsanakārikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='95.3' data-uid='thi-ap17:95.3'><span class='pli'>Anuppattā sakaṁ atthaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='95.4' data-uid='thi-ap17:95.4'><span class='pli'>pāde vandāmi cakkhuma”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='96.1' data-uid='thi-ap17:96.1'><span class='pli'>Dassetvā vividhā iddhī, </span></span></span>\n<span class='verse-line'><span class='segment' id='96.2' data-uid='thi-ap17:96.2'><span class='pli'>orohitvā nabhattalā; </span></span></span>\n<span class='verse-line'><span class='segment' id='96.3' data-uid='thi-ap17:96.3'><span class='pli'>Vanditvā lokapajjotaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='96.4' data-uid='thi-ap17:96.4'><span class='pli'>ekamantaṁ nisīdi sā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='97.1' data-uid='thi-ap17:97.1'><span class='pli'>“Sā vīsavassasatikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='97.2' data-uid='thi-ap17:97.2'><span class='pli'>jātiyāhaṁ mahāmune; </span></span></span>\n<span class='verse-line'><span class='segment' id='97.3' data-uid='thi-ap17:97.3'><span class='pli'>Alamettāvatā vīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='97.4' data-uid='thi-ap17:97.4'><span class='pli'>nibbāyissāmi nāyaka”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='98.1' data-uid='thi-ap17:98.1'><span class='pli'>Tadātivimhitā sabbā, </span></span></span>\n<span class='verse-line'><span class='segment' id='98.2' data-uid='thi-ap17:98.2'><span class='pli'>parisā sā katañjalī; </span></span></span>\n<span class='verse-line'><span class='segment' id='98.3' data-uid='thi-ap17:98.3'><span class='pli'>“Avocayye kathaṁ āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='98.4' data-uid='thi-ap17:98.4'><span class='pli'>atuliddhiparakkamā”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='99.1' data-uid='thi-ap17:99.1'><span class='pli'>“Padumuttaro nāma jino, </span></span></span>\n<span class='verse-line'><span class='segment' id='99.2' data-uid='thi-ap17:99.2'><span class='pli'>sabbadhammesu cakkhumā; </span></span></span>\n<span class='verse-line'><span class='segment' id='99.3' data-uid='thi-ap17:99.3'><span class='pli'>Ito satasahassamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='99.4' data-uid='thi-ap17:99.4'><span class='pli'>kappe uppajji nāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='100.1' data-uid='thi-ap17:100.1'><span class='pli'>Tadāhaṁ haṁsavatiyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='100.2' data-uid='thi-ap17:100.2'><span class='pli'>jātāmaccakule ahuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='100.3' data-uid='thi-ap17:100.3'><span class='pli'>Sabbopakārasampanne, </span></span></span>\n<span class='verse-line'><span class='segment' id='100.4' data-uid='thi-ap17:100.4'><span class='pli'>iddhe phīte mahaddhane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='101.1' data-uid='thi-ap17:101.1'><span class='pli'>Kadāci pitunā saddhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='101.2' data-uid='thi-ap17:101.2'><span class='pli'>dāsīgaṇapurakkhatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='101.3' data-uid='thi-ap17:101.3'><span class='pli'>Mahatā parivārena, </span></span></span>\n<span class='verse-line'><span class='segment' id='101.4' data-uid='thi-ap17:101.4'><span class='pli'>taṁ upecca narāsabhaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='102.1' data-uid='thi-ap17:102.1'><span class='pli'>Vāsavaṁ viya vassantaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='102.2' data-uid='thi-ap17:102.2'><span class='pli'>dhammameghaṁ anāsavaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='102.3' data-uid='thi-ap17:102.3'><span class='pli'>Saradādiccasadisaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='102.4' data-uid='thi-ap17:102.4'><span class='pli'>raṁsijālasamujjalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='103.1' data-uid='thi-ap17:103.1'><span class='pli'>Disvā cittaṁ pasādetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='103.2' data-uid='thi-ap17:103.2'><span class='pli'>sutvā cassa subhāsitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='103.3' data-uid='thi-ap17:103.3'><span class='pli'>Mātucchaṁ bhikkhuniṁ agge, </span></span></span>\n<span class='verse-line'><span class='segment' id='103.4' data-uid='thi-ap17:103.4'><span class='pli'>ṭhapentaṁ naranāyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='104.1' data-uid='thi-ap17:104.1'><span class='pli'>Sutvā datvā mahādānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='104.2' data-uid='thi-ap17:104.2'><span class='pli'>sattāhaṁ tassa tādino; </span></span></span>\n<span class='verse-line'><span class='segment' id='104.3' data-uid='thi-ap17:104.3'><span class='pli'>Sasaṅghassa naraggassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='104.4' data-uid='thi-ap17:104.4'><span class='pli'>paccayāni bahūni ca. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='105.1' data-uid='thi-ap17:105.1'><span class='pli'>Nipacca pādamūlamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='105.2' data-uid='thi-ap17:105.2'><span class='pli'>taṁ ṭhānamabhipatthayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='105.3' data-uid='thi-ap17:105.3'><span class='pli'>Tato mahāparisatiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='105.4' data-uid='thi-ap17:105.4'><span class='pli'>avoca isisattamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='106.1' data-uid='thi-ap17:106.1'><span class='pli'>‘Yā sasaṅghaṁ abhojesi, </span></span></span>\n<span class='verse-line'><span class='segment' id='106.2' data-uid='thi-ap17:106.2'><span class='pli'>sattāhaṁ lokanāyakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='106.3' data-uid='thi-ap17:106.3'><span class='pli'>Tamahaṁ kittayissāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='106.4' data-uid='thi-ap17:106.4'><span class='pli'>suṇātha mama bhāsato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='107.1' data-uid='thi-ap17:107.1'><span class='pli'>Satasahassito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='107.2' data-uid='thi-ap17:107.2'><span class='pli'>okkākakulasambhavo; </span></span></span>\n<span class='verse-line'><span class='segment' id='107.3' data-uid='thi-ap17:107.3'><span class='pli'>Gotamo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='107.4' data-uid='thi-ap17:107.4'><span class='pli'>satthā loke bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='108.1' data-uid='thi-ap17:108.1'><span class='pli'>Tassa dhammesu dāyādā, </span></span></span>\n<span class='verse-line'><span class='segment' id='108.2' data-uid='thi-ap17:108.2'><span class='pli'>orasā dhammanimmitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='108.3' data-uid='thi-ap17:108.3'><span class='pli'>Gotamī nāma nāmena, </span></span></span>\n<span class='verse-line'><span class='segment' id='108.4' data-uid='thi-ap17:108.4'><span class='pli'>hessati satthu sāvikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='109.1' data-uid='thi-ap17:109.1'><span class='pli'>Tassa buddhassa mātucchā, </span></span></span>\n<span class='verse-line'><span class='segment' id='109.2' data-uid='thi-ap17:109.2'><span class='pli'>jīvitāpādikā ayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='109.3' data-uid='thi-ap17:109.3'><span class='pli'>Rattaññūnañca aggattaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='109.4' data-uid='thi-ap17:109.4'><span class='pli'>bhikkhunīnaṁ labhissati’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='110.1' data-uid='thi-ap17:110.1'><span class='pli'>Taṁ sutvāna pamoditvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='110.2' data-uid='thi-ap17:110.2'><span class='pli'>yāvajīvaṁ tadā jinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='110.3' data-uid='thi-ap17:110.3'><span class='pli'>Paccayehi upaṭṭhitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='110.4' data-uid='thi-ap17:110.4'><span class='pli'>tato kālaṅkatā ahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='111.1' data-uid='thi-ap17:111.1'><span class='pli'>Tāvatiṁsesu devesu, </span></span></span>\n<span class='verse-line'><span class='segment' id='111.2' data-uid='thi-ap17:111.2'><span class='pli'>sabbakāmasamiddhisu; </span></span></span>\n<span class='verse-line'><span class='segment' id='111.3' data-uid='thi-ap17:111.3'><span class='pli'>Nibbattā dasahaṅgehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='111.4' data-uid='thi-ap17:111.4'><span class='pli'>aññe abhibhaviṁ ahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='112.1' data-uid='thi-ap17:112.1'><span class='pli'>Rūpasaddehi gandhehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='112.2' data-uid='thi-ap17:112.2'><span class='pli'>rasehi phusanehi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='112.3' data-uid='thi-ap17:112.3'><span class='pli'>Āyunāpi ca vaṇṇena, </span></span></span>\n<span class='verse-line'><span class='segment' id='112.4' data-uid='thi-ap17:112.4'><span class='pli'>sukhena yasasāpi ca. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='113.1' data-uid='thi-ap17:113.1'><span class='pli'>Tathevādhipateyyena, </span></span></span>\n<span class='verse-line'><span class='segment' id='113.2' data-uid='thi-ap17:113.2'><span class='pli'>adhigayha virocahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='113.3' data-uid='thi-ap17:113.3'><span class='pli'>Ahosiṁ amarindassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='113.4' data-uid='thi-ap17:113.4'><span class='pli'>mahesī dayitā tahiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='114.1' data-uid='thi-ap17:114.1'><span class='pli'>Saṁsāre saṁsarantīhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='114.2' data-uid='thi-ap17:114.2'><span class='pli'>kammavāyusameritā; </span></span></span>\n<span class='verse-line'><span class='segment' id='114.3' data-uid='thi-ap17:114.3'><span class='pli'>Kāsissa rañño visaye, </span></span></span>\n<span class='verse-line'><span class='segment' id='114.4' data-uid='thi-ap17:114.4'><span class='pli'>ajāyiṁ dāsagāmake. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='115.1' data-uid='thi-ap17:115.1'><span class='pli'>Pañcadāsasatānūnā, </span></span></span>\n<span class='verse-line'><span class='segment' id='115.2' data-uid='thi-ap17:115.2'><span class='pli'>nivasanti tahiṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='115.3' data-uid='thi-ap17:115.3'><span class='pli'>Sabbesaṁ tattha yo jeṭṭho, </span></span></span>\n<span class='verse-line'><span class='segment' id='115.4' data-uid='thi-ap17:115.4'><span class='pli'>tassa jāyā ahosahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='116.1' data-uid='thi-ap17:116.1'><span class='pli'>Sayambhuno pañcasatā, </span></span></span>\n<span class='verse-line'><span class='segment' id='116.2' data-uid='thi-ap17:116.2'><span class='pli'>gāmaṁ piṇḍāya pāvisuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='116.3' data-uid='thi-ap17:116.3'><span class='pli'>Te disvāna ahaṁ tuṭṭhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='116.4' data-uid='thi-ap17:116.4'><span class='pli'>saha sabbāhi itthibhi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='117.1' data-uid='thi-ap17:117.1'><span class='pli'>Pūgā hutvāva sabbāyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='117.2' data-uid='thi-ap17:117.2'><span class='pli'>catumāse upaṭṭhahuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='117.3' data-uid='thi-ap17:117.3'><span class='pli'>Ticīvarāni datvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='117.4' data-uid='thi-ap17:117.4'><span class='pli'>saṁsarimha sasāmikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='118.1' data-uid='thi-ap17:118.1'><span class='pli'>Tato cutā sabbāpi tā, </span></span></span>\n<span class='verse-line'><span class='segment' id='118.2' data-uid='thi-ap17:118.2'><span class='pli'>tāvatiṁsagatā mayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='118.3' data-uid='thi-ap17:118.3'><span class='pli'>Pacchime ca bhave dāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='118.4' data-uid='thi-ap17:118.4'><span class='pli'>jātā devadahe pure. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='119.1' data-uid='thi-ap17:119.1'><span class='pli'>Pitā añjanasakko me, </span></span></span>\n<span class='verse-line'><span class='segment' id='119.2' data-uid='thi-ap17:119.2'><span class='pli'>mātā mama sulakkhaṇā; </span></span></span>\n<span class='verse-line'><span class='segment' id='119.3' data-uid='thi-ap17:119.3'><span class='pli'>Tato kapilavatthusmiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='119.4' data-uid='thi-ap17:119.4'><span class='pli'>suddhodanagharaṁ gatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='120.1' data-uid='thi-ap17:120.1'><span class='pli'>Sesā sakyakule jātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='120.2' data-uid='thi-ap17:120.2'><span class='pli'>sakyānaṁ gharamāgamuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='120.3' data-uid='thi-ap17:120.3'><span class='pli'>Ahaṁ visiṭṭhā sabbāsaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='120.4' data-uid='thi-ap17:120.4'><span class='pli'>jinassāpādikā ahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='121.1' data-uid='thi-ap17:121.1'><span class='pli'>Mama puttobhinikkhamma, </span></span></span>\n<span class='verse-line'><span class='segment' id='121.2' data-uid='thi-ap17:121.2'><span class='pli'>buddho āsi vināyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='121.3' data-uid='thi-ap17:121.3'><span class='pli'>Pacchāhaṁ pabbajitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='121.4' data-uid='thi-ap17:121.4'><span class='pli'>satehi saha pañcahi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='122.1' data-uid='thi-ap17:122.1'><span class='pli'>Sākiyānīhi dhīrāhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='122.2' data-uid='thi-ap17:122.2'><span class='pli'>saha santisukhaṁ phusiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='122.3' data-uid='thi-ap17:122.3'><span class='pli'>Ye tadā pubbajātiyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='122.4' data-uid='thi-ap17:122.4'><span class='pli'>amhākaṁ āsu sāmino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='123.1' data-uid='thi-ap17:123.1'><span class='pli'>Sahapuññassa kattāro, </span></span></span>\n<span class='verse-line'><span class='segment' id='123.2' data-uid='thi-ap17:123.2'><span class='pli'>mahāsamayakārakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='123.3' data-uid='thi-ap17:123.3'><span class='pli'>Phusiṁsu arahattaṁ te, </span></span></span>\n<span class='verse-line'><span class='segment' id='123.4' data-uid='thi-ap17:123.4'><span class='pli'>sugatenānukampitā”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='124.1' data-uid='thi-ap17:124.1'><span class='pli'>Tadetarā bhikkhuniyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='124.2' data-uid='thi-ap17:124.2'><span class='pli'>āruhiṁsu nabhattalaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='124.3' data-uid='thi-ap17:124.3'><span class='pli'>Saṅgatā viya tārāyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='124.4' data-uid='thi-ap17:124.4'><span class='pli'>virociṁsu mahiddhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='125.1' data-uid='thi-ap17:125.1'><span class='pli'>Iddhī anekā dassesuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='125.2' data-uid='thi-ap17:125.2'><span class='pli'>piḷandhavikatiṁ yathā; </span></span></span>\n<span class='verse-line'><span class='segment' id='125.3' data-uid='thi-ap17:125.3'><span class='pli'>Kammāro kanakasseva, </span></span></span>\n<span class='verse-line'><span class='segment' id='125.4' data-uid='thi-ap17:125.4'><span class='pli'>kammaññassa susikkhito. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='126.1' data-uid='thi-ap17:126.1'><span class='pli'>Dassetvā pāṭihīrāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='126.2' data-uid='thi-ap17:126.2'><span class='pli'>vicittāni bahūni ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='126.3' data-uid='thi-ap17:126.3'><span class='pli'>Tosetvā vādipavaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='126.4' data-uid='thi-ap17:126.4'><span class='pli'>muniṁ saparisaṁ tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='127.1' data-uid='thi-ap17:127.1'><span class='pli'>Orohitvāna gaganā, </span></span></span>\n<span class='verse-line'><span class='segment' id='127.2' data-uid='thi-ap17:127.2'><span class='pli'>vanditvā isisattamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='127.3' data-uid='thi-ap17:127.3'><span class='pli'>Anuññātā naraggena, </span></span></span>\n<span class='verse-line'><span class='segment' id='127.4' data-uid='thi-ap17:127.4'><span class='pli'>yathāṭhāne nisīdisuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='128.1' data-uid='thi-ap17:128.1'><span class='pli'>“Ahonukampikā amhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='128.2' data-uid='thi-ap17:128.2'><span class='pli'>sabbāsaṁ cira gotamī; </span></span></span>\n<span class='verse-line'><span class='segment' id='128.3' data-uid='thi-ap17:128.3'><span class='pli'>Vāsitā tava puññehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='128.4' data-uid='thi-ap17:128.4'><span class='pli'>pattā no āsavakkhayaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='129.1' data-uid='thi-ap17:129.1'><span class='pli'>Kilesā jhāpitā amhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='129.2' data-uid='thi-ap17:129.2'><span class='pli'>bhavā sabbe samūhatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='129.3' data-uid='thi-ap17:129.3'><span class='pli'>Nāgīva bandhanaṁ chetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='129.4' data-uid='thi-ap17:129.4'><span class='pli'>viharāma anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='130.1' data-uid='thi-ap17:130.1'><span class='pli'>Svāgataṁ vata no āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='130.2' data-uid='thi-ap17:130.2'><span class='pli'>buddhaseṭṭhassa santike; </span></span></span>\n<span class='verse-line'><span class='segment' id='130.3' data-uid='thi-ap17:130.3'><span class='pli'>Tisso vijjā anuppattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='130.4' data-uid='thi-ap17:130.4'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='131.1' data-uid='thi-ap17:131.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='131.2' data-uid='thi-ap17:131.2'><span class='pli'>vimokkhāpi ca aṭṭhime; </span></span></span>\n<span class='verse-line'><span class='segment' id='131.3' data-uid='thi-ap17:131.3'><span class='pli'>Chaḷabhiññā sacchikatā, </span></span></span>\n<span class='verse-line'><span class='segment' id='131.4' data-uid='thi-ap17:131.4'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='132.1' data-uid='thi-ap17:132.1'><span class='pli'>Iddhīsu ca vasī homa, </span></span></span>\n<span class='verse-line'><span class='segment' id='132.2' data-uid='thi-ap17:132.2'><span class='pli'>dibbāya sotadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='132.3' data-uid='thi-ap17:132.3'><span class='pli'>Cetopariyañāṇassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='132.4' data-uid='thi-ap17:132.4'><span class='pli'>vasī homa mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='133.1' data-uid='thi-ap17:133.1'><span class='pli'>Pubbenivāsaṁ jānāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='133.2' data-uid='thi-ap17:133.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='133.3' data-uid='thi-ap17:133.3'><span class='pli'>Sabbāsavaparikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='133.4' data-uid='thi-ap17:133.4'><span class='pli'>natthi dāni punabbhavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='134.1' data-uid='thi-ap17:134.1'><span class='pli'>Atthe dhamme ca nerutte, </span></span></span>\n<span class='verse-line'><span class='segment' id='134.2' data-uid='thi-ap17:134.2'><span class='pli'>paṭibhāne ca vijjati; </span></span></span>\n<span class='verse-line'><span class='segment' id='134.3' data-uid='thi-ap17:134.3'><span class='pli'>Ñāṇaṁ amhaṁ mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='134.4' data-uid='thi-ap17:134.4'><span class='pli'>uppannaṁ tava santike. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='135.1' data-uid='thi-ap17:135.1'><span class='pli'>Asmābhi pariciṇṇosi, </span></span></span>\n<span class='verse-line'><span class='segment' id='135.2' data-uid='thi-ap17:135.2'><span class='pli'>mettacittā hi nāyaka; </span></span></span>\n<span class='verse-line'><span class='segment' id='135.3' data-uid='thi-ap17:135.3'><span class='pli'>Anujānāhi sabbāsaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='135.4' data-uid='thi-ap17:135.4'><span class='pli'>nibbānāya mahāmune”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='136.1' data-uid='thi-ap17:136.1'><span class='pli'>“Nibbāyissāma iccevaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='136.2' data-uid='thi-ap17:136.2'><span class='pli'>kiṁ vakkhāmi vadantiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='136.3' data-uid='thi-ap17:136.3'><span class='pli'>Yassadāni ca vo kālaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='136.4' data-uid='thi-ap17:136.4'><span class='pli'>maññathā”ti jinobravi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='137.1' data-uid='thi-ap17:137.1'><span class='pli'>Gotamīādikā tāyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='137.2' data-uid='thi-ap17:137.2'><span class='pli'>tadā bhikkhuniyo jinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='137.3' data-uid='thi-ap17:137.3'><span class='pli'>Vanditvā āsanā tamhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='137.4' data-uid='thi-ap17:137.4'><span class='pli'>vuṭṭhāya agamiṁsu tā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='138.1' data-uid='thi-ap17:138.1'><span class='pli'>Mahatā janakāyena, </span></span></span>\n<span class='verse-line'><span class='segment' id='138.2' data-uid='thi-ap17:138.2'><span class='pli'>saha lokagganāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='138.3' data-uid='thi-ap17:138.3'><span class='pli'>Anusaṁyāyī so vīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='138.4' data-uid='thi-ap17:138.4'><span class='pli'>mātucchaṁ yāvakoṭṭhakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='139.1' data-uid='thi-ap17:139.1'><span class='pli'>Tadā nipati pādesu, </span></span></span>\n<span class='verse-line'><span class='segment' id='139.2' data-uid='thi-ap17:139.2'><span class='pli'>gotamī lokabandhuno; </span></span></span>\n<span class='verse-line'><span class='segment' id='139.3' data-uid='thi-ap17:139.3'><span class='pli'>Saheva tāhi sabbāhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='139.4' data-uid='thi-ap17:139.4'><span class='pli'>“pacchimaṁ pādavandanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='140.1' data-uid='thi-ap17:140.1'><span class='pli'>Idaṁ pacchimakaṁ mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='140.2' data-uid='thi-ap17:140.2'><span class='pli'>lokanāthassa dassanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='140.3' data-uid='thi-ap17:140.3'><span class='pli'>Na puno amatākāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='140.4' data-uid='thi-ap17:140.4'><span class='pli'>passissāmi mukhaṁ tava. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='141.1' data-uid='thi-ap17:141.1'><span class='pli'>Na ca me vandanaṁ vīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='141.2' data-uid='thi-ap17:141.2'><span class='pli'>tava pāde sukomale; </span></span></span>\n<span class='verse-line'><span class='segment' id='141.3' data-uid='thi-ap17:141.3'><span class='pli'>Samphusissati lokagga, </span></span></span>\n<span class='verse-line'><span class='segment' id='141.4' data-uid='thi-ap17:141.4'><span class='pli'>ajja gacchāmi nibbutiṁ”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='142.1' data-uid='thi-ap17:142.1'><span class='pli'>“Rūpena kiṁ tavānena, </span></span></span>\n<span class='verse-line'><span class='segment' id='142.2' data-uid='thi-ap17:142.2'><span class='pli'>diṭṭhe dhamme yathātathe; </span></span></span>\n<span class='verse-line'><span class='segment' id='142.3' data-uid='thi-ap17:142.3'><span class='pli'>Sabbaṁ saṅkhatamevetaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='142.4' data-uid='thi-ap17:142.4'><span class='pli'>anassāsikamittaraṁ”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='143.1' data-uid='thi-ap17:143.1'><span class='pli'>Sā saha tāhi gantvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='143.2' data-uid='thi-ap17:143.2'><span class='pli'>bhikkhunupassayaṁ sakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='143.3' data-uid='thi-ap17:143.3'><span class='pli'>Aḍḍhapallaṅkamābhujja, </span></span></span>\n<span class='verse-line'><span class='segment' id='143.4' data-uid='thi-ap17:143.4'><span class='pli'>nisīdi paramāsane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='144.1' data-uid='thi-ap17:144.1'><span class='pli'>Tadā upāsikā tattha, </span></span></span>\n<span class='verse-line'><span class='segment' id='144.2' data-uid='thi-ap17:144.2'><span class='pli'>buddhasāsanavacchalā; </span></span></span>\n<span class='verse-line'><span class='segment' id='144.3' data-uid='thi-ap17:144.3'><span class='pli'>Tassā pavattiṁ sutvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='144.4' data-uid='thi-ap17:144.4'><span class='pli'>upesuṁ pādavandikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='145.1' data-uid='thi-ap17:145.1'><span class='pli'>Karehi uraṁ pahantā, </span></span></span>\n<span class='verse-line'><span class='segment' id='145.2' data-uid='thi-ap17:145.2'><span class='pli'>chinnamūlā yathā latā; </span></span></span>\n<span class='verse-line'><span class='segment' id='145.3' data-uid='thi-ap17:145.3'><span class='pli'>Rodantā karuṇaṁ ravaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='145.4' data-uid='thi-ap17:145.4'><span class='pli'>sokaṭṭā bhūmipātitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='146.1' data-uid='thi-ap17:146.1'><span class='pli'>“Mā no saraṇade nāthe, </span></span></span>\n<span class='verse-line'><span class='segment' id='146.2' data-uid='thi-ap17:146.2'><span class='pli'>vihāya gami nibbutiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='146.3' data-uid='thi-ap17:146.3'><span class='pli'>Nipatitvāna yācāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='146.4' data-uid='thi-ap17:146.4'><span class='pli'>sabbāyo sirasā mayaṁ”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='147.1' data-uid='thi-ap17:147.1'><span class='pli'>Yā padhānatamā tāsaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='147.2' data-uid='thi-ap17:147.2'><span class='pli'>saddhā paññā upāsikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='147.3' data-uid='thi-ap17:147.3'><span class='pli'>Tassā sīsaṁ pamajjantī, </span></span></span>\n<span class='verse-line'><span class='segment' id='147.4' data-uid='thi-ap17:147.4'><span class='pli'>idaṁ vacanamabravi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='148.1' data-uid='thi-ap17:148.1'><span class='pli'>“Alaṁ puttā visādena, </span></span></span>\n<span class='verse-line'><span class='segment' id='148.2' data-uid='thi-ap17:148.2'><span class='pli'>mārapāsānuvattinā; </span></span></span>\n<span class='verse-line'><span class='segment' id='148.3' data-uid='thi-ap17:148.3'><span class='pli'>Aniccaṁ saṅkhataṁ sabbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='148.4' data-uid='thi-ap17:148.4'><span class='pli'>viyogantaṁ calācalaṁ”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='149.1' data-uid='thi-ap17:149.1'><span class='pli'>Tato sā tā visajjitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='149.2' data-uid='thi-ap17:149.2'><span class='pli'>paṭhamaṁ jhānamuttamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='149.3' data-uid='thi-ap17:149.3'><span class='pli'>Dutiyañca tatiyañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='149.4' data-uid='thi-ap17:149.4'><span class='pli'>samāpajji catutthakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='150.1' data-uid='thi-ap17:150.1'><span class='pli'>Ākāsāyatanañceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='150.2' data-uid='thi-ap17:150.2'><span class='pli'>viññāṇāyatanaṁ tathā; </span></span></span>\n<span class='verse-line'><span class='segment' id='150.3' data-uid='thi-ap17:150.3'><span class='pli'>Ākiñcaṁ nevasaññañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='150.4' data-uid='thi-ap17:150.4'><span class='pli'>samāpajji yathākkamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='151.1' data-uid='thi-ap17:151.1'><span class='pli'>Paṭilomena jhānāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='151.2' data-uid='thi-ap17:151.2'><span class='pli'>samāpajjittha gotamī; </span></span></span>\n<span class='verse-line'><span class='segment' id='151.3' data-uid='thi-ap17:151.3'><span class='pli'>Yāvatā paṭhamaṁ jhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='151.4' data-uid='thi-ap17:151.4'><span class='pli'>tato yāvacatutthakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='152.1' data-uid='thi-ap17:152.1'><span class='pli'>Tato vuṭṭhāya nibbāyi, </span></span></span>\n<span class='verse-line'><span class='segment' id='152.2' data-uid='thi-ap17:152.2'><span class='pli'>dīpaccīva nirāsavā; </span></span></span>\n<span class='verse-line'><span class='segment' id='152.3' data-uid='thi-ap17:152.3'><span class='pli'>Bhūmicālo mahā āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='152.4' data-uid='thi-ap17:152.4'><span class='pli'>nabhasā vijjutā pati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='153.1' data-uid='thi-ap17:153.1'><span class='pli'>Panāditā dundubhiyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='153.2' data-uid='thi-ap17:153.2'><span class='pli'>parideviṁsu devatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='153.3' data-uid='thi-ap17:153.3'><span class='pli'>Pupphavuṭṭhī ca gaganā, </span></span></span>\n<span class='verse-line'><span class='segment' id='153.4' data-uid='thi-ap17:153.4'><span class='pli'>abhivassatha medaniṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='154.1' data-uid='thi-ap17:154.1'><span class='pli'>Kampito merurājāpi, </span></span></span>\n<span class='verse-line'><span class='segment' id='154.2' data-uid='thi-ap17:154.2'><span class='pli'>raṅgamajjhe yathā naṭo; </span></span></span>\n<span class='verse-line'><span class='segment' id='154.3' data-uid='thi-ap17:154.3'><span class='pli'>Sokena cātidīnova, </span></span></span>\n<span class='verse-line'><span class='segment' id='154.4' data-uid='thi-ap17:154.4'><span class='pli'>viravo āsi sāgaro. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='155.1' data-uid='thi-ap17:155.1'><span class='pli'>Devā nāgāsurā brahmā, </span></span></span>\n<span class='verse-line'><span class='segment' id='155.2' data-uid='thi-ap17:155.2'><span class='pli'>saṁviggāhiṁsu taṅkhaṇe; </span></span></span>\n<span class='verse-line'><span class='segment' id='155.3' data-uid='thi-ap17:155.3'><span class='pli'>“Aniccā vata saṅkhārā, </span></span></span>\n<span class='verse-line'><span class='segment' id='155.4' data-uid='thi-ap17:155.4'><span class='pli'>yathāyaṁ vilayaṁ gatā”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='156.1' data-uid='thi-ap17:156.1'><span class='pli'>Yā ce maṁ parivāriṁsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='156.2' data-uid='thi-ap17:156.2'><span class='pli'>satthu sāsanakārikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='156.3' data-uid='thi-ap17:156.3'><span class='pli'>Tāyopi anupādānā, </span></span></span>\n<span class='verse-line'><span class='segment' id='156.4' data-uid='thi-ap17:156.4'><span class='pli'>dīpacci viya nibbutā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='157.1' data-uid='thi-ap17:157.1'><span class='pli'>Hā yogā vippayogantā, </span></span></span>\n<span class='verse-line'><span class='segment' id='157.2' data-uid='thi-ap17:157.2'><span class='pli'>hāniccaṁ sabbasaṅkhataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='157.3' data-uid='thi-ap17:157.3'><span class='pli'>Hā jīvitaṁ vināsantaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='157.4' data-uid='thi-ap17:157.4'><span class='pli'>iccāsi paridevanā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='158.1' data-uid='thi-ap17:158.1'><span class='pli'>Tato devā ca brahmā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='158.2' data-uid='thi-ap17:158.2'><span class='pli'>lokadhammānuvattanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='158.3' data-uid='thi-ap17:158.3'><span class='pli'>Kālānurūpaṁ kubbanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='158.4' data-uid='thi-ap17:158.4'><span class='pli'>upetvā isisattamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='159.1' data-uid='thi-ap17:159.1'><span class='pli'>Tadā āmantayī satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='159.2' data-uid='thi-ap17:159.2'><span class='pli'>ānandaṁ sutasāgaraṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='159.3' data-uid='thi-ap17:159.3'><span class='pli'>“Gacchānanda nivedehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='159.4' data-uid='thi-ap17:159.4'><span class='pli'>bhikkhūnaṁ mātu nibbutiṁ”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='160.1' data-uid='thi-ap17:160.1'><span class='pli'>Tadānando nirānando, </span></span></span>\n<span class='verse-line'><span class='segment' id='160.2' data-uid='thi-ap17:160.2'><span class='pli'>assunā puṇṇalocano; </span></span></span>\n<span class='verse-line'><span class='segment' id='160.3' data-uid='thi-ap17:160.3'><span class='pli'>Gaggarena sarenāha, </span></span></span>\n<span class='verse-line'><span class='segment' id='160.4' data-uid='thi-ap17:160.4'><span class='pli'>“samāgacchantu bhikkhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='161.1' data-uid='thi-ap17:161.1'><span class='pli'>Pubbadakkhiṇapacchāsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='161.2' data-uid='thi-ap17:161.2'><span class='pli'>uttarāya ca santike; </span></span></span>\n<span class='verse-line'><span class='segment' id='161.3' data-uid='thi-ap17:161.3'><span class='pli'>Suṇantu bhāsitaṁ mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='161.4' data-uid='thi-ap17:161.4'><span class='pli'>bhikkhavo sugatorasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='162.1' data-uid='thi-ap17:162.1'><span class='pli'>Yā vaḍḍhayi payattena, </span></span></span>\n<span class='verse-line'><span class='segment' id='162.2' data-uid='thi-ap17:162.2'><span class='pli'>sarīraṁ pacchimaṁ mune; </span></span></span>\n<span class='verse-line'><span class='segment' id='162.3' data-uid='thi-ap17:162.3'><span class='pli'>Sā gotamī gatā santiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='162.4' data-uid='thi-ap17:162.4'><span class='pli'>tārāva sūriyodaye. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='163.1' data-uid='thi-ap17:163.1'><span class='pli'>Buddhamātāti paññattiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='163.2' data-uid='thi-ap17:163.2'><span class='pli'>ṭhapayitvā gatāsamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='163.3' data-uid='thi-ap17:163.3'><span class='pli'>Na yattha pañcanettopi, </span></span></span>\n<span class='verse-line'><span class='segment' id='163.4' data-uid='thi-ap17:163.4'><span class='pli'>gatiṁ dakkhati nāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='164.1' data-uid='thi-ap17:164.1'><span class='pli'>Yassatthi sugate saddhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='164.2' data-uid='thi-ap17:164.2'><span class='pli'>yo ca piyo mahāmune; </span></span></span>\n<span class='verse-line'><span class='segment' id='164.3' data-uid='thi-ap17:164.3'><span class='pli'>Buddhamātussa sakkāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='164.4' data-uid='thi-ap17:164.4'><span class='pli'>karotu sugatoraso”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='165.1' data-uid='thi-ap17:165.1'><span class='pli'>Sudūraṭṭhāpi taṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='165.2' data-uid='thi-ap17:165.2'><span class='pli'>sīghamāgacchu bhikkhavo; </span></span></span>\n<span class='verse-line'><span class='segment' id='165.3' data-uid='thi-ap17:165.3'><span class='pli'>Keci buddhānubhāvena, </span></span></span>\n<span class='verse-line'><span class='segment' id='165.4' data-uid='thi-ap17:165.4'><span class='pli'>keci iddhīsu kovidā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='166.1' data-uid='thi-ap17:166.1'><span class='pli'>Kūṭāgāravare ramme, </span></span></span>\n<span class='verse-line'><span class='segment' id='166.2' data-uid='thi-ap17:166.2'><span class='pli'>sabbasoṇṇamaye subhe; </span></span></span>\n<span class='verse-line'><span class='segment' id='166.3' data-uid='thi-ap17:166.3'><span class='pli'>Mañcakaṁ samāropesuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='166.4' data-uid='thi-ap17:166.4'><span class='pli'>yattha suttāsi gotamī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='167.1' data-uid='thi-ap17:167.1'><span class='pli'>Cattāro lokapālā te, </span></span></span>\n<span class='verse-line'><span class='segment' id='167.2' data-uid='thi-ap17:167.2'><span class='pli'>aṁsehi samadhārayuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='167.3' data-uid='thi-ap17:167.3'><span class='pli'>Sesā sakkādikā devā, </span></span></span>\n<span class='verse-line'><span class='segment' id='167.4' data-uid='thi-ap17:167.4'><span class='pli'>kūṭāgāre samaggahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='168.1' data-uid='thi-ap17:168.1'><span class='pli'>Kūṭāgārāni sabbāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='168.2' data-uid='thi-ap17:168.2'><span class='pli'>āsuṁ pañcasatānipi; </span></span></span>\n<span class='verse-line'><span class='segment' id='168.3' data-uid='thi-ap17:168.3'><span class='pli'>Saradādiccavaṇṇāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='168.4' data-uid='thi-ap17:168.4'><span class='pli'>vissakammakatāni hi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='169.1' data-uid='thi-ap17:169.1'><span class='pli'>Sabbā tāpi bhikkhuniyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='169.2' data-uid='thi-ap17:169.2'><span class='pli'>āsuṁ mañcesu sāyitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='169.3' data-uid='thi-ap17:169.3'><span class='pli'>Devānaṁ khandhamāruḷhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='169.4' data-uid='thi-ap17:169.4'><span class='pli'>niyyanti anupubbaso. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='170.1' data-uid='thi-ap17:170.1'><span class='pli'>Sabbaso chāditaṁ āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='170.2' data-uid='thi-ap17:170.2'><span class='pli'>vitānena nabhattalaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='170.3' data-uid='thi-ap17:170.3'><span class='pli'>Satārā candasūrā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='170.4' data-uid='thi-ap17:170.4'><span class='pli'>lañchitā kanakāmayā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='171.1' data-uid='thi-ap17:171.1'><span class='pli'>Paṭākā ussitānekā, </span></span></span>\n<span class='verse-line'><span class='segment' id='171.2' data-uid='thi-ap17:171.2'><span class='pli'>vitatā pupphakañcukā; </span></span></span>\n<span class='verse-line'><span class='segment' id='171.3' data-uid='thi-ap17:171.3'><span class='pli'>Ogatākāsapadumā, </span></span></span>\n<span class='verse-line'><span class='segment' id='171.4' data-uid='thi-ap17:171.4'><span class='pli'>mahiyā pupphamuggataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='172.1' data-uid='thi-ap17:172.1'><span class='pli'>Dissanti candasūriyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='172.2' data-uid='thi-ap17:172.2'><span class='pli'>pajjalanti ca tārakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='172.3' data-uid='thi-ap17:172.3'><span class='pli'>Majjhaṁ gatopi cādicco, </span></span></span>\n<span class='verse-line'><span class='segment' id='172.4' data-uid='thi-ap17:172.4'><span class='pli'>na tāpesi sasī yathā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='173.1' data-uid='thi-ap17:173.1'><span class='pli'>Devā dibbehi gandhehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='173.2' data-uid='thi-ap17:173.2'><span class='pli'>mālehi surabhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='173.3' data-uid='thi-ap17:173.3'><span class='pli'>Vāditehi ca naccehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='173.4' data-uid='thi-ap17:173.4'><span class='pli'>saṅgītīhi ca pūjayuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='174.1' data-uid='thi-ap17:174.1'><span class='pli'>Nāgāsurā ca brahmāno, </span></span></span>\n<span class='verse-line'><span class='segment' id='174.2' data-uid='thi-ap17:174.2'><span class='pli'>yathāsatti yathābalaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='174.3' data-uid='thi-ap17:174.3'><span class='pli'>Pūjayiṁsu ca niyyantiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='174.4' data-uid='thi-ap17:174.4'><span class='pli'>nibbutaṁ buddhamātaraṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='175.1' data-uid='thi-ap17:175.1'><span class='pli'>Sabbāyo purato nītā, </span></span></span>\n<span class='verse-line'><span class='segment' id='175.2' data-uid='thi-ap17:175.2'><span class='pli'>nibbutā sugatorasā; </span></span></span>\n<span class='verse-line'><span class='segment' id='175.3' data-uid='thi-ap17:175.3'><span class='pli'>Gotamī niyyate pacchā, </span></span></span>\n<span class='verse-line'><span class='segment' id='175.4' data-uid='thi-ap17:175.4'><span class='pli'>sakkatā buddhaposikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='176.1' data-uid='thi-ap17:176.1'><span class='pli'>Purato devamanujā, </span></span></span>\n<span class='verse-line'><span class='segment' id='176.2' data-uid='thi-ap17:176.2'><span class='pli'>sanāgāsurabrahmakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='176.3' data-uid='thi-ap17:176.3'><span class='pli'>Pacchā sasāvako buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='176.4' data-uid='thi-ap17:176.4'><span class='pli'>pūjatthaṁ yāti mātuyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='177.1' data-uid='thi-ap17:177.1'><span class='pli'>Buddhassa parinibbānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='177.2' data-uid='thi-ap17:177.2'><span class='pli'>nedisaṁ āsi yādisaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='177.3' data-uid='thi-ap17:177.3'><span class='pli'>Gotamīparinibbānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='177.4' data-uid='thi-ap17:177.4'><span class='pli'>atevacchariyaṁ ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='178.1' data-uid='thi-ap17:178.1'><span class='pli'>Buddho buddhassa nibbāne, </span></span></span>\n<span class='verse-line'><span class='segment' id='178.2' data-uid='thi-ap17:178.2'><span class='pli'>nopaṭiyādi bhikkhavo; </span></span></span>\n<span class='verse-line'><span class='segment' id='178.3' data-uid='thi-ap17:178.3'><span class='pli'>Buddho gotaminibbāne, </span></span></span>\n<span class='verse-line'><span class='segment' id='178.4' data-uid='thi-ap17:178.4'><span class='pli'>sāriputtādikā tathā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='179.1' data-uid='thi-ap17:179.1'><span class='pli'>Citakāni karitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='179.2' data-uid='thi-ap17:179.2'><span class='pli'>sabbagandhamayāni te; </span></span></span>\n<span class='verse-line'><span class='segment' id='179.3' data-uid='thi-ap17:179.3'><span class='pli'>Gandhacuṇṇapakiṇṇāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='179.4' data-uid='thi-ap17:179.4'><span class='pli'>jhāpayiṁsu ca tā tahiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='180.1' data-uid='thi-ap17:180.1'><span class='pli'>Sesabhāgāni ḍayhiṁsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='180.2' data-uid='thi-ap17:180.2'><span class='pli'>aṭṭhī sesāni sabbaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='180.3' data-uid='thi-ap17:180.3'><span class='pli'>Ānando ca tadāvoca, </span></span></span>\n<span class='verse-line'><span class='segment' id='180.4' data-uid='thi-ap17:180.4'><span class='pli'>saṁvegajanakaṁ vaco. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='181.1' data-uid='thi-ap17:181.1'><span class='pli'>“Gotamī nidhanaṁ yātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='181.2' data-uid='thi-ap17:181.2'><span class='pli'>ḍayhañcassā sarīrakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='181.3' data-uid='thi-ap17:181.3'><span class='pli'>Saṅketaṁ buddhanibbānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='181.4' data-uid='thi-ap17:181.4'><span class='pli'>na cirena bhavissati”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='182.1' data-uid='thi-ap17:182.1'><span class='pli'>Tato gotamidhātūni, </span></span></span>\n<span class='verse-line'><span class='segment' id='182.2' data-uid='thi-ap17:182.2'><span class='pli'>tassā pattagatāni so; </span></span></span>\n<span class='verse-line'><span class='segment' id='182.3' data-uid='thi-ap17:182.3'><span class='pli'>Upanāmesi nāthassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='182.4' data-uid='thi-ap17:182.4'><span class='pli'>ānando buddhacodito. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='183.1' data-uid='thi-ap17:183.1'><span class='pli'>Pāṇinā tāni paggayha, </span></span></span>\n<span class='verse-line'><span class='segment' id='183.2' data-uid='thi-ap17:183.2'><span class='pli'>avoca isisattamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='183.3' data-uid='thi-ap17:183.3'><span class='pli'>“Mahato sāravantassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='183.4' data-uid='thi-ap17:183.4'><span class='pli'>yathā rukkhassa tiṭṭhato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='184.1' data-uid='thi-ap17:184.1'><span class='pli'>Yo so mahattaro khandho, </span></span></span>\n<span class='verse-line'><span class='segment' id='184.2' data-uid='thi-ap17:184.2'><span class='pli'>palujjeyya aniccatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='184.3' data-uid='thi-ap17:184.3'><span class='pli'>Tathā bhikkhunisaṅghassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='184.4' data-uid='thi-ap17:184.4'><span class='pli'>gotamī parinibbutā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='185.1' data-uid='thi-ap17:185.1'><span class='pli'>Aho acchariyaṁ mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='185.2' data-uid='thi-ap17:185.2'><span class='pli'>nibbutāyapi mātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='185.3' data-uid='thi-ap17:185.3'><span class='pli'>Sarīramattasesāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='185.4' data-uid='thi-ap17:185.4'><span class='pli'>natthi sokapariddavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='186.1' data-uid='thi-ap17:186.1'><span class='pli'>Na sociyā paresaṁ sā, </span></span></span>\n<span class='verse-line'><span class='segment' id='186.2' data-uid='thi-ap17:186.2'><span class='pli'>tiṇṇasaṁsārasāgarā; </span></span></span>\n<span class='verse-line'><span class='segment' id='186.3' data-uid='thi-ap17:186.3'><span class='pli'>Parivajjitasantāpā, </span></span></span>\n<span class='verse-line'><span class='segment' id='186.4' data-uid='thi-ap17:186.4'><span class='pli'>sītibhūtā sunibbutā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='187.1' data-uid='thi-ap17:187.1'><span class='pli'>Paṇḍitāsi mahāpaññā, </span></span></span>\n<span class='verse-line'><span class='segment' id='187.2' data-uid='thi-ap17:187.2'><span class='pli'>puthupaññā tatheva ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='187.3' data-uid='thi-ap17:187.3'><span class='pli'>Rattaññū bhikkhunīnaṁ sā, </span></span></span>\n<span class='verse-line'><span class='segment' id='187.4' data-uid='thi-ap17:187.4'><span class='pli'>evaṁ dhāretha bhikkhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='188.1' data-uid='thi-ap17:188.1'><span class='pli'>Iddhīsu ca vasī āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='188.2' data-uid='thi-ap17:188.2'><span class='pli'>dibbāya sotadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='188.3' data-uid='thi-ap17:188.3'><span class='pli'>Cetopariyañāṇassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='188.4' data-uid='thi-ap17:188.4'><span class='pli'>vasī āsi ca gotamī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='189.1' data-uid='thi-ap17:189.1'><span class='pli'>Pubbenivāsamaññāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='189.2' data-uid='thi-ap17:189.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='189.3' data-uid='thi-ap17:189.3'><span class='pli'>Sabbāsavā parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='189.4' data-uid='thi-ap17:189.4'><span class='pli'>natthi tassā punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='190.1' data-uid='thi-ap17:190.1'><span class='pli'>Atthadhammaniruttīsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='190.2' data-uid='thi-ap17:190.2'><span class='pli'>paṭibhāne tatheva ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='190.3' data-uid='thi-ap17:190.3'><span class='pli'>Parisuddhaṁ ahu ñāṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='190.4' data-uid='thi-ap17:190.4'><span class='pli'>tasmā socaniyā na sā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='191.1' data-uid='thi-ap17:191.1'><span class='pli'>Ayoghanahatasseva, </span></span></span>\n<span class='verse-line'><span class='segment' id='191.2' data-uid='thi-ap17:191.2'><span class='pli'>jalato jātavedassa; </span></span></span>\n<span class='verse-line'><span class='segment' id='191.3' data-uid='thi-ap17:191.3'><span class='pli'>Anupubbūpasantassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='191.4' data-uid='thi-ap17:191.4'><span class='pli'>yathā na ñāyate gati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='192.1' data-uid='thi-ap17:192.1'><span class='pli'>Evaṁ sammā vimuttānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='192.2' data-uid='thi-ap17:192.2'><span class='pli'>kāmabandhoghatārinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='192.3' data-uid='thi-ap17:192.3'><span class='pli'>Paññāpetuṁ gati natthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='192.4' data-uid='thi-ap17:192.4'><span class='pli'>pattānaṁ acalaṁ sukhaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='193.1' data-uid='thi-ap17:193.1'><span class='pli'>Attadīpā tato hotha, </span></span></span>\n<span class='verse-line'><span class='segment' id='193.2' data-uid='thi-ap17:193.2'><span class='pli'>satipaṭṭhānagocarā; </span></span></span>\n<span class='verse-line'><span class='segment' id='193.3' data-uid='thi-ap17:193.3'><span class='pli'>Bhāvetvā sattabojjhaṅge, </span></span></span>\n<span class='verse-line'><span class='segment' id='193.4' data-uid='thi-ap17:193.4'><span class='pli'>dukkhassantaṁ karissatha”. </span></span></span></p></blockquote>\n<p><span class='segment' id='194.1' data-uid='thi-ap17:194.1'><span class='pli'>Itthaṁ sudaṁ mahāpajāpatigotamī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='195.1' data-uid='thi-ap17:195.1'><span class='pli'>Mahāpajāpatigotamītheriyāpadānaṁ sattamaṁ. </span></span></p></article>\n"
  },
  "thi-ap18": {
    "previous": "thi-ap17",
    "next": "thi-ap19",
    "content": "<article id='thi-ap18'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap18:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap18:0.2'><span class='pli'>Ekūposathikavagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap18:0.3'><span class='pli'>Khemātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap18:1.1'><span class='pli'>“Padumuttaro nāma jino, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap18:1.2'><span class='pli'>sabbadhammesu cakkhumā; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap18:1.3'><span class='pli'>Ito satasahassamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap18:1.4'><span class='pli'>kappe uppajji nāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap18:2.1'><span class='pli'>Tadāhaṁ haṁsavatiyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap18:2.2'><span class='pli'>jātā seṭṭhikule ahuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap18:2.3'><span class='pli'>Nānāratanapajjote, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap18:2.4'><span class='pli'>mahāsukhasamappitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap18:3.1'><span class='pli'>Upetvā taṁ mahāvīraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap18:3.2'><span class='pli'>assosiṁ dhammadesanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap18:3.3'><span class='pli'>Tato jātappasādāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap18:3.4'><span class='pli'>upemi saraṇaṁ jinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap18:4.1'><span class='pli'>Mātaraṁ pitarañcāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap18:4.2'><span class='pli'>āyācitvā vināyakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap18:4.3'><span class='pli'>Nimantayitvā sattāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap18:4.4'><span class='pli'>bhojayiṁ sahasāvakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap18:5.1'><span class='pli'>Atikkante ca sattāhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap18:5.2'><span class='pli'>mahāpaññānamuttamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap18:5.3'><span class='pli'>Bhikkhuniṁ etadaggamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap18:5.4'><span class='pli'>ṭhapesi narasārathi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap18:6.1'><span class='pli'>Taṁ sutvā muditā hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap18:6.2'><span class='pli'>puno tassa mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap18:6.3'><span class='pli'>Kāraṁ katvāna taṁ ṭhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap18:6.4'><span class='pli'>paṇipacca paṇīdahiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap18:7.1'><span class='pli'>Tato mama jino āha, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap18:7.2'><span class='pli'>‘sijjhataṁ paṇidhī tava; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap18:7.3'><span class='pli'>Sasaṅghe me kataṁ kāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap18:7.4'><span class='pli'>appameyyaphalaṁ tayā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap18:8.1'><span class='pli'>Satasahassito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap18:8.2'><span class='pli'>okkākakulasambhavo; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap18:8.3'><span class='pli'>Gotamo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap18:8.4'><span class='pli'>satthā loke bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap18:9.1'><span class='pli'>Tassa dhammesu dāyādā, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap18:9.2'><span class='pli'>orasā dhammanimmitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap18:9.3'><span class='pli'>Etadaggamanuppattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap18:9.4'><span class='pli'>khemā nāma bhavissati’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap18:10.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap18:10.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap18:10.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap18:10.4'><span class='pli'>tāvatiṁsūpagā ahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap18:11.1'><span class='pli'>Tato cutā yāmamagaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap18:11.2'><span class='pli'>tatohaṁ tusitaṁ gatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap18:11.3'><span class='pli'>Tato ca nimmānaratiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap18:11.4'><span class='pli'>vasavattipuraṁ tato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap18:12.1'><span class='pli'>Yattha yatthūpapajjāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap18:12.2'><span class='pli'>tassa kammassa vāhasā; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap18:12.3'><span class='pli'>Tattha tattheva rājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap18:12.4'><span class='pli'>mahesittamakārayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap18:13.1'><span class='pli'>Tato cutā manussatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap18:13.2'><span class='pli'>rājūnaṁ cakkavattinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap18:13.3'><span class='pli'>Maṇḍalīnañca rājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap18:13.4'><span class='pli'>mahesittamakārayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap18:14.1'><span class='pli'>Sampattiṁ anubhotvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap18:14.2'><span class='pli'>devesu manujesu ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap18:14.3'><span class='pli'>Sabbattha sukhitā hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap18:14.4'><span class='pli'>nekakappesu saṁsariṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap18:15.1'><span class='pli'>Ekanavutito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap18:15.2'><span class='pli'>vipassī lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap18:15.3'><span class='pli'>Uppajji cārudassano, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap18:15.4'><span class='pli'>sabbadhammavipassako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap18:16.1'><span class='pli'>Tamahaṁ lokanāyakaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap18:16.2'><span class='pli'>upetvā narasārathiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap18:16.3'><span class='pli'>Dhammaṁ bhaṇitaṁ sutvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap18:16.4'><span class='pli'>pabbajiṁ anagāriyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap18:17.1'><span class='pli'>Dasavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap18:17.2'><span class='pli'>tassa vīrassa sāsane; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap18:17.3'><span class='pli'>Brahmacariyaṁ caritvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap18:17.4'><span class='pli'>yuttayogā bahussutā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap18:18.1'><span class='pli'>Paccayākārakusalā, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap18:18.2'><span class='pli'>catusaccavisāradā; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap18:18.3'><span class='pli'>Nipuṇā cittakathikā, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap18:18.4'><span class='pli'>satthusāsanakārikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap18:19.1'><span class='pli'>Tato cutāhaṁ tusitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap18:19.2'><span class='pli'>upapannā yasassinī; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap18:19.3'><span class='pli'>Abhibhomi tahiṁ aññe, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap18:19.4'><span class='pli'>brahmacārīphalenahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap18:20.1'><span class='pli'>Yattha yatthūpapannāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap18:20.2'><span class='pli'>mahābhogā mahaddhanā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap18:20.3'><span class='pli'>Medhāvinī rūpavatī, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap18:20.4'><span class='pli'>vinītaparisāpi ca. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap18:21.1'><span class='pli'>Bhavāmi tena kammena, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap18:21.2'><span class='pli'>yogena jinasāsane; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap18:21.3'><span class='pli'>Sabbā sampattiyo mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap18:21.4'><span class='pli'>sulabhā manaso piyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap18:22.1'><span class='pli'>Yopi me bhavate bhattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap18:22.2'><span class='pli'>yattha yattha gatāyapi; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap18:22.3'><span class='pli'>Vimāneti na maṁ koci, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap18:22.4'><span class='pli'>paṭipattibalena me. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap18:23.1'><span class='pli'>Imamhi bhaddake kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap18:23.2'><span class='pli'>brahmabandhu mahāyaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap18:23.3'><span class='pli'>Nāmena koṇāgamano, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='thi-ap18:23.4'><span class='pli'>uppajji vadataṁ varo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap18:24.1'><span class='pli'>Tadā hi bārāṇasiyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap18:24.2'><span class='pli'>susamiddhakulappajā; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap18:24.3'><span class='pli'>Dhanañjānī sumedhā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='thi-ap18:24.4'><span class='pli'>ahampi ca tayo janā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='thi-ap18:25.1'><span class='pli'>Saṅghārāmamadāsimha, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='thi-ap18:25.2'><span class='pli'>dānasahāyikā pure; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='thi-ap18:25.3'><span class='pli'>Saṅghassa ca vihārampi, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='thi-ap18:25.4'><span class='pli'>uddissa kārikā mayaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='thi-ap18:26.1'><span class='pli'>Tato cutā mayaṁ sabbā, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='thi-ap18:26.2'><span class='pli'>tāvatiṁsūpagā ahuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='thi-ap18:26.3'><span class='pli'>Yasasā aggataṁ pattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='thi-ap18:26.4'><span class='pli'>manussesu tatheva ca. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='thi-ap18:27.1'><span class='pli'>Imasmiṁyeva kappamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='thi-ap18:27.2'><span class='pli'>brahmabandhu mahāyaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='thi-ap18:27.3'><span class='pli'>Kassapo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='thi-ap18:27.4'><span class='pli'>uppajji vadataṁ varo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='thi-ap18:28.1'><span class='pli'>Upaṭṭhāko mahesissa, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='thi-ap18:28.2'><span class='pli'>tadā āsi narissaro; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='thi-ap18:28.3'><span class='pli'>Kāsirājā kikī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='thi-ap18:28.4'><span class='pli'>bārāṇasipuruttame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='thi-ap18:29.1'><span class='pli'>Tassāsiṁ jeṭṭhikā dhītā, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='thi-ap18:29.2'><span class='pli'>samaṇī iti vissutā; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='thi-ap18:29.3'><span class='pli'>Dhammaṁ sutvā jinaggassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='thi-ap18:29.4'><span class='pli'>pabbajjaṁ samarocayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='thi-ap18:30.1'><span class='pli'>Anujāni na no tāto, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='thi-ap18:30.2'><span class='pli'>agāreva tadā mayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='thi-ap18:30.3'><span class='pli'>Vīsavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='thi-ap18:30.4'><span class='pli'>vicarimha atanditā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='thi-ap18:31.1'><span class='pli'>Komāribrahmacariyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='thi-ap18:31.2'><span class='pli'>rājakaññā sukhedhitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='thi-ap18:31.3'><span class='pli'>Buddhopaṭṭhānaniratā, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='thi-ap18:31.4'><span class='pli'>muditā satta dhītaro. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='thi-ap18:32.1'><span class='pli'>Samaṇī samaṇaguttā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='thi-ap18:32.2'><span class='pli'>bhikkhunī bhikkhudāyikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='thi-ap18:32.3'><span class='pli'>Dhammā ceva sudhammā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='thi-ap18:32.4'><span class='pli'>sattamī saṅghadāyikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='thi-ap18:33.1'><span class='pli'>Ahaṁ uppalavaṇṇā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='thi-ap18:33.2'><span class='pli'>paṭācārā ca kuṇḍalā; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='thi-ap18:33.3'><span class='pli'>Kisāgotamī dhammadinnā, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='thi-ap18:33.4'><span class='pli'>visākhā hoti sattamī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='34.1' data-uid='thi-ap18:34.1'><span class='pli'>Kadāci so narādicco, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='thi-ap18:34.2'><span class='pli'>dhammaṁ desesi abbhutaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='thi-ap18:34.3'><span class='pli'>Mahānidānasuttantaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.4' data-uid='thi-ap18:34.4'><span class='pli'>sutvā taṁ pariyāpuṇiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='35.1' data-uid='thi-ap18:35.1'><span class='pli'>Tehi kammehi sukatehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.2' data-uid='thi-ap18:35.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='35.3' data-uid='thi-ap18:35.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.4' data-uid='thi-ap18:35.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='36.1' data-uid='thi-ap18:36.1'><span class='pli'>Pacchime ca bhave dāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.2' data-uid='thi-ap18:36.2'><span class='pli'>sākalāya puruttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='36.3' data-uid='thi-ap18:36.3'><span class='pli'>Rañño maddassa dhītāmhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.4' data-uid='thi-ap18:36.4'><span class='pli'>manāpā dayitā piyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='37.1' data-uid='thi-ap18:37.1'><span class='pli'>Saha me jātamattamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.2' data-uid='thi-ap18:37.2'><span class='pli'>khemaṁ tamhi pure ahu; </span></span></span>\n<span class='verse-line'><span class='segment' id='37.3' data-uid='thi-ap18:37.3'><span class='pli'>Tato khemāti nāmaṁ me, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.4' data-uid='thi-ap18:37.4'><span class='pli'>guṇato upapajjatha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='38.1' data-uid='thi-ap18:38.1'><span class='pli'>Yadāhaṁ yobbanaṁ pattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.2' data-uid='thi-ap18:38.2'><span class='pli'>rūpalāvaññabhūsitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='38.3' data-uid='thi-ap18:38.3'><span class='pli'>Tadā adāsi maṁ tāto, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.4' data-uid='thi-ap18:38.4'><span class='pli'>bimbisārassa rājino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='39.1' data-uid='thi-ap18:39.1'><span class='pli'>Tassāhaṁ suppiyā āsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.2' data-uid='thi-ap18:39.2'><span class='pli'>rūpake lāyane ratā; </span></span></span>\n<span class='verse-line'><span class='segment' id='39.3' data-uid='thi-ap18:39.3'><span class='pli'>Rūpānaṁ dosavādīti, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.4' data-uid='thi-ap18:39.4'><span class='pli'>na upesiṁ mahādayaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='40.1' data-uid='thi-ap18:40.1'><span class='pli'>Bimbisāro tadā rājā, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.2' data-uid='thi-ap18:40.2'><span class='pli'>mamānuggahabuddhiyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='40.3' data-uid='thi-ap18:40.3'><span class='pli'>Vaṇṇayitvā veḷuvanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.4' data-uid='thi-ap18:40.4'><span class='pli'>gāyake gāpayī mamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='41.1' data-uid='thi-ap18:41.1'><span class='pli'>Rammaṁ veḷuvanaṁ yena, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.2' data-uid='thi-ap18:41.2'><span class='pli'>na diṭṭhaṁ sugatālayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='41.3' data-uid='thi-ap18:41.3'><span class='pli'>Na tena nandanaṁ diṭṭhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.4' data-uid='thi-ap18:41.4'><span class='pli'>iti maññāmase mayaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='42.1' data-uid='thi-ap18:42.1'><span class='pli'>Yena veḷuvanaṁ diṭṭhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.2' data-uid='thi-ap18:42.2'><span class='pli'>naranandananandanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='42.3' data-uid='thi-ap18:42.3'><span class='pli'>Sudiṭṭhaṁ nandanaṁ tena, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.4' data-uid='thi-ap18:42.4'><span class='pli'>amarindasunandanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='43.1' data-uid='thi-ap18:43.1'><span class='pli'>Vihāya nandanaṁ devā, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.2' data-uid='thi-ap18:43.2'><span class='pli'>otaritvā mahītalaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='43.3' data-uid='thi-ap18:43.3'><span class='pli'>Rammaṁ veḷuvanaṁ disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.4' data-uid='thi-ap18:43.4'><span class='pli'>na tappanti suvimhitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='44.1' data-uid='thi-ap18:44.1'><span class='pli'>Rājapuññena nibbattaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.2' data-uid='thi-ap18:44.2'><span class='pli'>buddhapuññena bhūsitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='44.3' data-uid='thi-ap18:44.3'><span class='pli'>Ko vattā tassa nissesaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.4' data-uid='thi-ap18:44.4'><span class='pli'>vanassa guṇasañcayaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='45.1' data-uid='thi-ap18:45.1'><span class='pli'>Taṁ sutvā vanasamiddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.2' data-uid='thi-ap18:45.2'><span class='pli'>mama sotamanoharaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='45.3' data-uid='thi-ap18:45.3'><span class='pli'>Daṭṭhukāmā tamuyyānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.4' data-uid='thi-ap18:45.4'><span class='pli'>rañño ārocayiṁ tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='46.1' data-uid='thi-ap18:46.1'><span class='pli'>Mahatā parivārena, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.2' data-uid='thi-ap18:46.2'><span class='pli'>tadā ca so mahīpati; </span></span></span>\n<span class='verse-line'><span class='segment' id='46.3' data-uid='thi-ap18:46.3'><span class='pli'>Maṁ pesesi tamuyyānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.4' data-uid='thi-ap18:46.4'><span class='pli'>dassanāya samussukaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='47.1' data-uid='thi-ap18:47.1'><span class='pli'>‘Gaccha passa mahābhoge, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.2' data-uid='thi-ap18:47.2'><span class='pli'>vanaṁ nettarasāyanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='47.3' data-uid='thi-ap18:47.3'><span class='pli'>Yaṁ sadā bhāti siriyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.4' data-uid='thi-ap18:47.4'><span class='pli'>sugatābhānurañjitaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='48.1' data-uid='thi-ap18:48.1'><span class='pli'>‘Yadā ca piṇḍāya muni, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.2' data-uid='thi-ap18:48.2'><span class='pli'>giribbajapuruttamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='48.3' data-uid='thi-ap18:48.3'><span class='pli'>Paviṭṭhohaṁ tadāyeva, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.4' data-uid='thi-ap18:48.4'><span class='pli'>vanaṁ daṭṭhumupāgamiṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='49.1' data-uid='thi-ap18:49.1'><span class='pli'>Tadā taṁ phullavipinaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.2' data-uid='thi-ap18:49.2'><span class='pli'>nānābhamarakūjitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='49.3' data-uid='thi-ap18:49.3'><span class='pli'>Kokilāgītasahitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.4' data-uid='thi-ap18:49.4'><span class='pli'>mayūragaṇanaccitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='50.1' data-uid='thi-ap18:50.1'><span class='pli'>Appasaddamanākiṇṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.2' data-uid='thi-ap18:50.2'><span class='pli'>nānācaṅkamabhūsitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='50.3' data-uid='thi-ap18:50.3'><span class='pli'>Kuṭimaṇḍapasaṅkiṇṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.4' data-uid='thi-ap18:50.4'><span class='pli'>yogīvaravirājitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='51.1' data-uid='thi-ap18:51.1'><span class='pli'>Vicarantī amaññissaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.2' data-uid='thi-ap18:51.2'><span class='pli'>‘saphalaṁ nayanaṁ mama’; </span></span></span>\n<span class='verse-line'><span class='segment' id='51.3' data-uid='thi-ap18:51.3'><span class='pli'>Tatthāpi taruṇaṁ bhikkhuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.4' data-uid='thi-ap18:51.4'><span class='pli'>yuttaṁ disvā vicintayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='52.1' data-uid='thi-ap18:52.1'><span class='pli'>‘Īdise vipine ramme, </span></span></span>\n<span class='verse-line'><span class='segment' id='52.2' data-uid='thi-ap18:52.2'><span class='pli'>ṭhitoyaṁ navayobbane; </span></span></span>\n<span class='verse-line'><span class='segment' id='52.3' data-uid='thi-ap18:52.3'><span class='pli'>Vasantamiva kantena, </span></span></span>\n<span class='verse-line'><span class='segment' id='52.4' data-uid='thi-ap18:52.4'><span class='pli'>rūpena ca samanvito. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='53.1' data-uid='thi-ap18:53.1'><span class='pli'>Nisinno rukkhamūlamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='53.2' data-uid='thi-ap18:53.2'><span class='pli'>muṇḍo saṅghāṭipāruto; </span></span></span>\n<span class='verse-line'><span class='segment' id='53.3' data-uid='thi-ap18:53.3'><span class='pli'>Jhāyate vatayaṁ bhikkhu, </span></span></span>\n<span class='verse-line'><span class='segment' id='53.4' data-uid='thi-ap18:53.4'><span class='pli'>hitvā visayajaṁ ratiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='54.1' data-uid='thi-ap18:54.1'><span class='pli'>Nanu nāma gahaṭṭhena, </span></span></span>\n<span class='verse-line'><span class='segment' id='54.2' data-uid='thi-ap18:54.2'><span class='pli'>kāmaṁ bhutvā yathāsukhaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='54.3' data-uid='thi-ap18:54.3'><span class='pli'>Pacchā jiṇṇena dhammoyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='54.4' data-uid='thi-ap18:54.4'><span class='pli'>caritabbo subhaddako’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='55.1' data-uid='thi-ap18:55.1'><span class='pli'>‘Suññakan’ti viditvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='55.2' data-uid='thi-ap18:55.2'><span class='pli'>‘gandhagehaṁ jinālayaṁ’; </span></span></span>\n<span class='verse-line'><span class='segment' id='55.3' data-uid='thi-ap18:55.3'><span class='pli'>Upetvā jinamaddakkhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='55.4' data-uid='thi-ap18:55.4'><span class='pli'>udayantaṁva bhākaraṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='56.1' data-uid='thi-ap18:56.1'><span class='pli'>Ekakaṁ sukhamāsīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='56.2' data-uid='thi-ap18:56.2'><span class='pli'>bījamānaṁ varitthiyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='56.3' data-uid='thi-ap18:56.3'><span class='pli'>Disvānevaṁ vicintesiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='56.4' data-uid='thi-ap18:56.4'><span class='pli'>‘nāyaṁ lūkho narāsabho. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='57.1' data-uid='thi-ap18:57.1'><span class='pli'>Sā kaññā kanakābhāsā, </span></span></span>\n<span class='verse-line'><span class='segment' id='57.2' data-uid='thi-ap18:57.2'><span class='pli'>padumānanalocanā; </span></span></span>\n<span class='verse-line'><span class='segment' id='57.3' data-uid='thi-ap18:57.3'><span class='pli'>Bimboṭṭhī kundadasanā, </span></span></span>\n<span class='verse-line'><span class='segment' id='57.4' data-uid='thi-ap18:57.4'><span class='pli'>manonettarasāyanā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='58.1' data-uid='thi-ap18:58.1'><span class='pli'>Hemadolābhasavanā, </span></span></span>\n<span class='verse-line'><span class='segment' id='58.2' data-uid='thi-ap18:58.2'><span class='pli'>kalikākārasutthanī; </span></span></span>\n<span class='verse-line'><span class='segment' id='58.3' data-uid='thi-ap18:58.3'><span class='pli'>Vedimajjhāva sussoṇī, </span></span></span>\n<span class='verse-line'><span class='segment' id='58.4' data-uid='thi-ap18:58.4'><span class='pli'>rambhoru cārubhūsanā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='59.1' data-uid='thi-ap18:59.1'><span class='pli'>Rattaṁsakupasambyānā, </span></span></span>\n<span class='verse-line'><span class='segment' id='59.2' data-uid='thi-ap18:59.2'><span class='pli'>nīlamaṭṭhanivāsanā; </span></span></span>\n<span class='verse-line'><span class='segment' id='59.3' data-uid='thi-ap18:59.3'><span class='pli'>Atappaneyyarūpena, </span></span></span>\n<span class='verse-line'><span class='segment' id='59.4' data-uid='thi-ap18:59.4'><span class='pli'>hāsabhāvasamanvitā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='60.1' data-uid='thi-ap18:60.1'><span class='pli'>Disvā tamevaṁ cintesiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='60.2' data-uid='thi-ap18:60.2'><span class='pli'>‘ahoyamabhirūpinī; </span></span></span>\n<span class='verse-line'><span class='segment' id='60.3' data-uid='thi-ap18:60.3'><span class='pli'>Na mayānena nettena, </span></span></span>\n<span class='verse-line'><span class='segment' id='60.4' data-uid='thi-ap18:60.4'><span class='pli'>diṭṭhapubbā kudācanaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='61.1' data-uid='thi-ap18:61.1'><span class='pli'>Tato jarābhibhūtā sā, </span></span></span>\n<span class='verse-line'><span class='segment' id='61.2' data-uid='thi-ap18:61.2'><span class='pli'>vivaṇṇā vikatānanā; </span></span></span>\n<span class='verse-line'><span class='segment' id='61.3' data-uid='thi-ap18:61.3'><span class='pli'>Bhinnadantā setasirā, </span></span></span>\n<span class='verse-line'><span class='segment' id='61.4' data-uid='thi-ap18:61.4'><span class='pli'>salālā vadanāsuci. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='62.1' data-uid='thi-ap18:62.1'><span class='pli'>Saṅkhittakaṇṇā setakkhī, </span></span></span>\n<span class='verse-line'><span class='segment' id='62.2' data-uid='thi-ap18:62.2'><span class='pli'>lambāsubhapayodharā; </span></span></span>\n<span class='verse-line'><span class='segment' id='62.3' data-uid='thi-ap18:62.3'><span class='pli'>Valivitatasabbaṅgī, </span></span></span>\n<span class='verse-line'><span class='segment' id='62.4' data-uid='thi-ap18:62.4'><span class='pli'>sirāvitatadehinī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='63.1' data-uid='thi-ap18:63.1'><span class='pli'>Nataṅgā daṇḍadutiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='63.2' data-uid='thi-ap18:63.2'><span class='pli'>upphāsulikatā kisā; </span></span></span>\n<span class='verse-line'><span class='segment' id='63.3' data-uid='thi-ap18:63.3'><span class='pli'>Pavedhamānā patitā, </span></span></span>\n<span class='verse-line'><span class='segment' id='63.4' data-uid='thi-ap18:63.4'><span class='pli'>nissasantī muhuṁ muhuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='64.1' data-uid='thi-ap18:64.1'><span class='pli'>Tato me āsi saṁvego, </span></span></span>\n<span class='verse-line'><span class='segment' id='64.2' data-uid='thi-ap18:64.2'><span class='pli'>abbhuto lomahaṁsano; </span></span></span>\n<span class='verse-line'><span class='segment' id='64.3' data-uid='thi-ap18:64.3'><span class='pli'>‘Dhiratthu rūpaṁ asuciṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='64.4' data-uid='thi-ap18:64.4'><span class='pli'>ramante yattha bālisā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='65.1' data-uid='thi-ap18:65.1'><span class='pli'>Tadā mahākāruṇiko, </span></span></span>\n<span class='verse-line'><span class='segment' id='65.2' data-uid='thi-ap18:65.2'><span class='pli'>disvā saṁviggamānasaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='65.3' data-uid='thi-ap18:65.3'><span class='pli'>Udaggacitto sugato, </span></span></span>\n<span class='verse-line'><span class='segment' id='65.4' data-uid='thi-ap18:65.4'><span class='pli'>imā gāthā abhāsatha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='66.1' data-uid='thi-ap18:66.1'><span class='pli'>‘Āturaṁ asuciṁ pūtiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='66.2' data-uid='thi-ap18:66.2'><span class='pli'>passa kheme samussayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='66.3' data-uid='thi-ap18:66.3'><span class='pli'>Uggharantaṁ paggharantaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='66.4' data-uid='thi-ap18:66.4'><span class='pli'>bālānaṁ abhinanditaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='67.1' data-uid='thi-ap18:67.1'><span class='pli'>Asubhāya cittaṁ bhāvehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='67.2' data-uid='thi-ap18:67.2'><span class='pli'>ekaggaṁ susamāhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='67.3' data-uid='thi-ap18:67.3'><span class='pli'>Sati kāyagatā tyatthu, </span></span></span>\n<span class='verse-line'><span class='segment' id='67.4' data-uid='thi-ap18:67.4'><span class='pli'>nibbidā bahulā bhava. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='68.1' data-uid='thi-ap18:68.1'><span class='pli'>Yathā idaṁ tathā etaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='68.2' data-uid='thi-ap18:68.2'><span class='pli'>yathā etaṁ tathā idaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='68.3' data-uid='thi-ap18:68.3'><span class='pli'>Ajjhattañca bahiddhā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='68.4' data-uid='thi-ap18:68.4'><span class='pli'>kāye chandaṁ virājaya. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='69.1' data-uid='thi-ap18:69.1'><span class='pli'>Animittañca bhāvehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='69.2' data-uid='thi-ap18:69.2'><span class='pli'>mānānusayamujjaha; </span></span></span>\n<span class='verse-line'><span class='segment' id='69.3' data-uid='thi-ap18:69.3'><span class='pli'>Tato mānābhisamayā, </span></span></span>\n<span class='verse-line'><span class='segment' id='69.4' data-uid='thi-ap18:69.4'><span class='pli'>upasantā carissasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='70.1' data-uid='thi-ap18:70.1'><span class='pli'>Ye rāgarattānupatanti sotaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='70.2' data-uid='thi-ap18:70.2'><span class='pli'>Sayaṁ kataṁ makkaṭakova jālaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='70.3' data-uid='thi-ap18:70.3'><span class='pli'>Etampi chetvāna paribbajanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='70.4' data-uid='thi-ap18:70.4'><span class='pli'>Anapekkhino kāmasukhaṁ pahāya’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='71.1' data-uid='thi-ap18:71.1'><span class='pli'>Tato kallitacittaṁ maṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='71.2' data-uid='thi-ap18:71.2'><span class='pli'>ñatvāna narasārathi; </span></span></span>\n<span class='verse-line'><span class='segment' id='71.3' data-uid='thi-ap18:71.3'><span class='pli'>Mahānidānaṁ desesi, </span></span></span>\n<span class='verse-line'><span class='segment' id='71.4' data-uid='thi-ap18:71.4'><span class='pli'>suttantaṁ vinayāya me. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='72.1' data-uid='thi-ap18:72.1'><span class='pli'>Sutvā suttantaseṭṭhaṁ taṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='72.2' data-uid='thi-ap18:72.2'><span class='pli'>pubbasaññamanussariṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='72.3' data-uid='thi-ap18:72.3'><span class='pli'>Tattha ṭhitāvahaṁ santī, </span></span></span>\n<span class='verse-line'><span class='segment' id='72.4' data-uid='thi-ap18:72.4'><span class='pli'>dhammacakkhuṁ visodhayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='73.1' data-uid='thi-ap18:73.1'><span class='pli'>Nipatitvā mahesissa, </span></span></span>\n<span class='verse-line'><span class='segment' id='73.2' data-uid='thi-ap18:73.2'><span class='pli'>pādamūlamhi tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='73.3' data-uid='thi-ap18:73.3'><span class='pli'>Accayaṁ desanatthāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='73.4' data-uid='thi-ap18:73.4'><span class='pli'>idaṁ vacanamabraviṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='74.1' data-uid='thi-ap18:74.1'><span class='pli'>‘Namo te sabbadassāvī, </span></span></span>\n<span class='verse-line'><span class='segment' id='74.2' data-uid='thi-ap18:74.2'><span class='pli'>namo te karuṇākara; </span></span></span>\n<span class='verse-line'><span class='segment' id='74.3' data-uid='thi-ap18:74.3'><span class='pli'>Namo te tiṇṇasaṁsāra, </span></span></span>\n<span class='verse-line'><span class='segment' id='74.4' data-uid='thi-ap18:74.4'><span class='pli'>namo te amataṁdada. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='75.1' data-uid='thi-ap18:75.1'><span class='pli'>Diṭṭhigahanapakkhandā, </span></span></span>\n<span class='verse-line'><span class='segment' id='75.2' data-uid='thi-ap18:75.2'><span class='pli'>kāmarāgavimohitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='75.3' data-uid='thi-ap18:75.3'><span class='pli'>Tayā sammā upāyena, </span></span></span>\n<span class='verse-line'><span class='segment' id='75.4' data-uid='thi-ap18:75.4'><span class='pli'>vinītā vinaye ratā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='76.1' data-uid='thi-ap18:76.1'><span class='pli'>Adassanena vibhogā, </span></span></span>\n<span class='verse-line'><span class='segment' id='76.2' data-uid='thi-ap18:76.2'><span class='pli'>tādisānaṁ mahesinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='76.3' data-uid='thi-ap18:76.3'><span class='pli'>Anubhonti mahādukkhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='76.4' data-uid='thi-ap18:76.4'><span class='pli'>sattā saṁsārasāgare. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='77.1' data-uid='thi-ap18:77.1'><span class='pli'>Yadāhaṁ lokasaraṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='77.2' data-uid='thi-ap18:77.2'><span class='pli'>araṇaṁ araṇantaguṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='77.3' data-uid='thi-ap18:77.3'><span class='pli'>Nāddasāmi adūraṭṭhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='77.4' data-uid='thi-ap18:77.4'><span class='pli'>desayāmi tamaccayaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='78.1' data-uid='thi-ap18:78.1'><span class='pli'>Mahāhitaṁ varadadaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='78.2' data-uid='thi-ap18:78.2'><span class='pli'>ahitoti visaṅkitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='78.3' data-uid='thi-ap18:78.3'><span class='pli'>Nopesiṁ rūpaniratā, </span></span></span>\n<span class='verse-line'><span class='segment' id='78.4' data-uid='thi-ap18:78.4'><span class='pli'>desayāmi tamaccayaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='79.1' data-uid='thi-ap18:79.1'><span class='pli'>Tadā madhuranigghoso, </span></span></span>\n<span class='verse-line'><span class='segment' id='79.2' data-uid='thi-ap18:79.2'><span class='pli'>mahākāruṇiko jino; </span></span></span>\n<span class='verse-line'><span class='segment' id='79.3' data-uid='thi-ap18:79.3'><span class='pli'>Avoca tiṭṭha khemeti, </span></span></span>\n<span class='verse-line'><span class='segment' id='79.4' data-uid='thi-ap18:79.4'><span class='pli'>siñcanto amatena maṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='80.1' data-uid='thi-ap18:80.1'><span class='pli'>Tadā paṇamya sirasā, </span></span></span>\n<span class='verse-line'><span class='segment' id='80.2' data-uid='thi-ap18:80.2'><span class='pli'>katvā ca naṁ padakkhiṇaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='80.3' data-uid='thi-ap18:80.3'><span class='pli'>Gantvā disvā narapatiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='80.4' data-uid='thi-ap18:80.4'><span class='pli'>idaṁ vacanamabraviṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='81.1' data-uid='thi-ap18:81.1'><span class='pli'>‘Aho sammā upāyo te, </span></span></span>\n<span class='verse-line'><span class='segment' id='81.2' data-uid='thi-ap18:81.2'><span class='pli'>cintitoyamarindama; </span></span></span>\n<span class='verse-line'><span class='segment' id='81.3' data-uid='thi-ap18:81.3'><span class='pli'>Vanadassanakāmāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='81.4' data-uid='thi-ap18:81.4'><span class='pli'>diṭṭho nibbānato muni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='82.1' data-uid='thi-ap18:82.1'><span class='pli'>Yadi te ruccate rāja, </span></span></span>\n<span class='verse-line'><span class='segment' id='82.2' data-uid='thi-ap18:82.2'><span class='pli'>sāsane tassa tādino; </span></span></span>\n<span class='verse-line'><span class='segment' id='82.3' data-uid='thi-ap18:82.3'><span class='pli'>Pabbajissāmi rūpehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='82.4' data-uid='thi-ap18:82.4'><span class='pli'>nibbinnā munivāṇinā’. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='83.1' data-uid='thi-ap18:83.1'><span class='pli'>Dutiyaṁ bhāṇavāraṁ. </span></span></p>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='84.1' data-uid='thi-ap18:84.1'><span class='pli'>Añjaliṁ paggahetvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='84.2' data-uid='thi-ap18:84.2'><span class='pli'>tadāha sa mahīpati; </span></span></span>\n<span class='verse-line'><span class='segment' id='84.3' data-uid='thi-ap18:84.3'><span class='pli'>‘Anujānāmi te bhadde, </span></span></span>\n<span class='verse-line'><span class='segment' id='84.4' data-uid='thi-ap18:84.4'><span class='pli'>pabbajjā tava sijjhatu’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='85.1' data-uid='thi-ap18:85.1'><span class='pli'>Pabbajitvā tadā cāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='85.2' data-uid='thi-ap18:85.2'><span class='pli'>addhamāse upaṭṭhite; </span></span></span>\n<span class='verse-line'><span class='segment' id='85.3' data-uid='thi-ap18:85.3'><span class='pli'>Dīpodayañca bhedañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='85.4' data-uid='thi-ap18:85.4'><span class='pli'>disvā saṁviggamānasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='86.1' data-uid='thi-ap18:86.1'><span class='pli'>Nibbinnā sabbasaṅkhāre, </span></span></span>\n<span class='verse-line'><span class='segment' id='86.2' data-uid='thi-ap18:86.2'><span class='pli'>paccayākārakovidā; </span></span></span>\n<span class='verse-line'><span class='segment' id='86.3' data-uid='thi-ap18:86.3'><span class='pli'>Caturoghe atikkamma, </span></span></span>\n<span class='verse-line'><span class='segment' id='86.4' data-uid='thi-ap18:86.4'><span class='pli'>arahattamapāpuṇiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='87.1' data-uid='thi-ap18:87.1'><span class='pli'>Iddhīsu ca vasī āsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='87.2' data-uid='thi-ap18:87.2'><span class='pli'>dibbāya sotadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='87.3' data-uid='thi-ap18:87.3'><span class='pli'>Cetopariyañāṇassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='87.4' data-uid='thi-ap18:87.4'><span class='pli'>vasī cāpi bhavāmahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='88.1' data-uid='thi-ap18:88.1'><span class='pli'>Pubbenivāsaṁ jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='88.2' data-uid='thi-ap18:88.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='88.3' data-uid='thi-ap18:88.3'><span class='pli'>Sabbāsavā parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='88.4' data-uid='thi-ap18:88.4'><span class='pli'>natthi dāni punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='89.1' data-uid='thi-ap18:89.1'><span class='pli'>Atthadhammaniruttīsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='89.2' data-uid='thi-ap18:89.2'><span class='pli'>paṭibhāne tatheva ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='89.3' data-uid='thi-ap18:89.3'><span class='pli'>Parisuddhaṁ mama ñāṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='89.4' data-uid='thi-ap18:89.4'><span class='pli'>uppannaṁ buddhasāsane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='90.1' data-uid='thi-ap18:90.1'><span class='pli'>Kusalāhaṁ visuddhīsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='90.2' data-uid='thi-ap18:90.2'><span class='pli'>kathāvatthuvisāradā; </span></span></span>\n<span class='verse-line'><span class='segment' id='90.3' data-uid='thi-ap18:90.3'><span class='pli'>Abhidhammanayaññū ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='90.4' data-uid='thi-ap18:90.4'><span class='pli'>vasippattāmhi sāsane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='91.1' data-uid='thi-ap18:91.1'><span class='pli'>Tato toraṇavatthusmiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='91.2' data-uid='thi-ap18:91.2'><span class='pli'>raññā kosalasāminā; </span></span></span>\n<span class='verse-line'><span class='segment' id='91.3' data-uid='thi-ap18:91.3'><span class='pli'>Pucchitā nipuṇe pañhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='91.4' data-uid='thi-ap18:91.4'><span class='pli'>byākarontī yathātathaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='92.1' data-uid='thi-ap18:92.1'><span class='pli'>Tadā sa rājā sugataṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='92.2' data-uid='thi-ap18:92.2'><span class='pli'>upasaṅkamma pucchatha; </span></span></span>\n<span class='verse-line'><span class='segment' id='92.3' data-uid='thi-ap18:92.3'><span class='pli'>Tatheva buddho byākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='92.4' data-uid='thi-ap18:92.4'><span class='pli'>yathā te byākatā mayā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='93.1' data-uid='thi-ap18:93.1'><span class='pli'>Jino tasmiṁ guṇe tuṭṭho, </span></span></span>\n<span class='verse-line'><span class='segment' id='93.2' data-uid='thi-ap18:93.2'><span class='pli'>etadagge ṭhapesi maṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='93.3' data-uid='thi-ap18:93.3'><span class='pli'>Mahāpaññānamaggāti, </span></span></span>\n<span class='verse-line'><span class='segment' id='93.4' data-uid='thi-ap18:93.4'><span class='pli'>bhikkhunīnaṁ naruttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='94.1' data-uid='thi-ap18:94.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='94.2' data-uid='thi-ap18:94.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='94.3' data-uid='thi-ap18:94.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='95.1' data-uid='thi-ap18:95.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='95.2' data-uid='thi-ap18:95.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='95.3' data-uid='thi-ap18:95.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='96.1' data-uid='thi-ap18:96.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='96.2' data-uid='thi-ap18:96.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='96.3' data-uid='thi-ap18:96.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='97.1' data-uid='thi-ap18:97.1'><span class='pli'>Itthaṁ sudaṁ khemā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='98.1' data-uid='thi-ap18:98.1'><span class='pli'>Khemātheriyāpadānaṁ aṭṭhamaṁ. </span></span></p></article>\n"
  },
  "thi-ap19": {
    "previous": "thi-ap18",
    "next": "thi-ap20",
    "content": "<article id='thi-ap19'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap19:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap19:0.2'><span class='pli'>Ekūposathikavagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap19:0.3'><span class='pli'>Uppalavaṇṇātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap19:1.1'><span class='pli'>“Bhikkhunī uppalavaṇṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap19:1.2'><span class='pli'>iddhiyā pāramiṁ gatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap19:1.3'><span class='pli'>Vanditvā satthuno pāde, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap19:1.4'><span class='pli'>idaṁ vacanamabravi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap19:2.1'><span class='pli'>‘Nitthiṇṇā jātisaṁsāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap19:2.2'><span class='pli'>pattāhaṁ acalaṁ padaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap19:2.3'><span class='pli'>Sabbadukkhaṁ mayā khīṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap19:2.4'><span class='pli'>ārocemi mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap19:3.1'><span class='pli'>Yāvatā parisā atthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap19:3.2'><span class='pli'>pasannā jinasāsane; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap19:3.3'><span class='pli'>Yassā ca meparādhotthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap19:3.4'><span class='pli'>khamantu jinasammukhā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap19:4.1'><span class='pli'>Saṁsāre saṁsarantiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap19:4.2'><span class='pli'>khalitaṁ me sace bhave; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap19:4.3'><span class='pli'>Ārocemi mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap19:4.4'><span class='pli'>aparādhaṁ khamassu taṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap19:5.1'><span class='pli'>‘Iddhiñcāpi nidassehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap19:5.2'><span class='pli'>mama sāsanakārike; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap19:5.3'><span class='pli'>Catasso parisā ajja, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap19:5.4'><span class='pli'>kaṅkhaṁ chindāhi yāvatā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap19:6.1'><span class='pli'>‘Dhītā tuyhaṁ mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap19:6.2'><span class='pli'>paññavanta jutindhara; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap19:6.3'><span class='pli'>Bahuñca dukkaraṁ kammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap19:6.4'><span class='pli'>kataṁ me atidukkaraṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap19:7.1'><span class='pli'>Uppalasseva me vaṇṇo, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap19:7.2'><span class='pli'>nāmenuppalanāmikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap19:7.3'><span class='pli'>Sāvikā te mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap19:7.4'><span class='pli'>pāde vandāmi cakkhuma. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap19:8.1'><span class='pli'>Rāhulo ca ahañceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap19:8.2'><span class='pli'>nekajātisate bahū; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap19:8.3'><span class='pli'>Ekasmiṁ sambhave jātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap19:8.4'><span class='pli'>samānachandamānasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap19:9.1'><span class='pli'>Nibbatti ekato hoti, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap19:9.2'><span class='pli'>jātiyāpi ca ekato; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap19:9.3'><span class='pli'>Pacchime bhave sampatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap19:9.4'><span class='pli'>ubhopi nānāsambhavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap19:10.1'><span class='pli'>Putto ca rāhulo nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap19:10.2'><span class='pli'>dhītā uppalasavhayā; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap19:10.3'><span class='pli'>Passa vīra mamaṁ iddhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap19:10.4'><span class='pli'>balaṁ dassemi satthuno’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap19:11.1'><span class='pli'>Mahāsamudde caturo, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap19:11.2'><span class='pli'>pakkhipi hatthapātiyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap19:11.3'><span class='pli'>Telaṁ hatthagatañceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap19:11.4'><span class='pli'>khiḍḍo komārako yathā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap19:12.1'><span class='pli'>Ubbattayitvā pathaviṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap19:12.2'><span class='pli'>pakkhipi hatthapātiyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap19:12.3'><span class='pli'>Cittaṁ muñjaṁ yathā nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap19:12.4'><span class='pli'>luñci komārako yuvā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap19:13.1'><span class='pli'>Cakkavāḷasamaṁ pāṇiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap19:13.2'><span class='pli'>chādayitvāna matthake; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap19:13.3'><span class='pli'>Vassāpetvāna phusitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap19:13.4'><span class='pli'>nānāvaṇṇaṁ punappunaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap19:14.1'><span class='pli'>Bhūmiṁ udukkhalaṁ katvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap19:14.2'><span class='pli'>dhaññaṁ katvāna sakkharaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap19:14.3'><span class='pli'>Sineruṁ musalaṁ katvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap19:14.4'><span class='pli'>maddi komārikā yathā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap19:15.1'><span class='pli'>‘Dhītāhaṁ buddhaseṭṭhassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap19:15.2'><span class='pli'>nāmenuppalasavhayā; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap19:15.3'><span class='pli'>Abhiññāsu vasībhūtā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap19:15.4'><span class='pli'>tava sāsanakārikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap19:16.1'><span class='pli'>Nānāvikubbanaṁ katvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap19:16.2'><span class='pli'>dassetvā lokanāyakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap19:16.3'><span class='pli'>Nāmagottañca sāvetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap19:16.4'><span class='pli'>pāde vandāmi cakkhuma. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap19:17.1'><span class='pli'>Iddhīsu ca vasī homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap19:17.2'><span class='pli'>dibbāya sotadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap19:17.3'><span class='pli'>Cetopariyañāṇassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap19:17.4'><span class='pli'>vasī homi mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap19:18.1'><span class='pli'>Pubbenivāsaṁ jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap19:18.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap19:18.3'><span class='pli'>Sabbāsavā parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap19:18.4'><span class='pli'>natthi dāni punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap19:19.1'><span class='pli'>Atthadhammaniruttīsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap19:19.2'><span class='pli'>Paṭibhāne tatheva ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap19:19.3'><span class='pli'>Ñāṇaṁ me vimalaṁ suddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap19:19.4'><span class='pli'>Sabhāvena mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap19:20.1'><span class='pli'>Purimānaṁ jinaggānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap19:20.2'><span class='pli'>saṅgamaṁ te nidassitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap19:20.3'><span class='pli'>Adhikāraṁ bahuṁ mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap19:20.4'><span class='pli'>tuyhatthāya mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap19:21.1'><span class='pli'>Yaṁ mayā pūritaṁ kammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap19:21.2'><span class='pli'>Kusalaṁ sara me muni; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap19:21.3'><span class='pli'>Tavatthāya mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap19:21.4'><span class='pli'>Puññaṁ upacitaṁ mayā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap19:22.1'><span class='pli'>Abhabbaṭṭhāne vajjetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap19:22.2'><span class='pli'>vārayantī anācaraṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap19:22.3'><span class='pli'>Tavatthāya mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap19:22.4'><span class='pli'>cattaṁ me jīvituttamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap19:23.1'><span class='pli'>Dasakoṭisahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap19:23.2'><span class='pli'>adāsiṁ mama jīvitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap19:23.3'><span class='pli'>Pariccattā ca me homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='thi-ap19:23.4'><span class='pli'>tavatthāya mahāmuni’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap19:24.1'><span class='pli'>Tadātivimhitā sabbā, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap19:24.2'><span class='pli'>sirasāva katañjalī; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap19:24.3'><span class='pli'>‘Avocayye kathaṁ āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='thi-ap19:24.4'><span class='pli'>atuliddhiparakkamā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='thi-ap19:25.1'><span class='pli'>Satasahassito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='thi-ap19:25.2'><span class='pli'>nāgakaññā ahaṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='thi-ap19:25.3'><span class='pli'>Vimalā nāma nāmena, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='thi-ap19:25.4'><span class='pli'>kaññānaṁ sādhusammatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='thi-ap19:26.1'><span class='pli'>Mahorago mahānāgo, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='thi-ap19:26.2'><span class='pli'>pasanno jinasāsane; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='thi-ap19:26.3'><span class='pli'>Padumuttaraṁ mahātejaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='thi-ap19:26.4'><span class='pli'>nimantesi sasāvakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='thi-ap19:27.1'><span class='pli'>Ratanamayaṁ maṇḍapaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='thi-ap19:27.2'><span class='pli'>pallaṅkaṁ ratanāmayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='thi-ap19:27.3'><span class='pli'>Ratanaṁ vālukākiṇṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='thi-ap19:27.4'><span class='pli'>upabhogaṁ ratanāmayaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='thi-ap19:28.1'><span class='pli'>Maggañca paṭiyādesi, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='thi-ap19:28.2'><span class='pli'>ratanaddhajabhūsitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='thi-ap19:28.3'><span class='pli'>Paccuggantvāna sambuddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='thi-ap19:28.4'><span class='pli'>vajjanto tūriyehi so. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='thi-ap19:29.1'><span class='pli'>Parisāhi ca catūhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='thi-ap19:29.2'><span class='pli'>parivuto lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='thi-ap19:29.3'><span class='pli'>Mahoragassa bhavane, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='thi-ap19:29.4'><span class='pli'>nisīdi paramāsane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='thi-ap19:30.1'><span class='pli'>Annaṁ pānaṁ khādanīyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='thi-ap19:30.2'><span class='pli'>bhojanañca mahārahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='thi-ap19:30.3'><span class='pli'>Varaṁ varañca pādāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='thi-ap19:30.4'><span class='pli'>nāgarājā mahāyasaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='thi-ap19:31.1'><span class='pli'>Bhuñjitvāna sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='thi-ap19:31.2'><span class='pli'>pattaṁ dhovitvā yoniso; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='thi-ap19:31.3'><span class='pli'>Anumodanīyaṅkāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='thi-ap19:31.4'><span class='pli'>nāgakaññā mahiddhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='thi-ap19:32.1'><span class='pli'>Sabbaññuṁ phullitaṁ disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='thi-ap19:32.2'><span class='pli'>nāgakaññā mahāyasaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='thi-ap19:32.3'><span class='pli'>Pasannaṁ satthuno cittaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='thi-ap19:32.4'><span class='pli'>sunibandhañca mānasaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='thi-ap19:33.1'><span class='pli'>Mamañca cittamaññāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='thi-ap19:33.2'><span class='pli'>jalajuttamanāmako; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='thi-ap19:33.3'><span class='pli'>Tasmiṁ khaṇe mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='thi-ap19:33.4'><span class='pli'>bhikkhuniṁ dassayiddhiyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='34.1' data-uid='thi-ap19:34.1'><span class='pli'>Iddhī anekā dassesi, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='thi-ap19:34.2'><span class='pli'>bhikkhunī sā visāradā; </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='thi-ap19:34.3'><span class='pli'>Pamoditā vedajātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.4' data-uid='thi-ap19:34.4'><span class='pli'>satthāraṁ idamabravi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='35.1' data-uid='thi-ap19:35.1'><span class='pli'>‘Addasāhaṁ imaṁ iddhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.2' data-uid='thi-ap19:35.2'><span class='pli'>sumanaṁ itarāyapi; </span></span></span>\n<span class='verse-line'><span class='segment' id='35.3' data-uid='thi-ap19:35.3'><span class='pli'>Kathaṁ ahosi sā vīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.4' data-uid='thi-ap19:35.4'><span class='pli'>iddhiyā suvisāradā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='36.1' data-uid='thi-ap19:36.1'><span class='pli'>‘Orasā mukhato jātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.2' data-uid='thi-ap19:36.2'><span class='pli'>dhītā mama mahiddhikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='36.3' data-uid='thi-ap19:36.3'><span class='pli'>Mamānusāsanikarā, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.4' data-uid='thi-ap19:36.4'><span class='pli'>iddhiyā suvisāradā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='37.1' data-uid='thi-ap19:37.1'><span class='pli'>Buddhassa vacanaṁ sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.2' data-uid='thi-ap19:37.2'><span class='pli'>evaṁ patthesahaṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='37.3' data-uid='thi-ap19:37.3'><span class='pli'>‘Ahampi tādisā homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.4' data-uid='thi-ap19:37.4'><span class='pli'>iddhiyā suvisāradā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='38.1' data-uid='thi-ap19:38.1'><span class='pli'>Pamoditāhaṁ sumanā, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.2' data-uid='thi-ap19:38.2'><span class='pli'>patthe uttamamānasā; </span></span></span>\n<span class='verse-line'><span class='segment' id='38.3' data-uid='thi-ap19:38.3'><span class='pli'>Anāgatamhi addhāne, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.4' data-uid='thi-ap19:38.4'><span class='pli'>īdisā homi nāyaka’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='39.1' data-uid='thi-ap19:39.1'><span class='pli'>Maṇimayamhi pallaṅke, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.2' data-uid='thi-ap19:39.2'><span class='pli'>maṇḍapamhi pabhassare; </span></span></span>\n<span class='verse-line'><span class='segment' id='39.3' data-uid='thi-ap19:39.3'><span class='pli'>Annapānena tappetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.4' data-uid='thi-ap19:39.4'><span class='pli'>sasaṅghaṁ lokanāyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='40.1' data-uid='thi-ap19:40.1'><span class='pli'>Nāgānaṁ pavaraṁ pupphaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.2' data-uid='thi-ap19:40.2'><span class='pli'>aruṇaṁ nāma uppalaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='40.3' data-uid='thi-ap19:40.3'><span class='pli'>‘Vaṇṇaṁ me īdisaṁ hotu’, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.4' data-uid='thi-ap19:40.4'><span class='pli'>pūjesiṁ lokanāyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='41.1' data-uid='thi-ap19:41.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.2' data-uid='thi-ap19:41.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='41.3' data-uid='thi-ap19:41.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.4' data-uid='thi-ap19:41.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='42.1' data-uid='thi-ap19:42.1'><span class='pli'>Tato cutāhaṁ manuje, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.2' data-uid='thi-ap19:42.2'><span class='pli'>upapannā sayambhuno; </span></span></span>\n<span class='verse-line'><span class='segment' id='42.3' data-uid='thi-ap19:42.3'><span class='pli'>Uppalehi paṭicchannaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.4' data-uid='thi-ap19:42.4'><span class='pli'>piṇḍapātamadāsahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='43.1' data-uid='thi-ap19:43.1'><span class='pli'>Ekanavutito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.2' data-uid='thi-ap19:43.2'><span class='pli'>vipassī nāma nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='43.3' data-uid='thi-ap19:43.3'><span class='pli'>Uppajji cārudassano, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.4' data-uid='thi-ap19:43.4'><span class='pli'>sabbadhammesu cakkhumā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='44.1' data-uid='thi-ap19:44.1'><span class='pli'>Seṭṭhidhītā tadā hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.2' data-uid='thi-ap19:44.2'><span class='pli'>bārāṇasipuruttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='44.3' data-uid='thi-ap19:44.3'><span class='pli'>Nimantetvāna sambuddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.4' data-uid='thi-ap19:44.4'><span class='pli'>sasaṅghaṁ lokanāyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='45.1' data-uid='thi-ap19:45.1'><span class='pli'>Mahādānaṁ daditvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.2' data-uid='thi-ap19:45.2'><span class='pli'>uppalehi vināyakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='45.3' data-uid='thi-ap19:45.3'><span class='pli'>Pūjayitvā cetasāva, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.4' data-uid='thi-ap19:45.4'><span class='pli'>vaṇṇasobhaṁ apatthayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='46.1' data-uid='thi-ap19:46.1'><span class='pli'>Imamhi bhaddake kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.2' data-uid='thi-ap19:46.2'><span class='pli'>brahmabandhu mahāyaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='46.3' data-uid='thi-ap19:46.3'><span class='pli'>Kassapo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.4' data-uid='thi-ap19:46.4'><span class='pli'>uppajji vadataṁ varo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='47.1' data-uid='thi-ap19:47.1'><span class='pli'>Upaṭṭhāko mahesissa, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.2' data-uid='thi-ap19:47.2'><span class='pli'>tadā āsi narissaro; </span></span></span>\n<span class='verse-line'><span class='segment' id='47.3' data-uid='thi-ap19:47.3'><span class='pli'>Kāsirājā kikī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.4' data-uid='thi-ap19:47.4'><span class='pli'>bārāṇasipuruttame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='48.1' data-uid='thi-ap19:48.1'><span class='pli'>Tassāsiṁ dutiyā dhītā, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.2' data-uid='thi-ap19:48.2'><span class='pli'>samaṇaguttasavhayā; </span></span></span>\n<span class='verse-line'><span class='segment' id='48.3' data-uid='thi-ap19:48.3'><span class='pli'>Dhammaṁ sutvā jinaggassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.4' data-uid='thi-ap19:48.4'><span class='pli'>pabbajjaṁ samarocayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='49.1' data-uid='thi-ap19:49.1'><span class='pli'>Anujāni na no tāto, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.2' data-uid='thi-ap19:49.2'><span class='pli'>agāreva tadā mayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='49.3' data-uid='thi-ap19:49.3'><span class='pli'>Vīsavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.4' data-uid='thi-ap19:49.4'><span class='pli'>vicarimha atanditā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='50.1' data-uid='thi-ap19:50.1'><span class='pli'>Komāribrahmacariyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.2' data-uid='thi-ap19:50.2'><span class='pli'>rājakaññā sukhedhitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='50.3' data-uid='thi-ap19:50.3'><span class='pli'>Buddhopaṭṭhānaniratā, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.4' data-uid='thi-ap19:50.4'><span class='pli'>muditā sattadhītaro. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='51.1' data-uid='thi-ap19:51.1'><span class='pli'>Samaṇī samaṇaguttā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.2' data-uid='thi-ap19:51.2'><span class='pli'>bhikkhunī bhikkhudāyikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='51.3' data-uid='thi-ap19:51.3'><span class='pli'>Dhammā ceva sudhammā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.4' data-uid='thi-ap19:51.4'><span class='pli'>sattamī saṅghadāyikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='52.1' data-uid='thi-ap19:52.1'><span class='pli'>Ahaṁ khemā ca sappaññā, </span></span></span>\n<span class='verse-line'><span class='segment' id='52.2' data-uid='thi-ap19:52.2'><span class='pli'>paṭācārā ca kuṇḍalā; </span></span></span>\n<span class='verse-line'><span class='segment' id='52.3' data-uid='thi-ap19:52.3'><span class='pli'>Kisāgotamī dhammadinnā, </span></span></span>\n<span class='verse-line'><span class='segment' id='52.4' data-uid='thi-ap19:52.4'><span class='pli'>visākhā hoti sattamī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='53.1' data-uid='thi-ap19:53.1'><span class='pli'>Tehi kammehi sukatehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='53.2' data-uid='thi-ap19:53.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='53.3' data-uid='thi-ap19:53.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='53.4' data-uid='thi-ap19:53.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='54.1' data-uid='thi-ap19:54.1'><span class='pli'>Tato cutā manussesu, </span></span></span>\n<span class='verse-line'><span class='segment' id='54.2' data-uid='thi-ap19:54.2'><span class='pli'>upapannā mahākule; </span></span></span>\n<span class='verse-line'><span class='segment' id='54.3' data-uid='thi-ap19:54.3'><span class='pli'>Pītaṁ maṭṭhaṁ varaṁ dussaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='54.4' data-uid='thi-ap19:54.4'><span class='pli'>adaṁ arahato ahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='55.1' data-uid='thi-ap19:55.1'><span class='pli'>Tato cutāriṭṭhapure, </span></span></span>\n<span class='verse-line'><span class='segment' id='55.2' data-uid='thi-ap19:55.2'><span class='pli'>jātā vippakule ahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='55.3' data-uid='thi-ap19:55.3'><span class='pli'>Dhītā tiriṭivacchassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='55.4' data-uid='thi-ap19:55.4'><span class='pli'>ummādantī manoharā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='56.1' data-uid='thi-ap19:56.1'><span class='pli'>Tato cutā janapade, </span></span></span>\n<span class='verse-line'><span class='segment' id='56.2' data-uid='thi-ap19:56.2'><span class='pli'>kule aññatare ahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='56.3' data-uid='thi-ap19:56.3'><span class='pli'>Pasūtā nātiphītamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='56.4' data-uid='thi-ap19:56.4'><span class='pli'>sāliṁ gopemahaṁ tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='57.1' data-uid='thi-ap19:57.1'><span class='pli'>Disvā paccekasambuddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='57.2' data-uid='thi-ap19:57.2'><span class='pli'>pañcalājāsatānihaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='57.3' data-uid='thi-ap19:57.3'><span class='pli'>Datvā padumacchannāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='57.4' data-uid='thi-ap19:57.4'><span class='pli'>pañca puttasatānihaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='58.1' data-uid='thi-ap19:58.1'><span class='pli'>Patthayiṁ tepi patthesuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='58.2' data-uid='thi-ap19:58.2'><span class='pli'>madhuṁ datvā sayambhuno; </span></span></span>\n<span class='verse-line'><span class='segment' id='58.3' data-uid='thi-ap19:58.3'><span class='pli'>Tato cutā araññehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='58.4' data-uid='thi-ap19:58.4'><span class='pli'>ajāyiṁ padumodare. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='59.1' data-uid='thi-ap19:59.1'><span class='pli'>Kāsirañño mahesīhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='59.2' data-uid='thi-ap19:59.2'><span class='pli'>hutvā sakkatapūjitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='59.3' data-uid='thi-ap19:59.3'><span class='pli'>Ajaniṁ rājaputtānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='59.4' data-uid='thi-ap19:59.4'><span class='pli'>anūnaṁ satapañcakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='60.1' data-uid='thi-ap19:60.1'><span class='pli'>Yadā te yobbanappattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='60.2' data-uid='thi-ap19:60.2'><span class='pli'>kīḷantā jalakīḷitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='60.3' data-uid='thi-ap19:60.3'><span class='pli'>Disvā opattapadumaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='60.4' data-uid='thi-ap19:60.4'><span class='pli'>āsuṁ paccekanāyakā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='61.1' data-uid='thi-ap19:61.1'><span class='pli'>Sāhaṁ tehi vinābhūtā, </span></span></span>\n<span class='verse-line'><span class='segment' id='61.2' data-uid='thi-ap19:61.2'><span class='pli'>sutavīrehi sokinī; </span></span></span>\n<span class='verse-line'><span class='segment' id='61.3' data-uid='thi-ap19:61.3'><span class='pli'>Cutā isigilipasse, </span></span></span>\n<span class='verse-line'><span class='segment' id='61.4' data-uid='thi-ap19:61.4'><span class='pli'>gāmakamhi ajāyihaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='62.1' data-uid='thi-ap19:62.1'><span class='pli'>Yadā buddhā sutamatī, </span></span></span>\n<span class='verse-line'><span class='segment' id='62.2' data-uid='thi-ap19:62.2'><span class='pli'>sutānaṁ bhattunopi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='62.3' data-uid='thi-ap19:62.3'><span class='pli'>Yāguṁ ādāya gacchantī, </span></span></span>\n<span class='verse-line'><span class='segment' id='62.4' data-uid='thi-ap19:62.4'><span class='pli'>aṭṭha paccekanāyake. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='63.1' data-uid='thi-ap19:63.1'><span class='pli'>Bhikkhāya gāmaṁ gacchante, </span></span></span>\n<span class='verse-line'><span class='segment' id='63.2' data-uid='thi-ap19:63.2'><span class='pli'>disvā putte anussariṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='63.3' data-uid='thi-ap19:63.3'><span class='pli'>Khīradhārā viniggacchi, </span></span></span>\n<span class='verse-line'><span class='segment' id='63.4' data-uid='thi-ap19:63.4'><span class='pli'>tadā me puttapemasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='64.1' data-uid='thi-ap19:64.1'><span class='pli'>Tato tesaṁ adaṁ yāguṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='64.2' data-uid='thi-ap19:64.2'><span class='pli'>pasannā sehi pāṇibhi; </span></span></span>\n<span class='verse-line'><span class='segment' id='64.3' data-uid='thi-ap19:64.3'><span class='pli'>Tato cutāhaṁ tidasaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='64.4' data-uid='thi-ap19:64.4'><span class='pli'>nandanaṁ upapajjahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='65.1' data-uid='thi-ap19:65.1'><span class='pli'>Anubhotvā sukhaṁ dukkhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='65.2' data-uid='thi-ap19:65.2'><span class='pli'>saṁsaritvā bhavābhave; </span></span></span>\n<span class='verse-line'><span class='segment' id='65.3' data-uid='thi-ap19:65.3'><span class='pli'>Tavatthāya mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='65.4' data-uid='thi-ap19:65.4'><span class='pli'>pariccattañca jīvitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='66.1' data-uid='thi-ap19:66.1'><span class='pli'>Evaṁ bahuvidhaṁ dukkhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='66.2' data-uid='thi-ap19:66.2'><span class='pli'>sampattī ca bahubbidhā; </span></span></span>\n<span class='verse-line'><span class='segment' id='66.3' data-uid='thi-ap19:66.3'><span class='pli'>Pacchime bhave sampatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='66.4' data-uid='thi-ap19:66.4'><span class='pli'>jātā sāvatthiyaṁ pure. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='67.1' data-uid='thi-ap19:67.1'><span class='pli'>Mahādhanaseṭṭhikule, </span></span></span>\n<span class='verse-line'><span class='segment' id='67.2' data-uid='thi-ap19:67.2'><span class='pli'>sukhite sajjite tathā; </span></span></span>\n<span class='verse-line'><span class='segment' id='67.3' data-uid='thi-ap19:67.3'><span class='pli'>Nānāratanapajjote, </span></span></span>\n<span class='verse-line'><span class='segment' id='67.4' data-uid='thi-ap19:67.4'><span class='pli'>sabbakāmasamiddhine. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='68.1' data-uid='thi-ap19:68.1'><span class='pli'>Sakkatā pūjitā ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='68.2' data-uid='thi-ap19:68.2'><span class='pli'>mānitāpacitā tathā; </span></span></span>\n<span class='verse-line'><span class='segment' id='68.3' data-uid='thi-ap19:68.3'><span class='pli'>Rūpasobhaggasampannā, </span></span></span>\n<span class='verse-line'><span class='segment' id='68.4' data-uid='thi-ap19:68.4'><span class='pli'>kulesu atisakkatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='69.1' data-uid='thi-ap19:69.1'><span class='pli'>Atīva patthitā cāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='69.2' data-uid='thi-ap19:69.2'><span class='pli'>rūpabhogasirīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='69.3' data-uid='thi-ap19:69.3'><span class='pli'>Patthitā seṭṭhiputtehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='69.4' data-uid='thi-ap19:69.4'><span class='pli'>anekehi satehipi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='70.1' data-uid='thi-ap19:70.1'><span class='pli'>Agāraṁ pajahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='70.2' data-uid='thi-ap19:70.2'><span class='pli'>pabbajiṁ anagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='70.3' data-uid='thi-ap19:70.3'><span class='pli'>Aḍḍhamāse asampatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='70.4' data-uid='thi-ap19:70.4'><span class='pli'>catusaccamapāpuṇiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='71.1' data-uid='thi-ap19:71.1'><span class='pli'>Iddhiyā abhinimmitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='71.2' data-uid='thi-ap19:71.2'><span class='pli'>caturassaṁ rathaṁ ahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='71.3' data-uid='thi-ap19:71.3'><span class='pli'>Buddhassa pāde vandissaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='71.4' data-uid='thi-ap19:71.4'><span class='pli'>lokanāthassa tādino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='72.1' data-uid='thi-ap19:72.1'><span class='pli'>‘Supupphitaggaṁ upagamma pādapaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='72.2' data-uid='thi-ap19:72.2'><span class='pli'>Ekā tuvaṁ tiṭṭhasi sālamūle; </span></span></span>\n<span class='verse-line'><span class='segment' id='72.3' data-uid='thi-ap19:72.3'><span class='pli'>Na cāpi te dutiyo atthi koci, </span></span></span>\n<span class='verse-line'><span class='segment' id='72.4' data-uid='thi-ap19:72.4'><span class='pli'>Bāle na tvaṁ bhāyasi dhuttakānaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='73.1' data-uid='thi-ap19:73.1'><span class='pli'>‘Sataṁ sahassānipi dhuttakānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='73.2' data-uid='thi-ap19:73.2'><span class='pli'>Samāgatā edisakā bhaveyyuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='73.3' data-uid='thi-ap19:73.3'><span class='pli'>Lomaṁ na iñje na sampavedhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='73.4' data-uid='thi-ap19:73.4'><span class='pli'>Kiṁ me tuvaṁ māra karissaseko. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='74.1' data-uid='thi-ap19:74.1'><span class='pli'>Esā antaradhāyāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='74.2' data-uid='thi-ap19:74.2'><span class='pli'>kucchiṁ vā pavisāmi te; </span></span></span>\n<span class='verse-line'><span class='segment' id='74.3' data-uid='thi-ap19:74.3'><span class='pli'>Bhamukantarikāyampi, </span></span></span>\n<span class='verse-line'><span class='segment' id='74.4' data-uid='thi-ap19:74.4'><span class='pli'>tiṭṭhantiṁ maṁ na dakkhasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='75.1' data-uid='thi-ap19:75.1'><span class='pli'>Cittasmiṁ vasībhūtāmhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='75.2' data-uid='thi-ap19:75.2'><span class='pli'>iddhipādā subhāvitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='75.3' data-uid='thi-ap19:75.3'><span class='pli'>Sabbabandhanamuttāmhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='75.4' data-uid='thi-ap19:75.4'><span class='pli'>na taṁ bhāyāmi āvuso. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='76.1' data-uid='thi-ap19:76.1'><span class='pli'>Sattisūlūpamā kāmā, </span></span></span>\n<span class='verse-line'><span class='segment' id='76.2' data-uid='thi-ap19:76.2'><span class='pli'>khandhāsaṁ adhikuṭṭanā; </span></span></span>\n<span class='verse-line'><span class='segment' id='76.3' data-uid='thi-ap19:76.3'><span class='pli'>Yaṁ tvaṁ kāmaratiṁ brūsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='76.4' data-uid='thi-ap19:76.4'><span class='pli'>aratī dāni sā mama. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='77.1' data-uid='thi-ap19:77.1'><span class='pli'>Sabbattha vihatā nandī, </span></span></span>\n<span class='verse-line'><span class='segment' id='77.2' data-uid='thi-ap19:77.2'><span class='pli'>tamokhandho padālito; </span></span></span>\n<span class='verse-line'><span class='segment' id='77.3' data-uid='thi-ap19:77.3'><span class='pli'>Evaṁ jānāhi pāpima, </span></span></span>\n<span class='verse-line'><span class='segment' id='77.4' data-uid='thi-ap19:77.4'><span class='pli'>nihato tvamasi antaka’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='78.1' data-uid='thi-ap19:78.1'><span class='pli'>Jino tamhi guṇe tuṭṭho, </span></span></span>\n<span class='verse-line'><span class='segment' id='78.2' data-uid='thi-ap19:78.2'><span class='pli'>etadagge ṭhapesi maṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='78.3' data-uid='thi-ap19:78.3'><span class='pli'>Aggā iddhimatīnanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='78.4' data-uid='thi-ap19:78.4'><span class='pli'>parisāsu vināyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='79.1' data-uid='thi-ap19:79.1'><span class='pli'>Pariciṇṇo mayā satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='79.2' data-uid='thi-ap19:79.2'><span class='pli'>kataṁ buddhassa sāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='79.3' data-uid='thi-ap19:79.3'><span class='pli'>Ohito garuko bhāro, </span></span></span>\n<span class='verse-line'><span class='segment' id='79.4' data-uid='thi-ap19:79.4'><span class='pli'>bhavanettisamūhatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='80.1' data-uid='thi-ap19:80.1'><span class='pli'>Yassatthāya pabbajitā, </span></span></span>\n<span class='verse-line'><span class='segment' id='80.2' data-uid='thi-ap19:80.2'><span class='pli'>agārasmānagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='80.3' data-uid='thi-ap19:80.3'><span class='pli'>So me attho anuppatto, </span></span></span>\n<span class='verse-line'><span class='segment' id='80.4' data-uid='thi-ap19:80.4'><span class='pli'>sabbasaṁyojanakkhayo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='81.1' data-uid='thi-ap19:81.1'><span class='pli'>Cīvaraṁ piṇḍapātañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='81.2' data-uid='thi-ap19:81.2'><span class='pli'>paccayaṁ sayanāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='81.3' data-uid='thi-ap19:81.3'><span class='pli'>Khaṇena upanāmenti, </span></span></span>\n<span class='verse-line'><span class='segment' id='81.4' data-uid='thi-ap19:81.4'><span class='pli'>sahassāni samantato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='82.1' data-uid='thi-ap19:82.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='82.2' data-uid='thi-ap19:82.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='82.3' data-uid='thi-ap19:82.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='83.1' data-uid='thi-ap19:83.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='83.2' data-uid='thi-ap19:83.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='83.3' data-uid='thi-ap19:83.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='84.1' data-uid='thi-ap19:84.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='84.2' data-uid='thi-ap19:84.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='84.3' data-uid='thi-ap19:84.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='85.1' data-uid='thi-ap19:85.1'><span class='pli'>Itthaṁ sudaṁ uppalavaṇṇā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='86.1' data-uid='thi-ap19:86.1'><span class='pli'>Uppalavaṇṇātheriyāpadānaṁ navamaṁ. </span></span></p></article>\n"
  },
  "thi-ap20": {
    "previous": "thi-ap19",
    "next": "thi-ap21",
    "content": "<article id='thi-ap20'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap20:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap20:0.2'><span class='pli'>Ekūposathikavagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap20:0.3'><span class='pli'>Paṭācārātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap20:1.1'><span class='pli'>“Padumuttaro nāma jino, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap20:1.2'><span class='pli'>sabbadhammāna pāragū; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap20:1.3'><span class='pli'>Ito satasahassamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap20:1.4'><span class='pli'>kappe uppajji nāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap20:2.1'><span class='pli'>Tadāhaṁ haṁsavatiyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap20:2.2'><span class='pli'>jātā seṭṭhikule ahuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap20:2.3'><span class='pli'>Nānāratanapajjote, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap20:2.4'><span class='pli'>mahāsukhasamappitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap20:3.1'><span class='pli'>Upetvā taṁ mahāvīraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap20:3.2'><span class='pli'>assosiṁ dhammadesanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap20:3.3'><span class='pli'>Tato jātapasādāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap20:3.4'><span class='pli'>upesiṁ saraṇaṁ jinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap20:4.1'><span class='pli'>Tato vinayadhārīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap20:4.2'><span class='pli'>aggaṁ vaṇṇesi nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap20:4.3'><span class='pli'>Bhikkhuniṁ lajjiniṁ tādiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap20:4.4'><span class='pli'>kappākappavisāradaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap20:5.1'><span class='pli'>Tadā muditacittāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap20:5.2'><span class='pli'>taṁ ṭhānamabhikaṅkhinī; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap20:5.3'><span class='pli'>Nimantetvā dasabalaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap20:5.4'><span class='pli'>sasaṅghaṁ lokanāyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap20:6.1'><span class='pli'>Bhojayitvāna sattāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap20:6.2'><span class='pli'>daditvāva ticīvaraṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap20:6.3'><span class='pli'>Nipacca sirasā pāde, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap20:6.4'><span class='pli'>idaṁ vacanamabraviṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap20:7.1'><span class='pli'>‘Yā tayā vaṇṇitā vīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap20:7.2'><span class='pli'>ito aṭṭhamake muni; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap20:7.3'><span class='pli'>Tādisāhaṁ bhavissāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap20:7.4'><span class='pli'>yadi sijjhati nāyaka’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap20:8.1'><span class='pli'>Tadā avoca maṁ satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap20:8.2'><span class='pli'>‘bhadde mā bhāyi assasa; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap20:8.3'><span class='pli'>Anāgatamhi addhāne, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap20:8.4'><span class='pli'>lacchase taṁ manorathaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap20:9.1'><span class='pli'>Satasahassito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap20:9.2'><span class='pli'>okkākakulasambhavo; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap20:9.3'><span class='pli'>Gotamo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap20:9.4'><span class='pli'>satthā loke bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap20:10.1'><span class='pli'>Tassa dhammesu dāyādā, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap20:10.2'><span class='pli'>orasā dhammanimmitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap20:10.3'><span class='pli'>Paṭācārāti nāmena, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap20:10.4'><span class='pli'>hessati satthu sāvikā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap20:11.1'><span class='pli'>Tadāhaṁ muditā hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap20:11.2'><span class='pli'>yāvajīvaṁ tadā jinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap20:11.3'><span class='pli'>Mettacittā paricariṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap20:11.4'><span class='pli'>sasaṅghaṁ lokanāyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap20:12.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap20:12.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap20:12.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap20:12.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap20:13.1'><span class='pli'>Imamhi bhaddake kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap20:13.2'><span class='pli'>brahmabandhu mahāyaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap20:13.3'><span class='pli'>Kassapo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap20:13.4'><span class='pli'>uppajji vadataṁ varo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap20:14.1'><span class='pli'>Upaṭṭhāko mahesissa, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap20:14.2'><span class='pli'>tadā āsi narissaro; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap20:14.3'><span class='pli'>Kāsirājā kikī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap20:14.4'><span class='pli'>bārāṇasipuruttame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap20:15.1'><span class='pli'>Tassāsiṁ tatiyā dhītā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap20:15.2'><span class='pli'>bhikkhunī iti vissutā; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap20:15.3'><span class='pli'>Dhammaṁ sutvā jinaggassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap20:15.4'><span class='pli'>pabbajjaṁ samarocayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap20:16.1'><span class='pli'>Anujāni na no tāto, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap20:16.2'><span class='pli'>agāreva tadā mayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap20:16.3'><span class='pli'>Vīsavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap20:16.4'><span class='pli'>vicarimha atanditā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap20:17.1'><span class='pli'>Komāribrahmacariyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap20:17.2'><span class='pli'>rājakaññā sukhedhitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap20:17.3'><span class='pli'>Buddhopaṭṭhānaniratā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap20:17.4'><span class='pli'>muditā sattadhītaro. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap20:18.1'><span class='pli'>Samaṇī samaṇaguttā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap20:18.2'><span class='pli'>bhikkhunī bhikkhudāyikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap20:18.3'><span class='pli'>Dhammā ceva sudhammā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap20:18.4'><span class='pli'>sattamī saṅghadāyikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap20:19.1'><span class='pli'>Ahaṁ uppalavaṇṇā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap20:19.2'><span class='pli'>khemā bhaddā ca bhikkhunī; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap20:19.3'><span class='pli'>Kisāgotamī dhammadinnā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap20:19.4'><span class='pli'>visākhā hoti sattamī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap20:20.1'><span class='pli'>Tehi kammehi sukatehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap20:20.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap20:20.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap20:20.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap20:21.1'><span class='pli'>Pacchime ca bhave dāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap20:21.2'><span class='pli'>jātā seṭṭhikule ahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap20:21.3'><span class='pli'>Sāvatthiyaṁ puravare, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap20:21.4'><span class='pli'>iddhe phīte mahaddhane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap20:22.1'><span class='pli'>Yadā ca yobbanūpetā, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap20:22.2'><span class='pli'>vitakkavasagā ahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap20:22.3'><span class='pli'>Naraṁ jārapatiṁ disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap20:22.4'><span class='pli'>tena saddhiṁ agacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap20:23.1'><span class='pli'>Ekaputtapasūtāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap20:23.2'><span class='pli'>dutiyo kucchiyā mamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap20:23.3'><span class='pli'>Tadāhaṁ mātāpitaro, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='thi-ap20:23.4'><span class='pli'>okkhāmīti sunicchitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap20:24.1'><span class='pli'>Nārocesiṁ patiṁ mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap20:24.2'><span class='pli'>tadā tamhi pavāsite; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap20:24.3'><span class='pli'>Ekikā niggatā gehā, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='thi-ap20:24.4'><span class='pli'>gantuṁ sāvatthimuttamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='thi-ap20:25.1'><span class='pli'>Tato me sāmi āgantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='thi-ap20:25.2'><span class='pli'>sambhāvesi pathe mamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='thi-ap20:25.3'><span class='pli'>Tadā me kammajā vātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='thi-ap20:25.4'><span class='pli'>uppannā atidāruṇā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='thi-ap20:26.1'><span class='pli'>Uṭṭhito ca mahāmegho, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='thi-ap20:26.2'><span class='pli'>pasūtisamaye mama; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='thi-ap20:26.3'><span class='pli'>Dabbatthāya tadā gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='thi-ap20:26.4'><span class='pli'>sāmi sappena mārito. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='thi-ap20:27.1'><span class='pli'>Tadā vijātadukkhena, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='thi-ap20:27.2'><span class='pli'>anāthā kapaṇā ahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='thi-ap20:27.3'><span class='pli'>Kunnadiṁ pūritaṁ disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='thi-ap20:27.4'><span class='pli'>gacchantī sakulālayaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='thi-ap20:28.1'><span class='pli'>Bālaṁ ādāya atariṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='thi-ap20:28.2'><span class='pli'>pārakūle ca ekakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='thi-ap20:28.3'><span class='pli'>Sāyetvā bālakaṁ puttaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='thi-ap20:28.4'><span class='pli'>itaraṁ taraṇāyahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='thi-ap20:29.1'><span class='pli'>Nivattā ukkuso hāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='thi-ap20:29.2'><span class='pli'>taruṇaṁ vilapantakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='thi-ap20:29.3'><span class='pli'>Itarañca vahī soto, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='thi-ap20:29.4'><span class='pli'>sāhaṁ sokasamappitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='thi-ap20:30.1'><span class='pli'>Sāvatthinagaraṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='thi-ap20:30.2'><span class='pli'>assosiṁ sajane mate; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='thi-ap20:30.3'><span class='pli'>Tadā avocaṁ sokaṭṭā, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='thi-ap20:30.4'><span class='pli'>mahāsokasamappitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='thi-ap20:31.1'><span class='pli'>‘Ubho puttā kālaṅkatā, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='thi-ap20:31.2'><span class='pli'>panthe mayhaṁ patī mato; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='thi-ap20:31.3'><span class='pli'>Mātā pitā ca bhātā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='thi-ap20:31.4'><span class='pli'>ekacitamhi ḍayhare’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='thi-ap20:32.1'><span class='pli'>Tadā kisā ca paṇḍu ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='thi-ap20:32.2'><span class='pli'>anāthā dīnamānasā; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='thi-ap20:32.3'><span class='pli'>Ito tato bhamantīhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='thi-ap20:32.4'><span class='pli'>addasaṁ narasārathiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='thi-ap20:33.1'><span class='pli'>Tato avoca maṁ satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='thi-ap20:33.2'><span class='pli'>‘putte mā soci assasa; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='thi-ap20:33.3'><span class='pli'>Attānaṁ te gavesassu, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='thi-ap20:33.4'><span class='pli'>kiṁ niratthaṁ vihaññasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='34.1' data-uid='thi-ap20:34.1'><span class='pli'>Na santi puttā tāṇāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='thi-ap20:34.2'><span class='pli'>na ñātī napi bandhavā; </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='thi-ap20:34.3'><span class='pli'>Antakenādhipannassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.4' data-uid='thi-ap20:34.4'><span class='pli'>natthi ñātīsu tāṇatā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='35.1' data-uid='thi-ap20:35.1'><span class='pli'>Taṁ sutvā munino vākyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.2' data-uid='thi-ap20:35.2'><span class='pli'>paṭhamaṁ phalamajjhagaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='35.3' data-uid='thi-ap20:35.3'><span class='pli'>Pabbajitvāna naciraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.4' data-uid='thi-ap20:35.4'><span class='pli'>arahattamapāpuṇiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='36.1' data-uid='thi-ap20:36.1'><span class='pli'>Iddhīsu ca vasī homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.2' data-uid='thi-ap20:36.2'><span class='pli'>dibbāya sotadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='36.3' data-uid='thi-ap20:36.3'><span class='pli'>Paracittāni jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.4' data-uid='thi-ap20:36.4'><span class='pli'>satthusāsanakārikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='37.1' data-uid='thi-ap20:37.1'><span class='pli'>Pubbenivāsaṁ jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.2' data-uid='thi-ap20:37.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='37.3' data-uid='thi-ap20:37.3'><span class='pli'>Khepetvā āsave sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.4' data-uid='thi-ap20:37.4'><span class='pli'>visuddhāsiṁ sunimmalā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='38.1' data-uid='thi-ap20:38.1'><span class='pli'>Tatohaṁ vinayaṁ sabbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.2' data-uid='thi-ap20:38.2'><span class='pli'>santike sabbadassino; </span></span></span>\n<span class='verse-line'><span class='segment' id='38.3' data-uid='thi-ap20:38.3'><span class='pli'>Uggahiṁ sabbavitthāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.4' data-uid='thi-ap20:38.4'><span class='pli'>byāhariñca yathātathaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='39.1' data-uid='thi-ap20:39.1'><span class='pli'>Jino tasmiṁ guṇe tuṭṭho, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.2' data-uid='thi-ap20:39.2'><span class='pli'>etadagge ṭhapesi maṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='39.3' data-uid='thi-ap20:39.3'><span class='pli'>‘Aggā vinayadhārīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.4' data-uid='thi-ap20:39.4'><span class='pli'>paṭācārāva ekikā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='40.1' data-uid='thi-ap20:40.1'><span class='pli'>Pariciṇṇo mayā satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.2' data-uid='thi-ap20:40.2'><span class='pli'>kataṁ buddhassa sāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='40.3' data-uid='thi-ap20:40.3'><span class='pli'>Ohito garuko bhāro, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.4' data-uid='thi-ap20:40.4'><span class='pli'>bhavanetti samūhatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='41.1' data-uid='thi-ap20:41.1'><span class='pli'>Yassatthāya pabbajitā, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.2' data-uid='thi-ap20:41.2'><span class='pli'>agārasmānagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='41.3' data-uid='thi-ap20:41.3'><span class='pli'>So me attho anuppatto, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.4' data-uid='thi-ap20:41.4'><span class='pli'>sabbasaṁyojanakkhayo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='42.1' data-uid='thi-ap20:42.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.2' data-uid='thi-ap20:42.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='42.3' data-uid='thi-ap20:42.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='43.1' data-uid='thi-ap20:43.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.2' data-uid='thi-ap20:43.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='43.3' data-uid='thi-ap20:43.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='44.1' data-uid='thi-ap20:44.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.2' data-uid='thi-ap20:44.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='44.3' data-uid='thi-ap20:44.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='45.1' data-uid='thi-ap20:45.1'><span class='pli'>Itthaṁ sudaṁ paṭācārā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='46.1' data-uid='thi-ap20:46.1'><span class='pli'>Paṭācārātheriyāpadānaṁ dasamaṁ. </span></span></p>\n<p class='endsection'><span class='segment' id='47.1' data-uid='thi-ap20:47.1'><span class='pli'>Ekūposathikavaggo dutiyo. </span></span></p>\n<p class='uddana-intro'><span class='segment' id='48.1' data-uid='thi-ap20:48.1'><span class='pli'>Tassuddānaṁ </span></span></p>\n<blockquote class='uddanagatha'><p><span class='verse-line'><span class='segment' id='49.1' data-uid='thi-ap20:49.1'><span class='pli'>Ekūposathikā ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.2' data-uid='thi-ap20:49.2'><span class='pli'>saḷalā cātha modakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='49.3' data-uid='thi-ap20:49.3'><span class='pli'>Ekāsanā pañcadīpā, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.4' data-uid='thi-ap20:49.4'><span class='pli'>naḷamālī ca gotamī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='50.1' data-uid='thi-ap20:50.1'><span class='pli'>Khemā uppalavaṇṇā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.2' data-uid='thi-ap20:50.2'><span class='pli'>paṭācārā ca bhikkhunī; </span></span></span>\n<span class='verse-line'><span class='segment' id='50.3' data-uid='thi-ap20:50.3'><span class='pli'>Gāthā satāni pañceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.4' data-uid='thi-ap20:50.4'><span class='pli'>nava cāpi taduttari. </span></span></span></p></blockquote></article>\n"
  },
  "thi-ap21": {
    "previous": "thi-ap20",
    "next": "thi-ap22",
    "content": "<article id='thi-ap21'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap21:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap21:0.2'><span class='pli'>Kuṇḍalakesīvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap21:0.3'><span class='pli'>1 Kuṇḍalakesātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap21:1.1'><span class='pli'>“Padumuttaro nāma jino, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap21:1.2'><span class='pli'>sabbadhammāna pāragū; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap21:1.3'><span class='pli'>Ito satasahassamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap21:1.4'><span class='pli'>kappe uppajji nāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap21:2.1'><span class='pli'>Tadāhaṁ haṁsavatiyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap21:2.2'><span class='pli'>jātā seṭṭhikule ahuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap21:2.3'><span class='pli'>Nānāratanapajjote, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap21:2.4'><span class='pli'>mahāsukhasamappitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap21:3.1'><span class='pli'>Upetvā taṁ mahāvīraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap21:3.2'><span class='pli'>assosiṁ dhammadesanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap21:3.3'><span class='pli'>Tato jātappasādāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap21:3.4'><span class='pli'>upesiṁ saraṇaṁ jinaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap21:4.1'><span class='pli'>Tadā mahākāruṇiko, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap21:4.2'><span class='pli'>padumuttaranāmako; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap21:4.3'><span class='pli'>Khippābhiññānamagganti, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap21:4.4'><span class='pli'>ṭhapesi bhikkhuniṁ subhaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap21:5.1'><span class='pli'>Taṁ sutvā muditā hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap21:5.2'><span class='pli'>dānaṁ datvā mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap21:5.3'><span class='pli'>Nipacca sirasā pāde, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap21:5.4'><span class='pli'>taṁ ṭhānamabhipatthayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap21:6.1'><span class='pli'>Anumodi mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap21:6.2'><span class='pli'>‘bhadde yaṁ tebhipatthitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap21:6.3'><span class='pli'>Samijjhissati taṁ sabbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap21:6.4'><span class='pli'>sukhinī hohi nibbutā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap21:7.1'><span class='pli'>Satasahassito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap21:7.2'><span class='pli'>okkākakulasambhavo; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap21:7.3'><span class='pli'>Gotamo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap21:7.4'><span class='pli'>satthā loke bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap21:8.1'><span class='pli'>Tassa dhammesu dāyādā, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap21:8.2'><span class='pli'>orasā dhammanimmitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap21:8.3'><span class='pli'>Bhaddākuṇḍalakesāti, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap21:8.4'><span class='pli'>hessati satthu sāvikā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap21:9.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap21:9.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap21:9.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap21:9.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap21:10.1'><span class='pli'>Tato cutā yāmamagaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap21:10.2'><span class='pli'>tatohaṁ tusitaṁ gatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap21:10.3'><span class='pli'>Tato ca nimmānaratiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap21:10.4'><span class='pli'>vasavattipuraṁ tato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap21:11.1'><span class='pli'>Yattha yatthūpapajjāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap21:11.2'><span class='pli'>tassa kammassa vāhasā; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap21:11.3'><span class='pli'>Tattha tattheva rājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap21:11.4'><span class='pli'>mahesittamakārayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap21:12.1'><span class='pli'>Tato cutā manussesu, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap21:12.2'><span class='pli'>rājūnaṁ cakkavattinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap21:12.3'><span class='pli'>Maṇḍalīnañca rājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap21:12.4'><span class='pli'>mahesittamakārayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap21:13.1'><span class='pli'>Sampattiṁ anubhotvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap21:13.2'><span class='pli'>devesu mānusesu ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap21:13.3'><span class='pli'>Sabbattha sukhitā hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap21:13.4'><span class='pli'>nekakappesu saṁsariṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap21:14.1'><span class='pli'>Imamhi bhaddake kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap21:14.2'><span class='pli'>brahmabandhu mahāyaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap21:14.3'><span class='pli'>Kassapo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap21:14.4'><span class='pli'>uppajji vadataṁ varo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap21:15.1'><span class='pli'>Upaṭṭhāko mahesissa, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap21:15.2'><span class='pli'>tadā āsi narissaro; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap21:15.3'><span class='pli'>Kāsirājā kikī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap21:15.4'><span class='pli'>bārāṇasipuruttame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap21:16.1'><span class='pli'>Tassa dhītā catutthāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap21:16.2'><span class='pli'>bhikkhudāyīti vissutā; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap21:16.3'><span class='pli'>Dhammaṁ sutvā jinaggassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap21:16.4'><span class='pli'>pabbajjaṁ samarocayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap21:17.1'><span class='pli'>Anujāni na no tāto, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap21:17.2'><span class='pli'>agāreva tadā mayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap21:17.3'><span class='pli'>Vīsavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap21:17.4'><span class='pli'>vicarimha atanditā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap21:18.1'><span class='pli'>Komāribrahmacariyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap21:18.2'><span class='pli'>rājakaññā sukhedhitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap21:18.3'><span class='pli'>Buddhopaṭṭhānaniratā, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap21:18.4'><span class='pli'>muditā satta dhītaro. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap21:19.1'><span class='pli'>Samaṇī samaṇaguttā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap21:19.2'><span class='pli'>bhikkhunī bhikkhudāyikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap21:19.3'><span class='pli'>Dhammā ceva sudhammā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap21:19.4'><span class='pli'>sattamī saṅghadāyikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap21:20.1'><span class='pli'>Khemā uppalavaṇṇā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap21:20.2'><span class='pli'>paṭācārā ahaṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap21:20.3'><span class='pli'>Kisāgotamī dhammadinnā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap21:20.4'><span class='pli'>visākhā hoti sattamī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap21:21.1'><span class='pli'>Tehi kammehi sukatehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap21:21.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap21:21.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap21:21.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap21:22.1'><span class='pli'>Pacchime ca bhave dāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap21:22.2'><span class='pli'>giribbajapuruttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap21:22.3'><span class='pli'>Jātā seṭṭhikule phīte, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap21:22.4'><span class='pli'>yadāhaṁ yobbane ṭhitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap21:23.1'><span class='pli'>Coraṁ vadhatthaṁ nīyantaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap21:23.2'><span class='pli'>disvā rattā tahiṁ ahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap21:23.3'><span class='pli'>Pitā me taṁ sahassena, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='thi-ap21:23.4'><span class='pli'>mocayitvā vadhā tato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap21:24.1'><span class='pli'>Adāsi tassa maṁ tāto, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap21:24.2'><span class='pli'>viditvāna manaṁ mama; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap21:24.3'><span class='pli'>Tassāhamāsiṁ visaṭṭhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='thi-ap21:24.4'><span class='pli'>atīva dayitā hitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='thi-ap21:25.1'><span class='pli'>So me bhūsanalobhena, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='thi-ap21:25.2'><span class='pli'>balimajjhāsayo diso; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='thi-ap21:25.3'><span class='pli'>Corappapātaṁ netvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='thi-ap21:25.4'><span class='pli'>pabbataṁ cetayī vadhaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='thi-ap21:26.1'><span class='pli'>Tadāhaṁ paṇamitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='thi-ap21:26.2'><span class='pli'>sattukaṁ sukatañjalī; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='thi-ap21:26.3'><span class='pli'>Rakkhantī attano pāṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='thi-ap21:26.4'><span class='pli'>idaṁ vacanamabraviṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='thi-ap21:27.1'><span class='pli'>‘Idaṁ suvaṇṇakeyūraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='thi-ap21:27.2'><span class='pli'>muttā veḷuriyā bahū; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='thi-ap21:27.3'><span class='pli'>Sabbaṁ harassu bhaddante, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='thi-ap21:27.4'><span class='pli'>mañca dāsīti sāvaya’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='thi-ap21:28.1'><span class='pli'>‘Oropayassu kalyāṇī, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='thi-ap21:28.2'><span class='pli'>mā bāḷhaṁ paridevasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='thi-ap21:28.3'><span class='pli'>Na cāhaṁ abhijānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='thi-ap21:28.4'><span class='pli'>ahantvā dhanamābhataṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='thi-ap21:29.1'><span class='pli'>‘Yato sarāmi attānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='thi-ap21:29.2'><span class='pli'>yato pattosmi viññutaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='thi-ap21:29.3'><span class='pli'>Na cāhaṁ abhijānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='thi-ap21:29.4'><span class='pli'>aññaṁ piyataraṁ tayā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='thi-ap21:30.1'><span class='pli'>Ehi taṁ upagūhissaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='thi-ap21:30.2'><span class='pli'>katvāna taṁ padakkhiṇaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='thi-ap21:30.3'><span class='pli'>Na ca dāni puno atthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='thi-ap21:30.4'><span class='pli'>mama tuyhañca saṅgamo’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='thi-ap21:31.1'><span class='pli'>‘Na hi sabbesu ṭhānesu, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='thi-ap21:31.2'><span class='pli'>puriso hoti paṇḍito; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='thi-ap21:31.3'><span class='pli'>Itthīpi paṇḍitā hoti, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='thi-ap21:31.4'><span class='pli'>tattha tattha vicakkhaṇā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='thi-ap21:32.1'><span class='pli'>Na hi sabbesu ṭhānesu, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='thi-ap21:32.2'><span class='pli'>puriso hoti paṇḍito; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='thi-ap21:32.3'><span class='pli'>Itthīpi paṇḍitā hoti, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='thi-ap21:32.4'><span class='pli'>lahuṁ atthavicintikā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='thi-ap21:33.1'><span class='pli'>Lahuñca vata khippañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='thi-ap21:33.2'><span class='pli'>nikaṭṭhe samacetayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='thi-ap21:33.3'><span class='pli'>Migaṁ uṇṇā yathā evaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='thi-ap21:33.4'><span class='pli'>tadāhaṁ sattukaṁ vadhiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='34.1' data-uid='thi-ap21:34.1'><span class='pli'>Yo ca uppatitaṁ atthaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='thi-ap21:34.2'><span class='pli'>na khippamanubujjhati; </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='thi-ap21:34.3'><span class='pli'>So haññate mandamati, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.4' data-uid='thi-ap21:34.4'><span class='pli'>corova girigabbhare. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='35.1' data-uid='thi-ap21:35.1'><span class='pli'>Yo ca uppatitaṁ atthaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.2' data-uid='thi-ap21:35.2'><span class='pli'>khippameva nibodhati; </span></span></span>\n<span class='verse-line'><span class='segment' id='35.3' data-uid='thi-ap21:35.3'><span class='pli'>Muccate sattusambādhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.4' data-uid='thi-ap21:35.4'><span class='pli'>tadāhaṁ sattukā yathā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='36.1' data-uid='thi-ap21:36.1'><span class='pli'>Tadāhaṁ pātayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.2' data-uid='thi-ap21:36.2'><span class='pli'>giriduggamhi sattukaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='36.3' data-uid='thi-ap21:36.3'><span class='pli'>Santikaṁ setavatthānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.4' data-uid='thi-ap21:36.4'><span class='pli'>upetvā pabbajiṁ ahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='37.1' data-uid='thi-ap21:37.1'><span class='pli'>Saṇḍāsena ca kese me, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.2' data-uid='thi-ap21:37.2'><span class='pli'>luñcitvā sabbaso tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='37.3' data-uid='thi-ap21:37.3'><span class='pli'>Pabbajitvāna samayaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.4' data-uid='thi-ap21:37.4'><span class='pli'>ācikkhiṁsu nirantaraṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='38.1' data-uid='thi-ap21:38.1'><span class='pli'>Tato taṁ uggahetvāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.2' data-uid='thi-ap21:38.2'><span class='pli'>nisīditvāna ekikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='38.3' data-uid='thi-ap21:38.3'><span class='pli'>Samayaṁ taṁ vicintesiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.4' data-uid='thi-ap21:38.4'><span class='pli'>suvāno mānusaṁ karaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='39.1' data-uid='thi-ap21:39.1'><span class='pli'>Chinnaṁ gayha samīpe me, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.2' data-uid='thi-ap21:39.2'><span class='pli'>pātayitvā apakkami; </span></span></span>\n<span class='verse-line'><span class='segment' id='39.3' data-uid='thi-ap21:39.3'><span class='pli'>Disvā nimittamalabhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.4' data-uid='thi-ap21:39.4'><span class='pli'>hatthaṁ taṁ puḷavākulaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='40.1' data-uid='thi-ap21:40.1'><span class='pli'>Tato uṭṭhāya saṁviggā, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.2' data-uid='thi-ap21:40.2'><span class='pli'>apucchiṁ sahadhammike; </span></span></span>\n<span class='verse-line'><span class='segment' id='40.3' data-uid='thi-ap21:40.3'><span class='pli'>Te avocuṁ ‘vijānanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.4' data-uid='thi-ap21:40.4'><span class='pli'>taṁ atthaṁ sakyabhikkhavo’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='41.1' data-uid='thi-ap21:41.1'><span class='pli'>Sāhaṁ tamatthaṁ pucchissaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.2' data-uid='thi-ap21:41.2'><span class='pli'>upetvā buddhasāvake; </span></span></span>\n<span class='verse-line'><span class='segment' id='41.3' data-uid='thi-ap21:41.3'><span class='pli'>Te mamādāya gacchiṁsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.4' data-uid='thi-ap21:41.4'><span class='pli'>buddhaseṭṭhassa santikaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='42.1' data-uid='thi-ap21:42.1'><span class='pli'>So me dhammamadesesi, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.2' data-uid='thi-ap21:42.2'><span class='pli'>khandhāyatanadhātuyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='42.3' data-uid='thi-ap21:42.3'><span class='pli'>Asubhāniccadukkhāti, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.4' data-uid='thi-ap21:42.4'><span class='pli'>anattāti ca nāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='43.1' data-uid='thi-ap21:43.1'><span class='pli'>Tassa dhammaṁ suṇitvāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.2' data-uid='thi-ap21:43.2'><span class='pli'>dhammacakkhuṁ visodhayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='43.3' data-uid='thi-ap21:43.3'><span class='pli'>Tato viññātasaddhammā, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.4' data-uid='thi-ap21:43.4'><span class='pli'>pabbajjaṁ upasampadaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='44.1' data-uid='thi-ap21:44.1'><span class='pli'>Āyācito tadā āha, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.2' data-uid='thi-ap21:44.2'><span class='pli'>‘ehi bhadde’ti nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='44.3' data-uid='thi-ap21:44.3'><span class='pli'>Tadāhaṁ upasampannā, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.4' data-uid='thi-ap21:44.4'><span class='pli'>parittaṁ toyamaddasaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='45.1' data-uid='thi-ap21:45.1'><span class='pli'>Pādapakkhālanenāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.2' data-uid='thi-ap21:45.2'><span class='pli'>ñatvā saudayabbayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='45.3' data-uid='thi-ap21:45.3'><span class='pli'>Tathā sabbepi saṅkhāre, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.4' data-uid='thi-ap21:45.4'><span class='pli'>īdisaṁ cintayiṁ tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='46.1' data-uid='thi-ap21:46.1'><span class='pli'>Tato cittaṁ vimucci me, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.2' data-uid='thi-ap21:46.2'><span class='pli'>anupādāya sabbaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='46.3' data-uid='thi-ap21:46.3'><span class='pli'>Khippābhiññānamaggaṁ me, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.4' data-uid='thi-ap21:46.4'><span class='pli'>tadā paññāpayī jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='47.1' data-uid='thi-ap21:47.1'><span class='pli'>Iddhīsu ca vasī homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.2' data-uid='thi-ap21:47.2'><span class='pli'>dibbāya sotadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='47.3' data-uid='thi-ap21:47.3'><span class='pli'>Paracittāni jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.4' data-uid='thi-ap21:47.4'><span class='pli'>satthusāsanakārikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='48.1' data-uid='thi-ap21:48.1'><span class='pli'>Pubbenivāsaṁ jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.2' data-uid='thi-ap21:48.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='48.3' data-uid='thi-ap21:48.3'><span class='pli'>Khepetvā āsave sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.4' data-uid='thi-ap21:48.4'><span class='pli'>visuddhāsiṁ sunimmalā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='49.1' data-uid='thi-ap21:49.1'><span class='pli'>Pariciṇṇo mayā satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.2' data-uid='thi-ap21:49.2'><span class='pli'>kataṁ buddhassa sāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='49.3' data-uid='thi-ap21:49.3'><span class='pli'>Ohito garuko bhāro, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.4' data-uid='thi-ap21:49.4'><span class='pli'>bhavanetti samūhatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='50.1' data-uid='thi-ap21:50.1'><span class='pli'>Yassatthāya pabbajitā, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.2' data-uid='thi-ap21:50.2'><span class='pli'>agārasmānagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='50.3' data-uid='thi-ap21:50.3'><span class='pli'>So me attho anuppatto, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.4' data-uid='thi-ap21:50.4'><span class='pli'>sabbasaṁyojanakkhayo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='51.1' data-uid='thi-ap21:51.1'><span class='pli'>Atthadhammaniruttīsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.2' data-uid='thi-ap21:51.2'><span class='pli'>paṭibhāne tatheva ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='51.3' data-uid='thi-ap21:51.3'><span class='pli'>Ñāṇaṁ me vimalaṁ suddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.4' data-uid='thi-ap21:51.4'><span class='pli'>buddhaseṭṭhassa sāsane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='52.1' data-uid='thi-ap21:52.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='52.2' data-uid='thi-ap21:52.2'><span class='pli'>bhavā sabbe samūhatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='52.3' data-uid='thi-ap21:52.3'><span class='pli'>Nāgīva bandhanaṁ chetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='52.4' data-uid='thi-ap21:52.4'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='53.1' data-uid='thi-ap21:53.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='53.2' data-uid='thi-ap21:53.2'><span class='pli'>mama buddhassa santike; </span></span></span>\n<span class='verse-line'><span class='segment' id='53.3' data-uid='thi-ap21:53.3'><span class='pli'>Tisso vijjā anuppattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='53.4' data-uid='thi-ap21:53.4'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='54.1' data-uid='thi-ap21:54.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='54.2' data-uid='thi-ap21:54.2'><span class='pli'>vimokkhāpi ca aṭṭhime; </span></span></span>\n<span class='verse-line'><span class='segment' id='54.3' data-uid='thi-ap21:54.3'><span class='pli'>Chaḷabhiññā sacchikatā, </span></span></span>\n<span class='verse-line'><span class='segment' id='54.4' data-uid='thi-ap21:54.4'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='55.1' data-uid='thi-ap21:55.1'><span class='pli'>Itthaṁ sudaṁ bhaddākuṇḍalakesā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='56.1' data-uid='thi-ap21:56.1'><span class='pli'>Kuṇḍalakesātheriyāpadānaṁ paṭhamaṁ. </span></span></p></article>\n"
  },
  "thi-ap22": {
    "previous": "thi-ap21",
    "next": "thi-ap23",
    "content": "<article id='thi-ap22'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap22:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap22:0.2'><span class='pli'>Kuṇḍalakesīvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap22:0.3'><span class='pli'>Kisāgotamītherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap22:1.1'><span class='pli'>“Padumuttaro nāma jino, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap22:1.2'><span class='pli'>sabbadhammāna pāragū; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap22:1.3'><span class='pli'>Ito satasahassamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap22:1.4'><span class='pli'>kappe uppajji nāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap22:2.1'><span class='pli'>Tadāhaṁ haṁsavatiyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap22:2.2'><span class='pli'>jātā aññatare kule; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap22:2.3'><span class='pli'>Upetvā taṁ naravaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap22:2.4'><span class='pli'>saraṇaṁ samupāgamiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap22:3.1'><span class='pli'>Dhammañca tassa assosiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap22:3.2'><span class='pli'>catusaccūpasañhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap22:3.3'><span class='pli'>Madhuraṁ paramassādaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap22:3.4'><span class='pli'>vaṭṭasantisukhāvahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap22:4.1'><span class='pli'>Tadā ca bhikkhuniṁ vīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap22:4.2'><span class='pli'>lūkhacīvaradhāriniṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap22:4.3'><span class='pli'>Ṭhapento etadaggamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap22:4.4'><span class='pli'>vaṇṇayī purisuttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap22:5.1'><span class='pli'>Janetvānappakaṁ pītiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap22:5.2'><span class='pli'>sutvā bhikkhuniyā guṇe; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap22:5.3'><span class='pli'>Kāraṁ katvāna buddhassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap22:5.4'><span class='pli'>yathāsatti yathābalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap22:6.1'><span class='pli'>Nipacca munivaraṁ taṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap22:6.2'><span class='pli'>taṁ ṭhānamabhipatthayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap22:6.3'><span class='pli'>Tadānumodi sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap22:6.4'><span class='pli'>ṭhānalābhāya nāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap22:7.1'><span class='pli'>‘Satasahassito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap22:7.2'><span class='pli'>okkākakulasambhavo; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap22:7.3'><span class='pli'>Gotamo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap22:7.4'><span class='pli'>satthā loke bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap22:8.1'><span class='pli'>Tassa dhammesu dāyādā, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap22:8.2'><span class='pli'>orasā dhammanimmitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap22:8.3'><span class='pli'>Kisāgotamī nāmena, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap22:8.4'><span class='pli'>hessati satthu sāvikā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap22:9.1'><span class='pli'>Taṁ sutvā muditā hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap22:9.2'><span class='pli'>yāvajīvaṁ tadā jinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap22:9.3'><span class='pli'>Mettacittā paricariṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap22:9.4'><span class='pli'>paccayehi vināyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap22:10.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap22:10.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap22:10.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap22:10.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap22:11.1'><span class='pli'>Imamhi bhaddake kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap22:11.2'><span class='pli'>brahmabandhu mahāyaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap22:11.3'><span class='pli'>Kassapo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap22:11.4'><span class='pli'>uppajji vadataṁ varo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap22:12.1'><span class='pli'>Upaṭṭhāko mahesissa, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap22:12.2'><span class='pli'>tadā āsi narissaro; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap22:12.3'><span class='pli'>Kāsirājā kikī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap22:12.4'><span class='pli'>bārāṇasipuruttame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap22:13.1'><span class='pli'>Pañcamī tassa dhītāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap22:13.2'><span class='pli'>dhammā nāmena vissutā; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap22:13.3'><span class='pli'>Dhammaṁ sutvā jinaggassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap22:13.4'><span class='pli'>pabbajjaṁ samarocayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap22:14.1'><span class='pli'>Anujāni na no tāto, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap22:14.2'><span class='pli'>agāreva tadā mayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap22:14.3'><span class='pli'>Vīsavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap22:14.4'><span class='pli'>vicarimha atanditā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap22:15.1'><span class='pli'>Komāribrahmacariyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap22:15.2'><span class='pli'>rājakaññā sukhedhitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap22:15.3'><span class='pli'>Buddhopaṭṭhānaniratā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap22:15.4'><span class='pli'>muditā satta dhītaro. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap22:16.1'><span class='pli'>Samaṇī samaṇaguttā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap22:16.2'><span class='pli'>bhikkhunī bhikkhudāyikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap22:16.3'><span class='pli'>Dhammā ceva sudhammā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap22:16.4'><span class='pli'>sattamī saṅghadāyikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap22:17.1'><span class='pli'>Khemā uppalavaṇṇā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap22:17.2'><span class='pli'>paṭācārā ca kuṇḍalā; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap22:17.3'><span class='pli'>Ahañca dhammadinnā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap22:17.4'><span class='pli'>visākhā hoti sattamī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap22:18.1'><span class='pli'>Tehi kammehi sukatehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap22:18.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap22:18.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap22:18.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap22:19.1'><span class='pli'>Pacchime ca bhave dāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap22:19.2'><span class='pli'>jātā seṭṭhikule ahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap22:19.3'><span class='pli'>Duggate adhane naṭṭhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap22:19.4'><span class='pli'>gatā ca sadhanaṁ kulaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap22:20.1'><span class='pli'>Patiṁ ṭhapetvā sesā me, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap22:20.2'><span class='pli'>dessanti adhanā iti; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap22:20.3'><span class='pli'>Yadā ca pasūtā āsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap22:20.4'><span class='pli'>sabbesaṁ dayitā tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap22:21.1'><span class='pli'>Yadā so taruṇo bhaddo, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap22:21.2'><span class='pli'>komalako sukhedhito; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap22:21.3'><span class='pli'>Sapāṇamiva kanto me, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap22:21.4'><span class='pli'>tadā yamavasaṁ gato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap22:22.1'><span class='pli'>Sokaṭṭādīnavadanā, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap22:22.2'><span class='pli'>assunettā rudammukhā; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap22:22.3'><span class='pli'>Mataṁ kuṇapamādāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap22:22.4'><span class='pli'>vilapantī gamāmahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap22:23.1'><span class='pli'>Tadā ekena sandiṭṭhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap22:23.2'><span class='pli'>upetvābhisakkuttamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap22:23.3'><span class='pli'>Avocaṁ ‘dehi bhesajjaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='thi-ap22:23.4'><span class='pli'>puttasañjīvananti bho’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap22:24.1'><span class='pli'>‘Na vijjante matā yasmiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap22:24.2'><span class='pli'>gehe siddhatthakaṁ tato; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap22:24.3'><span class='pli'>Āharā’ti jino āha, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='thi-ap22:24.4'><span class='pli'>vinayopāyakovido. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='thi-ap22:25.1'><span class='pli'>Tadā gamitvā sāvatthiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='thi-ap22:25.2'><span class='pli'>na labhiṁ tādisaṁ gharaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='thi-ap22:25.3'><span class='pli'>Kuto siddhatthakaṁ tasmā, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='thi-ap22:25.4'><span class='pli'>tato laddhā satiṁ ahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='thi-ap22:26.1'><span class='pli'>Kuṇapaṁ chaḍḍayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='thi-ap22:26.2'><span class='pli'>upesiṁ lokanāyakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='thi-ap22:26.3'><span class='pli'>Dūratova mamaṁ disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='thi-ap22:26.4'><span class='pli'>avoca madhurassaro. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='thi-ap22:27.1'><span class='pli'>‘Yo ca vassasataṁ jīve, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='thi-ap22:27.2'><span class='pli'>apassaṁ udayabbayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='thi-ap22:27.3'><span class='pli'>Ekāhaṁ jīvitaṁ seyyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='thi-ap22:27.4'><span class='pli'>passato udayabbayaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='thi-ap22:28.1'><span class='pli'>Na gāmadhammo nigamassa dhammo, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='thi-ap22:28.2'><span class='pli'>Na cāpiyaṁ ekakulassa dhammo; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='thi-ap22:28.3'><span class='pli'>Sabbassa lokassa sadevakassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='thi-ap22:28.4'><span class='pli'>Eseva dhammo yadidaṁ aniccatā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='thi-ap22:29.1'><span class='pli'>Sāhaṁ sutvānimā gāthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='thi-ap22:29.2'><span class='pli'>dhammacakkhuṁ visodhayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='thi-ap22:29.3'><span class='pli'>Tato viññātasaddhammā, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='thi-ap22:29.4'><span class='pli'>pabbajiṁ anagāriyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='thi-ap22:30.1'><span class='pli'>Tathā pabbajitā santī, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='thi-ap22:30.2'><span class='pli'>yuñjantī jinasāsane; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='thi-ap22:30.3'><span class='pli'>Na cireneva kālena, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='thi-ap22:30.4'><span class='pli'>arahattamapāpuṇiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='thi-ap22:31.1'><span class='pli'>Iddhīsu ca vasī homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='thi-ap22:31.2'><span class='pli'>dibbāya sotadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='thi-ap22:31.3'><span class='pli'>Paracittāni jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='thi-ap22:31.4'><span class='pli'>satthusāsanakārikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='thi-ap22:32.1'><span class='pli'>Pubbenivāsaṁ jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='thi-ap22:32.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='thi-ap22:32.3'><span class='pli'>Khepetvā āsave sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='thi-ap22:32.4'><span class='pli'>visuddhāsiṁ sunimmalā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='thi-ap22:33.1'><span class='pli'>Pariciṇṇo mayā satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='thi-ap22:33.2'><span class='pli'>kataṁ buddhassa sāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='thi-ap22:33.3'><span class='pli'>Ohito garuko bhāro, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='thi-ap22:33.4'><span class='pli'>bhavanetti samūhatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='34.1' data-uid='thi-ap22:34.1'><span class='pli'>Yassatthāya pabbajitā, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='thi-ap22:34.2'><span class='pli'>agārasmānagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='thi-ap22:34.3'><span class='pli'>So me attho anuppatto, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.4' data-uid='thi-ap22:34.4'><span class='pli'>sabbasaṁyojanakkhayo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='35.1' data-uid='thi-ap22:35.1'><span class='pli'>Atthadhammaniruttīsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.2' data-uid='thi-ap22:35.2'><span class='pli'>paṭibhāne tatheva ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='35.3' data-uid='thi-ap22:35.3'><span class='pli'>Ñāṇaṁ me vimalaṁ suddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.4' data-uid='thi-ap22:35.4'><span class='pli'>buddhaseṭṭhassa vāhasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='36.1' data-uid='thi-ap22:36.1'><span class='pli'>Saṅkārakūṭā āhitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.2' data-uid='thi-ap22:36.2'><span class='pli'>susānā rathiyāpi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='36.3' data-uid='thi-ap22:36.3'><span class='pli'>Tato saṅghāṭikaṁ katvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.4' data-uid='thi-ap22:36.4'><span class='pli'>lūkhaṁ dhāremi cīvaraṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='37.1' data-uid='thi-ap22:37.1'><span class='pli'>Jino tasmiṁ guṇe tuṭṭho, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.2' data-uid='thi-ap22:37.2'><span class='pli'>lūkhacīvaradhāraṇe; </span></span></span>\n<span class='verse-line'><span class='segment' id='37.3' data-uid='thi-ap22:37.3'><span class='pli'>Ṭhapesi etadaggamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.4' data-uid='thi-ap22:37.4'><span class='pli'>parisāsu vināyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='38.1' data-uid='thi-ap22:38.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.2' data-uid='thi-ap22:38.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='38.3' data-uid='thi-ap22:38.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='39.1' data-uid='thi-ap22:39.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.2' data-uid='thi-ap22:39.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='39.3' data-uid='thi-ap22:39.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='40.1' data-uid='thi-ap22:40.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.2' data-uid='thi-ap22:40.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='40.3' data-uid='thi-ap22:40.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='41.1' data-uid='thi-ap22:41.1'><span class='pli'>Itthaṁ sudaṁ kisāgotamī bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='42.1' data-uid='thi-ap22:42.1'><span class='pli'>Kisāgotamītheriyāpadānaṁ dutiyaṁ. </span></span></p></article>\n"
  },
  "thi-ap23": {
    "previous": "thi-ap22",
    "next": "thi-ap24",
    "content": "<article id='thi-ap23'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap23:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap23:0.2'><span class='pli'>Kuṇḍalakesīvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap23:0.3'><span class='pli'>Dhammadinnātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap23:1.1'><span class='pli'>“Padumuttaro nāma jino, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap23:1.2'><span class='pli'>sabbadhammāna pāragū; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap23:1.3'><span class='pli'>Ito satasahassamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap23:1.4'><span class='pli'>kappe uppajji nāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap23:2.1'><span class='pli'>Tadāhaṁ haṁsavatiyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap23:2.2'><span class='pli'>kule aññatare ahuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap23:2.3'><span class='pli'>Parakammakārī āsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap23:2.4'><span class='pli'>nipakā sīlasaṁvutā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap23:3.1'><span class='pli'>Padumuttarabuddhassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap23:3.2'><span class='pli'>sujāto aggasāvako; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap23:3.3'><span class='pli'>Vihārā abhinikkhamma, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap23:3.4'><span class='pli'>piṇḍapātāya gacchati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap23:4.1'><span class='pli'>Ghaṭaṁ gahetvā gacchantī, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap23:4.2'><span class='pli'>tadā udakahārikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap23:4.3'><span class='pli'>Taṁ disvā adadaṁ pūpaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap23:4.4'><span class='pli'>pasannā sehi pāṇibhi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap23:5.1'><span class='pli'>Paṭiggahetvā tattheva, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap23:5.2'><span class='pli'>nisinno paribhuñji so; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap23:5.3'><span class='pli'>Tato netvāna taṁ gehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap23:5.4'><span class='pli'>adāsiṁ tassa bhojanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap23:6.1'><span class='pli'>Tato me ayyako tuṭṭho, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap23:6.2'><span class='pli'>akarī suṇisaṁ sakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap23:6.3'><span class='pli'>Sassuyā saha gantvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap23:6.4'><span class='pli'>sambuddhaṁ abhivādayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap23:7.1'><span class='pli'>Tadā so dhammakathikaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap23:7.2'><span class='pli'>bhikkhuniṁ parikittayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap23:7.3'><span class='pli'>Ṭhapesi etadaggamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap23:7.4'><span class='pli'>taṁ sutvā muditā ahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap23:8.1'><span class='pli'>Nimantayitvā sugataṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap23:8.2'><span class='pli'>sasaṅghaṁ lokanāyakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap23:8.3'><span class='pli'>Mahādānaṁ daditvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap23:8.4'><span class='pli'>taṁ ṭhānamabhipatthayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap23:9.1'><span class='pli'>Tato maṁ sugato āha, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap23:9.2'><span class='pli'>ghananinnādasussaro; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap23:9.3'><span class='pli'>‘Mamupaṭṭhānanirate, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap23:9.4'><span class='pli'>sasaṅghaparivesike. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap23:10.1'><span class='pli'>Saddhammassavane yutte, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap23:10.2'><span class='pli'>guṇavaddhitamānase; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap23:10.3'><span class='pli'>Bhadde bhavassu muditā, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap23:10.4'><span class='pli'>lacchase paṇidhīphalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap23:11.1'><span class='pli'>Satasahassito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap23:11.2'><span class='pli'>okkākakulasambhavo; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap23:11.3'><span class='pli'>Gotamo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap23:11.4'><span class='pli'>satthā loke bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap23:12.1'><span class='pli'>Tassa dhammesu dāyādā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap23:12.2'><span class='pli'>orasā dhammanimmitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap23:12.3'><span class='pli'>Dhammadinnāti nāmena, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap23:12.4'><span class='pli'>hessati satthu sāvikā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap23:13.1'><span class='pli'>Taṁ sutvā muditā hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap23:13.2'><span class='pli'>yāvajīvaṁ mahāmuniṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap23:13.3'><span class='pli'>Mettacittā paricariṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap23:13.4'><span class='pli'>paccayehi vināyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap23:14.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap23:14.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap23:14.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap23:14.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap23:15.1'><span class='pli'>Imamhi bhaddake kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap23:15.2'><span class='pli'>brahmabandhu mahāyaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap23:15.3'><span class='pli'>Kassapo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap23:15.4'><span class='pli'>uppajji vadataṁ varo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap23:16.1'><span class='pli'>Upaṭṭhāko mahesissa, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap23:16.2'><span class='pli'>tadā āsi narissaro; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap23:16.3'><span class='pli'>Kāsirājā kikī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap23:16.4'><span class='pli'>bārāṇasipuruttame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap23:17.1'><span class='pli'>Chaṭṭhā tassāsahaṁ dhītā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap23:17.2'><span class='pli'>sudhammā iti vissutā; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap23:17.3'><span class='pli'>Dhammaṁ sutvā jinaggassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap23:17.4'><span class='pli'>pabbajjaṁ samarocayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap23:18.1'><span class='pli'>Anujāni na no tāto, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap23:18.2'><span class='pli'>agāreva tadā mayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap23:18.3'><span class='pli'>Vīsavassasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap23:18.4'><span class='pli'>vicarimha atanditā. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='19.1' data-uid='thi-ap23:19.1'><span class='pli'>Tatiyaṁ bhāṇavāraṁ. </span></span></p>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap23:20.1'><span class='pli'>Komāribrahmacariyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap23:20.2'><span class='pli'>rājakaññā sukhedhitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap23:20.3'><span class='pli'>Buddhopaṭṭhānaniratā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap23:20.4'><span class='pli'>muditā satta dhītaro. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap23:21.1'><span class='pli'>Samaṇī samaṇaguttā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap23:21.2'><span class='pli'>bhikkhunī bhikkhudāyikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap23:21.3'><span class='pli'>Dhammā ceva sudhammā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap23:21.4'><span class='pli'>sattamī saṅghadāyikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap23:22.1'><span class='pli'>Khemā uppalavaṇṇā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap23:22.2'><span class='pli'>paṭācārā ca kuṇḍalā; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap23:22.3'><span class='pli'>Gotamī ca ahañceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap23:22.4'><span class='pli'>visākhā hoti sattamī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap23:23.1'><span class='pli'>Tehi kammehi sukatehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap23:23.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap23:23.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='thi-ap23:23.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap23:24.1'><span class='pli'>Pacchime ca bhave dāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap23:24.2'><span class='pli'>giribbajapuruttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap23:24.3'><span class='pli'>Jātā seṭṭhikule phīte, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='thi-ap23:24.4'><span class='pli'>sabbakāmasamiddhine. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='thi-ap23:25.1'><span class='pli'>Yadā rūpaguṇūpetā, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='thi-ap23:25.2'><span class='pli'>paṭhame yobbane ṭhitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='thi-ap23:25.3'><span class='pli'>Tadā parakulaṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='thi-ap23:25.4'><span class='pli'>vasiṁ sukhasamappitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='thi-ap23:26.1'><span class='pli'>Upetvā lokasaraṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='thi-ap23:26.2'><span class='pli'>suṇitvā dhammadesanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='thi-ap23:26.3'><span class='pli'>Anāgāmiphalaṁ patto, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='thi-ap23:26.4'><span class='pli'>sāmiko me subuddhimā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='thi-ap23:27.1'><span class='pli'>Tadāhaṁ anujānetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='thi-ap23:27.2'><span class='pli'>pabbajiṁ anagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='thi-ap23:27.3'><span class='pli'>Na cireneva kālena, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='thi-ap23:27.4'><span class='pli'>arahattamapāpuṇiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='thi-ap23:28.1'><span class='pli'>Tadā upāsako so maṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='thi-ap23:28.2'><span class='pli'>upagantvā apucchatha; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='thi-ap23:28.3'><span class='pli'>Gambhīre nipuṇe pañhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='thi-ap23:28.4'><span class='pli'>te sabbe byākariṁ ahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='thi-ap23:29.1'><span class='pli'>Jino tasmiṁ guṇe tuṭṭho, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='thi-ap23:29.2'><span class='pli'>etadagge ṭhapesi maṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='thi-ap23:29.3'><span class='pli'>‘Bhikkhuniṁ dhammakathikaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='thi-ap23:29.4'><span class='pli'>nāññaṁ passāmi edisiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='thi-ap23:30.1'><span class='pli'>Dhammadinnā yathā dhīrā, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='thi-ap23:30.2'><span class='pli'>evaṁ dhāretha bhikkhavo’; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='thi-ap23:30.3'><span class='pli'>Evāhaṁ paṇḍitā homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='thi-ap23:30.4'><span class='pli'>nāyakenānukampitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='thi-ap23:31.1'><span class='pli'>Pariciṇṇo mayā satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='thi-ap23:31.2'><span class='pli'>kataṁ buddhassa sāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='thi-ap23:31.3'><span class='pli'>Ohito garuko bhāro, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='thi-ap23:31.4'><span class='pli'>bhavanetti samūhatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='thi-ap23:32.1'><span class='pli'>Yassatthāya pabbajitā, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='thi-ap23:32.2'><span class='pli'>agārasmānagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='thi-ap23:32.3'><span class='pli'>So me attho anuppatto, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='thi-ap23:32.4'><span class='pli'>sabbasaṁyojanakkhayo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='thi-ap23:33.1'><span class='pli'>Iddhīsu ca vasī homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='thi-ap23:33.2'><span class='pli'>dibbāya sotadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='thi-ap23:33.3'><span class='pli'>Paracittāni jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='thi-ap23:33.4'><span class='pli'>satthusāsanakārikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='34.1' data-uid='thi-ap23:34.1'><span class='pli'>Pubbenivāsaṁ jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='thi-ap23:34.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='thi-ap23:34.3'><span class='pli'>Khepetvā āsave sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.4' data-uid='thi-ap23:34.4'><span class='pli'>visuddhāsiṁ sunimmalā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='35.1' data-uid='thi-ap23:35.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.2' data-uid='thi-ap23:35.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='35.3' data-uid='thi-ap23:35.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='36.1' data-uid='thi-ap23:36.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.2' data-uid='thi-ap23:36.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='36.3' data-uid='thi-ap23:36.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='37.1' data-uid='thi-ap23:37.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.2' data-uid='thi-ap23:37.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='37.3' data-uid='thi-ap23:37.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='38.1' data-uid='thi-ap23:38.1'><span class='pli'>Itthaṁ sudaṁ dhammadinnā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='39.1' data-uid='thi-ap23:39.1'><span class='pli'>Dhammadinnātheriyāpadānaṁ tatiyaṁ. </span></span></p></article>\n"
  },
  "thi-ap24": {
    "previous": "thi-ap23",
    "next": "thi-ap25",
    "content": "<article id='thi-ap24'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap24:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap24:0.2'><span class='pli'>Kuṇḍalakesīvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap24:0.3'><span class='pli'>Sakulātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap24:1.1'><span class='pli'>“Padumuttaro nāma jino, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap24:1.2'><span class='pli'>sabbadhammāna pāragū; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap24:1.3'><span class='pli'>Ito satasahassamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap24:1.4'><span class='pli'>kappe uppajji nāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap24:2.1'><span class='pli'>Hitāya sabbasattānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap24:2.2'><span class='pli'>sukhāya vadataṁ varo; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap24:2.3'><span class='pli'>Atthāya purisājañño, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap24:2.4'><span class='pli'>paṭipanno sadevake. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap24:3.1'><span class='pli'>Yasaggapatto sirimā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap24:3.2'><span class='pli'>kittivaṇṇagato jino; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap24:3.3'><span class='pli'>Pūjito sabbalokassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap24:3.4'><span class='pli'>disāsabbāsu vissuto. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap24:4.1'><span class='pli'>Uttiṇṇavicikiccho so, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap24:4.2'><span class='pli'>vītivattakathaṅkatho; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap24:4.3'><span class='pli'>Sampuṇṇamanasaṅkappo, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap24:4.4'><span class='pli'>patto sambodhimuttamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap24:5.1'><span class='pli'>Anuppannassa maggassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap24:5.2'><span class='pli'>uppādetā naruttamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap24:5.3'><span class='pli'>Anakkhātañca akkhāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap24:5.4'><span class='pli'>asañjātañca sañjanī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap24:6.1'><span class='pli'>Maggaññū ca maggavidū, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap24:6.2'><span class='pli'>maggakkhāyī narāsabho; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap24:6.3'><span class='pli'>Maggassa kusalo satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap24:6.4'><span class='pli'>sārathīnaṁ varuttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap24:7.1'><span class='pli'>Mahākāruṇiko satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap24:7.2'><span class='pli'>dhammaṁ desesi nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap24:7.3'><span class='pli'>Nimugge kāmapaṅkamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap24:7.4'><span class='pli'>samuddharati pāṇine. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap24:8.1'><span class='pli'>Tadāhaṁ haṁsavatiyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap24:8.2'><span class='pli'>jātā khattiyanandanā; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap24:8.3'><span class='pli'>Surūpā sadhanā cāpi, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap24:8.4'><span class='pli'>dayitā ca sirīmatī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap24:9.1'><span class='pli'>Ānandassa mahārañño, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap24:9.2'><span class='pli'>dhītā paramasobhaṇā; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap24:9.3'><span class='pli'>Vemātā bhaginī cāpi, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap24:9.4'><span class='pli'>padumuttaranāmino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap24:10.1'><span class='pli'>Rājakaññāhi sahitā, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap24:10.2'><span class='pli'>sabbābharaṇabhūsitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap24:10.3'><span class='pli'>Upāgamma mahāvīraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap24:10.4'><span class='pli'>assosiṁ dhammadesanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap24:11.1'><span class='pli'>Tadā hi so lokagaru, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap24:11.2'><span class='pli'>bhikkhuniṁ dibbacakkhukaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap24:11.3'><span class='pli'>Kittayaṁ parisāmajjhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap24:11.4'><span class='pli'>aggaṭṭhāne ṭhapesi taṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap24:12.1'><span class='pli'>Suṇitvā tamahaṁ haṭṭhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap24:12.2'><span class='pli'>dānaṁ datvāna satthuno; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap24:12.3'><span class='pli'>Pūjitvāna ca sambuddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap24:12.4'><span class='pli'>dibbacakkhuṁ apatthayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap24:13.1'><span class='pli'>Tato avoca maṁ satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap24:13.2'><span class='pli'>‘nande lacchasi patthitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap24:13.3'><span class='pli'>Padīpadhammadānānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap24:13.4'><span class='pli'>phalametaṁ sunicchitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap24:14.1'><span class='pli'>Satasahassito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap24:14.2'><span class='pli'>okkākakulasambhavo; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap24:14.3'><span class='pli'>Gotamo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap24:14.4'><span class='pli'>satthā loke bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap24:15.1'><span class='pli'>Tassa dhammesu dāyādā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap24:15.2'><span class='pli'>orasā dhammanimmitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap24:15.3'><span class='pli'>Sakulā nāma nāmena, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap24:15.4'><span class='pli'>hessati satthu sāvikā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap24:16.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap24:16.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap24:16.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap24:16.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap24:17.1'><span class='pli'>Imamhi bhaddake kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap24:17.2'><span class='pli'>brahmabandhu mahāyaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap24:17.3'><span class='pli'>Kassapo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap24:17.4'><span class='pli'>uppajji vadataṁ varo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap24:18.1'><span class='pli'>Paribbājakinī āsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap24:18.2'><span class='pli'>tadāhaṁ ekacārinī; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap24:18.3'><span class='pli'>Bhikkhāya vicaritvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap24:18.4'><span class='pli'>alabhiṁ telamattakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap24:19.1'><span class='pli'>Tena dīpaṁ padīpetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap24:19.2'><span class='pli'>upaṭṭhiṁ sabbasaṁvariṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap24:19.3'><span class='pli'>Cetiyaṁ dvipadaggassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap24:19.4'><span class='pli'>vippasannena cetasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap24:20.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap24:20.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap24:20.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap24:20.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap24:21.1'><span class='pli'>Yattha yatthūpapajjāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap24:21.2'><span class='pli'>tassa kammassa vāhasā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap24:21.3'><span class='pli'>Pajjalanti mahādīpā, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap24:21.4'><span class='pli'>tattha tattha gatāya me. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap24:22.1'><span class='pli'>Tirokuṭṭaṁ tiroselaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap24:22.2'><span class='pli'>samatiggayha pabbataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap24:22.3'><span class='pli'>Passāmahaṁ yadicchāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap24:22.4'><span class='pli'>dīpadānassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap24:23.1'><span class='pli'>Visuddhanayanā homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap24:23.2'><span class='pli'>yasasā ca jalāmahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap24:23.3'><span class='pli'>Saddhāpaññāvatī ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='thi-ap24:23.4'><span class='pli'>dīpadānassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap24:24.1'><span class='pli'>Pacchime ca bhave dāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap24:24.2'><span class='pli'>jātā vippakule ahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap24:24.3'><span class='pli'>Pahūtadhanadhaññamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='thi-ap24:24.4'><span class='pli'>mudite rājapūjite. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='thi-ap24:25.1'><span class='pli'>Ahaṁ sabbaṅgasampannā, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='thi-ap24:25.2'><span class='pli'>sabbābharaṇabhūsitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='thi-ap24:25.3'><span class='pli'>Purappavese sugataṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='thi-ap24:25.4'><span class='pli'>vātapāne ṭhitā ahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='thi-ap24:26.1'><span class='pli'>Disvā jalantaṁ yasasā, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='thi-ap24:26.2'><span class='pli'>devamanussasakkataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='thi-ap24:26.3'><span class='pli'>Anubyañjanasampannaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='thi-ap24:26.4'><span class='pli'>lakkhaṇehi vibhūsitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='thi-ap24:27.1'><span class='pli'>Udaggacittā sumanā, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='thi-ap24:27.2'><span class='pli'>pabbajjaṁ samarocayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='thi-ap24:27.3'><span class='pli'>Na cireneva kālena, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='thi-ap24:27.4'><span class='pli'>arahattamapāpuṇiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='thi-ap24:28.1'><span class='pli'>Iddhīsu ca vasī homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='thi-ap24:28.2'><span class='pli'>dibbāya sotadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='thi-ap24:28.3'><span class='pli'>Paracittāni jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='thi-ap24:28.4'><span class='pli'>satthusāsanakārikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='thi-ap24:29.1'><span class='pli'>Pubbenivāsaṁ jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='thi-ap24:29.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='thi-ap24:29.3'><span class='pli'>Khepetvā āsave sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='thi-ap24:29.4'><span class='pli'>visuddhāsiṁ sunimmalā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='thi-ap24:30.1'><span class='pli'>Pariciṇṇo mayā satthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='thi-ap24:30.2'><span class='pli'>kataṁ buddhassa sāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='thi-ap24:30.3'><span class='pli'>Ohito garuko bhāro, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='thi-ap24:30.4'><span class='pli'>bhavanetti samūhatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='thi-ap24:31.1'><span class='pli'>Yassatthāya pabbajitā, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='thi-ap24:31.2'><span class='pli'>agārasmānagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='thi-ap24:31.3'><span class='pli'>So me attho anuppatto, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='thi-ap24:31.4'><span class='pli'>sabbasaṁyojanakkhayo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='thi-ap24:32.1'><span class='pli'>Tato mahākāruṇiko, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='thi-ap24:32.2'><span class='pli'>etadagge ṭhapesi maṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='thi-ap24:32.3'><span class='pli'>Dibbacakkhukānaṁ aggā, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='thi-ap24:32.4'><span class='pli'>sakulāti naruttamo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='thi-ap24:33.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='thi-ap24:33.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='thi-ap24:33.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='34.1' data-uid='thi-ap24:34.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='thi-ap24:34.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='thi-ap24:34.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='35.1' data-uid='thi-ap24:35.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.2' data-uid='thi-ap24:35.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='35.3' data-uid='thi-ap24:35.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='36.1' data-uid='thi-ap24:36.1'><span class='pli'>Itthaṁ sudaṁ sakulā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='37.1' data-uid='thi-ap24:37.1'><span class='pli'>Sakulātheriyāpadānaṁ catutthaṁ. </span></span></p></article>\n"
  },
  "thi-ap25": {
    "previous": "thi-ap24",
    "next": "thi-ap26",
    "content": "<article id='thi-ap25'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap25:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap25:0.2'><span class='pli'>Kuṇḍalakesīvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap25:0.3'><span class='pli'>5 Nandātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap25:1.1'><span class='pli'>“Padumuttaro nāma jino, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap25:1.2'><span class='pli'>sabbadhammāna pāragū; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap25:1.3'><span class='pli'>Ito satasahassamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap25:1.4'><span class='pli'>kappe uppajji nāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap25:2.1'><span class='pli'>Ovādako viññāpako, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap25:2.2'><span class='pli'>tārako sabbapāṇinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap25:2.3'><span class='pli'>Desanākusalo buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap25:2.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap25:3.1'><span class='pli'>Anukampako kāruṇiko, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap25:3.2'><span class='pli'>hitesī sabbapāṇinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap25:3.3'><span class='pli'>Sampatte titthiye sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap25:3.4'><span class='pli'>pañcasīle patiṭṭhapi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap25:4.1'><span class='pli'>Evaṁ nirākulaṁ āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap25:4.2'><span class='pli'>suññataṁ titthiyehi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap25:4.3'><span class='pli'>Vicittaṁ arahantehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap25:4.4'><span class='pli'>vasībhūtehi tādibhi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap25:5.1'><span class='pli'>Ratanānaṭṭhapaññāsaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap25:5.2'><span class='pli'>uggatova mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap25:5.3'><span class='pli'>Kañcanagghiyasaṅkāso, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap25:5.4'><span class='pli'>bāttiṁsavaralakkhaṇo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap25:6.1'><span class='pli'>Vassasatasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap25:6.2'><span class='pli'>āyu vijjati tāvade; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap25:6.3'><span class='pli'>Tāvatā tiṭṭhamāno so, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap25:6.4'><span class='pli'>tāresi janataṁ bahuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap25:7.1'><span class='pli'>Tadāhaṁ haṁsavatiyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap25:7.2'><span class='pli'>jātā seṭṭhikule ahuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap25:7.3'><span class='pli'>Nānāratanapajjote, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap25:7.4'><span class='pli'>mahāsukhasamappitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap25:8.1'><span class='pli'>Upetvā taṁ mahāvīraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap25:8.2'><span class='pli'>assosiṁ dhammadesanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap25:8.3'><span class='pli'>Amataṁ paramassādaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap25:8.4'><span class='pli'>paramatthanivedakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap25:9.1'><span class='pli'>Tadā nimantayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap25:9.2'><span class='pli'>sasaṅghaṁ lokanāyakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap25:9.3'><span class='pli'>Datvā tassa mahādānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap25:9.4'><span class='pli'>pasannā sehi pāṇibhi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap25:10.1'><span class='pli'>Jhāyinīnaṁ bhikkhunīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap25:10.2'><span class='pli'>aggaṭṭhānamapatthayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap25:10.3'><span class='pli'>Nipacca sirasā dhīraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap25:10.4'><span class='pli'>sasaṅghaṁ lokanāyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap25:11.1'><span class='pli'>Tadā adantadamako, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap25:11.2'><span class='pli'>tilokasaraṇo pabhū; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap25:11.3'><span class='pli'>Byākāsi narasārathi, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap25:11.4'><span class='pli'>‘lacchase taṁ supatthitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap25:12.1'><span class='pli'>Satasahassito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap25:12.2'><span class='pli'>okkākakulasambhavo; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap25:12.3'><span class='pli'>Gotamo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap25:12.4'><span class='pli'>satthā loke bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap25:13.1'><span class='pli'>Tassa dhammesu dāyādā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap25:13.2'><span class='pli'>orasā dhammanimmitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap25:13.3'><span class='pli'>Nandāti nāma nāmena, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap25:13.4'><span class='pli'>hessati satthu sāvikā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap25:14.1'><span class='pli'>Taṁ sutvā muditā hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap25:14.2'><span class='pli'>yāvajīvaṁ tadā jinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap25:14.3'><span class='pli'>Mettacittā paricariṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap25:14.4'><span class='pli'>paccayehi vināyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap25:15.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap25:15.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap25:15.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap25:15.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap25:16.1'><span class='pli'>Tato cutā yāmamagaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap25:16.2'><span class='pli'>tatohaṁ tusitaṁ gatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap25:16.3'><span class='pli'>Tato ca nimmānaratiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap25:16.4'><span class='pli'>vasavattipuraṁ tato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap25:17.1'><span class='pli'>Yattha yatthūpapajjāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap25:17.2'><span class='pli'>tassa kammassa vāhasā; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap25:17.3'><span class='pli'>Tattha tattheva rājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap25:17.4'><span class='pli'>mahesittamakārayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap25:18.1'><span class='pli'>Tato cutā manussatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap25:18.2'><span class='pli'>rājānaṁ cakkavattinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap25:18.3'><span class='pli'>Maṇḍalīnañca rājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap25:18.4'><span class='pli'>mahesittamakārayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap25:19.1'><span class='pli'>Sampattiṁ anubhotvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap25:19.2'><span class='pli'>devesu manujesu ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap25:19.3'><span class='pli'>Sabbattha sukhitā hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap25:19.4'><span class='pli'>nekakappesu saṁsariṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap25:20.1'><span class='pli'>Pacchime bhave sampatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap25:20.2'><span class='pli'>suramme kapilavhaye; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap25:20.3'><span class='pli'>Rañño suddhodanassāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap25:20.4'><span class='pli'>dhītā āsiṁ aninditā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap25:21.1'><span class='pli'>Siriyā rūpiniṁ disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap25:21.2'><span class='pli'>nanditaṁ āsi taṁ kulaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap25:21.3'><span class='pli'>Tena nandāti me nāmaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap25:21.4'><span class='pli'>sundaraṁ pavaraṁ ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap25:22.1'><span class='pli'>Yuvatīnañca sabbāsaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap25:22.2'><span class='pli'>kalyāṇīti ca vissutā; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap25:22.3'><span class='pli'>Tasmimpi nagare ramme, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap25:22.4'><span class='pli'>ṭhapetvā taṁ yasodharaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap25:23.1'><span class='pli'>Jeṭṭho bhātā tilokaggo, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap25:23.2'><span class='pli'>pacchimo arahā tathā; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap25:23.3'><span class='pli'>Ekākinī gahaṭṭhāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='thi-ap25:23.4'><span class='pli'>mātarā paricoditā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap25:24.1'><span class='pli'>‘Sākiyamhi kule jātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap25:24.2'><span class='pli'>putte buddhānujā tuvaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap25:24.3'><span class='pli'>Nandenapi vinā bhūtā, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='thi-ap25:24.4'><span class='pli'>agāre kiṁ nu acchasi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='thi-ap25:25.1'><span class='pli'>Jarāvasānaṁ yobbaññaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='thi-ap25:25.2'><span class='pli'>rūpaṁ asucisammataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='thi-ap25:25.3'><span class='pli'>Rogantamapicārogyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='thi-ap25:25.4'><span class='pli'>jīvitaṁ maraṇantikaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='thi-ap25:26.1'><span class='pli'>Idampi te subhaṁ rūpaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='thi-ap25:26.2'><span class='pli'>sasīkantaṁ manoharaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='thi-ap25:26.3'><span class='pli'>Bhūsanānaṁ alaṅkāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='thi-ap25:26.4'><span class='pli'>sirisaṅghāṭasannibhaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='thi-ap25:27.1'><span class='pli'>Puñjitaṁ lokasāraṁva, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='thi-ap25:27.2'><span class='pli'>nayanānaṁ rasāyanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='thi-ap25:27.3'><span class='pli'>Puññānaṁ kittijananaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='thi-ap25:27.4'><span class='pli'>ukkākakulanandanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='thi-ap25:28.1'><span class='pli'>Na cireneva kālena, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='thi-ap25:28.2'><span class='pli'>jarā samadhisessati; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='thi-ap25:28.3'><span class='pli'>Vihāya gehaṁ kāruññe, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='thi-ap25:28.4'><span class='pli'>cara dhammamanindite’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='thi-ap25:29.1'><span class='pli'>Sutvāhaṁ mātu vacanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='thi-ap25:29.2'><span class='pli'>pabbajiṁ anagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='thi-ap25:29.3'><span class='pli'>Dehena na tu cittena, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='thi-ap25:29.4'><span class='pli'>rūpayobbanalāḷitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='thi-ap25:30.1'><span class='pli'>Mahatā ca payattena, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='thi-ap25:30.2'><span class='pli'>jhānajjhena paraṁ mama; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='thi-ap25:30.3'><span class='pli'>Kātuñca vadate mātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='thi-ap25:30.4'><span class='pli'>na cāhaṁ tattha ussukā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='thi-ap25:31.1'><span class='pli'>Tato mahākāruṇiko, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='thi-ap25:31.2'><span class='pli'>disvā maṁ kāmalālasaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='thi-ap25:31.3'><span class='pli'>Nibbindanatthaṁ rūpasmiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='thi-ap25:31.4'><span class='pli'>mama cakkhupathe jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='thi-ap25:32.1'><span class='pli'>Sakena ānubhāvena, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='thi-ap25:32.2'><span class='pli'>itthiṁ māpesi sobhiniṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='thi-ap25:32.3'><span class='pli'>Dassanīyaṁ suruciraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='thi-ap25:32.4'><span class='pli'>mamatopi surūpiniṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='thi-ap25:33.1'><span class='pli'>Tamahaṁ vimhitā disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='thi-ap25:33.2'><span class='pli'>ativimhitadehiniṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='thi-ap25:33.3'><span class='pli'>Cintayiṁ ‘saphalaṁ meti, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='thi-ap25:33.4'><span class='pli'>nettalābhañca mānusaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='34.1' data-uid='thi-ap25:34.1'><span class='pli'>Tamahaṁ ehi subhage, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='thi-ap25:34.2'><span class='pli'>yenattho taṁ vadehi me; </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='thi-ap25:34.3'><span class='pli'>Kulaṁ te nāmagottañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.4' data-uid='thi-ap25:34.4'><span class='pli'>vada me yadi te piyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='35.1' data-uid='thi-ap25:35.1'><span class='pli'>Na vañcakālo subhage, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.2' data-uid='thi-ap25:35.2'><span class='pli'>ucchaṅge maṁ nivāsaya; </span></span></span>\n<span class='verse-line'><span class='segment' id='35.3' data-uid='thi-ap25:35.3'><span class='pli'>Sīdantīva mamaṅgāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.4' data-uid='thi-ap25:35.4'><span class='pli'>pasuppaya muhuttakaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='36.1' data-uid='thi-ap25:36.1'><span class='pli'>Tato sīsaṁ mamaṅge sā, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.2' data-uid='thi-ap25:36.2'><span class='pli'>katvā sayi sulocanā; </span></span></span>\n<span class='verse-line'><span class='segment' id='36.3' data-uid='thi-ap25:36.3'><span class='pli'>Tassā nalāṭe patitā, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.4' data-uid='thi-ap25:36.4'><span class='pli'>luddhā paramadāruṇā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='37.1' data-uid='thi-ap25:37.1'><span class='pli'>Saha tassā nipātena, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.2' data-uid='thi-ap25:37.2'><span class='pli'>piḷakā upapajjatha; </span></span></span>\n<span class='verse-line'><span class='segment' id='37.3' data-uid='thi-ap25:37.3'><span class='pli'>Pagghariṁsu pabhinnā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.4' data-uid='thi-ap25:37.4'><span class='pli'>kuṇapā pubbalohitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='38.1' data-uid='thi-ap25:38.1'><span class='pli'>Pabhinnaṁ vadanañcāpi, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.2' data-uid='thi-ap25:38.2'><span class='pli'>kuṇapaṁ pūtigandhanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='38.3' data-uid='thi-ap25:38.3'><span class='pli'>Uddhumātaṁ vinilañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.4' data-uid='thi-ap25:38.4'><span class='pli'>pubbañcāpi sarīrakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='39.1' data-uid='thi-ap25:39.1'><span class='pli'>Sā paveditasabbaṅgī, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.2' data-uid='thi-ap25:39.2'><span class='pli'>nissasantī muhuṁ muhuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='39.3' data-uid='thi-ap25:39.3'><span class='pli'>Vedayantī sakaṁ dukkhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.4' data-uid='thi-ap25:39.4'><span class='pli'>karuṇaṁ paridevayi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='40.1' data-uid='thi-ap25:40.1'><span class='pli'>‘Dukkhena dukkhitā homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.2' data-uid='thi-ap25:40.2'><span class='pli'>phusayanti ca vedanā; </span></span></span>\n<span class='verse-line'><span class='segment' id='40.3' data-uid='thi-ap25:40.3'><span class='pli'>Mahādukkhe nimuggamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.4' data-uid='thi-ap25:40.4'><span class='pli'>saraṇaṁ hohi me sakhī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='41.1' data-uid='thi-ap25:41.1'><span class='pli'>Kuhiṁ vadanasobhaṁ te, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.2' data-uid='thi-ap25:41.2'><span class='pli'>kuhiṁ te tuṅganāsikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='41.3' data-uid='thi-ap25:41.3'><span class='pli'>Tambabimbavaroṭṭhaṁ te, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.4' data-uid='thi-ap25:41.4'><span class='pli'>vadanaṁ te kuhiṁ gataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='42.1' data-uid='thi-ap25:42.1'><span class='pli'>Kuhiṁ sasīnibhaṁ vaṇṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.2' data-uid='thi-ap25:42.2'><span class='pli'>kambugīvā kuhiṁ gatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='42.3' data-uid='thi-ap25:42.3'><span class='pli'>Doḷālolāva te kaṇṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.4' data-uid='thi-ap25:42.4'><span class='pli'>vevaṇṇaṁ samupāgatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='43.1' data-uid='thi-ap25:43.1'><span class='pli'>Makuḷakhārakākārā, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.2' data-uid='thi-ap25:43.2'><span class='pli'>kalikāva payodharā; </span></span></span>\n<span class='verse-line'><span class='segment' id='43.3' data-uid='thi-ap25:43.3'><span class='pli'>Pabhinnā pūtikuṇapā, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.4' data-uid='thi-ap25:43.4'><span class='pli'>duṭṭhagandhittamāgatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='44.1' data-uid='thi-ap25:44.1'><span class='pli'>Vedimajjhāva sussoṇī, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.2' data-uid='thi-ap25:44.2'><span class='pli'>sūnāva nītakibbisā; </span></span></span>\n<span class='verse-line'><span class='segment' id='44.3' data-uid='thi-ap25:44.3'><span class='pli'>Jātā amejjhabharitā, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.4' data-uid='thi-ap25:44.4'><span class='pli'>aho rūpamasassataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='45.1' data-uid='thi-ap25:45.1'><span class='pli'>Sabbaṁ sarīrasañjātaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.2' data-uid='thi-ap25:45.2'><span class='pli'>pūtigandhaṁ bhayānakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='45.3' data-uid='thi-ap25:45.3'><span class='pli'>Susānamiva bībhacchaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.4' data-uid='thi-ap25:45.4'><span class='pli'>ramante yattha bālisā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='46.1' data-uid='thi-ap25:46.1'><span class='pli'>Tadā mahākāruṇiko, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.2' data-uid='thi-ap25:46.2'><span class='pli'>bhātā me lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='46.3' data-uid='thi-ap25:46.3'><span class='pli'>Disvā saṁviggacittaṁ maṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.4' data-uid='thi-ap25:46.4'><span class='pli'>imā gāthā abhāsatha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='47.1' data-uid='thi-ap25:47.1'><span class='pli'>‘Āturaṁ kuṇapaṁ pūtiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.2' data-uid='thi-ap25:47.2'><span class='pli'>passa nande samussayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='47.3' data-uid='thi-ap25:47.3'><span class='pli'>Asubhāya cittaṁ bhāvehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.4' data-uid='thi-ap25:47.4'><span class='pli'>ekaggaṁ susamāhitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='48.1' data-uid='thi-ap25:48.1'><span class='pli'>Yathā idaṁ tathā etaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.2' data-uid='thi-ap25:48.2'><span class='pli'>yathā etaṁ tathā idaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='48.3' data-uid='thi-ap25:48.3'><span class='pli'>Duggandhaṁ pūtikaṁ vāti, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.4' data-uid='thi-ap25:48.4'><span class='pli'>bālānaṁ abhinanditaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='49.1' data-uid='thi-ap25:49.1'><span class='pli'>Evametaṁ avekkhantī, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.2' data-uid='thi-ap25:49.2'><span class='pli'>rattindivamatanditā; </span></span></span>\n<span class='verse-line'><span class='segment' id='49.3' data-uid='thi-ap25:49.3'><span class='pli'>Tato sakāya paññāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.4' data-uid='thi-ap25:49.4'><span class='pli'>abhinibbijjha dakkhasi’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='50.1' data-uid='thi-ap25:50.1'><span class='pli'>Tatohaṁ atisaṁviggā, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.2' data-uid='thi-ap25:50.2'><span class='pli'>sutvā gāthā subhāsitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='50.3' data-uid='thi-ap25:50.3'><span class='pli'>Tatraṭṭhitāvahaṁ santī, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.4' data-uid='thi-ap25:50.4'><span class='pli'>arahattamapāpuṇiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='51.1' data-uid='thi-ap25:51.1'><span class='pli'>Yattha yattha nisinnāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.2' data-uid='thi-ap25:51.2'><span class='pli'>sadā jhānaparāyaṇā; </span></span></span>\n<span class='verse-line'><span class='segment' id='51.3' data-uid='thi-ap25:51.3'><span class='pli'>Jino tasmiṁ guṇe tuṭṭho, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.4' data-uid='thi-ap25:51.4'><span class='pli'>etadagge ṭhapesi maṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='52.1' data-uid='thi-ap25:52.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='52.2' data-uid='thi-ap25:52.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='52.3' data-uid='thi-ap25:52.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='53.1' data-uid='thi-ap25:53.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='53.2' data-uid='thi-ap25:53.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='53.3' data-uid='thi-ap25:53.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='54.1' data-uid='thi-ap25:54.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='54.2' data-uid='thi-ap25:54.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='54.3' data-uid='thi-ap25:54.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='55.1' data-uid='thi-ap25:55.1'><span class='pli'>Itthaṁ sudaṁ nandā bhikkhunī janapadakalyāṇī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='56.1' data-uid='thi-ap25:56.1'><span class='pli'>Nandātheriyāpadānaṁ pañcamaṁ. </span></span></p></article>\n"
  },
  "thi-ap26": {
    "previous": "thi-ap25",
    "next": "thi-ap27",
    "content": "<article id='thi-ap26'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap26:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap26:0.2'><span class='pli'>Kuṇḍalakesīvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap26:0.3'><span class='pli'>Soṇātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap26:1.1'><span class='pli'>“Padumuttaro nāma jino, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap26:1.2'><span class='pli'>sabbadhammāna pāragū; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap26:1.3'><span class='pli'>Ito satasahassamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap26:1.4'><span class='pli'>kappe uppajji nāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap26:2.1'><span class='pli'>Tadā seṭṭhikule jātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap26:2.2'><span class='pli'>sukhitā pūjitā piyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap26:2.3'><span class='pli'>Upetvā taṁ munivaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap26:2.4'><span class='pli'>assosiṁ madhuraṁ vacaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap26:3.1'><span class='pli'>Āraddhavīriyānaggaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap26:3.2'><span class='pli'>vaṇṇesi bhikkhuniṁ jino; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap26:3.3'><span class='pli'>Taṁ sutvā muditā hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap26:3.4'><span class='pli'>kāraṁ katvāna satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap26:4.1'><span class='pli'>Abhivādiya sambuddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap26:4.2'><span class='pli'>ṭhānaṁ taṁ patthayiṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap26:4.3'><span class='pli'>Anumodi mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap26:4.4'><span class='pli'>‘sijjhataṁ paṇidhī tava. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap26:5.1'><span class='pli'>Satasahassito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap26:5.2'><span class='pli'>okkākakulasambhavo; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap26:5.3'><span class='pli'>Gotamo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap26:5.4'><span class='pli'>satthā loke bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap26:6.1'><span class='pli'>Tassa dhammesu dāyādā, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap26:6.2'><span class='pli'>orasā dhammanimmitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap26:6.3'><span class='pli'>Soṇāti nāma nāmena, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap26:6.4'><span class='pli'>hessati satthu sāvikā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap26:7.1'><span class='pli'>Taṁ sutvā muditā hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap26:7.2'><span class='pli'>yāvajīvaṁ tadā jinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap26:7.3'><span class='pli'>Mettacittā paricariṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap26:7.4'><span class='pli'>paccayehi vināyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap26:8.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap26:8.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap26:8.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap26:8.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap26:9.1'><span class='pli'>Pacchime ca bhave dāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap26:9.2'><span class='pli'>jātā seṭṭhikule ahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap26:9.3'><span class='pli'>Sāvatthiyaṁ puravare, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap26:9.4'><span class='pli'>iddhe phīte mahaddhane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap26:10.1'><span class='pli'>Yadā ca yobbanappattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap26:10.2'><span class='pli'>gantvā patikulaṁ ahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap26:10.3'><span class='pli'>Dasa puttāni ajaniṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap26:10.4'><span class='pli'>surūpāni visesato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap26:11.1'><span class='pli'>Sukhedhitā ca te sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap26:11.2'><span class='pli'>jananettamanoharā; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap26:11.3'><span class='pli'>Amittānampi rucitā, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap26:11.4'><span class='pli'>mama pageva te piyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap26:12.1'><span class='pli'>Tato mayhaṁ akāmāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap26:12.2'><span class='pli'>dasaputtapurakkhato; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap26:12.3'><span class='pli'>Pabbajittha sa me bhattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap26:12.4'><span class='pli'>devadevassa sāsane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap26:13.1'><span class='pli'>Tadekikā vicintesiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap26:13.2'><span class='pli'>‘jīvitenālamatthu me; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap26:13.3'><span class='pli'>Cattāya patiputtehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap26:13.4'><span class='pli'>vuḍḍhāya ca varākiyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap26:14.1'><span class='pli'>Ahampi tattha gacchissaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap26:14.2'><span class='pli'>sampatto yattha me pati’; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap26:14.3'><span class='pli'>Evāhaṁ cintayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap26:14.4'><span class='pli'>pabbajiṁ anagāriyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap26:15.1'><span class='pli'>Tato ca maṁ bhikkhuniyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap26:15.2'><span class='pli'>ekaṁ bhikkhunupassaye; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap26:15.3'><span class='pli'>Vihāya gacchumovādaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap26:15.4'><span class='pli'>‘tāpehi udakaṁ’ iti. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap26:16.1'><span class='pli'>Tadā udakamāhitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap26:16.2'><span class='pli'>okiritvāna kumbhiyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap26:16.3'><span class='pli'>Culle ṭhapetvā āsīnā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap26:16.4'><span class='pli'>tato cittaṁ samādahiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap26:17.1'><span class='pli'>Khandhe aniccato disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap26:17.2'><span class='pli'>dukkhato ca anattato; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap26:17.3'><span class='pli'>Khepetvā āsave sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap26:17.4'><span class='pli'>arahattamapāpuṇiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap26:18.1'><span class='pli'>Tadāgantvā bhikkhuniyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap26:18.2'><span class='pli'>uṇhodakamapucchisuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap26:18.3'><span class='pli'>Tejodhātumadhiṭṭhāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap26:18.4'><span class='pli'>khippaṁ santāpayiṁ jalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap26:19.1'><span class='pli'>Vimhitā tā jinavaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap26:19.2'><span class='pli'>etamatthamasāvayuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap26:19.3'><span class='pli'>Taṁ sutvā mudito nātho, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap26:19.4'><span class='pli'>imaṁ gāthaṁ abhāsatha. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap26:20.1'><span class='pli'>‘Yo ca vassasataṁ jīve, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap26:20.2'><span class='pli'>kusīto hīnavīriyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap26:20.3'><span class='pli'>Ekāhaṁ jīvitaṁ seyyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap26:20.4'><span class='pli'>vīriyamārabhato daḷhaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap26:21.1'><span class='pli'>Ārādhito mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap26:21.2'><span class='pli'>mayā suppaṭipattiyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap26:21.3'><span class='pli'>Āraddhavīriyānaggaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap26:21.4'><span class='pli'>mamāha sa mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap26:22.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap26:22.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap26:22.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap26:23.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap26:23.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap26:23.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap26:24.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap26:24.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap26:24.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='25.1' data-uid='thi-ap26:25.1'><span class='pli'>Itthaṁ sudaṁ soṇā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='26.1' data-uid='thi-ap26:26.1'><span class='pli'>Soṇātheriyāpadānaṁ chaṭṭhaṁ. </span></span></p></article>\n"
  },
  "thi-ap27": {
    "previous": "thi-ap26",
    "next": "thi-ap28",
    "content": "<article id='thi-ap27'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap27:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap27:0.2'><span class='pli'>Kuṇḍalakesīvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap27:0.3'><span class='pli'>Bhaddākāpilānītherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap27:1.1'><span class='pli'>“Padumuttaro nāma jino, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap27:1.2'><span class='pli'>sabbadhammāna pāragū; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap27:1.3'><span class='pli'>Ito satasahassamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap27:1.4'><span class='pli'>kappe uppajji nāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap27:2.1'><span class='pli'>Tadāhu haṁsavatiyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap27:2.2'><span class='pli'>videho nāma nāmato; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap27:2.3'><span class='pli'>Seṭṭhī pahūtaratano, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap27:2.4'><span class='pli'>tassa jāyā ahosahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap27:3.1'><span class='pli'>Kadāci so narādiccaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap27:3.2'><span class='pli'>Upecca saparijjano; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap27:3.3'><span class='pli'>Dhammamassosi buddhassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap27:3.4'><span class='pli'>Sabbadukkhabhayappahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap27:4.1'><span class='pli'>Sāvakaṁ dhutavādānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap27:4.2'><span class='pli'>aggaṁ kittesi nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap27:4.3'><span class='pli'>Sutvā sattāhikaṁ dānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap27:4.4'><span class='pli'>datvā buddhassa tādino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap27:5.1'><span class='pli'>Nipacca sirasā pāde, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap27:5.2'><span class='pli'>taṁ ṭhānamabhipatthayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap27:5.3'><span class='pli'>Sa hāsayanto parisaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap27:5.4'><span class='pli'>tadā hi narapuṅgavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap27:6.1'><span class='pli'>Seṭṭhino anukampāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap27:6.2'><span class='pli'>imā gāthā abhāsatha; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap27:6.3'><span class='pli'>‘Lacchase patthitaṁ ṭhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap27:6.4'><span class='pli'>nibbuto hohi puttaka. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap27:7.1'><span class='pli'>Satasahassito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap27:7.2'><span class='pli'>okkākakulasambhavo; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap27:7.3'><span class='pli'>Gotamo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap27:7.4'><span class='pli'>satthā loke bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap27:8.1'><span class='pli'>Tassa dhammesu dāyādo, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap27:8.2'><span class='pli'>oraso dhammanimmito; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap27:8.3'><span class='pli'>Kassapo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap27:8.4'><span class='pli'>hessati satthu sāvako’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap27:9.1'><span class='pli'>Taṁ sutvā mudito hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap27:9.2'><span class='pli'>yāvajīvaṁ tadā jinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap27:9.3'><span class='pli'>Mettacitto paricari, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap27:9.4'><span class='pli'>paccayehi vināyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap27:10.1'><span class='pli'>Sāsanaṁ jotayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap27:10.2'><span class='pli'>so madditvā kutitthiye; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap27:10.3'><span class='pli'>Veneyyaṁ vinayitvā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap27:10.4'><span class='pli'>nibbuto so sasāvako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap27:11.1'><span class='pli'>Nibbute tamhi lokagge, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap27:11.2'><span class='pli'>pūjanatthāya satthuno; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap27:11.3'><span class='pli'>Ñātimitte samānetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap27:11.4'><span class='pli'>saha tehi akārayi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap27:12.1'><span class='pli'>Sattayojanikaṁ thūpaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap27:12.2'><span class='pli'>ubbiddhaṁ ratanāmayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap27:12.3'><span class='pli'>Jalantaṁ sataraṁsiṁva, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap27:12.4'><span class='pli'>sālarājaṁva phullitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap27:13.1'><span class='pli'>Sattasatasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap27:13.2'><span class='pli'>pātiyo tattha kārayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap27:13.3'><span class='pli'>Naḷaggī viya jotantī, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap27:13.4'><span class='pli'>rataneheva sattahi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap27:14.1'><span class='pli'>Gandhatelena pūretvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap27:14.2'><span class='pli'>dīpānujjalayī tahiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap27:14.3'><span class='pli'>Pūjanatthāya mahesissa, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap27:14.4'><span class='pli'>sabbabhūtānukampino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap27:15.1'><span class='pli'>Sattasatasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap27:15.2'><span class='pli'>puṇṇakumbhāni kārayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap27:15.3'><span class='pli'>Rataneheva puṇṇāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap27:15.4'><span class='pli'>pūjanatthāya mahesino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap27:16.1'><span class='pli'>Majjhe aṭṭhaṭṭhakumbhīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap27:16.2'><span class='pli'>ussitā kañcanagghiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap27:16.3'><span class='pli'>Atirocanti vaṇṇena, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap27:16.4'><span class='pli'>saradeva divākaro. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap27:17.1'><span class='pli'>Catudvāresu sobhanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap27:17.2'><span class='pli'>toraṇā ratanāmayā; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap27:17.3'><span class='pli'>Ussitā phalakā rammā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap27:17.4'><span class='pli'>sobhanti ratanāmayā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap27:18.1'><span class='pli'>Virocanti parikkhittā, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap27:18.2'><span class='pli'>avaṭaṁsā sunimmitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap27:18.3'><span class='pli'>Ussitāni paṭākāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap27:18.4'><span class='pli'>ratanāni virocare. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap27:19.1'><span class='pli'>Surattaṁ sukataṁ cittaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap27:19.2'><span class='pli'>cetiyaṁ ratanāmayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap27:19.3'><span class='pli'>Atirocati vaṇṇena, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap27:19.4'><span class='pli'>sasañjhova divākaro. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap27:20.1'><span class='pli'>Thūpassa vediyo tisso, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap27:20.2'><span class='pli'>haritālena pūrayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap27:20.3'><span class='pli'>Ekaṁ manosilāyekaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap27:20.4'><span class='pli'>añjanena ca ekikaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap27:21.1'><span class='pli'>Pūjaṁ etādisaṁ rammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap27:21.2'><span class='pli'>kāretvā varavādino; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap27:21.3'><span class='pli'>Adāsi dānaṁ saṅghassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap27:21.4'><span class='pli'>yāvajīvaṁ yathābalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap27:22.1'><span class='pli'>Sahāva seṭṭhinā tena, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap27:22.2'><span class='pli'>tāni puññāni sabbaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap27:22.3'><span class='pli'>Yāvajīvaṁ karitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap27:22.4'><span class='pli'>sahāva sugatiṁ gatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap27:23.1'><span class='pli'>Sampattiyonubhotvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap27:23.2'><span class='pli'>devatte atha mānuse; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap27:23.3'><span class='pli'>Chāyā viya sarīrena, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='thi-ap27:23.4'><span class='pli'>saha teneva saṁsariṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap27:24.1'><span class='pli'>Ekanavutito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap27:24.2'><span class='pli'>vipassī nāma nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap27:24.3'><span class='pli'>Uppajji cārudassano, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='thi-ap27:24.4'><span class='pli'>sabbadhammavipassako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='thi-ap27:25.1'><span class='pli'>Tadāyaṁ bandhumatiyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='thi-ap27:25.2'><span class='pli'>brāhmaṇo sādhusammato; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='thi-ap27:25.3'><span class='pli'>Aḍḍho santo guṇenāpi, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='thi-ap27:25.4'><span class='pli'>dhanena ca suduggato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='thi-ap27:26.1'><span class='pli'>Tadāpi tassāhaṁ āsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='thi-ap27:26.2'><span class='pli'>brāhmaṇī samacetasā; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='thi-ap27:26.3'><span class='pli'>Kadāci so dijavaro, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='thi-ap27:26.4'><span class='pli'>saṅgamesi mahāmuniṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='thi-ap27:27.1'><span class='pli'>Nisinnaṁ janakāyamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='thi-ap27:27.2'><span class='pli'>desentaṁ amataṁ padaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='thi-ap27:27.3'><span class='pli'>Sutvā dhammaṁ pamudito, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='thi-ap27:27.4'><span class='pli'>adāsi ekasāṭakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='thi-ap27:28.1'><span class='pli'>Gharamekena vatthena, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='thi-ap27:28.2'><span class='pli'>Gantvānetaṁ sa mabravi; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='thi-ap27:28.3'><span class='pli'>‘Anumoda mahāpuññaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='thi-ap27:28.4'><span class='pli'>Dinnaṁ buddhassa sāṭakaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='thi-ap27:29.1'><span class='pli'>Tadāhaṁ añjaliṁ katvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='thi-ap27:29.2'><span class='pli'>anumodiṁ supīṇitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='thi-ap27:29.3'><span class='pli'>‘Sudinno sāṭako sāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='thi-ap27:29.4'><span class='pli'>buddhaseṭṭhassa tādino’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='thi-ap27:30.1'><span class='pli'>Sukhito sajjito hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='thi-ap27:30.2'><span class='pli'>saṁsaranto bhavābhave; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='thi-ap27:30.3'><span class='pli'>Bārāṇasipure ramme, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='thi-ap27:30.4'><span class='pli'>rājā āsi mahīpati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='thi-ap27:31.1'><span class='pli'>Tadā tassa mahesīhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='thi-ap27:31.2'><span class='pli'>itthigumbassa uttamā; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='thi-ap27:31.3'><span class='pli'>Tassāti dayitā āsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='thi-ap27:31.4'><span class='pli'>pubbasnehena bhattuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='thi-ap27:32.1'><span class='pli'>Piṇḍāya vicarante te, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='thi-ap27:32.2'><span class='pli'>aṭṭha paccekanāyake; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='thi-ap27:32.3'><span class='pli'>Disvā pamudito hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='thi-ap27:32.4'><span class='pli'>datvā piṇḍaṁ mahārahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='thi-ap27:33.1'><span class='pli'>Puno nimantayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='thi-ap27:33.2'><span class='pli'>katvā ratanamaṇḍapaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='thi-ap27:33.3'><span class='pli'>Kammārehi kataṁ pattaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='thi-ap27:33.4'><span class='pli'>sovaṇṇaṁ vata tattakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='34.1' data-uid='thi-ap27:34.1'><span class='pli'>Samānetvāna te sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='thi-ap27:34.2'><span class='pli'>tesaṁ dānamadāsi so; </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='thi-ap27:34.3'><span class='pli'>Soṇṇāsane paviṭṭhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.4' data-uid='thi-ap27:34.4'><span class='pli'>pasanno sehi pāṇibhi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='35.1' data-uid='thi-ap27:35.1'><span class='pli'>Tampi dānaṁ sahādāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.2' data-uid='thi-ap27:35.2'><span class='pli'>kāsirājenahaṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='35.3' data-uid='thi-ap27:35.3'><span class='pli'>Punāhaṁ bārāṇasiyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.4' data-uid='thi-ap27:35.4'><span class='pli'>jātā kāsikagāmake. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='36.1' data-uid='thi-ap27:36.1'><span class='pli'>Kuṭumbikakule phīte, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.2' data-uid='thi-ap27:36.2'><span class='pli'>sukhito so sabhātuko; </span></span></span>\n<span class='verse-line'><span class='segment' id='36.3' data-uid='thi-ap27:36.3'><span class='pli'>Jeṭṭhassa bhātuno jāyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.4' data-uid='thi-ap27:36.4'><span class='pli'>ahosiṁ supatibbatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='37.1' data-uid='thi-ap27:37.1'><span class='pli'>Paccekabuddhaṁ disvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.2' data-uid='thi-ap27:37.2'><span class='pli'>kaniyassa mama bhattuno; </span></span></span>\n<span class='verse-line'><span class='segment' id='37.3' data-uid='thi-ap27:37.3'><span class='pli'>Bhāgannaṁ tassa datvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.4' data-uid='thi-ap27:37.4'><span class='pli'>āgate tamhi pāvadiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='38.1' data-uid='thi-ap27:38.1'><span class='pli'>Nābhinandittha so dānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.2' data-uid='thi-ap27:38.2'><span class='pli'>tato tassa adāsahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='38.3' data-uid='thi-ap27:38.3'><span class='pli'>Ukhā āniya taṁ annaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.4' data-uid='thi-ap27:38.4'><span class='pli'>puno tasseva so adā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='39.1' data-uid='thi-ap27:39.1'><span class='pli'>Tadannaṁ chaḍḍayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.2' data-uid='thi-ap27:39.2'><span class='pli'>duṭṭhā buddhassahaṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='39.3' data-uid='thi-ap27:39.3'><span class='pli'>Pattaṁ kalalapuṇṇaṁ taṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.4' data-uid='thi-ap27:39.4'><span class='pli'>adāsiṁ tassa tādino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='40.1' data-uid='thi-ap27:40.1'><span class='pli'>Dāne ca gahaṇe ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.2' data-uid='thi-ap27:40.2'><span class='pli'>apace padusepi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='40.3' data-uid='thi-ap27:40.3'><span class='pli'>Samacittamukhaṁ disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.4' data-uid='thi-ap27:40.4'><span class='pli'>tadāhaṁ saṁvijiṁ bhusaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='41.1' data-uid='thi-ap27:41.1'><span class='pli'>Puno pattaṁ gahetvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.2' data-uid='thi-ap27:41.2'><span class='pli'>sodhayitvā sugandhinā; </span></span></span>\n<span class='verse-line'><span class='segment' id='41.3' data-uid='thi-ap27:41.3'><span class='pli'>Pasannacittā pūretvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.4' data-uid='thi-ap27:41.4'><span class='pli'>saghataṁ sakkaraṁ adaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='42.1' data-uid='thi-ap27:42.1'><span class='pli'>Yattha yatthūpapajjāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.2' data-uid='thi-ap27:42.2'><span class='pli'>surūpā homi dānato; </span></span></span>\n<span class='verse-line'><span class='segment' id='42.3' data-uid='thi-ap27:42.3'><span class='pli'>Buddhassa apakārena, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.4' data-uid='thi-ap27:42.4'><span class='pli'>duggandhā vadanena ca. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='43.1' data-uid='thi-ap27:43.1'><span class='pli'>Puna kassapavīrassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.2' data-uid='thi-ap27:43.2'><span class='pli'>nidhāyantamhi cetiye; </span></span></span>\n<span class='verse-line'><span class='segment' id='43.3' data-uid='thi-ap27:43.3'><span class='pli'>Sovaṇṇaṁ iṭṭhakaṁ varaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.4' data-uid='thi-ap27:43.4'><span class='pli'>adāsiṁ muditā ahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='44.1' data-uid='thi-ap27:44.1'><span class='pli'>Catujjātena gandhena, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.2' data-uid='thi-ap27:44.2'><span class='pli'>nicayitvā tamiṭṭhakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='44.3' data-uid='thi-ap27:44.3'><span class='pli'>Muttā duggandhadosamhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.4' data-uid='thi-ap27:44.4'><span class='pli'>sabbaṅgasusamāgatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='45.1' data-uid='thi-ap27:45.1'><span class='pli'>Sattapātisahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.2' data-uid='thi-ap27:45.2'><span class='pli'>rataneheva sattahi; </span></span></span>\n<span class='verse-line'><span class='segment' id='45.3' data-uid='thi-ap27:45.3'><span class='pli'>Kāretvā ghatapūrāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.4' data-uid='thi-ap27:45.4'><span class='pli'>vaṭṭīni ca sahassaso. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='46.1' data-uid='thi-ap27:46.1'><span class='pli'>Pakkhipitvā padīpetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.2' data-uid='thi-ap27:46.2'><span class='pli'>ṭhapayiṁ sattapantiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='46.3' data-uid='thi-ap27:46.3'><span class='pli'>Pūjanatthaṁ lokanāthassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.4' data-uid='thi-ap27:46.4'><span class='pli'>vippasannena cetasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='47.1' data-uid='thi-ap27:47.1'><span class='pli'>Tadāpi tamhi puññamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.2' data-uid='thi-ap27:47.2'><span class='pli'>bhāginīhaṁ visesato; </span></span></span>\n<span class='verse-line'><span class='segment' id='47.3' data-uid='thi-ap27:47.3'><span class='pli'>Puna kāsīsu sañjāto, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.4' data-uid='thi-ap27:47.4'><span class='pli'>sumitto iti vissuto. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='48.1' data-uid='thi-ap27:48.1'><span class='pli'>Tassāhaṁ bhariyā āsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.2' data-uid='thi-ap27:48.2'><span class='pli'>sukhitā sajjitā piyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='48.3' data-uid='thi-ap27:48.3'><span class='pli'>Tadā paccekamunino, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.4' data-uid='thi-ap27:48.4'><span class='pli'>adāsiṁ ghanaveṭhanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='49.1' data-uid='thi-ap27:49.1'><span class='pli'>Tassāpi bhāginī āsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.2' data-uid='thi-ap27:49.2'><span class='pli'>moditvā dānamuttamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='49.3' data-uid='thi-ap27:49.3'><span class='pli'>Punāpi kāsiraṭṭhamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.4' data-uid='thi-ap27:49.4'><span class='pli'>jāto koliyajātiyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='50.1' data-uid='thi-ap27:50.1'><span class='pli'>Tadā koliyaputtānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.2' data-uid='thi-ap27:50.2'><span class='pli'>satehi saha pañcahi; </span></span></span>\n<span class='verse-line'><span class='segment' id='50.3' data-uid='thi-ap27:50.3'><span class='pli'>Pañcapaccekabuddhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.4' data-uid='thi-ap27:50.4'><span class='pli'>satāni samupaṭṭhahi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='51.1' data-uid='thi-ap27:51.1'><span class='pli'>Temāsaṁ tappayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.2' data-uid='thi-ap27:51.2'><span class='pli'>adāsi ca ticīvaraṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='51.3' data-uid='thi-ap27:51.3'><span class='pli'>Jāyā tassa tadā āsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.4' data-uid='thi-ap27:51.4'><span class='pli'>puññakammapathānugā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='52.1' data-uid='thi-ap27:52.1'><span class='pli'>Tato cuto ahu rājā, </span></span></span>\n<span class='verse-line'><span class='segment' id='52.2' data-uid='thi-ap27:52.2'><span class='pli'>nando nāma mahāyaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='52.3' data-uid='thi-ap27:52.3'><span class='pli'>Tassāpi mahesī āsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='52.4' data-uid='thi-ap27:52.4'><span class='pli'>sabbakāmasamiddhinī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='53.1' data-uid='thi-ap27:53.1'><span class='pli'>Tadā rājā bhavitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='53.2' data-uid='thi-ap27:53.2'><span class='pli'>brahmadatto mahīpati; </span></span></span>\n<span class='verse-line'><span class='segment' id='53.3' data-uid='thi-ap27:53.3'><span class='pli'>Padumavatīputtānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='53.4' data-uid='thi-ap27:53.4'><span class='pli'>paccekamuninaṁ tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='54.1' data-uid='thi-ap27:54.1'><span class='pli'>Satāni pañcanūnāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='54.2' data-uid='thi-ap27:54.2'><span class='pli'>yāvajīvaṁ upaṭṭhahiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='54.3' data-uid='thi-ap27:54.3'><span class='pli'>Rājuyyāne nivāsetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='54.4' data-uid='thi-ap27:54.4'><span class='pli'>nibbutāni ca pūjayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='55.1' data-uid='thi-ap27:55.1'><span class='pli'>Cetiyāni ca kāretvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='55.2' data-uid='thi-ap27:55.2'><span class='pli'>pabbajitvā ubho mayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='55.3' data-uid='thi-ap27:55.3'><span class='pli'>Bhāvetvā appamaññāyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='55.4' data-uid='thi-ap27:55.4'><span class='pli'>brahmalokaṁ agamhase. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='56.1' data-uid='thi-ap27:56.1'><span class='pli'>Tato cuto mahātitthe, </span></span></span>\n<span class='verse-line'><span class='segment' id='56.2' data-uid='thi-ap27:56.2'><span class='pli'>sujāto pipphalāyano; </span></span></span>\n<span class='verse-line'><span class='segment' id='56.3' data-uid='thi-ap27:56.3'><span class='pli'>Mātā sumanadevīti, </span></span></span>\n<span class='verse-line'><span class='segment' id='56.4' data-uid='thi-ap27:56.4'><span class='pli'>kosigotto dijo pitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='57.1' data-uid='thi-ap27:57.1'><span class='pli'>Ahaṁ madde janapade, </span></span></span>\n<span class='verse-line'><span class='segment' id='57.2' data-uid='thi-ap27:57.2'><span class='pli'>sākalāya puruttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='57.3' data-uid='thi-ap27:57.3'><span class='pli'>Kappilassa dijassāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='57.4' data-uid='thi-ap27:57.4'><span class='pli'>dhītā mātā sucīmati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='58.1' data-uid='thi-ap27:58.1'><span class='pli'>Ghanakañcanabimbena, </span></span></span>\n<span class='verse-line'><span class='segment' id='58.2' data-uid='thi-ap27:58.2'><span class='pli'>nimminitvāna maṁ pitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='58.3' data-uid='thi-ap27:58.3'><span class='pli'>Adā kassapadhīrassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='58.4' data-uid='thi-ap27:58.4'><span class='pli'>kāmehi vajjitassamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='59.1' data-uid='thi-ap27:59.1'><span class='pli'>Kadāci so kāruṇiko, </span></span></span>\n<span class='verse-line'><span class='segment' id='59.2' data-uid='thi-ap27:59.2'><span class='pli'>gantvā kammantapekkhako; </span></span></span>\n<span class='verse-line'><span class='segment' id='59.3' data-uid='thi-ap27:59.3'><span class='pli'>Kākādikehi khajjante, </span></span></span>\n<span class='verse-line'><span class='segment' id='59.4' data-uid='thi-ap27:59.4'><span class='pli'>pāṇe disvāna saṁviji. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='60.1' data-uid='thi-ap27:60.1'><span class='pli'>Gharevāhaṁ tile jāte, </span></span></span>\n<span class='verse-line'><span class='segment' id='60.2' data-uid='thi-ap27:60.2'><span class='pli'>disvānātapatāpane; </span></span></span>\n<span class='verse-line'><span class='segment' id='60.3' data-uid='thi-ap27:60.3'><span class='pli'>Kimī kākehi khajjante, </span></span></span>\n<span class='verse-line'><span class='segment' id='60.4' data-uid='thi-ap27:60.4'><span class='pli'>saṁvegamalabhiṁ tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='61.1' data-uid='thi-ap27:61.1'><span class='pli'>Tadā so pabbajī dhīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='61.2' data-uid='thi-ap27:61.2'><span class='pli'>ahaṁ tamanupabbajiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='61.3' data-uid='thi-ap27:61.3'><span class='pli'>Pañcavassāni nivasiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='61.4' data-uid='thi-ap27:61.4'><span class='pli'>paribbājavate ahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='62.1' data-uid='thi-ap27:62.1'><span class='pli'>Yadā pabbajitā āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='62.2' data-uid='thi-ap27:62.2'><span class='pli'>gotamī jinaposikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='62.3' data-uid='thi-ap27:62.3'><span class='pli'>Tadāhaṁ tamupagantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='62.4' data-uid='thi-ap27:62.4'><span class='pli'>buddhena anusāsitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='63.1' data-uid='thi-ap27:63.1'><span class='pli'>Na cireneva kālena, </span></span></span>\n<span class='verse-line'><span class='segment' id='63.2' data-uid='thi-ap27:63.2'><span class='pli'>arahattamapāpuṇiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='63.3' data-uid='thi-ap27:63.3'><span class='pli'>Aho kalyāṇamittattaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='63.4' data-uid='thi-ap27:63.4'><span class='pli'>kassapassa sirīmato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='64.1' data-uid='thi-ap27:64.1'><span class='pli'>Suto buddhassa dāyādo, </span></span></span>\n<span class='verse-line'><span class='segment' id='64.2' data-uid='thi-ap27:64.2'><span class='pli'>kassapo susamāhito; </span></span></span>\n<span class='verse-line'><span class='segment' id='64.3' data-uid='thi-ap27:64.3'><span class='pli'>Pubbenivāsaṁ yo vedi, </span></span></span>\n<span class='verse-line'><span class='segment' id='64.4' data-uid='thi-ap27:64.4'><span class='pli'>saggāpāyañca passati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='65.1' data-uid='thi-ap27:65.1'><span class='pli'>Atho jātikkhayaṁ patto, </span></span></span>\n<span class='verse-line'><span class='segment' id='65.2' data-uid='thi-ap27:65.2'><span class='pli'>abhiññāvosito muni; </span></span></span>\n<span class='verse-line'><span class='segment' id='65.3' data-uid='thi-ap27:65.3'><span class='pli'>Etāhi tīhi vijjāhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='65.4' data-uid='thi-ap27:65.4'><span class='pli'>tevijjo hoti brāhmaṇo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='66.1' data-uid='thi-ap27:66.1'><span class='pli'>Tatheva bhaddākāpilānī, </span></span></span>\n<span class='verse-line'><span class='segment' id='66.2' data-uid='thi-ap27:66.2'><span class='pli'>tevijjā maccuhāyinī; </span></span></span>\n<span class='verse-line'><span class='segment' id='66.3' data-uid='thi-ap27:66.3'><span class='pli'>Dhāreti antimaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='66.4' data-uid='thi-ap27:66.4'><span class='pli'>jitvā māraṁ savāhanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='67.1' data-uid='thi-ap27:67.1'><span class='pli'>Disvā ādīnavaṁ loke, </span></span></span>\n<span class='verse-line'><span class='segment' id='67.2' data-uid='thi-ap27:67.2'><span class='pli'>ubho pabbajitā mayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='67.3' data-uid='thi-ap27:67.3'><span class='pli'>Tyamha khīṇāsavā dantā, </span></span></span>\n<span class='verse-line'><span class='segment' id='67.4' data-uid='thi-ap27:67.4'><span class='pli'>sītibhūtāmha nibbutā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='68.1' data-uid='thi-ap27:68.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='68.2' data-uid='thi-ap27:68.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='68.3' data-uid='thi-ap27:68.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='69.1' data-uid='thi-ap27:69.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='69.2' data-uid='thi-ap27:69.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='69.3' data-uid='thi-ap27:69.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='70.1' data-uid='thi-ap27:70.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='70.2' data-uid='thi-ap27:70.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='70.3' data-uid='thi-ap27:70.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='71.1' data-uid='thi-ap27:71.1'><span class='pli'>Itthaṁ sudaṁ bhaddākāpilānī bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='72.1' data-uid='thi-ap27:72.1'><span class='pli'>Bhaddākāpilānītheriyāpadānaṁ sattamaṁ. </span></span></p></article>\n"
  },
  "thi-ap28": {
    "previous": "thi-ap27",
    "next": "thi-ap29",
    "content": "<article id='thi-ap28'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap28:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap28:0.2'><span class='pli'>Kuṇḍalakesīvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap28:0.3'><span class='pli'>Yasodharātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap28:1.1'><span class='pli'>Ekasmiṁ samaye ramme, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap28:1.2'><span class='pli'>iddhe rājagahe pure; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap28:1.3'><span class='pli'>Pabbhāramhi varekamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap28:1.4'><span class='pli'>vasante naranāyake. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap28:2.1'><span class='pli'>Vasantiyā tamhi nagare, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap28:2.2'><span class='pli'>ramme bhikkhunupassaye; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap28:2.3'><span class='pli'>Yasodharābhikkhuniyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap28:2.4'><span class='pli'>evaṁ āsi vitakkitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap28:3.1'><span class='pli'>“Suddhodano mahārājā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap28:3.2'><span class='pli'>gotamī ca pajāpatī; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap28:3.3'><span class='pli'>Abhiññātā mahātherā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap28:3.4'><span class='pli'>theriyo ca mahiddhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap28:4.1'><span class='pli'>Santiṁ gatāva āsuṁ te, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap28:4.2'><span class='pli'>dīpaccīva nirāsavā; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap28:4.3'><span class='pli'>Lokanāthe dharanteva, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap28:4.4'><span class='pli'>ahampi ca sivaṁ padaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap28:5.1'><span class='pli'>Gamissāmī”ti cintetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap28:5.2'><span class='pli'>passantī āyumattano; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap28:5.3'><span class='pli'>Passitvā āyusaṅkhāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap28:5.4'><span class='pli'>tadaheva khayaṁ gataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap28:6.1'><span class='pli'>Pattacīvaramādāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap28:6.2'><span class='pli'>nikkhamitvā sakassamā; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap28:6.3'><span class='pli'>Purakkhatā bhikkhunībhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap28:6.4'><span class='pli'>satehi sahassehi sā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap28:7.1'><span class='pli'>Mahiddhikā mahāpaññā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap28:7.2'><span class='pli'>sambuddhaṁ upasaṅkami; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap28:7.3'><span class='pli'>Sambuddhaṁ abhivādetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap28:7.4'><span class='pli'>satthuno cakkalakkhaṇe; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.5' data-uid='thi-ap28:7.5'><span class='pli'>Nisinnā ekamantamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.6' data-uid='thi-ap28:7.6'><span class='pli'>idaṁ vacanamabravi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap28:8.1'><span class='pli'>“Aṭṭhasattativassāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap28:8.2'><span class='pli'>pacchimo vattate vayo; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap28:8.3'><span class='pli'>Pabbhāramhi anuppattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap28:8.4'><span class='pli'>ārocemi mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap28:9.1'><span class='pli'>Paripakko vayo mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap28:9.2'><span class='pli'>parittaṁ mama jīvitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap28:9.3'><span class='pli'>Pahāya vo gamissāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap28:9.4'><span class='pli'>kataṁ me saraṇamattano. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap28:10.1'><span class='pli'>Vayamhi pacchime kāle, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap28:10.2'><span class='pli'>maraṇaṁ uparuddhati; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap28:10.3'><span class='pli'>Ajjarattiṁ mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap28:10.4'><span class='pli'>pāpuṇissāmi nibbutiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap28:11.1'><span class='pli'>Natthi jāti jarā byādhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap28:11.2'><span class='pli'>maraṇañca mahāmune; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap28:11.3'><span class='pli'>Ajarāmaraṇaṁ puraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap28:11.4'><span class='pli'>gamissāmi asaṅkhataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap28:12.1'><span class='pli'>Yāvatā parisā nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap28:12.2'><span class='pli'>samupāsanti satthuno; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap28:12.3'><span class='pli'>Aparādhamajānantī, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap28:12.4'><span class='pli'>khamantaṁ sammukhā mune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap28:13.1'><span class='pli'>Saṁsaritvā ca saṁsāre, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap28:13.2'><span class='pli'>khalitañce mamaṁ tayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap28:13.3'><span class='pli'>Ārocemi mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap28:13.4'><span class='pli'>aparādhaṁ khamassu me”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap28:14.1'><span class='pli'>Sutvāna vacanaṁ tassā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap28:14.2'><span class='pli'>munindo idamabravi; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap28:14.3'><span class='pli'>“Kimuttaraṁ te vakkhāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap28:14.4'><span class='pli'>nibbānāya vajantiyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap28:15.1'><span class='pli'>Iddhiñcāpi nidassehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap28:15.2'><span class='pli'>mama sāsanakārike; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap28:15.3'><span class='pli'>Parisānañca sabbāsaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap28:15.4'><span class='pli'>kaṅkhaṁ chindassu yāvatā”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap28:16.1'><span class='pli'>Sutvā taṁ munino vācaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap28:16.2'><span class='pli'>bhikkhunī sā yasodharā; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap28:16.3'><span class='pli'>Vanditvā munirājaṁ taṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap28:16.4'><span class='pli'>idaṁ vacanamabravi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap28:17.1'><span class='pli'>“Yasodharā ahaṁ vīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap28:17.2'><span class='pli'>agāre te pajāpati; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap28:17.3'><span class='pli'>Sākiyamhi kule jātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap28:17.4'><span class='pli'>itthiaṅge patiṭṭhitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap28:18.1'><span class='pli'>Thīnaṁ satasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap28:18.2'><span class='pli'>navutīnaṁ chaduttari; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap28:18.3'><span class='pli'>Agāre te ahaṁ vīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap28:18.4'><span class='pli'>pāmokkhā sabbā issarā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap28:19.1'><span class='pli'>Rūpācāraguṇūpetā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap28:19.2'><span class='pli'>yobbanaṭṭhā piyaṁvadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap28:19.3'><span class='pli'>Sabbā maṁ apacāyanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap28:19.4'><span class='pli'>devatā viya mānusā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap28:20.1'><span class='pli'>Kaññāsatasahassapamukhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap28:20.2'><span class='pli'>Sakyaputtanivesane; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap28:20.3'><span class='pli'>Samānasukhadukkhatā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap28:20.4'><span class='pli'>Devatā viya nandane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap28:21.1'><span class='pli'>Kāmadhātumatikkamma, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap28:21.2'><span class='pli'>saṇṭhitā rūpadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap28:21.3'><span class='pli'>Rūpena sadisā natthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap28:21.4'><span class='pli'>ṭhapetvā lokanāyakaṁ”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap28:22.1'><span class='pli'>Sambuddhaṁ abhivādetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap28:22.2'><span class='pli'>iddhiṁ dassesi satthuno; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap28:22.3'><span class='pli'>Nekā nānāvidhākārā, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap28:22.4'><span class='pli'>mahāiddhīpi dassayī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap28:23.1'><span class='pli'>Cakkavāḷasamaṁ kāyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap28:23.2'><span class='pli'>sīsaṁ uttarato kuru; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap28:23.3'><span class='pli'>Ubho pakkhā duve dīpā, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='thi-ap28:23.4'><span class='pli'>jambudīpaṁ sarīrato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap28:24.1'><span class='pli'>Dakkhiṇañca saraṁ piñchaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap28:24.2'><span class='pli'>nānāsākhā tu pattakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap28:24.3'><span class='pli'>Candañca sūriyañcakkhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='thi-ap28:24.4'><span class='pli'>merupabbatato sikhaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='thi-ap28:25.1'><span class='pli'>Cakkavāḷagiriṁ tuṇḍaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='thi-ap28:25.2'><span class='pli'>jamburukkhaṁ samūlakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='thi-ap28:25.3'><span class='pli'>Bījamānā upāgantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='thi-ap28:25.4'><span class='pli'>vandantī lokanāyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='thi-ap28:26.1'><span class='pli'>Hatthivaṇṇaṁ tathevassaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='thi-ap28:26.2'><span class='pli'>pabbataṁ jaladhiṁ tathā; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='thi-ap28:26.3'><span class='pli'>Candimaṁ sūriyaṁ meruṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='thi-ap28:26.4'><span class='pli'>sakkavaṇṇañca dassayi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='thi-ap28:27.1'><span class='pli'>“Yasodharā ahaṁ vīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='thi-ap28:27.2'><span class='pli'>pāde vandāmi cakkhuma”; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='thi-ap28:27.3'><span class='pli'>Sahassalokadhātūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='thi-ap28:27.4'><span class='pli'>phullapadmena chādayi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='thi-ap28:28.1'><span class='pli'>Brahmavaṇṇañca māpetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='thi-ap28:28.2'><span class='pli'>dhammaṁ desesi suññataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='thi-ap28:28.3'><span class='pli'>“Yasodharā ahaṁ vīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='thi-ap28:28.4'><span class='pli'>pāde vandāmi cakkhuma. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='thi-ap28:29.1'><span class='pli'>Iddhīsu ca vasī homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='thi-ap28:29.2'><span class='pli'>dibbāya sotadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='thi-ap28:29.3'><span class='pli'>Cetopariyañāṇassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='thi-ap28:29.4'><span class='pli'>vasī homi mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='thi-ap28:30.1'><span class='pli'>Pubbenivāsaṁ jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='thi-ap28:30.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='thi-ap28:30.3'><span class='pli'>Sabbāsavā parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='thi-ap28:30.4'><span class='pli'>natthi dāni punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='thi-ap28:31.1'><span class='pli'>Atthadhammaniruttīsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='thi-ap28:31.2'><span class='pli'>paṭibhāne tatheva ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='thi-ap28:31.3'><span class='pli'>Ñāṇaṁ mayhaṁ mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='thi-ap28:31.4'><span class='pli'>uppannaṁ tava santike. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='thi-ap28:32.1'><span class='pli'>Pubbānaṁ lokanāthānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='thi-ap28:32.2'><span class='pli'>saṅgamaṁ te nidassitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='thi-ap28:32.3'><span class='pli'>Adhikāraṁ bahuṁ mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='thi-ap28:32.4'><span class='pli'>tuyhatthāya mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='thi-ap28:33.1'><span class='pli'>Yaṁ mayhaṁ pūritaṁ kammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='thi-ap28:33.2'><span class='pli'>Kusalaṁ sarase mune; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='thi-ap28:33.3'><span class='pli'>Tuyhatthāya mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='thi-ap28:33.4'><span class='pli'>Puññaṁ upacitaṁ mayā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='34.1' data-uid='thi-ap28:34.1'><span class='pli'>Abhabbaṭṭhāne vajjetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='thi-ap28:34.2'><span class='pli'>vārayitvā anācaraṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='thi-ap28:34.3'><span class='pli'>Tuyhatthāya mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.4' data-uid='thi-ap28:34.4'><span class='pli'>sañcattaṁ jīvitaṁ mayā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='35.1' data-uid='thi-ap28:35.1'><span class='pli'>Nekakoṭisahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.2' data-uid='thi-ap28:35.2'><span class='pli'>bhariyatthāyadāsi maṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='35.3' data-uid='thi-ap28:35.3'><span class='pli'>Na tattha vimanā homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.4' data-uid='thi-ap28:35.4'><span class='pli'>tuyhatthāya mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='36.1' data-uid='thi-ap28:36.1'><span class='pli'>Nekakoṭisahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.2' data-uid='thi-ap28:36.2'><span class='pli'>upakārāyadāsi maṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='36.3' data-uid='thi-ap28:36.3'><span class='pli'>Na tattha vimanā homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.4' data-uid='thi-ap28:36.4'><span class='pli'>tuyhatthāya mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='37.1' data-uid='thi-ap28:37.1'><span class='pli'>Nekakoṭisahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.2' data-uid='thi-ap28:37.2'><span class='pli'>bhojanatthāyadāsi maṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='37.3' data-uid='thi-ap28:37.3'><span class='pli'>Na tattha vimanā homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.4' data-uid='thi-ap28:37.4'><span class='pli'>tuyhatthāya mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='38.1' data-uid='thi-ap28:38.1'><span class='pli'>Nekakoṭisahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.2' data-uid='thi-ap28:38.2'><span class='pli'>jīvitāni pariccajiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='38.3' data-uid='thi-ap28:38.3'><span class='pli'>Bhayamokkhaṁ karissanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.4' data-uid='thi-ap28:38.4'><span class='pli'>dadāmi mama jīvitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='39.1' data-uid='thi-ap28:39.1'><span class='pli'>Aṅgagate alaṅkāre, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.2' data-uid='thi-ap28:39.2'><span class='pli'>vatthe nānāvidhe bahū; </span></span></span>\n<span class='verse-line'><span class='segment' id='39.3' data-uid='thi-ap28:39.3'><span class='pli'>Itthimaṇḍe na gūhāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.4' data-uid='thi-ap28:39.4'><span class='pli'>tuyhatthāya mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='40.1' data-uid='thi-ap28:40.1'><span class='pli'>Dhanadhaññapariccāgaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.2' data-uid='thi-ap28:40.2'><span class='pli'>gāmāni nigamāni ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='40.3' data-uid='thi-ap28:40.3'><span class='pli'>Khettaṁ puttā ca dhītā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.4' data-uid='thi-ap28:40.4'><span class='pli'>pariccattā mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='41.1' data-uid='thi-ap28:41.1'><span class='pli'>Hatthī assā gavā cāpi, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.2' data-uid='thi-ap28:41.2'><span class='pli'>dāsiyo paricārikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='41.3' data-uid='thi-ap28:41.3'><span class='pli'>Tuyhatthāya mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.4' data-uid='thi-ap28:41.4'><span class='pli'>pariccattā asaṅkhiyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='42.1' data-uid='thi-ap28:42.1'><span class='pli'>Yaṁ mayhaṁ paṭimantesi, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.2' data-uid='thi-ap28:42.2'><span class='pli'>dānaṁ dassāmi yācake; </span></span></span>\n<span class='verse-line'><span class='segment' id='42.3' data-uid='thi-ap28:42.3'><span class='pli'>Vimanaṁ me na passāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.4' data-uid='thi-ap28:42.4'><span class='pli'>dadato dānamuttamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='43.1' data-uid='thi-ap28:43.1'><span class='pli'>Nānāvidhaṁ bahuṁ dukkhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.2' data-uid='thi-ap28:43.2'><span class='pli'>saṁsāre ca bahubbidhe; </span></span></span>\n<span class='verse-line'><span class='segment' id='43.3' data-uid='thi-ap28:43.3'><span class='pli'>Tuyhatthāya mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.4' data-uid='thi-ap28:43.4'><span class='pli'>anubhuttaṁ asaṅkhiyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='44.1' data-uid='thi-ap28:44.1'><span class='pli'>Sukhappattānumodāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.2' data-uid='thi-ap28:44.2'><span class='pli'>na ca dukkhesu dummanā; </span></span></span>\n<span class='verse-line'><span class='segment' id='44.3' data-uid='thi-ap28:44.3'><span class='pli'>Sabbattha tulitā homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.4' data-uid='thi-ap28:44.4'><span class='pli'>tuyhatthāya mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='45.1' data-uid='thi-ap28:45.1'><span class='pli'>Anumaggena sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.2' data-uid='thi-ap28:45.2'><span class='pli'>yaṁ dhammaṁ abhinīhari; </span></span></span>\n<span class='verse-line'><span class='segment' id='45.3' data-uid='thi-ap28:45.3'><span class='pli'>Anubhotvā sukhaṁ dukkhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.4' data-uid='thi-ap28:45.4'><span class='pli'>patto bodhiṁ mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='46.1' data-uid='thi-ap28:46.1'><span class='pli'>Brahmadevañca sambuddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.2' data-uid='thi-ap28:46.2'><span class='pli'>gotamaṁ lokanāyakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='46.3' data-uid='thi-ap28:46.3'><span class='pli'>Aññesaṁ lokanāthānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.4' data-uid='thi-ap28:46.4'><span class='pli'>saṅgamaṁ te bahuṁ mayā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='47.1' data-uid='thi-ap28:47.1'><span class='pli'>Adhikāraṁ bahuṁ mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.2' data-uid='thi-ap28:47.2'><span class='pli'>tuyhatthāya mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='47.3' data-uid='thi-ap28:47.3'><span class='pli'>Gavesato buddhadhamme, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.4' data-uid='thi-ap28:47.4'><span class='pli'>ahaṁ te paricārikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='48.1' data-uid='thi-ap28:48.1'><span class='pli'>Kappe ca satasahasse, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.2' data-uid='thi-ap28:48.2'><span class='pli'>caturo ca asaṅkhiye; </span></span></span>\n<span class='verse-line'><span class='segment' id='48.3' data-uid='thi-ap28:48.3'><span class='pli'>Dīpaṅkaro mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.4' data-uid='thi-ap28:48.4'><span class='pli'>uppajji lokanāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='49.1' data-uid='thi-ap28:49.1'><span class='pli'>Paccantadesavisaye, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.2' data-uid='thi-ap28:49.2'><span class='pli'>nimantetvā tathāgataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='49.3' data-uid='thi-ap28:49.3'><span class='pli'>Tassa āgamanaṁ maggaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.4' data-uid='thi-ap28:49.4'><span class='pli'>sodhenti tuṭṭhamānasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='50.1' data-uid='thi-ap28:50.1'><span class='pli'>Tena kālena so āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.2' data-uid='thi-ap28:50.2'><span class='pli'>sumedho nāma brāhmaṇo; </span></span></span>\n<span class='verse-line'><span class='segment' id='50.3' data-uid='thi-ap28:50.3'><span class='pli'>Maggañca paṭiyādesi, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.4' data-uid='thi-ap28:50.4'><span class='pli'>āyato sabbadassino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='51.1' data-uid='thi-ap28:51.1'><span class='pli'>Tena kālenahaṁ āsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.2' data-uid='thi-ap28:51.2'><span class='pli'>kaññā brāhmaṇasambhavā; </span></span></span>\n<span class='verse-line'><span class='segment' id='51.3' data-uid='thi-ap28:51.3'><span class='pli'>Sumittā nāma nāmena, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.4' data-uid='thi-ap28:51.4'><span class='pli'>upagacchiṁ samāgamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='52.1' data-uid='thi-ap28:52.1'><span class='pli'>Aṭṭha uppalahatthāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='52.2' data-uid='thi-ap28:52.2'><span class='pli'>pūjanatthāya satthuno; </span></span></span>\n<span class='verse-line'><span class='segment' id='52.3' data-uid='thi-ap28:52.3'><span class='pli'>Ādāya janasammajjhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='52.4' data-uid='thi-ap28:52.4'><span class='pli'>addasaṁ isimuggataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='53.1' data-uid='thi-ap28:53.1'><span class='pli'>Cirānugataṁ dayitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='53.2' data-uid='thi-ap28:53.2'><span class='pli'>atikkantaṁ manoharaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='53.3' data-uid='thi-ap28:53.3'><span class='pli'>Disvā tadā amaññissaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='53.4' data-uid='thi-ap28:53.4'><span class='pli'>saphalaṁ jīvitaṁ mama. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='54.1' data-uid='thi-ap28:54.1'><span class='pli'>Parakkamaṁ taṁ saphalaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='54.2' data-uid='thi-ap28:54.2'><span class='pli'>addasaṁ isino tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='54.3' data-uid='thi-ap28:54.3'><span class='pli'>Pubbakammena sambuddhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='54.4' data-uid='thi-ap28:54.4'><span class='pli'>cittañcāpi pasīdi me. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='55.1' data-uid='thi-ap28:55.1'><span class='pli'>Bhiyyo cittaṁ pasādesiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='55.2' data-uid='thi-ap28:55.2'><span class='pli'>ise uggatamānase; </span></span></span>\n<span class='verse-line'><span class='segment' id='55.3' data-uid='thi-ap28:55.3'><span class='pli'>Deyyaṁ aññaṁ na passāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='55.4' data-uid='thi-ap28:55.4'><span class='pli'>demi pupphāni te isi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='56.1' data-uid='thi-ap28:56.1'><span class='pli'>‘Pañca hatthā tava hontu, </span></span></span>\n<span class='verse-line'><span class='segment' id='56.2' data-uid='thi-ap28:56.2'><span class='pli'>tayo hontu mamaṁ ise; </span></span></span>\n<span class='verse-line'><span class='segment' id='56.3' data-uid='thi-ap28:56.3'><span class='pli'>Tena saddhiṁ samā hontu, </span></span></span>\n<span class='verse-line'><span class='segment' id='56.4' data-uid='thi-ap28:56.4'><span class='pli'>bodhatthāya tavaṁ ise’. </span></span></span></p></blockquote>\n<p class='endsutta'><span class='segment' id='57.1' data-uid='thi-ap28:57.1'><span class='pli'>Catutthaṁ bhāṇavāraṁ. </span></span></p>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='58.1' data-uid='thi-ap28:58.1'><span class='pli'>Isi gahetvā pupphāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='58.2' data-uid='thi-ap28:58.2'><span class='pli'>āgacchantaṁ mahāyasaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='58.3' data-uid='thi-ap28:58.3'><span class='pli'>Pūjesi janasammajjhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='58.4' data-uid='thi-ap28:58.4'><span class='pli'>bodhatthāya mahāisi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='59.1' data-uid='thi-ap28:59.1'><span class='pli'>Passitvā janasammajjhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='59.2' data-uid='thi-ap28:59.2'><span class='pli'>dīpaṅkaro mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='59.3' data-uid='thi-ap28:59.3'><span class='pli'>Viyākāsi mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='59.4' data-uid='thi-ap28:59.4'><span class='pli'>isi muggatamānasaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='60.1' data-uid='thi-ap28:60.1'><span class='pli'>Aparimeyye ito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='60.2' data-uid='thi-ap28:60.2'><span class='pli'>dīpaṅkaro mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='60.3' data-uid='thi-ap28:60.3'><span class='pli'>Mama kammaṁ viyākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='60.4' data-uid='thi-ap28:60.4'><span class='pli'>ujubhāvaṁ mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='61.1' data-uid='thi-ap28:61.1'><span class='pli'>‘Samacittā samakammā, </span></span></span>\n<span class='verse-line'><span class='segment' id='61.2' data-uid='thi-ap28:61.2'><span class='pli'>samakārī bhavissati; </span></span></span>\n<span class='verse-line'><span class='segment' id='61.3' data-uid='thi-ap28:61.3'><span class='pli'>Piyā hessati kammena, </span></span></span>\n<span class='verse-line'><span class='segment' id='61.4' data-uid='thi-ap28:61.4'><span class='pli'>tuyhatthāya mahāisi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='62.1' data-uid='thi-ap28:62.1'><span class='pli'>Sudassanā supiyā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='62.2' data-uid='thi-ap28:62.2'><span class='pli'>manāpā piyavādinī; </span></span></span>\n<span class='verse-line'><span class='segment' id='62.3' data-uid='thi-ap28:62.3'><span class='pli'>Tassa dhammesu dāyādā, </span></span></span>\n<span class='verse-line'><span class='segment' id='62.4' data-uid='thi-ap28:62.4'><span class='pli'>viharissati iddhikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='63.1' data-uid='thi-ap28:63.1'><span class='pli'>Yathāpi bhaṇḍasāmuggaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='63.2' data-uid='thi-ap28:63.2'><span class='pli'>anurakkhati sāmino; </span></span></span>\n<span class='verse-line'><span class='segment' id='63.3' data-uid='thi-ap28:63.3'><span class='pli'>Evaṁ kusaladhammānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='63.4' data-uid='thi-ap28:63.4'><span class='pli'>anurakkhissate ayaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='64.1' data-uid='thi-ap28:64.1'><span class='pli'>Tassa te anukampantī, </span></span></span>\n<span class='verse-line'><span class='segment' id='64.2' data-uid='thi-ap28:64.2'><span class='pli'>pūrayissati pāramī; </span></span></span>\n<span class='verse-line'><span class='segment' id='64.3' data-uid='thi-ap28:64.3'><span class='pli'>Sīhova pañjaraṁ bhetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='64.4' data-uid='thi-ap28:64.4'><span class='pli'>pāpuṇissati bodhiyaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='65.1' data-uid='thi-ap28:65.1'><span class='pli'>Aparimeyye ito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='65.2' data-uid='thi-ap28:65.2'><span class='pli'>yaṁ maṁ buddho viyākarī; </span></span></span>\n<span class='verse-line'><span class='segment' id='65.3' data-uid='thi-ap28:65.3'><span class='pli'>Taṁ vācaṁ anumodentī, </span></span></span>\n<span class='verse-line'><span class='segment' id='65.4' data-uid='thi-ap28:65.4'><span class='pli'>evaṅkārī bhaviṁ ahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='66.1' data-uid='thi-ap28:66.1'><span class='pli'>Tassa kammassa sukatassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='66.2' data-uid='thi-ap28:66.2'><span class='pli'>tattha cittaṁ pasādayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='66.3' data-uid='thi-ap28:66.3'><span class='pli'>Devamanussakaṁ yoniṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='66.4' data-uid='thi-ap28:66.4'><span class='pli'>anubhotvā asaṅkhiyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='67.1' data-uid='thi-ap28:67.1'><span class='pli'>Sukhadukkhenubhotvāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='67.2' data-uid='thi-ap28:67.2'><span class='pli'>devesu mānusesu ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='67.3' data-uid='thi-ap28:67.3'><span class='pli'>Pacchime bhave sampatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='67.4' data-uid='thi-ap28:67.4'><span class='pli'>ajāyiṁ sākiye kule. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='68.1' data-uid='thi-ap28:68.1'><span class='pli'>Rūpavatī bhogavatī, </span></span></span>\n<span class='verse-line'><span class='segment' id='68.2' data-uid='thi-ap28:68.2'><span class='pli'>yasasīlavatī tato; </span></span></span>\n<span class='verse-line'><span class='segment' id='68.3' data-uid='thi-ap28:68.3'><span class='pli'>Sabbaṅgasampadā homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='68.4' data-uid='thi-ap28:68.4'><span class='pli'>kulesu abhisakkatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='69.1' data-uid='thi-ap28:69.1'><span class='pli'>Lābhaṁ silokaṁ sakkāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='69.2' data-uid='thi-ap28:69.2'><span class='pli'>lokadhammasamāgamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='69.3' data-uid='thi-ap28:69.3'><span class='pli'>Cittañca dukkhitaṁ natthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='69.4' data-uid='thi-ap28:69.4'><span class='pli'>vasāmi akutobhayā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='70.1' data-uid='thi-ap28:70.1'><span class='pli'>Vuttañhetaṁ bhagavatā, </span></span></span>\n<span class='verse-line'><span class='segment' id='70.2' data-uid='thi-ap28:70.2'><span class='pli'>‘rañño antepure tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='70.3' data-uid='thi-ap28:70.3'><span class='pli'>Khattiyānaṁ pure vīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='70.4' data-uid='thi-ap28:70.4'><span class='pli'>upakārañca niddisi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='71.1' data-uid='thi-ap28:71.1'><span class='pli'>Upakārā ca yā nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='71.2' data-uid='thi-ap28:71.2'><span class='pli'>yā ca nārī sukhe dukhe; </span></span></span>\n<span class='verse-line'><span class='segment' id='71.3' data-uid='thi-ap28:71.3'><span class='pli'>Atthakkhāyī ca yā nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='71.4' data-uid='thi-ap28:71.4'><span class='pli'>yā ca nārīnukampikā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='72.1' data-uid='thi-ap28:72.1'><span class='pli'>Pañcakoṭisatā buddhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='72.2' data-uid='thi-ap28:72.2'><span class='pli'>navakoṭisatāni ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='72.3' data-uid='thi-ap28:72.3'><span class='pli'>Etesaṁ devadevānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='72.4' data-uid='thi-ap28:72.4'><span class='pli'>mahādānaṁ pavattayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='73.1' data-uid='thi-ap28:73.1'><span class='pli'>Adhikāraṁ mahā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='73.2' data-uid='thi-ap28:73.2'><span class='pli'>dhammarāja suṇohi me; </span></span></span>\n<span class='verse-line'><span class='segment' id='73.3' data-uid='thi-ap28:73.3'><span class='pli'>Ekādasakoṭisatā, </span></span></span>\n<span class='verse-line'><span class='segment' id='73.4' data-uid='thi-ap28:73.4'><span class='pli'>buddhā dvādasa koṭiyo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='74.1' data-uid='thi-ap28:74.1'><span class='pli'>Etesaṁ devadevānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='74.2' data-uid='thi-ap28:74.2'><span class='pli'>mahādānaṁ pavattayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='74.3' data-uid='thi-ap28:74.3'><span class='pli'>Adhikāraṁ mahā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='74.4' data-uid='thi-ap28:74.4'><span class='pli'>dhammarāja suṇohi me. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='75.1' data-uid='thi-ap28:75.1'><span class='pli'>Vīsakoṭisatā buddhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='75.2' data-uid='thi-ap28:75.2'><span class='pli'>tiṁsakoṭisatāni ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='75.3' data-uid='thi-ap28:75.3'><span class='pli'>Etesaṁ devadevānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='75.4' data-uid='thi-ap28:75.4'><span class='pli'>mahādānaṁ pavattayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='76.1' data-uid='thi-ap28:76.1'><span class='pli'>Adhikāraṁ mahā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='76.2' data-uid='thi-ap28:76.2'><span class='pli'>dhammarāja suṇohi me; </span></span></span>\n<span class='verse-line'><span class='segment' id='76.3' data-uid='thi-ap28:76.3'><span class='pli'>Cattālīsakoṭisatā, </span></span></span>\n<span class='verse-line'><span class='segment' id='76.4' data-uid='thi-ap28:76.4'><span class='pli'>paññāsa koṭisatāni ca. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='77.1' data-uid='thi-ap28:77.1'><span class='pli'>Etesaṁ devadevānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='77.2' data-uid='thi-ap28:77.2'><span class='pli'>mahādānaṁ pavattayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='77.3' data-uid='thi-ap28:77.3'><span class='pli'>Adhikāraṁ mahā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='77.4' data-uid='thi-ap28:77.4'><span class='pli'>dhammarāja suṇohi me. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='78.1' data-uid='thi-ap28:78.1'><span class='pli'>Saṭṭhikoṭisatā buddhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='78.2' data-uid='thi-ap28:78.2'><span class='pli'>sattatikoṭisatāni ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='78.3' data-uid='thi-ap28:78.3'><span class='pli'>Etesaṁ devadevānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='78.4' data-uid='thi-ap28:78.4'><span class='pli'>mahādānaṁ pavattayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='79.1' data-uid='thi-ap28:79.1'><span class='pli'>Adhikāraṁ mahā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='79.2' data-uid='thi-ap28:79.2'><span class='pli'>dhammarāja suṇohi me; </span></span></span>\n<span class='verse-line'><span class='segment' id='79.3' data-uid='thi-ap28:79.3'><span class='pli'>Asītikoṭisatā buddhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='79.4' data-uid='thi-ap28:79.4'><span class='pli'>navutikoṭisatāni ca. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='80.1' data-uid='thi-ap28:80.1'><span class='pli'>Etesaṁ devadevānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='80.2' data-uid='thi-ap28:80.2'><span class='pli'>mahādānaṁ pavattayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='80.3' data-uid='thi-ap28:80.3'><span class='pli'>Adhikāraṁ mahā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='80.4' data-uid='thi-ap28:80.4'><span class='pli'>dhammarāja suṇohi me. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='81.1' data-uid='thi-ap28:81.1'><span class='pli'>Koṭisatasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='81.2' data-uid='thi-ap28:81.2'><span class='pli'>honti lokagganāyakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='81.3' data-uid='thi-ap28:81.3'><span class='pli'>Etesaṁ devadevānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='81.4' data-uid='thi-ap28:81.4'><span class='pli'>mahādānaṁ pavattayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='82.1' data-uid='thi-ap28:82.1'><span class='pli'>Adhikāraṁ mahā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='82.2' data-uid='thi-ap28:82.2'><span class='pli'>dhammarāja suṇohi me; </span></span></span>\n<span class='verse-line'><span class='segment' id='82.3' data-uid='thi-ap28:82.3'><span class='pli'>Navakoṭisahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='82.4' data-uid='thi-ap28:82.4'><span class='pli'>apare lokanāyakā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='83.1' data-uid='thi-ap28:83.1'><span class='pli'>Etesaṁ devadevānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='83.2' data-uid='thi-ap28:83.2'><span class='pli'>mahādānaṁ pavattayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='83.3' data-uid='thi-ap28:83.3'><span class='pli'>Adhikāraṁ mahā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='83.4' data-uid='thi-ap28:83.4'><span class='pli'>dhammarāja suṇohi me. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='84.1' data-uid='thi-ap28:84.1'><span class='pli'>Koṭisatasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='84.2' data-uid='thi-ap28:84.2'><span class='pli'>pañcāsītimahesinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='84.3' data-uid='thi-ap28:84.3'><span class='pli'>Pañcāsītikoṭisatā, </span></span></span>\n<span class='verse-line'><span class='segment' id='84.4' data-uid='thi-ap28:84.4'><span class='pli'>sattatiṁsā ca koṭiyo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='85.1' data-uid='thi-ap28:85.1'><span class='pli'>Etesaṁ devadevānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='85.2' data-uid='thi-ap28:85.2'><span class='pli'>mahādānaṁ pavattayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='85.3' data-uid='thi-ap28:85.3'><span class='pli'>Adhikāraṁ mahā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='85.4' data-uid='thi-ap28:85.4'><span class='pli'>dhammarāja suṇohi me. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='86.1' data-uid='thi-ap28:86.1'><span class='pli'>Paccekabuddhā vītarāgā, </span></span></span>\n<span class='verse-line'><span class='segment' id='86.2' data-uid='thi-ap28:86.2'><span class='pli'>aṭṭhaṭṭhamakakoṭiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='86.3' data-uid='thi-ap28:86.3'><span class='pli'>Adhikāraṁ mahā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='86.4' data-uid='thi-ap28:86.4'><span class='pli'>dhammarāja suṇohi me. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='87.1' data-uid='thi-ap28:87.1'><span class='pli'>Khīṇāsavā vītamalā, </span></span></span>\n<span class='verse-line'><span class='segment' id='87.2' data-uid='thi-ap28:87.2'><span class='pli'>asaṅkhiyā buddhasāvakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='87.3' data-uid='thi-ap28:87.3'><span class='pli'>Adhikāraṁ mahā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='87.4' data-uid='thi-ap28:87.4'><span class='pli'>dhammarāja suṇohi me. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='88.1' data-uid='thi-ap28:88.1'><span class='pli'>Evaṁ dhamme suciṇṇānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='88.2' data-uid='thi-ap28:88.2'><span class='pli'>sadā dhammassa cārinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='88.3' data-uid='thi-ap28:88.3'><span class='pli'>Dhammacārī sukhaṁ seti, </span></span></span>\n<span class='verse-line'><span class='segment' id='88.4' data-uid='thi-ap28:88.4'><span class='pli'>asmiṁ loke paramhi ca. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='89.1' data-uid='thi-ap28:89.1'><span class='pli'>Dhammaṁ care sucaritaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='89.2' data-uid='thi-ap28:89.2'><span class='pli'>na naṁ duccaritaṁ care; </span></span></span>\n<span class='verse-line'><span class='segment' id='89.3' data-uid='thi-ap28:89.3'><span class='pli'>Dhammacārī sukhaṁ seti, </span></span></span>\n<span class='verse-line'><span class='segment' id='89.4' data-uid='thi-ap28:89.4'><span class='pli'>asmiṁ loke paramhi ca. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='90.1' data-uid='thi-ap28:90.1'><span class='pli'>Nibbinditvāna saṁsāre, </span></span></span>\n<span class='verse-line'><span class='segment' id='90.2' data-uid='thi-ap28:90.2'><span class='pli'>pabbajiṁ anagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='90.3' data-uid='thi-ap28:90.3'><span class='pli'>Sahassaparivārena, </span></span></span>\n<span class='verse-line'><span class='segment' id='90.4' data-uid='thi-ap28:90.4'><span class='pli'>pabbajitvā akiñcanā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='91.1' data-uid='thi-ap28:91.1'><span class='pli'>Agāraṁ vijahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='91.2' data-uid='thi-ap28:91.2'><span class='pli'>pabbajiṁ anagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='91.3' data-uid='thi-ap28:91.3'><span class='pli'>Aḍḍhamāse asampatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='91.4' data-uid='thi-ap28:91.4'><span class='pli'>catusaccamapāpuṇiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='92.1' data-uid='thi-ap28:92.1'><span class='pli'>Cīvaraṁ piṇḍapātañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='92.2' data-uid='thi-ap28:92.2'><span class='pli'>paccayaṁ sayanāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='92.3' data-uid='thi-ap28:92.3'><span class='pli'>Upanenti bahū janā, </span></span></span>\n<span class='verse-line'><span class='segment' id='92.4' data-uid='thi-ap28:92.4'><span class='pli'>sāgareyeva ūmiyo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='93.1' data-uid='thi-ap28:93.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='93.2' data-uid='thi-ap28:93.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='93.3' data-uid='thi-ap28:93.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='94.1' data-uid='thi-ap28:94.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='94.2' data-uid='thi-ap28:94.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='94.3' data-uid='thi-ap28:94.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='95.1' data-uid='thi-ap28:95.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='95.2' data-uid='thi-ap28:95.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='95.3' data-uid='thi-ap28:95.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='96.1' data-uid='thi-ap28:96.1'><span class='pli'>Evaṁ bahuvidhaṁ dukkhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='96.2' data-uid='thi-ap28:96.2'><span class='pli'>sampattī ca bahubbidhā; </span></span></span>\n<span class='verse-line'><span class='segment' id='96.3' data-uid='thi-ap28:96.3'><span class='pli'>Visuddhibhāvaṁ sampattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='96.4' data-uid='thi-ap28:96.4'><span class='pli'>labhāmi sabbasampadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='97.1' data-uid='thi-ap28:97.1'><span class='pli'>Yā dadāti sakattānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='97.2' data-uid='thi-ap28:97.2'><span class='pli'>puññatthāya mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='97.3' data-uid='thi-ap28:97.3'><span class='pli'>Sahāyasampadā honti, </span></span></span>\n<span class='verse-line'><span class='segment' id='97.4' data-uid='thi-ap28:97.4'><span class='pli'>nibbānapadamasaṅkhataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='98.1' data-uid='thi-ap28:98.1'><span class='pli'>Parikkhīṇaṁ atītañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='98.2' data-uid='thi-ap28:98.2'><span class='pli'>Paccuppannaṁ anāgataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='98.3' data-uid='thi-ap28:98.3'><span class='pli'>Sabbakammaṁ mamaṁ khīṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='98.4' data-uid='thi-ap28:98.4'><span class='pli'>Pāde vandāmi cakkhuma”. </span></span></span></p></blockquote>\n<p><span class='segment' id='99.1' data-uid='thi-ap28:99.1'><span class='pli'>Itthaṁ sudaṁ yasodharā bhikkhunī bhagavato sammukhā imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='100.1' data-uid='thi-ap28:100.1'><span class='pli'>Yasodharātheriyāpadānaṁ aṭṭhamaṁ. </span></span></p></article>\n"
  },
  "thi-ap29": {
    "previous": "thi-ap28",
    "next": "thi-ap30",
    "content": "<article id='thi-ap29'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap29:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap29:0.2'><span class='pli'>Kuṇḍalakesīvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap29:0.3'><span class='pli'>9 Yasodharāpamukhadasabhikkhunīsahassaapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap29:1.1'><span class='pli'>“Kappe ca satasahasse, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap29:1.2'><span class='pli'>caturo ca asaṅkhiye; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap29:1.3'><span class='pli'>Dīpaṅkaro nāma jino, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap29:1.4'><span class='pli'>uppajji lokanāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap29:2.1'><span class='pli'>Dīpaṅkaro mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap29:2.2'><span class='pli'>viyākāsi vināyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap29:2.3'><span class='pli'>Sumedhañca sumittañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap29:2.4'><span class='pli'>samānasukhadukkhataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap29:3.1'><span class='pli'>Sadevakañca passanto, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap29:3.2'><span class='pli'>vicaranto sadevakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap29:3.3'><span class='pli'>Tesaṁ pakittane amhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap29:3.4'><span class='pli'>upagamma samāgamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap29:4.1'><span class='pli'>Amhaṁ sabbapati hohi, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap29:4.2'><span class='pli'>anāgatasamāgame; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap29:4.3'><span class='pli'>Sabbāva tuyhaṁ bhariyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap29:4.4'><span class='pli'>manāpā piyavādikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap29:5.1'><span class='pli'>Dānaṁ sīlamayaṁ sabbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap29:5.2'><span class='pli'>bhāvanā ca subhāvitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap29:5.3'><span class='pli'>Dīgharattañca no sabbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap29:5.4'><span class='pli'>pariccattaṁ mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap29:6.1'><span class='pli'>Gandhaṁ vilepanaṁ mālaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap29:6.2'><span class='pli'>dīpañca ratanāmayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap29:6.3'><span class='pli'>Yaṁ kiñci patthitaṁ sabbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap29:6.4'><span class='pli'>pariccattaṁ mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap29:7.1'><span class='pli'>Aññaṁ vāpi kataṁ kammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap29:7.2'><span class='pli'>paribhogañca mānusaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap29:7.3'><span class='pli'>Dīgharattañhi no sabbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap29:7.4'><span class='pli'>pariccattaṁ mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap29:8.1'><span class='pli'>Anekajātisaṁsāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap29:8.2'><span class='pli'>bahuṁ puññampi no kataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap29:8.3'><span class='pli'>Issaramanubhotvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap29:8.4'><span class='pli'>saṁsaritvā bhavābhave. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap29:9.1'><span class='pli'>Pacchime bhave sampatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap29:9.2'><span class='pli'>sakyaputtanivesane; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap29:9.3'><span class='pli'>Nānākulūpapannāyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap29:9.4'><span class='pli'>accharā kāmavaṇṇinī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap29:10.1'><span class='pli'>Lābhaggena yasaṁ pattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap29:10.2'><span class='pli'>pūjitā sabbasakkatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap29:10.3'><span class='pli'>Lābhiyo annapānānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap29:10.4'><span class='pli'>sadā sammānitā mayaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap29:11.1'><span class='pli'>Agāraṁ pajahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap29:11.2'><span class='pli'>pabbajimhanagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap29:11.3'><span class='pli'>Aḍḍhamāse asampatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap29:11.4'><span class='pli'>sabbā pattāmha nibbutiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap29:12.1'><span class='pli'>Lābhiyo annapānānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap29:12.2'><span class='pli'>vatthasenāsanāni ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap29:12.3'><span class='pli'>Upenti paccayā sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap29:12.4'><span class='pli'>sadā sakkatapūjitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap29:13.1'><span class='pli'>Kilesā jhāpitā amhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap29:13.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap29:13.3'><span class='pli'>viharāma anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap29:14.1'><span class='pli'>Svāgataṁ vata no āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap29:14.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap29:14.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap29:15.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap29:15.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap29:15.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='16.1' data-uid='thi-ap29:16.1'><span class='pli'>Itthaṁ sudaṁ yasodharāpamukhāni dasabhikkhunīsahassāni bhagavato sammukhā imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='17.1' data-uid='thi-ap29:17.1'><span class='pli'>Yasodharāpamukhadasabhikkhunīsahassāpadānaṁ navamaṁ. </span></span></p></article>\n"
  },
  "thi-ap30": {
    "previous": "thi-ap29",
    "next": "thi-ap31",
    "content": "<article id='thi-ap30'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap30:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap30:0.2'><span class='pli'>Kuṇḍalakesīvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap30:0.3'><span class='pli'>10 Yasodharāpamukhaaṭṭhārasabhikkhunīsahassaapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap30:1.1'><span class='pli'>Aṭṭhārasasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap30:1.2'><span class='pli'>bhikkhunī sakyasambhavā; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap30:1.3'><span class='pli'>Yasodharāpamukhāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap30:1.4'><span class='pli'>sambuddhaṁ upasaṅkamuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap30:2.1'><span class='pli'>Aṭṭhārasasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap30:2.2'><span class='pli'>sabbā honti mahiddhikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap30:2.3'><span class='pli'>Vandantī munino pāde, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap30:2.4'><span class='pli'>ārocenti yathābalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap30:3.1'><span class='pli'>“Jāti khīṇā jarā byādhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap30:3.2'><span class='pli'>maraṇañca mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap30:3.3'><span class='pli'>Anāsavaṁ padaṁ santaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap30:3.4'><span class='pli'>amataṁ yāma nāyaka. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap30:4.1'><span class='pli'>Khalitañce pure atthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap30:4.2'><span class='pli'>sabbāsampi mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap30:4.3'><span class='pli'>Aparādhamajānantī, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap30:4.4'><span class='pli'>khama amhaṁ vināyaka”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap30:5.1'><span class='pli'>“Iddhiñcāpi nidassetha, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap30:5.2'><span class='pli'>mama sāsanakārikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap30:5.3'><span class='pli'>Parisānañca sabbāsaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap30:5.4'><span class='pli'>kaṅkhaṁ chindatha yāvatā”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap30:6.1'><span class='pli'>“Yasodharā mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap30:6.2'><span class='pli'>manāpā piyadassanā; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap30:6.3'><span class='pli'>Sabbā tuyhaṁ mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap30:6.4'><span class='pli'>agārasmiṁ pajāpati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap30:7.1'><span class='pli'>Thīnaṁ satasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap30:7.2'><span class='pli'>navutīnaṁ chaduttari; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap30:7.3'><span class='pli'>Agāre te mayaṁ vīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap30:7.4'><span class='pli'>pāmokkhā sabbā issarā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap30:8.1'><span class='pli'>Rūpācāraguṇūpetā, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap30:8.2'><span class='pli'>yobbanaṭṭhā piyaṁvadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap30:8.3'><span class='pli'>Sabbā no apacāyanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap30:8.4'><span class='pli'>devatā viya mānusā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap30:9.1'><span class='pli'>Aṭṭhārasasahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap30:9.2'><span class='pli'>sabbā sākiyasambhavā; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap30:9.3'><span class='pli'>Yasodharāsahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap30:9.4'><span class='pli'>pāmokkhā issarā tadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap30:10.1'><span class='pli'>Kāmadhātumatikkamma, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap30:10.2'><span class='pli'>saṇṭhitā rūpadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap30:10.3'><span class='pli'>Rūpena sadisā natthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap30:10.4'><span class='pli'>sahassānaṁ mahāmuni”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap30:11.1'><span class='pli'>Sambuddhaṁ abhivādetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap30:11.2'><span class='pli'>iddhiṁ dassaṁsu satthuno; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap30:11.3'><span class='pli'>Nekā nānāvidhākārā, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap30:11.4'><span class='pli'>mahāiddhīpi dassayuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap30:12.1'><span class='pli'>Cakkavāḷasamaṁ kāyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap30:12.2'><span class='pli'>sīsaṁ uttarato kuru; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap30:12.3'><span class='pli'>Ubho pakkhā duve dīpā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap30:12.4'><span class='pli'>jambudīpaṁ sarīrato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap30:13.1'><span class='pli'>Dakkhiṇañca saraṁ piñchaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap30:13.2'><span class='pli'>nānāsākhā tu pattakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap30:13.3'><span class='pli'>Candañca sūriyañcakkhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap30:13.4'><span class='pli'>merupabbatato sikhaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap30:14.1'><span class='pli'>Cakkavāḷagiriṁ tuṇḍaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap30:14.2'><span class='pli'>jamburukkhaṁ samūlakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap30:14.3'><span class='pli'>Bījamānā upāgantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap30:14.4'><span class='pli'>vandantī lokanāyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap30:15.1'><span class='pli'>Hatthivaṇṇaṁ tathevassaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap30:15.2'><span class='pli'>pabbataṁ jaladhiṁ tathā; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap30:15.3'><span class='pli'>Candañca sūriyaṁ meruṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap30:15.4'><span class='pli'>sakkavaṇṇañca dassayuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap30:16.1'><span class='pli'>“Yasodharā mayaṁ vīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap30:16.2'><span class='pli'>pāde vandāma cakkhuma; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap30:16.3'><span class='pli'>Tava cirapabhāvena, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap30:16.4'><span class='pli'>nipphannā naranāyaka. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap30:17.1'><span class='pli'>Iddhīsu ca vasī homa, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap30:17.2'><span class='pli'>dibbāya sotadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap30:17.3'><span class='pli'>Cetopariyañāṇassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap30:17.4'><span class='pli'>vasī homa mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap30:18.1'><span class='pli'>Pubbenivāsaṁ jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap30:18.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap30:18.3'><span class='pli'>Sabbāsavā parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap30:18.4'><span class='pli'>natthi dāni punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap30:19.1'><span class='pli'>Atthadhammaniruttīsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap30:19.2'><span class='pli'>paṭibhāne tatheva ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap30:19.3'><span class='pli'>Ñāṇaṁ amhaṁ mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap30:19.4'><span class='pli'>uppannaṁ tava santike. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap30:20.1'><span class='pli'>Pubbānaṁ lokanāthānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap30:20.2'><span class='pli'>saṅgamaṁ no nidassitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap30:20.3'><span class='pli'>Adhikārā bahū amhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap30:20.4'><span class='pli'>tuyhatthāya mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap30:21.1'><span class='pli'>Yaṁ amhaṁ pūritaṁ kammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap30:21.2'><span class='pli'>kusalaṁ sarase mune; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap30:21.3'><span class='pli'>Tuyhatthāya mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap30:21.4'><span class='pli'>puññānupacitāni no. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap30:22.1'><span class='pli'>Abhabbaṭṭhāne vajjetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap30:22.2'><span class='pli'>vārayimha anācaraṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap30:22.3'><span class='pli'>Tuyhatthāya mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap30:22.4'><span class='pli'>cattāni jīvitāni no. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap30:23.1'><span class='pli'>Nekakoṭisahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap30:23.2'><span class='pli'>bhariyatthāyadāsi no; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap30:23.3'><span class='pli'>Na tattha vimanā homa, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='thi-ap30:23.4'><span class='pli'>tuyhatthāya mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap30:24.1'><span class='pli'>Nekakoṭisahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap30:24.2'><span class='pli'>upakārāyadāsi no; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap30:24.3'><span class='pli'>Na tattha vimanā homa, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='thi-ap30:24.4'><span class='pli'>tuyhatthāya mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='thi-ap30:25.1'><span class='pli'>Nekakoṭisahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='thi-ap30:25.2'><span class='pli'>bhojanatthāyadāsi no; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='thi-ap30:25.3'><span class='pli'>Na tattha vimanā homa, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='thi-ap30:25.4'><span class='pli'>tuyhatthāya mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='thi-ap30:26.1'><span class='pli'>Nekakoṭisahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='thi-ap30:26.2'><span class='pli'>jīvitāni cajimhase; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='thi-ap30:26.3'><span class='pli'>Bhayamokkhaṁ karissāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='thi-ap30:26.4'><span class='pli'>jīvitāni cajimhase. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='thi-ap30:27.1'><span class='pli'>Aṅgagate alaṅkāre, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='thi-ap30:27.2'><span class='pli'>vatthe nānāvidhe bahū; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='thi-ap30:27.3'><span class='pli'>Itthibhaṇḍe na gūhāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='thi-ap30:27.4'><span class='pli'>tuyhatthāya mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='thi-ap30:28.1'><span class='pli'>Dhanadhaññapariccāgaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='thi-ap30:28.2'><span class='pli'>gāmāni nigamāni ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='thi-ap30:28.3'><span class='pli'>Khettaṁ puttā ca dhītā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='thi-ap30:28.4'><span class='pli'>pariccattā mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='thi-ap30:29.1'><span class='pli'>Hatthī assā gavā cāpi, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='thi-ap30:29.2'><span class='pli'>dāsiyo paricārikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='thi-ap30:29.3'><span class='pli'>Tuyhatthāya mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='thi-ap30:29.4'><span class='pli'>pariccattaṁ asaṅkhiyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='thi-ap30:30.1'><span class='pli'>Yaṁ amhe paṭimantesi, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='thi-ap30:30.2'><span class='pli'>‘dānaṁ dassāma yācake’; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='thi-ap30:30.3'><span class='pli'>Vimanaṁ no na passāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='thi-ap30:30.4'><span class='pli'>dadato dānamuttamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='thi-ap30:31.1'><span class='pli'>Nānāvidhaṁ bahuṁ dukkhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='thi-ap30:31.2'><span class='pli'>saṁsāre ca bahubbidhe; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='thi-ap30:31.3'><span class='pli'>Tuyhatthāya mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='thi-ap30:31.4'><span class='pli'>anubhuttaṁ asaṅkhiyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='thi-ap30:32.1'><span class='pli'>Sukhappattānumodāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='thi-ap30:32.2'><span class='pli'>na ca dukkhesu dummanā; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='thi-ap30:32.3'><span class='pli'>Sabbattha tulitā homa, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='thi-ap30:32.4'><span class='pli'>tuyhatthāya mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='thi-ap30:33.1'><span class='pli'>Anumaggena sambuddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='thi-ap30:33.2'><span class='pli'>yaṁ dhammaṁ abhinīhari; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='thi-ap30:33.3'><span class='pli'>Anubhotvā sukhaṁ dukkhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='thi-ap30:33.4'><span class='pli'>patto bodhiṁ mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='34.1' data-uid='thi-ap30:34.1'><span class='pli'>Brahmadevañca sambuddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='thi-ap30:34.2'><span class='pli'>gotamaṁ lokanāyakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='thi-ap30:34.3'><span class='pli'>Aññesaṁ lokanāthānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.4' data-uid='thi-ap30:34.4'><span class='pli'>saṅgamaṁ tehi no bahū. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='35.1' data-uid='thi-ap30:35.1'><span class='pli'>Adhikāraṁ bahuṁ amhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.2' data-uid='thi-ap30:35.2'><span class='pli'>tuyhatthāya mahāmune; </span></span></span>\n<span class='verse-line'><span class='segment' id='35.3' data-uid='thi-ap30:35.3'><span class='pli'>Gavesato buddhadhamme, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.4' data-uid='thi-ap30:35.4'><span class='pli'>mayaṁ te paricārikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='36.1' data-uid='thi-ap30:36.1'><span class='pli'>Kappe ca satasahasse, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.2' data-uid='thi-ap30:36.2'><span class='pli'>caturo ca asaṅkhiye; </span></span></span>\n<span class='verse-line'><span class='segment' id='36.3' data-uid='thi-ap30:36.3'><span class='pli'>Dīpaṅkaro mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.4' data-uid='thi-ap30:36.4'><span class='pli'>uppajji lokanāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='37.1' data-uid='thi-ap30:37.1'><span class='pli'>Paccantadesavisaye, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.2' data-uid='thi-ap30:37.2'><span class='pli'>nimantetvā tathāgataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='37.3' data-uid='thi-ap30:37.3'><span class='pli'>Tassa āgamanaṁ maggaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.4' data-uid='thi-ap30:37.4'><span class='pli'>sodhenti tuṭṭhamānasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='38.1' data-uid='thi-ap30:38.1'><span class='pli'>Tena kālena so āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.2' data-uid='thi-ap30:38.2'><span class='pli'>sumedho nāma brāhmaṇo; </span></span></span>\n<span class='verse-line'><span class='segment' id='38.3' data-uid='thi-ap30:38.3'><span class='pli'>Maggañca paṭiyādesi, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.4' data-uid='thi-ap30:38.4'><span class='pli'>āyato sabbadassino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='39.1' data-uid='thi-ap30:39.1'><span class='pli'>Tena kālena ahumha, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.2' data-uid='thi-ap30:39.2'><span class='pli'>sabbā brāhmaṇasambhavā; </span></span></span>\n<span class='verse-line'><span class='segment' id='39.3' data-uid='thi-ap30:39.3'><span class='pli'>Thalūdajāni pupphāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.4' data-uid='thi-ap30:39.4'><span class='pli'>āharimha samāgamaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='40.1' data-uid='thi-ap30:40.1'><span class='pli'>Tasmiṁ so samaye buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.2' data-uid='thi-ap30:40.2'><span class='pli'>dīpaṅkaro mahāyaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='40.3' data-uid='thi-ap30:40.3'><span class='pli'>Viyākāsi mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.4' data-uid='thi-ap30:40.4'><span class='pli'>isimuggatamānasaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='41.1' data-uid='thi-ap30:41.1'><span class='pli'>Calatī ravatī puthavī, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.2' data-uid='thi-ap30:41.2'><span class='pli'>saṅkampati sadevake; </span></span></span>\n<span class='verse-line'><span class='segment' id='41.3' data-uid='thi-ap30:41.3'><span class='pli'>Tassa kammaṁ pakittente, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.4' data-uid='thi-ap30:41.4'><span class='pli'>isimuggatamānasaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='42.1' data-uid='thi-ap30:42.1'><span class='pli'>Devakaññā manussā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.2' data-uid='thi-ap30:42.2'><span class='pli'>mayañcāpi sadevakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='42.3' data-uid='thi-ap30:42.3'><span class='pli'>Nānāpūjanīyaṁ bhaṇḍaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.4' data-uid='thi-ap30:42.4'><span class='pli'>pūjayitvāna patthayuṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='43.1' data-uid='thi-ap30:43.1'><span class='pli'>Tesaṁ buddho viyākāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.2' data-uid='thi-ap30:43.2'><span class='pli'>jotidīpa sanāmako; </span></span></span>\n<span class='verse-line'><span class='segment' id='43.3' data-uid='thi-ap30:43.3'><span class='pli'>‘Ajja ye patthitā atthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.4' data-uid='thi-ap30:43.4'><span class='pli'>te bhavissanti sammukhā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='44.1' data-uid='thi-ap30:44.1'><span class='pli'>Aparimeyye ito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.2' data-uid='thi-ap30:44.2'><span class='pli'>yaṁ no buddho viyākari; </span></span></span>\n<span class='verse-line'><span class='segment' id='44.3' data-uid='thi-ap30:44.3'><span class='pli'>Taṁ vācamanumodentā, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.4' data-uid='thi-ap30:44.4'><span class='pli'>evaṅkārī ahumha no. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='45.1' data-uid='thi-ap30:45.1'><span class='pli'>Tassa kammassa sukatassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.2' data-uid='thi-ap30:45.2'><span class='pli'>tassa cittaṁ pasādayuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='45.3' data-uid='thi-ap30:45.3'><span class='pli'>Devamānusikaṁ yoniṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='45.4' data-uid='thi-ap30:45.4'><span class='pli'>anubhotvā asaṅkhiyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='46.1' data-uid='thi-ap30:46.1'><span class='pli'>Sukhadukkhenubhotvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.2' data-uid='thi-ap30:46.2'><span class='pli'>devesu mānusesu ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='46.3' data-uid='thi-ap30:46.3'><span class='pli'>Pacchime bhave sampatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='46.4' data-uid='thi-ap30:46.4'><span class='pli'>jātāmha sākiye kule. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='47.1' data-uid='thi-ap30:47.1'><span class='pli'>Rūpavatī bhogavatī, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.2' data-uid='thi-ap30:47.2'><span class='pli'>yasasīlavatī tato; </span></span></span>\n<span class='verse-line'><span class='segment' id='47.3' data-uid='thi-ap30:47.3'><span class='pli'>Sabbaṅgasampadā homa, </span></span></span>\n<span class='verse-line'><span class='segment' id='47.4' data-uid='thi-ap30:47.4'><span class='pli'>kulesu abhisakkatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='48.1' data-uid='thi-ap30:48.1'><span class='pli'>Lābhaṁ silokaṁ sakkāraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.2' data-uid='thi-ap30:48.2'><span class='pli'>lokadhammasamāgamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='48.3' data-uid='thi-ap30:48.3'><span class='pli'>Cittañca dukkhitaṁ natthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='48.4' data-uid='thi-ap30:48.4'><span class='pli'>vasāma akutobhayā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='49.1' data-uid='thi-ap30:49.1'><span class='pli'>Vuttañhetaṁ bhagavatā, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.2' data-uid='thi-ap30:49.2'><span class='pli'>‘rañño antepure tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='49.3' data-uid='thi-ap30:49.3'><span class='pli'>Khattiyānaṁ pure vīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='49.4' data-uid='thi-ap30:49.4'><span class='pli'>upakārañca niddisi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='50.1' data-uid='thi-ap30:50.1'><span class='pli'>Upakārā ca yā nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.2' data-uid='thi-ap30:50.2'><span class='pli'>yā ca nārī sukhe dukhe; </span></span></span>\n<span class='verse-line'><span class='segment' id='50.3' data-uid='thi-ap30:50.3'><span class='pli'>Atthakkhāyī ca yā nārī, </span></span></span>\n<span class='verse-line'><span class='segment' id='50.4' data-uid='thi-ap30:50.4'><span class='pli'>yā ca nārīnukampikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='51.1' data-uid='thi-ap30:51.1'><span class='pli'>Dhammaṁ care sucaritaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.2' data-uid='thi-ap30:51.2'><span class='pli'>na naṁ duccaritaṁ care; </span></span></span>\n<span class='verse-line'><span class='segment' id='51.3' data-uid='thi-ap30:51.3'><span class='pli'>Dhammacārī sukhaṁ seti, </span></span></span>\n<span class='verse-line'><span class='segment' id='51.4' data-uid='thi-ap30:51.4'><span class='pli'>asmiṁ loke paramhi ca’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='52.1' data-uid='thi-ap30:52.1'><span class='pli'>Agāraṁ vijahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='52.2' data-uid='thi-ap30:52.2'><span class='pli'>pabbajimhanagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='52.3' data-uid='thi-ap30:52.3'><span class='pli'>Aḍḍhamāse asampatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='52.4' data-uid='thi-ap30:52.4'><span class='pli'>catusaccaṁ phusimha no. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='53.1' data-uid='thi-ap30:53.1'><span class='pli'>Cīvaraṁ piṇḍapātañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='53.2' data-uid='thi-ap30:53.2'><span class='pli'>paccayaṁ sayanāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='53.3' data-uid='thi-ap30:53.3'><span class='pli'>Upanenti bahū amhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='53.4' data-uid='thi-ap30:53.4'><span class='pli'>sāgarasseva ūmiyo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='54.1' data-uid='thi-ap30:54.1'><span class='pli'>Kilesā jhāpitā amhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='54.2' data-uid='thi-ap30:54.2'><span class='pli'>bhavā sabbe samūhatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='54.3' data-uid='thi-ap30:54.3'><span class='pli'>Nāgīva bandhanaṁ chetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='54.4' data-uid='thi-ap30:54.4'><span class='pli'>viharāma anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='55.1' data-uid='thi-ap30:55.1'><span class='pli'>Svāgataṁ vata no āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='55.2' data-uid='thi-ap30:55.2'><span class='pli'>mama buddhassa santike; </span></span></span>\n<span class='verse-line'><span class='segment' id='55.3' data-uid='thi-ap30:55.3'><span class='pli'>Tisso vijjā anuppattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='55.4' data-uid='thi-ap30:55.4'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='56.1' data-uid='thi-ap30:56.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='56.2' data-uid='thi-ap30:56.2'><span class='pli'>vimokkhāpi ca aṭṭhime; </span></span></span>\n<span class='verse-line'><span class='segment' id='56.3' data-uid='thi-ap30:56.3'><span class='pli'>Chaḷabhiññā sacchikatā, </span></span></span>\n<span class='verse-line'><span class='segment' id='56.4' data-uid='thi-ap30:56.4'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='57.1' data-uid='thi-ap30:57.1'><span class='pli'>Evaṁ bahuvidhaṁ dukkhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='57.2' data-uid='thi-ap30:57.2'><span class='pli'>sampattī ca bahubbidhā; </span></span></span>\n<span class='verse-line'><span class='segment' id='57.3' data-uid='thi-ap30:57.3'><span class='pli'>Visuddhabhāvaṁ sampattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='57.4' data-uid='thi-ap30:57.4'><span class='pli'>labhāma sabbasampadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='58.1' data-uid='thi-ap30:58.1'><span class='pli'>Yā dadanti sakattānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='58.2' data-uid='thi-ap30:58.2'><span class='pli'>puññatthāya mahesino; </span></span></span>\n<span class='verse-line'><span class='segment' id='58.3' data-uid='thi-ap30:58.3'><span class='pli'>Sahāyasampadā honti, </span></span></span>\n<span class='verse-line'><span class='segment' id='58.4' data-uid='thi-ap30:58.4'><span class='pli'>nibbānapadamasaṅkhataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='59.1' data-uid='thi-ap30:59.1'><span class='pli'>Parikkhīṇaṁ atītañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='59.2' data-uid='thi-ap30:59.2'><span class='pli'>paccuppannaṁ anāgataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='59.3' data-uid='thi-ap30:59.3'><span class='pli'>Sabbakammampi no khīṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='59.4' data-uid='thi-ap30:59.4'><span class='pli'>pāde vandāma cakkhuma”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='60.1' data-uid='thi-ap30:60.1'><span class='pli'>“Nibbānāya vadantīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='60.2' data-uid='thi-ap30:60.2'><span class='pli'>Kiṁ vo vakkhāma uttari; </span></span></span>\n<span class='verse-line'><span class='segment' id='60.3' data-uid='thi-ap30:60.3'><span class='pli'>Santasaṅkhatadosañhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='60.4' data-uid='thi-ap30:60.4'><span class='pli'>Pappotha amataṁ padaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='61.1' data-uid='thi-ap30:61.1'><span class='pli'>Itthaṁ sudaṁ yasodharāpamukhāni aṭṭhārasabhikkhunīsahassāni bhagavato sammukhā imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='62.1' data-uid='thi-ap30:62.1'><span class='pli'>Yasodharāpamukhaaṭṭhārasabhikkhunīsahassāpadānaṁ dasamaṁ. </span></span></p>\n<p class='endsection'><span class='segment' id='63.1' data-uid='thi-ap30:63.1'><span class='pli'>Kuṇḍalakesīvaggo tatiyo. </span></span></p>\n<p class='uddana-intro'><span class='segment' id='64.1' data-uid='thi-ap30:64.1'><span class='pli'>Tassuddānaṁ </span></span></p>\n<blockquote class='uddanagatha'><p><span class='verse-line'><span class='segment' id='65.1' data-uid='thi-ap30:65.1'><span class='pli'>Kuṇḍalā gotamī ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='65.2' data-uid='thi-ap30:65.2'><span class='pli'>dhammadinnā ca sakulā; </span></span></span>\n<span class='verse-line'><span class='segment' id='65.3' data-uid='thi-ap30:65.3'><span class='pli'>Varanandā ca soṇā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='65.4' data-uid='thi-ap30:65.4'><span class='pli'>kāpilānī yasodharā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='66.1' data-uid='thi-ap30:66.1'><span class='pli'>Dasasahassabhikkhunī, </span></span></span>\n<span class='verse-line'><span class='segment' id='66.2' data-uid='thi-ap30:66.2'><span class='pli'>aṭṭhārasasahassakā; </span></span></span>\n<span class='verse-line'><span class='segment' id='66.3' data-uid='thi-ap30:66.3'><span class='pli'>Gāthāsatāni cattāri, </span></span></span>\n<span class='verse-line'><span class='segment' id='66.4' data-uid='thi-ap30:66.4'><span class='pli'>cha ca sattatimeva ca. </span></span></span></p></blockquote></article>\n"
  },
  "thi-ap31": {
    "previous": "thi-ap30",
    "next": "thi-ap32",
    "content": "<article id='thi-ap31'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap31:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap31:0.2'><span class='pli'>Khattiyāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap31:0.3'><span class='pli'>1 Yasavatīpamukhaaṭṭhārasabhikkhunīsahassaapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap31:1.1'><span class='pli'>“Bhavā sabbe parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap31:1.2'><span class='pli'>bhavā santi vimocitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap31:1.3'><span class='pli'>Sabbāsavā ca no natthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap31:1.4'><span class='pli'>ārocema mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap31:2.1'><span class='pli'>Purimaṁ kusalaṁ kammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap31:2.2'><span class='pli'>yaṁ kiñci sādhupatthitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap31:2.3'><span class='pli'>Paribhogamayaṁ dinnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap31:2.4'><span class='pli'>tuyhatthāya mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap31:3.1'><span class='pli'>Buddhapaccekabuddhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap31:3.2'><span class='pli'>sāvakānañca patthitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap31:3.3'><span class='pli'>Paribhogamayaṁ dinnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap31:3.4'><span class='pli'>tuyhatthāya mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap31:4.1'><span class='pli'>Uccanīcamayaṁ kammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap31:4.2'><span class='pli'>bhikkhūnaṁ sādhupatthitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap31:4.3'><span class='pli'>Uccākulaparikammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap31:4.4'><span class='pli'>katametaṁ mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap31:5.1'><span class='pli'>Teneva sukkamūlena, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap31:5.2'><span class='pli'>coditā kammasampadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap31:5.3'><span class='pli'>Mānusikamatikkantā, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap31:5.4'><span class='pli'>jāyiṁsu khattiye kule. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap31:6.1'><span class='pli'>Uppatte ca kate kamme, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap31:6.2'><span class='pli'>jātiyā vāpi ekato; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap31:6.3'><span class='pli'>Pacchime ekato jātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap31:6.4'><span class='pli'>khattiyā kulasambhavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap31:7.1'><span class='pli'>Rūpavatī bhogavatī, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap31:7.2'><span class='pli'>lābhasakkārapūjitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap31:7.3'><span class='pli'>Antepure mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap31:7.4'><span class='pli'>devānaṁ viya nandane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap31:8.1'><span class='pli'>Nibbinditvā agāramhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap31:8.2'><span class='pli'>pabbajimhanagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap31:8.3'><span class='pli'>Katipāhaṁ upādāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap31:8.4'><span class='pli'>sabbā pattāmha nibbutiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap31:9.1'><span class='pli'>Cīvaraṁ piṇḍapātañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap31:9.2'><span class='pli'>paccayaṁ sayanāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap31:9.3'><span class='pli'>Upanenti bahū amhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap31:9.4'><span class='pli'>sadā sakkatapūjitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap31:10.1'><span class='pli'>Kilesā jhāpitā amhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap31:10.2'><span class='pli'>bhavā sabbe samūhatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap31:10.3'><span class='pli'>Nāgīva bandhanaṁ chetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap31:10.4'><span class='pli'>viharāma anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap31:11.1'><span class='pli'>Svāgataṁ vata no āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap31:11.2'><span class='pli'>mama buddhassa santike; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap31:11.3'><span class='pli'>Tisso vijjā anuppattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap31:11.4'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap31:12.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap31:12.2'><span class='pli'>vimokkhāpi ca aṭṭhime; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap31:12.3'><span class='pli'>Chaḷabhiññā sacchikatā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap31:12.4'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='13.1' data-uid='thi-ap31:13.1'><span class='pli'>Itthaṁ sudaṁ yasavatīpamukhāni khattiyakaññābhikkhuniyo aṭṭhārasasahassāni bhagavato sammukhā imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='14.1' data-uid='thi-ap31:14.1'><span class='pli'>Yasavatīpamukhaaṭṭhārasabhikkhunīsahassāpadānaṁ paṭhamaṁ. </span></span></p></article>\n"
  },
  "thi-ap32": {
    "previous": "thi-ap31",
    "next": "thi-ap33",
    "content": "<article id='thi-ap32'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap32:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap32:0.2'><span class='pli'>Khattiyāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap32:0.3'><span class='pli'>2 Caturāsītibhikkhunīsahassaapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap32:1.1'><span class='pli'>“Cullāsītisahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap32:1.2'><span class='pli'>brāhmaññakulasambhavā; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap32:1.3'><span class='pli'>Sukhumālahatthapādā, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap32:1.4'><span class='pli'>pure tuyhaṁ mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap32:2.1'><span class='pli'>Vessasuddakule jātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap32:2.2'><span class='pli'>devā nāgā ca kinnarā; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap32:2.3'><span class='pli'>Cātuddīpā bahū kaññā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap32:2.4'><span class='pli'>pure tuyhaṁ mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap32:3.1'><span class='pli'>Kāci pabbajitā atthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap32:3.2'><span class='pli'>sabbadassāvino bahū; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap32:3.3'><span class='pli'>Devā ca kinnarā nāgā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap32:3.4'><span class='pli'>phusissanti anāgate. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap32:4.1'><span class='pli'>Anubhotvā yasaṁ sabbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap32:4.2'><span class='pli'>Patvāna sabbasampadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap32:4.3'><span class='pli'>Tumhaṁ pasādaṁ paṭiladdhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap32:4.4'><span class='pli'>Bujjhissanti anāgate. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap32:5.1'><span class='pli'>Amhe brāhmaṇadhītā tu, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap32:5.2'><span class='pli'>brāhmaññakulasambhavā; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap32:5.3'><span class='pli'>Pekkhato no mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap32:5.4'><span class='pli'>pāde vandāma cakkhuma. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap32:6.1'><span class='pli'>Upāhatā bhavā sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap32:6.2'><span class='pli'>mūlataṇhā samūhatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap32:6.3'><span class='pli'>Samucchinnā anusayā, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap32:6.4'><span class='pli'>puññasaṅkhāradālitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap32:7.1'><span class='pli'>Samādhigocarā sabbā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap32:7.2'><span class='pli'>samāpattivasī katā; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap32:7.3'><span class='pli'>Jhānena dhammaratiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap32:7.4'><span class='pli'>viharissāma no sadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap32:8.1'><span class='pli'>Bhavanetti avijjā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap32:8.2'><span class='pli'>saṅkhārāpi ca khepitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap32:8.3'><span class='pli'>Sududdasaṁ padaṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap32:8.4'><span class='pli'>anujānātha nāyaka”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap32:9.1'><span class='pli'>“Upakārā mamaṁ tumhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap32:9.2'><span class='pli'>dīgharattaṁ katāvino; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap32:9.3'><span class='pli'>Catunnaṁ saṁsayaṁ chetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap32:9.4'><span class='pli'>sabbā gacchantu nibbutiṁ”. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap32:10.1'><span class='pli'>Vanditvā munino pāde, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap32:10.2'><span class='pli'>katvā iddhivikubbanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap32:10.3'><span class='pli'>Kāci dassenti ālokaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap32:10.4'><span class='pli'>andhakāramathāparā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap32:11.1'><span class='pli'>Dassenti candasūriye, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap32:11.2'><span class='pli'>sāgarañca samacchakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap32:11.3'><span class='pli'>Sineruṁ paribhaṇḍañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap32:11.4'><span class='pli'>dassenti pārichattakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap32:12.1'><span class='pli'>Tāvatiṁsañca bhavanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap32:12.2'><span class='pli'>yāmaṁ dassenti iddhiyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap32:12.3'><span class='pli'>Tusitaṁ nimmite deve, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap32:12.4'><span class='pli'>vasavattī mahissare. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap32:13.1'><span class='pli'>Brahmāno kāci dassenti, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap32:13.2'><span class='pli'>caṅkamañca mahārahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap32:13.3'><span class='pli'>Brahmavaṇṇañca māpetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap32:13.4'><span class='pli'>dhammaṁ desenti suññataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap32:14.1'><span class='pli'>Nānāvikubbanaṁ katvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap32:14.2'><span class='pli'>iddhiṁ dassiya satthuno; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap32:14.3'><span class='pli'>Dassayiṁsu balaṁ sabbā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap32:14.4'><span class='pli'>pāde vandiṁsu satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap32:15.1'><span class='pli'>“Iddhīsu ca vasī homa, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap32:15.2'><span class='pli'>dibbāya sotadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap32:15.3'><span class='pli'>Cetopariyañāṇassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap32:15.4'><span class='pli'>vasī homa mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap32:16.1'><span class='pli'>Pubbenivāsaṁ jānāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap32:16.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap32:16.3'><span class='pli'>Sabbāsavā parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap32:16.4'><span class='pli'>natthi dāni punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap32:17.1'><span class='pli'>Atthadhammaniruttīsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap32:17.2'><span class='pli'>paṭibhāne tatheva ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap32:17.3'><span class='pli'>Ñāṇaṁ amhaṁ mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap32:17.4'><span class='pli'>uppannaṁ tava santike. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap32:18.1'><span class='pli'>Pubbānaṁ lokanāthānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap32:18.2'><span class='pli'>saṅgamaṁ no nidassitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap32:18.3'><span class='pli'>Adhikāraṁ bahuṁ amhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap32:18.4'><span class='pli'>tuyhatthāya mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap32:19.1'><span class='pli'>Yaṁ amhehi kataṁ kammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap32:19.2'><span class='pli'>kusalaṁ sara taṁ mune; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap32:19.3'><span class='pli'>Tuyhatthāya mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap32:19.4'><span class='pli'>puññānupacitāni no. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap32:20.1'><span class='pli'>Satasahassito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap32:20.2'><span class='pli'>padumuttaro mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap32:20.3'><span class='pli'>Puraṁ haṁsavatī nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap32:20.4'><span class='pli'>sambuddhassa kulāsayaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap32:21.1'><span class='pli'>Dvārena haṁsavatiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap32:21.2'><span class='pli'>gaṅgā sandati sabbadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap32:21.3'><span class='pli'>Ubbāḷhā nadiyā bhikkhū, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap32:21.4'><span class='pli'>gamanaṁ na labhanti te. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap32:22.1'><span class='pli'>Divasaṁ dve tayo ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap32:22.2'><span class='pli'>sattāhaṁ māsikaṁ tato; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap32:22.3'><span class='pli'>Catumāsampi sampuṇṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap32:22.4'><span class='pli'>gamanaṁ na labhanti te. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap32:23.1'><span class='pli'>Tadā ahu sattasāro, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap32:23.2'><span class='pli'>jaṭilo nāma raṭṭhiko; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap32:23.3'><span class='pli'>Oruddhe bhikkhavo disvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='thi-ap32:23.4'><span class='pli'>setuṁ gaṅgāya kārayi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap32:24.1'><span class='pli'>Tadā satasahassehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap32:24.2'><span class='pli'>setuṁ gaṅgāya kārayi; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap32:24.3'><span class='pli'>Saṅghassa orime tīre, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='thi-ap32:24.4'><span class='pli'>vihārañca akārayi. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='thi-ap32:25.1'><span class='pli'>Itthiyo purisā ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='thi-ap32:25.2'><span class='pli'>uccanīcakulāni ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='thi-ap32:25.3'><span class='pli'>Tassa setuṁ vihārañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='thi-ap32:25.4'><span class='pli'>samabhāgaṁ akaṁsu te. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='thi-ap32:26.1'><span class='pli'>Amhe aññe ca mānujā, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='thi-ap32:26.2'><span class='pli'>vippasannena cetasā; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='thi-ap32:26.3'><span class='pli'>Tassa dhammesu dāyādā, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='thi-ap32:26.4'><span class='pli'>nagare janapadesu ca. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='thi-ap32:27.1'><span class='pli'>Itthī pumā kumārā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='thi-ap32:27.2'><span class='pli'>bahū ceva kumārikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='thi-ap32:27.3'><span class='pli'>Setuno ca vihārassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='thi-ap32:27.4'><span class='pli'>vālukā ākiriṁsu te. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='thi-ap32:28.1'><span class='pli'>Vīthiṁ sammajjanaṁ katvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='thi-ap32:28.2'><span class='pli'>kadalīpuṇṇaghaṭe dhaje; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='thi-ap32:28.3'><span class='pli'>Dhūpaṁ cuṇṇañca mālañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='thi-ap32:28.4'><span class='pli'>kāraṁ katvāna satthuno. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='thi-ap32:29.1'><span class='pli'>Setuvihāre kāretvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='thi-ap32:29.2'><span class='pli'>nimantetvā vināyakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='thi-ap32:29.3'><span class='pli'>Mahādānaṁ daditvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='thi-ap32:29.4'><span class='pli'>sambodhiṁ abhipatthayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='thi-ap32:30.1'><span class='pli'>Padumuttaro mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='thi-ap32:30.2'><span class='pli'>tārako sabbapāṇinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='thi-ap32:30.3'><span class='pli'>Anumodanīyaṅkāsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.4' data-uid='thi-ap32:30.4'><span class='pli'>jaṭilassa mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='thi-ap32:31.1'><span class='pli'>‘Satasahassātikkante, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='thi-ap32:31.2'><span class='pli'>kappo hessati bhaddako; </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='thi-ap32:31.3'><span class='pli'>Bhavābhavenubhotvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.4' data-uid='thi-ap32:31.4'><span class='pli'>pāpuṇissati bodhiyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='thi-ap32:32.1'><span class='pli'>Kāci hatthaparikammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='thi-ap32:32.2'><span class='pli'>katāvī naranāriyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='thi-ap32:32.3'><span class='pli'>Anāgatamhi addhāne, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.4' data-uid='thi-ap32:32.4'><span class='pli'>sabbā hessanti sammukhā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='33.1' data-uid='thi-ap32:33.1'><span class='pli'>Tena kammavipākena, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.2' data-uid='thi-ap32:33.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='33.3' data-uid='thi-ap32:33.3'><span class='pli'>Uppannā devabhavanaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='33.4' data-uid='thi-ap32:33.4'><span class='pli'>tuyhaṁ tā paricārikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='34.1' data-uid='thi-ap32:34.1'><span class='pli'>Dibbasukhaṁ asaṅkhiyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.2' data-uid='thi-ap32:34.2'><span class='pli'>mānusañca asaṅkhiyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='34.3' data-uid='thi-ap32:34.3'><span class='pli'>Tuyhaṁ te paricārema, </span></span></span>\n<span class='verse-line'><span class='segment' id='34.4' data-uid='thi-ap32:34.4'><span class='pli'>saṁsarimha bhavābhave. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='35.1' data-uid='thi-ap32:35.1'><span class='pli'>Satasahassito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.2' data-uid='thi-ap32:35.2'><span class='pli'>sukataṁ kammasampadaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='35.3' data-uid='thi-ap32:35.3'><span class='pli'>Sukhumālī manussānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='35.4' data-uid='thi-ap32:35.4'><span class='pli'>atho devapure vare. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='36.1' data-uid='thi-ap32:36.1'><span class='pli'>Rūpabhogayase ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.2' data-uid='thi-ap32:36.2'><span class='pli'>atho kittiñca sakkataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='36.3' data-uid='thi-ap32:36.3'><span class='pli'>Labhāma satataṁ sabbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='36.4' data-uid='thi-ap32:36.4'><span class='pli'>sukataṁ kammasampadaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='37.1' data-uid='thi-ap32:37.1'><span class='pli'>Pacchime bhave sampatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.2' data-uid='thi-ap32:37.2'><span class='pli'>jātāmha brāhmaṇe kule; </span></span></span>\n<span class='verse-line'><span class='segment' id='37.3' data-uid='thi-ap32:37.3'><span class='pli'>Sukhumālahatthapādā, </span></span></span>\n<span class='verse-line'><span class='segment' id='37.4' data-uid='thi-ap32:37.4'><span class='pli'>sakyaputtanivesane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='38.1' data-uid='thi-ap32:38.1'><span class='pli'>Sabbakālampi pathaviṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.2' data-uid='thi-ap32:38.2'><span class='pli'>na passāma na laṅkataṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='38.3' data-uid='thi-ap32:38.3'><span class='pli'>Cikkhallabhūmimasuciṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='38.4' data-uid='thi-ap32:38.4'><span class='pli'>na passāma mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='39.1' data-uid='thi-ap32:39.1'><span class='pli'>Agāraṁ vasante amhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.2' data-uid='thi-ap32:39.2'><span class='pli'>sakkāraṁ sabbakālikaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='39.3' data-uid='thi-ap32:39.3'><span class='pli'>Upanenti sadā sabbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='39.4' data-uid='thi-ap32:39.4'><span class='pli'>pubbakammaphalena no. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='40.1' data-uid='thi-ap32:40.1'><span class='pli'>Agāraṁ pajahitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.2' data-uid='thi-ap32:40.2'><span class='pli'>pabbajitvānagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='40.3' data-uid='thi-ap32:40.3'><span class='pli'>Saṁsārapathanitthiṇṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='40.4' data-uid='thi-ap32:40.4'><span class='pli'>vītarāgā bhavāmase. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='41.1' data-uid='thi-ap32:41.1'><span class='pli'>Cīvaraṁ piṇḍapātañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.2' data-uid='thi-ap32:41.2'><span class='pli'>paccayaṁ sayanāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='41.3' data-uid='thi-ap32:41.3'><span class='pli'>Upanenti sadā amhe, </span></span></span>\n<span class='verse-line'><span class='segment' id='41.4' data-uid='thi-ap32:41.4'><span class='pli'>sahassāni tato tato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='42.1' data-uid='thi-ap32:42.1'><span class='pli'>Kilesā jhāpitā amhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='42.2' data-uid='thi-ap32:42.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='42.3' data-uid='thi-ap32:42.3'><span class='pli'>viharāma anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='43.1' data-uid='thi-ap32:43.1'><span class='pli'>Svāgataṁ vata no āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='43.2' data-uid='thi-ap32:43.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='43.3' data-uid='thi-ap32:43.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='44.1' data-uid='thi-ap32:44.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='44.2' data-uid='thi-ap32:44.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='44.3' data-uid='thi-ap32:44.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='45.1' data-uid='thi-ap32:45.1'><span class='pli'>Itthaṁ sudaṁ caturāsītibrāhmaṇakaññābhikkhunīsahassāni bhagavato sammukhā imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='46.1' data-uid='thi-ap32:46.1'><span class='pli'>Caturāsītibhikkhunīsahassāpadānaṁ dutiyaṁ. </span></span></p></article>\n"
  },
  "thi-ap33": {
    "previous": "thi-ap32",
    "next": "thi-ap34",
    "content": "<article id='thi-ap33'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap33:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap33:0.2'><span class='pli'>Khattiyāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap33:0.3'><span class='pli'>Uppaladāyikātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap33:1.1'><span class='pli'>“Nagare aruṇavatiyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap33:1.2'><span class='pli'>aruṇo nāma khattiyo; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap33:1.3'><span class='pli'>Tassa rañño ahuṁ bhariyā, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap33:1.4'><span class='pli'>ekajjhaṁ cārayāmahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap33:2.1'><span class='pli'>Rahogatā nisīditvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap33:2.2'><span class='pli'>evaṁ cintesahaṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap33:2.3'><span class='pli'>‘Kusalaṁ me kataṁ natthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap33:2.4'><span class='pli'>ādāya gamiyaṁ mama. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap33:3.1'><span class='pli'>Mahābhitāpaṁ kaṭukaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap33:3.2'><span class='pli'>ghorarūpaṁ sudāruṇaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap33:3.3'><span class='pli'>Nirayaṁ nūna gacchāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap33:3.4'><span class='pli'>ettha me natthi saṁsayo’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap33:4.1'><span class='pli'>Evāhaṁ cintayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap33:4.2'><span class='pli'>pahaṁsetvāna mānasaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap33:4.3'><span class='pli'>Rājānaṁ upagantvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap33:4.4'><span class='pli'>idaṁ vacanamabraviṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap33:5.1'><span class='pli'>‘Itthī nāma mayaṁ deva, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap33:5.2'><span class='pli'>purisānittarā ahu; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap33:5.3'><span class='pli'>Ekaṁ me samaṇaṁ dehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap33:5.4'><span class='pli'>bhojayissāmi khattiya’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap33:6.1'><span class='pli'>Adāsi me tadā rājā, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap33:6.2'><span class='pli'>samaṇaṁ bhāvitindriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap33:6.3'><span class='pli'>Tassa pattaṁ gahetvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap33:6.4'><span class='pli'>paramannena pūrayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap33:7.1'><span class='pli'>Pūretvā paramaṁ annaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap33:7.2'><span class='pli'>saha sugandhalepanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap33:7.3'><span class='pli'>Mahācelena chāditvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap33:7.4'><span class='pli'>adāsiṁ tuṭṭhamānasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap33:8.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap33:8.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap33:8.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap33:8.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap33:9.1'><span class='pli'>Sahassadevarājūnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap33:9.2'><span class='pli'>mahesittamakārayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap33:9.3'><span class='pli'>Sahassacakkavattīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap33:9.4'><span class='pli'>mahesittamakārayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap33:10.1'><span class='pli'>Padesarajjaṁ vipulaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap33:10.2'><span class='pli'>gaṇanāto asaṅkhiyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap33:10.3'><span class='pli'>Nānāvidhaṁ bahuṁ aññaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap33:10.4'><span class='pli'>tassa kammaphalaṁ tato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap33:11.1'><span class='pli'>Uppalasseva me vaṇṇo, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap33:11.2'><span class='pli'>abhirūpā sudassanā; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap33:11.3'><span class='pli'>Itthisabbaṅgasampannā, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap33:11.4'><span class='pli'>abhijātā jutindharā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap33:12.1'><span class='pli'>Pacchime bhave sampatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap33:12.2'><span class='pli'>ajāyiṁ sākiye kule; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap33:12.3'><span class='pli'>Nārīsahassapāmokkhā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap33:12.4'><span class='pli'>suddhodanasutassahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap33:13.1'><span class='pli'>Nibbinditvā agārehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap33:13.2'><span class='pli'>pabbajiṁ anagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap33:13.3'><span class='pli'>Sattamīrattisampattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap33:13.4'><span class='pli'>catusaccamapāpuṇiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap33:14.1'><span class='pli'>Cīvaraṁ piṇḍapātañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap33:14.2'><span class='pli'>paccayaṁ sayanāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap33:14.3'><span class='pli'>Parimetuṁ na sakkomi, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap33:14.4'><span class='pli'>piṇḍapātassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap33:15.1'><span class='pli'>Yaṁ mayhaṁ pūritaṁ kammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap33:15.2'><span class='pli'>Kusalaṁ sarase muni; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap33:15.3'><span class='pli'>Tuyhatthāya mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap33:15.4'><span class='pli'>Paricattaṁ bahuṁ mayā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap33:16.1'><span class='pli'>Ekattiṁse ito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap33:16.2'><span class='pli'>yaṁ dānamadadiṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap33:16.3'><span class='pli'>Duggatiṁ nābhijānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap33:16.4'><span class='pli'>piṇḍapātassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap33:17.1'><span class='pli'>Dve gatiyo pajānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap33:17.2'><span class='pli'>devattaṁ atha mānusaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap33:17.3'><span class='pli'>Aññaṁ gatiṁ na jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap33:17.4'><span class='pli'>piṇḍapātassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap33:18.1'><span class='pli'>Ucce kule pajānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap33:18.2'><span class='pli'>mahāsāle mahaddhane; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap33:18.3'><span class='pli'>Aññe kule na jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap33:18.4'><span class='pli'>piṇḍapātassidaṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap33:19.1'><span class='pli'>Bhavābhave saṁsaritvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap33:19.2'><span class='pli'>sukkamūlena coditā; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap33:19.3'><span class='pli'>Amanāpaṁ na passāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap33:19.4'><span class='pli'>somanassakataṁ phalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap33:20.1'><span class='pli'>Iddhīsu ca vasī homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap33:20.2'><span class='pli'>dibbāya sotadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap33:20.3'><span class='pli'>Cetopariyañāṇassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap33:20.4'><span class='pli'>vasī homi mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap33:21.1'><span class='pli'>Pubbenivāsaṁ jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap33:21.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap33:21.3'><span class='pli'>Sabbāsavā parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap33:21.4'><span class='pli'>natthi dāni punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap33:22.1'><span class='pli'>Atthadhammaniruttīsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap33:22.2'><span class='pli'>paṭibhāne tatheva ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap33:22.3'><span class='pli'>Ñāṇaṁ mama mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap33:22.4'><span class='pli'>uppannaṁ tava santike. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap33:23.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap33:23.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap33:23.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap33:24.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap33:24.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap33:24.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='thi-ap33:25.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='thi-ap33:25.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='thi-ap33:25.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='26.1' data-uid='thi-ap33:26.1'><span class='pli'>Itthaṁ sudaṁ uppaladāyikā bhikkhunī bhagavato sammukhā imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='27.1' data-uid='thi-ap33:27.1'><span class='pli'>Uppaladāyikātheriyāpadānaṁ tatiyaṁ. </span></span></p></article>\n"
  },
  "thi-ap34": {
    "previous": "thi-ap33",
    "next": "thi-ap35",
    "content": "<article id='thi-ap34'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap34:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap34:0.2'><span class='pli'>Khattiyāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap34:0.3'><span class='pli'>4 Siṅgālamātutherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap34:1.1'><span class='pli'>“Padumuttaro nāma jino, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap34:1.2'><span class='pli'>sabbadhammāna pāragū; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap34:1.3'><span class='pli'>Ito satasahassamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap34:1.4'><span class='pli'>kappe uppajji nāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap34:2.1'><span class='pli'>Tadāhaṁ haṁsavatiyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap34:2.2'><span class='pli'>jātāmaccakule ahuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap34:2.3'><span class='pli'>Nānāratanapajjote, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap34:2.4'><span class='pli'>iddhe phīte mahaddhane. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap34:3.1'><span class='pli'>Pitunā saha gantvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap34:3.2'><span class='pli'>mahājanapurakkhatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap34:3.3'><span class='pli'>Dhammaṁ buddhassa sutvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap34:3.4'><span class='pli'>pabbajiṁ anagāriyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap34:4.1'><span class='pli'>Pabbajitvāna kāyena, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap34:4.2'><span class='pli'>pāpakammaṁ vivajjayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap34:4.3'><span class='pli'>Vacīduccaritaṁ hitvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap34:4.4'><span class='pli'>ājīvaṁ parisodhayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap34:5.1'><span class='pli'>Buddhe pasannā dhamme ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap34:5.2'><span class='pli'>saṅghe ca tibbagāravā; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap34:5.3'><span class='pli'>Saddhammassavane yuttā, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap34:5.4'><span class='pli'>buddhadassanalālasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap34:6.1'><span class='pli'>Aggaṁ saddhādhimuttānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap34:6.2'><span class='pli'>assosiṁ bhikkhuniṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap34:6.3'><span class='pli'>Taṁ ṭhānaṁ patthayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap34:6.4'><span class='pli'>tisso sikkhā apūrayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap34:7.1'><span class='pli'>Tato maṁ sugato āha, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap34:7.2'><span class='pli'>karuṇānugatāsayo; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap34:7.3'><span class='pli'>‘Yassa saddhā tathāgate, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap34:7.4'><span class='pli'>acalā suppatiṭṭhitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.5' data-uid='thi-ap34:7.5'><span class='pli'>Sīlañca yassa kalyāṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.6' data-uid='thi-ap34:7.6'><span class='pli'>ariyakantaṁ pasaṁsitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap34:8.1'><span class='pli'>Saṅghe pasādo yassatthi, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap34:8.2'><span class='pli'>ujubhūtañca dassanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap34:8.3'><span class='pli'>Adaliddoti taṁ āhu, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap34:8.4'><span class='pli'>amoghaṁ tassa jīvitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap34:9.1'><span class='pli'>Tasmā saddhañca sīlañca, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap34:9.2'><span class='pli'>pasādaṁ dhammadassanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap34:9.3'><span class='pli'>Anuyuñjetha medhāvī, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap34:9.4'><span class='pli'>saraṁ buddhāna sāsanaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap34:10.1'><span class='pli'>Taṁ sutvāhaṁ pamuditā, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap34:10.2'><span class='pli'>apucchiṁ paṇidhiṁ mama; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap34:10.3'><span class='pli'>Tadā anomo amito, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap34:10.4'><span class='pli'>byākarittha vināyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.5' data-uid='thi-ap34:10.5'><span class='pli'>‘Buddhe pasannā kalyāṇī, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.6' data-uid='thi-ap34:10.6'><span class='pli'>lacchase taṁ supatthitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap34:11.1'><span class='pli'>Satasahassito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap34:11.2'><span class='pli'>okkākakulasambhavo; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap34:11.3'><span class='pli'>Gotamo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap34:11.4'><span class='pli'>satthā loke bhavissati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap34:12.1'><span class='pli'>Tassa dhammesu dāyādā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap34:12.2'><span class='pli'>orasā dhammanimmitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap34:12.3'><span class='pli'>Siṅgālakassa mātāti, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap34:12.4'><span class='pli'>hessati satthu sāvikā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap34:13.1'><span class='pli'>Taṁ sutvā muditā hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap34:13.2'><span class='pli'>yāvajīvaṁ tadā jinaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap34:13.3'><span class='pli'>Mettacittā paricariṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap34:13.4'><span class='pli'>paṭipattīhi nāyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap34:14.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap34:14.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap34:14.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap34:14.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap34:15.1'><span class='pli'>Pacchime ca bhave dāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap34:15.2'><span class='pli'>giribbajapuruttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap34:15.3'><span class='pli'>Jātā seṭṭhikule phīte, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap34:15.4'><span class='pli'>mahāratanasañcaye. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap34:16.1'><span class='pli'>Putto siṅgālako nāma, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap34:16.2'><span class='pli'>mamāsi vipathe rato; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap34:16.3'><span class='pli'>Diṭṭhigahanapakkhando, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap34:16.4'><span class='pli'>disāpūjanatapparo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap34:17.1'><span class='pli'>Nānādisā namassantaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap34:17.2'><span class='pli'>piṇḍāya nagaraṁ vajaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap34:17.3'><span class='pli'>Taṁ disvā ovadī buddho, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap34:17.4'><span class='pli'>magge ṭhatvā vināyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap34:18.1'><span class='pli'>Tassa desayato dhammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap34:18.2'><span class='pli'>panādo vimhayo ahu; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap34:18.3'><span class='pli'>Dvekoṭinaranārīnaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap34:18.4'><span class='pli'>dhammābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap34:19.1'><span class='pli'>Tadāhaṁ parisaṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap34:19.2'><span class='pli'>sutvā sugatabhāsitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap34:19.3'><span class='pli'>Sotāpattiphalaṁ pattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap34:19.4'><span class='pli'>pabbajiṁ anagāriyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap34:20.1'><span class='pli'>Na cireneva kālena, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap34:20.2'><span class='pli'>buddhadassanalālasā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap34:20.3'><span class='pli'>Anussatiṁ taṁ bhāvetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap34:20.4'><span class='pli'>arahattamapāpuṇiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap34:21.1'><span class='pli'>Dassanatthāya buddhassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap34:21.2'><span class='pli'>sabbadā ca vajāmahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap34:21.3'><span class='pli'>Atittāyeva passāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap34:21.4'><span class='pli'>rūpaṁ nayananandanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap34:22.1'><span class='pli'>Sabbapāramisambhūtaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap34:22.2'><span class='pli'>lakkhīnilayanaṁ varaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap34:22.3'><span class='pli'>Rūpaṁ sabbasubhākiṇṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap34:22.4'><span class='pli'>atittā viharāmahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap34:23.1'><span class='pli'>Jino tasmiṁ guṇe tuṭṭho, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap34:23.2'><span class='pli'>etadagge ṭhapesi maṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap34:23.3'><span class='pli'>‘Siṅgālakassa yā mātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='thi-ap34:23.4'><span class='pli'>aggā saddhādhimuttikā’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap34:24.1'><span class='pli'>Iddhīsu ca vasī homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap34:24.2'><span class='pli'>dibbāya sotadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap34:24.3'><span class='pli'>Cetopariyañāṇassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='thi-ap34:24.4'><span class='pli'>vasī homi mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='thi-ap34:25.1'><span class='pli'>Pubbenivāsaṁ jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='thi-ap34:25.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='thi-ap34:25.3'><span class='pli'>Sabbāsavā parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='thi-ap34:25.4'><span class='pli'>natthi dāni punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='thi-ap34:26.1'><span class='pli'>Atthadhammaniruttīsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='thi-ap34:26.2'><span class='pli'>paṭibhāne tatheva ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='thi-ap34:26.3'><span class='pli'>Ñāṇaṁ mama mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='thi-ap34:26.4'><span class='pli'>uppannaṁ tava santike. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='thi-ap34:27.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='thi-ap34:27.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='thi-ap34:27.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='thi-ap34:28.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='thi-ap34:28.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='thi-ap34:28.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='thi-ap34:29.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='thi-ap34:29.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='thi-ap34:29.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='30.1' data-uid='thi-ap34:30.1'><span class='pli'>Itthaṁ sudaṁ siṅgālamātā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='31.1' data-uid='thi-ap34:31.1'><span class='pli'>Siṅgālamātutheriyāpadānaṁ catutthaṁ. </span></span></p></article>\n"
  },
  "thi-ap35": {
    "previous": "thi-ap34",
    "next": "thi-ap36",
    "content": "<article id='thi-ap35'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap35:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap35:0.2'><span class='pli'>Khattiyāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap35:0.3'><span class='pli'>Sukkātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap35:1.1'><span class='pli'>“Ekanavutito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap35:1.2'><span class='pli'>vipassī nāma nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap35:1.3'><span class='pli'>Uppajji cārudassano, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap35:1.4'><span class='pli'>sabbadhammavipassako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap35:2.1'><span class='pli'>Tadāhaṁ bandhumatiyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap35:2.2'><span class='pli'>jātā aññatare kule; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap35:2.3'><span class='pli'>Dhammaṁ sutvāna munino, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap35:2.4'><span class='pli'>pabbajiṁ anagāriyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap35:3.1'><span class='pli'>Bahussutā dhammadharā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap35:3.2'><span class='pli'>paṭibhānavatī tathā; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap35:3.3'><span class='pli'>Vicittakathikā cāpi, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap35:3.4'><span class='pli'>jinasāsanakārikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap35:4.1'><span class='pli'>Tadā dhammakathaṁ katvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap35:4.2'><span class='pli'>hitāya janataṁ bahuṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap35:4.3'><span class='pli'>Tato cutāhaṁ tusitaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap35:4.4'><span class='pli'>upapannā yasassinī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap35:5.1'><span class='pli'>Ekattiṁse ito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap35:5.2'><span class='pli'>sikhī viya sikhī jino; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap35:5.3'><span class='pli'>Tapanto yasasā loke, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap35:5.4'><span class='pli'>uppajji vadataṁ varo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap35:6.1'><span class='pli'>Tadāpi pabbajitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap35:6.2'><span class='pli'>buddhasāsanakovidā; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap35:6.3'><span class='pli'>Jotetvā jinavākyāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap35:6.4'><span class='pli'>tatopi tidivaṁ gatā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap35:7.1'><span class='pli'>Ekattiṁseva kappamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap35:7.2'><span class='pli'>vessabhū nāma nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap35:7.3'><span class='pli'>Uppajjittha mahāñāṇī, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap35:7.4'><span class='pli'>tadāpi ca tathevahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap35:8.1'><span class='pli'>Pabbajitvā dhammadharā, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap35:8.2'><span class='pli'>jotayiṁ jinasāsanaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap35:8.3'><span class='pli'>Gantvā marupuraṁ rammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap35:8.4'><span class='pli'>anubhosiṁ mahāsukhaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap35:9.1'><span class='pli'>Imamhi bhaddake kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap35:9.2'><span class='pli'>kakusandho jinuttamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap35:9.3'><span class='pli'>Uppajji narasaraṇo, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap35:9.4'><span class='pli'>tadāpi ca tathevahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap35:10.1'><span class='pli'>Pabbajitvā munimataṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap35:10.2'><span class='pli'>jotayitvā yathāyukaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap35:10.3'><span class='pli'>Tato cutāhaṁ tidivaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap35:10.4'><span class='pli'>agaṁ sabhavanaṁ yathā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap35:11.1'><span class='pli'>Imasmiṁyeva kappamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap35:11.2'><span class='pli'>koṇāgamananāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap35:11.3'><span class='pli'>Uppajji lokasaraṇo, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap35:11.4'><span class='pli'>araṇo amataṅgato. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap35:12.1'><span class='pli'>Tadāpi pabbajitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap35:12.2'><span class='pli'>sāsane tassa tādino; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap35:12.3'><span class='pli'>Bahussutā dhammadharā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap35:12.4'><span class='pli'>jotayiṁ jinasāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap35:13.1'><span class='pli'>Imasmiṁyeva kappamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap35:13.2'><span class='pli'>kassapo munimuttamo; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap35:13.3'><span class='pli'>Uppajji lokasaraṇo, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap35:13.4'><span class='pli'>araṇo maraṇantagū. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap35:14.1'><span class='pli'>Tassāpi naravīrassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap35:14.2'><span class='pli'>pabbajitvāna sāsane; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap35:14.3'><span class='pli'>Pariyāpuṭasaddhammā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap35:14.4'><span class='pli'>paripucchā visāradā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap35:15.1'><span class='pli'>Susīlā lajjinī ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap35:15.2'><span class='pli'>tīsu sikkhāsu kovidā; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap35:15.3'><span class='pli'>Bahuṁ dhammakathaṁ katvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap35:15.4'><span class='pli'>yāvajīvaṁ mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap35:16.1'><span class='pli'>Tena kammavipākena, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap35:16.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap35:16.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap35:16.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap35:17.1'><span class='pli'>Pacchime ca bhave dāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap35:17.2'><span class='pli'>giribbajapuruttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap35:17.3'><span class='pli'>Jātā seṭṭhikule phīte, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap35:17.4'><span class='pli'>mahāratanasañcaye. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap35:18.1'><span class='pli'>Yadā bhikkhusahassena, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap35:18.2'><span class='pli'>parivuto lokanāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap35:18.3'><span class='pli'>Upāgami rājagahaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap35:18.4'><span class='pli'>sahassakkhena vaṇṇito. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap35:19.1'><span class='pli'>Danto dantehi saha purāṇajaṭilehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap35:19.2'><span class='pli'>Vippamutto vippamuttehi; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap35:19.3'><span class='pli'>Siṅgīnikkhasavaṇṇo, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap35:19.4'><span class='pli'>Rājagahaṁ pāvisi bhagavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap35:20.1'><span class='pli'>Disvā buddhānubhāvaṁ taṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap35:20.2'><span class='pli'>sutvāva guṇasañcayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap35:20.3'><span class='pli'>Buddhe cittaṁ pasādetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap35:20.4'><span class='pli'>pūjayiṁ taṁ yathābalaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap35:21.1'><span class='pli'>Aparena ca kālena, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap35:21.2'><span class='pli'>dhammadinnāya santike; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap35:21.3'><span class='pli'>Agārā nikkhamitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap35:21.4'><span class='pli'>pabbajiṁ anagāriyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap35:22.1'><span class='pli'>Kesesu chijjamānesu, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap35:22.2'><span class='pli'>kilese jhāpayiṁ ahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap35:22.3'><span class='pli'>Uggahiṁ sāsanaṁ sabbaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap35:22.4'><span class='pli'>pabbajitvā cirenahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap35:23.1'><span class='pli'>Tato dhammamadesesiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap35:23.2'><span class='pli'>mahājanasamāgame; </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap35:23.3'><span class='pli'>Dhamme desiyamānamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.4' data-uid='thi-ap35:23.4'><span class='pli'>dhammābhisamayo ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap35:24.1'><span class='pli'>Nekapāṇasahassānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap35:24.2'><span class='pli'>taṁ viditvātivimhito; </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap35:24.3'><span class='pli'>Abhippasanno me yakkho, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.4' data-uid='thi-ap35:24.4'><span class='pli'>bhamitvāna giribbajaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='thi-ap35:25.1'><span class='pli'>Kiṁ me katā rājagahe manussā, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='thi-ap35:25.2'><span class='pli'>Madhuṁ pītāva acchare; </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='thi-ap35:25.3'><span class='pli'>Ye sukkaṁ na upāsanti, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.4' data-uid='thi-ap35:25.4'><span class='pli'>Desentiṁ amataṁ padaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='26.1' data-uid='thi-ap35:26.1'><span class='pli'>Tañca appaṭivānīyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.2' data-uid='thi-ap35:26.2'><span class='pli'>asecanakamojavaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='26.3' data-uid='thi-ap35:26.3'><span class='pli'>Pivanti maññe sappaññā, </span></span></span>\n<span class='verse-line'><span class='segment' id='26.4' data-uid='thi-ap35:26.4'><span class='pli'>valāhakamivaddhagū. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='27.1' data-uid='thi-ap35:27.1'><span class='pli'>Iddhīsu ca vasī homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.2' data-uid='thi-ap35:27.2'><span class='pli'>dibbāya sotadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='27.3' data-uid='thi-ap35:27.3'><span class='pli'>Cetopariyañāṇassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='27.4' data-uid='thi-ap35:27.4'><span class='pli'>vasī homi mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='28.1' data-uid='thi-ap35:28.1'><span class='pli'>Pubbenivāsaṁ jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.2' data-uid='thi-ap35:28.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='28.3' data-uid='thi-ap35:28.3'><span class='pli'>Sabbāsavā parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='28.4' data-uid='thi-ap35:28.4'><span class='pli'>natthi dāni punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='29.1' data-uid='thi-ap35:29.1'><span class='pli'>Atthadhammaniruttīsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.2' data-uid='thi-ap35:29.2'><span class='pli'>paṭibhāne tatheva ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='29.3' data-uid='thi-ap35:29.3'><span class='pli'>Ñāṇaṁ mama mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='29.4' data-uid='thi-ap35:29.4'><span class='pli'>uppannaṁ tava santike. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='30.1' data-uid='thi-ap35:30.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='30.2' data-uid='thi-ap35:30.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='30.3' data-uid='thi-ap35:30.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='31.1' data-uid='thi-ap35:31.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='31.2' data-uid='thi-ap35:31.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='31.3' data-uid='thi-ap35:31.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='32.1' data-uid='thi-ap35:32.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='32.2' data-uid='thi-ap35:32.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='32.3' data-uid='thi-ap35:32.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='33.1' data-uid='thi-ap35:33.1'><span class='pli'>Itthaṁ sudaṁ sukkā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='34.1' data-uid='thi-ap35:34.1'><span class='pli'>Sukkātheriyāpadānaṁ pañcamaṁ. </span></span><br>\n<span class='segment' id='34.2' data-uid='thi-ap35:34.2'><span class='pli'>Pañcamaṁ bhāṇavāraṁ. </span></span></p></article>\n"
  },
  "thi-ap36": {
    "previous": "thi-ap35",
    "next": "thi-ap37",
    "content": "<article id='thi-ap36'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap36:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap36:0.2'><span class='pli'>Khattiyāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap36:0.3'><span class='pli'>6 Abhirūpanandātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap36:1.1'><span class='pli'>“Ekanavutito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap36:1.2'><span class='pli'>vipassī nāma nāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap36:1.3'><span class='pli'>Uppajji cārudassano, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap36:1.4'><span class='pli'>sabbadhammesu cakkhumā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap36:2.1'><span class='pli'>Tadāhaṁ bandhumatiyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap36:2.2'><span class='pli'>iddhe phīte mahākule; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap36:2.3'><span class='pli'>Jātā surūpā dayitā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap36:2.4'><span class='pli'>pūjanīyā janassa ca. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap36:3.1'><span class='pli'>Upagantvā mahāvīraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap36:3.2'><span class='pli'>vipassiṁ lokanāyakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap36:3.3'><span class='pli'>Dhammaṁ suṇitvā saraṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap36:3.4'><span class='pli'>upesiṁ naranāyakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap36:4.1'><span class='pli'>Sīlesu saṁvutā hutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap36:4.2'><span class='pli'>nibbute ca naruttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap36:4.3'><span class='pli'>Dhātuthūpassa upari, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap36:4.4'><span class='pli'>soṇṇacchattamapūjayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap36:5.1'><span class='pli'>Muttacāgā sīlavatī, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap36:5.2'><span class='pli'>yāvajīvaṁ tato cutā; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap36:5.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap36:5.4'><span class='pli'>tāvatiṁsūpagā ahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap36:6.1'><span class='pli'>Tadā dasahi ṭhānehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap36:6.2'><span class='pli'>adhibhotvāna sesake; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap36:6.3'><span class='pli'>Rūpasaddehi gandhehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap36:6.4'><span class='pli'>rasehi phusanehi ca. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap36:7.1'><span class='pli'>Āyunāpi ca vaṇṇena, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap36:7.2'><span class='pli'>sukhena yasasāpi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap36:7.3'><span class='pli'>Tathevādhipateyyena, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap36:7.4'><span class='pli'>adhigayha virocahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap36:8.1'><span class='pli'>Pacchime ca bhave dāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap36:8.2'><span class='pli'>jātāhaṁ kapilavhaye; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap36:8.3'><span class='pli'>Dhītā khemakasakkassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap36:8.4'><span class='pli'>nandā nāmāti vissutā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap36:9.1'><span class='pli'>Abhirūpasampadampi, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap36:9.2'><span class='pli'>ahu me kantisūcakaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap36:9.3'><span class='pli'>Yadāhaṁ yobbanappattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap36:9.4'><span class='pli'>rūpalāvaññabhūsitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap36:10.1'><span class='pli'>Tadā mamatthe sakyānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap36:10.2'><span class='pli'>vivādo sumahā ahu; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap36:10.3'><span class='pli'>Pabbājesi tato tāto, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap36:10.4'><span class='pli'>‘mā sakyā vinassiṁsu’ti. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap36:11.1'><span class='pli'>Pabbajitvā tathāgataṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap36:11.2'><span class='pli'>rūpadessiṁ naruttamaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap36:11.3'><span class='pli'>Sutvāna nopagacchāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap36:11.4'><span class='pli'>mama rūpena gabbitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap36:12.1'><span class='pli'>Ovādampi na gacchāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap36:12.2'><span class='pli'>buddhadassanabhīrutā; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap36:12.3'><span class='pli'>Tadā jino upāyena, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap36:12.4'><span class='pli'>upanetvā sasantikaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap36:13.1'><span class='pli'>Tissitthiyo nidassesi, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap36:13.2'><span class='pli'>iddhiyā maggakovido; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap36:13.3'><span class='pli'>Accharārūpasadisaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap36:13.4'><span class='pli'>taruṇiṁ jaritaṁ mataṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap36:14.1'><span class='pli'>Tāyo disvā susaṁviggā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap36:14.2'><span class='pli'>virattāse kaḷevare; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap36:14.3'><span class='pli'>Aṭṭhāsiṁ bhavanibbindā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap36:14.4'><span class='pli'>tadā maṁ āha nāyako. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap36:15.1'><span class='pli'>‘Āturaṁ asuciṁ pūtiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap36:15.2'><span class='pli'>passa nande samussayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap36:15.3'><span class='pli'>Uggharantaṁ paggharantaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap36:15.4'><span class='pli'>bālānaṁ abhinanditaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap36:16.1'><span class='pli'>Asubhāya cittaṁ bhāvehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap36:16.2'><span class='pli'>ekaggaṁ susamāhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap36:16.3'><span class='pli'>Yathā idaṁ tathā etaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap36:16.4'><span class='pli'>yathā etaṁ tathā idaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap36:17.1'><span class='pli'>Evametaṁ avekkhantī, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap36:17.2'><span class='pli'>rattindivamatanditā; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap36:17.3'><span class='pli'>Tato sakāya paññāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap36:17.4'><span class='pli'>abhinibbijjha vacchasi’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap36:18.1'><span class='pli'>Tassā me appamattāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap36:18.2'><span class='pli'>vicarantiyā yoniso; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap36:18.3'><span class='pli'>Yathābhūtaṁ ayaṁ kāyo, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap36:18.4'><span class='pli'>diṭṭho santarabāhiro. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap36:19.1'><span class='pli'>Atha nibbindahaṁ kāye, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap36:19.2'><span class='pli'>ajjhattañca virajjahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap36:19.3'><span class='pli'>Appamattā visaṁyuttā, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap36:19.4'><span class='pli'>upasantāmhi nibbutā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap36:20.1'><span class='pli'>Iddhīsu ca vasī homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap36:20.2'><span class='pli'>dibbāya sotadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap36:20.3'><span class='pli'>Cetopariyañāṇassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.4' data-uid='thi-ap36:20.4'><span class='pli'>vasī homi mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap36:21.1'><span class='pli'>Pubbenivāsaṁ jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap36:21.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap36:21.3'><span class='pli'>Sabbāsavā parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap36:21.4'><span class='pli'>natthi dāni punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap36:22.1'><span class='pli'>Atthadhammaniruttīsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap36:22.2'><span class='pli'>paṭibhāne tatheva ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap36:22.3'><span class='pli'>Ñāṇaṁ mama mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap36:22.4'><span class='pli'>uppannaṁ tava santike. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='23.1' data-uid='thi-ap36:23.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='23.2' data-uid='thi-ap36:23.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='23.3' data-uid='thi-ap36:23.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='24.1' data-uid='thi-ap36:24.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='24.2' data-uid='thi-ap36:24.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='24.3' data-uid='thi-ap36:24.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='25.1' data-uid='thi-ap36:25.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='25.2' data-uid='thi-ap36:25.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='25.3' data-uid='thi-ap36:25.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='26.1' data-uid='thi-ap36:26.1'><span class='pli'>Itthaṁ sudaṁ abhirūpanandā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='27.1' data-uid='thi-ap36:27.1'><span class='pli'>Abhirūpanandātheriyāpadānaṁ chaṭṭhaṁ. </span></span></p></article>\n"
  },
  "thi-ap37": {
    "previous": "thi-ap36",
    "next": "thi-ap38",
    "content": "<article id='thi-ap37'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap37:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap37:0.2'><span class='pli'>Khattiyāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap37:0.3'><span class='pli'>7 Aḍḍhakāsitherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap37:1.1'><span class='pli'>“Imamhi bhaddake kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap37:1.2'><span class='pli'>brahmabandhu mahāyaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap37:1.3'><span class='pli'>Kassapo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap37:1.4'><span class='pli'>uppajji vadataṁ varo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap37:2.1'><span class='pli'>Tadāhaṁ pabbajitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap37:2.2'><span class='pli'>tassa buddhassa sāsane; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap37:2.3'><span class='pli'>Saṁvutā pātimokkhamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap37:2.4'><span class='pli'>indriyesu ca pañcasu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap37:3.1'><span class='pli'>Mattaññunī ca asane, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap37:3.2'><span class='pli'>yuttā jāgariyepi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap37:3.3'><span class='pli'>Vasantī yuttayogāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap37:3.4'><span class='pli'>bhikkhuniṁ vigatāsavaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap37:4.1'><span class='pli'>Akkosiṁ duṭṭhacittāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap37:4.2'><span class='pli'>gaṇiketi bhaṇiṁ tadā; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap37:4.3'><span class='pli'>Tena pāpena kammena, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap37:4.4'><span class='pli'>nirayamhi apaccisaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap37:5.1'><span class='pli'>Tena kammāvasesena, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap37:5.2'><span class='pli'>ajāyiṁ gaṇikākule; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap37:5.3'><span class='pli'>Bahusova parādhīnā, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap37:5.4'><span class='pli'>pacchimāya ca jātiyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap37:6.1'><span class='pli'>Kāsīsu seṭṭhikulajā, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap37:6.2'><span class='pli'>brahmacārībalenahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap37:6.3'><span class='pli'>Accharā viya devesu, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap37:6.4'><span class='pli'>ahosiṁ rūpasampadā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap37:7.1'><span class='pli'>Disvāna dassanīyaṁ maṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap37:7.2'><span class='pli'>giribbajapuruttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap37:7.3'><span class='pli'>Gaṇikatte nivesesuṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap37:7.4'><span class='pli'>akkosanabalena me. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap37:8.1'><span class='pli'>Sāhaṁ sutvāna saddhammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap37:8.2'><span class='pli'>buddhaseṭṭhena desitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap37:8.3'><span class='pli'>Pubbavāsanasampannā, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap37:8.4'><span class='pli'>pabbajiṁ anagāriyaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap37:9.1'><span class='pli'>Tadūpasampadatthāya, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap37:9.2'><span class='pli'>gacchantī jinasantikaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap37:9.3'><span class='pli'>Magge dhutte ṭhite sutvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap37:9.4'><span class='pli'>labhiṁ dūtopasampadaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap37:10.1'><span class='pli'>Sabbakammaṁ parikkhīṇaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap37:10.2'><span class='pli'>puññaṁ pāpaṁ tatheva ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap37:10.3'><span class='pli'>Sabbasaṁsāramuttiṇṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap37:10.4'><span class='pli'>gaṇikattañca khepitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap37:11.1'><span class='pli'>Iddhīsu ca vasī homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap37:11.2'><span class='pli'>dibbāya sotadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap37:11.3'><span class='pli'>Cetopariyañāṇassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap37:11.4'><span class='pli'>vasī homi mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap37:12.1'><span class='pli'>Pubbenivāsaṁ jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap37:12.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap37:12.3'><span class='pli'>Sabbāsavā parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap37:12.4'><span class='pli'>natthi dāni punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap37:13.1'><span class='pli'>Atthadhammaniruttīsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap37:13.2'><span class='pli'>paṭibhāne tatheva ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap37:13.3'><span class='pli'>Ñāṇaṁ mama mahāvīra, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap37:13.4'><span class='pli'>uppannaṁ tava santike. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap37:14.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap37:14.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap37:14.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap37:15.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap37:15.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap37:15.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap37:16.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap37:16.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap37:16.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='17.1' data-uid='thi-ap37:17.1'><span class='pli'>Itthaṁ sudaṁ aḍḍhakāsi bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='18.1' data-uid='thi-ap37:18.1'><span class='pli'>Aḍḍhakāsitheriyāpadānaṁ sattamaṁ. </span></span></p></article>\n"
  },
  "thi-ap38": {
    "previous": "thi-ap37",
    "next": "thi-ap39",
    "content": "<article id='thi-ap38'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap38:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap38:0.2'><span class='pli'>Khattiyāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap38:0.3'><span class='pli'>8 Puṇṇikātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap38:1.1'><span class='pli'>“Vipassino bhagavato, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap38:1.2'><span class='pli'>sikhino vessabhussa ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap38:1.3'><span class='pli'>Kakusandhassa munino, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap38:1.4'><span class='pli'>koṇāgamanatādino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap38:2.1'><span class='pli'>Kassapassa ca buddhassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap38:2.2'><span class='pli'>pabbajitvāna sāsane; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap38:2.3'><span class='pli'>Bhikkhunī sīlasampannā, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap38:2.4'><span class='pli'>nipakā saṁvutindriyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap38:3.1'><span class='pli'>Bahussutā dhammadharā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap38:3.2'><span class='pli'>dhammatthapaṭipucchikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap38:3.3'><span class='pli'>Uggahetā ca dhammānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap38:3.4'><span class='pli'>sotā payirupāsitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap38:4.1'><span class='pli'>Desentī janamajjhehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap38:4.2'><span class='pli'>ahosiṁ jinasāsane; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap38:4.3'><span class='pli'>Bāhusaccena tenāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap38:4.4'><span class='pli'>pesalā atimaññisaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap38:5.1'><span class='pli'>Pacchime ca bhave dāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap38:5.2'><span class='pli'>sāvatthiyaṁ puruttame; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap38:5.3'><span class='pli'>Anāthapiṇḍino gehe, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap38:5.4'><span class='pli'>jātāhaṁ kumbhadāsiyā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap38:6.1'><span class='pli'>Gatā udakahāriyaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap38:6.2'><span class='pli'>sotthiyaṁ dijamaddasaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap38:6.3'><span class='pli'>Sītaṭṭaṁ toyamajjhamhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap38:6.4'><span class='pli'>taṁ disvā idamabraviṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap38:7.1'><span class='pli'>‘Udahārī ahaṁ sīte, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap38:7.2'><span class='pli'>sadā udakamotariṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap38:7.3'><span class='pli'>Ayyānaṁ daṇḍabhayabhītā, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap38:7.4'><span class='pli'>vācādosabhayaṭṭitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap38:8.1'><span class='pli'>Kassa brāhmaṇa tvaṁ bhīto, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap38:8.2'><span class='pli'>sadā udakamotari; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap38:8.3'><span class='pli'>Vedhamānehi gattehi, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap38:8.4'><span class='pli'>sītaṁ vedayase bhusaṁ’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap38:9.1'><span class='pli'>‘Jānantī vata maṁ bhoti, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap38:9.2'><span class='pli'>puṇṇike paripucchasi; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap38:9.3'><span class='pli'>Karontaṁ kusalaṁ kammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap38:9.4'><span class='pli'>rundhantaṁ katapāpakaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap38:10.1'><span class='pli'>Yo ce vuḍḍho daharo vā, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap38:10.2'><span class='pli'>pāpakammaṁ pakubbati; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap38:10.3'><span class='pli'>Dakābhisiñcanā sopi, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap38:10.4'><span class='pli'>pāpakammā pamuccati’. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap38:11.1'><span class='pli'>Uttarantassa akkhāsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap38:11.2'><span class='pli'>dhammatthasaṁhitaṁ padaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap38:11.3'><span class='pli'>Tañca sutvā sa saṁviggo, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap38:11.4'><span class='pli'>pabbajitvārahā ahu. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap38:12.1'><span class='pli'>Pūrentī ūnakasataṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap38:12.2'><span class='pli'>jātā dāsikule yato; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap38:12.3'><span class='pli'>Tato puṇṇāti nāmaṁ me, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap38:12.4'><span class='pli'>bhujissaṁ maṁ akaṁsu te. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap38:13.1'><span class='pli'>Seṭṭhiṁ tatonujānetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap38:13.2'><span class='pli'>pabbajiṁ anagāriyaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap38:13.3'><span class='pli'>Na cireneva kālena, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap38:13.4'><span class='pli'>arahattamapāpuṇiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap38:14.1'><span class='pli'>Iddhīsu ca vasī homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap38:14.2'><span class='pli'>dibbāya sotadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap38:14.3'><span class='pli'>Cetopariyañāṇassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap38:14.4'><span class='pli'>vasī homi mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap38:15.1'><span class='pli'>Pubbenivāsaṁ jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap38:15.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap38:15.3'><span class='pli'>Sabbāsavā parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.4' data-uid='thi-ap38:15.4'><span class='pli'>natthi dāni punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap38:16.1'><span class='pli'>Atthadhammaniruttīsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap38:16.2'><span class='pli'>paṭibhāne tatheva ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap38:16.3'><span class='pli'>Ñāṇaṁ me vimalaṁ suddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.4' data-uid='thi-ap38:16.4'><span class='pli'>buddhaseṭṭhassa vāhasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='17.1' data-uid='thi-ap38:17.1'><span class='pli'>Bhāvanāya mahāpaññā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.2' data-uid='thi-ap38:17.2'><span class='pli'>suteneva sutāvinī; </span></span></span>\n<span class='verse-line'><span class='segment' id='17.3' data-uid='thi-ap38:17.3'><span class='pli'>Mānena nīcakulajā, </span></span></span>\n<span class='verse-line'><span class='segment' id='17.4' data-uid='thi-ap38:17.4'><span class='pli'>na hi kammaṁ vinassati. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap38:18.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap38:18.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap38:18.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap38:19.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap38:19.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap38:19.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='20.1' data-uid='thi-ap38:20.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='20.2' data-uid='thi-ap38:20.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='20.3' data-uid='thi-ap38:20.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='21.1' data-uid='thi-ap38:21.1'><span class='pli'>Itthaṁ sudaṁ puṇṇikā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='22.1' data-uid='thi-ap38:22.1'><span class='pli'>Puṇṇikātheriyāpadānaṁ aṭṭhamaṁ. </span></span></p></article>\n"
  },
  "thi-ap39": {
    "previous": "thi-ap38",
    "next": "thi-ap40",
    "content": "<article id='thi-ap39'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap39:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap39:0.2'><span class='pli'>Khattiyāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap39:0.3'><span class='pli'>9 Ambapālitherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap39:1.1'><span class='pli'>“Yo raṁsiphusitāveḷo, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap39:1.2'><span class='pli'>phusso nāma mahāmuni; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap39:1.3'><span class='pli'>Tassāhaṁ bhaginī āsiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap39:1.4'><span class='pli'>ajāyiṁ khattiye kule. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap39:2.1'><span class='pli'>Tassa dhammaṁ suṇitvāhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap39:2.2'><span class='pli'>vippasannena cetasā; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap39:2.3'><span class='pli'>Mahādānaṁ daditvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap39:2.4'><span class='pli'>patthayiṁ rūpasampadaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap39:3.1'><span class='pli'>Ekattiṁse ito kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap39:3.2'><span class='pli'>sikhī lokagganāyako; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap39:3.3'><span class='pli'>Uppanno lokapajjoto, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap39:3.4'><span class='pli'>tilokasaraṇo jino. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap39:4.1'><span class='pli'>Tadāruṇapure ramme, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap39:4.2'><span class='pli'>brāhmaññakulasambhavā; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap39:4.3'><span class='pli'>Vimuttacittaṁ kupitā, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap39:4.4'><span class='pli'>bhikkhuniṁ abhisāpayiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap39:5.1'><span class='pli'>‘Vesikāva anācārā’, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap39:5.2'><span class='pli'>jinasāsanadūsikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap39:5.3'><span class='pli'>Evaṁ akkosayitvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap39:5.4'><span class='pli'>tena pāpena kammunā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap39:6.1'><span class='pli'>Dāruṇaṁ nirayaṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap39:6.2'><span class='pli'>mahādukkhasamappitā; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap39:6.3'><span class='pli'>Tato cutā manussesu, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap39:6.4'><span class='pli'>upapannā tapassinī. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap39:7.1'><span class='pli'>Dasajātisahassāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap39:7.2'><span class='pli'>gaṇikattamakārayiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap39:7.3'><span class='pli'>Tamhā pāpā na muccissaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap39:7.4'><span class='pli'>bhutvā duṭṭhavisaṁ yathā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap39:8.1'><span class='pli'>Brahmacariyamasevissaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap39:8.2'><span class='pli'>kassape jinasāsane; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap39:8.3'><span class='pli'>Tena kammavipākena, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap39:8.4'><span class='pli'>ajāyiṁ tidase pure. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap39:9.1'><span class='pli'>Pacchime bhave sampatte, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap39:9.2'><span class='pli'>ahosiṁ opapātikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap39:9.3'><span class='pli'>Ambasākhantare jātā, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap39:9.4'><span class='pli'>ambapālīti tenahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap39:10.1'><span class='pli'>Parivutā pāṇakoṭīhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap39:10.2'><span class='pli'>pabbajiṁ jinasāsane; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap39:10.3'><span class='pli'>Pattāhaṁ acalaṁ ṭhānaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap39:10.4'><span class='pli'>dhītā buddhassa orasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap39:11.1'><span class='pli'>Iddhīsu ca vasī homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap39:11.2'><span class='pli'>sotadhātuvisuddhiyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap39:11.3'><span class='pli'>Cetopariyañāṇassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap39:11.4'><span class='pli'>vasī homi mahāmuni. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap39:12.1'><span class='pli'>Pubbenivāsaṁ jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap39:12.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap39:12.3'><span class='pli'>Sabbāsavā parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap39:12.4'><span class='pli'>natthi dāni punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap39:13.1'><span class='pli'>Atthadhammaniruttīsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap39:13.2'><span class='pli'>paṭibhāne tatheva ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap39:13.3'><span class='pli'>Ñāṇaṁ me vimalaṁ suddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap39:13.4'><span class='pli'>buddhaseṭṭhassa vāhasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap39:14.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap39:14.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap39:14.3'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='15.1' data-uid='thi-ap39:15.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='15.2' data-uid='thi-ap39:15.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='15.3' data-uid='thi-ap39:15.3'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='16.1' data-uid='thi-ap39:16.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='16.2' data-uid='thi-ap39:16.2'><span class='pli'>…pe… </span></span></span>\n<span class='verse-line'><span class='segment' id='16.3' data-uid='thi-ap39:16.3'><span class='pli'>kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='17.1' data-uid='thi-ap39:17.1'><span class='pli'>Itthaṁ sudaṁ ambapāli bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='18.1' data-uid='thi-ap39:18.1'><span class='pli'>Ambapālitheriyāpadānaṁ navamaṁ. </span></span></p></article>\n"
  },
  "thi-ap40": {
    "previous": "thi-ap39",
    "next": null,
    "content": "<article id='thi-ap40'><header><ul><li class='division'><span class='segment' id='0.1' data-uid='thi-ap40:0.1'><span class='pli'>Therīapadāna </span></span></li>\n<li><span class='segment' id='0.2' data-uid='thi-ap40:0.2'><span class='pli'>Khattiyāvagga </span></span></li></ul>\n<h1 class='sutta-title'><span class='segment' id='0.3' data-uid='thi-ap40:0.3'><span class='pli'>10 Pesalātherīapadāna </span></span></h1></header>\n<blockquote class='gatha'><p><span class='verse-line'><span class='segment' id='1.1' data-uid='thi-ap40:1.1'><span class='pli'>“Imamhi bhaddake kappe, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.2' data-uid='thi-ap40:1.2'><span class='pli'>brahmabandhu mahāyaso; </span></span></span>\n<span class='verse-line'><span class='segment' id='1.3' data-uid='thi-ap40:1.3'><span class='pli'>Kassapo nāma gottena, </span></span></span>\n<span class='verse-line'><span class='segment' id='1.4' data-uid='thi-ap40:1.4'><span class='pli'>uppajji vadataṁ varo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='2.1' data-uid='thi-ap40:2.1'><span class='pli'>Sāvatthiyaṁ pure vare, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.2' data-uid='thi-ap40:2.2'><span class='pli'>upāsakakule ahaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='2.3' data-uid='thi-ap40:2.3'><span class='pli'>Pasūtā taṁ jinavaraṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='2.4' data-uid='thi-ap40:2.4'><span class='pli'>disvā sutvā ca desanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='3.1' data-uid='thi-ap40:3.1'><span class='pli'>Taṁ vīraṁ saraṇaṁ gantvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.2' data-uid='thi-ap40:3.2'><span class='pli'>sīlāni ca samādiyiṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='3.3' data-uid='thi-ap40:3.3'><span class='pli'>Kadāci so mahāvīro, </span></span></span>\n<span class='verse-line'><span class='segment' id='3.4' data-uid='thi-ap40:3.4'><span class='pli'>mahājanasamāgame. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='4.1' data-uid='thi-ap40:4.1'><span class='pli'>Attano abhisambodhiṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.2' data-uid='thi-ap40:4.2'><span class='pli'>pakāsesi narāsabho; </span></span></span>\n<span class='verse-line'><span class='segment' id='4.3' data-uid='thi-ap40:4.3'><span class='pli'>Ananussutadhammesu, </span></span></span>\n<span class='verse-line'><span class='segment' id='4.4' data-uid='thi-ap40:4.4'><span class='pli'>pubbe dukkhādikesu ca. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='5.1' data-uid='thi-ap40:5.1'><span class='pli'>Cakkhu ñāṇañca paññā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.2' data-uid='thi-ap40:5.2'><span class='pli'>vijjāloko ca āsi me; </span></span></span>\n<span class='verse-line'><span class='segment' id='5.3' data-uid='thi-ap40:5.3'><span class='pli'>Taṁ sutvā uggahetvāna, </span></span></span>\n<span class='verse-line'><span class='segment' id='5.4' data-uid='thi-ap40:5.4'><span class='pli'>paripucchiñca bhikkhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='6.1' data-uid='thi-ap40:6.1'><span class='pli'>Tena kammena sukatena, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.2' data-uid='thi-ap40:6.2'><span class='pli'>cetanāpaṇidhīhi ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='6.3' data-uid='thi-ap40:6.3'><span class='pli'>Jahitvā mānusaṁ dehaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='6.4' data-uid='thi-ap40:6.4'><span class='pli'>tāvatiṁsamagacchahaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='7.1' data-uid='thi-ap40:7.1'><span class='pli'>Pacchime ca bhave dāni, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.2' data-uid='thi-ap40:7.2'><span class='pli'>Jātā seṭṭhimahākule; </span></span></span>\n<span class='verse-line'><span class='segment' id='7.3' data-uid='thi-ap40:7.3'><span class='pli'>Upecca buddhaṁ saddhammaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='7.4' data-uid='thi-ap40:7.4'><span class='pli'>Sutvā saccūpasaṁhitaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='8.1' data-uid='thi-ap40:8.1'><span class='pli'>Pabbajitvācireneva, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.2' data-uid='thi-ap40:8.2'><span class='pli'>saccatthāni vicintayaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='8.3' data-uid='thi-ap40:8.3'><span class='pli'>Khepetvā āsave sabbe, </span></span></span>\n<span class='verse-line'><span class='segment' id='8.4' data-uid='thi-ap40:8.4'><span class='pli'>arahattamapāpuṇiṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='9.1' data-uid='thi-ap40:9.1'><span class='pli'>Iddhīsu ca vasī homi, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.2' data-uid='thi-ap40:9.2'><span class='pli'>dibbāya sotadhātuyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='9.3' data-uid='thi-ap40:9.3'><span class='pli'>Cetopariyañāṇassa, </span></span></span>\n<span class='verse-line'><span class='segment' id='9.4' data-uid='thi-ap40:9.4'><span class='pli'>vasī homi mahāmune. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='10.1' data-uid='thi-ap40:10.1'><span class='pli'>Pubbenivāsaṁ jānāmi, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.2' data-uid='thi-ap40:10.2'><span class='pli'>dibbacakkhu visodhitaṁ; </span></span></span>\n<span class='verse-line'><span class='segment' id='10.3' data-uid='thi-ap40:10.3'><span class='pli'>Sabbāsavā parikkhīṇā, </span></span></span>\n<span class='verse-line'><span class='segment' id='10.4' data-uid='thi-ap40:10.4'><span class='pli'>natthi dāni punabbhavo. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='11.1' data-uid='thi-ap40:11.1'><span class='pli'>Atthadhammaniruttīsu, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.2' data-uid='thi-ap40:11.2'><span class='pli'>paṭibhāne tatheva ca; </span></span></span>\n<span class='verse-line'><span class='segment' id='11.3' data-uid='thi-ap40:11.3'><span class='pli'>Ñāṇaṁ me vimalaṁ suddhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='11.4' data-uid='thi-ap40:11.4'><span class='pli'>buddhaseṭṭhassa vāhasā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='12.1' data-uid='thi-ap40:12.1'><span class='pli'>Kilesā jhāpitā mayhaṁ, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.2' data-uid='thi-ap40:12.2'><span class='pli'>bhavā sabbe samūhatā; </span></span></span>\n<span class='verse-line'><span class='segment' id='12.3' data-uid='thi-ap40:12.3'><span class='pli'>Nāgīva bandhanaṁ chetvā, </span></span></span>\n<span class='verse-line'><span class='segment' id='12.4' data-uid='thi-ap40:12.4'><span class='pli'>viharāmi anāsavā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='13.1' data-uid='thi-ap40:13.1'><span class='pli'>Svāgataṁ vata me āsi, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.2' data-uid='thi-ap40:13.2'><span class='pli'>mama buddhassa santike; </span></span></span>\n<span class='verse-line'><span class='segment' id='13.3' data-uid='thi-ap40:13.3'><span class='pli'>Tisso vijjā anuppattā, </span></span></span>\n<span class='verse-line'><span class='segment' id='13.4' data-uid='thi-ap40:13.4'><span class='pli'>kataṁ buddhassa sāsanaṁ. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='14.1' data-uid='thi-ap40:14.1'><span class='pli'>Paṭisambhidā catasso, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.2' data-uid='thi-ap40:14.2'><span class='pli'>Vimokkhāpi ca aṭṭhime; </span></span></span>\n<span class='verse-line'><span class='segment' id='14.3' data-uid='thi-ap40:14.3'><span class='pli'>Chaḷabhiññā sacchikatā, </span></span></span>\n<span class='verse-line'><span class='segment' id='14.4' data-uid='thi-ap40:14.4'><span class='pli'>Kataṁ buddhassa sāsanaṁ”. </span></span></span></p></blockquote>\n<p><span class='segment' id='15.1' data-uid='thi-ap40:15.1'><span class='pli'>Itthaṁ sudaṁ pesalā bhikkhunī imā gāthāyo abhāsitthāti. </span></span></p>\n<p class='endsutta'><span class='segment' id='16.1' data-uid='thi-ap40:16.1'><span class='pli'>Pesalātheriyāpadānaṁ dasamaṁ. </span></span></p>\n<p class='endsection'><span class='segment' id='16.2' data-uid='thi-ap40:16.2'><span class='pli'>Khattiyāvaggo catuttho. </span></span></p>\n<p class='uddana-intro'><span class='segment' id='17.1' data-uid='thi-ap40:17.1'><span class='pli'>Tassuddānaṁ </span></span></p>\n<blockquote class='uddanagatha'><p><span class='verse-line'><span class='segment' id='18.1' data-uid='thi-ap40:18.1'><span class='pli'>Khattiyā brāhmaṇī ceva, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.2' data-uid='thi-ap40:18.2'><span class='pli'>tathā uppaladāyikā; </span></span></span>\n<span class='verse-line'><span class='segment' id='18.3' data-uid='thi-ap40:18.3'><span class='pli'>Siṅgālamātā sukkā ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='18.4' data-uid='thi-ap40:18.4'><span class='pli'>abhirūpā aḍḍhakāsikā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='19.1' data-uid='thi-ap40:19.1'><span class='pli'>Puṇṇā ca ambapālī ca, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.2' data-uid='thi-ap40:19.2'><span class='pli'>pesalāti ca tā dasa; </span></span></span>\n<span class='verse-line'><span class='segment' id='19.3' data-uid='thi-ap40:19.3'><span class='pli'>Gāthāyo dvisatānettha, </span></span></span>\n<span class='verse-line'><span class='segment' id='19.4' data-uid='thi-ap40:19.4'><span class='pli'>dvicattālīsa cuttari. </span></span></span></p></blockquote>\n<p class='uddana-intro'><span class='segment' id='20.1' data-uid='thi-ap40:20.1'><span class='pli'>Atha vagguddānaṁ </span></span></p>\n<blockquote class='uddanagatha'><p><span class='verse-line'><span class='segment' id='21.1' data-uid='thi-ap40:21.1'><span class='pli'>Sumedhā ekūposathā, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.2' data-uid='thi-ap40:21.2'><span class='pli'>kuṇḍalakesī khattiyā; </span></span></span>\n<span class='verse-line'><span class='segment' id='21.3' data-uid='thi-ap40:21.3'><span class='pli'>Sahassaṁ tisatā gāthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='21.4' data-uid='thi-ap40:21.4'><span class='pli'>sattatālīsa piṇḍitā. </span></span></span></p>\n<p><span class='verse-line'><span class='segment' id='22.1' data-uid='thi-ap40:22.1'><span class='pli'>Saha uddānagāthāhi, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.2' data-uid='thi-ap40:22.2'><span class='pli'>gaṇitāyo vibhāvibhi; </span></span></span>\n<span class='verse-line'><span class='segment' id='22.3' data-uid='thi-ap40:22.3'><span class='pli'>Sahassaṁ tisataṁ gāthā, </span></span></span>\n<span class='verse-line'><span class='segment' id='22.4' data-uid='thi-ap40:22.4'><span class='pli'>sattapaññāsameva cāti. </span></span></span></p></blockquote>\n<p class='endsection'><span class='segment' id='23.1' data-uid='thi-ap40:23.1'><span class='pli'>Therikāpadānaṁ samattaṁ. </span></span></p>\n<p class='endbook'><span class='segment' id='24.1' data-uid='thi-ap40:24.1'><span class='pli'>Apadānapāḷi samattā. </span></span></p></article>\n"
  }
});
